-- ----------------------------
--  Table structure for `bbs_post`
-- ----------------------------
DROP TABLE IF EXISTS `bbs_post`;
CREATE TABLE `bbs_post` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `uid` int(11) NOT NULL COMMENT '用户id',
  `title` varchar(30) DEFAULT '' COMMENT '帖子标题',
  `content` text NOT NULL COMMENT '帖子内容@好友',
  `label` varchar(50) NOT NULL DEFAULT '' COMMENT '帖子标签',
  `edition_id` int(11) NOT NULL COMMENT '版区id',
  `lat` varchar(50) DEFAULT '0' COMMENT '纬度',
  `lng` varchar(50) DEFAULT '0' COMMENT '经度',
  `location` varchar(200) DEFAULT '' COMMENT '发帖地址',
  `examin` int(11) DEFAULT '0' COMMENT '后台审核人',
  `operating_time` int(11) DEFAULT '0' COMMENT '审核时间',
  `is_del` tinyint(1) DEFAULT '0' COMMENT '状态；0正常；1主动删除；2后台删除',
  `is_top` tinyint(1) DEFAULT '0' COMMENT '置顶状态；0取消置顶；1添加置顶',
  `topped_at` int(11) DEFAULT '0' COMMENT '置顶时间',
  `is_essence` tinyint(1) DEFAULT '0' COMMENT '精华状态；0取消精华；1添加精华',
  `created_at` int(11) DEFAULT '0' COMMENT '创建时间',
  `updated_at` int(11) DEFAULT '0' COMMENT '更新时间',
  `deleted_at` int(11) DEFAULT '0' COMMENT '删除时间',
  PRIMARY KEY (`id`),
  KEY `index_id_isDel` (`id`,`is_del`),
  KEY `index_uid` (`uid`)
) ENGINE=InnoDB AUTO_INCREMENT=10861 DEFAULT CHARSET=utf8mb4 COMMENT='帖子表';

-- ----------------------------
--  Records of `bbs_post`
-- ----------------------------
BEGIN;
INSERT INTO `bbs_post` VALUES ('10001', '0', '海贼王：路飞的四个愿望，第三个只有女帝能帮他实现而且已经实现', '动漫《海贼王》中路飞在东海时与香克斯的相遇，让他对海贼的生活产生了无限的憧憬，更是立下了要当海贼王的誓言。长大后变开始了作为海贼的梦想，从他当海贼开始就有了四个愿望。第一个：要找10人的伙伴组成自己的海贼团路飞从只身一人出海到现在包括他在内已经有了10人的伙伴。而路飞刚开始时许的愿望是要找10人不包括他在内，也就说在甚平上船后路飞的这个愿意还是没有达成。第二个：成为海贼王海贼王即使路飞的愿望又是他的梦想，而他也正在为了这个愿望一点点的变的强大，也正在靠着最终之地拉夫德鲁成为海贼王。第三个：大闹海底大监狱路飞在女儿岛时得知艾斯要被处刑并且关押在了海底大监狱，于是他就萌生了救艾斯的这种想法，救艾斯无疑就是要大闹海底监狱。以路飞的性格越是危险他越想去，就像攻陷司法岛，暴打天龙人。而他的这个愿意也只有女帝能帮他实现，确实女帝也帮他实现了。如果没有女帝的帮助路飞是到不了海底监狱的。第四个：复活艾斯顶上战争路飞为救艾斯拼尽了全力，救出后艾斯又因救路飞被赤犬所杀。艾斯的死给路飞带来了很大的痛苦，如果没有甚平的心理疏导路飞可能就一蹶不振了。而复活艾斯也就成了路飞的愿望，如果艾斯真复活了该有多好。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10002', '0', '海贼王中天不怕地不怕的路飞，最怕这几个人', '海贼王的主角路飞是一个单纯天真、一根筋的人，所以做事情总是很少经过大脑，不计后果；另外，路飞积极乐观，对自己的实力也是十分自信，这也造就了路飞天不怕、地不怕的性格。可是纵观整部海贼王，路飞还是害怕过好些人，今天就来看看路飞最怕的几个人吧！卡普从路飞小时候，卡普就一直教育着他，一旦他做错了事情，上去就是一顿好打；而卡普作为路飞的爷爷，路飞自然不敢还手，久而久之，就给路飞留下了极为深刻的童年阴影。在水之都时，就是最好的证明，即便路飞已经17岁了，但他在卡普眼里还是个长不大的孩子，该教育教育，所以无论路飞实力达到什么地步，他还是非常怕卡普的！达旦卡普将年幼的艾斯、路飞托付给达旦照顾，所以达旦就是路飞名义上的“养母”，按照路飞性格，也会把她当成自己的家人，不敢轻易还手。达旦看上去是一个粗暴的男人婆，打人从不手软，相信路飞也没少挨她的拳头吧！不过达旦内心十分的善良，也是最了解和关心路飞的人之一。娜美路飞害怕娜美已经是所有人都知道的事实。每次路飞干了蠢事，娜美上去就是一顿臭骂、狂吐槽；有时候甚至忍不住直接给了路飞一拳；最惨的几次，路飞直接被揍得满头包，而且包比自己的头还要大，实在太搞笑了。不过，两年后很少看到这样的情节了，可能是路飞成长了，不再像以前的那么小孩子气，但娜美给他留下的阴影应该还没散去。麦哲伦其实第一次面对麦哲伦的时候，路飞并没有害怕他，而是选择与他硬刚，结果被毒掉了半条命，多亏了小冯和人妖王的帮忙，才得以活下来。但俗话说得好，一朝被蛇咬十年怕井绳，自此路飞内心也对麦哲伦产生了恐惧；虽然说第二次对决麦哲伦，有了MR.3的帮忙，也成功地把他揍出血，但却逼出了麦哲伦的大杀招，吓得路飞和越狱小分队落荒而逃。BIG MOM按照漫画进度，万国篇章已经结束有一段时间了。草帽一伙在蛋糕岛不仅破环了大妈的计划，干掉了两个将星，打碎了大妈最珍贵的修女照片，还带走了历史正文拓本，拐跑了宙斯......可以说大妈现在是恨死路飞了，甚至还电话凯多说别管闲事，自己要亲自动手干掉路飞。而大妈现在的实力肯定能吊打路飞，加上之前路飞是依靠杰尔玛和新鱼人海贼团等人的帮助才能逃离蛋糕岛，所以路飞现阶段最怕的人绝对是大妈。好了，今天就到这里了！你还知道路飞害怕哪些人呢？欢迎补充！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10003', '0', '海贼王路飞五档能力预测，路飞5档图片', '海贼王路飞五档这篇纠结好久，张山治一直犹豫要不要发，毕竟路飞5档能力属于剧情预测。本着“因为喜欢，所以YY”的原则，不想那么多了，打脸就打脸吧。本文仍然是脑洞和推理猜测，不喜勿喷，还是经常看评论区哈路飞五档·实力需要每一个大的篇幅都会有个实力的进阶提升，从司法岛的二档到对阵明哥的四档。进入新世界之前七武海看似神一般的存在，顶上战争之后四皇、大将、元帅、五老星变态丛生。对阵能力觉醒的明哥已经相当吃力，大妈这个篇幅还真不知道怎么开挂，后面还有毁灭明哥全团能力的凯多。。。有个脑洞就是尾田设计路飞能力参考了汽车档位，哈哈哈，日本汽车绝对是工业强势，为毛不能当参考坐标。一般汽车是有5档的，配合双离合加五级变速，忍不住笑了。。。路飞4档路飞四档·现有分析按身体器官维度拆分，一档开发的是皮肤、二档开发的是血液、三挡开发的是骨头、四档开发的是肌肉，那么问题来了，还有什么部位可以利用呢。皮肤类似于气球的功能，体现橡胶的弹性，可大可小。二档通过加速血液循环，提升速度和力量，血液的速度受心脏影响，同时也构成了负担。三档跟一档类似通过吹气使局部变大，由于强化的是骨头，所以杀伤力比一档强太多。四档的时候已经有了霸气的防护，压缩肌肉获得反弹力，毕竟弹性十足。路飞大猿王路飞五档·能力推测【速度】张山治还是陷入到汽车档位不能自拔，手动档5档跟4档的区别是什么？速度！！速度更快了！天下武功，唯快不破。还记得顶上之战黄猿的鄙视吧，“太慢了”。每一次的战斗对能力的开发都有一定的吸收和参考，3档4档是经历了巨人族之后的参考，通过变大加大攻击影响。速度快也意味着冲击力强，体量大有个弱点就是不灵活，有了弱点就有提升空间。【觉醒】路飞5档=4档+觉醒，明哥让我们知道果实有更高阶的版本能力觉醒。打七武海前，霸气好稀缺好牛逼，现在烂大街。能力觉醒可能跟进入新世界的霸气一样，四皇凯多极有可能。能力觉醒目前的设定是可以使周围的实物变成果实的实物，比如明哥的线线果实，那路飞是不是可以让周围变橡胶，这个脑洞怎么开呢。空气变橡胶，觉醒空气或外物成猿王枪？能力觉醒的明哥路飞5档·图片参考网上流传一组由国外海迷爱好者自行脑洞版的路飞5档图片，好多海迷信以为真。话说回来，也许尾田大叔也在观望看到比较好的点子被采纳也是可能的。路飞5档图片额头印记路飞5档霸气包裹全身路飞5档参考龙珠', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10004', '0', '海贼王中那些值得敬佩的人，索隆甘愿为路飞放弃生命', '海贼王之所以能成为世纪经典巨作，与其非常细腻的人物塑造是分不开的。在海贼王中，无论是正派反派还是配角小喽啰，尾田都是从不吝惜笔墨的，为的就是将他们刻画得更有模有样，更完整把人物性格表现出来，借此来感染大家。那么，今天就来跟大家说说尾田笔下最值得敬佩的几个角色吧!希鲁鲁克作为医生，他虽没有非常厉害的医术，却用自己的精神在感化磁鼓国的人们。他其实很像鲁迅先生，都是医生，都在救国，从某种意义上都成功了，但希鲁鲁克却被很多人遗忘。这里不得不提一下，希鲁鲁克那一句名言：人究竟什么时候会死?不是心脏被枪打中的，不是得到不治之症后，也不是喝了剧毒蘑菇汤之后，而是被世人遗忘的时候。希鲁鲁克虽然微不足道，但却值得我们敬佩。克洛克达尔克洛克达尔是为数不多的、比较讨人喜欢的反派。其实在我开始认识老沙的时候，对他并没有太多好感。他生性残忍，也擅于算计，对于任何没有利用价值的人都会毫不犹豫地干掉。可是在顶上战争中，老沙的表现简直圈粉无数，一方面各种怒怼四皇、海军大将、七武海，证明了他实力的强大，另一方面说出来了好些称得上至理的名言，实在令人敬佩，比如最经典的想要守护的人就要好好保护好。红发香克斯香克斯是驰骋新世界的四皇，有着绝对强大的实力。在他断臂之后会面白胡子，白胡子都不相信，他这样的角色竟然会在东海这个地方丢了一只手臂。当然，香克斯的手臂丢得也确实很蹊跷。但考虑到香克斯是为了激励路飞，那就没多少好质疑的。愿意为了路飞丢了一个手臂的人，绝对值得我们敬佩。冯·克雷说起海贼王最讲义气的人，小冯绝对榜上有名。第一次小冯为了帮助草帽团逃走，惨遭逮捕被关进了推进城;第二次小冯带着路飞求医，在LV5雪地与狼群搏斗;第三次小冯为了帮助路飞等人逃离监狱，不惜牺牲自己打开正义之门。回看小冯的三次义举，他正是这样一个愿意为了朋友伙伴，付出自由乃至生命的人，值得我们敬佩。艾斯在顶上战争中，艾斯本可以顺利逃走，但却因为看不过赤犬羞辱自己的“老爹”，不惜与之硬刚，惨遭一顿暴打;本可以挡下赤犬的攻击，伤不至死，但却为了路飞的安全，不惜用自己的血肉之躯去对抗岩浆，最终身体被击穿而亡。艾斯已经好好地守护了他想要守护的东西，非常值得我们敬佩。白胡子白胡子是海贼王世界中最强的男人，有着极为宽广的胸襟。虽然没有称霸伟大航道，但却让很多形影孤单的海贼有了一个温和的家;即便被家人背叛，也毅然地选择了原谅;为了自己的孩子们，不惜牺牲自己。在顶上战争中，白胡子总计承受了刀伤267处、子弹152发、炮弹46枚，即便是死亡，也依然仍保持着杀敌的姿态;在他的整个海贼生涯里，背上没有一处因为逃跑而留下的伤痕，真的非常值得我们敬佩。索隆索隆是草帽团的副船长，他本身颇为男子汉硬派的作风及坚毅的武士道精神，就已经吸引了无数粉丝。在恐怖三桅帆船篇中，索隆面对实力比自己强大数倍的大熊时毫不畏惧，虽然败下阵来，但不可否认他的英勇;最终索隆更是为了路飞，甘愿放弃自己的梦想和生命;这样一个宁愿牺牲自己，守护别人的索隆，正是最值得我们敬佩的人。个人觉得修炼两年之后的索隆更加的帅气，能够征服更多的海米，并且觉得索隆的实力已经达到了很高的程度，期待着他在和之国篇的精彩表现。部分来自海贼王中文网', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10005', '0', '海贼王：20岁的路飞像罗杰，40岁的路飞像红发，60岁的路飞', '有句话是这样说的：‘长大后我就成了你’，在小漫心中是这样理解的：一个人通过自己的努力，慢慢的靠近心中的偶像，当长大后说不定真的成了心中的那个“你”，当然这个“你”是内在的！对路飞一生影响最大的有三个人：卡普、香克斯、罗杰，一个让他顽强，一个让他成长，一个让他充满希望！也许是尾田大神有意为之，在《海贼王》中，当路飞长大到老去，无论是外表还是内心，都真的成了心中的“你”！20岁的路飞像罗杰，40岁的路飞像香克斯，60岁的路飞像卡普！20岁的路飞像罗杰成为海贼王是路飞的梦想，罗杰是他的榜样！从路飞出海，到达罗格镇，到达双子塔，再到新世界，很多人发现路飞与罗杰很像，这种像深入骨髓，是内在的像，连对罗杰最亲近最熟悉的雷利也是这种感觉！不过小漫认为最像的还是他们的长相，虽然罗杰一直给我们的感觉是个威严的大胡子叔叔！但罗杰年轻时也帅的一匹，尤其是刚刚出海与雷利相遇时，仿佛和路飞是隔世亲兄弟！不过现在路飞的脸稍显稚嫩，也许到了20岁，路飞真的成了年轻时的罗杰！40岁的路飞像红发香克斯香克斯是路飞海贼路上的领路人，是路飞的榜样与追求！虽然现在同为五皇，但路飞的地位和实力还和香克斯差的远，尤其是沉稳的性格和张扬的霸气，是20岁的路飞望尘莫及的！不过等路飞到了40岁，笑容稍作收敛，更显沉稳，但飘逸的头发和浅浅的胡茬，更显霸气，与同龄的香克斯非常的像！60岁的路飞像卡普卡普是路飞现如今唯一认识的亲人，是他最亲近的人，也是对他影响极大的人！虽然想法不同，一个是兵一个是贼，但骨子里的乐观和坚强都是一样的！等路飞到了60岁，也在慢慢向卡普靠拢，虽然人老面衰，但不失幽默风格，像个老顽童，那时的路飞也懂得了分寸，笑容更显深邃！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10006', '0', '《海贼王》路飞的家人实力排行，艾斯第四，路飞倒数第二', '《海贼王》路飞的家谱被称为是OP最强家谱，因为他们家有与海贼王罗杰、战国、白胡子等人齐名的海军最有地位和实力的中将卡普，还有被世界政府视为头号罪犯的革命军首领蒙奇·D·龙，目前还不知道路飞的妈妈和奶奶的身份和背景，相信也不会是什么普通人。路飞在草帽海贼团和十一超新星中实力可以说是最强的（因为还有一个索隆），但是，在家人中的路飞的实力如何呢？蒙奇·D·卡普卡普在海军的地位和声望很高，被称为最强海军中将，与战国、罗杰、白胡子等人齐名，能轻松把路飞揍得满头是包喊疼，路飞最恐惧的男人。蒙奇·D·龙卡普的儿子，路飞的老爸，革命军首领，世界政府的头号最凶恶的罪犯，在罗格镇轻松制止要抓捕路飞的斯摩格，实力高深莫测。萨博萨博是东海哥雅王国的贵族，艾斯和路飞的结义兄弟，革命军的二把手——参谋总长，轻松击败黑胡子海贼团第一队长芝沙斯·巴沙斯，对战海军大将藤虎表现从容。艾斯海贼王罗杰之子，悬赏金五亿，白胡子海贼团二番队队长，曾被世界政府邀请加入七武海，但是他并不感兴趣，与出海之前的路飞对战保持全胜记录。蒙奇·D·路飞草帽海贼团船长、草帽大船团头目，被称为最强十一超新星，虽然打败了各大高手，比如沙鳄鱼、艾尼路、多弗朗明哥、克力架等人，但是在他的家人中的实力排行中几乎是垫底。达旦最不像山贼的山贼，把艾斯和路飞从小带到大，一直养育到这哥俩出海，对路飞和艾斯来说是他们最要的家人，没有果实能力，只是一个普通的山贼。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10007', '0', '海贼王：最帅的路飞并不是女帝眼中的路飞，在他眼里路飞就是王！', '海贼王中路飞作为主角，出了名的神经大条，尤其是在平时的时候，像极了地主家的傻儿子~但是在少数几个人眼里，路飞那就是天上星，海中月，帅的没边！对于女帝来说，早已把路飞当成了自己的丈夫路飞曾多次出现在女帝的幻想中，顶上战争的时候，女帝曾幻想两人结婚路飞在女帝的幻想里，身穿结婚礼服，相当帅气在去往海底监狱的时候，女帝对路飞也曾有过幻想当时的路飞，浓眉大眼，帅气无比~但是，女帝幻想中的路飞还不是最帅的,在德岛，路飞遇到了自己的小迷弟巴托洛米奥对于巴托洛米奥来说，自己出海就是为了追寻路飞的脚步在巴托洛米奥的幻想中，路飞已然当上了海贼王从这样的画面里，大家是不是应经看到了路飞当上海贼王的样子？不得不说，巴托洛米奥是迄今为止，海贼王里最大的脑残粉，不管是对路飞，对草帽一伙的所有人都是很尊敬，但是我就没搞明白，他怎么敢烧了香克斯的旗子？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10008', '0', '路飞的第二个恶魔果实大起底，路飞到底有没有第二个恶魔果实', '随着剧情的发展和实力的需求，海迷们对路飞什么时间拥有第二个恶魔果实的讨论越来越多，小编就来起底一番。支持路飞拥有第二个恶魔果实的分析主要有三个方面【实力不够，果实来补】在海贼的世界观里实力就是一切，想要成为海贼王，首先要征服至少要比大妈、凯多、香克斯、黑胡子强，然后还有海军高层包含3大将藤虎、黄猿、绿牛和元帅赤犬。虽然路飞打败了明哥，但实力也就排在七武海的水平，甚至还比鹰眼要弱。PS.毕竟人家是世界第一的大剑豪好不好，不然索隆的面子往哪搁。影响实力有三大因素：果实、体术、霸气，目前果实和霸气有了，体术需要长期的锻炼，所以果实会被寄予很大的期望。萨博获得火火果实之后，实力飙升；黑胡子获得黑暗果实后地位暴增。所以路飞如果拥有第二个果实能够迅速拉到四皇的水准，可以为接下来打败大妈和凯多做铺垫。海军大将【黑胡子有，路飞也有】不可否认的是，所有的好处几乎都跟主角有一定的关系，诸如强大的朋友圈卡普、香克斯、龙、雷利，大家细思一下极恐有木有，一个小孩一路都受到呵护和引导。再来看看反面主角黑胡子，隐忍白胡子海贼团数十年杀死四番队长萨奇后得到暗暗果实实力暴增，而后杀死白胡子获得震震果实，目前来说简直天下无敌。路飞从小获得香克斯的橡胶果实，黑胡子有两个，路飞也可以设置剧情加入第二个。黑胡子暗暗果实【实力对阵，果实相克】之前有开过脑洞就是强大如能力觉醒的明哥大将都不怕，可能不敌鹤中将的洗洗果实或者亚尔丽塔的滑滑果实；原因在于果实相生相克遵循自然规律。前七武海克洛克达尔老沙实力远在那时路飞至上，之所以败阵是因为找到了沙子怕水的漏洞。那么问题来了，路飞的劲敌黑胡子拥有暗暗果实和震震果实怎么破，路飞的橡胶果实天生有减震缓冲的作用对震震果实，那暗暗果实代表的黑暗肯定需要光和火来解决。基于此，推导出路飞在后续的过程中还需要类似于黄猿的闪光果实和艾斯的火火果实。黄猿闪光果实路飞无需拥有恶魔果实的分析有3点【自力更生，自学成才】路飞应该不会再吃第二个果实，草帽海贼团靠的都是自己的实力，而且不断在进步。路飞不仅是以霸气作为自己的一项优势，而且他的学习能力也是非常强的。你看他没吃果实就学会了火拳，还学会了弹簧腿，在与CP9的较量下，还学会了第二档和第三档。虽然现在还在努力变强中，相信以后一定更让人吃惊的，至少在江湖上的地位一定是非常高的。目前暂时不能与厉害角色敌对，以后一定可以。目前打败多弗朗明哥，接下来还有4皇，马上就是凯多和大妈了，过了这关实力就差不多了。说不定以后还有第五档、第六档。。。。路飞4档【剧情衔接，价值不符】路飞应该是不会吃第二个果实，海贼王总体上来讲属于励志类的故事，尾田大叔应该会让草帽海贼团的成员们靠努力修行走上顶峰，恶魔果实只是辅助而已。比如索隆、香吉士，他们都没有果实能力，但是战力绝对不是盖的，再说路飞的爷爷卡普，至今没有表现出果实能力，但是他是把哥尔罗杰数次逼的走投无路的人，他的战斗力绝对不逊色于战国、白胡子这些人，至少我认为到现在出场的人物，卡普的对手不会超过五个人，所以说路飞以后的成长需要伴随着磨练，而不是恶魔果实这些外力因素。卡普【体术来补，船员来补】目前还没提到罗杰、雷利、香克斯、鹰眼这些超级大佬们拥有如何牛逼的果实而称霸海域，基本是依托体术和霸气。罗杰拥有能够聆听万物的声音的能力，在人鱼岛篇中，路飞也曾听懂过海王类的语言。在海贼王中，罗杰可以说是神一样的存在，贯穿整个海贼世界的重要人物。这样的一个人，他的本身能力加上经历早被传的神乎其神，他的能力即便政府海军想要隐瞒，也瞒不了!却没有传出来他的恶魔果实是什么?而他拥有霸王色的霸气却是众所周知!而且罗杰的副船长冥王雷利也是十分厉害，还是路飞的霸气指导老师。路飞霸气以此来看，世人都知道罗杰厉害，但是却不是什么恶魔果实能力。反而将霸王色的霸气传的厉害，要知道，罗杰和雷利大叔刚认识的时候，真有路飞和索隆相识的意味。那时候罗杰和路飞一样，根本还没有锻炼出霸气。没有霸气，就是有恶魔果实，而且是相当长的一段时间是用果实来抗敌，反而这样的一个名人，果实能力却没有表露，以此来推断，罗杰根本就没有恶魔果实能力!同样的道理也适用于雷利、鹰眼、卡普、索隆，通过体术来不断完善自己的战斗力。路飞说过要集齐10个伙伴一起航行，目前成员有索隆、娜美、乌索普、山治、罗宾、乔巴、布鲁克、弗兰克，还有一个超级厉害的没有加入。越往后发展加入的伙伴只会更加变态更加牛逼，所以新加入的伙伴能力目测抗衡大将。而索隆成为世界第一的大剑豪，显然能力要在鹰眼至上；作为副船长仅仅打败七武海鹰眼显然也不够，至少匹配大将和四皇，否则元帅和黑胡子怎么搞，不能全部都是路飞上，总要有些分担。最期望的部分还是厨子山治，最近山治因为大妈的婚事想退出，却跟娜美交代会回来。存在的可能就是山治实力大爆发，一举干掉大妈，送个路飞一份超级大礼包。山治实力爆发路飞的第二个果实小编怎么看综上，小编认为罗杰能成为海贼王，固然是有那个实力和魅力。能被世人传说，就在于他抛却拥有霸王色霸气之外，本身是一个普通人的缘故，而站在了海贼的巅峰，所以才成为传说。而路飞肯定是D的隐名人，本身隐藏着巨大的玄机，体术还有很大的提升空间，恶魔果实觉醒加上第五档、第六档的开发，都是变态级的实力。而船员都还没到期，山治、弗兰奇、布鲁克实力还没跟上，还需一路打怪历练，加上团队的自学能力应该是可以补足能力登顶海贼王。草帽海贼团成员因为喜欢，所以YY；欢迎关注和评论区给我留言。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10009', '0', '海贼王，为什么路飞全体成员都相信路飞能当上海贼王？', '今年是海贼王连载的第20年，海贼王带给了我太多的感动，也陪伴无数人的成长，而热血战斗一直都是海贼王中最核心的主题。从阿拉巴斯坦篇硬刚克罗克达尔、到司法岛上与CP9性命相搏、到香波地岛上全员被强制分割、到德雷斯罗萨对同伴的守护，再到现在万国篇怒怼四皇Big Mom，伟大航路上的强者如过江之鲫，只有战斗才能守护自己最珍贵的东西！为什么路飞全体成员都相信路飞能当上海贼王？难道仅仅只是因为他们是路飞的伙伴吗？索隆山治娜美乔巴罗宾布鲁克弗兰奇乌索普还有路飞未来的老婆！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10010', '0', '路飞背景太深厚，看看路飞背后有哪些强者，路飞想当海贼王很轻松', ' 海贼王中路飞的梦想就是当上海贼王，为此路飞扬帆起航进入了伟大的航道，但是仔细想想路飞其实真的不用那样的辛苦，因为路飞的背景太深厚了，如果路飞想当海贼王让这些人帮他一把绝对能分分钟当上海贼王。今天我就和大家盘点一下路飞背后的恨角色们。 第一个：爷爷，卡普，是海军中将，是海军的英雄，是海军里的灵魂人物，卡普的一句话就能引起海军的震动，如果最后必须选一个海贼王的话，那么卡普肯定会选择路飞，只要卡普愿意带着一帮海军把四皇灭掉，路飞可以轻轻松松的当上海贼王。 第二个：父亲，龙，革命军的最高首领，一直在和世界政府战斗，如果哪天龙想让自己的儿子当上海贼王，那么带着自己的一群小弟和卡普联手灭掉四皇，路飞就当上海贼王了。 第三个：朋友，红发香克斯，红发是引领路飞走上海贼之路的人，是路飞的恩人加朋友，现在的红发是四皇之一，而且感觉红发一直想培养路飞成为海贼王，如果再来个四皇来帮助路飞，路飞还用那么奋斗吗。 第四个：师傅，雷利，上任海贼王罗杰的副船长，雷利到现在都没有展现过真正的实力，但是我感觉雷利最少也拥有准四皇的实力，而且雷利对如何让路飞当上海贼王应该很有经验，所以有雷利帮助路飞，路飞可以很轻松的当上海贼王。 第五个：哥哥，萨博，现在的萨博应该拥有准大将的实力，而且萨博是真正愿意为路飞付出生命的人，只要路飞想要萨博帮助，萨博可以义无反顾的来到路飞身边。  上面这些狠人都是海军、海贼和革命军中的最高领导者，而且实力强的可怕，路飞的背景实在太深厚了，只要他们想让路飞当上海贼王，路飞可以分分钟坐上海贼王的宝座，感觉路飞根本不用这样辛苦啊。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10011', '0', '路飞娜美、路飞罗宾、路飞女帝、路飞索隆，总有一款适合你', '路飞，《海贼王》的主角，身边美女如云，让我们一起看看路飞的CP。（作者：行走的漫画。）路飞索隆：硬汉组合路飞娜美：帅男潮女组合路飞罗宾：姐弟组合路飞女帝：官配组合路飞白星：公主少爷组合你们喜欢哪一款，还有其他CP吗？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10012', '0', '路飞和佩罗娜相遇，女帝怀疑路飞有外遇，而路飞还是那么淡定', '大家都知道，“海賊女帝”波雅·漢庫克唯壹最愛的男人就是路飛，自從愛上路飛之後就壹發不可收拾，死心塌地的甘願為路飛做她不喜歡做的事情，對路飛完全是言聽計從，這就是愛情的力量啊！“海贼女帝”波雅·汉库克而“幽靈公主”佩羅娜是眾人所公認的“索嫂”，OP女神之壹，她可以說和路飛沒有半點CP關系，但是，她的出現卻讓女帝感到不安了。“幽灵公主”佩罗娜頂上戰爭之後，路飛再次回到亞馬遜·百合跟著雷利修煉，在修煉的過程中，卻遇到了號稱“世界破壞者”邦迪·瓦爾德，他抓走了女帝的兩個妹妹，並且讓女帝去救她們，然後路飛也跟著去了。草帽海贼团船长蒙奇·D·路飞在與邦迪·瓦爾德的交戰過程中，正好遇到了佩羅娜，並且幫助他和女帝，不過路飛壹下子認不出她來了。雖然路飛並不在意，可是，女帝都是看在眼裏，醋意大發，弱弱地問路飛佩羅娜是誰啊？而路飛卻淡定的回答：那個會飛的姑娘，那個誰，我記起來了，但是，感覺以前好像見過。路飛的隨意回答，卻讓女帝胡思亂想，真是不懂女人心啊！路飛雖然不是很在意佩羅娜到底是誰，但是，女帝卻很在乎，覺得是路飛在敷衍她，難道是路飛喜歡的女孩？此時此刻，女帝想的是佩羅娜，還有路飛和佩羅娜兩個人是什麽關系，而路飛卻著急地找出口。路飛越是無心，越是不在意，越讓女帝胡思亂想，還沒結婚呢，路飛就有外遇了。不過，這都是女帝壹個人胡思亂想的了，路飛才不是那種人呢！這樣的女帝好可愛啊', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10013', '0', '海贼王：路飞吓晕，大妈和路飞小时候的战绩对比', '大妈5岁：一巴掌拍死巨人族大熊，毁了自己的村庄，又毁了巨人族村庄，拍蚊子拍断巨人族的肋骨，干掉老巨人族船长，霸王色觉醒，最后忘说了，大妈根本没有任何训练路飞5岁到7岁之间：被小老虎虐，特训后联合艾斯萨博勉强打败了小老虎，被一只小海怪欺负，被菜鸟山贼虐，被菜鸟海盗虐，联合艾斯等人勉强打败了菜鸟海盗路飞苦练10年，17岁出海，感觉实力还不如没任何训练的5岁大妈大妈这天赋，如果像弗利萨一样特训4个月，5岁就能当上七武海，再训练1年，6岁稳坐海贼王，你们说是么', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10014', '0', '海贼王：东海的6位强者，最强能秒大将，最弱也是准大将', '东海在《海贼王》里被称为最弱的海域，但你可能并不知道，其实东海也盛产很多强者，今天Sherlock就给大家来说下东海的强者，最强的能够秒杀大将，最弱的实力也是准大奖实力，不信的话你可以往下看。第六：索隆索隆出生于东海霜月村，后来被路飞邀请上船，称为“草帽海贼团”第二人（第一人是路飞），索隆的目标是大剑豪，自从输给鹰眼之后就再也没输过，经过两年的训练，如今索隆的实力最起码也是准大奖级别。期待索隆在和之国的战斗，到时会把他真正实力会展示出来给大家看的。第五：路飞路飞出生于东海的风车村，因为小时候邂逅了香克斯，因此立志要成为海贼王。在路飞17岁的时候开始出海，经过不断的磨练和成长，路飞先后打败了四位七武海，还收了一位七武海为后宫。目前路飞已经成为了第五皇，Sherlock认为路飞现在的实力应该达到了大将级别。第四：萨博萨博是路飞的二哥，也是东海出生，但身份比路飞要好，他是东海哥亚王国贵族的公子，因为讨厌贵族就离家了，后来认识路飞和艾斯。萨博自从被龙救下后就在革命家里，经过龙的不断栽培，如今是革命家的二把手，实力也是相当强悍。后来在德岛的时候又吃了烧烧果实，现在的实力，最起码也是大将级别。第三：龙龙是路飞的老爸，在路飞很小的时候就离开了村子，开始干起革命工作。如今世界zf最大的敌人就是龙。龙的实力如何目前还是一个迷，既然作为革命家的一把手，实力绝度是比萨博强的，据推测，龙的实力最起码也是四皇级别的，这样才能和zf的站立齐平。第二：卡普卡普是路飞的爷爷，也是东海风车村的，卡普的实力可以说相当的强悍，最起码也是四皇级别。卡普在海军里面被称为英雄，在卡普年轻的时候打败了四皇凯多和四皇大妈的船长“洛克斯”，从而奠定了卡普在海军里的神话。后来卡普又满世界追罗杰跑，足以证明卡普的强悍。Sherlock说卡普能秒赤犬并非是大言不惭，在艾斯死的时候卡普亲口说的，后来也证明了卡普有那个实力，秒掉赤犬也不是不可能。第一：罗杰罗杰出生东海罗格镇，至于他实力，目前还没看到展示出来，但凭借他“海贼王”的身份，足以说明实力逆天。四皇都无法称王，唯独罗杰可以，可以说明罗杰的实力应该强于四皇，作为海贼王，秒掉一位海军大将也不是不可能的事，罗杰绝对有这个实力。以上就是Sherlock这次分享的六位东海强者，尤其是后面三位实力都还会再晋升的，尤其是索隆，今后绝对是大将级实力，路飞今后也会有超越四皇的实力，不过这都是后话。如果大家喜欢《海贼王》的话，记得关注哦！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10015', '0', '影子路飞比四档路飞厉害！路飞的变强之路！', ' 海贼王是热血少年漫，激烈的情感与酣畅淋漓的战斗是其主旋律。 这不是《一拳超人》，路飞不是埼玉，我们希望看到的是草帽一伙即使不是对手，也会勇往直前的去挑战，在战斗中成长，挑战一个又一个看似不可能！ 打爆世间不平事！ 在这过程中，战斗者需要不屈不挠的斗志、愈挫愈勇的精神和战斗中领悟出新招式从战斗中吸取经验强大自我的战斗者的智慧！ 盘点路飞在冒险过程中的成长。 一：三兄弟的情义与梦想！ 吃了橡胶果实后的路飞，身体软绵绵的，出拳毫无力度，在回忆录中，我们看到了路飞、艾斯、萨博三兄弟的成长，三兄弟为了各自的梦想，疯狂训练，即使最弱的路飞也获得了打败近海之王的实力。 而这时的路飞，借助自己恶魔果实的能力，开发出了用橡胶身体模仿各种武器，橡胶火箭炮、橡胶机关枪、橡胶子弹、橡胶战斧的战斗方式。 长大后，刚出海的他用橡胶枪击败了近海之王，奠定了路飞模仿武器的战斗方式。 二：一切为了伙伴，二档出击！ 在被青雉击败后，路飞意识到了敌人的强大，自己力量的不足，领悟出了二档三档形态，二档在营救罗宾的时候在水之岛初次登场，打败了CP9。 二档的原理是通过加速血液流动，来提高整体身体素质，但在招式上没有什么大的改进，多数是一档招式的加强版，比如橡胶JET枪、橡胶JET火箭炮等等。 三：巨人之力：三档！ 三档同样出现在水之岛剧情中，但只是一晃而过，而且看起来当时还不完整，在后来的战斗中逐渐展露全貌。 橡胶巨人战斧、橡胶巨人火箭炮、橡胶巨人手枪，路飞从巨人身上得到了灵感，用这种朴素的方式增强了攻击力。 这个能力让我想起来《火影忍者》中的秋道家族中的倍化术、局部倍化术、超级倍化术等秘术。超级像有木有！ 四：霸气的组合，四档 YY小说中的王霸之气……  可以看到，四档中不止用霸气武装了自己的拳头，在招式中还混入了各种生物。这无疑是两年时间里跟雷利在岛上训练时跟动物们学的！ 五：最强形态，影子路飞 个人认为路飞最强的时刻是在击败月光莫利亚的时候，影子路飞形态！ 超可爱，超有趣有木有！ 在魔鬼三角地带最精彩的就是和奥兹的战斗，可谓是精彩绝伦，尤其是被强化后的路飞暴走的那一幕，值得看100遍。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10016', '0', '路飞只是天然呆，路飞天然呆时刻', '1. 路飞和索隆刚出海,肚子饿了.路飞指着天上一只鸟说:我们吃那只鸟吧.(橡皮橡皮的，冲上天空)，下一秒，路飞的头被叼在一只大鸟的嘴里:救命啊!救命啊!....索隆(死命划船):白痴!你在搞什么啊!!!2.在薇薇要离开的时候大家都不舍。然后索隆就说：既然舍不得，当初不硬把她拉上船就好了。说完后大家就骂索隆。到路飞骂索隆时。路飞:\"三刀流!\" 乌索普:\"慢着，三刀流又不是骂人的话\" 路飞:\"哦......四刀流\"3.路飞和山治在前往可可西亚村的时候，听到了有关鱼人的事。第一次路飞画了一幅鱼人的画，一条鱼长了四条脚。山治（大笑）：“真是杰作啊。“之后，路飞又画了一幅。山治：\"你只不过是把刚才的画立起来了而已吧。“4.在长链岛上。有个老伯挑战世界最长高跷纪录，老伯虽然踩上去了，却因为恐高下不来。踩高跷的老伯：“由于太高不敢下来于是就在上面度过了10年。”路飞：“老伯，你是白痴吗？”5.在剧场版死亡尽头的冒险中娜美：“最近没什么收入，买完粮食几乎一毛不剩了。” 路飞（难得正经）：“你们什么态度。喂，身为船长，我要说说你们啊，花钱也太不节制了“。众人（一顿痛扁）：都是被你吃掉的！！！！！！！！6.这个看了的都会笑的吧。路飞在空岛上唱的白痴歌。南边的岛minamino shima wa真是暖和 atatakai菠萝好吃 baina bolo bolo脑袋发热 atama poka poka笨蛋白痴 a ho baga北面的岛 kitano shima wa真是好冷 samui鲤鱼好吃 umai koi koi脑袋发抖 atama fura fura笨蛋白痴 a ho baga', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10017', '0', '路飞死亡！这三次要不是主角光环路飞就到此为止', '海贼王路飞的实力是一步步提升的。面对强敌，路飞有时候也是无可奈何。然而有三次，路飞却是毫无还手之力，甚至差点死亡。到底是哪三位能至路飞于死地呢？让我们一起来看看吧。第一次：克洛克达尔——穿透路飞胸膛、吸干路飞水分。第一个强敌七武海克洛克达尔，路飞面对这么一个大人物曾经也是被秒杀过两次。第三次时才靠着主角光环打败了克洛克达尔。第一次被克洛克达尔穿透胸膛，要不是罗宾出手相助，路飞就已经死在了无边沙漠中。第二次被克洛克达尔吸干，靠着吐上天空的一口水才活了过来。可以说这两次路飞都是九死一生。第二次：麦哲伦——被麦哲伦的毒淹没，不知所措。路飞潜入海底大监狱大闹了一场之后，遇上了麦哲伦，当时的路飞实力和麦哲伦相比简直是天壤之别，中了麦哲伦的毒后，路飞濒临死亡。要不是小冯拼命拖着路飞找到伊娃，路飞就已经横尸街头了。第三次：艾斯死亡——精神崩溃的路飞被艾斯打中胸膛。自己的哥哥艾斯为了救自己死在了面前，路飞抱着死亡的艾斯，精神崩溃了。之前就已经打过了两针伊娃的亢奋荷尔蒙，又立马遭受到如此大的精神打击，路飞再次濒临死亡。之后，抱着路飞逃跑的甚平又被赤犬贯穿胸膛波及路飞，路飞一线生机更加渺茫。这次路飞是真的快被打死了。路飞这三次是真正意义上的差点死亡，每一次都是对路飞生命的考验。当然了我们的路飞有着主角光环，怎么可能死呢 别问我为什么我就不信这事儿只有我干过', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10018', '0', '《路飞大战娜美》', '（淡入）1：帐篷外 河边 外景 傍晚这个傍晚显得很不平静，北方的风夹杂着寒意刺痛人骨，夕阳暗淡无光似是烧完所有能量，河水卷卷波澜起伏。路飞约莫 20 岁，眼神凌厉而坚定似乎是一个固执的家伙，在他的周围有一群 18 岁左右的男男女女，他们高矮不一胖瘦不齐，但脸上都露着担忧的神色。路飞：我要去救艾斯！语气坚定不容置疑。克拉桑，一个瘦小的丫头，满脸担忧：团长。。。女魔头太强大了。。。我们现在怕是打不过她。。。。妮可，高挑，长发飘飘，有种不食人间烟火的遗世独立：团长，你根本打不过娜美！杰克,约莫 10 岁的小女孩，躲在妮可后面露出一个小脑袋呆呆的说：团长这么厉害，万一要打的过呢？从帐篷里传来一个模糊的声音，似乎嘴里还有东西没吃完：不可能的。。。（咽了一口），娜美她可厉害了，我们加起来都干不过她的！！声音来自弗兰奇，一个胖胖的带着白南瓜帽子女孩，嘴角残留着没吃完的南瓜干沫。路飞：如果我不去，艾斯必然会死在娜美手中！但是。。。。。。（看着滚滚河水，路飞欲言又止)大家注视着团长面露忧色，所有人都懂得团长的急切，所有人都不知道该如何帮助他。（切至）2：河边 外景 黎明 阳光微露河水还泛着黑色，北风未歇卷起滚滚浪花，路飞独自拖着小船离开。风很大，浪很高，水很冷，小船在河中飘摇。这时岸上传来一道声音很是急切：\"团长！！！！你去干什么？！！！！！\"原来是妮可，遗世独立的她此时被风吹散了头发，她大声的呼喊着。路飞看着岸边的妮可深吸一口气：\"找娜美！救艾斯！！\"、妮可：\"若一去不回。。。。\"路飞停顿了一下：\"那便一去不回！！！！！\"(切至） 3：火山口 外景 中午 荒芜，黑色的火山石似乎禁止任何生物存在，灼热沸腾的熔浆杀死了所有爱和希望。在火山口中央悬挂着一副透明的水晶棺，它被八根锁链固定在中央，牢不可破。里面躺着一位约莫 20 岁的姑娘，增一分则太长，减一分则太短，著粉则太白，施朱则太赤，眉如翠羽，肌如白雪，腰如束素，双手重叠静静地放在胸口。\"放了她!\"语气透露出浓浓的不容置疑，路飞看着火山口中央，大声的呵斥道！\"哈哈哈哈哈哈哈哈\"一个阴冷狂傲刺耳的笑声响彻寰宇，一个身穿黑色羽衣的女子看着孤身一人的路飞放声大笑，在她的周围还有三个长的奇形怪状的二男一女：黑夜找不见的罗宾，高三米赤裸上身背着巨棍的乔巴，牛头人身带着黑墨镜的小盆。娜美，世界头号女恶魔，杀人无数，以折磨人的灵魂为乐，她的身材妖娆足以魅惑众生，眼神妩媚足以倾人城倾人国，谁都无法理解如此美貌之下竟包藏着蛇蝎心肠。娜美：只有你一人吗？你的伙伴呢？小哥哥挺有胆量的呀~路飞：我一人足矣！娜美：哈哈哈~小哥哥口气不小嘛！但不知这口气之下有几分本事呢？罗宾！去陪小哥哥玩玩！别打死了呦！罗宾，一言不语，慢慢的逼近路飞，眼神中没有眼神。路飞大喝一声：呵！忽然提速冲了上去！打打打打打打打打。。。。。看着罗宾渐入下风，暴露狂乔巴忽然行动：\"吃老子一棒！！！\"路飞背后一凉心想完了。。。。就在此时。。。。一根长剑刺来挡住了势有千钧的铁棒\"滚！\"妮可冷声呵斥挑开了魁梧的乔巴。\"团长！你怎么能抛下我们呢？\"杰克的声音从妮可背后传来，一脸笑嘻嘻。\"团长！你没事吧？\"紧随其后克拉桑迅速飞到路飞身旁，一脸担忧。\"还有我啊。。。\"气喘吁吁的弗兰奇看是长途跋涉累的不轻在远处抱怨。\"你们。。。。。\"看着前来支援的伙伴，路飞忽然不知道该说些什么。\"团长，那三个丑东西就交给我们了！\"妮可抢声道。\"哈哈哈哈啊哈哈哈哈哈哈\"又是那刺骨的尖笑\"多么让人羡慕的友谊啊~罗宾，乔巴，小盆，去把他们统统杀掉，我倒要看看你失去他们能够怎样~\"娜美一脸笑容。双方人马 一齐跃空打打打打打打打打打打打。。。。。。。（切至）4：火山口 外景 傍晚 或是因为 5V3，或是因为被路飞的决心感染，克拉桑的长鞭，妮可的利剑，杰克的暗器，弗兰奇的狼牙棒，他们大声呵斥，愈战愈勇。这边，罗宾，小盆，乔巴节节败退，眼看不支。咻~一道黑影向路飞冲来，路飞被击退十丈。娜美：你们玩的这么热闹，怎地要冷落姐姐吗？话音未落，残影夹着风声便冲向路飞。一个横扫，\"好重\"路飞双手勉强挡住心中暗道。又是一个反向横扫，\"砰\"\"砰\"\"砰\"。。。。路飞被击砸向大地。。。。。飘在上方睥睨着路飞，娜美冷笑道：\"怎么？看到你被我击倒他们怎么不来救你？\"路飞的离开让妮可他们与对方之战陷入胶着。娜美：看见没？离开你，他们连那三个蠢货都打不过！说完，娜美徐徐飘向瘫在地上的路飞，用她的高跟鞋踩着路飞的脸：\"在绝对的力量面前，你们的友谊是何等的不堪一击！\"一边说一边用脚跟揉搓着路飞的脸颊。\"丑东西都是这么多话的吗？！！\"就在此时，不知妮可如何抽身前来支援\"看剑！！\"娜美纵身一跃便躲开了娜美的绝妙一击：\"哦？这就是友情吗？有趣！看能坚持住我几脚！\"\"聒噪！\"妮可呵斥一声便向娜美冲了过去。失去妮可的克拉桑，杰克，弗兰奇的陷入苦战，被打的节节败退。与娜美对决的妮可显然被虐。天空传来娜美得意的嘲笑和妮可克拉桑的疲惫的喘息。OS：大家都这么努力。。。而我却无能为力，瘫在这里像个废物。。。。我还是团长吗？我算什么团长！！！！路飞的呼吸越来越粗越来越沉重，他的胸口在抖动，双臂青筋暴露，微微闭上双眼\"啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊啊 啊啊啊 啊啊啊\"以路飞为中心，一阵狂暴的气息炸了开来。。。所有人不由自主的转向路飞。只见路飞站立了起来，左手放在左腹，右手搁在右背，眼睛望着左下角。\"团长！\"\"团长！\"\"团长！\"\"团长！\"众人齐喊。咻~一拳击倒罗宾；咻~一拳击飞乔巴；咻~一拳击落小盆；路飞慢慢的转向娜美，咻~他来到娜美和妮可之间，背对着妮可：\"交给我，回去吧\"妮可冰冷的脸满是诧异。。。。咻~对着娜美左一拳\"这一拳是送给妮可的\"右一拳\"这一拳是送给克拉桑的的\"左一拳\"这一拳是送给杰克的\"右一拳\"这一拳是送给弗兰奇的\"对着娜美鼻梁又是一拳\"这是送给艾斯的！\"被击飞的娜美先是惊讶的看着进化的路飞而后轻啐一口：力量变强了呢！但你以为这就能够击败我吗？呼~娜美以肉眼难以分辨的速度攻向路飞，又是那一脚踢向路飞的左脸，路飞急速左臂挡住，右手又是一拳打中鼻梁！一记翻滚退出十丈，娇美的脸庞多了几分狼狈。。。。路飞：你输了！娜美擦了一下鼻血：这就是友谊唤醒的力量吗？！呵！多么可笑！言罢又是', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10019', '0', '海贼王：还在拖剧情！路飞哥哥变成路飞弟弟', '小编是海贼王13年的粉丝，当时上初中3年级的时候家里买了第一台电脑之后，把游戏挂上闲着无聊就开始看海贼王。第一次看海贼王是因为火影忍者看完了，也挺无聊的，所以随便找了动画片看，那个就是海贼王，可是越看越喜欢海贼王（当时火影忍者连载到中忍考试中三代火影抽大蛇丸的灵魂，剧情特别拖）。刚开始看的时候感觉画风和内容不如火影忍者，可是看到娜美哭着求路飞帮忙，路飞把草帽给娜美戴上的时候我居然感动的哭了。从此每个星期期待更新的不是火影忍者，而是海贼王。这个时候我是16岁，路飞比我大一岁17岁，我们俩年龄也相仿，所以我更加喜欢上了路飞。可是时间过的越久，海贼王的人气越来越旺，剧情也就开始越来越拖，最让我可恨的拖剧情是在司法岛篇。路飞一伙终于突破难关，与CP9成员对立上了，以为战斗终于开打了，结果......这时候我太天真了，以为下个星期就开打了，解决下个星期开始开始草帽团成员一个一个回忆，这一拖就是一个多月，中间好像还停播。从那之后一顿拖剧情，上哪都有拖剧情，时间就这样过了10年左右，路飞也从17岁涨到了19岁，我却从16岁青春少年变成了摸爬滚打闯遍江湖的25岁青年。路飞在新世界冒险现在一直都是打凯多的准备工作，可是我却又涨了3岁，已经28岁了。估计凯多打完我的孩子过周岁了（小编打算要个属猪的孩子）。我希望那天路飞对我们这些一直忍耐拖剧情的还米们说一句：哥，辛苦了！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10020', '0', '海贼王：最可惜的三颗恶魔果实，要是被草帽团吃了，早成海贼王了', '海贼王中的恶魔果实多种多样。有的强大无比，有的十分鸡肋。不过恶魔果实其实没有什么好坏之分，之所以会有这种感觉，就是因为他们的使用者实在是太菜了。直接将恶魔果实糟蹋了。如果这些恶魔果实给了草帽团任意一人，估计路飞早就当上海贼王了。1、绘画果实绘画果实能够将画出来的任何东西都变为现实。这颗恶魔果实简直是糟蹋啊！因为它的拥有者压根不会画画啊！想一想，如果这颗恶魔果实给了乌索普。那简直想要什么样的武器就有什么样的武器啊！2、透明果实透明果实可以让人隐身。本来拿来做个奇兵是很有效果的。可惜遇到个傻子阿布萨罗姆，完全没有将恶魔果实的能力发挥出来啊。如果给山治吃了话，本来山治是在空中作战，就是奇兵了，再加上透明果实，更容易出其不意了。3、迟缓果实迟缓果实能够将迟缓光线照射到的一切生物动作变迟缓。本来这颗果实能力其实是很强大的。但是，他的使用者银狐福克西压根没有好好利用它。如果娜美吃了这颗果实，完全可以从容不迫地布置好强大的雷云。到时候，估计能秒杀一大片的敌人。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10021', '0', '路飞性格特点', '蒙奇·D·路飞草帽海贼团船长。由于他的标志性特征是一顶草帽，因此常被直接称呼为草帽。梦想是找到传说中的ONE PIECE，成为海贼王。性格积极乐观，爱憎分明且十分重视伙伴，对任何危险的事物都超感兴趣。看似白痴，却是一个大智若愚型的无愧船长之职的人。和其他传统的海贼所不同的是，他并不会为了追求财富而无故杀戮，而是享受着身为海贼的冒险。1、积极乐观在“开始与结束的城市”罗格镇的断头台上差点被小丑巴基动用私刑斩首，在临死前却异于常人的露出开朗的笑容，笑着说了一句“抱歉，我要死了。”被时任海军上校“白色猎人”斯摩格解读为早已抱有必死的觉悟。2、单纯天真对新鲜事物尤其机械和新奇武器方面有强烈的好奇心。比如面对弗兰奇的功能展示，活骷髅，半人马和下身武士等等，他都会冒着星星眼大叫好酷，或者一直追着要求其“成为我的伙伴吧！”3、对动物特别喜爱路飞对各种动物的匪夷所思的交流术，和对各种独角仙情有独钟。4、爱憎分明且直率从不会隐藏自己的想法，一旦遇到性情较敏感的对象就会感到头疼，会直言讨厌，也非常排斥气量狭小的对象。在轻视自己的国民的生命的迷倒众生的汉库克面前，愤怒地说“你真的很讨厌”。5、是非分明面对炮轰了MR.8的船的罗宾给出的永久指针，在娜美暗喜，薇薇犹豫，众人疑惑的时候，路飞一把夺过指针，毫不犹豫地捏碎，怒吼“这艘船的航路，不用你来决定！”6、胸襟宽广曾经和MR.2，贝拉米，汉库克等人为敌，但现都能放下过去成为朋友，并把他们像对待伙伴一样重视。也为了大局，和曾经为敌的沙·克洛克达尔，巴基，MR.3等人并肩作战。面对一直在攻击他们致使山治昏迷的雪兔拉邦，见到小拉邦拼命保护亲人后，路飞毫不犹豫出手相助。7、超级自信对自己的实力很有自信，坚定地认为自己能成为海贼王，不为任何人的阻扰而改变梦想。也用着这股自信去鼓励，影响周围人。8、意志力顽强面对强敌沙·克洛克达尔，路飞被打败后又继续跑去挑战，百折不挠到克洛克达尔大吼为什么，而他只是回答“因为你没有把你夺走的东西（薇薇的国家）还回来。”面对强敌路奇，路飞以自伤八百的代价达到伤敌一千的目的打倒了他。9、注重团队路飞非常喜欢开宴会，第一次开宴会庆祝布鲁克加入的时候索隆休养不在，所以他要重新庆祝一次。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10022', '0', '海贼王路飞VS路飞，这样的情节上演了两次？对战影路飞&镜路飞', '海贼王路飞自己打自己的场景，着实是上演了两次！对战影子路飞，对战镜子路飞！能有这样的奇遇也算是路飞的运气了，知己知彼百战不殆，可惜明知道是自己，以路飞的智商，也找不到弱点啊！路飞VS路飞…自己对战自己的影子。在莫里亚的小岛上，因为其恶魔果实能力的关系，剥夺了路飞的影子制造了奥兹尸体+路飞战斗素养的怪物，唯独就是不能施展恶魔果实能力……没办法了，路飞自己对战自己，最终还是在众多影子的帮助下才能险胜这场战斗就是怪物路飞对战怪物路飞！布蕾的果实能力可以不断的克隆、复制在BIG MOM家里的诱惑森林，路飞对战路飞的戏码再次上演了，这次打的有点难舍难分，战局异常的胶着，主要是这次的模拟路飞太像了不知道是什么原因，模拟的路飞，连战斗力都是相同地如果是动物变化出来地，不应该实力一般么？这次在BIG MOM蛋糕岛遇上的这位能力者，其作用太多了镜世界应该是BIG MOM至关重要的情报、兵源输出的地方，对于整个海贼团来说都是相当重要地。可惜被路飞给攻破了…在镜世界，路飞等人也获取了很多的便利。这颗恶魔果实如果能够带走就好了，再也不用那么费力的走路了。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10023', '0', '《海贼王》热热果实能力者山寨路飞绝招机关枪，遭到路飞吐槽', '机关枪，可以说是路飞日常战斗中使用频率最高的绝招之一，特别是路飞掌握和二挡霸气后，机关枪更是兼具了力量，范围，速度，几乎在每场战斗中都会使用。但是路飞可能没有想到，自己的绝招会有一天被别人山寨，而且做得还有模有样，至于效果确遭到了路飞的吐槽。他就是旗帜猎人热热果实能力者阿奇诺（动画原创角色）。在《海贼王》第334集中，路飞与阿奇诺展开激战，初期的战斗中，路飞因为没有办法直接接触阿奇诺（阿奇诺能融化他触碰的东西）而处于下风，期间路飞也使用了机关枪。后来，路飞转换思路，利用冰来作为保护开始攻击到阿奇诺的本体，愤怒的阿奇诺继续提升身体热量，使用爆弹攻击路飞，但由于速度太慢，路飞轻松躲过。于是，阿奇诺说道将用下一招击败路飞，于是灼热机关枪现身，刚开始与路飞的机关枪及其相似，娜美都露出不可思议的表情。不过，这只是表象，灼热机关枪发出的爆弹速度依然没有变化，让路飞狂吐槽。此外，除了阿奇诺，其实也有其他人模仿路飞的绝招，如脑残粉巴托洛米奥和巨人奥兹，但他们都是获得了路飞的授权的（偶像的力量和路飞的影子），而阿奇偌直接盗版，当然没有正版的作用力，最后被路飞干掉。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10024', '0', '路飞路飞路飞', '基本资料路飞 —— Monkey D Luffy年龄：16岁生日：4月22日（世界地球日）星座：金牛座出生：东海-风车血型：A型身高：170cm所属：草帽海贼团-船长职称：船长能力：超人系拥有“橡胶果实”能力的橡胶人特征 ：戴草帽，左眼下方有一道疤痕。悬赏金：3000万贝里(可可亚西村事件)→1亿贝里(阿拉巴斯坦事件)→3亿贝里(司法岛事件)→4亿贝利（海军本部马琳福德事件）恶魔果实：超人系——橡胶果实　弱点：怕刀、海楼石、怕烫、不能游泳(恶魔果实能力者共通弱点)喜欢的食物：肉代表动物：猿猴惧怕的类型：爷爷身上的味道：肉的味道（根据乔巴的鼻子判断）代表颜色：红色代表数字：01、56（56日文和橡胶同音）梦想：成为海贼王得到上个海贼王葛鲁多·D·罗杰的密宝——One Piece、跟红发再度见面身世在东海某个海岛的风车村长大，和暂驻在渔村的大海贼红发香客斯感情很好，于是一心想成为海贼。眼下的疤痕就是为了显示自己够狠自己用刀划的。可是路飞7岁时糊里糊涂的吃了海贼们抢来的恶魔果实——橡皮果实（ゴムゴムの実），从此再也不会游泳了。这样还能成为海贼吗？路飞的答案是——只要不掉到海里就可以了。有一次，为了捍卫海盗的名誉，路飞和山贼顶上了，差点丧命，还好海贼们及时赶到。但是路飞被山贼头子扔到了海里，香客斯为了救他被凶猛的海兽咬掉了左臂。红发香客斯离开渔村再次出海前，把自己的草帽戴在了路飞头上。“将来你一定要还给我，当你成为出色海盗的时候！”十年后，路飞真的出海了。他的目标是——海贼王！而那顶草帽，也成了路飞最珍爱的宝物。性格草帽海贼团船长。草帽海贼团三主力之一，是吃橡胶果实能力者。橡胶人，身体具有绝佳的弹性与延展性，可以抵挡绝大多数的物理攻击，对雷电攻击具绝缘性。特征是头戴草帽，天性热情、善良，善恶分明，有时候脑子里会少一根筋，作出一些傻里傻气的事情，但在关键时刻是一个非常靠得住的人，并表现出超出常人的冷静和机智，属于那种遇强则强的类型。草帽海贼团全员都非常信任他，且他身上有种很容易就让人把他当作伙伴的魅力。对任何危险事物都超感兴趣，骗人布说“他得了准备好要冒险的病”。漫画中其角色设定为16岁， 170cm高，食量非常大，一天以5餐来计算，并极度爱吃肉。出生日为4月22日（世界地球日），金牛座。其口头禅为：“我是要成为海贼王的男人！”传说中的家谱 姓名：蒙其.D.格普　地位：海军本部中将　关系：路飞的爷爷资料：曾经数次追赶海贼王（罗杰）的海军英雄实力: 爱的铁拳姓名：蒙其.D.多拉格　　地位：革命家　　　　　关系：路飞的爸爸　　资料：世界最恶劣的犯罪者（连海军本部和四皇也不敢招惹的人）实力：天气果实姓名：波特卡斯.D.艾斯地位：白胡子海贼团第二队队长关系：路飞的哥哥　　资料：赫赫有名的白胡子海贼团第二队队长实力：烧烧果实草帽海贼团职务：草帽海贼团船长全员总赏金：六亿六千七百万零五十贝利船长 路飞剑士 罗罗亚.索隆厨师 山治狙击手 乌索普航海士 娜美医生 乔巴考古学家 妮可.罗宾修船工 弗兰奇音乐家 布鲁克永远的伙伴 梅丽号 比比(薇薇)公主 阿碧丝 盆克雷（MR.2)报告1： 橡皮果实的能力者---可全身自由自在伸缩。报告2：惊人的战斗能力现在他的强是东海的NO.1﹗他在短时间里把在东海里奖金超过1千万贝里的3位海贼击倒。虽然东海是4个海域中最弱的，但他打倒锯齿阿龙，实力可说是达到顶级。史莫卡大佐的报告据我所看，他不是深思熟虑的类型，所以预测他下次的行动很困难。就算企图捕捉他，他也会引起突发事件而逃走。由于他有接受死的心理准备，所以不要瞧不起他比较好。服装幼年：上半身穿着写有ANCHOR和画有船锚的白色短袖衣服，再加上蓝色的短裤。东海篇：红色背心加上蓝色的短裤，是路飞的招牌服装。磁鼓岛：因为是雪国，所以穿着是红色大外套，后来因为搞丢了而借娜美的。阿拉巴斯坦篇：香吉士买的当地民服。加亚岛海域：遇到从天空掉下来的船，把船上捡到的的盔甲穿在身上，结果被娜美打碎。空岛篇：红背心的钮扣为敞开状态，后来被大蛇的胃酸腐蚀的破烂不堪，套在手上的护腕是跟乌索普借的。长环长岛-Davy Back Fight 之最终一对一头目战：头上顶着爆炸发，上半身打赤膊，穿着红色短裤，胸口贴有绿色火焰骷髅图案。司法岛：红色背心加上黑色有口袋的短裤。(对路飞来说口袋很小，因为放不下更多的肉)水都篇后段：印有Galley-La公司图案的白色无袖汗衫。幽灵岛篇：橙色背心加上红色的短裤，后来把摆在豪格巴克的宅邸金色盔甲穿在身上。香波地群岛篇：身着蓝色背心，将在鬼魂岛得到的“玻璃护腕”戴在手臂上。女儿岛篇/海底监狱推进城篇：原是穿蓝色背心，但被烧破。后来就穿着女儿岛给的湖水绿背心加红色的短裤。到了海底监狱推进城就把玻璃护腕给了小丑巴基。 六式之剃连续踢地面数十次产生强力反弹力来做高速移动。恶梦 路飞在鬼魂船上一战中，路飞的身体被翻滚海贼团塞进一百个影子，变成体型较正常的大，手臂变得粗大，身体也变成蓝色，拥有超强的攻击力和防御力，背上一把日本武士刀，一般人也难以承受，因为需要坚强的意志力，而且时限只有十分钟，时限一过，影子便会脱离身体，能力也会随之消失。路飞便是以此状态重创魔人 欧斯及王下七武海之一的 月光·摩利亚。一档 橡胶路飞是超人系“橡胶果实”能力者，全身拥有橡胶体质，包含内脏、血管，可以任意伸缩，不怕子弹只怕尖锐的武器如刀剑，除此之外身体也不导电，因此雷电的招式对他无效。橡胶橡胶之枪、橡胶橡胶之枪弹、橡胶橡胶之双重枪、橡胶橡胶之矛、橡胶橡胶之戳子、橡胶橡胶之图章、橡胶橡胶之鞭、橡胶橡胶之扫把脚、橡胶橡胶之爆、橡胶橡胶之缆、橡胶橡胶之钟、橡胶橡胶之钟摆、橡胶橡胶之导弹、橡胶橡胶之插花、橡胶橡胶之勾拳、橡胶橡胶之我踢、橡胶橡胶之火山、橡胶橡胶之剑山、橡胶橡胶之动力、橡胶橡胶之发呆、橡胶橡胶之造访、橡胶橡胶之风扇、橡胶橡胶之陀螺、橡胶橡胶之盾牌、橡胶橡胶之鱼叉、橡胶橡胶之鱼网、橡胶橡胶之圆锯、橡胶橡胶之螺旋摔、橡胶橡胶之气球肚、橡胶橡胶之镰刀、橡胶橡胶之大镰刀、橡胶橡胶之十字弓、橡胶橡胶之锤、橡胶橡胶之大锤、橡胶橡胶之烟火、橡胶橡胶之章鱼、橡胶橡胶之章鱼烟火、橡胶橡胶之烟火黄金牡丹、橡胶橡胶之螺旋弹、橡胶橡胶之黄金螺旋弹、橡胶橡胶之无敌拳、橡胶橡胶之水水枪、橡', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10025', '0', '《海贼王》中四大神预言！草帽海贼团将有一人死亡一人离开？', '【作者：卡密动漫】《海贼王》最近两话的漫画可以说是亮点不断，这里我们先不谈最新的漫画剧情，我们先来说说网上流传的《海贼王》中的五大神预言，我想再过几年，这些都会成为现实！寓言一：关于草帽海贼团新船员草帽海贼团的船员果实其实是有规律的：路飞的橡胶果实日语谐音是：5656。罗宾的花花果实，日语谐音是：8787。布鲁克的黄泉果实，日语谐音是：4343。乔巴的人人果实，日语谐音是110110。也就是说1到10个数字，还缺2和9，我猜测必然缺一个日语谐音为2929的果实能力。而这些数字加起来总和为325，325又和他们的船万里阳光号相同。而2929谐音正是昔日的七武海熊的肉球果实，所以熊这个角色很有可能会正是上草帽海贼团的船。成为路飞的伙伴，而这次熊沦为天龙人的奴隶很可能是一个契机。寓言二：百兽凯多的果实能力凯多的果实能力很有可能是动物系圣兽种·八岐大蛇果实，理由如下：凯多的造型非常像日本传说中的鬼王——酒吞童子，而酒吞童子正是传说中八岐大蛇的私生子。八岐大蛇的能力为水灾，而凯多也并不怕水，所有动物系恶魔果实中只有八岐大蛇符合这个设定。而从四皇的分配来看，急需一个动物系的来撑撑场面。寓言三：one piece是什么？从尾田一贯的习惯来看，关于one piece尾田一定已经埋下伏笔，很有可能就和草帽海贼团中各个船员的梦想有关，我大胆猜测，大秘宝就是让红土大陆沉没。理由如下：路飞的梦想是one piece，红土大陆沉默后世界融为一体，即为one piece，而山治的梦想all blue则更好理解，世界上的鱼儿游到了一起不正是山治所梦想的all blue吗？布鲁克也可以如愿见到红土大陆另一边的拉布。草帽团众人的梦想都可以实现。寓言四：外国大神预测最后说一个国外大神的预测，该大神曾经准确预测过火影的后续剧情，还是可以参考的。草帽海贼团的新船员会有两人，一男一女。草帽海贼团将会有一人永久离开，一人死于海军手下。草帽团中除了山治和罗宾还有三人隐藏着惊人的秘密。索隆在半年后将会有一个儿子，而路飞终生都不会结婚。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10026', '0', '路飞生日快乐！', '路飞生日快乐！你会成为海贼王的！来自 海贼头条@草帽红衣蓝裤裤', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10027', '0', '路飞与“路飞僵尸”奥兹的不同！', '奥兹拥有强大的身体，拥有路飞的影子，说实话这样的敌人真的是非常可怕，但是，奥兹根本比不上路飞，因为他只是月光莫利亚的产物与仆人，他没有属于自己的灵魂，他没有属于自己的梦想，也没有属于自己的思想，即便是在强大，也只能听从莫利亚的，可悲！路飞的强是因为他有要成为海贼王的梦想！他有要用生命去守护的伙伴！而这是奥兹没有的！奥兹一睁眼说的第一句话就是“肉”！我饿了，我要吃肉！这分明就是路飞嘛，奥兹的任性，好奇，贪玩，简直就是路飞的翻版，说实话，要不是，因为莫利亚的关系，小编还是非常喜欢这个奥兹的，可惜啊，真正的海贼王只有一个！那就是路飞！对利亚的僵尸军团来说，影子代表僵尸的战斗力，而尸体代表硬件条件，就是说，要想新做的僵尸强大，就必须要影子的原主任战斗力强大而且身体本身身体素质的强大，为了打造这样强大的士兵，莫利亚准备好了魔人奥兹的身体！奥兹的身体素质那可强大到哪里去了，光是他的块头就比普通的巨人族还大两倍，身体已经准备好了，就差拥有强大战斗力的影子，此时路飞出现了，这正是不二人选，通过一系列手法，路飞的影子到手！莫利亚最强的士兵，沉睡了500年的奥兹苏醒，路飞僵尸诞生了！在海贼王，三角贵帆船篇中，出现大boss是王下七武海月光莫利亚，他所拥有恶魔果实能力可以控制人的影子，可以把某个人的影子减下来，然后装进一具身体里，此时，原本不能自由行动的影子和原本没有生命力的尸体就能在莫利亚的操纵下自由的行动了，通过这样的方法，莫利亚打算靠着自己的恶魔果实能力，和被称为天才医生的霍古巴克的人体改造能力，打造出一个属于他自己的僵尸军团，这样的僵尸军团不怕痛，无论被打倒多少次，依旧还能够再站起来战斗，可想而知这有多么的可怕，而莫利亚正式想通过这种方式，来成为海贼王！所以，不论是否有主角光环，奥兹永远输给了路飞！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10028', '0', '来一下宝宝版路飞——山治，路飞饿了，乱吃毒蘑菇', '路飞好像很容易饥不择食⊙﹏⊙不过很可爱，不过我密集物恐惧症犯了……', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10029', '0', '海贼王：索隆的四位救命恩人，第二个被称为神，最差的也赏金过亿', '《海贼王》草帽海贼团的二把手索隆是个实力强大的剑士，目标是打败鹰眼成为世界第一大剑豪，不过即使是强大的索隆也有命悬一线的时候，下面我们来看一下索隆的四位救命恩人都有哪些厉害的人物吧。蒙奇.D.路飞作为主角的路飞和索隆的渊源自然是不用多说的，路飞出海找到的一个伙伴就是索隆。那时候索隆还是一个赏金猎人，因为打抱不平惹怒了当时是海军上校蒙卡的儿子贝鲁梅伯，贝鲁梅伯以威胁平民的生命为条件而抓捕了索隆。原本承诺只要索隆一个月不吃不喝就放过他，但是贝鲁梅伯的真正目的却是要杀死索隆。最后在路飞的帮助下打败了蒙卡他们，活了下来，如果不是路飞的出现，索隆早就被贝鲁梅伯弄死了，不过贝鲁梅伯现在也是一个正义的海军了。乌索普顺境靠索隆，逆境靠路飞，绝境靠乌索普，这句话不是没有道理的。虽然乌索普是草帽海贼船上的狙击手，但是他总是在关紧时刻发挥至关重要的作用。在路飞他们进入阿拉巴斯坦王国之前遭到了Mr.3他们的攻击，而索隆，娜美和微微差点都要变成了蜡烛雕像，在索隆准备自断双腿而和敌人以死相搏的时候，乌索普和路飞及时赶到救了大家。还有索隆面对海军锈锈果实能力者修恩无计可施的时候，也是乌索普一记火鸟星打败了修恩，让索隆摆脱了困境。最后在德雷斯罗萨王国，因为乌索普干掉了明哥家族成员砂糖，而拯救了十年间变成玩具的人类，作为他们救命恩人的乌索普也被称为了“GOD”，就连悬赏令上也是称他为神。巴索罗缪.熊身为七武海的大熊在最新一话也登场了，但是大熊现在的遭遇实在是让我们痛心不已。他被贝加庞克改造成没有意识的人之后，原本认为他只会像海军那样对世界政府效力，却没想到世界政府还让他变成了天龙人的奴隶。而大熊是索隆生命中一个重要的人物，在打莫利亚的时候，大熊被命令前去铲除草帽一伙，但是索隆和大熊结识，放过了草帽一伙。在香地波群岛的时候，大熊又从海军大将黄猿的手上救下了草帽一伙所有人，将他们送到了各个地方去修炼，在意识被改造之后还守护了桑尼号，这对草帽一伙来说是多么大的大恩大德啊。乔艾莉.波妮索隆和波妮都是被称为最恶的时代的海贼，波妮本身是吃了年龄果实的能力者，可以随意的让自己变成小时候的样子或者老了的时候的样子，这个能力还可以对他人使用，悬赏金两年前是1亿4000万贝利，算是比较低的悬赏金了。在最新一话，波妮侵入了玛丽乔亚，她的目的又是什么呢？在香地波群岛的时候，波妮就救过索隆，那时候索隆根本不知道顶撞天龙人的后果是什么，因为天龙人的嚣张跋扈，索隆对天龙人显露了杀机，正准备对天龙人动手的时候被变成了小时候模样的波妮救了下来。要不然索隆伤害了天龙人，是难逃一死的。当然还有很多对索隆有恩情的人们，比如幽灵公主佩罗娜帮受伤的索隆疗伤，鹰眼传授索隆剑术等，希望索隆能够越来越强大，实现自己的伟大目标。更多精彩内容，请关注ACG虫洞。（原创不易，侵权必究。）', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10030', '0', '海贼王：索隆最屈辱的三次战斗，一次不穿裤子，一次给人磕头', '动漫《海贼王》中索隆在东海与鹰眼的决斗之后便发誓再也不会输，确实索隆在剑术的比拼上没有输过，遇到的强敌也都成为了他实现野心的垫脚石。如果排除剑术的战斗说的话，索隆就输过了。比如败暴君熊打败，被黄猿差点杀死，被泰佐洛抓住当人质等等，这些战斗对于索隆来说输也输的情有可原。但下面要说的这三场战斗对于索隆来说就有点屈辱了。第一次：被巴基捅了后背后背有伤是剑士的耻辱，这对于每个剑士来说都是耻辱。而在出次遇到巴基时，索隆因为不知道巴基的果实能力，本来以为已经将他的一刀两断了就放松警惕了，结果才被巴基给捅了后背留下了耻辱的伤。第二次：被佩罗娜玩坏佩罗娜是幽灵果实能力者，在她的能力面前还没人不下跪，索隆就深深的体会到了这一点。恐怖三帆船时索隆等人首次遇到佩罗娜的消极幽灵就被迫下了跪，再次见到她时索隆还没拔刀见又跪下了。再有就是在鹰眼的岛屿时，索隆直接又被佩罗娜当成了玩偶，在佩罗娜的能力面前索隆只能乖乖的磕头。第三次：见到断腿青雉时z的剧场版中路飞，索隆等人见到青雉的出现确实让他们感到了恐慌，而且当时还是在浴室中。青雉一抬头吓的他们连裤子来不及穿想跟青雉战斗。虽然青雉没有战斗的意思，但真斗起来呢？难道还给他穿裤子的机会么？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10031', '0', '海贼王中那些值得敬佩的人，索隆甘愿为路飞放弃生命', '海贼王之所以能成为世纪经典巨作，与其非常细腻的人物塑造是分不开的。在海贼王中，无论是正派反派还是配角小喽啰，尾田都是从不吝惜笔墨的，为的就是将他们刻画得更有模有样，更完整把人物性格表现出来，借此来感染大家。那么，今天就来跟大家说说尾田笔下最值得敬佩的几个角色吧!希鲁鲁克作为医生，他虽没有非常厉害的医术，却用自己的精神在感化磁鼓国的人们。他其实很像鲁迅先生，都是医生，都在救国，从某种意义上都成功了，但希鲁鲁克却被很多人遗忘。这里不得不提一下，希鲁鲁克那一句名言：人究竟什么时候会死?不是心脏被枪打中的，不是得到不治之症后，也不是喝了剧毒蘑菇汤之后，而是被世人遗忘的时候。希鲁鲁克虽然微不足道，但却值得我们敬佩。克洛克达尔克洛克达尔是为数不多的、比较讨人喜欢的反派。其实在我开始认识老沙的时候，对他并没有太多好感。他生性残忍，也擅于算计，对于任何没有利用价值的人都会毫不犹豫地干掉。可是在顶上战争中，老沙的表现简直圈粉无数，一方面各种怒怼四皇、海军大将、七武海，证明了他实力的强大，另一方面说出来了好些称得上至理的名言，实在令人敬佩，比如最经典的想要守护的人就要好好保护好。红发香克斯香克斯是驰骋新世界的四皇，有着绝对强大的实力。在他断臂之后会面白胡子，白胡子都不相信，他这样的角色竟然会在东海这个地方丢了一只手臂。当然，香克斯的手臂丢得也确实很蹊跷。但考虑到香克斯是为了激励路飞，那就没多少好质疑的。愿意为了路飞丢了一个手臂的人，绝对值得我们敬佩。冯·克雷说起海贼王最讲义气的人，小冯绝对榜上有名。第一次小冯为了帮助草帽团逃走，惨遭逮捕被关进了推进城;第二次小冯带着路飞求医，在LV5雪地与狼群搏斗;第三次小冯为了帮助路飞等人逃离监狱，不惜牺牲自己打开正义之门。回看小冯的三次义举，他正是这样一个愿意为了朋友伙伴，付出自由乃至生命的人，值得我们敬佩。艾斯在顶上战争中，艾斯本可以顺利逃走，但却因为看不过赤犬羞辱自己的“老爹”，不惜与之硬刚，惨遭一顿暴打;本可以挡下赤犬的攻击，伤不至死，但却为了路飞的安全，不惜用自己的血肉之躯去对抗岩浆，最终身体被击穿而亡。艾斯已经好好地守护了他想要守护的东西，非常值得我们敬佩。白胡子白胡子是海贼王世界中最强的男人，有着极为宽广的胸襟。虽然没有称霸伟大航道，但却让很多形影孤单的海贼有了一个温和的家;即便被家人背叛，也毅然地选择了原谅;为了自己的孩子们，不惜牺牲自己。在顶上战争中，白胡子总计承受了刀伤267处、子弹152发、炮弹46枚，即便是死亡，也依然仍保持着杀敌的姿态;在他的整个海贼生涯里，背上没有一处因为逃跑而留下的伤痕，真的非常值得我们敬佩。索隆索隆是草帽团的副船长，他本身颇为男子汉硬派的作风及坚毅的武士道精神，就已经吸引了无数粉丝。在恐怖三桅帆船篇中，索隆面对实力比自己强大数倍的大熊时毫不畏惧，虽然败下阵来，但不可否认他的英勇;最终索隆更是为了路飞，甘愿放弃自己的梦想和生命;这样一个宁愿牺牲自己，守护别人的索隆，正是最值得我们敬佩的人。个人觉得修炼两年之后的索隆更加的帅气，能够征服更多的海米，并且觉得索隆的实力已经达到了很高的程度，期待着他在和之国篇的精彩表现。部分来自海贼王中文网', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10032', '0', '索隆打死都不愿承认的五件事！每一件都是黑历史', '点击上方的「动漫迷」↑ 关注我在《海贼王》中，索隆一直都是硬派担当，这个帅气的男人身上带着剑豪的硬气和豪情万丈，时不时就能帅我们一脸。不过，和草帽团的伙伴们一路走来，索大自然也有很多“不堪回首”的“黑历史”，，而且，对于男子汉包袱那么重的索大而言，有些小秘密是他致死都不愿意承认的。今天，路七就冒着被索大砍的危险，带着大家探秘索大那些不为人知的“黑历史”~NO.5 司法岛当奶爸在司法岛，索隆前去兵器店看刀时被两个小鬼缠着，之后小孩的妈妈和一群小孩就出现了，索大变升级成了保姆，穿着滑稽的短上衣，给婴儿喂奶，甚至还被尿了一身。脸皮薄的索大自然不愿意被同伴们看到自己的这副德行，但偏偏生不逢时，带着孩子的奶爸索隆在大街上每走一步都会偶遇一位熟人，可怜的奶爸只能拼命躲藏……但最后还是被腹黑的罗宾看到了，罗宾嘴上说着“很般配”，但眼神却完全不是这个意思啊！NO.4 身扛着达斯琪一直以来，钢铁直男索隆对待女孩子的态度基本上是“敬而远之”，而且，索大硬气的外表下其实藏了一颗羞涩的心~达斯琪在与雪女莫奈的战斗中，肩部不幸被咬伤，面对即将袭来的毒气，索隆索性把她扛在肩上跑了起来……当然，逃跑的过程中索大也光荣的迷路了……NO.3 被佩罗娜包成宠物佩罗娜可以说是最让索隆无奈的存在~索隆被暴君熊拍飞以后落在了一个岛上，结果被佩罗娜所救，佩罗娜因为寂寞，直接把索隆包成了她宠物的模样，同时佩罗娜的消极精灵更是让索隆吃了不少苦……NO.2 卡在烟囱里在水之都，路飞一伙人跟cp9打斗时，变身的路奇接连击败了路飞和索隆，还把他们丢到外面，路飞卡在两栋楼之间，索隆则卡在了烟囱中，只留下腿在外面，画面实在太美好……这丢脸的一幕还被乔巴目击，最后索隆还是在乔巴的帮助下从烟囱中逃脱……事后乔巴跟娜美和路飞说起这件事时差点被索隆灭口。NO.1 初吻给了山治要说索隆打死都不愿意承认的事情，这个绝对是NO.1，这件事大概也是索隆和山治这对相爱相杀的CP之间永远的秘密了~索大正在提刀赶来的路上！路七先跑为敬啦~你们还知道索大的哪些黑历史快来爆料啊!', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10033', '0', '海贼王，索隆的5次下跪，一个让索隆轻生，一个让索隆给葬了！', '海贼王，索隆是个铁骨铮铮的硬汉，拥有不输路飞的人格魅力，强大的背后又不失无厘头的搞笑，这一点博得了很多漫迷的喜爱，也因此将索隆称为“索大”。索隆无论是多么强悍的人，也总会有柔情的一面，就像我们的索隆，面对女士从来都是手下留情，面对敌人该了结时了结，该尊重时尊重。虽然索大一直都很完美，但在完美的背后也总会有脆弱或者悲伤的时候。比如一直贯彻“武士道”的他，曾有过5次双膝跪地。第一次，好友古伊娜的逝去古伊娜和索隆从小就是青梅竹马，互相练剑，偶然的意外让古伊娜丢了性命。当然此时的索隆并没有因此而变得消极，反而是越发的要变强，要让自己的名号响彻天空，让在天堂的古伊娜可以听见。索隆长大后的索隆来到古伊娜的墓地，给古伊娜跪下来了。第二次，不惜送命救路飞在莫利亚篇时，路飞承受了太多的疲劳和伤害。本以为打败莫利亚之后就是完结了，可不曾想到暴君熊的出现要取走路飞的脑袋。而此时的索隆拖着疲惫的身躯不敌暴君熊后，要用自己的脑袋救下路飞的性命。索隆索隆为了朋友的性命而跪。第三次，受到了幽灵果实能力的影响幽灵果实的能力可以让人变的消极，无论是多强的人在他能力面前一样都会变的消极。索隆也难道这个能力的影响。消极的索隆，让他觉得不该出生在这个世界上。索隆索隆因轻生而跪地。第四次，为达目的而跪香波地群岛时，暴君熊从黄猿手里救下了草帽一伙人。把索隆拍到了鹰眼所在的岛，在路飞下通知两年后再见后。索隆为变强，为了向世界第一大剑豪在进一步。他放下了尊严，向鹰眼跪地磕头拜师。索隆第五次，佩刀雪走的死索隆索隆是一名剑客，剑客都对自己的刀有很深的感情，甚至可以说他们已经把刀当做了与自己一直并肩作战的战友。雪走也不例外，雪走被锈锈果实能力毁掉后，因无法再修复。索隆就亲手帮它给葬了，还给了对待死者一样的礼节。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10034', '0', '海贼王——索隆手机壁纸。', '​​​​​ ', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10035', '0', '海贼王索隆帅气语录', '我不管这个世上的人怎么说我，我只想依照我的信念做事，绝不后悔，不管现在将来都一样！如果一直想着麻烦的事，只会更麻烦。苦难算什么，我本来，就喜欢走上阿修罗之路。受尽苦难而不厌，此乃修罗之道。请把那把刀给我吧！我要会连她的份也一起努力的，我要成为世界第一的大剑客，我要让我的名字，响彻天堂！既然已经决定做一件事，那么除了当初决定做这件事的我之外，没人可以叫我傻瓜。倘若出生于东海的我是无名小卒的话，那么被我砍倒的你到底又是哪根地上的葱呢。那真可惜了，我从来就没有向神祈祷过。我根本不相信有神，更不会尊敬他如果在这里退缩了一步，那么过去那些重要的誓言、约定，就全部会消失不见，而我再也无法回到这里了。背部受伤是身为剑客的耻辱。喜欢就关注我：wumuwj', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529206441', '0', null), ('10036', '0', '海贼王：索隆的四位救命恩人，第二个被称为神，最差的也赏金过亿', '《海贼王》草帽海贼团的二把手索隆是个实力强大的剑士，目标是打败鹰眼成为世界第一大剑豪，不过即使是强大的索隆也有命悬一线的时候，下面我们来看一下索隆的四位救命恩人都有哪些厉害的人物吧。蒙奇.D.路飞作为主角的路飞和索隆的渊源自然是不用多说的，路飞出海找到的一个伙伴就是索隆。那时候索隆还是一个赏金猎人，因为打抱不平惹怒了当时是海军上校蒙卡的儿子贝鲁梅伯，贝鲁梅伯以威胁平民的生命为条件而抓捕了索隆。原本承诺只要索隆一个月不吃不喝就放过他，但是贝鲁梅伯的真正目的却是要杀死索隆。最后在路飞的帮助下打败了蒙卡他们，活了下来，如果不是路飞的出现，索隆早就被贝鲁梅伯弄死了，不过贝鲁梅伯现在也是一个正义的海军了。乌索普顺境靠索隆，逆境靠路飞，绝境靠乌索普，这句话不是没有道理的。虽然乌索普是草帽海贼船上的狙击手，但是他总是在关紧时刻发挥至关重要的作用。在路飞他们进入阿拉巴斯坦王国之前遭到了Mr.3他们的攻击，而索隆，娜美和微微差点都要变成了蜡烛雕像，在索隆准备自断双腿而和敌人以死相搏的时候，乌索普和路飞及时赶到救了大家。还有索隆面对海军锈锈果实能力者修恩无计可施的时候，也是乌索普一记火鸟星打败了修恩，让索隆摆脱了困境。最后在德雷斯罗萨王国，因为乌索普干掉了明哥家族成员砂糖，而拯救了十年间变成玩具的人类，作为他们救命恩人的乌索普也被称为了“GOD”，就连悬赏令上也是称他为神。巴索罗缪.熊身为七武海的大熊在最新一话也登场了，但是大熊现在的遭遇实在是让我们痛心不已。他被贝加庞克改造成没有意识的人之后，原本认为他只会像海军那样对世界政府效力，却没想到世界政府还让他变成了天龙人的奴隶。而大熊是索隆生命中一个重要的人物，在打莫利亚的时候，大熊被命令前去铲除草帽一伙，但是索隆和大熊结识，放过了草帽一伙。在香地波群岛的时候，大熊又从海军大将黄猿的手上救下了草帽一伙所有人，将他们送到了各个地方去修炼，在意识被改造之后还守护了桑尼号，这对草帽一伙来说是多么大的大恩大德啊。乔艾莉.波妮索隆和波妮都是被称为最恶的时代的海贼，波妮本身是吃了年龄果实的能力者，可以随意的让自己变成小时候的样子或者老了的时候的样子，这个能力还可以对他人使用，悬赏金两年前是1亿4000万贝利，算是比较低的悬赏金了。在最新一话，波妮侵入了玛丽乔亚，她的目的又是什么呢？在香地波群岛的时候，波妮就救过索隆，那时候索隆根本不知道顶撞天龙人的后果是什么，因为天龙人的嚣张跋扈，索隆对天龙人显露了杀机，正准备对天龙人动手的时候被变成了小时候模样的波妮救了下来。要不然索隆伤害了天龙人，是难逃一死的。当然还有很多对索隆有恩情的人们，比如幽灵公主佩罗娜帮受伤的索隆疗伤，鹰眼传授索隆剑术等，希望索隆能够越来越强大，实现自己的伟大目标。更多精彩内容，请关注ACG虫洞。（原创不易，侵权必究。）', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10037', '0', '海贼王：索隆最屈辱的三次战斗，一次不穿裤子，一次给人磕头', '动漫《海贼王》中索隆在东海与鹰眼的决斗之后便发誓再也不会输，确实索隆在剑术的比拼上没有输过，遇到的强敌也都成为了他实现野心的垫脚石。如果排除剑术的战斗说的话，索隆就输过了。比如败暴君熊打败，被黄猿差点杀死，被泰佐洛抓住当人质等等，这些战斗对于索隆来说输也输的情有可原。但下面要说的这三场战斗对于索隆来说就有点屈辱了。第一次：被巴基捅了后背后背有伤是剑士的耻辱，这对于每个剑士来说都是耻辱。而在出次遇到巴基时，索隆因为不知道巴基的果实能力，本来以为已经将他的一刀两断了就放松警惕了，结果才被巴基给捅了后背留下了耻辱的伤。第二次：被佩罗娜玩坏佩罗娜是幽灵果实能力者，在她的能力面前还没人不下跪，索隆就深深的体会到了这一点。恐怖三帆船时索隆等人首次遇到佩罗娜的消极幽灵就被迫下了跪，再次见到她时索隆还没拔刀见又跪下了。再有就是在鹰眼的岛屿时，索隆直接又被佩罗娜当成了玩偶，在佩罗娜的能力面前索隆只能乖乖的磕头。第三次：见到断腿青雉时z的剧场版中路飞，索隆等人见到青雉的出现确实让他们感到了恐慌，而且当时还是在浴室中。青雉一抬头吓的他们连裤子来不及穿想跟青雉战斗。虽然青雉没有战斗的意思，但真斗起来呢？难道还给他穿裤子的机会么？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10038', '0', '海贼王中那些值得敬佩的人，索隆甘愿为路飞放弃生命', '海贼王之所以能成为世纪经典巨作，与其非常细腻的人物塑造是分不开的。在海贼王中，无论是正派反派还是配角小喽啰，尾田都是从不吝惜笔墨的，为的就是将他们刻画得更有模有样，更完整把人物性格表现出来，借此来感染大家。那么，今天就来跟大家说说尾田笔下最值得敬佩的几个角色吧!希鲁鲁克作为医生，他虽没有非常厉害的医术，却用自己的精神在感化磁鼓国的人们。他其实很像鲁迅先生，都是医生，都在救国，从某种意义上都成功了，但希鲁鲁克却被很多人遗忘。这里不得不提一下，希鲁鲁克那一句名言：人究竟什么时候会死?不是心脏被枪打中的，不是得到不治之症后，也不是喝了剧毒蘑菇汤之后，而是被世人遗忘的时候。希鲁鲁克虽然微不足道，但却值得我们敬佩。克洛克达尔克洛克达尔是为数不多的、比较讨人喜欢的反派。其实在我开始认识老沙的时候，对他并没有太多好感。他生性残忍，也擅于算计，对于任何没有利用价值的人都会毫不犹豫地干掉。可是在顶上战争中，老沙的表现简直圈粉无数，一方面各种怒怼四皇、海军大将、七武海，证明了他实力的强大，另一方面说出来了好些称得上至理的名言，实在令人敬佩，比如最经典的想要守护的人就要好好保护好。红发香克斯香克斯是驰骋新世界的四皇，有着绝对强大的实力。在他断臂之后会面白胡子，白胡子都不相信，他这样的角色竟然会在东海这个地方丢了一只手臂。当然，香克斯的手臂丢得也确实很蹊跷。但考虑到香克斯是为了激励路飞，那就没多少好质疑的。愿意为了路飞丢了一个手臂的人，绝对值得我们敬佩。冯·克雷说起海贼王最讲义气的人，小冯绝对榜上有名。第一次小冯为了帮助草帽团逃走，惨遭逮捕被关进了推进城;第二次小冯带着路飞求医，在LV5雪地与狼群搏斗;第三次小冯为了帮助路飞等人逃离监狱，不惜牺牲自己打开正义之门。回看小冯的三次义举，他正是这样一个愿意为了朋友伙伴，付出自由乃至生命的人，值得我们敬佩。艾斯在顶上战争中，艾斯本可以顺利逃走，但却因为看不过赤犬羞辱自己的“老爹”，不惜与之硬刚，惨遭一顿暴打;本可以挡下赤犬的攻击，伤不至死，但却为了路飞的安全，不惜用自己的血肉之躯去对抗岩浆，最终身体被击穿而亡。艾斯已经好好地守护了他想要守护的东西，非常值得我们敬佩。白胡子白胡子是海贼王世界中最强的男人，有着极为宽广的胸襟。虽然没有称霸伟大航道，但却让很多形影孤单的海贼有了一个温和的家;即便被家人背叛，也毅然地选择了原谅;为了自己的孩子们，不惜牺牲自己。在顶上战争中，白胡子总计承受了刀伤267处、子弹152发、炮弹46枚，即便是死亡，也依然仍保持着杀敌的姿态;在他的整个海贼生涯里，背上没有一处因为逃跑而留下的伤痕，真的非常值得我们敬佩。索隆索隆是草帽团的副船长，他本身颇为男子汉硬派的作风及坚毅的武士道精神，就已经吸引了无数粉丝。在恐怖三桅帆船篇中，索隆面对实力比自己强大数倍的大熊时毫不畏惧，虽然败下阵来，但不可否认他的英勇;最终索隆更是为了路飞，甘愿放弃自己的梦想和生命;这样一个宁愿牺牲自己，守护别人的索隆，正是最值得我们敬佩的人。个人觉得修炼两年之后的索隆更加的帅气，能够征服更多的海米，并且觉得索隆的实力已经达到了很高的程度，期待着他在和之国篇的精彩表现。部分来自海贼王中文网', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10039', '0', '索隆打死都不愿承认的五件事！每一件都是黑历史', '点击上方的「动漫迷」↑ 关注我在《海贼王》中，索隆一直都是硬派担当，这个帅气的男人身上带着剑豪的硬气和豪情万丈，时不时就能帅我们一脸。不过，和草帽团的伙伴们一路走来，索大自然也有很多“不堪回首”的“黑历史”，，而且，对于男子汉包袱那么重的索大而言，有些小秘密是他致死都不愿意承认的。今天，路七就冒着被索大砍的危险，带着大家探秘索大那些不为人知的“黑历史”~NO.5 司法岛当奶爸在司法岛，索隆前去兵器店看刀时被两个小鬼缠着，之后小孩的妈妈和一群小孩就出现了，索大变升级成了保姆，穿着滑稽的短上衣，给婴儿喂奶，甚至还被尿了一身。脸皮薄的索大自然不愿意被同伴们看到自己的这副德行，但偏偏生不逢时，带着孩子的奶爸索隆在大街上每走一步都会偶遇一位熟人，可怜的奶爸只能拼命躲藏……但最后还是被腹黑的罗宾看到了，罗宾嘴上说着“很般配”，但眼神却完全不是这个意思啊！NO.4 身扛着达斯琪一直以来，钢铁直男索隆对待女孩子的态度基本上是“敬而远之”，而且，索大硬气的外表下其实藏了一颗羞涩的心~达斯琪在与雪女莫奈的战斗中，肩部不幸被咬伤，面对即将袭来的毒气，索隆索性把她扛在肩上跑了起来……当然，逃跑的过程中索大也光荣的迷路了……NO.3 被佩罗娜包成宠物佩罗娜可以说是最让索隆无奈的存在~索隆被暴君熊拍飞以后落在了一个岛上，结果被佩罗娜所救，佩罗娜因为寂寞，直接把索隆包成了她宠物的模样，同时佩罗娜的消极精灵更是让索隆吃了不少苦……NO.2 卡在烟囱里在水之都，路飞一伙人跟cp9打斗时，变身的路奇接连击败了路飞和索隆，还把他们丢到外面，路飞卡在两栋楼之间，索隆则卡在了烟囱中，只留下腿在外面，画面实在太美好……这丢脸的一幕还被乔巴目击，最后索隆还是在乔巴的帮助下从烟囱中逃脱……事后乔巴跟娜美和路飞说起这件事时差点被索隆灭口。NO.1 初吻给了山治要说索隆打死都不愿意承认的事情，这个绝对是NO.1，这件事大概也是索隆和山治这对相爱相杀的CP之间永远的秘密了~索大正在提刀赶来的路上！路七先跑为敬啦~你们还知道索大的哪些黑历史快来爆料啊!', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10040', '0', '海贼王，山治是失败品？尼治虐了山治，反而暴露出山治的实力', '海贼王最新一集803中，由于山治之前维护了厨师柯塞特，而尼治也记恨在心。尼治偷偷将柯塞特揍了一顿，还特意送到山治的房间去挑衅山治。这导致了山治气冲冲的，要找尼治算账。在地下实验室中，山治一见到尼治，就直接冲过去质问尼治对柯塞特做了什么，同时一个踢技过去。尼治这么说也是杰尔玛科技下的超人类，却也来不及反应对抗山治的攻击。可想而知，愤怒下的山治完全将实力释放出来了，两年内的地狱修行山治果然实力大有提高勇治看见尼治也被山治瞬间踢飞，可能想起之前被山治痛揍时的阴影。勇治的表情反而像是，内心里不想承认，曾经的废物山治的实力比自己强。而伊治也见识到山治的实力，连忙威胁山治，不要忘记在东海的人质哲夫。侧面反映出，伊治内心里不敢正视山治已经变强了，不再是过去的弱小山治靠着利用东海的人质哲夫威胁山治，尼治趁机对山治攻击。假如山治没有被威胁，尼治肯定打不过山治。尼治还以为自己好厉害，利用山治的软肋折磨山治....伊治还特意在山治面前强调，山治是一个失败品，还说要决定一下他们兄弟之间的实力排行。伊治、尼治、勇治三人可以说是优越过头了，从小活在胜利的优越感的中，未尝有过失败的经历。如果单单一对一，不用手段，相信山治绝对比他们厉害', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10041', '0', '海贼王：富人靠科技，穷人靠变异，山治穿上战斗服后能有多强？', '相信很多小伙伴都曾看过扎克·施奈德执导，本·阿弗莱克、亨利·卡维尔、盖尔·加朵等主演的超级英雄电影《正义联盟》，其中蝙蝠侠的一句自嘲萌翻网友。你的超能力是什么？\"\"有钱\"其实蝙蝠侠口中的有钱就是科技。在海贼王中，我们看到的多是恶魔果实和自身修炼对个人实力的影响提升，如海军的六式，路飞霸气的修炼，黑胡子获得震震果实后的如虎添翼。唯独是比较少见科技对海贼王中人物的影响。在前期的设定中，海贼王是一部冒险漫，里面的 世界是 一个光怪陆离的世界，你不能和他太认真，里面有巨人族有小人族有长腿族就像镜花缘里不可思议的种族，有各式各样层出不穷的超能力，科技仿佛只是烘托而已。但是，在大妈篇的莫克家族统治的杰尔玛给我们展示了科技力量的重要性文斯莫克家族统治的杰尔玛是一个以科技力量著称的国家，曾经一度依靠强大的武力控制了整片北海的王族，通过一次四国斩的事件。一次性打败了四国国家的国王，并且还做成大照片悬挂在加治的城堡各处他们拥有着世界上最先进的变形金属科技与遗传因子科技。而且，他们还将这些科技转化成了自己独一无二的战力。就连身为四皇的大妈都非常渴望这种力量而不惜用自己的女儿和他们联姻。其中杰尔玛家族的战斗服便是他们科技的结晶，要知道看上去淡淡无奇又没有霸气文斯莫克·伊治穿上战斗服后一招\"火花冲拳\"，直接秒杀大妈军团的实力担当欧文。刚出场时被山治踢歪了脸的文斯莫克·尼治穿上战斗服后能以极快的速度踢飞了欧文，并且速度快到连欧文也没看清是谁踢的。可见战斗服可以对自身的力量速度作一个极大的提升，一直以来不被看好的杰尔玛穿上后拥有着仅次于四皇军队的实力。山治所属的战斗服是三号战斗服，如果路飞代表恶魔果实，索隆代表人为修行剑术，那山治就是科学的代表了{弗兰奇不是三大主力之一哈哈哈}，他需要用战斗服来提升力。感觉就有点路飞初期升二档开三档的意思（速度、力量）目前的山治，刚经历了两年的修炼，实力可以说已经是七武海的的级别了，如果再在速度力量上有个质的提升，恐怕堪比大将，最贴近得应该就是黄猿了（一直觉得山治会和黄猿一战）期待在和之国篇，山治能够穿上战斗服，给我们一场激动人心的战斗。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10042', '0', '海贼王，三大战力索隆有名刀，山治有黑科技，路飞有什么？', '海贼王漫画大妈篇章已经完结了，路飞的赏金也一路水涨船高，整整15亿！这样高的赏金已经对世界格局产生影响了，因此海贼王四分天下的格局成为五雄争霸，路飞也自然而然的当上了第五位皇帝！想要成为真正的海贼王，就难免要和四皇战斗，目前路飞的首要任务就是前往和之国和索隆会合，等待着他们的是号称海陆空最强生物的凯多！草帽海贼团老牌的三大战力路飞、索隆、山治是打败凯多的关键，想要赢得战斗每个人都必须拿出像样的杀手锏！索隆经过两年的掉线修炼，再加上手上还有名刀，即使战斗力无法达到大将高度也不会差很多！山治拥有着文斯莫克家族提供的最强黑科技战斗服，虽然山治目前并没有表态自己会使用，但是穿上以后绝对可以媲美大将！那么，作为草帽海贼团的船长同时也是核心战斗力的路飞又有什么依靠呢？虽然目前路飞的实力已经是准大将级别的，但是接下来的战斗要对抗四皇以及和之国的将军，现在路飞的实力远远不够，势必还要做出突破！因为作为主角，路飞面对的敌人往往是最强的敌人！不过，现在路飞的状态并不是极限，还是有很大的提升空间的！和卡二战斗的时候路飞将见闻色霸气运用到了极致，将见闻色霸气再提升一个等级是个很不错的选择!同时，从卡二给出的消息中路飞了解到恶魔果实还有觉醒这个选择！橡胶果实原本只是一颗非常普通的超人系果实，但是路飞开发的非常好，研究出1档到4挡等不同的形态，海贼王的剧情已经过半，是时候让主角的恶魔果实觉醒了！还有一个方面就是关于三大霸气的，从前面的剧情来看，见闻色霸气修炼到极致能够获得短暂的感知未来的能力，那么对应的霸王色和武装色霸气修炼到极致同样也会获得超凡的能力！假如和之国路飞见到霸气修炼非常强大的敌人，路飞有可能将自己的武装色霸气或者霸王色修炼到极致，这样才有资格和后面的四皇一战！总体说来，路飞想要成为草帽海贼团的主要输出，要么将果实觉醒，要么将霸气修炼到极致！​', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10043', '0', '路飞搞笑模仿草帽团的众人，模仿山治简直精髓，小冯也得甘拜下风', '在《海贼王》中，Mr2是模仿果实的能力者，他能够模仿自己接触过的人，然而他的模仿能力却远远没有路飞厉害，冯克雷只抓住了人物的外形，而路飞可是将人物的外形和神韵都模仿得淋漓尽致。模仿山治趁着山治不在就调侃山治，这些人对偷吃肉的事情没有一点反省，还敢调侃山治，不过模仿得倒是惟妙惟肖。模仿乌索普路飞利用自己橡皮人的特长，将鼻子变长，这模仿只能算是及格吧。模仿乔巴路飞自己不学好就算了，还拉上了罗宾。罗宾的手变成角，配合上路飞呆萌的表情，这一看就知道是乔巴。模仿索隆哈哈路飞模仿索隆可以说抓住了神韵了，索隆特有的死鱼眼，再加上那句“到早晨了吗？”，将整天睡懒觉的索隆的神态模仿的惟妙惟肖。不得不说路飞真的是草帽团的好船长，除了在面对敌人的时候挺身而出，在平时也是团队中的开心果。只有对于每一个船员都非常了解，才能模仿得这么传神。你们觉得路飞的哪一个模仿最传神呢？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10044', '0', '山治与10亿杰克的对战？山治的实力！', '文斯莫克·山治  是在日本漫画及动画《海贼王》中的主要人物之一。草帽海贼团三主力之一。路飞的第四个伙伴，左眼常被其黄发盖住，爱吸烟，有超强的腿技及菜刀法。最爱女人，很花心但很有风度。在船上担任三大主力之一兼任厨师。山治是北海王族“文斯莫克家族”的三子，悬赏金：1亿7700万贝里。杰克 日本漫画及动画《海贼王》中角色。绰号\"旱灾\"，声名远播的\"三大灾害\"中的一灾。四皇凯多的心腹、百兽海贼团招牌大将、凯多麾下三大舰队中猛犸象号船长，其赏金高达10亿。实力极其强大，就连面对犬岚公爵和猫蝮蛇的\"车轮战\",不眠不休的大战5天5夜都不分胜负，之后又紧接着前去营救\"多弗朗明哥\"，面对载有大将藤虎、前元帅战国、鹤的四艘军舰依然毫不犹豫的发动攻击，击沉其中两艘军舰。 一直以来，很多人都说山治两年后的实力提升是最少的，毕竟山治两年后的战绩实在令人堪忧，凭什么坐三大战力的位置？我们来看看山治的战绩vs 罗罗诺亚·索隆(草帽海贼团剑士)没有结果，发生冲突，后被弗兰奇中断。vs“和平主义者”熊 PX-7胜利，与索隆合力击败。vs 库拉肯(海底怪物)胜利，以“恶魔风脚·熟烧烤肉飞踢”重创后又与索隆、路飞、弗兰奇合力击败。vs 龙宫城护卫胜利，一击全部踢飞。vs 新鱼人海贼团天空部队胜利，以“恶魔风脚 烧铁锅光磁谱”完结。vs 伊卡洛斯(新鱼人海贼团干部)胜利，企图偷袭路飞，将其踢倒在地。vs 大光头海神(新鱼人海贼团新成员)胜利，与甚平合力击败，并以”恶魔风脚 地狱的回忆”完结。vs 新鱼人海贼团船员胜利，与草帽海贼团所有成员合力击败。vs 卡里布(卡里布海贼团船长、两年后的超新星、赏金2亿1千万贝里)胜利，与路飞、索隆合力击败并夺取财宝。vs M·凯撒·库朗手下胜利，与弗兰奇、乔巴合力击败。vs 特拉法尔加·罗(心脏海贼团船长、王下七武海之一、手术果实能力者)没有结果，被混乱空间击中，后因海军原因逃走。vs 斯科奇、洛克(雪山杀手、M·凯撒·库朗的手下)败北，被毒气弹击中跌落山崖昏迷。vs M·凯撒·库朗手下胜利，待在娜美身体里的情况下击败对手。vs 冰海鲨鱼胜利，以“首肉”完结。vs 鬼竹维尔戈(原海军G5基地长、现唐吉诃德家族最高干部)没有结果，但处于劣势，脚骨出现裂痕，因毒气蔓延而战斗中断。vs 维奥莱特(德雷斯罗萨公主、唐吉诃德家族成员、托雷波尔军干部、瞪瞪果实能力者)没有结果，用骑士精神将对方感动。vs 德雷斯罗萨警备队狙击手胜利，一脚踢倒，为了爱情而战斗。vs 唐吉诃德·多弗朗明哥(王下七武海之一、唐吉诃德家族首领、线线果实能力者)败北，被“寄生线”控制住后又被“五色线”重创，后被罗转移到桑尼号上。vs 西普斯海德(百兽海贼团成员、杰克手下)胜利，以“恶魔风脚·羊肉踢击”一击将其踢入水中。vs M·凯撒·库朗(原海军本部科学部队科学家、瓦斯果实能力者、赏金3亿贝里)胜利，将其劫持住。vs 卡彭·贝基(火焰坦克海贼团船长、超新星之一、坚城果实能力者)败北，无奈投降，助娜美等脱险后，与凯撒一起被卡彭·贝基带走。vs 文斯莫克·勇治(山治弟弟、文斯莫克家族第四子)胜利，勇治脸被打歪。vs 文斯莫克·伽治(山治父亲、文斯莫克家族兼杰尔玛66领导者)败北，伽治于身着科技装置的状态击败山治，但山治只有皮肉伤。vs 文斯莫克·尼治(山治哥哥、文斯莫克家族第二子)败北，因哲普被控制而无法反击。vs 蒙奇·D·路飞(草帽海贼团船长)胜利，对方毫不还手。vs 夏洛特·大福(夏洛特家族第三子、三十四位大臣之一『豆大臣』、蒸腾果实能力者)没有结果，处于下风，被对方一招打飞。山治在两年后没有拿得出手的战绩，和干部级别的对手战斗还无一胜率，现在急需一场大战为他正名。那么大战的对手是谁呢？现在的剧情已经接近《四皇凯多篇》了，在主题曲我们得知，山治的对手可是百兽海贼团招牌大将 旱灾杰克（10亿赏金）。看图片山治的大招恶魔风腿打断了杰克的鼻子，以杰克的实力，山治能够打败他 的确能够很好的为他正名，我们也一起期待吧。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10045', '0', '山治拖后腿？两年后草帽团山治实力解析', '一直以来路飞、索隆和山治都是草帽一伙的主要战力，索隆和路飞在两年的修炼期实力都得到很大的提升，但同位三大战力的山治在之后的表现很是平淡，骑士之道两年之后开始没落了嘛？路飞一伙在肥皂岛重聚时，船上所有人都有了很大的变化，在海军、海贼等龙蛇混杂的肥皂岛准备重新起航时，路飞和小伙伴们都在秀实力秀新装备时，山治在。。。流鼻血。。。持续流鼻血登陆鱼人岛之后，鱼人岛上反派的实力其实不是很高，反派头头霍迪在海里进行主场作战，还差点被索隆一刀砍死，嗑药强行增加实力对路飞等人来说，这个篇章就是让展示两年的修炼成功的。路飞霸王色霸气秒杀五万人，索隆炫酷剑击秒杀豹藏，山治。。。持续鼻血抢镜。尾田虐山治持续。。冰火岛对战鬼竹维尔戈，两人交战时间不长，但是短暂的双方互踢中，山治在对踢时骨裂的镜头让人惊掉了下巴！虽然半状态的山治和满状态的维尔戈，相比路飞和索隆在冰火岛的战斗，山治在逗比上的表现更亮眼！只是这样尾田还不够，虐山治还在持续，在德岛正面和多弗朗明哥的战斗中，短暂的交锋最后以落下风结束了德岛的战斗！在德岛的后续战斗，路飞四档败明哥，索隆三千世界砍了石头男，激烈的战斗让人赏心悦目，山治的存在感持续走低。山治两年后的实力是否被路飞索隆拉开很大差距？答案肯定是否定的，山治一直是智商+实力的代表，又在人妖岛进行了地狱式的训练，山治的本身实力是毋庸置疑的，在最新的大妈篇剧情中，山治在救戚风时，露出的无人察觉的速度（山治媳妇布林除外），尾田终于让山治的战力上线！小编被山治骑士道吸粉，期待山治之后的战斗！放上山治帅气图！真男人发言！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10046', '0', '海贼王，勇治挑衅山治，山治大展实力，就连伽治也不敌山治！', '海贼王最新一集792话中，其中一个镜头是有关于杰尔玛66的。在城堡中，勇治时隔多年再次见到了自己的哥哥山治，过去欺负山治的习惯重现了。勇治直接挑衅山治，称山治为废物没有选择权利山治不想理会勇治，由头到尾都只是让勇治滚开。不过面对勇治的一再挑衅，山治忍无可忍，准备一实力说话。而勇治，也是期望着看看山治所谓的实力勇治依靠的文斯莫克的科技力量，而山治则是靠自己多年都修炼，在加上两年间在地狱（人妖国）的修炼，勇治也不敌山治。而再看之后的伽治与山治一战中，山治实力全开，伽治完全应对不过来。如果不是关键时刻伽治利用人墙挡在前面，山治或许已经打倒了伽治。毕竟文斯莫克一家太依靠科学装备了，茶会上没有装备的他们实力大减还有一个细节就是山治与尼治的冲突。因为尼治殴打了那位女性厨师，山治怒了一脚踢飞了尼治，而且以非常迅速的速度准备追击。要不是伊治出口用人质威胁山治，尼治会被痛打一顿', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10047', '0', '海贼王：娜美的第一件事竟是发福利？', '海贼王中尾田最近一直是给海迷们发福利呢！当然，女主角娜美肯定是不例外的啦，就算是被抓住也有福利哦，接下来就让小编带大家来了解一下娜美被抓之后的福利吧！在娜美被抓的有五个福利，有两个是被求婚，有三次是被虐待，而且每一个福利大家绝对满意，在剧情中发条岛冒险的时候，路飞他们的船被别人给偷走了，因为他们没有衣服换，所以他们只能随便穿了几件衣服，但是娜美就不一样了，那美的穿了一件非常漂亮的婚纱，就在这个时候海贼团的手下居然看上了娜美，还说要把他给抓走给大熊王献上，虽然他口头上是这么说的，但其实他特别的喜欢娜美，他肯定是想将娜美占为己有。说实话，小编都觉得他长得有点过分了，他居然还想要娜美？这不是在开玩笑吗！之后在三桅杆上透明人不仅想要占娜美的便宜，还说要把娜美给抢走，做自己的妻子！因为那枚穿着一身婚纱，就连小编都觉得像天使一样超级美，不光是小编就连山治也直接愣住了，这个估计也是那枚第一次穿婚纱小编觉得这个福利还不错哦，之后在强者天下中，娜美被迫成为了金狮子的俘虏，金狮子对那没威逼利诱也就算了，还直接人身攻击，把娜美给插在空中，小编看着都心疼。还有一个就是在黄金之心中，铁链男把娜美给抓住了娜美浑身都被缠上了铁链，不过小编说真的他虽然被铁链男抓住还让铁链给缠上了还是那么的性感！嘻嘻嘻。同时被铁链男抓住了，除了娜美，还有罗宾他们两个人的身材被铁链给缠住之后真的太养眼了！还有最后一次是在大妈篇中，大家也都知道，娜美和路飞都被关进了监狱书里边儿，关进监狱书里边也就算了，还被路飞精神虐待，虽然说他们两个人最后得救了，多亏了甚平但可怜的娜美被甚平的一把火把衣服烧得精光，不过看到娜美那身材之后，大家和小编是不是一样想谢谢尾田呢！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10048', '0', '那些年娜美赠送的福利！', '我拣几个印象比较深刻的说一下吧。恐怖三桅帆船篇 娜美被野兽制住，呼救，但是因为野兽是透明果实能力者，所以闯进来的乔巴和乌索普只能看到全裸的娜美。因为物种的原因，乔巴对于人类的女孩并不感兴趣，它经常跟罗宾还有娜美一起洗澡。但是乌索普就不同了。看到了全裸的娜美，乌索普还道谢了。2. 香波地诸岛篇因为是两年后的重逢，见到乌索普的娜美特别激动，于是就。。。。3. 德雷斯罗萨篇以及其他比起乌索普总是料想不到的艳遇之外，桃之助这小鬼是真好色。所以我早就说过了，桃之助必须死。4. 海贼王，龙珠，美食的俘虏特别联动 这是13年阔别许久的龙珠全新剧场版《神与神》上映之前，《JUMP》为了烘托气氛特意做的联合动画。里面的龟仙人也享受到了袭胸的乐趣。不得不说，龙珠的女性角色美感欠佳，这些年苦了龟仙人了。好吧好吧，随着海贼王的持续连载，这些年女性角色的胸部是越画越大，除了证明作者是个色狼之外，也是为了满足男性读者中的色狼。以上是我比较印象深刻的娜美福利场景。有兴趣的同学，欢迎补充。。。。。233，，，，', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10049', '0', '桃之助色胆包天吃娜美豆腐，娜美不动声色反咬一口', '山治的好色是众多漫迷所能容忍的，毕竟他只是爱美，不会去玷污没，只会远远做着护花使者，不会零距离的辣手摧花的。但是桃之助就不同了，同样是好色，这个小屁孩的行为实在叫人咬牙切齿啊！先不说他有没有护花吧，就单单他有事没有就对娜美和罗宾来个袭胸，这实在不能忍啊！不过路飞一伙一个也不是吃素的，袭胸娜美是要付出代价的。桃之助仗着自己是个孩子，袭胸的手段更加高明了，他不是仅仅用手，而是直接将自己的头埋进娜美或者罗宾的美胸中，并且乐在其中不能自拔。这次桃之助正沉浸在袭胸的快乐中，娜美就反咬一口惊呆大家了。娜美知道桃之助是个有身份的主了，趁着他享受自己的美胸之时，直接问他有没有财宝，这就是赤裸裸什么来着，应该是等价交换哈！娜美此招一出，不仅是路飞一伙被惊诧到了，在场的所有人都吃惊了，娜美也实在太喜欢钱了吧！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10050', '0', '丈嫂直播连麦方丈温柔的一面！娜美倒流被粉丝骂惨！怒怼小黑粉！', '昨天丈嫂直播，许久没有露面的方丈来连麦，方丈说泰国蛮好玩的，这次要好好玩玩放松一下，期间特别的关心老婆，询问老婆有没有去医院检查，一定要照顾好自己和孩子!平常不要看方丈大大咧咧的，该细心的时候是一点不含糊!!这样的好男人哪里找，刚结完婚，媳妇怀孕了，自己出去放松了，让媳妇自己去医院检查，媳妇也是不错的点个赞~~娜美今天让大家关注自己抖音号被骂惨，粉丝纷纷说要倒流，娜美说不管在任何平台上收益都是给公司一样的收益，你们不知道我们在哪里赚钱都给师傅了！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10051', '0', '仙家&amp;丈门交战损失惨重 双方主播均遭连杀 万伊嘲讽娜', '昨晚丈门和仙家的战争正式打响，双方主播互开专场均遭官方封禁。仙家这边在逗逼队长和娜美被封直播间后，11点开播的语音骡子也遭官方封禁6天。至此仙家遭遇了官方“五杀”（仙家一哥、墨尘、张丹、娜美、语音骡子）今日凌晨，娜美用师弟夏天的账号开播。在直播间连麦语音骡子，提到仙家主播被5连杀。骡子表示：我最委屈，还没开口说话就被封了，还不如骂几句解气！娜美：我正在申诉，看能不能解封! 而丈门同样也是伤亡惨遭，在万伊被封的同时，傻强发作品称刚睡醒就被封禁八天。同时被封的其它主播也纷纷亮相。（具体封多少暂未明确，但是有粉丝投稿，丈门被封禁的账号也有八个）昨晚丈门万伊被官方封禁后，在微博发文再怼仙家：当你破口大骂开始的那一刻，你就已经输了。真拿你自己当个人物了，就你那语言啥都不是，语言粗俗骂爹骂娘，延续了你们翔家军的优良传统！你说过我们丈门挂不起假粉，真是说了一句公道话，真给自己装里了你也太傻逼了。并且配图为一双破鞋。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10052', '0', '全金属狂潮第四季：娜美含冤而死，相良宗介心里只想着千鸟', '全金属狂潮第8集分析与讲解，上集相良宗介独自前往决斗场，使用老式机器战斗凭借丰富经验战胜了最新型机器，之后就跑去克拉马藏身处，狡猾的士兵想到娜美，把娜美当做人质要挟相良宗介出来，眼看倒计时快到了，宗介又会怎样解救娜美呢？很遗憾的是克拉马改变了主意，选择了杀害娜美，身中枪弹的娜美还没反应过来呢，克拉马就接连补了几枪，娜美就这样当场牺牲了。小编也没想到会这样的结果，身为女二的娜美就这样牺牲了，说不出来伤感与疼痛，在一旁的相良宗介并没有呐喊冲出去而表现很是冷静无情的样子，娜美的牺牲真是一文不值，好歹也是娜美带他完全他想要完成的任务。连已经阵亡的娜美也不会想到，自己牺牲只是为了拯救另一个女孩，有点可笑至极。虽然没有流下一滴眼泪的相良宗介，可还是有七情六欲的人，内心还是无比痛苦打算乘坐旧式机器消灭克拉马，敌人也很清楚相良宗介的想法，早已准备好他的到来，还悬赏赏金为奖励附送最新型装备，真是费尽心机想消灭相良，可其他人只看到表面现象，却不知相良宗介真正实力与身份。被憎恨所冲昏头脑的相良宗介直接冲进敌阵，不管谁冲出来就是打，简直就是恶魔附体，满脑子只有克拉马可还是保持清醒在作战不能有丝毫的失误。在与最后一个敌人名为“陶”就是之前劫持娜美的那人，因为机器的陈旧，相良宗介差点被干掉了，想起了娜美给了他致命的一击，打倒了所有的敌人克拉马的诡计多端一步步引诱相良宗介上来，被一击打中。身受重伤，幸好其他部队赶来支援抵挡了一会攻击，相良宗介借此机会逃脱利用血记引诱克拉，给克拉马致命一击，为娜美报仇了。可心里还是挂念着的女人是千鸟，爱着千鸟，对只剩一口气克拉马一直追问，知道千鸟大概身在何处才倒下在快要陷入昏迷之时，隐约看到一个身影，相良宗介第一反应是娜美，可真实影子是千鸟，并说到“好想你啊，千鸟”此刻娜美要是听到真是死不瞑目啊，真为娜美感到伤心，本篇故事内容就到此结束了，该集内容很是伤感，尤其是开场部分，看着有点想流泪，请大家慢慢观看，谢谢大家支持与收看', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10053', '0', '乌索普是《海贼王》最大伏笔？2亿贝里乌索普实力分析', '大家都知道在动漫作品《海贼王》中，海贼们的悬赏金额是对个人战力的一个评价标准。这样的战力评判也是会从多方面进行考虑，一般来说赏金还是和个人战力对等的。但是对于《海贼王》中的乌索普的战力和表现来说，官方给出了价值2亿贝里的悬赏，很多网友还是觉得他的赏金不合理（估计大家光记得他的逃跑了）。近日就有网友质疑乌索普的悬赏金是不是太高了，不过很多网友还是表示认真看漫画，你就不会这样认为了。但是纵观《海贼王》整部漫画来说，乌索普的霸气值真的不是随便说说的，尤其是“顺境靠索隆，逆境靠路飞，绝境靠乌索普！”这句话，也慢慢流传开。那么乌索普到底是战五渣还是隐藏BOSS那？如果说《海贼王》里面的悬赏金额全是参照战斗力，这个说法感觉并不全面，也有很多人说是看对世界政府的威胁度。如果这么说的话乌索普确实值这个价钱，别的不说毕竟公然把人家的旗子打了下来，这种事情貌似之前也没几个人干过的。请输入当时路飞说“狙击王，把那面旗打穿”，乌索普说“明白”这段真是又燃又帅！图片描述要说起乌索普实力最突出的一段，那必然是德岛战争的故事线。德岛这段故事很多人看后都说这段故事看的很揪心。无论是拖稿还是其中不明原因的开打，都让观众有点“食之无味弃之可惜”但是小编认为，德岛这段故事线，也许正是作者想要表现乌索普已经成长的一段。后来有人评价说，如果没有乌索普，估计《海贼王》在德岛就要全军覆没了。乌索普本身给我的感觉就是小丑巴基一样的角色，虽然本身实力很一般，但号召力特别强。其实比起那种纯粹的强者，这样能够煽动气氛的人才是最可怕的吧?如果这么想来，作者或许在他身上是不是安插了跟多的伏笔？但是现在不知道乌索普对霸气的掌握能够达到什么样的程度。有人对于乌索普在德岛一战，评价为乌索普能力值的巅峰，但是巅峰这个词，真的不好说，纵观整个海贼的故事，所有人物的成长，作者刻画的还是非常好的，也许在船上乌索普是最弱的，但是下了这条船，乌索普已经不是刚开始遇到的那个不负责任的小屁孩了，已经开始成长为独挡一面的人物了。就现在剧情来说，乌索普的父亲是红发海贼团的一员，搞不好还有很多没有揭晓的秘密呢。在《海贼王》里，路飞一伙不止一次遇到绝境，有好多次几乎团灭。但是每次都会逢凶化吉，毕竟是主角，哪会那么快领便当呢？但我们总感觉乌索普是最弱的，因为他总是很担小，很消极，而且老爱吹牛，但是后来我渐渐的发现乌索普其实很重要，他真的是最好的狙击手。《海贼王》当中目前为止有两次其他人都全败的情况下，唯有乌索普，神一样干掉了令路飞一伙全败的两个女人。小编最后总结一下，什么也别说了，乌索普绝对是作者最大的伏笔！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10054', '0', '海贼王：风水轮流转，这位虐待大熊的天龙人将会成为乌索普的坐骑', '海贼王漫画908中，原七武海的大熊君只能用惨来形容，当初为了保护路飞的桑尼号伤痕累累。如今的他成为了天龙人的宠物，遭受着非人般的待遇，而这位天龙人正是罗兹瓦德圣，就是路飞打飞的查尔罗斯圣的父亲，这次的他听说自己的心爱的儿子居然被同族的人揍了而匆匆赶来查看情况，超新星波尼看见此情此景更是痛哭流涕，对于天龙人，尾田基本上把他们描述的坏到骨子里了，而大雄不仅成为了贝加庞克的实验体，如今又失去记忆成为奴隶，太让人心疼了。看着罗兹瓦德圣耀武扬威的样子被自己的儿子查尔罗斯圣看见了，他也希望和父亲一样拥有属于自己的专属坐骑，看起来这对父子真的是太人渣了，父子的长相和性格都是遗传的，从大雄的身上可以看见有很长的一条链子拴在他的脖子上，身上还被插了好几把匕首，全是上下全是伤，没有一处地方是好的，当初连索隆连着用了好几次斩击都没有破防的大雄的躯体，如今被天龙人的剑所伤，难不成这把匕首也是特制的，或者说大雄被改造的更弱了。早在香波地群岛的时候，这对父子就登场了，作为天龙人的首次亮相，查尔罗斯圣在大街上准备抢别人的妻子做妾的时候，碰见了喝酒的索隆，差点被一刀砍了，之后在鱼人拍卖会上，更是惹怒了我们的主角路飞，愤怒的路飞直接朝着查尔罗斯圣的脸上来了重重的一拳，这个镜头真的是大快人心，得知自己的儿子被揍的罗兹瓦德圣气的呼叫了海军大将，要求把这座岛上的所有孩子全部消灭掉，给他儿子报仇，天龙人的号召能力真的很强，没一会的功夫大将黄猿就赶到了现场。这时候就轮到乌索普大神登场了，罗兹瓦德圣正得意洋洋的时候，乌索普大神犹如天使一般从天而降，直接一屁股就坐在了罗兹瓦德圣身上，他的女儿夏露莉雅宫吓得脸色都变了，虽然这个镜头很多人不记得了，但是丝毫不亚于路飞给查尔罗斯圣的一拳，这一次，乌索普为大家出了一口气，在德雷斯罗萨中，尾田把乌索普安排成为扭转局面的神一般的男人，大神乌索普的名号逐渐传遍大海，而且乌索普在出海之前编了好多谎话和小故事，目前的话又应验了。风水轮流转，大熊先生在经历了悲惨遭遇之后必定会被革命军所救出了，希望之后能够安安稳稳的度过余生，别再受伤害了，而乌索普大神将会带着他的新坐骑天龙人罗兹瓦德圣踏平新世界。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10055', '0', '海贼王：乌索普实力终获伙伴认可，索隆亲口承认乌索普是大神！', '罗罗亚索隆是草帽海贼团的第二战力，第一战力是船长路飞，至于山治只能算第三战力，虽然山治与索隆没有战斗过，无法知道他们谁的实力更强，不过我们根据索隆与山治对敌的人物来看，索隆的对敌的敌人都是强过山治的对敌的敌人的，所以算索隆要更厉害点，排名草帽海贼团第二大战力！索隆两年前超新星11人中之一，实力强大，悬赏过亿的海贼，能操纵三把刀的三刀流剑客。为人爱喝酒，爱睡觉，讲义气，也是海贼里面第一大路痴（也不知道为什么这么路痴？），被索隆认可实力的人很少，草帽海贼团中只有船长“路飞”实力获得索隆认可，其余人索隆更本不认可其实力。直到两年后的某一天索隆终于亲口承认乌索普是“大神”，乌索普获得索隆的实力认可！小时候的索隆佩服古伊娜，索隆与她较量2001次仍未尝一胜，索隆是认可佩服古伊娜的实力。索隆的梦想是成为世界第一大剑豪，而鹰眼就是世界第一大剑豪，所以索隆非常认可鹰眼的实力直到后来拜鹰眼为师。索隆一身中遇人无数，真正被索隆认可实力的也就是这两人。乌索普一直以来是草帽海贼团最胆小的一个，遇到实力强大的人就吓得逃跑，虽然多次依靠其自身能力拯救草帽一伙，但其实力索隆真心不认可，索隆虽然讲义气，但在实力方面不认可就是不认可，索隆一直认为乌索普就是靠运气战胜对手！在水之都时梅利号被判定无法再航行，乌索普对路飞想换船的决定感到愤怒，毅然退出草帽一伙并挑战船长路飞，乌索普VS路飞，乌索普惨败，索隆对其表示不屑并说把船让给乌索普。水之篇，司法岛之后，乌索普听说蒙奇·D·路飞等人即将离开水之七都，为了回归海贼团，甚至想了100种解释方法，他独自一人演练的样子正好被路过的香吉士看到，他回去即告诉其他人，虽然乌索普要归队大家都很高兴，但因为他先前擅自退团，和路飞决斗也战败了，如果回来就等于破坏了团内的纪律，为了守住船长的尊严，索隆告诉路飞，（听着，你好歹也是在我之上的男人，要是做这种没出息的事，下次我也要离开你们这伙人），索隆意思是不允许乌索普有想随便糊弄过去的心态，坚持他第一声如果不是谢罪，一切就免谈！索隆称乌索普为白痴...两年前索隆对于乌索普的信任只建立在其伙伴身上，不管乌索普怎么逆境下救治过草帽一伙，索隆都对乌索普的实力看不上。直到两年后明哥篇时，索隆竟然终于承认乌索普的实力，被索隆认可其实力，乌索普是在索隆之上的男人！明哥篇，索隆VS琵卡，正在战斗的时候，蕾贝卡与罗宾搭乘小人族的飞虫赶了过来，琵卡看到后，直接同化岩石变成石巨人，意图一拳将其三人击毙，却被索隆的三刀流“千八烦恼风”一刀砍断石巨人身体，琵卡身受重伤，口吐鲜血。索隆对琵卡霸气的说了一句话“草帽一伙中可不止有船长路飞和大神乌索普啊，给我记住了，有朝一日我会成为世界的大剑豪”。乌索普终获索隆认可，草帽一伙的副船长之位乌索普竞争，索隆必定让位！女帝镇楼', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10056', '0', '乌索普乱入索大悬赏令，网友猜测乌索普大神才是隐藏Boss', '路飞的悬赏令大家早就看过无数遍了，草帽海贼团第一次悬赏就悬赏了两个人，虽然乌索普只有一个后脑勺，但是尾田大神还是有单独拿出来说一下这个小细节，这也让乌索普得意了好久。路飞的悬赏令上很容易就能找到隐藏的乌索普，那索大的悬赏令上的乌索普你有发现吗？网友纷纷猜测，有的网友甚至说乌索普就是最后的隐藏Boss。乌索普是隐藏Boss？这个问题别人真的有人想过，那就是小丑，多弗朗明哥有着看透未来的视野，小心经营着自己的地下王国，就算是路飞他也丝毫不畏惧，只是他忽略了一个男人，那就是乌索普。乌索普没有路飞那么强悍的战斗力，但是他误打误撞，他凝聚起来的力量对明哥的威胁程度丝毫不亚于路飞，甚至还要超过路飞，毕竟小人族一开始都是被乌索普忽悠的。虽然最后的赏金没有明哥在德岛开出的5亿高，但是一下子涨到了2亿也是吓到了很多人。虽然在新世界赏金过亿的海贼多如牛毛，但是只要赏金过亿就算是大海贼了，在伟大的航路前半段这赏金就已经非常高了。很多人觉得乌索普配不上这么高的赏金，其实，在海贼王里，真正有远见的就是明哥，乌索普真正的悬赏金就是应该5亿，和路飞不分上下。小看他的人都会付出惨痛的代价，毕竟乌索普已经成神了。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10057', '0', '海贼王：罗宾16岁、女帝16岁、龙16岁，都比不上16岁的红', '在动漫《海贼王》中很多角色目前的年龄都已经曝光了，像路飞一伙，超新星，七武海和海军大将等人；剧中也对部分角色的童年或者青年时的经历有过回忆篇章，也有的是轻描淡写。像罗宾有过回忆，对女帝也有过短暂的介绍，红发青年时也出现过，那么今天我们就来看看这些人16岁时的样子。（时间节点都以大事件时计算）第一个：罗宾16岁罗宾现在的年龄是30岁，她有不堪回首的童年。4岁时因为母亲的考古把她丢给了舅舅，8岁时亲眼目睹故乡奥哈拉被屠魔令抹掉。更是被政府以7800万的赏金一直抓杀。从罗宾8岁开始直到27岁基本都是过着背叛，逃跑的日子，为了生存她什么都可以干。尤其是在她16岁时，罗宾亲口说出了“让我加入吧，我16岁什么都可以干”瞬间让人感觉好心疼。第二个：女帝16岁时女帝现在的年龄是31岁，是海贼王中最美的女人，也受到了众星捧月般的待遇无论是男女老少都会拜倒在她的石榴裙下。可她青少年时的经历并不光彩，12岁时被人贩子卖给了天龙人，受尽了天龙人的凌辱被迫吃下甜甜果实供天龙人欣赏。这段经历整整持续了4年的时间，女帝16岁时费舍尔泰格大闹玛琳乔亚，女帝三姐妹才得以逃出天龙人的魔爪。而女帝16岁时的样子看上去与现在完全判若两人。第三个：龙16岁据情报显示龙现在的年龄是51岁左右。22年前他现身在罗杰刑场，当时的年龄是29岁，而且貌似已经成为了革命军的首领。那么龙在16时的经历并不寻找，有卡普这样的老爹，还能当上革命军的首领，怎么想都不会很简单了。但与红发相比还差了一点。第三个：红发16时红发在15岁成为罗杰船上的实习生，与罗杰一起经历很大大型的战役，比如艾德.沃海战役，与白胡子的决斗，见证了罗杰当上海贼王，并且亲眼目睹了罗杰的处刑。也就是说红发在年轻的经历确实比较精彩。传说中的强者们他都见过而且都与之战斗过，现在也贵为四皇了，而且还有天龙人有某种联系。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10058', '0', '海贼王：6种神奇的合体姿势，2次是男女合体，邀请罗宾合体被拒', '在海贼王中有着各种各样的组合，其中也不乏利用各种方式进行合体来进行作战的，而本期，我们就来和大家聊一聊海贼王里的5种合体姿势，有的是靠能力，而有的则是靠姿势，还有男女搭配的合体技能，可以说是让人非常的惊讶了，甚至倒数第2个山治看了都要去效仿，不过最后的结局很意外就是了，而曾经弗兰奇曾邀请罗宾合体遭拒绝？那么海贼王中都出现哪些神奇的合体姿势呢？相信一定有被你忽略了的，那么下面就让我们一起去看一看吧，温馨提示记得一定要看完哦，不然你会错过重要的细节的，因为路飞也曾体验过！本期导读：前3种为单纯的恶魔果实能力实现的合体，4和5为男女合体，最后一个是邀请罗宾合体被拒绝一、瓦尔波-吞吞果实能力瓦尔波的吞吞果实能力是海贼王中出现的最早的一次合体技能了，吞吞果实的能力就是通过吃，可以将被吞食的物体变成自己身体的一部分，当然吞掉生物的话，这个生物也会成为身体的一部分，在瓦尔波与路飞的对决中瓦尔波就曾用过此招数，讲道理吞吞果实的能力还是很厉害的，不然黑胡子也不可能看上，只不过瓦尔波这个人实在是太逗了，好好的果实给他都浪费了，但是好运就是眷顾这种人，靠着吃垃圾生产玩具都能成为爆发户，而且还重新当上了国王娶了女神当老婆，这种狗血励志故事，真是没谁了。二、夹克果实能力夹克果实的能力顾名思义，自己可以像夹克一样被人穿在身上，因此就可以实现合体了。这个果实的能力看起来并没有什么用出，并且似乎十分的无厘头。但实际上夹克果实是可以实现控制被合体的人的，无论对象是动物还是怪物只要被穿上，就会变成彻底的被控制对象，并且可以获得被控制人的力量。因此如果夹克果实能力者能穿在一个厉害一点的人身上就不会被吊打的那么惨了。三、路飞的第一种合体姿势，人马形态在路飞第一次遇到武士锦卫门的时候，锦卫门因为被罗的手术果实能力砍成了2半。而下半身就遇到了路飞，结果路飞不仅想把这个下半身找来当同伴，而且还让这个下半身与自己合体，姿势可以说是非常的滑稽了。四、路飞的第二次合体姿势，男女搭配的神奇合体路飞的第二次合体有点类似夹克果实的能力，在动画575集中，路飞曾遇到过一个有着迷你果实能力的巨人族女孩，就曾尝试体验了和她的合体，女孩通过果实能力变小，进入到路飞的体内，然后在利用果实能力恢复成巨人。因为路飞是橡胶，所以不会被撑爆，反而是跟着一起变成了巨人，不过这种合体的姿势真的是十分的奇怪，因为一个人要被套在另外一个人的身体里面，那么到底是处在外面的路飞控制动作呢，还是被路飞包裹在身体里面的妹子操纵的呢？五、阿鲁贝鲁，萨鲁哥在赏金旗猎人篇里，山治和乌索普曾遇到过这样一对情侣阿鲁贝鲁和萨鲁哥，利用双人滑冰的合体技能一起攻击山治和乌索普。因为地面很滑，山治难以用出踢技，所以就模仿了阿鲁贝鲁和萨鲁哥，也与乌索普合体，一下子就解决了打滑的问题，一脚就打赢了这对情侣，可是结局真的很意外，因为山治太好色，抱住阿鲁贝鲁后就被迷晕了。草帽一伙的终极兵器，大皇帝！在恐怖三竿帆篇里为了对付奥兹，弗兰奇等人想要利用合体来做到和奥兹一样的体型再跟他对抗，这个草帽一伙的终极兵器被称之为巨型·大皇帝。不过因为邀请罗宾来一起合体组成大头目，被罗宾拒绝了，所以这次男女合体并没有成功，并且罗宾还说出了十分嘲讽的话“身为一个人太丢脸了”不知道对此你怎么看呢？欢迎再留言区留言讨论，我们会认真去看每一条评论，也希望大家能够关注并收藏我们，因为没码字不易，关注是最好的鼓励，喜欢记得关注、收藏、点赞加转发哦！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10059', '0', '美丽的罗宾五次被人坐在身下！而且有两次是她主动的！', '在海贼王里面。罗宾是整部剧里面很受大家喜爱的人物，她也是很漂亮的，海贼王里面还有一位是叫娜美的女孩，不过很多喜欢海贼王的却是更喜欢罗宾一点。罗宾在剧中的形象很是性感，并且还很漂亮，所以剧中很多人物都会故意的占这位美女的便宜，更有甚者，有好几次都被人直接压到了身上。我们现在来看一看这几次经历。第一次就是在小人族那里，别看这小人族的个子不高，他们的胆子可真是大得不得了，最大的就是他们的族长叫做甘乔。还有一个兵长叫做雷欧，相信这一幕大家的印象是很深刻的。雷欧总是在罗宾没有注意的时候就跑到罗宾的身上坐着，还是坐在身上最软和的地方，真的是大胆极了。第二次的时候就是，罗宾和青雉，这两个人已经认识有几十年了，但是这两个人没有发展为恋人，不知道为什么，可能是罗宾对于青雉没有一点这方面的兴趣。但那时青雉可没有这么老实，就在这几年期间，他在解冻罗宾的时候，就一直抱着罗宾，想要趁机占罗宾一些便宜。第三次就是透明男，他因为吃了恶魔果实，对于山治来说，这个东西确实是用的正是地方。不过在这之前的时候，草帽团里面是没有人可以通过气味来确认的，因此罗宾是感受不到透明男的，就这样，透明男就偷偷的占了罗宾的便宜了。第四次的时候，就是桃之助，这次就更让人生气了。桃之助就把自己弄成一个很单纯很无害的样子，这个样子最能骗到女生了，他到了现在，已经把所有的漫画迷都得罪光了。最最令人伤心的是。他竟然还和罗宾一起洗澡，而且洗完之后还在罗宾身上睡觉，不过这次倒是罗宾自己主动地。第五次的时候，就是克尔拉了，在德岛的时候，他们两个人见面的时候，克尔拉很激动，就跑上去拥抱罗宾，但是当时她的脸放的地方是不对的，但是我们大家还是原谅她了，毕竟人家也是一个大美女了。而且当时罗宾自己也主动了。还有一次就不是压在身上了，而是直接折磨罗宾了，就是斯潘达姆，他竟然对罗宾用武，以至于大家现在都不喜欢他了。看来大家还都是喜欢美女啊，看脸的时代啊~', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10060', '0', '海贼王为什么罗宾老被绑？', '罗宾一路被捆绑 从未被超越', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10061', '0', '海贼王相亲记第二弹-妮可罗宾', '今天又一位草帽海贼团队员预在此征婚，今天这位征婚者虽然年龄稍大，但绝对会是一个居家过日子的好老婆。以下是其个人简介，请详阅。姓名:妮可.罗宾;昵称:恶魔之子;性别:女;年龄:保密;身高:188cm;故乡:西海的奥哈拉岛;学历:博士;职业:某知名海贼团历史顾问;爱好:在阳光下看书;喜欢的颜色:紫色;个人简介:原就职于地下暗杀组织巴洛克工作社，经过自己的努力升为副社长兼最高司令官，代号Miss.all sunday。后由于经营不擅，巴洛克解散而加入草帽海贼团。尤其擅长考古，讨厌任何践踏历史的人！学识渊博，个性冷静，再大的风雨都能理智对待！形象照:如有意向，请尽快来稿！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10062', '0', '海贼王：青雉关注了罗宾这么多年，他是不是喜欢罗宾？', '罗宾是奥哈拉大事件中的唯一幸存者，而她能够活下来主要和两个人有关系，一个就是D之一族的哈格瓦尔.D.萨龙，另一个人就是青雉了，而萨龙又是青雉的好朋友，青雉最后给罗宾开小灶放她走在很大的程度上是和萨龙有关系的，因为萨龙是在以生命保护罗宾，希望这个孩子能够活下去，同时也希望这个孩子能够带给世界一点改变，哪怕是一点点的改变，那么救她都是有价值的。有人说青雉和罗宾像是父女关系，虽然罗宾那时候才八岁，但是青雉也没有大到可以做八岁孩子的爸爸的程度，换个角度去考虑，更像是兄妹关系，哥哥一直都在关注着妹妹的动向和安全。在青雉第一次遇到草帽团的时候就把罗宾给冰冻住了，青雉的目的主要是想看看在伙伴遇到危险的时候，尤其是战力悬殊还很大时，草帽一伙会不会为了救伙伴而不要性命呢？草帽团的做法没有让青雉失望，他们为了救罗宾的确是可以牺牲自己的生命，这也是罗宾找到了一个好的团队，好的归宿，所以青雉才放心将罗宾交给路飞他们。让罗宾真正的融入草帽团还是司法岛事件，当时CP9威胁罗宾如果不跟他们走，他们就会启用屠魔令杀死草帽一伙，而草帽一伙为了救她，不惜跟整个世界政府作对，参考乌索普射击世界政府的旗帜。草帽一伙为了在司法岛上救出罗宾也是不怕牺牲的去拼了命的，最终也将罗宾救了下来，让罗宾想起了萨龙对她说过的话，“总有一天，你一定可以找到愿意为你牺牲一切的伙伴。”罗宾找到了，草帽一伙做到了。在剧场版Z当中，罗宾被吃了倒退果实的艾恩碰到了身体，从而让自己变小了12岁，倒不如说更加年轻了12岁，这时候的罗宾看上去是更加的美丽迷人，正直人生的黄金年龄段，其实不止是青雉看了会愣住，每一个男性看到这时的罗宾的变化都会被迷住的。那么为什么青雉会愣那么久呢？因为青雉没有见过罗宾青春期时的样子，当年放她走的时候她才八岁，再次见面的时候都过了二十年左右的时间了，青雉也没有想到自己当初的善意之举所放过的小女孩如今已长成亭亭玉立的样子吧。要说青雉是不是喜欢罗宾呢？当然是喜欢，我们也喜欢啊，不过青雉对罗宾是哥哥对妹妹的喜欢哦，哈哈。更多精彩内容请关注ACG虫洞。（原创不易，侵权必究）', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10063', '0', '罗宾是这样变白的！', '众所周知，两年后的罗宾皮肤变白了。至于原因，我猜测是在革命家基地的滋养所致！小时候的罗宾比较黑长大后一样黑而现在不光是变白了还将果实开发的更加完美，比如巨大树这种东西，真是让人浮想联翩啊！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10064', '0', '弗兰奇他老爸就是罗杰手下的铜，铜后面就是铁，弗兰奇外号铁人！', '罗杰是金，雷利是银，贾巴是铜，弗兰奇这个铁人绝不是偶然！435集说了，弗兰奇原本就是海盗的儿子。这里乌索普，罗宾，弗兰奇等于都是新成员。罗宾是奥哈拉唯一幸存者，父母都死了。乌索普老爸在红发船上，罗杰把帽子传给红发。当初汤姆给罗杰造船，说不定就顺便收养了罗杰船上贾巴的儿子。贾巴目前应该在和之国！和之国没有什么索隆的身世，但是应该会揭秘弗兰奇的身世。631扉页这个斗篷男，应该就是贾巴，一看就是和之国的服侍！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10065', '0', '海贼王弗兰奇工匠科技实力，能否拯救迷失的大熊', '弗兰奇在草帽海贼团的身份就是一个船工，实力也很一般。但是弗兰奇是否只有工匠这么简单呢？弗兰奇能否可以认为是草帽海贼团的科学家？我们对科学家的理解可能就是搞科研的，就像文斯莫克家族，是搞人类的潜能基因的，这样的才是科学家。但是弗兰奇算不算的上是科学家呢？文斯莫克伽治以前是贝加庞克的同时，贝加庞克是最伟大的科学家，领先世界几百年，但是PX和平主义者就是贝加庞克制造出来的。两年前泡泡岛，草帽一伙被熊拍的分崩离析，而弗兰奇恰好被拍的地方就是贝加庞克的故乡。弗兰奇两年之间获得的成长也颇多，还实现了贝加庞克年轻时候的梦想，合成了瓦波金属，所以弗兰奇也可以说是一个科学家，创造发明能力也很强。弗兰奇的动手能力我们所有海迷应该都知道，想要造什么东西都是瞬间的事情，在水之都和冰山造万里阳光号也就花了几天时间。在两年后回船的时候，弗兰奇发现同为改造人的熊，竟然在守护着万里阳光好，这是熊和贝加庞克做的一个交易，熊答应失去自己的意识，但是条件就是保护万里阳光号。众所周知，草帽海贼团是有恩必报，有仇也必报的一个团队，所以草帽一伙要是知道现在熊沦为天龙人的坐骑的时候，肯定也会出手相助。然后帮熊回复意识这一块，最能帮助他的可能就是弗兰奇了，因为弗兰奇也是改造人，对改造人有所了解，并且还得到部分贝加庞克的真传，这些都有可能是让弗兰奇掌握高端科技救助熊的伏笔。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10066', '0', '为什么艾斯一定要死', '看过海贼王的同学都知道顶上战争，我个人认为顶上战争是《海贼王》开幕以来场面最宏大，情节最激烈的战役，没有之一。估计只有大结局能媲美了。在这场战争中，白胡子海贼团及其旗下的43个海贼团与海军十万精锐以及王下七武海联合军进行了激烈的交锋，但最后却以艾斯和白胡子的死亡收场。当时，我们很多对于白胡子和艾斯的死都伤心了好久，可你们想过没有为什么艾斯一定要死呢。对世界格局而言，顶上战争是新旧时代的转折点。在这场战争后，随着罗杰时代的最后一个大海贼白胡子的逝去，旧有的势力格局被打破，以“最恶的世代”为代表的新生势力则在迅速崛起，不管是海贼还是海军，面对的都是一个与顶上战争前截然不同的形势。更多隐藏的高手逐一出世，已经闯出名头的家伙则更上一层楼。从作者的角度，路飞注定要成为海贼王，而哥哥的身份会让艾斯和路飞的关系变得非常尴尬，即使艾斯说他的目的是要让白胡子称王，然后艾斯是罗杰的儿子，在海贼中拥有很高的声望，如果他不死，那最后海贼王就有可能是他了。从路飞的角度，从香波地群岛开始的反思，经历了一连串事件的深化，到艾斯死亡达到顶点，之前被熊拍飞时，路飞虽然意识到敌人不好对付，但他还是坚持要回香波地群岛和伙伴集合，只有当真正宝贵的人死在自己面前时，他才会真正明白自己和世界的差距，这种反思才有意义和力度。从艾斯的角度，很多人说他跑回去和赤犬放对简直是愚蠢，浪费了救人者的好意，但赤犬的话直接侮辱了白胡子的尊严，按艾斯的性格是不可能装听不到夹尾而逃的，再者，艾斯是罗杰的儿子，在他血液中就流淌着永不后退的意念。再加上为了救艾斯，可以说死伤无数，连老爹自己都搭了进去，就算其他人无所谓，艾斯也不可能心安理得，他一辈子都要活在害死老爹和众多朋友的负罪感中，还不如一死了之来得痛快，起码死前知道有那么多人爱着他，也算是了无遗憾了。所以艾斯必须要死，但他永远活在我们心中，活在他回头面对赤犬的那一瞬间。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10067', '0', '尾田为何要写死艾斯？如果艾斯没死？', '艾斯顶上战争之死，一直都是海贼迷们的一个争议。相信大家在看顶上之战艾斯为救路飞而死的时候都看湿了眼。我有一个朋友说：艾斯死后，她的世界再无海贼。那为什么会有这么一个结果呢？尾田为何坚持将艾斯写死？如果艾斯不死会怎样？我们先来说一下为什么尾田要将艾斯写死。答案有两点：第一，尾田将艾斯设计成为了黑胡子的垫脚石！为了能让黑胡子在顶上战争中顺利的崛起，而让艾斯成为顶上战争的导火索！第二，尾田让艾斯死是为了让路飞更快的崛起，闯入伟大新航路的路飞一路上都没遇到过太大的挫折，在顶上战争之前甚至都不会霸气！为了让路飞快速的提升自己的能力，以艾斯的死为代价让路飞化悲愤为力量来提升自己，乃至整个草帽海贼团的实力！艾斯如果没死呢？会是怎样一个世界格局？大家都知道，艾斯是白胡子海贼团二番队队长，实力强劲，且背景十分强大。如果当初艾斯被路飞救下。在顶上战争中，白胡子海贼团是极有可能全身而退的。白胡子不死，四皇将仍是原来的四皇，黑胡子不可能借着这个机会上位。那么黑胡子的下场就会很明显：被四皇追杀！对，你没看错，是被四皇追杀！不是仅仅被白胡子追杀！为什么呢？因为他的野心！他想成为四皇之一，并且有暗暗果实助力。这么一个强劲的对手，四皇是不允许这样一个人存在，来撼动他们的地位，抢夺他们的地盘！而如果没有经历这种变故，路飞会是怎样的呢？没有那两年的经历，草帽海贼团里的成员可能会被新世界的海贼虐的体无完肤！毕竟路飞等人一路走来成长的太快了。没有一个真正锻炼的机会。很可能像超新星中的基德一样，被四皇之一瞬秒！所以艾斯的死，是尾田不得以而为之的，你们觉得呢？我是动漫00新发现，关注我看更多海贼新发现！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10068', '0', '海贼王的女人——波雅汉库克', '说真的很希望汉库克之后是路飞的妻子。处于害羞的汉库克很美啊想看更多有趣的内容，请关注微信公众号：zazhangyuedu', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10069', '0', '海贼王女帝的左右护法，与波雅汉库克义结金兰的两姐妹！', '海贼王女帝向来是深入简出，不过却少不了两大护法的陪伴！虽然其麾下海贼团可以说是能人辈出，而且几乎各个都是身怀绝技的那种人物，但是女帝依旧最重视这两位姐妹，她们才是女帝最珍视的同伴。女儿岛上的三姐妹，波雅汉库克是女帝，是首领其他人不管地位多高，大家几乎都没名字，全是花的名字就代替了，岛上数万战士，也不知道有没有这么多品类的鲜花呢？我估计没有吧她们常年都跟在女帝的身边，是其最好的伙伴，贴身护卫三人分别都是能力者，可惜这果实能力，当真是来之不易啊她们曾经被抓走，成为了天龙的奴隶，背上有相应的奴隶印记！！这是她们最大的秘密，至今无法消除，被印上天翔龙之刻蹄的人，会被认定为是天龙人的私有财产，所以拥有这种纹身，实际上是相当危险地如果不小心被世界政府发现，很可能出动大家抓她们回家到时候就有故事看了很多人觉得她们的果实能力重复了，其实不是重复，一个是蟒蛇能力者，一个是眼镜王蛇的能力者！！当然了，从外表是看不到任何区别地。在对战路飞的战斗之中，她们都没来得及发挥实力，就被光环给压制了…如果后面有剧情需要，路飞肯定是打不过她们两人地！两人超大的身躯加山果实能力的加持，怎么也是4、5亿量级的水准虽然看起来有点蠢笨+萌萌哒，实力战斗力真的不弱，希望以后还有发挥的余地吧。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10070', '0', '明天我要嫁给你啦的波雅·汉库克手办', '眼镜厂造形物语系列，海贼王女帝波雅・汉库克，景品，2018年4月24日出荷。具体手办详情在图下方。手办点评：明天我要嫁给你啦角色：波雅·汉库克原作：海贼王价格：95元（Hpoi自营）尺寸：H=100mm出荷时间：2018年4月24日厂商：眼镜厂', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10071', '0', '《海贼王》女帝波雅汉库克，集万千宠爱于一身的美人', 'Hello，漫迷们，大家好，【动漫你我他】又和大家见面了！这是【动漫人物志】专题的第五期，今天我们要说的主角是《海贼王》动漫中号称世界第一的绝世美女女帝波雅汉库克，一个集万千宠爱于一身的美人！说起《海贼王》动漫，大家都有一个痛处，那就是我们的娜美和罗宾女神惨遭桃之助毒手，于是乎大家都知道那个梗 - “桃之助必须死”！还好，《海贼王》里还有一个女神至今没被桃之助染指，那就是我们的傲娇女帝波雅汉库克！女帝波雅汉库克贵为王下七武海之一，同时也是素有“女儿国”之称的战斗民族九蛇国的皇帝，拥有素有王的资质之称的霸王色霸气，高贵身份以及天性憎恶男人让她生性高傲，自恋，“做了什么都会被原谅”，只因她太美，直到我们的主人公路飞的出现，才打破了这个定律！女帝波雅汉库克拥有一头乌黑的长发，深蓝色的双眸，佩戴一对蛇形耳环，身材窈窕高挑，平时多是穿着开叉裙和白色披风，大长腿吸睛，着实一副美人胚子！幼年时期，女帝与她的两个妹妹被当成奴隶贩卖给世界贵族，因此后背被烙下了奴隶的印记-“飞龙之蹄”。后来费舍尔泰格来到圣地玛丽乔亚解放了受苦的奴隶，女帝得以获救，在冥王雷利和古罗莉欧桑的帮助下重回九蛇国，隐瞒自己过去成为九蛇国的皇帝，也因此女帝从小对天龙人，也就是世界贵族深恶痛绝！后来，路飞被暴君熊拍到九蛇岛，路飞误入澡池并看到了女帝波雅汉库克后背的奴隶印记，因此女帝大怒要处死他。然而路飞主角光环加身，在女帝面前秀了一把霸王色霸气，并且帮助掩盖了女帝小妹的奴隶印记。女帝深深地被路飞的人格魅力所吸引，将不堪回首的往事告诉了路飞，从此在深爱路飞的道路上越走越远！女帝圈粉的原因不仅在于她那无人可及的美貌，同时她身上身为小女人的可爱一面也让很多动漫粉丝折服！她在感情方面单纯且专一，经常对路飞的一些言行产生误解，从而陷入自我的妄想之中，可以说跟“白痴”路飞是绝配！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10072', '0', '波雅汉库克 COS', '“无论妾身做了什么都会被原谅，因为妾身实在是太美了！\"——波雅. 汉库克 Boa Hancock #海贼王##波雅汉库克# 出境：原Po后期：原Po2016.10 London MCM 配图：更多海贼资讯下载海贼头条', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10073', '0', '波雅汉库克的魅力征服一切——这样的女帝谁不喜欢！', '波雅汉库克是海贼中最具女性魅力的角色！她的口头禅就是：“无论妾身做了什么都会被原谅，因为妾身实在是太美了！\"而且，最关键的是，这不是吹嘘，这都是真的！目前所知，确实没人经得住女帝的魅力，除了路飞。。。所以，在女帝的日常里，踢小动物，踢人，反正来讲，不管什么错误，都会被原谅的！起初以为是因为是在亚马逊·百合的战斗民族九蛇女儿国的国王，所以才会对女性也能产生作用，以为是因为这些女的没见过世面，可是谁知，在剧场版中，路飞试炼时候的情况，让我发现我的理解居然错了！在女帝露出害怕，委屈妩媚和卖萌的表情后，身为对手的老女人，年近花甲、六根清净的人，居然承受不住诱惑，竟然直接败下阵来！由此可见，我们大女帝的魅力真是征服一切啊！想一想也对，女帝实在太出众了！世界第一的绝世美女还拥有百万人之中仅有一个的霸王色霸气！路飞还在等什么？快来完婚吧！最后，我就想问，这样的女性，谁不喜欢？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10074', '0', '蛇姬女帝--七武海之波雅·汉库克点评', '(欢迎加小编微信 4415846，一起交流二次元动漫游戏!)波雅·汉库克，拥有霸王色霸气的海贼世界第一美女，王下七武海中唯一的女性，亚马逊·百合的皇帝和九蛇海贼团船长，有着众所周知的无限风光和鲜为人知的悲惨过往。首度出场时，有着强悍无比的气场和男女通吃的魅力，随后在与路飞误打误撞的交道中对路飞萌生爱意，每每与路飞相遇时便会显现出娇羞的小女儿态，可见这位“海贼女帝”内心中温柔细腻的一面。评语：表面的风光无法抹去背后的痛苦，无情的外表不能掩饰悸动的内心。从奴隶到皇帝汉库克姐妹幼年被人贩子拐卖到天龙人手下做奴隶，度过了一段不堪回首的悲惨岁月。碍于信息的缺少，我们无法得知女帝等人究竟经历了怎样的痛苦生活，但有一个细节或许可以窥其一二：费舍尔·泰格在旅行中被天龙人抓走做奴隶，只身逃回鱼人岛面见国王和王妃时，未语而泪流，令乙姬王妃大为震撼而未予阻止其大闹玛丽乔亚。泰格如此有泪不轻弹的丈夫，竟在天龙人的折磨之下也到了伤心处，那么彼时尚且年幼、在玛丽乔亚的磨难日子比泰格更久的汉库克姐妹的境遇可想而知。悲惨的奴隶经历除了给女帝一段不忍追忆的噩梦外，还有一件重要的事情是恶魔果实能力的得到。天龙人抱着取乐的心态给汉库克姐妹吃下了恶魔果实，他们并没有想到这成为了后来戈尔工姐妹们的资本。女帝等人的这一经历，和金庸小说《天龙八部》中的虚竹有些相似。虚竹的犯戒过程和女帝的奴隶生涯同样是不断做着违背本意的痛苦之事，并因此使原本平淡的人生变得波澜起伏，而人生轨迹到达顶点的基础又是在痛苦的过程中建立。 虚竹走向了和自己的意愿相反的一个极端，落得永远无法回少林寺的终身遗憾。女帝也是如此，虽然被泰格、古罗莉欧萨、雷利等人相救回到九蛇，并靠着美貌、果实能力和隐瞒过去成为了亚马逊·百合的皇帝，开启了一段叱咤风云的海上岁月，通过一次远征威震海上，最终成为七武海中唯一的女性。女帝纵然风光如此，昔日的痛苦却深深印于心中无法抹去，对路飞提及当年之事时，已经贵为皇帝、七武海的汉库克也不禁失声痛哭，桑达索尼亚更是哭晕在地。没有经历奴隶时代的痛苦，即便是拥有霸王色霸气的汉库克恐怕也无法在妙龄便成为七武海女帝，逞论横行海上的风光无限了。可背后和心中的双重烙印又时时刻刻让贵为皇帝的汉库克恐惧不已。在人前，她是高高在上、不可仰视的女帝，但当她一丝不挂入浴之时，想到她之所以要在重重森严的警戒中入浴，便不能避免地回忆起做奴隶的暗无天日的日子，而背后无法去处的奴隶烙印又让她对秘密一旦泄露、自己必会失去当前种种的富贵和地位而提心吊胆。曾为奴隶的秘密是女帝最大的软肋。一旦这一秘密为人所知，女帝不仅要失去拥有的一切、在亚马逊·百合再无立足之地，甚至会被天龙人派遣海军抓捕、重新掉入万劫不复的奴隶生活之中。因此，脱离奴隶身份的女帝有时甚至会有些神经过敏，对知晓她过去的古罗莉欧萨的意见颇为抵触，不愿再度踏足“圣地”，并在自己沐浴时设下极为夸张的警戒（这种警戒的实际效应恐怕相当有限，否则路飞也不会轻易掉入浴池中，因此女帝如此做法更多的成分也许是出于自己不会被人看见背后的心理暗示作用）。还好，女帝遇到了路飞。似无情却有情改变女帝的重大转折是她与路飞的相识。在此之前，女帝没有面对过去的勇气，她只能通过不断的心理暗示和故作高冷掩饰不安，做出一种看似无情的高高在上、盛气凌人的姿态，而她的甜甜果实能力、霸王色霸气和美貌又使得这一切看似十分自然，并让女帝的裙下之臣们欣然接受甚至趋之若鹜。而这种表面上的无情在三名战士被变作石头的时候到达顶点。起因在于，路飞进入亚马逊·百合犯了男人禁足的岛上传统忌讳，随即更是误打误撞地看到了女帝背后的印记，令女帝深为恐惧。在路飞即将被处死之际，对路飞有所了解、又不明真相的护国战士玛格丽特挺身而出维护路飞，宁可独立承担相对后果。玛格丽特的勇气堪比大事件中的克比，甚至在某种程度上胜过克比，毕竟赤犬当时在海军的地位远不能和女帝在亚马逊·百合的地位相比。女帝也毫不迟疑地将玛格丽特和为她求情的香豌豆、雅菲兰朵拉一同石化。九蛇族女人们初期感到惊愕，但随即在对女帝习以为常的倾慕中自然地遗忘了玛格丽特等人。但路飞不会忘记，他既对玛格丽特的恩情铭感于心，又对女帝的行为无比愤怒。因此，路飞在一片反对声的浪潮中向痛斥女帝“混蛋”。女帝第一次见到路飞这样对自己的美貌无动于衷、对自己在女儿岛上的所为敢唱反调的人，再加上路飞知晓了自己背后的秘密，女帝不禁惊怒交加，命令两个妹妹对路飞处以“武武”极刑。对于身为七武海的女帝而言，此时连霸气为何物也不知、揍飞天龙人的消息尚未传到女儿岛的路飞只能算个无名小卒，两个妹妹击败他本应轻而易举。但路飞初期虽然陷入苦战，却没有半点屈服之意，桑达索尼亚用蛇尾卷起玛格丽特威慑路飞时，更使路飞的霸王色霸气乍然觉醒。最终，行刑失败，路飞胜出，桑达索尼亚背后衣服意外被烧，让女帝悚然一惊。关键时刻，路飞挡在了桑达索尼亚背后，守住了戈耳工姐妹的秘密。清场之后，女帝又一次做了看似薄情的试探，让路飞在离开女儿岛和解除玛格丽特等人的石化中做出选择。路飞没有经过多少迟疑，选择了解除玛格丽特等人的石化。深受触动的女帝将路飞召入九蛇城，将背后的印记和曾经的秘密对路飞和盘托出。女帝以为路飞会因此瞧不起她，但路飞却无动于衷。更让女帝震惊的是，曾经将她们姐妹百般欺凌的天龙人竟然被路飞一拳揍飞。路飞这样的胸襟气度、胆识魄力让女帝第一次对男人有了异样的感觉。路飞得到了盛情款待和女帝的信任，在女儿岛的宴会上大大快活了一番。但另一边，由于舍不得路飞就此离开，女帝却在情窦初开后直接害了相思病。为了能够和路飞同行，女帝接受了世界政府的征兆，破天荒地前往昔日伤心之地，这让古罗莉欧萨和两个妹妹都感到不可思议。此时在对路飞的单恋中愈陷愈深的女帝，与当初那个高傲冰冷的形象已经判若云泥。名女帝实女人《海贼王》是一部热血漫画，恋爱从来就不是热血漫画的主旋律，但往往又是不可缺少的。例如尾田的偶像鸟山明的经典作品《龙珠》中，主角孙悟空和大多同伴也正常的爱情和家庭生活；《海贼王》漫画中，海贼王罗杰是娶妻生子的人，卡普、龙也同样如此（而且路飞的母亲是何身份可能会是一条重要的线索）。因此，神经大条的路飞如果能够收获爱情，其实也是情理之中的事情。除女帝之外，九蛇护国战士玛格丽特也是一个对路飞有着心底情感的人（这与《火影》中雏田对', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10075', '0', '兼具美貌与实力 女帝波雅汉库克招式解析', '如果美貌是时间，那么汉库克就是永恒！作为一个31岁的美女，汉库克拥有让全世界的女人都嫉妒的容貌，她被人们称为世界第一的绝世美女， “无论妾身做了什么都会被原谅，因为妾身实在是太美了！\"这句话如果从其他人的口中说出来，一定让人很反感，但是从她的口中说出来却意外的有被萌到。（恩？或许也可以说是被电到）汉库克不仅拥有美貌，更是拥有百万人之中只有一人才有的“霸王色霸气”。她拥有可以迷倒众人，甚至是让人石化的能力。美貌与实力并存的她让人感觉遥不可及，但她的高傲，自恋，坚强却是最接近人们的真情实感。9月2日是咱们女帝的生日，今天我们除了感叹她的美貌，还要和大家分享下汉库克的招式。果实能力——超人系甜甜果实招式解析——迷恋甘风迷恋甘风是汉库克经常使用的招式，这招也是极美的，汉库克出招时双手会摆出爱心的姿势，然后放出光线，然后被汉库克的美色所吸引的人就中招咯，瞬间石化！！手枪之吻手指轻碰嘴唇，造出一个桃色心形，然后将其当成吻枪子弹。芳香脚能在踢中的瞬间将对手的部分身体石化并且粉碎。俘虏之箭俘虏之箭的效果和芳香脚差不多， 出招时用粉红色的箭头射中的目标，使出俘虏之箭这一招，无论是否对女帝动心都会被石化。出现了!那个极度藐视众生的姿势!', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10076', '0', '看了这些动漫！十分的担心女帝波雅汉库克！', '动漫都有长篇短篇一说，有的时候弹指一挥间就好多年后，有的时候过了好久也才转瞬。最典型的代表就是名侦探柯南！我们都过了多少年了，柯南才二年级...还好时间流逝的慢，要不然小兰真的没办法再等下去了，岁月不饶人呀~看看到这些动漫后，也开始因因担心起了女帝波雅汉库克了~火影完结后，继承篇章博人传中的大家都已经老了，而让人最吃惊的形象就是红豆和水影了~红豆俨然吃撑了一个胖大妈的形象，再不像年轻时候的风姿卓越了。同样水月不饶人，水影这种大美女脸上也涨了皱纹，美人迟暮呀~人终结抵不过岁月。龙珠中，曾经的美女琪琪，在嫁给悟空后，相夫教子。也再不是那个可爱的小女了，当初多少人在天下第一武道会，没有认出这是牛魔王的女儿，只是觉得这个小姑娘好漂亮。但是在后续的剧情中，已经变成典型的教育型妈妈，非常唠叨。形象上也是变化很大！银魂中的登势婆婆，如果你没有见过她年轻的样子，你在看到她的样貌后，必然觉得是个尖酸刻薄的老太婆。不过别看她这样，看过银魂都知道，她是个面冷心热的老好人。这是经过岁月洗礼后的容貌，要知道她年轻时候，是多么善解人意的美女呀！最后，海贼王但愿时间过得慢点，路飞能早日成为海贼王！要不然女帝老了以后的样子，不知道会不会就是咋婆婆的样子...简直不敢想，要知道她当年也是同样的女帝，而且为了爱人离开过...韶华易逝，红颜迟暮，但青春在心中永驻！记忆里活着的始终是那个年轻人。女帝永远是最美的对不对？海米们！期待您的讨论，更希望得到您的点赞与关注，谢谢！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10077', '0', '海贼王：女帝永远不会知道路飞的5个秘密，知道一件就会杀掉一人', '海贼王中女帝波雅汉库克是世界公认的第一绝世美女，高傲，自恋，藐视群雄，直到遇到路飞后便不可遏止的爱上了他，总是会幻想与路飞在一起的时刻，对他身边的人或事也是十分的关注，尤其是对路飞身边的女人，女帝和路飞想当初女帝在护送路飞去海底大监狱时提到了娜美和罗宾，女帝就担心她们会跟她抢路飞。而这只是路飞的冰山一角而已，路飞的秘密有太多，其中有5个女帝是永远不会知道的。因为后果太严重。1，路娜之间的关系娜美坐在哪了？娜美骑路飞路飞和娜美之间的关系很微妙，路飞的肉只给娜美吃过，帽子也只给娜美戴过，身为船长财宝都是由娜美掌管着。路飞虽然经常被娜美揍的鼻青脸肿，可路飞也表现的心甘情愿。而且娜美与路飞之间有太多的亲密动作，像娜美可以骑在路飞的脖子上，又可以坐在路飞的重要部位上。这些事情是女帝幻想都不敢想的。女帝要是知道娜美对路飞做了这么过分的事情，娜美必死。2，罗宾要让路飞对她负责罗宾在阿拉巴斯坦篇结束时，罗宾上了路飞的船，当时对路飞说了要对她负责的话。说她受到了难以忍受的对待，要让要负责。当时山治听了之后就表现出了不理智，要是换做女帝，要是不多想才怪，不光是女帝，如果换做别的女人，难免都会起杀心，要么杀了对象，要么杀了对方。3，路飞在女儿岛上的遭遇路飞被洗澡路飞被看光路飞在被熊拍到女儿岛时，受到了众美女的特别待遇。先是给他洗澡，拔蘑菇，后来又在欣赏路飞的黄金蛋蛋。这样的事情换在哪个女人都无法接受，而且她们还都是女帝的子民，要是让女帝知道了这件事情，女儿国就有面临灭国的危险了。4，路飞的初吻被夺走了雷玖给路飞吸毒在前往大妈领地的途中，路飞因为吃了虎皮鱼而深重剧毒，这种毒是乔巴也无法解的，幸好遇到了雷玖她们，在雷玖的帮助下路飞才得以活命。不过雷玖的解毒方法，直接性的夺走了路飞的初吻。5，路飞与蕾贝卡的不雅举动路飞和蕾贝卡路飞与蕾贝卡这举动，要是让女帝看见，难免会私下里对蕾贝卡起杀心。还有蕾贝卡也是，已经是这种体位了，还对路飞说想怎样就怎样？女帝对路飞的痴迷程度已经到了无药可救的地步，而且为了路飞女帝可以随时献出自己的生命，无论什么时候什么状况之下，只有路飞需要女帝随时可以打头阵。就像大事件当中，暴打斯摩格，狠踢和平主义者，只要对路飞图谋不轨，必杀之。这几个秘密都已经超过了女帝的底线，一旦让她知道，女帝会悄悄的把这些人都给抹杀掉。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10078', '0', '迷一样的男人，鹰眼米霍克！', '鹰眼米霍克，背后一把大剑是他的标志性武器，一人一船独闯新世界却能成为王下七武海之一！为什么说鹰眼米霍克是迷一样的男人呢？米霍克，实力未知，背景未知，虽然在剧中只出现过几次，但是每一次，米霍克的出现都让众人眼前一亮！第一次，海上餐厅之战，因“打发时间”独自一人将克里克海贼团50只舰队从新航路追杀回东海，并与路飞等人相见。也是路飞等人第一次遇到完全无法面对的敌人！第二次，受世界政府召唤，于海军本部马林福多现身，本不愿意参战的米霍克在看见白胡子后决心与其一战试试自己与这个“世界最强男人”的差距，但被白胡子海贼团三番队队长钻石乔兹挡下。对于鹰眼米霍克，只知道是世界第一大剑豪，王下七武海之一。那么为什么称鹰眼为迷一样的男人呢？因为鹰眼每次的出现，都让人眼前一惊！但他的实力身世，都是一团迷雾一样。独自一人却能位居王下七武海之一！大家都知道，在这个大海贼时代。要想成长起来，只有依靠一个海贼团才能存活，特别是新世界的海贼团，都是巨型海贼团的存在，才能巩固其地位！而鹰眼米霍克却独自一人闯荡在新世界。要知道，就连四皇，手下都有很多人作为他的左右手才能巩固强大自己的地位和实力。那么这就有一个猜测。有人说，鹰眼实力只能屈居王下七武海，而我认为，鹰眼实力堪比四皇！独自一人就能成为七武海，如果有实力强大的海贼团，未必不能与四皇一争高下！海贼王中的七武海，每个人都有其自身身世经历，而唯独鹰眼没有介绍过，为什么呢？很有可能鹰眼身世里面藏着一个巨大的秘密。有人说他是佩罗娜的父亲。也有人说他与索隆的父辈有关系。一切，都是如迷一样的故事！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10079', '0', '鹰眼米霍克10亿赏金时代下，他究竟是怎样的存在~？', '米霍克总是给人神秘的感觉当初就是从烟雾中缓缓而来，乘坐一艘动力不明的小船，现在更是住在古堡，没有一个同伴，甚至连自己的海贼团都没有~！几乎所有海迷都认准了其实力七武海第一，然后从斩杀赏金1600万贝利的东海霸主克里克到现在10亿赏金，米霍克确实一直实力深不见底，自己每天摇摇晃晃到处玩，也不知道要干点什么，简直就是海贼中的奇葩存在~！有关实力我觉得米霍克确实直逼四皇但凡用剑的实力都应该在其之下，不然他不会自称世界第一因为日本武士道精神更加注重荣誉，虽然不太喜欢他们，但是这件事是事实，如果用荣誉换第一，还不如杀了他，所以米霍克实力不会低如果有强过他的剑客，他打不过，早就羞愤自杀了~！但是米霍克这样的独行侠，威胁也不大就是明哥海贼团，他都不一定能打的过，毕竟人数差距悬殊，能力者又是一个巨大的变数，指不定一种什么特殊的果实能力，就让这些独行侠吃个大亏，说不定命都保不住~！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10080', '0', '别在质疑鹰眼米霍克，他用战绩证明了自己的强大', '鹰眼米霍克的实力一直备受争议,但是他的战绩无法抹去.鹰眼VS邦迪·瓦尔德(见于《3D2Y跨越艾斯之死》,他的莫莫(100倍炮)被鹰眼斩成两半,最后体力不支倒下了)鹰眼VS人类模仿者(就是把索隆打得半死那些狒狒,鹰眼一出现直接吓跑了它们) .鹰眼VSMr.1 达兹·波尼斯(老沙工作室的头号小弟,被鹰眼一刀将其砍成重伤) .鹰眼VS小丑巴基(一瞬间被鹰眼看成好几段,还好能力变态没死).鹰眼VS甚平(被鹰眼劈了一刀,最后掉进水里逃过一劫).鹰眼VS索隆(被鹰眼的小刀蹂躏,最后用十字架刀把索隆砍成重伤).鹰眼VS红发(作为四皇之一的红发,实力强劲,鹰眼最后和他不分胜负,并且他们的战斗得到了当时号称最强男人的白胡子称赞,可见鹰眼的实力有多强.)总之，鹰眼绝对是七武海里面最强的，在新世界是跟红发香克斯、白胡子几乎同一层次的男人。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10081', '0', '鹰眼米霍克太过强大，这一点反而造成了他现在难以出场的窘境。', '顶上战争之后，鹰眼就回到了自己的住处——克拉伊咖那岛，并在那里收索隆为徒。两年之后，索隆出海，鹰眼就再无消息，除了偶尔在扉页中露一两次脸之外，几乎不见踪影。为什么呢？为什么一个人气超高、实力超强、长得帅、立场中立的角色会被雪藏？按道理他应该是很好利用的一个配角，能够创作出不少精彩剧情啊？其实，未必是尾田不愿意再给鹰眼安排剧情，而是因为鹰眼太强了，没法给他安排剧情。我们要知道，鹰眼现在的头衔是“世界第一大剑豪”，战绩至今未尝一败。回顾他以前的出手，要不就是主动攻击被人全力挡下，要不就是秒杀小喽啰。这都是为了渲染他实力超强的手段。可是剧情发展到了现在，剧情中的小喽啰已经不多了，随便揪出来一个都是8.6亿的三将星。在这种情况下，如果鹰眼要出场，你说是让他胜还是不让他胜？让他胜了，好，鹰眼的不败纪录是保住了，可对手本非喽啰，这样的失败几乎意味着对方实力体系的崩坏，也就是说，跟鹰眼为敌的组织，都会因为这一败而变弱，因为你既然败给了鹰眼，后面就没有理由再描述地多么多么强。让他败？这个是不可能的，鹰眼唯一的战败是可以预见的，就是与索隆一战。这一战肯定是要发生在最终结局之前的，所以，鹰眼在结局到来之前是有一张护身符的，因为他的头衔只能被索隆夺走，所以在索隆出手前，鹰眼是不败的。所以矛盾就这么出现了。鹰眼至今为止都没出场，不是因为尾田不想让他出场，而是因为他出场所引发的剧情波动，远大于他带来的粉丝关注效益。归根结底，还是他太强了。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10082', '0', '鹰眼米霍克实力大解析！他或许已经超越海军本部大将！', '大家好，我是小明。对于海米们来说，鹰眼米霍克绝不是个陌生的名字。作为王下七武海之一，被誉为“世界第一大剑豪”的他甚至从未在战斗中使出过全力。那么今天，小明就带大家根据鹰眼的对战记录及当时的状况来分析一波，他的实力到底在什么层次呢？对战记录VS 红发香克斯被人们称之为传说中的决斗一直流传下来，白胡子对其称之为“宛如昨天发生的一般”。胜负未知VS 克里克海贼团将海贼团旗下的50只舰队以打发时间的理由全部歼灭。胜利VS 罗罗诺亚·索隆以“黑刀”夜在索隆的胸口砍了一道深深地剑痕，最后放了他一马，并对索隆寄予非常大的希望。胜利VS 克利克对其没有兴趣，转身离开。没有结果VS 爱德华·纽盖特想测试自己与世界最强男人的距离而对白胡子发出了斩击，但被钻石乔兹挡住。没有结果VS 钻石乔兹斩向白胡子的斩击被乔兹挡住，被阻止攻击白胡子。没有结果VS 新世界海贼全部秒杀。胜利VS 蒙奇·D·路飞想要看看路飞的资质到底怎样，在顶上战争追击路飞，但被多人阻止。没有结果VS甚平一刀将甚平秒杀，将其砍入水中。（TV原创）胜利VS 小丑巴基瞬间将其斩成数段，后其依靠自己的能力复原。胜利VS 花剑比斯塔双方交手一段时间不分胜负，后因和平主义者的出现而在双方同意下暂时停战。没有结果VS Mr.1 达兹·波尼斯斩向路飞的斩击被其挡住，后近身一刀将其砍成重伤。胜利VS 克洛克达尔攻击路飞时克洛克达尔替路飞挡了一刀，之后战斗没有结果。没有结果VS 人类模仿者仅仅只是登场就把所有的狒狒吓跑。胜利VS 邦迪·瓦尔德将瓦尔德的莫莫「100倍炮」斩成两半，后瓦尔德因为体力不支而倒下了。胜利首先我们可以确定，在与红发的对战中，鹰眼一定使出了全力，不然不会被人称作传说中的决斗。而当时的红发很可能还没有达到四皇的高度，我们姑且将红发当时的实力定位大将。其次，在顶上战争时，我们先不提鹰眼是否能一刀秒掉甚平。在他与花剑比斯塔的战斗中，两人是不分上下的。但在这里我们要注意一点，两人的不分上下其实是含着不一样的心境，鹰眼并没有展现出完全的实力，是抱着一个打发时间和见证历史的心态来到了海军本部。而花剑比斯塔就不同了，他是非常焦急的，艾斯很可能在下一秒就死去，所以一定是使出了全力来攻击鹰眼。那么比斯塔是什么实力呢？身为白胡子的五番队队长，他曾在顶上战争中与小马哥合力击伤过赤犬，并且在后来与其他队长们合力阻止赤犬追杀路飞并成功。我们要明白一点，阻止人离开和与人战斗是两种概念，从某种意义上说，阻止赤犬离开要比击败赤犬难得多。而因为小马哥确实稍强一点，所以我们大概可以判断出，花剑比斯塔大概就是超越准将但没有达到大将的实力。而对于一位接近大将的人来说，鹰眼的实力就显得更加恐怖了。他并没有什么目的和目标，所以比起比斯塔来说心中缺乏拼命的动力。这种情况下的营养可以和玩命的比斯塔打个平手，再加上与当时的红发对战时实力非常接近，而且又过了这么多年，鹰眼一定还有非常大的长进。算是小明的大胆预言吧，在我看来，鹰眼应该已经达到大将实力甚至已经超越了大将实力。如果你有什么意见或建议，欢迎留言与我讨论~', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10083', '0', '鹰眼米霍克就是个老流氓，一出招就自带武装色霸气，对手很无语', '在克力架VS路飞一战中，我们发现了一个细节：那就是克力架非常谨慎的在使用自己的武装色霸气。路飞和克力架的交手，两人过了很多招，大多数情况下，克力架都是用“没有附上武装色霸气”的剑跟路飞打，只有在机会特别好时，才把霸气附上，然后给以路飞重创。也就是说，黑刀，对于一般剑客来讲，是一种有限的超强攻击模式。在索隆修炼时，鹰眼曾给他描述过：所有的刀剑，都可以成为黑刀。道理我们也很好理解，每个人的武装色霸气强度不一样，数量也不一样，有限的资源必定会选择在关键时刻使用，所以索隆、克力架等人平时不上霸气，都用名刀来作为黑刀的辅助，关键时刻再附上霸气...这样的话，鹰眼就比较特殊了，因为他就是个老流氓！我们知道，鹰眼手上的刀是无上大快刀之一，是最强的刀：大黑刀·夜！这把刀的颜色就是黑色，平时就跟附上了武装色霸气的效果是一样的....所以鹰眼这个老流氓，一出招就是使用着“附有武装色霸气”的刀，关键时刻再补上自己的武装色霸气...想不成为世界第一剑豪，也难！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10084', '0', '路飞先一步登基？别急，小丑巴基离“四皇”只差四步！', '哈喽，大家好，我是不爱萝卜和青菜、只爱樱桃糊一脸的兔子和樱桃放映室。最近刚刚听说路飞当上“五皇”了？听到这个消息的我实在是震惊不已，连路飞都带着主角光环荣登五皇，那么我海贼最气最高峰“小丑巴基”陛下居然还只是个王下七武海？难道你们真的以为“小丑巴基”先生只是个搞笑派而已吗？开玩笑，其实小丑巴基距离“四皇”其实只差四步而已！那么从果实开始说：1.果实能力细化。小丑巴基的果实能力大家早都知道了：超人系：四分五裂果实可以把自己分裂为很多小块，身体可随意块状分开或弹射（分裂时脚必须着地，能力范围为以脚为半径200公尺，超出200公尺也不会死，但会无法控制。“四分五裂果实”可以把自己的身体分裂为很多小块，重点是可以随意分开。那么，假设巴基经过辛苦的锻炼，控制达到“入微”层次，把身体的某一部分锻炼到可以分裂成细胞那么小呢？战场突然出现一个环绕巴基200米范围内充斥着死亡粉尘的“巴基粉碎风暴”，通过敌人的呼吸、毛孔进入体内，然后在内脏部分突然聚合！只要简单的在心脏聚合出一只手，那么轻轻一攥，什么敌人都得跪下，比罗的手术果实还强悍。想象一下，“方圆百米，皆我领地；一声号令，人畜皆灭”2.果实范围提升。不知道大家还记得不记得路飞的小迷弟“巴托洛米奥”，对他能力介绍是：“借着摆出Commons fingers的手势，让自身面前所处的区域张开一层透明的正方形防护屏障，保护自己不受任何外力攻击，缺点是面积有上限。但是这位仁兄把技能给改良了，屏障可以是正方形，长方形，甚至改良成条形，换句话说，完全可以改良成一厘米宽，几百米甚至几公里长的屏障带。好了，那巴基的“能力范围为以脚为半径200公尺”是不是也可以这样挖掘，调整一下操控范围，最次也可以做到单向延展范围400公尺。那这就不是一个随时随地的200公尺半径的球形“死亡粉尘”，而是延展为可调整的定向“死亡粉尘带”，能力值是不是又飙升一倍？3. 果实觉醒。参考多佛朗明哥的“线线果实”和夏洛特·卡塔库栗的“糯糯果实”觉醒，都是将周边同化为自己果实效果。那么巴基也是有样学样，四分五裂果实的“觉醒”大概率是将接触到的物体按照意愿分裂为不同份数。结合之前的“巴基粉碎风暴”，那句话“分分钟将你打成粉末”可就不是随便说说的了呦~霸气提升海贼王的文中其实已经隐约给出了答案。巴基的霸气方向是“见闻色”霸气，而且已经多次帮助自己躲开斩击，甚至现在已经可以达到躲避鹰眼的斩击程度。那么只要巴基持续修炼“见闻色”霸气，哪怕是达到卡塔库栗三分之一的水平，可以略微预测到敌人攻击方式的话，完全可以提前分裂化躲避，哪怕是武装色霸气包裹着拳头打来，也可以提前分裂成粉末随风飘散。“我即万物，万物即我”的境界，可不是谁都能达到的。综上，巴基攻有果实，防有霸气，攻守兼备，无人近身。“四皇”宝座还不唾手可得？以上。最后，我是不爱萝卜和青菜的兔子和樱桃放映室，我们专注播放最优质的影视动漫资源，欢迎关注呦~', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10085', '0', '海贼王907：我们都低估了史提利王，他将是另一个版本的小丑巴', '如果说在世界会议篇出镜率最高的角色，答案将是出人意料的。因为现在剧情属于世界会议的铺垫篇章，尾田一直在为世界会议进行铺垫，很多角色都仅仅是露个脸，但是有个人却几乎在每话中都有露脸，这个人就是被称为“酒囊饭袋”的哥亚王国国王史提利。史提利王因为其阴暗的性格以及进水的大脑成功的吸引了很多的仇恨，但是仔细品读下来，我们发现这位史提利王并不简单。首先就是其篡夺哥亚王国国王的过程，根据摩根斯交代，史提利先是通过联姻进入哥亚王国最高阶层，接下来通过暗杀国王以及国王继承人的血腥手段顺理成章的成为新的继承人。这个过程看似波澜不惊，但是却有一个关键人物，那就是史提利的妻子，哥亚王国的王妃，史提利的行为属于杀兄弑父，但是他能稳住哥亚王妃，这本身就是其阴暗手段的体现。而史提利却还有着更大的目标，那就是成为天龙人甚至是成为这个世界唯一的王，并且他也随时在为着自己的目标进行着谋划。比如脑残般的以君臣的关系要求卡普为他介绍贵族朋友，尽管这种想法有些过于天马行空，但是史提利就是用这种办法篡夺的哥亚王国，他这种脑残的计划并不是没有成功的可能性。而在代表着世界最高权力寓意的虚空王座面前，史提利执着的想要坐上去，其野心昭然若揭。按理说这样的一个角色注定只会是流星，但是尾田往往会创作出出人意料的剧情，比如被封神的小丑巴基。而作为极其邪恶的一方，史提利王很可能就是另一个版本的小丑巴基。尽管没有强大的实力、没有过硬的势力，但是他们都有着别人所没有的手段。不同的是巴基大神靠的是其霸王色运气，而史提利王靠的是其凶残的政治手段。在海贼王的剧情中，实力一直是一切的根本，或许史提利会成为第一个超出实力范畴的阴暗政治家，其“特殊”的程度甚至不会弱于巴基大神。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10086', '0', '小丑巴基，他的实力是怎么样，到目前为止都是在隐藏实力吗', '小丑巴基，在海贼剧情中，感觉这就是一位跑龙套，比较出色的跑龙套，贯穿剧情的始终。在故事的一开始被路飞打飞后，就一直渴望报仇，是路飞遇到索隆后的第一个敌人。一开始所有人都应该认为巴基只是个跑龙套的。其实资历不凡，从前的他和红发香克斯同在海贼王哥尔·D·罗杰的船上当见习的海贼，红发香克斯害他误吞了恶魔果实，还弄丢了他一张藏宝图，造成他出道的时间比预计的晚了十年，所以他对红发香克斯恨之入骨。与红发的感情真是难以言语。小丑巴基吃了四分五裂的恶魔果实，身体可以分裂，可以说是刀枪不入的。虽然怕火之类的东西。但是，能够在罗杰的船上实习的人，肯定不简单。还不知道，小丑巴基到底隐藏了多少实力，有没有可能是最后与路飞争夺海贼王王位的人呢。和黑胡子一样，隐藏很久。连白胡子都会记得的海贼，是个重要的角色。在红发最强霸气面前，毫无影响，在第一剑豪鹰眼的剑下，毫发无损。目前已经成为了七武海之一，期待小丑巴基的爆发。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10087', '0', '小丑巴基，逗比般的存在，实际却深藏不露！', '从海贼王的初期，巴基一登场就带来了众多的欢乐。被路飞打飞后，貌似一直沦为上不了台面的小人物。实际上呢，巴基其实是个非常有远见有头脑并且有野心的现实主义者，典型的乐观理性派。在大海贼时代，这个弱肉强食、适者生存的世界，在保证自己不被淘汰的情况下还能一步一步往上爬，直到做到七武海的位置。说到巴基的四分五裂恶魔果实，虽说是红发不小心让他误食的，好像后期海贼王这个能力没什么用，其实不然。因为尾田对四分五裂没有明确的界定，也就是说巴基可以把自己分成多少块，怎么分，能分成多小一块，我们是不知道的。要是能无限细分，那不是无敌了。我想这是巴基隐藏很深的地方，每次都打不死。还有一个特别逆天的地方，巴基能控制身体的部分到任何位置，也能漂浮在半空中。从社会现实来看，这种乐观和理性是最容易成功的，不管经历什么有自己的判断，能适应各种未知的挑战，并且坚持自己的理想。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10088', '0', '小丑巴基的几大谜团，至今未解。。。', '小丑巴基是路飞遇到的第一个恶魔果实能力者，被路飞打倒的巴基一直追路飞到伟大航路，甚至最后混了个七武海的职位，虽然很大程度都跟运气有关，但种种迹象表明巴基都在隐藏实力。下面说说几个疑点。1·巴基和红发曾是罗杰海贼团的见习船员，红发凭实力当了四皇，而巴基却靠运气混了个七武海，他们的背景相同，实力却严重不对称，这一点是说不过去的。换言之，罗杰海贼团经常和白胡子、金狮子这样的怪物打遭遇战，没有实力 是不可能与之抗衡的，就算你是个见习船员也必须有实力自保才行。2·巴基被鹰眼砍了数刀都毫发无伤。可能有人说鹰眼没用霸气，但鹰眼砍路飞的时候明显是下了狠心的，不然路飞也不会提前预知到后果而没有出手，所以鹰眼砍到巴基时有很大可能性是用了霸气。巴基没受伤就是实力的表现，提前预判自行分裂。3·巴基没吃恶魔果实之前有不带手套的时候，但之后却一直带着手套。莫非巴基四分五裂果实已觉醒，可以用手让碰到的物体也四分五裂，为了保险起见他只能带上手套吃饭睡觉都不脱？4·巴基和路飞第一次碰面交手的时候，娜美趁巴基不注意用绳子帮了巴基的身体，巴基为何没有分裂成更小的碎片逃脱？5·能让世界最强男人白胡子记住的人肯定不多，因为白胡子认可巴基的实力才能记住他，况且巴基是实习船员的时候就跟白胡子海贼团交手过了。巴基是不是真的隐藏实力，我们无从考证，诸多疑点作者尾田也没有解释，总之，巴基实力确实是个谜团。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10089', '0', '小丑巴基！他的潜力远不止你眼前所见到的', '看过海贼的人都知道，尾田在刻画每一个人物上，都很用心，不管是外貌，名字和名号，都有代表性。从海贼里那些以动物命名的人物就可以看出来，这些人物，在性格上多半是和名字代表的动物性格特征是相似的。那么为什么尾田给巴基的画风是小丑呢？不管是外貌或者名号，都是以小丑的方式来呈现。在马戏团里，每次最出入意料的就是小丑的表演了，因为其他人都是千篇一律，只要练好基本本领，就会有观众喝彩，但小丑不一样，小丑每次逗笑观众不能多次使用同一个套路，所以每次表演小丑都要有新的方式，所以小丑的潜力是很大的，而且在表演时，小丑总是滑稽的笑容，这样你就不会了解小丑真正的喜怒哀乐，你看到的，永远是表面的欢乐巴基在海贼中的表现不就是这样吗？表面上看，他并没有什么本事，但他总是能做到别人做不到的事，不要说是偶然，是运气，偶然的次数多了，也就成了必然！大家都知道，巴基以前是罗杰船上船员，罗杰死后，跟罗杰有关系的人都受到了牵连（弗兰奇的师傅就是很好的一个例子），但巴基就没事，别说是因为没被发现，能够在到处布满世界政府势力的海上生存还不被发现就是一种本事！后来在监狱里时，得到了一些有实力的部下，很多人都也是觉得是运气，可是确实是巴基救了他们呀，而且在路飞到达之前他就在逃狱，讲什么单凭他一个人不能逃出去，起码他敢这么去做，就证明他不简单。小丑巴基后面能够达到这地位的时候，我开始很难接受，但后来越来越发现，其实都是有伏笔的，并且发现巴基的实力其实是被隐藏了的，感觉以后巴基可能还是会做出更惊天动地的事情，让我们拭目以待吧！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10090', '0', '哥尔·D·罗杰被行刑时，都有哪些大人物到场了？', '《海贼王》目前登上海贼时代的巅峰，称王的只有海贼王哥尔·D·罗杰一个人，他是世界上集地位、财富、声望于一身的男人，是海贼世界的王者。在他成功被称为海贼王时却因为疾病（可能也是因为其他原因）主动投降，被世界政府在他的家乡——罗格镇，处于死刑。海贼王罗杰在罗杰被行刑时，世界很多大人物都到场了，其中包括后来颠覆世界的大人物，还有称霸一片海域的大海贼。唐吉诃德·多弗朗明哥，世界政府王下七武海之一，现已被除名。小丑巴基，罗杰海贼团见习船员，现在成为王下七武海之一，在顶上战争时被封为大神。“鹰眼”米霍克，王下七武海之一，其实力为七武海最强，是当今世界的第一大剑豪。月光·莫利亚，王下七武海之一，现已被除名。“红发”香克斯，剑术与鹰眼不分伯仲，现成为当今海贼时代的巅峰者“四皇”之一，称霸新世界一方海域。蒙奇·D·龙，革命军首领，被世界政府视为世界最凶恶的罪犯，站在与世界政府对抗的顶端。海贼王罗杰之死，引来了众多世界的强者，同时也开启了大海贼时代，而路飞就是那个结束大海贼时代的传奇海贼。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10091', '0', '海贼王中哥尔·D·罗杰的确切实力判断，顶上之战换成罗杰也得输', '—— “想要我的财宝吗？想要就都给你吧，去找吧！我把世界上的一切的放在了那里……” 哥尔·D·罗杰——被大家公认为海贼王，用自己的影响力开创了大航海时代罗杰从海贼王到现在都没有公布实力和果实能力，却被大家公认为海贼最强者。罗杰，红发，路飞应该走的都是精英海盗团，人数不多，但是个体实力很强。（图片均出自网络）从设定上来说罗杰的实力很高，都是旧时代的传说，至少和卡普，金狮子，白胡子在一个档次。但是不会高出很多。首先没有高到政府放弃追捕，而且多次被卡普逼上绝境。以卡普的性格，没有反驳外界的这些说法，那就应该全部属于事实。（图片均出自网络）其次在与金狮子的对峙中，并未完全占据绝对的上风。在和金狮子的无敌舰队作战，最后是仰仗突入起来的风暴得以解脱。当然不排除风暴也是罗杰的手段，只是从未公开过。还有小丑巴基害怕的举动，如果罗杰实力那么强劲船员多少会有点自负。但是小丑的害怕也预示了敌人的强大和危险。所以顶上之战如果换成连麾下海贼团都没有的罗杰也照样是得输。罗杰的形象是海贼王中最像海贼的海贼了。最后罗杰经历千辛万苦前往最终得岛屿，之后变销声匿迹了。直至在自己的家乡被处决，说出遗言，开创了大航海时代。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10092', '0', '引领大海贼时代 哥尔·D·罗杰', 'ONEPIECE故事的开端人物，世人公认的海贼王——哥尔·D·罗杰罗杰是真正拥有财富，名声、权势的海贼，是历史上唯一绕“伟大航路”一圈的罗杰海贼团的船长！更是找到了传说中的大秘宝“ONEPIECE”，又因为掌握了古代兵器的秘密，ZF称他拥有世界上最最危险的力量。年轻时的罗杰给人的感觉是任性自大，第一次见雷利时就任性的邀请他加入“要不要与我一起把这个世界搞的天翻地覆啊？”话说这一点真的与路飞很像！记得雷利遇见路飞时问”你能统治好这片海域吗？”路飞回答“我从没想过要统治，在这片海域上最自由的人就是海贼王”当时雷利好像看到了罗杰的影子，为什么这么说呢？海圆历1495年，也就是大海贼时代拉开帷幕的前三年，金狮子发现罗杰拥有一样足以毁灭世界的武器，这时他想与罗杰联手统治世界，当然罗杰是拒绝的，双方因此还爆发了一次激烈的战争——爱特·沃尔海战。拥有财富，名声，权势罗杰如果想要统治还不简单么，可是他要的仅仅是自由！海圆历1496年到1499年，三年时间，有着不治之症的罗杰终于征服了伟大航路！也在这一年海贼团正式解散， 然后罗杰向海军自首！在画面上看罗杰这个角色感觉是个霸气的好大叔形象，但据达旦回忆他的行为就像小孩子一样率真单纯！虽然仅仅是篇头出现，虽然回忆时也就几个镜头，可是在罗杰的眼神中我们从未看到过犹疑！罗杰是个非常珍视伙伴的人，他因为不愿让敌人追击身后的伙伴，面对强敌也从不逃走！他也绝不容许有人说伙伴的坏话，曾经还因此毁灭了一个国家的军队！记得在水之都弗兰奇回忆篇中汤姆先生有说过这样一句话“能为罗杰这样的男人出一份力，我响当当地感到非常荣幸！曾经因为罗杰的一句话而趋之若鹜奔向大海的人们到今天还记得： 罗杰昂首挺胸走过罗格镇的街，微笑坦然的面对死亡，在他出生并且死去的小镇。而今，ONEPIECE到底是什么已经不再那么重要，就让我们跟着路飞的步伐，一步一步的接近答案吧！！“想要我的财宝吗？想要的话可以全部给你，去找吧！我把所有财宝都放在那里。”愿另一个时空你能有如此平凡的幸福……本篇内容为原创，如有转载请注明来自：海贼王人物分析', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10093', '0', '海贼王哥尔.D.罗杰：伟人的悲剧人生', '罗杰这个名字早就成了海贼中的一个文化符号，代表着海贼的制高点，对每一个海贼来说，罗杰都是他们终其一生追寻的目标作为拥有“海贼王”称号的男人，在剧中任何人看来，他的人生几乎都是传奇一般的，成功的但是让我们放开“海贼王”这个可有可无的称号，看看他的一生到底是悲是喜呢？我认为，罗杰是一位乐观自由的过着悲剧人生的伟人。从他起航开始，他脸上的笑容自始至终都没有变过。“我叫罗杰，要不要和我一起去颠覆这个世界啊？”“如果不能做自己想做的事情，那当海贼还有什么意义？”“要不要我告诉你怎么去拉夫鲁夫啊，白胡子”“我是不会死的，兄弟”“我的财宝嘛？想要的话统统给你好了，去寻找吧！”和路飞一样，罗杰也是自由的，乐观的，可是比起路飞的航行，罗杰在伟大航路的航行更多了一份悲剧之感。一位伟大的船长竟然会因为病痛只能支撑4年的寿命，他用了3年，完成了伟大航路的航行，他是以怎样的心情在航行我们不得而知，但，相信船上的船员都希望能与时间赛跑，赶在病痛之前实现这位寿命只剩4年的船长的梦想吧。他的航行毫无疑问是伟大的，在没有任何前人的经验下，航行到了拉夫鲁夫。路飞是幸运的，生而逢时，有红发，雷利等优秀前辈的提携，有大量前人航行的经验可以参考，完成梦想，颠覆世界都指日可待。而罗杰或许是生不逢时，或许是天妒英才，也可能是命中注定，他要成为这个时代的开创者，耕耘者，探索者，而不是享受时代的弄潮儿。他只是单凭自己对航海的喜爱，对自由的憧憬，对世界真相的好奇，探索到了最后，却发现自己只剩下了一年寿命，而得知了历史的真相的一年之后，他也做出了自首的决定。大概这就是属于他们那个时代的无奈吧，54岁的他，航海的旅途已经走到了尽头，寿命也走到了尽头，即使得知历史真相，也已经没有时间继续改变世界，引领时代了，而两年前的路飞却是年仅17岁便走完了伟大航路的一半，拥有3亿贝利的赏金，拥有可以解读历史正文的船员，遇上了大航海时代，这一届的新人更是被成为极恶的世代，人才辈出，水涨船高，更是拥有了罗这样的同盟军，艾斯萨博这样的哥哥，巴洛托米奥，八宝水军这样的草帽大海团，前途似锦。评价路飞离不开罗杰的功绩同样，评价罗杰更离不开和路飞的对比两个性格如此相像的人，命运却这般不同，不得不让人哀叹，生不逢时。他再次出现，已经是一年后的罗格镇，一场全世界直播的处刑，处刑台上的罗杰说出了他一生中最重要的一段话，却也是对后人来说最重要的一段话，开启了大航海时代的序幕，这个时候的他，应该也同样是开心的，因为自己有了对全世界的人演讲的机会，他一定也是希望自己的这番话能让更多人发起对拉夫鲁夫，历史的真相的探索。他一生嘴边都挂着笑容，他确实的乐在其中，可我们在细数他的一生的时候却发现，是有如此多的无奈。历史的车轮滚滚向前，功绩再卓著，有的人也注定会成为引导主人公前进的配角。但，正因如此，这份笑对命运的勇气，才更值得我们钦佩。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10094', '0', '哥尔·D·罗杰绝对不是橡胶果实能力者', '曾经在网上看见，有人认为罗杰是上一届橡胶果实能力者。我并不那么认为。疑点一:罗杰才死几十年，即使不能说见过罗杰的人大有人在，对罗杰的能力肯定多少有点认知吧？海贼王世界的人看见路飞，都是一直说草帽草帽，从来没有人说过:这不是罗杰的能力吗？疑点二，战国以及三大将肯定见过罗杰吧，当他们见到路飞的时候，根本就没有人感叹:多么熟悉的能力啊。疑点三，巴基初见路飞的时候， 如果看到橡胶果实，为什么一点点想法都没有呢？巴基可谓是，当今世上少数最了解罗杰的人之一。疑点四，雷利看见路飞的时候，也许会感叹草帽，但是根本就没有感叹过橡胶果实能力。假设罗杰拥有橡胶果实能力，雷利肯定会把罗杰掌握的方法告诉路飞经过以上几点，我认为，罗杰根本不可能是橡胶果实能力者！如果罗杰是橡胶果实能力者，不知道尾田要写多少回忆才能让读者信服写的太沉重读者会觉得强行填坑，写的太随意尾田自己都不能说服自己。所以我，以及诸网友我们都及其期待尾田要使用哪些方法填补这个“BUG”最后，十分期待和之国！！！！！！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10095', '0', '世界最强男人不是六道仙人，不是哥尔.D.罗杰，而是他！', '不要再计较海贼火影谁更好、谁更强了。现在，世界最强男人已经露面了。他就是——平静地看着忍者争执突如其来的怒意，勺滤直指鸣人恩师，十分霸气！现场说教，感人肺腑！他就是世界最强男人：一乐大叔。全名为：黑崎.大筒木.D.一乐魔导士！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10096', '0', '海贼王哥尔D罗杰曾与他们一齐并肩作战过！看看有哪些吧！', '大海贼时代是由海贼王哥尔D罗杰拉开的序幕，他的海贼团名为罗杰海贼团。很多知名人物都出自于他的船上，比如红发香克斯还有小丑巴基，今天阿政来带你看看罗杰海贼团的成员都有谁吧。1.哥尔D罗杰（职位：船长）为首的当然是罗杰本人啦，他是唯一走完伟大航路全程的人，成为世界上第一个也是唯一一个拥有海贼王称号的人。在海园历1496年，罗杰发觉自己得了不治之症，决定在死前完成自己最后的心愿，成为海贼王，他花了三年的时间终于走完了伟大的航路，期间他去过空岛，并在空岛的黄金钟上留下了用历史正文书写的留言。在海园历1499年，花了三年时间的长途跋涉，罗杰终于抵达了伟大航路的终点——拉乎德尔，在那里他知晓了世界的秘密和大密宝ONE PIECE的真相，同年，他将这个秘密告诉了自己的老对手白胡子，并将罗杰海贼团解散，自己则向海军自首。在狱中，罗杰将自己怀孕的妻子托付给海军上将卡普，一年之后，也就是海园历1500年，罗杰在他的出生地罗格镇被公开处刑，临死前的那句话拉开了大海贼时代的序幕。2.西尔巴兹·雷利（职位：副船长）雷利在年轻时因自己家乡被烧毁而四处游荡，并以偷来的一艘小渔船为家，在巧合下遇到了当时还没出海的哥尔D罗杰——罗杰邀请雷利成为自己的伙伴，说“要不要和我一起把世界搅得天翻地覆啊？”，雷利虽然觉得罗杰的想法太中二，不过居然还是答应了下来，成为罗杰的第一个伙伴。之后雷利与罗杰一起经历了各种大风大浪，并以副船长的身份参加了25年前的艾特沃尔海战与大海贼金狮子对峙，在罗杰海贼团抵达伟大航路终点之后，被世人称为“海贼王的右腕”，罗杰海贼团解散之后，他舍弃了这个称号，定居在香波地诸岛以镀膜工匠为生，在罗杰被处刑时，他并没有出现在围观群众当中。在遇到路飞之后帮了路飞很大的忙，纵使岁月侵蚀，他的实力依然不减当年，能够和正面与海军大将黄猿对峙。3.库洛卡斯（职位：船医）还记得双子岬那个守着鲸鱼拉布的老头吗？他曾经在罗杰海贼团上担任船医，当年罗杰穿越双子岬的时候感觉自己的身体撑不到终点，便邀请了库洛卡斯上船，库洛卡斯因为拉布的事情，也想去伟大航路上找一找当年与拉布定下约定的伦巴海贼团，所以便答应了罗杰的邀请。与海贼王一起走完了伟大航路的全程，但可惜始终没有找到伦巴海贼团的踪影，在罗杰海贼团解散之后，他回到了双子岬，将伦巴海贼团不知所踪的消息告诉了拉布，但可惜拉布依然不听劝告一直在撞着双子岬，直到多年后遇到了路飞.....4.红发香克斯（职位：见习）香克斯在15岁时曾经与小丑巴基一起在罗杰海贼团上当过几年的见习，在罗格镇为海贼王送行，之后与小丑巴基因意见不合而分道扬镳，自己另建团队，进入了伟大航路。他应该是罗杰海贼团上日后取得的成就最高的人，目前是伟大航路上的四皇之一。他的左眼曾被黑胡子所伤，年轻时是个剑术高手，经常与鹰眼比剑，对战结果通常是不分上下，在因拯救路飞而失去左臂之后，就放弃了和鹰眼的比试，鹰眼经常感叹失去了一个好对手。顺带一说，他的草帽是海贼王罗杰送给他的，他对那个草帽一直很珍惜，在遇到路飞之后将这顶意义重大的草帽送给了路飞。5.小丑巴基（职位：见习）小丑巴基在年轻时曾与红发香克斯一起在罗杰的船上担任见习，在25年前对战金狮子时也在场，不过是一脸快吓尿的表情。在一次意外中，红发香克斯让他误吞了四分五裂恶魔果实，还弄丢了他一张藏宝图，害得他出道的时间比预计的晚了十年，所以他对红发香克斯恨之入骨。22年前，在罗格镇与红发香克斯一起为海贼王罗杰送行，在目睹海贼王罗杰的死之后，巴基因为不想成为香克斯的手下，两人就此分道扬镳。之后他因为不能下水，所以就在东海成立大陆上的海贼团，在陆地上各种烧杀抢掠，还开发出了巴基弹等特殊武器，之后遇到了路飞.....6.斯巴克‧贾巴带着墨镜扎着马尾，在对峙金狮子的时候和罗杰一起并肩作战，性格貌似很爽朗，其他详细不明。7.西卡尔是个壮汉，言语不多，似乎是沉默寡言之人，在对峙金狮子的时候和罗杰一起并肩作战，其他详细不明。8.光月御田（出自第818话）月光御田是和之国的大名，在818话中爆料出，他曾与海贼王罗杰一起到达过伟大航路的终点，并和罗杰一起知晓了这个世界的秘密，但由于知道的太多，被人盯上，遭到和之国的将军叛乱，被和之国的将军和四皇凯多杀死，只留下孩子桃之助。PS：看得不过瘾？关注阿政的公众号吧，干货满满哦，那里没有简单的资讯和图片，只有深度的动漫评析和开车用的动漫资源，欢迎关注公众号：阿政说（ID:azhengshuo）', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10097', '0', '海贼王906话，奥隆布斯是对称果实，另一顶草帽属于哥尔·D·', '海贼王906话已经发布了，先来看扉页的故事，依然是紧随905话关于奥隆布斯的故事。在905话的扉页中，我们看到了奥隆布斯的居家生活，哈哈，带着睡帽6点醒来的他，正在叠被子。针对之前他在战斗中的表现和招牌动作，书架上的书、箱子都是对称的酒杯、地图地球仪等也是对称的从905话的场景，我们基本可以得出奥隆布斯吃的是对称果实。在906话扉页中，我们又看到了他和另外一个孩子的出场，孩子名叫哥伦布斯，从名字和样貌分析，他可能是奥隆布斯的孙子。辫子都是对称的哥伦布斯根据哥伦布斯的名字，我们很容易想到大航海家——哥伦布，意大利的著名航海家，是地理大发现的先驱者。他在1492年到1502年间在西班牙国王的资助下四次横渡大西洋，到达美洲大陆，他也因此成为了名垂青史的航海家。根据哥伦布的画像和格罗布斯的穿着，两个人确实都是标准的西欧航海家。在接下来的故事中，哥伦布斯应该有不同凡响的表现，甚至可能是成为发现新大陆的人——拉乎德尔。906话最为诡异的场景最令人匪夷所思的便是在圣地玛丽乔亚出现的第二顶草帽，有人戏称这讨论了一年的国宝竟然是一顶草帽。其实三叔觉得真相并非如此，黑衣人就在玛丽乔亚，而且他的手上拿了不止一张路飞的通缉令。说明此人一直就在关注着路飞。黑衣人顺着台阶向上走去，可以猜测藏有草帽的地方应该在玛丽乔亚的顶层智商，伴有云朵和阳光地方，大概就楼顶的位置，这顶草帽应该是海贼王罗杰的帽子。罗杰被处刑之后，这顶草帽就被收缴上来，因为其带有非凡的意义，一直被封闭在圣地的密室内。年轻时候的罗杰戴着草帽知道大家是否还记得红发在赠予帽子时说的话，等你当上出色的海贼后再把帽子还给我。后来雷利也说过，路飞正一步步成长为和那顶帽子相配的男人了。从这里可以看出，这顶帽子的重要性不单是因为它源于海贼王罗杰，更重要的是它作为一个符号和象征物所代表的含义，这才是传承草帽的真正价值。而这个含义也就是D的意志。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10098', '0', '海贼王哥尔D罗杰为什么是海贼王？', '征服伟大的航路，不是说打败所有人，应该说是征服了“大海”，克服了新世界诡异的天气，并且达到了拉夫德鲁，因此而被世人称为海贼王。要成为海贼王，最重要的条件不是你的实力有多强，而是一，找到万匹丝，二，能读懂古代文字找到拉夫德鲁，三，能克服去拉夫德鲁上诡异的天气，四，从其他人的手中拿到“路标”的石板，这个顺序从下往上递进；关于罗杰的实力，唯一能衡量的大概也就卡普，卡普的顶峰大概在白胡子左右，巅峰的金狮子应该和白胡子差不多，但输给卡普和战国联手。应该就比四皇强一点吧。另外，关于卡普逼迫罗杰入绝境，其实这个说法也存在异议，就像斯摩格差点处决了路飞，但也可以说路飞被斯摩格逼入绝境。另外路飞看到海军多数是跑，和打不打得过关系不大，就像一堆小混混，看到一两个警察还是要怂，有些时候也说明不了什么。所以不要用“实力为王”这个观点去看，会很没意思。“海贼王”是世人对罗杰的敬畏。罗杰的时代是没有四皇的！香克斯只是一个小屁孩。海贼世界处于三足鼎立状态，罗杰当海贼王，金狮子第一个不服去找罗杰理论，结果被罗杰打哭了。白胡子对海贼王的位置不感兴趣，所以没有跟罗杰正面交锋，把海贼王的位置拱手让给了罗杰。罗杰在海上称王称霸无人不服。根据雷利的描述，罗杰在到达拉夫提尔之前，就被世人称为“海贼王”。史实只有罗杰海贼团经过拉夫提尔，走完了“伟大的航路”。罗杰死后，白胡子时代拉开序幕，不知何时出现了三个大海贼与白胡子相互制约谁也不敢出手打谁，世人称其为“四皇”。罗杰自首被公开处刑时，死前说把自己所拥有的一切都藏在伟大的航路了，谁找到就是谁的了！世人称罗杰的宝藏为“one piece”。不是必须打倒四皇。打倒四皇只是路飞的觉悟而已。只有到达最终之地的人，才能称为海盗王。而罗杰是偷了大妈的指路正文去的最终之地。海盗王只是一个海盗的最高成就而已。并不代表海盗王就是新世界个人武力和团体势力的巅峰。所以没必要打倒四皇。虽然没有必须打倒他们的必要，但是，路飞也不会蠢到以为别人会眼睁睁的看着自己获得这一最高成就而无动于衷。所以，必须要有和他们全部开战的觉悟。有赌神、赌圣、赌侠、独霸！别人给的称号也好，自称的也好！也不是元帅或者大将这种延续的官方的授予的称号，一定要有什么条件！海贼王之前有海贼王么？四皇之前有四皇么？凯多是世界最强生物！白胡子世界最强男人！不都是称号么？那归根到底海贼里的王厉害还是世界最强的男人厉害还是世界最强的生物厉害？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10099', '0', '海贼王，凯多很强但打不过巅峰白胡子，凯多弱点其实白胡子知道', '大家好哟，这里是次元边境的小百合！今天小百合要为大家带来的是海贼王，凯多很强但打不过巅峰白胡子，凯多弱点其实白胡子知道凯多和白胡子，这二人谁强谁弱一直都是争论的范围，其实这个问题很早就有答案，那就是重病的白胡子打不过凯多，但巅峰时期完虐白胡子。白胡子作为上个时代的三大海贼，大风大浪见得多了。而且在上个时代，海贼遇到的困难比现在要大很多，现在的势力基本已经稳定下来，但在上个时代可都是还在争夺中。而且当时最有可能得到宝藏的除了罗杰，其实还有一个人，那个人就是白胡子。只不过白胡子对其不感兴趣，当罗杰准备跟他分享宝藏位置的时候都被拒绝足以看出。而且罗杰为什么只愿意跟白胡子分享而没给金狮子或者卡普分享，足以看出罗杰对其的信任。我们都知道白胡子资深阅历深厚，加上罗杰没自首前曾经与白胡子畅聊，其中就有宝藏，那么畅聊肯定是聊罗杰这一路上的所见所闻，毕竟罗杰连三大古代兵器都知道，凯多那点秘密他肯定也知道。所以罗杰很有可能在跟他畅聊期间透漏过。最后就是凯多喜欢挑战强者，但我们发现白胡子虽说是重病，但从顶上之战的情况来看，他战斗力还是很强，但凯多却从来没去主动攻击过他。所以推断，白胡子应该是有一定底气的，不然凯多早去单挑他了。对此你怎么看~欢迎评论。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10100', '0', '多拉格、赤犬、白胡子巧好对应日本3大自然灾害，那他能力如何？', '龙的实力和果实一直都是众多海粉激烈争执和探讨的主要话题之一。那么龙的果实到底是什么？海贼王更新八百多集到现在，龙所出现的次数寥寥无几，一直保持着自己神秘的面纱，我们一步步的猜测，揭开这神秘的革命军领袖龙的真正果实和实力。龙的能力是召唤巨大的暴风，这是众所周知的，姑且认为龙是自然系风风果实，因为“龙”革命军的身份，所以代表吹散邪恶势力带走腐败的正义之风。我们这样考虑，尾田大大再给海军三大将起名字的时候，都采用了动物的形式，赤犬是只红色的狗，青雉则是只鸡，而黄猿是个逗比的猴子。那么尾田在营造海贼王中三大势力会不会也采用这种方法呢？我们都知道在日本最具备破坏力的三大自然灾害“火山，地震，台风” 火山代表这现任海军元帅赤犬，地震代表这原海贼实力最强的白胡子，那么台风是否就代表着拥有自然系风风果实的革命军领袖“龙”呢？当然这还只是猜测！这个时候我们需要注意了，在日本这三大自然灾害中，台风不论是给日本造成的经济损失，还是自然危害都是最大的，那我们能不能理解为，革命军领袖“龙”的实力要强于海军现任元帅赤犬和白胡子？看过顶上战争的小伙伴们都知道，白胡子在身体及其虚弱，甚至需要打吊针，哪怕又被斯库亚多偷袭，一剑刺透胸膛，依旧可以略胜年轻力壮的赤犬，将其打到地下，可想而知白胡子的全盛时期的实力绝对强悍到了极点，被战国视为强敌称为“最强的男人”但也有可能是战国对白胡子的印象还在他全盛实力的时期，对他现在的实力，评估过高。由此来看，革命军领袖“龙”的实力哪怕不敌全盛时期的白胡子，也不会比顶上战争的爱德华纽盖特差，至少要强于原海军三上将，甚至可以说根本就不是海军上将，七武海，四皇等可以比拟的，也许只有全盛时期的白胡子，或者海军最高荣耀的英雄蒙奇D路飞的爷爷卡普可以做到？毕竟我想龙也不会跟自己的老爹来真的吧', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10101', '0', '不死鸟马尔高，被忽略的隐藏属性，相当于潜在四皇资质', '原白胡子海贼团的二号人物，实力强劲怎么看都像是一众队长之首，也是最早跟随白胡子的海贼马尔高虽然在顶上展示了自己的超凡实力，但依然被低估了。马尔高的果实能力非常奇特比自然系果实更加稀少的动物系幻兽种，而且是不死鸟果实，自然要带给他不一样的特殊能力。马尔高的恢复，不同于自然系的元素化，即使是缠绕霸气的攻击一样可以恢复，仅仅这一点隐藏的属性就够骇人的了。马尔高中招之后是用凤凰火包裹的部分高速恢复所以即便是缠绕霸气的攻击依然不会奏效，必须要达到他受伤的临界点，或者说是让其丧失果实能力，不然马尔高就可以不停的恢复而且马尔高是特强的体术高手作为动物系的马尔高，主要攻击手段当然是体术应该对海军等等基本体术都有涉猎，是体术方面的高手不知道霸气是不是同等的强大，至少在顶上的时候完全没有展示过，不过这样的潜力，如果好好发展下一定有问鼎四皇的实力，可惜和凯多有点重复，不然机会很大。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10102', '0', '“不死鸟”马尔高再度出山，将帮助路飞除掉凯多三灾中的“天灾”', '在顶上之战以后，黑胡子海贼团击败马尔高率领的原白胡子船队队长，成功列为四皇宝座。自此，拥有古代幻兽系恶魔果实的“不死鸟”马尔高就再也没有了音讯。直到在佐乌岛，草帽、罗、毛皮族以及和之国结成讨伐凯多同盟，猫蝮蛇老大提到要邀请马尔高出山，协助同盟打倒凯多。要知道在顶上战争中，马尔高曾经一脚踢飞青雉，与黄猿对垒不落下风，就连能够打穿自然系果实艾斯身体的赤犬攻击，马尔高都能接下，可以说他拥有匹敌大将的能力，他的加入确实能够极大提升讨伐凯多同盟的胜算。那么问题来了，马尔高出山之后会对付谁呢？首先，肯定不可能是凯多，相信尾田大神一定会花重笔放在路飞PK凯多之上；其次，也不可能是旱灾杰克了，这家伙已经被象主一鼻子秒杀，虽然生死情况未卜，但是这家伙只有攻击力强而已，然而再强的攻击力对于防御性堪称完美的不死鸟马尔高来讲完全是无效的。众所周知，BGM手下有三将星，凯多手下有三灾，最先出场的旱灾杰克虽然完全是打酱油的角色，但是他可是截止到目前海贼王中悬赏金最高的主，那么其他两灾肯定实力不俗。在路飞打凯撒的时候，尾田大神为我们揭秘过，凯多正在组建一支SMILE果实能力者团队，而在漫画中还放出了这样一组图片。其中一头大象的头像格外显眼，要知道杰克就是猛犸象远古种恶魔果实，所以这很有可能就是尾田给出的线索。而他的旁边有一只秃鹫，格外凶悍，所以笔者揣测他很有可能就是凯多三灾中的“天灾”。前面已经说了凯多要留给路飞来打，那么不死鸟马尔高很有可能就是对位这个秃鹫果实能力者，两个人都是鸟类果实，都能飞，所以最有可能成为日后的对手！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10103', '0', '不死鸟马尔高并非不死，想累死赤犬还差些', '海贼王---------------------------------马尔高，即Marco，圣经中四大炽天使之一。正所谓人如其名，拥有动物系幻兽种不死鸟果实的马尔高使用能力时的确像个天使，这可能是最拉风的动物系恶魔果实，能与之相比的怕是只有战国的人人果实了吧。顶上战争中马尔高的表现也是可圈可点，但小马哥在顶上战争中大多展现的都是“不死”的特点，蓝色火焰，浴火重生，不管承受多大的伤害都是毫发无伤，能顶着黄猿的镭射上前并将其击落，直接用身体挡住众人都没办法的赤犬的岩浆。但问题来了，不死鸟真的就不死吗？答案是否定的，首先，顶上战争中小马哥与黄猿展开了激斗，但因担心白胡子的伤势而被鬼蜘蛛套上海楼石手铐，当时的黄猿完全可以将其射杀，可谁让黄猿是出了名的放水王呢，镭射从不对准要害，对准要害肯定会被人打断是黄猿一贯的作风。这是打败小马哥的第一种方法，能力者都怕海楼石是一贯的设定，所以不多说。重点说一下不被限制，能力最大化的小马哥是不是不死。顶上战争虽然我们看了很久，但其实这场战争的时间是出奇的短，并未有长时间的激斗，相比于赤犬与青雉长达十天的战斗，顶上真的是一晃而过，所以在此，小马哥仅显示的是其“无敌”的状态，但其实，不死鸟也并非不死，再生能力也是有限度，并非是不会受伤的，而且就算是在“不死鸟”形态下也会死亡（不然真是继黄猿的闪闪果实后的又一BUG）。尾田在SBS中也曾回答过这个问题。所以说，对于小马哥打不死赤犬也能累死赤犬的这种结论是丝毫不成立的，能与大将过招的不在少数，两年后的路飞见到藤虎也是照捶不误，可这能说明路飞已经有大将实力了吗？黑胡子见到赤犬就马上要跑路，但黑胡子见到小马哥可没有跑路，顶上之后，由小马哥率领的白胡子残党对战黑胡子海贼团，结果惨败。或许也间接说明了小马哥与大将的实力差距是有些的，但至少准大将实力还是有的。两年后的白胡子残党无疑会沦为路飞的帮手，这是白胡子的意志，也是艾斯的意志，路飞若有危险，小马哥等人必定不会坐视不管，坐等大妈篇与世界会议篇结束时最拉风的小马哥登场吧（可能要好几年）。我是老奈，一个热衷于海贼王深陷其中无法自拔的男子，同样喜欢海贼王的记得点波关注哦~我是老奈，一个热衷于海贼王深陷其中无法自拔的男子，同样喜欢海贼王的记得点波关注哦~请点击此处输入图', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10104', '0', '“不死鸟”马尔高专贴：浴火重生的凤凰？', '不死鸟马尔高 马尔高，绰号“不死鸟”，是动物系幻兽种果实能力者，名称不详；之所以称之为“不死鸟”的缘由是，在其果实能力发动的情况下，任何攻击对其造成的致命伤害，都能在转瞬间回复（这里可以考虑是动物系本身的回复能力突出导致）。其能力可以无视任何物理攻击（除霸气外）！ 马尔高，曾作为白胡子海贼团旗下第一番队队长，也是首席战斗员！其实力突出，在大事件中虽然对能力的展现不多，但能与大将相较量数回合，实力可见一斑；马尔高的初登场是在红发拜访白胡子的时候。所经历过的战役，基本上胜利占绝大部分；今天主要来讲讲其果实能力！ 开头已经说明了他的果实大概能力，这是目前已知的情况！从动画中看出马尔高在果实发动的时候，呈现出来的是蓝色的火焰，众所周知，不死鸟（凤凰）在西方以及中国的神话中，是红色的火焰，而在动画中却以蓝色火焰出现，但是漫画中并没有对其颜色着重描写；如果不是配色的问题，那么马尔高的恶魔果实或许不是不死鸟（凤凰），只是其觉醒后的回复能力比其他动物系果实突出罢了！另一种可能是，作者为了避免与神话中的不死鸟的属性重复，故意以蓝色火焰示人！毕竟在岛国也不止海贼如此，在青之驱魔师中也是以蓝色火焰的形式出现；貌似在岛国，比较喜欢“青炎”吧？ 不管是哪一种可能性，马尔高的实力毋庸置疑，在无视攻击的情况下，在大事件中，是海军认为最棘手的存在，不惜下套，使其能力受限。而红发也曾邀请过其加入自己的队伍，但是被拒绝（看来香克斯想在白胡子这边挖墙角，还是有点不行啊，毕竟比不上罗杰；看来最后可能会被路飞挖走吧）！ 大事件中以白胡子战死，艾斯护弟被杀而告终，而在事件中，最为突出的，莫过于黑胡子的双果实表演了，而其行为不仅仅可耻，还侮辱了一个为了家人不惜牺牲自己的男人；最后，为了报此仇，马尔高带着白胡子残余部队与黑胡子开战，最终以惨败告终，马尔高也因此消失。目前的剧情中，索隆小分队踏上了寻找马尔高的旅程，而在此期间，不能忘记了白胡子二世也在寻找马尔高，之后绝大可能性是索隆与白胡子二世的碰撞！小枫也很想知道马尔高最终的归宿会是哪里？让我们一起期待吧！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10105', '0', '海贼王：马尔高被黑的很惨，其实不死鸟实力很强大', '如果我们评选海贼王中果实能力炫丽排行，不死鸟马尔高的不死鸟果实绝对能名列前茅。稀有程度超过自然系的不死鸟果实，也是大家十分喜爱的一种能力，在顶上战争中我们也被马尔高的表现所折服。但是随着新世界剧情的展开，大家开始对马尔高的实力产生怀疑，因为凯多三灾、BIG.MOM甜点三将星、以及红发海贼团的本贝克曼，无论从气场还是战斗表现来看，都比外形偏向屌丝的小马哥要强。于是一部分人开始了漫长的黑马尔高之路，关于马尔高的实力也开始引起论战。马尔高强么？答案无疑是很强，马尔高的定位是白胡子海贼团的第二战力，是在白胡子死后可以使大家信服并且继续带领白胡子海贼团活动于新世界的人物。关于马尔高，在漫画中没有一次全力的战斗表现，唯一一次全力战斗还被尾田一笔带过。所以我们只能从定位上来看马尔高的实力。马尔高最主要的表现是来源于顶上战争，而其主要对手是三大将，是除白胡子外与三大将全部交过手的人。其最大的标签就是其再生能力。在顶上战争中与黄猿短暂交手势均力敌、一脚踢飞青雉、与赤犬抗衡而落于下风。小编并不认为马尔高能战胜大将，但是他面对大将是有一战之力。就好像我们并不认为三大将可以战胜白胡子，但是三大将却能在战场上牵制白胡子与之抗衡。这些都是看得见的战斗，而尾田没有进行过多描绘的黑白了断之战，则是为马尔高重新定位。在一场战斗中，顶级战力决定战场走向。就像顶上战争白胡子突然发病后白胡子海贼团几乎瞬间崩溃一样。在这场了断之战中，最重要的无疑就是马尔高与黑胡子的战斗。没有人认为马尔高可以打赢黑胡子，但是他可以与黑胡子抗衡多久是决定这场战争规模的X因素。尾田描述此次战争十分惨烈并且规模巨大。这样的战争持续时间都不会太短，而马尔高则在这段时间抵挡黑胡子的攻击，同样是不胜，但是仍然可以重伤逃离。事实上，败给黑胡子并不丢人，在设定上，似乎只有路飞可以击败黑胡子。了断之战以白胡子残团惨败而结束，但是却也是为马尔高正名的一次决战。马尔高的实力甚至超过所谓三灾三将星，只是其一直面对的都是海贼王战力的顶点。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10106', '0', '《海贼王》“不死鸟”马尔高即将再次登场，马尔高到底有多强？', '随着白胡子的落败马尔高也开始销声匿迹，曾经海上的一大霸主马尔高，现在又即将重新回到主线，说不准还会加入路飞的手下团队~！马尔高拥有的是号称比自然系更稀少的动物系幻兽种更加拥有不可匹敌的地位，是白胡子海贼团一番队队长，对白胡子也是忠心不二力战大将(与大将黄猿对打中，黄猿使用光束攻击，在完全是不死鸟形态下，可以浴火重生而不受影响)马尔高明显是领导众人的头目,而且在白胡子死后，也担任起了白胡子海贼团的首领虽然现在被打伤，弄的白胡子海贼团分崩离析不说，连麾下的海贼都已经照顾不过来了，完全没有白胡子往昔的地位。唯一的机会就是现在马尔高将在路飞等人的邀请下，再次回到主线对此海迷们还是很期待地，毕竟马尔高拥有为数不多的幻兽种果实能力，在顶上也没什么机会出手，这次刚刚好给了机会可以重拾地位，展现下幻兽种的威力', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10107', '0', '海贼王名画，不死鸟马尔高', '1号2号3号4号5号你喜欢几号呢？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10108', '0', '海贼王不死鸟马尔高将要出现，路飞一伙又添超强战力', '海贼王动画722话已经完全交代清楚接下来的行程，路飞半船人将要去救回山治。然后就直接前往和之国挑战四皇凯多，帮助桃之助打败凯多。猫蝮蛇老大也知道现在他们势单力薄，根本没有挑战凯多的资本。我们知道犬岚公爵和猫蝮蛇老大曾经追随光月御田先在白胡子的船上，然后又被挖角到罗杰的船上。他们两人跟罗杰的船员和白胡子的船员都很熟。在这个需要帮手的时候，自然就想到了以前白胡子的船员。猫蝮蛇老大想要寻找的人就是不死鸟马尔高，拥有超稀有的幻兽系恶魔果实不死鸟果实。对抗凯多这种皮糙肉厚的对手，就需要马尔高这种拥有超强恢复力的人打持久战。马尔高在顶上战争也充分证明了他的实力，如果有他帮助路飞肯定实力大涨。漫画里也说明了，马尔高为首的白胡子剩余部队去挑战黑胡子了。但是，实力差距太大惨败，现在应该潜伏在新世界的某个地方。当猫蝮蛇老大找到马尔高的时候，不仅会得到马尔高的帮助，还有其他白胡子船员的帮助。这些都是超强的战力，应该会使路飞得到很大的帮助。马尔高最后出现的画面。现在看来马尔高经历了白胡子和艾斯的死，然后挑战黑胡子失败的事件，变得沉默多了。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10109', '0', '我写你选：超经典恶魔果实来袭，不死鸟马尔高惊喜回归~~', '今天小世初给大家介绍几个恶魔果实，看看有木有大家喜欢的说：1、X·德雷克疑似动物系古代种恐龙形态，可以使他变身为远古恐龙形态，力气巨大，身材高壮，爪子锋利，是很罕见的品种。2、马尔高拥有比自然系还要罕见的动物系“幻兽种”恶魔果实，具体名字不详，暂被称为不死鸟，是五种会飞行的动物之一，恶魔果实使用者本身有实体，一旦受伤可以通过被包围的蓝色火焰复活重生，很棒的果实能力。3、乔兹白胡子海贼团第三队队长，人称钻石乔兹，顾名思义，就是身体可以变换成为钻石，可以抵挡所有的物理攻击，实力强悍。4、拉非特黑胡子海贼团5号船队长，恶魔果实名字不详，疑似动物系白鸽果实，可以在背上长出双翼飞行，使用手枪和手杖（跟布鲁克的一样，暗含剑）为武器。5、布拉曼克白胡子海贼团第六队队长，恶魔果实能力名称不详，疑似口袋果实或者储存果实能力者，可以在战斗时从下巴缝补处取出大锤进行战斗，并且这个大锤比自身还要重两倍，实力惊人地说。。。。。如果你也是个动漫爱好者，请加QQ群176836422，让我们来一起讨论动漫吧。。。。。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10110', '0', '不死鸟马尔高归来！海贼王接下来精彩了！', '海贼王顶上之战便已经消失的马尔高在最新一集的海贼王722《传说中的航海 犬和猫和海贼王》的剧情中，犬和猫提到将要找到白胡子的部下马尔高，真的是期待已久啊！海贼王马尔高我们来回顾一下马尔高在顶上之战的表现！先看看他在顶上战争和大将的交手情况首先黄猿想打白胡子，被马尔高挡下！海贼王马尔高正面挡下黄猿的攻击 而且还一脚踢飞黄猿！海贼王马尔高可见马尔高飞行速度之快 脚力以及体术都很强黄猿的体术都清楚 ，和年老雷利打的难分难舍 和Z对打时 黄猿略显优势 被马尔高踢飞 证明马尔高体术不一般！海贼王马尔高战斗结束的时候五老星在研究马尔高的时候说道下面这段话,，大意是能阻止【黑胡子】的只有【四皇】或【不死鸟马尔高】及白胡子残党，可以看出五老星对马尔高的认可度很高，说马尔高有阻止黑胡子能力，这难道不说明小马哥的实力？海贼王那么，喜欢马尔高的读者们，你们喜欢称呼马尔科什么？马皇，马神， 小马哥 ，马尔科 ，马叔 ，马大将， 马元帅 ，八武海-马尔科， 马一哥 ，还是不死鸟呢？欢迎底下留言哦~海贼王马尔高', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10111', '0', '不死鸟马尔高解析，世界最强海贼团的支撑者！', '马尔高一直是极受欢迎的人物之一几乎凝聚了所有最强的头衔，额，不是本人最强还是其背后的势力，还有自己的果实能力，说起来各方面都是最优越的条件，就差自己一跃而上了！而他本身也是这个世界最强海贼团的头号支撑者，实力超群不管是顶上之战，还是日常活动马尔高给人的印象首先是白胡子老爹的左膀右臂，其次是所有船员的大哥，论实力来说，从马尔高来说就是一个节点，从二番队的艾斯、三番队的乔滋和马尔高都不是一个实力层面的人，感觉相差巨大本来被寄以厚望的三番队乔滋，居然被明哥一下控制住了，还在顶上丢了胳膊！队长们的实力可能不是排名顺序但是他们之间的差距很大，马尔高作为其中的佼佼者已经具备无限接近大将的实力，而类似其他队长还处在苦战中将，随意被明哥操控的地步！尤其是红圈中的人物，简直弱的让人意外，完全就是实力断层而马尔高是整个海贼团的有力支撑者，这不仅仅是实力，还是气魄和控场的能力！不管是顶上之前还是顶上之后，马尔高都在尽自己最大的努力保护船员，已及麾下的海贼团船员，完全担负起来船长的职位，可惜事事没那么多顺心……马尔高带领旧部复仇黑胡子的计划破灭了，现在潜伏1年多的时间，也不知道情况怎么样了但无疑白胡子海贼团综合实力一般，光靠马尔高一个顶级强者可远远不够！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10112', '0', '海贼王人物列表——NO.6气度不凡的领袖，不死鸟马尔高', '海贼王中的马尔高不仅仅拥有不俗的实力就是气魄和指挥能力也不次于白胡子，这些本来也都是他来代劳的。在顶上之后马尔高更是直接担负起了新白胡子海贼团，最高领袖的位子。马尔高虽然实力不凡，但是要坐四皇的位子，还差点继承了白胡子海贼团的所有战力，马尔高依然没有保住四皇的位子其实马尔高已经尽力了，元气大伤的白胡子海贼团也承受不起那么多海贼的群起而攻。其实还有一个重要原因就是很多麾下海贼心不诚。他们毕竟是效忠白胡子，而不是马尔高，所以做事未免会上心。从马尔高两年后销声匿迹，但是麾下海贼还在各自地盘最威做福就能明白，在这两年来，他们并没有共患难。马尔高的果实能力确实稀少，是动物系中的幻兽种但是具体的作战效果在顶上没有体现，总不会因为过人的恢复力，这招就只是用来挨打和接招的吧，这些应该是留给他来帮助路飞的时候体现马尔高形象让我有点想起雪女了，感觉雪女的自然系果实能力就比马尔高的果实能力强的多。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10113', '0', '海贼王拥有不死之身的人物，不死鸟马尔高的恢复能力排不进前三？', '海贼王不死之身的存在，并非是传说在这里很多恶魔果实的能力者，都获取了不死之身，他们拥有超长的寿命、无法被破坏的身体。不死鸟·马尔高、布鲁克…究竟谁才是最强的不死之身对于布鲁克的果实能力，很多人还有疑惑，我们来简单复述下！简单点说，布鲁克吃了黄泉果实，然后就死了，尸体在几年的时间里腐烂成了一堆白骨，然后灵魂飘回来了！所以布鲁克就复活了！所以布鲁克没有心肝脾肺肾、眼睛耳朵这些器官！至于他怎么吃饭、看东西我就不清楚了…不过按照这种说话，他的身体不存在被破坏就没有使用了这一说！应该是彻头彻尾的不死之身加上长生不老。这颗黄泉果实没有白吃，一点不亏！外号不死鸟的马尔高，这颗果实能力也很牛逼。使用不死鸟的火焰虽然不可以攻击，不过可以快速的恢复伤势，按照道理来说，是没有恢复不了的伤势地…说他不死不灭也是正常地！弱点目前没有发现，可能不能一次恢复太多的伤势，毕竟是有极限地而今天说地，其他几位人物，都没有极限！在尾田的SBS之中，我们又认识了全新的人物，蛋蛋男爵！可以训返往复不断的复活、进化的蛋蛋男爵，BIG MOM麾下的战斗员！他可以从蛋蛋男爵进化雏鸡子爵，然后是公鸡伯爵，最终还能回到蛋蛋男爵！！这居然是一个圈？一个循环？岂不是等于不死不灭？死而复生，用在鸡身上？真当它是凤凰了？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10114', '0', '不死鸟马尔高能力解析，如何成为世界最强海贼团头号队长！', '我们大家对于海贼王这位不死鸟马尔高都是充满了期待！世界最强海贼团的一番队队长，其身份绝对是水涨船高啊，能替白胡子四下征战的人物，其实力该有多么强大呢？？他又如何成为头号队长？尾田自己说所有队长实力都在伯仲之间大家的地位平起平坐，不过谁高级不好判断，谁低级还不好判断么？例如我圈出来的几位，都是水准平平的队长…战绩渣的太明显了！左下角的队长，直接被明哥当木偶操控起来了，那是狂杀自己人，毫无还手之力！这种被瞬间秒杀的存在，也好意思当队长？与马尔高平起平坐么？然后就是三番队队长钻石·乔兹明哥也曾经控制过他，阻止他去攻击沙鳄鱼，不过明显控制起来吃力，控制时间短，而且仅仅是组织乔兹出手，没法控制乔兹行动！我想这就高下立见了，乔兹的实力就算弱，也要在刚刚的几位队长之上！而这么多队长之中，唯独马尔高可以硬抗海军大将一场大战下来唯独马尔高是毫发无损，乔兹则因为对抗大将，弄断了自己的一条手臂！你可以说马尔高是果实能力的缘故毫发无损，不过人家硬抗大将全身而退是事实，搞的其他队长没有果实能力一样，果实能力难道不是实力的一部分！综上马尔高在团队的地位就非常明显了！马尔高在整个白胡子团队之中是当之无愧的头目，不过其实力应该与大将在伯仲之间！恐怕与其他四皇的头马对战讨不到好处！不过马尔高赏金低、实力弱也有登场太早的原因在里面！地位却绝对不弱于其他四皇头马！', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10115', '0', '海贼王：不死鸟马尔高惨败黑胡子后，会怎么消失了？', '在白胡子的队长中，给人留下印象最深的，莫过于马尔高——小马哥，在海贼王动漫最新一集中，在顶上战争后，小马哥率领白胡子的残党们跟黑胡子又干了一架，毕竟一切的债主还是黑胡子嘛然而却是以惨败退场，之后马尔科和其他队长就完全消失了，那问题来了，马尔高和其他队长去哪了？可能一：跟黑胡子那场战争中阵亡了，因为那场战争是以惨败退场，而白胡子顶上战争也是惨败退场，代价就是头领的死亡，那小马哥会不会也在那场战争中阵亡了呢？可能二：小马哥因为那场战争，损失惨重，把白胡子最后的底都输光了，无颜再见其他人，所以选择退出“江湖”，找一个安静的地方领盒饭了。可能三：也是因为那场战争，损失惨重，但是小马哥选择暂避锋芒，养精蓄锐，在适当的时机出场！以上三种可能，前两者还是不会很大的！毕竟，小马哥在海迷的心中也是十分重要的，当然也不排除WT想把小马哥画死了呢？小马哥是白胡子船上一队队长，也算是白胡子船上的二把手，所以还是有很大的号召力的，如果按猫蝮蛇说的，有小马哥的加入，再加上草帽海贼团，对战四皇又有何惧？就像路飞所说的，我们也很期待小马哥的闪亮登场，顶上战争的小马哥太帅了！最后，看到这里，说明已经看完了全文了小编感谢你的支持，想要了解更多动漫趣事，可以关注一下小编哈~', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10116', '0', '海贼王不死鸟马尔高分析，其战斗力真的足以与大将相抗衡么？', '海贼王顶上之战，真的有不少人物扬名了，黑胡子算一个、赤犬算一个据说马尔高也是其中之一，成为现在的高人气角色，主要都是靠他自己在顶上之战中的表现…其实说实话，我从来没觉得他实力有多么变态的强大。顶上之战，其实白胡子方面是非常吃亏地主要是海军集结了10万大军队，在人数上，实在是太有优势了…这点真的是不服都不行啊…白胡子的干部们，不仅仅要对付地方的将领，还要应对流弹、暗箭、大炮以及无穷无尽的士兵马尔高对付大将的时候，旁边根本就没人助阵能打成这样就已经是相当的不错了相比较其他的队长们，马尔高的实力非常明显是要技高一筹地！可惜没有那么多队友支撑他出来秀一波，劣势战斗就是这样地，最终被黄猿抓住了空隙，一波就送走了白胡子海贼团，真是在转瞬间就分出了胜负啊！一瞬间的时间，白胡子、马尔高、乔兹都被重伤了，胜负在这里已经分晓了这时候就显示出来马尔高果实的强大了…因为重伤的马尔高，是被戴上海楼石手铐了摘除手铐的马尔高几乎就是一瞬间，就恢复了伤势，不死鸟的复活能力就是变态。至少这续航能力，就是大将也没法和他打持久战吧？另一方面也是硬性指标了，乔兹都被青雉给弄残废了白胡子海贼团除了白胡子，连个能对付大将的压箱底人物都没有，那真是没有脸面在新世界混了。说什么也是最强海贼团啊，这点实力还是有地马尔高就是他们的脸面，实力肯定不能弱打不过黑胡子是一回事，但是连大将都对付不了，那就尴尬了', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10117', '0', '四皇之首？深度解析红发香克斯的恐怖实力！', ' 红发香克斯，新世界四皇之一。新世界四皇：站在海贼世界顶端的四个极具实力的强者目前为红发香克斯、百兽凯多、夏洛特玲玲、以及新晋级的黑胡子蒂奇。这些强者或多或少都有过不少次的出手，唯独香克斯实力至今是个谜，小编本次给大家深度解析一下香克斯的恐怖实力。 在东海篇的时候，路飞和香克斯相识，那时候认为香克斯就是路飞成长过程中的指路人，过后就领盒饭了，但是在救路飞的时候香克斯却展现了强者的气息，虽然红发断臂，但是一个眼神就吓跑了近海之主（当时完全没有霸气的设定），可见那时的香克斯完全是凭借强者散发出来的气息吓跑的近海之主。 鹰眼拿着路飞悬赏单去找红发。鹰眼，世界第一大剑豪，王下七武海中实力最神秘的一个，也是索隆的第二任师傅，在红发没断臂之前经常和红发切磋剑术他俩平分秋色，这说明红发有着不须鹰眼的强大剑术。 为了阻止艾斯追杀黑胡子，登上当时四皇之首白胡子的海贼船，一上来就释放了强大的霸王色霸气，可以造成实质性的伤害，并和白胡子老爹对了一刀，天都被剑气劈开一道裂缝，可见香克斯的恐怖实力。 阻止凯多并结束顶上战争，当艾斯公开被处决的时候，白胡子海贼团全体出动营救艾斯，百兽凯多要在这个时候袭击白胡子老爹，红发前去阻止并在最后时刻出现在遥远的马林梵多的战场。在红发出现的时候，强如卡普，战国的这些老一辈强者也都倒吸一口凉气，赤犬更是被红发用剑挡住对克比的攻击时，满头大汗。 综上所述，红发有着当前已知最强霸王色霸气，有不亚于鹰眼的强大剑术，小编觉得在当下红发个人实力为四皇之首也不为过。各位看官有其他意见呢我们也可以一起在评论区讨论，谢谢大家。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10118', '0', '海贼王907话：红发香克斯背景成谜 连五老星都要给他面子', '海贼王907话简直比小说还要精彩，感觉尾田可以不用画漫画了，直接写小说销量也是杠杠的。原本以为906话就已经够劲爆了，没想到跟907话一比，那什么都算不上。早在之前阿酱就觉得红发香克斯定不简单，从莫名其妙的住在风车村一年就足以让人匪夷所思。而且到后面他的每一次出现都是那么的恰到好处，艾斯要去抓黑胡子，他出现了。直接找到白胡子让他把艾斯召唤回来。顶上战争打得不可开交，他出现了。“在场的各位都请给我一个面子！”我去，简直霸气侧漏啊。阿酱一直觉得红发是幕后操盘的人，为的就是维持航海的世界平衡，从而扶植新人登顶找到ONEPIECIE，继承D的意志。现在看最新话中红发直接进入五老星的房间，这应该也不是他第一次过来了。世界政府高层的地盘想来就来，想走就走，连五老星都说：“就因为是你才拨出时间来。”这让我脑海中自动YY出当时的情景。五老星说：“我仔细想了想，还是给你一个面子。”红发香克斯混得是真的开，这也恰恰印证了我之前的猜想。没准当年红发前往风车村真的是有目的的，他也许和世界政府达成了什么交易，稳定平衡什么的，结果意外中发现了路飞这根苗子，嗯很不错，你可以继承D的意志。 而且从之前赤犬的话中也能看出来，五老星和CP是不对付的。也就是说天龙人的傀儡是CP，至于五老星是哪头的现在还不确定，海贼王真是越来越刺激了，怪不得谁都得给红发面子。五老星都得为他空出时间的人，谁敢随便放肆？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10119', '0', '海贼王：香克斯身世曝光，背景比路飞还大，底气十足不惧五老星', '海贼王是目前最火的日本动漫，经过长达二十多年的连载，如今漫画已经进入到了世界会议篇。在这二十多年里，尾田挖了很多坑，也留下了很多未知的谜团，例如红发香克斯的身世。目前我们已知的就是他的面子很大，来头不小，所以大将和海军元帅都要给他面子，在上一话中甚至只身会见五老星。到底香克斯有怎样的底气，在接下来的几话中很有可能会透露。偏偏有意无意在上一话中还提到了40年前称霸大海的男人——洛克斯，尾田不可能无缘无故提到这个人物，所以洛克斯与目前出场的重要人物一定有关联，很多网友推测这个人就是香克斯。至于支撑这一推测的理由有以下三点:1.顶上之战前成功阻拦凯多我们都知道凯多被称为世界上最强生物，拥有强大的实力，所以又有“单挑最强”的称号。因此顶上之战前红发到底是如何成功阻拦凯多一直都是和谜，毕竟同为四皇，即使红发实力比凯多强，强得也有限，另外凯多一直都想自杀，所以更不可能因为怕死而退避。如果红发是洛克斯之子，那就解释得通了，因为在上一话中提到过凯多曾是洛克斯海贼团的船员，如果香克斯是曾经的船长之子，凯多看在旧情的面子上，就此退避也合情合理。2.海军大将和元帅卖他面子对于红吹来说，最大的依仗无非就是顶上之战时，香克斯一句话就终止了顶上之战，海军大将和元帅都要给他面子。要知道以当时在顶上之战中聚集的海军力量，绝对不会惧怕红发海贼团，毕竟当时全力出手的只有海军大将赤犬，卡普、鹤、黄猿和青雉都没有认真动手。如果红发香克斯是洛克斯之子，这一点也解释得通，毕竟洛克斯海贼团已知的船员中就已经出现了两位四皇，香克斯作为老船长的儿子，虽然平时不会对他俯首称臣，但是一旦有难还是会鼎力支援，如果再加上凯多和大妈两位四皇，那么可能海军本部真的抵挡不住。3.年龄和天赋从年龄上来看，洛克斯40年称霸伟大航路，而二十年前海贼王罗杰被处决时香克斯差不多刚好二十多岁，所以在时间点上只有香克斯的年龄对的上。另外就是天赋，红发香克斯能够在二十年里登上四皇之位，成为罗杰海贼船上最出色的船员，强大的天赋也是很重要的依仗，这一天赋很可能就是继承自他的父亲洛克斯。如果这一推测成立的话，香克斯的背景就比路飞还要大，有两位四皇作为依仗，再加上他自身的四皇实力，难怪他不惧五老星，敢只身赴会。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10120', '0', '红发香克斯的实力到底是在什么阶段？真的很厉害吗？', '四皇应该是目前海贼们能碰到的天花板境界了，当然肯定有比四皇更加厉害的人物，但目前没有怎么出现过，所以只能用四皇来算天花板了。前后四皇一共出现五位，除了白胡子挂掉，黑胡子是新晋的外，其他三位都在位很久了，我们对于四皇的实力不能很好的了解，也就是白胡子，黑胡子以及刚打完的大妈这三位能做个简单的战斗力分析，但很明显也是不完全的分析，因为他们从来没有发挥过自己真正的实力。白胡子在顶上之战虽然战死，但他本身实力是下降很多的，因为身体原因，还有小人捅肾这些让他的实力变得很飘忽，他当年的实力是脸黑胡子都想效忠于他的人物啊，但最后因为种种原因死在顶上了。黑胡子在顶上之战中也露过一手，但了解过的都知道在顶上之战他就是来夺取震震果实的也没有发挥过什么真实的实力，之后也就没有怎么出现过了大妈倒是刚打完，但大妈不是理智状态下和路飞他们打的，是在犯病敌我不分的时候进行战斗的，其实就算是犯病咱也能看出来大妈的实力是明显跟路飞他们不再一个阶层的。那么香克斯同样是多年的四皇之一，他的实力又是怎么样的，有很多中说法，说他实力不怎么样在四皇里垫底，但在有些方面又表现出他实力很厉害，厉害到让海军元帅战国都忌惮的境界，而且他在顶上之战中也露过一手，一下就制止看打在兴头上的赤犬，而且香克斯和白胡子比划过是不分上下的，也在大事件之前阻止过凯多进攻白胡子海贼团，最后他成功阻止了凯多。这么一看其实香克斯的实力应该是在四皇之首的位置上的，当然这是白胡子战斗力下降不少的情况下，要是白胡子还是巅峰时期的话，应该是白胡子为首的。', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10121', '0', '「分析」红发香克斯的过去及实力浅分析', '前记，近YY红发的太多了，因为我爱红发，所以忍不住，写了这篇文章，一直属于比较不会表达的类型。欢迎各位指正，那么正文，首先我们把时间屡屡，按照时间来推算，如图所示如果把香克斯的过去分为2大段第一段，就是遇见路飞前的12年，第二段是遇见路飞后至今，说实话，尾田对香克斯的背景画的太少了，我们只能根据一些细节推测出来，那么，从22年前开始，我是这样推测的，22年前当年红发和巴基在罗格镇分开罗杰海贼团解散以红发的梦想那么首先第一个疑问来了关于他的红发海贼团是什么时候组建的呢？我是这样假设的首先大家要注意红发海贼团的旗帜是什么样的？对，骷髅上有三条疤那么红发脸上的疤就是红发海贼团成立时间的关键从第0话我们看下图当时红发脸上是没有疤的也就是说在与金狮子战斗后过了1年左右海贼王死了就是距离现在的23年前但是红发海贼团成立至少是在海贼王在22年前才被处死后所以最早时间推断为22年前那么继续看图这是20年前，金狮子越狱后的事这时候红发来找亚索普希望他能加入红发海贼团看左边的图就可以知道这时候红发已经有自己的船了所以我的推断红发海贼团成立是在罗杰死后的22年前至遇到亚索普的20年前之间那么在遇到亚索普之后红发继续他的冒险那么红发是以怎样的线路或方法来旅行的呢？这个图可以看得出红发的世界旅行我的认为他是以伟大航路作为起点然后逐步的对四个海做旅行正如红发对东海小镇旅行已经快一年了然后又准备回伟大航路了那么这10年里关于红发冒险记尾田几乎没有做任何透露..我又不是来这里写YY文的所以只能继续找有线索的继续分析那就看这张图这个图我们可以总结出2个关键词：鹰眼和断臂如果根据时间推算红发与鹰眼决斗在前在东海丢失手臂在后其实关于红发与鹰眼想最后再说既然写到这里了就先提一下我的观点是红发是在遇到鹰眼以后因为和他比试而出名其实这里我主要是想说下红发出名的原因至于他与鹰眼的故事我是打算放在后面然后再说断臂白胡子，世界最强男对红发说“你的左手到底是给了多么厉害的对手阿”可见。10年前红发的实力已经很强了。说到这里我想再多说一点了很多人说他在遇到路飞前已经是四皇了但是我的看法红发在遇到路飞前不是四皇为什么这么说呢先看这张图从雷利的话可以知道红发是10年前也就是在东海丢失一只手以后去的伟大航路后半段大家都知道四皇是在伟大航路后半段像皇帝一样的男人换句话说，香克斯在这之前并没有到达伟大航路后半段因为如果要去伟大航路的后半段只有2条路1是通过世界ZF2就是从泡泡岛镀膜很显然在第0话雷利在20年前已经在泡泡岛了所以如果在10年前之前还来过这里雷利肯定是知道的所以我们可以这么推断香克斯重新返回伟大航路准备去后半段的时候是在10年前也就是说在这之前他并没有在那里呆过所以他是在10年前去了伟大航路的后半段以后才坐上的四皇的位置.那么再说一下红发的实力白胡子对红发的话“像你这样的高手，你的左手到底是给了多么厉害的对手阿”这句话我们可以看出首先红发是被白胡子肯定的男人被白胡子肯定的高手而且大家注意这可是白胡子说的10年前的红发哦因为如果两个人打斗被对手断一条胳膊不像被留一道伤疤一样简单所以如果要断对方一只胳膊必定是属于几乎秒杀或者是实力强出许多的人然后从白胡子的话，可以看出整篇大海比香克斯强的人不能说是寥寥无几也可以说是太少数吧其次关于红发实力的另一点证明我们看第1话的图路飞被山贼抓到了海上下图右图很明显的说明了红发他们坐船追的路飞那么从左图也看不出来有船影子好的，继续所以大家可想而知红发的速度是非常惊人的这应该算是体术很厉害的证明吧这也是他实力很强的一大表现顺便再多嘴一句这也是10年前。从之前2个图我们都看到了红发10年前实力已经可以说是非常厉害的了那么10年后的今天呢？想也不敢想象…那么说到红发的实力大家肯定很熟悉的还会想到一点就是他的霸气说到他的霸气自然会想到漫画434话那么我们就从红发上船开始回顾一下这个场景我相信很多人看到这一幕都被红发的霸气所迷倒了不得不说红发的霸气船都能被震裂而且按红发的口气说只是略施霸气而已..看到这里我还想再说一幅图也是434里面的当时最早看这一话的时候我看的是动画当时红发接了白胡子那一刀我真是热血澎湃阿敢问在这片海上有几个人谁能接住白胡子这一刀？而且红发还是单手接的看到这里，红发的实力大家也应该都十分清楚了超强的体术+强硬的剑法+惊人的霸气红发阿红发你真是太完美了TOT其实说红发的实力我本来在写前觉得有点废话的但是最近总看到许多人对红发的猜测以及很多讨论所以我才把我的观点写了出来大家轻拍。那么接下来我再说说关于红发与鹰眼、黑胡子的故事红发与鹰眼说到红发与鹰眼很多人的第一反应就是实力其实我想说的不止是这个那么我对红发和鹰眼的故事根据尾田给的信息做了个推测。如有雷同，纯属巧合。22年前罗杰被处死刑红发自己组建了海贼团以伟大航路做基准线以全世界做大冒险直到有一天他们在伟大航路相遇大家知道鹰眼的性格孤高，冷傲，还有点目中无人（详情见鹰眼vs卓洛）虽然当时没成为世界第一大剑豪但是在伟大航路应该也算是有点名气了吧继续说红发与鹰眼当时两人相遇了都是使剑的一场PK是不可避免的接下来是我的推测如有意见不符合骂人的出门左转素质，谢谢我的推测由于鹰眼的心高气傲（不是贬义词）输了这场比赛（尾田从来没有说过2人之间的比试谁赢谁输）鹰眼很有可能是第一次输从未体验过的挫败感为什么这么说呢肯定很多人认为鹰眼和红发只是平手而已吧虽然尾田没交代过他们到底打成什么水平了但是，这幅图大家注意红发的话“找我决斗”一般赢家没事应该不会找输的一方决斗尤其是鹰眼这种性格更不会了但是后来两人成为了朋友不然，如果只是决斗而已红发不可能和他提过路飞的事还有语气~完全是出自朋友的口吻说的话那么关于鹰眼我的观点就到这~最后再简单的说一下红发与黑胡子大家都知道红发的脸是被黑胡子挠的= =我们看图红发说了这么多他字重要的一句话就是他当时没有轻敌很显然黑胡子在遇到红发的时候是红发海贼团成立前（详情见上面讲红发海贼团成立）然而当时黑胡子已经是白胡子海贼团的了罗杰在世的时候黑胡子就已经是白胡子海贼团的一员了然而20年前黑胡子很显然也没有吃果实虽然说当时红发实力没有现在那么强但是至少是跟着罗杰经历过大风大浪的实力不会很弱然而在没有轻敌的情况下被没有吃果实的同为20年前的黑胡子所伤而且还是很深的三', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10122', '0', '红发香克斯的实力', ' 红发香克斯 提及这个名字我想大家都会觉得他是一个潇洒 ，实力强大，是一个有情有义的人。都会想起在顶上战争中的那一种勇气和霸气。我帅吗？ 红发 香克斯 ，原海贼王罗杰船上的船员，后来经过自己不段努力创建了属于自己别具一格的“红发海贼团“。并成为实力强大的四皇之一。香克斯别人都说他是有面子果实的能力，因为他喜欢说 ” 给我一个面子“，当然这个都是海米们娱乐。但是没有吃过任何的恶魔果实他，却能在众多海盗中占有一席之地且地位极高，那么他的能力如何而来？其实他的实力靠的是前 罗杰 和 雷利教导，然后自己埋头苦练几年之后具有一定级别。在和好友兼宿敌的”鹰眼“乔拉可尔·米霍克不断切磋较量中提升自己的实力。两人的切磋较量被白胡子评价”犹如昨日一般“的精彩还有一点就是香克斯的霸气。当他在得知”黑胡子“的底细之后便前往”白胡子“的”莫比·迪克号“，在等上“莫比迪克号”的时候他就用极其强大的霸气威慑了白胡子船上绝大部分船员。且白胡子的船员实力相对来说都是很好的。所以存托处他霸气的强大。香克斯登上莫比迪克号红发能熟练的运用霸王色霸气,武装色霸气和见闻色霸气。最强的是武装色 ，武装色和见闻色霸气相对弱一点。以上纯属个人观点，欢迎大家点评，补充。点击关注 不迷路~~~~女帝镇楼~~妾身 会死吗？', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10123', '0', '红发香克斯和巅峰时期的冥王雷利谁更强？', '同为海贼王罗杰的船员，巅峰时期的红发香克斯和巅峰时期的冥王雷利谁更强？都是霸气使用者，都以剑为武器，都在海贼王罗杰船上待过。都对路飞起到很大影响，巅峰时期都是名振大海的人物。 雷利和红发霸气极强，都是3色霸气集大成者！那么雷利作为海贼王的副船长，路飞的师傅，搞不好当年也指点过红发！罗杰船上的高手因该都是霸气体术高手！红发海贼团也是罗杰海贼团变相的一个传承！2人的剑术都很高，红发是鹰眼敬佩的人物，剑术极高!老年雷利在体力可以的情况下，甚至能压制大将黄猿！巅峰期因该可以收拾黄猿！区别在于一个是船长四皇！一个是海贼王副船长！至于哪一个厉害一些，有待考究，不过从目前看来，雷利使用过几次霸气，不知道你们注意过没有。一次是就人鱼那里，再来就是和黄猿打的那里，一个霸气就把黄猿的光速脚给踢开了，确实很厉害。而且之后还和黄猿抗衡，可知其实力不同反响。三大将是什么概念啊！！这个不用我说大家都知道，而且在岛上酒馆的老板都说了，雷利的实力比路飞他们强100倍！！！ 。一个字 就是强 ，再说说红发，一开始救路飞那里就不说了，看316集 ，红发上白胡子的船，当时是带着霸气上的，一下子就震了几个人。之后就是和白胡子稍微较量了一个，拿一把剑就档下了白胡子的攻击，可想而知了。红发没有恶魔果实但是却那么强，这和霸气是有密切的关系的。而且可以当上四皇的位置.....两人霸气都很厉害，但是不知道有没有霸王色他们两个人之中。不过我想有的话也应该是红发那边有了，毕竟四皇嘛。o(∩_∩)o 哈哈 。雷利的实力也不容忽视。我个人观点还是雷利比较好一些。当然也有大部分人认为海贼王的时代已经过去了，雷利也老了，但是非也，海贼王的部下有很多人都还没出现，这个有待WT解释了', '', '4018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10124', '0', '宇智波佐助为什么那么恨宇智波帶土', '带土在火影快要结尾时宇智波帶土在咱们的主角鸣人的嘴遁下幡然醒悟，但是对于变好的带土佐助很讨厌甚至在带土快要死的时候连一丝同情怜悯都没有，作为同族只人佐助为什么会这样呢？九尾其实佐助怨恨带土是情有可原的是可以理解的，原本宇智波一族在斑的带领下一直和千手一族对抗，后来虽然族人背叛了斑停止了战争，但是村里人对宇智波一族还是不太信任的，后来带土为了自己的目的召唤了九尾让九尾暴走，最后不但害死了四代和四代夫人还害死了很多村民，因为宇智波一族的写轮眼有控制和召唤九尾的能力，所以让原本被不信任的宇智波一族更加被边缘化，这也导致了宇智波一族产生了叛变的想法。宇智波鼬宇智波鼬没有能阻止族人的叛乱，爱好和平的鼬知道一旦木叶村内乱，别的村子可能是乘虚而入爆发更大的战争，于是迫不得已伙同宇智波带土灭掉了全族，从而保全了自己的弟弟和族人的名声，自己背负了一切。宇智波鼬一夜之间变成孤儿的佐助，励志好好修炼杀掉鼬为族人报仇，最终佐助得偿所愿杀死了最爱自己的哥哥，但是最后知道了一切的佐助内心指定是充满了怨恨的，这些事情的始作俑者就是带土，所以佐助怨恨带土也是可以理解的。宇智波鼬', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10125', '0', '《火影忍者》宇智波佐助的经典语录，不要用“天才”抹杀我的努力', '1. 我可是拼了命去做的，不要用“天才”两个字抹杀我的努力！2. 有太多的羁绊只会让自己迷惘。强烈的想法和珍惜的思念，只会让自己变弱。3. 我早就闭上了双眼，我的目的只在于黑暗之中。4. 放弃就代表了结束。5. 孤独，不是被父母责备后难过的那种程度比得上的。6. 我早已闭上了双眼，我的目的，只有在黑暗中才能实现。7. 羁绊太多，只会迷失自我。8. 蠕地之蛇 就算做着飞天之梦，终究也只是个幻想，但还是不想放弃，而盯上巢中雏鸟的你，其实反过来断送了自己，葬身于从此翱翔天际的鹰眼之中。9. 愿望越是强烈，就越会冲淡那份感情。10. 理由很简单，我并不是无法斩断我和你之间的羁绊。只是不想按他的方式做。微信公众号：最句子（ID：zuijuzi），关注开启句子管理神器。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10126', '0', '《火影》宇智波佐助屈辱史，装逼失败真是惨不忍睹', '对于宇智波佐助都知道这是火影忍者里面的男二号，与此同时也被人称之为最为装逼的存在，但这或许就是他身为宇智波这个高傲一族的特性吧，但即便如此他也有装逼失败、受辱的时候，这些情况就是如此。被鸣人打败这个是火影刚刚开始的时候，当时佐助觉得鸣人是个吊车尾没有什么大不了的，但谁知道鸣人学会了影分身之术一下子就将佐助放倒了。第二次就是他们两个人在天台上决斗，但是两个人同时释放出螺旋丸和千鸟，但看到效果还以为自己的千鸟威力更强，但到了后面才知道螺旋丸的威力让他感到非常不甘。被宇智波鼬吊打在中忍考试后不久，鼬来到木叶，佐助知道后找到他准备杀掉他，甚至用上了千鸟，但谁知道结果还是一样，照样被鼬吊打，毫无还手之地，这让他觉得非常的不甘，认为自己这些年的努力都没有任何用处。被音忍四人众吊打还是在中忍考试后不久，当时大蛇丸派遣音忍四人众来接佐助，当时佐助仗着自己咒印的力量还以为可以吊打他们，谁知道他们个个都有大蛇丸的咒印，佐助毫无还手之力，直接被吊打。被奇拉比吊打这个也是如此，仗着自己开启万花筒写轮眼，觉得可以去抓捕八尾了，但谁知道鹰小队第一次执行任务就失败了，奇拉比直接吊打鹰小队，佐助更是惨之又惨，若不是同伴们的救援，早就挂掉了。五影会场被雷影吊打佐助仗着自己万花筒写轮眼和须佐能乎独自硬闯五影会谈之地准备杀掉团藏，但刚去的时候就被雷影吊打，若不是我爱罗及时出现就挂了，最后好不容易闯进去又被水影和土影吊打，要不是白绝和宇智波带土又挂掉了。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10127', '0', '《火影忍者》宇智波佐助的天照已经超越了宇智波鼬', '火影忍者中天照曾被称为最强的物理攻击，号称可以燃尽一切。宇智波鼬虽然没烧死过谁，但是他用的时候还是很激动人心的。宇智波鼬也许是因为身体不好，每次使用天照都会流血泪当然也许这是天照本身的副作用，因为宇智波佐助刚开始使用天照的时候也流出了血泪，此术施展时，万花筒写轮眼会在目光聚焦处释放出黑色的火焰，黑炎会燃烧至目标完全烧尽才会自行熄灭。所以这种术就被取名为日本太阳神的名字——天照。在宇智波佐助换上鼬的写轮眼成为永恒万花筒之后在施展天照的时候就不用流血，这是永恒万花筒带来的好处当时他瞬间用天照秒杀了白绝，天照是从他眼里释放出来的，等后来再一次的遇见白绝的时候就不是用眼睛释放的天照，准确的说天照不是从佐助的眼睛里聚焦出来的天照是从须佐之男的手里出来的，而且宇智波佐助对天照控制的非常好，因为宇智波佐助会炎遁加具土命，可以随意的控制天照黑炎的形态变化，能够将黑炎变化为各种武器装备在须佐能乎上，可攻可防，可以用来控制黑炎的炎势将其减弱消除，也可将黑炎做成“八坂之勾玉”的形状来使用。所以宇智波佐助的幻术能力弱于宇智波鼬，但是使用天照的能力要更好一些，至少可以减轻眼睛的负担，不用每次使用都流血。这点恐怕是佐助没有开挂之前唯一能胜过宇智波鼬的地方', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10128', '0', '火影忍者——宇智波佐助', '宇智波佐助 cn: 知曦摄影：悲情Italia 清眠yume化妆：知曦后期：知曦协力：云云YoY（本篇转载于网络，版权归原作者本人所有，如有异议请告知，并给予马上删除）关注微信公众号“cos领域” ID：cosplay-vv投稿邮箱：423840042@qq.comQQ分享讨论群：71170674商业合作：微信：vv_5656', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10129', '0', '火影中尴尬女主角——春野樱', ' 很多人讨厌火影的小樱，其实我不是小樱的黑粉，一直以来我都不能理解别人为什么讨厌她。我是从小樱和千代婆婆大战赤砂之蝎开始看的火影（当时没有电脑，只能买碟片，前几百集的那个画质，实在难以入目啊，所以就从能看的这段开始了） 总感觉火影中充斥着血统和宿命论，上一代的三忍自来也大蛇丸纲手，对应着鸣人二柱子小樱。但是，本来是感情复杂的三角恋。却在鸣人和二柱子的开挂之路上溃散了，她总不能去抢两位主角的风头。其实作者对角色本身刻画的很好，小樱是个平凡女孩，你用一个平常的眼光看小樱，是不是就好多了，可无奈面对一个个强大的敌人，男女感情实在是难得写，毕竟是热血民工漫画，后期妹子主要是做吉祥物镇场，再说，没有妹子，怎么吸引宅男。就雏田大小姐来说，作者就比较好控制，不用和二柱子与鸣人对戏，额，其实也是本质上是给鸣人配个妹子显得不那么gay里gay气。作者确实把小樱的人设写崩了，初期给小樱的定位类似幻术天赋极高，不知道大家还记不记得，在大蛇丸大闹木叶，释放一个超大的幻术，所有人都中招了，在场的下忍里，只有小樱和鹿丸主动解开了幻术。小樱虽然成绩好，说到底也只是个普通人，能主动解开幻术，证明她在幻术领域确实有天赋。其实漫画中多次提到小樱的幻术天赋，可惜火影越跑越偏，忍术后期成了魔法。幻术有个diao用。小樱也只能跟随纲手变成怪力女。成了医疗忍者。之前的铺垫成了放p。哎，说到底，她只是一个在岸本这个大男子主义老头的笔下被啰啰嗦嗦塑造出来的支离破碎的模式化女性角色中的一个。火影结局，看起来皆大欢喜，小樱嫁给了二柱子，but，独自养女儿，还房贷，独自承受生活艰辛与压力，老公在外。常年不归，寂寞难耐，咳咳，跑偏了，其实是，她依然任劳任怨……所以，察觉到吗！-对于“追逐佐助”这件事，执着到我瞬间对她敬佩了。她才是一根筋，鸣人都不如她。小樱，一个没有故事的女同学，陪一群充满故事的大神走过风雨，无名无利，从平凡中来，到平凡中去。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10130', '0', '火影忍者：纲手公主并没有把毕生全部绝学交给春野樱？', '火影忍者里传说中的三忍就是黄赌毒的代表，自来也好色、纲手好赌博、大蛇丸喜欢搞搞科研。不过这三人的实力不容小觑，而且都有自己的接班人出现。自来也作为鸣人的师傅，传承了自己的忍道及螺旋丸；佐助算是大蛇丸的半个弟子，继承的也最少，只是大蛇丸的咒印；相比而言，春野樱倒是传承了纲手的几乎全部绝学，但是，纲手也没有全部授予春野樱，毕竟师傅得留一手啊。纲手知名的能力有：1、怪力：拥有精准查克拉控制手法的忍者将查克拉集中在身体任何一个部位，迅速燃烧之后爆发出来，当脚或手落空到地面会产生无数的裂痕，留下一个很深的大坑。2、百豪印：彻底解开“阴封印”，让阴封印中存储的大量的查克拉可以自由使用，从而使得发动大型忍术和禁术成为可能，并且由于查克拉灌注全身，体术和怪力的力量都会提高。百豪之术释放的查克拉也可以配合蛞蝓用于群体治疗，并且可以通灵出更大的蛞蝓。3、召唤蛞蝓：用自己的血和要召唤的湿骨林的蛞蝓签定一个契约，以后使用自己的血来结印，就可以召唤通灵兽，随叫随到。反之，通灵兽亦可逆向通灵，召唤施术者。4、医疗忍术：纲手作为医疗忍者而闻名，而小樱的能力也是不差。而以上这些小樱也是都已经在跟随师傅后习得，那么，还剩下什么没有学到呢？纲手除了一个“传说中的肥羊”绰号外，不晓得大家是否还记得，纲手年轻的时候还有个“飞机场纲手”的绰号（自来也因此被暴打），但是这绰号一去不复返，现在这么叫，观众姥爷都不会答应。而我们的小樱呢？好吧，比雏田女神永远差了这一点，没有大凶之兆啊。为什么纲手没有把秘诀交于小樱呢？哈哈，师傅果然留了一手。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10131', '0', '春野樱还是日向雏田？难怪鸣人会和雏田在一起', '如大家所知，《火影忍者》是日本漫画家岸本齐史的代表作，作品于1999年开始在《周刊少年JUMP》上连载，于2014年11月10日发售的JUMP第50号完结；后日谈性质的外传漫画《火影忍者外传：第七代火影与绯色花月》则于同杂志2015年第22、23合并号开始短期连载，至同年第32号完结。故事从主人公漩涡鸣人的孤儿生活开始，四代火影为了保护村子，将攻击村子九尾妖狐封印到了他体内，鸣人因此受尽了村人的冷落，只是拼命用各种恶作剧试图吸引大家的注意力。好在还是有伊鲁卡老师的关心，鸣人的性格才没有变得扭曲，他总是干劲十足、非常乐观纵观整部火影，个人感觉其中的多数人物和剧情都是靠着“执念”二字来推动的大家都知道，本作的女主角，第七班的唯一的女性成员，很喜欢佐助。但是怎么说呢，小樱对佐助有执念，根源在于崇拜，所以怎么她不愿承认佐助已经堕落的事实，还恳求鸣人将佐助带回来。并不是说小樱不好，可能在当代社会，爱情里面讲究的更是势均力敌吧，爱到了卑微的地步，并不让人舒服，岸本感情线刻画一言难尽，而且他们俩有共同语言吗？两个人在一起，针扎在身上会疼能忍是因为喜欢不能忍更多的是不合适，樱的日常表现和在佐助面前的表现，完全变成了两个人。人物性格和情感表现上的断裂，严重影响到读者的情感共鸣。在故事里不去合理铺垫，在事后加一句生硬的解释，读者是不可能追随的，更不可能为之感染。与其说小樱很自私，不如说她对佐助所谓的爱和痴情太廉价，她希望佐助和她在一起，这样她就会幸福。你看，她所谓的爱从来都是为了满足她自己。她最后感动的都是她自己。但实际上，小樱为佐助做过什么吗？没有。她都不去试着了解这个人内心的想法。她只是等待着，哭泣着，不停说着廉价的我喜欢你，却从未真正付出过，然后求着鸣人把佐助带回来。带佐助回来和自己在一起。这就是春野樱所谓的爱，也是我们当今最廉价的爱，说的永远比做得多。而雏田，是一直喜欢着鸣人的女孩子，害羞但也有坚强的一面，日向一族宗家之女。从头到尾都没有太重的执念或负担，甚至可以说是主角和高级配角中最接近日常化的角色了。遇到鸣人会脸红，中忍考试会替鸣人送上药膏，将鸣人拉队友的请求误认为是表白然后紧张到晕倒，一直默默喜欢着鸣人，但却在面对佩恩觉得可能是自己最后一次与鸣人说话时，才敢大胆表白自己最喜欢鸣人。不论鸣人成功与否，她都喜欢鸣人，也不畏惧他人的眼光，勇敢追求自己所喜欢的一切。因为她喜欢的不是那些外显的东西，所以即便鸣人没有家世、没有血统、没有能力、没有才华、没有长相、没有智商，还被别人嘲笑，她也还是喜欢鸣人。她喜欢的就是那个人，所以她感动了我们。也是当今社会我们所缺少的，不看家世背景，门当户对的爱情了了无几。不知道别人怎么看，我真的是非常感动。十五年，700话，鸣人和雏田各自一路的坎坎坷坷，实在是不容易。都说印象是不容易改变的。一个当初那么弱小的女孩，是怎样一步一步得到心爱人的肯定的。好的爱情到最后是会让人变得更好的，这样才是爱对了而不是爱错。执念会害了一个人，像雏田这样努力不作的妹子，为什么不喜欢？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10132', '0', '多少火影迷因为这一战，对春野樱路转黑？', '《火影忍者》644集，宇智波斑和众人开打，斑爷趁着大家不注意时跑掉，夺走了卡卡西的写轮眼。这时候宇智波带土和春野樱正在神威里面和黑绝对抗，宇智波带土为了不让斑爷和黑绝抢走轮回眼，让小樱立即毁掉他的眼睛，但是小樱却磨磨唧唧，一直不敢动手。等到小樱要下手时，斑爷却靠卡卡西的写轮眼进入神威空间，并夺走了带土的轮回眼。也正是因为这一集，火影迷吵翻天了，讨厌春野樱的更加讨厌，对她无感的也讨厌她，都在骂她。春野樱一直很努力，想要追上佐助和鸣人，还有纲手这个火影做师傅，可以说前途一片光明，但是她本人真的没有很令人讨厌，做事拖拉，遇事只会叫人来救她。其实很多人都认可春野樱的进步，但是她的人品和性格实在令人喜欢不上，也被评为讨厌女主的第一名！1、性格春野樱的定位就是花痴，一直喜欢佐助，讨厌鸣人，爱漂亮但是实力弱，拖后退，说话不经大脑，出口爱伤人。经常在佐助面前说鸣人的坏话，还说鸣人没爹没妈好幸福，嘲笑鸣人是吊车尾，甚至为了佐助和好朋友井野（小时候一直在帮她，帮她打架，帮她树立信心）绝交，面对鸣人时老是冒出一股莫名其妙的优越感。鸣人强大起来后，有事鸣人，没事追佐助。在鸣人与佩恩一战中，出了事就只会大喊鸣人，但是天道虐鸣人时，就看着，只有雏田不顾一切冲上去救鸣人！鸣人打败了佩恩后，小樱第一个出来拥抱英雄鸣人，怒刷了一波存在感，救鸣人的雏田反而只能在一旁看着（心疼太子妃）。2、人品不信任同伴，不是怀疑佐井，就是怀疑鹿丸。在大战中无视上司的命令，非要救助无法确认身份的忍者（真实身份是白绝），要不是鸣人及时赶到，差点害死了整个医疗班。哭着恳求鸣人将佐助带回木叶村，让鸣人背负了厚重的枷锁，害的鸣人不惜拼上性命也要完成跟她的这个约定。小樱的这个请求让鸣人感到很焦虑，带不回佐助，觉得自己还不够努力不够强，觉得自己辜负了另一个重要的人的期待。3、利用别人的感情在忍者大战前，春野樱抱着鸣人告白，其实就是一段假告白，利用鸣人对她感情。虽然她这个告白只是为了解开鸣人身上的束缚，但是却用了如此蠢的方法，小樱爱慕佐助，大家都知道，突然来段移情别恋的告白，简直就是对鸣人情感的践踏，感觉她的假表白简直是一场高高在上的施舍，自以为是！本文由中二君漫评原创，欢迎关注，带你一起长知识！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10133', '0', '谁才是你们心中最美的动漫人妻？雏田春野樱上榜！', '说起美丽的人妻，我就这么说几位吧。日向雏田《火影忍者》中的太子妃，雏田有着非常高的人气，也是很多人火影种最喜欢的女性角色。她有很多优点，也是我们很容易看到，看到害羞的她很多人也会有要去保护的冲动。在《最终物语》中荣升太子妃，在博人传中育有一儿一女。也是个人身赢家。结城明日奈人妻中的佼佼者，很多人得梦中女神，她就是来自于《刀剑神域》的Asuna。她真的是一个很完美的人设，要什么有什么；缺什么补什么。简直完美到让我颠覆了对\"人\"的认知。亚丝娜的颜值也是顶尖的，一手绝对领域让人羡慕。春野樱在博人传中，她成功升级为人妻，当然博人传中的她相当美。她是宇智波佐助的妻子，宇智波佐良娜的母亲，但是宇智波佐助常年在外。博人传中的她一颦一簇都让人心动。古河渚她是《CL》中的第一女主，在KEY社的众多游戏中人气也是相当不错的。古河渚是一个命途多舛的人，小时候本该死去的她看着小镇而续命。因此也导致了她的体弱多病，长期休学而遇到了冈崎朋也，最后成为了朋也的妻子，在生孩子的时候难产死亡（其实是必然）。当然结局处，光玉全部收集完成，世界重组，古河渚一家子幸福的生活下去。雾岛董香听说漫画中她已是人妻？还给金木研怀上了一个孩子。董香这个角色真的让人挺喜欢的。第二季开头看着金木研慢慢走远的董香很让人心痛。雾岛董香也是我比较喜欢的女角色。以上！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10134', '0', '《火影忍者》春野樱对佐助的感情是爱吗？', '春野樱对于佐助的感情，我认为确实是爱情，而且有些爱情的过分。我们先来看一下春野樱为了爱对做了一些什么：首先是最初阶段，春野樱还常常与山中井野令人争锋吃醋，想要一夺佐助女友的地位。在这个阶段我们可以很明显的看出，这与其说是爱，不如说只是因为“佐助”颜值而喜欢上的“脑残粉”。但是当故事接近到大蛇丸出场，春野樱割舍自己长发时，她整个人的心境已然发生了改变。特别是当佐助离木叶村而去，春野樱过去阻拦时，很明显的看出那时候她对佐助才真正发生了男女情感上的“爱意”。那么为何说春野樱爱的有些过分呢？原因很简单，她对于佐助的爱有些过分包容。首先，佐助在疾风传战团藏的时候，有过公开伤害小樱的行为。而从结果来看，小樱立刻就原谅的佐助对她与鸣人之间，无论是爱情还是友情的背叛。其次在整个疾风传中，佐助对于小樱甚至没有过一次“爱”的情绪，顶线也就是对她有些羞愧。换而言之，小樱就像对一个人单相思一样，做了一个类似于备胎的角色。最后也是最重要的，我们从博人传看得出来，小樱最后虽然得到了佐助并生了一个女儿，但是对整个家庭的不负责程度已经到达了一个难以置信的程度。哪怕偶尔回到木叶村，也只是和鸣人两人“交流”，家里的娇妻和女儿却宁愿守空房。就这情况，小樱还从没任何怨言，并没有考虑过所谓的“出轨”，一个人又要照顾家庭又要出去赚钱，房贷、生活费自己一个人赚，甚是难得！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10135', '0', '火影忍者：春野樱', '春野樱春野樱，新一代医疗忍者，五代目火影纲手的弟子，与漩涡鸣人、宇智波佐助隶属于旗木卡卡西领导的第七班。原本性格柔弱任性，在木叶忍者学校学习不错。在佐助叛离木叶村后，拜第五代火影纲手为师，学成优秀的体术怪力和医疗忍术。具有良好的查克拉控制能力与清晰的脑力，擅长体术怪力与医疗忍术以及解除幻术。忍界大战结束若干年后，春野樱与佐助结婚，并育有一女——宇智波佐良娜。﻿宇智波佐良娜春野樱在童年时，因为大家都叫她“宽额头”，所以感到自卑，也常用常常的刘海盖住额头，以免被他人取消。这时候，山中井野出现了，并没有歧视樱而且与樱成为了好朋友，为了让樱拥有自信，还送给她一条红色缎带束扎头发露出额头，让樱显露出最可爱的一面。后来因为樱知道井野也喜欢佐助而宣布两人从此是情敌。但是井野对樱的友情仍在，在樱有难时，会出来帮助她。她们心里一直把对方视为毕生好友，并在公平竞争中一起成长。后来两人通过一场不分胜负的较量，重新成为互相扶持的朋友。佐助是樱的一个心结，佐助的离去是她发生重大改变的原动力，当佐助离开后，她苦苦哀求鸣人将佐助带回，鸣人为信守与她的约定而去追赶佐助，可是失败了。她和漩涡鸣人一样，作为佐助的朋友，拯救沦陷于黑暗和仇恨的佐助。因为他们知道，佐助这样不会快乐，即使报了仇又能如何，剩下的只有更加吞噬人心的空虚和寂寞以及刻骨的懊悔。后来当樱看到伤痕累累的鸣人时，终于明白了不能一味的依赖鸣人，她要靠自己的力量变得更强，于是，她拜纲手为师，学习医疗忍术。樱自身在医疗方面极具有天赋，经过努力，樱已经学到了纲手医术的精髓。随着渐渐长大，樱的思想也逐渐成熟，对鸣人和佐助的看法也发生了巨大的改变。作为卡卡西班不可缺少的存在，对鸣人和佐助的关系也起着十分微妙的作用。樱的感情十分复杂，以前追逐着佐助，后来则变得十分纠结，她是否已经无法理解佐助了呢？她也变的相信鸣人，日后佐助归来，三人的羁绊会重新修好吧。春野樱', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10136', '0', '她不是应该宇智波樱，而应该是春野樱', '简单的说，从头到尾，小樱的问题其实只有一个，就是感情问题，但是对于整部来说，感情问题恰恰就是最大的问题。为什么？因为火影一直到最后都是一个感情向的东西，简单地说，友情、梦想、羁绊这些东西在火影中从头延伸到尾，其他一切的力量、武器、各种战场甚至是整本书的剧情全部都是为这些服务的。在这种情况下，小樱这个问题就被无限放大，直接导致整个人人设最后都出现了问题。有不少人说火影结束太快了。但是如果再不结束的话，整本书最后可能都会崩掉，事实上在700话结束的时候，基本上已经濒临崩掉的边缘了，只差轻轻一推。实际上，这也是很多人谈到火影就说“烂尾”的原因。岸本的火影后期在我看来，有一种为了显示规模而堆砌数字甚至堆砌一切的感觉，有点像啥呢？我们在小时候吹牛逼的时候，就喜欢怎么吹。“我家有XXX个”“我家有XXXXXX个”“我家有XXXXXXXX个！”“我比你多！我家最多，我家有XXXXXXXXX个！ ”而岸本在火影后期，恰恰就犯了这个错。或者说他处于一种“江郎才尽”的状态，根本不知道如何描写高端力量的作战。也正因为如此，在最后的第四次忍界大战里完全就成为了岸本的这种“堆砌”的作战，包括后面的尾兽大战斑，忍者联军的集体上九尾外套之流，基本上已经很烂了。也正是因为如此，我在最后一段里唯一认真看完的，就是凯开八门遁甲那几话，因为再不管怎么说，这都比各种技能乱轰要好得多了。有人可能问这和小樱有什么关系？当然很有关系。我在这里就是很想说清楚。小樱的各种恶评，和岸本的后期整个剧情崩坏有很大关系。小樱对鸣人的感情转折点始于哪里？虽然很多人都对这个很有争议，但实际上，小樱和鸣人第一次感情的大转折点在450话，基本上是没有疑问的。但实际上，在我看来，这里不应该作为感情的转折点，而应该作为感情的结尾。始于憧憬，终于成熟忍者学校的故事，成长路上的故事，还有帮你寻找佐助的事情。都在这一刻结束吧。再加上在前面有雏田的表白，其实这一刻小樱选择放下，开始选择做自我足够强大（实际上这也是小樱剪掉头发以后一直的目标）的女人，鸣人和雏田感情走向正轨，鸣人也开始拥有担任火影的实力，在这里结束其实是最好的。到最后和雏田结婚，生几个孩子，带着长门的遗愿给自来也上上香，当个第六代火影。其实挺好的。有人会提到佐助，这个是我在下一阶段要谈的问题。也就是小樱这个人设崩掉的开始。小樱人设的崩掉始于哪里？469话。也正好是毁灭木叶篇结束，实际上在这里，根本不应该重新开始，也就是说《火影忍者》应该在这一刻完结，就是最好的。但是开始了，由此掀开的就是整本书后半段的大崩溃。小樱的崩掉，就是始于此处。这里小樱的人设崩掉是为什么呢？强化鸣人。简单地说，因为小樱此时已经不需要佐助了，或者说她累了，在感情上就又从一个“自主独立的女强人”退化成了一个“普通女忍者”，在这种情况下，她最佳的选择自然就是鸣人。因此，在这时候，岸本就设定鸣人说出这句话。从这一刻开始，男主就不是为了小樱和朋友的承诺来寻找佐助了。而是为了自己和一份对佐助的感情。从“被”到“想”完成了一个蜕变，作为热血漫画，牺牲感情没什么奇怪的，事实上，在这里，虽然小樱的感情线有点崩。但是呢，实际上还好。真正崩掉是什么时候？结尾。岸本让小樱和佐助结婚，还生了个孩子，直接导致樱整个人设崩溃。在小樱和佐助结婚这个问题上，岸本可以说是强奸了所有人，包括书里的主角们。我们谈这个问题的时候，需要正视两个问题。1.佐助需要老婆吗？必须是小樱吗？2.小樱需要老公吗？必须是佐助吗？看似这两个问题似乎是一个问题，但实际上是两个问题。1.佐助需要老婆吗？答案已经很明显了，需要！客观地说，哪怕是为了宇智波家族血统延续，佐助还是需要娶上一个老婆，毕竟此时全球就剩一个宇智波佐助这个宇智波家族的人了，其他都以各种各样的方式先后魂归西天。但是……一定必须是小樱吗？当然不一定，这个问题又需要延伸到佐助爱不爱小樱了，事实上答案很明显，不爱，佐助结婚了以后之所以对小樱还不错，但是只是出自于丈夫的责任。在这种情况下，娶不娶小樱其实没什么区别。说句不客气的话就是，哪怕后来佐助娶的是香燐，佐助的态度也不会有任何的区别。照样生个孩子，说不定以后会多生几个孩子。然后周游世界，路见不平拔刀相助之类的，香燐出问题了佐助一定会去救。小樱和佐助没有爱情。是的，有温情和亲情，但真的和爱情没什么关系。2.小樱需要老公吗？不需要。真的不需要。作为新三忍里唯一一个以凡人之身力近神明的人。小樱不需要任何人证明自己，也不需要任何这类普通感情。即使有，也应该是小樱主动选择的，而不是佐助。事实上，小樱在结束以后，最好的结局并不是跟佐助结婚。而是跟纲手一样，选择独身。一方面，与小樱追求“自我”的情况相符。即从一开始靠佐助，中期靠鸣人，后期到“靠自己”。另一方面，也不至于在嫁给佐助以后，连生孩子都要生在路上。小樱真的幸福吗？或许是吧。但这真的是她所追求的吗？我想并不是。从一开始那个即使是追求佐助都是想用自己魅力勾引佐助上钩的美少女，甘为人妇，回到了很多年前，甚至还不如当时的少女心态，跟着他开始流浪世界。抱歉，这样的春野樱，我真的不想接受。如果说鸣人和雏田是正常的女追男恋爱修成正果，那么小樱和佐助完全就是一场年少时的作者的一种YY，最后被用一种搞笑的方式实现。这样的人设，甚至已经不能说是崩了。我再重申一遍。岸本最后的这个选择，强奸了所有人，包括主角。为小樱感到不值。她不是应该宇智波樱，而应该是春野樱。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10137', '0', '《火影忍者》鸣人雏田大婚在即，雏田越来越美了', '《火影忍者》马上就要完结了，鸣人雏田大婚在即。想来这么多年来，从相识到相知再到相爱，鸣人和雏田在一起经历的很多困难挫折，作为他们两个爱情的见证者，真心祝福他们能够永久的幸福下去。刚刚看完了最新一集，不知道大家有没有这个感觉，从准备婚礼开始，雏田被画的更美了。难道是好事将近，我的错觉吗？真是美腻了。下面是我挑选的几张截图，分享给大家。雏田为同伴担忧雏田被伊鲁卡老师逗笑，像朵花雏田内心满是愧疚，不想给大家添麻烦的神色雏田看着天空，满是憧憬雏田坐在伊鲁卡身边，马上要结婚的人了，却像个孩子。最后，雏田精美套图，长按复制关注公众号：天师张品漫', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10138', '0', '雏田为什么会嫁给鸣人呢？原来鸣人曾对雏田做过很奇怪的事情', '连载十四年的火影忍者完结了，最后的结局还算比较完美，大家都知道，火影忍者结局时鸣人和雏田在一起了，小樱终究还是跟着佐助在一起了，说实话，这个结局我并不是很喜欢，但是一想到雏田和鸣人也就勉为其难的接受了，然而再重新刷剧的时候，我发现鸣人和雏田大结局时是必定在一起的，原因便是因为鸣人对雏田做了一件很奇怪的事情。那么鸣人到底对雏田做了什么事情呢？大家应该清楚在鸣人小的时候，鸣人因为没有朋友所以非常的孤独，当时的雏田和鸣人很像，所以雏田就一直默默的关注着鸣人，也正是因为如此，所以才在雏田的心里埋下了一颗爱情的种子，在雏田的心里慢慢的生长发芽。但是这不是关键因素，关键因素是因为鸣人对雏田做的那件奇怪事情。在火影忍者中鸣人和雏田曾经一起执行过寻找微香虫的任务，最后雏田用白眼找到了他们要找的微香虫，鸣人高兴地抱住雏田，当时雏田脸红的像一个红苹果。当时鸣人对雏田的那一次拥抱或许对于鸣人来说没有什么感觉，但是对于大家闺秀的雏田来说，那一次拥抱就决定了她必定要嫁给鸣人了，看来鸣人真是一个撩妹高手啊，鸣人真的很懂雏田的内心啊，就因为这一次的拥抱让雏田已经非鸣人不嫁了。看来在火影忍者开始时岸本齐史老师就已经决定让雏田和鸣人在一起了，不过说实话雏田长大后实在太美丽了，比小樱强无数倍，鸣人能娶到雏田真的是鸣人一生最正确的决定，不知道动漫迷们怎么认为的呢？如果各位动漫迷们还有什么想法也欢迎多多的留言让大家一起来讨论。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10139', '0', '火影忍者：最让雏田感到羞耻的四张截图，差点被夺走初吻！', '1、雏田和小樱去泡澡在火影忍者的原创剧情中，木叶十二小强为了缓解疲劳，都去澡堂里泡澡了。而雏田在泡澡的时候，却一直把双手按在胸前。小樱认为雏田是太害羞了，对雏田说“大家都是女孩子，不要害羞啦~”而雏田的回复却让小樱脸都黑了，“那个...好像浮起来了！”好吧，雏田确实是因为害羞，但是害羞的原因却与小樱想的有点不一样啊！另外，求大家科普一下，那个...真的会浮起来吗？2、被迫穿上兔女郎服装同样是火影忍者动画版的原创剧情，在一次节日庆典中，纲手安排雏田负责迎宾工作，同时还特意给雏田选了一件兔女郎服装。不得不说，纲手确实非常懂男孩子的心理啊，她给雏田挑选的这套服装，简直是太棒了！只是雏田的性格比较容易害羞，这样暴露身材的衣服，对于雏田来说还是有点羞耻的，脸蛋都害羞的红起来了~3、被药师兜“袭胸”中忍考试期间，雏田被失去理智的宁次打成重伤，连吐了好几口鲜血。但是为了观看鸣人和宁次的比赛，雏田还是带伤来到了现场，可是在比赛进行到一半的时候，雏田的伤势突然严重，昏迷了过去。当时伪装成暗部人员的药师兜正好在场，于是便给雏田做了紧急医疗。只是他的这只手实在太过分了，竟然如此明目张胆的袭胸！​4、初吻差点被抢去在火影忍者剧场版《the last》中，雏田为了保护大家决定牺牲自己，跟着大筒木舍人走了。舍人为了与雏田结婚，用特殊的方法操控了雏田，并与雏田举行了婚礼。眼看着两人要进行“誓约之吻”了，鸣人突然现身，把雏田救了出来！幸好鸣人出现的及时，否则雏田的初吻就被别人夺走了！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10140', '0', '火影忍者中，鸣人为何会倾心于雏田，因为最后一点谁都无法拒绝', '美丽，温柔，专一，坚强，勇敢，家境殷实，为爱的人付出一切无怨无悔我觉得在现实生活中这种女生的数量应该比大熊猫还少吧鸣人碰上了应该感谢他爸在世的时候拯救世界为他积了德，祖坟冒青烟了吧对，即使是最强的男人能遇到这种好女生也应该暗自庆幸他爸金色闪光的那位可要火爆多了如果你有个哥们，平时有出力的地方都叫你，后来自己捅了娄子还求你帮他补锅。最后你特么要被人打死了，他特么怂了。你怎么看他？如果你有个同事，平时悄摸声儿的跟你说句话都小心翼翼的。后来你特么快被人打死了，他顶着狙击枪上来抢你的尸体。你怎么看他？人心都是肉长的呀！说几个关键词害羞、可爱下忍时期，见到鸣人会脸红的说不出话，不由自主的玩弄手指，再加上娇萌妩媚、略显夸张的配音，吸粉无数宅男~勇敢、坚持中忍考试时，面对堂兄宁次，雏田表现出明显的胆怯，在鸣人的鼓励下，她拿出了勇气，虽然最后仍然战败，但已经不再是那个胆小的雏田~敢爱佩恩攻打木叶时，雏田的父亲劝她不要去铤而走险，可能会帮倒忙。但是雏田为了增员自己喜欢的人，毅然决然的来到鸣人身边，并且向鸣人表白，为了救鸣人被佩恩打成重伤，也因此激发了鸣人体内的九尾查克拉~最后一点，最关键的一点，胸大...不解释', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10141', '0', '火影忍者，鸣人与雏田约会钱不够，雏田怎么解决？', '漩涡鸣人的人生是完美的，不仅成为了梦想中的火影，更拥有美满的家庭，如果说谁对鸣人帮助最大，那么鸣人的妻子；雏田绝对算一个，雏田从小就喜欢鸣人，帮助鸣人的次数很多，今天给大家简单介绍下其中最有爱的五件事，满满都是爱！考试作弊在中忍考试时，鸣人完全不能够答对任何问题，也没有发现中忍考试的精髓，只能在座位上抓耳挠腮，但是对于拥有白眼的雏田来讲，完全没有任何压力，雏田甚至担着扣分的风险，把试卷给鸣人抄袭。救命·在佩恩进攻木叶之时，鸣人在与佩恩们的对战中落入下风，被佩恩的黑棒戳中，并击杀深作仙人，在此之时，雏田不顾个人安危，前来搭救鸣人，并对鸣人告白，使用柔步双狮拳帮助鸣人，最后被佩恩重伤。救命二（动画原创），为了寻找到珍贵的昆虫微香虫，鸣人与雏田等人出发寻觅这种昆虫时，被人逼入绝境，在危难之时，雏田挺身而出，雏田使用自创招数柔拳法·守护八卦六十四掌拯救鸣人。一巴掌日向宁次为保护鸣人身死之时，鸣人一度陷入无尽的自责之中，雏田给了鸣人一巴掌，让鸣人从紧张焦虑等瞬间变的像满血复活了一样，这一记温柔的耳光，将鸣人带离阴暗。约会（番外）鸣人与雏田约会的时候，看了佐井的约会手册，对雏田说要请吃大餐，无奈囊中羞涩，雏田使用白眼发现其干涸的钱包，十分贤惠的没有点破，而是对鸣人说想吃拉面，拉着鸣人去了一乐拉面店。动漫先生公众号；dongmanxiansheng，长按复制，福', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10142', '0', '《火影忍者》日向雏田的八张图片，哪一张才是最美雏田', '《火影忍者》日向雏田无论是小时候，还是长大后，颜值都很高，性格还是那么温柔，是一位非常完美的女神。在雏田的这八张图片中，哪一张才是最美的她呢？下忍时候的雏田中忍时候的雏田第四次忍界大战结束后的雏田忧郁的雏田沉睡的雏田穿黑色婚纱的雏田结婚后的雏田天天梦境中的雏田', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10143', '0', '火影忍者——雏田图片集——你们要的雏田都在这里了！', '前段时间，小编发了一个火影美图——适合做壁纸、聊天背景的火影美图~（第三波）因为没有用雏田来镇楼，遭到@最wave、@abc超级奶爸、@垃圾假童鞋的吐槽~为了弥补不足，小编特意从网络搜罗了一大堆雏田的美图，这次全部整理到这里，让你们一次看个够有些带水印的，不清晰的就不贴过来了哈~喜欢雏田的赶快来抱图吧，还有，如果还想要谁的图，欢迎留言，小编去整理这样的雏田太惊艳~好长的一组图，描绘了雏田从小到大到为人妻的形象前方一大波虐狗图正在向你靠近，单身狗请跟小编一起自行撤离现场公主抱系列亲吻系列地咚（鸣人、雏田，泥萌在博人面前这样真的好吗~）扑倒就是秀秀秀小编表示受到一万点伤害，回家睡觉去了~/doge白了个白。。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10144', '0', '火影忍者：雏田四次差点被掳走的经历，最后一次险些成为压寨夫人', '说道《火影忍者》就不得不提到，火影的第一女神日向雏田，雏田作为日向一族宗族的长女，宗族第一继承人，妥妥的名门望女，但一丝没有富二代的秉性，反之从小就柔柔弱弱的，正是因为这一气质导致性格柔弱的雏田一再被坏人们盯上，再加上白眼的血界继限和宗族长女继承人的身份，雏田经常遇险危急生命，一次次的被坏人掳走，有一次次都被男神鸣人所解救。这才使鸣人一直上演了不少英雄救美的戏码，导致坏人们虽然一次次掳走雏田，但最终成功掳走雏田的还是拥有猪脚光环的鸣人，成功掳走雏田大小姐的人和心，最终抱得美人归。以下是雏田在火影的剧情中被俘虏的四次经历，一次比一次惊险，可见大小姐也不是好当的。第一次：云隐村使者夜袭宗族对阵日向日足火影剧情中云隐使者来到木叶村拜访，到了夜晚却偷偷跑到日向宗族里掳走大小姐，像借机获取日向一族白眼的能力，但使者没想到日向一族实力惊人，刚掳走雏田还没出门，就被雏田的父亲逮个正着，仅仅一个照面就被秒杀了，后续导致影响云隐村和木叶村的纷争，眼看即将要引起战争，最终还是由日向日足的弟弟日差出面承担了所有责任，选择牺牲自己保全了木叶，避免了战争。第二次：中忍考试云隐村对雏田下手又是云隐村，真是万恶啊，TV412宁次外传中，在中忍选拔考试中，药师兜对在场的所有人使用了幻术，当先挣脱幻术的两名云隐村忍者乘机将雏田抓走，之后醒来的宁次和牙两人，追踪到敌人，追上后打败了云隐村忍者救回了雏田，这也是第二次被云隐村盯上。第三次：岩隐村针对雏田不过这第三次雏田反而主动出击和敌人对抗起来，最后救了大家。岩隐村同为虫子能力操控忍者为了得到油女一族的微香虫，开始对雏田下手，拿雏田作为人质，要挟鸣人等人交出微香虫。雏田在对战岩隐村忍者的中间虽然或多或少又得到鸣人和志乃的帮助，但最后还是依靠自身的实力挣脱束缚，用自己的自创忍术打败了敌人，解救了大家，雏田也证明了自身的成长。第四次：大筒木舍人掳走雏田第四次换成了外星人掳走雏田，这也是最新的一次，这时雏田已经成为了鸣人的妻子，成为人妻的雏田魅力也还是非常大，这次被大筒木一族盯上，大筒木舍人在将雏田带走之后，鸣人瞬间心急如焚，恨不得立马飞奔月球救下雏田，最终鸣人成功解救了雏田，雏田又一次芳心暗许，看来雏田的心已经彻底被鸣人捕获了，这次也是雏田最危险的一次，差一点就被抢回山寨当压寨夫人了。不知后续雏田是否还会遇到多少次危急呢？毕竟雏田的魅力无法阻挡。如果可以成功让你掳走雏田，你愿意当这个坏人么？欢迎留言探讨。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10145', '0', '《火影忍者》连写轮眼都能看穿的人，智商跟宇智波鼬比如何？', '火影忍者中不少人可以老智商吃饭，比如宇智波鼬，鹿丸，四代火影波风水门等等，很多人认为宇智波鼬比鹿丸还聪明，因为当初鹿丸看穿飞段的招式比宇智波鼬长其实鹿丸分析的剧情长点，但是他用的时间并不长，只不是内心活动的时间长罢了，现实中是很短暂的时间，短到飞段都没来得及下死手就破解了，而宇智波鼬是用写轮眼在一旁看飞段杀死角度一个心脏之后分析出来的所以严格来说鹿丸要快一点，而且鹿丸没有借助写轮眼。不管怎么说这两个人都是看一遍对方的招式就分析出破解方法的人。其实早期的时候有一个人的经历跟宇智波鼬的这次一样，他就是白卡卡西跟再不斩交手的时候白一直在观察，这点就像宇智波鼬观察飞段跟角都一样。他们打完之后白就分析出了写轮眼的秘密，而且非常透彻的解析出来，并找到应对方法再不斩的解释是白相当聪明，任何招式只要看一遍就能分析出来，虽然火影忍者里不少人都可以这样，但是相当一部分的人是有瞳术的，比如卡卡西跟宇智波鼬没有瞳术，只看一遍就能分析出应对方法的，火影忍者里有三个人，他们分别是波风水门，鹿丸跟白。鹿丸的父亲智商更高，他应该也可以，但是剧情中没有太多描述白虽然有血继界限，但是他的血继界限跟瞳术和分析能力无关，就智商而言，恐怕白不输给宇智波鼬。更多精彩内容，请关注阿呆趣谈', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10146', '0', '宇智波鼬为什么要杀死自己的族人？', '很多人表示不理解为什么鼬会为了木叶而屠杀自己的族人，可能你们都跳过鼬真传……鼬当时别无选择，当时团藏只给了他两种选择，一是站在宇智波那边参与叛变，然后和宇智波一族一起被灭族。二是帮助木叶这边，灭掉宇智波一族，可以保你弟弟。虽然当时他爸也给了他一个差不多的选择，但是鼬并非常人，他早就不被家族这种形式上的东西束缚，他要顾全大局，他希望和平。他知道宇智波一族的野心很大，狂妄自大器量又小的一族，他爸用万花筒给他看了宇智波的未来。他后来杀族人的时候他爸用分身和他对话，为什么这样选择，鼬也用万花筒给他爸看看他选择的未来，就是佐助……鼬肯定也想过，如果当时选择参加政变，虽然宇智波一族当时也很强，但整体上还是要弱于木叶，毕竟木叶还有这么多暗部，还有根……他自保可能不成问题但保全佐助就难说了，那样的情况他不想要，他不希望佐助出事或者跟着他过天天被追杀的日子，他参与政变势必会造成两败俱伤，到时整个木叶会岌岌可危然后被他国趁虚而入！所以你让他怎么选！鼬在了解宇智波一族叛乱的那一刻起就只剩下两条路了，要么全死，要么自己下手，以此为功劳留下佐助一命。木叶高层在了解宇智波叛乱时就不会留活口了，作为族长和头目的富岳必须得死，就算鼬不出手，三代和团藏也会下手。至于宇智波的老幼妇孺，只要拥有宇智波一族的血继限界，木叶高层就不可能留下其性命，屠戮宇智波的所有反叛战力，就意味着活下来的宇智波族人都会开眼。（包括万花筒）这对木叶村来说是极大的威胁。也就是说鼬所谓的大义灭亲其实是必然的，如果不这么做，不要说佐助，连鼬都不可能活下来。如果鼬不去灭族，宇智波还是会被木叶灭掉（就像团藏说的），而且是被冠以叛徒之名，而且所有人可能都会死，鼬这么做即能保证宇智波的荣誉，又能让佐助代替宇智波一族的未来，唯一的缺点就是鼬要背负所有人的痛恨和唾骂。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10147', '0', '宇智波鼬经典语录', '1、那夜的黑暗，是你的光明 那夜的杀戮，是你的和平 那夜给你的恨，是你给的爱 那夜的月，是两个人的泪2、无论你今后走怎样的路，我都一直深爱着你。3、生命会生出，生命会死去，但生命同样能拯救。族人聚集，形成村落，然后一些人和它产生关联。如果这些人被称作同伴，我们的确算是同伴。正因为扯上了关系，才会那样。因为扯上了关系，那家伙..才死了。曾经我并不明白，真正的痛苦，失去生命的疼痛，只有失去了才知道，不！才被迫察觉到，不愿失去最重要的东西的心情。 有朝一日，你也会拥有这双眼睛吗？在你也了解到这悲哀的那一天。4、能原谅自己，认可真实的自己的人，才是真正的强者。5、当决定接受自己本该面对的结果，不再逃避之时，伊邪那美的循环就会被解开，这是一个引导术者不再依赖忍术，主动接受命运的忍术，劝诫术者接受命运承认过去的教训，向前进步。自以为得到了一切，自以为无所不能，于是盲目冒进，正因为如此，才会害怕失败，并且，欺骗自己说，我不可能失败，欺骗自己的下场，就是变得不再信任他人的力量，我曾经就是这样，兜和我同是被这个忍者世界玩弄于鼓掌之中的人，因此理解他为什么无论如何不能认可和原谅自己，虽然他的所作所为是不对的，但是，只指责他一个人同样是错误的。或许，没有那个单一的个体是完美的，所以才会吸引能够补充自身不足的东西，只有相互补充相辅相成，才能慢慢开始向好的方向接近。6、所谓的自我认知不是达成一切，不是什么事情都要做到完美，而是知道自己所能做到力所能及的事情和不能做到无能为力的事情，我要说的是，要学会原谅自己的无能为力，正因为自己不能摆平一切，所以才会有弥补自身不足的同伴在身边。7、我说过的吧，不要仅凭外表和臆测去判断一个人，我稍一忍耐，你们就会继续胡乱猜想下去，总是“一族一族”的，你们是高估了自己的能力，所以完全不了解我的实力，因此才会倒在那的！8、真相……往往深藏于黑暗之中。9、弟弟，不论经历多少岁月，不论走过多远路途，我都会永远守护你。原谅我，佐助，这是最后一次了……10、为你背负，是我的全部！11、这个世界就是这样，每个人每时每刻都在做出属于自己的最优选择，最后整个现实处于一个微妙的动态平衡，平衡随时被打破，但新的平衡又会瞬间建立起来，就像在下棋，双方所有的棋子都被对方互相锁住，无法解开，而现实，是一盘无数人在下的棋，那就不只两两平衡互锁了，那是张网，将所有人，将爱情、亲情、友情、利益、正义、梦想、未来，一切的一切，动态地锁在一起，微妙地互相影响，这就是现实。12、在月读的世界里，空间、时间、质量，全部由我来支配。13、你的写轮眼能看多远？14、我一直扮演着你理想中的大哥，是为了确认你的器量，你成为了我测量自己器量的对手，也包含着这种可能性，你厌恶我憎恨我，一直希望能够超越我，所以才让你活下来，为了我自己。你和我一样拥有将万花筒写轮眼开眼的资格，但是，这有一个条件，把自己最亲密的同伴杀死，就像我一般。15、优秀也是有烦恼的，有了力量就会被人孤立，也会变得傲慢起来，就算刚开始时被寄予了最大的期望。佐助，你是我这世上唯一的兄弟，我会作为你必须超越的障碍而存在下去，持续存在于这个世上，就算是被你憎恨也是一样，这就是所谓的兄长。16、忍者之所以为忍者，就是因为其经常要被迫作出残酷的抉择。17、并不是成为火影的人就会被大家所认可，而是被大家所认可的人才能成为火影。18、无论村子有多么黑暗，有多少矛盾，我都是木叶的宇智波鼬。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10148', '0', '是我不想当七代目的！犬冢牙的十二个小知识，全对的举手！', '是的，随缘更新的小编我更新了。上回写了志乃，这回还是写木叶十二小强吧，嗯，牙。准备好了吗？请听题：1，牙的搭档叫？A，赤丸B，白丸C，冰爪额，答错的请出去，，当然是赤丸。2，那么，赤丸为什么叫赤丸？A，因为它小时候是红色的B，随便起的C，因为吃了兵粮丸后会变成红色明显是第三个。3，牙最灵敏的是？A，视觉B，味觉C，嗅觉嗅觉不解释……4，牙的姐姐有一份工作是？A，兽医B，警察C，律师今天的题都这么简单……是的，兽医。5，牙哥和赤丸可以合体变成？A，双头狮B，白虎C，双头狼双头狼啊……6，双头狼的必杀技是啥？A，通牙B，牙通牙C，牙狼牙看名字就知道，牙狼牙啊。7，中忍考试时，鸣人靠什么逆转牙的？A，九尾B，靠个屁C，后宫术真的是靠个屁……8，追佐助的时候，牙的对手是？A，左近右近B，鬼童丸C，君麻吕如果不是左近右近可以分开，牙早就赢了。9，还记得牙曾经对带土（阿飞）用过什么术吗？A，通牙B，牙通牙C，牙狼牙嗯，普通的通牙，但无效……10，记得佩恩入侵的时候，牙和他的家族对上谁吗？A，修罗道B，人间道C，饿鬼道哈，不记得了吧，是饿鬼道。11，第四次忍界大战，牙曾经对鸣人说，在谁面前用不着逞强？A，雏田B，小樱C，佐助佐助毫无违和感……不过答案是雏田。12，在斑爷的无限月读中，牙梦见自己干啥了？A，养很多狗B，当火影C，和鸣人在一起虽然有个奇怪的选项，但牙梦见的是自己当火影，还让大家都爱狗……嗯，就这样，谢谢支持。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10149', '0', '火影忍者，犬冢牙的媳妇上线！且看牙同学教科书般的搭讪方式！', '鸣人雏田大婚，作为雏田所在第八班队友的犬冢牙和油女志乃，也开始为结婚礼物行动起来。在寻找礼物蜂蜜酒的途中，牙的媳妇正式上线！讲道理，妹子长相一流，除了头发颜色不同外，酷似鸣人老妈玖辛奈。下面牙同学就开始表演了！碰上一（长）见（得）钟（好）情（看）的妹子，该怎么做呢？第一步，不要怂，就是干！脸皮要厚，要主动上去跟妹子搭讪，关键时刻开不了口，那就活该单身一辈子！第二步，大概认识以后，就是寻找共同爱好。没有共同语言，就没有了交流下去的基础！对了，还要适时的夸奖下妹子，要做到不留痕迹，像拍领导马屁一样行云流水！第三步，该吹牛逼的时候还是得吹，反正吹牛要不要钱！要让妹子觉得你很厉害，我相信，没有哪个妹子希望自己的另一半是个弱鸡吧？完成这几步以后，一个完美的第一印象就建立起来啦！看看别人牙同学，多厉害！（此处心疼志乃大电灯泡一秒）不过，这只是成功了一小步，以后还是得继续努力，采用各种方法和妹子亲近，过不了多久你就成功啦！小伙伴们！牙同学的把妹技巧你学到了吗？欢迎 关注 本头条号哦！！O(∩_∩)O', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10150', '0', '油女志乃为何很少败绩？实力只是其中的一个原因', '《火影忍者》油女志乃是木叶十二忍中最低调的一个，智商也是仅次于鹿丸，实力排在十二小强前五位。同时，志乃也是十二忍中几乎没有败绩的同期忍者，为何他的实力排在三忍之后，却很少被人打败过呢？油女志乃志乃除了拥有实力外，还有一个重要的原因，那就是不自负，对每一位对手的重视态度。在油女一族对战晓组织的小南时，志乃说了一句话：“对任何对手都不能大意”。油女一族不管是面对任何对手，志乃都全力以赴，从不轻视任何一个对手，这就是他为什么能够几乎保持不败的原因。从志乃对对手的重视态度，可以看出，他是一个很认真的忍者，也是一个很谨慎的人，虽然存在感低了一点，但是只要有他在，就会让伙伴很有安全感和放心。小南虽然他们和小南的一战中，没有打败小南，但是志乃他们也没有处于下风。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10151', '0', '《火影忍者》油女志乃的眼睛是什么样子的？', '油女志乃真正的样子是谁都没有见过的，只有油女志乃的小虫子知道他的眼睛是什么样子，因为油女志乃的眼睛就是小虫子的通道，这是一个很可怕的忍术。油女志乃的家族是油女家族，这个家族的每个人都把身体当成虫子的巢穴，在身体的某些地方聚集着查克拉，用这些聚集起来的查克拉喂养小虫子，这些小虫子每个人都不一样，都有不同的作用，在油女家族战斗的时候会让小虫子出来代替查克拉和忍术。每一个油女家族的人身体都有一个部分要作为小虫子们进出身体的通道，这个说法听起来很可怕但是很有道理，如果虫子没有进出身体的通道，他们难道从皮肤钻出来吗，那样就更可怕了，只有在最危急的时候油女家族的人才会这么做。油女志乃一直不用真正面目展示给别人看，是因为油女志乃选择了眼睛当成小虫子进出的通道，动画里面有一集说的就是这件事，油女志乃的虫子都是从两个墨镜下面往外飞出来的，由于这样的景象太可怕了，还会引起同伴们的不安，所以油女志乃一直都戴着墨镜。动画里还有一集不小心露出来的油女志乃的眼睛，看起来好像很小，不过这个不知道是不是失误，因为官方也没有说油女志乃的眼睛就是这样的，这一集的动画也只有这一次稍微露出来一点眼睛，看起来不像是故意的，更像是制作出现了错误。我希望这一次露出眼睛是制作错误，因为油女志乃的眼睛还是保持神秘感显得更有魅力。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10152', '0', '要问为什么的话……油女志乃的十二个小知识，你能全对吗？', '嗯，好久不见，小编佛系更新来了！今天应邀写一下志乃，哈哈，一个有趣的家伙。话不多说，准备好了吗？请听题：​1，志乃是第几班成员？A，三B，八C，十嗯，和雏田、牙一起的第八班，侦查班。2，番外题，你知道油女志乃这个名字的日语发音和哪种动物的日语相近吗？A，蚊子B，跳蚤C，蟑螂是的，是小强，答案是第三个。3，志乃的什么很低？A，智商B，战斗力C，存在感这是一个梗啦，志乃存在感很低……4，志乃的兴趣？A，虫子B，种花C，钓鱼虫子啊，想都不用想。5，中忍考试第二场，志乃看到了谁的超强实力？A，我爱罗B，鸣人C，佐助我爱罗的疯狂……6，志乃中忍考试最后的对手是？A，宁次B，勘九郎C，佐助勘九郎，不过弃权了。​7，志乃用得最多的虫子叫？A，微香虫B，寄坏虫C，大角虫秘术-寄坏虫。8，说起来，寄坏虫是吃什么的？A，蜂蜜B，树脂C，查克拉你觉得养在身上的虫子会吃什么，查克拉啊。9，对了，志乃把什么放到了勘九郎的护额上？A，雌虫B，雄虫C，侦查虫嗯，雌虫。然后雄虫会找到位置。​10，志乃中了勘九郎的毒，是谁救了他？A，油女志微B，纲手C，小樱答案当然是他父亲，油女志微。11，佩恩入侵时，志乃他们一族对上了？A，小南B，畜生道C，饿鬼道哈，没有对上佩恩六道，而且遇到了小南。12，最后的最后，志乃成为了？A，暗部B，忍校老师C，侦查部队因为自认为对付孩子很有一手，就当了老师……好的，虽然志乃很啰里啰嗦的，但我就不啰嗦了，就这样。谢谢支持！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10153', '0', '奈良鹿丸：女人真麻烦啊……', '有一种生活，叫做平凡。有一种姿态，叫做低调。有一种口头禅，叫做\"真麻烦。\"', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10154', '0', '《火影经典语录》奈良鹿丸篇', '我啊，只想安稳地做个忍者，安稳地生活，和一个不美也不丑的女人安稳地结婚，安稳地生两个小孩，第一个是女孩，之后是男孩。等女儿结了婚，儿子独立后便退休，之后闲时和朋友下下象棋或围棋，过着悠哉悠哉的隐居生活，然后先太太一步离开这个世界，能有这种人生就好了。喜欢蓝天的鹿丸奈良鹿丸，IQ超过200，是木叶甚至整个火影里数一数二的分析师，虽然性格消极，战斗欲望不高，经常挂在嘴边的一句口头禅是“麻烦死了..”，但是确实是一个实力很强的智慧型忍者。鹿丸这个名字在日语中有很深的内涵，Nara即奈良的意思，是日本的一个行政区，奈良县在日本是以这个地区养鹿而闻名，在奈良公园里就好比中国承德的避暑山庄一样有很多鹿，大人小孩在此喂鹿，Shika是鹿的意思，Maru，日语里面是圆圈的意思，而奈良一族在木叶村的作用是提供鹿角等药材，拥有秘术影子模仿术。鹿丸和鹿在中忍考试篇中，鹿丸以一个没有斗志，态度懒散的形象出现，但在整个下忍队伍中表现出了出色的指挥家头脑，即使放弃了比赛，也是唯一一个晋升中忍的忍者（类似于现实中破格录取的特长生..）,随后的大蛇丸进攻木叶篇章里，他是三个没中幻术的下忍之一（另两个是春野樱和油女志乃），故意躺在地上消极避战啊有木有>.<。消极的鹿丸在之后的营救佐助篇，鹿丸独自一人对抗幻术忍者多由也，虽然打的有声有色，但最后查克拉耗尽，被咒印二的形态的多由也险些杀掉，幸亏好老婆手鞠及时赶到，才能幸免于难。果然爱情的力量比忍术更靠谱，恩恩*.*。美救英雄其实鹿丸的风格转折点是在追捕飞段和角都篇里，作为晓组织的变态二人组，没有主角光环的鹿丸即使知道不死飞段的弱点，也无法战胜角都，最终阿斯玛被杀，鹿丸发誓为恩师报仇，并在最后成功将飞段封印在自己的家族圣地下。鹿丸vs飞段之后的佩恩入侵，第四次忍者大战中，鹿丸担当了战场指挥家的角色，与忍者联军一起，对抗秽土忍者和宇智波斑。鹿丸在整个火影中的定位属于天才型，多次为同伴提供出色的情报分析，为整个火影的剧情增添绿叶。最后为大家奉上一句鹿丸经典口头禅，真是麻烦，我的对手居然是个女人，真不好下手……妹子别介意哦^_^Q版鹿丸', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10155', '0', '奈良鹿丸般的人，你值得拥有', '我本来想过着随便当个忍者，随便赚点钱……然后和不美又不丑的女人结婚，生两个小孩，第一个是女孩，第二个是男孩……等长女儿结婚，儿子也能够独当一面的时候，就从忍者的工作退休……之后，每天过着下将棋或围棋的悠闲隐居生活……然后比自己的老婆还要早老死……我就是想过这种生活……', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10156', '0', '太麻烦了！奈良鹿丸的十二个小知识，你了解鸣人的这位基友吗？', '嗯，随缘的路上越走越远……今天写鹿丸。1，鹿丸的妻子是？A，井野B，手鞠C，花火额哼，当然是风之国的手鞠妹纸。2，奈良一族的秘术与什么有关？A，水B，影子C，风影子模仿术……3，鹿丸的爱好除了睡午觉和下将棋还有什么？A，种花B，看云C，钓鱼像这种嫌麻烦的人，当然是看云……4，奈良一族主要是干什么的？A，警备B，卖花C，提供药材是的，第三个，他们还有养鹿场提供鹿角……​5，有一集提到，鹿丸的IQ超过多少？A，200B，250C，300嗯，过200。6，最后，鹿丸成为鸣人的？A，护卫B，军师C，老师军师啊……7，难题，中忍考试的木叶毁灭计划开始时，鹿丸中幻术没？A，中了B，没有木有哦，他是装的……另外，下忍还有小樱和志乃没中……8，还是一道难题，记得鹿丸和小樱、鸣人去追我爱罗和佐助的时候，鹿丸抓到了几个音忍吗？A，6B，7C，8答案是8个，第9个在树上，如果不是阿斯玛来了就麻烦了……9，佐助营救篇的时候，鹿丸对上的是？A，多由也B，君麻吕C，鬼童丸是那个妹纸，多由也。10，鹿丸是将棋中的哪个棋子呢？A，步兵B，银将C，桂马第三个，因为头脑灵活……11，佩恩入侵时，鹿丸去保护谁了？A，手鞠B，夕日红C，未来未来还没出生呢……当然是怀孕的红老师。12，第四次忍界大战中，鹿丸丁次和井野遇到了秽土转生的阿斯玛。封印后，阿斯玛夸他们是？A，火之意志继承者B，合格的木叶忍者C，完美的猪鹿蝶当然是第三个了！好，就这样，嗯。谢谢支持。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10157', '0', '奈良鹿丸，你我还未曾谋面', '我本来想过着随便当个忍者，随便赚点钱……然后和不美又不丑的女人结婚生两个小孩，第一个是女孩，第二个是男孩……等女儿结婚，儿子也能够独挡一面的时候，就从忍者的工作退休……之后，每天过着下将棋或围棋的悠闲隐居生活……然后比自己的老婆还要早老死……我就是想过这种生活……这是来自《火影忍者》中鹿丸说的一段话，平淡又真实的一段话让我感触良多。他是鹿丸，我在火影忍者里最喜欢的忍者，高冷帅气睿智神勇，智慧担当，出谋划策小能手，但我最喜欢的还是他的懒散，慵懒的躺在地上，嘴里叼着一根狗尾巴草，看着天上飘过的云彩，感慨一句，“今天的云彩真是好啊“。他总是拒绝一切热闹和麻烦的事情，但是朋友遇到困难他总是第一个冷静下来想出对策我很喜欢他一身酷劲，总是蔑视一切，仿佛对任何事情都不感兴趣似的，直到，他眼睁睁看着阿斯玛老师死去，他哭的像个孩子，伤心欲绝，那是他最喜欢的阿斯玛老师，平日里鹿丸总是吐槽阿斯玛老师抽烟的毛病，阿斯玛老师离开后，鹿丸开始抽烟，每次抽烟时都想念着阿斯玛，不是还吐槽，”老师，你为什么这么喜欢抽烟啊\"。鹿丸：“老爸为什么要和这么凶的妈妈结婚？”鹿丸他爸：“你这个老妈也有笑的开心的时候啊、只因为这个。”鹿丸：“对不起了老爸 现在还不能去找你 因为每次跟鸣人站在一起的时候 都止不住想要前进呐…”鹿丸：“无论什么样的局面都必然留有扭转局势的余地。鹿丸他不及佐助的家世显赫野心勃勃，不及鸣人刻苦努力坚持不懈，但他身上一种平淡又可贵的品质总是吸引着我。奈良鹿丸，你我还未曾谋面。我要找个像鹿丸一样的男子安稳地结婚，拍一组美丽浪漫的婚纱照，我会笑的很自然，很优雅，然后我们会安稳地生两个小孩，第一个是女孩第二个是男孩，等女儿出嫁，儿子独立后我就和我的他周游世界，拍很多照片，吃很多美食，到最后，我一定要先他一步离开世界……能有这样的人生就好了……', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10158', '0', '奈良鹿丸，桂马跳得高这才是忍者的战斗方式！', '- 鹿丸vs金（漫画74）鹿丸引诱金进入贴近墙的位置，使用影子控制住金，之后利用影子模仿术让金仰面朝天，金的后脑勺撞到墙壁，鹿丸胜。- 鹿丸vs手鞠（漫画107） 鹿丸决定终身大事一战，鹿丸使用伸长影子试探手鞠，被手鞠量出影子模仿术的最大攻击距离，鹿丸开始陷入被动，被手鞠的风遁·镰鼬轮番轰炸，鹿丸脱下衣服，将衣服四角绑在苦无上，做成降落伞形状，抛向空中。手鞠被鹿丸的苦无偷袭，一瞬的惊慌没有注意到降落伞的影子，差点中招，手鞠得意自己识破的未来老公全部的计谋，突然自己已经动惮不得，只能任老公摆布。鹿丸的影子模仿术成功了，利用鸣人和宁次战斗时打出的大洞中的影子，成为的从手鞠背后控制住手鞠- 鹿丸，阿斯玛vs飞段（漫画322）实力悬殊的一战，面对的是晓的不死二人组，角都刚开始一直在看戏，飞段和阿斯玛正面冲突，鹿丸试图使用影子模仿术控制住飞段，飞段使出杀手锏，死司凭血，让阿斯玛处于生死一线，鹿丸通过分析飞段一系列战斗过程，判断出死司凭血的弱点在于，只要飞段离开脚下的圈圈，死司凭血的效果就会消失，虽然最后角都出手，阿斯玛不幸牺牲，然而飞段已经完全败在鹿丸的计谋之下。- 鹿丸vs飞段（漫画335）卡卡西带着原阿斯玛班去追击角都和飞段，飞段是开了无敌的t在前面顶，后方的dps角都在疯狂的输出，鹿丸分析出一定要将飞段和角都分开，才可以将两人击败，独自承担抗击飞段的人物，鹿丸提前让卡卡西取到角都的血，诱骗飞段以为这是鹿丸自己的血，飞段得到血后得意忘形，使出死司凭血，但是由于这并非鹿丸的血，飞段的招式对鹿丸无效。路飞将飞段引入提前挖好的坑，利用影子聚合术往飞段身上贴满起爆符，duang,duang,duang飞段的身体四分五裂，掉入坑中。鹿丸为了防止飞段复活，鹿丸命令家族的鹿时刻看着飞段，防止飞段用嘴挖土爬出来。via:全仓压团子更多动漫资讯，海量动漫美女超全火影海贼剧场，就赶紧添加微信：rexuedongman回复：雏田，娜美绝对有福利赶紧在公众号给大王留言咳咳，我只能帮你到这了', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10159', '0', '长到成人，可能都希望有一个奈良鹿丸吧', '点上方蓝字关注「天生好梦」梦很小声，谢谢光临我们的，梦导读：今天要说的可能懂的人懂，如果不晓得的就我讲了一个故事吧。要说说追了很多年的火影忍者，差不多有七八个年头。今天又是一个普通的周末，打开最新更新的火影忍者，又是一集来赚眼泪的抒情篇。火影忍者，放到现在，可能知道的人知道，猪鹿蝶三人组，从孩子到成人的他们。猪（秋道丁次 ）：原本的丁次大胖子，现今留着些许的胡子，身材也变得更魁梧和精壮。鹿（奈良鹿丸）：一如既往的睿智，一如既往的大队长风范，只不过肩负了更多的东西，成为了火影的左膀右臂，兢兢业业。蝶（山中井野）：井野，从最初的小泼辣，现今已经长发及腰，基本成熟女性的特质都在了。从三个整天厮混的小伙伴，到长大成人，现如今每个人都有自己要做的事情，连吃饭都要提前预约。还记得小时候那句，走，出去玩去。然后就能不顾身后的乐呵乐呵的出去耍了吧。“长大成人，或许就是这么一回事吧”三个小伙伴吃完，收银给了小时候经常吃的糖果。有没有回忆起小时候的几毛钱一颗的可乐糖，5毛钱一包吃到嘴巴爆炸的跳跳糖，5毛钱填肚子的小浣熊……好像都不见了吧故事发展，一个较早的清晨，要外出的奈良碰到了起早的鸣人。他把那颗小时候很廉价的小糖果留给了鸣人。奈良跟鸣人说，你是将来要当火影的男人，别忘了这点……到故事后面，奈良一直是为了伙伴，为了伙伴鸣人的梦想，锻炼着自己，预备着筹谋着一切，为了是让鸣人在成为火影的路上和成为火影之后。鸣人还是那个鸣人，而奈良却不是那个满腹牢骚的小男孩。每个人，在长大或者说成功的路上，都会不得已的失去一些自己原有的样貌，也应正了那句“有得必有失”，那些失去的，不仅仅是儿时的糖果，而是那种简单的纯粹。奈良说：希望你永远都是现在这个样子。我也对你说，希望你永远都是现在这个样子，不要失去那些最简单的最快乐的纯粹。也希望每个人都有一个奈良鹿丸，当你在未来的路上摸索的时候，你知道身后一直有一个人，不会放开你的手。不管你变得怎么样，不管你成败与否，拍拍你说：有我在。 ——大孩子— THE END —', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10160', '0', '盘点火影忍者中的高帅富 奈良鹿丸领衔', 'NO.1奈良鹿丸奈良鹿丸奈良家族在木叶伊始之时就是村子的中坚力量，但是家族之中人丁并不是很兴旺，据目前分析应该是代代单传。奈良家族可以说是木叶村中依靠秘术发家的家族中最有权势的一个。这在鹿丸成为中忍之后更加的凸显了出来，作为木叶当代栋梁之才的“富二代”之一，鹿丸可以说是木叶众多“富二代”当中的领头人，这不仅体现在他的领导才能，更多的是体现在他那与年龄不符的老道和经验。然而这位怕麻烦的家族继承人经常干劲儿不足，而对待女人更是畏之如蛇蝎的他在婚姻上恐怕也比较难办。值得庆幸的是还好砂隐村有着一个家世和条件都不错的手鞠可以配对，这在很大程度上也可以说明了问题，怕麻烦怕女人的他也不会多接触女人，而手鞠这个和鹿丸非常登对又门当户对的女人绝对是结婚的不二对象。胸中燃烧的是炙热的“火之意志”，鹿丸是忍道非凡的稀世兵法家，在忍界战争爆发前夕被特批参加木叶高层会议，这更是让奈良家族的地位直线飙升，这一质的飞跃更体现出了他对村子的重要性。毫不夸张的说，鹿丸作为一个木叶新锐“富二代”的佼佼者，他的成长已经超越了其父，真正的有了大族家长的意味，至于他将来会成长为什么样，也绝对值得我们期待NO.2我爱罗我爱罗作为砂隐村的第五代风影，我爱罗可以说的上是不折不扣的“富二代”。父亲是上一代风影，母亲也是一名为村子奉献出生命的伟大忍者。家族在砂隐的地位极高，并且影响力也较为强大。单就一名“富二代”而言，年纪尚轻的我爱罗可以说是已经实现了人生最高的价值观，这在“富二代”中也是极其少见的。我爱罗从出生开始就有母亲大人一生的呵护，溶于血肉的保护；在渐渐长大后还有母亲大人的弟弟来做仆人；村子里的人也都对我爱罗又惧又怕；而在几年之后当我爱罗走出砂隐村之时，已经俨然有一副贵族公子哥儿的摸样了，一副冷冰冰拒人千里之外的表情，虽说桀骜不已但是那酷酷的感觉确确实实打动了N多少女的心扉，在身后还有姐姐和哥哥作为跟班，要气派有气派，要实力有实力，更是体现出了一个标准“富二代”的特点。然而这同时也是我爱罗的一个不幸，试想如此身世，如此背景的他，有几人能作为其伴侣呢？也只有少数的王孙贵族，一方豪强手握实权的人物的女儿才能配的上如此之英才吧。以弱冠之龄就坐上了风影的宝座，不但有实力，还有显赫的家世，我爱罗可以说是一个最成功的“富二代”，不但继承了其父之名，并将风影的名号变得更加之响亮。哪怕是在尾兽被抽离，生命的火焰熄灭之时，还有村子的元老顾问以命相救，这样如此美好，如此完美的生活，就算是同样身为“富二代”的其他忍者，也只能望其项背。NO.3猿飞木叶丸猿飞木叶丸木叶丸的名字，是三代火影所取，寄托了他的希望。从大树中长出新芽的嫩叶。虽然其父不表，但是爷爷是三代火影，叔叔是守护大名的十二士之一，还是村子的中坚力量，木叶丸绝对是木叶村中最有潜力的“富二代”，如果你要问凭什么，凭的就是猿飞这个震慑力十足的姓氏。我们从木叶丸小的时候就可以看出一二，私人保镖兼老师是火影特别指派的，而为了满足小孩子爱玩的习性，还有两个从小到大的玩伴——萌黄和乌冬。NO.4犬冢牙犬冢牙木叶村中有很多家族，而犬冢一族可以说是比较低调，甚至是趋向于平凡的一族。作为犬冢一族的族人，也是犬冢家新一代的男子，牙的人生可以说是一帆风顺。比鸣人还一根筋单细胞的他没有太大的理想和抱负，逐步的提升实力，渐渐的接管家族中的大权，做一个安逸的“富二代”想必是一件很快乐的事情。和牙组队的同伴同样来自木叶的大族，这也体现出了大族一贯的联合策略，强强联合的手段一直是大族掌权者的领导方针。不求有功，但求无过，犬冢一族在木叶村可以说是一直不温不火，在晓进攻木叶之时也给予了村子及时的帮助，这样稳定中求发展的策略也体现出了一个大族应有的底蕴。至于牙的婚姻估计也是水到渠成的事情，良好的身世和背景，再加上英挺的外表，估计将来犬冢家的门槛就要被踩塌了。而牙作为木叶的新锐，狂野的作风和洒脱的性格也终究会让他成长为一个足以独当一面的族中支柱，而在众多“富二代”中，牙也可以说是一步一脚印的扎实型人才，村中猛将的身份也非他莫属。NO.5油女志乃油女志乃如果说犬冢一族很低调的话，那么油女一族则是更加的低调。作为木叶村老牌家族之一的油女一族向来给人的感觉就是比较神秘。而新一代的继承人油女志乃更是严重继承了这一神秘感，外加他独有的冷冰冰的性格，也使得他看起来不像一个“富二代”，而是一个经常默默思考的老学究。而能深入木叶各个主要机构，也体现出了油女一族的实力。志乃很聪明，战斗时擅长冷静的分析，谋定而后动，快速判断局势作出最正确的选择，这也充分体现出了其良好的家教。作为一个“富二代”志乃没有那种年少的轻狂，有着与年龄不符的冷静和老道，认准一个死理就坚定不移的他一旦认真起来将爆发出可怕的实力。而无疑这样的性格也是不讨女人欢心的，对于满身虫子阴森可怕的志乃来说，将来的另一半看来只能在族里选择了，这也是一个无奈的选择。老牌家族经常出现杰出的后辈，而随着团藏的身死，纲手的恢复，“根”也烟消云散，而作为团藏心腹部下的油女取根也使得家族蒙上了不大不小的阴影。所以志乃想要使家族发扬光大还需要卧薪尝胆漫漫修矣。NO.6秋道丁次秋道丁次如果说哪个家族是比较与世无争的，那么非秋道一族莫属。秋道一族因为秘术的特点所以使得每代的传人都是大胖子，而或许是因为胖胖的特点，使得秋道一族的传人都显得极其友善，而也正是凭借憨厚稳重扎实这一特性，使得秋道一族在村子的地位也是日渐上升。从过去和三代火影并肩作战的秋道取步，到现在为了保护村子差点付出性命代价的秋道丁座，无疑秋道一族是木叶的栋梁一派，是无可否认的保皇派。而丁次作为这样一个家族当代的传人，实在是一个可以坐吃山空的最享受的“富二代”。而事实上，丁次的性格也比较接近一个中庸甚至有点无能的“富二代”，羸弱的性格使他总是在角落中徘徊，然而幸好有鹿丸的鼓励与信任使得他终于也有了自己的抱负和理想，成功脱离了啃老族的行列。然而因为体型的关系，丁次的婚姻注定要悲剧，一个母老虎性格的女人将注定成为他的人生伴侣，而他有些软弱的性格也使得他对这一切无法反抗。在一个寂静的时代当中，丁次这样的“富二代”注定是没有表现的机会的，然而乱世已经到来，留给丁次的机会还是有很多，只要他贯彻自己的忍道，有着明确的目标，还是可以在“富二代”之中做出一个很好的榜样NO.7日向', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10161', '0', '秋道丁次——你是最美绽放的蝶', '一开始看火影忍者，最初看着“猪鹿蝶”这组合，以为丁次代表“猪”，因为丁次是那种蠢笨贪吃的小配角。就连忍术“肉弹战车”看起来也很让人嗤笑。丁次不像鸣人那样拥有九尾的力量，智商也不如鹿丸那样，更不可能成为宁次那样的天才。有人强烈否认丁次大部分的时间都是用来吃，思考吃什么和睡觉，他也有刻苦修炼的时候，可惜他在努力这点对于小李来说都是微不足道。以前的他，经常被人取笑很胖。但是现在身为秋道一族的第十六代继承人，猪鹿蝶的一员，第十班的一份子。虽然还是有人取笑他，说他肥猪，他信任自己，肯定自己，不想输给任何人。也许正因为同伴之间的羁绊太过深厚，所以有人说起同伴的坏话时，即便他不是一个爱战斗的人，也毫不犹豫的为了保护同伴，为了守护同伴的尊严而站起来战斗。那一刻就算很疼痛，甚至会死亡，也毫不犹豫的吃下家族的密药三色药丸。那刻，他为了同伴化作蝴蝶，他那胖乎乎的身材，战斗也能如此华丽与绚烂。那刻，终于明白为什么秋道一族会被称作‘蝶’了，原来他们在保护重要的人的时候，背上会长出那么美丽的翅膀提起火影，我会想起鸣人、佐助、卡卡西还有自来也，甚至会想起鹿丸、宁次和小李。但是他那挥出比生命更重要的一拳，深深地触动了我内心的心弦。那瞬间我觉得秋道丁次是最强的，是那无比强大。那瞬间秋道丁次那帅气的英姿已经停留在我心里。在最近的大战中，与逝去的老师短暂的重逢，面对着熟悉的老师，下不了手，也许是正常的，当丁次再次下定决心，这次他依靠自己的力量化作蝴蝶，振翅高飞。 那刻，我发现喜欢上那不显要的角色——秋道丁次还会有谁，因为他的蝶弹爆击而喜欢他的？（请不要问为什么满篇都是丁次化蝶的样子，我只是想纪念他为蝶的样子！！因为反弹太快了）超爱二次元逗比青年、资深腐女不想错过任何一条你想看的动漫资讯?就赶快添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10162', '0', '秋道丁次：我只是有些丰满', '秋道丁次日本漫画家岸本齐史作品《火影忍者》中的人物。火之国木叶村的中忍，他是猿飞阿斯玛的第十班中一名忠诚热心的队员，是鹿丸最要好的朋友，他用自己的行动证明了自己是队伍中的一个值得信赖的伙伴。常用忍术是肉弹战车，针刺肉弹战车，倍化术，局部倍化术，超级倍化术等家族密术。秋道丁次是猿飞阿斯玛的第十班中一名忠诚热心的队员，并且，他被证明是队伍中的一个值得信赖的好同伴。作为秋道家族的一员，丁次精通改变身体任何部分的秘术，最常用的就是倍化之术。他使用该忍术并发展成为肉弹战车--即是把自己变成一个巨大的旋转着的肉球冲向对方，他常常独自修炼忍术。丁次总是被食物所诱惑，而且毫不掩饰对食物的热爱(他衣服前面的\"食\"字代表了一切……)，因此他非常非常的胖。他最反感的事就是被人骂为\"肥猪\"(一旦有人犯戒，后果很严重)，找借口说自己只是很\"丰满\"。他不是一个爱战斗的人。想让他战斗的方法，最安全的做法就是答应以食物作为酬劳。当然，也有可以刺激他的办法，像是肥猪一类和肥有关的词语，或者抢掉最后一口的食物，都会让他陷入狂热状态。作为秋道一族的成员，他除了家族的秘术外，还有家族的密药可以增强实力，但是，密药的副作用很大，如果不是纲手的医疗忍术强悍的话，死亡，是服用密药的唯一结果。秋道 チョージ(AKIMICHI CHOJI)AKIMICHI是常规日本姓氏。AKIMICHI街道。秋天被日本人认为是读书和野炊的季节。秋天的慢慢长夜用来读书，秋天瓜果成熟，渔业丰收，正是放开肠胃大吃的季节，丁次是个不错的名字，听上去比较正规的日本人名。CHO来自蝶(CHOU)。很多名字以\"JI\"结尾。就像MARU\"丸一样，有这个字根的名字一定是男孩。忍术技能肉弹战车:使用倍化之术将自己的身体肥大化，然后头和四肢缩进去，属于与倍化之术相配合的体术攻击，以惊人的破坏力攻击敌人。肉弹针战车:在肉弹战车的基础上，披上苦无串起的绳子，回旋力和破坏力都得到了强化，也可以将头发直接变长变硬覆盖全身。倍化之术:让自己的身体巨大化，秋道一族的秘传忍术，使用的时候将会消耗很多查克拉。部分倍化之术:一些时候无需全身变化的情况下出现部分倍化超倍化之术:在部分倍化之术的基础上，比普通部分倍化之术大出数倍的忍术超推手:在部分倍化之术基础上使用掌压体术攻击敌人蝶弹爆击:将全身脂肪转化为查克拉，当所有查克拉聚在一点的攻击是致命的。但是同时也有严重副作用。家族密药第一颗药丸·绿色菠菜丸药丸优点:短时间爆发出人类自身运动潜力30%。力量可以增加自身极限的3倍以上。药丸缺点:有短时间的爆发潜力就有短时间的痛苦回报。第二颗药丸·黄色咖喱丸药丸优点:身强体壮，补养气血，温暖命门。药丸缺点:服用后容易引起全身无力。第三颗药丸·红色辣椒丸药丸优点:增强自身100倍力量。药丸缺点:服用第三颗药丸随着自身力量的增加，会带来身体过度负荷，导致死亡。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10163', '0', '《火影忍者》最新一集，秋道丁次再次化蝶！', '秋道一族是《火影忍者》中木叶村的一个神秘家族，秋道家祖传的秘术是倍化之术，该族成员一般都比较胖，但战斗力都很强。秋道丁次是家族的继承人，他善良可靠，热心忠诚，对朋友很重视。他们一族有一招秘术，可以幻化出蝴蝶翅膀，使用者力量大增。记得他第一次使用，是在对付次郎坊的时候，那时候他还需要借助药丸才能使出来。第二次使用这招，是在第四次忍界大战的战场上。面对昔日恩师阿斯玛，丁次先是下不了手。后来振作起来，凭借自己的力量，没有借助药丸，长出了翅膀，并给了秽土阿斯玛最后一击。第三次看到他化蝶，是在最新一集 704集。木叶村遭受人肉炸弹袭击，面对巨大的爆炸，鹿丸让丁次用翅膀去驱逐。丁次吃了一袋薯片，然后提取力量，幻化出了一双美丽的翅膀。翅膀一扇，巨大的风力把爆炸控制住，然后鸣人配合九喇嘛把爆炸轰飞了。话说现在的丁次成长也很大嘛，虽然没有鸣人和佐助那么变态，但是能轻易用出翅膀，算是不错了。而且，虽然人家胖，但是人家也找到对象了。你看看你自己，你的对象呢？雏田女神镇楼！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10164', '0', '火影细节挖掘：你知道吗，秋道丁次跟他老婆的姻缘其实早有伏笔！', '在火影大结局中，大部分人都结婚生子，有了自己的家庭。丁次跟一个黑皮肤的女忍者结婚，在当时引起了不少人的议论。很多人没有意识到这个女忍者是谁。其实，丁次跟她的缘分，岸本很早就埋下了伏笔。这个女忍者叫卡鲁伊，是奇拉比的弟子。她在火影中后期出场，戏份不多，大部分时候负责搞笑。不过在仅有的几场搞笑戏份中，她脑洞大开，说出了这样的幻想：我们再来看秋道丁次关于婚姻的想法。当丁次继承秋道家主之位时，他的父亲秋道丁座将耳饰交给他，让他传给下一代。当时的丁次十分没有信心，觉得自己找不到结婚对象。丁座现身说法：连我都能结婚啊！拿出自信来吧！结果在第四次忍界大战中，丁次真的觉醒了，拿出了自信，不用药丸就能使出蝶弹暴击。看看这个时候的丁次吧。“长得帅”，瘦下来的丁次确实很帅。“长得高”，丁次的身高在第二部里本来就是数一数二的！“有钱”，丁次可是秋道家族的家主啊！完全符合卡鲁伊的幻想！所以，在大结局中，二人自然而然地在一起了，很多人觉得他俩结婚没有铺垫，其实岸本早就铺垫好了，大家没发现而已。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10165', '0', '不是肥胖只是有些丰满！秋道丁次 疾风传 决斗详解', '秋道一族祖传的是秘术倍化之术，该族的成员一般都是比较胖的，因此该家族的战斗力都很强大。作为秋道家族的一员，丁次精通改变身体任何部分的秘术，最常用的就是倍化术。他使用该忍术并发展成为肉弹战车——即是把自己变成一个巨大的旋转着的肉球冲向对方，他常常独自修炼忍术。【常用打法】秋道丁次【疾风传】的核心技能是肉弹针战车，不同意小丁次的肉弹战车，大丁次的肉弹针战车的范围和持续时间都非常优秀，而且可以轻松打断突进技能，轻松先手到对手。需要注意的是，普通状态下的肉弹针战车是会被秘卷和大范围抓取型技能打断的（如神威），所以大丁次多携带霸体秘卷，可以大大提高先手的成功率。不过即便是进入霸体状态也要小心抓取技能和控制型秘卷（如冻雪，扦插，地走等）。占据先手优势之后可以使用2A连击浮空状态的敌人，在对手交出替身反打后，如果对手使用霸体秘卷，那么就等待霸体快要结束时替身，再 使用部分倍化之术进行抓取。若是直接反打，可以使用通灵进行压制，轻松将之战胜。【普攻详解】大丁次的普攻一共分为4段，前俩段造成浮空较高且连招也较稳定，第3段会把敌人砸在敌人，会出现把敌人打倒地的情况，第4段普攻可以扫地。而第3和第4段普攻的攻击动画持续时间较长，非常容易被对手抓住后摇攻击。实战中，考虑到后摇的问题，建议先把敌人击飞，随后用2A进行连击。【技能详解】 【部分倍化之术】疾风丁次双手部分倍化朝前方合掌拍击，如果命中则对打出推掌击飞敌人。解析：判定范围X轴约为2个身位，Y轴1个身位，对身后敌人也有一定判定，可以重置敌人浮空，可以抓霸体，是短时间控制和输出的技能。 【肉弹针战车】疾风丁次将头发变长变硬后覆盖全身变成带刺的肉弹战车，持续滚动撞击敌人。解析：核心先手技能，命中可以控制对手4秒，配合霸体秘卷基本上可以百分百做到起手（没有控制型秘卷和抓取技能时）。技能有短暂前摇，是大丁次重要的输出和起手技能。  【超推手】秋道家的秘技!释放超倍化术跺脚之后，双手使用掌压术对四周的敌人造成巨大打击。解析：在以自身为起点，向前方的大概3个身位的矩形范围内进行砸地，最后一段范围较大，结束可以接肉弹针战车来进行奥义追击。【秘卷搭配推荐】【禁术·阴愈伤灭】首选秘卷，进入霸体状态4秒以上，并提高移动速度7%以上，持续时间和移速加成随秘卷等级提升，免疫僵直效果和通灵，但会被控制型技能或秘卷限制，冷却时间40秒以下。（被冻雪，扦插，地走，风杀阵，幻术·此处非，幻术·黑暗行等秘卷克制。）在对手没有控制型秘卷和大范围抓取技能的时候，开启霸体使用肉弹针战车是一个100%成功率的起手方式，【火遁·烈炎弹】从身上释放出8个快速追踪敌人的火焰弹，又称为“八连火”。伤害随秘卷等级提高。冷却时间约24秒。丁次的起手通常依靠肉弹针战车，起手能力十分稳定，携带烈炎弹既可以用来先手，也可以用来在对手替身反打时保护自己。【常用通灵搭配】熊猫：从空中砸下，将敌人击飞到空中，获得持续4秒的霸体效果。蛤蟆仙人：通灵出两位蛤蟆仙人释放大范围的音波幻术，判定范围为全Y轴，半屏幕X轴，造成大量僵直，控制持续时间约6秒。反手保护最强通灵，仅被霸体秘卷和熊猫克制。 蛞蝓：恢复当前忍者30%血量，落地造成大浮空。是非常珍贵的回血道具，是绝地翻盘，反败为胜的必备通灵。【奥义追击】奥义结束后可以使用肉弹针战车轻松衔接。【对敌分析】 优势对战：体术型忍者，如凯，李洛克等。位移少，先手能力较差的忍者，如宇智波佐助【新春限定】，春野樱【新春限定】。劣势对战：擅长远距离攻击的风筝型忍者，如宇智波佐助【疾风传】，宇智波鼬【暗部】，蝎等。【李洛克[杜门]】杜门李拥有两个突进技能，一常携带的秘卷有霸体和雷电击。面对这种近战型先手能力较强，有突进技能的忍者，大丁次其实是比较轻松的。因为大丁次的的肉弹针战车的范围和持续时间都是非常优秀的，而且可以轻松打断突进技能，包括杜门李的两个技能。不仅如此，还可以轻松先手到对手。需要注意的是，普通状态下的肉弹针战车是会被秘卷和大范围抓取型技能打断的（如神威），而且会被对手的远程技能所打断，所以大丁次多携带霸体秘卷，可以大大提高先手的成功率。不过即便是进入霸体状态也要小心抓取技能和控制型秘卷（如冻雪，扦插，地走等）。实战中，先了解对手携带的是何种秘卷，如果是控制型的秘卷，尝试走位骗出来。如果不是控制型的，那便直接开启霸体使用肉弹针战车强行先手。利用肉弹针战车可以打断突进技能的特点强势压制对手，取得先手优势。【心得体会】秋道丁次【疾风传】是疾风传忍者中强度最高的几个忍者之一，先手稳定，技能强度很高，可以进行奥义追击。唯一的不足就是十分依赖霸体秘卷或者熊猫，失去霸体的肉弹针战车很容易被对手的秘卷打断。实战中只要各位小伙伴多多练习，熟悉秋道丁次【疾风传】的技能特点，就可以轻松玩转这个强力B级忍者。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10166', '0', '经典动漫十大饭桶盘点 秋道丁次路飞纳兹上榜', '【科技讯】5月27日消息，经典动漫十大饭桶盘点，秋道丁次路飞纳兹上榜。在动漫中有着各种各样的饭桶存在，尤其是动漫中的那些夸张描写方式，这些动漫中的饭桶与体型基本上每月太大关系，就让我们来一起了解一下经典动漫十大饭桶吧。TOP10：:亚连沃克(驱魔少年) TOP9：格拉托尼 (钢之炼金术师)TOP6:.丽娜因巴斯(秀逗魔导士 TOP5：新兵卫(忍者乱太郎)新兵卫是《忍者乱太郎》里面人物，和主角乱太郎，阿丸组成3人小分队。他们的友谊也一天一天的增加起来。和乱太郎，阿丸不同的地方则是新兵卫家里非常富有，但他却没有任何架子，为人也很憨厚老实,忍术不怎么样但是食量惊人，回答老师的问题总能和吃扯上关系，但是又怪力，在剧场版片尾的时候出现过三五个人推不动的炮车，新兵卫能够轻松的推起来TOP4：李舜生(黑之契约者)本作的主人公，梅西耶代号BK-201，能力是自由操纵电子。隶属组织的一个行动小队，在东京进行特工活动。平常伪装成中国留学生李舜生(李舜生(リ シェンシュン))，居住在海月庄公寓。事实上不是真正的契约者，而是从妹妹“白”身上继承能力，所以不须支付代价。电击只是初级能力，真正能力为对物质的量子水平改造，并可借助流星碎片增幅能力，引爆“门”空间为不可侵入领域。在成为契约者前已被称为“黑色死神”，后又被称为“传说中受诅咒的契约者”，但本身其实并不想杀人。根据身份的切换，会表现出冷酷与温厚两种截然不同的性格。食量惊人，本身亦非常擅长制作中华料理。TOP3：:孙悟空(龙珠)原名：卡卡罗特(格古洛，卡洛特，卡罗特)日文名：力力口卜英文名:Kakarotto(卡卡罗特);Goku(悟空)性别：男身高:1米83体重:72KG胸围:94CM超级赛亚人4(GT登场)身高:1米91体重:88KG胸围:103CM年龄：第一次出场时是14岁(实为12岁，是悟空不会算术以为11后面是14)，截止到Z为47岁出生地：贝吉塔行星种族：赛亚人绝招：龟派气功，界王拳，元气弹，元气球(剧场版)，舞空术(腾空术)，残像拳，太阳拳，气圆斩，气功波，空气炮，龙拳(龙拳爆发)，瞬间移动，超级赛亚人1变身，超级赛亚人2变身，超级赛亚人3变身;超级赛亚人4变身，十倍龟派气功波(续作《龙珠GT》里的)师父：爷爷孙悟饭(第一个师父，教的是基本功)、龟仙人(训练悟空，教他龟派气功)、凯琳仙人(教的是一些战斗技巧)、神(教他运用气)、北界王(教他界王拳和元气弹)，也多拉星球人(教瞬间移动)家人：儿子孙悟饭，孙悟天，妻子琪琪，父亲巴达克(已故)，养育他的爷爷孙悟饭(已故),孙女小芳,哥哥：拉蒂兹(在Z开始时侵入地球时，被卡卡罗特和比克杀了)好朋友：龙珠战士们(如：小林,乐平,天津饭,饺子,贝吉塔，特兰克斯，孙悟饭，孙悟天，欧布，比克，小芳，胖布欧;四星龙(续作《龙珠GT》里的)天天，波波，雅吉洛贝。徒弟:欧布猫魔人Z劲敌：由剧情发展而增加，一个强过一个。但有些敌人也变成了朋友，如短笛(比克)、贝吉塔(达尔，比达),，弗利萨，沙鲁(西鲁)，贝比(续作《龙珠GT》里的)火焰使者17号(续作《龙珠GT》里的，由地狱里的17号和人间的17号合体)，邪念波(《龙珠剧场版》)。战斗力：2(出生)10(初登场)100(巨猿)180(第22届天下第一武道会)260(战大魔王) 416(战拉蒂兹)8000(战那巴)32000(4倍界王拳)180000(那美克星初登陆)300，0000(战弗利萨)1，5000，0000(超级赛亚人变身)最喜欢做的事：吃，除了吃还是吃最怕的东西：打针祺祺(汗...最强的赛亚人怕打针和老婆...)具体年龄：种族：赛亚人赛亚人是宇宙第一战斗民族，他们的特征是勇猛，头发为黑色，且有尾巴。一到月圆之夜，他们的眼睛会接收特殊电波(布尔兹光波)，尾巴会有反应从而变成巨猿，战斗力会比平常高10倍。赛亚人的战斗力也会在他们垂死之时被救活后大幅度增长，而且他们攻占星球不计其数，生活在10倍重力的贝吉塔行星。因此可以说赛亚人是最强战斗民族。同时赛亚人还有一个传说：每逢1千年会出现一个超级赛亚人，他的战斗力是宇宙第一。然而在悟空这个时代却出现了多位超级赛亚人：孙悟空父子三人，贝吉塔父子(及未来特兰克斯)。剧情版中的超级赛亚人布罗利，他的实力在当时的悟空之上。TOP2：路飞(海贼王)基本资料路飞 —— Monkey D Luffy年龄：16岁生日：4月22日(世界地球日)星座：金牛座出生：东海-风车血型：A型身高：170cm所属：草帽海贼团-船长职称：船长能力：超人系拥有“橡胶果实”能力的橡胶人特征 ：戴草帽，左眼下方有一道疤痕。悬赏金：3000万贝里(可可亚西村事件)→1亿贝里(阿拉巴斯坦事件)→3亿贝里(司法岛事件)→4亿贝利(海军本部马琳福德事件)恶魔果实：超人系——橡胶果实弱点：怕刀、海楼石、怕烫、不能游泳(恶魔果实能力者共通弱点)喜欢的食物：肉代表动物：猿猴惧怕的类型：爷爷身上的味道：肉的味道(根据乔巴的鼻子判断)代表颜色：红色代表数字：01、56(56日文和橡胶同音)梦想：成为海贼王得到上个海贼王葛鲁多·D·罗杰的密宝——One Piece、跟红发再度见面身世在东海某个海岛的风车村长大，和暂驻在渔村的大海贼红发香客斯感情很好，于是一心想成为海贼。眼下的疤痕就是为了显示自己够狠自己用刀划的。可是路飞7岁时糊里糊涂的吃了海贼们抢来的恶魔果实——橡皮果实(ゴムゴムの実)，从此再也不会游泳了。这样还能成为海贼吗?路飞的答案是——只要不掉到海里就可以了。有一次，为了捍卫海盗的名誉，路飞和山贼顶上了，差点丧命，还好海贼们及时赶到。但是路飞被山贼头子扔到了海里，香客斯为了救他被凶猛的海兽咬掉了左臂。红发香客斯离开渔村再次出海前，把自己的草帽戴在了路飞头上。“将来你一定要还给我，当你成为出色海盗的时候!”十年后，路飞真的出海了。他的目标是——海贼王!而那顶草帽，也成了路飞最珍爱的宝物。性格草帽海贼团船长。草帽海贼团三主力之一，是吃橡胶果实能力者。橡胶人，身体具有绝佳的弹性与延展性，可以抵挡绝大多数的物理攻击，对雷电攻击具绝缘性。特征是头戴草帽，天性热情、善良，善恶分明，有时候脑子里会少一根筋，作出一些傻里傻气的事情，但在关键时刻是一个非常靠得住的人，并表现出超出常人的冷静和机智，属于', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10167', '0', '二次元与三次元世界连接：5月1日，Link Start！今天', '终于到都5月1日，首先愿大家五一假期玩的愉快，也祝自己生日快乐！5月1日星座：金牛座幸运数字：1守护星：太阳、金星1.秋道丁次——《火影忍者》2.圆子令司——《光速蒙面侠21》3.冈崎直幸——《CLANNAD》4》乙宫草芽——《绅士同盟》5.阿渡——《死亡笔记》6.土田聪史——《黑子的篮球》持续更新中~喜欢的朋友点波关注', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10168', '0', '越长越漂亮，山中井野的十二个小知识，你都会吗？', '忙着做毕设的小编突然想起是不是该更新了，于是迅速写下此文。今天是阿斯玛班的第二人，井野。请听题：​1，井野小时候和谁是好闺蜜？A，天天B，小樱C，雏田嗯，宽额头女主。2，井野最后和谁在一起了？A，佐助B，佐井C，单着佐助是小樱的，单也不可能单着，就佐井吧。3，说起来，佐井第一次看见井野，对她的称呼是？A，丑女B，美女C，同学是的，美女，虽然他是反着说的……4，井野家在组合中的代表动物是？A，猪B，鹿C，蝶不用解释，是第一个，是的，一开始我还以为丁次家才是……知道我看到了丁次开大招。5，井野经常在家里的什么生意里帮忙？A，烤肉店B，花店C，蔬菜店你觉得美女会在什么店，当然是第二个。6，山中一族的秘术是？A，感知能力B，强化C，嗅觉当然是感知能力，，​7，井野老爸山中亥一在四战中是？A，参谋B，战斗队长C，情报部队队长都感知能力了，肯定是第三个。8，突然想起，猪鹿蝶的羁绊证明是？A，耳环B，戒指C，项链他们三个都有耳环就知道了……9，又想到一个更番外的，你知道猪鹿蝶源于现实中的什么吗？A，日本神话B，日本纸牌C，日本将棋纸牌游戏花札。​10，中忍考试，井野对小樱的结局是？A，井野胜B，平局C，井野输好闺蜜自然是平局。11，井野曾经向谁拜师？A，夕日红B，纲手C，自来也纲手，她还是小樱师妹呢，，12，佩恩入侵的时候，井野看着谁被人间道抓了？A，卡卡西B，木叶丸C，静音是的，又一位师姐，静音。好，就这样，唉，小编真的太忙了。谢谢支持。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10169', '0', '《火影忍者》山中井野的新造型，比女神雏田还美？', '《火影忍者》山中井野是木叶的一位才色兼备的女忍者，在好胜的性格之下也有体贴和温柔，是忍界最坚强的一朵忍者之花。以前和小樱是情敌，也做过很多冲动的事情，在后来随着她的成长，也逐渐成熟，变成一个非常出色的女忍者。山中井野在《火影忍者》最新的剧情中，山中井野换了一个新造型，其实，也没有多大的变化，就是涂了口红，性格也变更加地温柔，更加感性。山中井野同时，也更加地迷人，给人感觉很自然，不做作，是一个非常优秀的女孩。佐井能娶到井野，真是有些羡慕他啊！山中井野无论从哪个角度观察井野，都非常的漂亮，是名副其实的木叶大美女，也是一位女神啊！山中井野有没有觉得现在的井野比女神雏田还美呢？雏田也换了一个新造型，依然是女神。女神日向雏田雏田有雏田的魅力，井野也有井野的魅力，两个人都是忍界的女神。女神山中井野', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10170', '0', '热夏狂欢──《火影忍者》夏日泳装 山中井野连招技巧解析', '　　作为《火影忍者》里女神般存在的【夏日泳装】山中井野，在决斗场的表现又如何呢?下面，就为大家分析一下【夏日泳装】山中井野在决斗场时技能的亮点以及实战技巧。　　【招式解析】　　普通攻击：通常情况下，泳装井野的普通攻击分为5段， 前3段为近身普通攻击用腿向前进行踢击，第4段后空翻将敌人踢至空中，第5段腾空踢击敌人使敌人再次浮空。普通攻击最后一击命中后，井野会进入“Summer!!”状态。进入“Summer!!”状态下的井野，普攻的前4段没有变化，第5段则由之前击飞敌人变为下踢击落敌人。击落敌人之后如果没有技能进行衔接，敌人则会出现落地保护机制。　　【起爆花改】　　CD12秒。 向前方扔出起爆花，起爆花每飞行一定距离，产生一对在空中漂浮的起爆符，敌人遇到起爆符会被爆炸击飞。“Summer!!”状态下，起爆花会由之前的花束变成泳圈，起爆符也会变成向下降落的水气球。起爆花改技能的判定范围为四分之三屏幕距离，伤害高，前摇短，适合开局起手。　　【落叶滑踢-心乱身术改】　　CD10秒。向前滑跃，铲中敌人后接后空翻将敌人挑向空中，最终起身向前发射心转身之术。“Summer!!”状态下，心转身之术的范围会变得更大，击中敌人后会给敌人造成混乱状态约5秒。该技能前摇极短，快速的突进可以用作于攻击敌人，也可以反向使用脱离战斗现场。作为起手以及后续的连招都是不错的选择。　　【舞瓣沙滩】　　飞向空中向地面洒落泳圈，水球，黄鸭轰炸地面，最后地面的水会爆发起来并攻击敌人。奥义技能伤害较高，释放以后可续接技能，避免没有奥义点的尴尬局面。　　【推荐连招表】　　落叶滑踢-心乱身术改+ 5A普攻一套+ 4A两套+落叶滑踢-心乱身术改+起爆花改+奥义舞瓣沙滩　　奥义舞瓣沙滩+落叶滑踢-心乱身术改+4A普攻一套+起爆花改　　【小技巧】　　1.泳装井野的普通攻击不论是常态还是“Summer!!”状态下都无法进行5A普通的衔接，特别是“Summer!!”状态下的普攻的最后一击会将敌人直接踢至倒地。我们在进行墙角攻击敌人时，只打出第4段，可以打出多套普攻，再配合技能从而输出伤害的最大化。　　2.起爆花改作为泳装井野的核心技能，其消耗能力以及控场能力都非常强大。开局释放起爆花改，往往敌人很难躲避，如果没有打中敌人，残留的三对起爆符(水气球)也会在地面停留一段时间之后再爆炸，可以控制场地让敌人无法贸然进攻。如果敌人强行进攻触碰到落地爆炸起爆符，会被击飞，泳装井野也会获得奥义点。　　3.落叶滑踢-心乱身术改技能再正常状态下击中飞敌人之后再向前释放心乱身之术，“Summer!!”状态下使用滑踢速度更快，心乱身之术释放也直接从泳装井野身上发出，更容易后续普攻的衔接。　　4.奥义之后可以续接滑踢技能。如果敌人没有奥义点或者在替身之术技能的真空期，可　　以是使用此套连招。需要注意的是，如果在释放奥义之前，对敌人造成的伤害过多，奥义之后敌人会直接倒地无法续接滑踢技能。　　以上就是对【夏日泳装】疾风井野连招技巧解析，希望对大家会有所帮组。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10171', '0', '图集|山中井野，美丽的波斯菊！', '超爱二次元 逗比青年、资深腐女不想错过任何一条你想看的动漫资讯?就赶快添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10172', '0', '火影忍者配角逆袭，山中井野的最美女神成长记', '山中井野，火影中的十二小强之一，原本只是火影中名不经传的一位普通女孩子。最早出现的原因，估计也是为了衬托女主之一的小樱而存在的。少年时期的井野，样貌平平，争风吃醋，还爱哭鼻子。疾风传之后，清秀了不少，而且性格也有所改变，这时期的很多火影迷都对井野的印象有了改观。现今，井野不仅样貌愈发的漂亮，还女人味十足，着装上也是美到爆。最关键的是，明显化了妆，女为悦己者容，看来佐井真的是很疼爱井野呀。石一公众号；ssln_sy，长按复制，福', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10173', '0', '火影忍者博人传：日向雏田后又出现一位女神，与鸣人的关系不浅', '日本动漫《火影忍者博人传》的故事发生在《火影忍者》结局十几年后，以漩涡鸣人为首的主角们已经结婚生子，特别是曾经的女忍者手鞠、天天、日向雏田、春野樱、山中井野等人，如今她们的年龄已过30岁，成熟许多。手鞠嫁给了奈良鹿丸、日向雏田嫁给了漩涡鸣人、春野樱嫁给了宇智波佐助、山中井野嫁给了佐井、天天暂时未婚。《博人传》是以新生代为主线，以至于上一代的人物都将退居幕后，天天、山中井野和手鞠只是偶尔出场，春野樱和日向雏田分别是新生代主角宇智波佐良娜和漩涡博人母亲的缘故，出场的次数稍微多一些，但还是摆脱不了沦为配角的命运，可以说，曾经的女神日向雏田等人已经失去了往日的风采，那么《博人传》中，抛开那些小孩，年轻一辈里面还有那些忍者能够成为女神呢？风祭萌黄是木叶丸的队友之一，在《博人传》中的年龄是28岁，风祭萌黄之前在《火影忍者》中是以漩涡鸣人的跟班存在着，出场镜头不多。但在《博人传》中风祭萌黄成为了上忍，更是奈良鹿戴、山中井阵、秋道蝶蝶的导师，如此一来，风祭萌黄登场的机会多了很多。长大后的风祭萌黄的发型变化不大，但还是挺有个性的，长相也算是一位美女，但为达到女神的级别，根据资料显示，风祭萌黄会使用木遁术，可见以后的剧情中风祭萌黄还有发挥的空间。猿飞未来是猿飞阿斯玛和夕日红的女儿，出生于第四次忍界大战前夕，在《博人传》中的年龄是15岁，已经成为了一名中忍，剧情中的戏份不是很多。猿飞未来每次出现的时候着装相对保守，并且不到18岁，还无法判断猿飞未来的女神气质，不过考虑到猿飞未来的母亲是夕日红，相对来说，猿飞未来也不会差到哪里去？等再过几年，猿飞未来的女神潜力非常大。日向花火是日向一族族长的次女，姐姐是日向雏田，在《博人传》中的年龄是27岁，是一名上忍，也是笕堇、雀乃泪、伊豆野山葵的导师。当上一届的女神日向雏田退居幕后后，日向花火成功替代了日向雏田，成为《博人传》中名副其实的女神，日向花火优雅成熟，略带一些调皮，一颦一笑，举止投足间散发迷人的气息。何况还有广大火影迷的愿望，期待木叶丸和日向花火能够成为一对的话题，日向花火在《博人传》中想不火都难。好啦，本期就说到这里，有不同观点的朋友欢迎在下方留言，想要看到更多精彩的动漫内容，请大家记得关注我哦！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10174', '0', '木叶12小强战斗排名', '之前鄙人在悟空问答里面曾经回答过一个问题，不过由于考虑的不够周全，引起了不少的纷争，所以鄙人在此说声抱歉。我吸收了众多留言兄弟的评论，思虑再三，有了这个新排名。木叶12小强鄙人简单的将十二忍重新依据战斗排名了一下，由弱到强。1.山中井野山中井野山中井野，山族家族成员，属于阿斯玛领导的第十班的成员，也是猪鹿碟的成员，父亲山中亥一，木叶暗部成员，主要负责情报收集工作，由于山中一族的秘书，心转身之术，团藏的侍卫里面就有一位是山中家族的人，尽管有山中亥一和风这样出色的忍者，但是根据实战来看，井野的实战能力不强，相反比较弱，如果是组合的猪鹿蝶的总体水平就会有一个很大的提升。不过成年后的井野可是美女一枚，个人认为，井野可以是木叶第二美女，至于第一么，就留给各自的女神吧！2.天天天天天天，属于迈特·凯所领导的第三班的成员，天天的人物设定很有中国风，带着比较明显的中国元素，凯所率领的第三班，都是以体术见长的班级，天天也不例外，天天的战斗方式就是以体术和忍具控制为主，所以在某方面说天天的实力不强，不够再后面，就是第四次忍界大战中，天天以外获得六道仙人留下的忍具，实力有的一定的提升，曾经一扇扇灭了角都的一个心脏。不过查克拉的消耗太大，忍界大战结束后，天天开了一个忍具店，不过有这样的实力已经很不错了。3.犬冢牙犬冢牙犬冢牙，犬冢一族的成员，母亲是犬冢一族的族长犬冢爪，属于夕日红率领的第八班成员，性格开朗，不过有些冲动，冲动很有可能是基于他对自己的自信吧，牙与赤犬形影不离，虽然战斗能力不是特别的厉害，不过牙的追踪能力是首屈一指的，虽然人家的实力不是特别强，不过带有萌宠的男人总能获得美女的青睐，忍界大战结束后，牙与猫婆的孙女结成CP，这也是牙比较幸运的地方，毕竟志乃到现在还是单声狗一枚。4.日向雏田日向雏田日向雏田，日向家族宗家的大小姐，属于夕日红的第八班，是日向家族的嫡长女，正是因为嫡长女的身份，雏田的童年并不是特别的美好，毕竟日向一族的兴衰都寄托在他的身上，后来雏田甚至被她的妹妹花火打败了，更是遭到了很多来自父亲的责骂。不过由于从童年开始，雏田就得到了鸣人的鼓励，性格温柔的她从小就喜欢鸣人，甚至为了她不惜付出生命的代价，后来在宁次的帮助下，雏田学会了八卦六十四掌，忍界大战结束后，雏田是为一个实现月读梦的人，最后与我们的鸣人幸福地在一起了。并有了一对可爱的儿女。5.秋道丁次秋道丁次秋道丁次，秋道一族成员，属于阿斯玛的第十班，其父亲是秋道家族的家主，后来丁次也成为了下一任的家主。第四次忍界大战，在阿斯玛的羁绊中，秋道丁次有了质的飞升，作为猪鹿蝶的成员，丁次的作用就是保护其他的两位成员，在这一点上，丁次做的可以是非常完美，除此以外，丁次在后期再阿斯玛的鼓励下，已经做到了不需要用秘制药丸就能够控制卡路里，蝶化之后的秋道丁次，实力上有了飞跃一般的提升，你还别说，蝶化之后的丁次也是大帅哥一枚哟！6.春野樱春野樱春野樱，属于卡卡西率领的第七班的成员，不仅仅如此，第七班的每一个人物都有一个非常强的师傅，小樱的师傅就是三忍之一的纲手，也就是五代目火影，小樱的实力并不弱，作为我们的女主角，后期更是合力封印了大筒木辉夜，百豪之术，医疗忍术，加上怪力，个人感觉，小樱就是奶妈，作为奶妈的话，就在后面治疗就行了，不需要有太多的输出，这样12小强才会有更强的实力展示。不过不可否认的是，尽管定为是奶妈的小樱，实力还是很暴力的，不可轻视。7.奈良鹿丸奈良鹿丸奈良鹿丸，奈良家族成员，与父亲奈良鹿久一样，深受历代火影信任，不过可惜的是鹿丸的父亲在第四次忍界大战中不幸牺牲了，不过鹿丸继承了父亲优秀的大脑，虽然性格消极了一点，但是拥有出众的应敌策略，冷静的头脑，智商可是超过了200，初次以外，更是继承了，奈良一族的秘术，超高的智商，加上独有的秘术，与他对战的忍者都吃过不少的苦头，更是凭借一人之力，解决了飞段，作为同时期里面的智力担当，鹿丸后期更是成为了六代目火影卡卡西和七代目火影鸣人的助手。迎娶了砂隐村大姐大手鞠，小生活无比惬意。8.油女志乃油女志乃油女志乃，油女家族成员，属于夕日红的第八班成员，尽管作为火影里出场次数较少的成员，加上容易被人忽视的设定，但是志乃的实力是不可小觑的，各位别忘了，志乃在火影里面可是百分百先生，是火影十二强中唯一一位没有败绩的选手，利用油女家族特有的秘术，可与远处侦查，刺探敌情，可以远程输出，AOE伤害，个人能力贼强，主要还是虫子的实力太强。后来，忍界大战结束后，志乃就成为了木叶的一名老师，培育下一代，也是一个很不错的职业，唯一可惜的就是从来没有见过志乃的眼睛，这一点上有一点像海贼王中的多弗朗明哥，毕竟在最后我们已经见识过了卡卡西的盛世容颜。9.李·洛克李·洛克李·洛克，属于凯率领的第三班的成员，上文我们在介绍天天的时候，已经说明了，凯的小队是以体术为主的小队，李是十二强中唯一一位不会任何忍术的忍者，尽管如此，小李也获得了众多人的尊敬，这其中还包括了风影我爱罗，小李的例子告诉了我们两个道理，笨鸟先飞，持之以恒就能获得成功，由于小李专攻体术，体术是十二人中最强的，别忘了，小李的老师可是凯，这位曾经获得斑爷认可的男人，曾经一脚提出大结局的男人。那么如果是小李呢？八门齐开的小李实力不可小觑。10.日向宁次日向宁次日向宁次，属于日向家族的成员，属于凯率领的第三班的成员，不过雏田大小姐不一样的是，宁次是分家的人，分家的人就是为了保护宗家尔存在的，所以在中忍考试的时候，宁次想对雏田痛下杀手，后来被制止了。在鸣人的嘴遁下，宁次再后来也改变这种看法，宁次也是一个天才的忍者，无论是小时候还是长大后，别忘了，木叶十二忍中，最先成为上忍的人中，其中一个就是宁次，所以说从一定的程度上证明了宁次的实力。把他放在三位初次之外还有一个很重要的原因， 那就是十二忍中宁次是唯一一个在第四次忍界大战中牺牲的，为了缅怀宁次，也是对死者的一种尊重，所以我把宁次放在了第三位。NO.1鸣佐鸣人与佐助我们的主角们登场了，一个是阿修罗的转生，一个是因塔罗的转生。宇智波佐助，宇智波一族唯一的幸存者，从小就是天才的型的选手，后来为了追求力量，出走去大蛇丸那里去了，可以说大蛇丸算得上是佐助的师傅了，有这样的师傅还是很优秀的，毕竟人家也是曾经的三忍之一，再后来与鼬决战，得到鼬的眼睛，从此开启', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10175', '0', '《火影忍者》春野樱对佐助的感情是爱吗？', '春野樱对于佐助的感情，我认为确实是爱情，而且有些爱情的过分。我们先来看一下春野樱为了爱对做了一些什么：首先是最初阶段，春野樱还常常与山中井野令人争锋吃醋，想要一夺佐助女友的地位。在这个阶段我们可以很明显的看出，这与其说是爱，不如说只是因为“佐助”颜值而喜欢上的“脑残粉”。但是当故事接近到大蛇丸出场，春野樱割舍自己长发时，她整个人的心境已然发生了改变。特别是当佐助离木叶村而去，春野樱过去阻拦时，很明显的看出那时候她对佐助才真正发生了男女情感上的“爱意”。那么为何说春野樱爱的有些过分呢？原因很简单，她对于佐助的爱有些过分包容。首先，佐助在疾风传战团藏的时候，有过公开伤害小樱的行为。而从结果来看，小樱立刻就原谅的佐助对她与鸣人之间，无论是爱情还是友情的背叛。其次在整个疾风传中，佐助对于小樱甚至没有过一次“爱”的情绪，顶线也就是对她有些羞愧。换而言之，小樱就像对一个人单相思一样，做了一个类似于备胎的角色。最后也是最重要的，我们从博人传看得出来，小樱最后虽然得到了佐助并生了一个女儿，但是对整个家庭的不负责程度已经到达了一个难以置信的程度。哪怕偶尔回到木叶村，也只是和鸣人两人“交流”，家里的娇妻和女儿却宁愿守空房。就这情况，小樱还从没任何怨言，并没有考虑过所谓的“出轨”，一个人又要照顾家庭又要出去赚钱，房贷、生活费自己一个人赚，甚是难得！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10176', '0', '火影忍者山中井野图鉴介绍 如花般的少女', '火影忍者手游山中井野怎么样详细技能属性图鉴分享给大家，相信很多玩家都对于这个角色并不是太了解，而本次小编就为大家分享一下关于山中井野的详细图鉴说明，希望能够让大家更进一步的了解这个角色是否值得选择哦!火影忍者山中井野图鉴介绍 火影忍者山中井野图鉴介绍 扫描二维码下载全民手游攻略 全民手游攻略微信公众号二维码：扫描二维码关注全民手游攻略微信 关于全民手游攻略全民手游攻略致力于为广大玩家提供最新、最全、最详尽的手游攻略，在这里你可以查询到任何你想要了解的内容。问答社区服务为玩家们提供了相互交流并相互解惑的平台，让玩家们的疑问尽快得到解决。97973手游网声明：97973手游网登载此文出于传递更多信息之目的，并不意味着赞同其观点或证实其描述。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10177', '0', '《火影忍者》最热血的女忍者，实力接近佐助', '最热血的女忍者是春野樱，春野樱从很小的时候开始性格就很外向，她最早的时候是个性格内向的人，还总是被班上的其他同学欺负，这些同学欺负春野樱的时候山中井野就保护小樱，小樱很崇拜山中井野。所以春野樱想要成为山中井野那样厉害的女性，春野樱就性格越来越外向，她有了两种人格，一种是很有礼貌的人格，另一种是特别霸气的人格，小时候春野樱总是把有礼貌的那个人格放在外面。长大以后春野樱知道做人不能太在意别人的眼光，小樱就把够霸气的人格展现在外面了，她还学会了纲手的怪力，纲手的怪力必须传授给能控制查克拉而且足够霸气的人，小樱就是这样的一个人。小樱学会了怪力以后实力接近了佐助，小樱跟佐助和鸣人三个人是新的三忍，他们的实力都差不多，小樱的纯粹攻击力可能比佐助更厉害，是一个很值得期待的厉害的女忍者。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10178', '0', '火影忍者中国风合集，真的别有一番风味哦！', '相信火影忍者都是被大家熟知的，本期小樱酱要给你们来点不一样的，让你们欣赏一下中国风的火影忍者，体会不一样的感觉！漩涡鸣人，火之国木叶隐村的忍者，四代目火影波风水门和漩涡玖辛奈之子，六道仙人次子阿修罗转世。山中井野，木叶忍者村的中忍，阿斯玛所领导的第十班的成员，队友是奈良鹿丸和秋道丁次，继承了山中一族的独特秘术心转身之术，能让自己的精神占据对方身体，控制对方内心。同时上进心很强，成功学会医疗忍术。我爱罗，风之国•砂隐村的第五代风影。小时候由于体内封印着一尾守鹤而被村人害怕，又因为至亲之人的背叛而导致性格变得冷酷无情、喜好杀戮，但在和漩涡鸣人一战后，由于鸣人的影响而逐渐摆脱心魔。大蛇丸，曾经是火之国木叶村的叛忍。木叶村的“三忍”之一，与自来也、纲手同为第三代火影猿飞日斩的弟子。具有极其强大的实力，和近乎长生不老的恢复能力。宇智波佐助，火之国木叶隐村宇智波一族的天才忍者，六道仙人长子因陀罗的转世者。旗木卡卡西，火之国木叶隐村的精英上忍，原木叶暗部成员，四代目火影波风水门的弟子，木叶第七班队长，漩涡鸣人、宇智波佐助、春野樱的老师。年仅12岁时就成为上忍的天才忍者，后左眼成为宇智波带土移植的写轮眼。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10179', '0', '火影忍者：日向宁次，一个从开始就必须死的忍者', '《火影忍者》中的木叶十二小强中，最后只有日向宁次死了，很多火影迷们对于日向的死都不能接受，虐身虐心。但是纵观《火影忍者》的故事来看，其实宁次从开始存在，就注定是死亡的结局。其实原因可以从各方面来说：第一个原因：血脉相承大家都知道其实《火影忍者》是非常讲究血脉的故事，每个人的设定都是从祖上开始就定型，一点一点的影响着后人。所以先来看看宁次的爸爸日向日差的故事。雷之国的忍者头目曾经拐带了雏田，被日向日差的弟弟日向日足杀掉了，但是雷之国不但不承认拐带之事，而且还拿忍者头目被杀为藉口提出了无理的要求-要求见到日向一族头领的尸首，否则扬言对木之叶隐村及日向一族发动攻击。日向日差为了拯救日向家族及木之叶隐村的人民，甘愿代哥哥（日向日足）去死。这是他头一次有了选择的自由，可惜也是最后的一次。因为他的死亡，曾导致日向宁次对本家的憎恨，宁次的爸爸留下了一句话：“转告宁次，我不是因为分家保护宗家而死的，而是因为保护自己的兄弟和村子而死的。”年幼的宁次完全不能理解这份保护的心情，他对本家的感情也是心里有结但是嘴上不说罢了。以《火影忍者》的作者岸本的尿性来说，怎么可以有这种不能理解的情绪那！于是乎，让宁次死于“保护他人而牺牲自我，最后得以真正的自由。”这种设定是必然的。第二个原因：日向一族分家问题日向一族，是最古老的族群之一，拥有比写轮眼还强的白眼。宁次作为一个日向一族百年难见的天才，从一开始不服从于宗家，甚至当着众人的面把妹妹打成重伤、决心下杀手，到后来和叔叔和解，主动去保护妹妹。不可否认，他一直都是这个家族不得不去关注的存在，也是尚武的火影世界里理所当然被崇拜的对象。他的存在就是宗家分家矛盾的化身，更不用说背负着父亲的血海深仇，而中忍考试并不是他真的和宗家和解，是他和宗家和解的开始。毕竟这时候解决的只有宁次和日足的问题，宗家分家的矛盾依旧存在，偏见也仍旧存在，而第四场忍者大战的时候，大家换上了同样的服装，在同一个小队，共同浴血，这时候的双方没有区别，才能够真正地去正视彼此，宗家和分家才试着去和解。日足和日差的一战，其实就是开始和解的重要证明。而宁次死后，日向一族出席宁次的葬礼，日向未来的家主花火捧着宁次的照片参加姐姐的婚礼，这一刻是真的没有宗家分家之分了。宁次用他的血让大家放弃了这个可笑的规矩，让大家记住了，他只是一个保护妹妹的哥哥，是日向一族的骄傲，他有权利参与亲人接下来的每一刻。第三个原因：能力越大责任越大，日向一族不能毁灭火影中有四大家族，其中漩涡一族有鸣人，千手一族有千手柱间，宇智波一族全是天才，而日向一族最古老。漩涡一族厉害，所以死的剩几个，宇智波一族厉害，所以也剩下几个，千手一族也厉害，也剩下那几个，日向一族一般，却还剩下一大堆人。虽然目前日向一族人最多，但是要说天才、风云人物着实不好找，能力越大责任越大，其他几大家族都是分分钟灭天灭地，然而他们的牺牲也多，而日向一族一直低调，哪怕后来引发大家恐惧的漩涡一族被灭，日向一族依旧安安静静。然而，宁次就是这个出头鸟。他的实力毋庸置疑。在岸本的设定世界中，这种跟众人不同的，除了主角就是炮灰了。第四个原因：经典是需要血的代价的在《火影忍者》中，剧情到了后期，岸本无力把持剧情是大家有目共睹的，然而各种粉丝不让完结岸本也很无奈，他需要这部作品在快要脱线的剧情中给大家一个惊喜，在木叶木叶十二小强中，只有宁次的死是最合理的，最让人心疼的。宁次的结局让他成为了遗憾的经典，这也是长篇的故事情节必然会有的老套路。宁次自己曾经说过，命运是无法改变的。现在看来，其实从最开始宁次的设定就是悲剧收场。最后终于得到自由也算是安慰，唯一的遗憾可能就是没有办法参加参加雏田的婚礼了。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10180', '0', '日向宁次到底有多聪明', '都说木叶出来很多天才，第一梯队的有大蛇丸，佐助，鼬，宁次，第二梯队的，也有被称为努力的天才的李洛克，他们各有所长，虽然很多人都觉得“鼬”这个家伙才是实至名归的天才，但是我今天却要着重说一下“日向宁次”这个人。和很多悲情男主角一样，日向宁次也有着一段不堪回首的痛苦回忆，同佐助，给与他这份痛苦的不是别人，正是自己的家族。日向家族表面光鲜亮丽，头衔多到数不清，可是家族内部也有着不可调解的矛盾。佐助一家被兄长灭门，而宁次一家，则是另一个角度的悲剧，宗家一家享尽齐人福，而作为分家的他们，却要被从小在额头上刻上咒印，一辈子都不得反抗宗家，关键时候，分家还是替宗家送死的小白鼠。命运的不公一开始没有让年幼的宁次痛苦，直到自己的父亲为了保护家族的利益，自愿赴死的那一天，他开始恨自己的家族，恨害死自己父亲的宗家人，本事同根生，却要让自己的手足去替自己死，这样的家族，还有什么荣誉感，还有什么脸面教育后辈立足木叶，从那时候起，宁次就怀着推翻命运，干翻宗家的使命，不断的磨练自己。重点来了，由于日向是木业最最强大并且最古老保守的一族，宗家对分家的压制除了可以控制分家能力的咒印“笼中鸟”之外，还限制了很多日向一族秘不外传的忍术。只有宗家能学，分家本事再大，也没有机会接触，从而保证了宗家永远绝对的地位。可是宁次千真万确是投错了胎，生在分家的他，却是日向一族百年难得一遇的奇才，在父亲死后，他仅仅靠日向一家最基础的忍术，一步一步的练出了连宗家男子都未必能练出的身手。在中忍考试中初次使用出来的“回天”，就是日向一族引以为傲的秘术，即使拥有白眼，也未必学得来，世世代代都是口耳相传，没有书籍，没有记录，日向一族的族长日向日足在看到宁次，也就是自己的亲侄子使用出这招之后，当场是震惊的无以言表。在他看来，这是不可能的事情，分家的人学会回天的难度，无异于鸠摩智学习六脉神剑，后者至少还有段誉那掐头去尾的剑谱真迹，可是宁次则真的空穴来风，无中生有的悟出来的。（一来没有典籍，二来宗家人自己都未必会，三来，宁次还是个小学生），加上后来的八卦柔拳掌的64掌和128掌，都是口传秘术，此等天才，偏偏出生在分家，一个生来就被束缚住手脚的家族中，日向日足也是悲伤的地下了头，那时候的他不知道是在惋惜自己的侄子，还是在无奈自己家族的法则，总之，谁都不是赢家。和宁次相比，佐助也是天才，他小小年纪就能驾驭豪火球术，凤仙火术，受到刺激后，小小年纪又开启了单勾玉写轮眼，可是别忘了，同样年纪的宁次，已经能够使用“回天”，不用结印就能直接开白眼了。而且那时候的宁次同样心狠手辣，堂妹都能下死手，中忍考试要是和佐助对上，估计佐助够呛。同期生中，论才华和悟性，宁次是最高的。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10181', '0', '火影 日向宁次专辑 赶快收藏转发起来吧', '欢迎关注头条号，每天都有好的动漫人物和文章推送，记得收藏点赞奥！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10182', '0', '《谍报》热血经典日漫火影忍者日向宁次八卦掌回天Gk手办雕像模', '关注漫优堂，给你好看！导语订制有风险，购买需谨慎！漫优堂是为广大工作室与玩家提供宣传与信息交流的平台，对于GK工作室制作出现的问题，漫优堂概不负责，望各位小主多多理解 …...编写｜晴小卿频道｜漫优堂最专业的内容评论最贴心的作品推荐每日“谍报”是获取动漫资讯的绝佳渠道 评测  Hello，小伙伴们，欢迎收看本期的《谍报》栏目，我是你们的小卿妹妹，今天给大家带来的是一款火影忍者题材的gk作品，那就是Soul Maker studio工作室的SM-M-001日向宁次八卦掌回天。这款产品可是不小哦～（小喆叔：话说图里抱着产品的那个看着咋那么像个妹子呢？话说大妹子，你拿着这个不沉吗？沉的话把这个给我，我帮你拿着，拿完我就不给你了，啧啧，居然还送个小天使，让我想起了劳斯莱斯车头上的小天使，你们是要做手办中的劳斯莱斯吗？ ps：小喆叔，你......）好了，随我一睹情报信息与谍照吧～～情报：工作室：Soul Maker studio产品名称：SM-M-001 日向宁次 八卦掌回天尺寸：场景高32cm，宽35.5cm，深38cm，人物约1/7配置：不规则岩石地台，回天特效件，宁次本体，LED灯，独立编号硬币及工作室LOGO样式硬币摆台定价：全款988，定金388，补款600，现货1488体数：国内260（20体自留做售后）开订时间：2018年5月12日晚8点开订预计出货时间：2018年7月运送方式：顺丰到付或德邦到付其他：1，在生产环节会将特效件石头上的数字抹去2，人物脚下的太极边沿为夜光效果3，透明件的灯效分为常亮和呼吸灯两种模式4，生产过程中会严格控制透明件气泡5，赠送的硬币底座为工作室LOGO小天使，采用电镀上色处理', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10183', '0', '致那个白衣少年——日向宁次，生日快乐！', '初见你时，你一袭白衣，目空一切，好似周围事物全然与你无关，可谁又知你埋藏在心底的痛苦？当你父亲死去的时候，谁又知道你的痛苦？分家天生就是宗家的守护者，你肩上承担着的，不只是责任吧？你拥有了这个年龄不该有的沉稳和现实，这一切都让我心疼。中忍考试的时候，你认为以雏田的能力根本不足以成为你的对手，劝雏田早早放弃比赛。你认为人一生的能力已是命中注定，雏田就算再怎样令自己变强也无法改变是弱者的命运。就像你不管怎样努力都还是个分家，也只是个分家。但当雏田在鸣人的鼓舞下站起来的时候，你的信仰动摇了。一个不配跟你过招的弱者，如今却与你实力相当，这究竟是自己太弱，还是自己的想法出现了错误？你从来都不是一个偏执的人，错了就是错了，错了就要改。所以当鸣人出现危险的时候，你毫不犹豫地挡在他身前，感性大过了你的理智，相信你咒印消失的那一幕，很多人都泪流满面了吧。是啊，你在无形之中感染了我们这么多人。你的一切我们都看在眼里。宁次你看到了么，雏田、天天、小李他们都在月读中看到了你安然无恙啊……猜你喜欢：【投稿】宁次，感谢你陪我成长【投稿】致那个我心中从未离去的日向宁次人可以改变吗：日向宁次的人生观致那个分家的天才——日向宁次。本期投稿作者：Chris斯莱特林', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10184', '0', '小慧的男神之一——日向宁次', '关于火影，男神众多，但不知道问什么，每次看到宁次，都会觉得心酸和崇拜日向宁次，被称为日向一族的天才，也是他们那一届学生的第一名。他蓄着褐色长发并且在发尾绑成小束，出生于名门日向一族，拥有着三大瞳术之一的白眼，自学了日向宗家的秘传忍术——八卦掌·回天，被称为天才忍者。但是由于宁次的父亲甘愿代兄赴死，而宁次却一直以为是宗家人逼死了父亲，一直仇视日向宗家的人。直到中忍考试时，经过与鸣人一战，才化解了仇恨。宁次的性格十分冷静沉默，但也由于这一点使他拥有比一般人更聪明敏锐的能力，曾因为父亲之死的缘故，保持着悲观的宿命论，执着于命运的论调，再被鸣人打败以及了解了真相后重新审视了自己，变得温和了许多。擅于理性的分析状况，虽说看似毫无感情，但其实常常沉思，也十分关心同伴，有着良好的统率力。或许是因为能力较强，曾多次帮助鸣人以及木叶完成任务，其中包括抢回佐助战役,为此也差点丧命。但是，谁都没想到，忍者世界大战的过程中，他们那一批小伙伴只有宁次牺牲了。宇智波带土复活了十尾后，宁次等忍者联军成员来到战场支援鸣人等人。最终，漫画614话/动画584话，宁次为了保护鸣人和雏田，来不及使用回天的他以自己的身体抵挡十尾的扦插之术后壮烈牺牲，额头上终身束缚的分家咒印亦消失。“我没有犹豫，也不后悔，因为我保护了雏田，保护了鸣人。”', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10185', '0', '卡卡西万花筒写轮眼开眼之谜', '在小时候，第一次看到火影忍者是在小学二年级时在星空卫视看到的，每次都是播放到第60多集就没有了，一直到现在，大学毕业，火影忍者也算终结了，我的童年动漫也追完了。粗略一算，这部动漫追了十几年啊。万花筒写轮眼我相信和我一样，很多的小伙伴在看到卡卡西开启万花筒写轮眼对战迪达拉的时候很是激动，因为那一次算是火影中第一次正式出现万花筒写轮眼（之前在佐助的回忆里有鼬的万花筒），打的迪达拉是措手不及，不仅断了手臂，还只能用假自爆逃跑，简直是颜面扫地啊。迪达拉不过，很多人对卡卡西开启万花筒写轮眼很是不解，原因主要有以下两点：一、卡卡西的写轮眼并非是自己的，而是由好友带土赠予的；二、卡卡西不属于宇智波一族，而写轮眼是独属于宇智波一族的血继限界。其实关于卡卡西的万花筒开眼之谜，虽然在后来的动漫中没有详细的提到过，但是认真看过火影每一集的小伙伴其实是清楚的。在火影剧情中，带土的回忆中有一节就清楚的说明了，那就是带土亲眼目睹卡卡西亲手杀死琳的时候，那个时候，带土的写轮眼进化成了万花筒，与此同时卡卡西的万花筒也随之开启，毕竟眼睛都是带土的，只不过卡卡西当时晕倒了，自己应该不是很清楚吧。万花筒开眼因为非宇智波一族长时间保持写轮眼状态会很耗费查克拉，而卡卡西自身的查克拉也不多，不可能一直保持。所以在与迪达拉对战时，因为太远，无法打到迪达拉，而又担心鸣人暴走，所以才耗费了自身大量的查克拉和时间才重新激活万花筒，使用了神威。而在后来的剧情中，卡卡西的万花筒开启的很顺畅，但是使用一两次神威之后就很容易进入疲劳状态。值得一提的是，卡卡西的写轮眼是属于时空间类的写轮眼，通过瞳力，能强行将一个东西或者其一部分转移到另一个空间。因为火影剧情的原因，所以很多人觉得火影前后剧情中，同一个人的实力波动很大，其中卡卡西最为争议，原因就是卡卡西不管和什么人打，无论是前期感觉很厉害的再不斩，还是中期杀了自来也的佩恩，或者是后期对战几大尾兽，他总是能和对方强行五五开，当然了，毕竟是动漫，剧情也要有个过渡，所以有点波动也是很正常的。主要是观众开的开心。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10186', '0', '依鲁卡：人死去之后，他的过去、现在的生活以及未来都会一起消失', '依鲁卡日本动漫《火影忍者》中的角色。是木叶的一名平凡而伟大的学校中忍老师，虽然没有强大的力量，但是给予了鸣人很好的精神鼓励。其父母在木叶九尾之乱中牺牲，在包括第三代火影等人的关怀下成长。漩涡鸣人等人的第一任老师，鸣人被水木骗去偷走卷轴时，伊鲁卡相信并保护了鸣人，因此鸣人得到积极成长并升为下忍。伊鲁卡的家庭本是幸福和谐的，但是木叶的九尾之乱打破了一家人的幸福生活，伊鲁卡亲眼目睹身为上忍的父母为保护村子而双双牺牲。此后伊鲁卡性格大变，在学校同伴们面前是开心果，但自己一个人却常常在木叶慰灵碑前默默哭泣直到被三代火影细心教导。升为中忍以后的他决心留在木叶忍者学校，为村子孕育出新的生命和力量，并成为了包括漩涡鸣人、宇智波佐助、春野樱、奈良鹿丸、秋道丁次、山中井野、油女志乃、犬冢牙(忍犬赤丸)、日向雏田等后来的优秀忍者在内的第一任老师。漫画第700话，第四次忍界大战十几年后，已经是老前辈的伊鲁卡劝导已成为优秀上忍的木叶丸说要在慕留人(鸣人之子)真正成长起来之前，给予其必要的宽容和理解。忍术替身术:瞬间以动/植物与自己的身份对换，用以逃避攻击。分身术:以查克拉制造『残影』，所以并不是实体，用于扰乱敌人的视线。魔幻·奈落见之术(动画原创使用):使对手见到最能引发心中恐惧的场面。是幻术的一种。忍法·方爆法阵(动画原创招式):由几张起爆符组成。一糸灯阵:近距离结界忍术。曾试图用来阻止鸣人，但被九喇嘛模式的鸣人轻易挣脱。语录：人孤独的死去之后，所有的东西都会消失，他的过去、现在的生活以及未来都会一起消失。许多人会在战争中或是执行任务时死亡，而且会真的会以非常轻易的方式死去...疾风也是其中一个人。在这些死去的人当中，有些人也是有梦想的.每个人都有自己最珍惜的东西...父母、兄弟姊妹、朋友、情人、村子里的伙伴，对自己来说是非常重要的人，和彼此相互信赖、帮助，并且从生下来的那一刻开始，就一直觉得很重要的人们的牵绊，而这种牵绊会随着时间的增长，而变得越来越坚定……这不是什么大道理，而是拥有这种牵绊的人都会这样，因为觉得很珍惜。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10187', '0', '火影忍者：疾风传依鲁卡按实力干得过井野吗？', '火影忍者：疾风传依鲁卡按实力干得过井野吗？以下是各大网友的回答，你赞同谁的观点，或是你有什么新的看法，欢迎留言表态！1，中忍时期的井野和伊鲁卡么？。。。感觉伊鲁卡吧 毕竟经验多 而且井野的招式需要打配合，，，没人配合的话，很难命中吧（指中忍时期）2，从能力上来说我觉得还是井野厉害一些（参考井野他爸3，井野吧。毕竟木叶小强资质高。4，我第一眼看成疾风传井野打得过阿斯玛么，然后一群人说井野，赶紧回去看下标题5，通灵术召唤召唤学生之术随便来个井野都打不过6，伊鲁卡还是有点东西的好不好，临之书伊鲁卡能力值就48了，井野者之书也才427，结局可能打不过，小樱都是上忍了，井野也是跟着纲手的，还有秘术，伊鲁卡没有什么特别的忍术。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10188', '0', '火影忍者里依鲁卡为什么一直只有中忍水平', '先说结论，因为岸本齐史给这个人物的定位就是这样的。鸣人一路走来，能称得上老师的人一共有三位：伊鲁卡，卡卡西和自来也。自来也，三代火影之徒，四代火影的老师，传说中三忍之一，忍者中的豪杰。卡卡西，四代火影之徒，木叶白牙之子，木叶的写轮眼天才，忍者中的精英。而伊鲁卡，仅仅是一个平凡的中忍。平凡，就是他的关键词。他没有显赫的家世，没有尊贵的血统，没有强劲的招式。他只有平凡人的发乎天然的朴素情感。因为九尾毁了他的家庭，所以他痛恨九尾；因为鸣人是他的学生，所以他关爱鸣人。如果说卡卡西让鸣人迈进了忍者的大门，自来也把鸣人推向了英雄的宝座，那伊鲁卡就是给了鸣人一个男孩应有的关怀和照料。鸣人自己也说过，他虽然没有父亲，但想到伊鲁卡老师，就觉得父子就是那样的吧。像父亲一样，伊鲁卡凭借真挚的责任感和理解，在鸣人险些误入歧途的时候，用一句话救赎了他。像父亲一样，伊鲁卡凭借温暖的陪伴和关怀，在鸣人因自来也之死而悲恸不已的时候，用一个行为开解了他。对忍者世界的残酷，他不如卡卡西理解的深刻。对忍者的真正含义，他不如自来也诠释的精准。他作为忍者的生涯可能只是给木叶的孩子们讲课，考核他们的分身术，等到年纪大了就退休，也许终其一生只是一个平凡的中忍。但是，他一直都在木叶村里——鸣人生长的地方——踏实地过好每一天，为鸣人时常被人谈论起而发自内心的骄傲，然后等鸣人回来，陪他一起吃一碗拉面。就像我们每个人温暖而又平凡的老父亲一样。岸本齐史想借这个角色表达的，就是这样一种形象，所以，他不是精英，不是天才，没成为上忍，只是个中忍。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10189', '0', '《火影忍者》这4位人物触犯了禁忌,作者根本不打算洗白他们。', '作品设定在一个忍者的世界，故事从主人公漩涡鸣人的孤儿生活开始，他的父母为了保护村子，将攻击村子九尾妖狐封印到了他体内，鸣人因此受尽了村人的冷落，只是拼命用各种恶作剧试图吸引大家的注意力。好在还是有依鲁卡老师的关心，鸣人的性格才没有变得扭曲，他总是干劲十足、非常乐观。为了让更多的人认可自己，鸣人的目标是成为火影。整个故事就围绕鸣人的奋斗、成长，鸣人的同伴们的故事，以及这个忍者世界的各种争斗和阴谋展开。一、飞段不死之身的飞段，上位的手段非常的残忍，屠杀自己和平的村子的忍者和居民，用杀戮完成自己的邪教，在最终被鹿丸活埋后，岸本没有为飞段说任何话语，只是在后续的公式书中说到，飞段应该得不到营养，死了！二、水木鸣人的老师，怂恿鸣人偷禁术，然后鸣人学会了多重影分身，被抓回木叶后，又策划越狱，之后还与鸣人等人大战，最终变成了老虎的模样，人不人鬼不鬼的！实在是报应！三、金角银角号称史上最厉害的罪犯，生吃九尾肉的家伙，最终他们的目的是什么也没表示，而且还是神奇的拥有一大堆六道仙人的刃具，曾经将二代火影围攻，最终二代火影神秘死亡，再次被秽土转生出现时，很轻松的就被封印了，至于洗白的机会，可能岸本都忘了他们吧！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10190', '0', '卡殿真的会护肤？那么多年过去看上去比伊鲁卡还要年轻', '在最新一集的火影忍者博人传里，终于有看看卡卡西了，不过出现的方式比较逗比这里我们就直接忽略，我们来看啊看样貌，先看看伊鲁卡伊鲁卡seisei看这张脸，差不多也有个40多了吧...再来看看我们的卡殿卡卡西seisei好吧为毛这张脸感觉就没变老过（是不是卡殿私下喜欢保养皮肤？）哈哈哈，厉害了呢，不过话说回来，卡卡西应该年级比伊鲁卡还大吧，我印象中，可是这逆天的容颜也是没谁了毕竟卡殿的人气在火影里算很高的了，虽然大蛇丸也没老，不过蛇叔经常换脸，比较恶心两人的合照不管怎么说，火影伴随小编度过了很长的日子，看着火影长大，现在自己也工作了，鸣人也有儿子了时间过的真的好快啊，小编最喜欢的还是那个最苦最冷的人，各位火影迷们，你们都喜欢谁呢？终极男神鼬帅到爆炸', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10191', '0', '第三百九十八章  张恬自杀', '华易恒走到汤世玉面前，低头看了看汤世玉腰际的骇人伤口，连华易恒都感到有些心疼，这么长一条伤口躺在汤世玉本身也不算太硬朗的身上。“没事吧？”华易恒皱了皱眉，开口问道。汤世玉失血过多，已经有些神情恍惚，摇了摇头，没听清刚才华易恒的问题，虚弱的：“你，你说什么？”华易恒皱了皱眉，还是算了，问什么问，一看就是不好！伸手一把将汤世玉的一条手臂放在自己肩膀上，一米九的个头，让汤世玉勾上来倒有些吃力。努了努最，华易恒干脆学着顾北刚才的样子，将汤世玉一把打横抱起，朝着楼梯口走去。张恬低着头，让顾北', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10192', '0', '纲手命运也不太好', '说到命运我想起纲手，来了，纲手也是倒霉的人，爱赌，逢赌必输，人生就有一次赢钱了，他觉得不是好事，心想输一辈子，今天怎么赢了呢，殊不知，他赢钱之时，自来也死了，看来纲手命运也不太好。命不好都有一个特点，刑克六亲，就是六亲没给他带什么好事，父母没什么祖业给他，兄弟无靠，这样人的兄弟，自身难保呢，那有帮扶他的力啊，妻子有些能力也不帮他，儿女就更不用提了，没有最好，还能多活俩年，这是我昨天给一个人算命看的，他问我财富事业，我又看了他的大运，大运也不是什么好大运，然后我就说，你的一生求什么富贵，能平平安安，就行了，不求富贵你还能多活几年。啊对，我想起来了，昨天找我算命的是一个98年的小孩，现在想想他，以后得路，够他喝一壶的了，这个小孩的人生，不容易啊，一切皆造化而', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10193', '0', '千手纲手，有容乃大！', '火影忍者中女性角色其实挺多的，不过论戏份比重，还是人气，木业忍者村的第五代火影，木业村传说三忍之一的“千手纲手”可谓是绝对女一号！火影忍者中最强大的女性（辉夜姬不是正常人类）连受伤都受的这么性感也是没谁了！这乳量是否充足？雏田最崇拜的女忍者！（雏田一直在努力超越，想要成为真正的“第一女忍者”，雏田还有很长的路要走！）活的最久的木叶三忍并不是没有原因的！（因为观众离不开纲手，所以作者可以保证在被斑爷的须佐能乎连续插了好几下都不死）年轻的纲手就已经霸气外露！注定是不“平凡”的女忍者！这年龄和现在的小樱不相上下吧！但是容量完全不是一个级别的！纲手姬属于事业型的女性，在自己的领域里面取得“巨大”的成功，（第五代火影）与其事业线是密不可分的！这个角色最重要的就是有“内涵”，广大火影迷们一直热衷于在各大网络搜索其本子同人素材！也可以看出纲手在火影迷心中不可撼动的地位！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10194', '0', '火之意志继承者纲手', '自来也：没错，我就是要去寻找和我并成为三忍的。拥有超强医疗忍术的纲手！蝎：木叶的火影纲手被公认为是一位超一流的医疗忍者！千代：比我更加擅长解毒的只有木叶的那个玩蛞蝓的小妞，在忍者大战期间她破解了我所有的毒药，还制作出了解药。奈良四角：毕竟她是三忍中的一点红啊，是这个世界上最美丽最强大的女性。大野木：没想到喋喋不休的蛞蝓公主还健在啊！宇智波斑：速度不及雷影，力量却在雷影之上！认真的女人果然最美！！超爱二次元 逗比青年、资深腐女不想错过任何一条你想看的动漫资讯?就赶快添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10195', '0', '火影忍者：纲手卖萌截图合集，没想到纲手还有这么傲娇的一面', '火影忍者中，纲手毫无疑问是最受欢迎的女忍者之一。毕竟整个火影世界，像纲手这样的角色可不多见。大部分角色从开始到结束都是一个性格，但纲手却不一样。小时候萌，长大后美。曾经颓废过，后来特霸气。老了以后还容颜不老，没事就喜欢卖萌装嫩。这样的角色怎么能不受人喜欢呢？今天我们就盘点一下纲手傲娇卖萌的一面，让你看看不一样的纲手。（1）纲手醉态纲手喝醉了以后特别萌。在放下了所有防备后，女汉子的气势消失殆尽，变得特别可爱。也难怪鸣人第一次见到纲手简直不敢相信这人竟然50岁了。（2）小骗子纲手纲手为了躲避债务，特意变成小时候的模样来骗讨债人，卖萌打滚无所不用。没相当我们的纲手竟然还有这么腹黑的一面。（3）回忆中的纲手出现在自来也回忆中的纲手，小时候的纲手简直是卖萌无敌啊，这个小舌头吐的老夫心都要化掉了。也难怪自来也一生都对纲手念念不忘。（4）Q版纲手本图出自火影忍者小李忍传，因为里面的人物都Q版化了，所以看起来特别有趣，纲手也变得特别有意思。（5）经典一幕纲手喝醉了以后酒品特别差，这可是自来也亲口说的。果然，在木叶女子力哪一集，纲手喝醉了以后就在大街上耍酒疯。（静音也是惨，本身不胜酒力还要照护纲手这个宝宝）然后就是超级经典的一幕了，发现木叶女忍者在聚会的纲手也非要加进去，并且说了一句经典名言：“是因为.....太大了么”纲手竟然也会脸红.....（雏田在一旁诚惶诚恐，比不了比不了啊）。（6）消愁的纲手纲手每次回忆起弟弟绳树和爱人加藤断的时候，都会借酒消愁，但是每次都是先把静音喝倒。然后一个人暗暗流泪。（7）发怒的纲手火影忍者小李忍传中，天天一行人在背后吐槽某位女士装嫩，没想到这位竟然是纲手.....但是这个生气的姿势真的好可爱啊.... 出个手办就好了。（8）火影岩纲手为自己设计的火影岩，真是少女心不死啊。结语：上面这些就是纲手不为人知的一面，这些有趣的截图，你最喜欢哪一张呢？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10196', '0', '火影忍者：自来也vs纲手谁强？', '火影忍者：自来也vs纲手谁强？以下是各大网友的回答，你赞同谁的观点，或是你有什么新的看法，欢迎留言表态！1，毫无疑问自来也，自来也有一次对纲手说如果她胆敢做出背叛木叶的事会亲手杀了他，自叔清楚纲手实力，才敢这么说，毕竟能力比纲手全面太多2，多全面？连不死术都没有掌握 三忍唯一没有不死能力的人3，论全面性 三忍第一 大蛇丸 不死各类禁术，纲手 医疗 体术 不死术 ，自来也？？半吊子不完整仙术 ？还精通什么来着？？4，不开仙人的自来也也是很强的。。。老提战术干嘛，我发现你跟针对他，无所谓了5，听见全面性 我只能吐槽一下 三忍 岸本笔下都是均衡各项 各有所长 哪来的谁更全面6，螺旋丸只是一个忍术吧 蛤蟆是通灵术 加上仙术 总类就是忍术 和仙术7，我觉得自来也打前期纲手可以 后期真不好说 不开仙人等着被纲手吊打吧8，自来也的忍术还就是比纲手全面，要攻有攻，要速度的有速度的，要防守的也有，仙法不比纲手的忍术厉害？谁的实力更强没点数？9，自来也忍术？攻击力强的有什么哈哈哈 纲手一个怪力破坏力强于自来也一切忍术哈哈 自来也半吊子仙人 更是拿不出手 要速度有速度 ？什么速度？防守？什么防守？防守会被黑棒插死？三忍唯一被被插死的人？10，纲手厉害，自叔是喜欢纲手女神的。被纲手完克11，自来也 他说过纲手要是背叛的话就杀了纲手 结果被下药了12，这种问题还用想，谁跟六个佩恩打起来的还弄死一个？但自来也永远不可对跟纲手出全力13，:你认真的？，自来也对付纲手开出仙人，花式吊打没有开出仙人也足以做掉把须佐打出裂纹就**了没有水影，纲手把须佐打出裂纹之后就得被烧掉两层皮14，纲手站着给自来也打 怕是自来也也打不死 自来也曾经差点被纲手杀死，自来也不完整仙人被吹上天 结果用出来的蹩脚仙术威力普通 转换仙人模式都要半天 三忍水平都一个级别 纲手是五影里唯一一个可以硬杠斑五个须佐的 换成自来也 怕是仙人都开不出就要死了15，不好说，纲手的百豪太bug了16，能和佩恩打的男人 你觉得呢17，就好像在问，自来也和小樱谁厉害18，Z流星河: 小樱厉害啊，小樱一拳能打趴辉夜呢，自来也连佩恩都对付不了19，火影注重的血脉被直接忽略 纲手的生命里使她不开百豪 也可以腰斩不死 查克拉庞大程度也是小樱不能级 小樱百豪印攒了三年 纲手百豪耗尽 也就睡了一段时间20，自来也强，但生死对决应该是纲手赢，毕竟可能会舍不得', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10197', '0', '火影忍者：大蛇丸中过的三大封印，其中两种都让九尾要跑', '大蛇丸在《火影忍者》前期可以算是坏的发黑的大反派，他杀掉了三代火影，还试图将佐助当做自己的容器，而且最恐怖的是大蛇丸对人体研究的着深度着迷，最终让自己成为了一个打不死的怪物。当然大蛇丸也不是无敌的存在，毕竟人外有人，天外有天。而今天就小编为大家介绍三个险些干掉大蛇丸的封印吧！至于前两个让九尾看到都心惊胆战。第一种：禁术·尸鬼封尽尸鬼封尽在《火影忍者》中仅仅出现过两次，第一次就是四代目封印九尾，而第二次就是三代目封印大蛇丸的双手（险些丧命）。对于大强的九尾能够封印它的术可以说是寥寥无几，而四代这个忍术天才就捣鼓出了怎么个以命抵命的封印术，而这也是九尾唯一憎恨和害怕的封印术了！第二次：十拳剑众所周知，十拳剑是一个拥有封印能力的日本神器，在《火影忍者》中大蛇丸就险些被它给封印了（差点没活过来）！而如果用它来封印九尾也是轻松加愉快，毕竟宇智波鼬的幻术可不弱于带土，控制九尾应该是没毛病，然后在用十拳剑将其封印，看到这一套连招九尾必定背后发凉！第三次：人体封印术药师兜一直想超越大蛇丸，为了这个目的他不惜将所有优质细胞融入体内，而大蛇丸也毫不例外。虽然只是一具大蛇丸尸体，但是依旧算是一种封印，而药师兜也通过这种方法得到了非凡的力量，并且最终修炼成了仙人模式。在兜巅峰时期就连佐助和鼬联手都险些阴沟里翻船，如果不是鼬神的伊邪那美，感觉凉了。可见大蛇丸的肉体Buff有多强，感觉和前期的九尾比也毫不逊色！最后以上就是小编发现的大蛇丸三大封印，大家还知道些什么封印了大蛇丸的吗？可以下评论交流下哦！（图文来源于网络，如有侵权请联系删除）', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10198', '0', '《火影忍者》自来也在博人传复活！已经是实锤了！', '火影忍者里面死掉的三忍自来也，很多人觉得自来也死得太悲惨了，他这么好的一个人不应该死，也许博人传的作者也是这么想的，所以博人传里面自来也已经复活了，这次很可能是实锤！博人传里面有一个叫果心居士的戴着面具的人，这个戴着面具的人头发跟自来也一样都是白色的，年纪跟自来也差不多，面具下面还有跟自来也的眼睛下面文身差不多的图案，所以很多人都说果心居士是自来也。之前这些都是猜测，现在博人传漫画里面果心居士用了很多自来也的技能，先通灵召唤出来了一个蛤蟆，蛤蟆必须跟妙木山签约才能召唤，果心居士很可能就是自来也，所以他才能召唤蛤蟆。而且果心居士还用过螺旋丸！螺旋丸只有四代目火影、鸣人、自来也、卡卡西、木叶丸会用，其他的人不是死了就是站在木叶忍者村一边，现在果心居士用螺旋丸，实锤了他就是自来也的真相！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10199', '0', '博人传：果心居士就是自来也，将再次遇见鸣人', '在博人传里，遗留给观众思考的问题太多了，在壳组织出现的时候，突然冒出了个果心居士，此人带着面具，面具上刻着油，同时身材和体征都很像自来也，这让每一个观众看到这里不会不自觉的认为果心居士就是自来也。在果心居士与木叶丸的战斗中，也用处了螺旋丸，同时还对木叶丸有点了解，这更能说明果心居士就是自来也。下面我们来看看自来也在与佩恩一战后发生了什么！在自来也与佩恩的交战中，自来也断了左手，最后被佩恩的铁棒捅死，自来也陨落，靠着自己坚强的意志在蛤蟆仙人背后写上鸣人是救世主，最后沉入大海，当时相信很多影迷都极为心痛。但剧情往往会让观众出乎所料，自来也没有死。当时的自来虽已断气，但被大蛤蟆仙人召回了妙木山，在这里，大蛤蟆仙人用家传的仙术符为自来也疗伤。当年六道仙人杀死自己的弟弟羽村的时候，就是用了这个符救活了羽村，虽然这个符是有了些年代了才有这样的神力，相信在六道仙人用了这个符之后，大蛤蟆仙人也会继续弄这个符，继续让这符吸收自然能量，而过了这么多年之后，这个符用在了自来也身上，救活了自来也。拥有六道之力的鸣人都能救活开了死门的凯，能让卡卡西长出一颗眼睛，而这道符更是显示出其神效。自来也受到了这符的恩惠，身体慢慢的恢复，左手也长出来了，一切恢复了原样。就在这个时候，大蛤蟆仙人又做梦了，预言又来了，梦见了忍界又将有大难，这个大难的起点在于一个组织，这个组织力量极为强大，鸣人也会招到这个组织的祸害，听到这些的自来也，又重新开始自己的游行，寻找这个组织的痕迹。经过自来也的调查，寻找到了壳组织，自来也也做了点恶事得到了该组织的信任，加入了该组织，正式成为壳的一名成员。之前，自来也也是独自一人在调查晓组织，如今的壳组织，自来也因为自身实力的受限，只能加入该组织，才能调查。如今的自来也当然也是掌握了不少该组织的信息，当然还不能说，还不能去见鸣人，因为还有很多重要信息没有把握。当然，最后自来也终会摘下面具，与鸣人会面，可能自来也与鸣人的会面将不会那么简单，也许是在自来也与鸣人的一次对战中，鸣人摘下了自来也的面具，才得以知道真相，这个场景想想都美好。以上是小编对剧情的猜测，欢迎大家一起来讨论果心居士是不是自来也这个问题。​', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10200', '0', '《博人传》漫画24话，又一线索表明果心居士真有可能是自来也！', '在《博人传》之前的剧情中，一个神秘的反派组织“壳”引起了大家的注意，特别是里面的一位名为“果心居士”的成员，这名成员之所以备受观众不是因为他实力超强，而是因为从已知的线索来看，此人极有可能便是《火影忍者》中的自来也。可能有人会说自来也在和佩恩的战斗中已经死了，并且尸沉大海。但是虽然这个结果大家都知道，但是自来也的尸体并没有被找到，而且在第四次忍界大战中也没有被秽土转生，这点很有可能便是《博人传》再让自来也登场的理由，只要没见到尸体，就不能保证他已经死了。在之前的剧情中，果心居士虽然脸上带着面罩，但是透过面罩的眼睛部位，还是可以很明显的看到果心居士有着类似于自来也一样的眼纹。并且他在战斗中曾经使用过通灵术召唤出了一只蒸汽蛤蟆，也有用有关蛤蟆的封印术。另外在战斗中，果心居士曾经和木叶丸进行过螺旋丸对拼，他搓丸子的技术更胜木叶丸，连博人都说到除了木叶丸和他爹以外，竟然还有人会搓丸子。而螺旋丸本身就是自来也经常使用的忍术，鸣人的螺旋丸也是自来也教的，木叶丸的螺旋丸则是鸣人教的，可以说在搓丸子这个方面，自来也是木叶丸的祖师，因此果心居士是自来也的可能性又大了一分。并且果心居士在得知博人拥有“楔”印记的时候还放过了博人，并且他还知道巳月和大蛇丸的关系，可以说果心居士的真实身份已经是呼之欲出了。而在最新一话的《博人传》中，“壳”组织中的乾大人的一段话，更加的让人确定果心居士就是自来也。乾说果心居士有手段有头脑，但是他是一个比任何人都难以捉摸的家伙。从乾说的话中可以看出，“壳”组织对于果心居士这个成员并不是知根知底，可见存在果心居士是卧底这种可能性的。在加上之前剧情中的各种线索，我们有理由相信果心居士便是自来也，只不过他卧底进“壳”组织的真正目的暂且未知，我们只能期待接下来的剧情中作者给我们答案。你们觉得果心居士是自来也这件事是否已经实锤？欢迎在下方评论区发表你们的看法。如果你喜欢这篇文章，别忘了点赞、收藏、分享支持一下小编，你们的支持就是小编的动力。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10201', '0', '火影忍者：拥有仙术的八大忍者，自来也排名倒数', '日本动漫《火影忍者》中实力强大的忍者有着绝对的话语权，拥有轮回眼或者仙术的忍者均是站在金字塔顶端的存在，由于轮回眼在整个忍者世界凤毛麟角，得到的人屈指可数。以至于在普通忍者心目中习得仙术是他们最大的追求，本期一起来分析《火影忍者》中拥有仙术的八大忍者。自来也‍自来也并没有完美掌握仙术，凭借自身的力量无法进入仙人模式，需要依靠蛤蟆仙人深作和志麻的帮助才能仙人化，在与佩恩六道作战中，让我们了解到仙术的强大，自来也仙人化后实力略低于佩恩六道。漩涡鸣人漩涡鸣人作为《火影忍者》的主角，是一位把仙术发扬光大的人，在自来也死后，漩涡鸣人在妙木山习得仙术，并完美进入仙人模式，战斗力接近佩恩六道的实力，剧情的后期，漩涡鸣人可以施展多种仙法。千手柱间千手柱间是木叶村的初代火影，是曾经的最强地球人，千手柱间可以完美驾驭仙术，仙术来自湿骨林，再配合木遁术，几乎无敌手。宇智波斑宇智波斑是一位传奇人物，早期有着与千手柱间匹敌的实力。宇智波斑原本不能施展仙术，但在第四次忍界大战中，宇智波斑吸取了千手柱间的仙术查克拉后便会施展仙术，配合轮回眼的能力，实力远超千手柱间。药师兜药师兜是大蛇丸的手下，在宇智波佐助打败大蛇丸后，药师兜独自追求强大的实力，机缘巧合下，药师兜习得龙地洞仙术，可以完美进入仙人模式，从药师兜凭借一人之力对抗宇智波佐助和宇智波鼬而不落下风，可见药师兜习得仙术后实力的强大。大蛇丸大蛇丸从来没有施展过仙术，但根据大蛇丸的自述中，大蛇丸能够使用仙术，大蛇丸开发的咒印中注入过仙术查克拉。很久以前，大蛇丸已经习得龙地洞仙术，只是大蛇丸的身体不能很好的承受仙术的力量，导致大蛇丸无法进入仙人模式。巳月巳月是大蛇丸制造的人造人，在《博人传》中登场，大蛇丸经过多次的实验下，巳月掌握了龙地洞仙术，能够自主的开启仙人模式，至于巳月的实力有多强大，目前还未知。重吾重吾的一族有着独特的体制，可以像仙术一样，吸收自然能量，只是那种情况下，重吾的意识不稳定，比较狂暴，勉强算作是仙人化，威力不及正常的仙术。总体来说，漩涡鸣人、千手柱间、药师兜和巳月完美掌握了仙术，而宇智波斑、大蛇丸、重吾和自来也没有完美习得仙术。因此，自来也掌握的仙术排名只能倒数了，而还是少年的巳月应该是完美掌握仙术的最年轻者。好啦，本期就说到这里，有不同观点的朋友欢迎在下方留言，想要看到更多精彩的动漫内容，请大家记得关注我哦！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10202', '0', '火影博人传，像鼬那样的无私，这几大证据表明自来也没有黑化！', '火影博人传的故事继续进行着，最新一回合的漫画也更新到了24回。自从自来也和果心居士联系到了一块以后，许许多多的火影迷开始了自己的脑洞。有的说自来也被幻术所控制，无奈加入【壳】组织。有的说自来也在死之前性格大变，彻底陷入无边的黑夜，甚至还有大蛇丸的一番话作为佐证。那么，事实上自来也真的黑化了吗？在果心居士就是自来也的前提下，这几个证据表明自来也并没有黑化。首先是在最新一期的漫画中，果心居士自言自语的感叹了一句话，那即使是我还没有得到你们的信任。这里表明一件事，那就是果心居士并不是最开始就加入【壳】组织的，是中途加入的，虽然执行过很任务，但并没有得到绝对的信任。为什么会得不到绝对的信任呢？因为果心居士曾经的地位让人不相信他会做出这样投敌的举动。加上之前他自己说过对木叶很熟，所以能符合这一特征的，也就只有自来也。在他与木叶丸对战的时候，故意放走了听他们三人，如果真的觉得博人的【楔】有趣，为什么连木叶丸他们一起放过？其次，他本身一直带着面具。就像之前带土那样，为了方便自己行事，特地做出伪装。只有隐藏自己的身份，才方便融入敌人内部，打探出真正的情报。最后也就是最有力的证据，妙木山的蛤蟆可不是只有一只，如果自来也真的没有死，那两大蛤蟆仙人深作和志太为什么不去通知鸣人呢？原因只有一个，那就是自来也发现了新的危机，需要前往打探情报。只有这样的情况，鸣人才不会知道自来也还活着的消息，综上所述，打扮成果心居士的自来也并没有黑化，只是像鼬一样，默默的保护着木叶。好了，这就是本期小欣为大家带来的最新分析。想要看到最新的动漫情报，请大家记得右上角关注一下（永远的龙珠）。当然了，为了让更多的动漫迷们看到这条消息，也请大家多多评论，为小欣点个赞哦！文/永远的龙珠原创，未经授权谢绝转载！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10203', '0', '这些数据告诉你，自来也有多强', '自来也的陨落对于火影来说甚至火影迷来说真是一件悲痛的事情，那么自来也到底有多强呢，简单说一下官方对自来也的评价，自来也是“三忍”之一，拥有影级实力，在官方的者之书中综合评分是71分，是最后一次公布人物的综合评分，在榜单上自来也的评分是最高的，与宇智波鼬持平。记得干柿鬼鲛说过这样的一句话：鼬大哥，自来也真的很强，也只有你能跟他一战。从这句话可以看出，鬼鲛对自来也实力的认可，说明宇智波鼬与自来也的实力差不多，谁胜谁负不好说，这也印证了者之书中两人评分相同，下面来看一下自来也的战斗记录。据官方爆料说自来也在《火影忍者》中对战记录有12场，除了死亡之战中拿出真本事与佩恩六道对战外，自来也的其它战斗并没有使出全力过，在12场战斗中仅输了2场。自来也在与佩恩的交战中，虽然使出了全力但自己也死了，这就仅仅说明自来也说给了实力吗？不是的，只能说自来也不了解佩恩的秘密打的很被动，而佩恩非常了解自来也的作战方式，其实是一场不公平的战斗，就连战斗后佩恩自己都说，要不是因为这个秘密，我们根本就赢不了他，这是佩恩的原话。从佩恩的话中就可以知道佩恩并没有十足的把握能够干掉自来也，毕竟自来也是自己的师父，还有自来也在战斗的过程中有机会逃跑的，根本就死不了，只能说剧情的需要，必须要自来也牺牲，所以，自来也的实力是不输给佩恩的，应该是前期出现人物最牛的了。还是自来也色色的时候最开心，但他也是最值得信赖的人。为了村子甘愿牺牲的人，和鼬一样。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10204', '0', '八门遁甲后继无人！小李难当大任？', '在火影忍者中，除了那些强大的血继限界之外，还有一些平庸体质。只是这并不代表他们就无法成为一名强大的忍者。也许在忍术道路上无法有所建树，但却可以另辟蹊径，体术就是最好的证明。云隐村的历代雷影，还有木叶忍者村的日向一族，他们的诸多招式，都离不开那强悍体术的配合。就单纯的最强体术奥义来说，除了大筒木辉夜的八十神空击之外，只有八门遁甲所向披靡。它是一种激发身体潜力的超高级别的体术，通过为身体设置八种限制，也就是俗称的开门、休门、生门、伤门、杜门、景门、惊门、死门，一共八门，每开启一门，就能短暂获取庞大的查克拉，对身体承受负荷比较大，也有一定的损伤，尤其是一旦开启死门，虽然可以扭曲时空，但同时也会付出生命的代价。八门遁甲的辉煌战绩也是十分耀眼，迈特戴第一次施展，就让雾隐村损失惨重。雾隐村最强的忍刀七人众，个个有着精英上忍的实力，硬是被迈特戴变成了忍刀三人众。到了迈特凯之后，更是成就了凯皇之名，先是只开启六门就杀死了拥有影者实力的干柿鬼鲛，随后开启第八门，以燃烧生命为代价，凯皇的实力直接就超越了五影，即便是拥有六道之力的宇智波斑也差点栽在他的手里。幸好有漩涡鸣人，才让凯皇保住了性命，但双腿却残疾，后半身只能坐在轮椅上。八门遁甲也就此闻名，它是由木叶忍者村万年下忍之一的迈特戴所创，一直流传于凯皇一脉，其他忍者很难修行，即便是卡卡西当初拥有写轮眼的时候，也只能开启第一门而已。只可惜到了博人传后，八门遁甲后继无人，小李目前并没有展现出相关的实力。第四次忍者大战之后，本来以为凯皇虽然隐居了，但李洛克却继承了他的衣钵，八门遁甲想必会再度崛起。只是没想到的是，在博人传的新中忍考试中，昔日木叶忍者村诸多家传秘术纷纷亮相，写轮眼、影子模仿术、多重影分身、超兽伪画无一不再现辉煌，唯独八门遁甲却迟迟不上场，也许李洛克的儿子还没有学会八门，也太次了吧。还记得在火影忍者中忍考试的时候，小李凭借八门遁甲硬抗我爱罗的场景历历在目，如今李洛克的儿子梅塔尔李，却一点八门遁甲的痕迹都没施展出来，早早的就败于我爱罗义子新希之手。要知道当初第四次忍者大战的时候，李洛克也不过只能把八门遁甲开到第六门景门，如今难道没有丝毫长进，不足以担起传播八门遁甲的重任？回过头来看看，小李在博人传中并不经常露面了，忍者学校里没有他的位置，教导新一代的木叶十二小强也没有他的份，只是在中忍考试中担任一个考官，木叶在遭遇诸多危险的时候，小李也没有怎么上场。按理来说，以八门遁甲的威力，即便是开启第七门也应该能够达到普通忍者的水平，为什么在遭遇大筒木桃式的时候，小李依旧没有出现。有的网友认为科学忍具的出现，连忍术都可以被复制，不需要修炼就能获取强大的忍术，估计八门遁甲被池本老师边缘化了？当人也有网友不认同，也许梅塔尔李还未能把八门遁甲练到家，八门遁甲还是会在小李和梅塔尔李父子俩的带领下，再度发扬光大。孰对孰错小编也没法判断，各位看官你觉得呢？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10205', '0', '[火影忍者]如果宁次还活着', ' 作者：巴豆不是豆之前看见有帖子说天天在博人传中是和小李在一起了的，但是我一直认为天天到最后应该还是单身的，因为她一直都是喜欢着宁次的。我截了几幅图，是在大战中宁次死了之后天天的表现。虽然只有短短的几秒钟，其余的并没有什么说明，但我觉得在无形之中已经说明了一切。一句“宁次，你为什么扔下我”让人无比心疼。在被施了无限月读以后，天天的梦境中宁次还活着，即使她明白那不是真实的世界，她也愿意融入进去了。我想有很大一部分原因也在于宁次吧，至少那里的宁次还活着。我一直都很喜欢宁次这个角色，其实我一直都希望宁次没有死。如果宁次没有死，那他一定会和天天在一起，像最后一张同人图里面一样，说不定会生两个孩子，一个帅气的男孩，一个可爱的女孩，博人和向日葵就会多一个疼爱他们的舅舅，一个温柔的舅妈，还有两个哥哥姐姐（或者是弟弟妹妹）……那样的话，一切都会很不一样呢。可是，一切都只能是如果啊，因为天天爱着的宁次已经不在了……追求着自由的笼中鸟啊，希望你去的天堂，可以任你自由地翱翔。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10206', '0', '博人传：最穿帮的3大情节，小葵一招点晕九尾，宁次表示学不会！', '博人传更新也有一段时间了，在看博人传的时候，大伙儿有没有发现有些逻辑不通之处？今天我们就来带领大家回忆一下博人传中的穿帮合辑。1.向日葵一只手点晕鸣人这个是发生在火影番外里面的故事，因为博人和向日葵两人准备和父亲鸣人一起出席火影继任大典，向日葵要带她的布偶一起去，结果遭到博人的拒绝，博人觉得带布偶去那么隆重的场合会显得他们很幼稚，于是抢夺向日葵手中的布偶，向日葵紧抓不放，结果一不小心布偶被扯成两半。看着被撕成两半的布偶，向日葵大怒，在博人还没反应过来的时候就觉醒了白眼，将博人打出房间。而见比情形的鸣人立马挡在博人面前，向日葵也没收手，直接一只手点在了鸣人身上，而这一下子，不仅点晕了鸣人还点晕了鸣人体内的九尾，导致鸣人整整昏迷了一天，结果错过了继任大典。但是在火影中，鸣人被宁次点过好多遍，虽然是把鸣人点晕了但是却没点晕体内的九尾。而向日葵小小年纪，轻轻一点就同时点晕了鸣人和九尾？实在是不可思议。至少宁次表示自己学不会啊2.班长的木遁在博人传一开篇就提到了木叶村十几个人被操纵的事件，调查组佐井也多次查到了木遁的痕迹，想着以为是凶手留下来的，结果在后来的破案中木遁这一线索从未提到过，那镜头出现的木遁到底是干啥的，难道被遗忘了吗？最后的凶手却是班长，还是巳月暗中发现的。那木遁是干啥用的，好奇。3.我爱罗的黑眼圈我爱罗永远都是一副黑眼圈上场，一开始说是因为守鹤，晚上睡眠不好所以才有的。后来他没有守鹤了，黑眼圈还是一直存在，从来没有消失过。我爱罗的黑眼圈永远都是一个谜，他是多久没有休息好了啊。也有人说是因为磁遁的原因，但是新希用磁遁的时候并没有黑眼圈的出现啊。真的很谜了，可能编剧也没注意到这些细节吧。最后你认为这些事作者故意的铺垫，还是漏洞呢？欢迎留言点赞收藏，喜欢火影的可以关注一波。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10207', '0', '火影忍者中，宁次的死其实并不简单，其中隐藏的玄机你看懂了吗', '宁次的死其实并不简单，其中隐藏的玄机你看懂了吗把火影分为两类人群1.以123代，卡卡西他爹，鼬他爹，团藏等等等为主，看作是《二战前昭和系》2.以鼬神，卡卡西，带土，长门等等等等为主，看作是《战后的昭和系》3.以木叶十二小强等为主，看作是《平成系》这样就容易理解了。战前昭和系是一帮极其传统，忠君爱国，国家大于个人主义，君君臣臣，父父子子（注意一个细节：卡卡西之前的火影，下属见到是要下跪的），不惜牺牲自己朋友的生命而捍卫集体主义。战后昭和系是一帮反抗的一派，对之前所有信条充满质疑的反抗的一派，但没有完全成功的迷茫的一派（苏联中国80年代，日本60年代，美国70年代都有这类人：“垮掉的一代” ）。平成系就是现代的个人主义，小团队大于虚无缥缈的集体利益的小圈子主义，亲情友情主义（很符合现代吧）。鼬神，卡卡西，带土等人是战前和战后的思想纽带，所以安排鼬神和带土死，来衬托对旧时代的抛弃。日向宁次，是十二小强里，最富二代，同时却是新时代里最传统最保守的人，剧情却又安排他作除了主角以外第一个挣扎和反抗的人。所以选择了他去死，来衬托一个新时代的彻底来临。我这可不是过度解读，从佩恩宣扬的思想和团藏的价值观以及鼬神的价值观可以看出，岸本这人的文化底蕴很深，绝非单纯为了情情爱爱而安排一个小强去死。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10208', '0', '其实剧情杀不死宁次，杀死宁次的是他！', '在《火影忍者》中，宁次被鸣人誉为“天才少年”，而鸣人却被宁次说成是“吊车尾”，然而在之后故事的发展中，宁次在中忍考试中输给了鸣人，甚至最后为了保护雏田挡刀惨死。有人说这是作为日向一族分家的命运，注定就是这样的，分家就该为宗家牺牲，也有人说这是剧情杀，明明有回天不用，非要肉身挡刀，除了剧情杀以外没有其他更好地解释了。在我看来事情并没有那么简单，年少时被誉为天才少年，而长大后又成为了上忍，要知道那时候鸣人还是下忍，那么问题来了，宁次死因到底因为什么呢？依我看，其中很大一个原因，那就是宁次的“外挂”没有，或者说是更新的太慢了。明明宁次每一次见面都有变强，甚至都变成了上忍，怎么可以说宁次不强呢？其实不然，并不是宁次不强，而是宁次和后来的鸣人佐助一比，就显得“不够强”了。在前期宁次一个回天几乎让人无法近身，而且是359度的免疫任何飞行道具，可以说的上是“正常水准中的BUG级”，然而随着故事的推进，主角实力突飞猛进，又是九尾，又是仙人模式，又是月读，又是须佐之男。而反观宁次呢？除了对于八卦掌的进一步掌握和超越，几乎没有更多地描述了，也没获得什么超常规模的力量，反而在主角和反派那些近乎于“外挂”一样的力量面前，他就像是一个普通人，再加上作为释放回天的代价查克拉，每释放一次都是要从全身释放，而宁次体内也没有尾兽，并没有源源不断地查克拉。而宁次的死也告诉了我们现实中的一个道理，那就是人生就是逆水行舟，不进则退！也许你这学期还是班里的前10，下个学期掉到第15名中游水准也是可能的，而且是即使你在努力的情况下，因为下学期的知识未必是你擅长的领域，反而也许是你排名后面的几位同学擅长的知识点。还有工作生活中也是如此，也许今天这个人还在默默无闻的努力，看起来存在感很低，过了一阵子居然成了自己的顶头上司，你甚至都不知道发生了什么，明明自己更努力为什么比不过平时看着不起眼的那个人？其实不然，不一定努力了就会一直领先，因为还存在“天赋”，也存在“看不见的努力”，宁次天天都在日常锻炼，或者做一些适合上忍级别的任务，反观鸣人佐助，他们都开始跟着卡卡西去灭刃刀七人众的再不斩了，更不用说后面单挑佩恩那几乎都是超越影级的大战了。而且自认为自己努力了，也只是“自己认为”，在别人眼中是否对自己的努力认可了也是个问号，毕竟自己努力的时候，往往是在自己擅长的领域更努力，比如宁次就是，而鸣人他们最后早就超越了从前被尾兽控制身体，甚至后来不仅能够控制尾兽的查克拉，还和尾兽成为了朋友，也就是说鸣人是在自己短板的地方努力，而那个短板正好是宁次身上没有的。这也就是为什么宁次明明顶着上忍的称号，却最后被沦为挡刀龙套的原因之一了。也许今天你朋友还是灵魂画师，甚至你画的比他好看，但也许过几年他就是一个知名画师，不仅努力很重要，努力的方向更重要，现实的我们要引以为戒，不要因为现在人生上有一点小优势就变成了骄傲的将军，反而应该更加小心更加努力。烧烧说，一个污污的漫画平台，', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10209', '0', '《火影忍者》忍者战斗都有不能输的理由，宁次的难以理解！', '在火影忍者里，几乎每一场真正的较量都是玩命的，死对于一些真正的忍者来说不算什么，但是他们战斗都是有不能输的理由，很多忍者都是为了伙伴而战，像凯皇和小李这样的人，不能输的理由就是因为想证明吊车尾也能很牛逼，所以凯皇最后被宇智波斑称为最强。日向一族的宁次自然也是有不能输的理由，他的理由是因为他是天才，这一句话是从鸣人嘴里说出来的，别人也有说但鸣人说的让他最深刻，因为曾经一个十足的吊车尾打败了一个天才的他，所以吊车尾能做到的身为天才的他岂能做不到？​宁次说出自己不能输的理由是在117集，那是佐助逃离木叶村的时候，纲手女神命令鹿丸组队去追回佐助，宁次作为木叶强大的战力自然也被邀请了，那一场战斗他的对手是使用蜘蛛丝的怪物，最后身负重伤赢了对手，换做被鸣人打败之前的他可能早就输了。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10210', '0', '上单凯？打野凯？七七半肉暴击凯教你做人 不服的solo！', '哈喽，大家好，我是王者荣耀原创文章努力更新者，七七奇说，欢迎关注哦～今天七七给大家带来的是一场秀和一张战书～今天的主角呢？当然如题——凯。​玩过王者荣耀的人大家都知道，凯是王者峡谷里唯一一个名字只有一个字的英雄？他是长城守卫军，他是王者荣耀峡谷里的单挑王……也有太多人争论他怎样最厉害，打野凯？全输出凯？肉凯？那七七为了平息一下小伙伴在我这儿的争论，先上一波数据图吧，定义一下什么叫最强战士！​​​​看完数据，相信很多小伙伴都惊呆啦吧，这么高的输出，是怎样打哒？是怎样出装的？那么七七当然会无私的爆出自己的出装呢！如图，物理防御鞋还是​​战士的必备。第一大件是暗影。(注意，即使是半肉，第一件也绝不能出肉哦～为什么？哪能这么没出息？)紧接着第二件竟然是420高物理防御的反刺！既然要打暴击凯，第三件当然得出暴击20%的！后面的？经济已经压制了，随便点哈哈哈～​之所以叫后面的随便点，是因为七七还要说正事呢！你们还在吹自己的凯是单挑王？打野凯？肉凯？七七65+输出的半肉暴击凯教你做人！七七今天就在这儿下战书啦！欢迎加我solo！征召也可以！(给私信哦～才能联系到。)面对极度膨胀的七七，你准备好了吗？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10211', '0', '王者荣耀之凯皇', '王者荣耀里凯的定位是刺客/战士型的英雄，自带回血，大招高额免伤。凯一直是位非长热门的英雄，能扛能输出，也是小编的本命英雄，在S9和S10赛季更被称之为，单挑王，站撸玩，但是S11由于射手的强势崛起，战士的时代好像就要没落啦。结下来们说说凯皇如何出装秒脆皮的，打野和上单的凯出装也是有点不一样的，下边简单的介绍一下出装和铭文。第一套巨人之握-抵抗之靴-爆裂之甲-暗影战斧-闪电匕首-无尽战刃打野装，游走，发育快伤害高，抓人简单。第二套抵抗之靴-暗影战斧-暴烈之甲-宗师之力-无尽战刃-破军暴击流，让对面看到你就怕。4第三套暗影战斧-影忍之足-无尽战刃-宗师之力-碎星锤-破军，上单这套也是没问题的，伤害很高。蓝色狩猎（移速+10%攻速+10%）绿色鹰眼（攻击+9、物理穿透+64） 红色异变（物理攻击+20，物理穿透+36） 铭文属性总和：移速+10%攻速+10%、物理穿透+100，物理攻击+29召唤师技能建议带闪现，要是打野的就带惩戒，发育快点。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10212', '0', '王者荣耀：版本之子凯？改版后的凯怎么玩？', '众所周知，新赛季的开启，不仅新英雄的上线十四的英雄也都调整了，四个装备也调整了。适合上分的英雄也就随之改变了。接下来为大家介绍一个比较强势的适合上分的英雄。凯：请叫我凯皇优点：回复能力强，猥琐守塔能够保持健康血量；版本更新的受益者，2级大以后尤为暴躁，能抗能打；缺点：单独带线被抓缺乏有效逃生能力；适合人群：原曹操、杨戬爱好者，肉搏爱好者；颜值控...操作简单，又肉又有输出，绝对的上分利器！ 那么如何玩好凯呢？首先铭文：由于凯的23技能都加移速，游走支援后期有470，开大更是达到500+，所以不是非要出移速铭文了（跟花木兰一样）。我的建议是，凯用和花木兰一样的铭文，长生（+750血）和百穿，如果没有长生，带狩猎隐匿也是可以的，但是前期硬度会下降一点出装：鞋子（如果没有很强的控制建议物抗） 黑切 冰脉 不祥征兆（建议为了不拖节奏，出守护者之铠就行了，大后期再补完不详） 魔女 破甲弓这个英雄严重耗蓝，并且伤害不低，冰脉必出，所以前三件可以是鞋子，黑切，冰脉。第四、第五出反甲or不详或魔女，第六件末世破军血手复活甲都可以凯毕竟是近战英雄，攻速慢，所以出暴击效益不大，而且因为攻速慢，所以凯比较吃技能来打出伤害，所以cd装可以多出。好了大家可以联系联系这个英雄，唯一的缺点就是不能穿墙，不然真的可以进入上单T1', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10213', '0', '王者荣耀——凯出装思路', '王者荣耀中凯可以说是一个上单的霸主和单挑王也不足为过，输出能力强，防御力更强，最重要的是有很强的续航能力，能反杀敌人，所以受到许多玩家的喜爱，下面我就来说说他的出装思路。出装思路；第一件我们还是老规矩，出鞋子加强移动速度，抵抗之靴，这件装备是很多坦克战士型的英雄必出的装备，这件装备主要时减少被敌方控制的时间，有更好的前排突进时间；第二件暗影战斧，这个装备可以说是就像是为凯量身定制的一样，提高血量，攻击，技能时间的减少，在前期可以说是又一次提升了凯的全方面能力；第三件反伤刺甲，这件装备其实主要就是针对敌方物理攻击型的英雄，因为凯的续航能力出了这件装备，可以说只要不是太逆风的局势，大部分都可以反杀；第四件破军，这个装备还有一种说法叫做杀人剑，顾名思义这个装备提供非常高的物理伤害，在单挑时往往能打出爆炸的伤害；第五件魔女斗篷，这件装备增加了生命值和法术防御，让凯在对法师爆发型的英雄，不用那么吃力；第六件冰痕之握，这件装备主要就是留人，又加血，又加防的，配合前面出的装备，可以说是非常的变态；开团建议；因为凯的控制能力极强，建议从敌方中间切入。以上就是今天凯的出装介绍了，小伙伴们要是喜欢的话，就点个关注吧！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10214', '0', '王者荣耀新英雄“凯”独家揭秘！凯技能详解', '近期王者荣耀官方限时活动正在进行中，集齐7名英雄卡即可兑换新英雄“凯” 不知道各位农药战友们现在集齐没？今天迅哥就为大家提前了解下新英雄“凯”一些概况技巧。希望大家能积极分享给身边的基友们。王者荣耀凯技能解读被动：铠拥有精湛的战斗技巧，如果凯的挥砍(普攻与二技能)只命中了一个单位，则这个单位受到50%额外伤害。一技能：铠向前投掷刀刃，在敌人中最多弹射4次，对目标造成xx点物理伤害和减速，命中的第一个单位将会受到额外眩晕和减速效果，如果该技能至少命中一个单位，则会回复生命值与30%冷却时间。二技能：铠向前连续挥砍，造成xx点物理伤害，第二刀会造成额外的击飞效果，同时使下一次普攻获得额外的伤害和冲锋效果。三技能：在1秒的延迟后铠召唤魔铠，造成范围xx点法术伤害，并强化自身攻击力、移动速度、格挡、法术吸血，同时对周围敌人造成法术伤害持续十秒。被动：脱离战斗后铠的生命、能量恢复、移动速度将大幅度提升铠是物法双系、技能分割明显的英雄，并非是老夫子一样的法球反弹，也并非狄仁杰的双系伤害结果。他的一二技能都是物理系，而三技能是纯粹的法系，单挑时候的伤害额外提升，无疑是大大的强化了其在单挑方面的能力，估计会成为S8的主流强势英雄。出装推荐抵抗之靴+红莲斗篷+暗影战斧+不祥征兆+魔女斗篷+末世/破军/复活甲出装分析：根据铠的技能定位，我们可以看出这是一个上单像更好的英雄!但是除了有加速和减速外，开作为上单位和现在的曹操、杨戬以及花木兰比都缺少突进位移技能!所以上单位方面可能稍逊一筹!出装方面也是现在比较流行的半肉出装!抵抗之靴，减少控制时间的最佳选择，当然如果对线的射手压力大的话，可以选护甲鞋!红莲斗篷，前期性价比非常高的防御装，还能很好的带线功能!暗影战斧，现在半肉战士的通用输出装，有了暗影战斧，现在的战士都不缺输出!魔女+不祥，性价比超高的物防和法防装备!最后一件补输出或者容错都是可以的!当你用上新英雄凯后，配合着哥给大家的攻略，再加上能帮你至少提升50%手游网络稳定性的迅游手游加速器，相信谁也无法阻止你5杀、MVP了！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10215', '0', '火影忍者，手鞠为什么看上了鹿丸？', '随着时间的推移，忍界中曾经的少年们逐渐长大成人，并且大多成家立业，手鞠作为砂隐村最抢手的女忍者，不仅父亲和哥哥都是风影，自身也很优秀，如此，为什么手鞠会看上懒散的鹿丸呢？门当户对虽然手鞠的身份看似远超鹿丸，但其实两家可谓是门当户对，奈良一族是木叶村秘传家族之一，虽然没出现火影，但是家族却世代担任木叶要职，奈良鹿久曾担任木叶上忍班长、木叶高层智囊、忍者联军总指挥。性格相符因为身份不同，手鞠养成了直来直去的性格，雷厉风行，讲究效率，鹿丸做事情总是觉得麻烦，但因为极高的智商和办事效率，却非常符合手鞠的性格，而且因为手鞠的直爽，也不那么让鹿丸觉得麻烦。不打不相识实力高强的手鞠在村子中顶级的强者，除了我爱罗，似乎没有忍者能够入她的眼，但是这种自信在与鹿丸对决时被摧毁，鹿丸虽实力一般，但依靠智谋战斗却让手鞠大惊失色，这使得手鞠对鹿丸印象十分深刻，手鞠救助鹿丸更是让两人了解更深。家庭因素从小鹿丸就经常目睹父亲鹿久被母亲压制，完全不能反抗的父亲只能去酒馆喝酒，这种熟悉的感觉似乎也就是手鞠对鹿丸的吸引，这让鹿丸对手鞠和对其他女忍者有所不同，手鞠因此也会对鹿丸有所优待。最主要的是，手鞠虽然厉害，但是还是一个女人，需要一个肩膀作为依靠，而鹿丸则对手鞠说过，男人不能输给女人这样的话，这也极大的给鹿丸加分，毕竟鹿丸确实是一个优秀的家庭支柱。动漫先生公众号；dongmanxiansheng，长按复制，福', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10216', '0', '恋爱情结~鹿丸与手鞠的“麻烦”爱情', '中忍考试，一个新星云集的地方，也正是这个地方，一对新人正挥洒着汗水享受着战斗的乐趣。一个拥有压倒性的力量，一个拥有超出常人的智力;一个是来自砂忍村的妹纸手鞠~一个是来自木叶村的小鲜肉鹿丸~俩人斗智斗勇，虽然最后我们的鲜肉君败下阵来，对手却也没有打败他的那种荣誉感，总感觉这是自己输了。他们俩的相遇是那么的尴尬，也正是这次有趣的邂逅才有了之后感人的爱情情结…原文作者：萌宝宝慕所谓有缘千里来相见，经过这次的对战手鞠对这小子也应该也是产生了一点好感。毕竟能通过如此精密的计算把她逼到如此境地，也正是这件事的铺垫才有了后面的感情发展…执行佐助追回任务时，木叶不敌对方毫无还手之力，在紧要关头，手鞠出手救了鹿丸，当时的他们也是差点被敌人那恐怖的力量所压制!危急时刻他俩充分的展示了作为情侣所拥有的“默契” 成功的击败了对手。也正是这次他俩就开始有了过于友情的“特别情感”~据说他俩的第一次私会什么的也是在这哦~之后他俩的感情可以说是愈演愈烈。宁次、丁次等人被救回木叶治疗时，鹿丸和手鞠在手术室外等候，当时身为队长的鹿丸觉得是自己指挥不当才使得队友有了生命之危。见此情形，手鞠先是感受到了鹿丸如此的温柔，随后便安慰了这个爱哭的小鬼，在见证鹿丸立下热血的誓言后，此行也达到了终章…离别时的那句叮嘱：“再发生什么事，我们还会来帮忙。到那时再见了，爱哭鬼。”也正是这句话才有了之后的种种吧~没错我们知道的是他们结婚了并且有了一个可爱的男宝宝(嘛，所谓的爱情结晶好羡慕哦小编也想快点生一个呢~恩恩。等等话说我有女朋友吗?咳咳)好了别闹别闹看了这莫多大家是不是也觉得这段“麻烦”的爱情其实也是很感人呢~嘛，接下来就和小编一起体验体验游戏里这对情侣的麻烦技能吧(很强势的一对组合哦虽然比不上鼬神什么的)鹿丸作为决斗场异常强势的跑位忍者，从他的技能的控制效果和伤害的数值来看他都是不容忽视的忍者其强悍程度我们就不多说了作为c忍这的确也是对草根玩家的一种福利。另一方面手鞠也是拥有强大的控场能力的忍者同样也是c忍，他俩的组合不会玩的先别说，会玩的真是恶到心里去了…同样是c忍获得方法也是相当的简单，忍者学员的米拉桑赶紧行动起来一起来体验这麻烦的爱情吧~', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10217', '0', '博人传：新五影的五位侍卫，可能继承“影”？鹿丸在一旁偷笑', '火影忍者中有五个村：木叶村、云隐村、雾隐村、岩隐村和砂隐村，每个村都有相应的村长，五个村也就是五影，五影的职责除了维护村子的安全还得注意与邻村的交涉。每一次召开五影大会，也就是各村之间联络感情维护和平的时候，虽然现在是和平年代，参加五影大会时，各村长还是习惯性的带上自己的护卫，之前也有一些护卫成了下一任火影。今天我们就来分析分析下一代五影人选会不会是现在五影的护卫。1.木叶村火影护卫——鹿丸木叶村当前村长是鸣人，因为木叶隶属火之国，所以鸣人被称为火影。按理说鸣人的实力是杠杠的，根本不需要什么护卫，但是这个鹿丸并不是保护鸣人的安全，更多的角色是助理，帮助鸣人管理一些琐碎事物。而且鹿丸的实力也不差，虽然没有鸣人厉害，但是也算得上是高手了，那这样子的鹿丸有可能会是第八代火影吗？如果是的话，鹿丸就在一旁偷笑了，但是按现在博人传的剧情发展，感觉鹿丸没戏，下一代火影人选估计就是鸣人儿子博人了。2.云隐村雷影护卫——奥摩伊奥摩伊作为达鲁伊的护卫，实力不错，而且论资历和实力，在云隐村估计也没人比的过他了，新一代年轻人中目前也没有天赋比较高实力比较强的佼佼者，所以下一代雷影人选很有可能是奥摩伊了。3.雾隐村水影护卫——新面孔长十郎也是从护卫走到今天的水影之位的，而他的护卫是一个新面孔，从来没有出现过，感觉这位新人做下一代水影的可能性很小，连一个名字都有给她。而最有可能的估计就是神乐了，神乐的实力和地位在雾隐村都很突出。4.岩隐村土影护卫——赤土赤土作为黑土的护卫，还是非常有可能是下一代土影的。毕竟黑土和赤土曾经都是大野木的护卫，最后大野木死后，由黑土继位，赤土继续辅佐黑土，而且黑土没有孩子，子承父业一说也没人，所以下一代土影最大的可能就是赤土了。5.砂隐村风影护卫——勘九郎勘九郎作为风影我爱罗的护卫，实力不用多说，在砂隐村也是数一数二的存在，如果是下一代风影感觉也是有可能的。但是我爱罗还有一个儿子新希也非常厉害，在近期中忍比赛中连博人都不是他的对手。这样看的话，下一代风影估计就是从这两人中间选了。最后你认可“影”的侍卫一般都会成为下一届影的说法吗？欢迎留言点赞收藏，喜欢火影的可以关注一波。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10218', '0', '井野结婚后比任何人都幸福', '火影忍者博人传动漫已经更新到第33集，一个没有灵魂的画师可不是一个合格的佐井哦，说起来小编最羡慕的一对情侣，不是鸣人雏田，也不是佐助小樱，而是画与花结合的井野佐井夫妇。￼这一集本来焦点还是在于如何教育小朋友热爱生活，而不是走捷径，突然间，冷冷的狗粮在脸上胡乱地拍，不过说回来，井野的傲人身材，可不是盈盈可握的搓衣板小樱可比的，而且人家老公佐井可是木叶有数的神笔马良——可不是嘛，这么好的一个IP就给火影给借用过去了，画饼充饥不说，唉，反正国漫任重道远啊，扯远了，搞艺术的男生要嘛泡死妞，要嘛被妞泡死。￼所以小编恨啊，当年干嘛读的不是艺术类专业，感觉错失了一大片森林，咳咳，因此井野和佐井的这段关系里面，一看就知道谁是主动派啦，如同这次佐井随便挑逗一下老婆，就让其情不自禁，手足无措——井野大姐，你手上拿的可是花还有剪刀啊，注意一下，儿子都打酱油了，还那么激动，想干嘛！￼不过要说婚前谁比较主动一点，感觉还是偏向佐井呢，毕竟女孩子要是太主动的话，感觉就要变成小樱呢——可怜我小樱，别人都成双成对，她就只等来了一只鸟（鸣人结婚的时候），婚后还独守空房10年有余，又当娘又当爹，难道女汉子就活该没有爱情嘛（佐助：你说呢，一言不合就还房贷，宇智波家族再有钱也不顶事啊）？￼不过儿子井阵倒是遗传了母亲的容貌，画画什么的还是算啦，人家漩涡家的向日葵妹妹都能当你老师，就知道你没啥天赋了，还是好好地孝敬父母吧，好吧，以上胡说，作为女神妈妈的井野当然是爱生活爱儿子啦，心转心之术会不会是大家都想要的忍术呢，相比超兽伪画什么的，（你说有这个忍术可以干嘛，真是不亚于海贼王的透明果实哦）！￼最后，井野不愧是木叶的花店女神，可是总感觉哪里有点不对，你们看到了吗？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10219', '0', '火影井野什么时候成了女神了', '山中井野，木叶忍者村的中忍，阿斯玛所领导的第十班的成员，队友是奈良鹿丸和秋道丁次，继承了山中一族的独特的密术，能让自身精神占据对方身体，同时，基于强烈的上进心，学成医疗忍术。第四次忍界大战中作为忍者联军第一部队(中距离联合军分队)的一员。忍界大战结束后若干年，与佐井结婚并生有一子山中亥人。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10220', '0', '火影忍者，十二忍成长的变化？井野算逆袭！', '随着历练的增多，木叶十二位少年忍者逐渐走向成熟，经过几年的战争，曾经的小忍者逐渐成为独当一面的精英，不仅实力变化很大，同时，他们的造型也同样发生了翻天覆地的改变！漩涡鸣人少年鸣人似乎因为吃不饱饭的缘故，营养有点跟不上，身高在班级中是最矮的一位，甚至连小樱的身高也远超过他，随着时间推移，鸣人逐渐长大，曾经矮小的身材与偏胖的身材一去不复返！最终成为忍界第一男神！小时候长大后宇智波佐助少年佐助因为家族被灭的缘故，从一个爱说爱笑的少年成为了沉默寡言的冰山，但因为颜值高，搭配着冷漠，佐助毫无疑问的成为忍者学校校草，在叛逃木叶后加入音隐村，身形变化较大，依旧是木叶颜值担当。小时候长大后春野樱虽然外表非常恬静，但是春野樱的内心确实是极度狂野的，少年时的她有着美貌的外表，陷入对佐助的爱中不能自拔，经过时间的沉淀，小樱也从圆脸变成了瓜子脸，但是似乎身材还是保持原有的样子~小时候长大后犬冢牙牙从小就是班级中的捣蛋鬼，似乎因为天性的原因，一直与鸣人不太对付，但随着接触增多，成为好友，犬冢牙与小时候最大的区别就是身高变化，面容依旧是原本的野性。小时候长大后日向雏田日向一族宗家大小姐，因为小时候被鸣人救过，一直对其芳心暗许，但是也因为害羞而没有主动表达，小时候的雏田有着可爱的短发与婴儿肥，长大后不仅脸型发生变化，而且身材更是大变样~小时候长大后油女志乃作为木叶村秘传家族，油女一族有着非常成熟的忍者体系，从不将真面目示人，因此总是带着眼镜等装饰物，长大后变化不小，以至于鸣人回来时都没认出来!小时候长大后洛克李为了能够成为忍者，洛克李的努力远超多数同学，也因此被迈特凯赏识，从前的洛克李是一个头发散乱的少年，长大后则受到老师的影响，不仅头发改变，身材也变化很大。小时候长大后日向宁次少年时代的宁次因为父亲的死，一直对世界怀有敌意，但是因为天赋异禀，小时候的宁次狂妄无比，眼睛中也常含轻蔑，在被鸣人开导后发生改变，在两年后成为木叶最优秀的上忍之一，标准男神。小时候长大后天天小时候的天天完全是一副呆萌的样子，家里是开武器店的，所以天天非常擅长使用各种刃具，经过多年的成长，天天也逐渐成为标准美女，但是却一直没有结婚。小时候长大后奈良鹿丸虽然有着超高的智商，但是鹿丸天生怕麻烦的性格使得其总是一副闲人的模样，但是经过父亲、老师的死亡，少年时期的模样逐渐褪去，留下一个蓄着胡子的忍村高层。小时候长大后山中井野因为从小家里经营花店的缘故，山中井野在少女时期就是班级中最夺目的一位，无论是穿着还是打扮都是一流的，长大后更是如此，不仅身材有了很大的变化， 长相也绝不逊色，成为木叶辣妈。小时候长大后秋道丁次秋道一族是木叶猪鹿蝶组合中“蝶”的代表，为了获得更高的破坏力，成员需要大量囤积能量，因此小时候的丁次完全是一个胖子，但在全力时能够消耗肉体换取力量，长大后虽然依旧肥胖，但是却是一副壮汉的造型。小时候长大后动漫先生公众号；dongmanxiansheng，长按复制哦，有料~', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10221', '0', '井野颜值逆袭，佐井简直太好命', '等了好久终于新更的火影，简直是给了大家一个福利啊，井野妹子的颜值，有点逆天，特别是侧颜太无敌了！井野不再是曾经的那个和小樱争风吃醋的单马尾的女孩了，从图中可以看到她魅力倍增，变身了妖艳大美女小时候的井野满满的都是大姐大气质，总是大大咧咧的她，但对待小樱却是无微不至的照顾花朵不绽放就失去意义了不是吗？说不定，那会是一朵… 比大波斯菊还要美丽的花喔长大后的井野，似乎给我们留下的印象一如既往的大大咧咧，一如既往的大嗓门额，话说这霸气的形象也是和小樱有的一拼啦但最近点开最新一集的火影，简直简直被井野的颜值惊到了好吗？这发型这眼神，感觉是瞬间圈粉啊完美的侧颜额，人变美惊讶都这么与众不同井野简直就是妖艳御姐啊，大王真的觉得时时刻刻微笑的佐井简直是好命到不行啊！逆袭成功想要更多动漫资讯就狠狠添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10222', '0', '小樱、雏田、井野，三美齐聚，哪个是你的最爱', '春野樱，木叶新一代医疗忍者，第五代火影千手纲手的弟子，与漩涡鸣人、宇智波佐助隶属于旗木卡卡西领导的第七班。具有良好的查克拉控制能力与清晰的脑力，擅长怪力与医疗忍术。经过自己艰苦卓绝的努力奋斗，最终成长为一个贯彻自己座右铭“勇气”的优秀忍者。最后与宇智波佐助在一起。体术：怪力、痛天脚、樱花冲忍术：百豪之印、忍法创造再生 百豪之术封印术：阴封印 解医疗忍术：基本医疗忍术、医疗术·掌仙术、医疗术·细患抽出术小樱语录：1.我每次都受到其他人的保护，好不甘心哦……既然如此，这次……必须有我来保护我所珍惜的人们了……2.这一次，这两个人都由我来拯救!3.对不起，鸣人，要让你稍微等我一阵了，下一次，我一定不会拖后腿的。4.我从你们身上学到了一些东西……我也想变得跟你们一样……请你们……这次就好好的看着……我的背影吧!5.我一直犯错…一直失败…我再也不想犯错……再也不想失败6.我一定要，一定要抓住你！就算手脚断掉了也好，身中剧毒无法动弹也好，也一定要抓住你！日向雏田日向雏田,火之国木叶隐村的忍者，木叶名门日向一族宗家的嫡长女，大筒木羽村的后代之一，拥有纯度极高的白眼，被舍人和大筒木一族的亡魂们称为“白眼的公主”。从小喜欢主人公漩涡鸣人。原本是个性格柔弱的女孩，但是在鸣人的影响下逐渐变得坚强，并渐渐成长为一名优秀的女忍者。故事最后成为鸣人的妻子，并育有一子一女：漩涡博人、漩涡向日葵。血继限界白眼--视角接近360 度的、极适合侦查的血继限界，衍生招式柔拳白眼强化--能看到10公里左右的查克拉流动变化，曾看到写轮眼发动的天照日向家族的家传绝学-柔拳. 八卦空掌、八卦空壁掌、　点穴、　柔步双狮拳、八卦掌·回天、柔拳法·八卦三十二掌、　柔拳法·八卦六十四掌、柔拳法·守护八卦六十四掌雏田语录：1.在喜欢的人面前，我可不能……丢脸啊……2.笔直向前，说到做到，因为这就是我的忍道。3.因为我，喜欢鸣人君…………鸣人君没事真是太好了。4.我要成为像爸爸一样强大，像妈妈一样温柔的出色忍者。5.永不放弃，这就是我的忍道。有话就直说，这也是我的忍道。6.虽然你一直失败……但……但是在我眼里看来……我觉得你是个拥有极高荣耀的失败者呢7.我……我只要看着你，就会觉得心里受到冲击……8.人不是十全十美的……所以……一个人因为遭遇失败，才会拥有从那里再站起来的强悍……我觉得那种强悍就是真正的强悍……所以我觉得……你是个非常强悍的人……13.这场战争是守护鸣人君的战争！所以我绝对不会向敌人示弱的！山中井野山中井野,，木叶忍者村的女忍者，特征是浅黄长发，让刘海遮着右眼，带上一个红色发夹，身穿一件露腰的紫衣。与鸣人他们是忍者学校同期毕业的学员，后来成为阿斯玛所领导的第十班的成员，队友是奈良鹿丸和秋道丁次，继承了山中一族的独特的密术，能让自身精神占据对方身体，控制对方内心。同时，基于强烈的上进心，学成医疗忍术。并和小樱一样成为了医疗忍者。第四次忍界大战中作为忍者联军第五部队（战斗特别部队）的一员。忍界大战结束后若干年，与佐井结婚并生有一子山中井阵。忍术：忍花·鸟兜,心转身之术，心转分身之术，心乱身之术,16连爆击和医疗忍术（掌仙术）同组队友：奈良鹿丸，秋道丁次（猪鹿蝶）井野语录：1.“我觉得如果你在还是个花蕾的状况下枯萎……那就真的太可惜了!一朵花不绽放就没有意义啦……因为，搞不好那是朵……比大波斯菊更漂亮的花喔。2.高兴的时候爱怎么哭都没有关系啦!3.你在说什么啊。其实说起来，小樱你还不算是一朵花，还是花蕾。4.你终于也盛开了，成为一朵漂亮的鲜花。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10223', '0', '火影忍者里的妻管严，鹿丸和丁次一对难兄难弟！', '忍者的世界，男女都是平等的，男性忍者厉害点也就罢了，女忍要是厉害起来，那可是相当的可怕的！波风水门，四代火影，有名的暖男，小时候就跟妻子结识相恋，漩涡玖辛奈的外号红色辣椒，水门跟妻子是相当的恩爱，当时要说谁更强势一点的话，水门肯定是要让着妻子的。丁次，娶的妻子是卡鲁伊，卡鲁伊的强势和暴躁是鸣人亲身体会过的，一言不合就让鸣人挨了揍，鸣人脸肿的老高，而且眼睛也用纱布包起来，揍鸣人揍累了都没有想歇歇的意思。鹿丸的妻子也是很强势，手鞠婚前不止一次的对鹿丸动手，鹿丸为鸣人大婚准备礼物时闹了个误会，两人差点去宾馆了，手鞠明白那是误会后，直接拿镰鼬来闪鹿丸，婚后生活有点家暴什么的，也是正常的吧！可怜的丁次鹿丸兄弟，找的妻子都是那么的强势，一对难兄难弟！鹿丸的老爹，木叶的天才级军师，在村子里身份不低，但是到了家里就老是受妻子的唠叨，鹿丸也挨老妈的训斥，老爹还一套一套的教育鹿丸，最后临死之前还叮嘱儿子自己藏起来的东西不要鹿丸的母亲看到，可想而知，鹿久是有多怕自己的老婆啊。原创“浅言情深”，每日动漫资讯畅谈，欢迎留言，转发，请有素质的抄袭，谢谢。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10224', '0', '丁墨之《他与月光为邻》，三次见面足以！', '麻辣君又来和大家见面了，很抱歉已经好几天没和大家说好了，前两天感冒，今天才稍稍好了一旦。现在我们就一起聊聊丁大大的一部小说，想当然大家对于他的小说已经不再陌生，今天的这不听说又要改变电视剧了，就不知道到底会怎样了。简介：第一次见面，她非要赠送给他一枚糕点。尽管他最讨厌甜食，还是努力吃掉了； 第二次，她因为害怕伸手抱了他。他脸色微红：“这位小姐，请先松手。” 第三次，她不小心亲了他，他一副见了鬼的表情，她却说：“意外而已，你不必介怀。” 三次见面，却让二人的交集不再那么简单！《他与月光为邻》的九句经典句子！你看我长这么大，从来没谈过恋爱。不是因为我要求高，也不是因为没有人追求。而是从小到大，我的想法，就是要找一个永远不会离开我的男人。他能理解我所理解的东西，他与我有相同的追求。他心里没有别人，一心一意只对我好。我也只对他好。可是如果作为爱人，我不知道有一天，你是否就会离开我。是否我也要……一个人孤独地度过余生。我只能思念你，却不能跟你白头到老。我曾经以为，两个人只有白头到老，只有相伴一生，才是圆满的爱情。我也以为，只有走过经年累月，水到渠成，才谈得上刻骨铭心的爱。可是现在遇到他，那么好的他，我才明白，真正的爱情，哪怕只有一年一月、一分一秒，这辈子，都忘不了了。如果上天赐予了你与众不同的东西，那就接受它，并且保护它。人生所有的事，从来就没有什么真正大不了的。也许是星辰注定了我们的相遇。但愿……能与你再次相逢。他们实际上，是为了侵略和攫取而生，他们的文明苍白而可悲。星流不会忠于这样的文明，也不会沉沦于这样虚妄的复兴梦。我见过恒星的诞生，也目睹过行星崩塌于黑暗中。我航行了数千光年，才遇见了这一个心爱的女人。她会是我终身所爱，比生命更重要，与信仰同等珍贵。如果这一次未来依然不可逆转，我不可能陪你再走下去。我又怎么忍心让你掏出一颗真心然后在这个寂寞如梦的宇宙里，孤独终老?如果不是曜日坠落，他大概会永生被囚禁于地下。再也见不到日月星辰，也不会遇见她，就这样结束一生。你看完的句子还有什么是比较好的呢？一起来分享分享！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10225', '0', '火影：还记得猿飞阿斯玛临死前的三个心愿吗？可惜都没完成……', '大家好，喵喵又跟大家见面啦，今天我们来说说火影里阿斯玛临死前的三个心愿，其实阿斯玛是大家都非常喜欢的人物，因为他的性格非常随和，并且拥有非常罕见的风属性查克拉，他是一名实力非常强大的近战忍者，作为三代火影的儿子，大家可从来没在他身上看到过一丝傲慢，阿斯玛是猪鹿蝶三人组：鹿丸、井野、丁次的老师，可性格随和的他从来没有老师的架子，而且阿斯玛整天请学生们吃烤肉，和学生们打成一片。后来在跟晓的不死二人组战斗时，因为对敌人的情报不明，最后被飞段杀死……别看阿斯玛性格随和，其实少年时代可是个不良少年，曾经与父亲三代火影发生冲突而离开木叶外出闯荡，后成为身为守护忍十二士担负起保卫大名的任务，而且整天不离嘴的香烟更是让大家对这位酷酷的大叔心生好感。而阿斯玛和夕日红的恋情更是成就了一段佳话，女儿猿飞未来在博人传里已经成为一名中忍了。阿斯玛后来在与晓成员飞段战斗中受了重伤，在他的弥留之际，曾经对弟子们交代下了三个心愿，大家还记得吗？希望井野可以赢过小樱第一个心愿是对井野说的，他说井野虽然逞强好胜，可是却是个心细、会照顾人的好孩子，鹿丸和丁次都非常粗心，希望井野以后可以照顾好他们，另外阿斯玛希望井野可以赢过小樱，无论是忍术还是恋爱。不过我们从后来的事知道，井野无论是忍术还是恋爱，都输给小樱了啊……希望丁次可以减减肥第二个心愿是对丁次说的，他说丁次是个会替伙伴着想的温柔男子，所以丁次一定会成为一个强大的忍者，不过宁次一定要对自己有信心才行。并且希望丁次可以减减肥。不过后来的丁次虽然成为了一名强大的忍者，可是减肥这件事他没有做到啊。希望鹿丸成为火影他对鹿丸说鹿丸的头脑非常好，身为忍者的资质也非常强，有成为火影的能力，可是鹿丸一定会怕麻烦而不愿意当吧。后来我们知道鸣人成为了七代火影，鹿丸想成为火影还得努力啊。阿斯玛死的时候小编哭了……喜欢喵喵的文章，别忘了点赞、收藏和分享哦！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10226', '0', '猿飞阿斯玛，做为守护忍十二士的真正实力', '猿飞阿斯玛的实力，一直都不是特别被看好，甚至有个别人认为他实力不过中忍！这种态度大概是因为阿斯玛是猿飞日斩的儿子，认为他成为上忍是走关系的，具体走不走关系，靠说没有用，我们还是用事实来说明吧！阿斯玛作为一个【风】属性查克拉的忍者，对于性质变化和形态变化的掌控能力是十分纯熟的，所以卡卡西才会允许他给鸣人指导，他不但能将查克拉融合进入刀中（当然，刀必须是特制的），而且还能通过优秀的控制力，大大提高刀刃的锋利度。在给鸣人的示范中，阿斯玛的查克拉刀不但贯穿了树木，还深深刺入了后面的岩石（其实据他自己说，认真的话连岩石都能贯穿），就凭这种控制能力，就是他成为上忍的一个标准。除了投掷出这种极其锋利的忍刀，阿斯玛的忍术——翠岚烈风甚至还可以对这种忍刀进行操控！这是查克拉的操控方面，阿斯玛已经达到甚至超越了上忍应有的标准。这是地陆传授给阿斯玛的奥义·来迎千手杀，这一招我认为是一个短距离的强力忍术，阿斯玛使用这一招轻易弹开飞段的血腥三月镰，并且将飞段迫退，可见其威力绝对不弱，并且这个术的一大优点是不用自己冲上去攻击，可以操控多个手掌形状的具象化查克拉对敌人造成伤害，不过从飞段躲开这招来看，范围不广。这一招是阿斯玛的范围忍术————灰积烧，大概为5-10米的范围，从图中可以看出，这个纯属于中距离攻击忍术，范围顶多和豪火球术一个级别。给予对手三度烧伤，虽然不能致命，不过可以有效的削弱对手。其实阿斯玛的忍术，范围都不算很广，比如风尘之术，狼牙伐锥，飞燕·空穿，这些招数都很强大，风尘之术轻松灭掉角都一个心脏。但是它只是向前方射出一道直线，属于单体攻击招数。因此，阿斯玛的忍术特点我认为是————威力大，容易躲。综合对查克拉的完美控制，以及超强攻击力忍术，阿斯玛的实力可以说已经超过一般上忍了，接下来，我们说说他的弱点。1.速度不够快；对于一般的中忍或特别上忍来说，他的速度已经足够了，但是对于高手，还不够。这一点在对战飞段时得到了体现。阿斯玛是被飞段这么一招普通攻击命中的，我仔细观察了一下，飞段这一招出的非但不算迅疾，反而可以说是缓慢，飞段自己也承认过自己是整个【晓】里攻击速度最低下的。阿斯玛在手持两把查克拉刀的情况下，防不住也躲不开这一招，可以看出他的速度真心不算快。之前的作战，阿斯玛的身手也明显比卡卡西和阿凯这些人迟缓。2.忍术范围不广，难以命中。这个之前已经说过了，灰积烧最广不过10米，查克拉刀范围更是小的可怜，风尘之术直接就一道线射出去............阿斯玛的查克拉属性太少，以至于他掌握的忍术太少，范围也很小，如果他能再多拥有一~二种查克拉，飞段和角都就不会赢得那么轻松了。这也是阿斯玛的一大硬伤，使用忍术的时候就是心有余而力不足。（本文由大王整理于网络,向原作者致敬）想要更多动漫资讯就狠狠添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10227', '0', '玉之守护者——猿飞阿斯玛', '木叶村每年都会诞生并孕育出许多忍者，回忆那依稀的笑容传递着你对三个弟子的厚望与挚爱第十班，秋道丁次，奈良鹿丸，山中井野，阿斯玛老师，永远留存在我们心中，挥之不去……分班第十班，命运的相会，猿飞一族、奈良一族、秋道一族、山中一族……猪鹿蝶组合的宿命，必定要与你相遇，相识，而你，欣然接受，因为这是族与族之间的使命，也是你必将完成的使命，传递你的意志，火之意志……推荐“阿斯玛所辖第十班，山中井野、奈良鹿丸、秋道丁次，以上三名以我猿飞阿斯玛之名参加中忍考试”是你，秉承着那份自信，第一次将猪鹿蝶的组合送入中忍考试，这份自信，不仅仅是对他们的信任，更是对他们的期待，对他们的爱……可爱“虽然输了，还是带他（丁次）去吃烤肉吧，哈哈！”是你，鼓舞着缺乏自信的丁次上场，即便是他输了你也愿意掏空钱包，让学生大饱口福，那份包容，那份可爱，是师，亦友……伯乐“天才就是天才，那家伙是个IQ200以上的超级天才”是你，发现了鹿丸的才能，那并不起眼的拼图游戏，实际上是测试他IQ的工具。千里马常有，伯乐不常有。你，正是那伯乐，更是一名好老师……热血是你，曾经作为守护忍十二士的存在……拥有着年轻时的执着，守护着心目中的双“玉”。“为何你仍旧不认可我？！”，得胜归来的人这样对着自己的父亲说道。原来我们的阿斯玛老师，也迷茫过，也那样的热血过……浪漫适时的送上花种，而不是直接献上大捧的鲜花，“感兴趣的话就试种一下吧，不需要太多照顾哦”，让女孩儿观赏到辛勤的花朵，这真的是一种别样的浪漫呢……那种快乐，是怎样的呢?哀思“很熟悉对吧，地陆用出来的时候会更厉害呢！”那沉沉的思念，化作了“仙族之才”的力量，无情的打在飞段身上，那是对战友与兄弟——地陆的哀思，更是一种力量的传承……更是一种信念的寄托……面对无法战胜的不死敌人，你选择舍生取义，独自一人冲在最前方……不是作为队长，不是作为老师，而是作为火之意志的继承者。曾经的守护忍十二士啊，为了同伴，为了家人，为了国家，献上了自己宝贵的生命……人到死前才会明白一些事情吧。曾经年少的懵懂，在此刻方才醒悟。“我现在终于能够明白到三代火影当时的所作所为了”，曾经守护的“玉”，原来一直是错误的……不是大名，亦不是火影，而是……村子里年轻的孩子们，那些下一代。“井野，你是个会照顾人的女孩，鹿丸和丁次就交给你了”，“不要输给小樱哦，忍术，还有爱情……”，“丁次，你是一个体贴同伴的孩子，所以你变成一个强大的忍者”，“要对自己有信心！”，“还有，你也该想办法减肥了……”，“如果把木叶比作将棋的话，鹿丸，你就是桂马。”你庆幸自己还有力气完成最后的嘱托，自己对“玉”的信念、对“玉”的托付，在那一刻完成。“本打算彻底戒烟的，这一次再次为我点燃吧”，火之意志……“我的火之意志就拜托你了，鹿丸……”每次想到这句就不禁泪流满面！超爱二次元逗比青年、资深腐女不想错过任何一条你想看的动漫资讯?就赶快添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10228', '0', '鹿丸秘传中，猿飞阿斯玛的遗腹子初长成，与鹿丸关系最近！', '在鹿丸秘传中，开启了最新的一段人物感情。每个人已经长大，有着自己的思维和生活方式。鹿丸也不再是那个整天只会嫌烦的小男孩，而是成为了有担当有责任的大男人，也成为了火影的得力助手。但无论怎么改变鹿丸始终坚持这老师阿斯玛的遗愿，为木叶守护着，为木叶的孩子们而守护着。这不当即将鹿丸前往默之国探寻失踪忍者的真实意图的时候，来到了老师阿斯玛的墓前，进行祈祷。而恰巧也遇到了红和猿飞未来的前来看望自己的亲人，而猿飞未来看到鹿丸哥哥的那一瞬间显得非常的高兴。这也间接说明了，鹿丸是真的在用心照顾阿斯玛的妻子和孩子，而曾经的那个刚出生不久的猿飞未来，也已经长大，已经到了鹿丸曾经懒散的那个年纪。而猿飞未来这一代也是代表着木叶新的种子，虽然生活在了和平年代，但也要居安思危，加强锻炼己身！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10229', '0', '杂谈：不忠世俗，无负初心——猿飞阿斯玛！', '作者：桑子在我的印象里，阿斯玛就是一个胡子拉碴、双手插兜叼着根烟的懒散大叔。他没有什么酷炫帅气的招式，也没有英俊出众的外表，放在同届的上忍里看，简直就完完全全像个路人，容易被忽视，以至于让我曾误以为岸本设计这个角色就是为了凑数的。 而这个观点的转变，是在空的事件发生后。 之前写鼬的时候我说过这样一句话：一个人的强大与否，在于他是否拥有一颗坚定的心。而这句话，同样也能适用于阿斯玛。 即使没有什么能吸引人眼球的华丽招式，也没有太多声名远扬的雄伟事迹，阿斯玛依旧是一个当之无愧的强者——因为他拥有一颗充满爱与大义并甘愿为之付出所有的火热的心。 【和马他……他是贯彻了自己所信仰的大义死去的。】 他温柔善良，为了不打破空对自己父亲伟岸形象的崇拜，宁愿含糊其辞地掩盖和马的罪过。 【即使无法强大，但仍可以传承下去。】 他刚正坚定，就算无法在短期内使火之国变得无比强大，也拼尽全力守护每一个后辈，让火之意志能够延续长存。 最好的幸福就是平凡。 有空闲的时候就去找鹿丸下棋，但每次都是他被“将军”输棋，无奈之中只好叹口气，看一看庭院里嬉戏叫唤的鸟儿，一派宁静之景，倒也算是乐在其中了。 任务出发前他会去心爱的女人窗前望一望，即使透过厚厚的玻璃只能模糊地看见一个人影，他也是心满意足地转身离去。窗外花坛种的是他之前送给红的罂粟花，馥郁的芳香顺着风的脚步钻进他的鼻孔，令他心旷神怡。 按照惯例，任务结束后第十班都会去吃烤肉，但倒霉的是大胃王丁次不但能把他的钱包吃瘪，有时甚至还要他去向店里赊账。明明是高高兴兴地走进店里，出来的时候却一脸欲哭无泪。可到了下次饭局，他还是心甘情愿地掏出钱包结账，没办法，谁让他是老师呢？ 那些最普通不过的场景，才是最值得人珍惜与怀念的。 传承的意义，是为了永不止息。 阿斯玛深刻地理解到自己作为“前辈”的责任，将火之意志用自己的方式传递给后辈，并为了孩子们的未来赴汤蹈火，将生的希望留给年轻人。 曾几何时，他也只是个年轻气盛的小鬼，不愿听从父亲的教诲，甚至一气之下离开村子，只为了见识所谓的“更广阔的天地”。那时的他不谙世事、心浮气躁，一番经历后又回到木叶，看见这小小村子的繁荣景象，他才突然明白，真正的“广阔天地”不在于你身处何方，而是决定于你的心有多大。一群群嬉笑打闹的孩子从他身边走过，他感受到的是前所未有的幸福感。 玉，指的是承载着希望的孩子们。 在他弥留之际，他凑近了鹿丸的耳朵，说出了这个他耗尽一生才找寻到的答案。他的嘴里还残留着些许烟味，地上剩了半截的烟头还未熄灭。可是他，木叶上忍猿飞阿斯玛，已经离去。 这一次，终于可以彻底戒烟了… 遗憾的是，他没能亲口和牵挂着的女人道别，也没能见见他们当时尚未出世的可爱乖巧的孩子。 从乖张叛逆的少年到稳重成熟的大叔，时间与经历的积淀让他变得洒脱随性，愿意相信，愿意托付，最终以最英勇的方式献出生命，成为万人祭奠的英雄。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10230', '0', '猿飞阿斯玛决斗场攻击', '文/Might波 作为木叶猪鹿蝶组合的老师阿斯玛可以说得上是十二小强老师当中最负责任的了，让三个成员尽自己的个性自由发展，对于学生的培养也采取循循善诱的模式。例如秋道丁次对于食物能够爆发出超强的能力，阿斯玛则根据这点不断的引导丁次的力量。只可惜在后面对上不死二人组时，牺牲了。不得不让人为之惋惜。在火影忍者手游中，阿斯玛的力量和迈特凯以及卡卡西，他们都属于同一级别，都是A级忍者，相比于迈特凯的全部体术攻击和要高V才能招募的旗木卡卡西来说。阿斯玛的战斗力则来得更为灵活多变，体术加上和自己本身的风遁属性相结合，下面让我们来看看吧！普通技能（在敌人众多时，很有效，可开辟一条路径用以逃跑或进攻boss）普通技能（击杀技能，可代替普攻）奥义技能（该技能判定范围挺广，同时还能防止敌人逃脱）可以这样说，在火影忍者手游中，在宇智波鼬、迪达拉和小南他们出现之前。猿飞阿斯玛和迈特凯他们两人的获得难度较低，同时上手操作也比较容易，伤害较高不容易落空，是早期手游中常运用的A忍之一。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10231', '0', '火影中的各对情侣，有你们喜欢的吗？', '大同木辉夜姬也是被征过的！那时的她还很善良！纲手姬和加藤断！加藤二字让我想到某人！夕日红和猿飞阿斯玛！不过后来阿斯玛挂了！弥彦和小南！弥彦要是没死，忍界会不同！佐助和小樱！讲真表示不喜欢二柱子！月光疾风和卯月夕颜！佐井和井野！两口井之家！鹿丸和手鞠一家！鸣雏！我觉得最般配的组合，只能说有情人终成眷属！鸣人一家,好羡慕鸣人啊，雏田又漂亮又贤惠！自来也和纲手！不知道自来也看着那里呢？哈哈！佐助和名人，及其各自家人！再不斩和白！只是不知道白的真实性别，我也是醉了！水门和玖辛奈！宁次和天天！可怜的宁次，成了十二小强中唯一挂掉的！两个小屁孩，不知道将来是不是会在一起！千手柱间和漩涡水户！挺温馨的！丁次和黑妞媳妇！丁次和黑妞，佐樱，鸣雏，井野和佐井，鹿丸和手鞠！宇智波斑疑似弟控，所以留下镇楼！看了那么多对情侣，对我这单身狗造成了十万暴击！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10232', '0', '果然还得看血统！猿飞阿斯玛并没有教导人的才能', '猿飞阿斯玛是《火影忍者》火之国木叶忍者村的上忍，隶属于第十班（奈良鹿丸、山中井野、秋道丁次），曾向鸣人介绍过风遁的性质。近日，猿飞阿斯玛的一个23岁的下忍弟子引起了日宅的热议，让我们一起来看看吧。1：アニはつの名無しさん 2017年09月15日23岁还是个下忍，猿飞阿斯玛的育成能力简直MAX。4：アニはつの名無しさん 2017年09月15日不知为何，23岁的下忍给人一种十分现实的感觉。5：アニはつの名無しさん 2017年09月15日也许是更注重自己班级的培养吧，毕竟那些都是关系户。67：アニはつの名無しさん 2017年09月15日感觉像在嘲笑多次落榜的自己一样，真是太悲伤了。7：アニはつの名無しさん 2017年09月15日24岁，职业是学生，在上XX的学校。10：アニはつの名無しさん 2017年09月15日木叶村的上中下忍比例到底是怎么样的？14：アニはつの名無しさん 2017年09月15日回复10楼：下忍5、中忍3、上忍1、暗部1的比例吧？29：アニはつの名無しさん 2017年09月15日回复14楼：暗部是不是有点多了？38：アニはつの名無しさん 2017年09月15日不如说暗部才更像是忍者吧。11：アニはつの名無しさん 2017年09月15日23岁还是下忍到底如何才能在这个世界活下去啊。12：アニはつの名無しさん 2017年09月15日这货连额带都没有戴。15：アニはつの名無しさん 2017年09月15日话说回来鸣人那个时代第一次中忍考试全部都合格了简直是逆天，凡人应该连笔试和森林都无法突破。16：アニはつの名無しさん 2017年09月15日万年下忍大多数都引退去卖拉面了吧，中忍考试的时候应该有一大堆大叔。17：アニはつの名無しさん 2017年09月15日学历只有高中毕业的感觉。24：アニはつの名無しさん 2017年09月15日回复17楼：中忍就像是部长和课长，上忍就是议员干部候补的感觉呢。19：アニはつの名無しさん 2017年09月15日我记得小樱的父亲好像也就是个下忍来着。78：アニはつの名無しさん 2017年09月15日能够成为忍者的都是人才了吧，中忍更是十里挑一。22：アニはつの名無しさん 2017年09月15日成为下忍就是十个人里的一个人了，然后下忍的十分之一成为中忍，再十分之一成为上忍。27：アニはつの名無しさん 2017年09月15日就算从学院毕业，但是无法通过之后的演习的话就会回到学院吧？落榜生的感觉。31：アニはつの名無しさん 2017年09月15日凯的父亲也是被嘲笑的万年下忍。39：アニはつの名無しさん 2017年09月15日能够成为上忍的大多是靠运气吧。51：アニはつの名無しさん 2017年09月15日那个世界除了“某某一族”这种有名号的以外都没有才能吧。56：アニはつの名無しさん 2017年09月15日这个年纪还要和十多岁的小孩一天到晚接一些找猫咪的委托，想想真是悲哀。63：アニはつの名無しさん 2017年09月15日还以为有什么努力的元素呢，结果说到底还是血脉。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10233', '0', '决斗场实战解析，强势来袭猿飞阿斯玛', '　　忍者简介：猿飞阿斯玛的一位拥有近战和远程消耗能力的一位忍者，普通攻击也是非常帅气的，大招也是利用手里剑进行攻击的，非常之帅。　　【忍术：二刀流·近身斩】　　听到了这个名字，想必各位小伙伴们就应该知道这个忍术是近战的还是远战的了。猿飞阿斯玛释放此忍术时会利用自己的查克拉刀向前放方发出连斩，第三次连斩的时候可以将敌方忍者击飞，并且此忍术还可以通过方向键的调整来断定连斩的方向(只可以前后调整)，可以用来吓唬对手哦!　　【忍术：火遁·灰炽热】　　猿飞阿斯玛使用“火遁·灰炽热”向前方吐出一团高温烟灰，烟灰落地后会将敌方忍者控在此烟灰里，短暂时间后并会产生爆炸效果和高额的伤害，同时也会将敌方忍者击飞，这个忍术是猿飞阿斯玛的主要输出忍术，因为猿飞阿斯玛的“二刀流·近身斩”伤害不高，所以这个忍术成为了猿飞阿斯玛的核心忍术。　　【忍术：查克拉刀·真一文字】　　这个忍术是猿飞阿斯玛的大招，猿飞阿斯玛使用自己的查克拉刀对敌方忍者进行多次连斩，并且最后一下连斩可以改变方向，摇动方向键就可以改变这个忍术的方向了，各位小伙伴们可以用这个忍术来起手(在敌方忍者2没有替身术的情况下)再接“火遁·灰炽热”其他的忍术是接不上的。　　秘卷推荐【忍术：风遁·风杀阵】　　“风遁·风杀阵”向前方释放飓风，这股飓风会自动向前方只直线移动，但敌方忍者在这股飓风范围内时，会被这股飓风带走。释放这个忍术的时候可以选择开局就使用，但是各位小伙伴们要预判好敌方忍者的位置再释放、当敌方忍者向走来时，可以释放此忍术，利用飓风将敌方忍者强行带走(霸体效果也是会被带走的)。　　【实战解析】　　猿飞阿斯玛可以打先手可以打后手，打先手的猿飞阿斯玛要使用“火遁·灰炽热”来起手，这个忍术命中后，不要那么着急用“二刀流·近身斩”来接着攻击，这时候各位小伙伴们可以选择直接上前用普通攻击来攻击，敌方忍者这时候肯定会使用替身术，替身术后被敌方忍者攻击到了，大家可以选择秒替身术再来反打他，不要拖着了，必须要迅速打伤害，反应快的小伙伴们可以在敌方忍者使用替身术后立马反方向放个通灵，再将敌方忍者击杀。打后手的猿飞阿斯玛不要那么着急使用忍术来gank敌方忍者，先利用自己高超的走位技术躲掉敌方忍者的忍术，大家在利用风杀阵起手，跟在后面用普通攻击来攻击，骗出替身术的他没有忍术只有普通攻击，所以肯定选择逃跑，这时候大家可以反向一个“火遁·灰炽热”让敌方忍者想跑也不跑不了，没有忍术和替身术的忍者也只有被挨打了，可以迅速打伤害了这时候，然后将其击杀。　　总结：希望这篇猿飞阿斯玛攻略对各位小伙伴有所帮助。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10234', '0', '《火影忍者》中的年轻的寡妇，给你一个不为人知的夕日红！', '为什么会写《火影忍者》中的一个戏份不多不起眼的夕日红呢？原因很简单，因为她和我们大多数人一样，渺小得被人淡忘却又坚强得让人感动。我们不妨来看看夕日红的一生。首先是小时候，夕日红和其他小伙伴一样，有着一个天真无邪的童年和和平的环境。青年夕日红出落成木叶的美少女，红色的眼睛，完美的身材，透露着活力，与阿斯玛坠入爱河。命运的转折发生在阿斯玛被飞段杀死之后，此时的夕日红怀有身孕，却只能在阿斯玛的墓碑前感伤，让未出世的孩子一开始就失去了父亲。在怀孕期间，佩恩袭击木叶，在自己受到攻击时，能保护自己的阿斯玛早已死去，如果不是鹿丸，恐怕连个照顾自己的人都没有。在孩子出生之后，夕日红再没有了当时的生机，容颜也憔悴了许多，再也无法从脸上扑捉到笑容，真的是应了那句话“人如其名”，夕日红了！但是她并没有放弃生活，还是坚强地和未来带着阿斯玛的思念生活着，这不就是真正的火之意志吗？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10235', '0', '阿斯玛送给夕日红礼物，内涵实在太隐晦', '火影忍者第298集，片尾曲之后的小剧场哦，求关注求关注求关注~阿斯玛好像是烟不离手呢，连约会的时候都在抽烟。夕日红曾经说过房间太空很煞风景，结果阿斯玛就记下来了，然后送了一小瓶罂粟的种子给夕日红。夕日红很惊讶，说阿斯玛的心意她很高兴，不过肯定会枯死的。阿斯玛说这个并不需要特殊的照顾，夕日红刚想说什么，阿斯玛就急急忙忙走了，留下了一脸茫然的红。结果红还是很在乎阿斯玛送的礼物，虽然不会种罂粟，但是却在家里仔细翻书打算种好它。看了书红突然开心起来，原来罂粟的话语是安慰和体贴。阿斯玛也太沉得住气了，送了礼物都不说明意义，这么隐晦的内涵要不是红翻书还真不知道。这也说明两个人的内心互相珍惜吧~', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10236', '0', '火影体质最差的五个人，夕日红竟然比重病的鼬更差！', '官方公式书曾经给出过每个忍者的数据，其中“精力”这个数值代表着每个人的查克拉量，同时也代表着这个人的身体状况，只有身体好的人才有足够的基础储存查克拉。在已经成长完毕的忍者中，下面五个人是“精力”数值最低的。1.鼬精力数值满分10分，鼬只有5分，这个数值已经相当低了。要知道鼬的总能力值是排名第一的！其他项目都是九分十分左右，只有精力数值拖了后腿。官方给出这样的数值，是考虑到了鼬的身体状况，公式书发布的时候鼬已经是病入膏肓了，他在跟佐助的战斗中因为身体原因所有没能躲过一些攻击，最后也是重病发作身亡的。2.千代千代婆婆的精力数值是4分（顺便一提，下面提到的所有人也都是4分），比鼬只低了1分，不过千代婆婆的身体很好，也很长寿，她的健康状况是没什么问题的。只不过千代婆婆毕竟年纪大了，战斗起来的体力跟年轻时候不能比。我们其实可以把4分理解成岸本所认为的“一个健康的忍者上了年纪后所应该具备的体力数值”，这样一对比，鼬年纪轻轻只有5分，就很尴尬了。3.月光疾风更尴尬的是月光疾风，这个总是顶着黑眼圈咳嗽的木叶忍者，虽然号称是学会了三日月之舞的天才，体质却跟千代是同一个水平。怪不得每次看到月光疾风都有一种他随时要病危的感觉，那不是错觉，官方给他的设定就是这样的设定，月光疾风的寿命注定不会很长。4.右近右近是左近的兄弟，两个人因为血继限界——双魔之攻的存在，可以合体成为一个人。在公式书中，左近的精力数值是8分，右近却只有4分，这就很有意思。平时两兄弟合体后，都是以左近的身份示人，所以我们是不是可以这样理解：左近是控制身体的那一个，右近只是寄生的那一个，由于右近长期寄生所以他的体质比较弱。5.夕日红上面的所有人体质弱都有自己的理由，有人是因为重病、有人因为年老、有人因为血继限界，可夕日红的体质跟他们一样，就很难理解。也许有人会认为是怀孕的缘故，但官方给出的夕日红怀孕前怀孕后的精力值都是4分，所以跟怀孕没关系。真看不出来为什么夕日红的体质会这么差……', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10237', '0', '火影忍者：木叶的四大寡妇仅夕日红有孩子其他人只能孤独终老', '有战争就有会伤亡，在火影忍者中，光是忍者大战就发生了四次，更别提那些大大小小的间谍之战了。其中木叶忍者村，作为五大国之首，一直被其他人视为眼中钉，总想着削弱它的实力。所以纵观整个忍界，木叶忍者村遭受的袭击是最多的。其实光是灭顶之灾就有三次，大蛇丸袭击、九尾袭击、佩恩袭击，都差点将木叶毁于一旦。虽然最后木叶都化险为夷，但死亡惨重，无数的家庭支离破碎。今天我们就来讲讲木叶最典型的四位寡妇，只有夕日红有孩子，其他人只能孤独终老，实在有些可怜。夕日红猿飞一族少族长家的遗孀，与阿斯玛是同期忍者，也算是青梅竹马，在阿斯玛那辈忍者中，卡卡西和凯皇都是老光棍，而阿斯玛却娶得了一位美娇妻。只是好景不长，阿斯玛在执行任务中被飞段莫名的杀死，夕日红从此沦为寡妇。好在他们之间已经有了孩子，夕日红也放弃自己危险的忍者职业，不再担任第八班的导师，一心养育猿飞未来，如今猿飞未来已经成为了七代火影的贴身护卫，又有木叶丸和鹿丸相互扶持，夕日红的后半生也算过的还可以。纲手同为木叶三忍，年过半百，连大蛇丸都有自己的孩子了。而纲手却一直孤苦伶仃，她与照美冥不同，虽然同为五影之一，照美冥是无暇谈恋爱，而纲手却是先后失去了两位恋人，加藤断和自来也的离世，让她不敢再敞开心扉，从而退居幕后，过起了简单的日子。但因为是一个人，显得有点落寞。卯月夕颜卡卡西在火影直隶暗部时的队友，年轻貌美，注重情感，她的木叶流剑术是难得的暗杀技能，本来遇到月光疾风后，生活不再是那么枯燥，而变得有意义，没事赏赏花，也是一种情调。但没想到因为砂隐村的阴谋，在中忍考试时被马基杀死，在秽土转生后，虽然夕颜释怀了自己，但却从暗部调到了医疗部，用繁重的工作来麻痹自己对爱人的思念。天天木叶十二小强中，唯一一个退出忍界的忍者，在第三班时，面对青春活力的凯老师和小李，天天只有吐糟。只有日向宁次进入了她的心，本以为第四次忍者大战之后，他们也会像鹿丸和手鞠一样，确定爱情关系，从而结婚生子。没想到宁次的死，对她打击很大，自己本来还很年轻，却甘心守活寡，开个忍具店孤独的度过余生。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10238', '0', '浅谈止水须佐能乎的能力', '宇智波止水号称宇智波的天才忍者，有瞬身止水的美誉，双眼都可以开最强幻术别天神，在躲避根的追兵时，曾单眼开须佐能乎秒了所有追兵，那么止水须佐能乎的能力都有那些呢？小编说一下自己的观点一，瞬身：根的追兵是一个团队，长期多年协作，具有很好的默契和配合，不会扎堆的对止水进行追求，而是会分散性各司其职的追击。那么止水如果想一击秒掉所有人的话，需要很大范围的大威力忍术。在视频中我们也看到止水秒掉众人后周围的树木和大地基本没有什么破坏性，可以排除大范围伤害性忍术。那么止水秒掉追兵的方式就只能是准确的定位瞬身，然后通过须佐能乎的高伤害给予致命一击。二，幻术加强：在卡卡西开启须佐能乎对战辉夜时，曾把神威之力融合在须佐能乎的手里剑中，一击便对辉夜造成很大伤害，仔细看的朋友一定发现了，须佐能乎使用的神威手里剑，相比于卡卡西开启的神威，具有转移速度更快和转移范围更广的特点，也就是说可以对神威进行加强。那么止水的幻术被称为最强幻术，双眼都可以释放，是不是开启须佐能乎后也可以对自己的幻术进行加强呢？比如同时操控多人的思维。三，止水须佐能乎的外形是左手拿着一个类似于钻头的锥形的武器，胸口有一块类似于传送门的东西，中间的勾玉可能是开关的钥匙。我们在来看一下止水写轮眼的形状很像一个风车，如果小编大胆猜测止水的查克拉属性是火和风的话，那么就不难理解其须佐能乎锥形的武器。因风属性可以加在武器上进行单点突刺，如阿斯玛的风之刃匕首。那么胸口的勾玉加传送门很有可能是一个幻术空间。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10239', '0', '火影忍者止水技能视频 理性分析止水定位事件', '随着昨日某某玩家曝光了止水的演示视频，整个游戏圈都被带了一波又一波节奏，对于止水这个忍者大家明白，绝对是会出的，但是现在唯一不确定的也就止水的定位，有人说是限定B，有人说是A，而今天小编也整合了一下现在所有的情报，为大家理清下思路，希望大家理性思考。下面就是昨日曝光的止水视频：在这段视频中（先不讨论真假），我们也看到了止水的技能和奥义，一个技能类似分身瞬移击中敌人，另一个技能貌似是后跳吐火球，击中敌人还能连上一次攻击。而最厉害的大招奥义，别天神，特效华丽，伤害全屏。火影忍者以上视频就是当前玩家争论的话题，作为游戏迷和漫画粉的小编，看了看止水，觉得大家的担忧不无道理的，毕竟魔方游戏这么久出个新A忍者，很多玩家希望来个帅气又牛逼的角色，接替小南来玩下去。而止水究竟如何也让人怀疑，小编也是理了理思路，下面就是止水的相关看法。一，止水的技能这个视屏曝光之后，玩家最大的争论就是技能的演示了，从游戏中看得出来，这个止水的技能并不对空，也就是可能打不到上方的忍者。对于飞行的忍者，如迪达拉，小南，因为会飞，所以在决斗场变成了强盗的存在，南天门组合虐过一个又一个新手，演示中的大招虽然华丽，但奥义释放毕竟较慢，这也让不少人诟病。所以现在不少玩家都在争论止水如果是A的话，这样的技能不合想象，太弱。火影忍者二，止水的人气和实力对于止水，游戏中属于暗线人物（前面的小北分析很多，感兴趣玩家看以前的攻略），出场次数不多，实力不俗，会瞬身止水，拥有万华镜写轮眼，可使用最强幻术别天神，改变他人意志，但可惜英年早逝，属于鼬的哥哥和引导者。火影忍者动画中和漫画中都是很少说到，给过的镜头都是少之又少，最后人物形象都是在外传和回忆中补全的。这里止水实力我们是肯定的，但出场太早死的太快，加上人设不是妖艳俊美，没赶上美好时代，所以这次的登场悲剧了。而玩家现在分为两派，一派是止水党，认为止水属于暗线角色，属于强势的英雄，有一定人气，技能演示不代表最终操作一切，还是以忍者本身为准，应该是A。另一派则是止水黑，认为止水就是个酱油角色，出场少，死得早，长得磕碜不说，技能还不对空，不符合A忍定位，作为个B限定还差不多。（现在吵的很火的止水B忍的截图已经说是PS的了，还有A忍的截图也是玩家PS的，一切以最终版本为准）火影忍者而对小编来说，其实我觉得这都无所谓，新英雄每次的出现都伴随着一定的争议，就像当年的水门和杜门李，新忍者的出现无疑是一件更好的体验，止水这个角色虽然有着演示视频，但不能百分百确定这个英雄的操作感觉。加上马上要改动的决斗场，迪达拉的重做，游戏上应该有大量的不确定性，如果这个英雄真的很厉害，作为新忍绝对实至名归，每个人都在用的话，你会坚持不买吗？玩游戏能玩到版本强势忍者这才是游戏的乐趣！火影忍者作为火影粉丝，我尊重每一个漫画角色，止水喜欢就买，不爱就走开，新版本还有许多未知等着我们去探索！我喜欢鸣人，想看太子慢慢在游戏中也成长为独挡一面的伟大忍者，毕竟这才是我玩游戏的初衷。所以大家理性看待止水事件，静等游戏的发布。了解更多《火影忍者》手游攻略，欢迎下载官方攻略APP：点我下载扫描二维码下 载全民手游攻略全民手游攻略微信公众号二维码：扫描二维码 关注全民手游攻略微信关于全民手游攻略全民手游攻略致力于为广大玩家提供最新、最全、最详尽的手游攻略，在这里你可以查询到任何你想要了解的内容。问答社区服务 为玩家们提供了相互交流并相互解惑的平台，让玩家们的疑问尽快得到解决。97973手游网声明：97973手游网登载此文出于传递更多信息之目的，并不意味着赞同其观点或证实其描述。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10240', '0', '带土应该是有止水尸体', '我想说一下止水其实是有非常之高的天赋和能力的。止水，瞬身止水，宇智波最强。拥有最强幻术别天神（别天神是日本创世神明的总称，我只是想表达岸本在取名时的用意）止水死后，寄宿在乌鸦里的单眼释放别天神的瞳力可以覆盖秽土转生，而斑本身是无法自行解除秽土转生的（兜被釉伊邪纳岐后，班在弥留之际用了秽土转生解加轮回天生）这足以证明瞳力之强大，还有有人说止水速度比4代2代慢，你们就请闭嘴了，两种术一个是靠标记的时空间忍术，一个是完全靠自身速度的体术（不用结印）。止水的眼睛属于直巴写轮眼（和斑一样，应该是速度型的，眼睛中间有孔代表她是直巴写轮眼），止水的速度在少年时已经可以达到形成实体的残影，自己也有独有的忍体术，比如说炎跃剑，日晕之舞。只是唯一的特点就是太过于正直了，没宇智波的那股阴暗，或者说是涉世未深。其实很多人没注意细节，兜想要止水的dna复活止水，带土说了一句，别得寸进尺，可知带土有止水尸体。都不听兜的了。秽土转生应该是属于幻术控制的，别天神不是解除了鼬的控制，而且别天神顺发。很有可能秒解控制的，大蛇丸和兜应该不会做这种危险的事。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10241', '0', '温暖的写轮眼——鼬与止水！', '《火影忍者 鼬真传》宇智波鼬，四岁。在第三次忍界大战中近距地观看了战场之后的他，做出一个决定。我一定要彻底改变这个世界。鼬一直作为目标的梦想，那就是，成为宇智波一族的首位火影。然后，他希望和自己所珍爱的人生活在充满和平的未来。在忍者学校时代，他和止水的相遇，然后是作为下忍，作为中忍，随后是不久加入暗部。鼬，本应当是光荣的路上不停奔跑。然而，路的前方，残酷的黑暗蔓延的都不知道。这是一个被称为天才的忍者所经历的光荣和挫折，以及决断的故事。里面提到鼬的恋人宇智波泉，其实鼬最爱的还是他弟弟啊！小时候的鼬也是萌萌哒~还有他的乌鸦~佐助你怎么能这样呢，从小就喜欢哥哥啊！哥哥抱抱就不哭。。。“ 爸爸，教我忍术吧。”——来自天才忍者“生命会诞生，生命会死去。”“ 是吗 真不愧是我儿子 ”哈哈想吃就说嘛，鼬神害羞了。。。猫耳鼬也是萌哭我哥哥的笑容很暖！“ 和一个能让自己放松的人一起吃饭 感觉很好” ——止水“但是 有一个事实毋庸置疑 那就是我绝不会背叛你 唯独这毫无疑问”加入暗部的鼬，立志从黑暗中注视木叶，守护木叶！（部分资料由大王整理于网络，像作者致敬！！）超爱二次元逗比青年、资深腐女不想错过任何一条你想看的动漫资讯?就赶快添加微信：rexuedongman这里有一枚低调逗比的大王等着你不要叫我女王大人叫我大王', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10242', '0', '火影忍者之“最强幻术大师”瞬身止水', '无论是在幻术还是在现实，你都已经败北了！——宇智波止水宇智波止水他是火影bug级别的人物，他是宇智波一族的天才忍者。他擅长瞬身术和幻术，他以“瞬身止水”的称号闻名于天下。宇智波止水他是宇智波“鼬”的挚友和导师，他也是宇智波最强的存在。他是能单眼开须佐的男人，他的bug技能迫使岸本不得不强行写死。单眼开“须佐”他的幻术“别天神”被很多火影迷戏谑称为“作弊修改器”。最强幻术“别天神”他一心为村子，他是这个时代的悲剧英雄。他怕自己的眼睛会带来更多的动扰纷乱。于是将左眼托付给“鼬”止水动图当他倒向黑色深渊，又一个天才的“凋零。宇智波止水', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10243', '0', '火影忍者：曾经多少人梦寐以求的女神，如今物是人非，御手洗红豆', '曾经多少人儿时的女神多少人是拜倒在她的服装上！只有纲手能跟她平起平坐无奈给作者砍了，真的好伤心啊出现的集数也不多，刚开始的时候人气绝对是火爆的！我记得当初还有几集是专门讲红豆的回忆的如今物是人非，还我女神！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10244', '0', '火影忍者疾风传那些你没见过的御手洗红豆精美拼图第一篇！', '火影忍者疾风传中御手洗红豆出场太少了，现在火影完结了，想回忆一下，自已找点图片拼接一下，发现挺好玩的，还能分享给大家。拼接的有点差，不太会。大家可以看到火影中不完全的精美图片，尤其是一些出场毕竟少的角色。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10245', '0', '火影忍者：消失的记忆，大蛇丸叛逃的时候对御手洗红豆做了什么', '木叶三忍，在火影的早期是被奉为传说的高人前辈。直到第一次中忍考试，三忍之一的大蛇丸才在众人面前现身。然而，大蛇丸的出现却并不讨喜，不仅出手打垮了第七班，还差一点被自己暴走的女弟子杀死。曾身为木叶精英的大蛇丸，因为太过醉心与禁术的研究，而触碰了木叶所不能容忍的禁忌，以活人做实验！东窗事发之后，大蛇丸选择了逃离木叶忍村，并且抛弃了自己唯一的女弟子，御手洗红豆！在中忍考试中登场的御手洗红豆，从12岁起就一直跟随大蛇丸执行任务，直到大蛇丸叛离之前，红豆始终陪伴在老师身边。然而，大蛇丸叛离之时不仅对红豆施加了消除记忆的禁术，还将天之咒印赋予了她，差一点将她杀死。之后的数年时间里，红豆一度记不起任何与大蛇丸有关的任何事情。本是师徒关系的二人，红豆却在与大蛇见面的一瞬间，直接动用了杀招。为了杀死大蛇丸，甚至不惜将自己的手连同大蛇丸的手，一同钉死在树上。控制住大蛇丸之后，红豆连一句寒暄都没有，借助大蛇丸的另一只手，想直接施展禁术，双蛇相杀，与大蛇丸同归于尽！身为自己的亲传弟子，大蛇丸究竟对红豆做了什么，让红豆竟有如此之深的怨念，恨不得抹杀与大蛇有关的一切！大蛇丸与御手洗红豆之间，曾发生过什么样的事情，我们至今无从得知。只能从诸多的蛛丝马迹之间，寻找红豆怨恨大蛇丸的理由。因为天之咒印的关系，大蛇丸可以随时限制红豆的行动。虽然红豆恨不得杀了大蛇丸，但对红豆的行为，大蛇丸却不置可否，只是爱怜的摸了摸红豆脸颊。或许是因为当初舍弃红豆的歉意，或许是因为对弟子爱护，大蛇最终选择放过了红豆，并没对她痛下杀手。看着大蛇丸丢下无法行动的红豆，独自消失的那一刻，石一在想：或许大蛇丸与红豆的关系远不止是师徒那么简单！十二岁的红豆，一直追随于大蛇丸的左右，当红豆从一个十二女孩渐渐成长到情窦初开的年纪，心中爱慕的男子就应该是这位，实力强大而名动忍界的男子吧。或许，被自己爱慕的男人抛弃，才是御手洗红豆想要不惜一切代价，抹杀大蛇丸的原因吧！（图：引用自岸本齐史火影忍者及同人作品）', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10246', '0', '火影：当年的女神御手洗红豆和照美冥，如今竟成了豆腐渣', '当年看火影的漫迷，都偷偷的预定了一个老婆。比如御手洗红豆，比如水影大人照美冥。当年的照美冥，还是个身材火辣，貌赛西施的大大大美女。棕色的长发像一棵树，碧绿的眼睛，妖娆的像一颗海草。成熟，端庄，性格和善。就是恨嫁。这个时候的冥应该有三十左右了吧，还没结婚。因此特别讨厌听到别人说话中带有婚的词语。她的部下因为这个就被莫名威胁过多次。时隔多年，冥一直没嫁人。再见到就在博人传里了。仍然是蓝色的抹胸裙，棕色长发，碧眼。但是，岁月在她身上的痕迹太重了。松弛的皮肤，深深的法令纹。明显老了很多。虽然风韵犹存，但已经与当年不能相比了。真是让人悲伤。好好的一个美女！令人悲伤的是另一个美女，御手洗红豆。当年的御手洗红豆 也是一位美女再看博人传中的红豆吧。如果不知道当年的红豆的模样，直接看四十岁的红豆，应该也还挺顺眼的。圆润的脸庞，圆润的身材。很和善温柔的大婶。但是知道红豆曾经的惊人美貌的，就会咒骂时间的无情了。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10247', '0', '浅析御手洗红豆——她的存在代表着大蛇丸人性的残存！', '御手洗红豆这个角色的作用不太大，岸本又给她安排了很多的剧情，看起来好像有点鸡肋的感觉，食之无味弃之可惜。但是御手洗红豆也不是一点作用都没有的，她最大的作用是衬托出大蛇丸这个角色。大蛇丸是一个反派角色，他出场之后的所作所为都是反派的行径，而且因为早期只有大蛇丸这么一个标准的反派，岸本把他塑造成了阴险毒辣的纯恶人。可如果大蛇丸真的只是这样一个纯粹的坏人，他就不会有现在这么多人喜欢了，一个完美的反派角色需要有一些人性，这种人性体现在大蛇丸杀三代火影之前流下的眼泪，同样也体现在御手洗红豆的剧情里。大蛇丸离开木叶的时候，御手洗红豆想过要跟着一起走，大蛇丸也想过要带着她，但大蛇丸问要不要一起走的时候，红豆选择了留下，这个时候身为木叶忍者的红豆心里十分挣扎，最终选择了守护木叶而不是跟随老师。他们两个人本来是师徒的，选择了不同的道路意味着这两个人会成为敌人，后来大蛇丸回到木叶看到了御手洗红豆，御手洗红豆马上冲上来说要杀了大蛇丸。红豆甚至用出了禁术想要同归于尽，大蛇丸都没有杀了红豆，要是换了别的人这么做早就被大蛇丸杀了。大蛇丸不杀红豆是因为他还有一点人性，御手洗红豆是大蛇丸最早的徒弟之一，那个时候的大蛇丸还是木叶的希望之星，御手洗红豆这个人的存在本身就代表着大蛇丸早期的那段人生。如果大蛇丸看到御手洗红豆以后毫不犹豫地下了杀手，他的魅力就消失了，一个真的对过去完全没有怀念的反派不是个合格的反派，因为这就代表着这个人物没有过去、角色形象十分苍白。在红豆心里，大蛇丸同样也代表着过去，所以大蛇丸说出红豆是被遗弃的时候，红豆接下来表现得十分愤怒且伤心。其实如果岸本能好好构思一下这对师徒之间的关系，他们的剧情会更加精彩，御手洗红豆这个角色可塑性是很高的，可惜岸本没有在她身上好好下点功夫，导致御手洗红豆后来一直在打酱油。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10248', '0', '火影忍者：御手洗红豆和照美冥这是怎么了，昔日女神形象彻底毁了', '最近几周，博人传主线故事围绕着雾忍村修行之旅展开。但剧情中除了透露了一个关键人物，枸橘神乐与四代水影枸橘矢仓是同族关系外，并无太大的亮点出现。反而是曾经在疾风传中大受追捧的两位昔日女神，御手洗红豆和照美冥更加容易受到大家的关注。图：引用自岸本齐史火影忍者及同人作品御手洗红豆曾是大蛇丸的弟子，但大蛇丸叛离木叶的时候，曾亲手抹掉了红豆的记忆。这让红豆对于大蛇丸一度恨之入骨，甚至不惜同归于尽也要杀掉自己的昔日恩师，大蛇丸。图：引用自岸本齐史火影忍者及同人作品御手洗红豆的恩仇，暂且放到一边不谈。最早在火影忍者第一部中登场的她，不仅身材傲人，样貌也是十分的出众。不能说是女神级的人物，却也算得上是一位美人坯子。图：引用自岸本齐史火影忍者及同人作品然而画风一转，如今在博人传中的红豆，不仅身材愈发的敦实，就连容貌也沧桑了不少，简直是惨不忍睹。图：引用自岸本齐史火影忍者及同人作品相较于红豆，五代水影照美冥绝对可以算得上是女神级的人物。虽然动不动就要给你一个蚀骨熔心般的吻，让一般的男人有些无福消受。但无论是身材还是样貌，绝对是最顶级的配置。图：引用自岸本齐史火影忍者及同人作品可惜如今的画风，昔日的风华绝代的照美冥，已经变成了中年欧巴桑一位，身材上发福走样还多少可以将就着看。图：引用自岸本齐史火影忍者及同人作品但是这张脸，真的很想亲口问问博人传TV剧组，你们这画的真是照美冥么？昔日女神，就这样完全炸了！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10249', '0', '火影忍者手游：清凉夏日，御手洗红豆邀你参加泳衣派对啦', '在平日明朗的表情，有时也会露出诡异的笑容。让人想到死亡的阴影，挂着甜美的笑容舔着鲜血的红豆令人畏惧。木叶最危险的女忍者——御手洗红豆【夏日泳装】各位翘首期盼的御手洗红豆【夏日泳装】本周五就要正式登场！俗话说女大十八变，更知道女孩子穿不一样的衣服也会有不一样的表现的哦！作为木叶三忍之一大蛇丸的学生，木叶最危险的女忍者，换了一身泳衣的御手洗红豆究竟和原来有哪些不同之处呢？下面小编就为大家一一介绍。1技能：忍法·双蛇相杀术快速向前突进，将敌人按压在地，召唤出蛇群对敌人进行缠绕挤压。最后引发巨大的深水爆炸，将敌人炸飞到空中。2技能：潜影多蛇手手中召唤出蛇群向前攻击敌人，蛇群会把攻击的目标拉近，然后红豆自身进行一次钩腿追击，把敌人击飞到空中。3奥义：猛蛇蹂躏通灵术召唤出巨蛇，把前方敌人缠绕后抛至高空，最后巨蛇在空中头往下冲击地面终结。个人点评：御手洗红豆【夏日泳装】的普攻保留着原有性能与机动性，与原版的红豆变化不是很大。一技能多蛇手与原版红豆相似，但是多了一个向上攻击的方式，可对空。二技能为突进投技，可把对付扑倒。召唤出大量蛇后爆炸，与原版奥义类似。三奥义召唤出巨型的蓝蛇进行攻击。御手洗红豆【夏日泳装】的平A无限连操作简单，而且伤害也比较不错。但是连招的操作难度比较高，比较依赖起手技能。下面是小编个人推荐的卷轴和通灵术仅供参考，如果各位读者有更好的搭配也可以在评论区告诉小编。秘卷搭配：【火遁·裂炎弹】小编分析：火遁·烈炎弹可以一次性打出8个火炎追踪弹，还可以自动追踪敌人。作为一款输出型秘卷技能，这款秘卷技能伤害较高，满级冷却时间仅为15秒。可以更频繁的出手，搭配拥有一定消耗能力的御手洗红豆【夏日泳装】一起使用效果绝对拔群。通灵兽搭配：蜃小编分析：蜃是火影忍者手游中后期的一款主力通灵兽，它不仅判定范围广，伤害高，更是可以提升我方忍者的移动速度。御手洗红豆【夏日泳装】自身的保护能力并不强，蜃所提供的移动速度加成可以帮助我们更好的走位。小编带大家一起看看玩家对御手洗红豆【夏日泳装】的看法！看来你是对红豆有特殊的感情呀~！少年，你的侧重点不对呦~！⁄(⁄ ⁄ ⁄ω⁄ ⁄ ⁄)⁄今天小编给玩家们分享的攻略就是这些了，玩家们有其他关于御手洗红豆【夏日泳装】的攻略可以在下方留言给小编哦~！更多精品文章请关注本文头条号：天空君玩游戏。每日更新。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10250', '0', '博人传中御手洗红豆为什么会大变样？原因终于找到了！', '在火影中，御手洗红豆一直是位非常豪爽的老师，曾经在中忍考试中担任考官，而且还是木叶中特别上忍，实力非同一般！御手洗红豆小时候曾跟随大蛇丸学习，就连通灵兽都是和万蛇差不多的，后来大蛇丸因为实验背叛了木叶，而御手洗红豆和老师大蛇丸的关系纠缠不清。而御手洗红豆为了表明自己的立场，曾打算与大蛇丸同归于尽，性格也是有些冲动的。火影时期的御手洗红豆一直都是一位大美人，身材也不逊于纲手，但岁月却是一把杀猪刀，博人传开播后，御手洗红豆就完全大变样。从原来的一位苗条的美女到一个肥胖的妇女，简直是不忍直视，与原来的模样可真是完全不同。而在更新的第六集剧情中，我们发现御手洗红豆变胖的原因，虽然依旧还是忍者学校的老师，但在课间休息的时候，在办公室御手洗红豆总是在吃零食，就连和伊鲁卡说话，满嘴都是食物，不胖才怪呢！就御手洗红豆现在这样暴饮暴食，也不知道与感情有没有关系，因为红豆老师一直都是单身，从来没有结婚，即便是当初那么美丽，也许是不婚主义者，对自己的未来也不在意了，才会不注重身材，暴饮暴食呢！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10251', '0', '来电静音——手机实用手册', '即日起，本头条号将会持续推出《手机实用手册》《手机使用手册》系列文章。《手机实用手册》将会给大家介绍手机上的实用功能，而《手机使用手册》将会给大家介绍手机上的日常使用功能。后续如果觉得有所帮助，欢迎关注推荐。注：前期文章可能不会具体到手机型号突然来电，我来教你怎么迅速静音总会有时候忘记调节模式，而恰恰又来了电话，有点尴尬？不要慌，又不是什么大不了的事，慢慢来都有的商量！今天我将介绍：在有来电的时候，如何快速响应。对于大部分安卓手机用户而言，可以按下音量键或者锁屏键让来电直接静音。也许会有特殊会设置锁屏键挂断，在正常情况下，锁屏键按下会是静音，再次按下挂断来电（前提你要打开“按电源键结束通话”开关，可在设置中查找）对于苹果用户而言，也可以按下音量键或者锁屏键让来电直接静音，双击锁屏键拒接。还可以顺手波一下响铃/静音开关当然触屏拒接或者再次提醒以及短信回复同样会静音在骚扰电话这么多的年代，没有系统管理的一定要学会手动！希望我有荣幸帮到您！我是手机鼠标', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10252', '0', '为高考静音', '6月6日，银川市北京中路安居苑社区开展“为高考静音，为莘莘学子护航”宣传活动。 该社区成立高考静音临时宣传组，要求社区各文化队伍“歇舞”至高考结束；向居民、商家发放静音倡议书，倡议小区内车辆不要随意鸣笛，装修户在施工中不要产生扰民噪音，商家不要使用高音喇叭招揽生意；监督建筑工地严禁进行噪音扰民作业。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10253', '0', '“静音”助高考', '钟山风雨起苍黄，百万雄师过大江。虽然已经是老生常谈，谈掉了牙的话题。BUT！我还是要再说一遍：“同志们！明天要高考了！现在学子们已经上满‘子弹’越出‘战壕’冲向他们人生中第一个‘战场’作为过来人的我们，在此郑重通知每一个看到这条信息的人！不管你身边有没有人参加高考，你都必须做到保持安静！别耽误学子们高考！你不能帮他们考！但是你可以完成一波很好的助攻！” 你知道高考英语听力，当洪荒之力汇聚于双耳时听到一阵阵喇叭声是多恐怖吗？！那种感觉就像你捡到一张10亿元的银行卡，别人正在告诉你密码，突然一阵喇叭声，一切希望变没了。 你知道高考语文作文，当你文思泉涌时听到一阵阵的哈哈大笑是多无奈？！即便我们可以做到细雨湿衣看不见，闲花落地听无声。但是那种感觉就像你在洗澡打了一身的浴液，突然没水了！ 你知道高考数学大题，当你绞尽脑汁用尽你的思维逻辑时听到一阵阵敲击金属的声音是多么的痛苦！那种心情如同夏日三伏，身穿皮袄，热风呼啸，一个字儿：燥 最后呼吁大家在即将到来的高考日里，安静出行，轻声工作，让身边的考点安静一点。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10254', '0', '现代途胜全车静音处理！', '作为北京现代的又一款拳头产品，似乎在定价上全新途胜走上了一条血拼之路，它并没有按常理出牌，作为新一代产品，全新途胜首先给了我们一种全新的感觉，同时在定价方面它也务实了不少。其价格几乎与ix35价格重合，在同级别合资品牌车型中售价也占了一定优势。车主途胜提车后立即就做了降噪处理?其实任何车型都是可以做隔音降噪的处理的，只是每个人对噪音的敏感层度是不一样的，如果你觉得开车过程中，那些轮胎、底盘、发动机的噪音可以接受，那就不需要做任何处理，享受就可以了。但是事实上，困扰着绝大部分车主反应的问题就是行车过程中的胎噪和发动机噪音最为明显。所以途胜车主来到云浮至上音乐选择了全车美特静隔音。引擎盖隔音之后，能够大量吸收发动机运转时的噪声，并且还具有隔热功能，有效保护引擎盖漆面避免长时间高温而造成的漆面老化现象。底盘与尾箱的面积最大同时距离路面较近，很容易受到胎噪和路噪的侵袭，所以底盘是最值得进行隔音的部位，隔音之后可有效抑制因轮胎行驶所引起的底盘噪音，车辆过颠簸路面的时候,整车感觉也会扎实许多。除了尾箱位置的隔音，还要对后尾箱盖进行隔音处理，这样会使尾箱整体变得更加安静。由于尾箱盖空洞较大，施工难度也大，所以技师先减振后吸音，最后再用一整块吸音棉既密封吸音，保证了尾箱的隔音效果。在经过砂石路面时，石子细沙随着车轮的转动，拍击底盘钣金和轮胎上面的轮弧衬板，噼里啪啦的声音相信很多车主都会印象深刻，翼子板隔音之后不仅降低了沙石撞击声，同时也会明显的感觉到胎躁得到了有效的抑制。做完隔音，车主迫不及待地上车感受下安静的效果，出去试了一圈回来，车主表示原本速度80以上的胎噪、风噪就特别大，现在明显降低了很多，噪音再也不会成为困扰了；原来用手敲击车上任意铁皮的位置，就像在敲一面大空鼓一样，空空的，现在都变得结实了，瞬间感觉高大上了！【云浮至上音乐专业汽车音响改装、汽车隔音】地址：云城区牧羊路140号', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10255', '0', '一汽大众的静音秘密', '中央人民广播电台录音棚内，录音师正在台上讲解如何借助顶级音响设备感受浓厚的音乐氛围，通过对比聆听乐队现场演出效果以及录制音乐文件的重放效果，进一步感受声音的魅力与科技的强大。这就是丹拿音响注重声音还原及场景再造的独特技术，同时也揭开了一汽大众整车静谧性和静音制造的工艺。众所周知，车辆的降噪隔音，主要来自于动力总成、路面、轮胎、风噪等；而这些声音又通过底盘、车身等几种传递路径传递到车内。要实现车内绝对的安静，除了先进的技术及优质的材料，更多的是对提高用户体验的追求。通过对噪声源-动力总成噪声的优化，同时借助带吸音PVB膜的前风挡玻璃、一体化的发动机舱隔音垫和近百项车身声学措施，以及0.28超低风阻系数，全新一代迈腾成功将车内发动机怠速噪音降至40分贝以下，为驾乘者营造出图书馆级别的静音效果；而在时速60公里行驶时，车内将降至58分贝以下，为消费者带来居家生活般的舒适。 （黄裕）', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10256', '0', '《火影忍者》中被人秒杀的月光疾风，实力真的如此不济？', '月光疾风，火影中的一个龙套角色，木叶村的特别上忍，中忍考试正赛预选的考官，一副病怏怏的样子，有着严重的黑眼圈、脸色苍白且经常咳嗽，善用剑术。一般在动漫作品中，看起来病怏怏的人往往实力都很强大，但是月光疾风却在跟踪药师兜和马基时候，被发现并且被马基秒杀，让人大跌眼镜。在第四次忍界大战时，疾风被药师兜秽土转生，参与战斗，与昔日恋人卯月夕颜相见并展开战斗。战斗后在最后的关头被兜控制意识而拔刀冲向夕颜，但用仅存的意念将剑刃偏离刺进夕颜身边的树干，自己也因冲击力被夕颜的刀刺穿而倒下。今天小编就和大家说说月光疾风他的实力真的这么弱吗？首先在他当初与马基战斗的时候，用了“三日月之舞”，马基也说过疾风这么年轻就会木叶流了，说明他也为疾风的实力感到吃惊，但他也说了，毕竟是普通的武器，普通的武器是当然可以挡下来的，后来就用风之刃秒了疾风。而且马基并不是无名小卒，他是砂隐村的精英上忍，他是我爱罗，手鞠，勘九郎的老师，深受历代风影的信赖，有丰富的领导才能，是风之国不可缺少的一员大将。他在火影中极少出手。曾在上一代风影死后力排众议扶持学生我爱罗成为新一代风影。可见他在砂隐村也是地位极高，而且实力应该很强，不然也不能担任四代风影三个子女的老师。所以被马基击败并不能说明月光疾风太弱。而且在那场战斗中兜说，疾风竟然能够在他的眼皮底下潜入。这句话也说明疾风潜入、偷袭这方面很强。之后疾风的尸体被兜收藏，并在第四次忍界大战中被“秽土转生”复活。而能够被兜复活的，也体现了月光疾风也是有着过人之处。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10257', '0', '月光疾风，一个努力而坚强的人', '月光疾风，日本动漫《火影忍者》中的人物，比较个人主义，性格温和。火之国木叶村的特别上忍，中忍考试正赛预选的考官，体质似乎很不好，有着严重的黑眼圈、脸色苍白且经常咳嗽，善用剑术。因跟踪并听见药师兜和马基谈论袭击木叶的事，被马基发现并迅速赶上，被迫在屋顶决战。疾风用\"三日月之舞\"劈中马基肩膀，却因为马基的特殊能力而发现刀拔不出来，最后被马基的风之刃所杀。动画中，在第四次忍界大战时，疾风被药师兜秽土转生，参与战斗，与旧日爱人卯月夕颜相见，战斗后在最后的关头强行控制自己冲向夕颜而被夕颜的刀刺穿而倒下，遂被封印，灵魂升天。所属部门：暗部性格：个人主义，性格温和、沉稳正像他浪漫的名字一样，月圆月缺，人生无常，岸本在设定他名字的时候已经安排好了他像风一样消逝的命运。在加上他那略现孱弱的病态，就给人朝不保夕的感觉。风中之烛，昙花一现。特殊能力：透遁技能木叶流剑术·三日月之舞：是第二代火影创造的木叶流剑术奥义中的奥义，甚至是能够一击杀敌的致命招数，具体是施术者需要利用三个分身（不是影分身）同时向目标发动突然攻击，然后本体在一旁观察出对手死角后一刀砍下去，从而抹杀对手。是无法躲避的闪电攻击术。影分身之术：此忍术可以使施术者分出多个实体分身。这些实体分身可以掌握知识，有自主、自我意识，可以说就是另一个你，但是分身知道自己是分身。分身可以自己解开忍术，一旦分身消失。分身所学到的知识，经验，以及看到的东西会立刻转给本体和其他的分身替身术：瞬间以动植物与自己对换身份，以此躲避攻击。疾风的死月光疾风发现兜与马基的阴谋，回去报信，被马基迅速赶上，被迫在屋顶决战。疾风用“三日月之舞”劈马基肩膀，却发现刀拔不出来。接着被马基风刃所杀。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10258', '0', '我在云间给你写信——月光疾风to卯月夕颜', '我在云间给你写信——月光疾风我在云间给你写信月有阴晴圆缺我不知道我要以什么样的语气来向你问好夕颜四月满树满树的花开正是我眼中你的样子卯月缤纷年年有却不得夕颜年年的欢笑撑一把雨伞在春的缕缕青烟中走过低矮的墙根是否可以看见你的身影？我在云间给你写信在战争时代从来都是经得起风雨的你在安稳的日子里必定也经得平凡我们都是凡夫俗子只能做彼此生命中的过客但是未免太过匆匆以至于我来不及守护你的一生来不及与你慢慢共论剑法凋零的爱情没有因为所以有的只是离别与回忆我不知道死后的再次重逢意味着什么但仍然觉得可以再次看见你是有多么幸运无论你在哪里无论将来的你选择了谁共度余生我都希望你可以一直幸福地走下去在和平年代里再也不会有人突然撒手离你而去了没有人可以挥得去生离也没有人可以抵得住死别再长情也没有什么可以再次回到你身边的机会了看不清楚你戴着暗部面具时隐藏的忧愁尘世中的你我无法久久相依我也无法离开这个世界前往你的身边守护你我在云间给你写信我爱你所以不愿看到你在月明星稀的夜晚独自一人流泪我爱你并且希望你能柔情执剑斩愁思暗部虽暗仍有出生入死的同伴相随你说你不忍执剑不忍想起执剑之初的模样可身为忍者又怎能不管家园的安危？我愿意用我所有的力量去争取你所能触及得到的美好朝颜复夕颜你仍旧美貌如初我亦安然离去岁月一轮又一轮剑法日益精湛的你必定能够以子之剑，护此家国', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10259', '0', '夜归人—惠比寿', '柴门闻犬吠，风雪夜归人。平安京的冬天一如既往地寒冷刺骨，虽有神力护体却还是将身体微微蜷缩，以寻得几分暖意。四周静谧，只能听到远处几声低沉的动物叫声，以及几声夜鸦啼鸣。忽地，就下起雪来。鹅毛般大雪飞飞扬扬仿若要将整个平安京掩埋，伸手接住一片雪花看着它在指间一点点融化成水，滴在地上与其他雪花合二为一，一时之间竟如同孩童般兴奋起来。金鱼上浮，雪花片片，不一会便将手中笏板头顶红伞染上白霜。低头向下望去，只见得平安京被雪覆盖，仿若于画卷中的点睛之笔。黑夜，白雪，寥寥灯火，虽不如万家灯火那般绚烂，却也别有一番风采。有趣，有趣。衣袖轻盈浮空，福数化作点点萤火撒入人间，为在沉睡中的人们送去几分福气，愿他们睡得安稳。大抵，也只有这平安京配得上如此繁华罢。不知为何，心中隐隐升起一股离愁别绪，仿佛又看到那些举杯共饮的神祗。细细想来，已是有六千余载未曾归乡，或者说，自己本身便未曾有过故乡。生来便被放逐水上，竟连个神子的名号都未曾得到，游历千山万水，看遍世间百态，怕是早已忘了故乡的模样。他人的愿望由福神实现，而福神的愿望大抵只有他自己才能实现吧。手中笏板轻挥，落下星星点点神力，飘洒空中，合着飞雪，一不留神便已然消散。“老友，老夫想回家看看。”低头在鱼背轻抚，鳞片入手冰凉。夜归，夜归，可若是无处可依，又几时能归？归往何处？当归，当归。理应归去，却又因着这天下福祸而无处安身。而这也是一种福祸罢？所谓求而不得，理当如是。忽地，便笑了起来，苍老笑声于嗓间传出，盖过落雪纷杂，却不曾传入凡人耳中。身为福神，却是个无处可归的弃子，如何不荒唐？恍然又回到那年初夏，阳光正好，父母与自己坐在潭边谈天论地的模样。虽没了神籍，却有幸如同普通人家般温暖。虽然那只是个不愿醒来的梦境。天边已逐渐泛起鱼肚白，人们已陆续来到街上观雪赏冰，而他们不会知道自己昨夜所思所想，只知祭神求福。“走吧，天亮了。”', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10260', '0', '决战平安京：英雄超级攻略之能奶能坦又能控的万能辅助惠比寿', '(决战平安京）英雄超级攻略之 能奶能坦又能控的万能辅助 - 惠比寿惠比寿大家可能不熟悉，比较多人称呼他「爷爷」，也是目前版本中个人认为最强的辅助位。一、技能福报被动：惠比寿每损失1%法力值增加2%法力回复。讲解：这个被动让惠比寿在初期的法力也能有更好的续战能力。 五福临门冷却：14/13/12/11/10秒消耗：60/70/80/90/100法力值惠比寿招来福气，治疗自己和周围的友方单位，每0.5秒恢复22.5/30/37.5/45/52.5(+10%法强)点生命值，持续3秒。讲解：这招就是爷爷可以当超级奶妈的招牌技能，走到哪奶到哪，不管是前期中期还是后期，都是非常奶的技能。 愿者上钩冷却：18/17/16/15/14秒消耗：100法力值惠比寿向指定位置甩出鱼竿，若命中敌方式神，则进行2次拖拽；若没有命中，则进入垂钓状态，移动和施法均可中断此状态。再次点击收起鱼线，将命中的敌方式神拉向自己，造成120/180/240/300/360(+50%法强)点法术伤害。讲解：这招决定了你是神爷爷还是躺分爷爷，施放有前摇动作大概0.5秒左右，需要更准确的「预判」敌人的动向，才能更准确的拉中敌人，这招可以搭配移动跟瞬步使用。 祸福相依冷却：10/9.5/9/8.5/8秒消耗：30/35/40/45/50法力值惠比寿对前方小范围敌人造成100/165/230/295/360(+50%法强)点法术伤害并晕眩0.6/0.7/0.8/0.9/1.0秒。讲解：建议起手只需要点1等，1等跟5等的晕眩时间只差了0.4秒，投资报酬率不是那麽高，最后在点满就可以。 鲤鱼旗冷却：80/70/60秒消耗：100法力值惠比寿召唤幸运的鲤鱼旗，保护周围的友军式神，使进入范围的友方式神立即获得(+8%最大生命值)的治疗及淨化效果，每秒额外恢复30(+10%法强)点生命值，持续6.5秒。讲解：这招的关键在于「淨化效果」，可以瞬间消除队友的负面状态，可以让被控制的友军瞬间解除，很像是范围更大的一技能加上拔除的效果，不过要注意施放的时机跟位置，因为鲤鱼旗是固定位置的补血效果。01 二、阴阳术搭配 首先要了解辅助这个位置的重点在哪裡，以及惠比寿的技能优势，惠比寿是一个不需要装备也能发挥能力的辅助式神，所以对于经济的要求就没那麽高，更多的是依靠我们的操作。言灵?守：增加一个类似护盾的结界，冷却时间很短，相当好用。巫女祷言：可以更快速的使用瞬步跟束魂。护灵：大幅增加惠比寿的奶量跟残血救援能力。移速X2：可以灵活的穿梭战场。冷却X2：可以更快施放技能，奶度跟控制技能都更快。法强X2：惠比寿的奶度会受到法强影响，选择这个应该没什麽疑问。在阴阳术上我会推荐以上的组合，对于惠比寿是最好用的组合，有些人会把巫女祷言换成点金星尘或是狐火明焰，但我前面有说惠比寿更多的是依靠操作而不是装备，而你多那几百滴血也许能让自己活下来，但队友呢？不如降低冷却时间，可以更好的使用束魂跟瞬步，可以更有效的帮助队友。三、灵咒的选择02基本上个人觉得适合辅助位使用的灵咒有几种，瞬步、俊足、盾守、束魂，瞬步几乎是人手一个的灵咒，不管是追击、逃跑、秀操作或是撞牆都是最好用的，所以要考虑的只有从俊足、盾守、束魂之中来选择，来分析一下这三个的优点。俊足：10秒内增加30%移动速度，可以更快速的抵达会战区域，或是迅速的走到适合放二技能的位置。盾守：给自身跟周围血最少的队友施放一个护盾，可以让原本就很奶的惠比寿，增加一个保队友的护盾。束魂：很具争议的灵咒，牺牲自身的攻击来降低敌人攻击跟移动速度，对于其他位置可能没用，但是本来就不需要输出的辅助位就很适合，光是降低敌人40%移动速度的特性，就能有效的留住敌人，给队友更大的输出空间，如果队友被抓放这招可以降低敌人40%输出，也是非常的好用，但具有争议的部分是施放时机，很多人不会使用这招。个人是习惯携带瞬步+束魂，万用的瞬步可以打出很多骚操作，而束魂搭配二技能可以有效地留住敌人。四、出装建议惠比寿依照战局的状况，我有推荐两套出装，但别过于的在意出装，毕竟惠比寿本身会补血，比起其他辅助能够承受更多的伤害，时常发现每一局结算都是惠比寿的承受伤害最高，但不一定是惠比寿死最多次，全部都是拜一技能所赐。惠比寿的核心装备：七面天女、轻音、空岚之境03第一套出装是我的通用出装，不管是面对什麽情况都能轻鬆应对，最主要的思路就是「奶，还要更奶」这套出装在前期「七面天女」提供了大量的法力值跟一定的法强，让惠比寿在初期就拥有很不错的续战能力。鞋子唯一选择护甲鞋，这双鞋子提供了60护甲跟6%的物理伤害减少，在下路大多射手都是纯物理输出，可以有效的增加惠比寿的坦度跟生存能力。「轻音」可以有效增加惠比寿的奶度，20%的治疗跟护盾提升，完全就是为了惠比寿设计的装备，必出装备之一。接著可以出小黄书「曼茶罗密经」，是中后期不会魔乾的重点装备，击败或助攻时恢复20%最大法力值，对于非常容易助攻的惠比寿来说，可以让魔量几乎维持在8成以上。也可以视情况先出「空岚之境」，也是惠比寿的核心装备之一，他的主动技能「蚌精」是一个神技，这个护盾可以在任何情况下施放，就算你被晕眩、被绑住的情况也能施放，可以有效的在关键时刻拯救队友(或自己)，配合一技跟大招可以把残血队友救活。最后补上一个「出云之章」，让惠比寿的奶量大幅上升，到了大后期一技可以补射手将近三分之一甚至一半的血量，让全队的续战力非常强。04第二套装备偏向坦型的出装，适合在逆风场或是对方针对你的时候，可以选择这套出装，毕竟死人是没办法辅助或输出的，唯有活下来才有希望。将小黄书换成「月读尊衣」虽然奶量降低一点，但是增加了更多的护甲，将最后的出云换成「繁盛绯衣」或「金装武神铠」，一个可以增加全体法抗、另一个可以增加全体攻速跟护甲，并且削弱敌人护甲，这两件都是光环型装备，可以依照情况来选择。五、对线技巧通常惠比寿会走辅助位，也就是所谓的下路双人组合，身为一名专业的辅助，是不会跟射手抢兵跟抢人头，那在经济上肯定是相当的少，前面我就有提过了，我们玩惠比寿更多的时候是依靠我们的操作，而不是装备跟经济，要玩惠比寿之前要有一个觉悟，「经济跟装备都是浮云，我们靠的是操作」，不要有想要经济起飞的妄想了。05如果想要在1等就尝试杀人的小伙伴可以先点二技能拉人，比较保守型', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10261', '0', '《火影忍者手游》6月14日更新：团藏可能团购获得、新饰品上架', '各位忍者学员： 父亲节祝福，登录送好礼；喜迎端午，周周好礼，不见不散；粽子集集乐，收集不同种类的粽子兑换奖励；忍者拼图，完成拼图送碎片；购买指定次数的体力和铜币，将获得额外奖励；封印宝箱，兑换S忍碎片。最新活动父亲节祝福6月17日（本周日）是父亲节，记得给爸爸送去一份祝福吧。值此之际，火影办公室准备一份好礼相送。6月17日至6月18日，登录游戏即可领取父亲节专属福利。小编：这次应该送水门和阿斯玛吧~当然送三代也没意见喜迎端午 周周好礼 不见不散活动时间：6月16日至7月1日 每周周末参与条件：等级≥20级活动内容：活动期间，周末登录游戏，即可领取超值奖励。粽子集集乐活动时间：6月15日05:00至6月22日05:00参与条件：等级≥20级活动内容：学员通过冒险/精英/修罗副本、小队突袭、任务集会所和活跃度宝箱，可以获得不同种类的粽子，不同种类的粽子组合可以兑换不同的奖励。忍者拼图活动时间：6月15日05:00至6月21日05:00参与条件：等级≥20级活动内容：活动期间，学员通过活跃度宝箱和商城礼包可以获得拼图点，完成忍者拼图即可获得相应忍者的碎片和其他奖励，每位忍者拼图仅可完成一次。限时购惠活动时间：6月15日05:00至6月22日05:00参与条件：等级≥20级活动内容：活动期间，学员购买指定次数的体力，可额外获得体力奖励；完成指定次数的招财，可以额外获得铜币奖励。小编：购买体力的活动出多很多次，请看下面的数据。封印宝箱活动时间：6月15日12:00至6月18日24:00参与条件：等级≥20级活动内容：活动期间，学员开启封印宝箱可以获得忍者碎片和稀有道具“忍者信物”。忍者信物可用来兑换波风水门、大蛇丸和佩恩[天道]忍者碎片。解封次数达到指定要求，还可以额外领取忍者碎片和忍者信物。小编：又来！！！这是要榨干的节奏吗？提醒大家一下，团藏大概率是金币团购获得，大约5000金币左右。血继限界大作战 决斗场胜场三倍活动时间：6月11日05:00至6月18日05:00参与条件：等级≥20级活动内容：活动期间，全部使用拥有或移植血继限界的忍者参加决斗场，每获胜一场，每周奖励的胜利场次增加三场，如果失败则维持原有规则。无论胜利失败，积分计算规则不变。另外，每日首次全部使用拥有或移植血继限界的忍者击败对手，还可以领取额外奖励。旗木卡卡西[新春限定]礼包活动时间：6月15日12:00至6月18日24:00参与条件：等级≥20级礼包内容：旗木卡卡西[新春限定]碎片*40，售价980点券，限购1次。小编：收藏忍者，买了就后悔系列忍者。（最后悔招募的忍者投票结果）忍者特惠礼包活动时间：6月15日12:00至6月18日24:00参与条件：等级≥20级礼包内容：宇智波带土[少年]碎片*40、奈良鹿丸[疾风传]碎片*40、山中井野[夏日泳装]碎片*40、春野樱[夏日泳装]碎片*40、御手洗红豆[夏日泳装]碎片*40、日向雏田[夏日祭]碎片*40、香燐碎片*40、药师兜[恶灵之影]碎片*40。忍者多选一礼包，售价1980金币，限购3次。小编：都属于收藏忍者，缺哪个买哪个，萌新推荐带土。提醒大家一下，团藏大概率是金币团购获得，大约5000金币左右。1.团藏根据今天更新的文件来看，团藏大概率会是金币团购获得，建议想招募的玩家留好金币。如果金币够多，建议原价购买，因为最终会返还金币，最主要是原价购买的奖励很不错。2.新饰品：祝福新饰品已经可以掉落了，不过具体魔抗还不清楚，有知道的老铁麻烦评论里留言。从饰品属性看祝福饰品要比封印更好，又是一大波强化。饰品强化道路漫长，老铁们慢慢来~3.二代上架特权小编今天刷新了一次，结果刷出二代了。好多更新内容官方公告都不说了这是为什么呢？难道想制造一个', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10262', '0', '深度分析团藏真实实力，结论：白活一大把年纪', '火影忍者把本来阴暗的忍者世界以光鲜的面貌搬到荧幕上，让人耳目一新，火迷们目之所及都是“火之意志”的光辉形象。但任何事物都是一体两面的，只谈光鲜的一面很明显是片面的，所以作为忍者阴暗面的团藏就出现了。团藏和三代火影同期，相比猿飞日斩，他倒是更像二代火影的弟子，可惜个人性格自私、考虑问题利己多于利人，所以最终二代火影还是把火影的位子传给了猿飞日斩。而团藏后期则进入暗部，作为木叶阴暗面的存在。今天小编不聊团藏的功过是非，来聊聊团藏的实力。和三代火影同期，甚至还可能是二代火影的亲传弟子，理论上来说团藏的实力应该是忍界顶尖。但很可惜，和佐助的一战中，团藏并没有表现出过人的实力，下面小编分几个方面来给火迷们分析下：1、战斗经验不足，只会权谋诡计从团藏临死前的回忆来看，他曾经和二代火影等人遭遇了云隐村的一场追击战，当面临断后抉择的时候，团藏整个人身体在发抖。反观二代的成熟稳重和猿飞日斩的干练，团藏就像是没见过沙场的小孩。在二代火影的掩护下团藏等人顺利回到木叶，此后就进入暗部工作，作为“隐藏在黑暗中的根”存在。长期坐镇大本营，手下精兵良将无数，自己动手的机会更少，以致于团藏严重缺乏战斗经验。在和佐助的战斗中，他竟然是第一次见到须佐能乎，这点对一个先后经历过第二次、第三次忍界大战的人来说，简直不可想象，也侧面说明团藏只是个关起门来搞权谋诡计的“阿宅”。2、缺乏攻击力强劲的忍术同样是和佐助的战斗中，面对须佐能乎，团藏对它的性能一无所知，竟然直接冲上去用拳头硬撼，当然结果是对方纹丝不动。意识到须佐能乎的超强防御后，团藏试图打破它，但尴尬的是自始至终没有拿出像样的强劲忍术，最多是风遁真空玉这种“对人”忍术。这点上先不说和同期的三代相比，就连后辈自来也有大玉螺旋丸、波风水门瞬身术、鸣人的螺旋手里剑，他都比不过。最后在通灵兽的加持下，加强风遁威力，终于破开了须佐能乎的表层，但最后也功亏一篑。可以说正常战斗，二柱子几乎都在城堡里朝他扔炸弹，而团藏却没有任何强力忍术能攻破这座城堡。3、体术就是渣渣在和佐助的战斗中，团藏展示了什么叫体术渣渣。虽然有伊邪纳岐加护，但团藏死在“拳头轰击”、“手里剑投掷”下的次数至少有五次，躲不开手里剑这种事，恐怕也只有龙套做不到吧。整场战斗中团藏的体术只起过两次作用，一次是踢开香磷，另一次是躲开掉落的碎石……4、防御力太弱，肉体强度低，不抗揍缺少战斗经验的团藏，单纯到以为只要堆装备就能万事无忧，满胳膊的写轮眼，就被他这么轻易的浪费了。除了缺少强劲攻击性忍术之外，防御力弱也是团藏的硬伤，能被手里剑投死，这身体强度是弱到怎么样的弱鸡程度啊。反观三代火影有猿魔，鸣人有九尾外衣，团藏这弱鸡般的肉身，也难怪会败北。坐拥金山银山却被小毛孩弄死，团藏这辈子算是白活了。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10263', '0', '「火影忍者」你还记得团藏吗？', ' 作者：她i团藏这个角色在《火影忍者》中是一个非常重要的角色，他的存在表示就算是非常爱护村子与同伴的木叶忍村，依然有黑暗的一面，在最后的关头，团藏用自己的死表示了对村子的在乎，让人唏嘘在《火影忍者》中，团藏这个人物的定位有些难，说他是好人吧，他训练根的原则就是没有过去，没有未来，只有任务，并且成为根的准则就是要和自己最亲的人决斗，直至一方死亡。说他是坏人吧，他是保护村子的强硬派代表，在与佐助的战斗中，为了守护村子，使出四象封印想跟佐助和面具男同归于尽。 他在临死前曾说“ 可我最终还是没能成为火影，无论走到哪里我都追不上你啊， 你是沐浴着光明的树叶，我是暗藏于黑暗的根，这样的我你是怎么想的呢？日斩，对你来说我是……”这句话让许多人微微动容，所以还是大家来决定吧。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10264', '0', '火影忍者：药师兜秽土转生为何没有团藏？原来兜是害怕团藏的禁术', '什么才是火影中最强的忍术？其实秽土转生怎么也能排上前几名的位置。其实熟悉火影忍者的小伙伴都知道，在火影忍者后期，开挂是剧情的日常；而从药师兜继承大蛇丸的意志之后就变得更加的恐怖了，毕竟曾经的大蛇丸都没有秽土转生这么多的忍者，而药师兜竟一下就转生了一大堆超强忍者；其中不乏曾经的王者，然而，药师兜并没有转生团藏？这到底是为什么呢？如果说团藏不厉害，我想大多数人都不会认同；毕竟是和三代火影抢位置的男人，作为一起长大的三人，团藏的实力其实蛮厉害的。尤其是在窃取了宇智波一族的眼睛之后；当然在和宇智波佐助对打的时候最终还是失败了，可以说这里本身就是一个Bug，或者说是二柱子的主角光环太强的原因，不然团藏是不可能这么久挂掉的。虽说这里团藏最终使用了封印之术，结果还是惨败了。按照兜的逻辑，只要能找到点基因，秽土转生团藏还是可行的！然而并没有这样的操作，那只能说明一点，就是药师兜其实是有顾虑的，所谓的顾虑就是转生出来之后有风险；不然，药师兜肯定不会放过这个大咖的！大家觉得是这样吗？很多人觉得还有一个原因，那就是兜找的最后一个就是长门了，之后就不再去挖坟了；而长门本身就很厉害，所以在拥有了长门和宇智波斑两枚超强棋子之后，药师兜就放弃了其他人的转生之术；个人觉得这个逻辑其实是不对的，毕竟谁会嫌弃钱多呢？这个道理是固定的！连很多小角色都召唤出来了，所以肯定不会放弃团藏的。其实药师兜是害怕团藏，并不是说团藏很强；而是团藏会逆秽土转生之术会反过来控制施术者，兜害怕被控制就放弃团藏，动漫里是有讲过；所以要承受这么大的风险，最终药师兜就没有转生团藏，不然复活大军又要多一位高手了。最后，大家觉得团藏到底是怎么样的一个人呢？毕竟，评价团藏还真是不好评价，因为他具有太强的两面性了。最后如果大家喜欢一定要点赞、收藏和分享喔！想要了解更多的吐槽和YY，请继续关注我们吧！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10265', '0', '火影忍者：凯想加入暗部被三代和团藏拒绝，团藏说的理由很搞笑', '迈特凯和卡卡西是一对多年的好基友，一个热血过头，一个冷静懒散，两人的性格非常互补，是火影中少见的一对挚友。卡卡西成为暗部成员后，每天任务繁多，所以每次和凯他们见面的时候都是一副身心疲惫的样子，其实卡卡西本来就是一副很懒散的样子。然后凯看到后，就非常担心卡卡西，想要帮他分担，所以他就想到一个最直接的办法。就是加入暗部，这样就可以随时和好基友在一起了。可是，他去请求三代目火影加入暗部的时候，三代却说他不适合，说他的优点在暗部根本得不到发挥。接着，他遇到暗部“根”的头目团藏，他又请求团藏让他加入团藏直接领导的暗部“根”，结果还是被拒绝了。团藏只对他说一句话，说开还不够黑暗，然后就走了，留下一脸懵逼的凯。团藏确实说的没错，像凯这样的热血忍者，为人又正直，根本不适合暗部的工作，更加不适合充满黑暗的“根”。后来凯还是得到了与卡卡西一起执行暗部的任务，那时候还有刚刚加入暗部不久的宇智波鼬，但是凯看到卡卡西和鼬杀掉任务目标的时候，却不认同这种做法，还直接称呼卡卡西的名字，暗部里是不能称呼成员名字的，这是暗部的忌讳。完成任务后，凯对三代说他明白自己为什么不适合暗部了，像他这样的刚正忍者，根本不适合暗部的刺杀任务。如果说凯代表的是光明的那一面，那么卡卡西就是代表黑暗的那一面，不过，现在这对基友已经退居二线了，但是，凯还是老样子，还是那个热血大叔。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10266', '0', '你认为团藏是什么人？', '宇智波叛变最根本的原因是带土，要不是他控制九尾破坏木叶，木叶和宇智波之间的矛盾也不会激化，说到底这还是宇智波自己的锅，就算木叶不针对宇智波，看到九尾照样会怀疑。不灭宇智波爆发内战，死的人更多，所以说不灭宇智波才是间接的杀人犯。而且不好意思，最后他的结局是作者画的，所以说岸本官方都已经给他洗白了，说啥都没用。团藏只不过是继承了柱间的意志，两害相权取其轻。柱间杀斑的时候就说过“谁危害到村子，我绝不放过”。。。还有我感觉让雷影原谅佐助是根本不现实的，不知道为什么会有鸣人说这话的设定。雷影认为亲如兄弟的八尾被佐助杀了，而鸣人居然想去让一个根本就没有任何关系的雷影来原谅杀了自己兄弟的人，这根本就不现实。个人认为这里是鸣人的一处败笔，因为他只为了自己着想，让雷影去原谅杀害了雷影兄弟的佐助。鸣人真是一厢情愿，他就没有考虑过佐助回到木叶的处境吗？一个终身监禁，二禁锢查克拉，无论是哪一种都是生不如死。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10267', '0', '《火影忍者》长门的一生！', '宇智波斑在年迈的时候把轮回眼交给了长门。因为长门是漩涡一族，拥有足够支配轮回眼的查克拉，而且斑也只是想利用长门实现自已的复活计划，并且后来指使带土监视着长门，想等时机成熟了，使用外道轮回天生复活自已！​长门从小就目睹了双亲被误杀，所以就开始流浪，在后来碰到了小南，和弥彦，成立了晓组织，让弥彦当组织的老大，主张以战止战的思路，不过后来随着组织的扩大，已经威胁了其他忍村的地位，随后雨隐村的山椒鱼半藏，联合木叶团藏的暗部，准备除掉晓组织，抓住了小南，弥彦为了救小南而选择牺牲自已！​长门看到自已最好的朋友死在自已面前，轮回眼的力量开始爆发，召唤出了外道魔像但是副作用却被插满了黑棒长门还被雨忍村事先埋伏的起爆符把退炸残废了！​长门一生想要制造和平，到最后却想要毁掉木叶，因为他看透了，忍者村的争权夺势，最后却被鸣人的嘴遁说服，使用了轮回天生复活了，佩恩杀死的忍者但是自来也的尸体沉入大海，我成为了火影忍者的一大遗憾，长门也因为过度使用查克拉而牺牲！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10268', '0', '为什么长门不直接复活弥彦？', '怎么复活，轮回天生之术呀。其实很简单，岸本老贼说我要是复活弥彦，就把小南也写死，还说我只要杀了五五开就复活弥彦，可是我后来才知道，老贼也是这样整带土的，让他来杀小南，否则就不让他复活琳……最合理的解释就是，轮回天生术只能复活死了，但是灵魂还在世上，还没来得及去另一个世界的人。不可能说人一死，嗖一下就到另一个世界了，看看断的技能，灵魂还可以到处飞。所以长门复活了刚杀死不久，灵魂还没到另一个世界的，就像过奈何桥一样，在中途。班是因为灵魂被召唤到了现实世界，这样都说得通了。一，长门被骗了，以为只能复活短时间内的死者；二，岸本自己埋的坑。可轮回眼长你身上啊长门，你不会试试啊！综上所述，是岸本填不了的坑。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10269', '0', '火影忍者，带土与长门如果正面厮杀，恐怕长门会败下阵来？', '大家好我是小百合，今天为大家带来的文章是带土与长门如果正面厮杀，恐怕长门会败下阵来？带土和长门几乎不可能正面强上。长门一个残废只能靠六道分身强上。带土早知道轮回眼的情报，他是脑子进水了才会跟六道分身打个没完。所以很快就会转变成带土暗杀长门本体。整部火影里面，几乎没有阴不死的人。斑爷秽土出来之后大杀四方，玩五影跟玩孙子一样。但愣是装B不开须佐完全体。结果被土影老爷子打碎了一半。秽土长门被秽土鼬看了一眼，就烧了个七七八八。连一人扭转一国战局的四代都差点被带土一摸摸到时空间里去。对，带土就是整部火影中最强大的刺客，不需要考虑神威的身体负担，只要注意五分钟的穿透持续时间，可以任意突破任意封印结界去任意地点偷袭任意一人。何况他还有火遁木遁阴阳遁和各种阵法。当然长门和小南肯定会在长门的位置放万亿起爆符陷阱等着带土上钩。但是带土不会像看不起小南一样看不起长门，必定会等到万无一失的机会才出手。或者干脆用各种替代术分身术来引出两人的各种埋伏。我觉得两人会无限僵持下去。非要说谁胜的话，因为带土有一只额外的写轮眼来读档，比较起死守的陷阱来更加主动，我看好他。个人意见，欢迎大家讨论~', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10270', '0', '火影忍者，手鞠为什么看上了鹿丸？', '随着时间的推移，忍界中曾经的少年们逐渐长大成人，并且大多成家立业，手鞠作为砂隐村最抢手的女忍者，不仅父亲和哥哥都是风影，自身也很优秀，如此，为什么手鞠会看上懒散的鹿丸呢？门当户对虽然手鞠的身份看似远超鹿丸，但其实两家可谓是门当户对，奈良一族是木叶村秘传家族之一，虽然没出现火影，但是家族却世代担任木叶要职，奈良鹿久曾担任木叶上忍班长、木叶高层智囊、忍者联军总指挥。性格相符因为身份不同，手鞠养成了直来直去的性格，雷厉风行，讲究效率，鹿丸做事情总是觉得麻烦，但因为极高的智商和办事效率，却非常符合手鞠的性格，而且因为手鞠的直爽，也不那么让鹿丸觉得麻烦。不打不相识实力高强的手鞠在村子中顶级的强者，除了我爱罗，似乎没有忍者能够入她的眼，但是这种自信在与鹿丸对决时被摧毁，鹿丸虽实力一般，但依靠智谋战斗却让手鞠大惊失色，这使得手鞠对鹿丸印象十分深刻，手鞠救助鹿丸更是让两人了解更深。家庭因素从小鹿丸就经常目睹父亲鹿久被母亲压制，完全不能反抗的父亲只能去酒馆喝酒，这种熟悉的感觉似乎也就是手鞠对鹿丸的吸引，这让鹿丸对手鞠和对其他女忍者有所不同，手鞠因此也会对鹿丸有所优待。最主要的是，手鞠虽然厉害，但是还是一个女人，需要一个肩膀作为依靠，而鹿丸则对手鞠说过，男人不能输给女人这样的话，这也极大的给鹿丸加分，毕竟鹿丸确实是一个优秀的家庭支柱。动漫先生公众号；dongmanxiansheng，长按复制，福', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10271', '0', '不删档新忍情报公开 砂忍傀儡师堪九郎详情', '火影忍者手游在不删档时将会有不少忍者登场，在此期间口袋青瓷不得不说风之国的忍者们，其中我爱罗、手鞠和堪九郎是形影不离的三人组，当然本次游戏也少不了他们，现介绍堪九郎详情，一起来看看吧。堪九郎是风之国砂隐村的上忍，第五代风影我爱罗的哥哥，手鞠的弟弟。他是一名傀儡师，特征是身穿黑色连身装以及涂绘在脸上的紫色状纹，且每次现身总有新的傀儡术。在五影会谈中，勘九郎和手鞠一起担任我爱罗的护卫。第四次忍界大战中，勘九郎作为奇袭部队的队长。技能详情：侵击机关--操控傀儡乌鸦向前快速突进，旋转切割敌人五月雨--操控傀儡乌鸦升空盘旋扔掷爆玉，爆玉着地后将爆炸并击飞敌人黑秘技·机机一发--释放出傀儡黑蚁向前突进，抓捕突进路径上的所有敌人到傀儡黑蚁的身体当中，配合傀儡乌鸦四肢的利刃刺穿敌人，最后引爆傀儡黑蚁重创敌人堪九郎是高级傀儡师，主要以操作木偶进攻敌人，酷炫的状态有没有让你更喜欢他呢？最新最快火影忍者手游攻略以及资讯，只需要在百度输入【火影忍者手游口袋巴士】就可以看见我们的火影忍者手游精品专区。更多内容请关注口袋巴士火影忍者手游专区。 玩 法 攻 略 小队突袭 决斗场 妙木仙境 武斗祭 通灵兽阵容 试练之地 新 手 攻 略 鹿丸连招攻略 难点副本 铜币获取 干柿鬼鲛 迎击判忍 新手快速升级', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10272', '0', '远程傀儡师堪九郎实力解析 技能连招分析', '火影忍者手游C级忍者堪九郎是2月的签到忍者，相信不少小伙伴已经获取了这个新忍者，那么堪九郎的实力如何呢？以及其连招有哪些技巧呢？与小编一起看看详情吧。忍者品质：C级忍者星级：★忍者招募：签到、忍者招募侵击机关：操控傀儡乌鸦向前快速突进，旋转切割敌人技能解析：堪九郎的侵击机关有效的防御了敌人的进攻，从我方周围将敌人弹远，同时切割敌人输出伤害。五月雨：操控傀儡乌鸦升空盘旋扔掷爆玉，爆玉着地后将爆炸并击飞敌人技能解析：堪九郎此技能就如同一个移动的炸弹，在不停的释放炸弹源伤害敌人，同时也让敌人无法近身，此技能可谓是攻防兼备。伤害同时击飞敌人可供普通攻击连招使用哦。黑秘技·机机一发：释放出傀儡黑蚁向前突进，抓捕突进路径上的所有敌人到傀儡黑蚁的身体当中，配合傀儡乌鸦四肢的利刃刺穿敌人，最后引爆傀儡黑蚁重创敌人技能解析：堪九郎奥义技能如同动漫中一样，可以死死的困住敌人，然后多重尖刺伤害敌人，最后引爆傀儡伤害周遭敌人。远程大范围攻击技能。算得是清屏效果技能了哦。从堪九郎以上技能来看，所有技能都属于远程攻击，所以防御类的动作可要做好哦，连招可使用侵击机关+五月雨+普攻。这么使用的目的在于将敌人与我们的距离拉开攻击，在五月雨的炸弹攻击后敌人来不及反映之时使用普通攻击继续输出伤害。连招也可使用侵击机关+五月雨+黑秘技·机机一发。最后配合奥义技能使用，清屏同时扫荡前方所有敌人，并浮空他们，不给敌人连招机会。制造我们跑路的空间，拉开距离继续战斗哦~', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10273', '0', '疾风堪九郎获取途径 快速招募疾风新忍者方法', '火影忍者手游5月19日疾风版本正式见面，那么游戏中的疾风忍者如何获取呢？接下来就与口袋小编一起看看新一代忍者疾风堪九郎的获取方法吧，快速招募刻不容缓哦~疾风传勘九郎获得方法获取途径为精英副本第27关勘九郎可以刷取碎片。有消息称官方在版本更新的时候送的忍者是勘九郎，是真是假就不清楚了。因为还有一种说法送的鸣人，不过碎片的获取方式到时都有。所有的玩家都能花时间得到，并且十连是一定会有的。新版本等级上限调整人物等级能到多少并不清楚，但是刃具的等级开启了传说级别+7，9级勾玉。你见过吗？ 玩 法 攻 略 风杀阵 再不斩 常见忍者连招 迪达拉 决斗大赛阵容 宇智波鼬 新 手 攻 略 杜门李 少年带土 生存挑战技巧 铜币赚取 剖析任务集会所 药师兜', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10274', '0', '故事：真假九郎', '话说南宋孝宗年间，临安府有一秦九郎，同进士出身。夫人邹氏，乃是西台御史邹文渊之女，温良贤淑，与九郎二人恩爱有加。这秦九郎还有个孪生兄弟，唤作秦牧，因痴傻不慧，所以一直养在身边，就连邹氏夫人对他也是甚为关爱。一日，九郎去往友人府中赴宴，因醉酒不能归返。当晚忽得一梦，梦见自己被兄弟秦牧所害，而后他又换上了自己的装扮，将夫人邹氏据为了己有。紧要之处，九郎猛然醒转，虽知是梦，可心里却也忐忑难安。次日天明，辞别了好友，九郎便匆匆忙忙赶回了家中。刚一进门，就瞧见夫人正坐在床边抽泣，九郎心下不解，于是便出言相问。‘夫人为何如此模样？’‘只因昨夜梦见你弟兄反目，你被叔叔他刀斧加身，就连我也是深受其害……直至早起，那梦仍是清晰可见，妾身我心中悲苦，故此才会泪满衣衫……’夫人邹氏，哽咽着说道。九郎闻听顿时是毛骨悚然，心说，这天下真有如此蹊跷之事吗？为何我与夫人所梦，竟是如出一辙？转念再一想，兄弟秦牧虽说痴傻不慧，可平日里很是乖巧温顺，他又如何会做出这等有悖人伦的恶事呢？于是，只好劝慰了夫人几句，见夫人重展笑颜，这才将此事抛在了一边。半月之后，九郎得丈人相助，捐了一个江南西路会昌县的知县。旨意一到，九郎和夫人都是欣喜万分，当即打点行囊，带着秦牧和两个随从，赶往会昌赴任。按理说，功成名就，这本是人生至喜，哪曾想就在九郎赴任的途中，却招来了一桩天大的祸事！这一日，一行人来在了华亭城外，突然天降大雨，九郎见眼前有座古庙，于是便领着众人前去躲避。等进了大殿一瞧，这才发现原来偌大的庙宇竟然空无一人，而且殿内积满了灰尘，似乎已是荒废了很久。九郎与夫人乘坐的是一辆车轿，并未淋雨，可秦牧和两个随从只是骑马赶路，加上那雨来的又急，所以都被淋了个通透。九郎心疼兄弟，见此情景，连忙取出了几套干爽的衣服，打发随从带他去偏殿更换，而自己则是陪着夫人留在了轿内。不多时，九郎和夫人正在轿中说话，猛然就听见外面的随从喊道：‘老爷！您快过来瞧瞧，二爷他不知为何，竟然口吐鲜血！’九郎闻听顿时大急，赶忙由打轿中下来，慌慌张张的便往偏殿跑去。夫人虽说也是心急如焚，奈何叔叔正在更换衣物，她自然也不好过去瞧看。过了大概能有半柱香的功夫儿，这才瞧见九郎满面悲戚的由打偏殿走了出来。‘叔叔他怎么了？是否无恙？’邹氏夫人急切的问道。‘二弟他口中溢血不止，此时、此时、已是暴病而亡……’‘什么！叔叔死了？他怎么会……’惊闻噩耗，夫人顿时也是泣不成声。‘哎！世事无常，阎王叫人三更死，岂会留你五更亡？他自幼便痴傻呆愣，此一去，倒也算得了个解脱。我已打发随从将他抬在院中埋了，还请夫人你莫再悲伤……’‘可怜他也是二十有五，平日里乖巧温顺，这老天又为何会待他如此的不公呢？难道说，前日里的那场噩梦，竟真的灵验了不成？’夫人心慌意乱，不知为何却突然想起了前日的那个梦来。‘噩梦？如何的一场噩梦？’夫人本是无心之语，可再瞧九郎却似乎对此并不知晓。见九郎不解，邹氏夫人也是一愣，心说这才过去了几日，你怎么就将此事忘得如此干净？刚想开口，可猛然就觉得浑身一冷，紧接着，一个荒诞的念头突兀浮现在了脑海当中……该不会真的就如那梦中的一般吧？想到此处，夫人邹氏不由得遍体生寒，紧退了两步，上上下下，仔仔细细的又打量了九郎几眼，良久之后，却发觉眼前的九郎似乎并没有任何不妥地方。‘夫人，你这是怎么了？’九郎瞧见夫人神情有异，连忙问了一句。可他话音刚落，就见那两个随从，湿漉漉的由打殿外跑了进来。‘二、老爷，我二人已将二爷的尸身掩埋，如今这雨也停了，不知我们要何时上路啊？’随从开口说道。‘即刻便走。’‘慢！方才你分明是想喊二爷，见我这才猛然改口，快说，你们到底是做下了什么？’夫人心中本就疑惑，那随从的话语当中更是露出了蛛丝马迹。而且又一想到九郎对自己的这个弟弟，可以说是视若珍宝，即便死在了半路途中，又岂会草草掩埋，不管不顾？这种种的迹象足以表明，眼前的这个九郎一定不是自己的丈夫，而是与他长得一般无二的孪生兄弟秦牧！‘夫人此言何意？’九郎似乎还是一头雾水。‘秦牧！事到如今，你还要装到几时？’‘你！你看出来了？’俗话说：做贼心虚，见邹氏夫人声色俱厉，再瞧九郎，果然是变了一副嘴脸。‘你将九郎他如何了？’‘不是说了已经埋了吗……’‘你你你！’‘嫂嫂是想问我为何会杀死兄长，还是想问我又为何会不再痴傻了呢？’‘……’‘嫂嫂莫要动怒，你可知我又如何会又呆又傻？’见夫人气的浑身颤抖，秦牧这才似笑非笑的继续说道。‘也无非是先天不全！’‘嫂嫂错了，我之所以装疯卖傻，皆是那秦九郎所害！在你未过门之时，我与府中婢女私好，一日酒醉，错手将那婢女打死，埋在了花园当中。秦九郎不知为何竟知道了此事，他先是将我一顿毒打，随后更是要扭送我到府衙。我不得已这才装作呆傻，逃过了此劫！’‘你与婢女通奸有染，不能与她名分也就罢了，竟然还将她亲手杀死，这简直就是禽兽不如！’夫人自然听得出来是非对错，可那秦牧却是无动于衷，而是接着说道：‘如今他功成名就，可叹我与他同日所生，却要装傻保命，你说，像这等绝情之人，我该不该杀？今日与两位随从将他杀死，一是报了大仇，二来我也可顶替他的功名，前去会昌赴任。本想就此以他的名姓，与嫂嫂欢好百年，岂料竟被你瞧出了端倪。事已至此，多说无益，但不知嫂嫂又打算何去何从呢？‘言下之意，邹氏就此顺从，那她还是秦九郎的夫人，即便此九郎已非是彼九郎。可要是不答应，恐怕也是万难逃得活命。’秦牧！像你这等禽兽不如的畜生，还想要我委身于你，简直就是痴人说梦！九郎与我情深意重，如今他命丧你手，我又岂能能独活。待等我夫妻下的地府阴曹，见了那十殿阎罗，化作厉鬼，再来向你讨个公道！‘话音未落，就见邹氏夫人猛然转身，一头便撞在了身后的香炉之上，当场是脑浆迸裂，气绝身亡。秦牧见邹氏夫人如此节烈，脸上也不仅有些动容，又吩咐两个随从将那尸首埋了，随后这才装作无事一般，离开了古庙，奔着会昌而去。简短皆说，秦牧来到了会昌，如愿的做上了知县。他本打算瞒过邹氏，带她一同前来赴任。可如今邹氏身死，自己堂堂的一任知县，没有个家眷，与情理上也说不过去。所以到任不久，他便打算寻一个美貌的女子，迎娶过门。刚刚生出了这个念头，忽然就瞧见跟自己一起前来的那两个随从跑了进来。’二爷，不知是谁在大堂上留下了一张喜帖，我二人未敢拆开，特拿来与二爷查看。‘说着，随从将一张大红的喜帖递到了秦牧的手中', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10275', '0', '火影忍者手游试练之地堪九郎+乌鸦过法解析', '火影忍者手游试练之地走至后期开启难度增加，同时守关BOSS也在上升，今天口袋青瓷介绍的就是试练之地堪九郎的过法哦，还在此关卡不能快速通过的小伙伴可以一起商量对策哦。战力推荐：11000及以上；忍者推荐：我爱罗堪九郎分析：本关卡是由堪九郎及一群小boss守着，同时还有堪九郎的傀儡乌鸦在场哦，乌鸦在半空发起攻击，堪九郎在地面攻击。过关技巧：因为本关卡的堪九郎与乌鸦同时出战，我们应该选择远攻为妙，并且要多加防范乌鸦随时出暗器发起攻击，与堪九郎保持同一线上，然后将已有技能释放对堪九郎造成伤害，进而无法控制傀儡乌鸦，后近战体术对其发起攻击，当技能有冷却的就请愉快的释放吧，连续攻击效果比较明显哦。特别提醒：注意傀儡乌鸦的攻击！！！最新最快火影忍者手游攻略以及资讯，只需要在百度输入【火影忍者手游口袋巴士】就可以看见我们的火影忍者手游精品专区。更多内容请关注口袋巴士火影忍者手游专区。 初入火影 每日必做 新手成长 二测下载 电脑版玩法 VIP特权 熟悉火影 秘宝系统 天赋系统 排行榜 一乐拉面 通灵系统 秘卷系统 挂机战斗 试练之地 武斗祭 丰饶之间 进军火影 组织玩法 小队突袭 连招技巧 快速升级 通灵养成 三星副本 铁血凯 扫荡技巧 武斗祭阵容 卡卡西', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10276', '0', '2月签到忍者堪九郎视频及技能操作心得分享', '火影忍者手游2月签到忍者堪九郎，相信小伙伴们都已经获得了，今天口袋小编就带来堪九郎实战视频以及相关技能介绍，感兴趣的小伙伴可不要错过了哦堪九郎是风之国砂隐村的上忍，第五代风影我爱罗的哥哥，手鞠的弟弟。他是一名傀儡师，特征是身穿黑色连身装以及涂绘在脸上的紫色状纹，且每次现身总有新的傀儡术。在五影会谈中，勘九郎和手鞠一起担任我爱罗的护卫。第四次忍界大战中，勘九郎作为奇袭部队的队长。技能详情：侵击机关--操控傀儡乌鸦向前快速突进，旋转切割敌人五月雨--操控傀儡乌鸦升空盘旋扔掷爆玉，爆玉着地后将爆炸并击飞敌人黑秘技·机机一发--释放出傀儡黑蚁向前突进，抓捕突进路径上的所有敌人到傀儡黑蚁的身体当中，配合傀儡乌鸦四肢的利刃刺穿敌人，最后引爆傀儡黑蚁重创敌人堪九郎是高级傀儡师，主要以操作木偶进攻敌人，酷炫的状态有没有让你更喜欢他呢 玩 法 攻 略 风杀阵 再不斩 决斗场赛事 夕日红 通灵兽 S级忍者对比 新 手 攻 略 少年卡卡西 少年带土 生存挑战技巧 铜币赚取 带土PK卡卡西 药师兜', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10277', '0', '火影忍者：静音能秒杀兜吗？若不是纲手叫停，兜怕要挂了，兜很怕', '静音是纲手的手下，兜是大蛇丸的手下，同是三忍之一的手下，兜为什么差距那么大？静音和兜开打时候，是大蛇丸杀了三代残废了双手，双手被诅咒，废了。如是大蛇丸带着兜去找纲手医治。纲手那个时候在短册街，成天只知道赌博，早已不问火影俗事了，而且还弃医从赌了，可想纲手那个时候是多么堕落。大蛇丸找到纲手后，见面就没有说好听话给纲手，大蛇丸就这幅德性，哪怕找人家帮忙，都不会说漂亮话。大蛇丸的话激怒了旁边的静音。如是静音就先发制人，冷兵器飞了过去，兜连忙上前挡住。可静音速度太快了，快的惊人。兜都不得不佩服纲手的手下。静音一招之内，便刺向兜的要害。倘若不是纲手叫静音冷静下来，恐怕兜这个时候就没命了，大蛇丸是个废人，肯定救不了兜。纲手为什么不让静音杀了兜，这让人很不解。还有纲手知道大蛇丸杀了三代，为什么还要帮大蛇丸医治手臂？难道就是为了弟弟吗？她不知道大蛇丸是骗他的吗？她不知道大蛇丸是有多么的坏吗？医治好大蛇丸的手臂，多少无辜生命又要惨遭大蛇丸之手啊？纲手那么自私自利，怎么就当上火影？真想不通。后来大家都知道纲手并没有答应大蛇丸，而是独自一人战斗，并且把自来也给稍弱了。再后来鸣人的出现，让纲手重新找回自己，不然纲手继续堕落。那么静音到底是不是兜的对手呢？兜如果不吃药的情况下，估计静音可以打败兜，兜就是喜欢开挂，吃军粮丸。吃了军粮丸兜能虐三忍，把纲手都打的不要不要的。可见火影忍者里的军粮丸的威力，堪比开挂。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10278', '0', '仙人掌科的奇葩，星兜', '属 名 星球属 属于仙人掌目仙人掌科，别名：有星属星球属又称有星属 (Astrophytum)仙人掌科中的一属 ， 原产墨西哥。本属仅5种，但变种及杂交种很多，由于全株被有白色星状小点，故称为星球属或有星属。具有2-15棱，形态呈扁球状至圆筒状，也有种类呈龙舌兰牡丹状，棱少，表皮簇生丛卷毛，即通常所谓的\"星\"。刺座具绵毛或刺，顶部近中央处开黄色或黄色红底大花，观赏价值很高。原产地 墨西哥品 种 1、兜 2、鸾凤玉3、瑞凤球4、般若5、美杜莎园艺品种 碧方玉、波托西鸾凤玉、超兜、恩冢四角鸾凤玉、花园兜黄刺大凤玉、鸾凤阁、裸般若、 瑞凤玉等。养 护 星球属类品种喜光，可全日照栽培，秋冬放置在向阳的窗台夏季高温时，适当遮荫防止灼伤。生长温度18-30°左右，冬季最好保持在5°以上。4-6月为生长盛期，7-10为生长期11-2月休眠期。换盆一般4月初以后进行。培养土需疏松透 气，排水良好，保水性强为好。易浅植，不宜深植。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10279', '0', '奇葩多肉“兜”', '第一次听到兜这个名字，是从一位带着浓厚地方口音的老花农那儿，他把这家伙叫作“斗儿”，后来我开始到处查兜的资料。当时的动机并不是研究它怎么养，而是特别想要知道这个家伙为什么会被叫作“兜”。关于兜的最古老记录关于兜这个名字的来历，最靠谱的答案来自于一个台湾的仙人掌讨论区：兜这个字在汉语里的古义是武士的头盔，后来才被用作口袋的意思。现在的日语里，兜依然还是头盔的意思。兜是日本人从南美引进的，所以就根据它的外观起了这么一个名字。兜的原产地在墨西哥。这种肉肉在原产地远没有我们想象的光鲜亮丽。她们把大多数球体都埋在土壤里，只留下很小的一部分露出土表。在干旱少雨的季节，她们会变得干瘪，最严重的时候，她们甚至会完全缩进土壤里去，来躲避恶劣的气候。关于兜的最古老记录是在1842年。当时，身为探险家的凯文斯基男爵在墨西哥探险。他在深入塔毛利帕斯州的时候发现了兜这个奇怪的物种。根据凯文斯基撰写的回忆，当时他正在荒野中跋涉，这时候突然刮起一阵狂风，吹走了地表的一片浮土。一个神奇的植物就这样在他眼前浮现了出来。当时他甚至一度认为这是被人们故意埋藏的宝物。唯一的兜样本死在了慕尼黑这棵神奇的植物被小心翼翼地挖出来，并在1843年送到了德国慕尼黑植物园养着。1845年的时候，兜终于有了自己的名字，并且被划分到了仙人掌科的金琥属。直到1868年，兜才被正式划归到现在的星球属中去。很多花友都有一种错觉，就是肉肉们在她们的原生地到处都是。其实并不是这样。兜的发现者凯文斯基在墨西哥探险期间，就只发现了兜的唯一一个样本——没有第二个，你就知道兜在原生地有多么稀少了吧。而且，最不幸的事情发生了。1850年，这个唯一的兜样本在慕尼黑植物园死亡。很多植物学家听到这事眼睛都红了，四处募集探险基金，然后派人再次远赴墨西哥原生地想挖一棵兜回来。然而事与愿违，植物学家们把塔毛利帕斯州翻了个底朝天也没找到第二个兜。如果你家窗台上有一个兜，而且你有机会在90年前把这个兜拿出来卖的话，估计你可以直接完成一个亿的小目标了。因为植物学家们足足找了70年也没在墨西哥找到第二个兜。不用担心兜再消失就在人们怀疑兜是否真的在地球上存在过时，奇迹发生了。一名叫伏雷克的植物学家声称在墨西哥再次发现了兜，而且足足有上千棵之多。于是欧洲人立即乘船奔赴墨西哥，争夺这些新发现的兜。而墨西哥人也不傻，他们立即启动了濒危物种保护法案，禁止任何人把兜带出墨西哥本土。禁止出口并没有遏制住人们寻找兜的热情，虽然后来在墨西哥的其他地区确实零星发现了野生的兜，但数量实在太少了。1967年，有人声称在美国德州的斯塔尔发现了数量不少的野生兜的亚种，但是这些亚种始终都没被植物学家所认可，但德州最终还是成为了除了墨西哥以外的唯一一处有野生兜生长的地方。现在，这片区域也已经被保护起来。在美国，私自采挖野生兜是严重的违法行为。1982年，在日本的一个园艺博览会上，展出了几颗品相完美的兜。这是兜首次在园艺博览会上以可售商品的形式展出。随着收藏者的加入，我们再也不需要担心兜会从地球上消失了。现在，品相漂亮饱满的兜成为很多花友追捧的品种。在中国，兜象征着圆鼓鼓的口袋，还有着招财进宝的良好寓意，这让很多非园艺爱好者也开始追捧它。（文/呜莎 刊于燕赵都市报2017年11月26日第14版）', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10280', '0', '有些兜不住', '暴露狂吊带就算暴露也喜欢啊，夏天嘛就要清清凉凉，一阵小飘逸这个面料呢是肯定有点透的，里面需要贴乳贴，不是那种聚拢的哦，就是两个小小片的这个面料是自带褶皱的不会很普通，而且是斜裁，这样面料就不会贴在身上很尴尬再加上抽绳一系，胸部位置面料更宽松一点，所以这件衣服不是平胸专属，我反而觉得微大的胸部更好看哦觉得太露的时候肩带可以调节，也可以披一个小开衫最喜欢的是后面的大露背，加上那圈松紧版型更好看，超大面积的露背其实更显瘦一点买家秀走起~~~', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10281', '0', '兜转不出的人生', '曾今的跌跌撞撞，只为把心中的向往，披上彩色的衣衫；如今的寂寂壤壤，只为把染色的过往，褪去彩色的渲染；用尽全力勾勒出的，却不是曾经向往的世界；拼尽心力倾注来的，却不是渴望已久的心想；面对的，不是彩色的辉煌，而是褪去色彩的沧桑。兜兜转转，转转兜兜，兜转出的人生，却回到了原点。留下的，是忘不掉的划痕，带走的，是染不上的色彩。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10282', '0', '李若彤红色长裙美出新高度，上半身这一细节惹争议，网友：兜不住', '李若彤红色长裙美出新高度，上半身这一细节惹争议，网友：兜不住！近日，李若彤亮相出席某个活动，穿着的是抹胸长礼服，红色的蛋糕长裙，衬得李若彤整个人都是很好看的，手中拎的包包也是红色系，不少网友打趣道，李若彤这身红裙子的颜色真的好想最近上市的杨梅！不过李若彤的上半身胸部的地方貌似有点儿“满”，这一细节也是惹起了争议，网友纷纷调侃李若彤：虽然瘦，但是身材好，兜不住！李若彤的颜值是很高的，在以前的时候穿着这样的穿搭，不管是换成谁的脸都是很容易显得难看的，但是李若彤真的用颜值撑起来这一套穿搭，让人把注意力全都放在了她的脸上！看来李若彤是真的非常适合红色，这一身红黑色为主色调的衣服，让李若彤看上起非常清新，而且红底的背景也是没有很奇怪，反而很适合！李若彤出街的时候，穿着玫红色的卫衣，这种卫衣颜色一般人穿起来都会显得气色不好，但是李若彤本来就算是比较白的，也能很好驾驭，看上去后面是自己的女儿抱住了李若彤，李若彤也是一手抓着孩子，一手扶着，看上去非常幸福。很多人说李若彤前两年复出之后拍的电视剧，真的显老，但是尽管如此，她的颜值真的摆在那里！现在很多二十来岁的年轻人都没有四五十的李若彤好看！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10283', '0', '吃不了“兜”着走', '吃不了“兜”着走，是珲春市河南街道职工食堂发起的志愿活动，已经成为一道亮丽的风景线。这个食堂只有每天中午一顿饭，四十余人在此就餐。因为是自助餐式的食堂，每餐结束后都会有些许剩余的饭菜。从今年三月份开始，河南街道的志愿者自备了4个多层饭盒，每天午餐结束，都会有人用饭盒装好剩余的饭菜，送给居住在永盛社区的生活困难的残疾人、低保户及孤寡老人。钟阳现年27岁，患有脑瘫，手颤抖得厉害，靠低保生活。原来奶奶与他一起生活，照顾他的饮食起居。可奶奶去世后钟阳独自一人生活，有时候甚至端一杯水都会洒，更别说做饭了。志愿者们把打包好的剩余饭菜送给钟阳后，解决了他生活中的一大难题。金香美患有智力二级残疾、脑瘫，她的母亲患有糖尿病、心脏病、哮喘病。母女二人疾病缠身，生活拮据，一直是河南街道志愿者们的重点扶持对象。当母女二人收到志愿者送来的热乎乎的饭菜时，都非常感激。同样是志愿者重点帮扶对象的老人郭成云说：“我八十岁了，自己一个人生活，实在是不愿意做饭，也做不动。有时候饿极了就做点疙瘩汤糊弄一口，对付吃饱了肚子就行了。现在社区的志愿者们常常给我送好吃的饭菜，我不但省钱还省事，谢谢大家关心我。”河南街道的志愿者表示：“对于需要帮助的人来说，这些剩下的饭菜可以让他们填饱肚子、改善伙食，希望能有更多人在饭菜有剩余时不要扔掉，花几分钟装好，送给需要的人，和我们一起，吃不了‘兜’着走。”作者 卜丽峰编辑 于悦', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10284', '0', '白骨―君麻吕，用生命起舞', '我们知道，君麻吕是辉夜一族的，有些强大的血继限界，有人称之为尸骨脉，然而并非如此。 君麻吕是辉夜一族的血继​君麻吕的血继限界是自由操纵骨芽细胞和破骨细胞,甚至能够控制钙的浓度,老生成骨头,骨头的数量并非像正常人那样200多跟骨头,而是不定数,君麻吕虽然出场时间不多,但是给人却留下了很深的印象,很多人都为他的实力感到惊讶.其实他的实力不仅如此,在出场的时候也提到过了,那时的君麻吕是依靠精神力进行战斗的,而在他的回忆中又有暗杀风影那一段,如果他真的是没有咒印状态的话就能与4代风影战斗吗?那么如果他没有得病的话实力会不会更强呢,不得而知.​身为辉夜一族最优秀(也有一种翻译叫竹取)的他,具有就连辉夜一族也会为之动容的力量,那种稀有的血继限界让每一个人都想拥有,包括大蛇丸在内.拥有强硬的骨骼,让所有物理攻击都难以接近的防御,一旦开始攻击,骨头就会化成最强的矛.​身为体术高手的他,以骨头当作武器,而他的骨头不是普通的骨头,有着高密度的骨头如同钢铁般坚硬.并且拥有着五种舞柳之舞:将骨头作为攻击武器,并攻击处于一条直线上的对手,攻击破坏能力极强.椿之舞:攻击速度比普通的物理攻击快上数倍.唐松之舞:身体进行旋转,同时将露在身体外的骨头随着身体的高速旋转攻击对手.铁线花之舞:铁线花之舞-蔓:将骨头当作绳子捆住对手​ 君麻吕是大蛇丸最忠诚的手下，也是有着地之咒印的男人，可惜得了绝症，他毕竟是亲手杀死了罗沙的人，重吾最崇拜与欣赏的人。欢迎下方评论。关注：烊炀动漫', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10285', '0', '《博人传》最新一话，川木真实身份揭晓，能力类似君麻吕！', '在《博人传》之前的剧情中，博人和木叶丸等人遇到了昏迷的川木，并且发现了川木手上和博人一样都拥有名为“楔”的印记。在最新一话中，川木在昏迷之中回忆起了自己的曾经，原来他是被他的父亲卖到“壳”组织的，从剧情中可以看出川木的亲生父亲看起来就是一个普通人，而且他还经常虐待川木，把川木当成沙包来打。在川木父亲知道川木卖给对方可以获得重金的时候，更是喜不自胜，还称对方是上天派来的神明，从这里可以看出川木的父亲对于自己的这个儿子可以说是毫无感情，这其中又有什么隐情呢？当时的川木虽然看起来似乎已经昏迷，但是他还是看到了父亲的丑陋嘴脸。在川木被卖掉之后，买走他的人也露出了自己的真面目，从他的服装看应该就是“壳”组织的成员，而且在他下巴那里同样有一个类似“楔”的印记。并且这不是乾第一次出现，在之前的剧情中，“壳”组织开会的时候，这个乾就登场过（就是坐在椅子上那一位），而且他似乎是“壳”组织的领导阶层。之后昏迷中的川木开始恢复了意识，在恢复意识之后，川木看到博人一行人以为他们是“壳”组织的追兵，还使用了一招类似于《龙珠》爆气的招式。虽然之后木叶丸像川木解释了自己一行人是木叶村的忍者，但川木似乎不怎么相信，于是博人亮出了自己手中的“楔”印记。看到博人手中也有“楔”之后，川木更加觉得博人等人就是“壳”组织来抓捕他的追兵，并且摆出一副随时想开干的姿态，但是当他们之间还没有打起来的时候，真正的“壳”组织追兵来了。一见面他就表明了自己的目的，是要来回收“器”的，也就是川木便是“壳”组织所谓的“器”。并且他在看到木叶丸等人之后也认出了他们是木叶村的忍者，也知道青已经失败了，最后他还说了一句真给“外阵”抹黑，看来他和青一样都是属于组织“外阵”的成员。而川木也认出了来抓捕自己的“壳”组织成员，他的名字叫做“我娄”，从两人的对话中可以看出，他们彼此是认识的，而且我娄下巴的伤也是拜川木所赐，可以说是冤家路窄。两人随机就打了起来，我娄利用手中的科学忍具攻击川木，而川木也展示了他的能力，这种能力似乎和《火影忍者》中君麻吕的能力有些类似，应该也是利用骨骼来攻击。你们觉得川木的这个能力是否是血继限界呢？欢迎在下方评论区留言。如果你喜欢这篇文章，别忘了点赞、收藏、分享支持一下小编，你们的支持就是小编的动力。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10286', '0', '君麻吕没有打醒小李醉拳，肯定会被小李醉拳击败？', '小李偷了一瓶酒后便去找君麻吕单挑了，自以为曾经喝了一滴酒就牛逼的很，凯和宁次两人才制服了他。小李想，要是喝一瓶，那不天下无敌了，就连纲手都认为小李要是喝了一瓶，那就强到无敌了吧。后来小李找到了君麻吕，并成功的救下了鸣人。在没有喝酒前和君麻吕打，伤不到君麻吕半根毫发，如是中途喊停，对不住，我该吃药了，不吃药的话我会很惨。没想法君麻吕还真允许了。要知道在敌对敌的情况下，让敌人喘口气休息，那简直是自取灭亡啊，不过君麻吕自认为自己太强大，面对小李，料他也耍不出什么大招，你要吃药就吃吧，反正你也打不过我。此时的君麻吕就好像《龙珠超》里的比鲁斯一样，自认为对手太菜，怎么使诈，都打不过自己。此时的君麻吕，就是这样的，他不屑小李吃什么军粮丸，或是更牛逼的药丸，君麻吕照样是把小李打的屁股开花。事实证明小李吃的不是什么军粮丸，也不是什么药，而是酒，小李喝了一瓶就自认为可以飞起来打君麻吕了。小李喝酒后，招数变幻莫测，速度快的惊人，确实跟之前没喝酒的小李判若两人。而且君麻吕都快招架不住了，被逼得使出了咒印血迹，才把喝醉酒的小李打醒。如果当初君麻吕要是没有把小李醉意打醒，估计君麻吕也是伤不到小李半根毫发的。小李喝醉酒后，身上几乎长了千百个眼睛。看似喝醉，实则很清醒地能躲过任何攻击，看似睡着了，实则比谁都清醒，怎么打都打不中。这就是醉拳的最高境界，小李自己说的。估计君麻吕的挂也就是血迹了，君麻吕如果没有开血迹的话，那估计也不是小李的对手。逼近小李的醉拳是火影忍者里独创的，凯都不会，是小李自己开发的。凯都说起小李的醉拳，都怕的要命，威力太强大了。要知道小李打君麻吕的时候，还不是满状态，因为身体刚被纲手治好，还不敢乱来，就连八门遁甲只敢开一个门，可开一个门的八门遁甲没想到都踢不破君麻吕的骨头，都踢不断君麻吕的骨头。小李很惊讶，这到底还是不是骨头？君麻吕的骨头，可是血迹，是强大的血迹查克拉骨头啊，也不是一般的骨头，要是一般的骨头，估计早被小李踢的粉碎。所以小李很惊讶，小李惊讶之余被君麻吕反伤了。最后我爱罗出场了，救下了小李。如果我爱罗不出场，小李也就是一个死吧。招数不能开到最满，八门遁甲都不敢再乱用，醉拳已经醒了，没有酒了，小李就是等死啊。如果小李是满状态下，君麻吕那还不一定打得过小李。毕竟小李也不弱，体术是超一流的。满状态真的可以跟君麻吕一拼呢，就像当初打我爱罗一样，虽然最终被我爱罗打残。但是君麻吕要打残小李，我看未必呢，因为我爱罗最后把君麻吕收拾了，显然我爱罗在君麻吕之上。如果小李满状态打君麻吕，这场战斗谁输谁赢，还真不好说呢，小李只要满状态，不要酒，不用醉拳，估计君麻吕就很难对付。如果喝一瓶酒，那君麻吕绝对的输。因为小李不是满状态喝了酒就逼得君麻吕开了血迹，可想小李的醉拳，还是很牛的一招。特别是让君麻吕很无奈不知道小李的出拳招数，变化多端，而且还可以出奇制胜般的还击。君麻吕自认为看到小李破绽，实则小李估计让出的虚招，然后反击，让君麻吕很是头痛。所以说如果小李醉拳没有被打醒，君麻吕不是小李的对手。如果小李是满状态，君麻吕更不是小李的对手。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10287', '0', '杂谈：血继限界“尸骨脉”的拥有者——君麻吕！', '第一次看见君麻吕，感觉厉害，但是后面你们可能觉得很差，所以我解析一下君麻吕的实力. 君麻吕患有疾病，实力下降很多，被兜转生后恢复了真正的实力·兜转生了很多强者，但是最后都被打败了，千代婆婆被打败了，算是出局。 这下唯一靠实力没被封印的有3个，君麻吕――斑爷――鼬神，所以君麻吕实力不错。 3代雷影这么厉害，和鸣人打这么久，你们都知道吧，3代雷影是和忍者军队打了3天死亡了，君麻吕和这么忍者联军打了3天的几倍都没挂，外加鸣人这么多分身（还靠身体接下了螺旋丸手里剑）可以靠身体接下这个的就只有君麻吕和3代雷影了，斑爷都接不下。从这里可以知道君麻吕胜过3代雷影，到最后他咒印都没开。可见君麻吕的实力胜过N多人.这才是君麻吕的真正实力。希望你们喜欢我的解析。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10288', '0', '火影忍者君麻吕有多强？和卡卡西比呢？', '辉夜一族血继限界\"尸骨脉\"的拥有者，被大蛇丸看中，一心为大蛇丸效命，后因不治之症，在与我爱罗的交战中病发而亡。第四次忍者大战被药师兜秽土转生，后被宇智波鼬解除秽土转生，灵魂升天年幼时被族人视为人肉盾牌兼武器而软禁起来。后辉夜一族进攻雾隐村，君麻吕这个秘密武器也被释放出来参与战斗。结果全族被灭，由于君麻吕有病在身因而无法参战，他才捡回一条命;唯一幸存的君麻吕被大蛇丸抓去，并受到大蛇丸的教养而成长，因此极为崇拜大蛇丸，甚至到了疯狂的地步。他也乐意成为大蛇丸转生的容器。君麻吕之后从大蛇丸那里得到\"地之咒印\"，能自由地操纵骨芽细胞和破骨细胞，还能控制钙质浓度形成攻击的武器，并且体术强劲君麻吕的战绩有：1.协助大蛇丸杀死四代风影，2.一对四打爆音忍四人众，3.轻松制服狂暴状态的重吾，4.病入膏肓的情况下，常态击败醉拳小李，咒印第二状态差点干掉我爱罗根据者之书，君麻吕的各项基础能力值总和为61，与再不斩相当，高于多数上忍。再加上君麻吕罕见的血继界限，以及地之咒印的加成，其水平完全达到了精英上忍级别', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10289', '0', '火影忍者：大蛇丸团伙之腹黑女多由也', '大蛇心腹，音忍五人众之一，是个女孩子，却性格好胜、不服输，擅长幻术和通灵术。最大的特点是毒舌，总是说脏话，经常和其他人吵架。早年过着艰辛的生活，经常被拿来做实验。直到后来脱颖而出，成为大蛇丸最强最忠心的手下之一。曾协助大蛇丸对战三代火影。在佐助夺还篇时与鹿丸对战。为了防止鹿丸的影子模仿术而通灵出三鬼，利用魔笛操控攻击。​但不久被鹿丸破解了通灵术。 用拿手的影子模仿术抓住她，并且以影首缚之术和她纠缠。​但是多由也状态二的却强到以自己的力量挣脱束缚。最终在快成功杀死鹿丸时，手鞠及时赶到，被其利用通灵·斩斩舞杀死。 几年后，兜将鬼童丸的细胞移植到自身体内，并获得了他的能力。笛子使用幻术的必需品。吹笛子可以迷惑敌人，咒印状态二时还能吹奏十种乐曲用幻术麻痹对手。​咒印状态一使用时身体会浮现一些奇怪的符文，自身的各项能力包括查克拉在内会比原来强大得多。咒印状态二属于地之咒印，皮肤会变黑，头发会变长，眼白也会变成黑色，身体会长出一些角。通灵术B级忍术，多由也的通灵兽是“三鬼”，擅长体术攻击，一般用笛声控制行动。三鬼里分别是：一只拿着粗大的木棒；一只用骨爪；一只上半身包括头也被绷带包住，没有手臂，使用“背撞”攻击。多由也可以配合他们使用“魔镜之乱”吸收对手查克拉。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10290', '0', '谁说免费没好货 火影手游多由也简单分析', '相信很多玩家都十分期待多由也这一个角色，火影忍者手游多由也到底如何呢，相信很多玩家都对于这么一个角色不太了解，而本次小编则为大家带来关于多由也的详细分析，希望能够帮助到各位玩家认识这个强势的角色，感兴趣的玩家可是千万不要错过本次的精彩内容。终曲第九章 魔乱之镜多由也演奏底子吹出鬼魂向前方攻击。通灵术 怒鬼多由也通灵随机召唤出三只怒鬼其中一只对敌人发起攻击，不同的怒鬼拥有不同的攻击方式。魔笛 魔幻音锁多由也进入咒印二阶段演奏敌人召唤出三道灵魂。灵魂会将敌人击退并包裹，最后爆炸。从技能和视频上来看，多由也绝对是C忍之中的最为强势的存在，强势技能魔乱之镜远程命中率高，怒鬼释放之后人物可以移动，既能够作为攻击，也能够作为骗替身的强势之选，相比之前的几位音忍，多由也应该能够带来更有趣的内容体验。希望本文能够对于广大的玩家有着一定的帮助价值，也希望大家都能够继续支持火影忍者手游魔方网，小编也会为大家带来更多的精彩内容。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10291', '0', '人妻娜，本子娜，刀剑神域亚丝娜最新图片壁纸，还是你的亚丝娜呦', '大家好，我是木子，木子李的木子。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10292', '0', '刀剑神域：结婚后亚丝娜猜测桐人比她小，然后露出了这种表情！', '大家应该都知道亚丝娜喜欢桐人，但可能有一些人并不知道其实桐人比亚丝娜还要小。按照划分来说的话，亚丝娜是弟控。在《刀剑神域》SAO里，桐人和亚丝娜在游戏里结婚了，当时两人生活在第22层，他们在那里买了房子，两人睡在一间房子里，不过床是分开的。某一天早上亚丝娜趴在桐人床边看着桐人的睡姿，亚丝娜说桐人平时是那么地目中无人，但其实挺可爱的，甚至有点孩子气。其实从这些话里，可以听出亚丝娜对桐人的评价。看来在亚丝娜的眼里，桐人是一个很任性又目中无人的小孩子。小透露：亚丝娜第一次见桐人的时候，是在第一层，当时他们在一起开会讨论BOSS攻略，之后两人组队，晚上桐人送奶油给亚丝娜。第二天他们两人和其他人参与BOSS攻略，打败BOSS桐人成为了封弊者，很是目中无人。当亚丝娜想到“孩子气”的时候，突然在内心猜测，桐人不会比自己还要小吧，如果真这样，亚丝娜可是桐人的“姐姐”。话说小伙伴们是姐控也是妹控呢，或者兄控还是弟控？然后亚丝娜露出了这么一个表情，惊奇中带了一点羞涩，脸上还有几道红红的线条，在亚丝娜心里，也许桐人真的比她小，因此才这么的孩子气吧。话说大家觉得这个表情应该如何描述，笔者感觉有点词穷！欢迎会描述的人留下你的描述语句，分享给其他人。不过亚丝娜这个表情只是出现了一会儿，然后就恢复了。喜欢一个人的话，就不要在乎他的年龄大小、身份地位，只要自己喜欢就行了。亚丝娜此时伸手摸着桐人的脸，心中充满了爱意。其实亚丝娜猜对了，在SAO结尾的时候，亚丝娜和桐人相互交换了真实姓名，桐人真名为桐谷和人，年龄16岁；亚丝娜真名为结城明日奈，年龄17岁。亚丝娜比桐人大一岁。大家可以说亚丝娜其实是一个弟控。这一次主要是分享亚丝娜和桐人在SAO里的生活故事，当时亚丝娜猜测桐人比她小的时候所露出的那种表情。如果大家喜欢亚丝娜的话，记得给笔者点赞支持哦，因为笔者也很喜欢这个妹纸。同时，觉得文章不错的话，记得分享给其他漫迷们哦。笔者今后会为大家带来更多的动漫。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10293', '0', '刀剑神域：婚后的亚丝娜，满满的人妻属性！', '大家好，欢迎来到麦麦的壁纸板块，本期推荐的壁纸人物是刀剑神域里的结城明日奈。关注麦麦，跟你一起探寻二次元的美好。结城明日奈，也被大家亲切的称呼为亚丝娜，或者是本子娜。当然原因也就不说了，相信看过刀剑神域的小伙伴都知道。本子娜的属性暂且不说，亚丝娜本身具有的人妻属性，在遇到桐人以后展现的淋漓尽致。SAO的那两年里，从最开始的害怕到躲进宾馆，到后来主动站出来挑战自己并且成长为一个独当一面的主战力，当中的心里路程已经不可考。但是在遇到桐人以后，并且在一起生活了两年，亚丝娜的人妻属性技能被点到了满级。作为结城家的大小姐，亚丝娜从小就被父母安排好了人生，也被赋予了莫大的期待。而发生SAO事件之后，亚丝娜改变了想法，在游戏里彻底的释放了自己的天性，在跟桐人两年的羁绊中，喜欢上了他并且愿意陪伴他一生。有这样的女神陪伴着，桐人真的是很好的诠释了幸福这个词。不管是在SAO里还是从SAO里出来之后，亚丝娜都永远的相信着桐人。放弃了家族安排的所有精英路线，选择用自己的一生来陪伴守护着自己最爱的人。这样的亚丝娜，又怎么不让我们爱着呢？脱下战斗服，回归日常的亚丝娜，给人更加平易近人的感觉。没有了战斗时的高冷，退却了战斗时的锐气，剩下的，满满都是温柔，都是对这个世界的爱。亚丝娜最温柔的时候，还有面对结衣的时候。不仅在游戏里将结衣照顾的很好，还想把结衣带到现实世界中来。而她和桐人，作为结衣的养父母，也一直尽职尽责。不得不说，母性光辉散发的亚丝娜，更让人喜爱呢。那么，本期壁纸就到这里了。欢迎留言给麦麦你想看的壁纸，麦麦会在接下来的日子里，陪你一起找寻这些美好的世界。关注麦麦，跟你一起探寻二次元的美好。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10294', '0', '你最爱哪一种？亚丝娜女神的各种形态', '你最爱哪一种？亚丝娜女神的各种形态。因好奇借用哥哥结成浩一郎的NERvGear进入SAO的新手，并用真名取游戏角色名字。直到认识“活在”SAO里的桐谷和人后改变想法，也因此喜欢上桐谷和人并在游戏里结婚。SAO中的亚丝娜亚丝娜最经典的形象，绝对领域真的超棒啊！ALO中的亚丝娜精灵尖尖的耳朵，配合蓝色的长发，真的美如画。校服亚丝娜穿着校服的亚丝娜显得非常的清纯美丽。SAO中和结衣在一起的亚丝娜和女儿结衣在一起，亚丝娜非常的有人妻味，散发着母性光辉。女仆装亚丝娜有些娇羞，实在是太赞了。圣诞亚丝娜很喜庆，身材也很突出啊！小时候的萝莉亚丝娜真的太可爱了，还有一种呆萌的感觉。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10295', '0', '刀剑神域：各种形态的亚丝娜，你最心水哪一种？', '你最爱哪一种？亚丝娜女神的各种形态。因好奇借用哥哥结成浩一郎的NERvGear进入SAO的新手，并用真名取游戏角色名字。直到认识“活在”SAO里的桐谷和人后改变想法，也因此喜欢上桐谷和人并在游戏里结婚。SAO中的亚丝娜亚丝娜最经典的形象，绝对领域真的超棒啊！ALO中的亚丝娜精灵尖尖的耳朵，配合蓝色的长发，真的美如画。校服亚丝娜穿着校服的亚丝娜显得非常的清纯美丽。SAO中和结衣在一起的亚丝娜和女儿结衣在一起，亚丝娜非常的有人妻味，散发着母性光辉。女仆装亚丝娜有些娇羞，实在是太赞了。圣诞亚丝娜很喜庆，身材也很突出啊！小时候的萝莉亚丝娜真的太可爱了，还有一种呆萌的感觉。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10296', '0', '《刀剑神域》推出亚丝娜手办，剑舞飞扬，英姿飒爽', '大家好，欢迎阅读最新一期的拾部次元。自从《刀剑神域》的轻小说以及动画推出以来，其中的主要女角色结城明日奈，也就是亚丝娜一直拥有非常高的人气，常年居于各大动漫角色人气排行榜的前列。亚丝娜个性温暖而且善良，许多动漫迷都把她当作了本命角色看待。最近，官方推出了一款名叫“闪光的亚丝娜”的手办，我们就一起来欣赏一下吧。从手办的图片中我们可以看出，亚丝娜身穿着观众以及读者都非常熟悉的服装，这套服装迎着风在飘扬，无论是裙摆又或者是服装上其他部分的皱褶，做工还是比较精细的。亚丝娜那头标志性的长发也在迎风飘舞，出众的长相以及身材再加上这样的情景，很好地将亚丝娜进入战斗的姿态表现了出来。在这一款亚丝娜的最新手办中，生产商为我们展现出了亚丝娜进入战斗的形象，亚丝娜手中握着绿色的剑，一副冲刺的状态冲向敌人，剑指向前方。亚丝娜那双美丽可人的眼睛望向剑指向的方向，表情上充满了毅然决然的样子。从整体上来看，这款亚丝娜的最新手办建模做工的质量都比较好，亚丝娜那英姿飒爽的模样十分讨粉丝们的喜爱。亚丝娜的衣着比较多变，除了这一款手办上展示出来的这套服装之外，在作品中，亚丝娜进入SAO一个月后披着一件深红色斗篷，使别人看不清她的脸。往后加入血盟骑士团身着公会副团长制服，以及自己的休闲服装。被囚禁在ALO中穿着精灵女王服装，被桐人救回到现实世界中身着校服等。粉丝们表示，亚丝娜无论穿什么服装都好看，因为亚丝娜的长相以及身材条件都很优秀嘛。这一款亚丝娜的最新手办是按照1比7的比例进行制作的，手办高度在20厘米左右，这样的高度也是比较适合摆在房间中观赏用。官方宣布这款手办将会在今年12月正式出货，手办的价格约合人民币986元。喜欢亚丝娜这款最新手办的粉丝们可不要错过这次机会了，心动不如行动，马上去入手一套吧。关注【巨象动漫】，回复“东京食货”，“东京搜查官”获取全三季动画！国漫神作《史上最强》现已上线各大平台，点击下方【了解更多】即可跳转查看！', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10297', '0', '亚丝娜怀孕了', '桐人：你是喜欢我的剑还是我的刀亚丝娜：流氓当时出来这部时，真的惊得不要不要的，超喜欢的。桐人君，一个很平凡的类宅男，为什么类呢，因为人家会剑术哦，纯宅男可难掌握此项技能。剧情走向很简易了当，那便是升级打怪，当然热血战斗什么的很少出现，比较多的自然就是我们都喜欢的扮猪吃老虎啦。套路千篇一律，但是不妨碍看得爽美女倒追众人敬畏奇遇不断连喝口水都能有美人出浴妥妥标准光环闲了去打打巨龙，轻松获得腐妹一枚累了携美游山玩水兼戏耍打劫逗逼团队，一举夺下小萝莉芬心久不久与亚丝娜暧昧互动，聊聊人生，结结婚什么的，那个啥，就差没那个啥饿～若没记错，他俩好像～嗯～对桐人君，自带帅酷光环，一身黑衣先不说，光那二刀流就炫酷极了，有木有他可是直接冠上‘封弊者’称号，神域中最强者，话说开挂能不强么还能男能女男的酷到花骨开女的美到月藏云刀剑一最喜欢，二呢勉强可以，三呀，呵呵，我就不说了桐人:亚丝娜，我亚丝娜：爱过桐人：……内心场白：桐人是崩溃了，自亚丝娜生下孩子后，他的地位就自动降级了', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10298', '0', '动漫中惨被侮辱的角色，刀剑神域亚丝娜不是第一？网友：大开眼界', '为了让剧情变得更加丰满，创作者在构思的时候，会给角色添加一些独特的属性。比如，一些动漫的女性角色就不得不面对被侮辱的设定，有时候让人看着十分捉急。今天，小编就要给大家盘点动漫中那些惨被侮辱的角色。1、春日野椿《未来日记》​春日野椿她是新兴宗教“御目方教”的教主。曾满足于有父母陪伴的幸福，乖乖的接受了父母的安排,把一切看做父母为自己做的事。父母遭遇车祸身亡后，为了维持教团而被当成玩物失身给信徒，连她唯一的心灵依靠“母亲给的手球”也丢失了。所以她希望自己成为神来消灭这个可恨的世界。2、丽姿·霍恩施泰因《黑之宣告》丽姿·霍恩施泰因她是男主提奥多尔 艾伯巴赫的义妹，为了补充范氏兰负伤导致的战斗力下降，曾是东欧派遣兵团一员的她被编入第666中队。她非常喜爱自己的哥哥，为了能够和自己的哥哥相遇付出了很多，包括自己的贞洁！3、亚丝娜亚丝娜她是《刀剑神域》的女主角，与男主角桐谷和人的恋人，陪伴桐人度过了人生中最艰难和危险的时光，是桐人的心灵支柱。不过，她在动漫中却被须乡这个混蛋侮辱，让很多漫迷看着十分揪心，纷纷表示想给作者寄刀片。4、桂言叶《日在校园》桂言叶她有着魔鬼般的身材和天使般的面容，以及和外表完全相反的内向性格。感性意识强烈，容易冲动，是病娇类角色的鼻祖。在剧中，她被自己的心上人伊藤诚的朋友黄毛推倒。悲惨女主的设定，真是让人大开眼界了。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10299', '0', '《刀剑神域》亚丝娜性感泳装手办 肤白貌美', '《刀剑神域》中的女神亚丝娜上得战场，下得厨房，深受动漫迷们的喜爱。官方也是频频推出各种亚丝娜的手办，从战斗到家居各种装束都有，这次为大家介绍的是一款非常应景的亚丝娜泳装手办。这次的泳装是非常典型的比基尼款，颜色依然是亚丝娜的经典色彩，红色和白色相间的条纹，映衬出她的雪白细腻的肌肤。而胸前的红色蝴蝶结，更显得她“波涛汹涌”，再加上那无以伦比的大长腿，这样的傲人身材搭配比基尼泳装真是绝了。这款《刀剑神域》亚丝娜泳装手办为1/6比例，高为250mm左右，比一般的手办要大一点，手办已经在发售中。还要告诉大家一个好消息，目前这款手办正在亚马逊上打折销售，想要把老婆带回家的伙伴们可不要犹豫了。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10300', '0', '好帅！《刀剑神域》推出亚丝娜手办', '作为近年来人气最高的轻小说之一，《刀剑神域》拥有着大批粉丝的关注。而女主角亚丝娜也凭借其完美的形象，成为了不少粉丝心中的老婆人选。近日官方公布了一款名为“闪光的亚丝娜”的手办，让我们快来看看吧。请点击输入图片描述请点击输入图片描述请点击输入图片描述请点击输入图片描述从图片上可以看到，亚丝娜正挥舞着自己手中的剑，并且露出了似乎很得意的笑容。无论是飞扬的头发还是飘动的衣服，都让她显得更加英姿飒爽。请点击输入图片描述请点击输入图片描述请点击输入图片描述请点击输入图片描述这款手办是按照1:7的比例进行还原，高度在200mm左右。手办于今年12月正式出货，价格是17064日元，约合人民币986元。《刀剑神域》讲述了一个近未来的世界。由于技术的发展，能够模拟现实效果的网络游戏成为了人们关注的焦点，不过隐藏在背后的阴谋也在蠢蠢欲动之中。作品的设定也引发了很多现实中技术人员的兴趣，此前IBM就宣布开发了以《刀剑神域》为模板的测试游戏。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10301', '0', '你知道亚丝娜的这5个小秘密吗？', '《刀剑神域》里关於亚丝娜的5个有趣小秘密：1.按照SAO篇的故事内容，亚丝娜「倒追」桐人啥的剧情是不合理的，所以川塬砾老湿又创作了SAO进击篇，增加了不少亚丝娜和桐人「秀恩爱」的桥段。2.亚丝娜又被称为劳模和本子王，C82时，她一人的同人本数量就超过了当届comiket半数，本子王之名由此而来。不过要论本子数量，亚丝娜也就是哪一届称雄，歷史上同人本最多的动漫角色还是归属《东方project》和《新世界福音战士》。3.亚丝娜的头髮颜色和爸爸妈妈以及哥哥都不一样，所以粉丝之间一直有亚丝娜的爸爸结城彰叁戴绿帽的笑称，在SAO中还有过亚丝娜染髮的桥段。还有网友说亚丝娜其实是结城梨斗的后代(大雾)。值得一提的是，亚丝娜的声优户松遥也是《TO LOVE》中菈菈·萨塔琳·戴比路克的声优。4.川塬砾在提到亚丝娜这个角色的塬型时曾说过他参考过女演员栗山千明，她曾参演过《杀死比尔》、《SPEC：天》、《图书馆战争》等电影，并且作为动漫歌手演唱过《机动战士高达UC》等动漫作品的主题曲。5.据说剧场版《刀剑神域：序列之争》中会包含亚丝娜的入浴镜头，并且BD版本中将会修正电影版中被剪掉的部分。来源：jpnest', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10302', '0', '听完这句话，亚丝娜决定这辈子非桐人不嫁', '动漫里那些甜到腻的告白宣言，《刀剑神域》在第五个：“我的人生给你一半，所以你的人生也给我一半吧！” 出自《钢之炼金术师》场景再现：爱德华：“我把我的人生给你一半，所以你的人生也给我一半吧！”温莉：“真是笨蛋！别说一半了，全部都给你！……果然不能是全部……9成，不，8成吧……7成不太好，那就8.5成吧……是啊，这种程度的话给你好了……”——出自《钢之炼金术师FA》最终话。点评：这两个笨拙的人的表白每次看都让人忍不住莞尔。爱德华一开口就是他的职业病“等价交换”，而温莉在毫不犹豫地答应后才意识到自己的心意完全暴露，慌忙地掩饰着。最后两人的拥抱更是充满了温情。这一幕可以说是最经典的告白场景之一啦。“就算我是福尔摩斯也做不到啊，要我准确地猜出心上人的心思……”场景再现：工藤新一：“你就是个棘手的难案啊！夹杂了太多的个人情感，就算我是福尔摩斯也做不到啊……要我准确地猜出心上人的心思……”——出自《名侦探柯南》TV 621话。点评：虽然在TV动画和剧场版里，新兰两人都已经多次间接地表白过自己的心意，但像这样以新一的身份面对面地直接表白，意义到底是不一样的。要让口是心非的推理笨蛋打出这个直球可真是太不容易了……而且还是在新一最喜欢的福尔摩斯的故乡，伦敦，在大本钟下的表白，可见青山对于一直虐这对大概也有点不好意思，于是在告白场景上下足了功夫。“渚，我们结婚吧。”场景再现：朋也：“渚，我们结婚吧。”渚：“好。”朋也：“……真的好吗？”渚：“我也一直觉得，除了朋也以外，没有别人可以的。”朋也：“我可是这样的啊，这么的没用……”渚：“我也很没用。但是，两个人在一起的话就能变得坚强。”朋也：“你能一直在我身边吗？”渚：“是的，我会一直在你身边。无论何时，直到永远。”——出自《CLANNAD》第二季12话。点评：在朋也因为父亲的关系而极度低落、陷入自我厌恶的时候，“和我结婚”这句话就像是他最后的求救呼喊。而渚毫不犹豫地接受了这样的他，“两个人在一起的话就能变得坚强”，的确是这样，和渚组建的家庭拯救了朋也，也引发了这个小镇的奇迹。“你绑马尾辫的样子简直合适到犯规！”场景再现：长门说是[进化的可能性]。朝比奈学姐说是[时间的歪曲]。古泉则把她当作了[神]。但是对于我来说又是怎么样的呢？我是怎样认识[凉宫春日]这个存在的呢？春日就是春日，也只能是春日。并不是想用循环论证蒙混过关，虽然不是，但我的确没有确定的答案。对于我来说，春日并不仅仅是普通的[同班同学]，当然也不是[进化的可能性]或者[时间的歪曲]，更不是[神]！绝不可能是这样的！阿虚：“我，其实萌的是马尾辫。”春日：“…什么？”阿虚：“忘记是什么时候看到的了，你绑马尾辫的样子简直合适到犯规！”——出自《凉宫春日的忧郁》2006版第14话，2009版第6话。点评：严格来说并不能算是告白，因为这时候阿虚自己也还没有明白自己的心意，但他已经模模糊糊地察觉到了团长在自己心中有着与众不同的地位。在堪称世界末日的危机中，阿虚很有个性地对春日来了这句称不上表白的表白，配合一个KISS——达成happy ending拯救了世界。对于团长来说，阿虚是她想要抛弃世界时唯一无法割舍的存在，也是让她重新觉得“世界很有趣”的那个人，这也是一种很特别的浪漫吧。“我的命是属于你的，亚丝娜。”场景再现：桐人：“我的命是属于你的，亚丝娜。所以我会为你而使用。我们一起生存到最后一刻吧。”亚丝娜：“我也是。我绝对要保护好你。从此以后就由我来永远保护你，所以……”桐人：“不管发生什么，我都要把你带回去，回到那个世界。”——出自《刀剑神域》第10话。点评：桐亚夫妇的感情感动了很多人。尽管在虚拟世界相遇，他们的感情却毫无疑问是真实的。一起经历过困境，面对过死亡，生死相依后结下了无比牢固的羁绊。对于桐人和亚丝娜而言，因为现实生活里他们并不幸福，所以这份爱情就像是他们的救赎，让他们学会了珍惜幸福、学会了去爱他人。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10303', '0', '《刀剑神域》不只是桐人意志超越系统，其实她也是！', '绅士君是一个喜欢动漫的二次元死宅，点击上方头像关注我们即可查看往期精彩推送！看过《刀剑神域》的小伙伴应该都知道，在这部作品里面男主角就是一个挂B，每次开挂的时候就会眼冒金光，官方美名曰“意志超越系统”。其实在《刀剑神域》里面，不止是桐人一个人意志超越系统，还有一人的意志也超越系统了，她就是桐人的正牌女友——亚丝娜!!!绅士君为什么会说亚丝娜和桐人一样都是意志超越系统，当然不会是平白无故的和大家一通乱说，而且关于亚丝娜意志超越系统这件事还是川原烁自己写的。在《刀剑神域》第一季第14集中，桐人、亚丝娜等人在攻略了75层BOSS之后，桐人突然指出希兹克利夫就是茅场晶彦，因为开始两人在决斗的时候，自己有一击希兹克利夫是躲不过去的，但是他却突然加速躲过那一击。就在这时候，桐人也是攻击了一下希兹克利夫，结果发现希兹克利夫是“不可破坏”的状态，这个时候希兹克利夫的真实身份也是昭然若揭。希兹克利夫的真实身份被识破之后，告诉桐人自己本应该化身100层BOSS等着他的到来，但是既然被识破了就决定将两人的对战提前。在两人对战之前，希兹克利夫先是将自己的“不可破坏”的状态取消了，然后又让桐人之外的其他人进入麻痹状态。可是在桐人和希兹克利夫的对战中，桐人一直是处于下风，那怕他使用的特殊技能二刀流，这些都被希兹克利夫一一防御下来，而且桐人使用二刀流最后一击之后，逐暗者的剑尖更是直接断裂，而希兹克利夫也举起了致命的一刀。就在这时候原本处于麻痹状态的亚丝娜挡在了桐人的面前，最终亚丝娜也是代替桐人殒命在这里。最后桐人在被希兹克利夫用剑贯穿之后，依靠着意志超越系统反杀了希兹克利夫，两人就此同归于尽。游戏通关了之后桐人和亚丝娜却同时出现SAO外面，就在两人温存的时候茅场晶彦出来和他们啰嗦了一大堆。对于桐人这次死而复生的事情，因为他已经坐定了他就是一个挂B的事情，所以大家都不会觉得奇怪，但是亚丝娜作为一个普通玩家怎么可能复活，而且其他人都挣脱不了的麻痹状态，为什么就她能够挣脱。这个就不得不让人怀疑她其实也是一个挂B，也就是她也意志超越系统了。而且茅场晶彦最看好的其实是桐人，但是最后见的却是亚丝娜和桐人两人，难道茅场晶彦还有这种成人之美的爱好?其实不然，这里面最好的解释就是桐人和亚丝娜两人都是意志超越系统的人，只是桐人的意志要比亚丝娜强。因此茅场晶彦最后才会见这两个人，并且祝贺两人通关这个游戏。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10304', '0', '桐人这辈子都不会点开这篇文章', '作者：尸白监修：天天座大家好第一期我们讲到了，轻小说作者被腰斩作品的种种苦痛。可以说，职业生涯这种东西都便随苦难而生。作者的磨难远不止于此。很多时候，作者的成败不光是个人的，也被时代所左右。打个比方，就算桐人不是个装逼犯实力也没这么强，还是个怂货，在那个时代，他真的有可能火吗？不言自明。今天我们就来聊聊，轻小说作者的一段往事。02年，有两个作者在轻小说文库征文投稿。他们彼此不同，写着风格完全不一样的小说，甚至两人可能完全不认识。只是，他们两个因为自己的小说，却有着完全不同的境遇。这两个作者，一个叫浅井ラボ，一个叫川原砾。当年，浅井以《罪人与龙共舞》荣获征文大赏，在角川sneaker文库开始出版，同时作者浅井还交到了很多作家朋友。不仅书籍质量优异，对奇幻世界观有独特的视角和看法，并以特异的写法获得“暗黑系轻小说鼻祖”的美誉。那个叫川原砾的小子，却在电击游戏小说大奖投稿了一本名叫《刀剑神域》的小说。这本小说，以VR技术讲虚拟网游生活，本来可以说是及其超前。不过却没有通过评选，名落孙山。不得已，川原只能将《SAO》的原稿发布在网站上。原本，这已经是故事的结局了。然而这次，历史的玩笑却开了个大的。08年的时候，浅井因为角川编辑工作的失误和角川公司本身敷衍的回应而与之决裂，前往GAGAGA文库继续发展。就在这段时间前后，在网络上通过连载积累人气又坚持不懈的川原终于携新作——《加速世界》荣获电击小说大奖。这份属于他的荣誉，足足迟到了六年。和《SAO》在电击文库出版，并且随即大热，TV动画，剧场版，游戏层出不穷，而跳槽去了GAGAGA的《罪龙》却只能借着今年业界“文艺复兴”的热潮赶上一波动画化，与时雨泽惠一原著的《SAO外传》动画对垒，还要在热度上被碾压。两者究竟是因为什么才会在同年创作的情况下，发展轨迹来了个一百八十度大反差呢？《刀剑神域》想必各位已经十分熟悉了。12年的TV动画在国内掀起了热潮，时至今日，二期动画、游戏、剧场版、外传小说等等企划也后进跟上，好莱坞真人化和三期动画同样排在日程表上，而无论是劳模老婆亚丝娜还是官方芭比娃娃桐子，也都是看视频的各位耳熟能详的角色。但是也正如大家所知道的，在02年的时候川原试图投稿电击文库，但是被编辑以“字数太长不符合出版规范”为由退稿。当然混过实体出版业的朋友都知道，编辑的退稿通知基本都是随便写个理由，真相为何我们现在已不得而知。总之，02年川原被退稿之后不得不回头，将稿子投放到网站上，进行了为期六年的连载，直到08年年中才结束。事实上，这本书直到连载了两年，才成功的炒起热度。对比同时期的天朝网文，02年的时候已经通过五年的积累。在02年已经完成了三次进化，从个人网站上的免费分享进化到版权买断出版，又再次进化在读写网上成功建立了付费阅读制度并以此敛财。就在同期，起点也正式成立。02年的日本，川原却只能化名九里史生在类似博客的个人网站上进行连载，二者之间发展的差距一直延续到了今天也没有抹平。川原理所当然没有放弃自己的出版梦，在连载《刀剑神域》的同时，他也在撰写着自己的新小说，网络连载的经验对他而言显然是有利的。最终，他如愿以偿。在《加速世界》出版之后两个月，SAO这部当年号称是因为“篇幅”问题而落选的作品，也随之出版。由此可见当年的评委，八成只是觉得这本书的题材不能赚钱。实际上在02年的时候，SAO的确可能会亏本，甚至死得比谁都要惨。总之，川原最终凭借着自己的本事达成了梦想。借网络连载时期积攒下来的人气，和各式各样复杂的内在缘由，多媒体改编的轮番轰炸，SAO的火热程度远超想象。同期的作品中就连《加速世界》也无法与SAO抗衡。川原成为了史上最快达成累计发行一千万本小说的作家，对于轻小说出版业而言几乎是无法想象的数据。对SAO的一系列开发计划，直到现在都是各大新闻板块的头条，毫无疑问，川原就此登上了人生巅峰。然而，罪龙呢？同样在02年进行投稿的《罪人与龙的共舞》，则在当年就成为了角川sneaker文库大赏的受赏作。当年，美少女奇幻战斗一类的轻小说并没有成为市场主流，只是冉冉升起的新星。浅井的罪龙在当时以奇幻、科幻这样的故事类型下，显得即讨好了主流读者，又迎合了读者想求新求变的心思。隔壁的川原明显在02年的时候，是步子太大扯到蛋了。太过超前的事物，没人会觉得时尚，反而只会认为它是个怪物。之后在03年1月正式出版，这一年浅井的存稿似乎格外充足，本身《罪龙》的长度较之一般的轻小说就要多出许多，甚至一些较短的作品只有他一半的长度，而他这一年又连续出版了四卷。理所当然就像网文爆更之后一定会陷入疲软一样，之后浅井的出版速度必不可免的慢了下来，在04年和05年都是只出版了两本，06年则只出版了一本。这几年的业界实际上还不流行“轻改动画”这种东西，同时《罪龙》的故事内容较为刺激，为了和谐社会不便多提，而战斗描写也相对晦涩，例如下面一段：　　生物强化系第五位阶——「钢刚鬼力臂法」在吉吉那的体内发动。全身的强化肌肉膨胀，破坏掉身上的甲壳铠，甚至连固定镗甲零件的螺丝也绷开了。他的双脚一直到脚踝都埋入柏油路，阻止了龙的前进。　　随着咒式的程序，别称红肌的慢缩肌纤维中的柠檬酸循环，利用氧气以最佳的效率分解肝醣与葡萄糖，转化成三磷酸腺苷（注：即ATP于生物体内传递能量）。而在被称作白肌的快缩肌纤维中，以最佳效率利用肌磷酸（注：即CP）与三磷酸腺苷，藉由代谢ATP—CP产生肌肉收缩的能量，被强化的慢缩肌与中间肌纤维之间，进行强力的神经传递，合成大量的肌磷酸三磷酸腺苷。　　负荷过重时，韧带与关节的高尔基肌腱器，会将讯号传达到脊髓，抑制运动神经元的活动。为了防止韧带或关节损伤的脊髓反射，只会送到高尔基肌腱器发出讯号的运动区，由脊髓发出的抑制讯号，会强制停止人体的动作。呃……出自动画第一集开头，吉吉那脚下冒魔法阵的部分总之，出于改编难度和画面表现以及当时角川sneaker文库的政策，这本书并没有在这段时间内迎来动画化。到了07年，这本书迎来了重大转折，因为角川snaker文库杂志的编辑工作失误，将浅井的原稿未经许可就放在了杂志上。此事一出，双方随即决裂。次年，角川方面宣布无条件放弃版权，《罪龙》移籍至GAGAGA文库继续出版，再之后一年，角川方面给出了原作者称之为“没什么诚意的”谢罪书，双方的瓜葛也就此结束。这', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10305', '0', '刀剑神域：结婚后亚丝娜猜测桐人比她小，然后露出了这种表情！', '大家应该都知道亚丝娜喜欢桐人，但可能有一些人并不知道其实桐人比亚丝娜还要小。按照划分来说的话，亚丝娜是弟控。在《刀剑神域》SAO里，桐人和亚丝娜在游戏里结婚了，当时两人生活在第22层，他们在那里买了房子，两人睡在一间房子里，不过床是分开的。某一天早上亚丝娜趴在桐人床边看着桐人的睡姿，亚丝娜说桐人平时是那么地目中无人，但其实挺可爱的，甚至有点孩子气。其实从这些话里，可以听出亚丝娜对桐人的评价。看来在亚丝娜的眼里，桐人是一个很任性又目中无人的小孩子。小透露：亚丝娜第一次见桐人的时候，是在第一层，当时他们在一起开会讨论BOSS攻略，之后两人组队，晚上桐人送奶油给亚丝娜。第二天他们两人和其他人参与BOSS攻略，打败BOSS桐人成为了封弊者，很是目中无人。当亚丝娜想到“孩子气”的时候，突然在内心猜测，桐人不会比自己还要小吧，如果真这样，亚丝娜可是桐人的“姐姐”。话说小伙伴们是姐控也是妹控呢，或者兄控还是弟控？然后亚丝娜露出了这么一个表情，惊奇中带了一点羞涩，脸上还有几道红红的线条，在亚丝娜心里，也许桐人真的比她小，因此才这么的孩子气吧。话说大家觉得这个表情应该如何描述，笔者感觉有点词穷！欢迎会描述的人留下你的描述语句，分享给其他人。不过亚丝娜这个表情只是出现了一会儿，然后就恢复了。喜欢一个人的话，就不要在乎他的年龄大小、身份地位，只要自己喜欢就行了。亚丝娜此时伸手摸着桐人的脸，心中充满了爱意。其实亚丝娜猜对了，在SAO结尾的时候，亚丝娜和桐人相互交换了真实姓名，桐人真名为桐谷和人，年龄16岁；亚丝娜真名为结城明日奈，年龄17岁。亚丝娜比桐人大一岁。大家可以说亚丝娜其实是一个弟控。这一次主要是分享亚丝娜和桐人在SAO里的生活故事，当时亚丝娜猜测桐人比她小的时候所露出的那种表情。如果大家喜欢亚丝娜的话，记得给笔者点赞支持哦，因为笔者也很喜欢这个妹纸。同时，觉得文章不错的话，记得分享给其他漫迷们哦。笔者今后会为大家带来更多的动漫。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10306', '0', '《刀剑神域》里桐人的5个有趣小秘密', '《刀剑神域》里桐人的5个有趣小秘密：1.桐人和亚丝娜是姐弟恋，二人相差一岁，才开始玩SAO时桐人14岁，亚丝娜则是15岁。2.按照小说里的描述，桐人“五官纤细得像少女一般”，所以在《幽灵子弹篇》里一开始桐人以“桐子”的角色登场了。比较亮眼的是“桐子”由桐人本来的声优松冈祯丞配音，理由是他的声音温柔细腻，就算用女声听起来也十分悦耳。松冈祯丞的女声配音确实是一绝，在《茅野饮酒》第23期节目里，松冈还为大家现场表演了女声配音。3.桐人是日本网络安全形象大使，为此剧场版动画《刀剑神域：秩序之争》制作委员会还专门制作过主题宣传片。有趣的是，在《幽灵子弹》篇中也有过桐人接受政府委托的剧情。4.桐人在现实生活中是学霸，并且对网络知识非常精通，六岁时候就会自己组装电脑，十岁的时候就通过自己查询资料窥探到了自己身世的秘密。5.亚丝娜并非是桐人的初恋，虽然故事中没有明确说明是谁，但是普遍认为是幸，因为川原砾在访谈录里边曾经说过，“是月夜黑猫团的某人”此文为看点（一图超人）原创内容，特此声明。', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10307', '0', '勇敢何其重要？亚丝娜靠它才能遇见桐人', '亚丝娜是富家千金，从小就接受着良好的教育。机缘巧合下，玩起了哥哥的游戏，还因此被卷入了死亡游戏。亚丝娜是个游戏新手，现实生活中并不怎么玩游戏，开始时害怕得不敢离开初始之镇的旅馆。​​但她深知自己需要做出改变，即使在游戏中死亡，也绝不能向游戏低头。​学会了勇敢的她，开始了崭新的人生。​', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10308', '0', '刀剑神域，那些年桐人收集的后宫们', '《刀剑神域：序列之争》剧场版香港上映宣传PV公布，预计3月30日上线开播。该作或许将成为首部在中国大陆引进公映的日本电视深夜档动画的剧场版动画。今天文娱君就来和大家聊聊，那些年在刀剑神域里面桐人收集后宫们。老规矩，欢迎各位小伙伴在下方评论区留言，说说你的意见。亚斯娜本子王，父亲是大型电子用品制造商“RECT”的CEO，母亲是某大学中的教授。在SAO中除了是原本就不多的女性玩家外，更是在反映真实长相的SAO系统下拥有排名前五名美貌的美女玩家。朝田诗乃为克服童年阴影而登入VRMMO-RPG “Gun Gale Online” 的玩家，称号为“冰之狙击手”。“死枪”事件后在桐谷和人的邀请下转换到新生了ALO中，角色为猫妖精，擅长使用弓箭狙击。幸一所高中的电脑研究社社员，被困“SAO”后和一起被困在“SAO”的同社社员一起成立了公会“月夜的黑猫团”，并作为中层玩家开始攻略，后由于陷阱在“SAO”中死亡。筱崎里香在艾恩葛朗特49层琳达司开店的锻造师少女，在SAO中是使用锤的前卫。ALO事件之后接到桐人的联络，在病房和亚丝娜重逢。醒来后到SAO归还者聚集的学校就学。绫野珪子稀有的“驯兽师”少女（“驯兽师”并非游戏设定的职业），被中层玩家称为“龙使·西莉卡”。新生ALO营运后，将帐号转移至ALO，种族设定为猫妖精。桐谷直叶桐人的表妹，剑道少女，从小毫不间断地挥舞了8年竹刀的努力女孩。自从七岁的时候和人放弃了剑道后，两人之间便逐渐出现很深的代沟。好了，今天的盘点就到这里了。欢迎各位小伙伴在下方的评论区留言，说一说你的意见。喵！点击下方的“关注”按钮订阅文娱君，文娱君每天会推出动漫的精彩内容哟~好文推荐网球王子，我心目中的奇技淫巧！本文图片来源网络', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10309', '0', '桐人的后宫', '结城明日奈（benzina）这个开始没毛病吧桐谷和人的女朋友，正宫无误。跟随桐人去了许多个虚拟世界还在UW世界中由于内部时间加速的原因和桐人一起呆了200年。对于料理非常拿手，在现实世界料理技能也是一流，最出名的就是用桐人得到的“S级食材·杂烩兔肉”做成的炖肉杂烩，上得战场下得厨房，贤内助爱丽丝·辛赛西斯·萨蒂UW中曾经与桐人和优吉欧是青梅竹马，现在是第三十位整合骑士，实力和亚丝娜不相上下。爱丽丝曾在村外搭建小木屋和桐人生活了半年（睡一张床无误）。目前是对亚丝娜最有威胁的劲敌。幸桐人初恋，少有的和桐人睡过一张床的妹子之一。公会【月夜黑猫团】的成员，和桐人有共同的道具栏（桐人一直保留着这个道具栏）。死的太惨了（那会儿一直认为她的死是为了亚丝娜铺路所以，，，，，，，，，）。喜欢这妹子，偏偏挂了，酱油的命运啊，吐槽作者莉兹贝特SAO中亚丝娜的朋友，亚丝娜曾推荐桐人来莉兹贝特的店中打造武器，后来被桐人的温暖感动喜欢上桐人。告白时却被桐人以风太大听不清楚给坑掉了，后来正式告白时却因为亚丝娜的出现，为了她的幸福而放弃（经国际认证是个货真价实的苦逼）朝田诗乃桐人在GGO遇到的女性，患有对枪械的恐惧症，但在GGO里却是高手玩家，在GGO中以为桐人是女性新手玩家而热血帮助他，后在更衣室发现他的男性身份。单恋桐人联盟一员桐谷直叶桐谷和人的妹妹，在得知桐谷和人不是自己亲哥哥后发现心中对他的感情，但由于桐人从SAO回来后执于寻找亚丝娜而隐藏了自己的心意。直叶在桐人被困SAO时，为了了解哥哥所以喜欢的世界而开始接触ALO并渐渐喜欢上了这个世界。（我的妹妹就这么可爱）娘化桐人（长得这么可爱一定是男孩纸）桐人在GGO里的游戏形象，是个伪娘呢', '', '4686', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10310', '0', '你以为Saber只是亚瑟王吗，盘点FATE里的Saber们！', '从06年初露锋芒的《fate/stay night》，到10年UFO制作，盛极一时的《Fate/Zero》，再到今年问世的《Fate/Grand Order》动画版和惨遭A1动画化的《fate apocrypha》（虽然原作也不怎么样）。但还是有很多小伙伴以为，Saber只是亚瑟王——阿尔托利亚的简称。但其实论其历史的话，Saber最开始的意思只是军刀。由于《Fate系列》的风行，漫友们说道SABER，一般代指圣杯战争中的剑之骑士——剑士职阶的从者。为了科普每个职介的人物，从本期开始，卡密讲给大家介绍一下fate系列七大职介里的那些英灵们！废话不多说，首先进入第一期：盘点FATE系列里的Saber们！阿爾托莉雅·潘德拉貢呆毛王的大名是最为广大读者熟悉的SABER，是TYPE-MOON旗下遊戲《Fate/stay night》中的女主角、小說《Fate/Zero》中的主要人物之一，是第四、第五次聖杯戰爭時期的Saber，通称蓝Saber或元祖Saber。原型是公元五到六世紀不列顛的統治者亞瑟·潘德拉貢。在stay night的原型作品《Fate/Prototype》中直接以男性形象登場。到了stay night，由於要製作galgame，而且Saber的Master由女變為男，為了凸現主從關係便於補魔將其性轉換。于是我们可爱的亚瑟王就这么来了......Saber LilySaber Lily的初次登场是在《Fate/unlimited codes》，实际上就是阿尔托莉雅·潘德拉贡，仅在形象设定上有变化，其余设定都和阿尔托莉雅一样，通称白Saber。与元祖Saber不同的是，Saber Lily的甲胄的色调以白色为主，其发型也变成了马尾，个人认为Lily比原形更加唯美，“纯白的骑士姬”“如白百合一般的少女剑士”的美称可以体现广大玩家对其的喜爱。其设定仅在形象设定上有变化，其他如能力、宝具、语音等设定和原形阿尔托莉雅一样。当然其背景故事比起元祖saber做了些许改变，鬼畜的奈须蘑菇在《Fate/Grand Order》中，终于补上了正式的背景设定。感兴趣的朋友可以自行去查询。尼禄·克劳狄乌斯尼禄·克劳狄乌斯的初次登场是在游戏《Fate/EXTRA》中。同样是游戏《Fate/EXTRA》、《Fate/EXTRA CCC》及《Fate/EXTELLA》中初始可选Servant之一，和阿尔托莉雅一样是被女性化的Servant，身穿裙前摆设计为露出内衣的半透明裙子。性格外向，善辩，喜爱奢华、自我表现；对待心爱的对象时则会傲娇，变得百依百顺；性取向是只要外表美丽即可，男女通吃。自称为余，你称为卿，称Master为奏者。在手办界是不输给蓝SABER的角色。拥有EX级别的皇帝特权，原则上可以驾驭任何职阶。但以“骑马坐车会屁股痛”为由拒绝了Rider；最终听说Saber是最强的职阶，而霸占此位降临在主人公面前。亚瑟·潘德拉贡终于来了一位男saber了，亚瑟·潘德拉贡的登场是在《Fate/Prototype》及前传《Fate/Prototype 苍银的碎片》中。是上文我们提到的元祖Saber的原型。旧Saber忠实于史实，是一位男性。然而在同一世界观之下的梅林却是女性你们到底是多么讨厌史实啊？？？贞德作为新番《Fate/Apocrypha》的登场角色，职阶为Ruler，但是在其职阶曝光前一直被认为是Saber职阶，通称紫Saber。加上Ruler人数过少就不特别拿出来说了（主要是懒）。圣女贞德在《Fate/Zero》中首次作为背景故事人物登场，并短暂露面；在轻小说《Fate/Apocrypha》中，贞德则是以圣杯战争的Ruler身份登场；在游戏《Fate/Grand Order》中，以Ruler职阶登场，黑化版本以Ruler和Avenger职介登场。齐格飞齐格飞，另译齐格弗里德，在轻小说《Fate/Apocrypha》中首次登场。总之是一位死的很惨的角色，由于是男性就不作过多介绍了。莫德雷德终于到我们可爱的小莫了，和齐格飞一样同样登场于《Fate/Apocrypha》。亚瑟王的姐姐摩根为了让亚瑟王陷入危难，利用魔术将亚瑟拟似性转后获取其遗传物质制造的人造人。但是莫德雷德对自己的父亲亚瑟王很尊敬，为其为自己的父亲而自豪，并没有按照摩根的意图而行动，而是想成为父亲认可的骑士行动。但是一旦揭开了其出身就会被亚瑟王否认，莫德雷德陷入了那种犹如被背叛了的感情，而与摩根一同行动。之后便与传说中的一模一样了。有兴趣的自己去看历史书吧。对圆桌骑士团抱有仇恨，得知自己是被圆桌的碎片召唤时感到愤怒，将其劈成两半。对自己的能力有着强烈的自信，因而愿望是“挑战选定之剑”而非“成为不列颠之王”。“你也可能拔不出剑来啊”“我不可能拔不出来！！”作战时战斗场面十分凶残，因而被Master吐槽“你真的不是Berserker吗？”，“只要能赢的话拳打脚踢，就算是用牙咬都没问题啊”。将Saber Lily称之为Angel Saber。梅林对小莫的评价：圆桌骑士他们也不是笨蛋，不过莫德雷德是个笨蛋就是了！冲田总司冲田总司在漫画《Fate/KOHA-ACE 帝都圣杯奇谭》中首次登场。原型是幕末同名剑士。作为漫画的女主角，总司的登场其实是个意外。身为御主的琥珀原本准备了阿瓦隆作为圣遗物召唤骑士王，却由于担任护法的秋叶遭遇敌袭倒地，撞击圣遗物脱手，袋子里的日本刀触发召唤阵震歪而被召唤。真名是幕末最后的剑客集团，“新选组”一番队队长冲田总司。身着和服，举止柔和，且谦虚的有如笔下描绘的大和抚子一般。总是很冷静也有意外的开朗的地方，作为英灵的处事也与Saber相符。但是，一旦交锋的话冷静而化作透彻的刽子手。武器一旦抵达对方的空隙就会立即斩下，在互相残杀和拥有生死观方面极为严厉的感性。生前，想要带病与新选组的同胞们一起在战场上迎来最后，向圣杯寄托的愿望是，“战斗到最后”。有着不输给元祖saber的人格魅力。其他诸如黑saber，魔神saber因为都是元祖saber的扩展包，这里就不作过多介绍了，感兴趣的朋友自己去研究吧！反正不管你怎么研究。亚瑟王都不可能是女孩子的！', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10311', '0', '全都是吾王！Saber原来有这么多形态', 'Saber阿尔托利亚原来有这么多形态。原版阿尔托莉雅性格认真、忠诚正直，拥有身为骑士王的荣耀和骄傲。喜欢尝试独立解决问题，对于自己的能力抱持着自信，曾为卫宫切嗣的对自己的不信任而感到气愤。不喜欢拖泥带水的人，不想在自己喜欢的对象面前失败。以男性的行为方式生活着。唯有在Fate线中，阿尔托利亚逐渐感受到了卫宫士郎对自己的爱，才常常会显现出自己作为少女的一面。Saber LilySaber Lily的甲胄的色调以白色为主，其发型也变成了马尾，被称为纯白的骑士姬。拔出选定之剑，向成王之路开始迈进的阿尔托利亚。对自己尚不能独当一面一事有着自觉，略缺乏自信显得不成熟可说是白璧微瑕。高兴的事情就率直的高兴欢喜，悲伤的事情就坦率表示悲伤，是原本的阿尔托利亚所不被容许的少女之形。Saber Alter最早登场于游戏《Fate/stay night》的间桐樱线路。被圣杯的诅咒侵蚀、显现出的亚瑟王冷酷无情的另一面。阿尔托利亚很善于抑制自己的力量，不过这个状态的她对于挥舞自己庞大的魔力没有一丝犹豫 。由于拥有Master间桐樱近乎无限的魔力供应，可以连续地放出誓约胜利之剑，是HF线路后期最强的英灵。圣诞阿尔托莉雅Alter阿尔托利亚为了消除自己的形象，而决心作为孩子们的同伴·圣诞老人展开行动时的姿态。手上的袋子，塞满了她一年以来收集并精心挑选的礼物。圣诞Alter所乘的雪橇是她自己亲手制作的。取骏马和雄狮的形象，用白蜡树枝搭成，名字叫做拉姆利二号。Lancer阿尔托莉雅是作为王扩张势力后，没有了圣剑带来的成长停止，成长到了和王的身份相符的肉体年龄。因为使用圣枪而成为了接近神灵的存在，她是由通常的阿尔托利亚「不同的可能性」诞生的英灵。因为蕴藏在圣枪中的特性，可勉强称之为接近女神的存在。比圣剑的阿尔托利亚要变得合理、冷静得多，但没有失去人性。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10312', '0', 'Fate系列中saber介绍', ' 身份为古不列颠传说中的亚瑟王，中文名阿尔托利亚·潘德拉贡，经常又称之为“呆毛”、“军刀”，在第四、五次圣杯战争中一直以“Saber（剑之骑士）”职阶被召唤到现世。 15岁拔出石中剑，即刻起，时光长河便为王而冻结，王的身体和容颜停止了成长和衰老，永驻在15岁的那一刻，头顶有着一根呆毛，阿尔托利亚自称，这根呆毛象征着王的荣耀，无论谁都不能触碰。 现在saber的形态也有这样的可惜没了呆毛 甚至还有这种的，难道大家都只看脸和呆毛的么？大小明明差这么多。尼禄 尼禄 尼禄 上面几个就是最常见的了，还有一些不常见的毫无违和感的saber 最后既然有saber怎能没有咖喱棒', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10313', '0', 'cos化妆技：Saber教你如何完成saber发型', '【最终效果】【步骤】先将毛理顺，全部拨到后面将毛分成三份，中间一部分扎起来，将中间的马尾分成两股，如图把两股扭起来，两股头发分别扭起，再扭成一股扭成一股后用皮筋扎好，不要松手把头发盘起来，成花苞状将左侧留出的头发编成麻花再绕到中键盘好的花苞上用发夹固定好发尾右边同样编一个麻花绕道花苞上用发夹固定然后我们把saber的丝带作装饰丝带有扣上去的也有系上去的。这个你们自己选择（本篇转载于网络，版权归网络所有，如有异议请告知，并给予马上删除）梦幻理想乡ID：our-avalon我们爱动漫，但并不幼稚！不一样的世界，不一样的精彩！快来签订契约吧，乡娘苦苦等着你。。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10314', '0', '这里是吾王saber的领地，漫友们你们是怎么喜欢上saber', '王者的誓言是不能破坏的，我有自己不得不去完成的使命，我的愿望从一开始就只有一个，从我拔剑之时开始，这份誓言就永远不会改变。欢迎吾王saber登场为什么你会喜欢saber？可能很多人都看错了saber，什么帅气，强大，呆萌，吃货……这都是表面的东西。那她到底是什么样的？【16岁的少女，手上抬的是沉重的咖喱棒，身上穿的是冰冷的盔甲，肩上扛的是国民。】仅此而已……凭什么她没有像其它同龄女孩一样有那么轻飘飘的裙子?！凭什么大家在花园里玩时，她要在剑丘上哭泣?！就因为拔出了莫名其妙的剑，就失去了全部吗！！！毕竟，她只是一个一无所有的16岁少女。为什么会喜欢saber？看到有人这样总结，我觉得还是有几分道理的：1，首先是人设游戏原文： “比我还小了几岁的少女，那个是非常美丽的美人被月光照耀的金发，像是洒了砂金一般细致还留有稚气的脸庞有着气质，白皙的肌肤看起来就很柔软“美丽的铠甲少女形象让很多人看了张图片就入坑了。2，背景故事生死之际，背负命运的少女，立誓守护国家的骑士王，纠结着过往，浴血奋战，苦苦追寻圣杯，最终获得了救赎。但也算是爱情悲剧。3，性格，品格少女的骑士王，凛然高贵与呆萌娇俏前后 反差对比 ，即使经历悲惨的失败也不放弃，即使面对更强一档的对手必要时也不退缩，坚毅，认真负责，有时对自己有些苛刻，恪守本愿4，有了 以上基础，加上持续的动画化是角色总会有掉泪的时候，人气这东西与个人无关，喜欢好自己喜欢的就行了，有的时候喜欢就是喜欢，根本没有什么道理可言。接下来是几张吾王的cos照：各位漫友，你们是为什么喜欢saber呢？', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10315', '0', '这八位saber你都认识吗？saber大盘点，谁才是你的最爱', '剑之骑士（Saber）一般也被称作“剑士”或“剑兵”，是圣杯为被召唤的从者（Servant）所准备的“容器”，即“职阶（Class）”之一，而Saber这个职阶又被特别地称为“三骑士”职阶之一。符合此职阶的英灵自然要有与“剑之骑士”相称的传说，被要求“魔力”以外的能力值皆为最高等级。职阶能力是“对魔力”和“骑乘”。另外，符合的英灵大多有着瞬间攻击力优秀的特长。fate系列作为一个超大IP，系列作品自然有很多，在不同作品中往往会有着不同的saber，他们的能力与经历也各不相同，所想要实现的愿望也是各式各样（这些愿望令小编感触极深），这些你都了解吗？你又最喜欢哪一个saber呢？今天小编就来带大家盘点一下fate系列中的各位saber。（只在fgo手游中出现的小编就不盘点了）（下面文章盘点了各位saber的历史经历和寄托于圣杯的愿望，有些繁琐，请各位看官耐心观看）1.阿尔托利亚·潘德拉贡阿尔托利亚·潘德拉贡，身份为古不列颠传说中的亚瑟王。性格忠诚正直，谦逊有礼，个性认真。因有圣剑Excalibur的传承，在第四、五次圣杯战争中以“Saber”职阶被召唤到现世。阿尔托利亚带兵远征罗马时，莫德雷德在摩根的怂恿下，趁机带兵叛乱。剑栏之战中，实力远不如巅峰的阿尔托利亚和莫德雷德两败俱伤，她用圣枪伦戈米尼亚德杀死了“叛逆之子”，而莫德雷德也在临死前用王剑劈碎了“父王”的头盖骨，砍瞎了“父王”的一只眼睛。曾经的首席骑士兰斯洛特带兵前来救王，但终迟了一步，这位圆桌骑士也因此悔恨一生。此刻，倒在剑栏之丘上的阿尔托利亚看着即将灭亡的王国悲痛欲绝。意志坚定的她从未怀疑过自己的王道，但无法接受不列颠灭亡得如此惨烈。将死之际，她与“世界”阿赖耶定下契约，希望得到圣杯许下愿望“重回选定之日” 。2.阿尔托利亚·潘德拉贡 （Saber alter版）被圣杯的诅咒侵蚀、显现出的亚瑟王冷酷无情的另一面（被拔下呆毛后也会变成这样）。又或许、这才是亚瑟王所追求的“理想的王”应有的姿态。阿尔托利亚很善于抑制自己的力量，不过这个状态的她对于挥舞自己庞大的魔力没有一丝犹豫 。根据游戏中的介绍，由于拥有Master间桐樱近乎无限的魔力供应，可以连续地放出誓约胜利之剑（Excalibur Morgan），是HF线路后期最强的英灵。最终战前奉黑樱的命令拦住卫宫士郎等人，只放远坂凛一人过去。之后被早已计划好的Rider和士郎合作击败，被士郎用凛的Azoth剑击杀。（选不杀的话黑Saber会说：“我从未如此恨你。”），话说HF线好致郁啊。3.尼禄·克劳狄乌斯·凯萨·奥古斯都·日耳曼尼库斯生前为罗马帝政的第五代皇帝，生涯被涂上谋略与毒之色彩的恶名昭彰的暴君。新圣杯战争中以Saber的职阶被召唤，虽然是三骑士之一的Saber，不过是靠皇帝特权的技能硬当上的，本人说过自己能当Rider，也暗示过能担任Assassin，至于为何是以Saber的职阶出现，对这问题是以“最优秀的Servant是Saber，那照道理来讲余自然就是Saber了。”来回答。性格外向，善辩，喜爱奢华、自我表现；对待心爱的对象时则会傲娇，变得百依百顺；性取向是只要外表美丽即可，男女通吃(⊙﹏⊙)。在剧情中会对主角展开猛烈追求。热爱艺术与表演，自称“比肩阿波罗神的艺术家”，但是本身歌喉不佳，受母亲毒害而感染的头痛症也限制了其才能的发挥。尼禄在平民阶层中声誉尚佳，却因为残酷镇压基督教势力和对抗元老院而引发了权力阶层的叛乱，被迫自杀而死。原谅小编没有找到她的愿望，知道的人可以告诉小编一下吗？4.高文亚瑟王传说登场的圆桌骑士之一，也是亚瑟王的外甥.。圆桌的白骑士，亦被称为亚瑟王的影武者，另一位肩负圣剑之人。不过，却因为盲目崇拜亚瑟王，而不知道王的苦恼。看着亚瑟王逝世的贝狄威尔，虽然和高文卿一样也是个忠诚的骑士，但贝狄威尔却打从心底祈求王作为人类的幸福，而高文卿则是祈求王作为王的活跃吧。亚瑟王饶恕了兰斯洛特卿。说道，全都是本王不好。可是高文卿没有原谅兰斯洛特卿的背叛，直到最后都和他作对。高文卿仍然拒绝那位即使被开除也仍火速驰往剑栏之丘的兰斯洛特卿，结果他害国王战死，自己也战死了。因为这个结局，所以英灵高文似乎把“协助君主到底”理解为绝对的使命。就像在剑栏之丘上自律自戒的亚瑟王一样，高文卿也在死亡深渊中呼叫。自己一时的冲动把王诱进了死亡。『——如果有下一次的话。还有挽回的机会、第二次生命的话，这次，可要把自己的全部奉献给王——』胸拥这个誓言而Servant化的他，为了纠正生前的后悔与过失，而作为更完美的「骑士」服务于主人。5.亚瑟·潘德拉贡阿尔托利亚·潘德拉贡（左） 亚瑟·潘德拉贡（右）亚瑟·潘德拉贡（旧版亚瑟王）是《Fate/Prototype》及前传《Fate/Prototype 苍银的碎片》中登场的从者saber。有着成熟的价值观与正义感。弃恶扬善，惩奸除恶，赏心悦目的英雄形象。《Fate/Prototype》是奈须蘑菇在学生时期所写的未完长篇小说。可以说是所有Fate系列的始祖和原型。虽然是《Fate/stay night》的原型，但是两者之间有着不同的设定。亚瑟·潘德拉贡，真身是古不列颠传说中的国王亚瑟王，形象是惩恶扬善，赏心悦目的英雄。拥有成熟的价值观及正义感，然而有时却会有些毒舌。对绫香而言他是理想的王子兼保护者。他的宝具不言而喻便是圣剑“誓约胜利之剑（Excalibur）”，但因为其过于著名而用“风王结界”所隐藏起来。原本为了改写命运而呼应了圣杯的召唤。在八年前的圣杯战争中一直赢到了最后，却在离得到圣杯只有一步之遥时被强制性地废弃了召唤，退场。而后遗症便是“他对上次战争的记忆模糊不清”，上述为他本人的宣言。在八年前的圣杯战争中的Master是绫香的姐姐沙条爱歌。取得胜利直到最后，却意识到了圣杯的疯狂。“无论是你还是圣杯都是疯狂的”，这样说完后就把爱歌杀死，将尸体弃置于大圣杯中。经过前一次战争从所谓的“拯救故国”的愿望中得到解脱。而为了拯救八年前间接了解到寄托在圣杯上的愿望是扭曲的少女（绫香），这次也回应了召唤。6.莫德雷德莫德雷德是由东出佑一郎所作小说《Fate/Apocrypha》中出场的Servant，作为红方阵营的Saber现世。[1] 其真实身份为亚瑟王阿尔托利亚的姐姐——摩根（摩', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10316', '0', '圣杯战争结束后，卫宫士郎究竟经历了什么才成为红a？', '看过Fate系列的盆友肯定都知道，红a就是未来的卫宫士郎，当年的红a也曾经以卫宫士郎的身份参加圣杯战争，但那次战争结束之后，他究竟经历了什么才变成红a？今天阿政就给各位念叨念叨。红a在年轻的时候，也经历了几乎同样的圣杯战争，在那次圣杯战争的最后，他与saber道别——然后他与幸存下来的远坂凛成为同伴，一同前往伦敦，学习魔术，增强自己的实力——他在伦敦的时钟塔学习了近三年，在认为自己拥有足以自保的实力后，便向远坂凛道别，独自前往中东（在UBW的结局中，远坂凛和他一起离开了伦敦）。在前往中东的旅途中，偶遇了某个喜欢咖喱的圣职者（官方未透露具体姓名，很有可能是《月姬》中的希耶尔），那个圣职者送给他一块红色的圣骸布，是一件防护力一流的概念武装，那块圣骸布他携带了一生，就是成为英灵后，围在腰间的那块——他在中东待了整整五年，五年里，他一直都在践行自己的正义，舍弃自己，拯救众生，那时的他皮肤已经被毒辣的太阳晒成了褐色。之后，在一次核电站的炉心熔毁事件中，有少部分职员困在核电站中，他义无反顾地穿着防护等级不够的防护服进入核电站，想阻止核泄漏事故，可惜，在濒临融毁的炉心面前，他也无能为力。这时，世界的抑制力向他对话，它可以拯救这些无辜的人，但代价是卫宫士郎死后要成为世界的抑制力为守护世界和平而战——卫宫士郎没有太多犹豫，立刻答应了下来，甚至对这份契约还有些期待，那时的他还认为如果拥有更强的力量，那也可以拯救更多的人吧。之后，奇迹降临，那些本应面临必死命运的人因卫宫士郎而被拯救，不过卫宫士郎本人却因为吸收过量的核辐射导致身体变异，寿命只剩下几年，头发也变得雪白，但卫宫士郎因为得偿所愿内心非常满足。后面几年，他继续在中东践行自己的正义，也收留了不少因为战乱而流离失所的孩童，并教给他们一些战斗的方法，让他们能在那个乱世活下去——最后，在某次巨大事故中出了意外，卫宫士郎被自己曾救过的人当成是造成事故的罪魁祸首，在未经过审判就被送上了绞刑架，卫宫士郎的人生就此落幕。然后，就是他死后的故事了。他死后按照生前的契约成为了英灵，开始为维护世界和平而战，可惜，真正开始之后，他才明白，维护世界和平最有效的方法不是在事故发生之后进行救援，而是在发生事故之前，将引发事故的人亲手消灭。比如他为了避免病毒的扩散，在医院中将患者与职工不分男女老幼，全部杀死，哪怕那些人恳求着救救他们——为了避免地区的战乱扩大，而将地方的反叛军全部剿灭，哪怕里面有那些他之前亲手养大的孩童们——他不想让别人哭泣，但世界却逼他让别人哭泣。他想要的只是拯救，但世界却逼他去杀戮。从理性的角度来说，这都没有错，他的杀戮确实拯救了更多的人，但本性善良的卫宫士郎从感情上根本难以忍受这种用杀戮来换取正义的方法。千百次的轮回之后，英灵卫宫终于崩溃，本想一死了之，可惜成为英灵之后，就没有了死亡的概念，哪怕是自杀都不会真正死去。所以，他打算尝试一个从概率上无限接近于零的办法，那就是由自己亲手杀死过去的自己，以此造成时间轴的混乱，或许，英灵卫宫就会自此消失。接着，他就被召唤到了第五次圣杯战争之中——PS：看得不过瘾吗？来“阿政说”逛逛吧，这里没有乏味的资讯和图片，只有深度的动漫评析和最实用的动漫资源，喜欢动漫的盆友都在这里。对了，最后来张吾王镇楼吧。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10317', '0', 'fate中的卫宫士郎们，能分清楚吗？', '圣杯是传说，可实现持有者一切愿望的宝物，能参加圣杯战争的7名由圣杯选出的魔术师被称为Master，与7名被称为Servant的使魔订定契约。其中卫宫士郎是让人又爱又恨的角色，众多的卫宫士郎们，让人摸不清头脑，今天小编盘点下fate中的卫宫士郎们。卫宫第五次圣杯战争时由远坂凛召唤出的Servant，在未来为了拯救一百名人类而祈求奇迹，作为代价而英灵化的卫宫士郎卫宫基础伊莉雅的哥哥，正常的普通人，没有魔术、没有奇遇、没有理想的温暖少年卫宫少年亚瑟王的恋人，在圣杯战争中与saber相遇相爱，为了防止战争的灾害扩大而参战，最后击败麻婆神父和吉尔加美什，将圣杯破坏卫宫少侠远坂凛的恋人，卫宫士郎于十年前的火灾中被养父救出，在得救时，他因看到养父的表情实在是太幸福而心生憧憬，也想成为拯救他人的正义之士卫宫大侠间桐樱的恋人，但在HF线中，他为了自己的爱人，居然放弃了这种执念，他不想成为英雄，只想选择平凡，只为拯救自己爱的人而行动卫宫巨侠美游的哥哥，魔法少女美游的哥哥。他的妹妹美游被人绑架，为了拯救美游，他借助了archer的力量，让英灵与自己完全同调卫宫Alter黑化卫宫，记忆被所损，也丧失了过去。男子抛弃了道德，蔑视着亲情无铭生前作为“正义的体现者”耗费了人生，在他被当做英雄受到祭祀的瞬间，他的名字就从每个人的记忆甚至历史当中被遗忘了。所以是无名的英雄，架空的英灵上篇文章，fate中开挂英雄“Archer” 们，你最喜欢哪个？——END——喜欢的话，请关注海贼小漫迷！！！', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10318', '0', 'Fate 卫宫士郎撩妹技巧大全，好厉害的小土狼', '刚看了Fate/stay night，感觉我们的正义伙伴小土狼撩起妹来简直是得心应手啊。下面为大家带来土狼（卫宫士郎）的撩妹技巧，感觉自己有主角光环的同学可以试试啦。第一招 放手就吐血红A放大招的时候，士郎拉着Saber的手跑出了攻击范围。Saber表示，我感谢你，但是你可以放手了吧。注意看士郎的套路，直接吐了一口血！这下不用牵手了，直接怀中杀。第二招 胸肌诱惑装无知明明上身半裸躺在别人家里，却装作无知反问对方要干嘛大忌：千万不要说出这种话。刚来的好感度瞬间没了。第三招 呆萌地说出心里话Saber问士郎为什么要救她，士郎一脸纯良，回答说：“我们握过手了不是吗”。看吾王的呆萌表情，攻略成功。第四招 带着别的女生见妹子士郎带着Saber给老师送便当，我樱吃醋了，攻略成功。什么？上面这张不明显？下面这两张呢，知道士郎和Saber要暂住在一起，我樱直接答应和Saber好好相处，满满的日在圣杯既视感。后面三女一男同处一室，更是士郎撩妹成功的展示。第五招 高冷+技术，土狼的高冷成功吸引了美缀关注，他的弓道则彻底征服了妹子。这个故事教育我们，妹子讲笑话的时候不要笑......咦？好好的一部正经动漫怎么被我描述成了里番。我要为fate正名，这是一部相当有意思的正经动漫，堪称阿宅入门级作品。那么各位对撩妹技巧什么看法呢，欢迎在下方评论。吾王镇楼，喜欢请关注，谢谢~', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10319', '0', 'Fate中亦是一个传说，脸接EA手刃英雄王的男人卫宫士郎', '卫宫士郎是《Fate》系列中本篇故事的男主角，亦是Fate世界中一个相当的传说，只要他愿意，即便是最强的英雄王也能打败，即便是遇到最强的宝具乖离剑，也能徒手挡给你看。Fate线，助力亚瑟王击败金闪闪Fate线的卫宫士郎，可以说是比较弱的，本体的战斗力低下，并且由于第一部《Fate》动画制作渲染上的关系，表现力也没有之后重制的UBW线那么华丽，在对付英雄王的一战上，缺乏一些气势，挂开的有些突然，没有把他为何是亚瑟王的剑鞘的由来彻底说清楚，把谜团留到了外传《Fate Zero》中。而反而是士郎和Saber对付Berseker的那一战，真的可谓是绝死绝命，最终召唤了黄金剑一口气杀死海格力斯7次，这个逼，我服。UBW线，手刃金闪闪很多人说，英雄王是放水放过头才让士郎有机可乘。不过按照士郎的说法，英雄王的王之财宝可以找到各路英灵的宝具的原典，反而自己的能力可以和英雄王的王之财宝进行相杀，对付别的英灵没什么大用，但对付英雄王正好是相性有佳，所以英雄王没有一开始就动真格，真的是大意了。不过，英雄王的一句，“我承认，现在的你，更强”，恐怕是对士郎实力最大的认可了。话说凛才是最强的有木有，同时支撑士郎开挂，Saber开大，自己还在污泥里救慎二，不亏是远坂家的女儿，要优雅不要污。HF线，苦战SaberHF线的故事，士郎可以说为了樱可以舍弃一切，召唤射杀百头，最终搭上“麒麟臂”大战Saber，被网友戏称为“离开Saber越远就实力最强”，感觉不无道理。而这条线的最后，可以说已经是拼上了性命，如果不是伊莉雅的献身，恐怕士郎也要灵魂散尽了，不过，能吞下英雄王，让Saber黑化，樱这样的妹妹也只有士郎这样有魄力的妹控才拿的下吧。外传，一个人的圣杯战争在和本篇没有太大关系的外传《魔法少女伊莉雅》中，卫宫士郎依然是一个战斗力爆表的存在。在美游所在的平行世界，卫宫士郎通过INSTALL获得匹敌英灵的战斗力，一个人摆平了整个圣杯战争，还和最强的英灵卡片，英雄王附体的安洁莉卡对峙，并且徒手接下了最强的宝具，乖离剑，并且救出了美游，让美游去了伊莉雅所在的世界。虽然没有打败伪英灵王，但在美游的世界里，依然是战斗力超群的巨侠级别。外传的外传，征服“王胃”的男人在番外故事《卫宫家今天的饭》中，卫宫士郎拿出自己最拿手的好戏，料理，每一顿饭都能招待到让亚瑟王品尝后会心一笑，征服了“王胃”的男人，还有什么征服不了的呢。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10320', '0', '果然，卫宫士郎最强的时候是这条线上', '卫宫作为Fate中吃了不少苦头，但某种程度上来说最后还是尝了不少甜头的人生赢家来说，在不同世界线路上所表现出的实力也有所不同，哪条线上的他最强大？笔者的看法是FATE线个人来说认为这部中的士郎偏弱，初期和普通人打架没两样了，后期主要功绩是投影EX级的宝具阿瓦隆，搭配Saber可以击退金闪闪，嘴炮能力㛑是几条线最强的了。但是综合战力觉得偏弱，难道是补魔过头的原因？UBW线战力最接近红A的感觉，能在凛的支援下使用无限剑制，从投影的质量和数量来看，投影魔术的水平也是相对最高的，本体的近战能力也是相当不错的，和红A、凛的羁绊程度也是最高的。HF线和樱这个黑化的妹子扯上关系，战斗就变得更加粗暴无度，召唤射杀百头，获得红A的手臂，甚至能和黑Saber一战，HF的士郎凶猛无比啊。魔伊中美游的哥哥虽然是Fate衍生作品，和原作关系不算太大，但是作为重要的角色，这个卫宫士郎出乎意料的战力爆棚，会无限剑制，会投影各路宝具，总体来说战斗经验也非常丰富，果然身位妹控就是不一样啊。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10321', '0', '这些伏笔早就暗示了卫宫士郎是红a，可惜在Fate动画里删掉了', '相信不少朋友都看过14年的FateUBW动画，剧情在高潮处才告知红a就是未来的卫宫士郎，但是，在原著故事中其实有很多地方就埋下了伏笔暗示这件事，只是在改编成动画后，导演把这些伏笔给删掉了，残念~~今天阿政带各位看看有哪些在动画中看不到的伏笔。1.archer的初战这是第4集的剧情，之前红a和saber联手对战B叔，红a用他的宝具击退了B叔，在这里红a对远坂凛吐槽说之前的那一战毫无成果，不仅没把B叔干掉还暴露了自己的宝具。动画里远坂凛只是瞪了一眼红a，没说什么。但在原著剧情里，远坂凛在脑中已经快崩溃了，因为她看明白了红a使用宝具的方式，那种方式任何英灵都可以用，但除非是疯了才会真的用。简单来说就是炸弹，红a把自己的宝具注入魔力后当成炸弹扔过去，威力当然大，但爆掉就没了，普通英灵绝对不会把自己仅有的几件宝具当成一次性炸弹扔出去，除非.....这个英灵拥有的武器已经多到爆掉一两件也没关系。这里就暗示了红a的真正能力是投影魔术，能复制出无限的武器。2.卫宫士郎的修炼第12集中卫宫士郎独自在仓库修炼，在动画中这里只是一笔带过，什么都木有，但在原著剧情中，这里有很大一段的内心独白。他回忆起之前和caster龙牙兵的战斗过程，在战斗中他的投影能力已经开始觉醒，可以复制出红a的黑白双剑，并发现只要模仿红a的招式就能很顺畅地将龙牙兵艹翻。而这里在独自一人锻炼时，他又发现，自己在模仿红a的呼吸方式，只要模仿红a，卫宫士郎就能更顺利地使出投影魔术。到此为止，还不知道具体原因，这个伏笔直到卫宫士郎与红a进行对决时才被揭晓，因为红a是未来的卫宫士郎，他的招数和能力是经过千锤百炼最适合卫宫士郎的战斗方式，所以卫宫士郎模仿红a才会变强。3.在教堂迎战caster第14集里，远坂凛带上红a去找caster算账，没想到被红a背叛，眼看就要被做掉，结果卫宫士郎跳出来救场，相信有些盆友肯定很疑惑，卫宫士郎咋知道远坂凛在这里，随随便便就找到了啊？动画没讲原因，但原著内容就讲了，因为头痛。卫宫士郎每次见到远坂凛都会有隐隐的头痛，这种头痛症是在圣杯战争开始之后才出现，而且离远坂凛越近头痛就越强烈，这次他是把头痛当成GPS定位才找到了远坂凛的位置。这个伏笔也是在与红a的决斗中揭晓，卫宫士郎的头痛不是来自远坂凛，而是来自远坂凛身边的红a，红a是未来的卫宫士郎，他们之间每接触一次，红a的知识和经验会传递给卫宫士郎，造成卫宫士郎的头痛。4.城堡前的对话第16集在埋葬伊莉雅的尸体后，远坂凛揭露出了卫宫士郎的生存方式，并对他说如果再按这样的方式继续生活下去的话，将来一定会后悔的。动画中只是表明了卫宫士郎想继续按自己的生活方式往下走的决心，但在原著中，卫宫士郎听完后的第一感觉是，这番话仿佛预言了卫宫士郎的结局。事实也是如此，远坂凛在那时已经知道了红a的身份，并且通过与红a的记忆共享看到了卫宫士郎人生的结局，所以才在这里恳求他不要按那样的方式去生活，否则会步上红a的后尘。个人认为，UBW的故事改编成动画之后，画面虽然炫酷，但在剧情的精巧性上不如原著，目前阿政从原著中找出的伏笔就这些，但貌似原著中的伏笔还有更多，欢迎大家补充哦！最后，来个吾王镇楼。PS：看得不过瘾？关注阿政的公众号吧，干货满满哦，那里没有简单的资讯和图片，只有深度的动漫评析和开车用的动漫资源，欢迎关注公众号：阿政说（ID:azhengshuo）', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10322', '0', '卫宫士郎在故事中投影过这七件宝具，最后一件只有少数人知道', '喜欢fate的盆友肯定晓得，卫宫士郎的本职是铸剑师，只有他拥有能复制别人宝具的能力，在fate故事中，他投影出的宝具一个个都很屌。今天阿政给各位盘点盘点，看看各位都能知道吗？1.干将莫邪这是红a（也就是英灵卫宫）最喜欢用的武器，黑色为阳剑干将，白色的为阴剑莫邪。剑的性能不错，同时装备能增加少许防御力，但如果是正版的干将莫邪还有着【对怪异】这个属性，能将邪神、魔物神马的一刀两断，可惜，红a的投影版就木有这个特性了。不过，作为红a既然把这两把剑作为惯用剑，也开发出了很多新招式，比如奥义【鹤翼三连】，在HF线的故事中，卫宫士郎投影了出干将莫邪并读取了红a的剑技，用这个奥义将黑化的saber的胸口打穿，成了残血状态，残念的是，卫宫士郎自己因为使用了超负荷的投影，灵魂崩坏，成了没有意识的活尸。另外，红a面对任何对手都喜欢用这两把剑先行试探，在圣杯战争的初次对战中，英灵卫宫投影了N多干将莫邪，被某个幸运E的对手吐槽：“二十七！打飞这么多，你还有哇！”2.螺旋剑原型是凯尔特神话中的英雄弗格斯·马克·罗伊的佩剑，他曾手持此剑轰掉阿尔斯特三座大山的山头，威力无比。这把剑是枪哥库丘林的天敌，如果持有者是和阿尔斯特有因缘之人的话，库丘林会因为生前的诅咒，导致战斗必败。在fate故事中，红a把这把剑给用到了极致，他注入魔力之后，将这把剑用弓射出去，当成炸弹引爆了（用来对付B叔），威力比真名解放还上了一个档次。远坂凛看明白了红a的使用方式，虽然在动画版里没说什么，但在原版游戏中，她在心里狠狠吐槽，只有疯子才会把宝具当成一次性的消耗品用吧，那时远坂凛心中已经在疑惑红a到底是什么人。3.石中剑注意，这把剑不是saber手中的誓约胜利之剑，而是saber当年从石头里拔出来的石中剑。这把剑源自英格兰的传说，它于某个圣诞前夜出现在人前，底座是一块四四方方的石头，上面刻着“凡能从石台上拔出此剑者，而且生于英格兰，它便是英格兰全境的国王”。saber正是拔出了这把剑才成为英格兰的亚瑟王，这把剑的功用主要是选王而非战斗，所以它比saber手中的誓约胜利之剑更加华丽，装饰更漂亮。在fate故事中，卫宫士郎因为和saber的契约联系，从她记忆中看到了这把剑的姿态，便在与B叔对决时投影出这把剑，成功将B叔一剑干掉了他七条命。4.万符必将破戒这是C妈的宝具，能破除所有魔术契约。在UBW线中，C妈就是用这件宝具解除了自己与御主之间的关系，然后又用这件宝具夺取了saber的控制权，最后还用这件宝具切断了红a与远坂凛的契约，可以说是用途很大。在HF线中，卫宫士郎就只在最开始见过一次，之后获得红a的左臂时，将这把剑复制了出来，并对黑化间桐樱使用，切断了间桐樱与圣杯中污泥的联系，将间桐樱从圣杯的诅咒中解放了出来。5.炽天覆七重圆环这是红a唯一的一件防御用宝具，人称花瓣盾，原型是典出希腊神话的特洛伊战争中，埃阿斯用来挡下赫克托尔的投枪的青铜盾。盾牌一共有七片，每一片都相当于古代的城墙。在UBW线中，红a用这个盾挡住了枪哥的最强招数，盾被破掉了六片，但用最后一片挡住了枪哥的杀招，在UBW结局中，卫宫士郎也用这面盾挡住吉尔加美什的王之财宝，用来拖延时间完成固有结界。6.约束胜利之剑这是黑化saber的武器，是【誓约胜利之剑】的反面状态，威力比原来还更强一些。在结局中，卫宫士郎在拯救出间桐樱后，投影出了这件他认知中最强的武器，用所有的魔力使用出这件宝具，将圣杯摧毁，然后魔力耗尽的卫宫士郎彻底死在了地底，活下来的间桐樱在卫宫家一直等待着那个永远无法回来的少年，日复一日，年复一年。7.宝石剑多年前，远坂凛的先祖曾遇到平行世界的奇人“宝石翁”，“宝石翁”将其收为弟子，并将这件武器的设计图和原理传授给远坂家的先祖，不过由于制作难度太大，一直无法成功打造，只能以设计图的状态流传下来。这件武器能借用平行世界的力量打击对手，威力强到夸张，是唯一一件能赢过黑化间桐樱的武器，本来以远坂凛的水平不可能创造出来的，但借用卫宫士郎的投影魔术，将这件武器重新再现人间。最后，远坂凛用这件武器将黑化后的间桐樱逼到绝路。PS：看得不过瘾？来“阿政说”逛逛吧，这里没有乏味的资讯和图片，只有深度的动漫评析和最实用的动漫资源，喜欢动漫的盆友都在这里。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10323', '0', '看遍fate系列第一男主卫宫士郎的全部结局 圣杯战争中的人生', '要说fate系列作品中的第一男配角，那毫无疑问就是卫宫士郎了，在差别的作品中士郎所享用的报酬和终局也是不尽相同的，本次我们就来看看凡是有涉及到士郎的fate系列作品其终局都是如何的吧，看过以后不得不说，圣杯和平的最初的胜利者与人生赢家真的是卫宫士郎啊。PS：不算英魂卫宫fate/zero：童年小士郎被公理的同伴切嗣救起，大命不死必有后福的典范。这么多人都便当了就惟独士郎被捡漏了。fate/stay night saber线：惯例终局送走吾王以后本人过上了一般不能再一般的生存，以后就没下文了，不外所幸这条线内部伊莉雅活的出格好，樱没黑化，凛也是干系相称的奇妙，也就是说除去吾王便当了统统看起来都是完满的。fate/stay night ubw线：ubw线士郎的终局都是完满的，战力飙升，手撕金闪闪，还有幸看见了王妃。不管是和远坂凛一同长相厮守仍是最初带上吾王一同“你们都是我的同党”总归这一条线的士郎是最让人倾慕的fate/stay night hf线：终局落差最大的一次，要末神形俱灭留下樱守活寡，要末就是人生美满情敌死光，和姐妹花加上御姐一同去赏花。fate/stay night Last Episode：最好的终局，梅林神助攻，让士郎和saber在阿瓦隆中不老不死长相厮守，今后过上了非常幸运的生存。Fate/hollow ataraxia：第五次圣杯和平完毕后半年，小安披上卫宫士郎的外壳，获得了临时的姿势与品德。最初终局理论上算是阖家欢乐了，并且借助小安士郎胜利的和卡莲与巴姐的刷了一大波的好感度，不外这四天的确遭罪遭的够多的，固然终局是好的。梦想嘉年华：全部就一花心大萝卜，脚踏N条船，最初差点玩脱，是fate系列一切人物终局最好的一部作品还和月姬玩了一把联动。邪术少女伊利亚：能在百合番里开后宫的汉子，估量也只要士郎有这个本领了，不管是伊莉雅仍是美游的，要末就是有车有房怙恃双忙羡煞旁人的一般人，要末就是秒天秒地秒氛围的卫宫巨侠。fate apocrypha四战压根就没发作，以是属于平行天下，士郎交运过连怙恃都活的非常好，和远坂凛、樱还有大蜜斯过着学校爱情悲剧生存，原本差点就要被天草完成RVA一样的异化了，亏了村姑和齐格开配角光环，才保住幸运的小日子。卫宫家今日的饭翻版食戟之灵，完满终局五战五战在一同各人开开心心的在士郎家用饭，比药王还要人生赢家。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10324', '0', '卫宫士郎：让我成为了无情的做饭之王 连枪兵都被我征服', '不如下厨，谈恋爱不如下厨。如果说“京紫”是人类圣经，POP子是人类古兰经的话...那么这部动画就可以称作是人类月经了...这部每月一更的《卫宫家今天的饭》是FATE系列开篇以来，最为轻松治愈的一部。和通常的FATE作品围绕着“圣杯大战”展开所不同。这部动画几乎只是叙述着一些“FSN世界观”下的轻松日常：这里没有圣杯大战，也没有任何打斗场面，更不会开虐。贯穿动画全篇的主题也只有非常简单的一个字，那就是——吃！吾王吃饭的样子炒鸡可爱！！枪兵的虎牙也非常呆萌！在这部动画里，我们可以领略到卫宫士郎那足以撼动整个瓦尔哈拉的超凡厨艺！更可怕的是，这部动画几乎把一样美食从食材的选择，到应该如何对其进行处理，要辅以何种佐料等等各种烹饪当中的具体环节都事无巨细的展示在你面前......有时候甚至让人都不禁怀疑起是不是自己走错了片场，点开了一档什么美食栏目。士郎这厨艺~连枪兵也完全被征服！SABER同学更是一脸的幸福和满足，士郎做的饭菜貌似已经好吃到一种令人忘我的境界~在这部动画里，你甚至可以看到枪兵卖（mo）鱼!不过，令人感到悲伤的是——像这样好看的动画，一个月居然只更新一话！虽然和漫画的更新节奏是一致了，但是对于漫迷们和月厨们来说，还是有点不太过瘾！不多说了，目前，本月份的《卫宫家今天的饭》已经送达。还请大家不要忘记准时食用~', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10325', '0', '人物鉴赏：他不是土狼 他的名字是卫宫士郎（上）', '拿起老的专题 捡起旧的人物 蹭上新的热点FGO今天就来解析这位动漫ACG里我最喜欢在天朝被黑的最惨在圈子里被误会最多卫宫士郎 Emiya Shiro《Fate/stay night》故事内容非常丰富改编动画次数很多人物自身形象复杂所以分成了上下两期首先从人设入手，提到人设这个东西，相信很多人都觉得人设有个鸟用，好看不就得了？但一个人物的人设能透露出很多内涵比如说性格 思想 作品地位等等历经三次主要的版本变动士郎的人设也遭到了小的重做让咱们一个一个的从头开始看最早的原作线稿：Galgame需要代入感，所以总体平庸。男主形象突出玩家自己咋办？年代相近的Deen社：延续了画风和人设风格，除发色外，脸型和着装都可以说是平庸ufotable：进行了大改，发型细节、日常着装、体型概貌、整体气质、表情丰富程度……简直是判若两人。人设总结：除了年代变更带来的画风和美型问题，这三个版本着重表达的人物内在也不一样。总的来看，士郎可以说最突出的就是那一头红发，哪怕是未来的红A，平行世界的卫宫巨侠，红都仍然是他的主色调（P站ID：59487411）红黄等色彩能带给人积极向上的感觉《火影》的鸣人 《海贼》的路飞 《龙珠》的悟空他们都有着深红 暗红 或黄色当做他们人设的主色调作为士郎当时最显眼的标志头发的红是基本没更改的，只是稍稍改了深浅。但是从发型、脸型、瞳孔等细节三个版本就不一样了。社长武内崇的原型人设作为根源，感觉是乍看平凡却透露出一股顽固的劲。虽然平铺，但是带领棱角的发型；虽然普通，但是却比平常人多出一轮的瞳孔。原作游戏中露脸不多的士郎基本做到了平庸、但却富有特点。不至抢夺女主和玩家的风采。但却保持了士郎自己的性格特征。本期主题Deen：士郎因为圆润很多，导致全身都透露出“普通”。原本有棱有角的头发也几乎不见，五官非常正，正的没有什么人物自身的特色。虽然可能有点过分，但确实是满大街走的平凡人设，但这个也和Fate线的基调有关系，咱们后边再说。经历十年武内和业界的画风已然天地一转，士郎的人设经历大幅度重做理所当然，黑白相间的外套 简单直接、时髦感加强，而且在Fate的这个故事里突出了人物的地位。因为Fate这个世界不讲历史和基本法，男的都能给你变成女的，神话历史的英雄模样也是一点不管。各种各样的发型、服装、颜色都有出现，士郎这件纯粹简单的黑白外套反而更好的突出了他的故事地位，黑白也诠释了他外表简单但富有内在的性格。头发发色变身 发型棱角变多《Fate stay night unlimited balde works》结合作品讲述卫宫士郎的故事主题非常合适Deen最大限度照顾了作品带入感和意淫感UFO完全在说四斋蒸鹅心，你的老婆终究是别人的老婆，这就是卫宫士郎的故事。明确人设传达给观众的感受和想法，我们才能更好分析一个人物的故事作用和地位。2006年的《Fate stay night》改编的是原作第一条线Fate线，内容就不用说了，讲的就是天降少女亚瑟王引领平凡高中生走向黑深残战争的胜利……才怪！所有人在提到任何一个版本的《Fate》首先想到的肯定是圣杯战争，但某个不会说人话的菌类想写的就是个简单的boy meets girl的故事。圣杯战争是个夺目的亮点 却不是这个作品的核心，至少不是《Fate stay night》的核心。Deen社的FSN按原作从平凡的高中生入手，不平凡的天降美少女，结为灵魂之友，升华为爱情后经历了不平凡的离别。saber结束不平凡的一生，士郎继续自己不平凡的旅途。这是个典型的天降系故事，只不过有圣杯战争这么个主题。动画也好、原作也罢，Fate线的这个故事确实起到了引导的作用。以不可能是女孩的亚瑟王作为引路人，带你领略了这个故事表层的所有一切。以及引路人棉被呆毛吃货、不懂人心、怎么抽都抽不到的五星王saber阿尔托利亚自己。放到原作三线合一的故事里，她是引导人、是士郎的镜子显而易见。但单独提取Fate线制作而成的动画，这就成了一个几乎看不出来的事情，因为这部作品留给观众最大印象的是圣杯战争、古代的英雄。所以在这个故事里最正常、最普通的那个，毫无疑问会带给观众拖后腿、可有可无、还不如自己的主观印象。恰好士郎在Deen版的人设也全力突出了平凡。没有棱角的发型，温柔但缺乏顽固的眼神，并不突出的身材，走到现实感觉土气的服装再加上现在看来根本就是个标准的路人脸。平心而论，这种人设你其实至少不能说是错。因为Deen的动画也好，原作游戏Fate线也罢，它说的其实就是阿尔托利亚的故事。Deen抓住了故事的核心人物把故事所有的重心放到了saber身上。你能喷画风烂、你能说原创不好、你能说剧情小儿科。但是唯独不能否认川井宪次的音乐和saber塑造的成功，王是有多美丽、多强大、多温柔、多有责任感同时有多惨、多令人伤心。这么个光彩夺目的完美的王的形象，贴身相伴的男主就需要突出她的性格和能力。卫宫士郎在deen版的雏形就有了：有理想 但是实力要弱 突出王的强大；要温柔 但得不会表达 表现王的悲伤；得坚强 但不能太过分 让王的光彩不至于被抢夺。本身在Fate线就缺少纠正和表现自己机会的士郎，在动画化需要的基础上完全成为了saber的陪衬品。也打开了卫宫士郎几乎长达十年被黑的序幕。成为了一个众人眼里：空谈梦想 没有能力 圣母嘴炮 卵用没有缺少魔力 拖王后退 中二爆表 只靠开挂在王身边的为什么不是我的辣鸡中的战斗机表现出来的温柔、善做家务的特点和圣杯战争这么个容易引起中二病幻想的舞台所需要的性格完全相反。这么一个士郎，不被喷 不被骂 不被黑说真的我自己都觉得奇怪。但尽管如此，他也不是土狼、他不是推土机他更不是只有梦想的圣母中二病。八年后的UFO的卫宫士郎，给他平了一波反，这个我们下期再说。==========================微博：阿卡林嫁给我吧微信公众号：JTWMLDMBilibili视频空间：http://space.bilibili.com/670902/#!/indexB站视频地址：http://www.bilibili.com/video/av6989769/', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10326', '0', '好看动漫《fate》系列之神父言峰绮礼', '又是一个连绵细雨天，今天小主们的心情如何呢，樱酱今天为大家带来的是《fate》系列中的神父角色，今天可不是里面的英灵从者咯，神父——言峰绮礼，Fate/stay night中的主要人物，圣堂教会第八秘迹会的司祭及监督者言峰璃正之子，最擅长的是体术攻击，以八极拳为主对灵体攻击也有着极强的伤害能力，在第四次圣杯战中以监督者的身份出现，第五次圣杯战中也是最大的作弊人员。言峰绮礼言峰绮礼不算是恶人但是个坏人，做事不算残忍但是非常邪气，天生的性格阴暗，对别人阐述的美好的事情感觉不到，对不好的事情难以忘怀，正因为这样的性格做事也并非会好到哪里去，在第四次圣杯战中作为魔术召唤师参战从者哈桑·萨巴赫（Assassin）和吉尔伽美什（Archer）到了第五次圣杯战中库·丘林（Lancer）和吉尔伽美什，作为了魔术召唤师，他都有着两个从者。言峰绮礼喜欢吃辣的东西，热衷与麻婆豆腐，卫宫切嗣的太太爱丽斯菲尔曾一度质疑，绮礼在第四次圣杯战争中对切嗣如此执着是否还有爱情上的原因。与后期追求愉悦且为达目的不择手段的形象不符的是，绮礼对中华料理麻婆豆腐有着超乎寻常的喜爱之情，卫宫士郎曾在料理店见过其大口享受麻婆豆腐的情景并觉得相当不可思议。也因此，言峰绮礼这个人物也在观众之中获得了“麻婆神父”的称呼。言峰绮礼与卫宫切嗣言峰绮礼擅长使用体术，与葛木宗一郎非常相似，拳脚格斗术做为基础，但是与葛木不同的是言峰绮礼是有自己最擅长的武器的，黑键专门投掷的武器，带着长刃的剑，黑键的刀身因为是以魔力编织出来的东西，则携带时只有柄。其父亲传授了中国武术八极拳，原本是作为精神休养的一环而传授的，不过随着在代行者时代累积的浴血战斗的经验，慢慢升华成了绮礼流人体破坏术这种扭曲的型态。身穿由Kevlar纤维制成的防弹僧衣，而且滴水不漏地裱上了教会代行者特制的防护咒符。锻炼到极限的肌肉铠甲，就算子弹造成的冲击中保护着骨头和内脏。在就是他的魔术，对教会的信徒来说魔术是异端的行为不过，若是形式改变成了教会流的魔术，则被允许学习。擅长治疗魔术。其处理灵体、精神的治疗技术达到了祭司的水平，右手臂上的多个令咒是第四次圣杯战争后从父亲那里接管来的，拥有异常数量的令咒，即使是活生生的人类，只要使用接近10个也能对英灵给予伤害。言峰绮礼与卫宫切嗣言峰绮礼的妻子克劳蒂亚·奥尔黛西亚，两人在意大利结婚，过了两年的生活后剩下了他们的女儿卡莲，因为言峰绮礼的扭曲性格克劳蒂亚·奥尔黛西亚想要治愈他，最后为了让他感受到情感在他面前自杀。女儿卡莲·奥尔黛西亚自母亲死后就被带到了一个神父那里寄养，直到第五次圣杯之战结束后才在冬木市出现，不但继承了言峰的位置还成为了吉尔伽美什与大狗的魔术召唤师，同样有着母亲美丽容颜还继承了言峰的爱好。在第四次圣杯之战与卫宫切嗣对决时一同被圣杯所释出的黑泥所浸，看见了卫宫切嗣与圣杯意识的对话。言峰绮礼逃脱圣杯的精神世界后，被卫宫切嗣从背后开枪射穿心脏所杀。但因其从者的关系；圣杯中的黑泥无法污染吉尔伽美什，反而顺着吉尔伽美什与御主的契约逆流到绮礼身上，填补了他的心脏。其后，绮礼一直靠着圣杯的内容物的魔力供给来活动心脏停顿了的身体。在第五次圣杯之战中言峰经历两个不同的路线结局，第五次圣杯战争开始前，他用计抢走巴泽特的令咒，成为枪之骑兵的御主，以“监督者”的身份作掩护，隐藏自己参战的事实而在暗中活跃。由库·丘林负责侦察、吉尔伽美什负责最后的战斗是他的基本方针。控制吉尔伽美什的令咒在右腕上，而夺取自巴泽特的令咒在左腕上。路线1：凭借监督者的身份隐藏到最后。击伤远坂凛，绑架依莉雅，在柳洞寺与卫宫士郎决战。最终对决中被卫宫士郎使用十年前绮礼自己赠送给远坂凛的AZOTH剑所杀。路线2：从故事中段开始装死。再次登场时命令库·丘林（Lancer）掏出远坂凛的心脏，库·丘林（Lancer）不从，被绮礼用令咒命令自杀。绮礼准备亲自下手时被濒死的库·丘林（Lancer）用Gáe Bolg刺死。卡莲·奥尔黛西亚言峰因扭曲的性格非常得到吉尔伽美什的看中，两人还除了偷税的梗，同样言峰还杀了自己的老师时辰（都是时辰的错），就这样一个扭曲的性格但是对金钱有所爱恋，在《幻想嘉年华》系列中，言峰因为收拾圣杯战后世的经费而哭泣，后来，女儿卡莲在赚了一大笔钱后，用成捆的钞票扇库丘林的脸而感到兴奋，被金闪闪与大狗吐槽好像觉醒了某人（言峰绮礼）的性格，由此也可以看出，即使是性格扭曲也是可以让金钱来治愈的就是言峰绮礼了。今天的内容就到这里了，有想了解更多的小主们可以在评论区留言！樱酱会为你们解答的！~', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10327', '0', '盘点声优“深井冰”的角色对比：言峰绮礼配了什么放飞自我的角色', '松冈祯丞初在《从零开始的异世界生活》中见到怠惰时，便觉得此声优不一般……有一种即便多残忍也能很喜感的感觉，怠惰花样扭曲的身体和精彩的表情包搭配上松岗爆发的声线，完全塑造了一位“干了很多坏事却不太惹人讨厌的”神经质反派，前前后后的台词够做好几个Rap了!但他却是桐人的声优……确实是《刀剑神域》的桐谷和人的声优……明明可以开后宫，偏偏就要当变态的架势真的拦不住～拦不住啊～小野大辅喜欢小野大辅是从《黑执事》中的塞巴斯蒂安开始，华丽的声线和优雅邪恶的气质十分扣人心弦。OnoD也是我第一位喜欢的声优，虽然在综艺里呆萌的气质有些许反差感，但也很喜欢，便开始补他的作品，直到《齐木楠雄的灾难》中的燃堂力……不要和我说这个缺无数根筋的人是赛巴斯啊!我撑不住……福山润说来惭愧，看鲁殿那会儿7还不了解啥是声优，也一直不知道那段摄人心魄的狂笑出自福山润之口，但看到《黑执事》里的格雷尔时，被这个扭曲又主动、脱线又可爱的人物驱使去看了声优列表……鲁殿这些年是经历了什么啊～张杰杰大是我近两年很喜欢的一位国漫配音演员，不仅动漫，他配的影视剧大多也有看，杰大的声音实在华丽，最喜欢的一位角色是《全职高手》中的叶修，声音塑造的超级符合原著中形象和感觉。反观《开封奇谈》里的白菊花,被展昭打倒在地那一句颤音的抱怨……真不是7想的多……中田让治本年度最“羞耻”的动漫角色非《宇宙战舰提拉米斯》中的“耻毛”莫属，没错……就是“耻毛”，不仅能言善谈，还和别的“耻毛”结了婚……7一开始还好奇声优表怎么写,一看“中田让治”……不是配音言峰绮礼 的声优么？“言峰绮礼”这是配了什么放飞自我的角色啊!', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10328', '0', 'Fate系列中的真男人，我最服的还是这几位！', 'Fate系列中的真男人，我最服的还是这几位！Lancer库丘林言峰绮礼的Servant。拥有很高的敏捷性与白刃战斗力。然而在巴泽特遭到绮礼的暗算，带有令咒的左臂被切断了之后，用令咒的力量被迫同意了Master更换成绮礼。谈到在战斗中存活的技能的话非他人所能比拟，如野兽般的灵敏勇猛和精湛的枪术，即使是在Lancer职阶中他的实力也可以称得上出类拔萃。Lancer迪卢木多富有正义感，注重骑士精神，重情义，对人温和有礼，是以个人名誉而战斗的男人。十分重视友情，就算是在芬恩下令对自己疯狂追捕的过程中，他也坚决不与曾经的伙伴们交手，因对于前世未能尽忠君主而一直抱有遗憾，参加圣杯战争的唯一目的是以骑士之名、尽职前世没能完成的职责，对圣杯本身没有追求。Rider伊斯坎达尔一头红色的短发，红色的瞳孔，红色的巨大风衣。身高高达2米多，浑身肌肉扎结，是一名可用“豪放不羁”形容的男人。对众多强敌持有敬意，有希望征服现代世界的意欲。 在战斗中途不继招募其他徒者作为手下，共同征服世界，后来在与英雄王的对决中被乖离剑贯穿胸膛。卫宫士郎继承了切嗣意志的魔术师见习生，十年前被卷进了大火灾，却被卫宫切嗣救出而成为了唯一的生还者。其后，成为切嗣的养子，接受着魔术的基础训练而成长。偶然召唤出Saber作为Master参加了第五次圣杯战争。为阻止这场战争而战。眼神顽固而直率的少年，从外表上看体格一般，其实很强壮。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10329', '0', '动漫中那些诱人的绝对领域，男孩子都喜欢！', '动漫中那些诱人的绝对领域，男孩子都喜欢！远坂凛《Fate》系列远坂家的现任当主，父亲在战争后期被其弟子言峰绮礼杀害，随后凛接受了其的监护。参加第五次圣杯战争，擅长使用远坂家流传的转换魔术，身为魔术师拥有优秀的资质和能力，圣杯战争后远渡重洋到魔术协会总部时钟塔深造。作为护身用的空手技也相当强。贞德《Fate》系列职阶是Ruler，作为被圣杯战争本身所召唤的英灵，有着正确管理圣杯战争的职责。Ruler不可能在普通的圣杯战争中被召唤出来，但是当一场圣杯战争的形式非常特殊，Ruler就会被召唤。贞德以依附在他人肉体上的形式，作为圣杯大战的Ruler被召唤出来。人格方面是贞德和蕾缇希娅两者的合并，无法进行灵体化。成濑澪《新妹魔王》刃更的义妹，总是逞强的巨乳美少女。继承前魔王能力。由于万理亚在施行契约时出了一些意外，使得她成为刃更的部下，如果有背叛主人或对主人心怀愧疚的话，就会受到契约的诅咒，在情绪失控时可以操控重力，力量却远胜于从父亲继承来的力量。加藤惠《路人女主的养成方法》存在感薄弱。没有加入社团，朋友不是很多但也没有勇气认识更多朋友，是容易一下子被无视的类型。 在被发现就是当时捡帽子的女生之前，没给伦也留下任何印象。相处起来会非常安心。非常好说话，面对伦也的自说自话也会认真去听。总是尽职尽责地关心和顾虑周围的人。爱蜜莉雅《从零开始的异世界生活》与\"嫉妒魔女\"·莎缇拉的外貌一模一样。根据爱蜜莉雅的姑姑菲露托娜所言，银发和紫绀色的眼眸遗传自亲生父亲的精灵家族，温柔的面容则继承自母亲。有着银色长发和蓝紫瞳的半精灵少女，搭档为精灵帕克。曾搭救了被小混混围殴的男主角菜月昴，格外善良温柔，是菜月昴不断死亡所想要拯救的对象。堀北铃音《欢迎来到实力至上主义的教室》D班的领军人物，学习成绩优异，常常表现的很冷漠。为了追赶自己的哥哥堀北学而来到学校，将绫小路倚为日后上升道路上的盟友，认可绫小路是唯一的伙伴，但对方却从没将她当作伙伴，之后在学校的特殊考试中的出色发挥使她渐渐成为领头人物，在失败中渐渐成长，试图靠自己赢得考试。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10330', '0', '从御主到英灵，“腿玩年”的远坂凛到底经历了什么？', '远坂凛是《Fate》系列中高人气的重要角色，UBW线的女主角，并且活跃在《Fate》的各个世界中。她除了做过JK被人畜无害的慎二摸出了“腿玩年”的梗外，在不同的线，不同的平行世界观中，都担当着不同的角色。例如，官方并不只一次让凛拥有从者的能力，又或者说是让从者附体在凛的肉体上。例如美索不达米亚神话中战争女神，伊什塔尔。还有同样出自美索不达米亚神话中的冥界女神，埃列什基伽勒 ，这一对来自同一神话的姐妹。故事中虽然人格是神话中的角色，但是外貌，性格，说话语气都受到远坂凛的影响，这也算是《FGO》中很方面的把拥有历史背景的角色代入《Fate》世界中的形象来说故事的办法。两者的职阶分别是Archer和Lancer。而说到枪职阶的凛，笔者不禁想到新番《Fate Extra Last Encore》中远坂凛作为失去了从者的御主，作为岸波白野的助手登场，但是她似乎也拥有了利用英灵的力量，可以使用Lacner的能力。而官方对这个形态的凛只有没有任何介绍，只是给了一套人设，也许在之后会揭露这个形态的凛的秘密。而《Fate Extra》的故事和本篇《Fate Stay Night》处于平行世界，大体设定共通，但遭遇的故事不同,这个远坂凛和本篇的远坂凛没有绝对的关系，但在性格设计上还是参考了本篇的远坂凛，并且这个是电子空间里的样子，本体是金发的远坂凛。而要说到战斗力，除了前面两个拥有女神附体的远坂凛，《魔法少女伊莉雅》中植入了英灵卡片的远坂凛也算是平行世界中，战斗力很强的存在，但是因为剧情需要，远坂凛将红宝石魔法杖给了伊莉雅，本体就变得非常薄弱，只有普通的魔术师的水平，嘛，远坂凛要是能够单独出一部《魔法少女远坂凛》的话，想必阿宅还是会买账的吧。233', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10331', '0', '马尾傲娇当本命？我只喜欢远坂凛！', '远坂凛远坂凛，第四次圣杯战争参与者——远坂时臣的女儿，由于父亲被杀害，被迫正式成为名门远坂家的继承人，性格好胜又带有傲娇属性，作为第五次圣杯战争Archer的Master参战。远坂凛日常生活中，远坂凛在与主角士郎相同的私立穗群原学园上学，平日里假装人见人爱的“乖乖女”，背后实际是一个爱恶作剧的傲娇双马尾，曾被士郎吐槽为“披着优等生面具的红色恶魔”。远坂凛在第五次圣杯战争中与士郎结盟，战斗中两人发现了圣杯的实质，破坏了圣杯结束了第五次圣杯战争，之后士郎拜凛为师继续学习魔术知识，两人共同到时计塔学习。远坂凛凛与士郎表面虽然是师徒关系，实际两人关系更贴近情侣，原本远坂凛只打算利用士郎获得圣杯战争的胜利，结果却在日常生活中一点一滴感情的积累，最后喜欢上了士郎。远坂凛之所以选择士郎，不仅仅是因为士郎长得帅，也是因为远坂凛的妹妹——樱一开始喜欢士郎，疼爱妹妹的凛打算照顾一下士郎，没想到自己也陷了进去。辣手摧花哪家强？霓虹冬木找士郎！', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10332', '0', 'Fate/Stay Night远坂凛COSPLAY作品欣赏', '今天为大家带来一组Fate/Stay Night远坂凛COSPLAY作品欣赏 cn:淼唯Jaune。如果你喜欢这一组美图的话，留言哦，会为大家提供更多的。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10333', '0', '没想到你是这样的远坂凛，远坂凛声优差点丢失FGO账号', '从互联网方面的角度来讲，我国手游中常见的社交账号联动一键登录，手机验证码登陆方式确实是很方便，即便设备不同只要记住一个主要的社交账号就可以实现数据继承，而在日本手游里用户往往是通过游戏随机给出的数字 ID 加上引继密码来继承数据，这种数据继承方式方式虽然安全性高可以有效杜绝盗号问题，但也会让玩家要特别注意自己的 ID 和引继密码，有时候玩家一糊涂可能账号就找不回来了。今天 Fate 系列中远坂凛的声优植田佳奈就因为自己更换 iPhone 手机忘记了 FGO 的数字 ID 差点没能继承账号数据。植田佳奈作为一名 Fate 声优以及 FGO 玩家突出的特色是脸黑，这点和远坂凛的角色有着一定的契合性，在 Fate 作品中作为贫乏大小姐，远坂凛在第五次圣杯战争中，本想召唤 Saber 结果召唤出了红 A，而红 A 很多时候担任了吐槽凛的角色，而在 FGO 抽卡方面植田佳奈的运气实在是不佳，植田佳奈曾经与岛崎信长、阿部敦在一起看谁先能抽到斯卡哈，最后变成了岛崎信长与植田佳奈两个非洲人的消耗战，到最后二人放弃了。这次植田佳奈换了一台最新的红色 iPhone 手机，还办了 20G 流量套餐，但在更换手机后植田佳奈忘了自己 FGO 的 ID 数字，无法继承自己原来账号的数据，但幸好植田佳奈自己也是个月球氪金人，所以通过提交自己的充值明细，可以证实自己与游戏账号之间的所属关系，在经历一番折腾后顺利的继承数据。植田佳奈也特别提醒大家要注意继承账号的问题。我自己曾经在玩《锁链战记》的时候遇到过一次无法继承数据的问题，当时给世嘉运营写了很多封邮件，邮件里面要列出自己最近的卡牌组队阵容，如果氪金的话还好可以通过写明氪金时间与金额来辅助证明，如果没有氪金的话就 GG 了，当时因为魔神祭的活动氪了一发所有幸运的把数据找了回来。现在玩新的手游下意识的都会在 evernote 里写上游戏的数字 ID，氪金除了为抽卡外也为以后自己找回账号提供方便。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10334', '0', 'FATE系列中的远坂凛', 'FATE系列中的远坂凛', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10335', '0', '制服远坂凛：眼睛真的好好看，我要陷进去了~~', 'AM-M －化妆AM-M －摄影幸会 －后勤K魂 －后期（未经coser本人同意，请勿私自转载）（作品由超能部授权）『 Cosplay控 』精美Cosplay 专业Cosplay资讯 完美Cosplay体验尽在Cosplay控喜欢我们的话，不要忘了关注cosplay控的公众号哦~点击微信右上角的按键→添加朋友→公众号→输入“Cosplay控”就可以关注我们啦~', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10336', '0', '从小被虫子夺去了“贞洁”，《Fate》里最悲惨的女人！', '在广大的Fate世界里，有一位命途多舛的少女，坚强而又让人怜惜，她就是间桐樱。樱原本是远坂时臣的女儿，和凛是姐妹，但为了圣杯却被父亲过继给了间桐家。间桐家的老家主是个活了500年的有着疯狂执念的老头，为了改造樱的身体，老头每天都用虫子折磨樱。成群的虫子在樱的身体里爬来爬去，也就是在这时，樱失去了一个女孩的“贞洁”。好不容易挺过了虫子的折磨，家族的“哥哥”间桐慎二又开始对樱施虐，把所有的怒火都发泄在了这个柔弱的少女身上。等到有了喜欢的人，但却因为自己残缺的身体而感到自卑，根本不敢有多的幻想。在HF樱线中，卫宫巨侠（士郎）将樱从黑圣杯中解放了出来，但自己却又神形俱灭。（樱之梦结局）。在所有的《Fate》作品中，樱的命运都是最让人惋惜的，希望她能被作者们温柔以待。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10337', '0', '盘点动漫中遭遇极惨的角色，佐助：谁能和我比惨？金木：1000', '日本动漫中难免有一些角色受尽了作者虐待的同时也虐待着观众的内心，今天小编就来盘点那些动漫中被作者虐待的人物。间桐樱（fate stay night）从小就被丢进全是虫的地下室，接受虫术的改造，成长过程中还被其非亲生哥哥欺负，HF线五战中成天作为慎二发泄的对象，最后忍无可忍，黑化反杀了慎二。2.浅上藤乃（空之境界）小时候被家族恐惧其能力，用药物治疗让其失去痛觉，后在外上学期间，被小混混无数次欺负，最后一次意外的击打，暂时性的恢复了痛觉，魔眼能力归来，扭断了在场所有小混混。3.金木研被壁虎抓去，一直伤害金木的脚趾，并一直问1000-7=？让其保持清醒，甚至还在金木面前欺负其好友，让金木感到绝望！4.桂言叶（school day）性格温顺，经常受人欺负，不敢反抗，一直压抑着，在被诚哥背叛，黄毛欺负，世界杀害诚哥后，完全黑了！5.美树沙耶加（魔法少女小圆）为心爱的人健康许愿成为魔法少女，却被人抢先告白，然后抛弃自己一直与魔女战斗，而不去净化，最后成为魔女。画师：ファルまろ6.我妻由乃（未来日记）小时候被父母关押在笼子里，长期的囚禁和冷漠对待导致内心扭曲。7.鸢一折纸（约会大作战）为弄清杀害亲人的凶手是谁，委托狂三让其回到过去，但回到过去后，发现凶手正是自己无意导致的过错，内心崩溃。8.(火影忍者）对自己最好的哥哥，早自己面前杀害了双亲，然后在对自己使用“月读”，精神直接崩坏（虽然我们都知道这是鼬不得不做的事情）。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10338', '0', 'fate剧场版（HF线）第二章上映，说一说这条线的女主间桐樱', '间桐樱原名远坂樱，是远坂凛的亲妹妹。应间桐家族的请求，远坂时臣将远坂樱过继给了间桐家族，为了让远坂樱适应间桐家族的魔术回路。在樱小的时候，就将她扔进了装满刻印虫的地下室。​由此可以想到，小樱当时是身心都遭到了间桐家族的摧残，后来有摊上了一个渣男哥哥间桐慎二，这家伙不停的奸污小樱。当年我玩这条线的时候真想冲进去杀了他。后来小樱遇到了温柔的士郎，尽管在这条线中他们两个人受尽了磨难和屈辱，但最终走到了一起。尽管其中一个结局士郎死掉了，虽然很悲伤。但是他们都活在彼此的心中。随说好结局也有，但是那个有点像后宫结局。这个结局对小樱太不公平了。​但是不管怎么说，小樱都是我很喜欢的fate系列女主，尽管吾王是我的真爱，但是我更同情小樱。小樱随时黑化过，但是都是被间桐家族给逼得，我真的很想亲手冲进去把间桐臧硕和间桐慎二给活劈了。​一句话，我喜欢间桐樱。即使她被\"人\"伤害过，侮辱过，我也喜欢间桐樱。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10339', '0', '处女厨不懂《Fate的间桐樱为什么如此受欢迎》因为角色价值在', '大家好，这里是天萌动漫ヽ（=^･ω･^=）丿日本网友将某些非常在意女性是不是处女的人称为「处女厨」，处女厨一旦发现艺人、偶像或虚构的女性角色可能不是处女就会尽全力抹黑攻击，最近在日本讨论区就有人以这种观点提问为什么《Fate》系列的间桐樱如此受欢迎，甚至最近上映的剧场版还创下亮眼票房呢？因为角色的价值根本不在这里，而是在剧情如何塑造她啊……10月14日于日本上映的动画电影「Fate/stay night[Heaven’s Feel]」经典游戏《Fate/stay night》10几年来经历多次改编动画，而真正揭开大量伏笔的HF路线（Heaven’s Feel）终于在最近成为剧场版登上大银幕，累积的票房稳定成长加上新的入场特典陆续推出，相信还可以继续创纪录！上映半个多月已经累积10亿日圆票房！由于间桐樱在HF路线跃升为主要角色，这部电影的好票房一部份也反映着樱的高人气，可是身为处女厨的日本网友却十分不解为什么她如此受欢迎……「不是处女还有这种人气真厉害啊！」这个讨论串引来大量网友激辩角色的人气与是不是处女有没有关系，看过《Fate/Zero》的人都知道樱在小时候……就被虫虫给……说起处女问题引爆最激烈的事件莫过于2008年改编动画的《神薙》了，日本讨论区在动画播映当时传出女主角薙早就不是处女的谣言，因此让动画和原作漫画招来大量批评与抹黑，偏偏刚好又碰上原作者因病休刊，让这把火烧得一发不可收拾……2008年的《神薙》引来大量处女厨批评原作者因病休刊也被谣言说成是逃避处女问题不过间桐樱的粉丝们完全不在意这种事情，反而嘲笑处女厨只会在意这种无聊的小事而不去探究《Fate/stay night》的剧情如何塑造出她的高人气，其实回顾TYPE-MOON官方举办的人气投票就可以发现樱的人气从10多年前到现在都非常高，跟她是不是处女一点关系也没有！2004年《Fate/stay night》第一次人气投票：第6名2006年《Fate/stay night》第二次人气投票：第5名2012年TYPE-MOON 10周年全作品人气投票：第8名即使纳入TYPE-MOON所有作品的角色依旧能挤进前10名，可见粉丝有多么支持樱了！（ﾟдﾟ）大家觉得女性角色的人气跟是不是处女有关系吗？---END---都看到这里了，给个关注吧！~', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10340', '0', 'FATE中命运最悲惨的角色，守护全世界最可爱的“间桐樱”！', '是否真的得不到幸福？间桐樱的归宿在被广大ACG观众所熟知的FATE世界中，有着这么一位从童年开始就经历了万千折磨，令人印象深刻的少女。由于天赋出众而得不到家族的培养，父亲远坂时臣不得不将樱托付给“御三家”中的另一家：间桐。可时辰万万没料到，间桐家的老家主居然是个活了500年，有着疯狂执念，忘记本心的老虫子。樱的悲惨命运从这里开始。为了适应间桐家的魔术回路，老虫子用御虫术改造樱的身体，其中的残忍与折磨相信只要是对FATE有所了解都会对老虫子恨之入骨。樱在间桐家基本上是被当作“工具”来使用。老虫子将她视为传承的容器，“哥哥”间桐慎二同样是一个执念颇深的魔术家，只不过因为魔术回路的问题没被老虫子放在眼里，转而将“不受爷爷重视”的怒火发泄在樱的身上。就是这么一个饱受摧残的少女，依旧遇见了她的幸福。那个黄昏，她看见了操场上一次又一次挑战“不可能”的卫宫士郎。“那个人的话，应该不会抛弃任何东西吧。”她这么想着。在那一次又一次的失败的撑杆跳中，樱得到了前所未有的慰藉。她喜欢上了卫宫士郎。命运弄人，她和士郎都卷入了魔法师的残忍厮杀游戏：“圣杯战争”之中。在FSN的三条线中，唯一一条属于樱的线，却是以永远的等待为结局。FATE线因为慎二掠夺rider的使用权，而被远坂凛保护了起来，到大结局时才出现，但并没有能和士郎在一起。Ubw线，同样是被慎二掠夺了rider的控制权，但结局却可能是最悲惨的，他要在慎二的虐待下度过余生，并且喜欢的卫工士郎和姐姐去了时钟塔，并且由于从小就被虐待的缘故，她要在“不知何为虐待”地虐待下度过余生。HF，卫宫士郎抛弃一切选择樱的路线。这时的卫宫士郎放弃了自己的理想，在经历了重重困难后，将樱从黑圣杯的控制中解救出来，自己却因为和言峰绮礼的战斗与投影圣剑破坏黑圣杯而永远的留在了地下，神形俱灭。{樱之梦结局}所幸，樱最终还是被作者们“放过”，得到了HF线中的GOOD end结局，与士郎，rider，凛共同活了下来，并且作为该线的女主角，得到士郎好感的可能性不言而喻。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10341', '0', '《Fate》里谁的命运最惨？间桐樱和伊利雅不相上下！', '其实比惨比幸福都是很难去比较的，因为没有一个参照衡量，这就好比我们拿中国男足和中国电影业哪个更烂一样，双方领域不同，但却都烂的丧心病狂，至于到底有多烂，对不起，实在是太烂了，烂到超乎我们的想象，感觉没有最烂只有更烂，看哪一个都比另外一个更烂一些（不过近些年的发展是有好转的，这个还挺惊讶的），其实间桐樱和伊利雅的命运都是一样的惨到泪奔，无法很好的比较。那不妨来客观的分析一下吧：首先是重口人妻系——间桐樱原名远坂樱，曾为远坂凛的妹妹，在3-4岁左右时被其父亲为了极其操蛋的原因而过继给间桐家，从此过上了生不如死的生活。 其惨烈之处，大可归为4类：1.每日被无数阴湿恶心的虫子侵入身体，各种蠕动撕咬，从Fate/zero开头脏砚老头说樱在被投入虫穴的第一月每次都不停的尖叫哀嚎，可以推想无论在生理或心理上，都是极为痛苦的。2.在身体被植入刻印虫的情况下，如果无法吸取男性XX就会痛苦万分，同时也不利于刻印虫的生长，所以子五岁起每天都会受到男性的侵犯，一开始是其养父间桐鹤野，然后当间桐慎二目睹了自己父亲侵犯自己义妹后明显极感兴趣，所以在其父力不从心后便承父业，继续OOXX。3.在间桐家长期以来受到的物理虐待，由于其身份比较尴尬，是作为一个半奴隶半能量源的存在，所以地位极其低下，目前可知间桐慎二对其长期殴打以及语言侮辱，由此可推断间桐慎二的父亲的态度与行为应该也差不多。4.对远坂凛的嫉妒，由于是亲姐妹，仅仅因为年纪上的细小差异，便自幼被像垃圾一样丢到别人家，每日生不如死、受尽凌辱和折磨，而另一面，自己同校的姐姐虽然死了父母，但却生活的非常光鲜自如，不仅是人人羡慕的女神，更是意气风发，与樱属于完全不同的极端， 从樱在对待凛的态度上可以推断，她对这个姐姐无疑是有颇深的恨意的，而这种包含着自卑的嫉妒，无疑也是一种精神上的折磨。当然了，更惨的是樱在Fate和UBW两条主线里存在感极低，而在HF线里的normal end中的结局又极惨，True end里的后宫味道太明显，樱肯定不会希望自己和其他3个女人分享自己最爱的男人，所以，啧啧啧，点背不能赖社会。其二，凄惨萝莉系——伊莉雅斯菲尔·冯·爱因兹贝伦是魔术师杀手卫宫切嗣与爱因兹贝伦的人造克隆体爱丽斯菲尔·冯·爱因兹贝伦所生的女儿。在其母化作圣杯并被其父摧毁之后，被作为圣杯容器而培养起来，其悲惨之处，可分为5类。1.在UBW动画里看到，其母爱丽斯菲尔·冯·爱因兹贝伦在被切嗣papa背叛并杀死后化作怨念回到了伊莉雅身边，并告知小女孩其papa彻底的背叛与抛弃了她，当时伊莉雅刚刚9岁，听到消息后精神几乎崩溃，从此开始了半黑化的转变。2.自出生其就被困在城堡里，知道19岁之前没有见过外面的世界，在其父母离开后也没有认识任何人，除了另外两个低质量的人造克隆体作为她的管家与女仆外没有任何对外界的交流，长期的孤立也导致了其三观并不是十分健康，详情可参考fate线里的bad ends.3. 得知自己本身只是一件工具的事实，在UBW里伊莉雅发现了城堡内堆放废弃克隆体的水池，在与无数个自己的\"翻版\"交流后得知，自己本身的人生没有意义可言，只是作为一样工具被制造出来，虽不明显，但对其态度也有一定影响。4.因为自胎盘里就被不断的受到魔术调整，身体在童年时便已通知生长，由于是作为一个用过就扔的容器，所以设计者（其祖父）并没有考虑其寿命，所以寿命极其短暂，间桐脏砚判断伊莉雅再圣杯战争后大概只有一年的寿命。5.在游戏中唯一可以作为父亲的替代品的Berserker不论在任何一条线都会惨死，UBW线中伊莉雅更是在目睹Berserker惨死后被金闪闪滑瞎双目后剖腹挖心而死，极为凄惨，此处默哀一秒钟。我能客观分析的就这么多了，这两人惨的方式不尽相同，但都是正常生活的人难以想象的，最后虽然都被土狼纳入后宫，但其过去的悲哀也不可能一笔带过，我觉得UBW里士狼对理想的坚持更合我胃口，在你们心中，认为她们谁更惨呢？', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10342', '0', '金闪闪吉尔伽美什在历史上到底是怎样的？', '金闪闪是fate系列中人气超高的角色，他的帅气外形和王的气质，以及至高无上的态度令观众大爱。但是fate系列都是参照传说历史或者神话中的人物来进行艺术加工的，那么原始的金闪闪到底是怎么样的一个人物。首先金闪闪被设定为最初始的以为王，王中王的原因，诗因为目前发现最早的一本英雄叙事诗《吉尔伽美什史诗》，这是他被设定为初始之王的依据。金闪闪在性格上最先接触的时候很反派为人实际上很暴力，那是后就算喜欢也是因为人设，这是因为吉尔伽美什最开始是一个很残暴的君主，作恶多端。之后某位基友战斗之后，成为知己，然后共享王权，然后渐渐成为人人爱戴的君主。动画中最后也通过剧情推进，表明闪闪本身是有一个信念的人，确实有作为王的气度，从来不会迷茫。由于年代很久远闪闪被后人说是有三分之二是神，三分之一是人，动画中吉尔伽美什的高傲，鄙视其他英灵也许也是因为这条线索产生的高傲地位吧。《吉尔伽美什史诗》中也记载了他的足迹已经踏遍整个世界，见过了万物，他将珍宝和睿智都为己所用，这就展现了动画中王之财宝和金闪闪看穿一切的设定，金闪闪一直都是抱着玩玩的态度在参加圣杯战争，在他眼里这只是很小的一件事情。其实fate系列对于闪闪还是美化了很多，但是都有能追溯到历史的地方，fate系列吸引人的地方莫过于此。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10343', '0', '《fate zero》吉尔伽美什，最古老的古惑仔——金闪闪！', '《fate》这个系列作品流传已久，在这部以圣杯战争为平台的动漫中，无数英雄接连登场——征服王，亚瑟王，狂战士，英雄王……他们以英灵之姿归来，在这场战争中继续着自己的王道。其中，让人印象最深刻的那就数那位古老者——吉尔伽美什，俗称“金闪闪”。他是从者（Servant）中的最强 ，是”从者杀手“般的存在。拥有典藏了世上一切珍宝的宝库，甚至英灵宝具的原型也被收藏其中，连令人棘手的传说都理所当然般的拥有着。参加圣杯战争的理由其实也很简单，不过是因为在他眼中圣杯也是属于自己的，别人竟敢未经自己允许就窥视争夺，乃是对自己莫大的不敬，因此必须要将其惩戒！作为世界上最古老的王者，他有着最执着的骄傲和最广博的自信。与其说他是人类的王，不如说他是游乐人间的神明。他的王道，不是征服王开疆扩土，勇往直前的豪气，也不是亚瑟王为国为民，虽死犹荣的义气，他的王道是包罗万象，是舍我其谁！我一直认为，作为王，他是最无心的。既会毫不留情地惩戒，也会毫不吝啬地赞扬。金闪闪，一直看得懂人心。当然，金闪闪其实一直是游戏人间的。如果爱打游戏的征服王是逗逼的话，那他其实有点神经质的。至于为什么叫金闪闪，除了他那件黄金闪闪的衣服外，就是他可怕的“黄金律”。那是诅咒一般的存在，吉尔伽美什一生财运缠身，一辈子都不为金钱所困，无论怎样的宝藏最终都会归他所有。同时，他所使用的攻击技能“王之财宝（Gate of Babylon）”即将收集得来的财宝当作箭矢射出！！！换句话就是打开通往黄金之都的门扉，从他的宝物库里把财宝打出去！！！！这是何等败家啊~~/(ㄒoㄒ)/~~这就是我的梦想！因为游戏人生，所以他敬佩亚瑟王，那是一条他没有选择的路，因为面临过选择，所以懂得他的艰难，但是他依然是最强的！有着绝强的、难以被任何东西所污染的信念。在故事的最后：“不要侮辱我。那种程度的诅咒不能吃下去叫什么英雄。这世间全部的恶？哈，要污染我的话还要多三倍！听好了Saber，英雄是背负所看到东西的人。这世上的一切,我在生前就早已背负在身了！”“现在想来，兴许是我失去了追求。能够得到的都已经得到了。这个时代已经不会再让我感到任何欢愉。我所能做的，只有以纯净之身长眠而已——死亡多尝那么几次又何妨？在那之后，我会被无数次的唤醒，无数次的重生，无数次的见证着将我唤醒的那个时代——直到世界终结——直到人类跨越我的星庭，驶出暗沉的大海——抵达苍穹的尽头而获得结论的那一天。”那就是，他在临终前所看见的梦。在“从心所欲而不逾矩”上，他已做到了最好。《》《》《》《》《》《》《》《》《》《》《》《》《》《》《》《》《》《》你们心中的金闪闪是什么样的呢，欢迎评价转发！原创作品，谢谢关注', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10344', '0', '细数一下 在fate中作为顶尖从者的吉尔伽美什真正认真作战的', '相信大家也都知晓吉尔伽美什的大名那么就让我们来看看作为天花板级别黄金三靶之一的金闪闪究竟认真战斗过几次吧。吉尔伽美什按时间线最开始出场在四站中作为Archer降临，不过纵观整个四站唯一一次认真当属在于大帝伊斯坎达尔的对局中对大帝的舍命一击哪怕是吉尔伽美什也不敢大意使用了天之锁将其束缚随后便击杀了伊斯坎达尔。其后在十年后的五战中吉尔伽美什在fate线，ubw线甚至是hf中都因为轻敌大意没有认真导致败亡。而在fate/strange fake中则恰恰相反在刚出场时便感受到了自己的好朋友恩奇都的降临于是立刻便前往希望与其见面。不过因为俩人作为从者降临双方的立场不同，所以俩人也开始了属于他们的战争，在这次当中是毫无疑问的全力全开的认真形态。而在Fate/Extra CCC中吉尔伽美什线面对印度神话中施舍的英雄迦尔纳中也全力对战并承认对手是必须尽全力才能战胜的对手。而最后便是在前不久才结束的fate grand order中的第七特异点 绝对魔兽战役 巴比伦尼亚中面对创世之母 提亚马特 凭借自己和御主藤丸立香的帮助最终成功将提亚马特击败平定特异点。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10345', '0', 'FATE中的吉尔伽美什是好是坏？“金闪闪”究竟算贤王还是暴君', '吉尔伽美什作为目前世界上最古老的英雄史诗的主人公，在FATE中被奉为最古的英（ji）雄（an），从者实力的天花板，“英灵杀手”，路灯王，型月的战力标尺，乌鲁克斧王，奈须蘑菇亲爹，黄金三靶之首，肘子王。。。咳。。咳，说正经的，吉尔伽美什的实力就不用多说了，旺财，天之锁，EA秒天秒地秒空气，理所应当的顶级从者实力（俗称闪级）因为早期被设定为最强从者，再加上原作那劣迹斑斑，导致他经常就实力问题被人拿来比较（荣登冠位指定靶子）。个人认为金闪闪的人物形象本该是复杂的，但他所呈现给观众的却是“分裂”的，其中一个重要原因就是fate系列很多故事并非出自一人之手，而每个人对金闪闪理解都有所出入（毕竟即便是同一个作者对一个复杂的人物形象也难以把握），所导致的结果就是金闪闪一部作品一个形象，这也是他粉厨半边天的原因。尽管后来“补丁狂魔”奈须蘑菇试图用各种补充来解释（例如在与恩齐都相遇前后，受圣杯黑泥的影响等）但终究难以弥补观众先入为主的思维习惯。所以说一千个作品有一千个吉尔伽美什，不同的作品的金闪闪理应分开来看。譬如在fsn原作中他就是个反派模板，凶残又傲慢的二闪（虐杀伊莉雅我特么怎么能原谅！！），在ubw线中因自大轻敌被士郎砍断胳膊败退，在saber线临消失时才有了英雄王该有的样子。满脸写着反派的二闪而在fz前传中“愉悦闪”的形象就值得玩味多了，在剧情中压倒性的实力展现，与言峰绮礼关于愉悦的讨论，和那句“王来承认,王来允许,王来背负整个世界。”一时间勾起了不少观众们的兴趣。麻婆人生导师——愉悦闪在fgo第七章中作为乌鲁克的国王，恪尽职守的领导者，威严又不失宽怀，暴躁而不减睿智，还傲娇（误），带领乌鲁克的子民面对注定的末日坚守到最后一刻，第七章的优秀表现成功扭转了我对金闪闪的一贯看法，没法不让人喜欢（官方洗白最为致命，好吧，我承认还是你蘑菇比较强）至于在魔伊中幼闪宛如个腹黑版黄金哆啦A梦，在fe ccc中就是个月球男友，在《幻想嘉年华》中秒变二逼死宅加土豪，金闪闪在各种程度上都很复杂就是了。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10346', '0', 'FATE：吉尔伽美什杀了萝莉还被疯狂追捧，金闪闪到底有何魅力', '“杂修！”Fate里争议最大的人物绝对就是金皮卡了。人家召唤出来的都是帮忙打架的servant，可是时辰召唤出的就是个难伺候的祖宗，master和servant的地位整个颠倒。最初见到金皮卡，直接拜倒在他的裤脚下，跪舔闪闪的颜，但是越看越对他的心情越纠结，一直到他砍了萝莉，只想把他拉出来暴揍一顿：杀萝莉不能忍。可是就算他杀了萌萝莉，金闪闪的人气还是没有下降，那么，这个人物到底有什么魅力，能让姑娘们还有一些蓝孩子觉醒了抖M之魂：金闪闪魅力之一：这位可是吉尔伽美什啊！吉尔伽美什是谁？现存最古老的史诗里面美索不达米亚地区的一位王啊。在史诗里，吉尔伽美什三分之一是人的高人格，其它三分之二是神。所以时辰召唤出他的时候为什么说赢定了，因为其他servant在历史上再怎么厉害，出生时也只是普通人，有神格的英雄和自己历练成英雄的普通人打，肯定是神更占上风。吉尔伽美什在叙事诗中被塑造成一位几乎无所不能的大英雄，他和恩奇都的故事又为这位英雄增添了很多人性化的魅力，知道这部史诗的人在FATE中看到吉尔伽美什就会自动对这个人物产生好感，所以“吉尔伽美什”这个原型就能拉来很多人气了。另外，不觉得“吉尔伽美什”这个名字很好听么？放小说里就是杰克苏男主的位置啊！金闪闪魅力之二：开挂的战斗力。别人只有一两件宝具，人家金皮卡有一个宝具库！别的英灵从天上飞到地上再飞到天上打得眼花缭乱，金皮卡要么抱胸站那一动不动，要么直接一把帝王椅歪头托腮坐在那，从身后一片闪瞎人的金色里飞出百八十把宝剑，手都不用抬的，就这么悠哉哉看着对方招架，太从容。金闪闪魅力之三：形象反差萌。战斗形态的金皮卡头发像打了二斤发胶，金色直发和坚硬的金色铠甲，让这位英灵一看就是找不到弱点的伟大英雄姿态，千年前和恩奇都联手退敌的时候不知道会不会比这还要英勇。待在家里的金闪闪头发软塌塌的，经常手里举着一杯红酒，王宗贵族感的优雅感扑面而来。第一眼看到压根没认出来这是谁……发自内心的好想摸头发，看起来手感就会很好……休闲的针织衫和长裤，忽略掉他蔑视人类的眼神，真的乖萌乖萌的感觉。金闪闪魅力之四：颜好即正义。闪闪人气不管怎么折腾都落不下来的最主要的原因，应该就是他的颜值太高了。红瞳金发，身材美好，会打扮有品味，不那么傲娇的时候又绅士体贴，不舔他舔谁？三次元哪里去找这样的尤物？所以，有颜、有钱、有声望、有品味、有实力，这样的从古代英雄人气怎么可能低，就算总是被闪闪骂“杂修”，人家也是有资本骂的。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10347', '0', '金闪闪被克制？fate zero七骑英灵战力口胡', '第一当然是Archer认真闪（吉尔伽美什）了，作为世界现存的最古老的叙事诗的主人公，最古的英（ji）雄（an），从者实力的天花板，路灯王，乌鲁克斧王，奈须蘑菇亲爹，黄金三靶之首，撒币弓兵。。。好了不口胡了，由于作为最古老的英雄史诗，金闪闪在fate中被设定为所有英雄传说的渊源，这也是他的宝具王之财宝（Gate of Babylon）的根据，宝物库中拥有所有历史传说中的武器原型，除此之外还有规诫神灵的天之锁（Enkidu），以及属于金闪闪自己的对界宝具天地乖离开辟之星（Enuma Elish），正因如此金闪闪在fz原作中被设定为“英灵杀手”，在整个fate世界也属于超一流从者（什么你说被士郎砍了(Д≡д)!？其次是你月看板娘，fate第一女主角，Saber呆毛王（阿尔托莉雅·潘德拉贡）作为赫赫有名的亚瑟王传说中的主人公，其事迹广为流传（什么你说亚瑟王不可能是个女孩？我听不见）呆毛王在fz中的形象塑造有所偏差，不过战绩还是摆在那里：与rider刚宝具取胜，击败兰斯洛特。作为最有优势的职阶saber，面板摆在那里，当然，少不了对城宝具ex咖喱棒（Excalibur）除此之外，其实她还有最终的宝具 远离一切的理想乡（Avalon），在fsn中成功抵挡住金闪闪的EA。作为fate中最主要的角色，战力当然属于一流级别。（所以fgo呆毛王快给我加强啊！！！）第三就是大帝了，Rider伊斯坎达尔。在fz中应该是塑造的最成功的角色了吧。犹记得大帝释放宝具王之军势（AionionHetairoi）时的震撼，以及当他对战吉尔伽美什时那份奋不顾身，一往无前的气概。作为fz七骑英灵中知名度最高的亚历山大大帝，他可以说非常符合我心中的形象了（尽管在历史中大帝是个矮子）。不过他的神威车轮在对咖喱棒中败下阵来（给韦伯留下了一生的阴影），并最终负于金闪闪，故排前两者之后。第四是长江，哦，不，Berserker兰斯洛特了。其实开始关于他与大帝谁先谁后我还是挺纠结的，毕竟如果这位手抓地球的话。。。（雾）兰斯洛特爵士是圆桌骑士团最负盛名的骑士。在fz中因为对亚瑟王的执念而狂化，参加圣杯战争只是想被亚瑟王亲手杀死（圆桌骑士团只有王厨和扭曲的王厨233）兰斯洛特作为骑士团最强战力之一，再加上b阶的狂化，武力可以说非常强了。由于宝具【骑士不死于徒手】跟【王之财宝】微妙的相性（能接住并支配从旺财中投出的宝具）正面面对金闪闪不落下风（固然也有金闪闪态度不认真的因素在）第五是Lancer迪卢木多，这男人有两把刷子。自古枪兵幸运e，悲剧的迪卢木多呦（排第五也够悲剧啊，别怪我）费奥那勇士团的首席战士，上边兰斯洛特传说的原型，普通英灵的战力单位（误）。典型的骑士，富有正义感，重情义，对人温和有礼，被赐予爱的泪痣，“所有女士的心上人”，亦是悲惨命运的源头。在与saber的对决中互有来回，甚至占了上风。不过碍于宝具所限，与其他英灵相比并没有太大优势。值得一说，迪卢木多在传说中除双枪外还有双剑和一把魔刀，他的战力绝不止于此。第六，蓝胡子Caster吉尔斯·德·莱斯。圣女贞德的战友（单相思），法国元帅，不过他留给后人的却是在城堡进行惨绝人寰的炼金实验的恐怖传说。作为圣杯战争中最棘手的caster，加上其扭曲的思想，最后造成众人共同讨伐的麻烦存在。最终在宝具咖喱棒的的攻击中看到贞德，得到救赎。元帅太辣眼了（精神污染A）就不放图了。最后，Assassin百貌哈桑，emmmmm，嗯。作为最弱职阶assassin，排最后也没什么奇怪（山之翁：喵喵喵？）在众哈桑中，百貌也算不上多强，不过刺客又不是正面作战的，百貌的能力在圣杯战争中是最好用的，先诈死再苟到最后岂不美滋滋，只可惜他的御主不是切嗣。fate并不是热血战斗番，战斗起推动剧情作用，而设定有时也会为戏剧性牺牲（日常吃书）所以在fate中不必太过纠结于战力，以上都是口胡。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10348', '0', '看完FATE全系列：关于英雄王金闪闪——吉尔伽美什的一些看法', 'Fate（フェイト）是TYPE-MOON原作的系列作品，于2004年1月30日发售的PC平台18禁文字冒险游戏《Fate/stay night》，同时也是TYPE-MOON商业化后初次亮相的作品。由于广受欢迎，吸引了一些作家参与制作，由本篇衍生诸多人气作品。英雄王吉尔伽美什其中最重要的几部是FATE/ZERO; FATE STAY NIGHT三部曲; FATE/EXTRA。最让广大粉丝关注与热爱的便是FATE STAY NIGHT。下面就让我们来看看金闪闪的实力吧（从各部说起）。金皮卡首先当然是金闪闪的一些背景介绍：真名——吉尔伽美什，其真实身份为古代美索不达米亚地区苏美尔王朝的都市国家乌鲁克的第五任国王，人类最古老的史诗《吉尔伽美什史诗》的主人公，最古老的“英雄王”。他有着“三分之二为神，三分之一为人”的神性，性格高傲，曾是自认为无人能敌的暴君。 拥有所有英雄持有的宝具原型的宝具——王之财宝（GATE OF BABYLON）。金闪闪王之财宝作为一件存储性宝具，其本身并没有任何的战斗力，仅仅是能够无限的放进和取出东西，其本身是由吉尔伽美什的生前传说升华而成。对于其他从者来说最大的威胁便是宝库中的无数宝具。拥有世间一切财富的王之传说所升华出来的宝具——王之财宝甚至连后世的财富都拥有着，其中既有神话时代的兵装，也有中世纪的各种奇异武器。可以说是典藏了世上一切珍宝的宝库，甚至不但英灵宝具的原型也被收藏其中，连令人棘手的传说都理所当然般的拥有着。普通的英灵根本不是这个男人的对手。西装吉尔伽美什但作为FATE中经常的反派担当，这位英雄王总是败在自己的缺点下——性格高傲蛮横、唯我独尊，习惯称呼别人为“杂种”。讨厌俗人的搭话，曾说过“不自高自大做什么王”。有这般的性格是基于自身强大的实力，但由于过于自信，时常会犯粗心、轻敌的毛病。尤其是在「Unlimited Blade Works」剧情线，在士郎开启的无限剑制里，吉尔伽美什发动“王之财宝”对其进行攻击，却被士郎用投影的武器挡下。同时处于暗处的Archer（英灵卫宫）在关键时刻开启的“炽天覆七重圆环”也帮助了抵挡了吉尔伽美什的进攻。士郎凭借着自己近身战的优势，以及吉尔伽美什的大意和慢心，砍断了吉尔伽美什的右手。这场战斗以吉尔伽美什的失败告终。另一方面由于远坂凛救出了间桐慎二，流满黑泥的圣杯便欲将吉尔伽美什作为魔力吸收。吉尔伽美什用天之锁捆住卫宫士郎，想借此挣脱出黑泥的漩涡。最后Archer在暗中发出的一箭正中吉尔伽美什的眉心，将其射入黑泥当中。另外在多部作品中关于金闪闪的混沌善的争论？笔者的理解是所谓混沌即自身所认为之善，吉尔伽美什作为第一位王可以说是定义世间善恶之人，正如小说中所说：“王来承认，王来允许，王来背负整个世界。”', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10349', '0', 'FGO金闪闪吉尔伽美什最详细攻略与心得，让萌新快速上手', 'FGO金闪闪吉尔伽美什超详细共略与心得强力的弓职很多,如宝具威力破格的罗宾汉,帅气的正义伙伴红A,胸前宏伟的女神.但最后我选择率先练满的是英雄王闪闪01原因无他,因为他真的太帅了,天上天下唯我独尊的气质让人无法抗拒,动画中与征服王的王者对决那段更是让我久久无法自己.不过动画归动画,游戏中的性能将在下面介绍.首先介绍的是他的保有技能的部分第一个保有技能为领袖魅力A+02相当强悍的技能,增加我方全体的攻击且维持整整3回合.仅仅一等时增加量就高达10.8趴,且通常可无障碍的升至四级,加成量是可怕的13.7趴,大大增加我方的攻击输出.第二个保有技能为黄金律A03增加NP的上升率,一等时增加20趴,无障碍的升至4等可达29趴.以下为黄金律四等,蓝牌放在三位,且无首张蓝牌加乘的状况.加成后闪闪一张蓝牌可收集约20~25左右的NP,大约4~5张蓝牌可开一次宝具.不过闪闪收集爆击星的性能优异,爆击的击率很高,若运气好碰上爆击,可增收集约30左右的NP,等于是三张蓝牌就可开一次宝具不过如果有宝石翁的话,这招便显得鸡肋了些,毕竟NP一开始就处于几乎满的状况...第三个保有技能为收藏家EX04奠定闪闪爆击流优势的技能,仅仅一级就可以使集星率提高300趴,升至四级更可达390趴.闪闪的集星率本来就很高,搭上这招更是如虎添翼.再者闪闪的攻击为多段打击,基本上只要有闪闪的绿牌参与,基本上该回合10颗左右的星星不是问题.闪闪三张同牌的话,更可打出15颗星左右一回合.此时闪闪的爆击率几乎是7.8成在算,可说是爆击流的神技.再来介绍阶职技能的部分.第一个阶职技能为魔抗E05只提升微微的4趴异常状态耐性,很少...第二个阶职技能为单独行动A+06优异的技能.奠定闪闪爆击流优势的技能之二,补正爆击威力高达11趴,使得闪闪的爆击威力很痛,相当的痛.第三个阶职技能为神性B07增加175点的伤害,很少08接著是闪闪宝具的部分,为天地乖离开闢之星,是只有被英雄王认可的真正英雄才有资格看到的至宝.说是这样说,不过真实的性能将在以下讨论.首先难点非常明确,那就是闪闪为五星限定抽,宝具的等级和威力因此相当难以提升.一等时的倍率只有非常少的300趴,不过有红牌的加乘因此变为450趴.有趣的地方为对从者特攻,伤害会在乘与150趴,也就是说攻击从者会有675趴的伤害,这就有不错的威力了,不过这也就是说要是对手不是从者,威力就偏低,这导致了闪闪在刷肥时的立场较为尴尬...推荐的礼装为...主要是增加爆击性能的礼装都满推荐,以下优雅与闪闪相性相当好,增加攻击时的爆击星掉落率,加上闪闪的多段打击,可将效能发挥到最大09再来是宝石魔术?対影,增加爆击伤害,与星星量产,暴击威力高的闪闪也是相性良好10接著是比较难取得的活动限定礼装ムーンライト?フェスト,增加集星的机率与暴击的威力,与闪闪相性良好11最后就是宝石翁,虽然闪闪的宝具威力普普,但加上瞬间强化割割草还是没有问题的.12', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10350', '0', '论，《Fate》系列中的金闪闪（吉尔伽美什）为何人气那么高', '吉尔伽美什（《Fate》系列中的角色），“英雄王”外号有金闪闪，金皮卡等，古巴比伦的第一位也是人类历史上第一位王，拥有着世间一切宝物的“王之宝库”以及分开天地之初的“乖离剑”在fate系列中皆有登场，而动画登场的最老版是《命运守护夜》最新的则是《FATE/UBW》而人们对印象最深刻与出名的便是在《FATE/ZERO》中得知的。而吉尔伽美什的原型则是出自史诗《吉尔伽美什史诗》人类最古老的史诗之一，而其年代太久远近三分之一的部分已遗失。但是剩下的部分依然能感受到苏美尔人对他们伟大英雄（吉尔伽美什）的崇拜赞美之情。《FATE/ZERO》中的每一个人物都十分的富有个性与魅力，其中最出众的便是 征服王：“亚历山大大帝”与英雄王：“吉尔伽美什”了，骑士王：“阿尔托利亚·潘德拉贡”虽然也有与王者等同的人格魅力，但是在老虚笔下的骑士王却缺乏王者的魄力与雄心壮志是个中干外强的“小姑娘”“为了挽救天下那般苍生，被众人推筹到孤独尽头的她，她始终，只是一个纯洁无暇的少女”说回“英雄王”。相比英雄王，征服王的人格魅力与在大众的知名程度来讲都比英雄王要高，粗犷豪放的态度，包容一切的心胸，敢于追寻一切，以及做过的事就绝不后悔的人生态度，一位有极强人格魅力的“理想主义”的帝王。而英雄王，高傲蛮横、唯我独尊，习惯称呼别人为“杂修”典型的“自我中心主义”英雄王表面看起来是个十分不可一世的暴君，但实际上他是个十分有理性思想与哲理的明君（嘛，不过性格上确实挺狂傲）。《FATE》系列中最像一位王的王者，那便是“吉尔伽美什”他以一位王的视角居高临下，唯我独尊的看待一切，而这却有恰恰是一位王应有的风范，同样他也有着能与之匹配的强悍实力。能够一眼看穿任何事物的他，却很少插手事物本身的行为规律，能一眼事物本质的他，却十分欣赏灵魂本质也就是有着自我意识的人。而在故事的最后，桀骜不驯，唯我独尊的他，这个世间唯一的王者，竟然承认了亚历山大大帝的王者地位。人们十分崇拜向往征服王，是因为他有着包容一切的心胸，比天还高的理想，相比于臣下，更像是在一起的兄弟与家人。而英雄王，人们更多的则是尊敬，因为那是遥不可及的一个目标，但是他却又是无比的正确，如此的强大。他做的行为甚至说的每一句话都让人很不爽，却又不是毫无道理与逻辑的，这世上也只有他有实力也有资格能做这些。征服王似乎是个触手可及的理想。而英雄王，则是一般众人遥不可及的梦想。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10351', '0', 'FGO永远的金闪闪，吉尔伽美什另类的风格分析，强力英灵科普', 'FGO浅谈吉尔伽美什今天我想介绍的是吉尔伽美什(金闪闪，以下简称闪闪)虽然目前绝对不算最强的英灵，却是我最看好的英灵至于为什麽不算最强，以及我最看好的种种原因就让我娓娓道来吧102吉尔伽美什通称金闪闪.闪闪你也可以叫他胖虎还是什麽黄金乌龙茶，随便啦以下我就试著用维基的资料带过一下他的相关介绍1.基本资料03人物能力是属于前后期型相对的中期成长速度就慢在六十等之后成长幅度才会慢慢回来牌组部分属于很特殊的两红两蓝一绿目前为数不多的弓兵两红卡组以四星以上来看的话是唯一的两红弓兵(四星以下有自爆弓)除了两红卡组之外，连宝具都是红卡所以这非常容易打出大量的输出运气不错抽到两红卡也可以打出Burst Brave Chain2.宝具04宝具是范围伤害技能对从者有额外加成不过对星属性英灵就没这个加成了oc的话效果还会往上加宝具整体来说还不错，但目前并不出色原因是还没开放宝具解放的绊关卡所以以目前来说，这是他目前不太出色的原因但以打从者来说，伤害还能看，宝具解放后的伤害是可以期待的3.技能0506カリスマA+目前最高等级的全体Buff满等的情况来说可以全体增伤破20%的超强Buff07黄金率A也是目前最高级的黄金律虽然看起来很强，但在目前的环境下来说其实并不出色主要的原因是技能不好练，这招在初期的情况下NP加成没有太理想但我觉得主要的原因其实是宝石翁太强势有了宝石翁，其实你根本就不太需要开这个技能当你想开宝具的时候通常NP早已是满的了我认为这招日后点高，外加玩暴击流的情况下是可以期待的然后在某些高难度关卡，也是有办法做到一场开两次宝具的状况但碍于我只是无课玩家，这部分没有办法在现阶段测试(技能点高的情况08コレクターEX同样是目前最高等级的吸星技能，也就是把上一回打到的星星集中于自身基本上要吸掉绝大多数的星星很容易这招还满好用的，加上闪闪本身就是最强攻击判定每次攻击都有五下的判定的情况下，要打出大量的星星非常容易一次连段共20连击，很容易星星一次就破十颗这有多强，我拿开了千里眼的红A跟你比，红A初期没点高千里眼的情况下一次连段，星星大概都没超过10吧(虽然红A真正会开千里眼的时候多半是配合宝具去开)闪闪在有这招的情况下除了本身可以玩普攻流+月女神礼装也可以配合能够打出大量星星的角色，例如穿刺公4.被动0910对魔力E不容易中状态但是等级只有E11单独行动A+配合本身的攻击判定和集星技能，很容易打出大量伤害12神性B虽然看起来很强，不过伤害是加固定值的印象中不是很多5.适合的阵容献丑了，以下是我的阵容13大概上的玩法就是...算好卡牌之后，穿刺公开大，下一回合给闪闪集星完美的打出两回合的高伤害伪娘灵基三之后也可以用嘲讽在队伍中适当的帮助队友去扛伤害(补充:我的伪娘再过几天就灵基了! 在这之前都是拿圣乔治帮扛伤害卡牌方面也建议是找有两红两蓝的队友个人用上面这种队伍，通常是再找自己的宝石翁吾王来搭(本人玩了四个帐号QQ)两红两蓝是个人最喜欢的卡牌，比较不容易卡牌五蓝的话有点不容易打出三蓝但是六蓝就比较容易了，同样的六红也很容易打(虐菜来说，红卡则是越多越好)外加我开局花了大量的时间手动刷帐号，有幸刷出双宝石翁所以通常在3/2的时候就很容易有宝具可以开配卡方面也同样很好配(因为你多了宝具可以去搭配卡牌使用)对了，不要哭哭我一直说什么宝石翁什么宝石翁的不是我特别喜欢宝石翁，是因为宝石翁就是一张做坏的礼装在现有环境下，几乎任何礼装在宝石翁面前都像是个玩笑至于其他建议搭配的队友...以闪闪为主力的话，自然建议找剑兵来帮忙砍枪兵十选一的伪娘刚好是剑兵，还能扛伤害，刚好两红两蓝，还可男可女(?)离题了...咳咳个人觉得目前来说最配的就吾王吧同样两蓝两红，有カリスマ和直感在以现在版本的环境下来说(需要爆发力.清场能力的环境)还满适合的当然，换黑S也没问题如果有需要打单体王的关卡则是找穿刺主要是在狂战职中优秀的生存能力，外加开大之后的星星两红卡+狂化EX也是方便清怪碰到特殊关卡找龙娘也没问题(像是上次的女神关卡)龙娘本身也有カリスマ，外加有技二能做单体破防本身有技三的情况下生存能力也很好，不用特别照顾不过两绿卡算是小小的不足然后其他大多数的关卡其实通通找B职就能搞定，缺范围清场找兰斯玉藻，其他就找金时穿刺海叔三红配两红(穿刺两红就是了)，非常好打出大量的稳定伤害我上面之所以没有特别强调九红卡的阵容是因为九红卡是虐菜限定打某些高难度关卡，九红卡的阵容没有嘲讽很容易大爆炸14', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10352', '0', '在fate里不全是时臣的错—人物详解：间桐脏砚', '.间桐脏砚，间桐樱和间桐慎二的祖父，实际上是六代之前的间桐家初代当主，活了五百年的大魔术师。舍弃了本来的肉体，并操纵魔术性质的虫子来组成暂时的肉体。作为间桐家的初代当主，与圣杯战争的创始有关。第五次圣杯战争中的真Assassin的Master。企图在第五次圣杯战争的幕后实现不老不死。有着完全没有魔术师天赋的长子间桐鹤野，与次子fate前传中的Berserker的master间桐雁夜，.长孙间桐慎二（一个我不想去评论的人），与养女间桐樱（时臣的次女，fate中最惨的一个角色，可以说fate中的最搞事情的人就是脏砚）。间桐家本来的家名叫”佐尔根“，移居到日本后，把名字”玛奇里（Makiri)“换读成”间桐(Matou)“作为家名，把原本的姓氏”佐尔根（Zolgen/Zoruken）“取谐音改成名字“脏砚（zuoken）”在初代时脏砚有着一头飘逸的蓝发和英俊的面容，详见下面fate go出场的玛奇里·佐尔根。.500年后再fate本章中的形象是秃头，四肢如木乃伊般干瘦，深陷的眼窝中露出矍铄的精光，驼背的矮个儿，无论外貌或行为都异于寻常的怪人。出现时总伴着股难闻的腐败的气味。身着群青色与深灰色搭配的和服，手拿木头拐杖。.脏砚的性格：狡猾、残忍。对生存抱有异样的执着，为了延长生命可以将他人利用、犠牲到底的人面兽心。用花言巧语推动周围的人而逐渐接近目的。不过据樱所说偶尔也会莫名其妙的露出慈祥老人的一面。过去似乎是个志向远大的人，但那个理想早已从间桐的精神中消失了。厌恶日渐腐烂的灵魂，又不想一事无成死去，久而久之已化成执念。把自身化作虫子，寄生于他人从而延长寿命。身体跟妖怪无异，为此不喜欢阳光，平时都躲在阴暗的地方。据绮礼所说跟吸血虫差不了多少——不论是特性、性格，都像是阴湿的蛞蝓。但是脏砚曾经其实是一个有着美好的愿望“废绝一切的恶”的一位正直的魔法师，但是为了得到圣杯的漫长的时间里，渐渐忘记过去变成了现在的样子。fate go中一件礼装五百年的妄执就是描述的脏砚—曾经，宏愿与男子同在。但流逝的时间，将他的一切都变成了异形。姿态，灵魂，连那个愿望一并。圣杯战争的全部经过我在之前的一篇介绍里已经说过，今天就只说说脏砚在全部圣杯战争里所做的事。第二次圣杯战争，三家制定规则时，脏砚担当了令咒制作。第四次圣杯战争从脏砚那代起，『佐尔根』的子孙就开始渐渐失去魔术回路，到间桐慎二这代则是完全衰落的状态。为让间桐家能作为魔术师家系延续下去，在第四次圣杯战争开始的一年前，脏砚与远坂时臣交涉将远坂樱过继到间桐家。为了让樱适应间桐家的魔术，对她的肉体（魔术属性）进行改造。所以一切不全是时臣的错，fate中最爱搞事情的其实是脏砚。但是在圣杯战争开始前脏砚接受了间桐雁夜用圣杯换樱离开间桐家的条件。但此时雁夜作为魔术师的水平没达到Master的资格，故接受雁夜另一个要求，用刻印虫来提升他的能力。但脏砚从一开始就不太看好雁夜能赢得圣杯，更偏向是对他的制裁。一年后第四次圣杯战争开幕，雁夜的改造也已完成。在脏砚提议下召唤出了最耗魔的职介“Berserker”。此后脏砚一直在幕后观察圣杯战争发展，偶尔给雁夜一些“帮助”。第四次圣杯战争结束后，脏砚趁火打劫，回收了被Saber破坏的圣杯的碎片。第五次圣杯战争脏砚让樱从第五次圣杯战争开始的一年半前就监视着卫宫士郎。将樱制作出来的伪臣之书让给慎二，使Rider（美杜莎）成为他的Servent。在《HF》线中，利用自己设计的从者系统的漏洞从佐佐木小次郎的腹腔中召唤出了真Assassin（哈桑·萨巴赫），中途介入圣杯战争。配合十年前就开始准备的“王牌”在幕后蠢蠢欲动。注意到身在圣杯里面的安哥拉·曼纽。将圣杯的破片作为触媒而生出的刻印虫埋入樱的体内，把她转变为不完全的小圣杯。让樱成为安哥拉·曼纽的Master。打算等樱被安哥拉·曼纽夺取意识之后再夺取樱的身体。然后换到安哥拉·曼纽的灵魂里，借助冬木圣杯真正的机能“第三魔法（灵魂物质化）”实现不老不死。.在HF线的True end中，脏砚的过去被揭开，他寻求永生的最初目的只是为了实现年轻时的梦想——“废绝一切的恶”。换言之，是灭却人类所怀的“业”。他认为这只能藉着到达根源来实现，。但是越到后来越发现，这个梦想需要大量的时间，而自己，却已经快油尽灯枯了。为了亲眼目睹其将来，不惜把自己的身体变成虫子以谋求延命。不过，悠久的岁月侵蚀了他的灵魂，最终，他遗忘了最初的梦想，成为了放弃躯壳只求永生的怪物。暗中操纵着樱/黑影，在爱因兹贝伦之森被言峰绮礼提着脑袋在石头上摩擦，之后躯体被净化，实际上躯体藏在樱的心脏，但是在最终战被樱挖出来捏死。仅存的最后分身在大空洞苟延残喘，遇到了穿着天之衣的伊莉雅，想起了冬之圣女羽斯缇萨的样子，死前终于记起了自己的愿望。Fate/Apocrypha（最大快人心的一个系列）由于第三次圣杯战争时爱因兹贝伦没有选择召唤Avenger，而是召唤了Ruler职介，从而导致与《Fate/stay night》的世界产生分歧。第三次圣杯战争的Master之一，达尼克·普雷斯通·尤格多米雷尼亚偶然间发现了大圣杯的所在。在圣杯战争结束后，借助所属的纳粹德国的军力，排除了御三家与日本帝国陆军，抢夺了大圣杯。与达尼克爆发激烈大战的脏砚在大圣杯遭到夺取的冲击下几乎沦为废人。也由于缺乏后继者，魔术师的家系毫无未来可言。游戏fate go中在第四章《死界魔雾都市》以玛奇里·佐尔根的身份登场。因无法实现的大愿而使灵魂渐渐腐朽时受所罗门感召，奉他为王协助他执行灭世。得到圣杯后在1888年的伦敦与查尔斯·巴贝奇与所罗门召唤帕拉塞尔苏斯执行毁灭英国的「魔雾计划」。帕拉赛尔苏斯与巴贝奇被击败后于地下铁深处的大蒸汽机关迎击主角一行，召唤第八柱魔神巴巴托斯。在被击败后付出包含生命在内的一切召唤出尼古拉·特斯拉并附加狂化，企图以他的雷电使魔雾活性化葬送一切。虽然马基里在召唤特斯拉时加入一些类似狂化的咏唱，不过特斯拉还是凭自己的意志来行动。当然狂化并非完全没有影响，特斯拉也表示自己本来应该是深爱人类的英灵才对，但现在却只想在魔雾中心来发雷击，使魔雾活性化毁灭一切。主角方虽然成功在地下通道赶上了特斯拉，但是因为特斯拉的雷电可以活性化他周围的魔雾，主角方逐渐居于下风，但是凭借莫德雷德的宝具展开反', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10353', '0', 'Fate天之杯，第一章就挂了3个英灵！最令人胃疼的圣杯战争来', 'Fate意为命运，三大线最后一条，是为天之杯的樱线。Fate HF同样来自月球人对命运的思考，它和前两部稍有不同，不是表面上最阴暗的Fate，而是对Fate一词来说，这部天之杯的圣杯战争把“命运”推到了最巅峰的境界。HF的残酷远超想象，阴险恶心的老虫子间桐脏砚为实现他的不老不死的愿望混入圣杯战争。原本出身在远坂家的樱被送给间桐家作为魔法师的继承者，而间桐家的魔法，对女孩子来说是一场比恶梦还要可怕的灾难。尽管第一章的伊始我们所见到的樱腼腆可爱，很有领家妹妹的味道。但不要忘记从间桐雁夜被远坂时臣打败时起，樱就在忍受刻印虫的非人道修炼，更遑论间桐慎二的欺负，他的魔法才能卑微很嫉妒被老虫子脏砚看好的樱，拳脚有加施以凌辱。樱呈现给士郎的越是纯粹美好，黑樱的存在就越残忍。黑樱的存在是HF里诸多角色究极对立化命运的最高展现。樱希望士郎不要做什么正义的伙伴，不要把那么多的责任往自己身上扛，只要做卫宫士郎就好了。但很多年前，在恐惧的大火中，士郎为当初懦弱的自己而自责，未能去拯救火中哭喊的人群捂住双耳逃避，是一个卫宫切嗣的男人从火中救出了他，从此卫宫士郎视切嗣为最大的榜样。命运是一个错误接着一个错误，还是一个美好接着一个美好？卫宫士郎看到切嗣救下自己，做到自己未能做到的事，在那个全世界都在燃烧的地方，他正是看到这一点才选择要追逐切嗣的脚步，要成为一个像他那样的男人，做正义的伙伴，可他不知道切嗣的过往又是怎么得来的，士郎做看到的切嗣是一个看透了圣杯本质的男人最后的结果面，最重要的过程面却忽略了。因此他在没有经历过人生的阶段起，就在学着怎么去做正义的伙伴。慎二的变坏、樱的不幸、无辜民众的死……只要身边有不幸的人或事，卫宫士郎最会站出来，最终的结果卫宫巨侠红A的下场凄惨。而回到HF里，间桐脏砚的加入和麻婆神父使得整个天之杯变得扑朔迷离，盘踞在柳洞寺的Caster美狄亚和守门员佐佐木小次郎竟然是被哈桑以闪电般的速度给秒了，两位在UBW大显身手的英灵在HF里结结实实的打了波酱油。可怜的大狗遭到算计，心脏被哈桑给吞了，呆毛王亦遭摆布，被黑影吞噬成为黑Sbaer。要知道这才是第一章的事，嚣张跋扈目中无人的脏砚差点大满贯，如果不是Rider美杜莎即时赶到，恐怕天之杯的故事就此画上句号了。挂了3个英灵会让人痛觉惋惜，尤其是Caster的尸体还被脏砚利用，卑鄙到无耻都无法形容的老虫子以她人的血为自己续命，像脏砚这样的角色，是注定好要被灭得体无完肤的。不过，HF最大的看点还不在于厮杀和价值轮回的报应，最主要的还是男主士郎的命运，士郎的命运，更具体就是他的爱情抉择。也是本篇最令人胃疼的地方。Saber骑士王的圣洁灵魂注定她会一切以士郎为先，HF里虽不会像UBW那么展开，但Saber为保护士郎而战，化为黑Saber也是不争的事实，但却被士郎以正义的一方消灭；可爱的远坂凛和士郎保持着相敬如宾的友情，两人组队的经历也不免惋惜其结局，凛无法下手伤害樱反而被刺穿身体，创造出樱的解救机会。就这样，前面两条线的女主，Saber也好，凛也好，为成全士郎和樱，全部消失。当年那个内心呐喊要成为正义的伙伴的男孩，为心爱的樱，不惜与所有人为敌。理想终究败给现实，尽管士郎和樱走在了一起，可没有Sbaer和凛，这是不完整的。这也是为什么HF将命运一词推到最颠覆的地步的原因，好在动画表现的只是一种结局，大家更喜欢的是Rider、Saber、凛、樱、士郎都存活下的这个结局。但在本届动画里，天之杯的故事，能够和士郎走在一起，只有樱一个，所以说HF也是最令人胃疼的圣杯战争！不说啦，开饭开饭', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10354', '0', '血砚（民间故事）', ' 程承志考中进士后，被朝廷委任为松山县令，就带着妻子袁红袖高高兴兴地去上任了。 这天路过一个小镇，此时正是集日，街上人头攒动，一片繁荣景象。程承志心里高兴，就和妻子一起慢慢逛着，顺便采买一些日用之物。 这时，一名老人来到程承志面前，笑道：“看公子应该是个读书人吧，小人这里有一块砚想卖出去，不知能不能入公子的眼。”说罢就从袋中拿出一块砚来。 程承志一看，只见砚石中散布金黄色小点，黑底黄星，宛若夜幕繁星。接过来细看，但觉温润细腻，纹理清晰，星晕明显，一看就知道这是砚中极品金星砚，轻轻敲击，有一种清越的金属声。再观雕刻的猛虎图案，与石上花纹配合得天衣无缝，显然出于名家之手。他不禁问道：“这是哪来的？” 老人笑道：“这是我祖上留下来的，说来让公子见笑了，小人祖上也曾是读书人，只是到了小人这代不成器，没人再读书。因家里急用钱，只好变卖。”程承志爱不释手，再一问价格，也不算很贵，就将这块歙砚买了下来。 有了这块砚，程承志每次坐在书桌前，都觉得手中的笔平添了几分灵气。每次同僚来访，看到这块歙砚，都赞不绝口，说他无意中淘到了宝贝。 这天早上程承志刚醒过来，就看到妻子袁红袖坐在身旁，脸色苍白。他吃了一惊，就问：“出了什么事？是不是病了？” 袁红袖摇摇头，道：“我做了一个梦，梦见那块歙砚变成了一只猛虎，说我欠了它二十两银子，扑过来咬我。” 程承志大乐，笑道：“是不是你见我太喜欢那块砚了，觉得它抢了我对你的爱，才有这样的怪梦？放心吧，你在我心中，什么也替代不了。”程承志是寒门之子，而袁红袖却是大户人家的女儿，她的家人本来是很反对两人结合的，但袁红袖却铁了心要嫁他，家人没办法，只能答应。因而程承志对于妻子除了爱之外，更带着感激。幸好皇天不负苦心人，他终于考取了功名，也算是不负她一片深情。 见妻子仍闷闷不乐，程承志就笑道：“我这就去拿砚过来给你看看，看它能不能真变成一只老虎。”说罢来到书房，刚想伸手，就吃了一惊。 只见砚上刻着的那只老虎眼中流出一点儿红色的液体来，像是血从眼中流出似的。这时袁红袖也跟着走了过来，看到砚上的血滴，也吃了一惊，道：“这……这是怎么回事？难道这砚真的会变成老虎？” 程承志也莫名其妙，真不知虎眼中的血迹和妻子的梦有什么关联，只得将砚拿去洗了。这一天，他心里老想着血砚的事，心里总觉得有些不安。 他正在公堂上处理一些事情，突然使女匆匆跑来，叫道：“老爷，不好了，夫人突然病了。” 程承志吓了一跳，急忙跑回家，只见袁红袖躺在床上，脸色苍白，地上还有血迹。使女道：“夫人刚才吐了好多血，也不知患了什么病。” 这时，大夫也来到了，给袁红袖诊脉后，道：“夫人的病暂无性命之忧，只是非本人单方不可，偏偏有些药是紧缺的，一时不好找，得花些钱到外地去求购。”然后开了个方子。程承志一看果然有几味药并不常用，只得吩咐人到外地去买。 就这样折腾了近半个月，袁红袖的病才渐渐好了。这天她对程承志道：“我算了一下，这场病花的钱正好是二十两，莫非当时的梦是真的？” 两人顿时流出了冷汗，莫非这块歙砚已经看到了她将要到来的灾难，在向他们示警？程承志拿起那块砚来看，却看不出任何异常，真不知那血是怎么流出来的。 日子就这样过去了。这天早上，程承志刚醒来，又看到袁红袖呆呆地坐在身边出神，他惊声问道：“是不是又有什么梦了？” 袁红袖点了点头，道：“我也不知道为什么，又梦到那只老虎了。好像是说，这次我们要损失一百两的家产，所以心里有些慌。” 程承志安慰道：“别想太多了，也许上次的梦只是巧合罢了，就算真有财物损失，只要我们大家没事，比什么都强。” 尽管如此说，但两人的心都觉得有些不安，立即打开书房去看那块砚石。这一看，更是目瞪口呆，只见砚上刻着的虎眼中，又有一缕血迹。 他们吩咐家里人一切小心，防火防盗。几天过去了，什么事也没有发生，夫妻俩这才松了一口气，看来上次的事只是巧合罢了。 这晚一家人吃过晚饭，正在屋里谈论着县里的事情，突然从大门口一瘸一拐地走进一个人。这人衣不遮体，身上的衣服几乎都成了布条儿，一看就是一个乞丐。 程承志眉头皱了皱，刚想开口问，那人却看着袁红袖，道：“大小姐不认识老朽了？” 袁红袖仔细一看，不禁叫道：“这……这不是赵叔吗？你怎么来到这的？” 这人正是袁家的管家赵叔，他“扑通”一下跪了下来，叫道：“大小姐，老朽对不起你啊。”  夫妻俩急忙过去，将赵叔扶到椅子上坐好。袁红袖一看这情形也有些急，忙问道：“是不是家里出了什么事？我爹娘他们怎么样了？” 赵叔道：“老爷没事，家里也没事，只是老朽出了一点儿事，对不起大小姐了。”这才说出事情的经过。原来，袁红袖的父母听说了袁红袖生病的事情，就托赵叔来探望。考虑到程承志刚上任，手头也不宽裕，还带来了一百两银子。赵叔跟着几个商人搭伙赶路，不料被一伙强盗盯上了，他们不但把银子抢走，还要将人杀死灭口。幸好赵叔机灵，装死才逃过了毒手。 将赵叔安顿好后，夫妻俩不禁面面相觑，他们来到书房，拿起这块歙砚细看，却仍看不出有何异常。袁红袖道：“看样子，这是一块有灵气的砚石，还是叫人去打听一下它的来历吧。” 那天使女跟他们一起买歙砚，见过老人的面，所以就叫她去打听，程承志还派一个男家人跟着保护。十多天后使女回来了，夫妻俩急忙问：“打听到了吗？” 使女点了点头，道：“这块歙砚果然有点儿怪异。” 原来老人卖歙砚时，并没有说真话，歙砚并不是他家传的。那个老人曾在一名县令家里打杂，据说这块歙砚是一名道士送给县令的，谁知没过多久，县令总做怪梦，在梦中有一只老虎向他索债。结果没几天，家里就真的失财了，不是有人生病就是失火，损失往往都和梦中老虎索债的数目差不多。那名县令恼了，就将这块砚丢到河里，谁知两天后，砚又自己回到了书房里，县令更怒，就叫老人将这块砚带到很远的地方去砸烂。老人看这砚实在好看，不忍心下手，而是拿到山洞里藏了起来。 程承志问：“县令后来还做梦吗？” 使女道：“从此以后，县令果然没有再做梦，也没再过问砚石的事，这事才算平息下来。不过只过了几年，县令却死了。” 袁红袖一惊，问道：“死了？怎么死的？” 使女道：“据说是贪赃枉法，被朝廷处斩的。其实这县令也很有才华，只可惜太贪了，结果让人告到京城里去了，朝廷一查果有其事，于是就把他杀了。老人见事隔多年，这才拿出砚来卖。” 这名县令被杀的事，程承志也知道，皇上为了让天下当官的看看', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10355', '0', '间桐雁夜：樱，我一定会救你的', '看到这个题目，相信不少看过《Fate/Zero》的人都会点进来，默默的在心中感叹。\"你说说，雁夜这人，天天盯着时臣他老婆干啥，活该这样。\"\"都是为了救别人家的Loli？看，出事儿了吧。\"的确，为了救远坂时臣的女儿樱，间桐雁夜确实付出了比其他人更多的努力。他回家跟自己的父亲谈判，但是他的父亲（那个老不死的虫爹）并不打算放掉可怜的樱。雁夜还是晚了一步，樱的贞洁最终被虫爹库中之虫侵蚀。原先的樱是远坂时臣超级宠爱的女儿。她和凛是十分好的亲姐妹。但是由于怕两姐妹长大后互相争执，于是才把樱送到了间桐家，想着间桐家也是魔法世家，樱一定能在那边得到更好的照顾，学习那些能令她强大的魔法。樱与凛一直以来都是一起玩的姐妹。想想雁夜会帮助远坂葵，之后再杀掉远坂葵，其实一切的来源都是因爱生恨。他喜欢上了远坂时臣的老婆远坂葵，但是远坂时臣貌似对此并没有表现出什么不满，好像也不太在意，貌似在远坂时臣的心里，也并不是很看得起间桐雁夜。远坂时臣心里很清楚，葵是喜欢他的，雁夜永远不会得到她。而自己的女儿，也有了个好的归宿，自己已经是个很优秀的父亲了。远坂葵是雁夜从小的暗恋对象，尽管她已嫁给远坂时臣，但是自己会尽力帮助葵。没想到救樱失败，把自己搭了进去，也没有成功。他还答应樱，带她去找妈妈，再像原来那样，一起玩，但是承诺没有兑现。间桐雁夜加入了圣杯战争的一瞬间，就已经是个废掉的棋子了。兰斯洛特是一个狂战士，狂战士和其他职阶的英灵相比，耗费的魔力比其他的人更多。雁夜召唤出的若是个Caster，说不定可以自己储存魔力；召唤的若是个Saber，可以靠令咒强化；召唤的若是个Lancer，也绝大多数都拥有幸运属性，幸运E分分钟战斗续航，运气好出个狗哥还有回避；召唤的若是Assassin，更会尽量节省魔力完成刺杀。于是在兰斯洛特和亚瑟王对抗的时候，魔力耗尽，自己也献出了生命。最终，他没能救的了樱。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10356', '0', '砚边随想录', '砚边随想录作者：蓝建田■ 写碑贵有书卷气．文柔、虚灵、通达的心性是写碑的基础．文柔之极，方能返虚入浑，返朴归真。至柔才能走向至刚．否则，碑派这条路走不远．■写碑贵在籍碑养我阳刚，古朴，雄健，厚重之气，过于刻意描摹其圭角突兀之形，我以为意义不大。单纯写一碑而毕肖，不如融合多家，饶有碑意而又不泥于某碑面目为高。■ 多数传统书法线条精深、文化内蕴丰富、耐人寻味，但结体含蓄内敛，张力不足，视觉冲击力弱，往往不宜写大字，创作大幅作品．时下书坛也有另一类书法作品，初看大开大合，笔头很放得开，声势夺人，但线条粗浅，干瘪乏韵，银样蜡枪头，不耐久看．我所欣赏的是精深有内涵的线条和开张外拓的结体的结合。■ 努力锻造一种有深度、有内涵、有感染力和穿透力、有自己特色的个性化的线条语言，并将其贯穿于各种书体的创作之中，这一直是我的追求目标。■ 学书之道，因人而异．有的人先写好，后写特；有的人先写特，后写好．但最后要特！要有自己的笔墨语言．个性是艺术的生命！笔墨能触及自我灵魂的最深处，肯定是独一无二的。都说是字如其人，但这是有条件的。这条件就是笔墨技法的娴熟，笔墨表达能力的具备，达到得心应手，心手双畅无碍。而这需要长期艰苦的训练，学习!并不是所有的人随便涂几笔，都能充分展现出其人性情。■从艺者当有自知之明——当明已之长，当明已之短．平日用功当攻已之短，创作应扬已之长．■ 书法贵有文学味，书法贵有哲学味，书法尤其贵有道气。技道兼得，天人合一，斯为神品。■“功力”与“功夫”是二个完全不同的概念。今人往往把他们混淆了。“功力”是体现在作品中的情感感染力。他取决于作者“入古”有多深，“气”有多沉。“入古”越深，“气”越沉，则其书情感感染力越强，或谓“功力”越深。而“功夫”则是作者对作书之法——技巧的准确，熟练的把握与驾驭的程度。如果书者不从其它方面去开掘书艺，片面地发展功夫——技巧，则很容易“因熟生俗”，所以“功夫”越好书艺未必就越高。■ 书贵熟后生，雅后朴。■ 骨须硬，气须和。做人如此，作字亦如此。■ 精能是奔放的基础，所谓“淳然后肆”。未能精微，即求奔放，往往失之粗野无韵。文柔之极，始返刚建。对于运笔，有人主张宜快，有人主张宜慢，我认为，当顺书家的情性之自然，要在快而不滑，慢而不滞。■ 书法贵在“耐看”。耐看与否关键在线条，线条须得“深”，“化”。深者，墨气深沉，古味盎然，力透纸背；化者，和雅，儒雅之谓也，可以让人感觉到线条里蕴含有丰富的文化气息。能深未必能化，能化未必能深，既深且化，始为难能。好的字，笔墨线条中自有一种渊深莫测的东西，使人摸不着底，看不透。■ 有的人是用眼睛看书法，看它的漂亮；有的人是用心灵品味书法，品赏它的味道、内蕴。品赏需要时间，需要静对、玩味再三；品赏还需要品赏者本身具备相当的人生阅历、艺术阅历、文化涵养。这样才能和观赏对象产生碰撞、共鸣------■ 我看字，偏向喜欢平实而有味道的字。不喜欢过于夸张、变形、狂野、诡异、故弄玄虚的字。平实还须有味——内蕴丰厚，耐人寻味。初看有型，细品索然，味同嚼蜡，那就不好---书法作品应该具备较长的欣赏时效性。■书法艺术无疑要继承传统。但书法传统是什么？怎样继承传统？我以为,书法传统当包括二个方面：其一是潜藏在历代书法作品中传统文化的人文精神之内核，主要是儒、道、释的哲理思想，姑且称之为“道统”；其二是体现在历代经典书法作品中的笔墨技法，即规范的书法语言，姑且称之为“法统”。既不能只重视“道统”而忽视“法统”，也不能只重视“法统”而忽视“道统”！应该二者并重。■ 传统书法审美观念需要传承，更需要拓宽和改造。书法审美观念的改造其实质是对传统知识份子心理定势和人格形像的改造。我一直不太喜欢中国传统的特别是唐宋以后的书生形像：四体不勤，五谷不分，说的多做的少，优雅有余，强劲不足．我认为的理想人格应该是老子、孔子的智慧再加上一些美国西部牛仔的体格和精神．平常看电影电视时我对很现代，很时尚，但同时又能从他们身上闻到一些唐诗宋词气息的那一类男人情有独钟．当然，现代社会也有一小部分男人，着长衫马褂，手执折扇，满口之呼者也，我觉得其实是矫柔造作，大可不必。■ 书法艺术的古典美与现代美不同．古典书法侧重于＂漂亮＂，现代书法则侧重于＂可爱＂，追求情趣、味道，不太讲究“漂亮”。这不仅在书法界，其他艺术领域也有类似现象，如影视界，这些年“丑星”大行其道，而长相漂亮的男演员则被称之为“奶油小生”。言下不无鄙薄，不屑之意。这不仅体现于中国艺术史，也体现于西方艺术史。文艺复兴时期的大师如达．芬奇、拉斐尔，他们的作品，特别是笔下的女性形像全是优雅、漂亮之至。而这种优雅、漂亮的女性形像在西方现代美术家笔下已很难找到。我们看到的是罗丹的老妓女，毕加索笔下的那些身体状如面粉袋、水桶在海边狂跑的妇女……■ 在书法史上，阴柔与阳刚，文雅与质朴这对矛盾对立而统一，在一定的条件下相互转化，体现了艺术发展的辩证关系。秦汉时期，以汉隶为代表的那种雄健、质朴、生机勃勃之美得到充分的张扬，并且深入普及于民间。在这以后，士宦阶层开始标榜、倡导另一种优雅、文柔、含蓄之美，来修正取代之。东晋王羲之的书法把这种美推到极至．唐宋之后，由于统冶者的大力推崇、倡导，使之越来越深入人心，普及于民间。国人的精神也越来越文雅、文化甚至于文弱，直至当外族、洋夷入侵时，我们便老打败仗。于是到了明清近代，一些有识之士又开始提倡阳刚、雄健、质朴、开张之美，以挽积弱之弊。傅山的＂宁拙毋巧、宁丑毋媚、宁直率毋安排＂，康有为的＂尊碑卑唐＂，鲁迅的＂关键在于强健民族的灵魂＂即是其代表．如此反复，螺旋上升，月亮下去，太阳又升起来了……■ 历史上，帖书多出自文人雅士之手，是高度文明的产物。碑字多出自原始先民之手。魏碑产生于南北朝时期的北方，当时北方正处于“五胡乱华”时期……文明人和原始人各有利弊长短，文明程度愈高，人种愈趋退化，离道愈远，所谓“为学日益，为道日损”。原始人虽然有诸多短缺，但他们体魄健壮、质朴天真，更富有开拓进取冒险精神，两者若能相互融合，取长补短，定能孕育出更高程度的文明。事实上，正是以晋贴为代表的汉文化，和以魏碑为代表的胡文化的融合，才使中华文明在隋唐时期出现再一次复兴。唐以后，如宋末、明末也都是来自原始游牧部落的入侵，给古老的汉民族输入新鲜血液，', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10357', '0', 'P站美图推荐——《点兔》桐间纱路', '今天为大家带来的是动漫《请问您今天要来点兔子吗？》中桐间纱路的图集。多说无益，先舔为上！有兴趣的小伙伴可以去P站为原作收藏并打分哦！ID：42771990ID：43532820ID：44769769ID：45697560ID：46829352ID：48109159ID：53011722ID：53570264ID：49451694ID：53150981想要图包却不会去P站找图的小伙伴可以搜索并关注“动漫之家”微信公众号，也可以扫一扫下方二维码，小编会定期推荐和更新图包哒！小伙伴如果有想看的画师作品或某些类型的图也可以告诉小编哦！', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10358', '0', '千年的“砚”遇', '古人云：“文人之有砚，犹美人之有镜也”。一块石头，有了文人参与，便大不同了。砚，也称“砚台”，文房四宝之一。中国传统文房用具，始于汉代。砚虽然在“笔墨纸砚”的排次中位居殿军，但从某一方面来说，却居领衔地位，所谓“四宝”砚为首，这是由于它质地坚实，能传之百代的缘故。昊拍一方砚，讲述的是一个工艺美学的故事。中国砚台经过历朝历代的不断演变，其制作工艺愈发成熟精致。特别是一些优质石料的发现，加上制砚匠人的娴熟的制作手艺，让砚台成了珍玩藏品。至此，有了广东肇庆端砚、安徽歙县歙砚、甘肃卓尼洮河砚、山西绛县澄泥砚的“四大名砚”。1、端砚：端砚出产于唐代初期端州（今广东肇庆），世称端砚为“群砚之首”，不但具有“体重而轻，质刚而柔，摸之寂寞无纤响，按之如小儿肌肤，温软嫩而不滑”之特点，更具有“秀面多姿，呵气研墨，发墨不损笔毫”的长处。昊拍2、洮（táo）砚：洮砚的生产距今已有1300多年的历史。产于定西市岷州一带。洮砚以其石色碧绿、雅丽珍奇、质坚而细、晶莹如玉、扣之无声、呵之可出水珠、发墨快而不损毫、储墨久而不干涸的特点饮誉海内外，历来为宫廷雅室的珍品，文人墨客的瑰宝，馈赠亲友的佳礼，古玩库存中的奇葩。历代文人、学者、书画家对洮砚赋铭咏诗，赞叹不已。昊拍3、歙（shè）砚：歙石的产地以婺源与歙县交界处的龙尾山下溪涧为最优，所以歙砚又称“龙尾砚”。其特点是色如碧云，声如金石，湿润如玉，墨峦浮艳。其石坚润，抚之如肌，磨之有锋，涩水留笔，滑不拒墨，墨小易干，涤之立净。自唐以来，一直保持其名砚地位。昊拍4、澄（dèng）泥砚：绛州澄泥砚始创于唐代，历史悠久，当时曾被列为“贡砚”。它不是石块砚，是用沙泥烧炼制成的砚，质地类瓦，属陶瓷砚的一种，非石砚材。其特点是质地坚硬耐磨，易发墨，且不耗墨。澄泥砚由于原料来源不同、烧制时间不同，具有鳝鱼黄、蟹壳青、绿豆砂、玫瑰紫等不同颜色。昊拍“四大名砚”之外，亦有种类繁多的名砚供书者珍藏使用。砚无定形，就古来名砚而言，大致有四直式砚，圆砚(人面砚)，风字砚，棱形砚，圭形砚，钟形砚，葫芦形砚。荷叶砚，龟形砚，竹节砚，井田砚，杂式砚，琴样砚，芴砚，鼎砚，汉壶砚，瓜砚，蟠桃砚，双鱼砚，琵琶砚，石鼓形砚，腰鼓砚及自然式砚等。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10359', '0', '在动漫《fate/zero》中，卫宫切嗣为什么会选择杀父杀妻', '卫宫切嗣，是日本动漫《fate/zero》中的男主角。第四次圣杯战争中Saber的Master（御主），且是卫宫士郎的养父。有人说他这一生，杀父杀妻杀母（妻子为间接杀害）坑儿子坑女儿，是一个可悲又可怜的动漫人物！也有人说他是双手染血的圣人，天真的理想主义者，行事黑暗卑鄙的结果主义者，正义的伙伴，站在大义的角度为了维护和平。那么今天且跟小编一起探讨一下，他为什么会选择走这样一条路。一、 关于杀父卫宫切嗣本是一个天真无邪的少年，有自己喜欢的妹子，有严厉的魔术师父亲，有自己的的小小理想，和普通少年没有什么区别，直到自己喜欢的妹子夏莉的魔术实验失败而变成丧尸，而这一切都是因为自己的父亲。切嗣没有杀死理智尚存的夏莉，导致岛上的所有人都变成丧尸死掉了，他认为这一切都是自己造成，但是又有谁能够对着自己心爱的女孩下得去手？在悲痛与悔恨之中他有了第二次选择，他没有放过自己同样心爱的人，于是拿起了手枪杀死了自己的父亲，这虽有违人伦，但他真的有错吗？他就像天平，将生命放在两边，选择更重的那边，选择多数人那一边。二、 关于杀母一位名叫娜塔莉亚的女性在那座全是丧尸的小岛中救了他，她是一位赏金猎人，为了生存自然教会了卫宫切嗣各种生存之道。这位女性于他亦师亦母，切嗣的大部分杀人本领也都是出自她的手臂，这有些类似《灰色的果实》中的风见雄二，但二者的结果虽大不相同，风间雄二的母亲是病死的，而卫宫切嗣的母亲是被他杀死的！在一次娜塔莉亚执行任务的时候，任务没有完美完成导致一飞机的人都变成了丧尸，活下来的人只剩下娜塔莉亚。是让飞机落地造成美剧行尸走肉那般的结果，还是选择杀死这位养育了自己几年的女性？天平又一次出现在了他的眼前，最后他毅然决然地选择了后者，那一刻娜塔莉亚嘴角扬起了一丝微笑，大概在全世界最懂切嗣的人只有他了吧，然而他还是选择杀死了自己。三、 关于间接杀妻和妻女一起度过的时光应该是切嗣一生中最幸福的了吧，卫宫切嗣是一个为了理想付出一切的人。当圣杯战争打响时，他的妻子便成为了这其中的牺牲者，即便如此他依然认为理想的终点便是幸福的起点！卫宫切嗣不是一个自欺、不懂爱和冷漠的人，恰恰是因为他懂得了爱的真正含义，他站在大爱与博爱的角度，往往利用少数人去拯救多数人!但这样真的对吗？生命数量的价值真的等同于质量吗？爱究竟是什么？如果因为大爱而失去了自己最亲的人那么自己所遵循的爱真的还是爱吗？在《fate/zero》的结局中，卫宫切嗣最终一无所有，那是无助的，同时也是无力的，但是这样残酷的选择他经历了一遍又一遍，同时他是坚强的，是一个真正内心存在正义的人！不论对错他都是一个真正的男人！', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10360', '0', '卫宫切嗣：以凡人之躯行圣人之道的故事', ' 我们多少在儿时会憧憬电视里的英雄角色，他们大多会为了家庭，国家亦或是信仰做出常人难以抉择的事情，并且得到大众的认可。憧憬归憧憬，真正想要去成为英雄的恐怕寥寥无几。年幼的卫宫切嗣就将“正义的伙伴”作为自己一生的“职业”与信仰，在不见未来的道路上奔波。 对于卫宫切嗣这个人，月球人们各有各的看法。他杀了死徒化的青梅竹马夏蕾，杀了研究死徒的亲生父亲为了心中的正义，炸毁了飞机，杀了教给他一切的，兼并了父母角色的娜塔莉亚。最后大家看到的，双目无神，如行尸走肉一般，只想通过奇迹修正世界的卫宫切嗣，就这么出现了。如果说杀死青梅竹马夏莉和亲生父亲是他“正义伙伴”道路的启程点的话，那么使他偏离这条道路的，一定是娜塔莉亚的死。亲手炸毁载有生化病毒与他人生导师的飞机后，切丝关闭了自己的心，蒙上了自己的心灵之眼，跌跌撞撞的走向“通过杀死一些人，让更多的人获救”的道路。当他再次睁眼时，他感到了自己的无力，他觉得人是救不完的，人类的欲望始终都在，并促使矛盾的形成，再来就是战争，死亡的死循环。他很绝望，当他发现自己前半生做的都是“无用功”时，他转而开始寻求“奇迹”，也就是圣杯。 能够实现一切愿望的许愿机，它是否能完成拯救人类的愿望呢？只要有可能性就一定会去尝试的切丝肯定不会放弃。前半生的戎马征程与经验带给他无与伦比的优势，让他参加了圣杯战争，并且活到了最后，但他发现这个被污染的圣杯只会成为人类的阻碍时，毅然决然地让SABER摧毁了它。连奇迹都不能实现的愿望，让切丝彻底死心了，从此他一蹶不振。 为什么说切丝是凡人，因为他没有通过最后一道心灵历程。在经理无数的杀戮后，他只看到了人类的阴暗面，却未曾注意人类拥有的美好品质。贞德注意到了，所以她“相信人类的可能性”，天草注意到了，只是他只想保留人类的美好面，想消除人类的阴暗面，但他们看得都比切丝全面。所以很可惜，切丝最终以一个没能完成自己愿望的悲情人物含恨而终。在迦勒底，自称杀人鬼，以Assassin的职介现世。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10361', '0', '趣动漫：盘点动漫中比反派还要邪恶的主角，不去当反派真是可惜了', '在动漫的世界中，反派和主角的分界线是很明显的。但是光从行为上看，却不一定能分得清，我见过比主角还要伟光正的反派，也见过比反派还要邪恶残忍的主角。今天，我们就来盘点一下动漫中那些近似于反派的主角们，他们不去当反派那真是可惜了。（1）卫宫切嗣-fate/zero说到近似反派的主角，那么很多人第一想到的就是卫宫切嗣。因为有着极为悲伤的过去，因此形成了扭曲的性格，为了正义可以变得冷酷无情，不择手段，有些手段可以称之为下三滥了。大家对他的评价都是：虽然是渴求和平的梦想家，但实际上是冷酷无情的现实主义者（长门，带土：？？？你是再说我么）这是妥妥的反派套路啊。（2）早安地球-衰衰这是一部比较早的国产漫画，也是所有我看过全年龄向的漫画中，最邪恶的主角之一。真~肆无忌惮，据说这部漫画看的比较爽的人，生活中都比较压抑。有兴趣的可以自己去看看，这里就不剧透了。（3）夜神月如果说卫宫切嗣为了正义是不择手段，冷酷无情的话，那么夜神月则是可以说的上变态了，对世界的腐朽感到无聊，得到笔记后希望成为世界的神，性格相当扭曲偏激。这种人简直就是完美反派的预备军啊。当然，其最后的结局和反派也没啥区别，也许在作者眼中，没有主角，都是反派。（4）我妻由乃-未来日记好吧，在这种循环类的剧情中，主角本身就很有可能变成boss，在未来日记中也的确如此，不同周目的我妻由乃究竟算不算同一个人呢？为什么同一个人能短期产生这么大的变化呢？（5）伊藤诚等系列主角其实想要找到那些主角酷似反派的动漫，只需要去看看那些少儿不宜的动漫就行了。特别是那种非纯爱动漫，里面的主角一个个都是大变态。这种动漫倒是为我们提供了一个不同的视角去观察世界呢。结语：除了上面这几个动漫中的人物，你觉得还有哪些动漫中的主角行为简直比反派还像是反派呢？', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10362', '0', '企鹅娘辩论！卫宫切嗣到底算好人还是坏人？', '在被称为香港电影巅峰的《无间道》中有着这样一段经典的对白——“我现在只想做个好人，给个机会吧！”“这句话你去和法官说吧。”人在社会上飘，并不是什么事都能如愿的。有些人哪怕内心的期望是好的，但是为了达成目标和梦想，有时候不得不走上一条“坏人”的道路。说到这里，恐怕很多二次元爱好者首先就想到了卫宫切嗣这个人。卫宫切嗣和他的养子卫宫士郎一样，从小的梦想就是成为正义的伙伴，成为一名英雄。但是事与愿违，卫宫切嗣经历了很多事后才意识到了依靠正经的手段和方法是远远无法拯救世界的，所以他便选择了“以暴制暴”的方式来达到自己的目的。于是一个口口声声说要成为正义伙伴的人，转身就成为了一个杀人不眨眼的恶魔的使者。可能切嗣自己都没有发觉，也许他杀的人要比他真正救的人多得多……于是这种情况下很多网友都产生了一些疑问——为了正义而杀人的行为到底还算不上得上是正义呢？同时，像卫宫切嗣这种人到底算是一个好人还是算是一个坏人呢？大部分网友都本着同情的态度表示，卫宫切嗣虽然在行动上存在很多问题，但是他的本质和大方向还是好的。毕竟他一直也都是努力用自己的方式来让这个世界变得更加美好嘛！所以综合来讲功大于过，他还算是一个好人。但是也有网友提出了不一样的意见。他们认为虽然好人也分很多种，但是绝对不可能是卫宫切嗣这样的！单单是他杀人不眨眼这点就基本与好人无缘了。大家见过那个所谓的“好人”是一言不合就拔枪然后对着敌人脑门来一枪的？这种人完全就是黑社会的料子，除了他自己没有人会觉得他是一个好人于是我们今天就来辩论一番，用事实论据来讨论一下：“卫宫切嗣到底算好人还是坏人？”【正方观点：从行为作风上来看，卫宫切嗣绝对是一个大坏蛋！】【论据之一】卫宫切嗣不管是办事还是战斗都是躲在暗处偷袭人，毫无君子作风！不管是动漫还是电影里，好人都是那种堂堂正正光明正大的人，像卫宫切嗣经常搞的这种乘人之危的偷袭根本就是卑鄙小人！打从一开始召唤出SABER后，卫宫切嗣就和这个从者分道扬镳了。因为在他看来，以最少的牺牲换来做大的成果才是王道，像SABER这种以骑士精神自居、只懂得正面战斗不耍花招的人根本和自己合作不来！于是我们就看到卫宫切嗣召唤出SABER后就几乎没怎么和她一起战斗过，一般都是让她吸引火力，而自己一直躲在暗处直接对魔法师进行暗杀。这种行为根本就和圣杯战争的神圣性质背道而驰吧！看看别的组合之间的战斗永远都是正面对决最终成王败寇，就算输也输一个服气！而卫宫切嗣却一直都避免正面决斗，一直都是想办法直接秒杀魔法师来效率地获得胜利。因此他干过炸大楼这种高调的恐怖袭击、也搞过闯城堡这种不道德的偷袭。所以从战斗方式上来看，卫宫切嗣完全算不上是一个君子！那么连君子都算不上的人，又怎么能说是一个好人？自古以来的英雄侠士压根就没有这种只在阴暗中战斗的人。对于他们而言，背后偷袭别人简直就是对自己人格最大的侮辱！【论据之二】卫宫切嗣这种杀人不眨眼的人怎么能算是好人呢？好人都是从不杀人的！就像我们前面所说的那样，卫宫切嗣这个人其实是很矛盾的，就连他自己有时候都搞不清楚自己在搞什么……正义的伙伴、魔法师杀手，这两个看上去相互矛盾的称号实在让人难以想象会出现在同一个人身上。年少时的卫宫切嗣一心向往着能够靠自己的力量让世界变的更美好，结果却因为几次作战失败就瞬间放弃了曾经的坚持，转而变成了一个中二少年，开始了杀人不眨眼的道路。喂喂喂这人也对自己太不负责任了吧！难道就因为几次想救人没救成就开始自暴自弃转而进行杀人工作了？卫宫切嗣可以为了完成任务毫不犹豫地杀死任何人，可以说完全是一个冷酷无情的杀手。随着杀的人越来越多，他也变得越来越麻木，甚至开始用“不想让达到正义所付出的牺牲白费”这种措辞来麻痹自己。所以无论怎么看，卫宫切嗣都不能被称之为一个光明磊落的英雄，反而更像是一个内心恐惧不断麻痹自己的懦弱份子……一个为了救人而杀人的人，怎么可能做得了正义的伙伴？不管在哪个时代，人命都是这个世界上最宝贵的东西。任何一个人只要身上背负了人命，那么就注定永远与“好人”这个词无缘了。卫宫切嗣杀人的时候只想着所谓的拯救世界，但是他有想过他刚杀死的这个人也是有父母有孩子的吗？杀了他会给他的家人带来怎样的痛苦？卫宫切嗣的这种价值观和逻辑根本就是拆东墙补西墙，注定成功不了！大家可以回忆一下电影和小说里的那种好人，其实别说是杀人了，就算是杀一只小动物他们往往都下不去手。只有这种尊重生命、爱护生命、善待生命的人才能够称之为好人。【反方观点：棺盖定论，卫宫切嗣其实还算是一个好人！】【论据之一】圣杯战争是一个不是你死就是我亡的死亡游戏，所以不管使用哪种手段都是可以理解的。很多网友都说卫宫切嗣的战斗方式非常的卑鄙和猥琐，所以认定他不是一个好人。但是圣杯战争有规定每次战斗必须是互相正面对轰吗？圣杯战争是非常残酷的，不是小孩子过家家，所以说在这个前提下采用任何策略都是可以理解的。不如说像SABER那样动不动就提着剑要和人刚正面，还必须要求有尊严的胜利的行为才是傻中之傻！圣杯战争本就是成王败寇，所以卫宫切嗣的战斗方式并算不上卑鄙，而且圣杯战争中耍心机的人比比皆是。比如远坂时臣、言峰绮礼等人一个一个都是心机婊。按照正方的理论，难道任何擅长使用谋略的人都是卑鄙小人吗？在这场战斗中，只有活到最后的人才是真正的胜利者、也是绝对的正确者，结果足以说明一切。试想一下，如果卫宫切嗣也和SABER那样是一个直肠子，俩人别管见到谁都是拔剑大吼正面直接刚过去，那么可以想象这俩人可能连第一天晚上都活不过去就被别人给打死了……人一旦死了，那可就什么都没有了，什么理想和梦想全都没法实现了。因此，在圣杯战争中，活下去才是选手们首先需要考虑的事情。什么绅士风度、战斗作风、君子不君子的问题还是交给后人来评论吧！【论据之二】卫宫切嗣之所以会变成一个杀手，完全就是世界的错！错的是世界！年少时的卫宫切嗣深信世界是美好的，所以也在很多次行动中也多次想靠自己的努力尽可能底拯救更多的人。但他实际不仅没有救到更多的人、反而还害死了更多的亲人和朋友……久而久之，他已经意识到了这个世界的腐朽和人心的堕落，所以靠正常的方式是绝对无法实现自己的理想的。错的并不是切嗣和时辰，而是这个世界！卫宫切嗣之所以变成现在这样杀人不眨眼的魔法师杀手，并不是自暴自弃或者说是自甘堕落，而是他深刻地意识到', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10363', '0', '这些动漫男主真是可怜又可恨，不作死就不会死！', '这些动漫男主真是可怜又可恨，不作死就不会死！卫宫切嗣《Fate/Zero》卫宫士郎的养父，故事开始时已去世，只在回忆中登场。 第四次圣杯战争中为爱因兹贝伦家雇佣的御主，同时也是伊莉雅斯菲尔的父亲。召唤出了从者Saber。对Saber仅仅说过三句话用过三次令咒。在大战的最后放走了言峰绮礼，被言峰绮礼许愿诅咒。 在火灾中为了救一个濒死的孩子而将自己携带的阿瓦隆给予了那个孩子。伊藤诚《日在校园》诚本是一名普通高中生，与泽永泰介与班上的其它一些男孩子是好朋友。后来他暗恋上了每日在电车上遇到的邻班女生桂言叶。新的同桌西园寺世界开始帮助他与言叶交往。从此，诚开始了与言叶在内多位女生的恋爱关系。伊藤诚常常独自在家并很会照顾自己，很会料理。这也使得他能够有许多机会与许多女孩发生关系。碇真嗣《EVA》由于童年丧母加上自小就被其父遗弃托管，令真嗣的平常的性格表现十分胆小怕事、内向兼没有自信心。 日常不擅长与他人交际，习惯把自己的内心向外界封闭──然而不愿意与人接触的同时又很在乎别人对待自己的 看法，是个本质上有所矛盾的人。虽然看似弱气，但个别非常时刻的时候也是会强大到让人震撼的。樱满集《罪恶王冠》樱满真名的弟弟，樱满春夏的养子。对每个人都显得很冷淡，而且对同学也显得沉默拘礼不过对他人的察言观色却很擅长。经过与楪祈和恙神涯的的相遇之后，意外的察觉到了在右腕中寄宿的王之力量这个事实。 被内心中楪祈的意识所救，从王之能力中得到解放。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10364', '0', '我想成为正义的伙伴！——卫宫切嗣', '无差别的爱所有人，和不爱任何人是一样的。无差别的救人，最终连自己都救不到。切嗣这个人物，是对传统热血的「正义伙伴」的一种解构。切嗣的思维方式，恐怕是将正义贯彻到登峰造极的殉道者的思维。反观其他作品里高喊着正义和友情的主角，仅仅是喊喊口号，开开外挂就实践了正义。卫宫切嗣，从头到尾都是一个人战斗!切嗣很强，但造就他强大的是他可以舍弃一切的内心。所以在因为父亲死徒化实验发生了意外使得一直作父亲助手的夏丽变成了食尸鬼，小岛上的居民则被代行者们全部灭口。由于是父亲引发这件事的祸端，不愿再让其他地方发生同样的事情，他将自己深爱的父亲从背后开枪射杀。在一次刺杀任务中，和师傅娜塔丽雅一同追杀研究丧尸病毒，被称为“魔蜂使者”的危险魔术师欧德·波尔扎克，虽然在客机上的师傅刺杀成功但魔蜂泄露到整个客机，客机上三百人除了师傅娜塔丽雅·卡敏斯基一人外其他乘客全部变成了食尸鬼。在地面配合的卫宫切嗣跑遍了大半个纽约的黑市搞到了地对空导弹，为了不让悲剧扩大，在飞机降落在机场前把飞机轰进了大西洋。卫宫切嗣的正义矛盾，可以用一个非常经典的悖论来阐述：电车悖论。你作为一辆电车司机，突然发现前面的铁轨上有5个人，你没有机会刹车，不变轨的话，那5个人必死，而若变轨，另一条轨道上会有1个人被杀死。这是一个没有正确答案的悖论!切嗣选择了做正义的伙伴，那么面临着飞机危机时，他有两种选择：要么脱下衣服变身内裤反穿把飞机平稳的安放在地上然后恰好杀死所有毒蜂，所有人都会为他喝彩，因为他精彩的拯救了世界;要么用毒刺打下飞机，确保残骸落入海中，纽约因此幸免，他的奖励和承受世界上最大的痛苦，和被所有人视为恶魔的指责。卫宫切嗣这个人物受到差别如此之大的评价与极其悬殊的好恶，着实反映了这个人物塑造的成功之处，比起热血的超级英雄与明显的正反形象，切嗣便是一个活生生的被命运和造化压榨的凡人。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10365', '0', '「Fate/Zero」用Cos告诉你爱丽丝菲尔与卫宫切嗣的故', '爱丽丝菲尔·冯·爱因兹贝伦 cn: 第一弹@白夜墨菲斯托 卫宫切嗣 cn：书简摄影： 摄影师爸比 凝雨化妆： 白夜墨菲斯托后期： 白夜墨菲斯托力： 猫猫大大 小泷这是今年第一套FZ的cos作品，由一本同人绘本《在旅途》改编而来，完全没有提到残酷的圣杯战争，可以看作是平行世界的卫宫夫妇了。但是这个故事本身，是一场惊醒的噩梦，还是残忍的悲剧，全看每个读者自己的理解了。究竟旅途上的恶魔是梦中所见，自己的妻子还好好的活着；还是清晨醒来是梦境，那个恶魔才是被自己杀死的妻子呢？……感觉切嗣总逃不过亲手杀死所爱之人的宿命（惨）感谢所有参与这套图的小伙伴们，感谢所有喜欢这套图的小伙伴们！', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10366', '0', '团圆美满的卫宫家的饭，却少了切嗣，日常番中看到了悲伤', ' 月更大戏《卫宫家今天的饭》，算是平行世界的温馨作品，而就算在从者们都友好相处的世界里，卫宫切嗣第一次出场，也带来了一次悲伤。没了争斗，却还是心力交瘁 原本的卫宫切嗣，为了救赎全人类的第三魔法，不惜牺牲任何人，但看到东木圣杯的本质之后，他知道了已经被污染的圣杯只会通过灭绝人类来实现完全的和谐，这时曾经强忍下来的无情和破坏黑圣杯引发东木大火的愧疚感立即爆发了出来，就像电车难题那样，切嗣选择拉动拉杆，为了五个人牺牲一个人，但换来的结局却是连那五个人都没有得救，所以在第四次圣杯战争后，切嗣成为了残骸，只靠愧疚苟活于世，而卫宫家的饭里，显然没有那么残酷的圣杯战争，但切嗣还是没有摆脱这样的结局，原因什么的，也许是受到其他的打击，也许只是世界线的收束吧，就像汪酱和呆毛王之类的从者为什么存在一样，这也是同人作品没法摆脱的原设。 没了诅咒，却还英年早逝 原本的切嗣因为击破了黑圣杯，所以被安哥拉曼纽所诅咒，曾经可以和言峰绮礼这种被枪击都能活下来的家伙正面交锋的他，不但慢慢失去了魔术师的能力，身体也逐渐衰弱，以至于病魔缠身，英年早逝，而卫宫家的饭里，切嗣吃着小士郎给他做的汉堡肉时，也该像他的叹道“期待你的成长啊”那样，不想离开这个世界吧，士郎确实成长了，不仅家事万能，而且独当一面，但就算是这个世界里，切嗣也没能亲眼见证士郎的成长，只能留下一段下雨天的记忆，而他最终没有见到的，还有自己的亲生女儿伊莉雅，这个世界的切嗣也频繁去往德国，也许是因为其他的一些原因，伊莉雅没能在他生前搬来东木。 没了切嗣，却有了团聚 卫宫家满门英灵虽然是一个梗，但也是事实，回忆散去，长大后的士郎更加熟练的做着汉堡肉，藤姐和呆毛王还是一脸呆萌的赖在家里等着吃饭，伊莉雅因为顺路跑来蹭饭，加上应邀而来的凛、樱和R姐，卫宫家满门的英灵算是团聚了，不过原本美满的时刻，却少了一个人，最喜欢吃汉堡肉的切嗣不在了，雨中的紫阳花（又名绣球花，不知道为啥二次元的紫阳花经常出现在下雨的场景里，有知道的小伙伴希望告知一下）的花语是团聚、美满和希望，但这三项都舍弃了切嗣，而切嗣不管在哪个世界线里，都是士郎的榜样，如今的团聚也能看做切嗣理想的延续吧。 ', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10367', '0', 'fate zero圣杯被污染，卫宫切嗣之死', '艾因兹贝伦家族（以下简称艾家）做成圣杯后，连续两次他们都没能夺得圣杯，因而恼羞成怒在第三次战争时，想要召唤一个强力的英灵。他们在一个叫做“拜火教”的宗教传说里，发现了一个叫“安格拉·曼纽”的魔王，传说这个魔王各种狂暴酷炫秒杀一切，于是艾家将其召唤出来，想凭借他的力量赢得圣杯，这个英雄不属于其他的七大职业，而是属于第八职业“复仇者”。安格拉 曼纽但事实上艾家获得了错误的信息，事实上，“安格拉·曼纽”只是一个怀有梦想的普通青年，他为了让世间没有罪恶，将世界上所有的罪恶都吸到了自己身上，因此人们既厌恶他，又尊敬他。这个青年作为一个“反英雄”而被召唤了出来。而在第三次战争中，身为普通人的他一开始就被被秒杀了。死后，他的灵魂被吸到圣杯里，他身上拥有的世间所有罪恶污染了圣杯，圣杯里的魔力变成了邪恶的力量。所以当已经被污染的圣杯替人实现愿望的时候，就会从坏的方面来实现愿望。比如切嗣要让人类不再有战争和痛苦，圣杯就认为，那把人类全杀死就行了。切嗣发现这个真相后，使用令咒让saber破坏圣杯，而他也沾了一身黑泥，没几年就衰竭而死。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10368', '0', '“腿玩年”的间桐慎二，做过哪些伤天害理的事？', '有正派就需要有反派，正因为反派和正派的对立，才让故事显得丰满有趣。间桐慎二作为Fate系列中的反派，塑造了许多令人讨厌的事件。对朋友刻薄间桐慎二和士郎可以说是“老相识”，但是很难称得上知音，因为士郎和樱的关系，也开始排挤士郎，最终士郎退出了弓道部。在圣杯战争的时候，向士郎“示好”提出合作，被拒绝后反目成仇，很快就因为自己的虚荣心和扭曲的性格设下陷阱逼迫士郎和他决斗，在决斗中也败北，被b叔杀死。虐待“妹妹”性格中二且扭曲的慎二，总觉得自己有着比别人高一等的才能，其实只是个半吊子的魔术师，所以在间桐家难以得到重用，加上自我中心性格，时间长了诞生了扭曲的倾向，虐待自己的妹妹间桐樱，甚至加以凌辱，侵犯。述反反复复纠缠远坂凛间桐曾向远坂凛表白，但被其拒绝，之后便一直纠缠远坂凛。直到圣杯战争的时候，向远坂凛提出联手，被其拒绝，之后便成为对立的敌手。绑架远坂凛在获得了超强的英灵从者金闪闪之后，慎二变得越发得意，命令金闪闪绑架了远坂凛，带到了城堡中想对其进行凌辱，被Lancer阻止，期间对远坂凛肆无忌惮的行为完全称得上是猥亵，也出了“腿玩年”的名言，是对远坂凛拒绝自己的报复， 是自己欲望横行的表现。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10369', '0', 'Fate中人畜无害的间桐慎二，为何“罪不可赦”？', 'Fate系列中，间桐慎二作为一个贫弱的御主形象，高傲自满但却没有什么实力，虽然被远坂凛称为“人畜无害”，但实际上却被阿宅们讨厌着，因为他做了这些事情让阿宅们觉得他的结局“罪有应得”。1、摸了远坂凛的大腿这也许是阿宅们最无法忍受的一条，理由是“我老婆的大长腿怎能是你能摸得了的”，当然“罪不可赦”了。不过面对远坂凛被捆绑play还露出了大长腿，你能忍得住不摸吗？2、常年侵犯间桐樱我小樱可是正统远坂家名门的女儿，凭什么被你间桐家的人侵犯，要上也是土狼上啊，被慎二欺负真是浪费了高质量的小樱。3、瞧不起自己的从者美杜莎喜欢神秘而性感的R姐的朋友也不少，但慎二明明自己能力不足却用侮辱性的言语咒骂R姐没实力，对不起，你好像连驾驭R姐的实力都没有，明明是靠着樱的魔力在战斗，凭什么骂人家无力，美杜莎可是希腊传说中的三位女神中的幺妹啊。4、和闪闪合作，害伊莉雅被“掏心”这事情不是慎二的错，甚至可以说跟慎二没关系。是言峰这假神父追求“愉悦”把金闪闪分配给了慎二这样的无能御主，就算不和慎二合作，闪闪也会去杀了伊莉雅的，但是在看到闪闪狂虐一群人后还能露出得意的嘴脸的时候，这个锅也要让你背了。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10370', '0', 'fate剧场版（HF线）第二章上映，说一说这条线的女主间桐樱', '间桐樱原名远坂樱，是远坂凛的亲妹妹。应间桐家族的请求，远坂时臣将远坂樱过继给了间桐家族，为了让远坂樱适应间桐家族的魔术回路。在樱小的时候，就将她扔进了装满刻印虫的地下室。​由此可以想到，小樱当时是身心都遭到了间桐家族的摧残，后来有摊上了一个渣男哥哥间桐慎二，这家伙不停的奸污小樱。当年我玩这条线的时候真想冲进去杀了他。后来小樱遇到了温柔的士郎，尽管在这条线中他们两个人受尽了磨难和屈辱，但最终走到了一起。尽管其中一个结局士郎死掉了，虽然很悲伤。但是他们都活在彼此的心中。随说好结局也有，但是那个有点像后宫结局。这个结局对小樱太不公平了。​但是不管怎么说，小樱都是我很喜欢的fate系列女主，尽管吾王是我的真爱，但是我更同情小樱。小樱随时黑化过，但是都是被间桐家族给逼得，我真的很想亲手冲进去把间桐臧硕和间桐慎二给活劈了。​一句话，我喜欢间桐樱。即使她被\"人\"伤害过，侮辱过，我也喜欢间桐樱。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10371', '0', '间桐雁夜：樱，我一定会救你的', '看到这个题目，相信不少看过《Fate/Zero》的人都会点进来，默默的在心中感叹。\"你说说，雁夜这人，天天盯着时臣他老婆干啥，活该这样。\"\"都是为了救别人家的Loli？看，出事儿了吧。\"的确，为了救远坂时臣的女儿樱，间桐雁夜确实付出了比其他人更多的努力。他回家跟自己的父亲谈判，但是他的父亲（那个老不死的虫爹）并不打算放掉可怜的樱。雁夜还是晚了一步，樱的贞洁最终被虫爹库中之虫侵蚀。原先的樱是远坂时臣超级宠爱的女儿。她和凛是十分好的亲姐妹。但是由于怕两姐妹长大后互相争执，于是才把樱送到了间桐家，想着间桐家也是魔法世家，樱一定能在那边得到更好的照顾，学习那些能令她强大的魔法。樱与凛一直以来都是一起玩的姐妹。想想雁夜会帮助远坂葵，之后再杀掉远坂葵，其实一切的来源都是因爱生恨。他喜欢上了远坂时臣的老婆远坂葵，但是远坂时臣貌似对此并没有表现出什么不满，好像也不太在意，貌似在远坂时臣的心里，也并不是很看得起间桐雁夜。远坂时臣心里很清楚，葵是喜欢他的，雁夜永远不会得到她。而自己的女儿，也有了个好的归宿，自己已经是个很优秀的父亲了。远坂葵是雁夜从小的暗恋对象，尽管她已嫁给远坂时臣，但是自己会尽力帮助葵。没想到救樱失败，把自己搭了进去，也没有成功。他还答应樱，带她去找妈妈，再像原来那样，一起玩，但是承诺没有兑现。间桐雁夜加入了圣杯战争的一瞬间，就已经是个废掉的棋子了。兰斯洛特是一个狂战士，狂战士和其他职阶的英灵相比，耗费的魔力比其他的人更多。雁夜召唤出的若是个Caster，说不定可以自己储存魔力；召唤的若是个Saber，可以靠令咒强化；召唤的若是个Lancer，也绝大多数都拥有幸运属性，幸运E分分钟战斗续航，运气好出个狗哥还有回避；召唤的若是Assassin，更会尽量节省魔力完成刺杀。于是在兰斯洛特和亚瑟王对抗的时候，魔力耗尽，自己也献出了生命。最终，他没能救的了樱。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10372', '0', '间桐慎二简直就是FATE里面的人生赢家', '间桐慎二虽然是很人渣，但是换个角度来说简直就人生的赢家年纪轻轻就可以参加圣杯战争，虽然就魔术师而言简直一名废柴但是人家生在了魔术师家这是许多人一辈子都想着但摸不到的事实，就如同你看那些富二代好像很废柴那样但是人家家里有钱随便做个什么都比你奋斗一生要强，这是事实没有办法改变。其次整天可以把不少人心中的女神小樱压在身下，这种福利谁受得了任哪个男人都羡慕得流口水，可以把人性最丑恶也是最率真的性格尽情释放这种没有约束的自由自在感觉难道不是一种愉悦，家里又不缺钱真所谓要钱有钱花要女人有女人，俨然就是一副人生赢家的样子。当然二爷不时也有作死的表现这能够怪谁给你有着这么大优势任谁都会飘飘然，不过二爷真的敢碰坂本凛这还真的不够班，即使差点得手还是被LANCER吓跑还被金闪闪捉去了做人肉圣杯，这能够怪谁自己的本事不够吧因为本来作为一个普通人就不应该跨次元参加圣杯战争，但是二爷做到了简直就是需要很大的勇气因为随时都会死啊。但是二爷终归自带光环即使再大的灾难只是受了一点苦头都能够全身而退，不能不说即使怒怼是狼，差点得手女神凛他们对二爷都既往不咎还是救了二爷一命难道这不算是幸运A的属性么，这样的二爷能够不说是人生的赢家吗？', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10373', '0', '千姿百态的伊莉雅，哪一种征服了你的心', '尽管只是个悲惨的配角，但是伊莉雅在整个Fate系列中的戏份不算很少，位置也比较重要，不过最令人关注的还是她可爱值颇高，拥有太太的美貌，同时拥有惹人喜欢的少女体型和萝莉系的情怀，作为魔伊的女主角更加是萌度爆棚，伊莉雅的换装也非常多。原版 伊莉雅斯菲尔 冯 爱因兹贝伦Fate线中的伊莉雅，作为狂战士的御主参加第五次圣杯战争，是卫宫切嗣和爱丽丝菲尔的骨肉，是为了获得圣杯战争的胜利而被制作出的存在，同时作为“小圣杯”的存在。Fate线中的形象为10岁左右少女的样子，但年龄已经是18岁，性格天真但为了圣杯战争也会肆无忌惮的杀人，称呼士郎为哥哥。一般身穿紫色的衣服。幼女 伊莉雅斯菲尔 Fate Zero中出现的伊莉雅，当时还值年幼的伊莉雅，因为父亲工作繁忙几乎没有和父亲一起玩的时间，称呼父亲为“切嗣”，性格非常调皮可爱，真正的天真无邪。HF线 天之服 伊莉雅穿上爱因兹贝伦代代流传传的魔术礼装后的伊莉雅，拥有开启“天之杯”的力量，在HF线中拯救过士郎。布鲁马 伊莉雅在Fate游戏BAD END后出现的“大河道场”中的“弟子一号”，即以运动衫的形态登场，和主线剧情关旭不大。泳装伊莉雅Fate/hollow ataraxia中出现的泳装姿态，按这个造型出过手办，舔值略低但收下不妨。幻想嘉年华 伊莉雅作为可爱的卖萌妹妹登场的伊莉雅，同样是带着略呆的巴萨卡出现，有变身能力，脸上经常泛着红晕是特征。魔法少女伊莉雅Fate延伸作品，以Fate的类似设定作为基准后，伊莉雅变成了普通的小学五年级学生，并且成为了主人公。这部作品中的伊莉雅只是作为普通的小学生存在，不过随着世界观的深入，连这个世界的伊莉雅也牵扯进了圣杯战争的概念， 在Fate GO中补入的设定说她是通过大人们的努力而获得的逃脱了残酷命运，作为普通小学生生活的伊莉雅。理想化的伊莉雅，命运比原版的伊莉雅要好得多，性格纯洁天真，吐槽能力满分，喜欢看动画，喜欢过魔法少女系列的作品，手持愉快型魔术礼装红宝石。自己成为魔法少女后却觉得非日常的日常有些接受不住，不过还是很有成为魔法少女的天赋。自从自己的另一个身份的小黑出现后，单体战斗力就比较低下，表现在魔法弹输出火力低，难以一口气消灭敌人，不过可以通过阶级卡进行强化。Saber 伊莉雅和剑士阶级卡融合后的强化形态，说实话这个设定太赞了。合情合理又符合魔法少女变身的有趣设定设定。强化后可以使用剑阶级的宝具【誓约胜利之剑】，原形是Saber Lily。Archer 伊莉雅伊莉雅打开体内的枷锁后，和弓阶级卡融合后的形态，可以看作是不具备肉体的小黑附体伊莉雅，不过在美游看来就是英灵附体。可以轻松投影圣剑，战斗力超过黑化的Saber。Caster 伊莉雅和术士阶级卡融合后的伊莉雅的形态，紫色的斗篷很合身，但却带来一股大妈味（美狄亚：谁黑我?）Assasin伊莉雅和刺客阶级卡融合后的形态，原形是哈桑萨巴赫，面具是蓝宝石的一部分，拥有刺客的，气息遮断、分身等能力。另外，非常可爱！Lancer伊莉雅和Lancer结合后的形态，原形是库丘林，可以使出宝具Gae Bolg。长马尾好评。Rider 伊莉雅原形应该是R姐，眼罩好评。蓝宝石 红宝石的真正力量合二为一才是魔法手杖的原本的样子，能让身体的各个系统都变成拟似魔术回路而打通全身的魔力（就是原来世界的伊莉雅嘛，毕竟是小圣杯），从而获得一时性爆发，但是对器官的损伤很厉害，红宝石称不到最关键的时候不能使用。使用后背后出现光的翅膀。蓝宝石伊莉雅和愉快型魔术礼装蓝宝石合体后的伊莉雅，拥有着和红宝石不同的装束风格，能力是接近的。玩偶伊莉雅被置换了肉体和精神的伊莉雅，肉体和精神被分开，精神被随便丢置于一个玩偶中。猫耳换装伊莉雅换上可爱又大胆的黑色猫耳伊莉雅，有同款手办。喵帕斯伊莉雅“魔法少女伊莉雅”和“悠哉日常大王”官方联动活动时候的，官方配信的伊莉雅的喵帕斯版本形象。被玩坏的伊莉雅OVA中因为受不了小黑的激吻而出现的形象，福利中的福利，官方逼死同人的节奏。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10374', '0', '伊莉雅究竟是魔法少女 or Master？', '说起伊莉雅，各位第一时间会想到什么？如果是Fate系列的死忠，毫无疑问会想到那位开口闭口都是“Berserker干♂他”“Berserker弄♂他”的白发萝莉~而且这位伊莉雅的知名度应该更高（虽然压根没当过女主角）（就是此等英姿飒爽）至于另一位伊莉雅（其实就是同一个）则是《Fate/kaleid liner 魔法少女☆伊莉雅》的主角。两者其实是处在不同的世界观当中，剧情也没多大关联，仅有的相同点我想就是名字——伊莉雅斯菲尔·冯·爱因兹贝伦（800字作文首选材料之一：撑字数）并且CV都是门胁舞以。实际上，本期我们要介绍的当然是轻松搞怪，日常卖萌偶尔战斗的《Fate/kaleid liner 魔法少女☆伊莉雅》里的伊莉雅（看封面就知道啦~）或者很多朋友会说“我没看过那个伊莉雅~”“魔法少女有什么好看的？”“Fate赛高！”等诸如此类的话。如此熟悉，如此家喻户晓，Fate的套路你们都知道了那还有什么好说的呢？而且本刷刷是个热爱生活讨厌暴力排斥血腥的天天向上正能量好青年，FSN里的伊莉雅下场辣么悲惨，你们叫我如何下手撸呢……双眼被废心脏被挖（金闪闪一生黑）（旁边的刷刷油姐姐问我为什么双眼满含泪水，我答曰：找图找哭哒！）所以说，我还是撸魔法少女吧~什么叫高下立见？既然是小学生的日常，圣杯战争那种残酷的东西自然不会出现。与FSN不同，伊莉雅此次是父母双全，也没有巨型怪蜀黍跟在后面，每天就是上学放学尽情享受平常人应有的生活~相比正作里的人设，魔法少女的性格有了360°的转变，彻底变成一个呆萌小萝莉，重度兄控~也许作者在创作这枚伊莉雅时也是想通过性格来告诉读者，这个伊莉雅与圣杯战争毫无瓜葛。直到有一天（是的，每个主角都会有那一天）遇到一条会动会说话的红色棒棒，被其强制缔结契约，成为了魔法少女（仔细想想，小圆她们还是自愿的）成为了红宝石之星持有人的她，被迫帮忙回收职阶卡片。而剧情发展到后面，大家也会看到不少熟悉的面孔，士郎、凛、黑Saber、夫人等等。除此之外也有一批新角色，排在首位的当然是美游和小黑啦~实际上剧情也一直围绕着这三个小女生之间展开（很抱歉，士郎真的是打酱油的）表面上是三个小学生，可是依本刷刷愚见，这简直就是大型长篇狗血三角恋电视连续剧……请接受小学生之吻！据最新消息，官方已确定第四季将在接下来的七月新番中播出~所以说萝莉控百合控绅士们都站起来再撸一回吧！（哇，好激动啊！）1. 伊莉雅枕头靠枕马猴烧酒伊莉雅福利满满的靠枕，相比正作中阴沉冷淡的性格，呆萌萝莉棒棒哒~能卖萌能战斗还是好奶妈，伊莉雅堪比金坷拉，魔力不够就找她（喂）把伊莉雅抱回家，累了就亲亲，成为魔力无限的永动机吧~（？）2. 伊莉雅圆领短袖马猴烧酒的T恤，宅气满满却威力无穷。平时呆萌的伊莉雅一旦化身魔法少女便瞬间有了一股成熟气息，即使是被强迫成为魔法少女，但为了要保护的人，便奋不顾身，勇气可嘉的小学生啊~【刷刷】网尽二次元好物官网：刷刷APP——http://www.shuaacg.com微信：刷刷微博：刷刷二次元安利娘在qq群498229789等你，有红包呦', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10375', '0', '魔法少女伊莉雅剧场版：你甚至能在其中看到伊莉雅', '《Fate/kaleid liner魔法少女伊莉雅》是由广山弘绘制的官方外传漫画系列，2013年动画化，将《Fate》本篇中的伊莉雅这个角色抽出，单独做了一个不同世界观的外传作品，让伊莉雅成为了普通的小学五年生，而获得了红宝石手杖的力量变成了魔法少女，故事中借用大量《Fate》原作的设定和梗，让不少喜欢《Fate》原作的粉丝和伊莉雅的粉丝都关注了这部作品。在第一季中，虽然作为类似粉丝向的动画，但是制作精良，人设精美，外加上《Fate》这块金子招牌的宣传力和类似的世界的设定的吸引力，第一季非常成功，并很快推出了第二季。而在第二季《魔法少女伊莉雅》中，不仅保持了第一季度的风格，还加入了新角色，伊莉雅的分身小黑，借由这个角色的出现，代入了大量的百合肉色福利，将这部粉丝向魔法少女题材作品变成了一部不争的绅士大作。而正当大家都觉得《魔法少女伊莉雅》会继续走向这个绅士的节奏之刻，第三季的故事急转直下，变成了紧张的战斗系故事，揭露了第一部中美少女美游的过去。而在今年8月，《魔法少女伊莉雅》剧场版，《雪下的誓言》在日本公映，笔者有幸目睹了这部剧场版。这部剧场版讲述的是漫画原作第7卷起的剧情，也就是紧跟着动画第三季结尾的故事，回忆美游和另一个世界的卫宫士郎的故事。而这部剧场版，虽然标题叫《魔法少女伊莉雅》，但是由于是回忆篇，在另一个世界的战斗中，美游才是圣杯并且并没有伊莉雅这个角色，而美游被囚禁，真正的战斗力则是另一个世界的卫宫士郎，所以剧情中，你是几乎看不到女主角伊莉雅的，而且第二季 中那样的湿吻福利也不会在剧场版中再现，虽然官方给出的电影票极为桃色，但是这还是以打斗为主的正经剧情剧场版，而并没有太多的绅士福利。而剧中，卫宫士郎因为为了救美游而和所有的艾因兹华斯家为敌，并且锻炼出“脸挡宝具”的神技，全然一副“巨侠”的风范，可以说这个世界的卫宫士郎比本篇的更强，也再一次验证一句玩笑话，“离开saber越远，士郎就越厉害”。这部剧场版在剧情把控方面还是可圈可点，制作上还算走心，但是在大场面的渲染上依然没有当年《Fatezero》的那种气势磅礴感，你说拿剧场版比比不过TV会不会有些夸张，但是事实就是这样，虽然说《魔法伊莉雅》这部TV动画的制作还算的是用心，但是果然和ufo社的华丽制作燃烧经费的画面比，总觉得还缺了一点什么。这部剧场版目前已在日本上映完毕，国内恐怕要等到BD出了才能看到，包括卫宫切嗣、间桐樱等角色都会出现。喜欢这部作品系列的粉丝还是值得期待的，不过要是冲着伊莉雅去看这部作品，多少还是会以失望告终。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10376', '0', '伊莉雅app在北美夺第一，外国人日常作死', '在《魔法少女伊莉雅》剧场版在日本上映前后，官方推出了由银链提供全新插图素材，全新收录语音的伊莉雅闹钟 APP，这款 APP 在国内的 AppStore 和各种安卓市场上也有正式上线。大概画个88元能够解锁，还有陪睡功能（ 陪睡功能简直是电池杀手而且家里完全不敢外放好嘛）然后4 月末在北美地区上线，在 Google Play 上分级 17+ 的 APP 已经成为了北美地区 Google Play 付费 APP 第一位。在不同地区的销售模式都不同在日本和北美地区，这款 APP 是要付费购买下载，之后在 APP 内还可以氪金解锁内容，在国内市场这款 APP 至少下载是免费的，同样 APP 内有氪金解锁项目。而在北美地区被 Google Play 评级为 17+ 被 AppStore 评级为 12+，都知道在北美这种对儿童色情标准认定比较严格的地区，你看个图片都有可能被fbl抓去喝茶，这款 APP 能够上线就不易。在 Reddit 上很多北美阿宅为伊莉雅闹钟 APP 登上了 Google Play 付费 APP 榜第一（北美地区）非常自豪，不过 Reddit 上的玩家也吐槽这么多人都想真的被 FBI 抓住去体验牢狱生活，还有就是北美网友对于 Google Play 给出的 17+ 评级的标准很好奇，越是分级看上去危险就越有人想要尝试购买下载，看看里面到底有什么。不得不说外国人日常作死。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10377', '0', '樱酱带你了解《fate》中的伊莉雅斯菲尔', '哈喽，樱酱前来报道了，今天樱酱带小主们来了解一下《fate》中的小公主依莉雅斯菲尔·冯·爱因兹贝伦，依莉雅斯菲尔·冯·爱因兹贝伦是type-moon旗下的小萝莉角色，是参加第五次圣杯之战的小圣杯，又是Berserker的魔术师，她还是卫宫切嗣和爱丽丝菲尔·冯·爱因兹贝伦的女儿，曾在《fate zero》还有《fate night》以及官方有病都不肯吃药的《幻想嘉年华》中登场，对辣非常敏感，所以后来看到麻婆豆腐会感到恐惧。依莉雅斯菲尔·冯·爱因兹贝伦依莉雅斯菲尔·冯·爱因兹贝伦是人造人，作为爱因玆贝伦家族得魔法师响应参加了第五次的圣杯战争，并且召唤了失去理性的强大硬汉英灵Berserker，由于卫宫士郎是第四次圣杯战中的受害平民，又是唯一一个幸存下来最后被卫宫切嗣收养的人，伊莉雅就变成了士郎的义姐，也就是我们说的“干姐姐”。其父亲卫宫切嗣是在经历过三次圣杯战争的爱意玆贝伦家族落败，为了找回优势，雇佣的魔法师杀手，在第四次圣杯战的时候与被誉为小圣杯的爱丽斯菲尔·冯·爱因玆贝伦相爱，于是剩下了伊莉雅，伊莉雅在还没有出生的时候就被调整整合了无数次的魔术，身体构造也是比母亲爱丽斯菲尔更高的技术结晶做完成。依莉雅斯菲尔·冯·爱因兹贝伦依莉雅斯菲尔·冯·爱因兹贝伦通常穿着紫色的洋装，跟爱丽丝菲儿相同的是银色的长发宝石红的瞳孔，与人交流时也显得贵族风范非常有礼貌，就连她要让Berserker与敌人作战时，也会轻抚裙角向对手有礼貌的鞠躬。体型与样貌一直停留在9岁的伊莉雅其实比士郎大一岁，单纯而且天真烂漫，同时伦理观薄弱，不抗拒杀人，因此在与士郎初次见面的时候还是称之为大哥哥。同时兼具纯真与残酷于一身的她幼年的时候，爱因兹贝伦并没有人教导她善与恶的区别，所以生气或自尊心受到损伤的话可能会微笑着杀人，原因在于依莉雅并不知道随便杀人是错的。依莉雅斯菲尔·冯·爱因兹贝伦依莉雅斯菲尔·冯·爱因兹贝伦魔术回路极高，全身都是咒令，就连召唤B叔维持同调伊莉雅都不在话下，不光出于她本身的身体就是魔术回路，更加强大的是她的核心心脏，在ubw线中金闪闪打败B叔亲手杀了伊莉雅并挖了她的心脏，心脏上基本都是魔术结晶！伊莉雅的魔术特性和术士其实就是炼金术，爱因玆贝伦家族是人造家族，伊莉雅的两个女侍从同样是也人造人，术士在ubw中伊莉雅有两个天鸟，可飞行追击，可变形，还可以抵挡魔术师对她带来的伤害，在远坂凛与其交手的时刻，远坂凛的宝石攻击全被抵挡住了。依莉雅斯菲尔·冯·爱因兹贝伦居住在郊外的树海中间的城堡里，从街道坐车需要一个小时的距离，不沿著杂木林走1公里的话是到不了树海的。和她住在一起的除了赫拉克勒斯之外还有她的两位人造人女仆：莉洁莉特和塞拉。森林中布有爱因兹贝伦家的结界，进出都会受到监视，并没有特定的居住场所，进入时，魔力越高者越会受到强力的抵抗。在《幻想嘉年华》中伊莉雅也有登场，而是作为主持人的形象在里面首次亮相，其中再就是士郎晒被子一起午休出场，戏份录像并没有很多，后来在最后出现了一场巨型怪物入侵城市的剧情，官方吐槽“没有我的路线”巨型怪物被击败后就是伊莉雅出来讲解，十周年的嘉年华结束后，后来依莉雅斯菲尔·冯·爱因兹贝伦终于迎来了自己番剧《魔法少女伊莉雅》。可见人物角色对官方的重要性。依莉雅斯菲尔·冯·爱因兹贝伦在《魔法少女伊莉雅》中，伊莉雅斯菲尔·冯·爱因兹贝伦憧憬魔法少女的小女孩，同时也向往普通的生活。真实身份是爱因兹贝伦家在圣杯战争中的小圣杯，在婴儿时期被母亲所封印魔力、记忆以及知识，变成了一个普通的女孩， 在某天无意间被魔法棒选中改变了一个普通学生的生活剧情，其中远坂凛与露维娅格丽塔·爱德菲尔特登场交其使用魔法战胜英灵收集英灵卡片。在《魔法少女伊莉雅》中伊莉雅斯菲尔·冯·爱因兹贝伦还分裂出来了双胞胎妹妹，红A版本得妹妹能战好斗！伊莉雅斯菲尔·冯·爱因兹贝伦在家对哥哥士郎还是一如既往的兄控。小主们看到这里你们有什么感想呢？是不是想有一个伊莉雅这样的小妹妹呢！有想了解的内容记得评论区留言，樱酱会为大家整理更好详情解答。', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10378', '0', 'Fate、FGO英灵伊莉雅详解', '作为在魔法少女伊莉雅的主角伊莉雅，在FGO里也是非常可爱的小萝莉在Fate/stay night 竟然被金闪闪刺瞎了眼还被杀了，心痛魔法少女伊莉雅Caster伊莉雅初始一破三破满破角色详细在穗群原学园小学部上学的，非常普通的女孩子。 虽然银发红瞳名字也像个贵族， 虽然总是不在家的父母在做谜一样的工作， 虽然不知为何普通住宅里中有两个女仆， 虽然顺便还有个没有血缘关系的哥哥， 但确实是个不能更普通的小学五年级女生。羁绊故事·一身高／体重：133cm・29kg 出典：Fate/kaleid liner 魔法少女☆伊莉雅 地域：日本・冬木市 属性：中立・善 性别：女性 意外习惯误入不同世界。普通普通。羁绊故事·二一天晚上，当伊莉雅和往常一样在浴室里送福利的时候，魔法棒从夜空突袭而来。 被扭来扭去而且滔滔不绝， 可疑度MAX的魔法棒轻易拉拢， 就在此刻就在此处，魔法少女☆伊莉雅 到底还是爆诞了。 当然，要对各位同班同学保密哦☆ ———那天晚上，她和命运相遇。羁绊故事·三又能驯服古怪魔法棒， 又能让寡言少女敞开心扉， 又能将来杀自己的少女迎入自己家庭， 适应力和交流力莫名高到飞起。 说难听点就是随波逐流体质。 随波又逐流，最终来到了迦勒底，不过在这里也能差不多干下去吧。 只要有魔法棒，和追求未来的意志， 和萌到翻天的可爱，一定可以的。羁绊故事·四『多元重奏饱和炮击』 等级：A+ 种类：对人宝具 Quintet Fire。 让肌肉组织、神经组织、血管组织、淋巴组织被错认为模拟的魔术回路，爆发出瞬间输出的舍身技巧。 环绕全身的五个回路奏出的壮烈魔力湍流，具有匹敌星光的歼灭能力。羁绊故事·五○可疑的药：A 红宝石天天晚上在阁楼精制的谜之药物。正是琥珀草。 原料主要是植物（毒草）。 据说偷偷摸摸地在爱因兹家后院种上了可疑的花草，建造一个人见必通报的超能花园。 效果好到恐怖，然而大多最终会造成不正经的展开所以还是想尽量避免使用。最终故事这位伊莉雅斯菲尔是「她」可能的、if的姿态。 是某些大人们抵抗命运、祈祷，以及赌上性命保护的「极为普通的女孩子」。 孩子成长、然后总有一天会意识到的吧。 “极为普通”所带来的幸福，才是这世上最珍贵的。 ———所以，伊莉雅一定没有弄错。 即使残酷的命运迫使她做出选择也好、现实挡在眼前使她迷茫困惑也好，最后一定会到达终点。 走向着她的目标——真正的happy end。请点击输入图片描述愉快型魔術礼装：自身的Buster卡性能提升（1回合）自然的婴儿：赋予自身无敌状态（1回合），NP获得量提高（3回合）奇怪的药：70%概率赋予己方单体胆识状态（1次·3回合），70%概率赋予弱体无效状态（1次），HP回复职阶技能：对魔力自身的弱体耐性提升（17.5%)无限的魔力供給：赋予自身每回合NP获得状态（3%)最后先上一张伊莉雅的动图', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10379', '0', '果然旗袍制服才是真爱 伊莉雅闹钟插图比赛结果公布', '《魔法少女伊莉雅》是以《Fate/stay night》中的登场角色伊莉雅斯菲尔为主人公、以魔法少女为题材的外传漫画作品，世界观及角色设定等以原作为基准，但皆与原作有所差异。在今年7月份，《魔法少女伊莉雅》官方推出了闹钟应用，随后还展开了闹钟插图比赛。13日官方公布了插图比赛的投票结果，我们就一起来看看吧。《魔法少女伊莉雅》的闹钟应用在8月下旬正式上线，税后售价为960日元（约合57元人民币）。昨天《魔法少女伊莉雅》的动画制作公司SILVER LINK.在推特上公布了闹钟插图比赛结果，第一名是旗袍装、第二名是忍者装、第三名是教师装。官方还表示前三名插图将周边商品化，对此感兴趣的网友们可以关注一下。《魔法少女伊莉雅》的故事主要讲述了女主角伊莉雅是穗群原学园的普通学生，在某天遇到了自称人工天然精灵的万花筒之杖“露比”，并强制地被缔结契约，成为了“魔法少女伊莉雅”。而已是露比持有人的她，还变成了万花筒之杖原持有者——魔术师远坂凛的奴隶。在凛的命令之下，伊莉雅被迫帮忙回收沉睡于冬木市的危险的卡片……', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10380', '0', '《舌尖上的卫宫家》月球人活着就是为了伊莉雅', '戳上面的蓝字关注我们哦！微信公众号：acg动漫污妖王/acgwu666今天槽什么正当宅友们还沉浸在新春的愉悦中享受着葛优瘫补番吃鸡躺尸肝活动的时候，3月的铁拳早已重重落在广大学生党和社畜的身上，开始上班上学苦不堪言的我们只能寄望二次元稍微能给我们点安慰。幸好，在1月霸权从各路奶妈的毒奶下争个你死我活的时候，《卫宫家今天的饭》依旧能带着温馨准时造访我们。从第一话开始，月更的《卫宫饭》就有种包揽全年4个季度霸权的既视感。卫宫士郎这位一度被近战和箭术耽误，家政技能ex的冠位cook（er）总算是回归本行，带着一众后宫给观众带来一个充满浓重生活气息，既温馨又暖心的故事。继吾王和士郎除夕用荞麦面撒狗粮，卖鱼汪酱（库丘林）自来熟鲑鱼饭之后，第三话由伊莉雅领衔为大家送上合法和服萝莉play外带女儿节特制什锦寿司饭。今天的冬木又是核平......咳咳，又是十分和平的一天，省去了圣杯战争尔虞我诈你死我活的神仙打架，冬木的各位都过起了良好市民的生活，打工卖鱼蹭吃串门迷恋手办电子游戏专业人妻成为看门老大爷的御主和英灵们今天要为同一个人庆祝女儿节~作为伊莉雅主场，第三话开篇就为月球萝莉控们送上一波福利。跟《魔法少女伊莉雅》里面满屏暴力开车的伊莉雅相比，这边的要更像花（合）季（法）少（萝）女（莉）一些，穿上和服之后瞬间有种爱丽斯菲尔的既视感，纪念天国的太太，亲闺女长大了，长得很像妈妈（捂脸）。今天是FGO伊莉雅限时up卡池的日子（大雾）有别于《F/Z》和《F/SN》，飞碟社在制作这部作品的时候舍弃了炸裂的特效，清新柔和的画风和日常系的剧情更令这部美食向的番剧突出一个暖字，而作为伊莉雅主场的第三话运用了大量暖色调的画面来烘托气氛，就像这一话大河说的，这是一个期盼获得幸福的日子。有什么比和三五知己朋友家人围坐在一起品尝饭菜，嬉笑怒骂云聚一堂更温馨的事呢？而第这一话最让人惊喜和意外的地方就是樱对凛无意中的一句“姐姐”，熟知原作的月球人都知道由于时辰的锅，樱和凛是一对从小就分开的悲剧姐妹，这一幕到底能让多少月球人捶胸顿足泪流满面，但至少天国的雁夜叔叔会露出会心一笑吧。这部作品不仅仅区别于原作的沉重压抑，传达出充满温馨治愈的日常气息，让你在舔屏之余体会到什么是“平平淡淡才是真”这句话，更重要的一点是，10多分钟下来你可能已经学会其中简单易做的菜色了~吐槽君不禁心想，这可能是圣杯战争以外，对于这群人也好对于我们观众也好，最好的结局了吧。让我们下个月依然带着被治愈之心围观“真·最强型月厨”给我们带来的最新菜色吧~下面彩蛋：谁说吾王不懂人心？', '', '4687', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10381', '0', '你为什么不讨厌《Overlord》的骨傲天与空气斗智斗勇？', '动画版《Overlord》两季都已经放送完毕，下个月第三季也要出了，反响还是很不错的，无论各位妹子，还是主角骨傲天人气都不错。那么作为一个“骷髅”，观众为何如此买单呢？今天就来探究一下。同情大家都知道骨傲天是独自一人怀着“陪游戏走向关服”的心态才穿越的，所以大家多少都有点同情。严格说起来，从穿越那刻起他就一直是“一个人”，各位守护者虽然忠心耿耿，但是在他心底始终认为守护者是当初伙伴的创造物，性格啊行为模式啊等等都是设定好的，并不是真正意义上的“同伴”，更多的是像“机器人部下”。上帝视角观众都是以上帝视角来观看的，这让观众可以清楚知道骨傲天的心理活动。骨傲天并不是真”傲天“这也是通过上帝视角才知道的，要不然以一张没有任何表情的骷髅脸来看，当真是逼格很高的感觉。合理性对于一个穿越到未知世界人来说，做事谨慎是必须的，哪怕自身实力很强，但是对于未知事物也怀有谨慎的心理。更何况派出去一个战斗型守护者都曾被控制住，这无疑让人更加谨慎。骨傲天的本质骨傲天的心理其实还是一个20左右的年轻男性，所以观众对于骨傲天的各种行为是很理解的。以试想一下，你自己跟骨傲天情况一样的话，你会怎么做？实力虽然观众一直说骨傲天一直在全程装逼，但是从作品本身来看，骨傲天是没有这一行为的，他的确很强，但是自己却不知道在这个穿越过来的世界自己的实力排在什么位置。万一这个世界遍地是斗罗强者那可怎么办...........当然了，在得知对手比自己弱很多的时候，强者姿态就有了装逼的嫌疑。反差萌一个强者，心理却是一个普通人。表面稳如狗，实则慌得一B。也因为普通人的身份满足了观众的中二幻想，所以是讨厌不起来的。后宫其他作品的”傲天“主角有了强劲的实力之后，大多数都开了后宫。然而骨傲天因为”强制疲软“，想开都是有心无力，于是只能搞正事。以上~就是不讨厌骨傲天的理由。欢迎讨论~欢迎补充~', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10382', '0', '《OVER LORD》骨傲天的后宫们', 'OVER LORD是一部骨傲天设定的动漫，讲述的是主角“安慈乌尔恭”满级神装和整个工会穿越到异世界，带着一群满级小弟前往新手村庄装X的故事。并且在大坟墓当中老骨还有许多小迷妹，老骨随意的一个亲昵动作和言语，都能让她们坠入爱河无法自拔。夏提雅-布拉德弗伦夏提雅是由无上至尊41人中的佩罗罗奇诺创造的工会守护者，一身萝莉外表，是动漫中的合法萝莉之一。实力不管是在大坟墓还是异世界都是名列前茅的存在，除了萝莉的外表夏提雅还有一些其他的职业装扮，血之狂乱模式，变身成为七腮鳗形态之后会丧失大部分理智。另外一个变装就是女武神形态了，女武神形态的夏提雅可以算是最强形态，足以战胜大部分工会成员。对老骨也是充满热爱的。毕竟佩罗罗奇诺给夏提雅的设定就是吸血鬼，对骸骨之类的有着异常的癖好，对不死族的安兹更是毫无抵抗力，一直与雅儿贝德竞争后宫正房之位而不合，老骨的忠实小迷妹之一。娜贝拉尔-伽玛娜贝经常跟随老骨外出前往新手村做任务，是由无上至尊41人中贰式炎雷创造的战斗女仆。在第一季王国篇中吊打了那个卡吉特，也许是跟随老骨久了，也学会了一些装X的方式，在和卡吉特的战斗中首先瞬移到身后给卡吉特一刀，然后陆续向卡吉特展示了多种可以杀掉他的方法。最后更是直接的放个大招消灭骨龙。以碾压的方式打败了卡吉特。索留香-艾普西隆看上去一头金黄色的长发，较好的面容。种族本体是无定形黏液，类似于史莱姆生物一样，把人吞噬进体内慢慢消化，被安兹安排了跟随在泡妹老管家赛巴斯身边，协助赛巴斯在王国收集情报。在第二季中因为赛巴斯拯救了被六臂所伤害的少女，并且收养了少女。让索留香觉得赛巴斯背叛了大坟墓，然后像老骨打了小报告。雅儿贝德雅儿贝德是动漫中的女主角，由无上至尊41人中的翠玉录所创造的三姐妹之一。大坟墓的守护者总管身份（老骨的私人秘书）。也是唯爱于老骨的，在动漫中可以看出雅儿贝德对于老骨的爱比对工会的爱更加热切。老骨和娜贝前往新手村装X，留守雅儿贝德在大坟墓里看家，雅儿贝德也是乐此不彼。整天没事儿的时候都在老骨的房间里面抱着老骨的抱枕暖床，在动漫中可以看出雅儿贝德对老骨的占有欲是非常强烈的，对同样喜欢老骨的夏提雅是非常不对眼的。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10383', '0', 'overlord：论装逼我就服这六位 这方面就连骨傲天也比不', 'overlord这部作品中男主角势力登场就是满级而且剧情越往后其实力强大之处显现的就越多，对于这种开局龙傲天就可以秒遍全场的人物，各种装逼行为让观众看起来会有一种异常的爽快感，本次我们就来盘点一下作品中装逼能力最强的几位人物吧。NO.6灭国萝莉高声作大死的典型，对自己实力太过自信的妹子，和虫妹开打后就一直以为自己占有绝对的优势，所以自信满满，而且大言不惭的说自己在虫妹实力至上，不过还好，依比鲁艾多少算是后期有点自知之明的人。NO.5娜贝因为和骨傲天旅行，就连比较冷门人形象的娜贝都多少有点自大和飘飘然了，在对抗卡吉特和双骨龙的时候这位昆虫学家嘴中的词汇就没停过，学会了骨傲天真传的扮猪吃老虎招呼，展现真实实力后先给了卡吉特一招背刺让他明白自己随时可以去他性命，最后用强横的魔法当面击溃骨龙，娜贝酱啊，跟老骨时间长了你真的是啥玩意都学会了，不过这行为果断要给满分。NO.4克莱门汀有一位高升作打死的人，看她和骨傲天的战斗真心是一边看着一边脑补千百种方法看这位自大到不知道实力差距还有天高地厚的妹子到底会被骨傲天怎么处理，结果骨傲天选择了最为喜闻乐见的手法，堂堂英雄等级的战士被法师活活的给抱在胸口挤死了。NO.3夏提雅装X的最高境界不是别的，而是不动声色就瞬间毁灭对手的斗争意识和身心，夏提雅很多人可能没看出来，面对王国实力比肩战士长的指甲刀不但用小拇指的指甲接下最强的武器，而且还嘲讽，包括之后第二次遇到指甲刀依旧也是不懂声色的嘲讽，实力表现满分。NO.2骨傲天全书的主角其实是最能装的，但平心而论，安兹这么做全都被逼迫的，他本来就是一个没多少野心就想保留现有实力当条咸鱼安安静静悄咪咪的调查清楚这个世界的实力，然后隐居生活的，结果越高越乱，越折腾事越大，为了维护NPC心中自己的高达形象而不得不常常开启演戏模式被迫装逼，当然安兹的演技终究没有让人失望。NO.1塞巴斯塞巴斯毫无疑问是第一位的，第二季动画的重头戏是王国篇，说简单点后8集全是以塞巴斯为核心去描写的，塞巴斯从开头到结尾都是处事不惊的表现和态度，但是表现力实在是太可怕了，不露声色不展现实力一拳一个小朋友，面对六臂成员，10秒解决，剩下的杂鱼追加10秒，这话我就一个大写的服字。而最经典的地方就是六臂头子零开了大招后不但毫发无伤接下然后一脚秒杀，然后淡定的说“看来我比六臂强那么一点点啊”对不起这一点点我看到了一个宇宙。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10384', '0', 'Overlord中骨傲天大概在个什么水平？公会中等，但比不上', 'Overlord不死者之王由小说改编，讲的是个推销员在某游戏里是个顶级公会的会长，在游戏关服的时候没跑出来穿越到了一个水平比较低的世界的故事。整个漫画偏向爽文风格，你想你一个100级大号带着一帮100级NPC和高级魔怪带着N多个世界级道具和不知道多少件六神装穿越到一个50级和蓝装都很少见的世界，那还不是要为所欲为了？好比你小时候玩口袋怪兽，带着超梦、裂空座、阿尔宙斯、mege喷火龙从真新镇出发，剩下的还用玩吗？既然主角团已经无敌了，咱就对内比一比吧。主角团最强大的时候是拥有41位顶尖玩家的全服排名第九的公会，最牛逼的一次是8个公会总共1200余人进攻纳萨力克地下大坟墓被全歼，自此安兹乌尔恭成为神话，那主角飞鼠在公会里水平是如何呢？首先可以确定的是主角飞鼠是的确牛逼的，100级不死者魔法咏唱着，魔力值高达1980（普通玩家顶级1300左右）会700多种魔法（普通玩家顶级也就300多种）可以独自对付两个顶级玩家，全服水平上游下等。但是纳萨力克地下大坟墓41人高手如云，飞鼠在里面也只能是中上，比如飞鼠的救命恩人塔其米，拥有称号世界冠军，全服第二的强者，还是个现充富二代。乌尔贝特，世界灾厄称号拥有者，虽然含金量要低点但也很厉害，实力公认在飞鼠之上。加上武人御建雷目标是打到塔其米一直在打磨武器（结果在研造出斩皇神刀后塔其米退服了）这些人实力都在飞鼠之上。塔其米乌尔贝特至于NPC纳萨力克地下大坟墓的管理者们，他们的净实力（不算世界级道具、神器等）很多人是强于安兹的，比如夏尔提亚，与安兹一战中安兹如果没有使用氪金道具估计就输了。雅儿贝德，守备力最强恶魔，基础能力高于安兹，加上高强的体术和纳萨力克最顶尖的防御力对于安兹来讲也是棘手的存在。迪米乌哥斯，小名小迪，虽然基础战力不如安兹，但从王国篇来看谋略在安兹之上。其他人虽然没有明显出手但可以想象百级NPC没有弱的。这个就比较尴尬了，到大还不如小弟厉害？这个问题很容易想象嘛，暗黑破坏神都玩过吧，你雇佣的仆人基础战力相较你怎么样？不管是第一关的射手第二关的战士，还是后来的法师，能力都远高于你，毕竟你是玩家，他是电脑，在魔兽横行的世界上混不能光靠你复活，自个还要有两下子，不知道你思考过没有你和你的仆人战斗会怎么样？打人机肯动输不了，因为你知道怎么对付电脑，但也不会太轻松。7月份《Overlord》第三季就要上线了，期待安兹一个超位魔法秒杀王国几万人的剧情。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10385', '0', '骨傲天又回来了！《Overlord》第三季即将上线！', '只间隔了三个月，老骨就又回来了~第三季将于7月播出，剧情对应原作7~9三卷内容，异世界大战持续爆发，并且这次老骨将要建国！精彩程度应该会比第二季好很多，起码老骨不会像第二季一样持续掉线，戏份少得可怜。这次将讲述从帝国来访到安兹魔导国建立的全过程，即从“大坟墓入侵者”到“破军的魔法吟唱者”。第二季最后登场的巴哈斯帝国宫廷老魔法师和与金闪闪迷之相似的年轻鲜血帝，这两位将在第三季里和老骨上演精彩的对手戏。宫廷大法师福鲁达 声优：土师孝也大法师福鲁达是个厉害人物，目前人类最强法师、能用6阶魔法、能匹敌帝国全军。本人对魔法知识非常狂热，所以对魔法非常厉害的老骨非常感兴趣，天生异能是能感知别人的魔力。有些妖孽的 巴哈斯帝国‘鲜血帝’吉尔克尼弗，感觉和金闪闪真是谜之相似。声优：樱井孝宏第二季结尾露出真面目的“灭国萝莉”伊鲁比艾成功晋升为‘安滋迷妹’，可喜可贺。这位曾经是人类，现在是吸血鬼，灭过一个国家，活了两百多岁，被十三英雄敌对、最后成为帮助十三英雄的传奇生物，将会有更多戏份。伊鲁比艾 声优：花守由美里《Overlord》讲述了某天，一款掀起大热潮的游戏停服，一位真爱玩家悉心等待最后时刻的到来。然而过了停服时间，玩家并没有被强制退出反而和自己的公会一起穿越到了异世界！麾下的NPC也有了各自的思想，这名玩家还变成了拥有骷髅外表的最强魔法师！于是，传说就此拉开帷幕！小说改编动画第一季于2015年7月播出，引起强烈反响，主人公更是被大家爱称为“骨傲天、老骨”。第二季18年1月播出，制作略显粗糙，但掩盖不住原作小说剧情的魅力，依旧吸粉无数。于是乎第三季趁热打铁，7月来袭。从以前的剧情就可以看出，老骨是非常爱纳萨力克的，一切以地下大坟墓利益为先。同时老骨也是个理想派，想创造多种族共存的世界，且看老骨是否会实现吧。第三季有搞笑、有热血、有建国和大坟墓日常、有目中无人的妖孽鲜血帝吃瘪、还有老骨的怀旧心情，看点颇多。希望不要像第二季一样省略诸多重要细节、制作略显粗糙，将老骨建立帝国这一澎湃过程完美还原，大家拭目以待吧。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10386', '0', '《overlord》动画第三季剧情预知，骨傲天将化作魔王？', '众所周知《overlord》的第三季动画即将在这年的7月回归，作为近些年最火爆的轻改动漫之一，它引起了我们的关注，第三季的内容究竟会是怎样呢？毕竟大多数人是没有看过小说的，这里我就来给大家预告一下，不想被剧透的观众请谨慎观看下面的内容要了解第三季的内容先看看一二季到底说了什么，第一季的内容是小说的第一卷至第三卷，也就是“不死者之王”、“黑暗战士”、“鲜血的战争少女”，全篇以骨傲天最终击杀了被世界级道具倾国倾城所控制的夏提雅作为结局总体来说第一季的格局相当的不错，骨傲天各种装逼让我们看得很爽，向我们呈现了这个宏大的世界观，为这一系列的成功奠定基础第二季的内容是小说的第四卷到第六卷，也就是“蜥蜴人勇者们”、“王国好汉上”、“王国好汉下”，全篇以漆黑英雄莫莫将恶魔王击溃作为结局。这一季主要以群像剧的形式呈现，一开始的蜥蜴人，科塞特斯，然后是赛巴斯，苍蔷薇等人，尽管能呈现大量的角色b站上的点击量也高居不下，但依然有不少人吐槽安兹的戏份太少，没有太多装逼的爽快剧情不过第三季可以说会解决这个问题，因为第三季安兹的戏份会暴增，并且会非常的劲爆。至今第二季的剧情已经到了小说第七卷的内容，第三季也应该是小说的3卷内容，也就是“大坟墓的入侵者”、“两位领导者”、“破军的魔法吟唱者”。骨傲天会从地下大坟墓蛰伏变成光明正大的打出自己旗帜建国，这个剧情是不是相当的爽快呢？故事主要会围绕在巴哈斯帝国与王国之间，安兹乌尔恭老骨邀请巴哈斯帝国的国王”吉尔克尼弗“游览墓地并向其展示力量，在”吉尔克尼弗“受到巨大震撼之后，同意了帝国成为老骨魔导附属国的建议骨傲天的行为引起了王国的不满，面临数十万大军，最精彩的一幕应该就是安兹使用了一个9介超位魔法——献给黑山羊母神的贡品，瞬间秒杀了几万大军，而随后死亡的士兵身上也诞生了许多90级名为黑羊幼崽的怪物，最终王国军全灭，并且安兹一对一单挑轻松杀死了战士长由此可知骨傲天将真正展现自己的实力在这一季，对于喜欢看他装逼的人来说应该是相当的期待，另外安兹在这一季中的表现相当的残暴，不知道会不会从一个逗比变成一个大魔王呢？另外如果要还原原著的话这一篇幅会相当的黑暗，尤其是第7卷的黑工篇，如果处理不好的话会有不好的反响，这也是人们比较担心的一点，希望动画组能够做好第三季，真的让我们相当的期待关注【拾部次元】，回复“东京食货”，“东京搜查官”获取全三季动画！国漫神作《史上最强》现已上线各大平台，点击下方【了解更多】即可跳转查看！', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10387', '0', 'Overlord雅儿贝德叛变成大BOSS？竟然对安兹名字直接', '在《overlord Ⅱ》第十话的结尾，我们看到雅儿贝德竟然一反常态，竟然对安兹嗤之以鼻，用嘲讽的话语说出了句「真无聊」。 老骨还在审判赛巴斯，赛巴斯也证明了自己不会叛变，难道会叛变的会是雅儿贝德吗？鉴于她对老骨的态度，和一直痴女的形象，满屋的老骨抱枕，证明了她确实是深爱着老骨，以至于在结尾让观众一头雾水。其实，雅儿贝德并不会叛变。 在第一季的时候，在等待关服时刻的时候，飞鼠无聊翻到了雅儿贝德的资料设定，因为内容太多而走马观花的观看，原先设定的反差萌让飞鼠觉得无语，好好的一个漂亮妹纸，竟然是碧池属性，实在是太可惜了，所以飞鼠就改了雅儿贝德的设定为「深爱着飞鼠」，雅儿贝德也成了唯一一个改了设定的NPC。 雅儿贝德设定的改变还是可以看得出来的，虽然也保留了一些本性，但是她只是对飞鼠一个人展示自己的痴态。雅儿贝德也变成了病娇属性，相信这绝不是老骨想看到，试想一下，你身边一直有个我妻由乃存在，这感觉....也恰恰是因为这个原因，雅儿贝德在第十话结尾说出了这话。老骨本名是飞鼠，而在在之后改成了安兹，安兹·乌尔·恭是公会名，老骨也想借机将公会名字打出去，看看是不是还有其他穿越者。而老骨在关服前改了雅儿贝德的设定为「深爱着飞鼠」，而他现在又改名为了「安兹」，雅儿贝德忠于的是飞鼠，而并非安兹。 在大坟墓中，NPC的忠诚度极高，只要是至尊说的，都会臣服，而且现今也只剩下一位至尊，他们都害怕老骨会抛弃他们，所以，基本上，是绝对的忠诚。而雅儿贝德忠诚的对象是飞鼠，而不是安兹，所以她才会说「安兹·乌尔·恭」这个名字很无聊。「这座纳萨力克地下大坟墓是只属于您的。我雅儿贝德只想向您一人效忠，希望有朝一日还能听见您那美妙的名字」，这是动画没做出的雅儿贝德的自言自语，可以看得出，雅儿贝德对飞鼠的执念。 雅儿贝德忠于飞鼠，对于其他抛弃他们的至尊，雅儿贝德憎恨着他们，而公会也跟雅儿贝德无关，她根本不管这些。雅儿贝德憎恨着其他的至尊，从原作小说第七卷可以看得出来，雅儿贝德提出要组建搜索无上至尊的队伍，不仅仅副官是潘多拉，高达80级以上的魔物也多达15只，甚至连卢贝多都想要动用，但是被安兹拒绝了，可以看出雅儿贝德的目的不是搜索无上至尊，而是要消灭他们。 安兹这个在现实中缺爱的角色，在公会里体会到了爱，所以他非常的在乎这些感情，很重视雅儿贝德她们，公会同伴也一样。雅儿贝德的行为某种意义上也是背叛了老骨，但她绝不会成为大boss，只会为了她心爱的飞鼠清除威胁。 雅儿贝德会变成这样，也只能怪老骨自作自受，虽然老骨隐约感觉到了这修改设定的影响，却也没有多大重视。雅儿贝德会背叛成大BOSS？现在看来应该没有这个可能性。文/动漫Con 编/我就是肥宅', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10388', '0', 'Overlord：雅儿贝德三姐妹一个比一个奇葩，最有可能背叛', '   Overlord中，各个NPC都被赋予了不同的属性和比较有趣的设定，而当中反差萌，是最常见的。说到反差萌，就不得不提雅儿贝德，而除了雅儿贝德之外，她的两个姐妹也一样的奇葩，今天我们就来说一说雅儿贝德三姐妹。  妮古蕾德（大姐）   首次出现在第三卷安兹对战夏提雅前让她确定夏提雅所在的位置，可能是因为长得太恐怖了，动画中没有给她露脸的机会，又或许是因为无法将她的恐怖表现出来。  妮古蕾德因为设定的关系，被困与第五层冰结牢狱，其能力是完全监视，职业都是专门用来收集情报的调查系。属于魔法咏唱者，等级未知（目测100），属于最高阶的NPC。    妮古蕾德在外貌上没有眼皮和嘴唇，对娃娃玩偶有着独特的爱好。创造者是设定狂魔翠玉录，因为反差萌，外表恐怖，内心温柔体贴。  当初创造出来的时候，安兹公会的同伴前去查看时，都被吓得要开始攻击妮古蕾德。应对妮古蕾德的方法很简单，就是要准备娃娃人偶，否则就会被攻击，这什么设定啊！    牢狱布置了很多类似婴儿的蠕动肉团魔物，等级为二十级，名为腐肉赤子，属于付费或支付游戏币布置的，死后不能复活，真不愧是氪金狂魔翠玉录。  王国篇中为了保护从王国抓来的人类小孩，和狗头女仆长一起被关了三天禁闭，雅儿贝德还想要处死她们，因为这违抗了至尊的命令，安兹你看看你都改了什么设定！  雅儿贝德（二姐）   文库版新增角色，负责管理大墓地的一切事物，这样安兹才能安心的出去装X。原本的设定为碧池，安兹觉得一个美人是碧池太恶趣味了，便修改设定为深爱着飞鼠。  雅儿贝德拥有翠玉录私下给的世界级道具【地狱深渊】，全身装备神器级铠甲，黑色三层式装甲，可液化随意改变形态，这也难怪雅儿贝德的角和翅膀能够装下，被称为大墓地最强之盾。    因不满安兹改名（设定是爱着飞鼠），对安兹这个名字十分的不满，甚至践踏公会旗帜，不过对安兹是绝对的忠诚，哪怕大墓地全员造反，雅儿贝德始终会站在安兹这边。为了安兹，就算是杀掉自己的创造者翠玉录也会毫不犹豫的去做，果然病娇都不好惹。    雅儿贝德组建了至尊搜索队，获得大墓地最强战力卢贝多的指挥权，外加潘多拉和85级的十五只魔物，虽然表面上是搜查至尊，其实是为了让安兹有绝对的统治权，安兹对此事并不知情。  雅儿贝德对抛弃大墓地的至尊们极其不满，这也是为什么说组建的是猎杀小队，真实目的疑似是因为对安兹的爱过于强烈而导致无法接受其他无上至尊归来并打算自行处置。也因为对安兹的名字抱怨不满而被小伙伴认为会背叛大墓地，但不会背叛安兹。  卢贝多（小妹）   原作小说中并没有多少出场机会，基本上都是一笔带过，是大墓地的最强单体，就连公会最强的世界冠军塔其米都不是她的对手，只有第八层的“那些”和世界级道具并用才能胜过她。  妮古蕾德曾警告雅儿贝德，虽同为翠玉录制作，卢贝多与自己和雅儿贝德的创造方法完全不同，必定会对纳萨力克带来巨大的灾害。    很多人猜测卢贝多是由世界级道具所创造出来的，而翠玉录这个设定狂魔和氪金大佬完全有可能这样做，翠玉录私底下给过雅儿贝德公会未知的世界级道具，谁知道他还会不会有别的，用来制作卢贝多。  翠玉录这个设定狂魔也许是知道安兹会穿越所以才会给安兹留下一个十分不稳定的麻烦（笑）。而卢贝多的外表，翠玉录将大姐设为恐怖少妇，雅儿贝德为美人，卢贝多应该与她们不一定，也许卢贝多的是萝莉也说不定。    以上就是雅儿贝德三姐妹的介绍，翠玉录不愧是设定狂魔，一个恐怖风，一个是碧池，还有一个不稳定的内在威胁，是最有可能背叛大墓地的存在，这可真的难为安兹了。    ', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10389', '0', '女主大盘点1：Fate呆毛王，宝儿姐，雅儿贝德你最喜欢哪一个', '首先我们说的Saber指的是阿尔托利亚·潘德拉贡，也就是FZ及FSN中的女主。其真实身份是亚瑟王，对！就是拔出石中剑的那位。（不要问我亚瑟王为什么是妹子，我们就喜欢妹子亚瑟王~！）江湖人称呆毛王。呆毛王saber作为召唤出的英灵，穿上盔甲可谓英姿飒爽、剑眉星目、英气逼人、百看不厌可谓人中豪杰；退下戎装而又蕙质兰心、绝世独立、明眸皓齿、闭月羞花真乃绝世佳人。卿本佳人，却因守护二字，披上战甲，拔出石中剑；只为保护哪些她最爱的人。正是这种本不该她承受的命运重担压在这个女子身上。更让人喜爱、尊敬、难忘！呆毛王那晚、库房“你是我的主人么？”言语鲜明、英气十足；“遵从召唤而来，此后我的剑与你同在，你的命运与我相存。于此，契约完成！”我不会忘记那个夜晚。冯宝宝《一人之下》女主，人送外号“铁锹猫人”，一手绝学《阿威十八式》江湖上罕逢敌手，江湖上的朋友见面那个不得叫声：宝儿姐。江湖人称：宝儿姐做人当如冯宝宝，或痴或傻任逍遥。宝儿姐貌如天仙却愿缠世间烟火；气若冰霜还精通埋人之术；冷艳高贵方可行煽猪能事；痴傻呆萌安知那大智若愚。就是这样一个非常规的女子，把你迷的神魂颠倒，让你知道劳动人民--宝儿姐最美。目前呢无论是动漫还是漫画都还没有揭晓宝儿姐的身世之谜，但是呢估计跟另一个神秘人物吴根生有莫大的关系。哎，不知道我们的百岁老人宝儿姐到底是谁啊？“别人说我瓜，其实有时候我机智的一匹！”其实这句话翻译成诗就是“别人笑我太疯癫，我笑他人看不穿。”你看看，什么叫大智若愚，这就叫啊。红薯咧，卖红薯，3块钱一个，十块钱3个咯！宝儿姐的另一大特点就是--口音。大家没觉得方言版的宝儿姐是那么接地气么？“瓜娃子”“脑壳痛”“哈麻皮”好吧，最后一个是我乱加的~（谁叫我是作者呢）最后我要给大家介绍的是你们的老婆--雅儿贝德。雅儿贝德身为纳萨里克大坟墓的最高主管，也是我们的骨傲天头号粉丝，疯狂的迷恋骨傲天大人，人生梦想就是给骨傲天生猴子，不！生骨头？雅儿贝德雅儿贝德是由于被骨傲天篡改过数据，所以疯狂的爱着骨头，忠诚度也是突破天际的高。而且智商非常高，是个出色的战士与战略家。雅儿贝德与迪米乌哥斯被称为纳萨里克大坟墓两大智囊。说女主你是老婆的那个，拔刀吧！说起雅儿贝德就不得不顺便说下我们的逼王骨傲天--安兹·乌尔·龚大人了。这个开挂般的角色马上又要和大家见面了。2018年7月新番《OVERLORD3》大家坐等骨王装逼吧！', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10390', '0', 'Overlord：雅儿贝德的“至尊搜查队”太强大，至尊遇到只', '雅儿贝德因为安兹在关服前改了设定，使得从一开始的碧池属性变成了“深爱飞鼠”的状态。这也让雅儿贝德只会对飞鼠忠诚，而对于抛弃她们的其他无上至尊，雅儿贝德为了飞鼠，可是组建了“至尊搜查队”来猎杀至尊。那么雅儿贝德的“至尊搜查队”真的能够让找到的至尊领便当吗？‍一、配置。‍首先我们来看一下“至尊搜查队”的配置：百级的雅儿贝德，安兹亲儿子潘多拉，大墓地最强单体卢贝多和十五只八十几级部下，有装备和道具。而另一边的四十位至尊，全身果装。至尊在人数上占优，如果是报团的至尊，搜查队也无可奈何。就算打不过，逃跑应该没什么问题。二、实力。至尊都为百级玩家，实力自然不用说，掌握着各种魔法与技能，但是已经退游的成员们，就算穿越过来了，也都相当于空号，没有装备和道具。而在Overlord中，装备和道具是非常重要的，不是还有神装的绝死绝命能够打赢无装夏提雅的说法，而安兹能够打赢夏提雅也是靠着装备的优势。另一边的雅儿贝德、潘多拉和卢贝多都是持有装备和道具，雅儿贝德是纳萨力克四名最擅长肉搏战的NPC之一，最强之盾；潘多拉按作用来说他一个人可以代替任何一位守护者，属于绝对的多面手；更别提卢贝多这个最强单体，另外光靠着装备的碾压，就足以击溃至尊们。三、未知因素。雅儿贝德是真的想要干掉抛弃她们的其他无上至尊，小说中也说过，NPC是绝对忠诚于自己的制作者的，雅儿贝德是因为更改了设定，所以肯定会背叛翠玉录。但是潘多拉是安兹的亲儿子，就算平时滑稽又中二，但是遇到其他的无上至尊时，应该会报告给安兹，除非潘多拉又滑稽被雅儿贝德给忽悠了。另外的卢贝多，除非是遇到自己的造物者，按照她的情况，应该会听雅儿贝德的话，努力的让至尊领便当，平时就不怎么稳定的卢贝多，发狂就是她的拿手好戏。另外，单数至尊遇到搜查队时无疑会被秒杀，但是遇到复数时，雅儿贝德她们之间的战斗配合就是个大问题。在封印魔树时，守护者之间的配合简直差得一塌糊涂，而至尊们玩游戏都这么久了，之间有配合，就算打不过搜查队，但是靠着配合牵制，能逃跑的几率非常大。话虽然是这么说，但是上帝视角的我们都知道，公会其他成员基本上是不可能会穿越过来的，所以不仅安兹与空气斗智斗勇，就连雅儿贝德也一样，看来这是会传染的！如果真的穿越过来的话，遇到搜查队，也只能掉头就跑。以上。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10391', '0', 'overlord大墓地忠诚心排行：雅儿贝德这表现已经不能说忠', '本次我们来说说overlord中纳萨力克大坟墓里所有成员忠诚度的排行，因为第一季动画夏提雅的叛变事件给人一种守护者很容易叛变的错局，到了第二季中因为塞巴斯救老婆事件还有主题曲小迪和飞飞对战的画面让人误以为这俩人也叛变了，实际上不是，大墓地的所有成员对于安兹都是100%忠心的，不过这个所谓的忠心也有个深浅的定位，忠心太过头的，甚至是达到了愚忠的程度。排名个人向，不喜勿喷NO.4仓助、战斗女仆等其他人员因为大墓地只留下了安兹一个人，在没有其他无上至尊的前提之下，他们肯定是只听命于安兹的，仓助这里表现的尤为明显，毕竟本身实力就不咋地，不听话的下场必定是被亚乌菈给玩死。NO.4双子姐弟双子姐弟的忠诚心相对于其他人来说比较高，当然这只是相对的，因为科塞特斯最初设定十分憧憬侍奉安兹的后代，所以相对于安兹来说（好像他更喜欢潘多拉？？？！！）而塞巴斯就不说了，瞒着安兹救老婆哪怕是因为有正义值的设定，其实这也是违背忠诚的，不然索留香也不会判定塞巴斯会叛变，最后夏提雅的叛变是因为不以个人意志所能左右的，这个其实不怪她相对于这几位守护者来说，双子姐弟明显没有太多的黑历史，安兹说啥是啥，吩咐什么是什么，可以说非常听话，当然安兹也给了他俩不少好装备和道具。NO.3潘多拉·亚克特亲儿子没的说，毕竟亲生的，就这层关系来说就和别的人不一样，忠诚心自然不用多说了，就是当年设定的太过中二了，反而成了安兹自己的黑历史。潘多拉对于安兹也是言听计从的，哪怕改动当年的设定也没事，果然亲儿子够听话。NO.2迪米乌哥斯光看动画很多人觉得小迪可能叛变了，其实不是，这只是演戏罢了。作为全书中的智商担当，像小迪这样有思想有头脑的人想要篡权是没有问题的，然而很可惜他一点这个想法都没有，而是对安兹奉上了无限的忠诚，哪怕是一句玩笑话都能当真（安兹：都是月下说什么征服世界惹的祸！！）剧中小迪一直以为安兹是最最聪明的，自己根本无法逾越，然而事实上很多计划安兹压根就想不到，所以为了故意装出来什么都懂的样子，安兹就故意让小迪代他表达（演戏太辛苦了）这样的事情发生了很多次，当然小迪从来没有意识到也没有想过，而是一直都以为安兹比自己聪明的多，是什么都想到了只是懒得说而已。而最直白的表达忠诚度则是在安兹与夏提雅一战中，雅儿贝德一个劲说没问题，小迪急的钛合金钻石眼都蹦出来，就差自己跑前线去灭了夏提雅了。NO.1雅儿贝德这其实是飞飞的一个失误，原本设定非常负责的，结果把雅儿贝德的碧迟设定给改成了深爱飞鼠，这算是埋了个祸根，雅儿贝德相对于其他守护者来说对于安兹的感情已经不能用忠诚来形容了，而是用深爱到有点病态来形容了，虽然看起来抱枕和小孩衣服一类的很正常。但是在小说中，有这样一段描述“安兹乌尔恭吗？无聊透顶。“而公会标志性的旗子则被积灰，这里雅儿贝德是唯一一个反感公会名字的守护者，因为设定上她深爱的是飞鼠而并非安兹，所以她效忠的也仅仅是飞鼠，甚至她有了当其他的至尊也出现的时候要将其消灭掉，这样就能保证飞鼠至高无上的地位了。当然这并不代表雅儿贝德叛变，就算是叛变，她叛变的也是公会而并非安兹。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10392', '0', '《overlordⅡ》第十集，雅儿贝德最后那句“真无聊”你看', '来说下《overlord第二季》第十集吧，这一集感觉有点快（水），打斗的场景都没有，本来还以为赛巴斯会和八指干起来，没想到又是冷场。期待落空，如果按照这季12集的话，后面的剧情真的很赶！第十集大致剧情第十集主要讲了两件事，第一件事是赛巴斯和琪雅蕾，赛巴斯被安兹叫了过去开会，问了他的忠诚如何，安兹还考验赛巴斯，让他杀了琪雅蕾，在赛巴斯杀琪雷娅的时候被阻止了，于是考验通过，然后安兹以他的名义宣布琪雅蕾是大坟墓的一份子，之后就继续在那里闲扯一些事，总之就是灌点水，然后再分配下工作。第二件事是公主展示了原来的自己，其实在上一集也有看到，在公主照镜子的时候画风突变，明显是双重人格。这一次公主和他皇兄聊袭击八指的事情，然后展现了她原本的品行。从可爱的小萝莉，变成了黑暗系萝莉，其实公主是个玩弄人心的“怪物”，一切都在他掌握中。在结尾的时候，琪雷娅被八指的人抓走了，赛巴斯把这件事告诉了安兹，安兹命令雅儿贝德去办，在和安兹通话结束后，雅儿贝德说了一句“真无聊”，当时很多人不明白这是什么意思，以为雅儿贝德要背叛安兹，其实并不。雅儿贝德那句“真无聊”是这层意思如果你注意观察的话，会发现雅儿贝德在房间里放了很多安兹的娃娃，这些娃娃其实表明了雅儿贝德是喜欢安兹的，其次工会旗帜被雅儿贝德仍在角落里，’这个旗帜象征着创办工会的41个人，不过如今只剩下安兹一个了。雅儿贝德把它仍在角落里，其实应该是讨厌这个旗帜，至于原因，应该是工会的其他至尊抛弃了他们这群守护者。而雅儿贝德所说的那个“真无聊”，是指安兹当时命令雅儿贝德做事的时候以安兹.乌尔.恭名义，这个其实相当于以工会的名义，而雅儿贝德是讨厌工会的，所以听到安兹这句话觉得很无聊！不知道大家还记得第一季安兹给雅儿贝德的设定嘛，最爱飞鼠了。但后来飞鼠因为工会改名为安兹.乌尔.恭，也正是因为这个工会，夺走了雅儿贝德最爱的飞鼠之名。其实安兹只要说以自己的名义叫雅儿贝德办事，雅儿贝德一定欢天喜地，因为雅儿贝德最爱的是安兹。也想让安兹一个人称霸罪域，而不是这个工会。总体来说就是：因为雅儿设定是喜欢飞鼠，但是飞鼠不是改名安兹了，然后以安兹的名义来说的话，对于雅儿来说确实是无聊的，她只需要她的飞鼠', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10393', '0', '盘点动漫《Overlord》的骑士装，其中雅儿贝德的邪恶骑士', '动漫《Overlord》是广受好评的力作，在这部作品中很多骑士装备令人映像深刻，这些装备有的是没有效果的白板装备，有的是史诗级套装。那么今天就来看看这些骑士装哪个最好看吧！首先看看骨傲天穿越的异世界中，有哪些装备值得观摩下呢。作为王国最强的骑士长葛杰夫，一身装备到底如何呢？首先据说他佩戴了---不会疲惫的活力护手（听起来像20几级的绿装）能够持续治疗的不灭护符，名字似乎很厉害的样子。那套银灰色的装备是可以避开致命一击的守护铠甲，听语气会不会是不超过30级的蓝色装呢？剃刀之刃是他的专属武器，能轻松切开敌人的护装。另外手指上有一个魔法戒指，戴上可以升级一级！总体上骑士长大人的装备是属于蓝绿混装，在骨傲天眼里大概是爆出的低级副本里的小蓝装，小绿装，凑合搭配一下。但是在异世界，这套装备是骑士长所在的国王家的秘宝，是国王赐予给最信任的骑士长葛杰夫的。另外看看一旁的克莱姆，也就是公主的忠犬，他身上的装备是秘银铠甲，宽剑和盾，说的好听点是厉害的高级骑士装，说难听点，是白板无效果套装，只是可能级数略高点。比如一群公会冒险者，穿的是5,6级的白板垃圾装，那他穿的就是10级左右的高级白板骑士装。骨傲天平时在公会搜集情报，到处冒险玩的装备是什么呢？铠甲和两把巨剑，是他自己用魔法创造的道具。不过还是具备名字的装备，脑袋上带着“必中眼镜”，这样平A（平砍，不带技能，本身也没技能）的打过去，是必中怪物的效果。因为本身是法系的缘故，精准度肯定有问题，总不至于一直“miss未击中”吧，要带这个砍怪省力点。黑色头盔其实是精神防壁之冠，另外的是他的靴子有加速效果。总体装备只是为了好看，中规中矩的装样子玩耍。夏提雅·布拉德弗伦，在第一季与主人安兹·乌尔·恭对决的时候，所穿装备是有来头的，她本人修习的职业中有一环是诅咒骑士的职业，所以装备骑士装是易如反掌的。装备也是传说级别的红色骑士套装，背部还有翅膀！（应该算紫装？），专属武器是滴管长枪，神器级武器（橙装？），这把长枪，打别人，别人掉血，她可以吸收别的血槽。其实看了那么久的《Overlord》的骑士装，雅儿贝德的骑士装最好看，雅儿贝德修习的职业中一环是邪恶骑士，还是满级。雅儿贝德作为大总管，装备肯定是最好的，一套漆黑的黑色骑士装备，看上去就是恶魔！金属手套上有尖刺，还配有发出紫色感觉的斧头，一套橙装也不为过！总体这套漆黑的装备纤细曼妙加大胸肌，嗯，这套装逼格高，又不失趣味！', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10394', '0', '教室大玩制服Play,雅儿贝德诱惑十足!', '更多美味同人点击上方↑↑↑“半次元”关注我们~《Overlord》雅儿贝德 cn: 浅野菌子欢迎戳左下阅读原文下载APP， 搜索浅野菌子（←长按可复制）完整版高清大图等你围观~更多有爱同人欢迎关注喵~半次元 ACG同人创作&同好社群www.bcy.net', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10395', '0', 'Overlord：对力量一无所知的土著，作死能力还强，竟想娶', '在Overlord中，大墓地的基本上可以说是异世界最强了，而安兹喜欢跟空气斗智斗勇的爱好，也使得大墓地的实力并没有被土著所知道，直到后来建立魔导国，土著们才能窥得大墓地的一角。而就是因为这样，所以土著中就有那么一群对力量一无所知，但偏偏喜欢作死的角色，今天我们就来看看动画中这些爱作死的人，是如何作死的。滑稽姐克莱门汀滑稽姐靠着自己踏入英雄领域的实力，再加上一次的算计，不知道干掉了多少的冒险者，挂了多少个胸牌了。在将安兹引出来后，一对一与安兹战斗，在战斗之前还不时的吹嘘自己的战绩，炫耀自己让多少冒险者领了便当，还亮了胸牌。要知道亮牌子绝对是一件吸引仇恨的事的，滑稽姐干掉了一整只「漆黑之剑」，而安兹又是当中的一员，可想实力也不怎么样，可谁知，在嘲讽了之后，被安兹一招怀中抱妹给秒了。扎克刚穿越到异世界的时候，安兹派出了赛巴斯和索留香到外面去收集情报，二人伪装成贵族小姐和管家，为了吸引盗贼和捕捉有特殊才能的人，索留香故意到诱饵，将盗贼们引了出来。索留香在得到夏提雅和赛巴斯的同意之后，亲自去应对盗贼小头目的扎克。而扎克在看到索留香脱衣的行为后，一无所知的他竟然想风流一把，结果被索留香用欧派给吞了，真的是做鬼也风流啊。六臂六臂作为八指的警备部门，是八指中号称组织最强战力的六人，每一位都可以匹敌精钢级冒险者，头领的杰洛对上指甲刀还游刃有余的聊天，幻魔在与指甲刀战斗时还邀请过指甲刀加入，可见六臂的实力是非常的强大的，当然这是相对于土著而言。赛巴斯好不容易才“捡到”老婆琪雅蕾，却被六臂给绑架了，还设计想让赛巴斯领便当，结果对力量一无所知的六臂被赛巴斯一拳一个便当，与赛巴斯实力只差那么一点点的六臂，这样自己作死领便当了。苍蔷薇在搜刮了八指的艾尔玛的住处后，虫妹艾多玛留下来殿后。而因为贪吃的关系，捕食的画面被赶来的格格兰所看到，格格兰表示不能放过这艾多玛这个怪物，要知道虫妹可以瞬秒你格格兰的，你还不放过虫妹。虽然虫妹一开始没想战斗，但是那位格格兰弄脏了女仆装和后来言语刺激，虫妹便开始调教了格格兰二人。最后在赶来的灭国的灭虫魔法之下，虫妹才败下阵来。面对随后赶来的迪米乌哥斯，格格兰二人并不知道什么情况，只有灭国隐约感觉到了死亡的降临，结果小迪一个火墙就让格格兰二人领便当，灭国最后也只能靠着安兹才活了下来。菲利普这位不是动画中出现的角色，在小说第十卷出场的人物，是本作最会作死和无知的人。靠着安兹的魔导国，还没有多大的权力就开始幻想自己与安兹是等同的，魔导国没什么了不起的，最重要的是，面对绝世美人的雅儿贝德，菲利普竟然认为自己才配得上雅儿贝德，不仅在雅儿贝德面前调戏她，更是想娶雅儿贝德，简直无知到头了，某种意义上，还是很佩服他的。以上，这些对力量一无所知的异世界土著，作死能力还一个比一个强，甚至还想对安兹的后宫下手，真不知道该说什么好了，自求多福吧。', '', '1018', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10396', '0', '细声轻叹，艾伦耶格尔的感情世界', '每个人都有自己的目标，每个人也需要自己的爱情，尤其在逐梦的途中遇到爱情是一件极其幸运的事情，许多我们在生活中不会遇到的让人憧憬的美好事物，往往就被人类写进了许多故事里，这也是这些故事为什么这么迷人的原因所在，今天就让肥糖带大家走进巨人的世界，进击的巨人——艾伦耶格尔的感情世界！NO.1 三笠·阿克曼流着东洋人血统，以第104期训练兵团首席的身份毕业，性格沉稳冷静，有以一敌百的战斗力，与艾伦一同加入调查兵团，并且参与作战。艾伦·耶格尔的青梅竹马，在儿时被艾伦救过一条命，无家可归时被艾伦父子带回了家，后二人经常一起活动，互相视对方为最重要的人。她一切的行动准则都是以和艾伦在一起或者保护艾伦的生命为前提的。NO.2 阿尼·利昂纳德金发碧瞳，高鼻梁，有着俄罗斯女人的标志五官，是训练兵当中的冰雪美人。阿尼是莱纳·布朗、贝特霍尔德·胡佛的同乡，实际身份为女巨人，长时间的相处使得三人之间拥有强大的羁绊。其性格冷静沉稳，外刚内柔，寡言少语，伪三无少女，格斗能力超强，对艾伦非常欣赏，十分崇拜艾伦，但是谁又知道她对艾伦的感情仅仅是崇拜呢？NO.3 阿明·阿诺德作为曾经让一大票艾明党哭晕在厕所的人物，感情方面就不用多说了，不过作者真的是太坑了，真的是生命不息，坑人不止，明明是个可爱的男孩子，却一直不做具体的设定，这让一直以为爱尔敏是女孩子的同学多伤心。NO.4 利威尔·阿克曼嗷呦~~~厉害了，重头戏终于来了，我们的兵长大人终于终于终于出来了，艾利党快来，我们要冲第一！！！哈哈哈~', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10397', '0', '一个“不像主角的主角”，艾伦耶格尔。进击的巨人！', '说起动漫的主角，我们都知道，主角一般都有主角光环和后宫成群，身边的人都是为了衬托主角的强大而存在的。可是我们进击的巨人的主角艾伦呢，主角光环？在第一次和女巨人打的时候是因为看见自己的同伴一个个牺牲，愤而变身，一般来说动漫主角看见同伴的牺牲就想打了兴奋剂一样会变得特别强大，可是艾伦呢，变身后，打了半天，最后被女巨人无情一脚爆头。在说说艾伦的后宫....算了，这个真心没有。再说说他身边有太多强人：三笠是104期训练兵团首席，战斗力以一敌百。“完全掌握了所有的科目，在历届中也是史无前例的天才”阿明是主角团队的智囊，拥有不输给将军参谋的头脑；利威尔兵长就更不用说了，“人类最强士兵”绝非浪得虚名。他想救妈妈，妈妈在他眼前被巨人咬死了。他相信同伴，同伴在他眼前被巨人杀害了。谏山创在一问一答采访中答道艾伦是剧情的奴隶（也就是衬托他人和推动剧情），并且要求动画组把艾伦表现的再没用一些，谏山你这贱人怎么可以这么对艾伦小天使。身为一名人类士兵，他没有杀过巨人甚至还被巨人吞过。过你说他能变个巨人吧，十刃里还有四位也是巨人。他的巨人只有15米，不足超大巨人的1/3，没有铠之巨人铠甲般的皮肤，更没有女巨人的速度恢复能力和局部硬化、论威胁程度在莱纳眼中还不如尤弥尔巨人。巨人化损耗寿命，被砍断手脚可以复原，但疼痛却不会消失。但是以上这些都是我以前对艾伦的看法，现在不一样了，他确实在巨人形态下不足超大巨人的1/3，但是最后解决超巨的是他，还是以一名人类士兵的身份！虽然没有凯巨的铠甲，但是一样不虚凯巨（他运用阿尼的格斗技吊打凯巨）。后面也习得了硬化能力，这一点可以和女巨人抗衡。在威胁程度上，因为有了坐标之力比尤弥尔巨人还要大没错他前面总是输，但从来不会认输。前面他也没打赢，但他已拼尽全力。他不废材，不缺乏杀巨人的能力和勇气，只是过早被发现了巨人化的能力，后被各种利用各种保护，不会再像其他士兵那样以人类形态杀敌、炫技。104期训练兵团第五名，仅次于三笠和故乡三人组，这其实是个相当了不起的成绩。谏山创便当发得毫不手软。“因为怀着一颗想要守护的心所以能爆发出巨大能量”、“因为相信朋友所以关键时刻会创造奇迹”吗？不，这种事情并不存在。这是个炼狱般的世界，面对巨人那种压倒性的力量，没有人会因为亲情友情爱情而幸免于难。唯一能做的，就是把他们的音容笑貌埋在心里，然后更加努力地去战斗。即使会失去一切，说不定自己明天就会死掉。见过地狱的惨状，有些人退缩，有些人崩溃，而他不畏惧，不屈服，不放弃。即使亲眼看着妈妈被吃掉、同伴们一个个惨死，即使被囚禁、被审判、被怀疑，他的信念也不曾有半点改变，半分动摇。武力不如三笠，智力不如阿明吗？是的。但高与常人的武力与智力无法复制，勇气却有感染力。他拥有不输给任何人的决心和信念，足够把世界都点燃！其实标题上说艾伦是一个不像主角的主角，其实在我心目中，他是最像主角的主角最后来两句艾伦的名言我们每个人，自从出生的那一刻起，便是自由的，不管阻挡我们的人有多么强大都没有关系。火焰之水也好，冰之大地也好，无论什么都好，能够亲眼看见那些东西的人，才是这个世界上获得最大自由的人。战斗吧！！为了这个，哪怕牺牲性命也在所不惜！无论这世界多么可怕，也没有关系！无论这世界多么残酷，也没有关系！战斗吧！！战斗吧！！战斗吧！！战斗吧！！上面部分文字摘自百度贴吧！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10398', '0', '进击的巨人最终的反派猜想，有可能会是艾伦耶格尔！', '《进击的巨人》是一部月更的漫画，更新比起其他漫画来说，确实有点慢。以至于漫画前期就提出来的夺回玛利亚之墙，到最近几个月才实现并已经结束。不过虽然更新非常慢，但是其剧情非常紧凑，从艾伦他们初阵到故事最新更新的部分，在剧情中也不过才过了几个月而已，在漫画85话我们知道了墙外人并没有灭亡，然后86话将整部作品的世界观给进一步的扩大了，然后我就在想这部作品的最终反派会是谁呢？小编我就在已经登场的人物中找的了两个合适的人选。第一个：吉克·耶格尔（也就是兽之巨人） 吉克他是故乡三人组的长官，主人公艾伦同父异母的哥哥，可以变身为兽之巨人，有着可以命令巨人的能力（不过不是控制巨人，因为有些巨人就不听话）。在漫画中目前他是最大的反派，所以猜想他是最终的反派也有几分道理，不过可能性非常低，目前就和他站在同一阵营的只剩下铠之巨人莱纳（女性巨人阿尼目前还处于“冰封”状态，超大型巨人贝特霍尔被阿明给吃了），不过不能够确地就剩下莱纳了，因为吉克背后可是马莱帝国，也许后面还会出现更吊的角色。而我们的主人公一行最后和吉克决一死战之类的，也是有可能的。第二个：艾伦·耶格尔（也就是本作的主人公，可以变身始祖巨人。）在艾伦搬动巨石前曾经失控过，艾伦本体在那个时候是处于梦境的，最后还是靠阿明叫醒了他。艾伦巨人在那个时候就和无脑巨人一样，这个其实完全可以解释为艾伦对操控巨人形态的经验不过，导致本体意思昏厥之类的吧。不过艾伦还有一次失控是在内地和阿尼决斗的时候出现的事，在被阿尼一顿拳打脚踢之后，艾伦本体说出了这样一句话：我要把巨人杀光，一只不留！他以往说的是驱逐巨人，而这次说的却是杀光巨人。还露出了非常邪恶的笑容。之后其巨人形态身上冒着火焰，身体上还有非常明显的火焰般的裂纹。并且在身体四肢没有想以往一样冒着蒸汽并再生！不过值得一提的是这一段在漫画中并没有，应该是谏山创要求动画组加上去的吧，看动漫的时候觉得艾伦这个样子真心好帅，不过艾伦视乎并没有失去意识之类的，他清楚的记得这件事。这也是为什么我认为艾伦可能成为反派。不过但是我并没有认为他是最终的反派，直到漫画最新更新的剧情中，艾伦完全觉醒了父亲的记忆，知道了之前最恨的巨人们，其实都是自己受难的同胞，而吃掉自己母亲的巨人就是父亲的前一任妻子，不知道艾伦在清楚了这一切之后又会作为。所以小编才认为艾伦有可能会成为《进击的巨人》最终的反派。其实我认为这两个人是最终的反派的可能性都不大，最有可能是还没有登场的人物，比如一个一直存在于墙内拥有最后一个巨人之力的人物。有可能会有这样一个人物哦！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10399', '0', '萌漫大乱斗艾伦·耶格尔资料 技能全解析', '萌漫大乱斗艾伦·耶格尔，前排刺客型英雄，物理爆发型英雄，中排物理输出，攻击速度快。他变身之后的巨人艾伦每下攻击都成为群体攻击。下面我们一起来看看他的详细资料。萌漫大乱斗艾伦·耶格尔 英雄定位：中排输出初始属性物理攻击 77能量攻击 153物理防御 36能量防御 36物理破防 20能量破防 28命中 32闪避 28暴击 28打断 300击退 300英雄初始评级：C英雄搭配建议搭配伙伴为前排肉盾英雄技能螺旋斩：旋转自己的身体，利用惯性造成大量物理绝对伤害。机动突袭：利用立体机动装备，进行来回突击，造成物理伤害。恐怖威吓：释放出巨人的气势，增加全体攻击力。巨人之力（被动）：被动，巨人体质，增加生命上限。变身后技能巨人拳击：连续俩次物理打击，有几率并击退敌人。石块攻击：举起巨大石块，砸向远处的敌人，造成大量物理伤害。巨人体质：巨人的咆哮声，回复自身生命值。巨人之力（被动）：被动，巨人体质，增加生命上限。艾伦·耶格尔曾经生活在西甘锡纳区的少年。与三笠·阿克曼、阿明·阿诺德是从小的玩伴，关系很好。拥有强韧的精神力量与非凡的行动力，对墙壁外的世界拥有比任何人都要强烈的憧憬，从小便立志要加入调查兵团。在目睹了母亲被巨人吞食后，立誓要驱逐所有的巨人。曾被父亲注射过与巨人能力有关的一针，拥有着可能揭示巨人秘密的地下室钥匙。好了，以上就是给您带来的萌漫大乱斗艾伦·耶格尔资料介绍，希望能够帮到大家。想了解更多关于萌漫大乱斗的资讯，攻略请下载新浪全民手游攻略APP，一手资讯尽在掌控~扫描二维码下载全民手游攻略 全民手游攻略微信公众号二维码：扫描二维码关注全民手游攻略微信 关于全民手游攻略全民手游攻略致力于为广大玩家提供最新、最全、最详尽的手游攻略，在这里你可以查询到任何你想要了解的内容。问答社区服务为玩家们提供了相互交流并相互解惑的平台，让玩家们的疑问尽快得到解决。97973手游网声明：97973手游网登载此文出于传递更多信息之目的，并不意味着赞同其观点或证实其描述。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10400', '0', '萌爷爷艾伦的童话小屋', '文/秦雨晨艾伦.麦克法兰（Alan Macfarlane）：英国历史学家、人类学家，剑桥大学国王学院终身院士，英国皇家历史学会院士，皇家人类学会院士，英国科学院欧洲科学院院士。“欢迎来到两个老古董人类学家在乡间沼泽中的小屋！”从剑桥坐了近半小时车到了位于附近的小村，这里手机是没有信号的。这种茅屋早先是穷人的栖身之所，现在却属于受国家保护的遗产。他们对老旧物品的热情要远远胜过对新东西的喜爱。英国人觉得住在老房子里显得有根、有文化、有自豪感。艾伦家的“茅屋”比最常见的维多利亚建筑更古老，看起来胖胖的很朴实，像一幢童话里的房子。艾伦和他的妻子萨拉已经在这里居住了四十多年，并在漫长的岁月里对这里进行了诸多改造工程。白色的小屋背后别有洞天。小屋里收藏了世界各地的物件一间不大的办公室就像一本装帧考究的古董书籍，从艾伦的视角诉说着剑桥的历史和对整个世界的认识。艾伦和萨拉在自家的院子里建了这个日式茶室，焚香饮茶，在禅与茶之间思考花园中央有个日晷石雕，是萨拉送给艾伦的生日礼物。上面刻着爱因斯坦的格言“简化一切，但不能过于简单。” （Make things as simple as possible - but no simpler）”。责任编辑：陈思 陈美希', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10401', '0', '震惊！这家店如何利用60平米的店赚300平米的利润！', '在北京市朝阳区朝外大街的朝外soho，朝外SOHO位于北京CBD中央商务区内，是餐饮业兵家必争之地；今天我来介绍的这家店叫kao！烤肉饭。kao！烤肉饭建在朝外soho的第五层，也就是说他不是临街的门面，而是在大楼内部，这家店呢，做到日流水1.5万元，但是他的店面多大呢？只有60平米！其中40平米作为厨房，20平米作堂食，房租低廉只要7500，这就决定了kao！烤肉饭成本很低，所以他能做到别人20元人均午餐消费，它可以做到12元，因为非常便宜的中午菜品，让引爆kao！烤肉饭的口碑和销量。1.外卖细节分5个时段，充分利用每一分钟餐饮有忙先落差之分，对于kao！烤肉饭来说并不存在，它把每天时间分5个时间段；a.在早上8.30-9.30，是做早餐的时间，这个时间是专门为第一天预定的早饭，作快餐领取的，大家可以拿着二维码来实体门店领取早餐。b.早上9.30-10.30是为大客户做准备的，也是头一天订好餐的，然后批量让他们制作为大客户配送。c.饭点时间中午11.30-下午1.00，集中自己的人力快速制作菜品，能够一分钟20套，数量非常惊人！d.下午1.30-5.00这个时间是下午茶的时间，用自己20平米的面积做堂食，为下午出来休息的，谈事的白领提供空间，提供相对精致的下午茶。e.晚上5.00-1000-10.00是做夜宵的时间，kao！烤肉饭针对其他餐饮卖家并不重视夜宵这档外卖销售，特意为有夜宵习惯的客户，准备高性价比的夜宵，也成为了kao！烤肉饭的主力卖点！kao！烤肉饭为何能如此高效的运转呢？原因是利用了网络大数据，对每天菜品的销量进行预估，并提前准备，使得它生产每样菜品时间变短，减少客户等待时间，并提高客户体验。2.员工一人多岗，一岗多能a.如此高的工作强度，如此高的产品产出，这个店面究竟需要多少人维护呢？只需要8个人！8个人分三班倒，除了中午的饭点高峰期需要4个人维护以外，其他时间段只需要两个人便可以完成它的维护和运营。b.kao！烤肉饭对人员的管理也很有自己的想法，很多餐饮业来说，很多餐饮业来说喜欢招一下年轻的服务员，但是kao！烤肉饭更倾向于招些40岁左右的家庭相对稳定的已婚妇女，这些人对于事业的野心以及企图相对小一些，对kao！烤肉饭比其他餐饮业高的收入和待遇相对满意，所以人员比较稳定！大家可以看到，从kao！烤肉饭的成功案例中，我们可以学到很多东西，从他面对客户的单品分类以及时间段分配和店铺整体利用价值包括人员管理方面都有很多值得借鉴的东西!你的店铺利用价值永远不只如此！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10402', '0', '斯诺克世锦赛 丁俊晖 肖国栋即将出场 艾伦战胜海菲尔德', '2018斯诺克世锦赛首轮比赛继续，艾伦在面对海菲尔德的比赛中，10-5大胜对手，将在下一轮面对淘汰了塞大师的乔-佩里。同一时段的比赛，霍金斯4-1领先卡灵顿。今晚北京时间9:30分，中国另一场德比之战将在丁俊晖与肖国栋之间上演。​​晚间另一场比赛是墨菲和琼斯的下半场比赛，目前墨菲5-4稍稍领先琼斯。私信小编“世锦赛”即可获取观赛地址。​', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10403', '0', '“大小艾伦”喜感对比引发遐想', '《人间·喜剧》定档并公布先导海报新快报讯 记者聂青报道 由孙周执导,艾伦、王智、鲁诺领衔主演,任达华、金士杰主演的喜剧电影《人间·喜剧》,近日正式宣布定档12月28日,并公布了“戏精”版先导海报。海报中“戏精”艾伦承担了所有的戏份,“大艾伦”表情夸张,瞪大眼睛盯着手里的试管,而“小艾伦”挣扎地想要逃出试管,如此“大小艾伦”的对比充满喜感,同时又富含深意引发观众无限遐想。影片定档12月28日,这也是艾伦主演的喜剧电影首次杀入贺岁档,此次他将再度携手麻花搭档“国民初恋”王智,两人在电影中会产生怎样的化学反应值得期待。事实上,作为开心麻花的得力干将,艾伦这两年喜剧电影的表现,从《夏洛特烦恼》到《情圣》再到《羞羞的铁拳》,无论是票房还是口碑都可圈可点。在《人间·喜剧》中,艾伦饰演的濮通是一位爱讲鸡汤的电台主播,正常的他,却突然在人生的某个瞬间,开启了一场神奇的际遇。正所谓“人生如戏,全靠演技”,此次的海报以“戏精”为名,一方面表示电影的剧情将围绕着濮通展开,另一方面也暗示着濮通即将开始的“奇遇之旅”,在“试管”里,他将遇到这个城市的地下大佬、白道首富……这些距离他的生活相当遥远的人。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10404', '0', '《进击的巨人》利威尔兵长艾伦图集，兵长被扑倒哦，偏腐向（上）', '再次声明，兵长是我的！艾伦是兵长的！希望大家可以多多支持一下呀！感激不尽！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10405', '0', '进击的巨人，利威尔到底有多强？兵长二字就说明了一切', '利威尔，调查兵团士兵长、特别作战班班长，通称\"利威尔兵长\"。原是王都地下街的混混，后被调查兵团第13代团长艾尔文·史密斯带回，成为调查兵团中的一员。超·精锐部队的王牌，实力强到逆天，号称人类最强，战斗力相当于一个旅团。兵长一词出自中国古代战神蚩尤，他是最强的士兵。兵之长者为兵长。日本军事制度的兵长并非常规军衔，兵长在战时可以指挥包含尉官以下的士兵。这大概类似中国的军士长军衔。军士长军衔只授予基层极少数的精英士兵。个人实力相当于进击的巨人里一个旅（约120人）的实力，拥有例如投掷抛弃刀片损伤巨人视力后斩杀，用立体机动装置使用螺旋斩等优秀技术，速度极快，战斗往往在一瞬间结束。格斗术一项为目前人类角色最高，超过表格上限达到11（女巨人格斗术亦为11，但因为其不是人类形态，故不作比较），位列第一。第二名为三笠、米克、阿尼三人并列，均达到格斗术满值10。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10406', '0', '为什么《进击的巨人》里，男神不是艾伦是兵长利威尔？', '《进击的巨人》第二季四月即将播出，是时候来讨论一下我们的男神兵长了！巨人的男主虽然是艾伦，但只有兵长才是男神！那么这是为什么呢？迷妹们后退，我要开始花痴了！兵长的人设是人类最强，但官方给了他萌萌哒身高，这两者之间的反差萌，萌了一脸血！尤其是兵长一瞬间便可以打倒两个巨人！他出场就是圈粉的开始！调查兵团主力走来，一脸嫌弃的兵长。然后立马切换到和巨人的战斗一脸嫌弃却又干净利落地杀着巨人，还不耐烦的嘲讽，抖s拽炸天！手上溅了巨人的血，很不爽的吐槽：脏死了。男神特质！在抖s又洁癖的同时，又人情味十足：当部下临死前问他自己有没有为人类做出贡献并向他伸出满是鲜血的手时，他毫不犹豫的紧紧握上那只手，激动地告诉他：你做了很多了，今后我会带着你的意志继续战斗地球最强人类的承诺，驱逐巨人！强大又充满人情味，兵长在这些优点之外还有一点：尊重别人的决定艾伦在马背上被女巨人，兵长没有卖弄经验，或以大压人，而是对生命和选择给予一个新人权利，并接受它所带来的结果！！最后，也是非常重要的一点！兵长的声优是神谷浩史！神谷以其高傲（ao）冷漠（jiao）的声线为兵长的角色加分不少，以其独特的声音（非常卖萌卖腐）赢得了很多cp粉丝！！！多少人一脚踏进了利艾爬不出来！嘛，不管大家怎么站利艾，我还是站这对的一个酷爱奇行种一个自称奇行种！这才是绝配！本文由盖饭GetFun原创内容团队提供转载请注明来源：盖饭GetFun', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10407', '0', '进击的巨人 可获取情报 一米六兵长利威尔', '其全名为利威尔。阿克曼，兵长其实来自于有名的家族后裔，阿克曼家族。第一次详细的介绍兵长的作品是进击的巨人番外篇（无悔的选择）。主要讲诉了兵长早年曾经是王都地下街力很有名的混混，之后由于各种原因被艾尔文带入调查兵团。亲眼目睹了法兰和伊莎贝尔被巨人杀死后，在暴怒中疯狂斩杀奇行种。（小编的相关的视频中有剪辑这部番外篇）经历过这次事件以后，兵长的眼神也早已麻木。后兵长成为兵团特别作战班班长，身材矮小，身高仅为一米六，却有着：最强人类战士的称号。利威尔班在掩护艾伦的战斗中，相继被女巨人虐杀。利威尔随后赶到，看到队友都被杀害后，兵长开启了暴走模式，用陀螺旋转的方式去消耗女巨人的再生能力。目的就是只有一个，救出艾伦。(小编也有该部分视频混剪，热血哦！）兵长有两个爱好，一是喝红茶，在清净悠闲的时候就喜欢一个人发着呆端起玻璃杯喝着红茶。而是打扫卫生，兵长有着重度洁癖，十分讨厌灰尘以及污渍。如果他能找到清洁工具的话也许他会笑出来的。这个貌似有点恐怖。哈哈，发点福利图片。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10408', '0', '《进击的巨人》大结局！利威尔兵长：吃三笠的醋一种怎样的体验？', '《进击的巨人》这周大结局，就要看到三笠对艾伦的“告白”了，这应该是艾笠党最想看到的瞬间。对于喜欢利威尔和艾伦的粉丝们，这季巨人似乎把利艾党“忘得”干净。除了《进击的巨人2》第3集里面的一些互动，两人便无瓜葛。虽有遗憾，但是原著的走向我们是要尊重的。对于卡熳来说，利威尔和艾伦的关系和情感发展是我《进击的巨人》这部作品中最关注的焦点之一。所以便有了之前的【利艾】分析文，主要分析了《巨人2》03集马车上利威尔对艾伦展现的关系。其实第3集中还有一处互动表现了“利艾”不为人知的私情。今天卡熳想把这个片段，分成两篇小短文、并结合《进击的巨人》第一季来分析。以下正文《进击的巨人》利威尔兵长：吃三笠的醋一种怎样的体验？文(≖‿≖)✧卡熳利威尔兵长曾对即将去往罗塞城墙的艾伦一行人说了一段话。在广大腐女看来，这段话有料可挖！此片段发生在《巨人2》的03集结尾处。利威尔兵长这段话是分别对阿尔敏、三笠和艾伦说的。对阿尔敏的话参考意义不大，我们重点要分析的是对三笠和小天使说的话。本节就重点来看利威尔跟三笠的短暂交流。利威尔兵长说了什么？首先我们看看兵长的原话：“三笠，我不知道你为什么如此执着于艾伦，但你要用你所有的能力去保护好艾伦。”为什么利威尔兵长要说这些话？动机分析在卡熳看来，他的话分为两个动机：以任务目标为导向的客观动机，以及个人私欲的主观动机。◇客观动机这次调查兵团的目的是为了到罗塞城墙，找到洞口，用艾伦的巨人化后硬化能力堵住洞口。所以利威尔肯定是以任务为重，而要完成这个任务的首要因素就是确保艾伦的安全。◇主观动机当然，出于对艾伦的喜爱和关心，利威尔不希望艾伦在这次任务中死掉，如果可以，他也不想看到艾伦受重伤的样子。其实，他可以跟整个小队说：你们需要保护艾伦。然而，利威尔兵长却是对着三笠说出这段话。因为他清楚三笠的实力，只有她能够护艾伦周全。字面分析两个关键短语：如此执着&所有的能力这两个短语从利威尔兵长口中说出来，多少有点出乎卡熳意料，因为这两个短语明显表露着个人的情绪。我们来简单看一下利威尔这个人的性格：外表冷酷内心细腻，情感丰富单不善于表达。如果你同意卡熳对利威尔的描述，就应该明白兵长说不出这样的话。但是他还是脱口而出，为什么？这要提到《进击的巨人》第一季他和三笠从女巨人手中拯救艾伦的片段。利威尔兵长亲眼看见三笠曾拼尽全力想去救艾伦。他一下便洞悉了三笠对艾伦的情意。他明白那是对自己所爱之人的奋不顾身。利威尔已经是30多岁的人，他一看便知道。卡熳为大家准备了精美的“利艾”图片，请私信“巨人”，得到官方图和同人授权图~私信“巨人”，得到第一季、第二季（更新至11集）、及OAD资源～而他对三笠所说的：“不知道你为什么如此执着于艾伦”，只是在说不晓得三笠喜欢艾伦的原因是什么，也就是那次“艾伦从人贩子中救出三笠”的故事。很显然，利威尔的话语中带有酸涩的味道——他对三笠和艾伦的亲密关系有微浓的醋意，尽管他已经在克制了。至于“所有的能力”跟之前说的主观动机差不多，他不希望艾伦死掉或者受重伤的意愿，但是自己受伤，无法亲自出马，所以必须找一个靠得住的人保护艾伦。小结一下卡熳觉得绝大部分“利艾党”都能这里利威尔有些迥异的表现。通过对三笠的一小句话，我们可以看出两点：一、利威尔已经意识到三笠与艾伦的关系不一般，并对他们不为人知的过去吃醋；二、利威尔知道自己无法参与自己任务，为了护艾伦周全，他选择战斗力仅次于自己的三笠紧盯着艾伦。卡熳最后要说我感谢自己是一个腐女。首先，喜欢动漫的人在动漫界被贴上了很多标签，腐女是其中一个。然而，它既不是褒义词也不是贬义词，它存在的意义只是为了让有相同喜好的人，能够更快的找到自己的归属。其次，“腐”其实赋予了我更多天马行空的想象力。我时常想象着，倘若我来写这对CP的故事，我会怎样描述他们的长相和心理。而我也做到了，所以卡熳一直在同人文的道路上奋斗了多年。我们要感谢自己的想象力，没有想象力，人类不可能有今天绚烂多彩的动漫产业。不论你是不是腐女or腐男，我都感谢你阅读今天的文章，也感谢你用一种开放的眼光来看待和喜欢卡熳。Attention&Preview最后送上兵长的短漫一则，笑尿了(๑•̀ㅁ•́ฅ)记得私信卡熳奥。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10409', '0', '动漫：“进击的巨人”利威尔兵长 美图壁纸', '\"你应该很讨厌白白送死吧？\" \"是啊，很讨厌。 不管是自己去，还是让别人去。\"这是我一贯的主张，我认为最有效的管教就是疼痛。我们全部的人，一出生开始就是自由的，不管阻挡此点的人有多强，不管是火焰之水也好，冰之大地也好，什么都好！看见那些东西的人，无疑是这世上最自由的人啊!那梦境是我心灵的居所，但它却比生命更脆弱。我无数次丢失后，又重新找回……鸟儿是为了翱翔于天空才破壳而出， 你的翅膀又是为何而生， 鸟笼中的天空太过狭小了吧没错...这个世界...原本就是残酷的能活着回来，才能算是独当一面吧。 你遗留下的这份意志，将会赋予我力量。你不是个坚强的人，所以你能理解弱者的心情，做出正确的判断！啊，这个世界，是残酷的啊，那个只允许胜利者生存的残酷的世界。我一直以来都弄不明白，为什么不管做了多么明智合理的选择，在结果出来之前，谁都无法知道它的对错。到头来我们被允许做的，只是坚信那个选择，尽量不留下后悔而已。如果你认为人为人死是理所当然的话，那你一定能够体谅，有时，牺牲一个人的宝贵性命，能够拯救众多生命这种事吧!这个世界是如此的残酷，却又如此的美丽。我只不过是想要待在他身边而已下期再见，期待下次跟新。。。。。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10410', '0', '爱上一个利威尔兵长，我要怎么睡到他？', '丁丁想说死宅比迷妹更值得同情毕迷妹还有机会睡到男神女神可是死宅的男神可是隔着一个次元啊！打破次元壁，我们来想想怎么操作！——————————丁丁爱兵长，就以兵长为例好了第一种办法，手办抱枕周边兵长在我桌角床上电脑旁每天酱紫抚摸他⁄(⁄ ⁄•⁄ω⁄•⁄ ⁄)⁄第二种，同人加脑补好了毕竟次元壁要打破只有脑补了兵长在看我兵长在我床上他叫我起来了（要防止自己被当成蛇精病，2333）第三种，买个等身机器人不太可能那就用VR吧怎么样？最后要是还不满足那就只有找替代品了怎么样？不少神级coser真的cos的兵长超级像啊！看的宝宝心花怒放！这就来！biubiu~阿宅们get到了吗？————————————转载请注明来源：盖饭GetFun本文由盖饭GetFun原创内容团队提供', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10411', '0', '进击的巨人：兵长利威尔威武+进击四大团徽', '进击的巨人：训练兵团（讨伐之剑）进击的巨人：宪兵团（执法独角）进击的巨人：调查兵团（自由之翼）进击的巨人：驻扎兵团（镇守蔷薇）意犹未尽？还想收藏更多精品福利美图？那么加入最爱二次元精品图会员Q群：480748934 吧！这里只发精品，拒绝糟粕，每天更新福利哦！谢谢支持最爱二次元，喜欢就订阅哦，最爱二次元精品图欢迎会员群你的加入！！！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10412', '0', '兵长利威尔死鱼眼动图特辑', '兵长死鱼眼特辑，喜欢这种犀利的眼神，各种帅一脸啊！！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10413', '0', '无奈的强者~利威尔兵长—进击的巨人最为强大的存在！', '利威尔兵长最为《进击的巨人》中最强大的角色，一米六他有太多让他悲愤到无可奈何的往事了，今天我们就来说一说这个短小的强者的无奈！在很小的时候，他生活在墙内的贫民区（也就是地下）他儿时还因为一个意外给他留下了一辈子心理阴影导致他喝茶的时候绝不会喷杯柄，只会握住杯身喝茶。他的母亲也很早就去世了，好在他还有个愿意来帮助他的舅舅，他的舅舅教给了如何在这弱肉强食的不平等世界生存下来，随后就离开了他。在这之后利威尔就经常干一些劫富济贫的“坏事”成为了地下有名的“混混”，还认识到了两个“混混”朋友：法兰和伊莎贝尔。不过悲剧的是他们三个在加入调查兵团后的第一次壁外调查中被巨人杀害，这也成为了兵长在第一次呼吸到了墙外空气后就意识到了巨人的恐惧和对他们的无奈，最后悲愤交加的兵长叫杀害他们的巨人砍成了肉渣，可是即使如此，信赖的朋友还是一去不复返了。后来他选择了信赖埃尔文团长的，还亲自挑选成员组成了利威尔班，该班成员有：利威尔 奥路欧·博札特 埃尔德·金 根塔·舒尔茨 佩特拉·拉尔 艾伦·耶格尔（艾伦是最后加入的）可是就是这也是兵长无奈生涯中的一环，在第57次壁外调查中不幸遭遇二度变身的女巨人除兵长艾伦外全员牺牲，看着战友们的尸体，兵长还是那样的面无表情，因为他的内心已经成为了一名强者，一个强者是不会将自己的软弱一面给暴露出去，其实他的内心又是怎样的无奈呢。但再次见到教会自己如何生存的舅舅是在内战中，那是自己活着的唯一亲人：凯尼·阿克曼。他发现自己的舅舅竟然是自己的敌人，时隔多年的再次重逢竟然是彼此都拿着枪对着对方，兵长此时此刻的内心是什么样的呢，只有他自己最清楚，那种无奈是如此的熟悉，如此的令他厌恶。在内战结束后，他最后的亲人自己的舅舅也死在了自己的面前。内战结束了，赫利斯塔哦不，那时候她已经恢复原来的名字，所以应该是希斯特利亚成了墙内世界的女王，不知道为了什么，他胆怯却又勇敢的一记软绵绵的拳头的却让但时正好一个人独自悲伤的兵长有振作起来了。（不过那个场景要是动画了绝对超有爱）可是就是这之后，玛利亚之墙夺回站开战，在这次战争中，调查兵团几乎全员牺牲，在面对两个濒死亡的战友时，他又一次面对一个抉择，一个是自己信赖的埃尔文团长，一个是团队中的智囊阿明。但是兵长有意让团长复活，可是艾伦不同意了，然后还差点和艾伦打了起来，然后又被后来赶到的三笠用刀架在了脖子上威胁！然后赶来的韩吉分队长和马布里特要复活团长，就在众人僵持不下的情况下，兵长毅然决绝的准备复活团长，绝望的艾伦和三笠无奈的离开了，可是最后因为团长最后的一句话，兵长无奈的选择了复活阿明！这并不是兵长的本意，这是兵长为了信赖的团长而做出的无奈的选择！兵长的一生都太无奈了，尽管他是最强的存在，但是这个强者确实那么的无奈，那么的无助！一个追求自由的人却那么的不自由！我不明白，一直这样，到底是相信自己的力量，还是选择信赖的朋友的抉择。结果谁都..... 分かってない、ずっとこのままで 自分の力を信じる、信頼を选んだ友の选択だとは分からない。結果は誰も……。（一个朋友的兵长画像，个人感觉非常可以，尤其是眼神！）', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10414', '0', '《进击的巨人》兵长利威尔的道路', '兵长很强大但是不完美虽然个子不高，但是非常强大。过去曾经做过小混混，不过却有着洁癖。！而他的脸看起来有30多岁的样子，感觉身上很多方面都产生了反差萌。他的身上感觉不到背负着什么特别沉重的东西，但是这个角色无论悲伤还是痛苦都压抑在心头，他那战斗的姿态让人情不自禁想要追随其后。他任何时候都在变强，而这样的角色也让人忍不住想要紧紧抱住。而且我也喜欢他那严格当中透露的温柔。他对于任何感情都十分珍重；兵长带有天生克队友的属性，在地下街时他身边的伊莎贝拉和法兰领了便当，调查兵团时身边的利威尔班领了便当，逃脱王都抓捕时所带的韩吉班也是基本全军覆没，在最近连团长也牺牲了。从小在地下街经历了母亲的死亡和舅舅的离开，然后在那样弱肉强食的环境里长大，最后加入了死亡率极高的调查兵团，我相信应该没有人会比兵长更懂生离死别的含义。而到从漫画前几话时团长身后站着的那些牺牲士兵对于残酷的世界这一主旨可见一斑。这些人里有多少曾经对他展现过笑颜，有多少人曾在油灯下共事，有多少人曾用仰慕的眼神看他，这些过往通通化为泡影，他还是会把下一个这样的人当作珍贵的伙伴，即使可能获得的美好还是一个泡泡，迟早会碎裂在面前。如果这个世界早以平和待我，我必报以微笑，而如果这个世界以残忍待我，我还是选择报以深情。这才是兵长难得可贵的的地方。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10415', '0', '这三点，足矣让你爱上兵长利威尔！', '《进击的巨人》第二季前几天刚刚更新完毕，粉丝是真心不舍，所以听到第三季的消息大家都在微博刷屏分享。这部动漫能这么火，我不认为目前主角艾伦的帮助有多大，因为到现在为止的剧情，艾伦的能力都还没有凸显。但是，有一个角色，绝对是巨人这么火的原因之一，他就是士兵长——利威尔。我记得当时第一季火起来的时候，大部分人就在刷兵长那“傲人”的身姿，那么多人对兵长的膜拜之情无以言表，这让我对这个角色产生了极大的好奇。所以，我看这部动漫的契机就是兵长，当然不知道你们是不是这样。第一季兵长出现的地方其实不是很多，比较集中在后半段，说实话，前面几集真的是看的我想吐，要不是因为想看看兵长，我都觉得我支撑不下来，最后，终于见到了兵长，我也彻底沦为他的迷妹了~每个人爱上兵长的方式不同，我这只是其中一种，但是兵长吸引人的特质，总结归纳为三点。第一点，帅，足矣。历史上死鱼眼还能那么帅那么有气场的不多，利威尔绝对排前头！我就不过多描述了，怕写太多！第二点，强，最强。虽然我们总是调侃兵长一米六，但是我们更是清楚地知道，这个小矮子身上到底能够爆发出多少能量，到底有多强。巨人最强是谁，我们不知道，但是人类最强就是利威尔！第三点，心，很软。其实兵长那冷酷的外表下，一直藏着一颗柔软的心。他从不落泪，但却能够让我们感受到他内心巨大的悲伤。他的关心你可能当时感受不到，但之后仔细想想，会觉得心里暖暖的。就是这么一个人，把我们的心绪牵的牢牢的，他强大时，我们跟着一起呐喊，他难过时，我们跟着一起悲伤。我想，这就是一部动漫成功的地方吧！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10416', '0', '奥达斯 VS 科金博', '【奥达斯 VS 科金博】开赛时间：06-18 7:00 星期一 ——基本形势在首回合交战当中，奥达斯凭借硬实力的绝对优势以5-0狂屠对手，已经基本上锁定了晋级的胜势，此役回到主场无疑可以更加从容地应付对手，值得一提的是，由于奥达斯在秋季联赛当中的表现实在太过糟糕——15轮比赛只赢了1场最终只排在倒数第二，相比起春季联赛第五的排名几乎是呈现出断崖式下滑，因此在联赛发挥失常的背景下，他们自然希望在接下来的杯赛赛场上重新证明自己，恰逢他们本轮的对手科金博仅仅是一支来自智利乙赛场的球队，反弹之心自然更为强烈。——盘口解读盘口方面，目前机构普遍开出了奥达斯主让半一盘，鉴于两队存在联赛级别的差距，且首回合如此悬殊的分差，在实力绝对占优的情况下如今开出如此深盘也是情理之中，而且在后期的调整中，上盘有继续降水升盘的趋势，有意进一步防范主胜，结合欧赔一致的走势以及偏低的胜赔，此役主场作战的奥达斯双杀对手晋级的希望很大。（亚盘：奥达斯 -0.75/ 竞彩：平胜）', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10417', '0', '“江南翰墨女王”——梅丽琼', '梅丽琼，师从著名书法家沈鹏、邹德忠。现居北京。现为中国书法家协会会员、中国书画界形象大使、CCTV《感恩中国》栏目年度人物评选颁奖组委会书画总监、CCTV《全民健身 健康中国》栏目春晚组委会书画总监、一带一路新媒体书画院院长、中国企业书画艺术协会书画总监、囯际文化产业联合会公益大使、北京星光影视园名家书画作品联展组委会艺术总监、书画中国公益行组委会执行主任、北京新华融媒书画院总经理兼书画院院长、中华艺术家协会副主席、中华文化传播大使、中美文化传播大使、亚洲最具影响力风云人物书法家唯一代表、中华文化艺术名家联合会副主席、中国书协《艺术资本》理事会副主席、中国双拥联合会副主席、中国拥军人网副总监、中国书画名家副总编、中国国画艺术书画院副院长、中国孔子诗书画艺术画院副院长、《中国新农村月刊》、《当代名家名作国礼典藏》编委会执行主编、文化部主任等职。梅丽琼女士书法造诣之深、功底之厚、靡声中外，素有“江南翰墨女王”、“京都书豪”的美誉。当年全国记者高峰论坛“传递正能量，提升竞争力”大型会议的题字人，曾为世界8大金奖建筑、中国唯一入选建筑“桥上书屋”题写门楼；为中国国礼榜首98投洽一号“白叶塘茶业”、“白芽奇兰”题写牌匾和国际商标；为孔子最高奖《孔子奖章》题写牌匾；为山东青州唯一的国营美术馆 “新华美术馆”题写牌匾；为山东济宁兴隆文化园题写牌匾；为山东滋阳山山门题写牌匾；为湖北石门山景区、河南大别山景区题写匾额；为福建省8大景点中第一最聚人气景点观川至题写牌匾；为闽南第一高峰题写大石头“闽南第一高峰”；为全国文学大奖赛题写主题、为钓鱼台国宾馆、人民大会堂、全国政协礼堂活动题写主题；为福建漳州大型古建筑威惠庙大唐儒教开漳圣王等20多家题写庙门；汉碑隶书《登高望远》被北京4A级旅游景区桃源仙谷锲刻在主峰……梅丽琼女士作品被人民大会堂、钓鱼台国宾馆、故宫博物院等收藏，被海峡两岸以及欧美国家领导和业界收藏，曾荣获中央电视台2016中国骄傲影响力德艺双馨书画家荣誉、21世纪德艺双馨时代精英书画家光荣称号等上百项奖励或荣誉。梅丽琼挥毫泼墨的景象，成了一道靓丽的风景线，浑然天成质朴、灵气活现的画面，飘逸清爽而洗练的风格，仿佛文化盛宴。厚重处，泼墨积水，有种挥洒宣泄的美感，淡雅处，枯笔飞白，线条奇崛，苍古豪放。她书法的气韵，有古汉碑隶书的十大灵性，萃取精华！把大自然的生气、雄气、霸气、灵气、才气，集中在她身上，体现的淋漓尽致！其作品有《华山碑》的纤细，《史晨碑》的古厚，《乙瑛碑》的隽秀，饱含作者心灵和情感的反映和寄托。字里行间，能品味到她习古效今不辍，倾心习练的她，自小将墨入骨髓，融入生命的灵气，透射出蕴藏历史性的大家潜能……【作品展示】', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10418', '0', '个个都是高手！动漫中被通缉过的角色！', '1.七大罪梅丽奥达斯七大罪成员都上了通缉令，关键是他们通缉令上的样子和实际的样子完全不一样，不是一个画风的，实在是不知道差距杂这么大，弄通缉令的人都不走心。2.海贼王路飞这个世界里知名海贼都会上通缉令，路飞他们一伙的通缉令个个拍的很有趣，一点都没有通缉的意味，娜美的写真最具个性，布鲁克是明星海报，路飞更是开心的逗比，估计也就他们的通缉令是这样随性了吧。3.鲁邦三世鲁邦三世作为怪盗被通缉也是很正常了，反正怎么样你也捉不到我，顺带还帮我扩大了知名度，何乐而不为呢，再说鲁邦三世的变装技术一流，通缉令对他来说根本不算什么。4.枪神Trigun法修这位被称作人间台风的通缉犯被悬赏600亿，是传说中的厉害人物，可见其武力值有多高，和他一起旅行也是需要一定胆量的。5.绝对可怜小孩兵部京介这位萝莉控因杀了很多人而被通缉，被捕入狱，但是期间他是来去自如的，这位危险人物又成了复合能力者，可以说很无敌了。6.心理测量者狡啮慎也曾经为监视官，对槙岛圣护异常执着，一直追查他导致他的犯罪系数也上升了，且逃往海外被通缉追捕中。7.黑礁莱薇在贫民区里待过的她热衷暴力和枪械，结果就遭到FBI和NYPD27局的通缉，大大咧咧的个性是个十足的武力女神，加入到黑礁商会很适合了。8.银魂桂小太郎桂是通缉令上的常客了，但他总是神出鬼没，各种变装，每次都与幕府擦肩而过，明明身边有这么显眼的伊丽莎白，幕府抓不到他也算很神奇。9.伽利略少女星月一无所知的三姐妹突然遭到谜之组织的袭击，之后更是变成了国际通缉犯，没有什么比这个更刺激的事了，三姐妹变得两边都不好过。10.名侦探柯南基德作为一名大名鼎鼎的怪盗被通缉也很正常，关键是永远都抓不到他，这就是警察小偷是宿敌这个套路，被抓到了也就完了。动漫里上过通缉令的人都是有魅力的角色。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10419', '0', '圣保罗奥达斯排雷在望', '回顾昨晚在头条分享的英甲牛津联5球大炒赢盘，微信公众号“易胜博盘365”加场免费的罗奇代尔大球、锡菲联让半一双双收米，继5中4后一波3中3，最重要的是全程免费，你准备好上车了没有？08:45 巴圣锦标 圣保罗奥达斯 vs 圣安德雷第12场的对决，圣保罗奥达斯目前11轮过后在主场的成绩是2胜0负3负，得8球失6球胜率40%。客场则0胜3平3负胜率为0，可见这支球队完全是靠主场横行，虽然作客不济但近两轮圣保罗奥达斯的表现可圈可点，2:2平路禾利桑天奴，2:2落后之余再逼平强大的彭美拉斯，在火力上有一定的保证。圣安德雷的路线和别家截然不同，别的球队着重主场抢分它反而放弃主场修为，全力以赴作客表现。5个客场2胜2平1负得8球失8球，胜率同样40%，今晚对抗圣保罗奥达斯有着一定的筹码。近两轮圣安德雷都是主场出战，3:3平了前例的邦迪比达表现不错，0:1输山度士也是情有可原。亚盘是圣保罗奥达斯让半一，从盘口初开中水到目前的低水赔率，圣保罗奥达斯一直未能再升一盘。鉴于两队近况来看，圣保罗奥达斯占主场之利和两连平赢到了游戏指数，如果初开半球则太浅，毕竟圣安德雷的客场也不是吃素的，那半一刚刚好。赔率一直打压不肯再升盘我想有以下原因，第一比赛时间是明天早上的8:45分，距离开赛时间尚早。第二长时间保留半一低水，吸引注码形成大热之势，再升一球高水回暖一下圣安德雷，以此来反诱下盘客。推:圣保罗奥达斯 一球半一赔率太低没吸引力请结合临场数据理性参考，欢迎添加微信公众号“易胜博盘365”，稍后将分享其它在公众号里面，祝红~', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10420', '0', '海贼：再见了梅丽，感谢你一路陪伴', '《海贼王》热血中充满着感动，总有一些情节让人想哭。黄金梅丽号的沉睡让人泪目，感谢梅丽一路陪伴，愿梅丽沉睡好梦。黄金梅丽号是草帽大船团最初的船，是乌索普的好朋友可雅送给他们的。路飞他们乘着这艘船出海航行，一路上加入好几个新的伙伴。这艘船陪着他们从颠倒山驶入伟大航路，经过许多岛屿，也经历了许多磨难。上山下海，从一万八千米的高空坠落，梅丽早已不堪重负。梅丽的船头断过，桅杆也断过，船身破了又补，补了又破。本来船上就没有船工，一直都是乌索普在修船。乔巴为了船战斗过，乌索普对船的感情更是深得不得了。梅丽早就是他们的伙伴。路飞一行人来到了水之七岛，带了3亿贝利要好好修船的。可是钱被弗兰奇一家偷走，乌索普为了抢回深受重伤。与此同时船工鉴定梅丽的龙骨损毁太严重，已经修不好了。乌索普为自己的无能和路飞要放弃梅丽而下船，他是第一个下船的人。（后来又回来了）乌索普误会了路飞，路飞这样做也是为了大家着想。每个人对梅丽都有感情，或许不如乌索普深，但是谁都舍不得梅丽。乌索普曾经见过有人在深夜拿着锤子修船，那个人正是梅丽所化的船精灵。只有特别受人珍惜的船才会化身为船精灵。在与CP9对战时，梅丽被CP9推入海中。司空岛一战，路飞一行逃跑无门。关键时刻，大家听到了有人在呼唤他们，于是他们跳入了海中。原来是梅丽来接他们了，梅丽自己航行到了这里。草帽船团保住了性命，回到了水之岛。原来被卷入浪潮破败不堪的梅丽还想着救他们，发出了声音，被冰山先生听见，帮忙修了这条船。梅丽才得以驶入大海，去救路飞他们。这也是梅丽陪伴路飞他们的最后一程了，这一次真的再也修不好了。路飞和梅丽互诉心声，最后大家同梅丽告别，在海上火葬了她。这一幕反正我是看哭了，船亦有灵，承载着难以诉说之情。草帽船团都很伤心，虽然梅丽不在了，但是他们不会忘了这个陪他们冒险的伙伴。他们会带着梅丽的记忆一起前行的。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10421', '0', '南球杯：博塔弗戈 vs 奥达斯', '南球杯：博塔弗戈 vs 奥达斯盘口：0.83 一球/球半 1.00欧指：1.40 4.60 6.69盘口解读：博塔弗戈上轮联赛中客场不敌克鲁塞罗吞下联赛首败，此役球队将在主场迎战奥达斯，在首回合与对手交锋中球队客场2比1拿下对手，此役回到主场优势更大。奥达斯在智利甲联赛中表现低迷，球队近四战只取得2平2负的不胜成绩，而球队离上次获胜已经长达2个月，本战客场作战难有翻盘的可能。亚盘直接开出一球/球半盘，回看首回合交锋，博塔弗戈在客场便开出受平/半球盘的力度，此役回到主场以一球/球半盘开出体现出两队实力差距，本战上季将是受热方，不过主队上轮联赛落败能为上盘热度有所减缓，而现时上盘中水水位对上盘信心较足，本战可看好博塔弗戈再度击败对手。竞彩胜平负：让胜 让平亚盘推荐：博塔弗戈', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10422', '0', '周四竞足2串1：巴拉纳竞技地利人和 奥达斯战意十足', '今日竞足2串1方案，南俱杯投注分析推荐，常跟常有。感谢打赏，欢迎订阅！今日竞足006 巴拉纳竞技VS纽维尔老男孩比赛分析：上赛季巴甲联赛，巴拉纳竞技凭借14胜9平15负的战绩在联赛中积51分，位列积分榜第11位，球队在联赛中算是中游球队，整体实力平平。而纽维尔老男孩近几年的状态也是下滑明显，球队已有多年无缘本赛事，上次参加南俱杯实在2010年，加上本赛季国内联赛表现欠佳，他们只能寄希望于杯赛中走的更远。竞彩总结：巴拉纳竞技的主场战绩平平，上赛季巴甲联赛取得了8胜5平6负的战绩，胜率和不败率偏低。好在球队近期表现不错，近五个主场未尝败绩，不难看出主场具备不错的抗衡力。而纽维尔老男孩虽然客场成绩尚可，但近期走势下滑严重，球队至今连续7个客场惨遭失利，状态持续低迷。综合来看，纽维尔再输一场是在所难免。推荐方案：胜今日竞足007 奥达斯VS博塔弗戈比赛分析：奥达斯新赛季以来连遭失利，最近4场比赛难求一胜，攻无力而守不稳，状态难言理想。好在队内前锋卡拉斯科期间打入两球并贡献了一个助攻，个人状态火热，可以说是球队最大的收获。而对于博塔弗戈来说，本场比赛结束即展开新赛季征程，好在球队有足够时间恢复状态，不需要为本场留力，此役的战意毋庸置疑。竞彩总结：好在奥达斯的主场表现略胜客场，本赛季基本每场均有进球，在场上的攻击力有所保证，值得信赖。同时球队也是时隔11年的回归，球队自然希望能够取得较好的成绩，本轮战意十足。而博塔弗戈的客战表现又十分平平，球队自三月份以来的6个客场取得3胜1平2负的成绩，状态较为飘忽，本场并没有十足把握赢球。综合来看，奥达斯至少争夺下一分问题不大。推荐方案：让胜版权申明：本文仅代表作者观点，不代表奖多多官方立场，未经许可，不得转载。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10423', '0', '智利甲：奥达斯 VS 西班牙联合', '智利甲：奥达斯 VS 西班牙联合比赛时间：2018-05-28 星期一 5：30赛事分析：奥达斯上一场作客以1-1与伊库伊奎握手言和，在智利甲已完场14轮赛事后，仅排在了积分榜的第15位。客队方面，西班牙联合上一场以3-2击退了科洛科洛，最近10场取得了5胜1平4负的成绩，状态有所好转。根据球探网的统计数据显示，双方的对上一次交锋，奥达斯以0-2输给了西班牙联合。主流亚盘公司这场开出了平手盘，反映了机构不太看好两队有必胜的把握。结合两队的近况战绩，奥达斯最近10场全部不胜，状态不堪入目，而西班牙联合最近试过获取一搏4胜1平的记录，明显技胜一筹。同时，历史交锋西班牙联合亦占据上风。此番理应看好西班牙联合作客带走分数。亚盘推荐：西班牙联合', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10424', '0', '智利甲：伊库伊奎 vs 奥达斯', '智利甲：伊库伊奎 vs 奥达斯 数据剖析：伊库伊奎上轮收获平局，球队取得3连平成绩表现中规中矩，目前球队排名第10位，值得一说的是球队本季主场7场赛事当中仅收获一场胜绩，客场反而取得有不俗的表现，可见球队主场并没太大的优势，本战迎来奥达斯 的挑战，近年球队面对对手取得2胜2平的不败成绩，优势明显。奥达斯上轮再吞败仗，球队已经十场赛事不胜，士气非常低落，而实际上球队13轮过后只取得过一场胜仗，客场仅得一分，本战面对伊库伊奎并没有太大的优势。亚盘直击：亚盘开出半球盘力度，态度统一。回看两队交锋历史成绩，最近两次主队只开出平手盘力度，而本战能开出半球盘则已经给予上盘提升。虽然主队近期保持 不败成绩，但主场也仅收获过一场胜仗，主场胜率并不是十分之高，所以本战上盘方面还是存在一定的拉力。另外在赢盘能力来看，主队也甚少赢盘，在这样的大数据下，本战伊库伊奎主场不妨高看一线。竞彩让球胜平负：胜亚盘选择：伊库伊奎', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10425', '0', '赖梅芳:梅丽人生  芳香事业', '编者语：女人如水，婉约缠绵，似水润荷花，如风拂杨柳，万端风情，在迷你酒窝中荡漾，她有这样的女人天生的潜质；衣着得体，短发干练，皮肤白皙光泽，不化妆素颜美丽，化淡妆锦上添花，不怒自威魅力永存，调侃嬉戏虽随而端，她有这样的内涵；不安于现状，不满足于当前，相机而动，择时爆发，纵有关山，一往无前，她有着这样的气魄；聚沙成塔，积水成渊，在厚积薄发中进取，在携手共进中开拓，她志存高远……她用自己的行动书写着“女子哪一点不如男”人生诗篇，她就是让赣州人民为之额手相赞的米伽素美容有限公司总裁赖梅芳。——徐知三 冯欣华丽转身哲人说“人因梦想而伟大”，从赖梅芳身上体现出这一点，但应附加上“人因梦想而美丽”。赣州的山水培育了这位高山俊鸟，开始的时候她与平常的赣州人没什么两样，勤于学习，1995年-2001年之间，安心执教，三尺讲台放飞孩子们的梦想，几只粉笔勾画着人生蓝图，她就如她的名字一样，芳香悠远人未识，幽谷绽放静静开。平淡的生活没有跳跃的旋律，也许是源于心中的那份追求，也许是“我想出去走一走”的想法，也许是回家务工子弟的外面世界的笑谈，种种因素的叠加，看似温文尔雅的她在2001年竟做出了一个惊人的决定：辞职下海。这一举动引起不小的震动。因为当时教师是手捧“铁饭碗”的职业，尤其是在社会经济水平不高、资源相对匮乏、信息相对蔽塞的大山深处，舍弃稳定、有假期、舒服的职业是多么的可惜啊。“不拼搏不是人生”的执着之心，让认理不认命的赖梅芳辞职去上海、深圳打工。文化的内涵，漂亮的外表，让她不由自主选择了大健康美容行业。她之前并不懂得护肤，而且不相信一个人通过改变皮肤就能够变得年轻。但是一个偶然的机会，让赖梅芳接触到了玫琳凯这个品牌，通过一段时间的使用，她发现护肤品的确能够改变自己的肤质，自己的人生发生了很多改变，并且身边的朋友们都在赞美自己。因此，有一个想法开始在她心中慢慢发了芽——如果她把身边的人的皮肤都变的越来越好的话，是不是能让他们也感受一下来自美丽的快乐呢？说干就干，2008年赖梅芳的创业就此拉开了序幕。在2009年开办了她人生中第一家美容公司——年轻几岁美容店。从此，揭开了她在美容事业上的新征程。经过将近两年的经营，赖梅芳的美容事业渐有起色。2011年4月，年轻几岁美容店便更名为“素颜美”。之后，美容事业如日中天地发展，从2012年开始，第二家连锁店、第三家连锁店等相继开业，直到2014年，连锁店已经发展为19家。2015年，随着事业的进一步扩大，素颜美联合广东卫视、北京中搜网络科技有限公司，成功转型。同年，注册了赣州米伽素美容有限公司，并申请“米伽素”商标，将连锁店扩展至35家。店面的不断扩大，无不透露着赖梅芳的经营有方和背后巨大的付出。创新中医药基于对美容行业的热爱与对顾客负责的态度，她常常思考怎么才能让化妆品安全、简单、实惠、有效。一次偶然的机会，因吃中药治好了自己及家人的疾病时，她一下子豁然开朗：中药老祖宗传下来的国粹，慈禧太后没有现代化妆产品，不是一样的护肤养颜吗？为什么不把中药移植到现代护肤品当中呢？在这种理念的支撑下，赖梅芳带领团队，科技攻关，萃取灵芝、天麻、人参等中药精华，发现了中药成分的调肤、养肤等美容功能。于是投入大量精力，研究并生产新型美容产品，现代科技与传统中医珠联璧合，米伽素系列品牌很快研制成功，传统中医药在现代科技作用下展现出她独有的神效。做美丽事业，传承与发扬中医药文化，实现古人所谓的“天人合一”，赖梅芳做到了。2016年8月，赣州米伽素有限公司携手湖南卫视国际频道成功举办了南康片区的“青少年联欢晚会”，并成为全球76个国家的上榜品牌。同年9月，米伽素又获得了 “全国质量过硬•信誉保证放心品牌”光荣称号。美在延伸“不是要做多大企业或是赚多少钱，但是我要无愧做人，一定要对得起任何一个相信自己的人。”这句话不仅表现出了赖梅芳做企业的根本，也表明了选择与自己合作的商家的标准。她相信只有改变人们生活的产品才是好产品，要让美容服务像柴米油盐一样融入到人们的生活中，让女人更美丽，男人更自信。物美价廉，就是她的核心竞争力，她把品牌定位在能让所有的人都可以消费得起，尤其是农村的女孩子。她想让更多的人受益，所以踏踏实实的把企业做到最好、最专业企业，打造一个立足于大众，面向 80% 的群体的一个品牌。米伽素，在赖梅芳的经营运作下成为了响当当的品牌。梅芳未来赖梅芳作为“影响中国社会责任感企业家”，她认为美容护肤是一个朝阳行业，是永远不会被取代的。虽说网购盛行，但是买回来你不一定就会使用的很好，不一定会适合你每个阶段、每个季节的皮肤情况，所以还是需要有专业的人员来帮助，毕竟专业能够提供给顾客最好的服务，是生存的法宝。为了企业的可持续发展，带领自己的团队，倡导着快乐文化，秉承“奉献，互助，互爱，感恩”的理念，同时借力“一带一路”，要让自己的品牌走出国门。今年3月8号赖梅芳将走出国门去往新西兰，把产品带到国外； 4月份例行参加博鳌论坛。传统中药文化划开世界，造福全人类的日子正一步步成为现实。她坚信，化妆品并不能代表中国，中药护肤品能代表中国！这正是：中药塑美传承文化，民族品牌造福世界。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10426', '0', '证明团长是团长', '转眼间，从部队退休15年了。前段时间，社会上流传证明“你爹是你爹”“你妈是你妈”的笑话，不由让我想起20多年前曾经历的一件事——证明团长是团长。那是1987年七八月份，在辽宁省本溪市火车站候车室，眼看火车要进站了，一向守时的团长去厕所还没回来，急得我踮起脚尖不停张望。忽然，老远看见团长急匆匆领着两个人朝我走来，还没站稳就对我说：“快！快证明一下我的身份！”然后，他转过身向那两个人介绍：“这是我们团的政委，可以给我作证明。”对方非常客气地掏出警察证，充满疑惑地问我：“他真是你们团长？”这时再仔细看看团长，一身工作服油乎乎，头发长得快盖住耳朵，胡子几乎遮住下巴，我一下子明白了是怎么回事。事情是这样的。1985年，我们军将打通本溪八盘岭隧道的任务交给我们团。八盘岭位于本溪市所属本溪县小市镇至田师傅镇之间，山高坡陡，原有一条公路曲曲弯弯，遇有下雨下雪，车辆行驶非常艰难。打通这条隧道，对方便两市三县交通、促进当地发展意义重大。但打通这条隧道不仅工程量大，而且没有大型机械、缺少技术骨干，地质结构又比较复杂。这对于一个步兵团来说，不能不说是一块难啃的骨头。作为百色起义中诞生的红军团队，面对上级党委的重托和当地人民群众多年的期盼，我们只能是有条件要上，没有条件创造条件也要上。团长于广文亲临一线指挥。为了掌握第一手地质资料，尽快制定出科学施工方案，他在掌子面常常连跟几个班不下来。每一个炮眼掘进时间的长短，每一块石头的细微变化，每一处稍纵即逝的异常，都逃不过他的眼睛。他一边自己认真琢磨学习，一边向能者和书本请教，眼睛熬红了，身体消瘦了，全然不顾。在技术人员的帮助下，他很快摸清了一些眉目，对什么地方要防止漏水，什么情况要注意塌方，基本上做到了心中有数。为了弥补缺少大型机械的不足，他和营连干部研究，采用往年国防施工用风钻打眼的办法，坚持小步不歇，部队三班倒、风钻连轴转，较好保证了工程进度。为了克服技术骨干不足的难题，他采取老带新、强帮弱，干中教、学中练的方法，很快班班都有了自己的骨干。他把整个心思都用在施工上，一段时期内几乎和家里失去了联系。半年过去，家属没接到他一个电话、一封信，实在有点坐不住了，买张车票就到了我们营区。团长知道后，考虑到施工正是关键时候不能分心走神，说啥不让家属去工地，硬是劝她回了家。那段时间，他头发长了顾不上理，个把月洗不上一次澡很正常。当我从大庆赶到八盘岭，准备和他会合回师里参加全委会时，第一眼看到他我心里也猛地咯噔一下：一向威武精干的团长，怎么活脱脱像个“山里人”？我俩往回赶时，他对我说：“先穿工作服回去吧，要不会影响军容的。”真是越念叨什么越来什么，结果就发生了上面那一幕。为了进一步证明我俩的身份，我也掏出了军官证，十分激动地对两位警察说：“我们是在八盘岭施工的那个团，他就是那里的总指挥，为了让本溪人民早日受益，你们看把我们团长累成什么样了？”两位警察不知是因为刚才的举动有点冒失，还是被团长感动了，忙上前握着团长的手，一个劲地表达歉意，还解释说近期市内连续发生几起大案，怀疑是流窜犯作案，看到团长蓬头垢面便起了疑心……送他俩走后，我俩赶紧进站上车，在车上，你瞅我我看你，不由都笑了起来。团长感激地说：“今天要不是你老哥在，我的洋相可就出大了。”火车在急速行驶，望着面前这个既熟悉又陌生的“山里人”，我心里不由阵阵发热：多好的团长，多好的搭档！（整理：周文洲）', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10427', '0', '竞彩足球分析预测：奥达斯 vs 博塔弗戈', '[南球杯]竞彩足球分析预测：奥达斯 vs 博塔弗戈奥达斯▪ 南美俱乐部杯分两个阶段，第一阶段为资格赛，28之球队通过两轮资格赛决出10支球队，采取主客两回合淘汰制。本场是资格赛第一圈的比赛，也是双方次首回合的较量。▪ 上轮智利甲赛事，奥达斯客场0-1不敌康塞普西翁大学，惨遭联赛三连败，士气跌至谷底。球队本赛季联赛至今仅拿下一场胜利，目前积5分位列积分榜垫底位置，表现令人大跌眼镜。▪ 奥达斯新赛季以来连遭失利，最近四场比赛难求一胜，攻无力守不稳，状态难言理想。好在队内前锋卡拉斯科期间打入两球并贡献了一个助攻，个人状态火热，可以说是球队最大的收获。▪ 好在球队主场表现略胜客场，要知道，他们本赛季唯一的一场胜利是在客场取得的，而且四个客场赛事均有球进账，在场上的攻击力较有保证，值得信赖。▪ 只是球队已有多年未参与本赛事，本次回归可以说是奥达斯时隔11年的回归，球队自然希望能够取得较好的成绩，本轮战意自然毋庸置疑。博塔弗戈▪ 上赛季巴甲联赛，博塔弗戈凭借14胜11平13负的战绩在联赛中积53分，最终位列积分榜第10位，领先降级区10分，整体表现还算可以。▪ 休赛期间，博塔弗戈主要依靠里约锦标赛保持比赛状态。目前比赛已经结束，球队经过一路杀敌，最终在决赛次回合力挽狂澜，扭转首回合的不利局面，并通过点球对决胜出，一波三折后终于拿到新赛季的第一个冠军荣誉，士气大涨。▪ 本场比赛结束后，博塔弗戈将展开巴甲新赛季征程，好在球队有足够时间恢复状态，不需要在本战留力。而且这也是他们时隔多年的回归，球队自然希望能走得更远，本轮战意毋庸置疑。▪ 值得一提的是，博塔弗戈客场战绩平平，上赛季巴甲联赛中，球队客场仅录得5胜8平6负，不败能力尚可。只是球队三月份以来的6个客场赛事仅收获3胜1平2负，状态较为飘忽。奥达斯▪ 近6场比赛，奥达斯仅一场战胜对手，场均进0.8球失1.5球，其中4场双方进球数小于2.5球。▪ 奥达斯8场智利甲赛事输足五场，大球率为50%，赢盘率为37.5%。▪ 奥达斯近6个主场智利甲赛事，其中4场未尝胜绩，场均进1.3球失1.7球，小球率为66.7%。博塔弗戈▪ 近6场比赛，博塔弗戈收获三胜三负，其中4场双方进球数大于2.5球，并有4场失球数在两球或以上。▪ 博塔弗戈过去10场比赛赢足五场，多达五场保持清白之身，小球率为60%。▪ 博塔弗戈近6个客场正赛，其中4场未尝败绩，场均进1球失1.2球，其中3场双方进球数大于2.5球。（编辑：猎球者-欣）', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10428', '0', '梅根缺乏王室礼仪知识，关键时刻，伊丽莎白伸出援手，替梅根解围', '大苏塞克斯公爵夫人梅根马克尔周四走出家门，参加了她与伊丽莎白二世的首次单独相处。梅根缺乏王室礼仪知识，在混乱的时刻，伊丽莎白伸出了援手，梅根显然很紧张，他不知道谁到柴郡时应该先上车。“你喜欢什么？”她转身问伊丽莎白，得到的回答是：“你先上车。”“哦，好吧，”梅根回答。在郊游中，梅根马克尔和92岁的伊丽莎白穿着得体的服装。这位前女演员梅根马克尔穿着纪梵希的连衣裙，没有戴帽子或手套，看起来很时尚，头发也没有梳起来。同时，女王伊丽莎白穿着他最喜欢的设计师斯图尔特·帕文(Stewart Parvin)的一套绿色服装，她用一双白手套和她最喜欢的黑色扣鞋完成了她的装扮。女王和梅根于周四上午离开伦敦，乘皇家列车前往默西大桥，并会见了建筑师、规划者和社区代表。这是梅根马克尔第一次在没有丈夫哈里王子陪伴的情况下，与伊丽莎白二世单独相处，但她在陪同女王的时候，从容不迫地接受了这一切。应当说，这不是梅根第一次得到王室成员的指点，毕竟刚刚成为王室成员，梅根马克尔对于繁琐的王室礼仪还了解的不够深刻。上周六，当梅根马克尔和哈里王子出现在白金汉宫阳台上时，梅根马克尔就向哈里王子求助，什么时候向伊丽莎白行屈膝礼。哈里王子给她非常明确的指示，“就是现在！”以确保梅根不出糗。哈里王子还甜蜜地问他的妻子，她在游行期间是否还好？一位唇读者告诉“镜报”，事实上，苏塞克斯公爵夫人梅根马克尔作为一名成员或皇室成员参加了她的第一次和女王正式出行时，她对自己的信心准备不足。有一次，她溺爱的丈夫转向她，简单地问：“好吗？”“是的，是吗？”她回答道，哈里王子说他是。他的妻子随后承认她“紧张”。哈里王子明白了。“是的，”他回答说，然后再去问她：“还好吗？”', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10429', '0', '历史上的今天：英国皇太后伊丽莎白·鲍斯-莱昂逝世', '伊丽莎白·鲍斯-莱昂逝世2002年3月30日，英国皇太后伊丽莎白·鲍斯-莱昂逝世。 　　王太后于2002年的3月30日驾崩于温莎堡的狩猎行宫，享寿101岁，女王随侍在侧。直到当时她仍保有英国王室最长寿成员的纪录，这个记录随后就被她的妯娌爱丽丝郡主——格罗斯特公爵夫人所取代，其两年后以102岁高龄病逝。 　　在她停灵于西敏宫的西敏大厅的三天之中，有超过20万人前来悼念。 　　不得不说的是，当天担任守护棺木的有一位再平常不过的小卫兵，他就是英国当红歌手——上尉诗人，詹姆斯·布朗特。 　　在王太后4月9日的葬礼当天，有超过百万人聚集在西敏寺之外，且23哩长的人群沿着伦敦中央道路的两旁绵延到她的安息地，她将与他的丈夫以及小女儿一起长眠在温莎堡的圣乔治教堂。依照她的遗嘱，在葬礼之后，她棺木上的花圈将被送到西敏寺的无名战士墓，一个她在葬礼当天使人印象深刻的仪式。 　　伊丽莎白·鲍斯-莱昂逝世人物简介 　　伊丽莎白·安吉拉·玛格丽特·鲍斯－莱昂，英王乔治六世的夫人，1936至1952年间的英国王后，号伊丽莎白王后（Queen Elizabeth）。她也是乔治六世的继位者，现任英女王伊丽莎白二世的母亲。自1952年起，她的官方头衔是，“伊丽莎白王后，女王的母亲”中文通译为“伊丽莎白王太后”。伊丽莎白是最后一位爱尔兰王后以及印度皇后。伊丽莎白最为人所周知的是她在二次大战所扮演的正义角色，希特勒甚至形容她是“欧洲最危险的女人”。 　　然而，在大战之前，她与她的丈夫就像大多数的议会与联合王国一样，在第一次世界大战之后，都认为应该不惜一切成本避免战争爆发，且都强力赞同张伯伦与姑息政策。在张伯伦下台之后，国王任命丘吉尔重组政府。 　　2000年8月4日，是伊丽莎白王太后的百岁寿诞，从6月开始英国就庆祝活动不断。当地时间7月19日，英王室特地为王太后举行了第一次大型庆祝活动———有几千人参加的阅兵式和盛大游行。伊丽莎白·鲍斯-莱昂逝世', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10430', '0', '不论对于梅根马克尔还是凯特王妃，伊丽莎白都是个慈祥的奶奶！', '伊丽莎白是一代君主，但从照片中可以清楚地看到，这位92岁的女王扮演着另一个非常不同的角色，那就是奶奶，她是多么美妙的奶奶！正如昨天哈里王子的妻子梅根马克尔在柴郡时所说的那样，她非常渴望将她与祖母的关系延伸到她孙子的配偶，并以非常热情的方式欢迎他们。从教他们关于皇室礼仪到总是准备好分享笑声和笑话，这就是为什么女王是一个梦幻般的奶奶。女王和梅根马克尔一起拜访了柴郡，当他们看到对方时，看起来非常放松，因为君主与梅根马克尔笑了笑，并逗得新苏塞克斯公爵夫人开怀大笑。凯特米德尔顿之前透露说，当伊丽莎白到她家中时，女王喜欢宠爱她的曾孙，乔治王子和夏洛特公主。在纪录片中，她说：“当他们离开时，她总是在房间留下一点礼物或东西，这表明了她对家人的热爱。”她以皇家职责支持家庭。作为一名皇室成员，从来不会让脚趾脱线并不容易，但凯特王妃之前曾表示，威廉王子的祖母在皇家参与方面总是非常支持。剑桥公爵夫人凯特王妃在一部纪录片中说道：“对我来说，最令人难忘的是莱斯特的休闲时光，当时没有威廉陪伴，所以我对此非常担心，但女王很好，并为那个特殊的场合而照顾我，这可能只是她所做的一切中的很小部分，这表明她真的是多么关心我。“无论如何，她都会拜访他们。女王用直升飞机前往她在肯辛顿宫的孙子的家中，以迎接家族中最新的成员路易王子。女王陛下在他刚满九天的时候就认识了这位小朋友，表明她对家人的关心，特别是在她的丈夫菲利普亲王当时正在恢复髋关节手术的时候。她在圣诞节与他们玩得很开心。与女王的圣诞节一定是神话般的，而伊丽莎白以前曾与她的曾孙，萨凡纳和菲拉普斯，米亚廷德尔，乔治王子和路易王子以及夏洛特公主一起享受今年的节日时光。她在“女王的绿色星球”纪录片中与年轻人聊天时说：“是的，这一直是个问题，孩子们喜欢把这些装饰品敲开。我的曾孙也是，无论如何，他们都喜欢自己。她补充说，小孩们还装饰自己的小玩意儿挂在树上，说：“最好的事情是让他们装饰圣诞树，他们会更加小心。”', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10431', '0', '伊丽莎白的黄金时代之女王的华服', '各为少女好，小编最近有被一部电影给吸粉哦，它叫做：伊丽莎白的黄金时代，不知道各位有没有看过，不过相比电影情节而言，小编更喜欢看电影中女王换装的速度，华丽丽的各种款式简直美的不要不要的，下面我们就一起看看女王的华服把！', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10432', '0', '伊丽莎白忙里偷闲赏花，收到来自中国的礼物，伊丽莎白赞不绝口！', '伊丽莎白她最喜欢的花被认为是玫瑰，伊丽莎白夸赞玫瑰气味很棒。尽管早已年过90岁，但伊丽莎白仍然异常忙碌，不过她还是在王室婚礼结束之后，抽出时间来赏花。花展成立于1913年，已成为世界最大的园艺卓越展览之一，吸引了来自全球的参观者和参展商。这个节目由皇家园艺协会组织，其中女王是赞助人。在参观了中国武汉的展示花园之后，女王穿着淡粉色的薄纱外套和一件马甲，她收到了一条画着鲸鱼的丝巾，上面的文字：永远保持着微笑。还有红色和粉红色的牡丹，象征着财富和荣誉。女王陛下强调园艺对心理健康的积极影响，并对这件丝巾赞不绝口。伊丽莎白还参观了约克郡花园的欢迎活动，那里有一座漂亮的石屋和一条流动的小溪。当她走近时，安妮公主正从大楼里走出来，品尝了里面的一块奶酪。约克公爵早些时候曾叫比阿特丽斯公主进去尝试过。她告诉女王：“里面有真正的奶酪。”设计师马克谈到对于伊丽莎白的评价时说：“她很喜欢它，她觉得它很迷人，她喜欢这种建筑风格。”伊丽莎白的最后一站是在柠檬树花园。在这里，人们可以看到一些日常用品，如作为植物容器使用的锡罐和瓶子，复制了居民种花的方式，以改善周围环境。设计师汤姆说：“我告诉她，难民们每天都是如何重新使用这些物品来建造花园的。”他说，无花果树、柠檬树和玫瑰等植物“有魅力让人们忘记烦恼，并提醒他们失去的宝贵东西。”', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10433', '0', '不要叫我伊丽莎白，叫我女王大人！', '英国女王伊丽莎白二世是一个非常糟糕的女人相对男人来说。有一次，沙特阿拉伯王子阿卜杜拉访问了苏格兰的巴尔德勒城堡。女王在那里迎接他。这位阿卜杜拉是一个非常保守的穆斯林。他认为女人不如男人，她们不能做男人能做的事情，其中之一就是开车。阿卜杜拉来到了城堡，并且在城堡里面参观了一些历史古迹。然后汽车被开到城堡的前面的空地上面，这样他就可以乘坐汽车参观庭院了。当阿卜杜拉漫不经心的坐在副驾驶座上的时候，令他吃惊的是，伊丽莎白女王就坐在驾驶座上。在他还没来得及抗议之前，伊丽莎白女王就快速发动了汽车，以极快的速度开始飞奔。当她在苏格兰极其狭窄和曲折的道路上疾驰而过时，伊丽莎白漫不经心地指出了一些地标性的建筑，并和阿卜杜拉进行了简短的交谈。阿卜杜拉恳求她放慢车速，并且提醒她把注意力集中在驾驶上，但伊丽莎白没有理睬他。她一直在苏格兰的乡村里面飙车，身边坐着一位极为紧张的沙特阿拉伯王子。当他离开苏格兰的时候回到沙特阿拉伯，三年没有回来。她不仅向他证明了女人可以和男人一样好，而且她也把他吓得几乎半死。如果这对你来说还不够，那么她还会用机关枪射击。真正的女王大人。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10434', '0', '美国司法部指控Theranos创始人伊丽莎白·霍尔姆斯犯有欺', '据外媒CNET报道，虽然目前“女版乔布斯”伊丽莎白·霍尔姆斯（Elizabeth Holmes）已不再经营血液检测科技公司Theranos，但根据美国司法部发布的一份新闻稿：霍尔姆斯和前Theranos总裁拉米什·巴尔瓦尼(Ramesh Balwani)现在被指控犯有欺诈罪。三个月前，美国证券交易委员会（SEC）以民事欺诈罪为由，向霍尔姆斯及巴尔瓦尼提出起诉，随后他们同意和解。霍尔姆斯同意支付50万美元的罚款，返还1890万股公司股票，放弃多数表决控制权，并被禁止在未来10年内担任上市公司的高管和董事。但随着新的刑事指控，美国政府正试图惩罚Theranos的高管，因认为他们既欺骗了投资者，又故意以不准确的血液检测结果危害人们的生命。美国司法部表示，这些指控可能使他们面临长达20年的牢狱之灾，并且每项指控都将追加25万美元的罚款。美国司法部宣称霍尔姆斯及巴尔瓦尼“以数百万美元的计划欺诈投资者，并且另外制定了欺骗医生和病人的计划。”自2016年年初以来，Theranos公司一直在接受调查，此前《华尔街日报》的一份报告显示该公司的测试技术尚未按照承诺开展工作。2016年6月， 美国最大的连锁药店沃尔格林(Walgreens)宣布与Theranos停止合作，关闭了亚利桑那州所有40个Theranos血液检测点。（该公司去年8月与Walgreens达成和解，并于去年5月和解了两起投资者诉讼。）2016年7月，霍尔姆斯被禁止在两年内运行实验室。根据Theranos周五的新闻稿，霍尔姆斯目前仍担任该公司的董事会主席，但总法律顾问David Taylor正接任首席执行官的职位。《华尔街日报》报道称，Theranos 在4月初解雇了大部分剩余员工。Theranos没有立即回应置评请求。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10435', '0', '伊丽莎白：童贞女王，嫁给国家', '十六世纪初，英格兰国王亨利八世爱上了王后的侍女，美丽的安昵·博林，遂以王后没有男嗣，在将来可能发生继承纠纷为由要求离婚。王后阿拉贡·凯塞林是亨利的寡嫂，他们生有一个女儿玛丽·都铎。 英国是天主教国家，亨利的离婚要求遭到了罗马大主教的拒绝。亨利为了达到离婚的目的，“冲冠一怒为红颜”，决定进行宗教改革，建立独立的英国安立甘教会，以摆脱罗马教皇的控制。1533年，坎特伯雷大主教克兰默在距伦敦30英里的女修道院开庭审理亨利离婚案。王后阿拉贡·凯塞林拒绝出庭，克兰默遂于5月23日判决亨利与凯塞林的婚姻无效。两天后，亨利就与已经怀孕的安妮举行了秘密婚礼。28日，克兰默宣布亨利与安妮的婚姻有效。同年9月，亨利被教皇开除出教。安妮也为亨利八世生了一个女儿，取名伊丽莎白·都铎，她随父亲加人了英国国教。 1554年，玛丽·都铎君临天下，她是一个狂热的天主教徒，为了将英国带回天主教门下，她实施了一系列的血腥镇压，人称“血腥玛丽”。她的残暴手段使帝国进一步的分裂，一时间，各种阴谋谣言不断。而伊丽莎白·都铎因涉嫌卷入新教运动也被玛丽软禁。1558年，玛丽一世驾崩。因其膝下无子女，伊丽莎白便在英格兰新兴资产阶级、新贵族和新教徒的拥戴下，继承大典，称伊丽莎白一世。伊丽莎白自幼聪慧、貌美，并接受了良好的教育。她才思敏捷，博览群书，通晓意大利、法兰西、西班牙等国语言。在她统治期问，注意任用贤才，积极推进国家在政治、经济、宗教等各方面的改革。她在位44年，带领大英帝国进入“黄金时期”，成为当时欧洲最富强的国家，为英国的强盛做出不可磨灭的贡献。但是，她却终身未嫁，引起了人们对她的种种猜测。难道守身如玉67年，号称“嫁给了英格兰”的伊丽莎白女王从来没有过正常人的情欲?一生都末有过惊心动魄的爱情经历吗？ 她的政敌为此攻击她有生理缺陷，因此不能够像正常的女人一样结婚生育;持反对观点的人们则说她曾有过私生子;也有很多人认为，她的父亲亨利八世三次杀妻，六娶皇后，在她的心灵深处留下了阴影,使她惧怕婚姻，拒绝婚姻。 以伊丽莎白的美貌、品行、才干、地位，获得欧洲各国国王、贵族的青睐与追逐，一直就未间断过。据记载，伊丽莎白在少女时代曾与英国贵族汤姆斯·西摩尔关系密切，他们之间的关系也一度成为宫廷的绯闻。但西摩尔追求伊丽莎白主要是想利用她争夺王位，后因阴谋败露，西摩尔被杀，他门之间的这段恋情也就告终。 1568年，西班牙国王腓力二世向伊丽莎白求婚，亦遭到拒绝。腓力二世曾是伊丽莎自的姐夫，她同父异母姐姐玛丽·都铎的丈夫。他早就对伊丽莎白另眼相看，在她遭受玛丽监禁期间曾给予了特别的关照。但伊丽莎白对是否缔结这桩婚姻非常冷静和慎重，玛丽与腓力的结合，曾经给英国带来危害，人们的记忆犹存。她相信腓力向她求婚是为了吞并英国的计划。于是，她以双方的宗教信仰不同为由，委婉地拒绝了腓力的求婚。以后，伊丽莎白又陆续拒绝了其他一些国家王公贵族的求婚，如瑞典王艾力克、罗马皇储查理大公等等。人们一直相传，伊丽莎白一世虽然终身未曾婚嫁过，但她一直都有个情人，这个神秘的情人就是当时大英帝国的莱斯特伯爵——罗伯特·达德利。 伊丽莎白与达德利在孩童时代就已相识，是青梅竹马的玩伴，两人之所以始终没能缔结良缘，是因为达德利早已结婚(他曾有过两任妻子，第一任妻子神秘死亡)，女王当然不能成为达德利的第三任妻子。据说，伊丽莎白女王与达德利长年保持着通信关系，女王喜欢在信中称呼他为“甜蜜的罗宾”，并给他取了个呢称：我的“眼睛”。 美国的福尔杰莎士比亚图书馆曾公开展览过达德利写给伊丽莎自一世的一封情书。这封情书写于1588年7月20日或27日，是达德利正在海上指挥英国舰队与西班牙舰队交战间隙写给女王的。在这封情书中，达德利称呼伊丽莎白为“最最甜蜜的女王陛下”和“我最最亲爱的女士”。有趣的是，他两次将代表“最”的英文单词“most”中的“o”双写，变成了“moost”，中间都空了一格，并在两个“o”上面加上“眉毛”，这样就成了一双“眼睛”。达德利在信里感谢“甜蜜的”女王给予了他“最伟大的安慰”，落款“莱斯特”。这也是他给其他人写信从来都没有川过的落款。 在写完此信后一个多月，达德利就战死在沙场，享年56岁。达德利的死讯令伊丽莎白女王几乎崩溃。她把自己锁在卧室里，好几天不眠不食。最后英国财政大臣和其他内阁大臣不得不破门而入，将女王抬出卧室。 1578年，伊丽莎白女王已经45岁，仍然待字闺中，法兰西国王亨利二世的四弟，年仅20岁的安如公爵向她求婚，据说，伊丽莎白女王当时答应了求婚，但在即将举行婚礼的前几天，突然变卦。这次拒婚差点使伊丽莎白女王丧命。前面提到过，伊丽莎白是新教徒，在罗马天主教皇的眼里，她继承王位是不合法的，合法承继人应是苏格兰的玛利·斯图亚特女王。玛利·斯图亚特是苏格兰詹姆士五世与法国盖斯家族的玛利所生的女儿，也是英王亨利七世的曾孙。而安如公爵则是玛利·盖斯的外甥。伊丽莎白的悔婚，使玛利·盖斯极其恼怒，她发誓要给伊丽莎白以颜色。一袭来自法国的丝裙，毒死了伊丽莎白的女官，惊魂未定的伊丽莎自决定反击。她派华星汉前往苏格兰引诱玛利·盖斯，然后在床上杀了她。 翻看欧洲历史，我们不难看出，由古至今，各国王室成员的婚姻，都很难由当事人自己做主。王室婚姻的缔结与国家的政治、经济、国际关系、对外战略密切相连。因而，王室婚姻也包含了太多的阴谋诡汁和利益舍取。伊丽莎白之所以终身未嫁，并非是因为她有生理残疾，也并非是她太挑剔，而是因为她睿智。她早已看得太透、太穿。所以她义无返顾地选择了独身，一生致力于英国的强大、昌盛。正如她在宣布解除与安如公爵的婚约时所说:“我无须再选择佳婿结婚，因为我在举行加冕典礼时，已将结婚戒指戴与我国臣民的手上，意即我将与全体臣民为伴，将我的生命与贞洁献给英国。”文章内容为本头条号原创，未经许可不得转载图片来源于网络关注本头条号，更多有趣有料的中外历史每天第一时间更新。每天学习一点点，你也可以成为通晓古今的文化人！', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10436', '0', '除了查尔斯王子一家，英国女王的其他子女都在做什么呢？', '英国王室目前仍然是世界中最有吸引力的家庭。很多人都很关心他们的一举一动。对于查尔斯王子以及他的子女很多人了如指掌，但是对于伊丽莎白的其他子女大家可能就不是那么熟悉了。英国女王的二儿子是安德鲁王子。他同时也是约克公爵。虽然在王位继承顺序中排名比较靠后，但是他可是女王最喜欢的儿子。在达特茅斯皇家海军学院有过服役经历。他和前妻有两个女儿比阿特丽斯公主和尤金妮公主，对了，他的前妻叫弗格森，和曼联的传奇主教练同姓。爱德华王子是女王的三儿子。据说在小时候，因为他是家中最小的孩子，所以得到了母亲很多时间的陪伴，当然这也和当时国事没有那么繁忙有关系。他和妻子苏菲王妃是在网球慈善赛的宣传活动中相识的，随后两人坠入爱河。他们有两个孩子，大女儿是露易丝·温莎郡主。儿子是塞文子爵詹姆斯。安妮公主是英国的长公主。她比查尔斯王子小，是家中的老二。热衷慈善事业的她很受爱戴。而她下嫁给了女王的近卫龙骑兵中的一位中尉。这位中尉后来成了上尉。本来按照惯例这位上尉菲利普斯会被封为伯爵。但是他婉拒了女王的封号。两人有一子一女。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10437', '0', '那个时候，伊丽莎白和凯特分享了他们自己的，非常有趣的笑话', '伊丽莎白女王和梅根·马克尔在第一次联合出访中微笑着的照片惊人，与凯特·米德尔顿与女王分享自己的笑话时有着惊人的相似之处。2012年6月，剑桥公爵夫人在伊丽莎白女王的钻石禧年庆典上坐在女王身边。威廉王子也在那里，当伊丽莎白女王被拍到和凯特开怀大笑时，他似乎是一天中最难忘的时刻的源泉。(图片/盖蒂图片社)这张如今已成为标志性的照片显示，伊丽莎白女王张开双臂回应眼前发生的事情。公爵夫人笑着看着伊丽莎白女王。剑桥公爵站在他的祖母和妻子面前。他打算在诺丁汉的弗农公园(Vernon Park)期间为一名儿童的障碍赛开枪。但当他们走上跑道时，他被激动的人群包围了。威廉对伊丽莎白女王的反应是迷惑不解的，但她只能笑，并伸出手来表示顺从。(图片/盖蒂图片社)一旦恢复了平静，公爵开玩笑说:“我们准备好了，不是吗?”没有压力。这一切都是注定要发生的。伊丽莎白女王身着一件由斯图尔特·帕尔文(Stewart Parvin)设计的绿松石和象牙色双排扣大衣，戴着雷切尔·特雷弗-摩根(Rachel Trevor-Morgan)的帽子。公爵夫人穿着一件蓝灰色的米索尼大衣，戴着一顶帽子。(图片/盖蒂图片社)这是剑桥公爵和公爵夫人第一次与伊丽莎白女王一起参加“告别日”活动。伊丽莎白女王一直在英国旅行，以纪念她登基60周年。威廉和凯特一年前在威斯敏斯特教堂结婚。', '', '4689', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10438', '0', '犬夜叉：拥有杀生丸的颜值和实力就能拥有爱情？网友：性格不行', '动漫界中有很多优秀的男配角，但是其中就有这样一个人，他不食人间烟火，他高冷，他能力爆表，他内心温柔，他就是《犬夜叉》的里男神杀生丸。很多杀生丸的粉丝都尊称他为杀殿，在《犬夜叉》中，杀生丸可以说是有别于其他角色的存在了，身边跟着邪见和玲，以及坐骑双头龙马阿哞，感觉高桥留美子大妈都可以出个杀生丸的番外篇了，指不定都能赶上原作的人气了。那么很多人就问了，要是能有杀生丸的颜值和实力，那么是否就意味着不用怕找不到女朋友了。答案是否定的，为什么呢？一起来分析下颜值：杀生丸颜值毋庸置疑，作为犬大将和凌月仙姬的儿子，杀生丸完美的继承了父母的盛世容颜，只能说基因非常强大，杀生丸有着一头银白色的长发，额生月印，金色眼眸，左右脸颊各有两条红色妖纹，白色和服上飘着六角梅，金眸灿烂，贵气逼人，是一个面无表情且沉默寡言的美貌贵公子。这里不得不说，好像杀生丸一次都没有笑过，开怀大笑的杀生丸可能再也见不到了，你们希望看大杀殿笑吗？实力：初登场时，杀生丸的佩剑是天生牙，不过一开始杀生丸对天生牙并不感兴趣，只执着于铁碎牙，不过后来被犬夜叉砍断了左手后才有点放下这个念头。之后得到了斗鬼神，虽说斗鬼神没有铁碎牙那么强，但是在杀殿的手上依然能够发挥巨大的威力，奥义苍龙破了解一下？，后来得到了爆碎牙，爆碎牙——是杀生丸自己的刀，证明自己超越父亲的名刀！可以说在《犬夜叉》里，没有一个人能够让杀生丸使出全力与他对决的，实力可想而知。家庭：可以说杀生丸是一位富二代，父亲是妖怪统领，这就意味着杀生丸有一位厉害的老爹了，并且给他留下的财富也是极具价值，这就是传说中的高富帅？有颜值，有能力，有钱！其母亲也是一位很有实力的犬妖，不得不说杀生丸放在现实生活中的话，确实是一位人生赢家，只能羡慕了！哦对了，还有一个二哈弟弟。性格：我不知道广大女性对于杀生丸的性格持什么态度，不过我想说的是杀殿的性格并不适合谈恋爱，虽然他内心温暖，但是他太高冷了，并且话很少，虽然他会用自己的行动表现出对你的爱，但是你是无法走进他的内心的，你根本不知道他的内心想法如何，如此高冷的男神不是一般人能够驾驭的了的，如果你觉得能够驾驭的话，那么你的掌控欲可以说非常的强，因为在杀殿面前，一切都只能够由他掌控。叫你和一位有颜值，有能力，有钱但是不会笑 的人谈恋爱你愿意吗？最后，如果大家喜欢这篇文章，一定要点赞、收藏和分享喔！更多动漫精品文章，请继续关注少年恰是同学，更多精彩内容和你一起分享哦！', '', '4690', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10439', '0', '女人的战争：桔梗VS戈薇', ' 模糊的爱恋，艰难的选择，无可奈何的避让……如此这般，酸溜溜的东西啊。《犬夜叉》在小编心里不只是热血动漫，更是一部治郁系动漫。犬夜叉、桔梗和戈薇的感情纠葛始终贯穿全剧。桔梗的爱情是永恒的爱，是无望的爱，桔梗渴望爱情而又无法得到爱情的结局令小编心痛。桔梗的气质是剧中任何女性都无法替代的，她出众的外貌极其恰当的衬托出了内在的高雅气质：从衣饰上看，桔梗上身是象征纯洁的白衫，下面是代表炽烈情感的红裙，其人面容清雅，眉宇间颦愁淡淡，行动处衣袂飘飘、长发拂风、死魂虫委蛇相随，再配上悲运巫女优美的旋律……即使不能说颠倒众生，也足算是风华绝代。最初的戈薇，最初的桔梗。《犬夜叉》第一话，戈薇被女妖拉进枯井，来到五百年前的妖怪战国。被妖怪不断攻击的她，听从枫婆婆的指示，取下了犬夜叉身上的封印之箭。前二十话。二人从一开始就气氛不和，总是拌嘴争吵。相处中，二人渐渐开始为对方考虑。斗嘴归斗嘴，戈薇很愿意考虑犬夜叉的感受。知道犬夜叉爱恋着桔梗却被桔梗封印，十分同情。很快，戈薇的前世桔梗出现。犬夜叉令戈薇不得而知的一面渐渐展现出来。一直狂妄冲动，头脑单纯的男孩展现出了温柔和忧伤。就故事创作而言，这大概就是桔梗存在的原因，也是戈薇开始对犬夜叉产生恋情的开端。接下来，犬夜叉和戈薇，二人有着某种意义上“宿命”的联系，动心也好，信赖也罢，都是十分自然的事情。本来故事可以就二人的冒险和陪伴为主线，顺利进行。桔梗揭示了犬夜叉的过去，和埋藏于内心的柔情。戈薇总是能让犬夜叉从悲伤中走出来，懂得开心也懂得发脾气。就此看来这样的姑娘，没有任何令人不满之处。说不上极具艺术魅力，但相貌可爱，本性温柔不做作，元气满满，还是会替人着想的行动派。这也是高桥老师心中，最佳女友的模样吧。故事伊始，桔梗就是这样一个为戈薇的善良和元气做对比的角色。桔梗的身体是一个陶制人偶，依据怨气行动。即便在生前，性格也是极为压抑的。在好不容易对生活产生希望的时候却带着怨恨而死，复活后更是无法遏制对犬夜叉的憎恨。明明是恋人，却刀剑相向。面对一直无法放下自己的犬夜叉，她唯一想做的，就是二人一同赴死。戈薇尝试说服桔梗，让她放下执念。也试图说明，犬夜叉也是受到伤害和无法放下思念的那一个。即便内心苦涩，也要这样去帮助犬夜叉解开误会，真是个温柔，善良又明理的姑娘。冷淡变本加厉到冷酷的桔梗，引出奈落和犬夜叉的恩怨，也令戈薇展现了温暖积极的一面，所有的角色任务都完成了，理应退居二线，甚至淡出故事。110话前后的白灵山篇中，桔梗追溯到了奈落的行踪。只身面对这个刚刚获得新的身体，变得更加强大的宿敌。犬夜叉在另一处来不及脱身，这边桔梗遭到致命一击。就此，奈落除去了自身的弱点，成为更可怕的敌人。桔梗完成了最后的使命，坠入满是瘴气的山崖。这一集的片名，叫做“再见了，美丽的桔梗”。这样的片名，说明了高桥老师下了某种决心。而且桔梗就此离开，其实是非常合理的。作为配角，她的作用几乎已经发挥到极致。然而，不知出于何种原因，高桥老师并没有令桔梗安息。也许是因为感受到这个角色的哀伤，不甘，甚至只是喜欢她外形的美丽。一想到再也看不到在幽暗的森林里，伴随着死魂虫的荧光，禹禹独行的长发巫女，就很难过。也许高桥老师也会为再也不能去画这般深幽美丽的画面而不快。总之，桔梗在理应离开的时候，被留了下来。这一决定，让最初的戈薇和最初的桔梗，变得迥然不同。 偏离最初的桔梗和无法解脱的戈薇无论如何，高桥老师最终还是无法停止对桔梗的刻画。戈薇的前世，悲运的巫女，奈落堕化的宿因。这些设定很可能只是高桥老师一时大胆的挥就。然而这些带着沉重气氛的设定，却让桔梗为高桥老师突破了故事原本的热血和少女基调，使得犬夜叉成为更深刻的作品。诸多对于作品的评论中，这个观点得到了相当程度的认同。高桥老师也意识到了这一点，所以才无法对桔梗放手。与其说桔梗是戈薇的前世，不如说戈薇令高桥认识到桔梗的魅力。戈薇身上的美好品质，以及情侣间有趣的打情骂俏，造就了老师的多部经典。例如《福星小子》里的拉姆，《乱马1/2》里的小茜，《相聚一刻》里的响子。喜剧出身又如此成功的高桥老师，在桔梗的身上看到了新的可能。动画版21话，桔梗试图带走犬夜叉。因为戈薇的阻挠，她放弃了。桔梗的转折开始。没有继续介入犬夜叉和戈薇的关系中，她转身开始寻求自己再次来到世上的使命。一边帮助受到妖怪侵扰的村民，一边追踪奈落。也曾想过停留在某一个村子，做保护大家的巫女，和孩子们一起玩耍。但是死者的身份给人们招来麻烦，于是放弃隐居。也意识到生死之间不可逾越的界限。动画版32话，桔梗终于在人见城遇到了奈落。试探一番后，聪明的她发现了奈落的企图和弱点。把自己收集到的四魂之玉碎片拱手让给奈落，这一举动引发了所有人包括观众的误会这个女人疯了么？到底在想些什么啊！果然是反派角色吧。随着事态发展，逐渐真相大白。在犬夜叉一行人被奈落牵着鼻子前进时，桔梗早早就发现了除掉奈落的方法。一路在暗地里助攻，并且多次留下线索。在犬夜叉多次寻找自己并声称保护自己的时候，推开了他的怀抱。完结篇中，桔梗的完美表现令人咋舌。她出手保护琥珀的灵魂，即便这阻挠了针对奈落的行动。随后引导戈薇去取得梓山之弓。接着弥勒的风穴反噬，是桔梗用有限的生命救他回来。此刻，桔梗已经快油尽灯枯。奈落终于再度出手，她指引戈薇射穿自己体内的四魂之玉，用最后的灵力护送破魔之箭，给奈落最后一击。更不用提，一路上帮助过的大大小小的人类和妖怪。这种情况，再怎么看，也远远超过了一个配角应该承担的责任吧。不仅紧紧攥着观众的感情，还控制着主线局势的进展。桔梗线，已经逆天了。她的存在和表现，呈现出了新的高桥留美子，也增加了《犬夜叉》的维度和分量。她提升了故事的品味，让主线和感情线有了更多的发展空间。然而，就某种故事需要的平衡而言，这并不是一件好事。因为戈薇是不可能不被与之比较的。桔梗的优秀表现唯一导致的问题，就是限制了女主角戈薇的成长。如果女主角的塑造有了缺憾，这部作品也不能称为完全成功。这种无可奈何的失衡，甚至造成了主角间的感情发展脱离了原先的设想。本应该在100集之前就了断的犬夜叉，直到故事完结也没能放下桔梗。一共26话的完结篇，桔梗活到了第8话。还有两集就全部完结时，敌人企图利用桔梗的存在，令犬夜叉失控。因为敌人的理解，依旧是“桔梗是犬夜叉最爱的女人”。幻境中，桔梗微笑着向他伸手', '', '4690', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10440', '0', '犬夜叉：拥有杀生丸的颜值和实力就能拥有爱情？网友：性格不行', '动漫界中有很多优秀的男配角，但是其中就有这样一个人，他不食人间烟火，他高冷，他能力爆表，他内心温柔，他就是《犬夜叉》的里男神杀生丸。很多杀生丸的粉丝都尊称他为杀殿，在《犬夜叉》中，杀生丸可以说是有别于其他角色的存在了，身边跟着邪见和玲，以及坐骑双头龙马阿哞，感觉高桥留美子大妈都可以出个杀生丸的番外篇了，指不定都能赶上原作的人气了。那么很多人就问了，要是能有杀生丸的颜值和实力，那么是否就意味着不用怕找不到女朋友了。答案是否定的，为什么呢？一起来分析下颜值：杀生丸颜值毋庸置疑，作为犬大将和凌月仙姬的儿子，杀生丸完美的继承了父母的盛世容颜，只能说基因非常强大，杀生丸有着一头银白色的长发，额生月印，金色眼眸，左右脸颊各有两条红色妖纹，白色和服上飘着六角梅，金眸灿烂，贵气逼人，是一个面无表情且沉默寡言的美貌贵公子。这里不得不说，好像杀生丸一次都没有笑过，开怀大笑的杀生丸可能再也见不到了，你们希望看大杀殿笑吗？实力：初登场时，杀生丸的佩剑是天生牙，不过一开始杀生丸对天生牙并不感兴趣，只执着于铁碎牙，不过后来被犬夜叉砍断了左手后才有点放下这个念头。之后得到了斗鬼神，虽说斗鬼神没有铁碎牙那么强，但是在杀殿的手上依然能够发挥巨大的威力，奥义苍龙破了解一下？，后来得到了爆碎牙，爆碎牙——是杀生丸自己的刀，证明自己超越父亲的名刀！可以说在《犬夜叉》里，没有一个人能够让杀生丸使出全力与他对决的，实力可想而知。家庭：可以说杀生丸是一位富二代，父亲是妖怪统领，这就意味着杀生丸有一位厉害的老爹了，并且给他留下的财富也是极具价值，这就是传说中的高富帅？有颜值，有能力，有钱！其母亲也是一位很有实力的犬妖，不得不说杀生丸放在现实生活中的话，确实是一位人生赢家，只能羡慕了！哦对了，还有一个二哈弟弟。性格：我不知道广大女性对于杀生丸的性格持什么态度，不过我想说的是杀殿的性格并不适合谈恋爱，虽然他内心温暖，但是他太高冷了，并且话很少，虽然他会用自己的行动表现出对你的爱，但是你是无法走进他的内心的，你根本不知道他的内心想法如何，如此高冷的男神不是一般人能够驾驭的了的，如果你觉得能够驾驭的话，那么你的掌控欲可以说非常的强，因为在杀殿面前，一切都只能够由他掌控。叫你和一位有颜值，有能力，有钱但是不会笑 的人谈恋爱你愿意吗？最后，如果大家喜欢这篇文章，一定要点赞、收藏和分享喔！更多动漫精品文章，请继续关注少年恰是同学，更多精彩内容和你一起分享哦！', '', '4690', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10441', '0', '《犬夜叉》：杀生丸穿鞋，犬夜叉为什么不穿鞋？原因很简单', '在《犬夜叉》中前中期的犬夜叉和杀生丸总是一言不合就打架，看得人很过瘾，不知道大家有没有发现一个细节，犬夜叉在动漫中是不穿鞋的，而杀生丸不管在哪里都会穿鞋子，这是咋回事呢？其实仔细想想很简单，大概有三个原因，今天小编和大家慢慢分析一下，要是写得不好大家不要骂我！首先就是教育问题了，杀生丸是妖族贵公子，母亲是个强大的妖怪，住的也是宫殿，杀生丸从小除了训练就是学习贵族的礼仪，就像小说中的花无缺一样，凌月仙姬把所以的爱给了他，希望他成为最优秀的妖怪，穿鞋子是基本，而犬夜叉出生不久母亲就死了，没有受过良好的教育，没有穿鞋子的习惯！然后就是经济实力了，杀生丸可是超级富二代，犬夜叉充其量是私生子，杀生丸别的没有就是钱多，而且剧中稍微混得好的妖怪穿的衣服都是法宝，像犬夜叉的火鼠裘就是那样，水火不侵，而杀生丸全身上下肯定也是如此，说不定还有自洁功能，造价肯定昂贵，犬夜叉这样的穷妖怪买不起，一般的鞋子也禁不起他的蹂躏啊！最后一点就是实力问题了，杀生丸实力强大，走在路上没人敢惹，穿着鞋子也没事，谁来惹事就打得对面妈都不认识，但是犬夜叉实力弱啊，尤其是没有得到铁碎牙的时候，混得很惨，遇到敌人只能靠上树，你说穿鞋子能上树有他的爪子灵活吗？不存在的，所以杀生丸习惯穿鞋子而犬夜叉不穿啊！本文由动漫秘密花园原创，欢迎关注，带你一起长知识', '', '4690', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10442', '0', '杀生丸为何独宠小玲一人？', ' 杀生丸殿下第一次遇到小玲的时候，那时他受了重伤（因跟犬夜叉大战被砍断一支胳膊）而被小玲发现，在他养伤的期间，小玲一直都来看望他。杀生丸离开后，一直都没有回去过，后来，他再一次去的时候，发现小玲已经死了，他用天生牙将小玲救活后，就一直让她待在自己的身边。 其实像杀殿这样的人，外表高冷而且也是极爱逞强的人，内心也是极孤独的。而小玲的出现无疑给他带来了不一样的感觉。他开始慢慢有了在乎的人，有了想保护的人。他开始懂得了感情。 他一直都是在很孤独的活着，而小玲对他来说是个不一样的存在。一个还没有长大的小女孩儿，对她来说，杀生丸对她来说就是她以后的依靠，她喜欢叫他杀生丸殿下，她也慢慢一直用她自己的方式温暖着他。 说实话，为什么没有小玲长大之后的续集呢？真的是超想看小玲长大之后跟杀生丸殿下发生的事呀。 最后，感谢大家一如既往地支持。谢谢。', '', '4690', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10443', '0', '清冷如月，贵气逼人——杀生丸', '小铃初遇杀生丸之时，他被风之伤打成重伤，只身躺在树下，无法动弹。小铃躲在树后偷偷望着他，后来怯弱的走近了他。当铃走近，他如同一只凶兽般。后来铃一次次把食物放在他面前，杀生丸才开口说：“我对人类的食物不感兴趣。”再后来，铃在偷抓鱼是时被村民打伤，杀生丸居然问了她脸上的伤是怎么回事。那一刻，铃看着杀生丸淡漠的脸，笑得十分开心。“我只是问你的状况，你那么高兴做什么？”这应当是杀生丸大人第一次感觉到失去一个人的恐惧与无法守护一个人的愤怒。铃在不知不觉中改变了他......当真是清冷如月虽然是乱配，但不可否认的是两人的样貌十分适合，只欣赏一下就好了。有很多人都心疼最后化作风的神乐，我们也无从得知杀生丸心中究竟有没有这样一个人，一生追寻自由......或许是有的，“连天生牙都救不了她......”他是这样说的。', '', '4690', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10444', '0', '伤感！这些动漫中相爱的情侣最终却没能在一起', '即使在美好的二次元也会有让人伤感的前景，而那些相爱却最终没能走到一起的情侣们无疑是最让人伤感的事了。黑崎一护＆朽木露琪亚《死神》黑崎一护靠着同伴们的帮助最终打败了最强敌人友哈巴赫，解救了尸魂界、现世与虚圈。大决战约十年后，黑崎一护与井上织姬婚后育有一子，名叫黑崎一勇。朽木露琪亚善良重情义，她的品格也如梅般傲然，如阳光般温暖明媚，其所持有的斩魂刀名为“袖白雪”，是尸魂界最美的斩魄刀十年后成为尸魂界十三番队队长，并与青梅竹马的阿散井恋次结婚，两人育有一女阿散井莓化。宿海仁太＆本间芽衣子《未闻花名》是一部用直达内心的笔触描写青春期的少男少女在面对恋情、友情、纠葛时，有欢欣笑容也有伤心泪水的青春原创片。有一天，芽衣子对仁太说：“帮我实现一个愿望吧”。仁太虽然有点为难，却还是答应帮助面码实现愿望。以此为契机，为了实现芽衣子的愿望，分散在各处的大家又再次地聚集在一起。有马公生＆宫园薰《四月是你的谎言》有马公生的母亲一心想把公生培育成举世闻名的钢琴家，而公生也不负母亲的期望，在念小学时就赢得许多钢琴比赛的大奖。11岁的秋天，公生的母亲过世，从此他再也听不见自己弹奏的钢琴声，沮丧的他也只好放弃演奏，但在14岁那年，经由儿时玩伴的介绍，公生认识了小提琴手宫园薰，并被薰的自由奔放吸引，没想到薰竟开口邀请公生在比赛时担任她的伴奏……土方十四郎＆冲田三叶《银魂》对三叶的感情，用土方本人的一句台词概括足矣：我只是想让自己心爱的女人得到幸福罢了。他爱三叶。爱到什么程度呢？“不知道哪天就会丧命的他，不可能接受姐姐。”土方故作残忍地拒绝了三叶，宁愿她嫁作他人妇，也要三叶幸福。樱满集＆楪祈《罪恶王冠》少年樱满集抱着抑郁的心境，对世间冷眼相待。与同学们保持着一定距离，过着闲散平静的日子。集安稳的日常在某天突然结束。在自己中意的地方和一名少女不期而遇。少女名为楪祈。她正是集所憧憬，并在网上有着巨大影响力的歌手。而她也有不为人知的一面。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10445', '0', '朽木露琪亚和阿散井恋次的婚礼细节', '作为日本三大民工动漫之一，《死神》陪伴了我们很多年，虽然这部漫画最终草草收场，堪称烂尾，但不能否认它曾在很长一段时间里带给我们诸多的乐趣。漫画结尾朽木露琪亚嫁给了阿散井恋次，黑崎一护娶了井上织姬，但具体细节并没有多说，笔者找了找死神的官方小说，找到了些关于恋次露琪亚的婚礼的细节，这里分享给大家。文章转自死神中文网BLEACH官方小说结局。这本书里的死神婚姻申请设定。1.居于静灵庭者的婚姻，应当在人别录管理局（七番区）提出婚姻申请书2.护庭十三队队员的婚姻，应当在护庭队士录入管理局提出队员婚姻申请书。3.席官以上的婚姻，应当在高次灵位管理局（六番区）提出高灵婚姻申请书。4.贵族婚姻，应当在贵族会议（中央一番区）提出贵族婚姻申请书。5.四大贵族婚姻，应当在金印贵族会议（中央一番区）、夫妇同时在场的情况下，由户主提出婚姻允许状。6.在队长会议上向所有队长和副队长报告。露琪亚第一个告诉了井上结婚的消息，说第一个告诉她是因为她是她最好的朋友。井上和莉露卡商量要给露琪亚准备结婚礼物。最后决定是婚礼上用的那种面纱，我不知道该怎么形容。总之为了这个礼物，井上邀请了茶渡、雨龙、游子夏梨、一心一护、水色、浦原商店所有人、志波空鹤、罗武、白哉的管家等人给露琪亚一起做。1.井上高中毕业后当了【ABCookies】这个卖蛋糕和面包的店的店员。乱菊和碎蜂经常去买吃的（碎蜂是给夜一买的）。雨龙是上了医大，一护问他上医大不是会花钱吗，他很稳重地回答说钱是父亲出的，之前他总是考虑一些没用的事，现在和父亲的关系改善了。一护应该是上了大学，但没提到大学名字，只是顺口说了一句他下课回家了。2.九席以上的席官会在队舍内有自己的房间。大部分人会在那里起居，然而住在队舍外的人也会拥有对应等级的住处。三席为止的是集合住宅的一室，队长和副队长是可以称为“宅邸”的巨大的住宅。恋次一直是住队舍，露琪亚是住朽木家的。他们结婚后要住队舍。再补充一点婚礼相关吧。恋露是突然在和乱菊修兵他们聚会的时候宣布要结婚的。然而他们没打算举行婚礼。被以乱菊为首的死神们集体谴责最终还是屈服了。乱菊他们还兴高采烈地去准备定婚礼上穿的和服。。在队长会议上宣布要结婚的时候，看了一圈居然大部分人都知道了（因为恋露要跑入籍手续，经过了很多番队）。京乐问婚礼准备的时候，恋露都惊呆了，因为他们根本没安排婚礼。是白哉已经通知了所有人婚礼的时间。他说他当初和绯真的婚礼就很麻烦，知道恋露肯定搞不清。顺便婚礼、婚服、居酒屋之类都是朽木家准备的。露琪亚虽然入籍，在户口上是阿散井露琪亚了，但是她坚持平时要用朽木姓。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10446', '0', '重温来时路—暴力萝莉？朽木露琪亚', ' 身高144cm,体重33公斤，眼睛的大小占据了脸的一半，喜欢兔子娃娃和白汤圆……不千万不要被这副萝莉可爱的外形欺骗，朽木露琪亚的另一面是不折不扣的暴力女王。​​让小编带大家看看暴力萝莉露琪亚的犯罪史！犯罪记录一犯罪地点：南流魂街78区“戌吊” 犯罪时间：与阿散井恋次相识之初 受害人：流魂街居民A 犯罪记录：一脚绊倒追打小偷恋次等人的居民A使其丧失战斗力，然后和恋次等人溜之大吉。在这次犯罪中露琪亚“暴力”性格崭露头角，以逸待劳、以弱胜强、乘其不备、攻其下盘。​犯罪记录二犯罪地点：空座町黑崎家，黑崎一护卧室 犯罪时间：工作中看见了死神的一护 受害人：黑崎一护 犯罪记录：用“鬼道”中的缚道捆住一护， 然后拿笔在他脸上画胡子，最后再捅了一护一刀，使他半死不活灵魂出窍······不，变身成死神。从此做牛做马任劳任怨。​犯罪记录三犯罪地点：空座町第一高中 犯罪时间：课间休息时间 受害人：黑崎一护 犯罪记录：众目睽睽之下一拳打在一护肚子上，然后露出甜美的笑容当众将其拖走。带到荒无人烟之处再一巴掌拍在他额头上，打的一护灵魂出窍，然后做监工状督促未成年人一护打“兼职死神”这种没有薪水还异常危险的黑工。​犯罪记录四犯罪地点：清原商店外 犯罪时间：以为传令神机坏了而去找浦原喜助修理的时候 受害人： 握菱铁斋 犯罪记录：明明是有求于人，却毫不客气的一脚踢在身高大约2米的铁斋大叔屁股上然后因为其屁股太硬踢痛了自己的脚……真是自作孽，不可活。​犯罪记录五犯罪地点 : 黑崎家、厕所。犯罪时间 : 决心离开黑崎家之后受害人：魂 犯罪记录 : 把魂所附体的小狮子娃娃绑在马桶后面，并用胶布封住它的嘴，让人产生SM的不良联想。害它陪黑崎一心大叔如厕数次，被一护发现后又因为身上太臭遭到了歧视待遇。人物小结被​尸魂界四大贵族之一的朽木家族收养，成为朽木白哉的义妹。人们传闻她是公主般的人物，高傲聪明，争人羡慕的境遇似乎不能使她感到满足，以至于跨越尸魂界来到人间追求快乐。她的首次出场如地狱冥王般无声无息，别有一番冷冽的风情。跟着小编重温来时之路，回忆动漫曾带来的美好回忆。点关注不迷路！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10447', '0', '死神：全方位分析主角小队', '黑崎一护—头发是橘色(天生)，瞳孔棕色，一护除了缺少阳光般的开朗性格外，经常因为天生怪异的发色而被人误认为是不良少年，因为有着痛苦的过去以及因为父亲而锻炼出来的强大打斗能力，完全无谋或出于本能的作战法则。富有同情心，责任心极强，还是个运动万能、品学兼优、内心纯洁的好孩子。朽木露琪亚—尸魂界十三番队副队长，她赋予了男主角黑崎一护死神的力量。她是一位天生丽质、身材娇小、洋溢高雅气的美少女，从小就有出众的灵力，鬼道成绩相当优秀，但是缺乏剑道天分。不习惯穿紧身的衣服，爱吃黄瓜和白汤圆，是个很可爱、美丽的女孩。井上织姬—性格单纯可爱、天然呆又无厘头，总有很多奇思异想，是一个能给人带来轻松和笑声的角色。能力是头上发夹中的“盾舜六花”，通常用做防御术和修复术，因为她的存在，才能使黑崎一护一行人很快从战斗中恢复。石田雨龙—继承祖父职业成为灭却师，与主角黑崎一护是对手，后成为好友。身材高挑纤瘦，戴着眼镜，平时性格较冷，但跟朋友在一起时完全不同，吐槽能力暴强，不过无论何时，都是黑崎组里头脑最冷静，眼光最锐利的一个，也是成员中的智囊。灵力很强大，主要依靠右手的灵弓作为武器。茶渡泰虎—平时沉默寡言，性情温和，拥有强大的体魄和实力，为朋友可以奋不顾身，是打不死的男子汉。褐色肌肤，留着卷曲的褐色短发，褐色的眼睛经常被刘海覆盖，左肩有一枚被蛇缠绕的双翼心刺青。阿散井恋次—护廷十三番之六番队副队长，女主角朽木露琪亚青梅竹马的好伙伴，独自练成卍解，实力非凡，性格豁达，样貌刚毅，。由于出身流魂街，脾气虽然不是很好，但骨子里却充满了人情味，是位兄长类型的人。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10448', '0', '《死神》相比于井上织姬我总觉得朽木露琪亚更适合一护，最佳cp', '看过死神的应该都不太看好井上织姬吧？前期还行，但是到了后期什么情况？尤其是后来井上织姬救活了黑崎一护敌人的行为，满满的圣母婊有没有，这也不怪那么多人不看好她，能力不行，还拖后腿，每次都要救她，这里我真的想要质疑黑崎一护的眼光了！唉，所以想想人家朽木露琪亚多好，要颜值有颜值，要实力有实力，性格果断，多好的女孩，不得不说黑崎一护没有得到她真的是损失啊！​​​​​​​​不过有个行为我还是比较佩服黑崎一护的，那就是在朽木露琪亚和恋次在一起后，黑崎一护还是和朽木露琪亚保持着暧昧，而且还是极尽暧昧，牛人啊！这样倒是有点像宋一护,马木露琪亚,王恋次啊！到底是主角！这光环666你们觉得谁更适合黑崎一护呢？', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10449', '0', '一露破灭：死神中为什么一护不选择露琪亚，而选择织姬作为老婆！', '作为一个“一露”党，当我知道一护的老婆是井上织姬的时候，我的内心是拒绝的，并说了句“MMP！”死神《BLEACH》（原意“漂白”，片假名：ブリーチ）是久保带人创作，2001年开始连载于集英社旗下《周刊少年JUMP》上的漫画，于2016年8月22日发行的《周刊少年JUMP》38号中完结。内容简介故事的主角黑崎一护是个看似暴力、单薄，实质上善良、勇敢、爱护家庭的少年，并且拥有能看见灵的体质。家里有一个开诊所的老爸和两个性格正常的妹妹夏梨和游子，一护每天七点必须按时回家,否则老爸便会使用“身体语言教训”的家规。吵闹的父子，懂事的妹妹以及与其他普通人并无大异的普通生活，直到女死神朽木露琪亚被他一脚踢到墙角并满脸惊疑地望着他问“你能看见我？”时漫画的序幕才这样被正式地揭开。 从此黑崎一护身边所有的事物都产生了翻天覆地的变化。吐槽小编追的是动漫版《死神》，所以看完动漫版后，就不知道剧情下文了，结果某一天无意间看到有人在吐槽《死神》逆他CP了，一护和织姬在一起了，露琪亚和恋次在一起了，小编第一反应是“假的！假的！都是假的！一护是露琪亚的！”然后跑去百度搜索相关信息，结果......千万个羊驼跑过......可能是小编看的是动漫版，后面剧情跟不上时代的步伐了，为什么一护会和织姬在一起，而不是和露琪亚在一起？就算他们一个是现世的人，一个是尸魂界的人，可一护的老爸黑崎一心和黑崎真咲不也在一起了吗？？而且光看剧情，露琪亚的戏份也比织姬多很多啊，直接把露琪亚从女一变成了女二，用现在比较流行的网络词来形容 ！这简直就是在“溜粉”！不是说恋次和织姬不好，但从一开始的动漫剧情来看！一护就和露琪亚感觉是一对！结果到了漫画剧情最后，告诉我，站错了CP！也看了很多网友的分析，但是还是不能接受“一露”最后没有在一起的结局！唉，以上吐槽，为个人意见吐槽，不喜勿喷！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10450', '0', '死神里两把冰雪系斩魄刀，一把最强，一把最美！', '死神是和海贼王，火影忍者共同称之为三大民工动漫的作品，自从动漫开播之后一直广受好评，吸引了大量粉丝的喜爱。不过作为三大民工动漫之一的死神，也是他们之中完结的最早的一部。死神里的斩魄刀千奇百怪，各种系列的都有，但要单论冰雪系的话，那么一护前期的领路人，女主角朽木露琪亚的那把冰属性的斩魄刀“袖白雪”就绝对是榜上有名的。无论是斩魄刀柔美的外观还是斩魄刀解放的能力都算得上美丽的，特别是袖白雪始解之后通体雪白的模样，再加上刀柄处的一条银白色飘带，无不是更添一份美丽。在斩魄刀异闻篇中，袖白雪实体化之后的样子也非常惊艳的，一头雪白色的长发，一身白色和服。不愧被喻为尸魂界最美斩魄刀，不过因为被村正斩魄刀实体化之后洗脑控制住了，所以对露琪亚十分仇视，想要杀掉露琪亚。不过说到冰系斩魄刀那就还有另外一把实力强大的斩魄刀了。十番队队长日番谷冬狮郎的斩魄刀“冰轮丸”可以算的上是尸魂界冰属性最强斩魂刀也不为过。但要论美丽的话，冰轮丸显然是没法和袖白雪相比的，毕竟一把被称之为是尸魂界冰属性最强斩魄刀，一把被喻为是有着最美之称的斩魄刀。卍解之后的冰轮丸（大红莲冰轮丸），卍解之后会出现一对完全是由冰组成的冰霜之翼，因为小白本身也是一位天才，年纪轻轻就成就了队长之位，但也正因为是最年轻的队长，所以卍解不是很成熟，并不是最完全状态。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10451', '0', '我的启蒙漫画书如此优秀！经历那个年代九年义务教育的人真幸运！', '从最初看的中国美术出版社的连环画，到国内的四格漫画单行本或杂志，再到初次接触日本漫画。感谢那个年代的那些神作，陪伴我们度过了美好的青少年时期。而初次接触漫画就是现在乃至后世被称为经典的作品，我们无疑是非常幸运的。而这其中，给我最大震撼的就是日漫了，《游戏王》《海贼王》《死神》……《游戏王》《游戏王》当时对我的影响真的很大，甚至到现在，我印象中记忆最深的动漫依旧是它。尽管后面的动画续作不尽人意，但我每一季都在追看。因为实在太喜欢里面的故事以及那个卡牌的世界，还记得当时我都是省下零花钱来买一包包卡牌的。尽管在现在看来那些卡牌都是假的，但现在还保留着，记忆中的战斗与经典卡组是记忆中不可磨灭的痕迹。冥界大门已经开启，踏上成为游戏王的道路吧！说起漫画，都是黑白的内容，但也被彩色封面的游戏（法老王）帅了一脸，然后从开篇的千年神器、黑暗游戏开始，让我看的津津有味、不忍释卷。尤其是战斗时刻的激情热血、神抽后绝地反击的时刻，真是爽！游戏、海马、城之内……一个个人物鲜活的被刻画出来，那个世界是如此的精彩，当然还有作者高桥和希了，感谢您为我们带来这么精彩的作品。可惜那时漫画没看完，但后来借了DVD，把完整的故事看完后。真有种荡气回肠的感觉……就是恋恋不舍法老王的背影啊！《犬夜叉》一边撒狗粮一边又虐的你无语，但又忍不住看下去！画面唯美且兼具日本传统和风风格，使人光看画面就有种淡淡的悲伤感，但马上就陷进去了。高桥留美子，每次看到封面上的这个名字，都想吐槽一万遍，令人纠结的故事还在继续（当然现在已经完结了）。桔梗桔梗、戈薇、犬夜叉、杀生丸……一个四魂之玉的故事，一段穿越灵魂的人与妖的爱情。纯洁、守护、奉献，这是我从他们的故事中看到的感情！战国的邂逅，穿越时空的爱恋。《海贼王》草帽路飞的故事还在继续，他陪伴着我们长大，也热血了一路。作为国内知名度很高的动漫，路飞的名字及周边大多数人都认识吧。《one piece》的世界，无数精彩的人物，这不是路飞一个人的征途，这是海贼们共同的理想。《死神》设定及世界观是最让我惊艳的，斩魄刀、鬼道、虚化；尸魂界、断界……作者描绘的这个世界很完整，很有魅力。我到现在都没去看结局，因为我害怕这个故事的完结。黑崎一护朽木露琪亚当然，还有朽木露琪亚女神，她的品格如梅般傲然，如阳光般温暖明媚。看过开篇一直以为是女主角的，结果后面的发展……虽然尊重作者的想法，但我还是有点接受不了她和黑崎一护没在一起……再念叨一句“朽木露琪亚是我女神！”《高达》看的第一部机甲类热血战斗动漫，前期的那几部在我看来可以算最好的机甲类动漫了。黑白漫画给人的肃杀感更强，但当时看的不多，燃爆的剧情还是有印象的。一部部经典作品的落幕，一场场悲欢离合的演绎，那个时代一去不复返了。或许这个年代也会有经典诞生，但也没有那种感觉了。而且，在这个难以平静下来的年代，我们接触的宅系卖腐动漫越来越多，看多了不仅对身体不好，也会真的让人怀疑啊！画面是越来越美了，可惜……', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10452', '0', '《死神》真人版，你对还原度有信心吗？', 'Jump 周年之际，曾经挑大梁的《死神Bleach》的真人版也要上映啦！《死神Bleach》与2016年8月完结，届时已经陪伴了它的观众们15年之久。很多观众都是看着它长大的，这么说也不为过。故事的主角黑崎一护是个看似暴力、单薄，实质上善良、勇敢、爱护家庭的少年，并且拥有能看见灵的体质。吵闹的父子，懂事的妹妹以及与其他普通人并无大异的普通生活，直到女死神朽木露琪亚被他一脚踢到墙角并满脸惊疑地望着他问“你能看见我？”时漫画的序幕才这样被正式地揭开。 从此黑崎一护身边所有的事物都产生了翻天覆地的变化。真人版的导演佐藤信介也是对这种题材信手拈来，他曾制作过《死亡笔记》新旧两版，《请叫我英雄》《图书馆心事》等风格强烈的作品。这次的真人版《死神》，也是牟足了劲儿，为了给大家的童年一个交代。真人版《死神》饰演黑崎一护的是福士苍汰小编觉得他冷峻清晰的线条还是很适合黑崎一护的角色的。备受关注的另一个角色：朽木露琪亚，则是由杉咲花饰演。说实话她的露琪亚扮相不太容易想象呢。石原贵雅饰演朽木白哉吉泽亮饰石田雨龙啊，不，应该是这张图更是有全日本国宝级的女形演员早乙女太一出演阿散井恋次。虽然阵容豪华，但网友们仍然充满担忧，究竟真人版能不能满足观众们的期待呢？', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10453', '0', '虚还原度惊人！《死神》真人电影将上线！', '最近《死神》官方公布了三段新预告，视频中都是男主角黑崎一护变身为死神代理后与恶灵——虚展开战斗的场景。虚的造型看上去十分还原动画，相比会获得不少原著粉丝们的认可。在电影中，福士苍汰将饰演黑崎一护、杉咲花将饰演朽木露琪亚、吉泽亮将饰演石田雨龙、Miyavi将饰演朽木白哉、早乙女太一将饰演阿散井恋次、长泽雅美将饰演黑崎真咲、江口洋介将饰演黑崎一心、田边诚一将饰演浦原喜助、真野惠里菜将饰演井上织姬、小柳友将饰演茶渡泰虎。《死神》真人电影将于2018年7月20日在岛国上映，对该作感兴趣的网友们可以关注一下。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10454', '0', '盘点死神中最美的斩魄刀', '《BLEACH》女主角朽木露琪亚的斩魄刀，袖白雪为冰雪系的斩魄刀。持有者为现任护廷十三队十三番队队长，朽木白哉的义妹朽木露琪亚。露琪亚的斩魄刀被称为最美的斩魄刀始解的时候，通体白色。全刀连同刀身、护手、刀柄等全变作雪白，上有花纹，手柄上系有洁白缎带。始解招式为初舞·月白、次舞·白涟、叁舞·白刃以及类似于白涟的绝招树白。实体化为穿着白色长振袖和服的女士，拥有雪白色的长发，蓝色的眼眸，在头的右上方带着一块花星形的发簪，手指可以发出冰雪来攻击，武器为自身斩魄刀化始解，并且也可发出自身原斩魄刀化始解的招数，后被朽木白哉折断。 技能-绝对零度：将自己的体温降至绝对零度（-273.15℃），挥刀砍向敌人的身躯，被此技击中的敌人，身体将会瞬间结冻，最终失温冻毙。朽木露琪亚维持绝对零度状态的极限是4秒，稍微超过4秒就会造成身体的损伤。化为绝对零度的身体，将随温度的上升恢复原状。因为解除时不逐渐进行就会损害身体组成，被朽木露琪亚自己称为麻烦的能力。袖白雪的卍解名为“白霞罚”。天空会出现大型的圆盘状洁白光辉，然后由地面窜出一道光束贯穿天空的光盘，同时地面也会浮现宛若巨型波涛般汹涌舞动的皎洁光波，将攻击范围内的所有事物全数冻结，受到光波触及的敌人将会全身结冻，最终同碎冰凋零粉碎，可使用负273.15度绝对零度，将自己的体温降至绝对零度，挥刀砍向敌人时可迅速冻结对手，缺点是自己也只有4秒的活动时间不然也会被冻伤。卍解自损，极难掌控，故被朽木白哉评为“虽然优秀，但是使用难度甚高，只要有半步失误就会丧命的危险卍解”。朽木露琪亚也凭此卍解将无形帝国麾下，星十字骑士团代号“F”的灭却师------艾斯·诺特斩杀。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10455', '0', '动漫世界中五大战斗力爆表的战斗女神（2）', '哈喽，大家好，今天小恬继续跟大家推荐五位动漫中战力爆表的女神。1、朽木露琪亚《死神》女主，出身于南流魂街78区 ，生活艰难但心性坚强，善良重情义，她的品格也如梅般傲然，如阳光般温暖明媚，正如她的名字寓意来源于光明一般。露琪亚擅长鬼道，其所持有的斩魂刀名为“袖白雪”，是目前尸魂界最美的斩魄刀。前期的露琪亚小编感觉并不是太强，只是有女主角光环的加持，不过在与第九刃破面的战斗中领悟了“三舞：白刀”，战力才有了突破。而在与星十字骑士团 艾斯 · 诺特的对决中开启卍解“白霞罚”后，实力一举晋升为队长级别的。露琪亚和斩魄刀都发出炫目的白光，光柱笼罩于周围几百里天地之间，是压倒一切的纯白色，在白霞罚白光笼罩范围内的敌人（包括露琪亚本人）、环境的温度都会迅速下降到最低点，瞬间就可将敌人绝对冻结，继而碎成冰砾凋零而死。连其哥哥朽木白哉都将白霞罚称之为“ 了不起的卍解 ”。2、人造人18号18号真人COS动漫《七龙珠》中的角色，人造人18号本名拉姿丽，与人造人17号拉彼斯是孪生姐弟，原本是个美丽、正常的地球女人，后来与孪生弟弟17号一同被格罗博士改造为人造人。贝吉塔等人追到研究所时被格罗博士放出来 。在沙鲁被消灭后因小林的两次救命之恩被感动嫁给了小林，过上了平静的生活，有个女儿玛伦。在未来的特兰克斯所属的时空里，17号和18号性格非常邪恶，到处杀人破坏，并杀死了贝吉塔、比克、孙悟饭等Z战士，可见他们的实力是非常强大，在《龙珠》正篇中刚出场就击败了超级赛亚人状态的贝吉塔及特兰克斯。虽然《龙珠》系列的人物一个比一个强，但在女性角色中，18号的战力应该毫无疑问的处于最顶端。3、纲手出自热血动漫《火影忍者》，纲手是火之国木叶隐村的第五代火影，初代火影千手柱间及其妻子漩涡水户的孙女，也是传说中的“三忍”中的一点红，最重要的人是恋人加藤断与弟弟绳树。擅长体术和医疗忍术。受自来也的请求，回村就任第五代火影。在后来的佩恩袭击等事件中保护着村子。在后来的第四次忍界大战中担任忍者联军总参谋，发挥着重大作用。纲手是一个精通医疗忍术的女忍者，擅长体术和医疗忍术，拥有毙命一击的“怪力”（将大量查克拉精确细致地提炼出来并迅速把其燃烧），在医疗忍术方面技术非常高超，堪称《火影忍者》里的“医圣”。第四次忍界大战如果没有纲手，估计五影要被团灭N次了。（火影里妹子也不少，为什么小编我会选这位“老奶奶”）4、三笠.阿克曼《进击的巨人》女主角，总是摆着八块腹肌，与艾伦和阿明是青梅竹马，父母在拐卖人口的事件中被杀害，险些自己也被贩卖。为了帮助艾伦打倒劫匪觉醒了自己战斗民族的血液(乱说的)，视艾伦为生命的全部。为了保护艾伦，一直阻止他加入调查兵团，但知道自己无法阻止时，决定跟他一起加入并下定决心要保护他。只要是为了艾伦，不管是巨人还是人类，全部都会杀掉。三笠以第104期训练兵团第1名的身份毕业，长官评价其：“完全掌握了所有的科目，在历届中也是史无前例的天才”。拥有优秀的身体能力与战斗技术，一人的表现就足以与100名士兵匹敌。5、伊卡洛斯《天降之物》中的女主之一，型号α，自称是\"娱乐用万能天使\"，其实是\"战略用万能天使\"，西纳普斯称她为\"空之女王\"。西纳普斯人畏惧其强大的能力而被封印，后被代达罗斯解开锁链后坠入地面与樱井智树邂逅并开启铭刻。有着强大的自我修复能力，在受到攻击遭损坏后能快速恢复，特别是进入\"空之女王\"模式后。拥有永久追尾空对空导弹、绝对防御圈、可变式羽翼系统、最终兵器阿波罗等强大等能力。好了，本期就为大家介绍到这里，大家心目中的战斗女神又有谁呢?欢迎在评论区中留言哦，我们下期再见。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10456', '0', '死神真人电影杉咲花出演朽木露琪亚 7月20日上映', '杉咲花这周官方正式报道了还公布了杉咲花露琪亚的死神装和校服装的和在一起的定妆照。『死神』真人电影将在7月20日上映。朽木露琪亚朽木露琪亚的动画人设，总感觉真人版的露琪亚有点老气和土气监督：佐藤信介原作：「BLEACH」久保带人（集英社「週刊少年ジャンプ」連載）配给：华纳兄弟映画黑崎一护：福士苍汰朽木露琪亚：杉咲花福士苍汰饰演的男主角黑崎一护福士苍汰饰演的男主角黑崎一护', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10457', '0', '盘点死神中人气最高的四人，一护倒数，朽木白哉仅排第二！', '4，一护黑骑一护，是死神动漫中的男主角，原本只是一名普通的高中生，但是因为能够看到虚而之后就成为一名优秀的死神，真是身份是死神与灭却师的孩子，拥有超强的实力与天赋，很多人对月牙天冲想必记忆犹新。3，剑八更木剑八，是死神中的人物之一，也是静灵庭中的死神队长，拥有非常强大的灵力与实力，而剑八是尸魂界中最强的战士的称号，而更木剑八不仅实力强大，并且非常喜欢战斗与对手厮杀，非常简单冲动、暴力。2，朽木白哉朽木白哉，是死神中的人物之一，也是静灵庭的队长之一，拥有非常英俊帅气的脸庞，为人非常稳重寡言，是四大贵族朽木家族的当家，也是露琪亚的哥哥，表面看似非常冷酷，其实内心非常温柔细腻，非常爱自己的妹妹。1，蓝染蓝染，是死神中的人物之一，也是人气非常高的人物，曾是静灵庭的死神队长，最后得到崩玉后逃离到虚圈，组建了自己的军队十刃，最终被一护所打败，虽然身为反派，但是蓝染并且超强实力与缜密的思维以及自己的人格魅力得到观众的喜爱。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10458', '0', '死神，朽木白哉第一次出手震住了多少人？', '标题“死神，朽木白哉第一次出手震住了多少人？”朽木白哉是护庭十三队之六番队队长，四大贵族之一朽木家的第二十八代当家，同时也是十三番队队长朽木露琪亚的义兄，实际上是姐夫。总是冷静彻底思考问题的他，实际上，是非常重视感情的人。白哉第一次出场是和恋次一起到现世捉拿私自把死神力量传给人类的露琪亚，这其中受到黑崎一护的阻拦，由于不是在尸魂界，白哉和恋次只有平时十分之一的力量，尽管如此还是碾压一护。正常状态的一护不是恋次的对手，但是暴走状态下差点杀了当时只有十分之一力量的恋次，不过被白哉用手掰断了斩魄刀！接着白哉一个错身把一护给秒了说了一句话“迟钝，连倒下都这么迟钝”虽然看不见他出招，但是这一下震撼了很多人，不愧是队长的实力，一句普通的话听起来都那么霸气虽说不是主角，其粉丝完全不少与一护', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10459', '0', '折纸樱花 专属于朽木白哉、绯樱娴的美', '【中关村在线办公打印频道原创】樱花，是美是浪漫，你想到谁......我想到的是，死神中的朽木白哉， 当随着一声“散落吧！千本樱”，斩魂刀直落消失，两侧出现无数刀刃，一时间刀刃起飞散落，如千朵凋零的樱花攻向敌人，让人震撼发声：原来，伤人也可以这样美！朽木白哉我想到的是，吸血鬼骑士中的绯樱娴，那个被称为狂笑姬的女子，她的一生也如樱花般绝美。当玖兰杀死她的那一刻，她风华绝代，一脸平静，告别痛苦了，她笑，光束将她环绕，是那样的美！ 绯樱娴折纸樱花教程：将彩纸裁切成小正方形大小根据自己要求定对角对折将右角沿中间位置翻折左角同样沿中间位置对折右侧沿中间位置向下翻折左角同样翻折如图沿最终翻折折痕向内撑起同上将余出的角翻折过来沿中间位置向内翻折同上用胶水粘连两侧集合5个花瓣就可以召唤樱花了美美哒樱花~王小胖折纸时代二维码王小胖折纸微信公众号开通了，每周已更新，更多好玩的，等你加入。搜索微信昵称：王小胖折纸时代', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10460', '0', '死神：战斗力不甚出彩的四位队长，但他们的能力与技能相对丰富！', '1、涅兰利基础能力较差，斩魄刀也比较一般：并没有很高的速度与力量，因为始解的命中率很成问题，而且卍解的大虫子防御和速度都很低，容易被秒杀，毒气看来效果也略微一般。优势是顽强不死的生命力和事先对对手的分析能力。只要对对手有所准备和研究，捏就没有胜不了的对手。2、朽木白哉鬼道、瞬步、攻击都比较平均的队长，各项实力都比较不错但又没有太出彩的特长。千本樱的攻击一般，能力不是很出色，对卍解依赖程度较高。3、凤桥楼十郎音乐家打扮的三番队长能力展现不多，但金沙罗的技能似乎还是很丰富的，推断其属于声波系或者远程近距离攻击皆可的多功能武器。而且掌握的假面，并属于百年前的队长，能力应该不错。4、六车拳西与凤桥同时代的假面队长，断地风疑似风系攻击，因为物理和法术攻击应该都不弱。再加上假面的能力，能力似乎不差，就是掉线时间过长。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10461', '0', '朽木白哉外貌进化演变史！', '成为灵体1', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10462', '0', '《死神》揭秘：朽木白哉不会笑的真正原因', '了解死神的同学都应该知道护庭十三队的颜值担当是六番队的朽木白哉，但是这个队长万年一个寒冰脸，喜怒哀乐都是一个表情，简直让猫叔怀疑他就是个面瘫（如果中国来拍死神的大电影，朽木白哉这个角色非霍建华莫属)。这件事一直困扰着猫叔，就像卡卡西到底长什么样子一样困扰着猫叔，不过最近有网友解答了这个问题，猫叔只能说两个字，完美！如果换成猫叔，猫叔一样笑不出来。朽木白哉：久保带人，你给我出来，我保证不用卍解弄死你！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10463', '0', '《灵魂撕裂》首充福利朽木白哉介绍', '众所周知，《死神》中实力与颜值并重的六番队队长朽木白哉一直都是广受欢迎的人气角色，其专属卍解更是“千本樱景严”攻防一体，威力十足的完美技能。而在死神题材的横版格斗手游《灵魂撕裂》中，朽木白哉还是首充即可获得的福利角色，那么这位入手难度极低的贵族队长在游戏中的实力如何呢？让我们一起了解一下吧。超越“极”限的队长级角色在《灵魂撕裂》中，朽木白哉属于队长级角色，角色类型为“极”。作为队长级死神，朽木白哉的攻击成长较低，但是生命、防御、耐力等属性成长都十分优秀，在自动模式下刷副本，或者作为竞技场前排肉盾都很好用。前期朽木白哉由于队长级出色的基础属性，配合“强者的自信”、“灵力强化”、“高雅”等强力鬼道可以在战斗中获得明显优势，在各种场合里都能起到不错的效果，属于万金油式的角色。攻防一体的大范围技能与《死神》中一样，在游戏中朽木白哉也将使用千本樱作为武器，技能有“走·千本樱”、“落·千本樱”和“散·千本樱”，其中“散·千本樱”还可以通过角色成长进化为白哉的招牌卍解“千本樱景严”。白哉技能的特色就是攻防一体且攻击距离极长，可以在自身保持安全的前提下持续消耗敌人血量。特别是在解锁卍解后，千本樱景严自带的无敌效果还将进一步提升战斗中的容错率，让这位贵族队长获得一场又一场优雅的胜利。操作简单易入手 推荐新手玩家使用由于朽木白哉的普攻和技能范围都比较大，自身防御属性也十分优秀，所以不需要太多操作也能打出很好的效果，非常适合刚进入游戏的新手玩家使用。并且白哉的碎片除了首充获得之外，还可以通过搜魂、商店、各类副本等多种途径入手，解锁难度也非常之低，对这位贵族队长感兴趣的玩家不妨入手体验一下吧。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10464', '0', '死神：黑崎一护的全部形态，最后的帅到炸裂！', '黑崎一护是《死神》中的男主角，是灭却师与死神结合生下的后代，体内同时拥有死神、灭却师以及虚的多种力量。黑崎一护的形态与斩魄刀的形态息息相关，让我们都来看看他都有什么形态吧！普通状态普通状态是黑崎一护的日常形态，黑崎一护的头发是橘色，瞳孔棕色，初登场为高中1年级生。黑崎一护是个灵力很强的高中生，时常能够看到灵体并为此苦恼不已，一直希望能过平凡的高中生活。 2.白一护始解 白一户不仅仅是侵蚀一户的本体还依旧使用一户死神力量的源泉斩月，其实白一户是死神力量和虚的有机结合体。 3.地狱形态 剧场版《死神：地狱篇》登场，地狱为了阻止黑刀逃跑，于是通过地狱之意将力量借给一护。融合地狱力量后，一护的身体和脸被金色的骷髅盔甲覆盖，地狱之意不会对一护发动攻击，但若解除地狱融合型态，则会被地狱之意视为敌人。 变身要求：地狱为防止黑刀从地狱逃走，将地狱灵魂之力给予了黑崎一护让他变成了全是金色骷髅的形态 4.白一护卍解 5.牛头面具白一护 6.完现术状态 完现术完成状态：紧握代理证时，代理证的侧边会出现短刀身与刀柄，变成一把短剑。将短剑收入剑鞘时，代理证上的交叉骷髅图案，会变成压着两条粗黑线与四枚黑色小三角形的黑色叉型。一护可借着挥动短剑施展“月牙天冲”。 7.半虚化状态 虚化受控制，不会暴走。头发倒竖，头部长出一支尖角，可以使一护的灵压、速度、斩击力、防御力较上一阶段再次大幅度提升，灵压极高。可以使用王虚的闪光。 8.完全虚化状态 虚化完全暴走，完全不受控制。发长过腰，头部长出两支尖角，胸口出现虚洞，脖颈与手腕、脚踝长出红毛，身体瘦长而挺直，总体形态如牛，失去理智，只知道粉碎敌人，可以使用虚闪、响转、超速再生，但不会使用死神的战斗技能（如瞬步、月牙天冲等），双角随便释放的虚闪都比十刃专有的“黑虚闪”更强，这一形态是一护的最强虚化形态，实力甚至超越十刃级破面。 9.无月状态（终极状态） 右手与斩魄刀融合，可以释放撕裂天空、斩断大地的巨大黑色斩击，但只要使用一次就会失去全部的死神之力。一护曾以此技将蓝染斩成两半。练成“最后的月牙天冲”后，右手就会与斩魄刀融合，超出死神范畴数个次元，拥有无人无物可挡的作战能力。 变身要求：需要黑崎一护集中精神修炼“刃禅”在修炼的时候可以和斩月刀进行沟通，在“刃禅”中的黑崎一护可以见到斩月刀里面的他教导练习“最后的月牙天冲”的小黑崎，黑崎一护想和他沟通并且学会“最后的月牙天冲”，不过因为使用了这个终极形态之后黑崎一护就会失去死神的力量，所以小黑崎没有轻易的交给黑崎一护并且暴打了他一顿，最后因为看到了黑崎一护坚定意志而心软告诉了他使用的方法。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10465', '0', '死神，七位巅峰强者，黑崎一护能排第几？', '黑崎一护应该是最特殊的存在，体内同时拥有死神、灭却师以及虚的多种力量，是解救尸魂界与现世、虚圈的英雄，那么在一切完结之时，一护真能被称为最强么？如果并非最强，那该是第几呢？杰拉德瓦尔基里原为灵王的心脏、无形帝国星十字骑士团的成员，友哈巴赫亲卫队成员之一，象征能力为奇迹，朽木白哉、剑八、冬狮郎联手也不能将其杀死，即使原本的肉身已死去，但仍能以灵子汇聚的光雨再度复活。黑崎一护一护是死神与灭却师的后代，通过不断磨砺和觉醒自身的力量，黑崎一护逐渐成为尸魂界的最顶尖的力量，多次拯救尸魂界于危难之际，被尊为尸魂界的英雄，是拥有三种力量的顶级强者。蓝染惣右介曾担任110年前五番队的副队长，原为尸魂界护廷十三队五番队队长兼灵术学院特聘书法老师，后叛离尸魂界逃至虚圈，创造十刃并成为虚圈统治者，是拥有不死之身的存在。哈斯沃德无形帝国灭却师，星十字骑士团最高阶存在，被友哈巴赫赋予圣文字“B”，象征能力为世界调和，是友哈巴赫的贴身护卫，入夜后，能与友哈巴赫交换力量，可以使用其全知全能的能力，可以预见未来。石田雨龙灭却师的嫡传后代，灵力强大，依靠右手的灵弓作为武器，被友哈巴赫指定为继承人，是下一任无形帝国的帝王，在终极一战中帮助一护封印友哈巴赫的力量，使其得以击败友哈巴赫。兵主部一兵卫尸魂界零番队领袖，尸魂界万物的命名者，因此对每位死神的斩魄刀真名了若指掌，以睿智被无形帝国认定为五大特记战力之一，拥有强悍的鬼道能力，曾经重创友哈巴赫，但不敌其全知全能的力量而被击败！友哈巴赫灵王之子，无形帝国创建者、灭却师始祖，号称最强的存在，率领星十字骑士团两次进攻尸魂界，杀害了灵王造成尸魂界、现世和虚圈的逐渐崩坏，拥有知晓一切未来的力量，是死神的终极BOSS。动漫先生公众号；dongmanxiansheng，长按复制，福', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10466', '0', '死神之最强的解，黑崎一护的解只能垫底！', '死神这部热血动漫，虽然早已经完结很多年了，不过好看的情节我们也是记住了他，今天来看看死神之最强的卍解，黑崎一护的卍解只能垫底！NO.1大红莲冰轮丸 冰轮丸本身是十番队的队长，日番谷冬狮郎的佩刀，冰雪系最强的斩魄刀，就是个子很小，经常被两个副队长大胸妹欺负，他的卍解是里面最强的，宛如一条冰龙横空出世，名字叫“大红莲冰轮丸”，解放后会形成一条冰霜巨龙，身后会出现三朵莲花，十二片冰花当花瓣消失时，卍解就失效了，威力很强大，对方妖怪也是分分钟解决的事情！NO2.千本樱.景严静灵庭六番队队长朽木白哉的千本樱的卍解。这个绝招相当之华丽，无数的樱花刃，铺满整个天空，能够随着召唤人的心意控制自动攻击，一共有4种形，一般的角色见不了后面3种就OVER了！除了千本樱景严外还有歼景，终景，吭景！歼景是舍弃防御的进攻法，终景 白帝剑:超华丽的姿态,将所有的力量灌注在剑上,是千本樱景严的终解。外观有点像展翼的鸟形。吭景 千本樱景严：是将千本樱的数亿片刀刃全部凝聚成球状，并将敌人包覆后，再由四面八方进行攻击。NO3. 更木剑八的卍解不晓得名字这个人物就厉害了，本身也是十番队队长之一，卍解释放的能力居然是自己的能力，手上的刀都只是装饰品而已，使出卍解时的剑八全身上下的皮肤会呈现血红色，从额头到眼睛下方有两条黑色的纹路，额头正上方长出两根角，面貌变得如同恶鬼般的姿态。卍解后的野晒跟始解的状态差不多，但野晒的剑刃部分会从中间断裂。虽然卍解下的斩魄刀看起来是断刀，但威力之强大却不能跟始解来比较。NO4. 花天狂骨·枯松心中京乐春水开启卍解时，灵压会波及非常广的范围。在范围内的人会感到寒冷无比，甚至会出现寒气。同时天色也会变灰暗，令人感到黑暗又绝望。范围里的释放者和被攻击者会共同上演四段“表演”。NO5.天锁斩月天锁斩月是斩月的卍解，主角一护的配刀，使出卍解之后刀体缩小，刀身全黑、刀柄黑红、柄的最后有小段铁链伸出，护手是“卍”字形的，同时换装成黑色红里长袍。效果：借由斩魂刀的变小，提高战斗、移动、攻击速度，从而达到超速战斗。威力非常之强大，造型特帅有没有', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10467', '0', '黑崎一护的最强的几种形态，到底哪个最厉害？', '说到一护，就想知道一护的那种形态最厉害。本来想说用崩蓝，有哈，无月，山本比的。不过现在说说一护的先。牛头一护狂暴的牛头解，六亲不认地砍人虐物：牛头一护各种超速虚闪、各种无视瞬步的移动速度，尤其是战斗方式、战斗技巧和攻防转换中的智商都发挥到极致，完全不同于一护那种胡乱破面后丢大招坐等结果，当然也可以说是绝对实力到了逆天的阶段，一切阴谋诡计都是浮云，反正牛头鸡骨最后轻而易举就把归刃后的小乌秒杀了。虽然这个形态很厉害，不过与下面两个比还是差了一点。最后的月牙天冲-无月在得到比大叔力量还猛的卍解后的天锁斩月的力量后，以牺牲自己死神能力为代价，在和斩魄刀人刀合一后，不但变帅了、眼神坚定了、冷酷了、还有就是废话少了等等的情况下，战斗力方面：一刀一座山。在崩蓝傲慢地炫耀自己的二次元的时候，一护能把他直接带到自己想要的战斗场，也没反抗的反应。好吧，且说蓝大大意了，可是接下来一脸鄙视地单手划开蓝染的潶棺又单手接住了蓝大的横斩后，我不淡定了-居然开外挂！然后在崩蓝和崩玉进一步融合的时候，一护淡定的让崩蓝各种贫血虚闪轰炸后突然丢句：你就这点本事吗？！最后就是众迷所知的无月，化身月牙状态的一护出现后崩蓝就被秒掉了！虽然崩蓝没有死，剧情需要，编剧要求做下去啊。双刀一护半完全状态的虚化状态一护释放了“半完全状态的虚化状态”，将变成白色的白色长刀和黑色短刀组合成一体，真正解放后，原本组合的黑白双刀变成拥有凸出的短角，刀身呈现黑色中心的白色剑刃，刀柄末端缀著一条黑色铁链。一护卍解的动作还有锁链让卍解看起来真的像是一把锁，卍解双刀合并的姿势像是上锁的动 。但这次虚化后的一护完全控制了理智，不会暴走并让织姬放心自己还是原来的那个自己。虚化后的一护与友哈巴赫展开了殊死对决，这种形态使一护的灵压、速度、斩击力、防御力较上一阶段再次大幅度提升，灵压极高。可以使用王虚的闪光（黑崎一护在吸收有哈巴赫的灵压后变为半牛头虚状态将月牙天冲和王虚的闪光一起用了出来），但是依旧被友哈巴赫能力破解并且力量被友哈巴赫吸收，并且连同自身的灭却师、虚死神的力量亦被友哈巴赫全数吸收，失去意识不支倒地。综合上面所述，我个人觉得无月是大于半虚化的一户的，因为，无月吊打崩蓝，而半虚化还是抵不过有哈。有哈和崩蓝差不多，因为普通的蓝大都可以和有哈一战，更何况是不同次元的崩蓝。不喜勿喷。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10468', '0', '死神：HQS黑崎一护终极形态，霸气十足', '动漫里的终极形态无月，可以使用“最后的月牙天冲”但是使用之后黑崎一护将会失去死神之力。这座雕像下有黑崎一护四个显眼的字，呃呃呃，设计师是怕黑化后我们认不出来嘛。整体雕像还是霸气十足，完全处于黑化阶段。神态还原度也比较高。出荷的时间是2016年10月份左右，官方的售价为369欧元(约2,864.55RMB，汇率7.76)，尺寸比例1/6，造型师是Muhammet Ay，厂商TSUME。另外第一眼看过去有点像火影忍者里的卡卡西，卡卡西，别以为你头发染成黑色我就不认识了，请回你的频道里去。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10469', '0', '死神——黑崎一护', '死神主人公黑崎一护，是灭却师与死神的混血。所以生下来就拥有死神，灭却师以及虚等力量。虚化再到之后，黑崎一护遇见露琪亚，一次偶然在家人遇到袭击时为救家人从朽木露琪亚那里得到死神之力（其实我觉得应该是觉醒了死神之力吧）虚-白崎一护是黑崎一护体内的一种本能之力。实力强横，经常与黑崎一护唱反调，说他是废物。他的招数是月牙天冲。月牙天冲月牙十字冲是真正的斩月才能施展的技能，是用短刀和长刀一起一后释放相互配合相交成十字形状。黑崎一护的虚化能力，最开始时虚化是假面，面部有三天红纹。最开始出现的都不止黑崎一护自己召唤的。后来在面具的左眼旁又出现了两条红纹之后虚化出现无法控制可以提升黑崎一护灵压，速度，住哪激励，防御力等能力。后面在一次经过进化过后第三次虚面是可以控制的其面具上，在其左眼上面四条红纹左眼下面两条红纹和左下颚有三条红纹。经过假面具团的训练之后可以大幅度提升黑崎一护的速度、灵压、斩击力、防御力等。但是只可以维持11秒。在第四阶段虚化有变换不定有时候会不受控制暴走。并且黑崎一护的左半边脸部几乎都是红纹覆盖。可以再次大幅度提升黑崎一护的速度、灵压、斩击力、防御力等。完全状态虚化：虚化之后是一个只知道攻击、粉碎敌人。可以使用虚王闪光、响转、超速再生等能力。完全虚化后的一护可以徒手将乌尔奇奥的雷霆之枪捏碎，并实力碾压乌尔奇奥。本文不足地方，欢迎指出。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10470', '0', '死神中战力最强前五排名，黑崎一护甚至无法与前三位近身交战', '死神已经将结局写入了尾声，一个酷似一护的少年出现在这个现实世界里，拥有死神能力的他有了小一护的感觉，而最后一团黑气的出现让我们浮想联翩，也许是久保大爷故弄玄虚，又或者是为以后的续篇做铺垫，当然我们更渴望的去相信能有续篇的出现，也希望久保带人能够将死神画出更多的剧情，满足我们这些小小死神漫迷，就像鸟山明似的，一言不合就出个超塞蓝黑悟空全王，超强的脑洞和剧情的延续。现在我们来说一下死神战力的前五排名吧，个人的小小观点，不成真正的排名，但是一护的实力在这真的排不上号了。第五名：蓝染惣右介对于蓝染大家肯定不陌生了，是众多死神迷中最爱的反派角色，其中他的实力无可厚非能拿第五，一护的主角光环是在太强，以永远失去死神身份才将蓝染打败，按正常实力与蓝染对拼，一护无疑会输的很惨，也是蓝染的自大而断送了自己的前程啊，而且最后还是帮助了死神们。第四名：黑崎一护将一护排在第四，其实是我想了很久的，要不要和蓝染的位置对调我也有在想，但是还是排在蓝染的前面好些。在与蓝染的决战之后，一护虽然失去了死神的能力，但是也通过那几个叼毛的“帮助”下重新获得了死神的力量，而且目前还结合虚和死神的力量成为双刀，无疑比之前又强上了一截，但是这些都还是只能虐菜，最后被友哈一碰就倒，碰都碰不上友哈，实力上来说还是不够啊。第三名：山本元柳斎重国尸魂界护廷十三队的创始人，兼初代总队长，兼初代一番队队长，也是真央灵术院的创始人，兼初代校长，在尸魂界拥有绝对权威，他的一生几乎等同于尸魂界的历史。 曾于1000年前率领初代护廷十三队对抗以友哈巴赫为首的灭却师，又曾率领护廷十三队骨干，前往现世对抗以蓝染为首的破面军团，与蓝染交战时中后者计谋失去左臂。在蓝染被封印后，他再度与入侵尸魂界的友哈巴赫交战，战死沙场。本该是一个足以和友哈一对一单挑的爸爸，但却为了迎合剧情的发展和对新的尸魂界有个交代，我们的总队长在这历经千秋万代后终究还是要死，怀念总队长三分钟！第三名副其实！第二名：友哈巴赫拥有全知全能的技能，我在想，居然都全知全能可还打什么，都是超越神的存在的人物（虽然漫画中已经将他的能力写的很夸张了，但是我总觉得还能更夸张点...），也许正是全知全能让友哈变得自大，才会中了蓝染镜像，不然一护也没法拯救三界了。第二是无可厚非的，即使之前和总队长打成个平手，但是他的能力越来越强，而总队长越来越老。在三界面前，瞬间瓦解尸魂界，虚界不过一眨眼，而人界不过是一挥手。还有一个灵魂赋予的挂逼能力。星骑士团的圣文字，雨龙的新力量，艾斯诺特的治愈，都缘于这个能力。但是这个能力厉害之处不在给，还能夺。对大和尚用过的篡夺圣坛，对手下用过的圣别，原理都是这个能力。最屌的是这个技能还能对自己用，在被大和尚一度消除力量时多次重新赋予自己力量。 最后再加上初次进军夺得山本卍解，上灵王宫吸收的灵王，但是，友哈还是输了。第一名：灵王看到这估计有人问了，什么，第一不是大前神？ lz只想说，大前神已经超出三界，这般牛逼人物把他排这我怕他跨越次元墙一颗鼻屎弹死我 言归正传。灵王做为三界之王，绝对是强过灭却师之王友哈的。别看他现在被友哈吞了，其实这时他已经剩不了多少东西了。双手双脚心脏已经确认都没了，而且看V最后剩了个脑袋，还不知道灵王是不是无脑货呢？ 虽然已被肢解，但分裂的器官都是能看出灵王的实力。 左手到了灭却师那边。登场就撸废了夜一的左手，之后把连陨石都能一刀砍爆的剑八的右手也撸的一滴血都不剩，可见灵王平时xx时多强  右手到了死神那边，也就是浮竹。浮竹虽然平时病殃殃的样子，要是没右手救他他已经死了。不过现在除了当肉盾好像没什么卵用…… 心脏也到了灭却师那边。看M现在怎么砍都砍不死反而越来越强，就知道心脏也不是把省油灯（左手那么能撸，心脏差怎么行？ ）。以上就是死神前五排名！其实还有一个更吊的我没有写：（不解释）', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10471', '0', '死神里最帅的人不是黑崎一护，也不是蓝染，居然是他！', '在日本动漫《死神》里有死神、破面等主要人物角色，这些角色都被富涵了丰富的个性和魅力，那么这些角色中哪个角色是你心目中最帅的角色呢？ 1.黑崎一护黑崎一护是动漫《死神》中的男主，是灭却师与死神结合生下的后代，体内同时拥有死神、灭却师以及虚的多种力量。偶然从死神朽木露琪亚那里得到了死神之力，从而开启了死神代理的工作。后来在尸魂界和虚圈拯救同伴于险境，并打败了想要破坏尸魂界安定的蓝染惣右介。在千年血战篇中一护觉醒了自己真正的斩魂刀，靠着同伴们的帮助最终打败了最强敌人友哈巴赫，解救了尸魂界、现世与虚圈。草鹿八千流常用“小一”来称呼黑崎一护。2.蓝染惣右介蓝染是主人公黑崎一护破面篇最主要的敌人，原为尸魂界护廷十三队五番队队长兼灵术学院特聘书法老师，后叛离尸魂界逃至虚圈，创造十刃并成为虚圈统治者。在进攻尸魂界时被黑崎一护重伤后被浦原喜助封印，收押在尸魂界一番队底下最深处的第八狱无间地狱。在千年血战中，友哈巴赫攻破了灵王宫，为了挽回战局，京乐春水又将蓝染从监狱中释放了出来。蓝染惣右介表面上看上去是个经常带着笑容十分温柔男人，但实际上是个为了达到目的而不择手段冷酷无情的男人。3.朽木白哉护庭十三队之六番队队长，四大贵族之一朽木家的第二十八代当家，同时也是十三番队副队长朽木露琪亚的义兄。总是冷静彻底思考问题的他，实际上，是非常重视感情的人。朽木白哉相貌英俊，蓄长发，眼瞳浅紫色，表情望似冷酷，头上及颈上戴有大贵族才有权配戴的发饰“牵星箝”，以及名为“银白风花纱”的颈巾，并习惯在穿着死霸装的时候，配戴白色露指护手。4.更木剑八更木剑八是护庭十三队十一番队队长，也是尸魂界第一位不懂“卍解”的队长。战斗就是他的乐趣，在虚圈时使出双手剑道击败诺伊特拉，后与白哉一起轻松击败牙密。更木剑八面目凶恶，左脸有道伤疤，由于无法感应灵力，于是将长发弄成一小束束，每束都带有能感应灵力的小铃，其灵压在十三位队长中是数一数二的。5.浮竹十四郎护廷十三队十三番队队长暨男性死神协会理事，真央灵术院创校以来第一任队长，文采很好，体弱多病。与京乐春水同为好朋友，两人不仅同是总队长山本元柳斋重国的弟子，更是山本总队长创办真央灵术院以来，最先当上队长的两位学生之一。是护廷十三队里，少数拥有两百年以上资历的队长。6.京乐春水京乐春水，尸魂界护廷十三队中的前任八番队队长，左右手都擅使长刀和短刀。在山本元柳斋重国战死后，接替他总队长一职。京乐春水举止轻浮，行为懒散，讨厌工作，但心思缜密，比任何人都了解人情世故，拥有相当程度的洞察力，一旦战斗时便展露超群绝伦的力量。7.葛力姆乔·贾卡杰克蓝染惣右介旗下破面军团十刃排名中的第六位，他代表的死亡方式是破坏，是黑崎一护破面篇中的主要敌人之一。特征是浅蓝色短发，天蓝色双眼，额头前方垂著几根刘海，留着湖水绿眼影纹，性情急躁、残暴，对于自身的实力相当有自信，认为所有看不起他的对象都应该被杀掉，不过对于杀死毫无抵抗力的弱者似乎并没有什么兴趣。8.乌尔奇奥拉·西法乌尔奇奥拉·西法，蓝染惣右介麾下破面军团中十刃的第4刃，代表的死亡方式是虚无。尔奇奥拉·西法拥有强大的力量，是黑崎一护在破面篇中的主要敌人之一。与黑崎一护有数次交战，最终在虚夜宫天盖之战中落败并死亡。个性沉着而冷酷，鲜有表情变化，经常在走路的时候，将双手放在裤裙两侧的口袋里。你觉得那个是最帅的呢，赶快在文章内容下点击回复吧！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10472', '0', '说到死神，我只服蓝染一人', '作为死神前期的大BOSS，蓝染惣右介的形象可以说是死神中最受欢迎的反派，而不得不承认，作者在刻画他的阴谋中，他的形象丰满而有魅力。近乎完美的能力蓝染的斩魄刀能力是近乎完美的，蓝染曾经说过，即便知道自己可能中了某种催眠，但是不知道破解办法也是无济于事，尸魂界除了在贴身伺机报复的市丸银，和失明的东仙，和有着主人公身份的一护外，和最终BOSS的友哈巴赫，其他人对他的能力几乎无解，而且不仅仅是刀的能力，本人的实力也是尸魂界首屈一指的，作为BOSS，硬实力撑得住场面。深谋远虑的谋划者蓝染的计划缜密，愿意为成为未来的王者而成“人下人”，而屈居于副队长的位置，并且一步一步布置自己的阴谋，巧妙利用自己的能力，暗算了店长，布置了露琪亚去现世，在他的几百年的策略中，稳稳的拿到了权利，为自己登顶尸魂界做足了准备，可以说他是深谋远虑的智者，仅仅有浦原喜助一人在智略上能高过他。尸魂界最后的王牌被关押在大牢中的蓝染，曾经拒绝了友哈巴赫的盛情邀请，然而蓝染的剧情并没有打住，在灭却师几乎攻占整个尸魂界之刻，京乐春水也发现事态不妙，只能放出了大牢中的蓝染，求助他能够帮助，本来是尸魂界的最大敌人，最后却不得不求助他成为尸魂界的最后底牌，讽刺至极。真正被认同的BOSS死神中的主要BOSS有蓝染、银城和友哈巴赫，但是真正“得人心”的BOSS恐怕只有蓝染惣右介一人，无数粉丝都称“如果蓝染篇”就结束，那死神才是完美的”，血战篇的故事角色实力混乱，剧情没有逻辑，并不得粉丝喜爱，所以蓝染才是真正让人记住的BOSS，也是死神中最有代表性的反派。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10473', '0', '蓝染到底有多强？可谓是死神第一人', '著名的演说家，书法家，阴谋家，野心家，诡辩家，科学家，心理学家，哲学家，政治家，权术家，战略家，由于其强劲的实力以及帅气的外貌，在《BLEACH》的女性粉丝中有着较高的人气。其斩术，步法，鬼道，白打，绝对都称得上是超一流的《死神》中最强的人物之一，实力之高不可思议，对付队长级对手不费吹灰之力。蓝染在对战无月一护之前，总队长以外，见谁基本都是秒杀VS日番谷冬狮郎（胜，秒杀）VS阿散井恋次（胜，秒杀）VS狛村左阵（胜，秒杀）VS黑崎一护（胜，秒杀）VS葛力姆乔（胜，仅仅依靠灵压将对方压倒在地）VS赫利贝儿（胜，并称其不够资格在他手下战斗）VS护庭十三队的队长们和假面军团众人（胜，除总队长外其他人基本秒杀）VS黑崎一心（平，处于劣势）崩玉模式 VS黑崎一心+四枫院夜一+浦原喜助（胜，轻松取胜）崩玉模式第二阶段VS市丸银(胜，秒杀）蓝染的灵压，是死神第一高，连总队长活了几千年的人物，灵压都不及蓝染，在封印的情况下，仅靠灵压就把灵王宫轰了下来。如果正面刚的话，总队长绝对是无敌的存在，可惜的是，蓝染太全面，综合实力超过总队长仅仅说死神的话，蓝染是站在了死神的顶点，靠崩玉进化到了更高的境界。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10474', '0', '死神，最悲剧的五大角色，蓝染大大第一！', '日本动漫《死神》已经完结了很久了，虽然最后烂尾了，但是也是追过好多年的了，毕竟曾经爱过。那么，在《死神》完结之后，偶然回想一下《死神》中最悲剧的五大角色，心有戚戚焉，竟然有些怅然若失之感。下面的话，就先来说一下小妖心目中《死神》里最悲剧的五大角色！TOP5.十三番队队长浮竹十四郎小妖个人是很喜欢浮竹的，主要是浮竹的性格让小妖感觉非常的舒服。但是，最悲剧的是浮竹最后虽然解放了力量，英勇就义，但是也没起到太大的作用......属于典型的被强行给画死了！TOP4.花姐花姐没有任何死掉的理由，但是就是死掉了.......而且是被小妖大爱的剑八给砍死了......实话实话，虽然剑八很强，但是论综合实力的话， 花姐简直甩了剑八好几条街！在《死神》完结之前，小妖最期望的是花姐能活过来，可惜......久保最后来了一个大烂尾！TOP3.乌尔奇奥拉和一护的大战让人印象极为的深刻！其实小妖觉得乌尔奇奥拉还有很大的刻画空间，现在来看，乌尔奇奥拉完全就是被一护的主角光环给克死的！TOP2.市丸银由于市丸银眯眯眼，面相看起来也不太好，所以《死神》的前期小妖是不太喜欢银的。但是，最后银的反转无间道让小妖彻底沦陷了！可惜，最后也没熬过去，死在了蓝染大大的手里！TOP1.蓝染久保就这样把我们蓝大一辈子固定在了轮椅上了，作为《死神》中的野心家和枭雄，怼蓝大最大的惩罚可能莫过于此。即使久保最后让蓝大再痛快的打一架，最后被友哈给干掉，也比限制我蓝大的自由要强呀！以上五个人，就是小妖觉得《死神》中最悲剧的五大角色了！你觉得《死神》中谁被悲剧呢？你最心疼哪个角色呢？欢迎与小妖留言互动交流~', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10475', '0', '蓝染完全轻蔑友哈巴赫', '蓝染惣右介：曾担任110年前五番队的副队长，后叛离尸魂界逃至虚圈，创造十刃并成为虚圈统治者在进攻尸魂界时被黑崎一护重伤后因内心动摇，崩玉停止进化，被浦原喜助封印这是一个继老头的最强死神，虽然败在一护手中，不过也是命运趋势，谁叫浦原一在呢结果被判20000年徒刑，收押在尸魂界一番队底下最深处的第八狱无间地狱。很好奇，当释放后，蓝染会真正自由么，他又何去何从呢未尾在千年血战中，友哈巴赫攻破了灵王宫，为了挽回战局，京乐春水将蓝染从监狱中释放了出来。动漫没出，是漫画出来，很多人说是败笔，但败笔又如何，有争议才会有期待，你期待蓝染篾视友哈巴赫场景么想想蓝染表面上看上去是个经常带着笑容十分温柔男人，但实际上是个为了达到目的而不择手段冷酷无情的男人。手持幻觉系斩魄刀，刀柄呈浅绿色，特点是敌人只要看到一次镜花水月的始解，从那瞬间起就会被完全催眠，这是精神系攻击，除了总队长那牛X人物，又有谁能破呢！千年血战篇详述：友哈巴赫邀请蓝染为其部下遭拒绝，随后使友哈巴赫错误估计来到尸魂界的时间当友哈巴赫在灵王宫杀害灵王准备要将尸魂界彻底毁灭的时候，京乐春水解开了蓝染的封印哼哼，要帮忙就放人，真当蓝染大大是什么了，那可是王的存在，好在对手勾起兴趣，不然是让春水吃闭门羹之后蓝染虽然身体仍被固定住，但却靠着灵压就压制了散落的灵王之力的奔流随后舍弃咏唱释放黑棺，直接将笼罩在众人上空的灵王奔流抹消殆尽，欲释放满溢的灵压却因周遭的灵压缚具而以失败告终看到没，说实话，光凭实力，蓝染可以胜任总队长一职后来利用“镜花水月”的催眠能力成功诱骗友哈巴赫，让唯一不受“镜花水月”催眠能力影响的一护重创对手，结果被友哈巴赫从后方袭击并遭对方吞噬吞噬又能如何，又不能消亡他，蓝染毕竟仅次无月形态之下，而且能硬抗无月不死，区区一千年反派能奈他何十年之后被束缚于无间之中的蓝染感知到友哈巴赫仅存的力量消失殆尽说：毫无恐惧的世界会让人停下去探索希望，而击退恐惧所迈出则称之为“勇气”。亦正亦邪，为人高傲，说一句蓝染是完全轻蔑友哈巴赫也不为过', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10476', '0', '死神：最有魅力的反派蓝染的各种名台词', '尽管说去年原作漫画已经正式完结了，不过作为当年的三大漫之一，《死神/境·界》还是拥有着不少的支持者。当然了，粉丝们感兴趣的或许不是那种槽点无数的剧情，而是里面塑造成功的角色吧？近日，就有日本宅人讨论：蓝染的名台词为啥可以这么多？1楼：这两天我重新复习了一下《死神/境·界》，感觉比起主人公一护，其实蓝染的塑造更加出色呢。尤其是除了强大的实力之外，他也有很多让人难忘的名台词。像是“憧憬是距离理解最远的感情”，哪怕用在现实生活当中，似乎也没有违和感呢。为啥久保对蓝染这么上心啊！ 3楼：我当年也是最喜欢蓝染了，甚至于到了蓝染被打败之后，就再也没有去买JUMP的地步。他那种面对主人公阵营的挑战，总是一脸淡然的模样太有魅力了。像是什么“不要将强大挂在嘴上，只能显得你很弱小”之类的，难道不算是人生哲理吗？说实话，我真的很想看看以蓝染为主人公的外传啊，至少比正传当中那个只能靠开无双来解决问题的一护要好多了。 6楼：说实话，蓝染应该可以排名在JUMP反派角色的前五位了。甚至于《死神/境·界》这部作品之所以当年那么受欢迎，也是因为蓝染从大好人突然变成大反派的功劳哦，战败之后的那句“就凭你们也配给我进行判决吗？看上去有些滑稽啊”，真的是太有王者之气了。所以说这部作品就该选择在打败蓝染之后立刻完结，那么对读者和作者而言都是好事情。后来那些BOSS看起来一个个除了臭屁就是装大爷，也搞不懂是怎么当上BOSS的。 7楼：目前为止让我印象最深刻的就是那句“憧憬是距离理解最远的感情”，另外还有“这是败者的理论！所谓胜者，根本不会在意世界是什么样，而只会谈论到底应该如何存在于世界啊！”也让人记忆犹新，这根本就是个导师级别的存在啊。其实我真的觉得久保诗人应该将蓝染作为最终BOSS，后来的友哈巴赫之流根本连给蓝染提鞋子的资格都不配，完全就是一个劣化版的蓝染。 8楼：我认为蓝染最帅气的台词应该是“你什么时候产生了我没有使用镜花水月的错觉”。当年看到这句话的时候，我真的不知道这样一个BOSS到底如何才能被击败。说真的，直到今天我都觉得久保在打败蓝染的设定方面做得太过于草率了。强行弄了个能够击败蓝染的BUG，这不是搞笑吗？ 14楼：其实我真的觉得镜花水月这把斩魄刀只配让蓝染使用呢。如果换成友哈巴赫之类使用镜花水月，简直就是拿着外挂胡闹的小孩子了。而且蓝染当年凭借镜花水月的力量顺利装死，哄骗了所有的人。这样的超级骗局，别说是本作品了，即便是所有的JUMP漫画里也都非常少见吧？ 21楼：我一直觉得《死神/境·界》之所以没有被腰斩，就是因为大家好歹还期待蓝染能够再度出场呢。不过还是得强调一句——蓝染的最终形态太丑了！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10477', '0', '从无解的技能到极致的力量，为何蓝染会放弃斩魄刀', '空座町大战中蓝染凭借镜花水月戏耍了一众队长副队长们，秒杀了他们并入侵尸魂界，镜花水月的能力是完全催眠，类似火影中的幻术，无限月读甚至可以催眠整个世界，那为什么蓝染要放弃这么无解的技能，反而一味追求与崩玉结合而产生出的极致的力量呢？下面就和大家一起分析其中原因：1.镜花水月存在缺点：最大的可能就是镜花水月存在一种致命的缺点，比如灵压，死神的战斗就是灵压的战斗，拥有强大灵压的人甚至可以不费吹灰之力就抹杀掉对手，可能灵压强的人会不受镜花水月的影响，蓝染害怕出现差错，所以还是绝对的力量差距更保守一点，或者精神力强的人可以摆脱镜花水月的控制（自行破解镜花水月的催眠）。2.蓝染过于自信：第二种原因可能就是蓝染太过自信，觉得完全不需要使用斩魄刀的能力就能击败护廷十三队，与崩玉的结合使得蓝染力量大增，甚至一刀就能摧毁一座城市，对力量的追求冲昏了头脑，忽略了镜花水月这个强力的技能。镜花水月的催眠能力在千年血战篇也出现过,蓝染催眠了友哈巴赫，干扰了他的五感，堪称无解的存在举两个例子：例子1：假如说山本元柳斋的真实身份是个在尸魂界扫地的老头...他和其他尸魂界的人都被镜花水月催眠了,认为自己是总队长,而其他人都认为他是总队长,而真正的总队长认为自己是个扫地的老头...例子2：死神剧场版2钻石星尘的反叛中的草冠宗次郎和小白的斩魄刀一样.其实是把小孩子玩的玩巨塑料刀...被蓝染催眠之后,认为自己的刀是冰轮丸...还有很多,可以自己想...然后说的就是他战斗的应用.假如他想弄死小白.只要对小白使用镜花水月催眠他,让小白看到一个场面.就是雏桃森被一个机器困住,解救他的办法只有一个,就是让小白自杀...当然一开始小白不会自杀,肯定会反抗.只要小白一反抗,机器就会自动砍掉雏桃森的一只手或脚,直到小白不再反抗为止.然后雏桃森会说一句话,小白,快点救我,你去自杀吧这样小白就被蓝染不费吹灰之力搞死了..所以说如果结合崩玉和镜花水月的能力那主角还玩什么，除非开无敌挂，毕竟从设定上蓝染就不可能统治尸魂界。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10478', '0', '排除主角光环！《死神》里有谁能战胜蓝染', '从某种意义来说，《死神/境·界》这部作品之所以可以在一段时间拥有着不输给《航海王》《火影忍者》的人气、并且被誉为“三大漫”，就是因为蓝染的魅力实在是太强了。这样的BOSS，放眼整个动漫圈都非常少见。而后来他被一护击败的事实，也只能用“主角光环”来形容。那么如果说我们排除一护的存在，在整个作品当中是否还能找到比蓝染更强的角色呢?对此，近日就有日本网友展开了讨论。1 風吹けば名無し这阵子我重新把《死神/境·界》的漫画看了一遍，真心觉得蓝染根本是个不可战胜的存在吧?本身就拥有着绝对压倒性的实力，再加上又彻底统治了虚圈。如果没有一护的话，那么他完全可以将尸魂界也攻陷了。一护后来击败他的方式根本是莫名其妙，我实在无法接受蓝染那么高傲的角色居然会因为主角光环而败北。但即便如此，在最后对抗友哈巴赫时，还不是需要借助蓝染的力量?其实蓝染的力量根本就没完全使用出来吧?这样的角色根本无敌了吧?如果不算一护的话，你们觉得作品当中到底还会有谁跟他战斗时获得胜算呢?反正我是想不到了……3 風吹けば名無し《死神》最让我无法接受的设定，就是蓝染莫名其妙地想要获得崩玉的力量。你们有没有觉得获得崩玉、变得很古怪之后，蓝染的实力实际上是下降的呢?如果是原本那个精通各种技能，并且还拥有镜花水月这种无敌斩魄刀的他，很容易就可以搞定全部的对手，即便一护有主角光环也是没问题的吧?所以说，如果要选一个能够战胜蓝染的人，那么就是没用崩玉的他好了!4 風吹けば名無し从整个作品的构架来看，我认为唯一没有真正发挥全部实力的角色是浦原喜助才对。在大多数时间里，他貌似都以辅助者的身份存在吧?好像还没有看到他动真格地去决战呢。只可惜作品完结得太过于草率，否则如果能够看到店长发挥全力，应该是可以跟蓝染好好做一番对决的。5 風吹けば名無し如果是一对一的话，我想不到谁能够战胜蓝染。但如果是日番谷、山本老爷子一起出手，或许还是有胜算的。日番谷可以先用能力停止来克制镜花水月，然后老爷子用全力展开进攻，这样的话，靠蓝染一个人或许很难扛得住呢。当然，谁也不知道蓝染到底拥有着多少鬼道技巧，这一点没能在作品里体现，真的是太可惜了。作品明明在后期剧情已经如此拖沓，却没有将那么多伏笔给填上……哎，想想看目前炒冷饭的情况那么多，也许过几年《死神》也会复刊了吧。7 風吹けば名無し怎么想都不可能有人战胜蓝染了。毕竟以蓝染的智慧，除了后来被主角光环击败之外，其他的时间基本上都是将一切算计在自己手中的。尸魂界的风波为啥被认为很经典呢?不就是谁也没想到幕后黑手居然会是蓝染，而他差点就完全实现了自己的野心。如果说并非一护的角色贸然去挑战他的话，也许在刚开始动这个念头的瞬间，就已经被蓝染的镜花水月迷惑了，以至于根本不存在反击的可能性呢。这样的角色没有能够作为最终BOSS来使用，真是久保带人的失败。8 風吹けば名無し7楼的，我认为或许久保带人本身就准备在尸魂界篇结束故事吧。例如蓝染并没有去虚圈，而是直接跟一护他们展开对决之类的。只是因为作品人气太高了，被迫画出了很多超乎他想象的剧情哦。因此，蓝染被设定得非常完美，可搞定蓝染之后，作品还得继续连载，于是乎才会出现后来什么月岛、友哈巴赫之类的BOSS。而这些BOSS的真正实力，也根本没办法跟蓝染相比呢。15 風吹けば名無し不用想太多了，蓝染就是为了被一护的主角光环击败而存在。所以说除了一护之外，根本不可能有其他人对他下手。如果你们说的什么日番谷、山老爷之类能够一战，又怎么会在尸魂界篇的时候完全没有招架之力呢?要知道，《死神》的时间跨度可是动辄几十年甚至几百年的哦。总不至于前面几百年根本没有进步，只靠着这几年的突破就能战胜蓝染吧?来源：腾讯动漫', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10479', '0', '蚁王，蓝染，夜神月，这八个反派人气直超主角！', '大家好， 欢迎收看最新一期的拾部次元，在这期内容当中拾部君将会给大家带来“魅力十足的反派角色”的介绍。在各式各样的动画人物中，一些经典的反派形象也是让我们印象深刻，甚至人气超越主角，让我们一一了解一下。梅路艾姆由嵌合蚁女王所生的，嵌合蚁们的王。由于凭着自己的意志，提早出生，导致嵌合蚁女王的死亡。并开始统治世界，捕食人类，学习人类的语言、知识，再王打发无聊时间的时候，学习人类的游戏。直到遇到军仪高手小麦，王从未赢过，并且对此产生强烈的兴趣，在与小麦的相处中，王的性格开始慢慢改变。也是让人心疼的反派角色。宇智波带土一生经历两大转折，其一神无毗桥之战重伤，濒死时将左眼送与挚友卡卡西——今后你就是我的眼。其二被宇智波斑所救，在疗伤后其亲眼目睹暗恋女孩野原琳的死去，痛不欲生，感受到人世间无边的残酷与黑暗——放下了曾经的一切。拥有空间穿梭的万花筒写轮眼神威的力量。早期理想是成为「火影」；琳死后，改为为创造一个永远和平而且有琳存在的「梦境世界」奋斗终生·.......怪盗基德一个充满传奇色彩的怪盗，拥有过人才智，精通易容、变声、逃脱术，以珠宝等各类贵重艺术品为目标，使用魔术手法进行犯案的超级盗窃犯。帅得犯规的外表，在加上潇洒的作案手段，基德自然有着火爆的人气奈落“奈落”一名在日文里的意思有地狱，永远的深渊……有俗语：“奈落之底”，是指无法脱离的极深的地狱世界，也指不知道底部的深的处所。个性凶狠，实力异常强大。他是山贼鬼蜘蛛被妖怪吞噬以后形成的半妖，在戈薇来到战国时代的50年前，假扮犬夜叉偷袭桔梗，导致犬夜叉被桔梗封印。在犬夜叉苏醒后，他又用计害死了珊瑚的亲友，嫁祸给犬夜叉，最终被犬夜叉一行击败临死前知道了自己原来一直爱慕着桔梗。充斥着悲情色彩。六道骸说他是反派，是因为一开始对他的背景以及能力设定，让人细思极恐。目标是摧毁黑手党。。。有时候会倒戈向敌人，因六道骸关在复仇者监狱，由库洛姆·髑髅代替他行动，利用她进行实体化。在继承者篇中六道骸的精神凭依在库洛姆·髑髅的精神之间，然而六道骸自身已变成空壳的肉体被彭格列初代雾之守护者D·斯佩多给占据了。吉尔伽美什古代美索不达米亚地区苏美尔王朝的都市国家乌鲁克的第五任国王，人类最古老的史诗《吉尔伽美什史诗》的主人公，最古老的“英雄王”。他有着“三分之二为神，三分之一为人”的神性，性格高傲，曾是自认为无人能敌的暴君。 拥有所有英雄持有的宝具原型。fate系列的人气反派，金闪闪有着别样的魅力。蓝染惣右介主人公黑崎一护破面篇最主要的敌人。曾担任110年前五番队的副队长，原为尸魂界护廷十三队五番队队长兼灵术学院特聘书法老师，后叛离尸魂界逃至虚圈，创造十刃并成为虚圈统治者。最具魅力的boss之一，有着深沉高深的谋略，枭雄一般的气质，强大的实力，简直是实力反派的代表。夜神月严格来说夜神月就是反派，对世界的“无聊”感到窒息，认为世界正在腐朽，希望创造一个没有犯罪的新世界。他容姿端丽，智力超群，体育全能，演技优秀，心理素质极强，近乎变态的完美主义者，为了贯彻扭曲的正义而疯狂。将自己的执念贯彻下去，拥有着极高的人气。关注【拾部次元】，回复“恶魔人”获得观影。更多资讯，点击下方链接阅读：一文透析《你的名字》的剧情内涵！两次穿越，两次死亡！“死亡笔记”夜神月有没有错？从经济学角度告诉你，大错特错！小智十大精灵,喷火龙仅第二,第一能与神兽抗衡', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10480', '0', '蓝染大大，真的失败了吗？', '棕色短发、黑框眼镜和棕色双瞳，略显文静的笑容，超强的实力，（单纯的从外表上来看，这分明就是一位男神呀~）没错，今天所提到的呢就是110年前五番队的副队长，这个拥有超强的实力 以及智慧的自尸魂界诞生的最大反面角色---------------蓝染现被判20000年徒刑，收押在尸魂界一番队底下最深处的第八狱无间地狱“碎裂吧，镜花水月”きょかすいげつ--------“阴谋色斩魂刀”“碎裂吧，镜花水月”这句话，大家有没有想过有可能不是解放语？蓝染在四十六室解放时就有疑问，后来到蓝染用镜花水月“锄奸”的时候更加充满着迷惑：蓝染把刀拿出来给拜勒岗看了一下，等他说出“碎裂吧，镜花水月”这句话时，拜勒岗看到了什么？自己的部下都挂了！结合剧情看这一幕应该是现实而不是幻术。再结合前两次看就会发现：无论是假蓝染还是假乌尔奇奥拉都是幻术，而现实是一把刀！而且在破面篇中进入最终状态的蓝染已经放弃了斩魂刀，而在千年血战中却对友哈巴赫施展了镜花水月，还有可能就是“碎裂吧，镜花水月”是收招语！作用是解除幻术！这个想法我觉得久保也暗示过，大家来看这句话：“碎裂吧，镜花水月”。想一下，“镜中花，水中月”无论多美丽毕竟还是幻象，它们“碎裂”之后，幻术不就解除了吗！而对手所看到斩魂刀的时候可能就已经进入了幻境，而斩魂刀最终的解放，不过是为了取消幻境，抹杀对手。（痣城剑八，东仙队长除外。）当然上面所说的都是我的个人观点，不喜勿污~作为破面篇中的最终boss，蓝染的 强大是毋庸置疑的（貌似看起来比有哈巴赫还要厉害的样子），但其实蓝染从一出生就是天才，因为没有人能和自己相提并论而感到孤独。因此暗中培养一护，希望一护能打败自己。曾表示尸魂界当中，只有浦原喜助是唯一在智能方面超越他的存在，可是小编认为蓝染在死神改造方面应该是尸魂界里面有史以来的第一人，毕竟蓝染所掌握的科技以及发明可是完美的支撑了动漫的高潮部分：孤独、衰老、牺牲、虚无、绝望、破坏、陶醉、疯狂、贪欲、愤怒，这是蓝染所带来的十刃，其为人类致死的十个主因，既是各个十刃的能力，也是十刃的思想，更是十刃存在的理由，当蓝染创造出十刃的时候可能就是为了有朝一日能为世人得到解脱，这可能就是蓝染的目的。既然提到了蓝染所带来的十刃 就不得不说一下蓝染所掌握的科技，破面技术利用崩玉让虚摘掉面具，实现破面，蓝染用此技术创造了无数的破面，其中也创造了完成品的高等级破面十刃和改造型破面汪达怀斯。虚化技术将虚的魂魄注入死神或人类体内，如果没有抑制虚化的手段，被注入者往往会死亡。蓝染在百余年前曾对流魂街居民进行虚化实验，后对六车拳西、平子真子等人进行虚化实验。死神化技术对虚进行改造，赋予虚死神的能力。蓝染用这种技术使吉良伊鹤、雏森桃、阿散井恋次魂葬实习中出现的巨大虚拥有隐藏灵压的能力，之后改造的梅塔史塔西亚拥有了使斩魄刀无效化的力量，害死了志波海燕[24] 。魂魄异物提取技术从死神或普通灵体中取出物体，又不会对其造成伤害的技术。蓝染在浦原喜助以往的科研资料中找到的，专门为了从露琪亚体内取出崩玉而开发的技术。黑斗篷能够完全隐藏使用者的灵压，使其他死神无法感应到使用者行踪的衣服。原本为浦原喜助所创造，在浦原喜助逃离尸魂界后，蓝染将黑斗篷复制成3件，并在20年前的空座町上空偷袭志波一心。不完全崩玉蓝染开发的用来超越死神极限的物质，虽然被蓝染注入了数百名死神和数百名有死神潜质者的魂魄，但并没有满足的迹象，最终以失败告终。蓝染得到浦原喜助的崩玉后，将其置入原有崩玉中，从而得到了较完美的新崩玉。反膜之匪可以将灵压较低者永远封印在异次元的小方块，如果对十刃级破面使用只能封印数小时。此物是蓝染赋予十刃用来惩罚下属的道具。葛力姆乔曾以此封印乌尔奇奥拉数小时。人造天盖蓝染在虚夜宫创造的人造天空，空中有白云、日光，与虚夜宫外的荒凉沙漠形成鲜明对比，为了保护人造天盖不被破坏，蓝染严禁前四位十刃在虚夜宫内归刃，禁止十刃使用王虚的闪光。但是掌握了如此多的科技的蓝大大，最终却还是输了，他为什么还是输了呢？其实从千年血战中蓝染的事迹就可以看得出来，蓝染其实还是想要保护尸魂界的，虽然他所说的是不想别人操纵啊，但是以蓝染的实力，以及能力想要统治虚圈应该也不是多么难的事情吧。就是不知道结束了千年血战蓝染会不会继续关进小黑屋，毕竟我蓝大大可是不死之身呀，仅仅被打穿了身体这根本就是不痛不痒的。ps：新人小编 希望得到大家的关注，', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10481', '0', '死神：分析死神中最神秘的角色，控制蓝染的幕后黑手', '牙密作为最早登场的十刃之一，在死神中的戏份可以说是少的可怜，有人会说牙密实力很弱小，被一护切断了胳膊，还被夜一狂虐过，其实这都是假象，是为了迷惑死神们的手段，蓝染起初也只是一个默默无闻的人，现在摇身一变成为了目前的大BOSS，牙密也是这样，这些都是牙密做出的铺垫一、牙密的实力。前面已经提到过了，牙密被一护切断过胳膊，还被夜一狂虐过，这都不是真的，牙密的根本实力不止如此。首先说一下牙密惊人的恢复能力，大家都知道葛力姆乔被一护的月牙天冲打了一下，身上留下了一道巨大的疤痕，其实根本不是葛力姆乔为了打败一护而刻意不去修复，而是他的恢复里根本就不行，就算是破面也是爱美的，爱美之心人皆有之，他修复不了，我们的牙密却不一样，胳膊虽然断了，但是接上以后根本没有影响，跟原装的一样，这足以体现了牙密惊人的恢复能力。葛力姆乔的胳膊也断了，但是那是织姬用她的能力还原的，也就是说那是原装的，葛力姆乔用原装的胳膊与一护打，结果挂了。可是我们强大的牙密却装着一只后配的胳膊与浦原喜助打了很长时间，而且全身而退！浦原喜助的实力肯定是要比目前的一护高的，虽然浦原喜助没有万解，但是大家要知道，牙密也没有释放自己的斩魄刀！还有一点，浦原喜助根本不敢正面接下牙密的虚弹，而是用了替身，这是为什么？这是因为浦原喜助根本就不敢正面接，他胆怯了！他知道牙密虚弹的威力！换句话说如果牙密使用虚闪的话，估计浦原喜助不死也得残废。我想这时有人会说了，牙密和一护打的时候用过虚闪，而且很垃圾，其实你们错了，因为一护是主角，这是剧情设定的，如果牙密的虚闪秒杀了一护，那么死神就没法继续画下去了，换句话说，这是牙密给一护开的一个玩笑。二、牙密的性格大家都知道，牙密很傻很天真，而且很狂妄，很傻很天真说明牙密是一个善良的人，而狂妄呢？牙密为什么狂妄？重点来了，这是因为牙密深深知道自己的实力，狂妄是一种本性，虽然牙密一直在隐藏自己的实力，而且处处手下留情，但是狂妄的本性是改不掉的。只有有实力才可以狂妄，没有实力那叫吹牛，牙密是个善良的人，他不会吹牛，所以牙密是有实力作为资本的，所以他才狂妄！大家也都知道，牙密总是和乌尔奇奥拉一起行动，为什么呢？大家都认为牙密是乌尔奇奥拉的小弟，错了！乌尔奇奥拉是一个性格很自负的人，对自己的实力极度的自信，然而我们的牙密是善良的人，他之所以经常和乌尔奇奥拉一起行动，是因为他知道乌尔奇奥拉性格上的弱点，是为了保护他，所以才会经常在一起行动，牙密是一个多么关心同伴的人，这在破面中真是太少见了！牙密太善良了！三、牙密的身份地位葛力姆乔擅自行动，而且打了好几次败仗，蓝染就除去了他的十刃资格，找了一个叫做露比的来顶替他的位置，小鱼记得露比应该是魔力宝贝中的一个角色，不好好在魔力里呆着，跑死神里面来，被小白打败是必然的。牙密却不一样，胳膊被一护切断了，失败了好多次，蓝染都不敢把他从十刃中踢除，这说明了一个问题，蓝染不敢这样做，从这点来看，足可说明牙密很有可能是蓝染的上司，也就是真正的幕后黑手，退一步来说，最次也是和蓝染平起平坐！死神的故事发展到现在，就像本文最开始提到过的，十刃都一一露面了，而且已经死了五个，牙密却迟迟没有露面！为什么呢？这更说明了牙密在保存实力，蓝染带着赫丽贝儿，拜勒岗，史塔克三个来到现实，命令乌尔奇奥拉留守虚圈，这都是命令！他却没有命令我们的牙密！为什么呢？因为蓝染不敢，换句话说这所有的一切都是牙密在幕后操纵着，牙密才是真正的幕后主使！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10482', '0', '《死神》十大美人排行！井上织姬位居第四', '第十名：虎彻勇音勇音的身高在11区里，就算是男人，也属于稀有物种了，别说还是一个面相清秀的女人。怎么看和妹妹清音都不像一家子，但又感情超好，就算吃稀饭就能长这个高，不知道会不会让很多人羡慕嫉妒恨了。有点双重性格，平时虽然胆小内向，但是战斗起来却又很有耐力，很可靠。就算跟卯之花队长比，也毫不逊色。做为副队长在救援战斗中，很是可靠的说。第九名：志波空鹤看到这个一脸做派很黑道的女人，宜男宜女的扮相，身材高挑，很是让人惊艳。就算是没落的贵族，依然难掩那份霸气。就算哥哥死掉，独自教育弟弟，依然豁达，只是一句道歉，就能原谅露琪亚，这份心胸，很有家主的气魄。喜欢一些奇怪的实验，可以跟涅队长对决一下，虽然不是死神，但是法术高明。用大炮就能帮一户闯进静灵庭，可见其实力。不过爱好是搬家，可不是什么好习惯，人家孟母三迁为了儿子，空鹤喜欢搬家，可能只是为了能做爆炸实验吧。第八名：九条望实是最初的改造魂魄，并且是由改造魂魄创始者由嶌欧许分裂而来。起初看似性格冷僻不愿与人交流，实际温柔善良不愿把魂等人牵扯进自己遭遇的麻烦之中。后来慢慢同一护、魂等人成为了朋友。最终与因幡影狼佐融合为由嶌欧许，被一护打败后死去第七名：赫利贝尔做为十刃中唯一的女性，赫利贝尔的实力不容得人们的质疑，面对世人的眼神是坚韧和桀骜的。在对战小白的时候，赫利贝尔始终占据上风，着实让咱们的小天才吃了亏，鲨鱼的形态充满战斗力，凶狠，冷酷。赫利贝尔对蓝染是相对尊敬和畏惧的，但结局也是悲凉的，在和小白近乎惨烈的战斗之后，结局竟然是被蓝染给秒杀了，如此杯具，着实让人很是同情。归刃后更近于人形，98对女性还很是善良的，没有画成奇怪怪的模样。第六名：涅音梦多亏是高科技产物（义魂技术和义骸技术的结合产物），要不打死我都不相信这么温柔懂事的女子可是涅队长的女儿，狂汗......就算被涅队长虐待也不生气，用药剂维持生命也不怨恨，依然善良。还好涅队长没把自己的恶趣味和变态也给涅音梦移植啦。善良的本性还是很不错的。钉宫女王这次不做萝莉和萌物，给小受御姐配音也是游刃有余啊。第五名：四枫院夜一无论是人类还是猫类，黑色都是夜一桑的代表，那个充满神秘的色彩，让夜一桑背景和店长的关系的都充满了谜团。就算１１２年前的故事已经被揭开，夜一和店长的故事仍被众多死神粉津津乐道着。一个可以抛弃贵族身份和队长头衔的女人，这份洒脱，是多少新时代女性所向往的。一个呆在猫咪义骸里，却洞察一切的女人，这份机敏，就算是店长，也得肃然起敬。一个被称为瞬神的女人，就算是大白那样死神中的佼佼者，关键时刻也需要她的教导保命。如此这般的强大和坚毅，正如98说道既瞬捷又洗练，既纯粹又耀眼，既神圣又豪迈，既镇定又热情。无论什么样的角度，就算舍弃死神的身份，夜一也是女性死神的代表。第四名：井上织姬 98口中的死神的女一，虽然都是橘色的头发，但是显然娜美桑在ＯＰ里的表现比井上在死神中更抢眼（井上的拥护者不要吐口水啊）。但是井上的表现也是可圈可点的，先是那漂亮的发卡体现的能力，足以让大家新奇一阵子了，那复原能力，俨然速效时光机，让我们觉得很雷很健康。就连蓝染大人都盯上她，还让小乌玩起了感情。虽然对一护的感情总不能尘埃落定，但是被人气角色小乌看上，也着实体现了女主的魅力。再说了，感情要是不坎坷，怎么提高出镜率呢并列第四名：碎蜂幼名蜂梢绫，蜂家第九代传人，是六兄妹中的第六人，“碎蜂”是其加入“刑军”后从她曾祖母那里继承来的名号，现为隐秘机动总司令官、第一分队“刑军”总括军团长，最不容忍叛徒存在的人。其家族〖蜂家〗是代代以处刑、暗杀为生的下级贵族。（如果不能加入刑军，就会被家族流放。）碎蜂自幼受家人的影响，以追随保护四枫院家族为己任，并疯狂地崇拜着四枫院夜一。进入“刑军”总括军团的第7年，由于个人成绩突出被调入总括军团长的直属的“护卫军”，从此一直被夜一以妹妹一样看待。其实至今还是深深崇拜着夜一，是个希望能为夜一付出一切的可爱女孩子，因为崇拜所以要超越。是个绝对可以依靠的女子。第三名：朽木露琪亚她是一位天生丽质、身材娇小、洋溢高雅气质（阿散井恋次言）的美少女。露琪亚中的‘露琪亚’原文意为‘光明’，后被引申到天主教里，指驱走黑暗，带来光明的圣女（也就是光明女神）。北欧由于纬度高，长年处于黑夜长，白天短的环境，人们对光明的渴望非常强烈。第二名：松本乱菊无论这个女人怎么不着调，都不妨碍别人对她的喜爱。无论她怎么嗜酒，都不影响他在十三番队中的人缘。橘色的波浪式长发，姣好的身材，豪爽的性格，俨然就是静灵庭的一道耀眼的风景线，有木有？乱菊是高洁而可靠的，就算面对的敌人是银子，也不忘履行自己死神的职责。她善待身边每个人，就算面对银子的背叛有多凄凉，还不忘安慰被打击的吉良。就连八番队那位冷漠的伊势七绪都对他热情有加。正如他的名字，高洁爽朗，要不然怎么能出现一个潜伏一百多年的天才情圣，一切的一切，在银子那久远的回忆中，只为了乱菊的身影。并列第二名：卯之花烈梳着特别的长辫子(编在胸前而不是后背)，温柔而慈悲，大和抚子般的存在，但也有其腹黑和毒舌的一面。即使最粗暴的战士也在她礼貌的语气下退却。她有着与其温和外貌大相径庭的高超剑道修为，在新篇章将解放灵压和斩魄刀参与战斗，战斗时会将胸前的头发解开第一名：妮露初登场可爱的小萝莉，天真无邪，身边又跟着那么搞怪的从属官，弄的一护和石田也哭笑不得。短暂的相遇，不影响妮露的心，与一护有些许暧昧，为了保护一护，舍命参加战斗，变身之后，绝对的无敌御姐，不禁感叹啊，人家是女大十八变，妮露这可是瞬间变身。放弃十刃，只因不喜欢无意义的战斗。在弱肉强食的虚圈，变成小萝莉，只是因为讨厌血腥吧。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10483', '0', '不用不服，久保带人说死神女主就是井上织姬', '《死神》漫画已经完结一段时间了，除去对结尾剧情烂尾的不满，粉丝们最大的怨念就是剧中的CP问题了。黑崎一护最终选择和井上织姬结婚，也是让很多支持露琪亚的粉丝愤怒不已，而且在戏份上织姬也是比露琪亚少很多。在近日一档日本的电视节目中，《死神》的编辑曝料称在作者久保带人看来，井上织姬一直都是女主角，而露琪亚则是同伴其实久保带人在之前也在访谈中说过他一直将织姬作为女主来塑造，但是之后大家在漫画中发现还是露琪亚和一护在一起的时间更多反正不管你服不服，买不买账，结果已经出啦，对于漫画你可以有自己的理解，可漫画最终还是要靠作者来完成，自己怎么想是自己的事情，只是你自己的一厢情愿，既然你可以有一厢情愿，最好一护和井上在一起也是作者的一厢情愿，可惜的是，人家才是作者。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10484', '0', '井上织姬vs春野樱，谁更不受人待见？', '死神和火影忍者都已经完结了，然而cp大套路却完全相反，男主一护娶了女二井上织姬，女主小樱嫁给了男二佐助，对于鸣人和雏田，大家都比较满意，因为小樱配不上鸣人，一户和织姬却很多人为露琪亚鸣不平日本女网友评选最令人讨厌的动漫女性 井上织姬高票夺冠，为什么大家不喜欢她呢？井上织姬善良，从头到尾的暗恋一护。这样的角色有点像是雏田，总是背后默默付出，但是雏田大受欢迎，而井上织姬人气则不高。最主要的原因，还是这个人物在剧中所发挥的作用不同。1是明明实力不够，却要逞能，总是拖后腿，2是动不动被抓然后主角团们去营救，然而井上就是这样的一个角色，3井上的存在对于一护的成长没有任何作用，一直陪伴一护的是露琪亚，然而最后没有在一起。小樱是无脑恋爱狂么？并不是。小樱拖主角团后腿了吗？并没有。那小樱为什么讨人厌——站在一个政治高位，却没什么政治觉悟，甚至没有立场，村子的未来她没什么打算，维持现状就可以。就好像一个大学生，思考问题的方法还是和小学生差不多。通俗点讲，长了肌肉，长了脑子，却没梦想，搞到最后也没有真的做到独当一面。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10485', '0', '一露党对不起！请容许我为井上织姬说句话，她不该被这样对待的！', '《死神》漫画已经完结很长一段时间了，除去对结尾剧情烂尾的不满，粉丝们最大的怨念就是剧中的CP问题了。而且这种怨念估计是永远不会消散的！一护最终选择和井上织姬结婚，这着实让广大的一露党愤怒不已，而且在戏份上井上也是比露琪亚少很多。近日小编又重温了一遍《死神》，经过仔细推敲琢磨之后，我觉得是时候站出来为井上说句话了，在小编看来，这个女孩的确不应该受到这种待遇！说到这里，我知道很多不待见织姬的朋友又要发作了，请别着急，给我点时间，容我慢慢道来。首先是戏份的问题，很多朋友都认为，从戏份上来看，露琪亚明显是女主角嘛。首先从作者的角度来看，久保带人在漫画完结后的一次访谈中也说过他一直是将织姬作为女主来塑造的，而露琪亚则是作为同伴。当然这些话在漫画完结后说出来未免有马后炮的嫌疑，就算是作者亲自说出来很多人也不买账。我觉得我们应该从设定上来分析，《死神》是一部热血动漫，这是战斗的世界，感情只是一朵点缀。整个故事的开端是由一个普通的高中生在机缘巧合之下得到死神之力而展开的，而露琪亚作为一护走向死神代理这条道路的引路人，可以说从一开始就注定故事将围绕这两个人而展开。我们都知道热血动漫的感情线一向很少，《火影》中雏田的戏份不也很少吗？所以，从戏份的多少来质疑井上该不该成为一护的妻子这件事是不对的。织姬出生于一个不和睦的家庭，双亲具备着严重的暴力倾向，时常对兄妹俩暴力相向，所以对井上来说，她的童年是相当悲惨的！所幸的是她的哥哥对她疼爱有加，后来她的哥哥井上昊便开始负责全权照顾她。在她3岁那年的春天，昊带着织姬远离双亲，在外相依为命生活了9年。可惜好景不长，有一天昊发生了车祸，终于离他而去。尽管织姬因为这个契机认识了一护，然而当时没能和昊亲自道别，便成为织姬最后悔的一件事。由于双亲下落不明，加上兄长身故失去生活依靠，因此织姬开始在远房亲戚的援助下独自生活。要知道，她仅仅是一个高中生啊！只是个弱女子啊！然而就算是这样，她依然一个人坚强的生活着，总是以没心没肺的笑声给身边的人带来温暖，如阳光般灿烂。一个人生活太久，就会格外珍惜每一份温暖。善良是人的本性，当这个世界满是黑暗恐惧的时候，这一份纯洁的心，难得可贵。她怀念死去的哥哥，眷恋现世的亲情。没有说出口的抱歉，成为一生的遗憾。因此总是在人前故作坚强，人后独自黯然神伤。这就注定了她的那份爱，是默默守护的爱。缘分总是那般恰如其分，在恰当的时间里，没有早一步，也没有晚一步，一护出现并保护了她。可能就是在那个时候，爱的种子便已经在她心头中下；或许也就是在那个时候，一护就暗自决心要保护这个女孩。爱的种子已经种下，只等一个时机来促使它发芽、成长。于是，乌尔奇奥拉和牙密来了，织姬和同伴们均被打伤，而一护恰恰在那时失去了虚化的力量，没能保护好同伴和织姬，因此非常沮丧，在浦原商店养伤的织姬则非常心疼自责的一护，希望他不要有心理包袱，不要自责，希望一护要好好的。而一护的则觉得自己太弱，保护不了身边重要的人，所以陷入了深深的自责和消沉情绪里。彼时，两人心之所想的事情是完全一样的，都希望对方好好的，却因为不能为对方分担肩上的重量（织姬不能帮助一护抒解心结，而一护无法保护好织姬让她受伤），而产生难以言说的隔阂。而同心同德的露琪亚从尸魂界赶来，打醒了一护，并把他拖到织姬的面前，帮助一护对着织姬喊出最难说出口的一句话： “太差劲了！真的很对不起！”别扭的青春期少年，只会自责，而很难勇敢地大声吼出来，我太差劲了！让你受苦了！让你担心了！就是这么别扭和羞涩，还有自责。震惊的织姬，惊讶露琪亚对一护的鼓励作用，也真心的感谢露琪亚。一护恢复真是太好了，终于变得不自责了，不用继续自己伤害自己。在这件事情过后，织姬的心境发生了一些小变化。织姬的内心深处嫉妒又难过，因为那个帮助黑崎恢复的人不是自己。 朽木小姐那么美丽能干，漂亮善良，自己实在不能去嫉妒她，而自己却控制不住，这样的自己真的很讨厌……对于一个少女来说，有这样的想法不是再正常不过了吗？你难道不会看着别人亲近自己喜欢的人、为那个人做着自己想做而做不到的事情而难过？多亏乱菊的开导才让她想通。爱的种子已然开始发芽，还需要一个契机让它显现出来，因为井上的爱是默默的守护，如果没有一个契机的话，我想她会一直埋藏在心底吧。于是，乌尔奇奥拉又来了！这么看来，小乌还真是一护和井上爱情的助推者！带走她之前还特地给她时间让她跟一个人告别。当只能选择和一个人告别的时候，她默默地来到一护的房间，面对着睡得不省人事一护，说出了『五世只爱君一人』的震撼告白：黑崎同学，我啊,有好多好多想做的事情哦。我想当学校的老师，也想当宇宙飞行员，也好想去MR DONUTS跟店员说『请给我所有的甜甜圈』，更想去31跟店员说『请给我所有口味的冰淇淋』，要是人生能有五次就好了, 这样的话，我五次都要住在不同的城市，五次都要吃不同的食物吃的饱饱的，五次都要做不同的工作……然后五次都要，喜欢上同一个人。 谢谢你，黑崎同学，永别了。。。这段告白也被评为动漫史上10大无法拒绝的表白，她从来没有后悔对一护的付出，井上从来没奢望一护能够喜欢她，她只求能够亲切的叫一声『黑崎同学』希望一护多看她一眼，她真的是抱着永别的心情去跟他告别的，一想到要永远离开心爱之人了，她忍不住想去亲她，终于又忍住了，可见其心理是多么的挣扎于不舍。其实一开始看到这里的时候，我就预感一护最终的感情归属就必是井上无疑了。在虚圈救井上的时候，一护一次又一次倒在强敌的面前，一次又一次因为井上而站起来战斗，即使自己已经伤痕累累，战斗结束后的第一句话就是问井上有没有受伤。最后在打倒乌尔奇奥拉之后，一护终于牵着井上的手，英雄救美的故事，多美好啊！一直觉得织姬对一护有种直觉上的了解，一护对织姬有种本能上的保护。同样是救人，一护在救露琪亚的时候就没有这么多细节方面的展开，相反倒是引出了露琪亚的青梅竹马恋次，一切都是有伏笔的。好了，我要说的话说完了，建议大家再去回味一遍死神，细细品味，你或许就会发现，井上和一护在一起，没毛病！虽然我也喜欢露琪亚，虽然我也很喜欢妮露！井上已经有一护了，我们就用妮露镇楼好了~~大家好，我是寂寞先生，我会努力给大家带来不一样的动漫资讯，喜欢的朋友可以点击一下关注哦，你的支持就是我最大的动力，谢谢大家的支持！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10486', '0', '终于明白一护未来的老婆为什么是井上织姬了，没毛病！', '死神的烂尾结局里一护和井上这对cp是被无数人不看好的，最后他们在一起的时候都不敢相信露琪亚原来只是打了个酱油，但仔细看看死神还是有很多小细节来促成一护和井上这对的。来看看血战篇里终于揭示的一护的妈妈身份，原本以为是个普通的母亲没想到也隐藏了巨大的秘密，竟然是灭却师家族的才女，一护身上的血统既死神又灭却师还虚，他不屌谁屌，这位母亲和一护老爸的恋爱故事只能用一见倾心来形容了，是事态发展推动的必然。这位母亲长相确定不是井上织姬妈妈吗？眼睛大大的，这发型也超像。这位母亲性格天然呆又超级乐观，最重要的是善良到无论是谁都想拯救，这样的性格还能想到谁？井上织姬必须的了。所以说看到一护妈妈的时候心里没点x数吗，再来看看另一位一护力量的引领人浦原喜助，浦原喜助可以说是各种帮忙撮合井上和一护了，谁都看得出来井上的爱慕之心，浦原喜助也会使点小坏，和夜一是一唱一和的帮助井上，让井上穿的有些暴露的衣服，还告诉单纯的井上一护肯定喜欢，洗刷他们，在血战篇后期也是越来越明显的。除去浦原喜助，后期茶渡的辅助也很明显了。最后事件上，在一护与那些大boss作战时，待在一护身边最多的大概还是井上了，露琪亚身边一直都是大哥大哥，井上和一护行动要多一些，所以说从各方面来看其实井上和一护cp味还是蛮明显的。血战篇之前特别是第一部分刚进入尸魂界，有些先入为主的概念，但其实在解救了露琪亚后，一护和露琪亚的互动就少一些了，基本恋次和露琪亚成双成对的出现，所以说果然女主还是井上啊。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10487', '0', '扳回一局！《死神》真人版井上织姬也太美了吧', '说到即将在7月20日上映的《死神》真人版电影，许多粉丝是头疼不已；之前公布的一护和露琪亚的定妆照简直让人无力吐槽，露琪亚更被称为“史上最丑露琪亚”。大家对这部还未上映的真人版几乎不抱有期待，只希望别毁得太厉害。然而，就在大家因角色们的定妆照失望不已的时候，官方公开的新角色让大家似乎又重燃希望之火了，一起来看看吧！《死神》真人电影官方在今天（5月30日）公开了大家一直关心着的，井上织姬的扮演者。据悉，井上织姬将由长相甜美的真野惠里菜出演，一起来感受下惠里菜的美颜：消息一出众人激动不已，纷纷直言终于有个长得好看的主角了！虽然官方还没有公开她的定妆照，可就冲这长相，也“意外”不到哪去吧？官方还公开了更多的角色演员，长泽雅美饰演黑崎一护母亲·黑崎真咲（话说麻酱已经到了要演母亲的年龄了嘛......）；小柳友饰演茶渡泰虎；江口洋介饰演黑崎一护父亲·黑崎一心；田边诚一饰演现实世界援助死神与虚战斗的商店店长浦原喜助。本次公布的演员看起来都不错的样子，看来还是比之前稍微有点看头了！看到井上织姬鼠绘君才明白，为什么最后一护会跟织姬结婚了......', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10488', '0', '《死神》真人电影追加演员 甜美妹子出演井上织姬', '《死神》真人电影官方在今天（5月30日），公开了新的角色演员，之前大家一直关心的井上织姬的演员终于公开，由长相甜美的真野惠里菜出演，小柳友饰演茶渡泰虎，目前官方还没有公开他们的定妆照，感觉织姬的形象不会有太大的意外。真野惠里菜小柳友官方还公开了更多的角色演员，长泽雅美饰演黑崎一护母亲·黑崎真咲；江口洋介饰演黑崎一护父亲·黑崎一心；田边诚一饰演现实世界援助死神与虚战斗的商店店长浦原喜助。由日本漫画家久保带人漫画改编的真人电影《死神》将于7月在日本上映。日前，官方陆续的公开了角色定妆照和一部PV，虽然在角色的形象上有所变化，但是在PV的视觉效果上还是相当令人期待的。近日，官方再次为大家带来了《死神》真人电影的片场照，一护和恋次激斗的场面，虽然没有特效加持，但这样的战斗也是看的人热血沸腾，而且街景的细节也做的非常到位，真是让人越来越期待《死神》的真人电影了。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10489', '0', '周末小茶 | 《死神》迎来完结，井上织姬或成为最大赢家？', '在98大神无数次放出完结宣言，已经将粉丝们搞得已经对这二字无感之后，《死神》终于迎来了真正的完结。而一直被广大死神粉丝喷成“软弱”、“无能”、“拖后腿的”、“她居然是女主角？难道不应该是露琪亚吗？”的井上织姬，也终于实现了“五世只为君一人”的祈愿……成为一护的妻子。不管如何，结局已定，就祝福他们吧。然后大家都迫不及待地生下了可爱的孩砸……就算没有看过《死神》的人，肯定也有过在笔记本上一脸自我陶醉地抄写什么“拿起刀就抱不到你，但放下刀我就无法保护你”之类话的经历。很多死神死忠粉对这个结局感到不满意，大概是因为大家都不愿意看到自己从少年时期便憧憬向往着的英雄，终于也是过上了柴米油盐的，板寸头型平淡生活。当初火影完结之时，木叶众小强像是配种大会一样地强行组了各自的CP，如今再看死神完结，也引人不禁唏嘘。但，这何尝不是一种好的归宿呢？愿他们都能在自己的世界里，过上幸福平静的生活吧。文/呱蹦☆＊☆＊☆＊☆＊☆＊☆＊☆＊☆＊☆', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10490', '0', '井上织姬，一个永远背后付出的角色究竟有怎样的特点', '井上织姬《死神》中的角色。性格单纯可爱、天然呆又无厘头，总有很多奇思异想，是一个喜好和平的女孩，也总是能给人带来轻松和笑声。能力是头上发夹中的“盾舜六花”，通常用做防御术和修复术，因为她的存在，才能使黑崎一护一行人很快从战斗中恢复，喜欢黑崎一护并对他暗暗许下“五世只爱君一人”的感人誓言，为了保护和支持一护而在不断努力修行，提高自己。织姬的观察力十分敏锐，她可以用灵压感知并准确的指定某人所在的位置，其灵压观察力更是主角五人当中最敏锐的，但是这多数都在同伴遇到危机的时候显现。而且在第二部剧情里，一护周围的灵压发生变化时，她也是第一个发现的。井上是一个在背后默默地付出的角色，在她的生活中鲜有完全是为了自己的活动，不论是战斗还是生活，她都是一个可以堪称完美的角色。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10491', '0', '盘点《死神》中实力强悍的12位女人！井上织姬没资格上榜！', '哈喽大家好～每部动漫中都有那麼些个实力强大的女人，虽然这样讲或许有点性别刻板印象，但强劲的女性角色真的会带给观众反差萌！今天小编就来介绍介绍，《死神》里那些实力强悍的女人们。1 修多罗千手丸2 曳舟桐生3 四枫院夜一4卯之花烈5 赫丽贝尔6 涅里耶尔7碎蜂8猿柿日世里9 志波空鹤10 草鹿八千流11 朽木露琪亚12 伊势七绪每位的顏值都是顶尖，实力也不差！大家最喜欢哪位呢？小编这就先抱走妮露了٩(｡・ω・｡)', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10492', '0', '《死神》相比于井上织姬我总觉得朽木露琪亚更适合一护，最佳cp', '看过死神的应该都不太看好井上织姬吧？前期还行，但是到了后期什么情况？尤其是后来井上织姬救活了黑崎一护敌人的行为，满满的圣母婊有没有，这也不怪那么多人不看好她，能力不行，还拖后腿，每次都要救她，这里我真的想要质疑黑崎一护的眼光了！唉，所以想想人家朽木露琪亚多好，要颜值有颜值，要实力有实力，性格果断，多好的女孩，不得不说黑崎一护没有得到她真的是损失啊！​​​​​​​​不过有个行为我还是比较佩服黑崎一护的，那就是在朽木露琪亚和恋次在一起后，黑崎一护还是和朽木露琪亚保持着暧昧，而且还是极尽暧昧，牛人啊！这样倒是有点像宋一护,马木露琪亚,王恋次啊！到底是主角！这光环666你们觉得谁更适合黑崎一护呢？', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10493', '0', '《死神》真人版选角公布！绝美的井上织姬，将被这一位女神演绎！', '在几天前，拾部君给你们带来了《死神》真人版的最新资讯介绍，从那里头你们也看到了黑崎一护与阿散井恋次因为迸发出强大的死神之力，从而致使他们所在街道的悉数建筑和车辆土崩瓦解，最后沦为了废墟之境的拍摄场面了，在拾部君看来，这是相当之还原的，也是相当让人充满期待的。可是你们啊，胃口无疑是相当挑剔的，在相关资讯的评论下方普遍能够看到的关键字就是“杀马特”，“毁原作”，“黑护斩月陪到变辣条”，“露琪亚被毁得最严重”等等各式揶揄的说法，这确实是让拾部君没少来气，好吧，竟然你们真的脸帅气的巧木白哉，石田雨龙等人如此英姿飒爽的挥刀姿态都能够视而不见的话，那么拾部君就在这一次给大家再度带来更为重磅的“视觉冲击”介绍吧。《死神》当中除了露琪亚还有哪一位女性角色具备着成为女主的潜质？不就是那位于剧情的最后完胜露琪亚的宅男女神井上织姬吗！在最近的信演员名单当中，我们看到了剧组公布了这位宅男女神的选角---同样的，担任这位重量级女主的三次元人物也具备着女神的一切特征，而她就是“真野惠里菜”。剧终的定妆照我们暂未能拿到，不过仅凭真野惠的颜值和身材，拾部君相信一定能够达到神还原的程度，也绝对能够洗刷掉你们先前对《死神》真人版的龌蹉之感。其实嘛，剧组的此番选角安排，在一定程度来看也是相当用心良苦的。站在动漫剧情的结局来看，露琪亚在与黑护的感情上方就是作为loser而存在着，那么在剧情的一开头就安排了这两位女主在颜值上的“天壤之别”，岂不就能到最后，让观众们心安理得的接受吗？厚黑嘴唇的露琪亚，虽说长相也相当清秀，但在井上织姬的映衬下，仍会带予观众相当之多的“相形见绌”的视觉冲击力。最后，在这一次的选角信息当中，剧组还给我们公布了担当黑护老爸和老妈，以及那位最为惹人钟爱的传奇角色“浦原喜助”的定妆图。很显然，饰演老爸的演员太过营养不良了，不过沧桑感还是十足的。而喜助的那位又显得有点儿臃肿了，标志性的斑马纹帽子却是得到了很好的还原。老妈怎样？没了那一头亮黄的头发加持，总会让人有点儿出戏呢。最终效果还得等待成片上映后才能分晓哦！关注【拾部次元】，回复“东京食货”，“东京搜查官”获取全三季动画！国漫神作《史上最强》现已上线各大平台，点击下方【了解更多】即可跳转查看！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10494', '0', '死神漫画你不曾了解过的茶渡泰虎', '高中生，沉默寡言，形象不佳，肌肉男。这往往就是大家对于茶渡泰虎的第一印象，却也极有可能是最后一个印象，因为也许不太会有人愿意去了解这样一个角色的内心，他的过去，他的拳。 年轻时的泰虎很早就失去了父母，前往墨西哥与他的爷爷生活。拥有与年龄不符的形象和天生强悍的力量，却早早的失去了父母的爱，使得他到处惹是生非，以拳，以力来宣泄自己的情绪。直到有一天，他的爷爷使他明白了自己的力量是应该为了保护别人而存在的，直到有一天，一护和他订下了把背后交给对方的男子汉的约定。那时起，茶渡才真正的感觉到了自己的存在。 没有正义的力量是暴力，没有力量的正义是无力。 关于力量 茶渡的力量最早体现为右臂铠甲状，能将灵子类别的物质用自己的拳一气发射出去，有些类似于龟派气功的感觉。大威力，命中差，招数不多变是茶渡的弱点。但截止最新一期漫画为止，茶渡在战斗方面的潜力并没有发挥出来，新招数“巨人的一击”就和冲击波与超级冲击波的关系差不多，单纯的力量进化，这不可能是蒲原在训练中发现的能力。如果将死神中的人物比做经典的勇者斗恶龙，那么一护是剑士石田是弓手，井上是牧师，而茶渡就是力量惊人生命力充沛的狂战士。相信在后5话内他展露自己真实的“身份”，作为人类中极其稀少的一部分存在，应该会有可与灭却师一比的能力。 =============================================================================================== 关于正义 与力量相比，茶渡更让我喜欢的一点是他的正义感。为了保护无人敢于领养的诅咒小鸟，即使看不见，也毅然挥出自己的拳头。为了保护一护的妹妹，初步觉醒了自己的能力。为了与一护的约定，前往他本不该去的尸魂界，即使面对队长级的人物也毫无畏惧之色。这份忠和义，实在不愧为一个真男人。 关于我们的背后 “茶渡，这里交给我了，你先走吧，不是你能应付的” 穿着死霸装，手持天锁斩月的一护，此时竟有些陌生，他已经不愿意将背后交给我了吗？我们之间的约定就这样作废了吗？ 不行，要变的更强，为了男子汉的约定，为了爷爷的话，一定要变的更强.我茶渡泰虎起誓选择保护者的道路,决不后退. 就是这样一个简单,朴实的有些笨拙的男孩子,为了自己的朋友,为了自己的誓言,走上了原本不需要他去捍卫的道路.认真的男人是很帅气的,认真的茶渡当然也是帅气,可爱的.不完美的角色也许更贴近我们的生活,华丽如大白,极端如蓝大的角色固然很有魅力,但也许在茶渡这样的普通人身上,我们更能看到自己向往的影子,自己所能做到和维护的一些东西吧.', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10495', '0', '虚还原度惊人！《死神》真人电影将上线！', '最近《死神》官方公布了三段新预告，视频中都是男主角黑崎一护变身为死神代理后与恶灵——虚展开战斗的场景。虚的造型看上去十分还原动画，相比会获得不少原著粉丝们的认可。在电影中，福士苍汰将饰演黑崎一护、杉咲花将饰演朽木露琪亚、吉泽亮将饰演石田雨龙、Miyavi将饰演朽木白哉、早乙女太一将饰演阿散井恋次、长泽雅美将饰演黑崎真咲、江口洋介将饰演黑崎一心、田边诚一将饰演浦原喜助、真野惠里菜将饰演井上织姬、小柳友将饰演茶渡泰虎。《死神》真人电影将于2018年7月20日在岛国上映，对该作感兴趣的网友们可以关注一下。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10496', '0', '黑崎一护，《死神》真人版2018年7月20日上映（预告公开）', '2018 年 6 月 13 日更新：正式预告公开2018 年 5 月 30 日更新：黑崎一心、黒崎真咲、井上织姬、茶渡泰虎演员公开2018 年 4 月 19 日更新：最新预告公开2018 年 3 月 9 日：朽木白哉、阿散井恋次、石田雨龙扮演者公开2018 年 2 月 21 日更新：特报公开2018 年 2 月 1 日更新：2018 年 7 月 20 日预定上映2017 年 7 月 6 日创建马上随着 7 月 14 日的《银魂》真人版电影上映，一批 Jump 漫画改编的真人电影将在夏天集中上映，银魂之后有《东京喰种》、《JOJO 的奇妙冒险》还有 10 月的《齐木楠雄的灾难》真人电影上映。而之前《死神 BLEACH》真人电影已经拍摄，《火影忍者》也正式宣布由好莱坞改编真人电影，根据最新的消息《死神》真真人版电影将在 2018 年夏季预定上映，福士苍汰扮演的黑崎一护拿着斩魄刀出镜。正式预告预告特报黑崎一护：福士苍汰朽木露琪亚：杉咲花朽木白哉：MIYAVI阿散井恋次：早乙女太一石田雨龙：吉泽亮《死神 Bleach》的漫画是之前完结的几部 Jump 人气漫画中争议比较大的一部，不过久保带人之前提到其实自己之前有一段时间是有点坚持不下去了想要弃坑，但是收到了一位没留下任何信息的身患绝症的少年寄来的信件，在信件中这位少年提到了《死神》的故事带给了他欢乐，他希望久保带人能够坚持将《死神》的漫画画到最后，自己也很想看到漫画的大结局。这位少年的这封信让久保带人又重整旗鼓继续坚持连载，不过现在看如果当初的那位少年能够看到漫画大结局的话不知道有什么感想。在漫画完结的当年《死神》也宣布福士苍汰主演的《死神》真人版电影企划，不过没想到的是，之前就已经在日本进行了实景拍摄的《死神》要放到 1 年之后才会真正的上映。久保带人在《死神》宣布真人电影化的时候开玩笑的说「自己唯一担心的就是一护的头发颜色问题」。黑崎一心：江口洋介黒崎真咲：长泽雅美浦原喜助：田边诚一井上织姬：真野恵里菜茶渡泰虎：小柳友 ​', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10497', '0', '我是死神橙卡茶渡泰虎属性技能图鉴大全', '我是死神游戏有许多不同星级的橙卡组成，从1-5星，5星橙卡最好。现在和小编一起来盘点下我是死神橙卡茶渡泰虎属性技能图鉴大全吧。茶渡泰虎茶渡泰虎(橙卡3星)进阶0灵压等级生命攻击防御灵力敏捷1983350133194940411337033703技能攻击血最少的目标，造成199%攻+4400物理伤害，提升自身10%的防进阶3灵压等级生命攻击防御灵力敏捷46478803280911332940685348534技能攻击血最少的目标，造成205%攻+6800物理伤害，提升自身10%的防进阶6灵压等级生命攻击防御灵力敏捷804881055950518523153231372813728技能攻击血最少的目标，造成212%攻+880000物理伤害，提升自身10%的防进阶10灵压等级生命攻击防御灵力敏捷1340881259972229883247502226522265技能攻击血最少的目标，造成214%攻+10000物理伤害，提升自身10%的防天赋天赋1全属性+8%天赋2魔人的一击品质提升为1阶天赋3全属性+8%天赋4魔人的一击品质提升为2阶以上就是小编为大家带来的全部内容希望对大家有所帮助﻿﻿》》》》更多精彩攻略,敬请登陆搞趣网《我是死神》专区查看！《《《《', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10498', '0', '《死神》真人电影追加演员 甜美妹子出演井上织姬', '《死神》真人电影官方在今天（5月30日），公开了新的角色演员，之前大家一直关心的井上织姬的演员终于公开，由长相甜美的真野惠里菜出演，小柳友饰演茶渡泰虎，目前官方还没有公开他们的定妆照，感觉织姬的形象不会有太大的意外。真野惠里菜小柳友官方还公开了更多的角色演员，长泽雅美饰演黑崎一护母亲·黑崎真咲；江口洋介饰演黑崎一护父亲·黑崎一心；田边诚一饰演现实世界援助死神与虚战斗的商店店长浦原喜助。由日本漫画家久保带人漫画改编的真人电影《死神》将于7月在日本上映。日前，官方陆续的公开了角色定妆照和一部PV，虽然在角色的形象上有所变化，但是在PV的视觉效果上还是相当令人期待的。近日，官方再次为大家带来了《死神》真人电影的片场照，一护和恋次激斗的场面，虽然没有特效加持，但这样的战斗也是看的人热血沸腾，而且街景的细节也做的非常到位，真是让人越来越期待《死神》的真人电影了。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10499', '0', '《死神》中那些燃爆了的时刻，你还记得几个？', '相信各位喜欢看死神的朋友，一定都有着一颗激昂而充满着热血的内心吧？这部动漫也是因为大家看到黑崎一护、斑目一角、阿散井恋次、茶渡泰虎等很燃的角色，再加上一首《number one》，嗨呀，这个夏天不要太燃！哈哈哈，是不是被小编说中了呢？那么，那些让你激动起来久久不能平息的画面，你还记得吗？1、黑崎一护变身成为死神的那一天。各位是否还记得黑崎一护是为了什么才成为死神的？对，没错，他是个珍惜自己家庭的好孩子，为了保护受到虚危害的妹妹，他不得不同受伤的朽木露琪亚达成协议，成为新的死神！就在成为死神的一瞬间，那只恐怖的虚就直接被斩断了一只手臂，一护的觉悟简直太可怕，还有那柄无比巨大的斩魄刀，听说灵力越强，斩魄刀就会越大，一护，你的天赋为何 如此之强！2、在和更木剑八第一次对战中，黑崎一护被完爆，剑八站在原地让他打，可是他却连剑八的皮肤都没办法破坏，这种战斗力的差距，足以让任何人绝望，但是反观黑崎一护，他完全没有放弃的意思，只是感到很震撼罢了，不得不说，这样的心理承受能力，也难怪会成为代理死神，真的心态太好了！然而，在我们都以为他被剑八打败倒地不起的时候，斩月带着一护进入了他的空间里，并让一护重新找回了战斗的欲望，和剑八大战一场，那一首《number one》伴随着慢慢起身的一护，真的要帅瞎小编的狗眼，哈哈哈！3、大家是否记得，在斑目一角对战破面军团的时候曾说过一句话，“在我们番队，和别的番队可是有着本质的区别，别人只有队长才可能卍解，而我们队，除了队长副队长以外，还有两人都可以卍解！”，然后各位可否看到了斑目一角的“龙纹鬼燈丸”，简直太帅了有木有！那个破面还自以为自己的防御牢不可破，可是一角的武器就是以点破面的强大斩魄刀，试问这破面又怎么可能扛得住呢！4、在剧场版《冰轮丸的反叛》中，很多人都看到自己最爱的小白（日番谷冬狮郎）终于当了一次主角，可是却被栽赃陷害，整个尸魂界都对他进行了通缉！在绝地和无奈中，冬狮郎应该怎么办才好呢？而且，在剧场版的最后那里，还有剑八的酷炫镜头哦，还是那么的无解呀，哈哈哈！大家是否还记得其他的精彩燃爆的一刻呢，来和大家一起快乐的分享吧！​', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10500', '0', '《死神》真人电影井上织姬演员公开 颜值完爆露琪亚', ' 漫改真人电影《死神》自从公布以后，可以说是颇受粉丝们的关注，尤其是其中几位主角的演员。男女主演员早已公开，今天官方公布了其它几位主角的演员。女二井上织姬由真野惠里菜出演，茶渡泰虎由小柳友饰演，定妆照尚未放出。真野惠里菜小柳友 官方还公开了更多的角色演员，长泽雅美饰演黑崎一护母亲·黑崎真咲；江口洋介饰演黑崎一护父亲·黑崎一心；田边诚一饰演现实世界援助死神与虚战斗的商店店长浦原喜助。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10501', '0', '《死神》真人电影演员定妆照公开，这画面一言难尽！', 'hello，大家好 我是墨汐，《死神》这部作品早在很久之前就已经宣布要制作真人电影的消息，日本曾经出过很多漫改的电影，可是能够保住口碑的作品确确实少之又少。就墨汐目前看过的那些真人版电影来说，唯一过得去的可能就只有《银魂》这一部。当初《死神》宣布制作真人版电影消息的时候，二次元也是一片哗然，要知道一旦走向真人化的道路，这部作品可能就离被毁不远了。就在今天，这部作品终于公开了《死神》真人版电影的定妆照，看完之后墨汐的心情只能用一言难尽来形容。《死神》真人电影是由拍摄过《Gantz杀戮都市》、《请叫我英雄》等影片的鬼才导演佐藤信介执导，而其中的男主角黑崎一护已由福士苍汰饰演，女主角朽木露琪亚由杉咲花饰演，而其他的角色分别为：石田雨龙由吉沢亮饰演、井上织姫由真野恵里菜饰演、茶渡泰虎由小柳友饰演、阿散井恋次由早乙女太一饰演、朽木白哉由MIYAVI饰演、黑崎真咲由长泽雅美饰演、黑崎一心由江口洋介饰演、浦原喜助由田边诚一饰演。《死神》真人版海报：《死神》真人电影演员剧照：看完这个剧照之后，不知道有多少小伙伴的心情是和墨汐一样的！至于《死神》真人电影的剧情大体上会和漫画相同，真人版电影并不会做太大的更改，而这部作品也将于7月20日在日本上映，至于是否会毁已经目前还不得而知。不过就从剧照来看，人物和动画还是有很大的出入，如果图片上没有字幕的解释，可能很难分清这人到底是谁。我是墨汐，每天为大家带来不一样的动漫解说，欢迎大家前排吃瓜，顺便给个关注吧！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10502', '0', '《死神归来》第三职业--至man男人 茶渡泰虎隆重登场', '自首测以来，《死神归来》的第三职业一直是最受关注的话题，死神友友们也对第三职业的身份有非常多的猜测。《死神归来》新版携同第三职业—— 茶渡泰虎隆重登场。 姓名：茶渡泰虎 英文名：Sado Yasutora 日文名：さど ヤストラ 性别：男 年龄：15岁 生日：4月7日 星座：白羊座 血型：AB型 身高：4750px 体重：112kg 【我很丑，但是我很man】 体型健壮，褐色肌肤，留着卷曲的褐色短发，褐色的眼睛经常被刘海覆盖，左肩有一枚被蛇缠绕的双翼心刺青，时常身穿一件无袖贴身背心亮相。茶渡并不是一个人见人爱的大众情人，甚至刚刚登场的时候，都不被喜欢。但是到了剧情往后，细细品味下来，缺发现他真的是一个不折不扣的真汉子。 【我不善言辞，但是我最讲义气】 茶渡童年时代非常暴力，也为自己的肤色与强壮的体格感到困扰，但在祖父的教诲下改变了暴力的性格，并且开始将自身的强健身躯视为荣耀。在祖父逝世后返回日本生活。国中时期曾被一群不良少年找麻烦，后来因为一护出手替他解围，自此与其约定“为了对方而挥拳”。他不是最瞩目那个领头人，但是一定是默默在为了大家而努力的那一个！虽然曾经因为受到月岛的能力影响和一护兵戎相见，但是这并不能改变他对友情的忠贞。 【我外表粗狂，但是内心柔情】 茶渡性格温和，学业良好，在校成绩名列第11名，喜欢番茄、小动物，以及可爱的事物。特别是从朋友手中获得被男童灵体柴田勇一附身的鹦鹉，并一直保护着他。相信就是这个情节感动了众多的女死神粉。如果是生活在现在，茶渡确实是个很耐人寻味的暖男。 死神友友们，看完这个，你是不是也爱上了这个认真的性情小伙子呢！来《死神归来》和他见面吧！ PS：以上资讯最终解释权归《死神归来》所有！ ', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10503', '0', '《死神》真人电影追加演员 长泽雅美/江口洋介/真野惠里菜参演', ' 《死神》真人电影官方在今天（5月30日），公开了新的角色演员，之前大家一直关心的井上织姬的演员终于公开，由长相甜美的真野惠里菜出演，小柳友饰演茶渡泰虎，目前官方还没有公开他们的定妆照，感觉织姬的形象不会有太大的意外。真野惠里菜小柳友 官方还公开了更多的角色演员，长泽雅美饰演黑崎一护母亲·黑崎真咲；江口洋介饰演黑崎一护父亲·黑崎一心；田边诚一饰演现实世界援助死神与虚战斗的商店店长浦原喜助。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10504', '0', '长泽雅美出演黑崎一护他妈，动画电影《死神》演员阵容全公开', '久保带人的《死神》改编的真人电影最近又公开了新的演员阵容，主角黑崎一护的父母、浦原喜、井上织姬、茶渡泰虎的扮演者纷纷公开。和上次仅仅公布男女主角，引来大范围吐槽不同，这次的演员阵容还是很令人满意的。  特别是长泽雅美饰演一护的母亲真咲，可以说是引爆了包包的关注点。热血父亲黑崎一心由出演《孤狼之血》的江口洋介饰演，在城市角落开店，帮助死神的店主浦原喜助由田边诚一饰演，长相可爱的同学井上织姬由真野惠里菜饰演，同学茶渡泰虎由小柳友饰演。也有很多人吐槽演员的还原度，觉得不如Cosplay。11区的coser在这个领域可以说是非常有话语权了，对于这种动画改编电影来说，很多角色其实都可以让coser来扮演。·《死神》真人电影将于2018年7月20日上映，导演佐藤信介，主角黑崎一护由福士苍汰饰演，朽木露琪亚由杉咲花饰演。说到期待死神动画，其实包包最近也在期待一款死神动画改编的手游《Blech 境界激斗》，游戏首先有正版授权，不然包包也不会玩（拒绝盗版）其次在画面、人物技能还原度等方面都做得很好，还有全程日文配音。偶尔打开玩一下还是不错的。现在游戏还在封测，等出来后，包包准备做一次评测，给大家试试水~包包比较期待的一款手游', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10505', '0', '《死神》四枫院夜一到底有没有战魄刀？你见过她的斩魄刀吗？', '瞬神夜一在最终章《千年血战篇》中展现其他人从未见过的奥义卐解。春水对一刃说过卍解一般是最后杀手锏。作者也形容夜一的卍解是奥义，作者隐藏那么多年都没让夜一使用斩魄刀，卍解肯定是其最强绝招。在夜一的回忆中她腰间也确实佩戴了一把短刀，至于是不是斩魄刀尚未确认。既然有斩魄刀，为什么夜一从出场到现在一直没拔过刀呢？可能性有以下几个：第一：无需用刀。夜一的瞬步白打都是尸魂界数一数二的，鬼道也不差。单凭这三项技能的使用就足以战胜队长级的人物碎蜂，何须拔刀呢！问题是夜一并非无敌，她也有吃败仗的时候，所以第一种可能性应该不成立。第二：不能用刀。也许夜一斩魄刀另作他用，也许夜一斩魄刀正处于冷却期，也许解放斩魄刀需要额外一些条件限制。所以无法使用，也许四枫院家族和神道家族伊势家一样世世代代仅一把斩魄刀。夜一作为昔日尸魂界的叛徒，被除了名无资格再用斩魄刀，不在身边等等。现任四枫院家当家，夜一弟弟夕四郎曾带着一个大包裹找夜一，浦原喜助说你带的东西一定能给夜一带来帮助。可能夜一斩魄刀就在其中！第三： 不敢用刀。死神官方小说《灵魂与你同在》苦屋敷剑八因为卍解会无视敌我吞噬自身外所有生命体非生命体目标。假如夜一斩魄刀解放需要付出更惨痛代价，比如夜一斩魄刀万一有使用次数限制等等。我觉得还有一点就是拿去卖了，把资金投注在浦原商店上了！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10506', '0', '死神：盘点尸魂界最古老的四大家族', '尸魂界存在四个最古老的家族也称四大贵族，与王族血统相近，本身互相亦为亲戚，代代皆出具有不凡灵力、或能力出众的人，接受英才教育，于护廷十三番队及其他组织担任要职，捍卫王室，身怀为尸魂界大众立下榜样的义务，惟近代成员不是被尸魂界流放就是无子嗣。朽木家、“天赐兵装番”四枫院家、管理大灵书回廊的纲弥代家都位列其中。志波家曾经也是四大贵族之一，如今已被排除，在志波氏没落以前，总共有“五大贵族”。朽木白哉朽木家：朽木家为四大贵族之首，与王族关系最密切，权力也最大，成员在所有贵族中最受敬仰，仆人众多，是所有死神的典范，以维护与执行尸魂界的定则为职责。现已传至第二十八代、也是史上最优秀的继承人——朽木白哉，不过白哉及其亡妻绯真夫人没有子嗣。其他已知成员仅有与朽木家并无血缘关系的露琪亚。传家之宝为银白风花纱。朽木家代表有朽木银铃（前任六番队队长，白哉的爷爷），朽木苍纯（前任六番队副队长，白哉的父亲），朽木白哉，朽木露琪亚。夜一四枫院家：人称“天赐兵装番”，有许多的神兵利器，极擅长暗杀,以其鬼魅般的速度,精准的刺杀,巧夺天工的攻击道具独树一帜。现任家主为第二十三任家主的四枫院夕四郎。上一任家主为四枫院夜一，夜一擅长白打与瞬步，有“瞬神夜一”之称，和原十二番队队长浦原喜助是青梅竹马的好朋友。黑崎一心志波家：志波家原是尸魂界统治支配阶层的四大贵族世家之一。现已没落，在流魂街西方地区居住。志波家对武器的了解和活用堪称尸魂界的顶尖，可惜衰败速度惊人。代表人物有志波海燕，志波空鹤，志波岩鹫，志波一心（黑崎一心，一护的老爹）。黑崎一心是志波家分家的家主。纲弥代时滩纲弥代家：尸魂界四大贵族之一，负责管理大灵书回廊。知晓尸魂界的一切历史与情报的家族，和灵王与四十六室有不可分割的关系。代表人物纲弥代时滩，出现于出自《BLEACH》番外小说《Can’t Fear Your Own World》。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10507', '0', '死神：曾退出过护庭十三队，他们一度是队长中的佼佼者！', '1、东仙要九番队队长，先天盲眼，也就是说唯一真正可以不受镜花水月影响的人，坚持自认为正义的阵营而跟随蓝染。空座大战中卍解+虚化的力量完全压制狛村，后被其副队修兵偷袭致死，临死前回光返照，生平第一次开眼见到了世界。2、狛村左阵七番队队长，犬面人身。千年血战篇中习得人化之术，卍解也进一步进化成“天谴明王-断铠绳衣”，以超强的力量击败了邦比爱塔。后似乎力竭，生死未卜，但七番队队长一职已换人。3、平子真子五番队队长，110年前因蓝染的虚化阴谋而被害，被浦原喜助救出后无法回到静灵庭，空座町大战中率生力军假面军团与蓝染大战。血战篇中没有怎么表现，直到篇末也未展现卍解，是极少数没有展现卍解的队长之一。4、市丸银三番队队长，不亚于日番谷的天才型死神（一年从真央灵术学院毕业），表面上伙同蓝染叛逃，实际上是为了心爱之人。空座町大战中与黑崎一护的战斗使得黑崎一护战意丧失，刺杀蓝染并成功的给予了蓝染重创，却也使其更进一步的进化，最后被杀。与东仙要不同的是银应该没有被赋予虚化的能力（估计是觉得丑吧）。5、四枫院夜一前任二番队队长，浦原喜助的知己好友，直到结束也未展现其斩魄刀，也许其本身就没有斩魄刀，但是超强的体术能力和机动性在死神中也是数一数二。血战篇中与D的战斗表现也颇为亮眼，当然，店长有很大的成分在里面。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10508', '0', '给日暮戈薇、志村妙、岬太郎、四枫院夜一配音的雪野五月来天朝啦', ' 现在想想，除了朋友推荐给我的入宅作——《犬夜叉》之外，或许我今天还不能够写30多篇《犬夜叉》系列漫评吧，所以她也就成了我第一个喜欢的CV——雪野五月！厦门国际动漫节 就在刚刚，在群里看到了雪野五月姐姐来天朝的消息——受11月18日厦门国际动漫节的邀请，雪野五月姐姐，再一次被邀请到了天朝！ 上次受邀的时候，还是在第七届萤火虫动漫嘉年华的时候，那个时候什么都不懂的我，看到了雪野五月姐姐居然就在漫展里面当嘉宾，感觉好困惑，疑？雪野五月姐姐来天朝了？？？？而那个时候根本就没看过宣传单页，所以在现场看到图片的时候，感觉好奇怪啊！于是就去问了，才知道——原来还有座位啊！真好啊！ 那么我们一起来看看雪野五月姐姐的一些主要CV作品吧：戈薇 1、《犬夜叉》日暮戈薇： 缘叶在《桔梗和日暮戈薇之间有着鲜明的对比》中说过，戈薇有一种独特的、正面的、积极向上的、有目标性的感觉！所以这种正能量，让犬夜叉团队一行人有了冲劲，有了想法，有了不可或缺的核心！当然，如果你还像查看更多犬夜叉动漫评论，一定要关注《缘叶二次元》里面的动漫简评哟。妙姐 2、《银魂》志村妙 如果说《银魂》这部作品是集热血、搞笑于一体的超级系列大作，那么作为大姐头的志村妙就是最强大的boss（我用的小写字母）！为什么呢？他的黑暗料理我想应该能秒杀所有人吧~黑猫 3、《死神》四枫院夜一 我去，雪野五月姐姐好像很喜欢配大姐头哇，比如《中华一番》（中华小当家）里面的梅丽、《纯情房东俏房客》乙姬睦美等等，都是大姐头！ 而且对于雪野五月姐姐来说，她配音的四枫院夜一，在个性方面也有随、开朗、沉稳、强势呢！感觉雪野五月姐姐比较合适配大姐头~足球小将 4、《足球小将》岬太郎 刚刚查资料的时候才看到，原来我们的童年男神——岬太郎居然是雪野五月姐姐配音的啊！而大空翼的那句：“足球是我的朋友，是不会交给你的”！对于也对岬太郎来说，也有着同样的评价吧！而雪野五月姐姐当时给岬太郎配音（前几个月就补习过足球小将）的时候也没发现是她配的，嘛，不知道你有没有兴趣再去补习一下这部童年作呢？犬夜叉 其实对于雪野五月姐姐，缘叶我本人了解得最多的，还是来自于《犬夜叉》里面的这个女孩子——日暮戈薇！而这次无意中看到居然又来天朝参加11月18日的厦门国际动漫节也真是感到高兴呢！不过由于社畜的关系，是赶不上去的了，但是依然感谢他们能够邀请到雪野五月姐姐来天朝，所以要是你们也喜欢她的配音作品的话，一定要看看哦！ 来源：缘叶二次元 http://www.dmacg.net/ycdmwz/4878.html 欢迎点击下面的链接，查看更多犬夜叉系列文字。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10509', '0', '看来是要将不要脸进行到底了！', '“我也是为了她能过得好一点。你傍上严谨不也是为了过好日子？”我警告自己不与傻瓜论短长。“钱和房子，你选一样。”我再次重申，这是我能做的最后让步，我不是圣母！“钱和房子，我都要！我几年的青春都被你们李家给耽误了！”看来是要将不要脸进行到底了！我将离婚协议揉成一团，站起来狠狠砸在他脸上！“你又要钱又要房子！凭什么！你这条癞皮狗！”陈母跳过来推我，“干什么！你还想打人是不是！你这种农民生的女儿太没教养了，居然敢动手打人！欺负我们阿源老实本分是不是！”我也不想客气了，反手就将陈母推开，她一下就摔回坐在了自己的沙发位上！“你敢打我！”陈母又开始大喊大叫。我是医生，这个行业基本就是男人当女人用，女人当牲口用，和老师那个群体差不多，肾不好，体力不好的都干不了。我真要跟她使蛮力，陈母未必打得过我。许珍珍撑着大肚子站起来，不停问陈母伤到哪里没有，要不要去医院。我把纸团扔到陈源脸上的时候，外面一辆玛莎拉蒂停到了咖啡厅外面，车上下来的女人飞快的跑进来。是又妍！“干什么！三个欺负一个是不是！”又妍踩着高跟鞋，甩着大波浪气势汹汹往里面走。他们看到又妍，都有了忌惮的神情，许珍珍倒是胆子大了起来，“盛小姐，我们之间的私事，请你不要插手。”又妍也不怕大肚子惹不起，直直走到许珍珍跟前，“我这个人就喜欢多管闲事，怎么了？”许珍珍惧怕又妍，心慌慌的退了一小步，“你再敢靠近一步，我肚子痛了！”又妍又往前走了一步，“你痛啊！你痛啊！我们微微和陈源这个渣男还没有离婚呢！你个小三顶个大肚子倒是跑到原配头上来拉屎了！你是不是欠骂啊？啊！”“你胡说什么！我和阿源五年前就认识了！”又妍哼了一声，“我还十年前就听薛之谦唱认真的雪了呢，特么的他都离婚了我也没上位！我几年前就认识杨洋了，他现在都还没有娶我！你还要跟我比比我更早认识谁吗？”我看到又妍的潇洒和肆意，安心又羡慕。我是医生，紧张的医患关系中，医生往往要忍气吞声。我很少跟别人对立，就算对方已经欺负到头上了，还是要选择隐忍。我有好几同事被患者家属打了，都没有还手，只要敢还手，网上流传的视频就是医生打患者家属，没有王法了。我们这个群体早已习惯了，这两天，陈源一家人却把我逼得脱胎换骨。可即便脱胎换骨，应付这家子人，我还是力不从心。但又妍就像战斗机一样，谁都不怕。又妍指着许珍珍的肚子，“你肚子里怀的这个孩子，大不了就是流产，我大不了赔你一点钱。你们这么喜欢钱，这个孩子我就赔你们一套房子的钱，你们觉得值不值？”许珍珍真的被吓住了，“你不要乱来。”又妍撩了撩她的大波浪长发，“我就喜欢乱来啊，哪里乱我就喜欢往哪里扎堆！现在就你们这一堆乱，所以我来了。你看看你哦，长得没我们微微好看是事实吧？你不会连这点自知之明都没有吧？男人说喜欢你的性格，你就信啊？你性格能比微微好？你性格要是比微微好，会不要脸的跟着窝囊废来要房子？他不会是也看上你的钱了吧？话说，你不会真是皇族后裔吧？”又妍半认真半讽刺的话气得许珍珍直哆嗦。陈源和陈母大怒，“你不要血口喷人！”又妍朝着陈母“呸”了一嘴，“我从来不喷人，只喷畜生！你以为许珍珍不知道你们是图钱么？只是她长得太一般了，所以没办法，陈源这种长得好看的男人，但凡有点本事的，会看上她这种颜值？”许珍珍气得直抽抽，我觉得这样骂许珍珍有点太狠了，可许珍珍也是活该。陈母举起巴掌要打又妍，又妍弯着要把脸伸到陈母面前，“来！来打！盛世集团的盛家小姐就在这里，你问问你儿子，这一巴掌你打不打得起！你们打得起，就一巴掌拍下来，我保证不还手！但是！后果自负！”陈母紧张的看着陈源，陈源咬牙把陈母举起的手拉下来，“妈！算了！”我忍不住想笑，这种不要脸的，也只有在又妍这种天不怕地不怕的女人面前才会老实得像狗。呸，这是狗狗被我黑得最惨的一次。又妍指着许珍珍，“你说说你长得不好看，要是流产了，万一不能生育的话，陈源还会不会要你？万一骗你结婚了又要跟你分共同财产，你没个孩子绑住他，怎么行？”许珍珍气得快哭了！她转头瞪着陈源。陈源已经脸红脖子粗，“盛又妍！你够了啊！”他伸手揽住许珍珍，轻声哄道：“珍珍，你别听她们的，我有多爱你，你心里还不清楚吗？我对你多好你还不知道吗？你在我心里比谁都美，比李见微漂亮多了！”又妍夸张的“呕！”了一声，“可拉到吧，瞎子也能看出来我们微微比这个小三好看 N 倍好么，不信你问问这些服务员啊！不就是开了宝马，家里比我们微微有钱么？”服务员看着这边的笑话，都比较有素质的转身过去笑。许珍珍今天怕是要被气死了。又妍嘴毒，没几个仇家惹得起，算他们运气不好。看着陈家人被又妍压得不敢还手，我莫名的爽。又妍自环着双臂，敲了敲桌子，“签个离婚协议而已，你们快点！签了我们就撤了。”我耸耸肩，又妍问，“怎么？谈不好？”陈源细框眼镜下的那双眼睛显露着小人之光，许珍珍恢复了战斗力，挺着大肚子走到了陈源身后，她瞪着我，“李见微！你有完没完！到底签不签！”“陈源得寸进尺！我肯定不签！“又妍看着陈许二人，朝着他们走过去，弯腰捡起地上的纸团打开，一条条看着细则，然后抬起眼皮瞄了一眼陈源，“你写的协议？”陈源承认得爽快，“是啊。”书名《彦希澈之无声》推荐阅读： 宇智波真一承认当年喜欢上队长时，年纪有点不对劲。 但这件事不能怪他啊！真的不能怪他！要怪就怪明明手上有资料却没告诉他队长真实年纪的兄长宇智波富岳啊！ ――不管是富岳还是日足都没告诉两个弟弟关于队长的年纪，他们生怕两个弟弟因年纪轻看队长以至于出问题=v= 这里要说一下木叶的暗部制度，暗部出门在外都带着面具，面具将脸盖的严严实实的，而忍术又是那么神奇，鬼知道面具下的人到底长什么样，至于露在外面的身高……砂忍叛忍赤砂之蝎海拔也不高，人家活了一把年纪还是一张正太身高和脸呢！ ――在忍者世界，身高和面容甚至性别都不能作为判断年龄的依据，想想纲手姬和大蛇丸……呵呵。 所以当年宇智波真一进入暗部，见到自己的队长时，根本没有意识到面前的队长是个真?正太。 毕竟他和日向日差是木叶两大名门嫡传弟子，日差虽然是分家，但他哥哥可是宗家族长，宇智波真一自己也是宇智波嫡传，族长宇智波富岳的亲弟弟，还开了两轮写轮眼，实力不低。 作为日向日差和他宇智波真一的队长，这样的暗部队长实力怎么会低？这样的暗部队长的年纪怎么会小？ 或者说木叶高层怎么敢弄一个真?幼齿?正太?队长来带他们俩人？ 所以一开始，不管是日向日差', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10510', '0', '盘点动漫中那些巨蟹座的帅哥！', '黑崎一护《死神》​一护除了缺少阳光般的开朗性格外，经常因为天生怪异的发色而被人误认为是不良少年，因为有着痛苦的过去以及因为父亲而锻炼出来的强大打斗能力，完全无谋或出于本能的作战法则，但在浦原喜助和四枫院夜一的特训下有所长进。七濑遥《Free!男子游泳部》性格其实很单纯，安静地喜欢自己喜欢的东西，保持自己的个性。喜欢活在自己的世界里面，有的时候也会有小害羞，实际上跟小孩子一样，在不熟悉的地方会因为害怕迷路而慌张。朝日奈风斗《兄弟战争》个性傲娇毒舌，有着与年龄不符合的色气。气质颇为盛气凌人，偶尔也会撒娇。经常调戏绘麻，第3集中曾说过绘麻很值得戏弄，但是同时非常喜欢绘麻。第10集时险些做出越界的事，但想到绘麻说过只想做兄弟而落泪离开。富樫勇太《中二病也要谈恋爱》性格开朗、富有正义感、温柔但有点迟钝，善于照顾人和做各种家务。妄想时的设定为“漆黑烈焰使(Dark Flame Master)”，曾妄想右手封印着黑炎龙而在手臂上绑着绷带，中二时喜欢穿黑袍，武器是一把大剑，口头禅是“被漆黑烈焰吞噬殆尽吧！名濑博臣《境界的彼方》与美月同样监视着神原秋人，曾经似乎与秋人有些纠结，但如今是可以彼此开玩笑的关系。喜欢把双手放在秋人腋下取暖，并经常为此遭到秋人吐槽。但其实是由于异界士能力的影响导致通身害怕寒冷，时刻带着围巾，但围巾也常常作为武器使用。极度的妹控，对美月非常疼爱。平时一副开玩笑的样子，但做事的时候很精干。实际上很会照顾人。佐田恭也《狼少女与黑王子》外表光鲜亮丽，是学校女生心目中的王子。其实是腹黑属性，S星人，而且十分别扭傲娇。由于某天在大街上被筱原绘里香偷拍后来发现与她在同一所学校就读。知道了绘里香是个满口谎言的女生，之后以让绘里香当自己的“狗”为条件同意她的请求，假扮其男友，称绘里香为“我的狗”“仆人”。及川彻《排球少年！！》青叶城西高校3年级，排球部主将，超攻击型的二传手，攻击力也是队伍中的顶级水平，影山中学时期的前辈、杀人发球的师父（被日向称为\"大王者\"）。八田美咲《K》性格直率的行动派，有着童颜，同伴概念尤其强烈，崇拜周防尊，不喜欢别人叫自己后面的名字（misaki）。见到女孩会害羞，意外的纯情。夏目贵志《夏目友人帐》温柔的少年，为人温和，话少内心感情却十分丰富，希望和人类或是妖怪可以友好相处，善良，为他人着想，乐于助人，经常帮助妖怪而引起麻烦。坚强，注重友情、亲情。温暖，性格治愈。即使从小就饱受欺凌，甚至到现在仍会不时地做噩梦，但却从未怨恨以前欺负过他的妖怪或人类。潮田渚《暗杀教室》心细，观察力优秀，平时一直在记录杀老师的各种弱点，展现出不同于其他同学的暗杀才能，打败了鹰冈老师。（隐藏杀气接近目标的才能，用杀气将对手彻底吓瘫的才能，在“正式下手”时毫不畏惧的才能。）平时很有大人的样子，对于引人注目这件事很是苦手的温柔男子。奇犽《全职猎人》​奇犽平时悠哉，嘴巴有些恶毒，喜欢捉弄人，对涉世未深的小杰感到头痛，心底其实是个很温柔的人。对比自己强的敌人有相当的恐惧。同时被比司吉指出奇犽的弱点就是太容易放弃。喜欢吃巧克力糖球，讨厌蔬菜。寿岭二《歌之☆王子殿下》早乙女学院的毕业生，是音也和时矢的指导者。性格超级阳光，细心，对任何人都是友好的。总是有种乐观的心态。喜欢用昵称称呼别人。古河秋生《CLANNAD》古河渚的父亲，和妻子古河早苗一同经营着面包店。性格豪爽、幽默，头上竖立着两根呆毛，喜好棒球和捉弄早苗，与早苗的戏谑成了小镇远近闻名的搞笑组合。杂贺八寻《S.A特优生》是个喜欢扮坏人的腹黑男生。小时候喜欢一起长大的明，因为帮助明身边赶走明身边想要利用明的人，明与他产生了嫌隙，之后约定如果不是我来找你就不要见面。杂贺八寻把对明的爱恋放在心底，用自己的行动来保护她，在明感到疑惑时再假装坏人，只为不让明忧心。埼玉《一拳超人》外貌除光头和吊眼的特点外非常普通，没有一点强者该有的性格（因为感情在消失），平时出战身穿一件很土的黄色超人套装。把成为英雄当做是自己的兴趣，拥有着远超常理的强大实力，对付绝大多数敌人只需要一拳。日常生活中，为人散漫，但在关键时刻，拥有着身为英雄的觉悟。​动漫中那些巨蟹座的帅哥，你喜欢哪个呢？欢迎大家继续补充。。。。。。。。。。。。。。。。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10511', '0', '第三期：说说那些你认为最好听的动漫人物的名字？', '人名是给人的第一印象，就像是一个标签贴在身上，而动漫人物的名字对我们这些二次元者也是有着深深的影响力的，无论他或她在番中的角色定位，只要听见就会让人感觉很舒服。所以大家努力想想让自己印象深刻，无法遗忘的动漫人物名字吧！本文先列举一些大家熟悉的动漫人物，快来为你喜欢的人物疯狂打call吧。欢迎大家在下面评论点赞~不知道动漫作品的欢迎在评论区留言~感谢大家的关注和支持！1.御坂美琴2.灰原哀3.白井黑子4.风鸟院花月5.桔梗6.名濑美月7.秋山澪8.四枫院夜一9.夏目贵志10.夜神月好了，上面就是今天小编为大家推荐推荐的一些好听的动漫人物名字。ps:动漫作品也都很不错噢~各位小伙伴还知道哪些可以在下方评论与大家分享哦~喜欢该文章和动漫视频的可以关注我，观看以前的视频作品，咱们下期再见~', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10512', '0', '死神：护庭十三队实力相差无几的主战力，他们是队伍的中坚力量！', '1、四枫院夜一拥有瞬神的称号。从和碎蜂交战可以看出即使不解放斩魄刀，凭借白打和瞬开 也具有压倒性的力量，凭借无人能敌的速度 可以压制很多对手，解放了斩魄刀 实力很可能要上好几个台阶。在千年血战篇，也是对付亲卫队很重要的战力，卍解没展示很遗憾。2、市丸银基础实力非常不错，神枪具有超快的速度和长度，闪避困难，卍解后几乎具有一击必杀的实力。3、平子真子基础实力还是很雄厚的，再加上斩魄刀的能力具有对敌人极强的干扰性，对多数对手都会造成极强的判断干扰从而重创之。战斗的次数很少，卍解应该很强。4、浦原喜助店长的鬼道和基础能力都非常逆天，红姬具有多种攻击防御效果，应该说是开发的比较完全的斩魄刀，卍解也是压轴的几个卍解。再加上出色的分析能力和科研能力，能从容不迫的应付强大的对手。5、浮竹十四郎目前对其战斗的展现只有对史塔克一场。由于与春水同期并且经常并肩作战，因为推断实力和春水也不会相差太多。具有较强的分析能力，基础实力应该也不会弱。双鱼理始解能力对付鬼道系和能量波系的敌人有很强的防御反击能力，卍解相信也会是略逆天的刀，不过很可惜看不到了。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10513', '0', '死神：全面分析死神智慧担当', '浦原喜助，为护廷十三队二番队第三席，兼隐密机动第三分队‧槛理队分队长，负责看守被囚禁在蛆虫的巢穴当中、没有犯错而又危险的死神。曳舟桐生晋升零番队之后，浦原担任十二番队队长兼技术开发局局长，110年前事件发生后，为救助被蓝染惣右介算计的平子真子一行人而被流放现世，虽然来到现世，但一直密切关注各方的动向。浦原喜助与四枫院夜一以及流魂街的烟火师志波空鹤是熟交，他和夜一等人虽然不算是主角小队，不过也经常出现，穿插在主剧情中，当主角们遇到重大危机时才现身帮助扭转战局，经常帮助训练一护，帮助其成长。夜一科技发明：浦原喜助就像哆啦A梦，经常拿出很重要的科技发明，例如便携式义骸：像气球一样可快速充气的义骸，在战斗时可当成替身使用；崩玉：与蓝染的半成品合成完全体崩玉，全面提升了蓝染的实力；义魂丸：包含改造灵魂的药丸，吃掉此药丸的身体或玩偶受改造灵魂的意识支配；侵影药：千年血战篇帮助被夺取卍解的死神夺回卍解（神药啊）。在他众多发明当中，有一种叫“转神体”的人型器具，能将斩魄刀的本体强行抽出并物质化，以便使用者令斩魄刀降服。据四枫院夜一所说，浦原喜助透过这个器具，用了三日便完成卍解。观音开红姬改斩魄刀：红姫有多种始解语，不同的始解语对应不同的功能通常以“啼叫吧、红姬！”解放语解放出来的红姬，能够用刀身释放出类似血的物质，浦原擅长使用此赤红物质来使用攻击技能，同时也是浦原最常用的解放语。另三种解放语是“切裂吧、红姬！”“缠绕吧、红姬！”“玩火吧、红姬！”。卍解：观音开红姬改千年血战篇中初次使用，施展卍解后，喜助可于斩魄刀的卍解能力范围内，将任何被斩魄刀触及的事物（包含自己在内的活体生物和建筑物等）重新建构改造，被其改造的物体会出现类似针线般缝补的痕迹。可用于将指定的对象身体重新建构，达到重创敌方的效果，还能修复己身伤势和重获行动力，也能够借此改造周围环境建筑物，制造陷阱和开拓道路对敌方展开突袭。曾形容自己的卍解不适合拿来协助修行或是帮助他人。和涅队长一样都属于实体化卍解，能够将“她所碰触到的物体”重新改造的能力，不仅限制于肉体，同样可作用于其他物体。665话中浦原喜助将自己已经“即死”的眼睛改造修复，并在“极上毒药之球”内向外改造出一条能从球外一口气攻进来的道路。浦原喜助鬼道：漫画中也经常使用番数较高的鬼道，例如破道之九十一 千手皎天汰炮；九十六京火架封灭：由浦原喜助专门为封印蓝染制作出的鬼道，封印了被无月一护削弱的蓝染。浦原喜助是死神里面智慧最高的人，以其“手段”--能用层出不穷的未知手段干净利落地解决一切难题与困境，被友哈巴赫视为五大特记战力之一，被蓝染称作唯一智慧超过自己的人，战绩辉煌，破面篇封印实力被削弱的蓝染，千年血战篇带队击败圣别后的“D”亚斯金·纳克鲁瓦尔。浦原喜助就像是团队中的辅助，在队伍最需要他的时候登场，以其智慧科技发明帮助团战取得胜利。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10514', '0', '盘点动漫中出现的紫发妹子们，这次口味有些重！', '大家在平时看动漫的时候有没有注意过人物的头发总是五彩斑斓的，红的黄的绿的都有，那今天你们有福了，小编给大家盘点了几个紫发的妹子，看你都认识她们吗？藤林杏第一位，藤林杏，出自《CLANNAD》。藤林杏是一个开朗阳光的元气少女，做事利落有劲，有一个相同发色的妹妹藤林椋，什么事都以妹妹为优先。她是男主角冈崎朋也隔壁班的班长，在打闹之间逐渐对其产生了好感，但在发现妹妹也喜欢朋也之后却努力撮合她俩，真是个好姐姐！雾切响子第二位，雾切响子，出自《弹丸论破》。响子有着紫色的长发和瞳孔，皮肤雪白的她像一个冰美人，加上总是戴着黑色的手套不摘，总是给人一种很神秘的感觉，一直觉得她很高冷。响子和其他学生不同，总能客观的看待事物，看到尸体也不怕，还总是帮着男主角破案。四枫院夜一第三位，四枫院夜一，出自《死神》。四枫院夜一初次以黑猫的形象登场，后来变回了一个大美女，她的背景也很厉害，是原护廷十三队二番队队长，和浦原喜助是青梅竹马，俩人一起在110年前离开尸魂界。同时她也是前隐密机动总司令官，在与现任碎蜂对战时也是宝刀不老，完胜！神代利世第四位，神代利世，出自《东京食尸鬼》。神代利世初次登场是举止文雅的眼镜娘，温柔，还喜欢看书，不过这一切都是为了迷惑金木所做的伪装，她意图把金木骗到小巷子并食用，结果遇上事故，自己的器官被移植给金木导致其也变成了食尸鬼，后来总是出现在金木的思想中，而其实她并没有死。猿飞菖蒲第五位，猿飞菖蒲，出自《银魂》。拥有紫色长发的菖蒲是御庭番的女忍者，实力也不算弱，但是一旦把眼睛摘掉，她就看不见人也听不清声音了，经常因此而误事。她尤其喜欢纳豆，还总是给自己玩捆绑，对银桑有着蜜汁暗恋，总是把自己“送上门”。出现在吐槽番中的她大部分时间都在浪，但在关键时刻还是会表现的很认真。间桐樱第六位，间桐樱，出自《Fate》系列。这个小樱有着紫发的长发，性格温柔但有些自卑，她有个不争气的哥哥间桐慎二，还有一个变态的祖父间桐脏砚老头子，虽说有个热心肠叔叔间桐雁夜，还是让她逃不了悲惨的命运。“圣杯可以不要，老头必须死！”美杜莎第七位，美杜莎，出自《fate》系列。美杜莎被间桐慎二以Rider职介的从者身份召唤，武器是锁链短剑，拥有对军宝具骑英之缰。在《Fate/Stay night》UBW线上，她在自己的鲜血神殿上被奇袭的葛木宗一郎了解，退场时间之早让人惊呼。这个美杜莎是希腊神话里登场的恐怖女怪，可以通过窥视他人眼睛将人石化，而传说本是女神的她是受了雅典娜的诅咒而堕落成了怪物。雅典娜第八位，雅典娜，出自《圣斗士星矢》。每当邪恶降临，都会有雅典娜的转世来阻止黑暗，当初双子座撒加篡取了教皇之位，并要杀死还是婴儿的雅典娜，多亏黄金圣斗士解救并托付给日本财团首长，他为这个婴儿起名为城户沙织。这个城户沙织在日本长大，并与青铜圣斗士们一起在战斗中成长，她是他们的女神和精神领袖。今天小编就说这么多，大家有什么想吐槽的就在评论区留言吧！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10515', '0', '死神中几个出场特别少，却起到很大作用的角色', '死神中有的角色出场次数屈指可数却起到很大作用，甚至每次都会在大战途中现身然后起到扭转战局的效果，狠刷存在感，虽然人不在江湖，江湖上却一直流传着他们的传说，下面就分析这些重要的角色：科技达人：浦原喜助原为护廷十三队二番队第三席，兼隐密机动第三分队‧槛理队分队长，负责看守被囚禁在蛆虫的巢穴当中、没有犯错而又危险的死神。曳舟桐生晋升零番队之后，浦原担任十二番队队长兼技术开发局局长，110年前事件发生后，为救助被蓝染惣右介算计的平子真子一行人而被流放现世，虽然来到现世，但一直密切关注各方的动向。浦原喜助与四枫院夜一以及流魂街的烟火师志波空鹤是熟交，他和夜一等人虽然不算是主角小队，不过经常出现，穿插在主剧情中，当主角们遇到重大危机时才现身帮助扭转战局，经常帮助训练一护，帮助其成长。浦原喜助是死神里面智慧最高的人，以其“手段”--能用层出不穷的未知手段干净利落地解决一切难题与困境，被友哈巴赫视为五大特记战力之一，被蓝染称作唯一智慧超过自己的人，战绩辉煌，破面篇封印实力被削弱的蓝染，千年血战篇带队击败完圣体的“D”亚斯金·纳克鲁瓦尔。浦原喜助就像是团队中的辅助，在队伍最需要他的时候登场，以其智慧科技发明帮助团战取得胜利。瞬神：四枫院夜一原护廷十三队二番队队长，前隐秘机动总司令，同第一分队刑军总括军团长，也是尸魂界四大贵族之一四枫院家的第二十二代当家，擅长白打与瞬步，有“瞬神夜一”之称，和原十二番队队长浦原喜助是青梅竹马的好朋友，110年前离开尸魂界。初登场时以一只黑猫的姿态，出现在浦原喜助的商店门口。铁斋为了掩饰夜一的身分，对釉屋雨和甚太声称她是浦原喜助唯一的家人。朽木露琪亚被朽木白哉和阿散井恋次带回尸魂界后，夜一开始指点一护等人，让他们更加熟练自身的能力。多次在主角们陷入危机时出现，破面篇中在队长们都被击败后，夜一和浦原喜助，黑崎一心出动想要阻止蓝染，然而实力差距太大败下阵来。千年血战篇中和主角们一起对抗星十字骑士团，一度被致死量压制，开启瞬哄最终形态，瞬哄·雷兽战形，然而免疫药剂失效后再度痛苦倒地，虽然一度被虐，不过最终还是在击败“D”的战斗中做出了贡献。挂壁父亲：黑崎一心主人公黑崎一护的父亲，为人低调，实力强大。黑崎一心原是尸魂界护廷十三队十番队队长，后隐居于现世，其妻子黑崎真咲是灭却师，有一儿两女分别是黑崎一护、黑崎夏梨、黑崎游子。任职期间在现世遇上实力极为强劲的虚白，却遭忽然现身的蓝染等人从背后偷袭而身受重伤的时候，灭却师黑崎真咲现身击退了虚白，还亲自为他疗伤，双方因为这项契机而邂逅了彼此。 但随后黑崎真咲开始了虚化，必须要有死神和其保持“连接”。此时志波一心因为想要感谢黑崎真咲的救命之恩而再次来到现世，随即在浦原喜助的帮助下与黑崎真咲“连接”，从而丧失了死神的力量，之后转而留在现世生活。空座町大战中，一心忽然以死神的姿态现身，一心最著名的一战是和崩一蓝染的一战了，虽然当时蓝大为了放翻各位队长耗费了不少体力，却脸不红心不跳的，可见他耗费的体力并不多。但后来一心和他的一战却直接几乎消耗完了崩一蓝大的体力，其中还中了蓝大的一个带咏唱的雷吼炮，只是受了一点小伤，可见一心的实力要凌驾在很多队长之上。 一心的灵压足以限制拘流三个月之久，还能领悟刀禅了解最后的月牙天冲，就光凭这些就超越了很多队长，一个手指伤蓝染，只有他有这种战绩。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10516', '0', '对比死神中的两个最强的女人，其中一个隐藏的最深', '作为死神中唯一一个入选护庭十三队的女队长，花姐的实力到底如何。看下面这张图： 卯之花烈，原名卯之花八千流，初代剑八。现任护庭十三队四番队队长，即医疗救援部门的最高负责人，亦是任职时间最久的四位队长之一。目前被更木剑八一刀刺穿身体，已确认死亡。卍解：皆尽，皆尽刀刃变为可以延伸和弯曲的血刃，拥有融化血肉的能力。 四枫院夜一。原护廷十三队二番队队长，前隐秘机动总司令，同第一分队刑军总括军团长，也是尸魂界四大贵族之一四枫院家的第二十二代当家，和原十二番队队长浦原喜助是青梅竹马的好朋友。110年前离开尸魂界。而夜一的最强技能是，瞬开。夜一曾经用瞬开击碎过蓝染大boss的虚化面具。 除去只有名字的前前十二番队长{升零番队那个｝死神中剩下的女性角色无疑就她们两个最强了。夜一战绩:瞬神，超越队长最快的碎蜂，估计这个次元速度最快就是她了，天赐兵装备可以一击将崩玉蓝染打落地面，再次重击使崩玉蓝染塌陷，不被崩玉蓝染{第一形态｝秒杀，勉强对打，瞬开将崩玉蓝染再次打落，并且表面的崩玉外壳裂开许多。 花姐：就算除去TV原创的瞬间满血满篮恢复，三十号鬼道无限连击，八十号鬼道无吟唱，花姐的战斗数据在破面篇的那代队长仅次于山老头和蓝染，剑道是最高段，剑八的剑道就是蛮力乱砍{从被总队长打飞木剑看出｝可也秒杀了十刃钢皮最硬的诺5，花姐的剑道十段在配合满值的灵压非常恐怖，介绍说最高攻击力，可能是最恐怖的对战即可见她的实力。春水浮竹也说花姐的资历远高于他们俩人，没说什么坏话看见被花姐听到吓得半死（开玩笑，哈哈哈！！！） 我认为夜一不穿天赐兵装备不是花姐对手，穿上之后来二人有得一拼。花姐特点：暴攻+回复;夜一特点：暴攻+极速不过攻击力我觉得还是花姐强一点，鬼道什么的也应该更全面，但是没有实战表现', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10517', '0', '死神：最让友哈巴赫担心的五个“未知数”', '五大特记战力是由友哈巴赫提出的不在其掌控中的“未知数” 其中每一个人各有其特点，共同点是具有会对阻碍其侵略尸魂界的可能性。五人分别为：挂壁一护、死萝莉控、光头和尚、死神颜值担当、科技达人。其中，黑崎一护是必须最优先处理的“特记战力”。下面就分析他们最让友哈担心的各个方面：挂壁一护光头和尚：兵主部一兵卫，以其“睿智”被友哈巴赫设定为五大特记战力之一，是已知最古老的死神，拥有高的资历与最深邃的才智。身份为“王族特务”零番队的领袖，亦为尸魂界一切事物的命名者。为最初替尸魂界所有事物起名的关键人物，故对每位死神的斩魄刀真名了若指掌。在友哈入侵灵王宫后率领零番队极力阻止，虽然给友哈造成了一些麻烦，不过最后还是被友哈击败，最后在一护到达灵王宫后呼唤其名而复活。光头和尚科技达人：浦原喜助，以其“手段”被设定为特记五大战力之一，能用层出不穷的未知手段干净利落地解决一切难题与困境，智力极高，擅长分析事物和发明物品，曾被蓝染惣右介承认他是“尸魂界当中，唯一在智力方面超越他的存在”，同时也是少数知晓“灵王”的存在并遵从其意识的对象。在他众多发明当中，有一种叫“转神体”的人型器具，能将斩魄刀的本体强行抽出并物质化，以便使用者令斩魄刀降服。据四枫院夜一所说，浦原喜助透过这个器具，用了三日便完成卍解。浦原喜助的科技实力让友哈认为或许会成为阻止无形帝国的障碍。科技达人死萝莉控：更木剑八，以其“战斗力”被设定为特记五大战力之一，在任何情况下都不会衰减只会进一步高昂的战斗力与斗志。更木剑八的战斗力成为一个未知数，在千年血战篇之前从未始解，在无形帝国第一次入侵尸魂界就击杀数位星十字骑士团成员，而后与花姐战斗（花姐实为初代剑八）找回最初自己的力量，在与“M”杰拉德·瓦尔基里的战斗中学会卍解，然而因为身体承受不住这股巨大的力量导致右手断裂。萝莉控死神颜值担当：蓝染惣右介，以其“灵压”被设定为特记五大战力之一，所谓一力降十会，任何技巧在这压倒性强大的灵压前，只能俯首称臣。蓝染的智力很高，然而让友哈担忧的还是其强大的灵压，千年血战篇中，曾借助他的力量抵御灭却师，虽然只解开了部分束缚，还是运用其强大的灵压帮助死神。颜值担当挂壁一护：黑崎一护，以其“潜在能力”被设定为特记五大战力之一，拥有无法预测的进化可能，必须最先排除。一护最让友哈担心的是他的潜在能力，每次遇到强劲的对手，一护总能不断进化提升自己的实力（其实就是开挂，哈哈），进入灵王宫那次也是，在零番队的帮助下强行提高实力，进化速度（开挂能力）令人咂舌。我们可爱的挂壁主角', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10518', '0', '死神里死得最无语的角色——市丸银：我跟着蓝染到底是要干嘛？', '《死神bleach》里面有太多莫名奇妙就死了或消失了的角色，不知道是不是因为前期设定太猛导致后期无法收场，岸本干脆就破罐子破摔不填坑了，因此导致许多角色虎头蛇尾，有始无终。在这些人物中，最出名也最让人伤心的，莫过于市丸银了。他是原瀞灵庭三番队队长，斩魄刀名为神枪，卍解语是“射杀他！神枪！”，他是首屈一指的天才，仅用一年就从真央灵术院毕业，更是在幼年时就杀掉了五番队第三席官，还因此获得了蓝染的赏识。他出生在魂街极为穷苦的地方，幼年时在尸魂界境内某座山上流浪时，遇到了饿昏过去的乱菊，他给了乱菊一块柿饼充饥，两人自此相遇，并将这天约定为乱菊的生日。后来蓝染开始谋划叛变，四处收集死神灵魂以炼崩玉，乱菊因此而负伤，差点死在林间空地上。虽然乱菊侥幸活了下来，但银的内心从此陷入了执念。他一意孤行的想要替乱菊报仇，不惜加入护庭十三队以接近并刺杀蓝染。他与蓝染、东仙合谋陷害假面军势成员，之后更是判离尸魂界，与蓝染一行人一起逃往虚圈。自此，他再未与乱菊在一起，告别时那一句“有点可惜呢！能被你抓久一点就好了，再见了乱菊，对不起”，实在是让人唏嘘。到目前为止，市丸银这一角色并没有崩，他的叛逃也让人猜测是不是另有隐情。可是，银的崩坏也是在这时候开始的。蓝染叛逃篇可以说是久保设计的最完美的长篇，从蓝染一步步的缜密布局到最终真相大白之时众人的意想不到，跌宕起伏让我们看得十分爽快，可蓝染篇最后的结尾却太烂了，完全对不起前期如此完美的布局。蓝染被帅到爆炸的“最后的月牙天冲”一招秒了，这没啥，毕竟是少年热血漫就会有的主角光环，可银的结局也交代的太仓促太无厘头了啊！当他将前来质问的乱菊用鬼道“白伏”击晕后，趁着与蓝染会合的空档反过来攻击蓝染，企图摧毁从蓝染身上掉落的崩玉。却没想到蓝染因为崩玉复活，银被撕掉了手臂，并被斩魄刀刺穿了胸膛。就这样，在蓝染身边潜伏了整整半生的银濒临死亡，却什么也没改变。他倒下时想起了自己叛逃的初衷，是为了不让乱菊再次哭泣，是为了改变一切，可他最终还是没能将乱菊失去的东西夺回。不得不说，久保在后期处理市丸银时真是太粗糙了。照理说，在蓝染身边潜伏了如此之久，即便无法击败蓝染，也不该是什么也没做成就死了，中途那么多空缺都没把握住根本不符合银的能力。虽然银的死和乱菊的眼泪很让人难过，可市丸银这一角色的后期崩塌与草草收场真是十分可惜。毕竟他是孤独的天才，是《死神bleach》里最受人欢迎的原三番队队长市丸银。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10519', '0', '死神悲情人物排行TOP5，市丸银第二，第一有疑问么？', '5、逝去的阳光【浮竹十四郎】他像阳光一般的温暖，白色的过肩长发，温文尔雅的面容。他是露琪亚的直属上司，却像父亲般对待露琪亚。他曾亲眼目睹副队长海燕的死并未出手相助，这件事一直让浮竹耿耿于怀。因此在海燕死后，浮竹再没有选新的副队长，已出于对海燕的怀念。他因为得知露琪亚判死刑时激动而吐血，之后浮竹决意要阻止这次判刑，不惜与恩师对决。蓝染叛变后，他是最后一个上前的人，被蓝染说成过于清高。千年血战的战斗中，恩师元柳斎被友哈杀死，浮竹黯然的看着恩师遗留的斩魄刀时，悲痛地道出后者的遗体被敌方彻底摧毁的消息，但还是在京乐春水的劝谏下重新振作，决定和护廷十三队携手度过接下来的挑战，此时内心似乎已明白，解放神掛的时刻来到了。灵王一死整个尸魂界和现世都会消失，就在大家都已经束手无策的时候，浮竹献出了自己的内脏释放了御眼之力，称之为神掛！为了保护静灵庭浮竹变成了灵王的右臂。他阻止了尸魂界的倒塌，似乎自己早就清楚出生的那天就已经知道的结果。百年的队长就这样如风一般消逝，从此尸魂界少了那一缕温暖的阳光。4、迟来的心【乌尔奇奥拉·西法】他有超出其他十刃的理智，也从来不欺负弱小。可是他不能理解人类，就像那个叫织姬的女人，明明很弱小，明明自己不费吹灰之力就可以杀死她，为什么她却无比的坚强？ 为什么她还能充满期待？ 因为心吗？遇到一户和井上之后，那个声音逐渐的清晰，却依然无法让他明白，心是什么？既然你门人类那么喜欢将心寄托给对方，那么我就摧毁你门的希望。可是那个男人却创造了奇迹，本来那无法醒来的肉身，却在织姬爱和泪水的呼唤下换来了希望的曙光。这一刻，他好像有点懂心的意义了。那个永远像冰一样冷漠的乌尔奇奥拉，此时似乎被人类同化。只有透过他人的感情才能确认自己，难怪自己是虚无的。无法感受别人的心情，只能在相对的比较中确定自己的存在，这才是真正的原因。“你害怕我吗，女人？”身体渐渐的灰化，来不及触碰人类温暖的手，也许内心感受到了，又一次听到了那个声音。这一次，这个声音清晰得像来自灵魂的深处，似乎这手中的温度，这掌中所拥有的东西就是心阿。3、凋零的白色花朵【卯之花八千流】她掌管着13番队所有人生命安全，作为4番队的队员们，他们也都是幸福的，因为有这样一个既伟大又很会关心部下的队长！你能够察觉到蓝染的尸体有异常，你是睿智的。还能治疗被茶渡击败的敌人NO.107刚腾拜恩，你又是如何的仁慈！但是这千年血战中你摇身一变，原来的你竟然是以杀人为乐趣的初代剑八！这是一场两代剑八的激烈对决，这应该是让我们兴奋的事情，却又高兴不起来，因为两人激战必有一人死亡。为了使剑八的潜藏力量觉醒，你一次次的将他逼上绝路，又一次次的用回道治愈他，最终你故意被剑八刺穿，依然是那个曾经被他刺中的伤口。此刻的心情是多么不舍和惋惜，好像还没有来得及了解真实的你，你就已经离开了。2、触不到的爱【市丸银】那一年纷纷扬扬的大雪里，一个银发的男孩递给倒在雪地中的橘发女孩一个柿子饼，他微笑着说 “我叫市丸银” 那一年，依然在纷纷扬扬的大雪里，银发的男孩穿着死神的衣服，对着橘发女孩说；我要去做死神，改变着命运，让乱菊不必总是哭泣。他就这样毫无顾忌的走了，只留下了一丝背影给乱菊。他自喻为蛇，将自己贬为冷血动物，到最后也不过是一场伪装而已，不过，伪装得好像，都骗过了最爱的人。百年难遇的天才，他并没有伟大的理想，他只是为了自己爱的女人，然而那个女人所看见的似乎从来都是他的背影！可是这一次不同，银回头了。他说：如果能让你抓的久一点该多好，再见，乱菊，对不起。。。一如既往的微笑，隔着惨白的光，女人依旧看不清，银隐藏在微笑之下的，那是悲伤吗?立身于蓝染面前，一双狐狸眼看不出任何波澜，唇角一如既往的上扬，他淡声宣布，能斩杀蓝染队长的，只能是我一人。可是手中的崩玉逐渐的消失，他正欲伸手抓住，却突然迎来一股杀意，愕然的瞬间那双伸出的手便被抓住，随即一白影显在面前，手起刀落，血光四溅。他不甘心的倒下了，还是不行……乱菊，到头来……我还是没夺回你所失去的…啊……真的……！我很高兴……有先对你道歉。那一年，女人再睡梦中哭醒。“梦里，一个俊秀的小男孩递给了自己一块柿子饼，他微笑的说；我叫市丸银，随后便转身离开，女人渐渐的看不清他孤独的背影....渐渐的走远，亦如流水，无迹可寻.....”1、落幕的英雄【山本元柳斎重国】当时的总队长额头上只有一道伤痕，那时候的雀部长次郎得知山本大名，想成为山本的助手，伴随他左右。可是山本嫌他实力不济便说道先习得卍解之后再来找我，结果雀部仅在一个月之内就达成了卍解，也就在那次的较量中，山本的额头又多出了一道伤疤，因新伤疤与旧疤交错形成了一道“十”字型伤痕，不知何时周围的人直称呼他为十字斎，此后山本重国为自己取了元柳斎之名。 万象一切﹑皆为灰烬，流刃若火！没有人不惧怕山本元柳斎重国，与蓝染和友哈不同，山本除了强大，而且重情重义。他可以不惜以自己的身体熄灭汪达怀斯的自爆，他可以因为部下的死悲愤的发誓，要将敌人全部歼灭。然而一身正气的总队长，又一次被阴险狡诈的敌人暗算，身体已被友哈一分为二的总队长，下半身却屹立不倒！老夫也许再也回不到这里了吧.....友哈将死去的元柳斋尸体炸成灰烬，可是最终总队长捍卫了瀞灵庭的尊严，您不愧是历史上最强的死神，不愧是护庭十三番的总队长，一路走好英雄，山本元柳斎重国。最后，妮露收尾。动动你们的小手，点赞并转发文章到朋友圈或微博，谢谢！更多精彩内容请搜索并关注微信订阅号：小李talk（←_←长按复制并打开微信搜索公众号）↓订阅', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10520', '0', '细数《死神》魅力人物（三）——护廷十三队之市丸银', '今天我们要讨论的是市丸银，这个如罂粟一般的男子，浅紫色短发、眯眯眼，平时总是以笑脸示人，却是个自喻为“是一条蛇，肌肤冰冷，没有感情，只是寻找猎物并将其一口吞下”的人。这个男人，小编觉得是《死神》里相当特殊的一个角色，他似乎不需要名利，不需要朋友，不追求任何东西，看起来就是个无欲无求的人，搞不懂他想干嘛，看似想和每个人产生联系，其实又似乎只喜欢独来独往，前期塑造得如此神秘之人，最后的真相居然一切都是为了女人，他唯一的真心，唯一的追求，就只是为了儿时喜欢的女孩。最后知道真相的我怎么都没法想到，卧底百年的他原来是个百年难得一见的情种。。。这或许就是这个男人的魅力所在吧！依照惯例我们简单的介绍一下市丸银，原瀞灵廷三番队队长。与十番队副队长松本乱菊是青梅竹马的关系并且相互喜欢。携带武器为斩魄刀——神枪。从小就拥有着超人般的天赋，仅用一年就从真央灵术院毕业，被誉为天才儿童！幼年就跟随蓝染身边，后蓝染叛变，于是跟随蓝染以及东仙三人叛逃至虚圈。而多年的奔走追随实则是为了夺走乱菊幼时被夺走之物以及自己的一厢情愿，后在刺杀蓝染中由于意料之外的变故导致计划失败，被蓝染洞穿胸口，在乱菊怀中死去，结束了悲惨的一生。银的实力很强！银是漫画中除了小白以外，又一名被冠以“天才”之名的死神，同时，银这个天才有个对比，当时久保带人借浮竹的口道出银这个天才之名时特地强调了“年纪和朽木队长的孙子差不多，可能还小一点”，可以说银起码当时表现出来的潜力是大于大白的。110年前就有了被超级野心家蓝染招揽的价值，其天赋实力毋庸置疑。回转到现在的时间，久保带人再次借剑八之口道出了银的强大，他对一户产生兴趣的原因很简单，那就是“和那个市丸交手还能活着的男人”，可见剑八是相当认可银的实力的，剑八的性格大家相当了解，一般的人他都不会放在眼里，后来他以一人之力单挑东仙和柏村两位队长，完全是藐视的态度嘛。而在蓝染的虚圈大集团中银的地位更加超然，对比下东仙和蓝染的关系，大家不难看出银的地位是明显高于东仙的，如果说东仙是部下的话，蓝染对银的态度相对平等，最后的结局也表明了这点，银和蓝染从来就不是一路人，可以说是一个旁观者，一个随时窥视着蓝染性命的刺客。蓝染自己也说过，他早就知道银的目的，就算如此他还是把银带在身边，因为他对银想如何取他性命很感兴趣，崩玉的进化需要恐惧，可以说，正是银的强大，才促进了蓝染的进一步进化！银的斩魄刀为神枪，始解解放语为：射杀他！神枪，始解后刀身可瞬间延伸改变长度以刺杀对手，并增强其攻击速度与力道，如鞭一样使用，最大伸展极限为神枪的100倍长。卍解的时候身边周围的东西都瞬间被斩开，有很大的冲击力和斩断力，伸缩速度为音速的500倍，而且刀身会在伸缩的瞬间变成灰，刀的内部会出现能够溶解细胞的猛烈毒药，刺出再收回刀的时候，可令一部分变成灰并残留在对手的体内。神杀枪属于实战型斩魂刀，卍解并不花哨，讲究一刀致命，在一场等级接近的战斗中，无伤解决对手的概率太小了，而且并没有人知道神杀枪的真正能力，一旦受伤就是死，技能太过变态了。所以总的来说，银的实力归属于一流队长水平问题不大。大家好，我是寂寞先生，我会努力给大家带来不一样的动漫资讯，希望喜欢的朋友可以点击一下关注哦，你的支持就是我最大的动力，谢谢支持！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10521', '0', '《死神》市丸银：背叛的背后是保护', '很多人看死神，也许会记不住一护，却忘不掉市丸银。因为他的爱与忍受。银是一个悲壮的人，也许只有悲壮，才能显示他的热血和不顾一切。一护的成长太过顺风顺水，也许是因为主角光环的威力太过明显。而银，则相对正常许多了。市丸银是整部漫画里相当特殊的一个角色，他似乎不追求名利，不需要朋友，不追求任何东西，看起来就是个无欲无求的人。不知道他在做什么、想什么，看似想和每个人产生联系，其实又似乎只喜欢独来独往，前期塑造得如此神秘之人，最后的一切都是为了女人，他唯一的真心，唯一的追求，儿时喜欢的女孩。银是一个像蛇的男人，那不愠不火的笑容是他最为冷酷也是最为迷人的地方。但是这个笑容，那个真正的笑容，永远只会留给乱菊。“吃吧…我叫市丸银。”与乱菊第一次相遇时的那个银是最为纯洁的，也是最幸福的银。一次偶然的机会，他看到蓝染从乱菊身上夺走了什么，并偶然的发现他的阴谋，于是，为了夺回那样属于乱菊的东西，也为了保护乱菊，银去了蓝染那里，他对乱菊说的理由是：“我决定做死神…让乱菊不必总是这样痛哭流涕。银可以像春水那样喝喝酒，晒晒太阳；也可以像剑八那样做自己喜欢的事；甚至可以像小白那样兢兢业业的工作以至于成为全尸魂界的劳模•••其实，银有很多条他能做到的路供他选择，但是，他偏偏选择了这么一条，一条不归路,赔上了自己所有的一切。所有的幸福，所有的痛苦，所有的欢笑，所有的泪水，所有的爱与恨。所有的所有都被埋藏在了这条名为「不归」的路上。为了这份感情，他自愿成为死神（当死神并不是好玩的，就想当兵一样，随时可能会死的，所以志愿当死神是非常伟大的）这个男人，「为爱人终身不悔，为承诺奉献一身」，“让乱菊不必总是这么痛哭流涕”，“最终•••也没能够把乱菊的东西夺回来”，谁能像这样，从始至终，只为自己心爱的人？无论他曾做过什么，无论他欺骗过敏谁，都不可否认——他所做的一切，都是为了乱菊。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10522', '0', '你们都在关注市丸银对乱菊的爱，但是他作为一个卧底真的合格吗？', '市丸银最开始在死神里是一个看起来坏坏的略带神秘的狡猾角色，谁也不曾想过他竟然是卧底，和蓝染一样给人反差强烈，留下了深刻印象，但是作为一名卧底，他真的算是合格的吗？欺骗敌方的同时也要欺骗自己人，特别是对于乱菊，我们知道他用情很深，卧底的目的其实也是为了乱菊，所以为此对蓝染的深仇，他宁愿选择背负一切一个人去面对蓝染，了解到蓝染的强大，伺机而动，然而结果是没用的，最后虽然给了蓝染一刀，可惜蓝染的强大不是一刀可以解决的，银完全就是个牺牲品，从他跟随蓝染开始，讲真他做了更多反派boss的事而不是卧底的。背叛他的副队长，对乱菊一句不说的就离开，跟着蓝染，引诱雏森，眼睁睁看着雏森被蓝染桶一刀，之后也是一直在旁边无所作为，就这么两袖清风的隐藏N年监视观察着蓝染，其实这段时间都可以好好的通风报信，至少留个指示啥的，但依然什么都没有，也以为银能掌握到蓝染的核心内容，然而还是低估了蓝染智商，不过还好在大战时没怎么出手，全体人民打的不可开交，乱菊都挨了一刀，还是依然作为旁观者，静静等待着蓝染大变身，这时候要是去和队长啥的打一把，就真的是个坑爹的卧底了。最后也是想通过和一护的打打闹闹来启示一护，然而一护并不领情，这个卧底当的确实吃力不讨好，结果两边都没什么战绩，可怜的银卧底打的是真酱油，刺了蓝染一刀却被瞬间反杀，还助了蓝染一力，这真的是难过，所以还是只能强调一下银对乱菊是真爱。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10523', '0', '细数动漫当中的“眯眯眼角色”，西索气场逼人，市丸银让人心痛！', '常言说道“眯眯眼都是怪物”，动漫中有很多这样的眯眯眼，我们看不到他们的眼球，看不清他们的内心世界，他们心机高深并藏着我们意想不到的东西不二周助不二可以说是眯眯眼世界里面的一个代表人物了，平时他待人接物都是笑眯眯的，很少有生气的时候，总是微笑待人，因此也有“微笑王子”的雅称，实力十分强大，俊美的外表配上眯眯眼有一种莫名的帅气今吉翔一作为桐皇篮球部队长，有着相当高的篮球智商，喜欢用心理战击溃对手，是一个可怕的人，因而被日向称为“腹黑眼镜”。愿意接受青峰的领导，也是他身边最好的帮手神威宇宙最强怪物猎人“星海坊主“神晃与夜兔母星徨安龙脉的化身江华的儿子，同时也是神乐的哥哥，平时他脸上总是面带微笑，对弱者不感兴趣，可是遇到强者就十分兴奋。姚麟和其他眯眯眼一样姚麟平时也是一副慵懒的样子，却有一种魅力，一种神秘色彩，拥有极高的智慧，一旦睁开眼睛对看就会十分认真霸气显露藤原文太藤原拓海的父亲，平时不显山水开一间豆腐店，实际曾经是秋名山车神，开一辆86简直无人能敌。直到后面他还是能在车技上秒杀拓海西索全职猎人中人气非常高的反派，虽然眼睛不是细成一根线，但依然是相当的小，在战斗时表现异常冷静，拥有强大的判断力，不管在任何情况下，他都不会让对手看到自己弱点。是一个让对手恐惧的存在芳村功善东京喰种中古董的店长，帮助金木很多次，是一位十分受人尊敬的老人，为人很绅士，从不发脾气，眯眯眼，带着微笑面对他人，但有着很强的实力，被ccg称之为不杀之枭市丸银银是我印象最深刻的眯眯眼，眼睛从不睁开，把一切都隐藏在自己的心里。对蓝染多年的奔走追随实则是为了夺走乱菊幼时被夺走之物以及自己的一厢情愿，后来对蓝染刺杀失败死在乱菊怀中，简直是死神迷一个永远的痛你心中最喜欢哪一位眯眯眼，欢迎在留言区讨论！关注【拾部次元】，回复“东京食货”，“东京搜查官”获取全三季动画！国漫神作《史上最强》现已上线各大平台，点击下方【了解更多】即可跳转查看！', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10524', '0', '盘点《死神》漫画7位冤死人物 市丸银之死举世皆悲！', '《死神》漫画已经迎来最终章，很快就要正式完结。纵观整个故事，很多名不见经传的小人物成长为能力逆天的高手，也有很多跺脚震天地的大人物无奈领便当。今天，我们就来盘点一下《死神》中几位冤死的人物。NO.1山本元柳斎重国 冤枉原因：杀了一个以为是正版的盗版，一时大意被夺去卍解参死死于黑科技（外挂）。 评论：由于灭却师研发的黑科技能够夺去敌人的卍解，这个很难论定他就比山本队长强应为他不是靠实力取胜。他老人家千年前能打败友哈，一千年来就没长进过吗，刚秀一把就领饭盒了作者简直不尊重老人。破面篇好不容易能大显身手，结果~被烧伤怎么说他也是总队长每次出场都这样作者你这是在拆他的台吗 NO.2凤桥楼十郎 冤死原因：多嘴。对手几乎打不死 评论：楼十郎的卍解是非常可观的感官系，这个想破解真的很难，把马斯可·多·马斯库林砍成两半，把小人砍成碎片，怎奈对手信春哥。最后他到底死了没有~NO.3吉良伊鹤 冤死原因：剧情需要评价：吉良死神里一直担任着龙套的戏份，实力不出众唯一出众的就是他又长又金黄色的发型，千年血战篇还么能说对白就死了NO.4雀部长次郎 冤死原因：剧情需要，被灭却师黑科技夺去卍解 评价：一番队副队长，死神了露脸次数简直惨不忍睹，最后连死都没能露会脸跟观众道别一下。他真的比吉良惨 NO.5市丸银 评价：市丸银的一生是难堪的，一直面对着自己的仇人蓝染还有松本对他的误解，他所做的一切都是为了松本他要拿回蓝染夺走的东西一直卧底卧到了成为队长。最后临死前松本终于解开了对他的误解，瞑目了~ NO.6卯之花烈冤死原因：给更木剑八送人头送经验。剧情需要评价：死神里她一直是给人母亲那般和蔼慈祥的感觉她还是一个医生专业救人300年，谁都想不到她居然是初代剑八。我去我至今都接受不了她是在转奶妈前是狂战的设定。佛曰：我不入地狱谁入地狱，花姐走好 NO.7六车拳西 冤死原因：只能怪自己太菜打不赢对手，对手几乎不死 评价：拳西说实他不是很冤。正所谓随便的卍解的队长不是好队长，他顶多打了些酱油。难道你就不会学一户又卍解又虚化吗，他顶多是重伤但是到底死了没有~ 声明：游迅网登载此文出于传递信息之目的，绝不意味着游迅网赞同其观点或证实其描述，若侵权请来信告知，我们将及时处理。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10525', '0', '哪个人物的离去让你最难过，如果能复活一个，我选择自来也', '嗨~大家好，这里是漫活社。一部好的动漫是能牵动人的感情，热血的时候你会很激动，伤心的时候你会跟着难过，动漫里的人物虽然是虚构，但又仿佛是熟人般真实，那么哪个动漫人物的离去让你感觉最难过？给你一个复活的机会，你会选择复活谁？下面给大家介绍一下网友们最想复活的十位动漫人物：10、《死神》 市丸银原三番队队长。与十番队副队长松本乱菊是青梅竹马的关系并且相互喜欢。幼年就跟随蓝染身边，后蓝染叛变，于是跟随蓝染以及东仙三人叛逃至虚圈。而多年的奔走追随实则是为了夺走乱菊幼时被夺走之物以及自己的一厢情愿，后在刺杀蓝染中由于意料之外的变故导致计划失败，被蓝染洞穿胸口，在乱菊怀中死去，结束了悲惨的一生。9、《犬夜叉》 桔梗战国时代灵力数一数二的巫女，守护四魂之玉，而因抢玉的犬夜叉与自己的处境相似，不忍痛下杀手，留他一命，竟就发展出一段恋曲，但因为误会，最终犬夜叉和桔梗没能在一起，后来遇到了篱，而桔梗最后微笑着在犬夜叉的怀里化成了碎片。命中注定桔梗悲壮的一生，也可怜她，因此希望能给她一个复活的机会来补偿。8、《火影忍者》 鼬宇智波一族的天才忍者，为了保护村子免受战乱，被迫接受了灭族任务，留下了弟弟佐助并刺激他向自己复仇，之后加入晓组织做卧底。最终在与弟弟宇智波佐助的战斗中为佐助注入瞳力后，因身体患有不治之症，体力不支而死亡。等后来佐助才知道真相，并且知道其为保护自己而死十分难过。7、《反叛的鲁路修》 鲁路修神圣布里塔尼亚帝国的第11皇子，阿什弗德学园学生会副会长，也是黑色骑士团的领导人。在生死攸关之际获得了神秘少女c.c.赋予的Geass之力，立志要粉碎布里塔尼亚帝国。与枢木朱雀、c.c.制定了零之镇魂曲”计划，让世人憎恶自己，最终被枢木朱雀假扮的“Zero”刺杀而死。6、《火影忍者》 日向宁次日向一族的天才，虽然被宗家限定了其血继限界白眼的能力，但是他凭借自己的天赋继承了宗家才可以使用的八卦掌等特异体术。在中忍考试输给鸣人后对鸣人和自己命运看法有所改观，不再认为命运是无法改变的。第四次忍界大战中，宁次为了保护鸣人和雏田，以自己的身体抵挡十尾的扦插之术而壮烈牺牲。5、《死亡笔记》 L世界第一名侦探，拥有自由调动全世界警力的绝对权力。夜神月以“KIRA”的身份用笔记的力量铲除大量罪犯，决心建立一个没有犯罪的世界。L着手调查，要求ICPO协助，并在日本与警察成立调查总部。在L调查下，确定KIRA就住在关东并向其发出挑战。受到L的挑战，夜神月认为L将会成为他创造新世界路上的绊脚石，而接受其挑战并与之展开对决，最终夜神月利用死神莱姆把L干掉了。4、《火影忍者》 自来也木叶村的“三忍”之一，鸣人的师父，被鸣人称为一生中最敬重的人。他指导鸣人修行，在鸣人成长的道路上有着重要的作用。后到雨忍村刺探情报，与佩恩交战战死，本来有逃生机会，但是为了弄清楚拥有轮回眼的“佩恩”的秘密，通过仙人化作战，最终揭穿秘密，临死之时将真相通过密码形式留在仙人蛙背后！3、《未闻花名》 面码五年前因意外滑下山崖，由于溪河十分湍急溺水而死。五年后出现在宿海仁太面前，并且以类似幽灵的型态存在。通过用以前的日记本和超平和busters的成员交流，并在十年后重新凝聚起当年的超平和busters，希望大家能不再为自己的事情而闹别扭，是组织中的核心。最后在大树下实现了“自己的愿望”，最后大家都看见了她，在微笑中消失了。2、《暗杀教室》 杀老师被称为史上最凶恶的超生物“杀老师”将月球炸掉了七成，并扬言在第二年要炸掉地球，成为了全世界的刺杀对象。为了遵守与某人的约定，以“绝对不加害学生”为条件与政府办妥协，担任3年E班的班导师。最后杀老师为了避免自身的细胞发生爆炸，经过所有同学讨论后由渚执刀杀死的。1、《海贼王》艾斯艾斯听到黑胡子要追捕路飞，跟黑胡子展开一场决斗。最后很不幸被黑胡子打倒，而送入大监狱“推进城”，将被受死刑，白胡子率领海贼团、海侠甚平、路飞和女帝等人前往到监狱所在地与海军及王下七武海开战营救艾斯。最后艾斯已经救出，但因为保护路飞用身体去挡海军大将赤犬的岩浆拳，结果被贯穿了身体，因伤势过重死亡。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10526', '0', '我眼中的护庭十三队——市丸银', '银色的暖秋——市丸银细腻的人才会笑意阑珊的看着周围，而不动声色。处女座的人往往细腻到深入骨髓，然后发现其实一切都已无所谓。9月10日出生的市丸银恰好赶上柿子熟了的好时候，于是这个男人在微笑杀人的背后，还做柿饼送给他人。当然，市丸银的微笑背后其实隐藏了太多的不屑，谁能看透他呢？这是个谜。当然有些时候，无须说话，温暖的秋季里有无数伤感的告别，但是只要有等待着你的人，你就有希望等到归期。他总是事不关己的样子，给人一副诡计多端的表象，他总是爱把话题脱离到是非之外，因为他总是被卷入是非之中。这个笑容匪浅的白皙男子，出人意表的充满了神秘感。奄奄一息的乱菊，给她生存下去的不是好的没边的浮竹，也不是好色浮华的春水，而是喜欢吃柿子的银，当然乱菊吃的也是银亲自做的柿饼。与其天天风花雪月，找歌姬弹唱，编造浮华的绯句，不如好好的享受生活。市丸银是个爱做柿饼给他人吃的务实的男人，他没有高贵的出身，只有浅浅的笑容，当然必要时他还有一把见血封喉的刀。土相星座的人，总是不爱浮华，实际而直接。所以他会微笑的说那句：射杀他，神枪。没有惊天动地的场面，但是我们看到了重点，那就是敌人的毙命。我毫不讳言的欣赏市丸银，其实很多被卷入风暴的人，没有人能逃脱出自己的宿命，无论是感伤，害怕，还是喊出一个又一个坚强的口号，都不如一种态度，那种态度叫做淡然。市丸银的淡然里包含着他的生活，他的过去，他的自信，还有就是他的不屑。他是那种静若处子，动如脱兔的人，他爱好散步，同时他的特技确是穿针。这犹如他这个人一样，往往给人一副闲散的样子，认真起来却一击中的，他用了一年就从真央灵术学院毕业，小小年纪，就能笑着砍翻第三席，他从来没有认真过，但是我们在最近看到了他瞬间13公里的惊人速度。他由始至终都是谜团。', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10527', '0', '市丸银情怀', '他是罂粟一般的男子，唇角微微上扬的弧度，璀璨了整个世界。他如月光，看得见却触摸不到。一头银白色的头发，在乱菊的眼里有如天上的月，可望而不可及。一柄斩魄刀，在无情的瞬间总能将乱菊绕过。一张永远在笑的脸，在离开的时候却说出了真心。渗进悲伤的笑。久保带人给予原三番队队长市丸银的扉页题词：市丸ギン / それは爱のように美しい杀意宛如爱一般绝美的杀意会拿美丽比喻成爱的人，都是不知爱情真相的人。会把丑陋比喻成爱的人，都是熟知爱的骄傲者。你明日为蛇开始噬人用你噬人之口声声嘶吼着说爱我我是否还能同今日一样对你说“爱你”呢__市丸银你从没表露出你刻意忽略了的看似无关紧要的过往,扑朔迷离间,只有自乱菊的回忆中才可窥探一二。差一点就让你蒙混过关了呢，然而无懈可击的微笑看不出一丝勉强，让人迷惘，究竟会是一个怎样的故事？我想象不到你的成长，只知道现在的你，在历经了这么多以后依旧坚强!有时候突发奇想，想把你想的单纯一点，想你也许只是一个贪玩的孩子，喜欢捉弄别人，喜欢新鲜刺激的东西。所以你闲着无聊，过来转转捉弄小露，得意自己轻而易举点操控了别人关于生死的意志；因为喜欢捉弄人带来的乐趣而成为坏人,只为了有更多机会去捉弄更多的人。没有什么复杂的理由，没有多么艰深的考量，只是任性贪玩而已。什么都知道，却什么也没说，甚至可能什么也没做，只是笑靥盈盈的看着一切就那么发生，等着最后答案揭晓时众人惊讶的神色，躲在一边偷笑。任凭是好人还是坏人的猜测声如潮涌来，一刻也未曾把这种事放在心上；即便得到蓝染那个不可一世的男人的欣赏，却也只是不在意的笑笑而已，若此平淡，看起来无关痛痒。围绕在你周身的神秘就像名为诅咒的线，绕成冲不开打不破的茧，盘出花样繁复的结；从此，你在笑容中隐去了所有真相。可是，久久不愿移开视线的却是你轻轻牵起的嘴角，那一抹若有若无的弧度，浅浅的，淡淡的，卷起些许温热的气息，带着地中海紫色树藤的缠绵。说不清道不明的暧昧。有人说笑是一种伪装，但是你的笑更像是一种习惯。邪恶也好，怜悯也好，嘲弄也好，温柔也好，你硬是用笑容代替了其他所有表情。笑眯的双眸，隐藏住万千思绪，将你深深掩埋，看不透；偶放精光的瞳眸却似强力射线般摄人魂魄，摸不着;只见过一次你睁开的眼眸,如染血般妖冶的红,惊鸿一现般震撼,便知一定还有些什么就像那被掩藏的目光一般,被你深埋.神秘的外衣、敛藏的光芒，勾勒独特的光华；惑人的嗓音、憨态的举止，绽现迷人风采。似射线般细微，但也灼人。这其实是最后才敲定的一个词，落笔的时候，好像会有很多话要说，又好像不知从何说起，会牵扯出太多沉眠于脚下的梦魇，经历了过久的发酵再开启时早已酿出一场直撸人心的蛊惑，让身体里的懒虫蠢蠢欲动。在没有过分的灼热阳光，寒冷永远会比温暖更贴近地，让人看到生命凛冽的真相，在一些雾的边缘，对不想听到的可以充耳不闻，对不想看到的可以视而不见。那么你呢？其实什么都知道的吧？', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10528', '0', '解析死神中最大的败笔：市丸银', '死神【bleach】当中较为神秘的角色我想大家都心里有数。像更木剑八 ，浦原喜助， 蓝染忽右介，黑骑一心，石田龙弦等等，有些人身上甚至略带谜团。但是小编实在很纳闷这个市丸银到底是说他神秘好呢？还是说他酱油好呢？1具有神秘角色的特性我想光以神秘性来分析，这个角色觉得是个好胚子。很多死神脑残粉们大概都知道市丸银总共就睁开过三次眼睛，而对于他的介绍也只是一些片段回忆。在回忆中我们得知他是为了自己青梅竹马的松本乱菊而变成死神的，因为蓝染当时的崩玉计划伤害到了乱菊，所以市丸银下决心要潜伏在蓝染身边伺机杀了他。但是死神中对于市丸银的身世只字未提，简单的只介绍他是个天才少年，而我们对于他的了解也仅仅是建立在他和乱菊的联系中。2角色定位不明确而就是这么一个好胚子但是关于他的重要事件就是很少，大概很多人关于市丸银的印象就是他和蓝染计划叛变的那几集，还有蓝染决战篇的那两集。而在其他的集数里他基本就是在打酱油，而既然久保带人【死神作者】设定他为酱油角色那为什么银要么不做声，一说话一做事马上会让人很在意。市丸银这个角色他本身的性格不应该被设定为酱油角色，因为本身带有神秘感，而且既然作为酱油角色那就不要太神秘，干脆脱离神秘一直酱油下去这样也不会浪费观众感情。3神秘而又酱油最终促成败笔小编觉得市丸银的确是久保带人的一大败笔，因为市丸银在刚出场没多久给我们大家的感觉他就是一个带有神秘色彩的角色。而官方的描述是：平时总是以笑脸示人，却是个自喻为“是一条蛇，肌肤冰冷，没有感情，只是寻找猎物并将其一口吞下”的人。我勒个去这么牛叉的描述你不想高调也不行啊，既然高调那就像更木剑八一样一直高调下去啊，非要弄得一关于他的事就会很高调，但平时却一直酱油气息。好吧这也可以理解为是更好的吊起观众的胃口让他成为人气角色，为了他以后的故事发展吧。但是最后在蓝染决战篇你直接给他判死刑又怎么回事？死的时候还这么高调，这完全就是在欺骗观众感情啊，如果是因为市丸银由于既神秘又酱油这种状态让故事不好写，至少也搁置一下他的故事吧。不必像废纸一样直接扔进垃圾桶吧，你这样让市丸银的粉丝们多伤心呢?据说曾经海贼王死个艾斯就有人崩溃，火影死个宇智波鼬又有人自杀。我想那些漫画家真应该好好考虑下了，你们的画笔真的是联系着诸多粉丝们的感情啊=。=', '', '4691', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10529', '0', '奇牙和佐助 想选择谁好朋友？', '《全职猎人》里的奇牙，《火影忍者》里的佐助，你想让谁成为你的好朋友呢？日本网友围绕着这个话题进行了激烈地讨论，我们一起来看一下吧。2 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:23:29.617 ID:AeSTf7kN0.net奇牙还强点。3 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:23:45.571 ID:CepOn/B30.net必须是奇牙7 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:24:44.634 ID:4iOY2/r10.net“还强点”是啥意思？奇牙也就是家庭奇葩了一点，他本人挺好的啊。5 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:23:54.810 ID:IGEDBkbG0.net奇牙可爱12 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:26:38.237 ID:9s0iNpsDr.net想跟奇牙两人去攻略贪婪之岛19 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:30:47.755 ID:bi490bvf0.net如果前提是他想当我的朋友，那么肯定是选奇牙。4 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:23:54.251 ID:l0CO+xLv0.net没人选佐助？8 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:24:47.281 ID:5AV1qjxL0.net奇牙9 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:24:59.844 ID:7Zwhsl7o0.net不踩奇牙的地雷的话他还挺正常的但佐助完全无法沟通啊17 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:30:13.843 ID:IkR6Ib6v0.net跟佐助成为朋友的话离鼬就近了！15 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:28:37.332 ID:dunoGHa30.net那要看是哪个时期的佐助了，差别很大的10 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:25:26.975 ID:chIvsN0C0.net早期的奇牙也很可怕啊，会杀人的11 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:25:54.711 ID:2QDLMtE+0跟佐助关系近的话他会来杀人的20 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:31:04.687 ID:ZjMwaKkj0.net两边都不是什么好人我选奇牙18 名前：以下、(^o^)／でVIPがお送りします：2016/01/07(木) 23:30:39.237 ID:7Zwhsl7o0.net奇牙早期比较那啥，佐助晚期比较那啥就看你想是早危险还是晚点危险了如果是你，会选奇牙还是佐助当朋友呢？（来源：腾讯动漫）', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10530', '0', '全职猎人：不平等的友情，奇犽和小杰的友谊走向终点', '1.奇牙的成长让他不再需要和小杰一起了。2.更何况小杰还伤害了奇牙，即便两人好像无所谓的样子，但是裂痕有了就是有了，伤疤有了就是有了。你以为好了，实际上没有好，你以为这是没关系的，实际上是有关系的。你以为不介意，实际上是介意的。（这里是指从蚂蚁篇到他们分开，没有指永远，因为未来是未知的）其实我从来没觉得他们是很好的朋友。（此我个人超主观想法，请无视）3.两个人的友谊，如果双方付出不平等的话，那迟早是走不下去的。仔细回想，小杰除了去找奇牙的那一次，从头到尾，他有为奇牙真的付出过什么吗？小杰很多都是为了自己了。因为他想，所以去做了。他固执，他自以为是。奇牙却不同。他付出太多，得到太少。一直惶恐不安，珍惜来之不易的友情，即便被伤害了，最后也不过等来一句云淡风轻的“对不起”。有屁用阿？所以分道扬镳，是富奸对他们目前友情走向的最好结局。当然，再相遇是另一回事了。或许时间会淡化两人在蚂蚁篇的友情上出现的裂缝，期待他们的再次重逢。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10531', '0', '全职猎人: 酷拉皮卡经典语录, 满满的回忆!', '一个人的气质用金钱也很难买到... 有些人是不能以外表判断性别的... 伪装、贪婪都一样卑劣，我认为这两样都是人类最可耻的行为。沉默就是最好的答案...你听好了，所谓的猎人是这个世界上最高贵的职业我不想去参加什么考试当什么猎人，我只要大家都过上幸福美满的日子，我就心满意足了中文版窟卢塔族的祷告词：天上太阳 地上绿树我们的身体在大地诞生我们的灵魂来自於天上阳光及月光照耀我们的四肢绿地滋润我们的身体将此身交给吹过大地的风感谢上天赐予奇迹与窟卢塔土地愿我们的心灵能永保安康我愿能与所有同胞分享喜悦愿能与他们分担悲伤请您永远赞美窟卢塔族的人民让我们以红色的火红眼为证英文版窟卢塔族的祷告词：The Sun in the SkyTrees upon the Ground.Our bodies are from the Earthour souls come from the Heavens above.The Sun and the Moon sheds light on our hands and feet.The nature rejuvenates our bodiessends our bodies to the Wind that blows across the plains.Give thanks to the Gods whoabode in the Heavens for the land of the KurutaLet our spirits live in everlasting vigor and protectionI seek to be capable of sharing the mirth with my peopleto be capable of sharing their sorrowsOffer homage to the people of the Kuruta TribeLet our Blazing Scarlet Eyes bear witness。全职猎人：奇犽·揍敌客经典语录，满满的回忆！', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10532', '0', '全职猎人：我，酷拉皮卡，真女主，全猎人最美！', '酷拉皮卡作为猎人中我最喜欢的角色，打动我的主要是这几点。1.智商。酷拉皮卡毫无疑问是继承了藏马的智商的，思路清晰，冷静，领导能力强。特别是旅团篇的那个摸黑行动，（个人认为雷欧力应该想不出如此精妙的计策，应该是酷拉想出来的），给我一种非常有趣和震撼的感觉，因为酷拉和团长这两位都是智商极高的存在，所以个人认为旅团篇可能是猎人中最有智商，情节最曲折的一篇了。2.坚强的意志和不动摇的决心。小杰另一个翻译是“冈”，名字一听就是那种迎难而上的热血少年，酷拉皮卡作为一个第一印象感觉是温柔型和参谋型的角色，真的没想到会有如此之强大的意志和决心（同样也很不好说话，这点反而有点像飞影）。很多人喜欢奇牙，奇牙秒杀比他弱很多的敌人时确实很帅气，但是在面对比他强很多的幻影旅团时，他选择避其锋芒。这固然是一种冷静理智的表现，却说明奇牙没有遇强则强的勇气。不过奇牙在蚂蚁篇和会长选举篇成长了很多，那就是后话了。3.强烈的善恶观念（也就是正直的三观）。四人组中，小杰，酷拉，雷欧力三个人都是三观极正的人。酷拉皮卡看似平淡的外表下，隐藏的那是一颗丝毫不逊于小杰的热情的心。哪怕是刚刚结识一天的赏金猎人同伴（费婕那些人，完全npc），他也关心他们的安危，质问窝金“就因为你个人的决定，就要有那么多人去死”，在与窝金决斗时，也不愿意殃及无辜，主动要求去野外，有些人把酷拉的“不择手段也要达到目的”理解为他不关心同伴，那就错了。他能够毫不犹豫为复仇舍弃的，不是同伴的命，甚至也不是陌生人的命，而只有他自己的命啊！这样的酷拉让人心疼。也正因如此，他才能放掉近在眼前的屠族仇人来换取同伴的安全。还有，酷拉的善良还表现在一件小事上。黑暗大陆篇的王妃让酷拉抱抱她的女儿时，酷拉的那个眼神是多么的温柔又忧伤！我永远也忘不了。4.当然就是颜值了 ，我相信奇犽帅，酷拉美，这句话没有谁不同意吧2333最后，我还是想问问，那些讨厌酷拉的人到底讨厌他的什么。一个年幼时被旅团屠杀全族，背负血海深仇，本该极度心理扭曲的人还能做到这样，还能成为一个为了朋友而放下仇恨的人，你还想要求他什么？', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10533', '0', '全职猎人：旅团决定上船开始，跟酷拉皮卡的冲突就不可避免了！', '从旅团决定上船开始，旅团跟酷拉皮卡的再次冲突就不可避免了。如果只是西索跟旅团的捉猫猫游戏，根本没必要特意玩到船上来。所以西索跟旅团的游戏应该一下子就破坏了原来固有的王子继承战格局。富奸就喜欢玩这种自己设置好能猜到故事走向的故事结构，然后全面打破它。比如仙水死于雷禅干预，三魔王大战一下子变成魔界比武大会。猎人淘汰比武一下子因为奇犽杀人而结束，库拉跟旅团的第一次冲突旅团本来要死一半人的，突然因为交换人质暂停。蚁王再次复活，我以为只有传说中金出面才能解决了，结果富奸再次让早就中毒的他复活只是为了让他跟小麦殉情。。。所以继承战肯定不会像我们想到那样四王子后发制人干死所有人，库拉帮助14王子干死四王子。富奸讨厌被才到走势的故事格局。肯定不会这么玩所以旅团都出现肯定会打破继承战的走向。何况这里面还有金跟帕丽斯通的最高智商对决！关于预言师能力消失。我觉得不用猜了，被酷拉皮卡保护的大小姐肯定死了。她的死跟旅团脱不了干系，所以库拉皮卡跟旅团的新仇旧恨要一起算了！说实在的，库拉皮卡天赋虽高，跟旅团实力还是有差距的。常态酷拉皮卡连子弹都防御不了。绝对领域状态也跟窝金有很大实力差距。他能干死所有旅团的秘密武器就是中指的誓约链。酷拉皮卡是不能对旅团以外的人出手的。这条情报有多重要，那个记忆女不惜用命传达给旅团。绝不会白白浪费。我当初看到那里，我就想，旅团要对付酷拉皮卡肯定不能硬干了。团长在突袭的状态下都被秒，硬上肯定损失巨大。那么肯定要雇人解决酷拉皮卡了。所以，有比揍敌客家族更好的雇佣对象吗？', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10534', '0', '全职猎人：那迷茫在黑暗中的王子——酷拉皮卡', '“右上角关注『酷漫人』更多动漫原创，都在这里！”今天要说的，不是团长、西索，也不是金、奇犽，而是猎人四小强之一：酷拉皮卡。首先，酷拉皮卡的本质是单纯、善良的。在旅团扫荡卢库塔族前，酷拉皮卡和小杰没什么不同，只是一个过着无忧无虑生活的小孩。可自从那晚毫无道理的灭绝人性后，他变了，变成一只游荡在黑暗中的孤魂。如果没人拯救他，估计他只能一直在那深不见底的黑暗中沉没下去，直到死亡。要说在《猎人》中最令人心痛惋惜的人是谁，酷拉皮卡绝对妥妥第一。下面就让我们从几个方面了解一下酷拉皮卡的魅力，小编觉得，正是这悲惨的命运捆住了他，也正是这命运成就了他。一、颜值高不同于男人的英俊和女人的妩媚，酷拉的帅气是介于两者之间的中性美。在第一次和小杰相遇时，觉得他给人的感觉和阳光的小杰很不一样，不会把他当主角，但也好像不是配角，现在看久了之后反而越看越顺眼。酷拉是属于拥有独特气质的那类人。二、个性温柔酷拉本性就是温柔的，对待朋友更是如此。只要你是他的朋友，他就能设身处地为你着想，体会你的难处和苦处，无论小杰还是奇犽还是雷欧力，这方面都不及酷拉。三、智商高无论平时生活工作的智商还是战斗智商都极高！虽然《猎人》的基调断定了角色们的智商普遍不错，但和其他人对比看来，酷拉的冷静睿智还是相当出众的，尤其是观察能力和推理能力。只是有时候被愤怒影响了情绪，但也能及时调整过来，这又是另一个优点了。四、极强的学习天赋酷拉学习念的速度非常快，并且开发了近乎bug的锁链技。刚才说那悲惨的命运成就了他，是的，正是这不公的命运让酷拉钻研出针对蜘蛛的近乎无敌的战技。网友亦普遍认为酷拉的单挑能力是最强的，小编也比较认可这个说法。酷拉的强和他的命运不可分割，可这种强，实在带着太多悲怆的味道。五、心存善念、杀伐果敢明明已经那么恨旅团，但在和窝金的战斗中依然忍不住问“为什么就可以那么冷静地杀人，你们杀人的时候到底在想什么？”。经过交手，酷拉知道即使自己再善良，有些人就是感受不到的，所以他不再犹豫，选择举起屠刀。可以这么翻译他的心情：我不想杀人，但你们已经做下了罪无可恕的事情，那我也只好不择手段，誓要把你们杀个片甲不留。地狱见吧，蜘蛛们！也正是从这一刻起，酷拉真正走上了那条黑暗的路。在最新的漫画情报中，酷拉已经失去了五年寿命，估计未来还会继续牺牲，是寿命？还是别的什么？不好说，但现在，酷拉是四小强里面最危险的。黑暗大陆本来就是超出想象的地方，酷拉还没算真正踏足已经损失这么大了，以后会怎么样，真的让人捏把汗。最后啰嗦说一句：富坚你打完麻将了么？（动漫、全职猎人、动漫、全职猎人、动漫、全职猎人、动漫、全职猎人）', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10535', '0', '全职猎人：8万米马拉松，雷欧力累成狗！而小杰奇犽……', '第一次猎人考试的内容就是跟着考官萨特次走到第二次试验会场。在单调又见不到终点的路线上，没有主角光环的人物已经开始陆续出现脱退者。如果有主角光环这一关就轻松过关,那也显得太容易了,总得找个人来衬托下考试不是一般人能完成的。这不是，跟着酷拉皮卡一起跑的雷欧力虽然也跑得越来越吃力，但基于他对猎人的执着，还是一直紧跟在后。总而言之呢，四大主角除了雷欧力表现差点，对其他人来说跑跑马拉松还是很轻松的嘛。在过了80公里后，眼前出现了阶梯，但萨特次的步伐却又加快了。最尴尬的是，人家小杰和奇犽奇才使出一分功力就轻松通关了，顺便还打赌请客吃饭…...真是羡慕妒忌恨呐！不知道小杰奇犽的体力上限到底在哪？第一场次考试到这也标志着结束了。那么就进入第二考试考场场-“,欺诈师之巢”吧！（未完待续……）', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10536', '0', '按照预言诗的发展，旅团的覆灭才走到一半，库洛洛境地凄惨', '旅团的命运现在处于岌岌可危的阶段，团长库洛洛在友客鑫市偷走了妮翁的天使自动笔记后，曾给团员预言，结果是噩耗，总计13人的旅团，将要失去一半的成员。友客鑫市在酷拉皮卡的设计下，已经失去窝金和派克诺妲两位，而和西索定下了约，相信预言诗所说一路向东，可以找到解开自己被封印的念能力的方法，结果是西索帮忙找到了除念师，而团长要和西索在天空竞技场对决，为了让西索败，而选择了侠客和库哔的能力，最后西索败了，但侠客和库哔也牺牲了。这一环扣一环，旅团真的万万没想到除了一个酷拉皮卡，厄运还来自西索。截止到现在，旅团就已经剩下9人了，按照13人来算还没有达到预言诗所说的一半，西索曾经说自己要把旅团一个一个杀尽，那么剩下的旅团成员很可能还会有人牺牲在西索之下，玛奇和西索见面的最早但西索没有杀她，也和他算是有点关系，所以应该是逃过这一劫了，剩下的人里小编以为打酱油的角色很容易被西索搞定，再加上酷拉皮卡的主要能力是针对旅团的，旅团只可能损失一半，再重新招人，但一定不会整个团都消失，否则酷拉皮卡要哭晕在厕所了。另一方面库洛洛现在在王子争夺战的船上，他夺取别人念能力后，念能力消失的方法有两个，一个是直接让书消失，一个则是原使用者死亡，所以在船上库洛洛正和一位大叔聊天，突然黑线的原因大概也是侠客和库哔的念能力从库洛洛的书里消失了，他知道这两位已经命归西天，也一定知道是西索干的好事，库洛洛也算是被西索摆了一道，因为自己用了成员的念能力而使他们丧生，明明想要规避预言保护旅团，没想到依然逃脱不了命运，再来现在失去了对付西索的有利武器，要是再和西索见面估计没得商量直接开打，所以他又要研究一遍怎么对付西索，从哪里获取合适的念能力了，库洛洛可以说这一下境地很悲剧。但下一次应该还有点时间，毕竟库洛洛正往黑暗大陆走，西索还在到处徘徊。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10537', '0', '论变态，西索、伊尔迷和库洛洛谁更胜一筹？', '小编之前被《幽游白书》圈住，成为了一个不折不扣的富奸粉，然后，从此就踏上了掉坑—爬坑—再掉坑的不归路。然而，富奸的坑让小编摔最惨的，还是《全职猎人》，它的动画版卡在选举篇之后，黑暗大陆篇可谓遥遥无期。不想去刷漫画的小编，只好眼巴巴地再次炒炒冷饭，舔舔奇犽和酷拉皮卡的盛世美颜。不过，每次刷都能刷出新意，越看越能发现富奸老贼的魅力——即使是刻画一个变态，也能刻画出一个讨人喜欢的变态！比如，西索，比如，伊尔迷，再比如，库洛洛。然而，此时小编就不得不问，都是变态，他们三个，谁更胜一筹呢？？首先，来说下，他们各自哪里变态。西索——喜好变态。西索作为猎人篇前期一直没有和主角团队对上的boss，一直有让主角团望而却步的实力。而他的生活目标，就是寻找强大的猎物、或培养敌人，等之成长到有杀死的价值，就将之打倒杀死，颇有独孤求败的意味。而支撑他这一喜好的，就是他强大的实力，以及对自身实力的绝对自信。西索的杀招名叫：伸缩自如的爱...不知道有没有其他的GAY友跟小编一起想歪...哈哈哈~~还有，就西索喜欢找人挑战，小编还是有个疑问的：蚂蚁篇蚂蚁王实力那么逆天，完全满足西索选对手的标准，他为什么没有参与围剿蚂蚁？蚂蚁王和西索打起来，是不是也能完虐西索？伊尔迷——恋弟变态虽然富奸没有直接点出，但是，是个人都看得出来，伊尔迷是个不知不扣的弟控。他控弟控到可以往亲弟脑子里插钉子，真的是“控”弟界当之无愧的王者！不得不说，揍敌客家族的教育方式，不出产奇葩和变态才奇怪。所以，当西索说要杀奇犽的这时候，伊尔迷分分钟黑化说要杀了西索。我控弟，也得我控，其他人谁都动不得。库洛洛——能力变态库洛洛，出生流星街，是幻影旅团团长，一位优雅的盗贼。他的能力，为“盗贼的极意”，是一本具现化的书籍，有夺取他人念能力之用，而念能力被偷走的那一方便无法再使用那种念能力。盗取他人的能力，是类似中国古代吸星大法的存在，一旦盗取成功，将是多么可怕。还好，库洛洛发动能力的限制条件挺多，不然，他真的无敌了。但是，就算条件多，也抵不住他脑子好啊。真的是盗贼不可怕，就怕盗贼有文化。当然，这三人的实力都是变态强。传说在漫画新篇中，西索和库洛洛已经对上了？那么，谁胜谁负？如果西索、伊尔迷、库洛洛三人对上了，谁又胜谁有负？这些，小编也不能肯定。但是，小编我有一定是绝对能肯定的，那就是，这三个人无论谁领了便当，富奸老贼都是要收刀片的。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10538', '0', '论颜值：库洛洛vs西索', '​ ​库洛洛·鲁西鲁，出自《全职猎人》（《Hunter X Hunter》）中的角色，身份为盗贼团体幻影旅团的团长。长的帅，可以说是少女杀手了​西索，幻影旅团成员之一，行事诡异变态，我的最爱，这里重复说了看看他的帅照​​​单论颜值谁是你的菜？？​​​​​​', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10539', '0', '全职猎人三大美色之一——库洛洛', '知道你们喜欢团长，所以不要大意的点进来收图吧！1.2.3.（id=1192457 ）4.（id=248222）5.（id=13289148）6.相爱相杀的两个人（id=1234667）【P站id已附，食用愉快】', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10540', '0', 'cos妆面教程——库洛洛·鲁西鲁妆面教程', '（本篇转载于网络，版权归原作者本人所有，如有异议请告知，并给予马上删除）关注微信公众号“cos领域” ID：cosplay-vvQQ分享讨论群：71170674商业合作：QQ：423840042', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10541', '0', '阿玛尼发光小滴管评测 全天不断电的LED发光妆就靠它', '如果你渴望珍珠般的润泽，就不要把“白”看作美白的唯一标准，一味地追求苍白只会让你看起来死气沉沉，总像挂着疲态。肌肤由内而外散发的光泽感才是好气色的关键！如果你皮肤不够白白，那么小编告诉你，只要发光，还是可以打造超强气场，不管是甜美柔情范还是冷艳女王范，底妆发光发亮除了给你加分外，还是加分！有没有一款粉底能够让皮肤自带“发光”效果，却同时持妆、遮瑕、养肤一步到位？答案是：阿玛尼发光小滴管! 有了它，轻松打造全天不断电的LED发光妆。评测结果：外观：在静置状态下，阿玛尼发光小滴管是分层结构，上层为抗氧化护肤精油，下层粉体，属于全精油无水配方。上下两层虽然不同，但是阿玛尼通过融萃粉妆的技术做到了两者的融合。滴管式的设计让整个上妆过程仪式感十足，由于不会直接接触皮肤，还能保证粉底的新鲜纯净。质地：轻轻摇匀粉底液，将阿玛尼发光小滴管滴在皮肤上，可以看到它的质地非常细腻轻薄，水润感十足，用手指轻轻推开就能轻松上妆。我们用眼线笔在肌肤上画字母，然后将阿玛尼发光小滴管涂抹于肌肤上。能观察到眼线笔的印记变得很淡，几乎看不出来了。这款粉底液在保证了质地轻盈的同时还拥有出色的遮瑕力，轻轻抹开就能达到自然遮盖的效果。使用高精度水分油分测试仪，测试产品使用前和使用后的肌肤水分、油分含量。评测前，肌肤的水分值为33.8%，油分为50.7%，在涂抹了“发光小滴管”后，水分值上升到84.2%，油分值下降至17.3%，可见阿玛尼发光小滴管蕴含的高浓度抗氧化精油，可以调节肌肤水油平衡，牢牢锁住皮肤的水分，软化角质，让皮肤更柔软顺滑。小编素颜时肤色暗黄不均、毛孔粗大，使用阿玛尼发光小滴管上妆后，肌肤变得很白皙，孔瑕疵都被修饰的很好，基本上都被隐藏起来了，遮瑕力非常好，而且一点粉感也没，清爽不油腻的舒适。整体肤色通透明亮，散发着自然的光泽，宛如肌肤天生就这般的无暇润泽，自带美肌效果的完美。闺蜜小编说：阿玛尼发光小滴管为分层设计，上层为护肤精油、下层粉体，护肤精油让皮肤在得到充分滋润的情况下散发出自然、透亮的光感。质地不仅轻薄，还拥有出色的遮瑕力，毛孔、痘印都能轻松遮盖。上妆后肌肤散发出由内而外透亮的光泽，并且水润十足，宛如肌肤天生就这般的无暇润泽，自带美肌效果。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10542', '0', '世界水日——水小滴“骑”妙之旅趣味骑行', '骑出绿水青山，珍惜滴滴水源。为迎接3月22日世界水日、中国水周，在北京市科协指导下，北京水源保护基金会联合通州区科协、通州区水务局、通州区园林绿化管理局、通州区市政市容委、通州区大运河森林公园管理处，于3月24日在通州区大运河森林公园举办“水小滴“骑”妙之旅”公益活动，北京排水科普馆、北京生态修复学会、北京生态修复与环境保护联合体、争鲜回转寿司、世界竞技叠杯协会（中国）、汉麻集团等合作单位积极响应号召以科普展览、公益捐赠、公益骑行等不同形式支持此次活动，活动共计200余人参加，一同践行水公益，为环境减负。世界水日知多少2018年3月22日是第二十六届“世界水日”，3月22－28日是第三十一届“中国水周”。2018年联合国确定世界水日的宣传主题是“Nature for water”（借自然之力，护绿水青山），我国“中国水周”的宣传主题是“实施国家节水行动，建设节水型社会”。北京作为水资源匮乏的城市之一，近年来随着人口的增长和首都经济的快速发展，水资源短缺已成为制约地区经济发展的主要瓶颈，积极采取有效措施建设高效的节水型社会，成为北京市发展的必然选择。北京水源保护基金会成立于2008年1月，是全国首家以水源保护为己任的4A级公募基金会，自成立至今一直以“科普先行，公益为本”的理念开展业务。2017年“安全水缘”项目上线，为水源地上游贫困学校捐赠净水设备，普及健康饮水观念，改善饮水环境，上万名中小学生收益。2018年，北京水源保护基金会继续将目标集中在增强社会保水意识、推进保水行动中，本次世界水日活动，更是“水科普展览+募捐+骑行”等多样性与趣味性公益分享互动的方式，引导和动员更多人关注、参与和支持水源的保护与建设。此次活动现场还摆放了众多来自受捐学校所捐赠的节水创意画作，作为公益捐赠礼品回馈捐赠者。用公益点缀生活，倾听受助人节水保水的呼声。水小滴“骑”妙之旅春风吹绿了北京通州大运河岸的柳芽，喧闹的花朵正在谈论如今的环境多么美好。然而，水资源日益短缺，污染也处处可见，大运河森林公园作为北京的城邑之水，更应该受到人们的关注。本次骑行活动，150名骑行队员，迎着春风，就是在用环保出行的方式号召更多人节水护水。本次活动的支持单位之一的争鲜回转寿司共50名员工参于活动，并现场摆放展板呼吁大家节水护水，其负责人表示：“作为健康美食的提供者，在餐品的制作过程中会用水保障食物的新鲜和干净，这样做偶尔会产生水资源的浪费，所以我们希望通过此次活动让我们员工切实感受到节水的重要性，在保证食物干净和新鲜的情况下，随手关闭水龙头、生活中充分利用可以循环利用的水尽可能的减少浪费，同时以身作则让更多顾客参与到节水护水的行列中来。我们都在消费水，是时候也要为水做些事情了”集公众力量，建生态中国北京水源保护基金会副理事长于立安表示水是生命之源、生产之要、生态之基。我们国家是一个水资源贫乏、人口众多、自然灾害频繁的国家。缺水是我国的基本国情，因此，我们每一个人都要深刻认识到水资源紧缺的严峻性，增强水的忧患意识，形成人人关心水、爱惜水、保护水、节约水的良好的风尚！今天在节水护水典范的北京通州大运河森林公园举办活动，是为了号召更多的人共同为建设生态美丽北京贡献力量！', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10543', '0', '阿玛尼发光小滴管怎么样 全新小滴管肌肤blingbling', '阿玛尼发光小滴管怎么样官网上介绍这款粉底是全精油粉底，滋润得来又非常轻薄。看到产品就迫不及待试了一下!阿玛尼发光小滴管：分层在使用之前，粉底会产生分层的，这是正常现象，用前需要摇晃，滴管头比沾取式粉底更加卫生方便。阿玛尼发光小滴管：质地从体验效果来看，粉底本身流动性很强，只是拍照了瞬间就滴下来了。推开之后，粉底颜色比上图试色要浅一点。质地比较清透容易推开，推开之后颜色比滴在手上看的粉底浅一点。光泽感明显，打开大图看到右边涂过粉底比左边没涂粉底的光泽感更好，也因为质地轻薄所以遮瑕度一般。阿玛尼发光小滴管：上脸试色点涂适量的粉底再点在你脸上，用气垫粉扑推开。粉底流动性比较强，非常好推开，质地轻薄，遮瑕度一般，整体出来的妆效偏清透。这里试色的是2号色，在脸上会有提亮的效果，比较浅的小红点能遮盖住，比较明显的有弱化效果但并不能完成盖住，毕竟不是主打遮瑕的粉底。效果图上看到的光泽感超棒，整体持久度还可以，油皮需要叠加控油产品。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10544', '0', '生活也需要零感——阿玛尼小滴管系列体验分享', '生活当然需要灵感，那我的底妆当然也是！今年的六月好像热的特别的快，太阳毒辣而且紫外线强，但是尽管如此雾霾沙尘暴还是一个没有少，坐标大天津的我表示防护面子工程做不好，简直太糟心了噢…这还好，要是露天逛一下午马路或者去海岛旅行度假，这脸保证妥妥的黑一圈…虽然底妆用了不少，但真正坚持下来的还真不多，轻薄遮瑕度又足够服帖的阿玛尼一直是我的最爱，绝对的彩妆良心品牌。怀揣着对品牌的好感，所以当它又隆重推出了应对夏日抵御紫外线（ UVA/UVB）侵害&外界空气污染导致的氧化现象新品时，作为忠实粉的我当然要妥妥的抢先体验看看。阿玛尼这一次将创新理念带入日常肌肤防护，同时秉承“小滴管家族”极致轻盈的独特配方质地的概念，推出了新品阿玛尼极缎丝柔防晒隔离露SPF50 PA++（后文简称“防晒小滴管”/“Maestro UV”）。阿玛尼的产品颜值都蛮高的，防晒小滴管除了配方与质地上沿袭了小滴管家族之外，外观上也蛮近似的，同样的黑色胶头滴管设计搭配白色瓶身，简约时尚自成一格。滴管方面一方面能够有效隔离外界的污染，最大限度延长使用寿命；而同时滴管可以方便精准控制用量，日常3-5滴就能涂抹全脸，比较耐用。防晒小滴管是一款薄到无感的革命性多重防护品。那么到底有多薄呢，当然要在手背上试试，轻薄无感的透明质地，涂抹的过程中完全没有厚重感，真正的薄如空气般轻盈。同时在这瞬间为肌肤覆盖上一层超薄膜，为肌肤抵御自由基外界避免紫外线侵害。简单的来说就是“看似什么都没有，但是什么都有的神奇存在！”集合超强防护防晒指数高达SPF50 PA++与强力抗氧化侵害的双重功效，我有用PH试纸对防晒小滴管质地进行测试，试纸呈现弱酸性，适宜日常的使用。包括敏感肤质也能安心体验！有人说精油基底会不会有些油腻，所以我用吸油纸按压再刚才涂抹了防晒小滴管的部位。静置了一会儿，可以发现吸油纸上残留极少油渍，也没有白色印记。证明了它的极致肤感，没有厚重油感，像第二层肌肤一般的亲和自己的面部，不会给肌肤带来负担。完美打底配方融合三种不同直径的球形填充物，能有效吸收和减少油性物质，让肌肤平滑柔软，与后续粉底液也能巧妙融合，简直不要再相配！我把小滴管涂抹在脸颊上，让大家观察情况哦！发现用过防晒小滴管的部位润而不腻，没有传统防晒隔离的泛白不适，对于暗沉肌肤有一定的提亮作用，整个人看起来气色也好了不少呢！！！接着粉底液部分我选择了叠加小滴管家族的阿玛尼极缎丝柔精华粉底液（又称“发光小滴管”/“Maestro Glow”）。选择它作为“防晒小滴管”拍档的主要原因是，这款“发光小滴管”能够以油养肤，与“防晒小滴管”一样涂在脸颊上轻盈细腻薄敲极薄，同时兼具润而轻盈，延展性佳，最重要的是特别特别滋润!!!没有错，就是我用过最润的粉底液！！像用了保养品一般的那种通透的亮，改善毛孔的遮瑕度也很赞，就连粗大的鼻翼毛孔也能修饰照顾到哦~~防晒小滴管与发光小滴管叠加使用后的妆后效果图。没有泛白卡粉，肤色均匀有光泽，给肌肤最强呵护力。现在压马路或者海岛游即使有大太阳，只要有这么两瓶就能放心去玩耍！！！', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10545', '0', '小滴事儿报丨7月刊', '纵观滴滴七月风云横扫快车头条资讯《小滴事儿报丨7月刊》重磅出炉Page 1 独家头条▼Page 2 七月大事记▼Page 3 七月大事记▼Page 4 福利榜▼Page 5 技能栏▼Page 6 广告位▼关注滴滴快车车主订阅号（didi-kuaiche），了解更多快车资讯。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10546', '0', '攻略｜小滴事儿报十月刊，总有你需要的', '邀您共赏十月资讯滴滴快车车主Page 1 广告位Page 2 独家头条Page 3 十月大事记Page 4 十月大事记Page 5 福利榜Page 6 技能栏关注滴滴快车车主订阅号（didi-kuaiche），了解更多车主资讯。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10547', '0', '阿玛尼发光小滴管～', '阿玛尼发光小滴管就是这么任性，对于发光肌的热衷应该是追溯到韩剧大热的时代，剧中的女主角每个都有着吹弹可破的透亮肌肤，美妆达人们尝试用高光液混合在粉底中达到底妆透亮的效果。然而我们现在有了更好的选择--阿玛尼发光小滴管，开启了新的美妆革命，一抹即可拥有水光肌。突破性的将护肤精油融入粉底液中，赋予肌肤更加水润细腻的体验，更加轻盈透亮。静止放置后就会出现精油的分层结构，是市面上少见的独特设计哦~所以每次使用前必须摇晃均匀。精油和粉底液混合均匀后就可以取用，发光小滴管的设计方便控制用量，一般一滴管就可以用全脸了。G米入手的是4号色，属于自然色号，适合打造轻盈无妆感的妆效。查看膏体的时候会觉得色号稍深，但是涂抹开后，完全融入肌肤自然通透。涂抹在手背流动性超强，快速的顺着肌肤的纹理滑落了。推开后完全融入肌肤，完全无妆感，但是明显有使用过的一边会有明显光泽感。每天早上都是最忙的时候，加班熬夜的暗沉肌补救起来是个大工程。阿玛尼发光小滴管，一抹既可拥有最in发光感的轻透妆效,简直就是美肤神器哦～', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10548', '0', '滴滴快车联手时空电动 杭州首推电动“小滴”', '第一电动网发表于：2015/12/19 09:06电动杭州车00导语 滴滴快车联手时空电动，在杭州细分市场首推电动“小滴”，采用车型时空E20电动汽车，最高时速80公里/小时，续航里程为260km。从8月17日开始，杭州滴滴快车的车型选择中悄然出现了“小滴”的选项，客户如果选择的了这一选项，则前来接送顾客的车就是一款时空电动出的电动汽车时空E20，价格比普通快车便宜20%，只搭载一位乘客。“小滴”时空E20电动汽车8月18日，滴滴正式公布了这项新业务的详情，滴滴专车华东区总经理孙枢表示，这是滴滴专门为一人出行这一细分市场的推出的定制化服务。从“滴滴快车”的运营数据来看，上下班、赴约等用车很大部分都是接送一个人的，这些需求采用微型电动汽车就是更为高效、环保的个性化解决方案，还能够分散高峰期的交通压力。据介绍“小滴”采用的这款时空E20电动汽车外型酷似Smart，不同的是“小滴”不烧油，只充电。E20的最高时速可达80公里/小时，续航里程为260km。可使用220V家用进行充电。快充20分钟可充80%，慢充8小时即可充满。百公里耗电约为10-14度。按照杭州最低电费换算，E20每跑100公里，仅需花费2.8元-3.92元。在价格方面，“小滴”比快车更实惠一些。据了解，“小滴”的定价为0.99元/公里，0.3元/分钟。收费方式为公里费叠加时间费。该收费标准相当于目前杭城滴滴快车的8折。“小滴”时空E20电动汽车开启换电模式时空电动CEO陈峰表示，“小滴”目前的续航里程是260公里，现在夏天开空调比较费电的情况下大概200公里左右，这样的续航目前还不能完成一整天的运营需求，所以该车还设计了换电模式。连续用车的情况下就采用换电的方式，另外配合快充和慢充，基本能够达到与传统快车一样的运营效率，但是使用成本却比传统快车低很多。具体低多少？陈峰表示，具体的数字不方便透露，但是可以说在国家补贴+地方补贴的方式下利润相当高。滴滴专车华东区总经理孙枢（左）、时空电动CEO陈峰（右）据悉，滴滴与时空电动将陆续在杭州投放800辆“小滴”，到年底前，在杭州，时空电动将投入5000辆新能源汽车到滴滴专车、快车平台。孙枢表示，从目前的投放情况来看，市场的需求远远大于这一投放量，只是目前考虑到保持服务质量以及电动汽车的供货量，暂时计划投放这个数量。如果这个模式能够成功，也会考虑向北上广深等城市快速复制。“小滴”的使用流程很简单，消费者通过滴滴打车APP呼叫快车时，可在快车的二级入口看见“迷你型”的选项，选择该选项后，消费者即可发送呼叫“小滴”的订单。不过，选择“迷你型”选项的同时，用户还需要专门取消快车选项，否则就是滴滴快车和“小滴”一起抢单，那多半是快车抢到，因为快车数量更多。记者试着选择“小滴”时却好久等不到接单车辆， 这可能是目前促销活动的结果，8月17-19日，使用“小滴”的消费者可以享受到“0.01元/公里+0.01元/分钟”，保底价1元的打车价格，算下来基本就是1元钱享受电动专车服务，按照中国人“乐于排队抢优惠”的传统，这两天估计无法正常体验了。这次“小滴”的司机都是与时空签约的专职司机，其中一位“小滴”的司机张师傅表示，很多人都在说电动汽车问题多，开始开的时候也有点担心，但是开过以后发现，和普通汽车也差不多，甚至电动车开起来还更安静、更舒服。以前担心里程不够用的问题基本没出现，因为在接单的空隙中就可以充电，实在时间来不及还可以换电，目前也不怎么担心里程问题。作者：杨世明相关阅读近日，奥迪美国公司产品管理总监Filip Brabec表示，奥迪在考虑引入一些不同形式的经销商，并提供更加完全的试...2015/12/04 09:49明年上海市免费沪牌等新能源车政策仍将持续，并进一步加大新能源物流车的投放力度，初期计划发展3000辆新能源...2015/11/17 10:10《节能与新能源汽车示范推广应用工程推荐车型目录》(第75批)发布，风神、红星、吉利、东南、众泰、理念、力帆...2015/11/17 10:1011月14日，由电动邦精心主办、北京宝诚百旺汽车销售有限公司全力协办的“电动邦宝马i3品鉴试驾会”在北京市百...2015/11/15 12:2311月18日，吉利汽车将推出续航里程达253km的纯电动车型新帝豪电动版，预计起售价将在25万左右。 2015/11/10 09:59捷泰纯电动物流车型是众泰汽车为金网新能源公司生产的纯电动流用车，这也是又一款将被应用在物流行业当中的纯...2015/11/06 09:32电动邦宝马i3试驾会等你来参加，现场不仅能领高端新能源车型的风采，更有Apple Watch、电动滑板车、咖啡机惊...2015/11/05 18:45三菱汽车在东京车展上发布了一款名为eX的纯电动概念车，据悉该车搭载新一代电池组，续航里程为399公里。2015/10/31 12:472015年10月23日，DS维珍车队2015-2016Formula E新闻发布会于北京召开。2015/10/26 10:03最近有海外媒体报道，日产在未来可能推出纯电动版本的GT-R。 2015/10/22 10:22评论', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10549', '0', '红遍美妆界半边天的阿玛尼的发光小滴管好用么？', '作为阿玛尼产品的忠实粉丝，不得不说阿玛尼的产品真的是非常的好，虽然价格有些贵，但是物超所值，女人就应该对自己好一点。那么阿玛尼的发光小滴管怎么样呢？我就来说说自己的使用感受，这款阿玛尼（GIORGIO ARMANI）发光小滴管丝柔精华粉底液，只有30ml，但是价格就需要400多，我也是听朋友说好用才用的。它是适合任何肤质的，能够提高皮肤亮度和立体感，摆脱面具脸的困扰，它附着力很好，轻轻涂抹，色斑、痘印等能够被隐藏，而且能够起到控油保湿的作用，还能够滋养肌肤，改善肤质。然后它的使用方法，我也是根据它产品的介绍使用的，首先先取适量粉底液点在额头，笔尖两处和下巴的五个部位，然后用无名指和中指，而且是要两个手指同时操作，由上往下，从额头到脸颊再到下巴，双手分别往左右两边呈一字型，轻轻推开，然后双手轮替着从眉心到鼻尖成1字涂抹。其实除了阿玛尼的发光小滴管好用外，阿玛尼的其他产品也是非常不错的，阿玛尼口红唇釉也非常的好，个人是非常喜欢大红色，所以我在选择这款口红的时候，我选的是大红色，非常的显气色，而且不沾杯，保湿效果也非常好。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10550', '0', '全球第一部混合现实小说「华腾云码」之四：AI小滴不断赋能AI', '上海城，第78届国际宇航大会现场，旭日初升，天空沐浴在一片粉红与橙黄中，许多人沿着长长的码头，走向一艘白色的大船。他们登上甲板后，船便起航，全速驶向水域深处的发射台——那里，有一艘绑定在巨型火箭上的宇宙飞船正在恭候他们。待他们鱼贯而入，火箭被点燃，飞船载着他们直冲云霄。大概 39 分钟后，他们便降落在了地球另一端的城市——纽约。这艘宇宙飞船已经工作三年了，它刚面世，斯僧就给它起了一个“AI小滴”的名字。这种1.0版本的“AI小滴”可在 1 小时内将多个乘客运送到地球的任何地方。譬如从香港到新加坡仅需22分钟，从洛杉矶到多伦多仅需 24 分钟，而从纽约到上海也仅需 39 分钟……。然而今天要发布的，是斯僧历时20年精心喂养深度学习开发的私人版“AI小滴”，拥有私人版“AI小滴”的人们，只要一对一的语音或眨眼等命令，就可以起飞和降落。不仅是在地球上各个国家来来往往，而且可以在各个星球之间的AI小滴站址之间精准穿梭。斯僧站在发布会舞台的正中央，仍然是那黑色套头衫和牛仔裤的行头，他很乐观地说到：乘客到时会感受到 2-3g的重力加速度（效果相当于 2-3 倍的体重加到自己身上），会觉得就好像在体验温和而不致过于刺激的游乐项目，先是上升，后趋于平顺、和缓，最后静悄悄失重，度过剩余的大部分旅程，直到平安落地。斯僧说，私人版“AI小滴”是人类从三维空间进入多维空间的革命性工具。这不是科幻未来，这就是我们的现在。无须购买，更可共享，这是人类和多维交通最优匹配，更安全更极速的自动驾驶。“AI小滴”是一款人类历史上最高品质的汽车和宇宙飞船等的混合式AI机器人，它的诞生就是为共享而不是（拥有它的）消费者设计，它的使命是为人类更加安全、舒适和高效率的享受出行。它诞生在中国，它要沿着一带一路普及到全球，赋能全人类，链接宇宙文明。“AI小滴”繁荣背后不仅有工程师，还有很多社会学家、经济学家、心理学家一起，在中国这个世界交通变革的中心和全球分享经济的中心，创造了“AI小滴”。今天，让我们一起来对“AI小滴”发布：起飞！“AI小滴”就像一条白色巨龙，直窜入深水。 现场等人还没回过了神来，却不见了“AI小滴”的踪影。斯僧说道：“这个“AI小滴”乃是9999次试验以后的成功，9999次过程中多次爆炸，多次失败，愈挫愈勇，今天终于顺利起飞。”不过一会儿，那条白龙钻出了水面，从飞龙摇身一变成为一匹智能白马，并向斯僧鞠了一躬说道：“主人，我把客人送达了AI小滴火星站址，听候主人下一步吩咐。斯僧情不自禁的吹起了唿哨，说道：小滴，对着你要一路护送客人去完成美好的出行，人们每一次美好的出行，也就是你喂养自己修炼的过程，你 要好自为之。”整个现场从一片宁静突然瞬间沸腾起来。众人看到被斯僧的喂养，点化和开发的 AI小滴如此这般，都激动欢雀不已。今天，AI小滴赋能成为人类的重磅消息，让全球各地的AIstudent的忙得不亦乐乎，接受采访和咨询。而在纽约时代广场的大显示屏上，24小时循环显示着与AIstudent小娜的各式各样关于AI小滴的交互问题。这注定是载入人类历史的一天。', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10551', '0', '滴滴\"小滴\"上线杭州 造福单人出行', 'Yesky天极新闻2015-08-18 17:13:52据报道，滴滴快的今日推出“迷你”电动车型，因其采用酷似Smart车型的电动车，故被取名“小滴”。由于车型较小，车内除驾驶员外只能供一人乘坐，造福了单人出行的用户。目前，“小滴”的项目优先在杭州推广，未来计划会普及到全国其他城市。据悉，此次推出的小滴计划投放800台，车型为E20，是一款微型乘用车，均来自浙江时空电动汽车有限公司。该车型最高时速可达80公里/小时，续航里程为260km，百公里耗电约为10-14度。从价格上来说，“小滴”的价格更加优惠，定价为0.99元/公里，0.3元/分钟。收费方式为公里费叠加时间费。此价格仅为杭州当地滴滴快车定价的8成。用户在叫车时，选择“迷你”的车型，就能叫到“小滴”。早晚高峰期存在大量一个人出行的乘客，而滴滴这次推出“小滴”也是一次细分解决出行需求的尝试。“小滴”为这类单独出行的乘客提供座位，收取更低的出行费用，同时多人出行的乘客也能更容易叫到车。据悉，预计到达年底“小滴”车型将增至5000辆，可为更多单人出行用户提供服务。【请搜索微信“今天的科技新闻”加关注】', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10552', '0', '全职猎人：酷拉实力碾压窝金？我看并不可能！', '上图所示,窝金被踩时甚至未能完全防御 而酷拉的攻击根本未对沙包窝金造成半点实质伤害即使酷拉事先察觉采取防御,手臂依然被打碎 而且威力本可打碎背骨,被酷拉反应过来采取了应对。而击飞后退但有效卸掉威力,因为时间与技巧不够无法完全卸掉而被打碎手臂红眼会对身体做成额外负荷,而这场战斗绝对不超过10分钟，综上所见,此时酷拉即使是开启红眼,基础攻防力也与窝金差了不少,直接攻击对窝金几乎不能造成有效伤害,而即使有用念气防御被打中也会造成很大的伤害 赢只是因为中指逆天的能力,无强制绝,输多赢少。个别酷粉请勿神化酷拉,硬吹此时酷拉实力已碾压窝金 ，嘴巴也放乾净点， 尊重自己也尊重别人。脑洞A：窝金怎么说也是强化系中的佼佼者，酷拉比卡强不是简单的强在红眼，而是5条链的特殊能力。用RPG说，酷是法师，不过可以提高直接的防御和速度，单实质武器还是有能力的链子。红眼是辅助脑洞B：锁链和火红眼也是酷拉的实力，去掉这些只靠体术自然打不过窝金这个肉体十分强悍的。脑洞C：一个强化系的和具现化系的比肉体攻击', '', '1348', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10553', '0', '小网红cos佐仓千代，这是要火的节奏啊！', '火辣的夏天，大家都穿什么服饰呢？不知道有没有喜欢cos装的朋友们！就在端午小长假，主编芒种先生为朋友在济南曲水亭街拍摄了一组cos装，下面就一起来欣赏下吧！大家肯定都想知道妹子是cos的哪位动漫人物，她cos的就是佐仓千代，《月刊少女野崎君》女主角，野崎君的粉丝，同时也对野崎君心生恋慕。佐仓千代可爱的高中女生，主角野崎梅太郎的粉丝，同时也对野崎君心生恋慕。本人觉得她cos这位动漫主角很适合，衣服颜色搭配也很漂亮，能彰显出她的可爱萌！这是在大明湖南边的曲水亭街附近的老济南建筑群取景的，济南的夏天，泉水与柳树映衬出盛夏的时光！看这一张动作好霸气啊！壁咚！壁咚！壁咚！济南的四合院，很安静舒适的地方，虽然是夏天，但是在这里感觉不到夏天的高温！这是在济南的酒吧一条街取景的，后宰门街，是济南的民谣酒吧一条街，整个巷子很有文艺气息！看有没有像在日本街头的感觉！萌妹子可以很萌！也可以很酷！在人来人往的曲水亭街，或许你在别人的镜头里变成了路人！夏天来济南拍一组照片吧，济南的夏天景色迷人！在这个夏天穿一身泥喜欢的服饰，走在曲水亭街上，感受老济南的味道！火辣的夏天，你身边都有哪些火辣的人和事？欢迎留言评论！主编等着你的爆料！‍‍', '', '4692', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10554', '0', '我最希望出续集的，还是这四部动漫！', '我最希望出续集的，还是这四部动漫！《月刊少女野崎君》故事从高中女生佐仓千代的一场告白开始，她的暗恋对象是与自己同年级的男生，男子气概十足的野崎君，而告白的结果居然是得到了他的亲笔签名。正当千代为自己的告白台词“我是你的粉丝”引起了误会而感到苦恼时，野崎君居然直接邀请千代回家。她又紧张又期待，谁知道等待着她的是一堆漫画原稿，她糊里糊涂地当起了漫画助手。《冰菓》折木奉太郎是一个“节能”主义者，不参加任何社团的他，却因为姐姐的拜托而加入了濒临废部的“古典部”。在古典部他结识了好奇心旺盛的千金小姐千反田爱瑠，国中好友福部里志、伊原摩耶花也相继加入古典部。 千反田失踪已久的舅舅就是当年古典部的部长，于是四人开始着手调查四十五年前古典部的神秘事件。《约会大作战》与之约会，使其娇羞，顺手收入后宫。《白箱》所谓的“白箱”，是指在动画放送之前，专门制作赠送给参与了动画制作的工作人员们的录像带，因为这些录像带通常都被装在白色的盒子里，所以被成为“白箱”。 一直坚守着自己理想的制作人宫森葵、个性认真但有些懦弱的原画师安原绘麻、怀才不遇的声优坂木静香、CG画师藤堂美沙和容易沉迷与自身兴趣爱好的剧本作家今井绿，朝着顺利完成白箱的梦想共同努力迈进。', '', '4692', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10555', '0', '对野崎君心生恋慕的佐仓千代，这一次不会失望而归！', '佐仓千代，动画以及改编作品《月刊少女野崎君》中的女主角。可爱的高中女生，野崎梅太郎的粉丝，经常去梅太郎家帮忙，同时也对野崎君心生恋慕。为人亲切友善，对待别人十分真诚，有较高水平的吐槽能力，脑补能力也很高，经常脑补和梅太郎在一起的情景。这一次的女主，精心装扮了一番，虽然觉得野崎君一定把我当做普通朋友 不过我 对野崎同学一直一直 都好喜欢！谢谢你 让我陪着你 现在这样就很幸福了。喜欢的赶紧下载收藏哟！图片来自于网络，如有侵权，请联系作者删除！', '', '4692', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10556', '0', '完美女友佐仓千代篇：月刊少女，野崎君！', '今天我们也出到第三篇了，相对于前两篇的后宫和纯爱番，我们今天来单纯谈谈少女漫画里的女主角。相对于后宫番还有着宫斗和几位女主之间的来回开片。日常里还有各种各样奇特的剧情发展以及生活里的故事，甚至很多番还有关于自己人生和梦想的追求，更有不少校园番，剧情番还得找找什么“道”，“真物”，“存在的意义”，反正是不会好好谈恋爱。战斗番更不用说了，几个大佬先放下你们手中的光剑、魔法和机甲我们再来谈爱情的问题。少女番更加纯粹一些，我们只谈两件事情，爱情和虐狗。一个对内，一个对外。而近几年的这方面番剧，《月刊少女野崎君》毫无疑问是其中翘楚，其女主角佐仓千代自然也就是我们今年要谈到的主角。这个本体其实是蝴蝶结的妹子，在2014年，可是最火热的“老婆”人选之一呢。小千代喜欢上野琦的原因很简单。在开学的时候自己迟到，这位一米九高的，膀大腰圆（无雾）的男子将自己抱过了护栏，因为一连串有趣的对话，两个人的人生连在了一起。小千代找到了自己的男朋友。而野琦。找到了自己的“女主角”在一个春天的早晨，阳光，樱花和少女……我和我的命运邂逅……等等，剧本拿错了。不过，小千代确实是野琦的“女主角”。迟到的小千代被野琦抱过了护栏，一场恋爱故事就开始了。那本《来恋爱吧》女主角麻美子的外形就是小千代，而性格是野琦的同学御子柴，当然，野琦醒来以后就忘记了一切不过或许是他装呢？然而这个一紧张眼睛就会变小的女孩子，却是本剧里最幸福的女主角了。毕竟，这次的中村悠一，可没有开后宫啊（大雾）！相对于很多同龄少女，千代的“少女心”和脑洞能力相当强大，而当这样的妹子遇到一个五口的冷面男人，这场爱情故事本来就没那么顺利。当然，不顺利的只是两个人感情进度。喂屎和其他是不会有的。相对于其他番的女主角，小千代是一个完完整整的“恋爱脑”，相对于其他动漫里女主角还会有很多稀奇古怪的习惯，友情和学习。甚至还会有男二来搅局。整部《月刊少女》里，千代满脑子只有一件事情。那就是野琦。从第一集到第十二集，千代说的最多的几个字就是“野琦同学”，我们可以思考一下，为什么其他番里很少会出现千代这种全心全意拴在男主角身上的女孩子？其实原因很简单。两个人一心一意谈恋爱的番，大多数没有这么轻松的氛围。而有这种轻松氛围的番，又大多数都是后宫。女主角这种热切的爱，绝大多数后宫男主根本承受不起。而千代如此强大的联想（脑洞）能力也发生了很多有趣的故事，以及各种有的没得误会。但整个故事的发展，还是相当乐观的。当然，不谈野琦，千代是一个相当理智，也非常热爱生活的妹子。与朋友的交往非常顺利（千代有相当多的朋友，和班里几乎所有妹子都打成一片）。并且并不会因为各种奇怪的事情看轻别人（即使是和不受欢迎的结月，千代的关系也和她相当不错）。学习，运动都相当出色（成绩排行前列，运动能和结月打平手）。甚至家务也非常优秀（剧情里表示“野琦同学居然做饭比我还强”，可见千代对自己的厨艺家务还是非常自信的）。即使算不上完美少女，差距也不大了。但一牵扯到野琦，千代立刻就会化身狂热的“粉丝”。真的是厨力十足。对野琦一切都了如指掌（知道身高体重甚至喜欢吃什么等一切细节）。和野琦做什么事情都不在乎（一起骑协力车，第一天就为其涂了4个小时漫画）。对野琦身边出现的一切人都会感觉患得患失（后来才发现全都是男的，并且迅速打成一片）。这样一个“厨力满满”的妹子，确实不是绝大多数后宫男主承受得起的。甚至这种“骑协力车而被周围很多人翻白眼”的剧情，其他动漫里很多妹子甚至都是做不来的。而出色的吐槽能力和恰到好处的脑洞，让千代给人一种非常“喜庆”的观感。这才是千代最有趣的地方。我们谈一个二次元女性的时候，经常会谈到“这个妹子是否会在现实出现”，这或许是二次元和三次元非常关注的一个内容了。而“千代”这种妹子，或许是现实里根本不可能出现的。也或许是现实里最可能出现的。千代有可能在现实里出现吗？当然几乎不可能了，这样一个如粉丝一样厨你的女孩子，还要拥有那么多丰富的个人生活和优渥的成绩与脑洞，那几乎是不可能的事情。我们常说对于宅男来说圣人惠是一个几乎完美的女孩子，那么对于大男子主义或者说部分直男来的话，千代就是他们的圣人惠。即使男生非常优秀，如此狂热的厨也是不会有的。但千代又是最可能出现的。《月刊少女野崎君》的主题曲是大石昌良的《非君莫属》，这首曲子的歌词相对于其“洗大衣”的梗来说，更要优秀一些。そう 確かに君しかいない 没错 的确只有你君だけにはありのままいたい 只有你能够看到我真实的一面笑い転げたり 愚痴たれたり 泣きべそ強がったり 有时会笑著打滚 偶尔发发牢骚 泛著泪时比谁都还逞强この世でただ一人みたい 这个世上只有一个人自分でも笑っちゃうんですけど 虽然我自己说来也觉得可笑他の誰かじゃもう満たされやしない 也因为如此所以没有人能够代替你君じゃなきゃ意味がない 如果不是你的话就没有意义了为什么是最可能出现的？因为这就是爱。《月刊少女野崎君》只是将一个少女怀春，热恋自己爱人的故事萌化，夸张化，在现实里，爱到这个地步的女孩子一点儿也不少。只是相对来说，没有动漫里那么直接，也没有那么夸张，但爱是不会少的。只有你能看见他真实的一面，也只有她能看见你真实的一面。而这，就是千代小小的少女心。也是很多女孩的少女心。而佐仓千代，其实就是现实里众多妹子们的少女心化身罢了。你希望遇到你的佐仓千代吗？那么，请你真正去感受这份爱。你真的做好承受的准备了吗？', '', '4692', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10557', '0', '推荐几部超甜的爱情动漫，你有看过几部', '高中女生佐仓千代好不容易提起勇气向同年级的野崎梅太郎告白，得到的却是野崎的亲笔签名，和要不要来我家的邀请。佐仓虽然对意料之外的展开感到困惑，却还是带着期待来到野崎家，没想到等着她的却是漫画原稿，不知不觉间就顺着野崎的指示开始进行图黑作业。到这时佐仓才发现野崎是知名少女漫画家梦野子由于儿时曾遭受玩伴们的背叛，性格逐渐内向阴沉的女生——橘梅，成为了高中班里公认的怪人。在这16年来从来没交过男朋友，甚至连朋友也没有，更有传言说她所在的班里没有人听过她的声音。独来独往的个性导致她成了同学欺负嬉笑的对象，却在阴错阳差之下踢伤了学校里颇受欢迎的男生——黑泽大和。阳光开朗的大和却并不计较，反而相当欣赏梅毫无所惧的帅气个性，丝毫不在意其它人的眼光，开始主动与梅接触。因为大和随和的个性与古道热肠，梅也开始逐渐卸下了心防关于友情和爱情的成长故事，描写外表阴暗，被周围的人称作\"贞子\"的女高中生黑沼爽子通过自我努力逐渐消除身边的人对她的误解。海棠家的长男晴从加拿大带来了四子零，海棠零逐渐习惯了和海棠家的兄弟们在同一屋檐下的生活，海棠晴所经营的咖啡店也逐渐步入了正轨。 到海棠家来访的海棠晴的母亲春子，对海堂四兄弟发表了要将海棠零带回加拿大的宣言。 越来越有默契的海棠零和海棠晴，面对突如其来的爆炸性宣言，内心又有了复杂的想法，这难道又是纠纷的前兆！', '', '4692', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10558', '0', '人人都有一个明星梦！追赶星光的绚濑绘里动漫手机壁纸', '以上图片全部来自冰贝贝高清动漫角色图片网（bingbeibei.com)', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10559', '0', '祝绚濑绘里生日快乐，南条爱乃早日康复', '10月21日是LoveLive!中学生会长绚濑绘里的生日，绘理在LoveLive!动画第一季中一开始是不赞同μ’s的存在以及其立场的，后来在姬友东条希以及μ’s本身的努力下绘理也光荣的成为了μ’s的一员，绘理是μ’s中少数的参谋型角色，而且还自带一种成熟的大姐姐气息，尽管绘理在μ’s中并非Center，但在μ’s的活动中也能够起到指点迷津的作用，这次绘理的生日也是套路啦，粉丝庆生，画师上贺图，手游送心，室田雄平送彩色线稿。绘理的声优南条爱乃的膝盖还尚未痊愈，希望借着绘理生日的喜事能够让南条的膝盖早日康复吧！<via：acgdoge', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10560', '0', '角色名：绚濑绘里CN：澄川ハルカ', '小鸟cn慧子。我家鸟无敌可爱！！这套卡的cp真的超萌啊///Д///// ps关于喵屋家给的是裤子我穿的是黑丝？原图看不到裤脚但是紧腿的，店家的裤子太肥穿着不原版，说白了就是腿细（x角色名：绚濑绘里CN：澄川ハルカ查看TA的更多作品，请登录COSPLAY啦APP[http://app.cosplay.la/],关注 澄川ハルカ如果您的作品也想出现在这里，可以到COSPLAY啦APP或者网站平台发布哦~', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10561', '0', '该来的终于来了！南小鸟、绚濑绘里拉拉队ver. 手办预定中', '之前和泥萌介绍了MaxFactory LoveLive！高板穗乃果拉拉队ver.的手办。现在figFIX 南小鸟、绚濑绘里的拉拉队ver. 也来啦！11月22日开启预定，价格各5800円(含税) 2017年6月发售哦~', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10562', '0', 'LL-绚濑绘里cos：想逮捕我？首先！你，不能爱上我！', 'cos_角色：绚濑绘里cn：明明大蓝鲸摄影：贺显鹏（作品由超能部授权发布）『 Cosplay控 』精美Cosplay 专业Cosplay资讯 完美Cosplay体验尽在Cosplay控喜欢我们的话，不要忘了关注cosplay控的公众号哦~点击微信右上角的按键→添加朋友→公众号→输入“Cosplay控”就可以关注我们啦~', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10563', '0', 'MPM大黄蜂；绚濑绘里手办：上周你可能错过了这些模玩新品', '碎碎念还有一个月的时间就是WF2017（冬）了，上周GSC的官网上也是挂出了今年W.H.L.4 .U.25（WONDERFUL HOBBY LIFE FOR YOU!! 25）的特设页面。除了大家最为关注的WF限定商品以外，现场会进行的各种活动也在页面上进行了预告。今年比较有意思的是GSC特别开设了名为GOODSMILE CLINIC模型診所的区域，有专业的原型师帮助大家修理出现问题的手办模型。除此之外的其他活动大家可以复制下面的链接进入GSC的特设页面进行了解。http://whl4u.jp/zh/限定品方面，雪初音两件套已经是每年必不可少的了，今年的黏土人和figma的数量均为2000只，当然没有什么执念的同学完全可以等待之后国内的通贩。除了雪初音以外，其他的限定商品还包括《Fate/Grand Order》的GUDA子黏土人和figma的saber lily第三再临Ver.;《舰队collection》中的瑞鹤改和武藏的黏土人；Phat！出品的，来自《碧蓝幻想》中的玛莉＆維拉 偶像Ver.套組Minicchu Q版手办以及GSC和主办方海洋堂合作推出的Wonda-chan NEXT DOOR PROJECT FILE:03 賀茂川Ver.。所有以上商品除了武藏和Wonda-chan以外均有WEB通贩可以购买。黏土人 雪未來 Twinkle Snow Ver.figma 雪未來 Twinkle Snow ver.黏土人 GUDA子figma Saber/阿尔托利亚・潘德拉贡Lily 第三再临ver.黏土人 瑞鶴改黏土人 武藏Medicchu 碧蓝幻想 玛莉＆維拉 偶像Ver.套組Wonda-chan NEXT DOOR PROJECT FILE:03 賀茂川Ver.GSC：哔哩哔哩 2233拜年祭限定版2017今天突然开放预订的「哔哩哔哩」2233拜年祭限定版给我的第一反应是“哇靠，中国老板真是有钱！”。因为从目前公布的各种信息来看，这套所谓的“中国限定”应该是B站自行负责各项费用的特别邀约版，之前关于本作的所有信息大概只有B站微博发出的一条预告微博。实际上早在去年的BML2016上，哔哩哔哩就公布了将要由GSC制作的2233娘大黏土的企划，同时黏土人的成品也已经在前段时间放出。不过今天开启预售的2233娘等比例手办还是让人略感惊讶的，尤其对于之前同样推出了2233娘等比例手办的ACTOYS来说跟GSC撞车实在是有点悲剧的情况，好在两款作品的价格有接近3倍的差距以及手办的预订制度，否则真的是个不小的打击。不久前公布的2233娘黏土人这次的手办找来了日本知名原型师宫川武先生担当原型。实际上说是知名原型师，近年来的商业手办作品上，宫川武先生并没有什么特别出彩的作品。提到宫川武先生估计大家印象最深的都是大邪神saber的原作。同时为了宣传手办，b站也是特别前往GSC总部拍摄了一段视频，大家不妨复制下面的链接观看一下吧！http://www.bilibili.com/video/av8037500/index_2.html宫川武最为出名的原型作品Alter：《LoveLive!》绚濑绘里美少女手办方面，上周最引人关注的恐怕是来自Alter的《LoveLive!》绚濑绘里。不得不承认的是来自Alter的LL系列的几位小姐姐每一款都是华丽到爆表，就算我不是一个LLer，看到如此华丽的手办都有些抑制不住自己的买买买之心了！GSC：《舰队collection》初月 黏土人除了WF上的两款舰娘黏土人以外，GSC上周还公布了另外一款来自《舰队collection》的黏土人角色：初月。来自于秋月型防空驱逐舰四号舰的「初月」娘化以后是一只身着水手服的妹子，不过配件方面个人感觉没有什么特别有趣的，仅仅推荐给把初月妹妹当做老婆的提督们。Phat！：《偶像大师 灰姑娘女孩》诸星琪拉莉＆双叶杏擅长可爱系手办的Phat！似乎越来越钟情于双人set。上周最新开订的诸星琪拉莉＆双叶杏同样是以双人组合的形式进行贩售的。集合Phat！所擅长的可爱色系，本作还是看点十足的！Takara Tomy：《野兽之战》MP-38 猩猩队长 传说的总司令官Ver.作为去年最受好评的MP作品之一，2017年一开始Takara Tomy就把MP32猩猩队长拿出来进行了重涂。命名为传说的总司令官Ver.并被冠以单独序号的MP38采用的是日版2D版本野兽战争动画配色，附带的战斗面罩不知道是不是为了致敬此前的“世界初”系列。本作售价12,960日元，预计2017年6月发售。孩之宝：《变形金刚电影版》MPM-3 大黄蜂时隔多年，《变形金刚》电影级MP系列MPM终于又有了最新产品：编号为MPM-3的电影版大黄蜂。本作在此前的MPM-2上做了不小的优化，变形设计和车型的还原度都在论坛得到一片褒扬。今年正好赶上《变形金刚》电影版上映十周年，不知道孩之宝年初的这份礼物大家还满意吗？万代：《机动战士高达》EXCEED MODEL ZAKU HEAD来自万代的扭蛋事业部在2017年开年就给我们带来了不小的惊喜，全新研发的EXCEED MODEL系列扭蛋首发三款扎古头像造型即将于17年2月份上市。本作最大的特色在于将扭蛋的蛋壳制作成了玩具的一部分，通过拆分+组装的过程，一颗扭蛋瞬间就能变成一款完美的扎古头部。更重要的是价格只有500日元哦！HotToys：《蝙蝠侠 阿克汉姆疯人院》小丑 1/6收藏级人偶在公布了《阿克汉姆疯人院》版本的蝙蝠侠没多久之后，HotToys又随即公布了同版本的小丑。如果你有心入手之前的老爷的话，那么这只是不是也需要考虑一下呢？毕竟没有小丑的蝙蝠侠算不上完整的超级英雄！除了上面重点介绍的新品以外，上周还有不少其他的新品发布，在这里我们就不一一详细介绍了。附上简单的官图，如果下面的玩具刚好戳中各位的G点，不妨去各大模玩论坛了解下更详细的信息哦！Phat！《舰队collection》岛风寿屋《星球大战外传：侠盗一号》ARTFX+ 死亡士兵万代《奇异博士》S.H.Figuarts 史蒂芬·斯特兰奇海洋堂 1/35比例扭蛋 Maschinen Krieger万代 RG 1/144 能天使高达R2 Sideshow Premium Format™ Figure 毒', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10564', '0', 'figfix 啦啦队南小鸟&绚濑绘里发售决定！', '南小鸟绚濑绘里', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10565', '0', '绚濑绘里小恶魔ver', '​', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10566', '0', 'LoveLive绚濑绘里人鱼觉醒，文雅闲静cos笑颜纯美清爽', '绚濑绘里是《lovelive!》的主要人物之一。原音乃木坂学院高中三年级生兼任学生会会长，现已毕业，拥有模特身材的大美人。冰蓝瞳，一头金黄色长发，束成马尾辫。coser@玫伍给我们带来了《Love Live!》绚濑绘里人鱼觉醒，文雅闲静cos笑颜纯美清爽，让我们一起来看看吧！！原作：Love Live!角色：绚濑绘里 cn：玫伍妆娘：玫伍摄影：云弈后期：青空原po微博：M玫伍W想要看最精美的Cos 最专业的Cos资讯 最完美Cosplay体验尽在二次元cosplay大全喜欢我们的话，不要忘了关注二次元cosplay大全的公众号哦~', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10567', '0', '开胸毛衣ver绚濑绘里，漂亮漂亮漂亮！', 'cn: 桃昔；摄影： 草莓；化妆： 桃昔；后期： 桃昔哇塞，喜欢这样的小姐姐，漂亮漂亮漂亮！', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10568', '0', '魔女驾到！万圣节绚濑绘里即将开订', ' 前几天Alter那华丽到爆炸的巨乳魔王东条希 1/7手办刚刚发布了预订通知，不少ller正在为钱包而心疼时，一条不知道该不该称之为噩耗的消息再次传来，Alter的另一款《LoveLive！》手办——绚濑绘里万圣节Ver即将在2月5日开始接受预定。 这款绚濑绘里万圣节Ver是1/7标准比例手办，它曾经在2015年11月28日举办的MegaHobby EXPO2015秋上在Alter的展台上展出过，其原图为由动画制作人西田亚沙子绘制的《LoveLive！》第二季蓝光DVD第4卷的封面，而预定这一卷蓝光DVD的观众还能获得以这张图为卡面的《LoveLive！学园偶像祭》的绚濑绘里UR卡一张。手办根据这张卡牌的牌面制作，原型由桥本凉担任，色彩由渡边恭大负责。 手办厂商Alter与MegaHouse强强联合，两个公司进行了一项共同企划，名为ALPHA×OMEGA（α×Ω），这款绚濑绘里万圣节Ver正是这个企划中的作品。身着万圣节特制服装的绘里周身环绕着浓浓的小恶魔氛围，如果她嘴里说着“不给糖就捣蛋”的话，小编宁愿被她捣蛋一下呢。 绘里的穿着十分符合万圣节的传统，不管是头上戴的黑色小礼帽，金色弯月的装饰，还是黄色的南瓜袖，南瓜脸装饰以及手中捏着的糖果，这些都是只有万圣节才能看到的装扮。 一脸坏笑的绘里那得意的表情让人忍不住和她一起微笑起来，一手捏糖一手掐腰的样子也十分俏皮，背后的蝙蝠翅膀让她更带有一股小恶魔的气息。 这款绘里的服装露出度很高，不管是露出的胳膊还是光滑的腹部都让人心驰荡漾，而南瓜袖与上衣之间的缝隙更是绅士们无法错过的神之领域，南瓜篮内糖果的甜美远比不上绘里笑容对我们的诱惑。 超短的皮质短裙以及吊带袜为绘里增加了一份色气，而袜子在大腿上形成的勒痕更是让人欲罢不能。值得一提的是，中靴的装饰蝴蝶结上也有黄色的南瓜脸，这种小细节上也没有忘记万圣节元素。 反光的靴子和袜子也提升了这款绘里Figure的档次，出没于夜间的小恶魔绘里什么时候才能敲响小编的家门呢？ 这款手办不管从面相还是细节处都做的非常不错，预定价格为15552日元（约合人民币844元），比之前Alter开始预订的东条希要稍微便宜一些。不过与她们同时在MegaHobby EXPO2015秋上展出的南小鸟估计也很快就要开始预订了，大家最好在这几款手办中好好做个选择，然后再付订金。▲与绘里同时在MegaHobby EXPO2015秋上展出的南小鸟手办名称：绚濑绘里 万圣节Ver手办价格：15552日元（约合人民币844元）预定时间：2016年2月5日发售时间：2016年9月下旬手办材质： PVC手办尺寸：全高24cm制作厂商：ALTER发售厂商：MegaHouse——————————————————玩具100网站：http://www.wanju100.com/玩具100官方微博：@玩具100网玩具100微信公众号：玩具100原文链接：http://www.wanju100.com/news/498.html', '', '4693', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10569', '0', 'P站美图推荐——助手牧濑红莉栖', '今天为大家推荐的是人气游戏和动画《命运石之门》中通称“助手”的牧濑红莉栖。在作品中，她是维克多·孔多利亚大学脑科学研究所的研究员，外表是个美人，纤细的双腿充满魅力。是个典型的傲娇女，而且是一旦关系变好后就用情很深的类型。有兴趣的小伙伴可以去P站为原作收藏并打分哦！ID：7584016ID：11854139ID：19571291ID：20039301ID：20679545ID：29466891ID：20859555ID：32102332ID：54064571ID：35291141最后希望大家可以去P站多多关注原作者，想要图包却不会去P站找图的小伙伴可以搜索并关注“动漫之家”微信公众号，小编会定期推荐和更新图包哒！', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10570', '0', '53期 傲娇中二的科学家助手--牧濑红莉栖', '【本文由特约作者简言酱于17173独家发表 未经授权请勿转载】穿梭于不同的世界线上游荡，一次次见证着命运的悲哀与绝望，孤独的观测者早已内心遍布伤痕。《命运石之门》里的男主角凤凰院凶真是个不折不扣的中二少年，沿着无数条通往悲剧的既定道路上跌跌撞撞的前行，撞到头破血流。在他即将陷入绝望彻底放弃的时候，遇到了那个腼腆傲娇的天才少女。深红色的披肩长发、熬夜过度的死鱼眼、自制装嫩的魔改校服、诱人犯罪的质感黑丝……名为克斯利蒂娜的女孩子，只用简单的一句话就揭开了真相。“无论在哪一条世界线，你都不会是一个人。”包裹在衬衫里的重量身为《命运石之门》全剧终无可争议的第一女主角，牧濑红莉栖的胸部尺寸并非傲视裙芳的奶牛巨乳款。B79 W56 H83的三围尺寸属于正常适中的胸部范围，比起无口手机娘桐生萌郁那B88 W59 H88的傲人数据显然有所不及，但是穿上泳装粘上水之后该有的性感和魅力也都一览无余。无论是在原作Gal还是二次元创作的同人本子里，克里斯蒂娜的可爱魅力都不在于单纯的胸部脂肪量，而是她那始终被包裹在白色衬衫里若隐若现的双峰曲线才最能撩动起男人的情感心弦。脱下衬衫后的景象如何，乃是无数宅男们都梦寐以求的绝美画面。长期习惯于美式文化和生活习俗的助手，偶尔换上内部真空的日式和服，会有多么享受其刺激感受呢？傲娇嘴硬的天才少女在被攻略抹胸之后，会露出怎样可爱的表情神态，想必是您也憧憬过的吧！？黑丝黑丝黑丝说三遍！ACG的世界里，从来不缺乏穿着黑丝挑逗宅男的青春美少女，许多尺度颇大的三俗片子里干脆让一众妹子们穿上情趣套装里的黑丝袜搔首弄姿。可是，再怎么撩人的动作，也比不上牧濑红莉栖那穿着“白大褂+校服”的黑丝组合。人设原案huke的画风独树一帜，将牧濑红莉栖赋予了非常立体鲜明的个人形象，理性与感性的魅力被完美的结合在了她的身上。基于这样的前提，苗条纤细的长腿套着黑色丝袜就兼具了性感与职业的双重属性，与她那介于学者和学生之间的身份相互融合，散发出了让人意乱情迷的知性美感。你敢质疑助手的智商吗？作为全Lab组织里真正的智商担当，拥有高端大气上档次科学家身份的牧濑红莉栖无疑是头脑最靠谱的一位。小时候就喜欢听父亲讲述艰涩的物理学，13岁牧濑红莉栖就能看懂父亲发表的学术论文，之后更是大张旗鼓的和父亲辩论。这样“打脸”的行为，当然招致了自己亲生父亲的暴走与嫉恨。在世界线β里为了得到父亲的认同亲自撰写了一篇有关“时间机器理论”的论文，结果竟被其所杀害。另外，在SERN的胁迫下第一个制造出了时间机器，被誉为“时间机器之母”……这样的超高智商，却在日常生活里被二笔气质浓郁的凤凰院凶真屡屡取笑，也实在是让人啼笑皆非啊。信任，即是勇气每一个喜欢《命运石之门》的玩家都不得不承认，那个穿着白大褂整天叫嚷着狂气设定的中二“科学家”冈部伦太郎，其实就是屏幕前的每一个宅男自己而已。只有宅男才能对冈部伦太郎的作所作为表示认同，只有宅男才能理解到他那夸张言辞里隐藏着的细微情感，只有宅男才能体会到那样骄傲放纵甚至于被他人视为怪物的少年情怀。在这样一条充满了傲娇、别扭、胡闹、不务正业、毫无希望等等情绪的中二道路上，桶子是我们志同道合的坚定战友、真由理是受伤时治愈的港湾，而被戏称为外号“克里斯蒂娜”的助手牧濑红莉栖，才是最能与我们产生心灵共鸣的人。除了她那超高智商的聪明头脑外，牧濑红莉栖最强的战斗力，应该就是她给予冈部伦太郎的支持与理解了。宅女都是萌妹子！虽然AVG里的女生通常长相都很不错，但是像牧濑红莉栖这种红色长发而不显得违和的萌妹子，确实算是相当少见的了。助手的外表颜值和那些烂大街的废萌不同，仔细一看的话就会觉得非常贴近宅男的喜好审美。不管是标志性的死鱼眼，还是蓝色瞳孔和尖下巴，都是在诠释着一种“初看很高冷，内在是逗比”的宅女熟悉气质。无论身处于哪一条的世界线上，助手都能始终如一的相信着冈部伦太郎那近乎于妄想般的言语，发自内心的理解他的一言一行。不管冈部伦太郎干出了多么荒唐可笑的事情，助手都能第一时间感受到认同，并毫无保留的前来帮助于他。在Lab组织里面，或许有暗恋他许多年的人，或许有从未来穿越回来的人，或许有真正发生了亲密接触的人，但是谁都没有办法像助手牧濑红莉栖那样对他的孤独产生共鸣。牧濑红莉栖主动承担起了“孤独的观测者”的精神压力，用言语和行动鼓励着冈部伦太郎，陪伴着他一起共同向着命运发起不顾一切的挑战，只为达到充满可能性的未来。如果说最初的助手是凭借着漂亮的长相与诱人的黑丝等等高颜值俏身材一众外在因素获得宅男好评的，等到冈部伦太郎一次次跳跃时间线回到往昔之后，就彻底被她那人格魅力所感动到了。正因如此，当漫长的时间旅行终于停止，冈部伦太郎与牧濑红莉栖在车水马龙的街道上侧身而过邂逅相遇时，一向喜欢高举火爆喊着烧烧烧的FFF团成员们罕见的没有愤怒纵火，而是集体向着这一对历经磨难的中二情侣表达祝福。这段隐藏文字只有受神之眷顾的魔法使才能看到，下面将告诉你一个不为人知的秘密！当本期的评论数达到10条的时候，神秘礼物将会出现！想要入手这份神秘大礼的骚年哟还不快去写评论！！！↑↑↑少年哟！快觉醒你体内的魔法之力，在此寻找消失的秘宝！', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10571', '0', '《命运石之门zero》主线剧情终于开启，牧濑红莉栖回归上线！', '命运石之门系列通篇都是比较烧脑的作品，有着完整的世界观设定，再加上基于科学理论上的完美剧情叙述，人设、作画、剧情等都算是出类拔萃，最终被奉为神作并不为过，然而很多看新作《命运石之门zero》入坑的小伙伴表示根本看不懂。那么在这里先简要给大家科普一下《命运石之门》和其新作《命运石之门zero》讲述的是什么！《命运石之门》系列的世界观设定！首先要知道这部作品讲述的是穿越的故事，因为某些必要的原因，在回到未来或者是过去后，整个剧情将发生重大变化，而主角冈部伦将如何应对处理就是命运石之门讲述的主要故事！这里没有《平行宇宙论》，世界都是一个整体，随着你选择的不同会造成不同时间线的产生，类似于一颗大树一般。假如你回到过去，重新作出选择，那么之前选择的时间线就会消失，延续现在选择的继续前进，不会产生平行宇宙！《命运石之门》和《命运石之门zero》的内容上的联系！《命运石之门（轻微剧透）》主角冈部伦中二少年，电机专业大学学生，对时光机器抱有相当大的兴趣，在发送短信的偶然间造成了时间线的变动，而他则有“Reading Steiner”这一特性（命运探知：保留之前时间线的记忆，而现在时间线的记忆被覆盖掉）而他渐渐掌握了把自己的记忆送回到过去的方法（时间跳跃）！结识女主牧濑红莉栖（助手）后，其对控制记忆回到过去更加熟练，终于被神秘组织SERN盯上，最终酿成其青梅竹马真由理（嘟嘟噜）的惨死，几近崩溃的冈部伦在尝试了上百甚至上千次的时间跳跃后，终于找到了挽回的办法，但是他必须放弃女主牧濑红莉栖（女主），冈部伦会做如何选择呢。一直到这里为《命运石之门》TV22话的剧情，而《命运石之门zero》就是接着这里的剧情开始的！男主放弃了牧濑红莉栖，拯救了嘟嘟噜！（女主都救不了还当男主!）到这里你就能理解为什么《命运石之门zero》里的冈部伦每天那么整天惆怅，不再中二病而整日忙于大学生活，他明白自己多么的无能，多么的渺小已经没有了梦想成为了一条咸鱼，甚至在谈起时间线就会让喘不过气来，想起牧濑红莉栖就会心然泪下......然而《命运石之门zero》的故事才刚刚开始！《命运石之门zero》主线剧情的开启！《命运石之门zero》在经过6集剧情的铺垫：结识合法萝莉(比屋定)、了解amadeus系统（牧濑红莉栖AI）、找到了椎名篝。到此开启主线剧情的准备基本完成！接下来只需要一个事件开启！历史总是似曾相识，就在冈部伦等人一起庆祝新年的时候突遭不明人士们袭击，而只有冈部伦知道，这不是演习，这绝不是演习（老子辛辛苦苦救下的嘟嘟噜又要让你们弄死了啦？）而其它人还以为是什么惊喜play。最后在打工战士（铃羽）和房东（天王寺）的阻止下，才终于让所有人安全事件平息。直到这里主线剧情出现三个事件：第一，amadeus系统为什么无法访问！第二，袭击冈部伦，想要绑走椎名篝的组织到底是谁？第三，椎名篝失踪的12年里到底去了哪里？经历了什么！还需注意的是冈部伦和其他角色的互动，这一直都是命运石系列的亮点，追番的小伙伴带着这几个问题去追寻线索就会非常有趣！牧濑红莉栖强势上线，女主回归主线剧情！在第TV动画第7集结束，冈部伦接到牧濑红莉栖AI的求救电话，这个电话改变了冈部伦的时间线，命运探知启动！在命运石里电话是不能乱接的这是个梗，正当冈部伦一脸懵逼居然看到了牧濑红莉栖还活着，阔以阔以这波不亏，老婆回来了！总之《命运石之门zero》主线剧情已开启，女主牧濑红莉栖也上线回归，前期铺垫剧情，后面集集高能，这就是命运石之门的选择！追番的小伙伴们接下来绝对不亏！', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10572', '0', '傲娇的牧濑红莉栖，一袭棕色长发撩动谁心？', '牧濑红莉栖，是游戏《命运石之门》及其衍生作品的女主角，维克多·孔多利亚大学脑科学研究所的研究员。外表清秀，一头红棕色长发，紫瞳。纤细的双腿穿着黑色的长筒袜，外套里面穿白衬衫。对于未知事物的好奇心十分强烈，但是研究时不近人情与高傲的态度使得她的朋友很少。只有在亲密的朋友间才会露出真正的姿态。极其不坦率，经常被桥田和真由理吐槽“傲娇”，自己对此也有自知之明。喜欢的赶紧下载收藏哟！图片来自于网络，如有侵权，请联系作者删除！', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10573', '0', '再再再版的天才牧濑红莉栖手办', '头脑清晰的傲娇女主人公！第二代冒险游戏“命运石之门”由5pb.和Nitroplus共同制作，女主人公“牧濑红莉栖”作为手办再再再登场！因上学跳级，牧濑红莉栖18岁就已经从大学毕业，成为维克多•孔多利亚大学脑科学研究所的年轻研究员。集“才女”与“傲娇”属性于一身的女主人公再再再闪亮登场。该模型以Xbox 360版的“限量版包装”插图为主题图案，实现构图立体化。红莉栖坐在互相咬合的齿轮底座上，遥望着远方。本款手办具有一种近似反重力的独特“漂浮感”。正篇作品“命运石之门”中的世界观均带有幻想性质，该款手办旨在真实还原剧中的氛围。另外，红莉栖的美貌是不用说的，那敞开的茄克和领带的造型、长筒皮靴、与底座融为一体的手机等，细节处都精密地立体化。请务必尽情享受这款与寿屋迄今为止的手办卓然不同的商品。具体手办详情在图下方。手办点评：逆天大长腿，还没收的话赶快收了吧都8102年了角色：牧濑红莉栖原作：命运石之门价格：7800日元尺寸：H=240mm出荷时间：2018年6月厂商：寿屋', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10574', '0', '《命运石之门0》第二话：命运的再相遇，“牧濑红莉栖”再登场', '《命运石之门0》第二话也已经放出，OP是石头门的常客伊藤香奈子演唱的，给人一种追寻前人脚步的感觉。op出现的全员照，你有发现亮点吗？总体来说，白狐社严格的按照游戏剧本在走，风格和主要叙述的内容没有多大改变，但动画更能表现出人物的性格与表情，所以在台词的生动性上做出了些许改变。萌萌哒真帆比游戏中给人的感觉更加亲近因为友人而哭泣也更加生动的表现出性格“万恶之源”，雷斯吉宁教授也终于出现在了我们的面前。一脸关切的表情我们期待已久amadues牧濑红莉栖版也终于出现在了我们面前，大家看到的第一眼有没有命运又开始转动的感觉？我们的冈伦又变成表情包了...过了这么久冈伦依旧没有忘记自己的助手娘，每次的小拌嘴总是那么的自然，让人看得不由心中一痛。但受动漫时长的限制，冈伦原本很多的内心独白和纠结并没有直观的表现出来，剧情节奏有些快，后面冈伦直接就得到了amadues的APP，不过整体来说没有太大的影响。玩过游戏的大家是不是觉得选择的时候到了？白狐社和声优的共同努力也将动漫的优势体现了出来，在一些小的细节上，总是能给我们带来触动。值得一提的是，ED《LAST GAME》是由Zwei演唱的（是个组合，他们还演唱过海猫和机器人笔记的主题曲），非常耐听，给人一种拼尽全力却依旧触及不到的悲凉感，总感觉白狐社可能先走Bad End线了...冈伦悲伤的背影', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10575', '0', '破石头门登顶 外媒Anime Trending四月新番人气榜', '今天下午，外媒Anime Trending公布了四月新番第六周的人气榜单排名情况，牧濑红莉栖出现的这一话，作品《命运石之门0》果然排在了第一位，而《失忆融合WIXOSS》居然排在了第四位，国外有这样的人气？实在想不到，不多说了，看具体排名吧~第一位：《命运石之门0》“熬了整整七话，终于看到了熟悉的这个人——牧濑红莉栖，再也不要和纸片人谈恋爱了”冈部伦太郎如此说道(逃~第二位：《超能力女儿》小时候听到父母出门要一段时间才回家，自己偷偷摸摸干一些“小坏事”正得意时，突然撞到父母的脸。。。新田的表情像极当时的我们QAQ。。。第三位：《多田君不恋爱》实力助攻的好队友，胃疼了这么多集终于发了一次糖~第四位：《失忆融合WIXOSS》醉了，一边打牌一边撩，监督你这样做。。。。。。实在太得人心了！干得漂亮！第五位：《刀剑神域GGO》谁干滴！才一周没看就突然冒出来了孩子，而且还一切冒出俩，禽兽！（迫真第六位：《宅男腐女恋爱真难》论腐这一段看完终于明白腐女的脑补能力多么强大，如果宅男是万物皆可萌的话，或许腐女就是万物皆可腐。第七位：《Megalo Box》最近两集节奏放慢了许多，五十周年企划，希望在过渡剧情之后可以一直保持最初的观感！第八位：《我的英雄学院》第三季不愧是号称世界三大邪神之一的日本化妆术，这个实力足够和天朝P图一战！第九位：《Comic Girls》太可爱了！这么可爱的老师为什么是别人家的！也就是来回看了十遍而已~第十位：《三次元女友 REAL GIRL》太快人心，踢得漂亮，宅男怎么可能在三次元会有女朋友，这是梦，一切都是假的，快醒醒！', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10576', '0', '4月新番《命运石之门0》牧濑红莉栖不在，椎名真由理荣登女主宝', '译自《命运石之门0》动画官网的消息。2018年4月2日，距离4月新番《命运石之门0》开播还有9天！《命运石之门0》动画官网公布了为椎名真由理配音的声优——花泽香菜的评论寄语。——再次为《命运石之门》的角色配音，是什么心情？请谈谈自己角色的魅力和配音时的要点。花泽　又让我参与进命运石之门的世界里，我感到非常开心！在这个系列中，真有理的温柔和包容力也是一如既往，但经常能感觉到她和冈伦之间有距离感，这令人非常痛心。现在我还不好多说，但其他世界线的真有理也会在动画中出场，希望大家可以多多关注。——请谈谈最近有什么事情，是自己想用时光机器穿越回去改变的。花泽　我忘记吃抑制花粉过敏的药，就出门了。结果纸巾根本不够用，我好绝望。——请对期待动画开播的《命运石之门0》粉丝说几句。花泽　这次是红莉栖酱已经死亡的世界线。我相信观看动画的大家，一定也是和冈伦一样的心情。虽然会非常痛苦，但请大家好好关注，愿我们能再遇到红莉栖酱。嘟嘟噜—♪', '', '1888', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10577', '0', '有个人我会坚强一辈子，她叫立华奏', '立华奏日本动画《Angel Beats!》及衍生作品中女主角之一。在死后世界的学校中担任着学生会会长。被“死后世界战线”的人称为“天使”，不过自己否认这一说法。很少有表情变化，同时也非常寡言，所以很难得知她在想什么。喜欢吃麻婆豆腐（在食堂里因为其恐怖的辣味而无人问津）。前世接受了音无结弦的恩惠，但还未对结弦表达感谢，所以落入死后世界后，只为与恩人说一声谢谢。角色背景死后世界学校学生会长，主要责任是维护学校学生的秩序。虽然身材娇小但通过程序编辑使自己拥有超人般的体质和攻击防御都非常强力的武器，还能够使自己拥有了一双翅膀（音无的推荐），完全展开就像天使一般。被“SSS”团成员称作“天使”，尽管本人并不认同这个称号。衣着外貌身材娇小。平时面无表情，有一头白银长直的头发（最后一话是马尾）和一双琥珀色瞳的眼睛。衣着与死后世界的校服款式不同。性格特点很少有表情变化，同时也非常寡言，所以很难得知她在想什么，由理对她的评价是无感情、冷冰冰、寡言少语，但比NPC个性得多。音无认为是因为能够成为她朋友的人都消失了，只留她一个而孤独所致。在最后一话中突然变得开朗，不但会笑，还会做出很可爱的害怕样子。天然呆的性格让自己表现得很笨拙，她在死后世界的目的实际是为了让没能好好度过青春时代的人们，有一个美满的青春，但因太过笨拙不知道怎么表达只好采取暴力。这一点还突出在她的喜好上，奏觉得麻婆豆腐很好吃，但并不知道自己其实是喜欢麻婆豆腐.', '', '4694', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10578', '0', '还是立华奏哦，今日动漫美图壁纸插画分享', '​​​​​​ ', '', '4694', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10579', '0', '天使的新衣！《幻想全明星》立华奏新皮肤闪亮登场', '立于浮华之世，奏响天籁之音!一听到这句话大家一定就知道小编今天要说的是哪位动漫人物啦!她就是在死后世界的学校中担任着学生会会长，被“死后世界战线”的人称为“天使”的立华奏，虽然小奏自己否认这一说法。这位典型的三无少女，在新年来临之际在《幻想全明星》中也换上了属于自己的天使新衣—“红莓天使”。弹奏着钢琴的小奏，真实让小编也沉醉其中。立华奏新皮肤“红莓天使”立华奏新皮肤“红莓天使”皮肤共有4件，分别为“红莓天使帽子”、“红莓天使上衣”、“红莓天使下装”、“红莓天使武器”，不可单独进行装扮备注：穿戴该皮肤，所有技能均有特效变化。养成界面展示：回城效果展示：在《幻想全明星》中，换上新皮肤“红莓天使”的立华奏从天而降后弹着钢琴，简直美得让人离不开眼。虽然天然呆的性格常常显得小奏笨拙而不知如何表达自己的心意，但是我们都知道小奏的善良。这一次，在《幻想全明星》中，也将小奏的性格展现的淋漓尽致。如果你也和小编一样喜欢小奏的话，那就快来《幻想全明星》里面一起来看看吧!全球首款10v10和200人吃鸡的二次元动漫大乱斗端游《幻想全明星》已经正式开启约会测试!现在注册登陆还送15英雄10款皮肤10000金币!《幻想全明星》更有吃鸡地图现已正式上线，满足你所有需求，还有女神Ladybeard以及哲学家♂比利同名英雄“比利王”陪你一起来玩，心动不如行动，快来《幻想全明星》享受这独一无二的浪漫约会吧!', '', '4694', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10580', '0', '金斯利安多维片是叶酸吗 金斯利安能代替叶酸吗', '金斯利安多维片一般是需要补充多种维生素和矿物质的育龄妇女、孕期和哺乳期妇女服用的,那么金斯利安多维片是叶酸吗?金斯利安能代替叶酸吗?金斯利安多维片是叶酸吗严格意义上来说金斯利安多维片与叶酸片是有所区别的，金斯利安多维片中除了叶酸之外，还含有其他维生素矿物质的多种维生素，其中叶酸的含量与普通叶酸相同，都是0.4毫克，其主要成分是叶酸、B族维生素、维生素C、维生素D、钙、铁、锌等，而如果是叶酸片的话里面就只含有叶酸，没有其它营养元素，因此如果是备孕期的话只吃叶酸片即可，如果是怀孕后，孕妈们的营养需求增大，这个时候建议服用含有多种维生素的金斯利安多维片。金斯利安多维片除了能够确保胚胎早期有一个较好的叶酸营养状态，预防出生缺陷，还能用于预防因缺乏B族维生素而引起的各种疾病。金斯利安多维片的适宜人群主要为：需要补充多种维生素和矿物质的育龄妇女、孕期和哺乳期妇女，营养全面，效果好，有些孕妈还觉得食用后没有那么强烈的呕吐感。金斯利安多维片的主要成分解析主要原料：叶酸、维生素B1、维生素B2、维生素B6、维生素B12、维生素C、维生素D3、烟酰胺、碳酸钙、葡萄糖酸锌、琥珀酸亚铁。金斯利安多维片每片含：叶酸0.4mg、维生素B11.4mg、维生素B21.4mg、维生素B61.9mg、维生素C78.6mg、维生素D34.6ug、钙170mg、锌9.2mg、铁4.8mg。1、叶酸：防止婴儿神经管畸形的发生;调节细胞及红血球的形成;提高精子质量。2、维生素B1：为碳水化合物新陈代谢和中枢神经系统的正常功能所必需。3、维生素B2：参与能量代谢，保护皮肤和视力。4、维生素B6：参与蛋白质新陈代谢，维持中枢神经系统的功能。5、维生素C：有助于铁元素的吸收，增强叶酸预防神经管畸形的功能。6、维生素D3：促进钙吸收，使骨骼牙齿正常生长发育并保持健康状态。7、钙Ca;骨骼和牙齿生长的必要元素，对促进细胞及神经系统功能至关重要。8、铁Fe：红血球内能携带氧的组成部分，承担着为机体运输氧的重要作用。9、锌Zn：进胎儿的生长发育和组织再生，提高人体免疫力。金斯利安多维片服用方法用法用量：每日1片、早饭或晚饭后食用服用方法：直接用温开水送服服用建议：结婚后即开始，男女双方服用金斯利安多维片，每日各一片。 计划怀孕开始每日各增服一片金斯利安多维片 ，女性至少增服到哺乳期结束，男性除了戒除烟、酒等不良习惯外，增服金斯利安多维片至女性确定怀孕。', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10581', '0', '一举多得补叶酸：金斯利安有高招', '叶酸 对于正在备孕和已经怀孕的女性来说，为了保证宝宝的健康，多重营养是必要的条件，除了每天补充0.4毫克叶酸来预防出生缺陷外，妈妈们还需要补充适量的维生素和微量元素。但由于很多食物在烹饪过程会导致营养的大量流失，因此专家推荐，每天只需一粒金斯利安多维片，即可满足备孕女性和已孕女性的所有营养需求。叶酸0.4毫克每日必补在怀孕的第一个月内，孕妇体内的胚胎细胞以惊人的速度分裂，叶酸有帮助细胞分裂成长以及形成DNA的作用，能有效地预防胎儿神经管发育异常，同时预防巨右细胞性贫血，还可以预防早产。若孕妇缺乏叶酸，会引起胚胎细胞分裂障碍，导致胚胎细胞分裂异常、胚胎细胞发育畸形，特别是由于神经管发育畸形，导致胎儿出现“无脑儿”或“脊柱裂”。研究表明，女性在服用叶酸后，要经过4周的时间，体内叶酸缺乏的状态才能得以切实的改善。只有这样，在怀孕早期胎儿神经管形成的敏感期中，足够的叶酸才能满足神经系统发育的需要。而且，已孕女性要在怀孕后的前三个月敏感期坚持服用才能起到最好的预防效果。对此，美国疾病控制中心已经建议：育龄的女性每天都应补充0.4毫克的叶酸。数据显示，在中国，每30秒就会有一个出生缺陷宝宝降临，为确保胚胎早期有一个较好的叶酸营养状态，预防出生缺陷，最好婚后即开始增补叶酸，避免由于非计划怀孕而错过增补叶酸的关键时期，由于多数女性结婚后怀孕存在一定的不可计划性，许多女性在此阶段并不知道自己缺乏叶酸，也不知道自己已经怀孕，这有可能错过补充叶酸的关键时期。微量元素对身体贡献巨大怀孕期间的准妈妈，对于能量的摄入和代谢都较怀孕前有所增加，相应地对于水溶性维生素、矿物质元素的需要也有所增加。维生素B1对神经系统的生理活动有调节作用，特别与食欲维持、胃肠道的正常蠕动及消化液的分泌有关。怀孕期间适量增加维生素B2的供应，有助于准妈妈对铁的吸收和利用，能预防妊娠期的贫血。另外，维生素B6可以明显地减轻食欲不振、呕吐等早孕反应这些症状。同时，补充维生素D 能对骨骼中的钙进行双向调节，直接强壮准妈妈和胎儿的骨骼。另一方面，怀孕期间的准妈妈还需要丰富的铁，以满足胎儿制造红血球的需要，并为分娩时出血做好准备，而同时补充维生素C能增进铁在肠道中的吸收。而锌则是人体所需的重要微量元素，能促进机体的生长发育和组织再生，这对于胎儿的生长有特别重要的意义。在产后，锌对于产妇的伤口愈合和子宫复原也是有益的。需要特别注意的是，虽然孕期需要的营养种类较多，但准妈妈们也并非补充得越全面越好。目前市场上存在一些主打“全面营养”的欧洲配方孕期保健品，含有铜、锰等重金属元素。虽然这些元素是人体必需的矿物质之一，但由于我国现阶段环境污染问题不容忽视，加之重金属人体摄入的途径广泛—空气、饮食、水，因此我国居民现阶段面临的问题不是重金属铜、锰摄入不足，而是重金属铜、锰摄入过多积累中毒的问题。因此，对于含有此类重金属的孕期营养保健品，建议准妈妈们要慎重挑选。金斯利安一举多得补营养有没有一款又能补充0.4毫克叶酸同时又能满足多种微量元素摄入的备孕及孕期营养补充品呢？专家向各位妈妈们推荐金斯利安多维片。很多妈妈都知道，斯利安是我国首个预防出生缺陷的预防性用药，是北京医科大学和美国疾病控制中心历经13年联合开展的“中美预防出生缺陷与残疾合作项目”的成果，是专门为备孕女性和孕妇特殊需要而研制的叶酸片，女性在妊娠前后每日增补0.4毫克斯利安叶酸可以有效预防出生缺陷，完全符合中国准妈妈的体质需求，安全可靠。而金斯利安多维片是在斯利安片的基础上，以多种维生素和矿物质为主要原料制成的保健食品，不仅满足了备孕妈妈每日对叶酸的需求，确保胚胎早期有一个最佳的叶酸营养状态，预防出生缺陷，同时还添加了多种水溶性维生素、矿物质元素，可谓营养一举多得。还在愁营养不能一举多得补充的妈妈，还在每天保健品药片吃一大把的妈妈，现在开始换成金斯利安多维片吧！小小一片，营养齐全，祝愿备孕妈妈们都能早日怀上一个可爱的宝宝！', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10582', '0', '孕期到底吃爱乐维还是金斯利安？不明白的看过来！', '前两天写了一篇有关孕期所吃维生素含量的文章，但有很多网友说没看懂，因为是春节期间写的，有些匆忙，所以写的有些前言不搭后语，以致有些网友说我的语文是看大门的门卫教的，在这里给大家道歉了，经过重新整理，在这个给大家重新普及一下，共勉！爱乐维的含量是:维生素A-1.2毫克，维生素B1-1.6毫克，维生素B2-1.8毫克，维生素B6-2.6毫克，维生素B12-4微克，维生素C-0.1克，维生素D3-12.5微克，维生素E-15毫克，生物素-0.2毫克，叶酸-0.8毫克，烟酰胺-19毫克，泛酸钙-10毫克，钙-0.125克，镁-0.1克，磷-0.125克，铜-1毫克，铁-60毫克，锰-1毫克，锌-7.5毫克。金斯利安的含量是:叶酸-0.4毫克，维生素B1-1.4毫克，维生素B2-1.4毫克，维生素B6-1.9毫克，维生素C-78.6毫克，维生素D-4.6微克，钙-170毫克，锌-9.2毫克，铁4.8毫克。有报道称，中国人缺乏的微量元素是:维生素B1，维生素B2，维生素B12，维生素C，维生素D，叶酸，钙，铁，锌。中国人不缺乏的微量元素是:维生素A，维生素E，铜，铬，锰，镁，硒。而爱乐维里却含有中国成人和孕期不缺乏的微量元素:维生素A-1.2毫克，维生素E-15毫克，镁-0.1克，铜-1毫克，锰-1毫克。这是为什么呢，我想是因为爱乐维是进口药品，因该是针对外国人体格设计的。而金斯利安是国产的，是专门针对中国人体质设计的吧！不过小编我最近在医院咨询过生殖科的医生，两种维生素都是适合计划怀孕和整个孕期吃的！怀孕的宝妈也可以根据自己所却的微量元素选择适合自己的维生素。你现在吃的是哪种孕期维生素呢？', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10583', '0', '金斯利安--孕妈给宝贝的最佳选择', '新时代的爸爸妈妈都会将自己最好的给予孩子，可是什么才是最好的呢？追根究底，孩子的营养健康是最根本、最重要的，尤其是对于孕育时期的胎宝宝来说，营养和健康也是妈妈唯一能够给予的最佳礼物——而这最佳礼物，便是金斯利安叶酸多维片。论0.4毫克叶酸的重要性提到叶酸多维片，很多准妈妈首先想到的就是其中的一个重要营养元素——叶酸。叶酸是人体必需的水溶性B族维生素之一，叶酸不足的准妈妈很容易患上巨幼红细胞贫血，使先兆子痫、胎盘早剥的发生率增高，甚至出现胎儿宫内发育迟缓、早产以及新生儿低出生体重等现象。特别是孕早期叶酸缺乏，可以引起胎儿神经管畸形。斯利安是我国首个预防出生缺陷的预防性用药，是北京医科大学和美国疾病控制中心历经13年联合开展的“中美预防出生缺陷与残疾合作项目”的成果，是专门为备孕女性和孕妇特殊需要而研制的叶酸，在90年代初25万例大规模临床证实，女性在妊娠前后每日增补0.4毫克斯利安叶酸可以有效预防出生缺陷，是完全符合中国准妈妈的体质需求，安全可靠，同时0.4毫克叶酸也成为了世界卫生组织在全球推广应用的国际标准补充剂量。论微量元素的巨大贡献金斯利安多维片是在斯利安片的基础上，以多种维生素和矿物质为主要原料制成的孕期营养补充品，除0.4毫克叶酸外，金斯利安还含有关系优生的必须矿物质元素钙、铁、锌、维生素D以及人体最易缺乏的水溶性维生素，如维生素B1、B2、B6、B12、维生素C等。怀孕期间的准妈妈对于水溶性维生素、矿物质元素的需要也有所增加。例如维生素B1对神经系统的生理活动有调节作用，特别与食欲维持、胃肠道的正常蠕动及消化液的分泌有关。怀孕期间适量增加维生素 B2 的供应，有助于准妈妈对铁的吸收和利用，能预防妊娠期的贫血。另外，维生素 B6可以明显地减轻食欲不振、呕吐等早孕反应这些症状。同时，补充维生素 D 能对骨骼中的钙进行双向调节，直接强壮准妈妈和胎儿的骨骼。需要特别注意的是，准妈妈日常所需的营养元素如叶酸、钙、铁、锌等等的吸收就不能单纯依靠从食物中来获取，因为很多营养元素在烹饪过程中就已经被破坏，从而这些营养元素根本不能很好的被准妈妈身体所吸收。另外B族维生素为水溶性维生素，人体无法自行制造合成，又由于食用方法不正确等原因，摄取B族维生素几乎很困难。因此专家建议，准妈妈应该在合理的膳食基础上每日额外补充金斯利安叶酸多维片。论金斯利安的必须之处金斯利安叶酸多维片以《中国居民膳食指南》中对我国孕期妇女和哺乳期妇女的膳食营养素摄入标准为基础，遵循“必需、适合、适量、安全”原则，全面满足中国育龄及孕期女性的多维营养需求。相较于其他产品来说，金斯利安避免了所有可能存在的风险，其组方及含量完全符合中国准妈妈的体质需求，且它是真正不含无推荐摄入标准重金属的孕期叶酸多维片，不仅能够补充孕妈妈的多重营养，而且轻松避免了脂溶性维生素和重金属元素摄入过量可能产生的蓄毒问题。坚持每日补充金斯利安才能够安全放心的孕育出一个健康聪明的宝贝，给予孩子最好的，从补充多种营养的金斯利安叶酸多维片开始吧！ ', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10584', '0', '备孕爸妈：你对金斯利安了解多少', '众所周知，各位准爸准妈想要科学备孕需要额外补充叶酸。而含有0.4毫克叶酸及更多营养元素的金斯利安叶酸多维片大家了解多少呢?小编带各位准妈准爸深层剖析一下：金斯利安的主要成分及作用，金斯利安的安全性，以及金斯利安的正确补充时间! 金斯利安的含量与作用有哪些?金斯利安多维片中含有0.4毫克叶酸以及关系优生的必须矿物质元素钙、铁、锌、维生素D和人体最易缺乏的水溶性维生素，如维生素B1、B2、B6、B12、维生素C等。其中维生素B1对神经系统的生理活动有调节作用，与食欲维持、胃肠道的正常蠕动及消化液的分泌有关。维生素 B2 有助于准妈妈对铁的吸收和利用，能预防妊娠期的贫血。而维生素 B6可以明显地减轻食欲不振、呕吐等早孕反应这些症状。与此同时，补充维生素 D 能对骨骼中的钙进行双向调节，直接强壮准妈妈和胎儿的骨骼。金斯利安中叶酸含量为什么是0.4毫克?叶酸是维生素B复合体之一，是从菠菜叶中提取纯化，与维生素M是同一物质。而斯利安历经25万例循证医学证实，准备怀孕的夫妻每日补充斯利安(叶酸)0.4毫克，可有效降低神经管畸形发生率85%，此结果已被全球50多个国家采用。而且这是被世界卫生组织唯一认定的研究成果，0.4毫克叶酸也因此成为了世界卫生组织在全球推广应用的国际标准补充剂量。因此金斯利安的主要成分叶酸也同样是0.4毫克。金斯利安是否安全可靠?金斯利安是以《中国居民膳食指南》中对我国孕期妇女和哺乳期妇女的膳食营养素摄入标准为基础，遵循“必需、适合、适量、安全”原则，全面满足中国育龄及孕期女性的多维营养需求。相较于其他产品来说，金斯利安避免了所有可能存在的风险，其组方及含量完全符合中国准妈妈的体质需求，且它是真正不含无推荐摄入标准重金属的孕期叶酸多维片，不仅能够补充孕妈妈的多重营养，而且轻松避免了脂溶性维生素和重金属元素摄入过量可能产生的蓄毒问题。金斯利安补充的正确时间是什么?从计划怀孕起开始服用金斯利安，如果服满三个月怀孕没有成功也不要停，需要继续服用。由于金斯利安中含有优生优育的营养元素，因此整个怀孕期间和哺乳期都是需要服用的，可一直服用到哺乳期结束。而且具体服用时段没有严格限制，一般饭后半小时服用较好，因此可按照自己方便的时段固定服用即可。科学备孕从了解金斯利安开始，坚持每日补充金斯利安才能够安全放心的孕育出一个健康聪明的宝贝。你想要拥有一个健康聪明的宝贝吗?那么从现在开始行动吧!中国经济网版权及免责声明：编或以其它方式使用上述作品。已经本网授权使用作品的，应在授权范围内使用，并注明 “来源：中国经济网”。违反上述声明者，本网将追究其相关法律责任。2、凡本网注明 \"来源：XXX（非中国经济网）\" 的作品，均转载自其它媒体，转载目的在于传递更多信息，并不代表本网赞同其观点和对其真实性负责。 3、如因作品内容、版权和其它问题需要同本网联系的，请在30日内进行。 ※ 有关作品版权事宜请联系：010-52886391 邮箱：entcecn@163.com ', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10585', '0', '金斯利-科曼：一点也不后悔当初离开巴黎', '虎扑足球5月24日讯 拜仁小将金斯利-科曼近日在接受《法国足球》专访时表示，自己希望一直为大俱乐部效力。在来到拜仁前，科曼曾效力过巴黎圣日耳曼和尤文图斯。“真是难以置信，我才20岁，但已经为三家豪门俱乐部效力过了。虽然我在前两家效力时得到的表现时间不多，但那些经历对我来说非常宝贵。”“我还是希望自己能为大俱乐部效力。我不惧怕竞争，竞争让我能够更快地提升自己。我一点也不后悔当初离开了巴黎，因为在此后的职业生涯中，我得到了长足的进步。”科曼说。', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10586', '0', '老将防守端锋朱利叶斯-佩珀斯倾向再战一年', '联盟最为年长的防守球员之一预计会在下赛季继续打球。38岁的防守端锋朱利叶斯-佩珀斯（Julius Peppers）将在三月成为自由球员，据知情人士透露，他倾向于回归在下赛季继续征战。上赛季佩珀斯与卡罗莱纳黑豹签下了一份1年350万美元合同。他在黑豹开始自己的职业生涯，为他们效力了8年，随后他加入芝加哥熊征战了4个赛季，在回到黑豹前他为绿湾包装工效力了3个赛季。佩珀斯生涯前景的不确定因素之一在于他能得到怎样的合同。显然在38岁的年纪他无法再像曾经那样成为联盟最高薪球员之一，但是他觉得在上赛季取得11次擒杀后，他应该得到比350万美元更多的薪水。目前的迹象表明佩珀斯最希望在黑豹结束生涯，所以他可能会在做出任何决定前观察黑豹有多大意愿留下他。对佩珀斯作出决定对于目前被出售球队事务缠身的黑豹来说可能有些困难，但是一旦佩珀斯相信自己和黑豹达成共识，那么他可能会为黑豹再征战一年。(编辑：姚凡) ', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10587', '0', '瑞典央行副行长史金斯利：若通胀如预期发展 将会加息', '周五，瑞典央行副行长史金斯利表示，若通胀如预期发展，将会选择加息。 瑞典央行副行长史金斯利认为，目前货币政策正通过多种渠道发挥作用。 汇市方面，瑞典央行副行长史金斯利表示，克朗的水平处于我们的预期内。周四，瑞典央行宣布维持基准利率为-0.5%不变,符合市场普遍预期。在会后声明中，瑞典央行副行长奥尔森表示，预计到2018年中期之前不会加息，并将现在加息时间点进一步推迟到今年年底之后。究其原因，他解释道，当前基础通胀有些低于预期，令人对通胀发展的力道产生疑问；如果通胀未来保持在接近目标水准，将仍需得到货币政策的持续支撑。展望国家经济，瑞典央行预计2018年GDP增速为2.6%，此前为2.8%。预计2019年GDP增速2.0%，此前预测1.8%。 更多优质内容，关注中金网微信公众号：cngold-com-cn（长按复制），或在应用商店下载中金网APP。利率决议公布后，欧元兑瑞典克朗短线拉升，刷新2009年以来高位，美元兑瑞典克朗涨幅扩大至0.5%。', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10588', '0', '再添伤员，金斯利-科曼在法国队训练期间受伤', '虎扑11月8日讯 根据法新社的报道，效力于拜仁的法国边锋金斯利-科曼在国家队训练期间受伤。科曼是在昨天的法国队全队合练时受伤的，他在对抗中脚部受伤后，没有继续参加全队的合练。除去目前伤情未定的科曼，法国队目前的伤情也不容乐观，瓦拉内此前就因伤退出了大名单，而中场核心坎特、博格巴更因为伤病而直接缺席了此次大名单。(编辑：姚凡) ', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10589', '0', '「译文」朱利叶斯-兰德尔，与紫金青年军一起野蛮生长', '赛季初的耀眼表现已经离这支紫金青年军而去。现在，刚刚经历八连败的他们，已经充分表明了现阶段的湖人就是那支我们原本认为的球队：一支充满打球乐趣的、颇具观赏性的年轻队伍，但同时也还没做好一直赢球的十分准备——原因大部分归结于年轻队伍一般都不知防守为何物。（截止本周四比赛日之前，湖人队在防守效率上排名NBA第29。）随着他们在各种数据排名上的进一步下跌（现在他们有8项数据在联盟垫底），赛季的主题将更少地关于输赢，而是更多地关于那一线希望，主要还是对组成这支紫金青年军的核心球员们的持续进步的关心。虽然这种觉悟对于球迷们以及这支以高于预期的10胜10负战绩为开端的球队本身来说，也许多多少少会有点令人失望，但其实这还能接受。在湖人未来计划中的这群青年才俊的技能集和角色定位已经开始初具雏形，而他们想要凝聚在一起养成未来的样子，这或许还需要花上一些时间。湖人队有5名24岁或24岁以下的、场均上场时间至少20分钟的球员，这是联盟最高的，而他们无论是首发还是替补都能全身投入，各显神通。卢克-沃顿本赛季只把这些年轻人的共同在场时间控制到五分钟，这是有原因的。丹吉洛-拉塞尔是球队的领头控球者和第一进攻创造者，而他也不止一次被期待成为沃顿麾下的“穷人版库里”。不久前他遭遇了一次伤病，除三分球外，他其他几乎所有地方的投篮都有了下滑，但他也展现了他完全可以应付这增加的负担。他原定的后场搭档——乔丹-克拉克森，本赛季大部分时候都是出任替补，在第六人路-威廉姆斯之后。 威廉姆斯本赛季一直火爆的表现，但克拉克森对于板凳微波炉角色的调整仍然是一项进行中的工作。拉塞尔是球队的第一控球者。照片拍摄来自Brad Penner-USA TODAY Sports布兰登-英格拉姆和小拉里-南斯也是板凳球员，两人都主要是利用他们的运动能力去帮助球队。英格拉姆作为一名外线防守者有着令人惊讶的强势表现，这是对他修长的双臂和轻快的脚步的证明。虽然他在投篮方面挣扎，但他已经展现出面对各种各样的防守者时有能力去落位进攻。南斯的双腿像是绑了弹簧（去看看隔扣布鲁克-洛佩斯这一球）；他在篮板球方面做得很棒，另外尤其是破坏传球路线和保护篮筐附近也很棒。全联盟只有20名球员的抢断率和盖帽率超过2%——南斯就是其中一员。最后的但同样重要的是朱利叶斯-兰德尔，虽然他这种球员类型看起来并不适合现代NBA，但他还是找到了自己的成功之道。兰德尔基本上看起来像是一名打美式橄榄球的近端锋。他没有一手能拉开空间的跳投。（107场比赛里，他三分线外53投13中。）他的臂展可以说是有点短，所以他不能够很好地护筐。（在80名每场至少尝试封盖4球的球员中，根据NBA.com网站上SportVU提供的数据，兰德尔在限制对手运动战命中率方面排名第70。）目前为止，你很难不去承认他本赛季有了长足的进步，很大原因在于他在进攻端找到了高效的方法，尽管说并不是能够拉开场上空间。兰德尔有着初具雏形的全能型技能集，这源于沃顿允许他成为场上的指挥官，这是让他觉得舒服的位置，这也让他有了取得成功的平台。兰德尔很强壮，也很灵巧，但是他的身高（六尺九）以及略短的臂展意味着更高大的防守者能够在低位罩住他。根据Synergy Sports，上赛季，他低位单打的命中率为32%，本赛季也仅有40%。然而，如果给他一些进攻空间，让他从外线运球过掉他的防守人，这样他会获得更多的成功。他有着快速爆发的第一步，一些大个子就是跟不上，还有今年当他直奔篮下时，他对防守阅读的感觉也有了提高。兰德尔在单打时喜欢做的是面框进攻，使出一个快速的试探步，让防守者以为他要往他的左手边，也就是他的强手侧突破，接着往右侧爆发突破过掉他们，再小范围急停，利用转身动作转回来，这样他就可以使用左手完成终结。很多时候他的这招都很好用，但同时也会出现这种情况——聪明的防守者会预判到他的动作，所以他们会尽力去等待时机，给他设下陷阱。“如果他们没被晃起，那我会直接往右突破，或者跳投，又或者做出上肩部假动作，”兰德尔说，“这个问题真的不大。”根据NBA.com网站上Synergy Sports提供的数据，在去年，NBA中一共有38名球员场均至少有2次单打回合。单打运动战命中率方面，兰德尔在其中排名倒数第3（第26）。而今年，场均至少有2次单打回合的人数为41人，与去年差不多，但兰德尔在命中率方面已经一跃而到前三（第14）。所以，那些筹码——以及如果没出现他想要的机会时，他完全愿意去把球传出去——帮助他本赛季成为一名突破后更有效率的球员。在他菜鸟赛季与第二个赛季间的夏天里，篮下终结是兰德尔特别想要改善的东西。“我在这方面下功夫，找到在篮下变得灵巧的方法，”他说道。这种灵巧已经兑现了。第二年时，他在离篮筐3英尺以内区域的投篮命中率只是57%；这个数字今年已经跃至69%，使得他成为了联盟中最好内线终结者之一。这帮助他慢慢提高了总体的效率，尽管他在禁区中部以外区域的投篮能力已经被搁置在一边。与此同时，他在传球部分的提升也已经是渗入到本赛季的每一场比赛。“我觉得现在我的定位是一名组织进攻型四号位，”兰德尔说，“我可以很全面，做许多不同的东西去帮助我们取得成功。”兰德尔并不是世界上反应最快的决策者，但如果你给他时间和空间去扫略场上局势，他会看到其他球员一般看不到的许多路线。根据NBA.com网站上SportVU提供的数据，在场均潜在助攻方面，兰德尔在所有前锋球员中排名第8；如果不是因为他的传球转化为得分的比率较低，在50名每场至少传球3.5次的前锋中排名倒数第9，那么他会比现在的场均3.2次的助攻数多得多。（兰德尔只有45.9%的潜在助攻转化为得分。与其他球员相比，例如马特-巴恩斯，他在那个组别里以56.6%的助攻转化率排名第8。对于兰德尔来说，56.6%的助攻转化率意味着将场均3.2次助攻提高到3.9次。这是无法忽略的。）尽管如此，兰德尔还是将他每36分钟的场均助攻数几乎翻了一倍，他也是仅有的13名能做到在场上助攻队友的得分占队友的总得分至少17%的大前锋或者中锋球员之一。本赛季他上升的助攻数也给球队带来了相应的个人失误峰值，不过这对于一名22岁的想要把自己开发成传球者的年轻人来说并不令人出奇。随着兰德尔继续去学习成为一名高效传球者该做与不该做的东西，他的那些数字应该会大不一样，', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10590', '0', '朱利叶斯-佩铂斯解释为何一直想回到黑豹', '就在几周前朱利叶斯-佩铂斯（Julius Peppers）的经纪人对外表示这位老将冲传手会回归2017赛季。但是目前还不确定是否会继续呆在绿湾包装工。佩铂斯刚刚与卡罗莱纳黑豹完成了一份一年350万美金的合同，黑豹正是他职业生涯开始的地方，2009赛季他离开黑豹那个时候他希望成为联盟顶薪球员。佩铂斯接受采访表示：“我总是希望回来，修复我们之间的关系，不仅仅是和球队的还有和粉丝的。美式橄榄球的比赛充斥着各种关系，这也是我觉得我回归最重要的事情，让所有人看到我再一次穿上黑豹的球衣。”回归黑豹之后他可能会扮演不同的场上角色，在过去的三年他在包装工的防守组担任冲传手完成了25次四分卫擒杀。自从2007年以来佩铂斯没有缺席任何一场比赛，而且单赛季至少完成7次擒杀。(编辑：姚凡) ', '', '1564', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10591', '0', '暗杀教室：细数那些看上去差点就能杀死杀老师的计划！', '《暗杀教室》又名《三年E班》自从2014年动画化后迅速成为当时的霸权番。来历未知的章鱼怪物声称自己是炸毁月球的元凶，并宣布自己1年后将会炸毁地球，而阻止自己的唯一方法就是被椚丘中学3年E班的学生杀死。暗杀教室这个章鱼怪物的存在超越了目前人类所认知的范围，拥有20马赫的移动速度，虽然力气很小，但是在这样的移动速度下，只要他不想，几乎没有人能有任何手段杀害他。就是这样的存在却主动提出要教导这群学生们杀死自己的手段和方法，随着故事的展开，杀老师的身世也逐渐揭开。在教导学生的过程中，学生们利用学到的知识和来自政府的帮助，一次又一次的制定了消灭杀老师的计划，有几次看上去就快成功了。1、冲绳海岛3天2夜旅行暗杀计划（第一季第17-18集）计划完成指数：★★★★结果：失败计划：1、精神压制：搜集杀老师的羞耻行为（看小黄书、女装、炸纸巾），让他陷入精神崩溃状态2、限制移动：计划实行的地点是远离冲绳岛岸的水上教堂，在杀老师精神崩溃的时候让水浸湿他的身体，在按约定打断他的7根触手；教堂倒塌后学生们用水上飞板组成水墙，再用弹雨封住杀老师的动作。3、必杀一击：最后由两位狙击手在远处对杀老师进行狙击。失败原因：杀老师最终杀手锏：完全防御形态（如下）完全防御形态2、“堀部营”杀老师的兄弟+转学生攻击计划（第一季第11集）计划完成指数：★★★结果：失败计划：崛部是被植入了触手的小孩子，属于后天植入型，精神不稳定，需要定时服药。其速度与杀老师相当，具有与其相等的力量。同时作为转学生进入E班，这样杀老师就不能对崛部还手，只能被动防御。失败原因：对杀老师有效的匕首和弹丸也同样对崛部有效，杀老师利用了这一点。崛部营3、“自律思考固定炮台”智能武器攻击计划（第一季第9集）计划完成指数：★★结果：失败计划：“自律思考固定炮台”作为从挪威转学而来的学生，参与杀死杀老师的行动中，由于“她”是登记在册的学生，所以杀老师不准对“她”反击。其实是高科技机械暗杀者，拥有世界最先进的AI系统，能够自主进化，对目标的动作进行自主分析，逐渐增加命中几率。失败原因：上课期间不间断进行攻击，打扰学生上课，被学生们用胶带封住武器装备。自律思考固定炮台4、死神人质暗杀计划计划完成指数：★★结果：失败计划：世界第一的杀手“死神”被委托对杀老师进行暗杀计划，先说服伊丽娜老师与自己合作，将学生们诱骗到地下水道并抓起来，布置好陷阱，再将学生们作为人质，引诱杀老师上钩。将杀老师和学生们一起关进特殊材质的地牢里，再从中央控制室里打开水闸，利用强大的水压连同学生将杀老师一起杀死。失败原因：将学生也卷入陷阱，危害到学生们的性命，执行最终计划时被乌间老师组织，错过机会。死神学生们为了完成100亿奖金的任务，也为了自己的未来，不断学习新的知识用于暗杀；而杀老师也尽自己所能，为学生们提供最好的学习环境，根据每个人的状况制定学习计划。一方是不断渴求知识的学生，一方是竭力给予知识的老师，虽然中间的连接点较奇葩，但是这就是最理想的学习氛围。喜欢本期文章的小伙伴，欢迎关注！点赞！评论！转发！收藏！', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10592', '0', '暗杀教室：细数杀老师那些令人哭笑不得的弱点！', '动漫《暗杀教室》中塑造了一个人气极高的章鱼型超生物，该生物宣称自己将在明年炸毁地球，同时具有让全世界国家都束手无策的超能力。虽然他宣称能炸毁地球，且拥有20马赫的移动速度，但是却是个性格古怪，心地善良，经常闹出笑话的老师。女装癖就算拥有这么强大的力量，可是在学生们的观察下，他拥有着不少的弱点，其中有些弱点让人哭笑不得。气量很小刚开始接触学生时，非常嫉妒乌间老师在学生们中的高人气，深度怀疑乌间想要夺走自己在学生心中的地位。嫉妒心触手几乎没有力道由于过度强化速度的关系，自己的触手几乎没有力量，所以有一个非常大的弱点：只要自己的触手都被按住（即使是小孩子），就会无法移动。但是由于速度太快，只要他不愿意，几乎没有任何手段能抓住他。与赤羽业初遇时被戏耍，心情失落时敲着墙壁，但是由于软绵绵的没有力道，翻出沉闷的声音，被学生投诉影响考试。拳头太软晕所有的交通工具由于自己的超快速度，一个人出行不需要任何交通工具，近处直接走，远处直接飞，而且带一两个人都没有问题。导致适应不了任何交通工具，有时为了陪伴学生才不得不乘交通工具。因为会晕，所以经常被学生作为暗杀行动的前戏，消耗他的精力。对巨乳没有反抗力在变成怪物之前就有这样的弱点，变成怪物之后貌似被放大了。经常在夏日偷看街上清凉装着的女性胸部，也会收集以巨乳为卖点的色情杂志。遇到学生用色情杂志布置的陷阱，也会因为控制不住不管学生的暗杀一直欣赏美图。看到巨乳的表情怕水杀老师的身体就像海绵一样，会吸收水，吸收了水的身体会变的沉重，影响移动速度，这是学生们制定暗杀计划的一个重要信息。所以遇到下雨天，如果没打伞头部就会膨胀33%（小律计算的数据）。头部胀大经常会缺一根经明明是非常聪明的人，可能是变成怪物的副作用，脑袋经常会少一根经。在学生们第一学期结束后的大作战时，因为被太阳晒黑了，自动脱下了一个月一次的皮（能抵挡炸弹的杀手锏），被学生吐槽：“这种笨蛋为什么到现在都杀不了。”懵逼状态因为害怕解雇而对上司献殷勤在这一点上比很多普通人都严重，椚丘中学的校长到E班视察的时候，杀老师非常殷勤的为校长服务，受到学生的吐槽。非常容易害羞当被学生看到自己偷看小黄书、穿女装的时候，会受到一万点精神冲击，这个弱点被学生们当做重要的消耗精力的手段。害羞的不敢看其实杀老师还有很多其他的弱点，虽然对暗杀没有什么特别的帮助，但是作为学生的吐槽点都是必不可少的，这些弱点也让杀老师明明有着怪物的外形却能迅速得到学生们的喜爱，毕竟身边有这么一个逗比，是一件无比欢乐的事。喜欢本期文章的小伙伴们，欢迎关注！点赞！评论！转发！收藏！', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10593', '0', '暗杀教室：给你一个杀老师，你愿意嫁吗？', '小编和大部分人一样，在看过最后两集后哭得稀里哗啦的。很感动，因为看到了人与人之间的爱，即彼此的付出、牺牲、珍惜与不舍。同样还伴随着自己对杀老师的不舍。所以最后两集才那么感人至深...从第一集开始就被这个拥有多个触手的章鱼老师所吸引。在小编心里杀老师就就是世界上最好的老师，又用自己的爱成就了28名学生、乌间、bitch，甚至理事长。尤其在最后，知道自己的生命将走到尽头后，给每一名学生说的话写成了7年都远远读不完的建议书，再加上亲手制作的、同样厚的纪念相册。只能用一个字概括这一切，就是---爱，他生命的最后一刻，闪光而快乐，照亮了28名学生的人生道路，也照进了我们这些观众的心。如果给你一个杀老师，你愿意嫁吗？', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10594', '0', '我心中最完美的老师：杀老师', '炎炎夏日，依旧有一群亢奋的人们每天都像打鸡血一样奋斗在脑力战的前线，他们就是迎来了期末考试的学生们。不管是决定命运的高考之战，还是其他各种升学考试，大家都因为忙碌的学业而在持续的高温中努力打起精神。而这时老师们也会助大家一臂之力。在动漫中有这样一位老师，可以教学生们所有的学习科目，一个人可以同时给一个教室的人分别指导，跟学生们的关系也不只是师生那么简单。他就是《暗杀教室》中那个把学生看的比什么都重要，扬言要炸掉地球却在地球当了个老师的黄色章鱼——杀老师。这只黄色章鱼在这部动漫中扮演着两个角色，一个是威胁世界安全的恐怖分子，另一个是知名高校差生班的老师。而这两个角色他无疑都扮演到了最高境界。作为恐怖分子，他公开向政府宣战，以毁灭世界为要挟，开出要在学校当老师的奇怪条件。而作为一名老师，想必这是史上最受学生待见的老师了。不经能和学生们打成一团，玩耍教书两不误，还能随时随刻扮演起学生们的人生导师，亲身传授作为杀手的经验（额，不，学习经验），对每一个学生都爱护有加。在差生班难免会有不好对付的学生，杀老师从来不会用强硬的手段逼他们从良，而是用灵活的方法，针对每个人的性格和长处，让他们能够最大限度得发挥自己的优点，为一个团队，一个集体贡献自己的能力。当然啦，我们的杀老师不仅有强烈的责任心，还有超能力来帮助大家学习，根据每个学生擅长的科目一个人同时超速指导一个班的人，学生们出的题，不论是学习还是生活中的，几乎没有能难住杀老师的。不过这样的老师偶尔也会有一些缺点和认怂的地方，就是这样才显得这只黄色章鱼很可爱呀。学生时代如果能够碰到一位这样性格的老师想必是一件很幸运的事吧，上课枯燥的知识都会变得有趣起来，生活中所有的疑难和趣事都能跟他分享，完全不存在繁重的任务和学习的压力山大，这样的校园生活简直完美。虽然最后杀老师因为政府的原不得已继续存在于这个世界，死于自己心爱的学生之手也算完成了最后的心愿。大家喜欢这样的老师吗？欢迎评论区留言哦。文：Lee酱', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10595', '0', '虐哭了！《暗杀教室》倒数第四话 潮田渚亲手杀死杀老师', '《暗杀教室》漫画将在180话完结，而动画第二季也赶在动画党被剧透前，将随漫画一同完结。剩下99话的漫画剧情要被压缩成16话动画来完成，也是不知会被删成什么样。（《极黑的布伦希尔特》的最后两话，想必大家都有所领略。）对此，追番姬只想跟制作组说 ↓以下是最新更新的漫画第177话。（汁源来自komica）不想被剧透的迅速撤离杀老师最后一次点名，潮田渚亲手杀死杀老师，杀老师化为光子消失 (ಥ _ ಥ)虽然很多漫友希望杀老师复活，但追番姬以为，按照松井优征的风格，应该不会出现这种反转了的，但也不排除“杀老师化作了无形的存在，一直陪在我们身边”之类的结局。那剩下3话要干啥呢？坐等。', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10596', '0', '精选暗杀教室美图十张，最后一张大爱杀老师！', '文 / 大白(●—●)你们要的无水印来了，直接抱走吧！----- END -----QQ公众号ID:ILOVEACG微信公众号ID：LOVEACG99图片源自网络收集,如侵权私信删除喜欢就点赞，喜欢就收藏，喜欢就订阅！QQ公众号丨微信公众号丨头条号：最爱二次元谢谢支持最爱二次元，下一期更精彩，敬请关注！', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10597', '0', '暗杀教室杀老师被杀了么', '暗杀教室此漫有些无厘头，真的吹得好大，不过关键你受呀在被称为史上最凶恶的超生物“杀老师”将月球炸掉了七成，并扬言在第二年要炸掉地球，瞧，这都玩上星辰了却以不明目的而担任椚丘中学3年E班的教师，上面还高大上了，下面就青春学园剧了，跳跃太大，表示死机状态中……、重启～～暗杀的人有报酬，所以呢各同学想尽办法杀死这名老师，钱果然万能，是贫穷维护了善良的吧？呵呵…、E班的同学都知道杀老师的身世，得知原来杀老师其实才是真正的死神，已经上升片神之领域了，话说是修仙动漫么，哈哈记者：如何能杀死你呢死神：这得看看那死亡笔记有名字没另一个死神是他徒弟，因为被徒弟背叛而被不法分子抓去作实验，结果实验失败，研究员炸掉了月球，为了保证密码不被泄露，这便是真相，残忍么，现实么，高层仅仅一句：白老鼠的后患好打击人，看着有些心灵被暴击一万感觉因误杀了E班原班主任雪村，雪村临死前希望杀老师当班主任，这理由好牵强，再添一个，那就是这是萌萌有爱的死神杀老师因为被改造过了，3月份就会死亡，并且会爆炸，地球消失...死神居然远能暴炸造成物理攻击，太不可思议了…读书少，可不能欺骗人死神不是都灵魂攻击么，这是不同维度，好么取冒蓝火加特琳突突你这骗子(………、)得知真相后大家就在思考究竟最后的时间是杀还是救杀老师，于是乎分成救派和杀派人性与利益碰撞，好写实抉择，这风格是要裸讽么…、嗯……总算做了一件看得顺眼事那就先收回加特琳，取四十米大刀来，让你三十九米半啦～但是杀老师为了不让班分裂，提议以对决决定，现在还在对决中，人数基本是一样结果呢？小编也不知道的，那位知晓，小声说说別担心，这里安全的很因为这可不是暗杀教室啦～[普通版]喜欢就点点关注吧', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10598', '0', '杀老师和我们的老师有什么区别？', '你想要一个什么样的老师呢？我想每个经历过初高中教育的孩子心中都有一个或清晰或模糊的答案，而且这个答案的形象恐怕和我们现实中的老师形象相去甚远。如果让大部分学生来给自己的初高中老师们做个客观的评价，恐怕分数都不会太漂亮。那么，如果给你一个《暗杀教室》里那样的杀老师呢？你会因此欢呼雀跃吗？你的人生会从此改变吗？你会从此对教师有个完全不同的认知吗？我想大部分人心中已经有了答案。那么，杀老师到底和我们老师有哪些区别呢？1.最大的不同——杀不死首先是最大的不同，话说，这一点杀老师大概和所有正常人类都不同吧——杀不死。是的，怎么杀，不管谁杀，不管何时杀都杀不死，那么“杀不死”这一点意味着什么呢？其实“杀不死”可以说是杀老师推广自己的教育模式中最基础最重要的支撑了，这意味着这位老师可以完全无视国家机器，无视理事长，无视所有规则，可以强大到确保每一个学生的自由和安全，可以按照自己的理想来培养每一位学生。可以说，没有“杀不死”这个优点，杀老师就不可能成为一位那么成功的教师。2.最让人欣赏的地方——不以学习成绩为评判学生的唯一标准为什么相对于我们自己的老师，你会这么喜欢杀老师？其实原因非常简单——杀老师不以学习成绩为判断学生好坏的唯一标准，他尽自己可能去了解每一位学生的特性，然后付出最大努力去帮助每一位学生，从来不轻视任何学生，也不去断言任何学生。他最开始接手三年E班的时候，可以说除了停学的业同学外，全班都是年纪吊车尾，但是他从来没有因此而对这群学生失望过，并且对于习惯于轻视这群学生的理事长表现出强大的敌对心理，这比起我们自己的老师常常说的“能救你的只有你自己”这种话要更加像一个为人师表的人。3.热爱教育胜过一切一个能够轻易毁灭地球的怪物，让所有职业暗杀者都铩羽而归的逆天存在，向政府唯一的妥协条件就是“成为三年E班的班主任”。如果不是因为热爱教育，如果不是将教育作为自己生存的意义，那么还有什么理由来解释这样不合常理的行为？也许你可以说那是因为记忆中那个不明身份的女人最后的遗言，但是如果不是发自内心的热爱，也不可能最终获得全班同学的认可吧。4.愿意在学生身上花时间不仅一个学生吐槽过杀老师总是在课下逼着他们补课，而他们在此间的种种暗杀行为都被杀老师轻易破解，“真恶心啊，那个老师。”——大家会笑着这么说，嘴上说着恶心，心里却是开心的。尽管我的成绩不尽如人意，但是这位老师并没有像其他老师那样放弃我们，他仍然愿意在我们身上投放足够多的时间，仍然愿意给予我们和其他优秀的学生一样的待遇。真是位让人感动到自己都恶心了的老师啊——大概这才是大家的心声吧。5.老师的专业素质绝对过硬——一人能扛下语数外全科虽然三年E班还有两位老师，但是大家都心知肚明，一个只能教体育课，一个则只会上课乱侃英文，语数外的教学责任由杀老师一人全包了。如果没有绝对过硬的专业素质，是不可能在短短一个学期内就帮助全班学生神速进步的。第二季中，杀老师和理事长的生死对决那里，杀老师的那句“大概全国大部分的老师都可以轻易将这些书中的习题背下来吧”这句话让理事长自己都汗颜了吧。6.将学生的错当成自己的错第二季中，学生们上学途中撞到老爷爷，杀老师亲自去道歉，并且还在老爷爷的病房内跪拜道歉，杀老师的行为表现出的是他完全将学生的错当成了自己的错，不仅敦促学生去道歉，而且以身作则第一个向老爷爷道歉。7.善于引导学生第二季中，三年E班和三年A班的数次交锋中，杀老师的对学生的引导都起到了关键作用，他懂得如果引导学生发现自己的价值所在，如何促进学生的成长。一次是在“倒木”对决中，一次则是“文化祭”中。8.能力强大，实力逆天。没有操场，没问题，我来建。被坏学生绑架，没问题，我来救。被职业杀手围困，没问题，我来救。……有这样一个老师，你的学生时代能不完美么？', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10599', '0', '盘点杀老师八大致命弱点', 'TOP.8影分身增加过头会使残像粗糙，也会十分疲倦。TOP.7每失去一条触手，行动能力会直接下降20%,分身会混入小孩形态。TOP.6在极近距离被压力光线照射会使细胞产生扩容反应，使身体瞬间僵硬。TOP.5再生很消耗体力，精神状态对触手的驱使带来重大影响。TOP.4由于过于强化速度的关系，本身是没有什么力气的，特别是在静止的状态下，一个人就能按住一只触手。TOP.3不能游泳，吸水性很强TOP.2一到月底就资金短缺。TOP.1痴迷于H杂志', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10600', '0', '杀老师、齐木楠雄、龙套、琦玉老师，究竟谁是终极boss？', '近两年风靡起来的超能力番中，拔得头筹莫过于《一拳超人》的琦玉老师，《齐木楠雄的灾难》中的齐木楠雄，《灵能百分百》的影山茂夫（龙套）还有《暗杀教室》的杀老师啦！然而有一个困扰很久的问题，如果让强者如云的超能力者在日漫世界里相遇，究竟谁才是终极胜利者！？是能一拳致胜的琦玉老师？还是稍有偏差就能改变世界线走向的齐木楠雄？还是说…充能到百分百就能毁天灭地的影山茂夫？还是…上知天文下知地理无所不能的杀老师！？先来说说琦玉老师一脸智障，对，没错，一脸智障感锃明瓦亮的大光脑壳别看他这样，人家还是有一拳打爆整个世界的实力的！（该帅还是能帅起来的，不要不服但不可否认的是琦玉老师只想做一个普普通通的老实人，没有什么大野心，时不时还露出一张很欠揍的樱桃小丸子爷爷般的智障脸…嘛，要说称霸日漫世界…我不信琦玉老师会去争的！【第一个pass！】再说说齐木楠雄通常说有能耐的大佬都不显山，不露水，一副超脱三界飞升成仙的淡然感…万年面瘫，外加一切好麻烦…好麻烦…麻烦统统退散体，连最美校花的倒贴都无动于衷！？你告诉我你是不是个真钙！？24K不含糖的那种！能改变一切却不想让一切改变，宁可在头上安俩外星发射器（？嘛嘛，大佬你还是适合隐居。【第二个pass】第三个影山茂夫这孩子打小就是数学不及格、脑子缺根弦的主（哦对，灵能百分百和一拳超人的作者都是同一个人——ONE先生，所以看着这人物…也就是长了头发的琦玉老师啊！！！！）别看这孩子蠢，但是人家爆发起来可不比其他人差然而，龙套却很幸运的有个师匠，欺诈之神霊幻新隆，师匠虽然是个克扣小孩子工钱，欺骗全世界的高智商欺诈师，但对于龙套的成长之路偶尔蹦出来一句两句人生哲理真的让人不得不服，动画最后那句“不愿意的时候，就算是逃跑也没关系。”有这样的师匠，龙套…会跑的吧【第三个pass】杀老师…杀老师，您的学生每一个都变的非常非常优秀，应该达到你期望的样子了吧…这一下，可以安心的去了…【第四个pass】综上…如果真的在一个世界相遇，我自认为他们不可能像DC一样正义与正义战斗，也不会像漫威一样亦正亦邪的大乱斗顶多…凑在一起和和气气的打麻将吧…', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10601', '0', '如此甚好～致 杀老师～', '他们的夏日祭～老师满脑子都是……想想当时自己的班主任……呜哇', '', '4695', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10602', '0', '坂田银时，我们都爱他', '坂田银时，我们都爱他——无论是剑指苍穹猎猎风起的白夜叉，还是劣迹斑斑落拓不羁的店长。就是这样一个浑身上下都是吐槽点，无下限到让人无力扶额的伪大叔陪伴了我们三年。我们都爱他，KUSO和煽情一样重要，多少次当你无奈到抓狂却因为下一句话而会心一笑，多少次当你感动到热泪盈眶被下一句吐槽惹得捶地挠墙。银还记得阿银说‘我的体内啊，有个器官比心脏还重要，它虽然看不见。但确实在我的体内从脑袋一直贯穿到胯间。就因为有它我才能站得直坐得正就算脚步摇晃也能笔直的向前行，在这里退缩的话那东西会折断的…银时的表情。那瞬间，即使是死鱼眼也光华无限。他，那如此世俗的死鱼眼下也包含着这样清澈的心呐。看JUMP的人都不坏。也许喜欢上的瞬间就是从那引入眼帘的jump开始的，要cos成热血少年什么的，连内心也要装扮。这样一个说着小孩女人什么的最麻烦了，却一次次用最温柔的方式把他们弄哭的混蛋。这样一个挖着鼻孔吐槽中二是世界最糟糕的东西的人却有着最少年最执着的内心的混蛋。这样一个在人生的旅途上飙车缺点很多却很热闹的混蛋。这样一个硬汉同心到死都保持的少年心的不伦不类的混蛋。银桑从你觉得——啊，果然天然卷的家伙都不坏。这一刻起，开始奔跑的爱就停也停不了了。白色天然卷红色死鱼眼性格糟糕懒散又奸诈。你是爱还是恨？一本正经地搞笑，极度耍痞地忧伤。你是在哭还是在笑？把正义当做醋昆布，把热血当做蛋黄酱。从秒速五厘米到光速一万年，从歌舞伎町的尘埃到红樱血海的每一个誓言，这最真的影象，藏在那懒散的眼神中，埋在那笔直的脊梁里。他用猥琐外表下那一颗认真的心杀进这个与他格格不入的绝望现实，他用背离了极端与迂回的方式践行着他的大义。那样的假不正经嬉笑怒骂的深处，掩藏着是一颗怎样的心？吸引着神乐、新八、登势、土方、冲田、高杉、假发、阿妙……你以及我。只想打心底去爱这个人，这样简单的一件事。连阿银这样不靠谱的人都被人爱着呀，所以明天也微笑吧。这样的反衬也意外的鼓舞人心呢！他是你的老朋友，跟你抱怨跟你吐槽。银桑聊人生、理想、爱情、友情、七姑八婆、家长里短、街头巷尾，聊光明也聊黑暗，聊市井也聊阳春白雪，聊旦夕祸福聊阴晴圆缺……你看到的是这样一个缺点比优点多、无所事事不上进、举止猥琐着装没品、蹉跎人生毫无生气的大叔。却教给你要交一个到老都能叫外号的朋友，坚持不懈与执迷不悟之一墙之隔，即使是折了腰也要笔直前行，COSPLAY要连心一起……甚至告诉你怎样打柏青哥，打架应该出直拳，结婚就是持续一生的错误，生孩子要有计划……这样一个我们连屁毛都爱的男人。果然微笑是最好的妆容呐——他的人生缩影在了这一杯巧克力巴菲中，当打着哈欠揉着头发的瞬间回忆起那些不经意的过往。曾经拿着刀不为修罗只为守护的双手，现在拿着勺子舀着巴菲。曾经盯着敌人充满杀气的眼，现在盯着jump歪着头思考它的垃圾分类。曾经的锐利如剑，现在的慵懒内敛。银时白色加衣，谱写一段镇魂之歌，镇守一方武士之魂。已而在衣角翩翩中卷去——现在的阿银半黑半白，衣角挥着蓝白逆卷的云纹，腰间挎着意义不明的洞爷湖木刀，脚踏笨头黑色长靴。能容乃整片晴空，能记着最初的赤子之心。坂田银时，多想跟你一起走。坂田银时，我想跟你回家。坂田银时，我们都爱他。——END——后记：后记神马的就是像红豆饭一样的存在！loli易逝正太已老，只有大叔是永恒的！当我们无数次发出‘世间大叔如此多，为什么没有一个属于我？’的感慨间。无数次的想起那个白色的身影，坂田银时。我们呐，都是如此爱他。那双死鱼眼溢满了晴空潋滟的暖阳呢？这种一见钟情的同类气场，令吾辈深深地不能自拔——谨以此篇，献给我的，不，我们的阿银————半日银时', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10603', '0', '坂田银时到底多大？你一定很想知道！', '不关注万事屋的头条号，你都不知道每天会错过什么 还没睡的和没睡醒的小伙伴你好，每天都要给你惊喜，今天也不能例外，谁让你关注了万事屋呢！今天我们来聊聊坂田银时到底多大！啧，别想歪了魂淡，这里旁白君说的多大是年龄，想歪的都给我罚站去！银魂有一集中，银八老师讲过，如果你还记得的话，大致是有个叫大西的同学问了这样的问题，银八老师也分析了一下。后面一段话是银八老师的解释，大概就是说，这场持续了二十多年的攘夷之战，银桑他们是后半段期才加入的。里面也提到了其他的东西，比如...20 年前天人来的时候，阿银还是个小屁孩。旁白君觉得，如果坂田银时现在看到这张照片一定觉得是黑历史吧。太羞耻了魂淡，阿银小时候就这么萌了！而幕府被迫开国的时候，阿银开始长毛了...额，这里的长毛是字面的意思...还附上了图！噗，你是不是想转到银桑后面看一看是什么情况？别闹！所以，其实很多人算下来，银桑的真实年纪差不多是在 28 岁左右。而且他们给的理由也是非常的合理和经得起推敲的，那旁白君觉得我就没什么好多说的，我想吐槽点别的！这么多年过去了，我们看了银魂好几年，银魂里也度过了很多个冬天圣诞节了，如果你记得的话。那么，你有没有发现一件很神奇的事情，银魂很多时候的更新都是一点点来的，就像日子一天天过一样，而我们眼中可能银魂一路走来十年多了，但在银魂的世界里可能只有几个春秋而已，所以今天这个问题，说到底没有多大意思，要知道银魂里很多东西是经不起推敲的，看银魂开心就好，不用那么细究！那旁白君你为什么要写这样的话题呢？还不是没东西写了，扯点老话题，大家一起吐槽一下，比较我知道你们很想知道坂田银时到底有多大，不管是哪个大！啧，你那点小心思我当然知道啊魂淡！新八，神乐这几年也在成长，如果你看地足够仔细，他们的成长不是在屏幕前的摸样，而是他们成熟了，有担当了，不再是过去那个围绕着阿银转的孩子了，他们可以自己判断自己想走的路了，他们也已经知道保护这个词的重量了。所以你说银魂未来会怎样？我想说，不管怎样，万事屋永远都会在的，三个人和一条狗就是啊魂淡！好了，有什么想说的赶紧给万事屋留言吧！转载请联系作者获得授权，非商业转载请注明出处。作者：旁白君来源：万事屋', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10604', '0', '坂田银时的那些坏毛病，数一数可以说半天', '你好，我是旁白君，欢迎关注万事屋头条号，每天更新一篇日常，让更多人知道银魂！今天我们来聊聊银魂的男主角，坂田银时！他似乎和旁白君看过的许多的动漫男主都不太一样，这种奇怪的特点让我觉得除了主角应有的帅气之外，还有如下几个比较突出的地方。责任感这算得上是贯彻银魂这部作品始终的一点，阿银一直在想方设法地保护着他想保护的人。从银魂一开始就反复提到了保护好想保护的东西，哪怕自己粉身碎骨。对自己做的事情负责，对身边的人负责，对自己说出的约定负责。那句话怎么说的来着，一旦约定至死也要遵守，银桑就是用这样的方式，一点一点，在每一集的银魂里表达出来，这样的担当是很具有魅力的。所以旁白君觉得坂田银时在这方面可以算是满分的！有趣与污阿银一直以来都是有趣的，他的有趣和幽默感让他在整部作品的人缘非常好，大家都会在不知不觉中接近他，靠近他，被他吸引，所以当银魂进入地球保卫篇的时候，整个歌舞伎町的小伙伴都出动了，这条街由万事屋来守护，那万事屋就由他们来守护。阿银有趣另外一个特点，也是整部银魂作品被吐槽所谓的太「污」，这个昨天其实有提到过，别说银魂「污」，你那些人脑洞太大，没见过世面而已！银魂里确实有很多看起来，听起来都有点超越 18 禁的内容，但实际上，点到为止的「污」才是最高境界的「污」啊魂淡！温柔与真诚银桑一直都很温柔。对小神乐，在下雨天买伞的那一集里（111话），银桑就表现出如父爱一般的温柔。在小猿第一次出场的那一集里全程帮助小猿，在蜘蛛篇里出手救了月咏，在银魂刚开始的时候把新八的姐姐阿妙从飞船上救了下来，他之前还被阿妙一顿揍呢，银桑不仅仅对女生是温柔的，对男性也是啊！我给你举个例子，我想想......要不，这个我们改天再说（其实是有，只是为了戏剧效果就不说了，你们自己领悟）此外一点就是坂田银时是一个真诚的人，你可能会说他拖欠房租之类的，其实真诚这点跟之前说到的责任差不多，那些在他身边经常添乱的人，也是他最想要保护的。嘴上经常是说着麻烦，这种事情我们才不会出手呢，实际上银桑早就想好要帮到底了。就是这样子的真诚待人，他的身边才会聚集这么多人。至于剩下的那些点，什么帅气啊，死鱼眼，挖鼻孔之类的，在旁白君看来，都是银桑的特征，这个角色需要有一些有反差的东西，当然，作为主角，帅是必须的，此外，还有配一个好声优！所以，旁白君说银桑虽然爱偷懒，不求上进，天然卷还特么没钱，但却一直当着大老板！这一点也不为过啊！好了，你有什么想说的，可以给万事屋留言哦~转载请联系作者获得授权，非商业转载请注明出处。作者：旁白君来源：万事屋银魂图库', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10605', '0', '女孩子的理想型应该是坂田银时一样的男人', '有这样一个男生，别人都在心系宇宙拯救地球他却安逸于眼前的生活很没有上进心的样子。别人为梦想破灭而走上歪路时 ，他却认清了现实，好好地经营着自己的万事屋能赚一笔钱是一笔，继续去守护身边的人。他血糖高却依然热爱甜食。他说愉快度过人生的秘诀之一就是不忘童心。他表面一副流氓的样子，却正经地过着自己的一生。懒懒散散踉踉跄跄荡悠在人间。他就是坂田银时。我身边的很多女生，二次元男神都是银桑。可能是因为他很温柔，他会说“被女人的眼泪淋湿，我可不想啊。”可能是因为他是那种在关键时刻靠得住的男人，因为他的信条是“只要你有想保护的东西 那就拔剑好了。”可能是他很有担当，不会去逃避生活中可能会出现的各种麻烦事吧。可能是他从来不吹牛逼，知道自己几斤几两，从来只做自己能胜任的事情吧。可能是他能让人开心，在一起肯定每天就是被他的吐槽逗得哈哈哈哈笑。可能是他很少让人失望吧，答应了的事情就一定会做到。可能是他心很软，到最后都会答应你的各种恳求吧。总觉得银桑那样的人活得很通透，不去仰望遥不可期的东西，只想着这么把房租交了，怎么吃到好吃的甜食，怎么照顾好身边的人，怎么帮客人解决问题就好了。虽然他很爱说鸡汤 ，但大部分时间都是吊儿郎当，至死都是少年的样子。很难不让人喜欢。一直都觉得男人的上进心并不只是表现在对事业 理想的无限渴望上，而是愿意为了身边的人积极努力地活着。最近朋友为爱情头痛，因为她男友一直好高骛远，不老老实实地做好眼前的工作，总想发财觉得自己能出人头地，东搞搞西搞搞，最后什么也没搞成，所以很消沉，在家当肥宅了。男友变得戾气很重，动不动就要操翻这个世界，所以他俩整天吵架。我想起来有一天遛狗的时候，看到一个外卖大哥把摩托车停在路边用手机和他儿子老婆视频， 儿子估计刚在学说话，咿咿呀呀不知说什么，大哥一个劲哈哈哈地笑，然后我听到大哥对她老婆说“辛苦你带孩子啦，你看我给你带的好吃的”，然后我看到他晃了晃手中的小龙虾外卖。真正厉害的男人，并不是空有抱负的男人，而是认清自己并不是英雄后还能好好地为家人努力，去做一些力所能及的事，还愿意承担起生活重担的男人。这两个男人，相信大家都会选择外卖大哥吧。银桑就是那种会好好活着的人。我对《银魂》电影中的一幕印象很深刻。大概是他要去战斗前去要些稳赢的装备，说怕自己会输，会死，好吃的太多了，他想活着。一点都不像男主角会说的话，太怂了，可是又非常真实的可爱 “对啊对啊我也是这样想的啊好怕死哦”。只有跟这样的男生在一起，你在生气的时候 ，他会放低身段去哄你。他会四处奔波打工赚钱养家不怕丢人。他会拼了命保护你。总之就是很靠得住啦，一辈子再长再难也不怕啦。从很早前就立志男朋友要找坂田银时一样的男人，对他就是从银发丝到挖鼻孔都喜欢。很高兴是小栗旬演的银桑，让我终于看到了男友长什么样子。', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10606', '0', '二次元男神专辑：坂田银时', '《银魂》中的主人公坂田银时，他平时懒散无拘束，喜欢打小钢珠看《Jump》，但遭遇事情时所展现出来的气度与实力却无人不被他所撼动。温柔体贴，在孩子面前表现了有当温柔爸爸的潜质，曾半夜起床给失眠的神乐做饭，以他为中心的万事屋是一个温暖的大家庭。也有抖S腹黑的一面，曾被土方十四郎吐槽和冲田总悟是“抖S二人组”。十分害怕鬼。关注“一起来看动漫吧”了解更多动漫资讯哦！图源网络如有侵权请告知删除', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10607', '0', '坂田银时，银色的救赎者', '如果要谈到一部动漫，无法避免要讨论的便是这部动漫的主角与主线，主题。今天小编就来胡乱分析一波坂田银时，这个吊儿郎当的银发自来卷。下面开始上正文上图。说正事专用配图《银魂》这部动漫的背景取自日本著名的明治维新时期，每一个动漫中的主要人物基本上都可以在当时的历史中找到原型。例如真选组的原型，新选组、泥水次郎长的原型，清水次郎长、桂的原型，桂小五郎，等等。那么我们的主角的原型是什么呢？金太郎原型就是这位，坂田金时，平安时期濑光四天王之一，也叫金太郎。据说是一个可以和熊打架的人。（emmmm，突然想到了一个和老虎打架的家伙，也不知道哪个厉害）一个平安时期的人，为什么空知猩猩要把他放在幕末大背景的情况中呢？因为在幕末悲剧的历史中，需要一个拯救者。幕末的背景其实是比较悲剧的，以新选组为例，冲田总司因为肺结核英年早逝，土方岁三战死，局长近藤勇被斩首……而在银魂中，猩猩并没有让悲剧重演，而万事屋三人组就是最大的拯救者。好了，现在开始写一下我们的主角，坂田银时。白夜叉在攘夷战争中最活跃的白夜叉，坂田银时，当老师松阳，自己最好的朋友，战友，桂，矮杉被抓，天导众给了他一个选择，杀掉老师，还是杀掉桂和矮杉。他选择了杀了松阳。这个抉择，应该便是银魂的主题了。是去保护你重要的人还是你重要的人重要的东西。这个主题其实在银魂的各种短篇中都有所体现。其实银时他自己也无法抉择，他也是很迷茫的。面对着矮杉的不解与离去，昔日的好友与战友分道扬镳，变为仇敌，银时自己也是非常痛心的。但是他却没有办法，一种深深的无力感从心底激发了莫名的悲凉。曾经有人跟我说过，银魂的主角好像从一开始就是满级的，与其他的动漫完全不一样。（好了，崎玉老师你先坐下，我们一会去打游戏）最开始我觉得真的很强。但是在看了比较长时间之后我觉得，其实银时也是在成长的。他的成长是他的内心，他的成长也是一直围绕着他内心的抉择，是选择哪个，你重要的人，还是重要的人最重要的东西？比如说狛犬，中村京次郎，为了保护老爷，一直隐瞒少爷的死讯，为了保住老爷所创下的魔死吕威组，对外宣称是自己为了地位而杀死了少爷。在最后，被重伤后在老爷的墓碑前死去。再者死亡伏笔篇，冲田一直隐瞒的真相，不让小女孩伤心，而谎称自己失手杀了她的父亲。女孩一直觉得自己的父亲是一个好人，而冲田也并不想让女孩知道自己的父亲的污点。他为了保护女孩而默默承担着。虽然女孩知道了真相，她也明白了自己的父亲是一个英雄。在这里，很多时候银时都见证了或者或多或少的推动了抉择的发展。而最最明显的一次，就是再见真选组篇了。在再见真选组篇中，因为真选组保护将军不利，而被撤销，局长大猩猩被关进监狱，真选组被解散。在银时面对着土方的委托的时候，他的心里应该很清楚，他应该选择什么。其实说土方是第二主角我觉得也无可厚非。因为土方和银时这两个人真的是太像了又太不像了。这两个人是活生生的死对头，也是一对不可多见的知己。空知猩猩其实也是一直在推动着这两个人的互动，比如灵魂互换。（银土赛高！）而这两个人的经历也是非常的相似。所以在再见真选组篇的时候，两个人也会产生更多的共鸣。猩猩被抓，即将被斩首。是去救出猩猩，还是带着真选组离开江户，保护猩猩最重视的队员们。土方面临的抉择其实是和银时一样的，所以银时选择了帮助土方。对银时来说，如果当时自己有能力保护老师，保护同伴，他就不会去做这种抉择了。他想全部都救下来。看着现在站在面前的土方，他想到了从前的自己，那么的无助。他不想让悲剧再次重演，也不想再次失去重要的伙伴了。所以他和万事屋，一起拯救了真选组，当然，也算是猩猩在银魂中给了新选组一个好的结局吧。有人说，银魂是一个幕末时期的理想的缩影。确实，把银魂从幕末的悲剧中拯救出来的人，是万事屋啊。万事屋合影本次的胡乱分析也就这样了。说点题外的，其实一直想写这种东西很久了，也是一直想当一个小编来着。但是就算是之前有过多少想写的东西，默默在心中忖度了许多遍的东西，坐在电脑前都会变的语无伦次。也许这篇东西写的比较乱，也是在最后请求一下读者的谅解了。因为是第一次发文，所以有许多生疏的地方。希望大家可以点个赞吧。以后也会发很多关于银魂的文章的。下期呢，大家想看谁的话可以留言（虽然我知道没多少人看，不过还是要安慰一下自己吧）祝大家新年快乐吧。嗯。', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10608', '0', '动漫《银魂》坂田银时 一大波美图壁纸来袭（二）', '有时候，我们明明原谅了那个人，却无法真正快乐起来。那是因为，你忘了原谅自己。别说什么快逃了，别再一个人背负所有的事情，这太见外了。流着眼泪乞求帮助，痛哭流涕的来依靠我。想哭的时候就哭出来吧，想笑的时候就笑出来。当你哭到变丑的时候，我会比你哭的更丑，当你捧腹大笑时，我会比你笑得更大声。只要这样就好了。比起舍弃自我高洁地死去，还不如找自己的想法活下去，就算肮脏点也没关系。。。。期待下次更新', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10609', '0', '作为松阳，再一次拜托了坂田银时', '今天看了鼠绘汉化的银魂680话，把昨天旁白君看的韩肉版的盲点都扫了一下，基本上知道这一话发生了一些什么。很多小伙伴最关心的高杉是不是要领便当这件事情，目前无从得知，但从高杉和银时的对话中可以看出来，高杉已经决定要从奈落中再次夺回虚或者吉田松阳！高杉的意思似乎是，不管它是松阳还是虚，先抢回来再说，只要有老师...想起高杉之前和坂田银时肉搏那段，不是电影版里的肉搏露大腿的画面，而是将军暗杀篇里，高杉和银时最虐的一场，高杉在那一瞬间还产生了幻觉...似乎可以理解，高杉是真想把松阳给找回来，不管最后是不是要付出自己的生命。那说到高杉会不会跟胧一样的下场，旁白君觉得，不会，至少没完结前空知英秋老师是不会对高杉怎样的，虽然这个男人至少经历了 2 次生死的挣扎。另一方面，关于那个孩子到底是不是松阳，虽然之前称之为小小松阳，总觉得很像呢，这一话里也给了一个答案！即是也不是，但基本上可以看出来，此刻的他就是松阳了。昨天也是说到这里，他有一件很重要的事情托付给坂田银时的时候，那句话还没说完，就遇到了奈落的人。情急之下，松阳把银时推下山，算是又救了银时一命，然后把重要的那颗心脏给了银时。这里最重要第一段话是这样的。「但是我很高兴...能够...再次见到你...」你看坂田银时那眼神。虽然知道他是松阳的时间短暂，但似乎有一种一言为定的感觉...那种作为松阳弟子的责任感，这件事情他管定了！毕竟，他 2 年前决定离开万事屋也是为了这件事情！这一幕掉落悬崖，像不像当年一国倾城篇中（257话），坂田银时和胧最后一站的画面。至于假发去哪里了，总会有小伙伴问这个问题，他不是 2 年后当首相了嘛！发现没有，保卫地球篇，烙阳决战篇和真选组动乱篇中有假发的身影，而与夺回老师的篇章中，似乎很少见到假发，可能是空知英秋老师有意避开了假发。桂小太郎要的是江户的黎明，心系国家。从那年他们在私塾里读书就可以看出来，他的注意力在学习，高杉的注意力全在松阳上，而银时就知道睡觉。所以假发不参与进来，可能是空知英秋老师希望桂小太郎从另一个方面去拯救这个国家吧。纯粹是旁白君个人的猜测了...至于神乐新八那边，可能会在某一个时间点再次会合吧，毕竟，三个人在一起才是万事屋啊魂淡！接下来的故事总觉得会很有趣啊魂淡！空知英秋老师挖了那么大一个坑，坂田银时会和高杉合作吗？看起来不像啊，但总觉得他们会用自己的方式去找回老师，所以，这是松阳夺回篇嘛！有趣了！好了，说说你的感受吧！欢迎留言哦！我是旁白君偶尔吐槽吐槽银魂的那些事，更多的时候，我会更新自己的故事。不关注万事屋的头条号，你都不知道每天会错过什么转载请联系作者获得授权，非商业转载请注明出处。作者：旁白君来源：万事屋', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10610', '0', '坂田银时这次玩大了，找时光机也没用！', '不关注万事屋的头条号，你都不知道每天会错过什么如果你看了银魂最新的一集，应该知道发生了什么！坂田银时把屁怒吕头顶的那朵可爱的小花花拔掉了，这意味着什么呢？就让旁白君来跟你讲个故事吧！这得先从屁怒吕的故事说起，他呢身高203厘米，体重140Kg，啧，感觉是要相亲的节奏，那么这就是他的照片！是不是很萌呢！第一次登场旁白君已经忘记了，好像就是花粉的那一集吧，在万事屋的附近经营着一家花店，而坂田银时在那一集里一直以为他是很可怕的人物，实际上他很善良。那万事屋他们在一番搞笑的脑洞之后，发现屁怒吕的人还是很善良的。虽然面相有点可怕。注意到了没有他头顶有一朵可爱的小花花，那么这朵花对他的人生产生了不一样的变化！屁怒吕是天人，战斗佣兵名族「荼吉尼族」，他有一段很厉害的传说，被称作「茶吉尼之神伊格鲁的最高杰作」以及「神之角」，看不懂没关系。大概就是眼前这个人被他轻轻松松打败了！那只断掉的角！是的后来屁怒吕厌倦了杀戮，离开了同族的佣兵群体。而寄生在他头顶的小花改变了他！屁怒吕变善了，非常热爱生命，为了保护小生命可以将人揍飞。 总觉得这里可以插播一些广告什么的！啧！你懂的！而他的强大是大家有目共睹的，据说可以是银魂里最强大脑，啊呸，最强者前四了！跟什么星海坊主啊，虚，等等人物并列，你大概懂那个意思就好！那么这么强大的好友，对于万事屋，歌舞伎町来说是一件好事情！然而这一切的一切，不小心发生了一些变化！大概就是坂田银时不小心把屁怒吕头顶的小花花拔掉了...屁怒吕的性情就会在好人和坏人之间反复！阿银尝试了各种方法，胶带，胶水，那朵花都耷拉了下来，这次真的玩大了啊魂淡！阿银也彻底忘记了曾经的那个口头禅，找时光机啊魂淡！可能是空知英秋老师决定这个梗不需要再用了吧！但是屁怒吕头顶不能没有花啊！空着多难受！！当所有人都在为赶走敌人高兴，欣赏着夕阳无限好，只是阿银你特么在干嘛啊！魂淡！喂！！！真是够啊魂淡！还不如找一个时光机啊银桑！！好了，差不多就是这些了，欢迎留言哦！我是旁白君偶尔吐槽吐槽银魂的那些事，更多的时候，我会更新自己的故事。转载请联系作者获得授权，非商业转载请注明出处。作者：旁白君来源：万事屋', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10611', '0', '《银魂》暖心爸爸坂田银时，贴心妈妈新吧唧，可爱萝莉小神乐', '你们有没有试过，因为一张图片而想起一个故事？前几天，室友去买衣服，给我发了一件T恤的照片——请忽略那只猪蹄请再次忽略猪蹄，污染了各位的眼睛真是对不起了~~瞬间想起了这一整个，暖心的故事——看过银魂的多多少少都了解，坂田银时家里的小萝莉神乐，属于强大怕阳光的夜兔族，一把紫伞从不离手。有一天下雨，万事屋三人组出门，路上有许多打着花伞的姑娘非常瞩目，成了雨中移的“花”，神乐非常羡慕那些打着花伞的人。她觉得自己的伞相比花伞显得非常的丑，索性就不打了。还很傲娇地说，伞坏了，自己就是想淋淋雨。新吧唧问银时神乐怎么了。银时满不在乎地说:“小鬼就喜欢下雨，暴风雨，台风什么的。” ——银桑，你这么不懂少女心可是嫁不出去的哦~~好在我们的死宅新吧唧是个温柔体贴细心好人妻，看着路过打花伞的小姐姐们，体会到了神乐的想法。所以新吧唧借口说要买洗涤剂，三人一起来到了大江户人民什么鬼都有的百货超市。新吧唧都知道了神乐的心，聪明的银桑自然也能懂。虽然家计紧张，但还是不忘给神乐买一把可爱的小花伞。不过我家神乐，可是个超级无敌可爱萌的小傲娇哦~~“小花伞什么的，我才不需要呢~~”哼！好在我家银桑也是个大傲娇——“我也没说是买给你的呀……”嘛~嘛……两死傲娇虽然爱闹别扭，不过小花伞还是给了我萌萌的神乐酱。笑靥如花，大概就是这样的吧——神乐特别特别喜欢这把小花伞。就像我们小时候，有了喜欢的东西，都喜欢跟小伙伴们炫耀一般。只要一下雨，神乐就撑着伞出门，带着甜美满足的笑容，走过江户的大街小巷。可是呀，如果台风的话，还是在家里待着比较好哦~~不然小花伞可是会被风吹坏的~~不过，带胶布~~我们心灵手不巧的神乐酱，就算十根手指都被针戳伤了，也要用银桑的胖次（大雾）把小花伞缝补好！缝补过的小花伞，虽然不如原来漂亮好看，却也别有一番风味。神乐再次高高兴兴地撑伞出门，可是这次却遇到了一对贫苦的兄妹。两人因为没有雨伞，只能在地藏像旁边瑟瑟发抖地躲雨。神乐经过一番纠结，忍痛留下了自己心爱的小花伞，帮助这对可怜的兄妹。没有伞的神乐，在垃圾堆里捡了一把只有伞骨的破伞。撑着破伞的花季少女，这一奇怪的组合，引来了路人的诧异的侧目与讥笑。一家人最重要是整整齐齐。我们坂田一家最最最最疼爱的小神乐，我们怎舍得让你一个人孤单呢~~sa~~一起回家吧！要永远一起哦~~神乐神乐，我家有好多好多好多好多的小花伞啊、醋昆布什么的，要不要嫁到我家来呀~~或者或者，我去入赘也可以哟~~呐呐，考虑一下嘛~~吃饭管不管饱啊魂淡！啊！眼镜好帅！宇宙第一笨蛋武士啊混蛋！哒——哒——哒——', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10612', '0', '天生牙为什么救不了神乐？', '看到这集真的很心疼神乐，为了自由…成为自由的风。天生牙不是救赎刀吗？为什么救不了神乐?可不可以理解成神乐中的是瘴气，天生牙复活不了，需要戈薇净化才可以吗？​​​​​', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10613', '0', '动漫美人谱：《银魂》神乐', '“我是自己决定自己的战场的。不是血，而是灵魂。为了自己想保护的东西而站在战场上。” ——神乐出自吉原炎上篇。我觉得神乐最了不起的地方在于，她很“普通”。当然了，从战斗力或者食量什么的来看她可能不是很符合“普通”这个概念……但她可是夜兔啊。神乐原本也有幸福的家庭。但母亲江华早早去世，哥哥神威因夜兔的“弑亲”习俗偷袭父亲星海坊主，在卸下父亲一条手臂后反而被父亲打倒，因神乐阻止父亲才保住一命。此后神威和星海坊主先后离家，丢下了年幼的神乐一人。这就是夜兔。一个以战斗为本能，强悍无比，但亲情淡薄，冷酷无情的种族。神乐却不一样。虽然有着和娇小外表不符的怪力和深不见底的食量，虽然大大咧咧还会毫不在乎形象地抠鼻孔，但她本性还是一个天真善良、重视亲人和伙伴的普通小女孩。在父亲和哥哥不和，母亲江华病重的日子里，小女孩神乐已经早早学会了懂事，用温暖的笑容安慰妈妈。江华曾经对神乐说：只要有你在，这个家就一定没问题的。神乐体内也流着夜兔之血，当然也有夜兔族的好战天性。但她却想要改变夜兔族，一直在与自己的天性战斗，想要把自己的力量用在保护他人而不是伤害他人上。吉原炎上篇里，万事屋在营救日轮的过程中兵分两路，神乐和新八负责牵制神威的手下——同为夜兔族的阿伏兔。然而神乐与新八却无法与身经百战的阿伏兔匹敌。阿伏兔看穿神乐在压抑夜兔族的战斗本能，对她说“拒绝血的你，以及以血为骄傲的我们，从一开始就没什么好分胜负的。”眼看新八就要被阿伏兔杀死，无法保护同伴的痛苦让神乐终于暴走了，体内的夜兔之血觉醒，爆发出的可怕的战斗力瞬间反杀了阿伏兔。而就在神乐被夜兔之血所控，几乎就要对阿伏兔下杀手时，新八拼命地制止了她。“和银桑约好了啊！我要保护……我们信赖的神乐！不是夜兔，不管什么哥哥和妹妹，就是那个冒失、任性、有点让人害怕，但是非常温柔的女孩子！我要保护——我们最重要的同伴！不会因为像你这样的人而弄脏神乐的手！”“清醒一下啊，神乐！你的敌人，我们要战胜的敌人，不是这种小混混！神乐——”这番话唤回了神乐的理智——或者应该说，是万事屋的羁绊唤回了神乐的理智，吧？就像神乐一直在为保护她的同伴而战斗一样，同伴也一直在守护着她。“我是自己决定自己的战场的。不是血，而是灵魂。为了自己想保护的东西而站在战场上。”这就是神乐所选择的道路。“以血战斗的哥哥和与血对战的妹妹”，或许压抑夜兔之血，不愿意伤人、不愿意杀人的神乐敌不过神威，但我却觉得这样的她远比任何人都强大。那坚持捍卫着自己的信念、与自己战斗的身姿深深打动了我。这才是我心中最耀眼的神乐！文：夏时千里@艾欧动漫', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10614', '0', '银魂！神乐！总悟！', '​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​​ ', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10615', '0', '给神乐的一封信', 'To:神乐命,钉宫病.说话总是阿鲁来阿鲁去的\"loli辉夜姬\".神乐我可以捏你么?额..果然还是不可以的么?对于你,留下的印象除了顽皮和可爱,就是可爱和顽皮了!其实不是啦!只是太萌你了.神乐女王也好工厂长也好,仍旧改变不了你是loli的事实阿!你有一张可爱的脸蛋.再加上丸子头和loli音.这种设定放在银魂里,就有了与众不同的特色.不仅得益于你的个性,也得益于钉宫的演绎.托她的福,你成为了女性角色中的本命.托你的福,我终究患上了流行的钉宫病.我想买好多的醋昆布给你,希望你能一直这么可爱下去.庆幸神虽然赐予了哥哥强大的威力,却把快乐留给了你.所以,你来到地球与他们相遇.希望你不会再感受到夜兔的孤寂,也不会再痛苦于夜兔的命运.我们和你在一起,所有的人都会一直爱护着你.神乐', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10616', '0', '来自神乐的同人美图', '喜欢的朋友在下面留言，客官喜欢什么我去搜罗筛选最精的作品回来与你分享。点个关注，收藏一波。ACG大家坛期待你的到来！', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10617', '0', '最适合戴眼镜的动漫角色！志村新八以“眼镜本体”获得第一！', '对于严重近视的人来说，脱下眼镜“五米开外男女不分，十米开外人畜不分”是正常的情况，所以他们戴眼镜是为了能够“看清这个世界”，而还有些人，戴眼镜纯粹是为了装饰，比如李易峰，真的太帅气了！在动漫中，同样有一些戴眼镜的角色，他们有些是近视有些只是装饰！和现实生活中一样，有些人戴眼镜加分，有些人则减分，因此，有日媒做了一次关于“最适合戴眼镜的动漫角色”的票选活动，到底有哪些动漫人物上榜了呢？第十：野比大雄《哆啦A梦》我一直以为大雄戴眼镜只是因为他是高度近视眼而已，谁知道，他戴眼镜原来跟他妈妈一样都是装饰用的，只不过不同的是，大雄妈妈戴眼镜是为了掩盖天仙一样的容貌，而大雄则是为了掩盖“眼睛小”“有时候眼睛会变成嘴”的事实！第九：坂本《在下坂本！有何贵干？》坂本大佬戴眼镜绝对是为了装逼！不管有没有近视，他绝对是为了装……第八：奥村雪男《青之驱魔师》雪男有很多备用的眼镜……而雪男的眼镜就是为了掩盖他腹黑的内心！不过总有人意淫雪男会黑化，那么到时候把眼镜一丢，帅气地将头发往背后一撩，乌黑有光泽……第七：齐木楠雄《齐木楠雄的灾难》作为超能力者，齐木南雄对于自己的能力并不能很好的控制，所以他给自己戴了很多抑制超能力的设备，比如头上两个萌萌哒的发夹，比如眼镜！戴着眼镜就可以抑制美杜莎之眼……噗呲！第六：江户川乱步《文豪野犬》江户川乱步在推理的时候戴眼镜，没错，就是为了在装逼的时候戴眼镜，坚信自己有异能力！不过，在小说里面，眼镜是他和社长的“定情信物”！第五：栗山未来《境界的彼方》天然呆的栗山未来，被有些人成为眼镜娘的代表，还有就是擦眼镜的时候，真的很萌啊！那么你喜欢戴眼镜的栗山未来还是没戴眼镜的呢？第四：柯南《名侦探柯南》柯南的眼镜其实是一件神器……第三：月岛萤《排球少年！！》月岛萤被称为“扶眼镜担当”，腹黑、别扭，思维敏捷，这种可爱的男人不是男神是什么~第二：绿间真太郎《黑子的篮球》其实早前的“眼镜角色”排名里面，绿间真太郎曾排到男子组第一，而栗山未来排到女子组第一！第一：志村新八《银魂》作为官方认可“眼镜才是本体”的新八酱能够获得第一毫不奇怪，毕竟其他人都只是在戴眼镜，而新八酱本身就是眼镜！以上就是此次日媒票选的“最适合戴眼镜的动漫角色”，你认为谁戴眼镜最没有违和感呢？你还知道哪些适合戴眼镜的动漫角色，不妨留言一起讨论一下吧！', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10618', '0', '哪些动漫角色的吐槽能力非常强？我只服《银魂》中的志村新八', '【哪些动漫角色的吐槽能力非常强？我只服《银魂》中的志村新八】欢迎大家来看小编的这篇文章，现在很多人喜欢看动漫，小编是一个资深的动漫迷，现在国漫也崛起了，会有更多的人喜欢动漫的，小编今天要和大家说的是你觉得哪部动漫中的人物吐槽能力是最强的，看过什么动漫的你看到这个问题的时候，想到的人物是谁呢？1、志村新八吐槽能力不是一般的强的，根本变身他的存在感是有点低的，但是加上他的吐槽属性之后便觉得还是很有存在感的，反射能力弱，运动神经差，没有女人缘，常被形容为“很平凡、很普通、大众脸、没有什么特点”，被坂田银时认为是“带着人类的眼镜”。有“吐槽八”与“眼镜吐槽男”之称，也被称为“江户第一吐槽男”。吐槽我真的只服他2、齐木楠雄本身就是主角的她就已经很有存在感了，加上他又有特殊的能力，其中就有读心术，随时都能知道别热的心里活动，所以吐槽起来那是相当的容易，被称为无口吐槽帝，都已经称帝了，你说厉不厉害3、津田琴美正值青春少女幻想期，喜欢无限制的幻想，有严重的中二病。成绩平平，因为离家近选择了樱才学园并通过了考试。性格开朗且十分搞怪，与哥哥的关系十分要好。她是每一个槽点都不会落下的人本文由动漫怪兽原创，欢迎关注，带你一起长知识！', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10619', '0', '票选最适合戴眼镜的男性角色 志村新八稳得第一', '有很多妹子都喜欢眼镜男子，如《名侦探柯南》的柯南和冲矢昴、《刀剑乱舞》的龟甲贞宗、《偶像大师 SideM》的樱庭薰、《偶像梦幻祭》的游木真等。究竟哪个男性角色最适合戴眼镜呢？日本动漫迷进行了相关讨论。1.18/6/1 21:11志村新八，但他并非很适合戴眼镜，而是附带新八的一副眼镜。2.18/6/1 21:03《黑子的篮球》的绿间。3.18/6/1 22:15我要提的不是戴眼镜的主角柯南，而是冲矢昴。4.18/6/1 22:43《Free!》的龙崎怜。5.18/6/1 21:04我会同时想起《网球王子》的乾贞治、手冢国光和忍足侑士。6.18/6/1 21:42《排球少年》的阿月，从眼镜就能看到他那冷冽的目光。7.18/6/1 21:32《齐木楠雄的灾难》的楠雄。8.18/6/1 22:22《飙速宅男》的小野田坂道。9.18/6/1 21:11《钻石王牌》的御幸一也和《XXXHOLiC》的四月一日君寻。10.18/6/1 21:16《无头骑士异闻录》的新罗。11.18/6/1 23:34我会想起《最游记》的八戒，基本上是单镜片，但我就是从他开始喜欢上眼镜男子的。12.18/6/1 22:27被说是眼镜本体的《月歌》弥生春。13.18/6/1 23:04《名侦探柯南》的工藤优作。14.18/6/1 22:59《网球王子》的手冢国光、《飙速宅男》的小野田坂道、《银魂》的新八、《黑子的篮球》的绿间都不错，但我最爱的眼镜男是《心理测量者》的宜野座伸元，超级性感。我还忘不了陪伴童年的《哆啦A梦》的大雄。15.18/6/2 13:54《世界第一初恋》的高野政宗和《高校星歌剧》的柊翼。', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10620', '0', '动漫史上最无存在感男二号，志村新八', '1作为眼镜架生存。2.作为银魂里的神级吐槽机器存活。3永远拿不到工资4人气投票里永远的失意者5总是谈不成的恋爱6对寺门通的崇拜，不仅仅是崇拜7永远陪在那个用毒鸡蛋刺激他的老姐身边。8即使在这个武士刀已经没用的时代也要坚定守护着的道场，以及这个没落道场里留下的珍贵回忆。9结识那个外星球的腹黑妹子，即使又贪吃又爱损自己，但那个家伙离开一定要拉住，那个家伙有危险就算自己更没用也要上前10来了就走不了的万事屋，愿意永远跟着那个不发工资的老板，银发天然卷的混蛋，有着宽厚肩膀的大哥，永远做那个小弟。', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10621', '0', '喜大普奔！银魂里的志村新八要做歌手啦！背后还有神秘组织为其撑', '要说到最近让小莫定下来截屏最多的一张脸那就是菅田将晖的帅颜啦~~~~话说，他参演的《银魂》还有半个月就要上映好期待啊因为菅田将晖日文发音“Soda”所以中国的粉丝亲切的叫他外号“苏打”最近又荣登万人评选的“心中国宝级帅哥”榜首果然大家都爱看帅哥啊娱乐圈里的“黄金定律”演而优则唱，唱而优则演在演艺界活动这么久的苏打这个月正式以“歌手”的身份出道啦！不过演员去唱歌还是慎重点为好想想某些演唱的车祸现场唱一首歌脱粉分分钟so easy~但对于菅田将晖的唱歌实力大家都放心常在影视剧里小露一手惊艳到不少路人粉《民王》里的武藤翔《悦音响起》里的天野空一《想吃什锦面》里的佐野雅志《何者》里的光太郎《奇迹，那天如此重要》里的HIDE话说菅田将晖能这么顺利出道自己的solo歌曲还是多亏了《奇迹》这部电影的主人公《奇迹》讲述了日本神秘组合GreeeeN成名前的故事因为在剧中主人公是主唱主创们为了不让观众因为音色而跳戏特地让演员参加两个月的声乐集训电影中出现的歌声均由主演们亲自完成为了宣传影片剧中四位演员也以“green boys”组合身份发首张专辑没想到歌声反响意外的好这也让唱片公司看到菅田将晖的潜力6月7日《从未见过的景色》歌手Soda首张个人solo专辑作为足球应援CM曲在各大电视台播出果然听到菅田将晖的歌声充满斗志呢~影片播出后也吸引不少人对这支神秘的组合产生兴趣之所以神秘是因为自GreeeeN出道以来都没有人知道他们是谁，长什么样子即使歌曲火到下载量拿到吉尼斯纪录颁奖都让其他人帮忙上台（难道是柯南里神秘的黑衣人组织？）原来这组合四人都是齿学部出身未来是以做牙医为目标不希望歌手的身份给他们生活造成困扰唱歌什么的只是爱好没错，只是爱好....为了纪念出道十年组合决定曝光成员的照片这可把粉丝激动坏了绝对是意外的惊喜然而。。。。之后又有人爆料说拿到真人照片市面上绝无仅有，就这一张，童叟无欺粉丝们含泪又激动一波~然而。。。。这一口明晃晃的大白牙因为不想露脸不参加商演、不参加宣传、不参加任何电视台节目在这种不炒作超低调的状态下单曲轻松上日本公信榜拿第一应了粉丝们的要求开巡回演唱会为了依然保持神秘身份他们就开了N场投影演唱会在活动期间每人都通过了牙科医生的考试除了歌手的身份外他们都成为了合格的牙医双重身份的设定果然适合写剧本~*《奇迹》剧照所以他们经常代言牙膏牙刷在组合官网上也会链接“日本齿科医师会”不会治牙的老大夫不是合格的歌手粉丝笑称“唱歌只是副业，牙医才是老本行”显然组合太沉浸于牙医的工作了导致这几年不怎么出歌粉丝们盼星星盼月亮，也盼不来一点动静有不少人都脱粉了好在上周官网发布消息今年8月13日巡回演唱会终于要开始啦！！普天同庆！喜大普奔！到时“green boys”组合也会出现在现场有四大帅哥助阵，想必这场演唱会要high翻天呐~PS：B站有影片，苏打MV出门左转网易云音乐，不蟹~~更多日本资讯，请关注早道日语微信（izaodao）', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10622', '0', '银魂图集 | 志村新八的历史原型，以及银魂即将登场的综艺节目', '回复『 志村新八 』进行今日签到长按可以复制哦文 / 旁白君 图 / 真的来源于网络新来的小伙伴，银魂定制二维码你不长按扫一扫关注嘛万事屋祝你生日快乐微信: gintama1212016年6月2日 曼珠妤 挥发油慧极必伤2016年6月3日 眭文昭混沌徐昕佳 Cindy祝你们生日快乐，节操掉不停！么么哒~ - 银 魂 最 新 动 态 -银魂竟然有综艺节目要登场了！下个月的今天，也就是7月2日！这节目名字叫「银魂 STATION(14分)」以下是日文的报道，旁白君最后有简单的译文版。番組では、“坂田銀時”をはじめとする「銀魂」のキャラクターたちが、視聴者からの悩みや相談を銀魂独自の世界観で回答するコーナーを展開。他にも、視聴者が選んだお題などに挑戦するコーナーなど、視聴者が参加できる企画を盛り込んだ構成となっています。“14分”というビミョーな番組尺で繰り広げられる、このバラエティ番組。果たして、TV放送に耐えられるのか!? 本当に放送がスタートできるのか!? 7月2日（土）からの放送をぜひともお楽しみに♪◆番組情報：「銀魂ステーション（14分）」7月放送開始！番組名：銀魂ステーション（14分）（ぎんたますてーしょん かっこじゅうよんぷん）放送予定：7月2日（土）スタート予定毎週（土）後7：50～後8：04 ほか※第1・第3（土）新話数放送出演：＜万事屋＞ 坂田銀時 、志村新八、神楽＜真選組＞ 近藤勲 、土方十四郎 、沖田総悟好啦，旁白君简单来说几句，这档节目写的很明显，14分，就是14分钟搞定的意思啦！然后会有银魂的声优们登场回答大家的问题.....你可以想象大家提问的都是些什么问题啊！！！时间是7月2日，每周六的19点50到20点04分。登场的人物你都看到了，万事屋和真选组的主角们！ 虽然银魂TV版继续的消息还没有传来，但至少有银魂的其他消息还是令人兴奋的呢！希望借着这档节目的播出，银魂能重新回到大家日常的生活中。话说今天旁白君带来了另外一个消息，你或许应该看下下一篇的微信！！揭秘了胧的过去......有事空知英秋的一个大坑啊！！- 重 温 银 魂 系 列 -目前重温至银魂第34集期待再次开启. . . 那让我们一起来看看志村新八的历史原型眼前这个男人是银魂中的主角，万事屋的吐槽男，志村新八！今天来说说这位戴眼镜的少年的历史原型吧，其实空知英秋在创作银魂的时候，一直是以恶搞为主的，各种历史人物都会被拿出来当槽点来吐，早期也惹来了一些尴尬的事情，因为银魂参考了很多日本历史上知名的人物，很多读者历史考试答题都回答的是银魂中的设定....好吧....后来据说相关部门来投诉了之类的.....不管怎样，我们来看看阿银的历史原型吧！！新八8月12日出生，166公分，55公斤，16岁。外貌与性格平时穿著为蓝白色和服袴。剑道道场「恒道馆」的少年馆长，以剑术为志向，为复兴道场兼学习武士道，因受到银时帮忙而成为万事屋成员。个性相当温柔，是个有礼貌的少年；但在追星时会变成一个热血男子汉。疯狂的迷恋歌手寺门通，为寺门通亲卫队队长。房间贴满了寺门通的海报、唱卡拉OK时只唱寺门通的歌曲。在没有进入「万事屋」与遇上银时前，在甜品店工作。当时他十分冒失，经常惹怒店长，是个将自尊和吐槽精神抛弃的打工仔。他经常被欺负，被形容为「很平凡、很普通、大众脸」，甚至被否定为人类，眼镜被视为本体，而肉身只是眼镜架。据阿银所述，新八的构造成分有95%是眼镜、3%是水、2%是垃圾。曾被批评没存在感是因为他不是「新一」而是新八。在一个不能缺少「吐槽」（ツッコミ）的《银魂》中负责吐槽，故有「吐槽八」与「眼镜吐槽男」（ツッコミメガネ）之称，也被称为「江户第一吐槽男」。在姊姊阿妙工作的夜总会假扮成女公关时化名八惠（香港称为「八千惠，中国大陆称为「八惠」）。以普通人的强度而言，新八具有相当的实力，但是周遭的人都太强，才会显得他十分平凡。剑术虽然练了好多年，但技术却一般，爆发的时候也是很厉害的。比常人的实力强两块昆布，是骑脚踏车姿势相当标准的小混混耕介学长3倍厉害。是个音痴，能够看见幽灵。因为一次事件而对猫耳萌娘有特殊僻好。十分喜欢姊姊，是个姐控。由于姊姊阿妙不擅家事，所以是个含煮饭在内的家务全能者。基本上包办万事屋的家务（除了做饭为三人轮流）。（做饭真的是轮流嘛？之前没注意！）剧情表现两年后篇依旧没变，但察觉身边的人有大幅变化，最后发现原来自己也被疣寄生。武器和银时一样，以木刀为武器其他资料在JUMP主办的三次人气投票结果中都是第八名，连作者都觉得是奇蹟。（这个我作证，前几天的海报上新八又一次是第八名！）历史原型永仓新八，原籍松前藩(今北海道松前郡)，为担任松前藩江户定府代理永仓勘次的次男。弘化3年(1846年)8歳进入冈田十松(第三代掌门人)的神道无念流剣术道场「撃剣馆」门下修习剑术，4年后为师死亡由继任第四代掌门人冈田助右卫门教导并授与切纸认可。安政3年(1856年)18歳升格为目录级等，成年后改名为新八。1857年获得「神道无念流免许皆传」认可后，次年为了精进剑术径行诸国武者修行之旅而脱藩，途中路经江戸牛込的天然理心流试卫馆道场，受武州多摩地方门人与近藤勇邀请成为试卫馆食客。历史原型——永仓新八新选组和近藤参加浪士组上京后，在新选组时期担任组织核心的第二队队长。由于与芹泽鸭同样握有神道无念流免许皆传的认可与实力，与芹沢为匪浅之亲交。元治元年（1864年），在冲田病倒、藤堂平助受伤的情况下，永仓新八仍像个狮子般和近藤勇、土方岁三在池田屋中奋力杀敌；断绝倒幕派浪人火烧京都的野心。池田屋事件后远近驰名的近藤等人，难免被世人与幕府捧的飘飘然，对队士和下属目空一切。对此感到遗憾的永仓，与原田左之助、岛田魁冒着违反局中法度切腹的风险脱离新选组住所向松平容保上书。认为近藤勇已违背了局中法度。收到投诉书的松平容保要求近藤勇改善，而近藤勇也视永仓等人为同志而非君臣的立场接受了永仓的谏言。油小路事件时永仓也参与其中，然而近藤勇亦私下交待「让藤堂活下来！」──毕竟藤堂也是由试卫馆时期便一同奋斗的伙伴。但是这样的密令到最后仍旧无法达成，藤堂平助依然在混战中战死。奋战、坚持、妥协而在冲田总司病重之际，永仓也负起带领第一队的重责大任。从1868年（庆应 4年）与新政府军战斗的戊辰战', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10623', '0', '《银魂》我的白夜叉男神（经）！', '坂田银时日本动漫《银魂》主人公，这个活跃在攘夷后期创造无数传奇的革命家，他有个令人闻风丧胆的名字——“白夜叉”现住歌舞伎町一条街，与神乐、志村新八经营万事屋，是个坚守自己武士道的男子汉！手握洞爷湖，左手搂着土方、右手搂着高杉（人生赢家？？）迷妹眼中的他 美化版（扣鼻子也这么帅(`・ω・´)）白夜叉模式迷妹立场：Gintoki！我！ 男！ 神！不 ！服 ！来！ 辩！为了不让越来越多的可爱萌妹子被这个傻叉给骗了，为了守护世界的和平，我来帮大家分析分析这个男神（经），我现在要爆要！猛！料！1.这个男人很不讲卫生你跟他讲话的时候，他都这样子，不管你喜不喜欢（好感度-10）2.这个男人喜欢赌博偷偷把员工的工资都给玩没了（好感度-10 生命值-20）头上一把刀、一把雨伞，拿去卖赚了~ 迷妹：即使他赌博..他挖那什么..我！也！爱！他!3.这个男人喜欢扮女装（好感度±10）我可能要弯了...4.这个男人摸过很多女人的胸（好感度-100）扫黄行动开始，犯案分子：男，年龄25-29岁，无业游民，喜欢赌博，经常拖欠员工工资，喜欢扮女装，和人说话喜欢扣XX，目前正在潜逃中，已有多名女子遭到袭击犯案分子至今生死不明（笑）可能仍在潜逃，应该会继续作案（这个男人没有一点求生欲吗...）这是犯逃照片，如有发现者，请立即联系我，举报有奖（笑）不说了，我去看神乐女神了，下次见！！', '', '4696', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10624', '0', '说一说一米六那些事 利威尔·阿克曼。进击的巨人！', '利威尔是调查兵团的士兵长，同时也是人类最强士兵，谏山创在公式书中公布的能力值评价是“格斗数11”格斗数值超过十的只有兵长一个人。其他士兵队能力很强的三笠给出过这样的评价，“她的体能能跟一百个普通士兵相等，失去他对于人类来说是一个重大的损失…”相比之下，对利威尔的评价则是 “他一个人的战力能够顶一个旅团！（不同于世界的旅团，所以我们并不知道人数）我们都知道兵长在进入调查兵团之前曾经是地下街里有名的小混混，他是阿克曼家族后裔，母亲为库谢尔·阿克曼，在阿克曼家族没落后，被迫成为一名妓女，父亲不详，舅舅为凯尼·阿克曼。之后由于各种原因被埃尔文带入调查兵团。目睹法兰和伊莎贝尔被巨人杀死后在暴怒中斩杀多匹巨人之后决定跟从埃尔文史密斯，现服役于调查兵团。利威尔班就是 他亲自挑选的人，亲自带领的人。法兰和伊莎贝尔某种意义上算是利威尔班最早成员了，利威尔班的成员由利威尔.奥路欧·博札特 埃尔德·金 根塔·舒尔茨 佩特拉·拉尔 艾伦·耶格尔组成的 以上所有人除了兵长和艾伦均已牺牲！ 随后利威尔班又被称为利威尔少年班，其成员有 艾伦耶格尔 赫利斯塔兰斯 三笠阿克曼 阿明 阿诺德 让 基尔希斯坦 康尼 斯普林格 沙夏 布劳斯 （目前均未阵亡! 汗)终结一下就是利威尔班是以猎杀巨人为目标而创立的精英作战团队，其成员个个身怀绝技，同时也视死如归，他们为壁内人类的生存做出了重要贡献。就目前来说，兵长是无敌的，还没有那个巨人可以打得赢他，就连目前被称为最强的兽之巨人，在兵长目前就是被虐的，因为兵长的速度太快了，兽巨根本来不及硬化，就被兵长切了个稀巴烂。说了这么多，我们来说一说兵长一些有趣的事情吧 我们都知道，兵长拿杯子的方式和别人不同，是这样的为什么呢，作者说了，兵长小时候因为是在贫民街的关系，相当向往美好的生活，好不容易拿到的红茶和茶杯组合，想说太好了总于可以喝了的时候，握住杯柄的瞬间，杯柄断了导致茶杯破裂造成心理创伤，以至于以后拿杯子都只敢拿杯身，不敢碰杯柄在别人采访作者的时候问到兵长人气超过了主角这个问题上，谏山创是怎么回答的：一开始对于利威尔的角色塑造确实下了点功夫，但没想到人气竟如此之高，高得都让我想让利威尔掉点粉了呢（笑）大家也多多选择一下其他角色啊！关于“有没有觉得TV化以后兵长的人气更上一层楼了呢”的回答：确实，在有声的表现下人物的形象变得更加深刻了。可以说在看动画的时候好像又重新认识了一下这个角色，这时候就会想“原来他是这么一种感觉啊！”前方有大批奇行种逼近利威尔条件反射往前走了一步，侧过脸对自己身后说【小鬼，别动，呆在我身后】同伴看着他的背影，目光有些涣散。他不忍心告诉他啊。利威尔兵长，你背后，没有人啊。一个三十四岁的男人。一个身高只有一米六的矮子。一个中分得完全没有美感的死鱼眼。一个常年垮着脸毫无表情的绝对面瘫。一个洁癖到了极点杀完巨人还要擦刀的重度患者。一个混混出身不留口德随兴所至的毒舌。一个富有S倾向一脚踹掉艾伦门牙的神经质。我并没有夸大事实，只是忘记了一些更重要的事。他的披风上映着蓝白双色的自由之翼。他的双刀，砍下的是寂夜，带来的是黎明。他的双眼不是无神，只是常年隐藏真实的自己从而过于深邃。他永远不会是个完美的英雄，报纸上大张旗鼓大肆宣扬的人不像他。但是无可否认，他是人类最强。他是囚笼中的人类反击巨人追寻自由的希望所在。他叫利威尔，但我们更习惯称他为——兵长以上多数内容来之进击的巨人贴吧！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10625', '0', '二次元男神专辑：利威尔·阿克曼', '《进击的巨人》中的利威尔·阿克曼号称人类最强的士兵，有传言他一个人的战斗力相当于一个旅，拥有例如投掷抛弃刀片损伤巨人视力后斩杀，用立体机动装置使用螺旋斩等优秀技术，速度极快，战斗往往在一瞬间结束（能够单挑兽之巨人，将其大卸八块，并一度活捉其真身）。有严重洁癖，个性神经质又粗鲁，令人难以接近；虽然实力强大，但仍遵从上级命令，并不因此而任性妄为。虽然外表冷酷且不容易透露出表情，但其实内心很细腻且注重感情，十分关心队友（有时看到队友阵亡后会暴怒）。关注“一起来看动漫吧”了解更多动漫资讯哦！图源网络如有侵权请告知删除', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10626', '0', '《进击的巨人》兵长——利威尔阿克曼获得超高人气的原因是身高？', '小编在看《进击的巨人》的时候只要出现兵长B站的弹幕上就会有人刷大爱兵长之类的话，那他是凭什么获得这么高的人气，而且还超越了主角。 1.高冷的气质 刚出场的时候就十分的高冷，而且整天板着个脸，如果不认识的人看到可能都会被吓跑。感觉这非常符合黑马王子的气质，很受女性观众的喜爱。像男主那种只有冲劲的就像一个傻大个虽然个子也不高。 兵长2.超强的实力 高冷的个性配上超强的实力堪称绝配，兵长作为调查兵团的士兵长，有着极强的领导能力，而且很有领导的魅力，这也是十分吸引人气的一点。 兵长3.超级洁癖症 每一个让人喜爱的角色都会有那么一两个让人又爱又恨的怪癖，比如兵长的洁癖症，但是动漫里面有一个场景，就是一个士兵被巨人给吃掉了一半，但是还有意识，想握住兵长的手说最后的遗言，而兵长毫不犹豫的就握住了满是鲜血与泥土的手，这里就又给兵长一个加分项，那就是暖心，每一个高人气的人物必备的特质。 兵长4、痞子般的吐槽 每次都会突然就爆粗口，给人形成了一种明显的反差，这也是人气这么高的原因。 兵长5、与其不符合的身高 一般像黑马王子类的人物都是高黑帅的类型，虽然他很帅而且性格各个方面都很有特点，但是居然不是高个子，这应该很戏剧性了，这也增加了人物的辨识度，变相的增加了人物的人气。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10627', '0', '利威尔·阿克曼一人战斗力可达一个旅', '外貌特征为黑色短发，肤色偏白，脸型小（出自公式书），五官立体而深邃，时常皱眉，眼下有黑眼圈。瞳色为深蓝色或灰蓝色（在op2中有特写，故得出）。实力拥有例如投掷抛弃刀片损伤巨人视力后斩杀，用立体机动装置使用螺旋斩等优秀技术，速度极快，战斗往往在一瞬间结束。一人战斗力可达一个旅，15秒之内可以歼灭了两个巨人，可以一口气歼灭3个奇行种，5分钟之内让女巨人几乎不能动弹。虽然外表冷酷且不容易透露出表情，但其实内心很细腻且注重感情。为了顾全大局，在看到特别作战小组的部下们皆阵亡后仍面不改色的向女巨人进攻。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10628', '0', '「进击的巨人」三笠&利威尔兵长的阿克曼家族之谜！', '阿克曼家族是巨人的副产物。为什么三笠和利威尔体内的力量觉醒了，而同是阿克曼一族的三笠父亲却没有觉醒呢？今天我们就对阿克曼家族的由来以及三笠父亲没有觉醒的原因做进一步讨论。阿克曼家族实际是王族为了保护自己的权利和地位通过科学和医学手段特别创造出来的人工人类。什么基因改造啊，强化人，超人，变种人，你觉得那个酷那就是哪一个！！！而巨人也是这样被人类创造出来的。不同点就在于巨人拥有巨大的体形和巨大的力量。而作为副产物的阿克曼族人在正常人的身体之下拥有强大的肌肉，以及惊人的骨密度和完美的身体控制能力。可以说是拥有常人外形的巨人吧。然而这么强大的阿克曼家族却无法被王族窜改记忆。这一点让王族非常担忧~~于是就要将自己的这支王牌君给赶尽杀绝。以绝后患！！！后来利威尔的叔父肯尼宣誓阿克曼家族将誓死效忠雷伊斯家族才让阿克曼家族得以延续。那么三笠的父亲为什么没有觉醒？因为觉醒这个强大的能力是需要条件的。三笠在自己的父母被一伙强盗杀死之后，前来营救的艾伦也要被杀死之时，彻底爆发了自己体内的洪荒之力。而利威尔是出生地下街的小混混，自然得应付各种我们想象不了的残酷场面。觉醒是必然的！而三笠的父亲很显然没有遇到过那种惊心动魄，生死攸关的场景，所以没有觉醒自己的力量！不知道小伙伴们有什么想法不妨评论留言告诉我！喜欢的请点赞收藏哦！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10629', '0', '小T官测：进击的巨人 艾伦·耶格尔&利威尔·阿克曼', '万代+小T官方合作新品评测魂Buddies 进击的巨人 艾伦·耶格尔 利威尔·阿克曼特别鸣谢Bandai Tamashii Nation提供本次评测产品。本评测为小T文艺社区官方专稿，转载请注明出处并保留全部文字从日本⇒走向世界。 全新的魂系列9厘米成品系列！通过精致细腻的头身比例（目测4头身），成品头约“9厘米”，并付加人物相关背景展示支架，全新的“魂哥们”系列就这么悄无声息的发售了。官方宣传该系列有较高的收藏品完成图和良心的定价及多收藏作品线同时发表，尺寸适中，易于展示。这……从一开始就告诉我们一个大坑就这么来了。■全线产品规格高度：约90毫米 材质：ABS，PVC制成■全线包括车身底座，底座背景价格：各1500日元全高：约90mm材质：PVC、ABS发售中以下转自百科107年前（743年），世界上突然出现了人类的天敌“巨人”。面临着生存危机而残存下来的人类逃到了一个地方，盖起了三重巨大的城墙。人们在这隔绝的环境里享受了一百多年的和平，直到艾伦·耶格尔十岁那年，60米高的“超大型巨人”突然出现，以压倒性的力量破坏城门，其后瞬间消失，巨人们成群的冲进墙内捕食人类。艾伦亲眼看着人们以及自己的母亲被巨人吞食，怀着对巨人无法形容的憎恨和痛恨，誓言杀死所有巨人。城墙崩坏的两年后，艾伦加入104期训练兵团学习和巨人战斗的技术。在训练兵团的三年，艾伦在同期训练兵里有着其他人无法比拟的强悍精神力。即使亲眼见过地狱也依然勇敢地向巨人挑战的艾伦，如愿以偿加入了向往已久的调查兵团。在他正做着到墙壁的外面去这个梦的时候，毁坏墙壁的超大巨人出现了。艾伦·耶格尔是动漫作品《进击的巨人》中主人公。曾经生活在西甘锡纳区的少年。与三笠·阿克曼、阿明·阿诺德是从小的玩伴，关系很好。拥有强韧的精神力量与非凡的行动力，对墙壁外的世界拥有比任何人都要强烈的憧憬，从小便立志要加入调查兵团。在目睹了母亲被巨人吞食后，立誓要驱逐所有的巨人。曾被父亲注射过与巨人能力有关的一针，拥有着可能揭示巨人秘密的地下室钥匙。利威尔·阿克曼编辑调查兵团士兵长、特别作战班班长,通称“利威尔兵长”。原是王都地下街的混混，后被调查兵团第13代团长艾尔文·史密斯带回，成为调查兵团中的一员。同时是兵团特别作战小组班长，实力强到逆天，拥有“人类最强的士兵”之称号。战斗力相当于一个旅团（约1000人）。是主角艾伦·耶格尔的监护人。虽然外表冷酷且没有什么表情，但其实内心细腻并且很关爱部下。处事冷静果断，顾全大局。战斗时常以速度取胜，拥有无人能企及的旋斩技术，往往能在瞬间致巨人于死地。一、盒照主体细节本系列最大特点，可以采用不同搭建方式展示人物与地台直接粘合无法取下，地台贴纸及背景均为纸质无贴膜等加工工艺，为了长期展示需规避阳光及水源对纸质印刷品的伤害。一些平常看不见的细节还是全部还原的==============================================================由于安装背景后图片无法展示背部造型，在未安装下进行拍照。展示示例兵长一米六的身材还是能看出点的全新的收藏系列，感觉会无穷无尽的出下去，对于系列编号强迫症玩家来说简直是噩梦般的存在，入坑需谨慎。当然只是看到喜欢角色买一两个玩玩的玩家这系列非常合适，整齐姿势、尺寸统一外带背景展示非常方便。相对于B社自家GUNDAM立盒蛋系列及眼睛厂WCF系列，本系列尺寸及展示方便度令人满意，唯一的缺点应该就算是纸制品的处理和相对喜人的定价了~~小编语：没有三爷差评！', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10630', '0', '我的世界进击的巨人利威尔阿克曼皮肤下载', '我的世界是一款自由度非常高的沙盒类游戏，有着非常高的人气。今天口袋小编glp给大家带来我的世界的一款皮肤，英雄联盟杰斯皮肤，下面小编放出这款皮肤的下载地址，希望大家喜欢。皮肤背景：利威尔在进入兵团前是首都地下街的混混，遇上团长后加入调查兵团。外表冷酷且不容易透露出表情但其实内心很细腻且注重感情。为了顾全大局，在看到特别作战小组皆阵亡后仍能继续向女巨人进攻。第一次捕获女巨人作战事件中，迅速砍伤女巨人多处部位的肌肉，后因阻止女巨人攻击三笠而导致脚受伤暂时退居二线，多半时候以便服出场。皮肤介绍：皮肤下载：我的世界手机版建筑汇总 手机版存档大全我的世界手机版跑酷地图 跑酷地图存档合集我的世界1.8.1mod大全下载 所有经典MOD下载我的世界喷泉设计图大全 9大喷泉设计汇总我的世界吉尔尼斯之战地图存档下载 ', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10631', '0', '居然有真人撞脸《进击的巨人》？这些人神还原利威尔和阿妮！', '记得网上曾经流传的《进击的巨人》巨人表情包吗？在漫画中惊现我们经常使用的一个“小学生狂写考卷表情包”：靠左边第二排的小朋友，是不是还原了表情包？漫画家肯定也日常逛逛微博，在画漫画的时候，皮一下也是很开心。不过《进击的巨人》漫画和动画，在人物上都很接近真实的人，有不少人，就被提出来，是真的靠颜值在还原动画角色，不是靠装备。下面一起来看看！团长埃尔文有人发现，团长很好找！埃尔文史密斯跟《比利·林恩的中场战事》的男主，真的像！从发色瞳色、眉毛走向、鼻梁形状、脸型、气质，这简直就是打破次元壁的版本！小哥你什么时候认识的谏山创？Leonhartani（ins名称）这是个很喜欢阿妮的妹子。本人长的跟阿妮也非常像：本人，进入了cos状态之后：还能cos阿妮的高潮脸：这简直就是阿妮本人了！！！利威尔作为《进击的巨人》的看点担当，兵长俘获了一票粉丝，其中就有能神还原他的尹正：哈哈哈哈哈，当时看到这张图，是真的笑死了，谁把兵长画成这样的？除了真人撞脸之外，还有不少的爆笑撞脸：这是《金刚》的海报，对比一下《进击的巨人》：我觉得谏山创不仅逛微博，还很喜欢看电影，每次灵机一动，就是不少的素材。粉丝也能发现，这样真的是非常好了。还有一个更搞笑的撞脸猩猩，真的猩猩长上面这样，漫画里↓哈哈哈哈哈哈我怀疑谏山创没事也很喜欢看动物世界！虽然画的是巨人，可是在表达自己的皮。你还发现有什么好玩的他的皮？', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10632', '0', '进击的巨人为2018新作品准备了新的利威尔·阿克曼素描', '在马上进入新的一年里，只有剩下的几天时间，看起来“进击的巨人”已经准备就绪。 2017年是该系列是一个让我们期待已久的作品，2018年将延续更多的动画和漫画呈现在广大粉丝免签。而且，如果每个粉丝都是幸运的，他们将会得到更多的利威尔·阿克曼素描。进击的巨人要知道，因为进击的巨人的创造者只是分享一个士兵的素描。在Twitter上，粉丝们开始传阅最近的素描Hajime Isayama在利威尔·阿克曼的年终杂志专辑。下面的素描作品显示了自从他介绍兵长回来以后，艺术家有多少改进。这张黑白的草图将重点放在利威尔·阿克曼上面，因为可怕的兵长向上看，他用剑武装到左肩上。进击的巨人当然，如果没有他的出现，剧情不会是完整的。如果不是因为皱眉，那么这个兵长就没有表情了，但是，嘿，生活在一个充满了巨人的世界末日的世界里，可以做到这一点。进击的巨人最近，兵长已经从巨人的进攻中失踪了，但领导者回来之前只是一个时间问题。由于惊人的渗透，该系列的漫画正处于重大转变的风口浪尖，“进击的巨人”将于明年发布第三季。这个节目的第三季的关键视觉特别关注兵长，所以漫迷们可以期待2018年这名兵长的复出。进击的巨人对于那些不熟悉进击的巨人的人来说，这个系列最初是由斋山肇创造的。这个系列从2009年开始在Kondansha的Bessatsu少年杂志中运行，截至2017年，已经收录到23卷。它是在一个世界里，人类最后的残余生活在一个城墙内，以逃离巨人的危险，巨人怪物吃人类的种族。 Eren Yeager在Titans突破墙壁攻击他的家乡之后，和他的两个童年朋友Mikasa和Armin一起加入了军队。进击的巨人这个系列被改编成了Wit Studio和Production I.G的动画。第三季度在2018年首映。截至2017年，漫画已售出6800万册，并在全球范围内获得重大的商业成功。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10633', '0', '为什么《进击的巨人》里，男神不是艾伦是兵长利威尔？', '《进击的巨人》第二季四月即将播出，是时候来讨论一下我们的男神兵长了！巨人的男主虽然是艾伦，但只有兵长才是男神！那么这是为什么呢？迷妹们后退，我要开始花痴了！兵长的人设是人类最强，但官方给了他萌萌哒身高，这两者之间的反差萌，萌了一脸血！尤其是兵长一瞬间便可以打倒两个巨人！他出场就是圈粉的开始！调查兵团主力走来，一脸嫌弃的兵长。然后立马切换到和巨人的战斗一脸嫌弃却又干净利落地杀着巨人，还不耐烦的嘲讽，抖s拽炸天！手上溅了巨人的血，很不爽的吐槽：脏死了。男神特质！在抖s又洁癖的同时，又人情味十足：当部下临死前问他自己有没有为人类做出贡献并向他伸出满是鲜血的手时，他毫不犹豫的紧紧握上那只手，激动地告诉他：你做了很多了，今后我会带着你的意志继续战斗地球最强人类的承诺，驱逐巨人！强大又充满人情味，兵长在这些优点之外还有一点：尊重别人的决定艾伦在马背上被女巨人，兵长没有卖弄经验，或以大压人，而是对生命和选择给予一个新人权利，并接受它所带来的结果！！最后，也是非常重要的一点！兵长的声优是神谷浩史！神谷以其高傲（ao）冷漠（jiao）的声线为兵长的角色加分不少，以其独特的声音（非常卖萌卖腐）赢得了很多cp粉丝！！！多少人一脚踏进了利艾爬不出来！嘛，不管大家怎么站利艾，我还是站这对的一个酷爱奇行种一个自称奇行种！这才是绝配！本文由盖饭GetFun原创内容团队提供转载请注明来源：盖饭GetFun', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10634', '0', '配角要上位就没主角啥事系列！盘点【配角很火】的动漫！', '陈独秀说得好“不想当主角的配角，不是好配角”。动漫里时不时就有抢戏抢关注度的配角存在。更有甚者因为配角人气高，把配角升为主角去做新的动漫。我来盘点几部配角热度高的番吧。雷姆——《Re：从零开始的异世界生活》御坂美琴——《魔法禁书目录》利威尔·阿克曼——《进击的巨人》怪盗基德——《名侦探柯南》时崎狂三——《约会大作战》康纳卡姆依——《小林家的龙女仆》流川枫——《灌篮高手》就记得那么多，剩下有请老司机补充吧ps：想了解最新人气声优排名点击跳转我另一篇文章', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10635', '0', '布鲁斯·阿克曼丨防止英国分裂的宪法方案', '耶鲁大学斯特林法学与政治学教授布鲁斯·阿克曼法意导言2018年6月7日，英国《政治季刊》发布了布鲁斯·阿克曼在伦敦大学玛丽皇后学院人文社会科学研究所的年度讲座记录。阿克曼的这次讲座探究了英国脱欧后的基本宪制选择，强调了爱尔兰、苏格兰和威尔士的地方自治诉求所带来的困境。阿克曼认为，只有一个经过特别选举的、独立于议会的制宪会议，才能适格且合法地打造一部以正式方式处理这些困境的成文宪法。一旦制宪会议开始运作，其提案应提交全民公决批准，但提交前应采取特别措施以鼓励选民理性决定。鉴于上次公决中误导信息的传播，英国可采取新设“协商日”这一全国性假日。这一天里，选民聚集在社区中心讨论会议提案。大量的社会科学实验证明一天的商讨时间能够极大的增强公众理解，以提高公投中赞成票的民主权威。以下系对阿克曼讲座记录的翻译。防止英国分裂的宪法方案作者：Bruce Ackerman翻译：史庆所有人都沉浸在讨论脱欧，但我想请你们花一点时间暂时脱离当下的争论，与我一同直面在英国重新确认其对于欧盟的政治独立后，所要面对的宪制问题。我更关注展现基础性问题，而不是提供确定的答案。在我演讲的最后，我会解释为什么我的诊断方案使我诉诸于立即行动。就在特蕾莎·梅以及她的继任者在接下来的几年内为脱欧问题发愁时，议会应当颁布法案，建立一个经特别选举的制宪会议以应对英国直面的脱欧困境，并制定一部提交给选举人进行全民公决的成文宪法。毫无疑问这是一个温和的建议，但是直到讲座最后部分我才会对它作解释。先不论它的优点，我希望你们会发现我对脱欧后问题的探索，对你们厘清关于这一问题的思考有所帮助。困境：英国非对称联邦制与地方自治诉求的矛盾英国正处于历史的拐点。三个世纪以来，它的几座岛屿一直处于帝国的中心，并在美国独立后不得不定期处理殖民地的独立问题。但这一次情况则刚好相反——欧盟虽不是帝国，确是欧洲的主导力量，而英国则是一个崭新的独立国家，在决定走自己的路后正试图重建一个宪制政府。然而它却面临着一个独特的问题。基于一系列历史原因，联合王国已经陷入了一种奇怪的制度安排。在这里，居住在苏格兰的五百万人，威尔士的三百万人以及爱尔兰的两百万人正适用重要的自治规则，而居住在英格兰的五千两百万人却没有被授予自治——这称为空间非对称联邦制（spatially asymmetric federalism）。同时，这三个区域的自治程度差异显著——这称为定性非对称联邦制（qualitatively asymmetric federalism）。2017年英国大选各政党支持情况的地理/选区/人口分布 来源：必应双重的不对称产生一系列严重问题。首先，下议院可以在任何时候改变联邦的定性条款，即便英格兰人没有感受到深远影响苏格兰、爱尔兰或威尔士的决定的后果。这就导致这三类少数群体会组织地方性政党来捍卫或扩大他们的自主权。如果这些地方性政党赢得了他们地区在下议院的多数席，特别是在像自由民主党这样相对较小的政党赢得了一些席位的时候，那么对国家导向的保守党或工党而言，要形成稳定的多数政府将困难许多。现在，特蕾莎·梅的孱弱政府被视为一个例外，因为大多数情况还是一个强势的首相领导并得到议会明确多数的支持。但是在接下来数十年里，不对称联邦的张力将使得“梅型政府”更为普遍——首相与各地区不断达成临时协议以赢得反对党一再提出的不信任挑战。这样的不择手段反过来则会导致长期的优柔寡断，并不时遭到被疏远的愤怒的英格兰选民的抗议。他们反感与地方主义者达成临时协议，并会倾向于把票投给具有煽动力的领袖——这些领袖宣称只要投给他们在野党，他就承诺给他们一个“强大而果断的政府”。时不时被极具煽动性的政治言论影响，政治脆弱的张力不仅会反噬自身，还会破坏英国公民身份的传统认知。在过去的三个世纪，大部分英国人将自己视为一个我称之为启蒙运动国家的公民——在这里他们可以自由地过上丰富多彩的宗教和文化生活，而不会危及他们作为不列颠民族里平等成员的地位。这种多元文化公民的启蒙思想虽经常被现实世界的事实所掩盖，但它仍然是英国传统的宝贵方面——在世界上许多地方，它一再被不同的思想挑战。苏格兰民族党现任党魁尼古拉·斯特金，该党宣称以苏格兰独立为主要政治目标来源：必应在十八世纪早期，约翰·哥特弗雷德·赫尔德（Johann Gottfried Herder）等思想家以及他们的众多追随者即认为国家应当放弃建立广泛多元生活方式的念头，各个国家应致力于保护区域范围内的那一个主导文化。我将之称为国家单一文化主义，在上世纪进程中，其多次展现出强大的吸引力。一战后，它成为哈布斯堡王朝分裂为匈牙利、捷克斯洛伐克以及南斯拉夫的意识形态基础，并在纳粹宣传的文化霸权中达到了令人生畏的巅峰。如今，我们还能看到其在世界各地的强力复兴。联合起来防止另一种单一文化军国主义的爆发固然是我们的首要任务，但本次讲座关注的是一个不易察觉的危险：英国不会很快变成纳粹，但它的启蒙思想很可能会被不对称联邦主义的政治张力所产生的、与其冲突的单一文化主义所侵蚀。由于苏格兰和北爱尔兰政府在议会中受到英格兰多数的掌控，地方政党会通过强调自治在保全苏格兰和爱尔兰身份认同中的重要作用，愈发捍卫地方自治。这些对地方自治下的单一文化的捍卫会鼓励英格兰地区的文化民族主义者去获得更多的政治支持。由于相互竞争的单一文化修辞逐渐开始主导政治，他们将损害启蒙思想。这一思想认为尽管存在实在的文化差异，一国内的居民愿意将彼此看待为大不列颠的平等公民，并准备同心协力解决他们的共同问题。而不断升级的单一文化竞争将导致一个不那么雄心勃勃的思想的崛起，可称之为克制的单一文化主义。这种方式下，苏格兰人民会授予居住于他们土地上的英格兰人基本权利，反之亦然。然而即便这些不愿意墨守成规的人被尊重对待，他们也会发现将自己视为参与启蒙运功的同胞将愈发困难。相反，他们会将自己视为一个生活在愈发陌生的外国土地上进行权利分担的局外人。防止相互疏远这一危险的方法是让各方深信地方自治的条款在现有的宪制安排中已根深蒂固，而且他们无需通过激进的政治组织来保护他们的自治权利。但是这种保证在目前的议会结构中难以实现，因为英格兰有绝对多数的选票，而下议院的执政党很可能发现文化民族主义符合其短期利益。那么，应如何避免联合王国的分裂？方案一：划分省份以建立对称联', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10636', '0', '冷酷的表情强大的战斗力的他', '利威尔·阿克曼，漫画《进击的巨人》 [1]  及改编动画、游戏中的角色，进击的巨人外传《无悔的选择》主人公。人类最强的士兵，战斗力相当于一个旅。帕拉蒂岛 城墙内调查兵团的士兵长、调查兵团特别作战班利威尔班班长，通称“利威尔兵长”或“利威尔士兵长”。原是王都地下街的混混，后被调查兵团第13代团长艾尔文·史密斯带回，成为调查兵团中的一员原型为《守望者》中的罗夏（Rorschach），名字则是取自电影《基督营》（Jesus Camp）中的主角少年Levi。利威尔·阿克曼，阿克曼家族后裔，母亲为库谢尔·阿克曼，在阿克曼家族没落后，被迫成为一名妓女，父亲不详，舅舅为中央宪兵队长凯尼∙阿克曼。利威尔早年曾经是王都地下街里很有名的混混，之后由于各种原因被艾尔文带入调查兵团。目睹法兰和伊莎贝尔被巨人杀死后在暴怒中将奇行种大卸八块，后决定跟从艾尔文·史密斯，现服役于调查兵团（请参考《无悔的选择》）。后任调查兵团士兵长（台湾东立译“士官长”），同时是兵团特别作战班班长。身材矮小，却拥有“人类最强的士兵”之称号。带领精英班利威尔班战斗，后因保护艾伦与女巨人战斗，利威尔班除利威尔自己、艾伦外全灭。相貌衣着特征为黑色短发，肤色偏白，脸型小（出自公式书），五官立体而深邃，时常皱眉，眼下有黑眼圈。瞳色为深蓝色或灰蓝色（在op2中有特写，故得出）。相貌“绮丽”（出自谏山创访谈）性格特点有严重洁癖，打扫卫生时会戴上口罩、头罩，对打扫的要求非常高，审讯犯人时会戴上围裙，斩杀巨人后会用手帕擦拭身上沾到的巨人之血，尽管如此，却会在自己的部下临终前，毫不犹豫的握住其沾满血的手，并郑重地答应会消灭世界上所有巨人。佩特拉形容利威尔的个性神经质又粗鲁，令人难以接近；虽然实力强大，但仍遵从上级命令，并不因此而任性妄为。虽然外表冷酷且不容易透露出表情，但其实内心很细腻且注重感情，十分关心队友（有时看到队友阵亡后会暴怒）。为了顾全大局，在看到特别作战小组的部下们皆阵亡后仍面不改色的向女巨人进攻。将特别作战小组某位成员战斗服的徽章拆下来，之後因故交给了一位士兵，并说明调查兵团的徽章就是战死的队友们曾经活过的证明，因此利威尔并不像外表那样冷酷。之後再与凯尼对决完使其重伤逃走後，向韩吉说明向其借用的直属3名手下都因此阵亡，感到自责并向韩吉道歉。人类最强的士兵，被称为“人类最强”，有传言他一个人的战斗力相当于一个旅（不同于现实世界的旅团）拥有例如投掷抛弃刀片损伤巨人视力后斩杀，用立体机动装置使用螺旋斩等优秀技术，速度极快，战斗往往在一瞬间结束（能够单挑兽之巨人，将其大卸八块，并一度活捉其真身）。。', '', '4688', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10637', '0', '暗杀教室：细数那些看上去差点就能杀死杀老师的计划！', '《暗杀教室》又名《三年E班》自从2014年动画化后迅速成为当时的霸权番。来历未知的章鱼怪物声称自己是炸毁月球的元凶，并宣布自己1年后将会炸毁地球，而阻止自己的唯一方法就是被椚丘中学3年E班的学生杀死。暗杀教室这个章鱼怪物的存在超越了目前人类所认知的范围，拥有20马赫的移动速度，虽然力气很小，但是在这样的移动速度下，只要他不想，几乎没有人能有任何手段杀害他。就是这样的存在却主动提出要教导这群学生们杀死自己的手段和方法，随着故事的展开，杀老师的身世也逐渐揭开。在教导学生的过程中，学生们利用学到的知识和来自政府的帮助，一次又一次的制定了消灭杀老师的计划，有几次看上去就快成功了。1、冲绳海岛3天2夜旅行暗杀计划（第一季第17-18集）计划完成指数：★★★★结果：失败计划：1、精神压制：搜集杀老师的羞耻行为（看小黄书、女装、炸纸巾），让他陷入精神崩溃状态2、限制移动：计划实行的地点是远离冲绳岛岸的水上教堂，在杀老师精神崩溃的时候让水浸湿他的身体，在按约定打断他的7根触手；教堂倒塌后学生们用水上飞板组成水墙，再用弹雨封住杀老师的动作。3、必杀一击：最后由两位狙击手在远处对杀老师进行狙击。失败原因：杀老师最终杀手锏：完全防御形态（如下）完全防御形态2、“堀部营”杀老师的兄弟+转学生攻击计划（第一季第11集）计划完成指数：★★★结果：失败计划：崛部是被植入了触手的小孩子，属于后天植入型，精神不稳定，需要定时服药。其速度与杀老师相当，具有与其相等的力量。同时作为转学生进入E班，这样杀老师就不能对崛部还手，只能被动防御。失败原因：对杀老师有效的匕首和弹丸也同样对崛部有效，杀老师利用了这一点。崛部营3、“自律思考固定炮台”智能武器攻击计划（第一季第9集）计划完成指数：★★结果：失败计划：“自律思考固定炮台”作为从挪威转学而来的学生，参与杀死杀老师的行动中，由于“她”是登记在册的学生，所以杀老师不准对“她”反击。其实是高科技机械暗杀者，拥有世界最先进的AI系统，能够自主进化，对目标的动作进行自主分析，逐渐增加命中几率。失败原因：上课期间不间断进行攻击，打扰学生上课，被学生们用胶带封住武器装备。自律思考固定炮台4、死神人质暗杀计划计划完成指数：★★结果：失败计划：世界第一的杀手“死神”被委托对杀老师进行暗杀计划，先说服伊丽娜老师与自己合作，将学生们诱骗到地下水道并抓起来，布置好陷阱，再将学生们作为人质，引诱杀老师上钩。将杀老师和学生们一起关进特殊材质的地牢里，再从中央控制室里打开水闸，利用强大的水压连同学生将杀老师一起杀死。失败原因：将学生也卷入陷阱，危害到学生们的性命，执行最终计划时被乌间老师组织，错过机会。死神学生们为了完成100亿奖金的任务，也为了自己的未来，不断学习新的知识用于暗杀；而杀老师也尽自己所能，为学生们提供最好的学习环境，根据每个人的状况制定学习计划。一方是不断渴求知识的学生，一方是竭力给予知识的老师，虽然中间的连接点较奇葩，但是这就是最理想的学习氛围。喜欢本期文章的小伙伴，欢迎关注！点赞！评论！转发！收藏！', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10638', '0', '暗杀教室：细数杀老师那些令人哭笑不得的弱点！', '动漫《暗杀教室》中塑造了一个人气极高的章鱼型超生物，该生物宣称自己将在明年炸毁地球，同时具有让全世界国家都束手无策的超能力。虽然他宣称能炸毁地球，且拥有20马赫的移动速度，但是却是个性格古怪，心地善良，经常闹出笑话的老师。女装癖就算拥有这么强大的力量，可是在学生们的观察下，他拥有着不少的弱点，其中有些弱点让人哭笑不得。气量很小刚开始接触学生时，非常嫉妒乌间老师在学生们中的高人气，深度怀疑乌间想要夺走自己在学生心中的地位。嫉妒心触手几乎没有力道由于过度强化速度的关系，自己的触手几乎没有力量，所以有一个非常大的弱点：只要自己的触手都被按住（即使是小孩子），就会无法移动。但是由于速度太快，只要他不愿意，几乎没有任何手段能抓住他。与赤羽业初遇时被戏耍，心情失落时敲着墙壁，但是由于软绵绵的没有力道，翻出沉闷的声音，被学生投诉影响考试。拳头太软晕所有的交通工具由于自己的超快速度，一个人出行不需要任何交通工具，近处直接走，远处直接飞，而且带一两个人都没有问题。导致适应不了任何交通工具，有时为了陪伴学生才不得不乘交通工具。因为会晕，所以经常被学生作为暗杀行动的前戏，消耗他的精力。对巨乳没有反抗力在变成怪物之前就有这样的弱点，变成怪物之后貌似被放大了。经常在夏日偷看街上清凉装着的女性胸部，也会收集以巨乳为卖点的色情杂志。遇到学生用色情杂志布置的陷阱，也会因为控制不住不管学生的暗杀一直欣赏美图。看到巨乳的表情怕水杀老师的身体就像海绵一样，会吸收水，吸收了水的身体会变的沉重，影响移动速度，这是学生们制定暗杀计划的一个重要信息。所以遇到下雨天，如果没打伞头部就会膨胀33%（小律计算的数据）。头部胀大经常会缺一根经明明是非常聪明的人，可能是变成怪物的副作用，脑袋经常会少一根经。在学生们第一学期结束后的大作战时，因为被太阳晒黑了，自动脱下了一个月一次的皮（能抵挡炸弹的杀手锏），被学生吐槽：“这种笨蛋为什么到现在都杀不了。”懵逼状态因为害怕解雇而对上司献殷勤在这一点上比很多普通人都严重，椚丘中学的校长到E班视察的时候，杀老师非常殷勤的为校长服务，受到学生的吐槽。非常容易害羞当被学生看到自己偷看小黄书、穿女装的时候，会受到一万点精神冲击，这个弱点被学生们当做重要的消耗精力的手段。害羞的不敢看其实杀老师还有很多其他的弱点，虽然对暗杀没有什么特别的帮助，但是作为学生的吐槽点都是必不可少的，这些弱点也让杀老师明明有着怪物的外形却能迅速得到学生们的喜爱，毕竟身边有这么一个逗比，是一件无比欢乐的事。喜欢本期文章的小伙伴们，欢迎关注！点赞！评论！转发！收藏！', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10639', '0', '暗杀教室：给你一个杀老师，你愿意嫁吗？', '小编和大部分人一样，在看过最后两集后哭得稀里哗啦的。很感动，因为看到了人与人之间的爱，即彼此的付出、牺牲、珍惜与不舍。同样还伴随着自己对杀老师的不舍。所以最后两集才那么感人至深...从第一集开始就被这个拥有多个触手的章鱼老师所吸引。在小编心里杀老师就就是世界上最好的老师，又用自己的爱成就了28名学生、乌间、bitch，甚至理事长。尤其在最后，知道自己的生命将走到尽头后，给每一名学生说的话写成了7年都远远读不完的建议书，再加上亲手制作的、同样厚的纪念相册。只能用一个字概括这一切，就是---爱，他生命的最后一刻，闪光而快乐，照亮了28名学生的人生道路，也照进了我们这些观众的心。如果给你一个杀老师，你愿意嫁吗？', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10640', '0', '我心中最完美的老师：杀老师', '炎炎夏日，依旧有一群亢奋的人们每天都像打鸡血一样奋斗在脑力战的前线，他们就是迎来了期末考试的学生们。不管是决定命运的高考之战，还是其他各种升学考试，大家都因为忙碌的学业而在持续的高温中努力打起精神。而这时老师们也会助大家一臂之力。在动漫中有这样一位老师，可以教学生们所有的学习科目，一个人可以同时给一个教室的人分别指导，跟学生们的关系也不只是师生那么简单。他就是《暗杀教室》中那个把学生看的比什么都重要，扬言要炸掉地球却在地球当了个老师的黄色章鱼——杀老师。这只黄色章鱼在这部动漫中扮演着两个角色，一个是威胁世界安全的恐怖分子，另一个是知名高校差生班的老师。而这两个角色他无疑都扮演到了最高境界。作为恐怖分子，他公开向政府宣战，以毁灭世界为要挟，开出要在学校当老师的奇怪条件。而作为一名老师，想必这是史上最受学生待见的老师了。不经能和学生们打成一团，玩耍教书两不误，还能随时随刻扮演起学生们的人生导师，亲身传授作为杀手的经验（额，不，学习经验），对每一个学生都爱护有加。在差生班难免会有不好对付的学生，杀老师从来不会用强硬的手段逼他们从良，而是用灵活的方法，针对每个人的性格和长处，让他们能够最大限度得发挥自己的优点，为一个团队，一个集体贡献自己的能力。当然啦，我们的杀老师不仅有强烈的责任心，还有超能力来帮助大家学习，根据每个学生擅长的科目一个人同时超速指导一个班的人，学生们出的题，不论是学习还是生活中的，几乎没有能难住杀老师的。不过这样的老师偶尔也会有一些缺点和认怂的地方，就是这样才显得这只黄色章鱼很可爱呀。学生时代如果能够碰到一位这样性格的老师想必是一件很幸运的事吧，上课枯燥的知识都会变得有趣起来，生活中所有的疑难和趣事都能跟他分享，完全不存在繁重的任务和学习的压力山大，这样的校园生活简直完美。虽然最后杀老师因为政府的原不得已继续存在于这个世界，死于自己心爱的学生之手也算完成了最后的心愿。大家喜欢这样的老师吗？欢迎评论区留言哦。文：Lee酱', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10641', '0', '虐哭了！《暗杀教室》倒数第四话 潮田渚亲手杀死杀老师', '《暗杀教室》漫画将在180话完结，而动画第二季也赶在动画党被剧透前，将随漫画一同完结。剩下99话的漫画剧情要被压缩成16话动画来完成，也是不知会被删成什么样。（《极黑的布伦希尔特》的最后两话，想必大家都有所领略。）对此，追番姬只想跟制作组说 ↓以下是最新更新的漫画第177话。（汁源来自komica）不想被剧透的迅速撤离杀老师最后一次点名，潮田渚亲手杀死杀老师，杀老师化为光子消失 (ಥ _ ಥ)虽然很多漫友希望杀老师复活，但追番姬以为，按照松井优征的风格，应该不会出现这种反转了的，但也不排除“杀老师化作了无形的存在，一直陪在我们身边”之类的结局。那剩下3话要干啥呢？坐等。', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10642', '0', '精选暗杀教室美图十张，最后一张大爱杀老师！', '文 / 大白(●—●)你们要的无水印来了，直接抱走吧！----- END -----QQ公众号ID:ILOVEACG微信公众号ID：LOVEACG99图片源自网络收集,如侵权私信删除喜欢就点赞，喜欢就收藏，喜欢就订阅！QQ公众号丨微信公众号丨头条号：最爱二次元谢谢支持最爱二次元，下一期更精彩，敬请关注！', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10643', '0', '暗杀教室杀老师被杀了么', '暗杀教室此漫有些无厘头，真的吹得好大，不过关键你受呀在被称为史上最凶恶的超生物“杀老师”将月球炸掉了七成，并扬言在第二年要炸掉地球，瞧，这都玩上星辰了却以不明目的而担任椚丘中学3年E班的教师，上面还高大上了，下面就青春学园剧了，跳跃太大，表示死机状态中……、重启～～暗杀的人有报酬，所以呢各同学想尽办法杀死这名老师，钱果然万能，是贫穷维护了善良的吧？呵呵…、E班的同学都知道杀老师的身世，得知原来杀老师其实才是真正的死神，已经上升片神之领域了，话说是修仙动漫么，哈哈记者：如何能杀死你呢死神：这得看看那死亡笔记有名字没另一个死神是他徒弟，因为被徒弟背叛而被不法分子抓去作实验，结果实验失败，研究员炸掉了月球，为了保证密码不被泄露，这便是真相，残忍么，现实么，高层仅仅一句：白老鼠的后患好打击人，看着有些心灵被暴击一万感觉因误杀了E班原班主任雪村，雪村临死前希望杀老师当班主任，这理由好牵强，再添一个，那就是这是萌萌有爱的死神杀老师因为被改造过了，3月份就会死亡，并且会爆炸，地球消失...死神居然远能暴炸造成物理攻击，太不可思议了…读书少，可不能欺骗人死神不是都灵魂攻击么，这是不同维度，好么取冒蓝火加特琳突突你这骗子(………、)得知真相后大家就在思考究竟最后的时间是杀还是救杀老师，于是乎分成救派和杀派人性与利益碰撞，好写实抉择，这风格是要裸讽么…、嗯……总算做了一件看得顺眼事那就先收回加特琳，取四十米大刀来，让你三十九米半啦～但是杀老师为了不让班分裂，提议以对决决定，现在还在对决中，人数基本是一样结果呢？小编也不知道的，那位知晓，小声说说別担心，这里安全的很因为这可不是暗杀教室啦～[普通版]喜欢就点点关注吧', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10644', '0', '杀老师和我们的老师有什么区别？', '你想要一个什么样的老师呢？我想每个经历过初高中教育的孩子心中都有一个或清晰或模糊的答案，而且这个答案的形象恐怕和我们现实中的老师形象相去甚远。如果让大部分学生来给自己的初高中老师们做个客观的评价，恐怕分数都不会太漂亮。那么，如果给你一个《暗杀教室》里那样的杀老师呢？你会因此欢呼雀跃吗？你的人生会从此改变吗？你会从此对教师有个完全不同的认知吗？我想大部分人心中已经有了答案。那么，杀老师到底和我们老师有哪些区别呢？1.最大的不同——杀不死首先是最大的不同，话说，这一点杀老师大概和所有正常人类都不同吧——杀不死。是的，怎么杀，不管谁杀，不管何时杀都杀不死，那么“杀不死”这一点意味着什么呢？其实“杀不死”可以说是杀老师推广自己的教育模式中最基础最重要的支撑了，这意味着这位老师可以完全无视国家机器，无视理事长，无视所有规则，可以强大到确保每一个学生的自由和安全，可以按照自己的理想来培养每一位学生。可以说，没有“杀不死”这个优点，杀老师就不可能成为一位那么成功的教师。2.最让人欣赏的地方——不以学习成绩为评判学生的唯一标准为什么相对于我们自己的老师，你会这么喜欢杀老师？其实原因非常简单——杀老师不以学习成绩为判断学生好坏的唯一标准，他尽自己可能去了解每一位学生的特性，然后付出最大努力去帮助每一位学生，从来不轻视任何学生，也不去断言任何学生。他最开始接手三年E班的时候，可以说除了停学的业同学外，全班都是年纪吊车尾，但是他从来没有因此而对这群学生失望过，并且对于习惯于轻视这群学生的理事长表现出强大的敌对心理，这比起我们自己的老师常常说的“能救你的只有你自己”这种话要更加像一个为人师表的人。3.热爱教育胜过一切一个能够轻易毁灭地球的怪物，让所有职业暗杀者都铩羽而归的逆天存在，向政府唯一的妥协条件就是“成为三年E班的班主任”。如果不是因为热爱教育，如果不是将教育作为自己生存的意义，那么还有什么理由来解释这样不合常理的行为？也许你可以说那是因为记忆中那个不明身份的女人最后的遗言，但是如果不是发自内心的热爱，也不可能最终获得全班同学的认可吧。4.愿意在学生身上花时间不仅一个学生吐槽过杀老师总是在课下逼着他们补课，而他们在此间的种种暗杀行为都被杀老师轻易破解，“真恶心啊，那个老师。”——大家会笑着这么说，嘴上说着恶心，心里却是开心的。尽管我的成绩不尽如人意，但是这位老师并没有像其他老师那样放弃我们，他仍然愿意在我们身上投放足够多的时间，仍然愿意给予我们和其他优秀的学生一样的待遇。真是位让人感动到自己都恶心了的老师啊——大概这才是大家的心声吧。5.老师的专业素质绝对过硬——一人能扛下语数外全科虽然三年E班还有两位老师，但是大家都心知肚明，一个只能教体育课，一个则只会上课乱侃英文，语数外的教学责任由杀老师一人全包了。如果没有绝对过硬的专业素质，是不可能在短短一个学期内就帮助全班学生神速进步的。第二季中，杀老师和理事长的生死对决那里，杀老师的那句“大概全国大部分的老师都可以轻易将这些书中的习题背下来吧”这句话让理事长自己都汗颜了吧。6.将学生的错当成自己的错第二季中，学生们上学途中撞到老爷爷，杀老师亲自去道歉，并且还在老爷爷的病房内跪拜道歉，杀老师的行为表现出的是他完全将学生的错当成了自己的错，不仅敦促学生去道歉，而且以身作则第一个向老爷爷道歉。7.善于引导学生第二季中，三年E班和三年A班的数次交锋中，杀老师的对学生的引导都起到了关键作用，他懂得如果引导学生发现自己的价值所在，如何促进学生的成长。一次是在“倒木”对决中，一次则是“文化祭”中。8.能力强大，实力逆天。没有操场，没问题，我来建。被坏学生绑架，没问题，我来救。被职业杀手围困，没问题，我来救。……有这样一个老师，你的学生时代能不完美么？', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10645', '0', '盘点杀老师八大致命弱点', 'TOP.8影分身增加过头会使残像粗糙，也会十分疲倦。TOP.7每失去一条触手，行动能力会直接下降20%,分身会混入小孩形态。TOP.6在极近距离被压力光线照射会使细胞产生扩容反应，使身体瞬间僵硬。TOP.5再生很消耗体力，精神状态对触手的驱使带来重大影响。TOP.4由于过于强化速度的关系，本身是没有什么力气的，特别是在静止的状态下，一个人就能按住一只触手。TOP.3不能游泳，吸水性很强TOP.2一到月底就资金短缺。TOP.1痴迷于H杂志', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10646', '0', '杀老师、齐木楠雄、龙套、琦玉老师，究竟谁是终极boss？', '近两年风靡起来的超能力番中，拔得头筹莫过于《一拳超人》的琦玉老师，《齐木楠雄的灾难》中的齐木楠雄，《灵能百分百》的影山茂夫（龙套）还有《暗杀教室》的杀老师啦！然而有一个困扰很久的问题，如果让强者如云的超能力者在日漫世界里相遇，究竟谁才是终极胜利者！？是能一拳致胜的琦玉老师？还是稍有偏差就能改变世界线走向的齐木楠雄？还是说…充能到百分百就能毁天灭地的影山茂夫？还是…上知天文下知地理无所不能的杀老师！？先来说说琦玉老师一脸智障，对，没错，一脸智障感锃明瓦亮的大光脑壳别看他这样，人家还是有一拳打爆整个世界的实力的！（该帅还是能帅起来的，不要不服但不可否认的是琦玉老师只想做一个普普通通的老实人，没有什么大野心，时不时还露出一张很欠揍的樱桃小丸子爷爷般的智障脸…嘛，要说称霸日漫世界…我不信琦玉老师会去争的！【第一个pass！】再说说齐木楠雄通常说有能耐的大佬都不显山，不露水，一副超脱三界飞升成仙的淡然感…万年面瘫，外加一切好麻烦…好麻烦…麻烦统统退散体，连最美校花的倒贴都无动于衷！？你告诉我你是不是个真钙！？24K不含糖的那种！能改变一切却不想让一切改变，宁可在头上安俩外星发射器（？嘛嘛，大佬你还是适合隐居。【第二个pass】第三个影山茂夫这孩子打小就是数学不及格、脑子缺根弦的主（哦对，灵能百分百和一拳超人的作者都是同一个人——ONE先生，所以看着这人物…也就是长了头发的琦玉老师啊！！！！）别看这孩子蠢，但是人家爆发起来可不比其他人差然而，龙套却很幸运的有个师匠，欺诈之神霊幻新隆，师匠虽然是个克扣小孩子工钱，欺骗全世界的高智商欺诈师，但对于龙套的成长之路偶尔蹦出来一句两句人生哲理真的让人不得不服，动画最后那句“不愿意的时候，就算是逃跑也没关系。”有这样的师匠，龙套…会跑的吧【第三个pass】杀老师…杀老师，您的学生每一个都变的非常非常优秀，应该达到你期望的样子了吧…这一下，可以安心的去了…【第四个pass】综上…如果真的在一个世界相遇，我自认为他们不可能像DC一样正义与正义战斗，也不会像漫威一样亦正亦邪的大乱斗顶多…凑在一起和和气气的打麻将吧…', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10647', '0', '如此甚好～致 杀老师～', '他们的夏日祭～老师满脑子都是……想想当时自己的班主任……呜哇', '', '4703', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10648', '0', '博人传：波风水门登场，九尾一分为二，博人小葵传承人柱力？', '在火影中，四代火影波风水门曾经将九尾一分为二，一部分在鸣人体内，一部分在自己体内，后来两只九尾再度合体在鸣人体内。而如今火影忍者续集博人传中，据说波风水门可能再次将鸣人体内的九尾一分为二，今天咱们就来分析一下这个可能性究竟有多大。很多漫迷觉得波风水门在九尾上做了什么特殊的记号或者忍术，等到与孙子、孙女见面的时候，水门会再次被召唤出来。能看到鸣人的孩子健康快乐的长大，看到家人们都好好的，这想必也是水门的心愿。为了这个愿望，生前就未雨绸缪布，在九尾身上布下忍术，以便满足自己的愿望。而在成功被唤醒之后，波风水门除了看看孩子们外，肯定会送晚辈一份见面礼，而现在的水门也没什么好送的，他的忍术他都已经教给鸣人了，而孙子孙女学忍术只需要跟他们的爸爸学习就好了。想来想去，最珍贵的就是九尾了，水门可能再次将鸣人体内的九尾一分为二，分别给博人和向日葵。有些漫迷可能觉得这样做有些不妥，鸣人失去了九尾的力量恐怕连博人都不如了，那么鸣人正式退出观众视线，在家养老，外面的打打杀杀都交给博人和向日葵下一代人，难道我们一路陪着长大的鸣人就这么谢幕了吗，心疼。但是也许还有一种可能是在鸣人有危急时刻，九尾感应到之后，激活了波风水门的印记，然后水门复活，看着奄奄一息的儿子，为了保家卫国，不得不再次将九尾一分为二，让博人和向日葵代父出征。这种可能会更让漫迷接受吧，如果在鸣人还好好的情况下将鸣人体内的九尾拿走，恐怕会遭到很多漫迷的不满。不管如何，咱们敬请期待就好了，不管哪种可能，波风水门想必都会再次出手将九尾一分为二。这是他的夙愿也是博人传发展的趋势。最后你更喜欢博人，还是喜欢曾经的鸣人呢？欢迎留言点赞收藏，喜欢动漫的可以关注一波。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10649', '0', '宇智波鼬 vs 波风水门谁的赢面大？', '宇智波鼬和波风水门两个人都是难得一见的天才，而且都是《火影忍者》里面超高人气的两位角色，那么如果对比这两位实力的话，你觉得哪一位赢面会更高呢？首先阐明一点：鼬的别天神和伊邪那美不纳入他的实力范畴，别天神需要十几年的冷却时间，而鼬因为没有初代细胞，所以无法自行解除封印；伊邪那美只对那些不承认自我的人有效。其次来看看对宇智波鼬和波风水门的评价：1、自来也评价波风水门是几十年难以遇到的天才；大蛇丸则评价鼬是就连天才的他自己都嫉妒宇智波鼬的才能。2、波风水门被四代雷影称之为“不败忍者”；而宇智波鼬的须佐能乎则被绝称之为“堪称无敌”。好了，如果要说宇智波鼬和波风水门两个人战斗的话，哪方赢面大我觉得应该是不分上下，但如果真要比个高低的话，应该是波风水门这边赢面要大些，为什么呢？主要原因有这几点：1、战斗经验方面：波风水门胜波风水门是在第三次忍界大战中成长起来的，被誉为“黄色闪光”，而且赢得了战争的关键胜利，那就是神无毗桥之战；宇智波鼬在离开木叶之后就直接加入了晓组织，并没有天天经历战斗。2、战绩方面：波风水门胜波风水门的对手都有哪些？雷影兄弟、宇智波带土、九尾。战绩呢？完胜雷影兄弟，打败宇智波带土，和妻子玖辛奈一起与九尾同归于尽，封印了九尾；宇智波鼬的对手都有哪些呢？宇智波止水、四代水影、迪达拉、大蛇丸、卡卡西。止水只是少年期间互相切磋，迪达拉也是前期，卡卡西、大蛇丸、都无法和九尾、带土这样的人相比。3、综合实力方面：波风水门胜波风水门的八项能力都基本上是非常顶尖的，查克拉量更是丰富（如果不丰富也不可能学会仙术），只有幻术未知宇智波鼬的八项能力也是非常顶尖的，除了自身查克拉量比较少之外（身体原因）4、战斗分析能力 ：旗鼓相当宇智波鼬在秽土转生的时候一下子就看出长门地爆天星的弱点从而反败为胜波风水门在与带土战斗的时候一下子就分析出带土神威的弱点从而反败为胜5、准备配置方面：差不多波风水门拥有飞雷神之术、螺旋丸、妙木山蛤蟆、有限仙人模式宇智波鼬拥有万花筒写轮眼：天照、月读、须佐能乎：八尺镜、十拳剑总结：如果两个人只是切磋的话，估计会不分上下。但如果是分生死的话，我觉得四代火影波风水门这边赢面更大。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10650', '0', '《火影忍者》波风水门的经典语录，想成为儿子努力的目标', '1. 正因为有我景仰和深爱之人的存在，所以我才有自信，不会输给任何人。2. 身为父亲，总会想走在儿子前面，成为他努力的目标。3. 我想说的话...你那啰嗦的妈妈都说了...4. 我...也有自己要守护的东西！微信公众号：最句子（ID：zuijuzi），关注开启句子管理神器。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10651', '0', '少年时代的波风水门能及时救下漩涡玖辛奈，才不是偶然！', '冠着不常见的姓氏，学着二代火影的飞雷神，迎娶木叶独一无二的人柱力，年纪轻轻就成为四代目火影。这样的人怎么可能简单？那时，第二次忍界大战刚过去没几年，兴盛一时的漩涡一族早已成为历史。体内封印着九尾妖狐，漩涡玖辛奈早已不是那个怀抱着好奇打量木叶的红发小女孩。身为九尾人柱力的她，是木叶深藏不出的人间兵器。千里迢迢来到木叶，却被告知永远不能回到自己的家，那一刻，那种绝望，只有她自己能理解。但木叶没有像砂隐对初代一尾人柱力分福那样，将玖辛奈囚禁在不为人知的地方。事实恰好相反，她被送进忍者学校，和数之不尽的同龄人在一片天空下，学习着相同的知识。但初来乍到的玖辛奈，怎么可能这么容易地融进木叶盘根错节的系统？不出意料，一头红发的玖辛奈被排斥在关系网外面，备受欺凌。但玖辛奈也不是好惹的，一双拳头打遍忍者学校，生生把自己的外号从西红柿改成了血红辣椒。那些胆敢欺凌她的人，只怕是不知道：身为涡之国独一无二的公主，她自幼受到国君父亲的极力培养，自然巾帼不让须眉！每当玖辛奈将那帮欺软怕硬的崽子们打得落花流水，回头时，总会看到班上那个金发男生站在她身后，默默地看着他。多少次，他都站在他身后，不曾出手，就像一个沉默的护卫。没错，那时候注视着玖辛奈的水门，给我的感觉就是护卫！刚开始，漩涡一族还没有灭族，玖辛奈还是无限尊荣的公主，身边怎么可能一个护卫都没有呢？实际上，木叶一方派出的人员，就是作为她同学的身份接近并保护她的水门。身为千手近亲，虽然自幼孤儿，波风水门依然受到村子里千手族人的栽培。再加上身家清白，正是极好的人柱力护卫人选！玖辛奈并非没有其他的护卫，但那些护卫人员都是暗部，并不像水门那样光明正大地招摇。在云隐的九尾夺取计划中，绝对不像玖辛奈所看到的那样，根本没有人出来找她，但那些人不会像水门那样出现在她眼前。太多人出现，反而会让玖辛奈的感激目标分散，英雄有一个就够了！', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10652', '0', '火影：传说级忍者波风水门，即使过了十几年，奇拉比也赢不过他！', '波风水门，四代火影，当之无愧的无冕之王。极速忍者当中，罕有人比得过波风水门！他凭借飞雷神之术时空位移的绝对优势，击败了四代雷影ab兄弟的联手之局；他谋略无双，合纵连横，以一己之力扭转了第三次忍界大战的局面，让一再衰退的木叶再次得到喘息。这样的超影级忍者，除了传说中的千手柱间和宇智波斑，还有谁能轻易打败金色闪光波风水门呢？波风水门离开十七年，51岁的四代雷影依然没从当年的噩梦中走出。数十次激战，波风水门默默地用行动教会了四代雷影和奇拉比怎么做人。即使在九尾事件中无奈地光荣了，波风水门依然是令各国最高领袖们忌惮的金色闪光，这在跟他同辈分的忍者当中也是独一份的。那么，在云雷峡一待17年的奇拉比，还有没有资格跟波风水门互怼呢？若论速度加成，擅长瞬杀刀法的木叶白牙和以瞬身术闻名的宇智波止水都可以追得上他的脚步。而宇智波鼬承认自己还比不上瞬身止水，但却能把奇拉比虐得四脚朝天！要知道宇智波鼬的速度还不及波风水门，但就这样，都几乎让奇拉比和鸣人反应不过来，真碰上了波风水门那还得了？就连三代火影，都庆幸过大蛇丸没能把四代火影给召出来，可见就算是秽土转生的削弱，也无法掩盖波风水门实力之强。别看在四战中奇拉比和鸣人都曾经冲过四代雷影的防线，但要知道，那是四代雷影放水的结果！因为雷影本人也不希望两位人柱力就这么折在那里，交手时都是以试探居多。真要殊死一搏，以雷影的老辣，常年被晾在云雷峡的奇拉比和年轻的鸣人，都只有被扁的份！幸亏被大蛇丸召唤出来的波风水门站在了忍者联军一边，要不然，单凭他一人之力就能让两大人柱力吃不了兜着走。况且整场战斗，四代雷影和奇拉比都几乎没跟波风水门打过照面，想来也是有点怵。不过在这里要吐槽一句：连波风水门都没信心打败的四代雷影，居然充满信心地挑上史前巨怪宇智波斑，该说真不愧是二愣子吗？', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10653', '0', '火影忍者手游波风水门', ' 波风水门，火之国木叶隐村的四代目火影，木叶三忍之一自来也的弟子，主角漩涡鸣人的父亲。拥有冷静睿智的头脑，擅长开发改进忍术，实力高超，速度冠绝忍界，被誉为“黄色闪光”。在九尾袭击木叶事件当中，为保护村子，封印九尾使用尸鬼封尽而牺牲。第三次忍界大战中，水门无人能及的神速威震忍界，被称为“黄色闪光”。除火之国外各国高层均下令，凡遇到木叶的“黄色闪光”可以立即放弃任务逃跑，而且不会受到处罚。那么冠名忍界的水门，在游戏里的再现有没有让广大的火影迷们失望呢？让我们一起来看：水门技能详情：普攻：风水门的普攻前摇短，出手速度较快，不太容易被敌方的技能打断。其连A总共可以分为5段，在前面4段的伤害打出之后，波风水门会向前冲刺并穿梭敌人。时空间忍术·神速——超高速的瞬移突进，对Z字形移动路径上的敌人均造成冲击伤害，最后再追加一次突进的苦无斩击。这是一个快速突进，贴近对手的技能，而其突进瞬移可以几乎贯穿大半个决斗场，非常强悍。可以在第一次瞬移时使用方向键控制瞬移距离与方向的设定，更能够在对手处于视觉盲区时进行试探性的起手，命中率非常高。电光火石——向前方甩出一枚飞雷神苦无之后，若成功命中敌人，则对敌人进行连续五次的闪光瞬移攻击；视野范围内的敌人全都可能成为攻击目标。在攻击过程中，可随时点击普通攻击按钮中止连击。起手时按住技能按钮，可以在跃起后向空中再进行一次瞬移。此技能建议在对方忍者视野范围内释放。如果不能命中敌人，此技能可以随时中止攻击的设定，除了进攻，也能防御，攻守兼备，避免空了被敌人抓住机会进行反击。而且浮空的敌人也会成为波风水门攻击的对象。该技能单体伤害较高，前摇较短。时空间·螺旋连丸——与飞雷神之术配合的二段螺旋丸连续攻击，第一次螺旋丸在地面释放将敌人击飞，第二次螺旋丸在空中释放将敌大浮空，该技能输出高，判定范围约为半屏。总的来说，波风水门不负金色闪光之名，如果搭配合适的密卷，例如霸体，真的可以说是无解，技能兼具突进、中止，进攻，防御，保障了波风水门的攻击范围，而且水门的所有技能都可以用作起手技能，命中率非常高。水门的上手难度相对高一点，属于技术流忍者，在使用时，我们要发挥好他的优势，例如位移能力优秀、出手速度快、技能范围广，另外水门本身的技能范围较广，伤害也比较足，勤加练习，能打出非常漂亮的连招。', '', '4019', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10654', '0', '史上最强的外婆夏目玲子！用遗物水了六季，目测还有七', ' 从第一季给贵志留下了友人帐之后就一直活在妖怪的回忆里，而夏目这一集中妖怪的一句话，“我以前好像见过和你相貌相似的男人。”桥桥一下炸了，对哈夏目玲子还是有老公的，当然那个男人也可能是夏目爸爸。 虽然第一季就在猜能跟玲子在一起的男人是怎样的人，但是《夏目友人帐》播了六季都没有关于夏目父母多余的讯息，好像是二还是三季结尾夏目爸爸和妈妈出现在夏目的梦中，虽然没有露脸但是感觉也是很温柔的人，看到那一幕的时候还以为全部完结了，没想到现在已经出到第六季。可喜可贺~ 但是大家肯定更感兴趣夏目的外公了，我快把夏目的爸爸当成石头缝里蹦出来的存在了。 于是听到关于可能是夏目外公的信息就不小心的震惊了！从11集透露的讯息夏目的外公很可能也与妖怪有关，不然也不会出现了一个除妖人家，当然也可能是这个妖怪在一个事件中遇到过夏目的爷爷，而且事件着夏目爷爷担任的角色绝对不是路人甲，一个妖怪是不可能记得一个路人甲。 以玲子牛逼的个性来说夏目的外公应该很“强大”，个性或者对人太多方面或许是能力。毕竟能忍受玲子不靠谱的个性一定很强大，对了！玲子最后不是未婚吗，我差点忘记玲子是单亲妈妈了。所以为什么两个人没有在一起，一夜情！以玲子的个性来说什么都可能啊。那么夏目该不会还有另外亲戚吧！细思极恐…… 还是说一说这一季的夏目友人帐吧！首先这一季的画风全部都是kilakila的，闪闪发光自带柔光滤镜。虽然沿袭了清丽的画风，但是第一猫咪老师居然没有圆成球，居然可以看到腿，夏目要升高中了，在思考要不要升学。 最重要的是这一季的夏目贵志和玲子的长相越来越像了，不应该是随着年龄的增长五官越来越，但是夏目脸越来越柔和了总受的地位已经不可动摇了。 今生无悔入夏目，来世愿为帐中妖。 六完了一定还有七，第七季的时候可能会有外公的更多故事吧~对了12集去哪儿了！被猫咪老师吃了吗？给以上动态图配文字你们会配什么？取：“夏目是我的！”猫咪老师：“滚粗！”', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10655', '0', '《夏目友人帐》第五季揭秘夏目玲子死亡之谜', '治愈系动漫《夏目友人帐》终于在观众的期盼中迎来了第五季，之前曝光的消息中称在最新一季中玲子的故事将会成为主线，妖怪口中的玲子总是在笑着，而真实的玲子又是个怎样的人呢？第五季又将迎来怎样的全新剧情呢？今日（5）号《夏目友人帐》正式在b站上映了，在第01话中，男神夏目大人和可爱的娘口三三终于回归了，依旧是熟悉的音乐、熟悉的开场白，《夏目友人帐》带给我们的感觉不管多久都不会变化。想要知道玲子过去的夏目碰到了住在壶中的妖怪，妖怪却称玲子为“小偷”，温柔又热情的玲子居然会偷妖怪的东西？这究竟是怎么回事呢？前四季的剧情，夏目在认识越来越多的人和妖怪之后，有关自己的外婆，夏目玲子的过去也在一点一点的展现出来。在第五季的先行预告与人物设定中，我们看到了消失了一整季的小狐狸，果然小狐狸不是龙套！最让人激动的是，先行图中只有玲子、夏目和斑，看来这次玲子要进入主线了。夏目玲子是夏目大人的外婆。夏目强大的灵力全部继承自外婆玲子。玲子是个性格迷糊的女孩，很受妖怪们的喜欢。在前四季对她唯一的交代就是“年纪轻轻就去世了”，可见是在夏目的父亲还很小的时候就死去了的。在之前的前四季剧情中，漫迷们都已经知道，夏目玲子是夏目贵志的外婆，作为友人帐的创造者，玲子的身份引发了不少观众的猜疑。1.夏目的外公也就是玲子的老公是谁？在之前的剧情中，只介绍说玲子是夏目的外婆，而夏目的父母也都很早就去世了，夏目在整理玲子的遗物时，意外发现了友人帐，从此能看到妖怪的他开始了一段神奇的冒险。在剧中，不管是对玲子还是对夏目的父母都没有做过太多的介绍，但是同样能看到妖怪并且创造友人帐的夏目身份绝对不简单。在日本，女孩嫁入男方家里，是要将姓氏改为男方的姓氏。而夏目和玲子同姓夏目，至少证明玲子是没有结婚的。关于死因，作者没有写很多人也只是猜测。2.玲子死了吗？怎么死的？在《夏目友人帐》中关于玲子的故事，都是一些妖怪的回忆，按道理说玲子应该是死了的，但是对于玲子的死剧中一直未作介绍，夏目和玲子的羁绊也仅仅是那本友人帐。带着这些问题，相隔四年半之久的《夏目友人帐》第五季在众人翘首以盼中，再度回归！新版的剧情也引发了网友的猜测。从刚上映的第一话中可以得知，夏目一直在打听玲子的过去，在这一季中他将再度遇到很多妖怪，其实这些妖怪的描述中，恰恰就是玲子人生的折射。在这一季中，夏目将了解到玲子更多不为人知的故事，在众多故事的拼凑中，玲子的身份死因或许就会揭开答案。而在夏目寻找玲子的过程中，夏目的能力也将得到提升，也许在故事的结局夏目会遇到玲子也说不定，当然这只是猜测。但是不管结局怎样，夏目带给观众的感动总是难以磨灭的！《夏目友人帐》第五季自2016 年 10 月 5 日起每周三0：35在东京电视台、大阪电视台、北海道放送首播。与此同时由北京杰外动漫第一时间引进发行，于同期在国内各大视频网站中火热播出。《夏目友人帐》系列是由绿川幸的漫画作品翻拍而成，2003年起在漫画杂志《LaLa Dx》上连载的这部治愈系作品很快就得到了漫迷们的认可，2007年10月转至《LaLa》继续连载。动画改编工作于08年开始，很好地还原了原作的氛围和主题，在国内引进后也是好评如潮，在各大在线网站播放数均已破亿。在姗姗来迟的第五季制作消息放出后搜索指数呈现大幅回升，充分反映了粉丝的殷切期待 。衍生作品有小说、Drama CD、广播剧、音乐剧等，作品主要取景地的熊本县人吉市曾举办过《夏目友人帐》联动活动，大量慕名而来的动漫粉丝一时令该市成为热门旅游景点。这部暖到刚刚好的新番佳作看似写妖实则描摹人间众生，充满哲理而又温馨治愈。玲子究竟经历了什么，在夏目大人之后的故事中，我们或能看到答案。一起去《夏目友人帐》第五季中寻找答案吧！编辑：木尹', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10656', '0', '夏目玲子是怎么死去的，难道真的和斑有关系？', '明明那么强大的人，但是年纪轻轻就死了，还不知道原因。。。。夏目玲子是日本少女漫画志《lala》中大受好评的《夏目友人帐》中的角色之一，基本只在回忆中登场，拥有强大的妖力，憎恨着自私的人类，也被人类所抛弃和鄙视。外表大大咧咧，甚至有时粗暴，但内心温柔而坚韧，热情宽厚，乐于助人，帮助他人时有着非同一般的智慧和勇气。和斑(猫咪老师)似乎有一段过往。除了头发长度外和夏目贵志长的几乎一模一样。玲子有着极其强大的灵力，因此可以看到并打败妖怪。但是就是这样拥有强大灵力的玲子，从一开始就只能从妖怪和夏目的亲戚口中得到只字片语，斑老师好像知道一些情况，但是每次都好像很敷衍和模糊。书里没有交待。至少我刷了动漫全部，又刷了已经翻译过的漫画全部，没有看见死的情况。从大学时代看夏目友人帐时，就对她十分好奇。在剧集的第二部结尾处斑老师在窗下抬头向上看时，一个貌似玲子的人坐在窗上。于是很多粉丝都怀疑玲子是否还活着？玲子的确已经去世了（假如没有夏目友人帐前传的话），因为在第三季时，夏目去询问曾经收养他的亲戚时，得知的确已经过世了。那么玲子真的是想我们通常认为的，像人类正常那样慢慢死去的吗？或者是生病死去？我们可以先看下夏目粉丝者流传最广的几种猜想。1 铃子的死绝对和妖怪还有的场一族有关， 阴谋论，被人或者什么组织设计致死漫画八十几回，寻找一个除妖人的遗物的时候，守护的大龙说见过男版夏目。夏目说应该是他爷爷。。那么，很多人怀疑玲子没有丈夫，还有人编是妖怪强奸她生孩子，应该是不对的。如果真是如此，夏目贵志桑岂不是半人半妖？但是从夏目父母的车祸死亡，人为的原因是极大可能。2 正常死亡，慢慢等到了生命的极限，逝去。玲子的确是去世了，漫画中也是这样说的。 在第二季末尾中窗台上的女子实际上是玲子的幻影，是动画中一种气氛渲染的手法，而不是玲子还活着的意思。 在《夏目友人帐》第一季第一集夏目贵志打了猫咪老师一拳后说“这友人帐啊，对我来说是祖母重要的遗物。祖母确实没能好好与人相处，几乎也没人记得她了。她在很年轻的时候就去世了，所以，唯一有血缘的我想要保留一些羁绊，而且也不能认为这事与己无关。”所以夏目玲子并不是老死的3 非正常死亡，玲子因为灵力强大，所以寿命很短。身体迅速衰弱，最后生命透支。这种说法我感觉有点可行的，因为《夏目》系列本身就是建立在超现实主义之上的。所以灵力这样的带有玄学色彩的论点也是立得住的。一些见过她的妖怪印象也仅仅停留在她的国中时代，很有可能是身体衰弱，灵力消逝，至于最后看不到妖怪。而后逐渐也淡出妖界，因为能力不足以维持她继续友人帐。。。。4 玲子和斑争夺友人帐而发生打斗，最后玲子被斑杀死。而斑被玲子封印，所以没有拿到友人帐，最后被夏目贵志获得。问题有点大，猫咪老师的名字并不在友人帐上，而猫咪老师之所以想要友人帐是因为不想让玲子的遗物无人继承，有一点逞强的成分。它跟着夏目也是防止夏目哪天放开友人帐了，这点在漫画第一话就解释清了，虽然TV里猫咪老师是傲娇了点，但是漫画里猫咪老师是越来越坦率了，以他这种大妖怪其实对友人帐没多大兴趣。总之，以绿川以往的作品而言是属于治愈灵动的风格，即很不可能与现实的想法和逻辑有太多瓜葛。所以生老病死的死亡最不可能。最大的可能是带有一丝空灵，玄学，而又暖暖治愈，却不失伤感的故事。很难保证之后不会把玲子部分单独拿出来讲。夏目母亲也是生下夏目不久后就去世了，但是玲子早逝出于什么原因，说不定还与夏目的外公有关。貌似夏目的爷爷是经常给玲子七千屋馒头还有不让她总呆树上温和的男人 ，是个人类， 还有貌似从照片上看 ，贵志反而和父亲一样 ，头发都是黄色而且长得很像。从新出的第六季里的线索上看，捉妖师组织里有人很忌惮夏目贵志的强大灵力。而且斑老师的傲娇属性，顶多是和玲子交过手而已。反而是捉妖师里的潜在力量与玲子的死有关。不管怎么说。玲子的主线故事应该又是一个很凄美的故事。希望能在新番里补上玲子遇到贵志外公的故事。弥补我们心中的好奇，和期待。每天更新很累的，喜欢的话就点赞吧。本文为动漫堂原创内容，请勿转载抄袭。微信公众号：dongmanjiyi', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10657', '0', '夏目友人帐中夏目玲子和夏目贵志为何是同一个姓？而非父辈姓氏？', '很多人看动漫，看字幕把“哦巴桑”翻译为外婆，就以为夏目玲子生了女儿，这是不对的！因为日语“哦巴桑”也可以指奶奶，只是字幕组翻译时的喜好问题！动漫或漫画并没有特指玲子到底是外婆还是奶奶。由回忆里看来，玲子学生时代，还没结婚就姓夏目了！所以不是和丈夫改姓的！漫画中有讲夏目玲子未婚先孕，那么孩子跟着玲子姓，然而不管玲子生的是贵志的妈妈或者爸爸，贵志父母双亡后还是未成年。所以要跟收养人姓，而剧情中每次家庭聚会都是夏目家族的亲戚，那么就表示他的第一收养人应该是母亲家的人也就是夏目的人。之后贵志辗转各家都属于寄住，而不是寄养。多次寄住使得贵志一直没有改姓。最后落座现在的家庭时已经高中生，在日本16岁虽然未成年但已经有婚配的权利，如果高中毕业后贵志要独立居住也不可阻拦。既然贵志都很大了就没有必要改姓。我觉得是这样的。', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null);
INSERT INTO `bbs_post` VALUES ('10658', '0', '疑云重重：夏目玲子到底嫁给了谁？', '种草关键词：夏目友人帐，夏目玲子，夏目外公之谜好久不见自从《夏目友人帐》预告第五季开播，很多夏目迷就对第五季的开播和剧情有种从一而终的笃定和自信。果然，干净温和的画风和熟悉的配音，以及铺天盖地的“夏目大人，欢迎回来”的弹幕都能让人瞬间泪目，夏目一如既往的温柔，娘口三三在夏目遇壶妖时变身斑盘旋屋顶守护家，这只圆滚滚的猫依旧是夏目忠实信赖的老师啊。❤夏目玲子の一切第五季的第一集围绕玲子展开，夏目想要更多地了解那个和自己“一样”的外婆，想知道她在面对众人的误解、冷漠、孤立时是怎样坚持下去。除了头发长度外和夏目贵志长的几乎一模一样的玲子，在年轻的时候就去世了，属于阳光系但有着悲伤往事的治愈系少女。洒脱而率性，有些喜欢捉弄人，有些大大咧咧的，同时有着奇特的处事风格。而实际上，和夏目牵扯上的妖怪都因玲子结缘，而友人帐则是玲子与妖怪们的羁绊，夏目与妖怪们的羁绊，也是夏目和玲子的羁绊。❤夏目の外公第五季第一集，玲子说：“最近遇到一个很奇怪的人，明明只是个人类却向我搭话了。”而且表情很开心。对于夏目外公的描述，玲子说：“那是一个很奇怪的人类，说什么爬树很危险，已经很迟了就别出门了……”这可能是玲子第一次被人类如此温柔相待，作为观众的我们仿佛已经看到了更甚于爱情的亲情与依赖在玲子心中逐渐萌芽。一直以来，相信很多粉丝和呆呆酱一样，对于玲子的身世充满着好奇，对玲子的丈夫更是好奇。虽然在前面的剧情都有寥寥提及，但是这次以玲子充满回忆的说起来，这表示那个谜一般的几乎被作品直接无视的夏目外公就要出现了吗。❤脑洞里の外公因为玲子是一个很孤僻又不喜与人类交往的人，所以，夏目的外公就成了大家脑洞中的各种假想。网友：绿豆子能娶玲子的男人，一定是能打得过玲子并且亲手收服娘口三三的男人。卧槽，那该帅成什么样！！！（呆：重点是这个？）网友：三木子87话出现一个叫依岛的除妖师。大概会是和夏目有血缘的关系吧。然后岁数可能和他祖父的年纪差不多的感觉。给我的强烈的祖父感啊。（呆：这···也不无可能啊）网友：赞赞萱我咋有点觉得是娘口三三……因为不论是从年龄（老年猫）还是谜之陪伴（傲娇状）还是对铃子的欣赏还是实力的强大来说，在我看来三三很有嫌疑啊，而且有一点老顽童的味道呢*^O^*（呆：娘口三三····这怎么可能！）网友：Lethe接近玲子的人类很少，她老公可能是妖怪，只能和自己姓了；也有可能是很短暂的情缘，她老公是个普通人，在玲子的心灵上不能陪伴她，所以必须要分开。（呆：骚年，你的情感好细腻喔~）网友：一二三玲子是未婚先孕生下她母亲，那之后不久便过世了...这一点跟夏目母亲也有点像，夏目母亲也是生下夏目不久后就去世了，可能身体有些虚，但是玲子早逝出于什么原因，还真心不好解释，说不定还与夏目的外公有关，总觉得绿川老师会给我们一个很特别的故事~（呆：(⊙v⊙)嗯···）网友：难明白漫画八十几回，寻找一个除妖人的遗物的时候，守护的大龙说见过男版夏目。夏目说应该是他外公······那么，很多人怀疑玲子没有丈夫，还有人编是妖怪QJ她生孩子，应该是不对的。（呆：(⊙o⊙)…）网友：乌拉拉比起她怎么消失的，到底谁能征服玲子大人更让我好奇。有可能是玲子跟夏目外公发生关系之后两人就分开了，所以她是单亲妈妈。不过我最希望还是玲子跟妖怪生下后代，再出一个安培晴明啊……（呆：不要跳脱····）❤总而言之不管是普通人还是除妖师，甚至是妖怪，呆呆酱都希望玲子外婆的经历不要有悲伤，希望那个向她搭话的人，曾给予了她最后的温暖和陪伴，就像猫咪老师给予夏目的陪伴一样。❤故事还在继续，夏目在猫咪老师的陪伴和守护下安然生活着。其实，呆呆酱也有一个猫咪老师陪伴哦，如果你也喜欢，请给我一个❤。赫赫。1夏目友人帐猫咪老师925银质项链我们的娘口三三啊，是个典型的馋嘴猫，而且体重和体型日渐增长。喝醉酒之后喜欢胡乱唱歌、平时爱整蛊和欺负弱者，还有些自大，有时还会因自大出丑…但是安静的样子还是蛮可爱的~这款银质吊坠高度还原了猫咪老师的萌萌哒样纸，做工精致，节日礼物选它错不了哦~￥71331人种草', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10659', '0', '《夏目友人帐》夏目玲子是外婆 外公与除妖师有着密切的关系！', '哈喽呀~这里是百合娘，今天的事主题是《夏目友人帐》夏目玲子是外婆 外公与除妖师有着密切的关系！1、首先，很多人再问夏目外公到底是谁，或者夏目伍会不会说明这个问题。我想说，现在连漫画都没有明说我们只能猜。估计现在绿川老师连坑都没有挖完更别说填了。动画怎么说都是要按着漫画来的，tv剧透了这种涉及主线的东西，难道一言不合就想搞事2.很多人搞不清楚玲子到底是外婆还是奶奶，其实漫画动画都有说，夏目自己是说玲子是未婚先育生的夏目的母亲。或许是因为日语外婆和奶奶不好区分，但是母亲和父亲的日语还是有区别啦，生的是母亲就是外婆啊。3.夏目为毛跟着玲子姓。个人觉得大家认为的因为不知道外公是谁，所以夏目母亲跟着外婆姓是靠谱的。在第四季最后一话，收养夏目的妇女提到夏目父母说的是『夏目夫妇』至于怎么回事，有人觉得可能是夏目父亲本来就姓夏目，这个……个人觉得，这种巧合有些诡异，从一个作者而言，如果夏目是跟着母亲姓，可以延伸出更多东西来。个人觉得是父亲入赘……别忘了夏目母亲是怎么长大的还不知道，有可能是被某个有名气的家族收养的，说不定和除妖师有关系。4.之前看到吧里有人问玲子怀孕了妖怪怎么不知道。别忘了，事实上夏目认识的妖怪里也有不清楚玲子的，比如一开始的中级，小狐狸，从他们的态度看来并不清楚玲子和夏目的关系。至于丙为毛也不知道，大概是玲子的性格太奇怪，丙曾经想把名字给玲子，但是玲子拒绝了，玲子说不想要丙这种太听话的妖怪的名字，其实也能看出丙是单方面去找玲子的，可惜玲子又喜欢到处跑。妖怪的时间观念和人不一样，怀孕十个月的时间对妖怪算不了什么，整不好突然想起来要见玲子了人家小孩都生完了。5.夏目祖父是不是人还在问题，但是万一真的是妖怪……这太美我不敢想，感觉多半和除妖有关系，不然漫画里的龙怎么会是看见过一个气息和夏目相似的男人。6.对于的场，我想对动画党说，第四季东方之森漏了一段，就是漫画结尾的场听手下说夏目的包里看到一本类似于账簿的东西。这个情节代表啥我觉得大家都懂得然而tv就是没有。第五季的场对夏目的态度跟之前已经不同了，难道那个时候的场就已经察觉到啥了？总之万一后面的剧情真的和这有关……官方在tv里怎么圆。7.夏目伍已经放了将近一半了于是我又没事想第六季的事，总觉得夏目会有第六季毕竟这么火，说不定下一季还不用等这么久。欢迎讨论~', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10660', '0', '我们是夏目玲子，也是菱垣', '从房间的角落里，翻出那本破旧的友人帐，一个个奇异、感动、令人怀念的故事就缓缓地呈现在你眼前。夏目贵志从小就拥有灵力，能看到常人不可见的妖怪和神明。按照中国人的说法，就是“阴阳眼”，能看见阴间的人和物。在被一只妖怪追赶时，夏目贵志无意中解开了“猫咪老师-斑”的封印。猫咪老师的出现，让夏目贵志明白了友人帐意味着什么；也让他渐渐体味了外祖母-夏目玲子的粗暴、温柔、坚韧、孤单，以及对人类朋友的渴望。友人帐，一本契约书；凡是“榜上有名”的妖怪，都必须听命于友人帐的拥有者。而夏目贵志决定将友人帐上的名字，都还给妖怪们。菱垣是从友人帐上消失的第一个名字。菱垣，一个低级的小妖怪；在一个坟墓旁，因为一个豆沙包与玲子发生纠葛。玲子答应他，只要菱垣能打赢她，就请他吃七辻屋的豆沙包。说完，玲子就用一根木棒偷袭了他，菱垣输掉决斗，交出了自己的名字。 “既然你是我的手下，如果我叫你名字，要马上出现哦。”玲子走之前，随口留下一句话。菱垣，一个单纯的小妖怪；因为玲子的一句话，在墓旁等了一年又一年。在樱花飘落的季节，在暴雨倾盆的夜晚，在大雪纷飞的时刻，菱垣在等着玲子叫他的名字。“今天也不叫我吗？还给我，把我的名字还给我，如果我怎么等，你都不叫我的话。”当夏目贵志把名字还给他，几十年前的那一幕幕画面又缓缓浮现在他眼前。“玲子，已经不要紧了吗？一个人也不要紧了吗？”即使菱垣要回了自己的名字，如果玲子呼唤他，他也会义无反顾的出现。友人帐上的名字消失了，而心里的名字却不曾有丝毫褪去，反而会随着时间越发地深刻。我们是夏目玲子，被众多小妖怪迷恋；也是菱垣，在玲子眼里，你只是众多被收服者中的一个。', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10661', '0', '《夏目友人帐》夏目玲子的老公是谁？', '最近又把夏目友人帐重温了一遍，突然发现里面一直没有交代夏目的祖父是谁。玲子这样孤僻的一个人，到底是怎么找到男人并且生下孩子的？七濑婆婆说这是孽缘，箱崎先生的龙说见过一个和夏目很像的男人，还有井岛先生说夏目身上有一种微妙的感觉。这三个人都不是一个普通人可以遇见并且给他们留下深刻的印象。所以夏目的祖父也就是玲子的丈夫可能是一个除妖人家族中的重要人物并且曾经这个家族的规模和地位不低于的场一族。但这个家族最后也败落了，也许是和除妖人家族之间发生了冲突，或许是在除妖中遭受了重大损失，也有可能是像别的家族一样没有可以看见的后继人而渐渐退出了这个圈子。而玲子和夏目的祖父，有可能两个人相爱但祖父背负着家族的使命导致两个人不能在一起。或者说，玲子的个人意愿与祖父家族的利益相违背，所以玲子离开了。也有可能，祖父一开始接触玲子的目的是为了利用玲子强大的能力，而玲子识破了他的意图之后离开。所以夏目的亲戚说玲子看起来总是不开心的样子。', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10662', '0', '被妖怪追赶打破了一只妖怪“斑”的封印', ' 夏目贵志生来拥有强大的灵力，能看到妖怪和神明的存在。因其父母双亡，常年辗转于互相推卸责任的亲戚之间，并受到同龄人的欺负。同时被其能力影响，未曾与任何人深交，造成性格一定程度上的孤 僻。本性和善，宽厚，踏实。对于自身的不幸，无论是对人类还是妖怪都未曾抱怨过。被藤原夫妇收养后踏入高中。一次被妖怪追赶打破了一只妖怪“斑”的封印， 继而牵涉到其祖母夏目玲子的遗物“友人帐”——一本记录着众多妖怪名字的契约书。对友人帐产生兴趣的“斑”与贵志定下约定，自己守护其一生以替换友人帐的所有权，平日则化身成大头招财猫模样的“猫咪老师”。在“斑”的陪伴、八原妖怪和同学们关心鼓励下，贵志经历了一个个或奇异、悲伤、感怀的怪诞故事，并逐渐学会与人类、妖怪友好相处，谱写出一段充满人性哲思的温馨物语。 ', '', '1017', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10663', '0', '完美女友宫水三叶篇：为爱不顾一切', '你要喝JK巫女的口嚼酒吗？与新海诚其他作品不同的是，《你的名字。》虽然并不算喜剧，但最后万幸拥有一个相当圆满的结局。这也是新海诚这次没收多少刀片的原因——但看到火车出现的时候想必很多人已经做好了拿出40米大刀的准备了，笔者也不例外。作为一对经过了生死考研和离别的情侣，实在不应该这样交错而过，这不但是对故事，对观众的不尊重，也是对宫水三叶，这个拼劲自己一切少女的不尊重。我们今天，就来谈谈这个少女。虽说《你在名字。》是二次元动画，且故事也偏向于“玄幻”，毕竟现实世界里几乎不可能出现这种跨越生死和时间次元的的故事。但如果抛弃外壳，我们就会发现这个故事的内核，是现实中能做到的，也正因为如此，三叶这个二次元“纸片人”，从内核上来看，反而是一个三次元的勇敢少女。说道《你的名字》，我们就不得不提起之前新海诚的另一部著名动画电影《秒速五厘米》。相对于《秒速五厘米》更多描写了少男少女面对青春爱情的迷茫，以及在生活中求不得的痛苦和感伤，《你的名字。》意义却不仅仅在于结尾而已。在《秒速五厘米》三大章节：樱花抄，宇航员，秒速五厘米里，我们可以发现一个很有趣的事情。虽然号称恋爱，但三大块主角却没有一个是篠原明里，即使号称“女主角”，但其心理描写甚至还不如在《宇航员》阶段出现的澄田花苗。当时看的时候还没有这种想法，但仔细想一想，贵树和明里的爱，真的有那么坚固吗？或许青春期的萌动和执着，以及之后迷茫与痛苦更多一些。与其说贵树是追忆篠原明里，不如说他是自己画地为牢不愿走出，他心中的篠原明里一直锁住了他的命运，直到火车交汇后的想开。四月番《月色真美》第七集标题是“为爱不顾一切”，日语是“惜みなく爱は夺ふ”，这句话的意义很有意思，翻译过来是这样的：“他认为爱他人本质上就是爱自己，爱他人就是把爱的对象吸收到自己的强烈个性中，爱不是给予，而是夺取。爱的本能是有私夺取，而所谓的私是一种高尚的境界”。这就是《你的名字。》两位主角最核心的本质。而在《你的名字。》中的三叶，本质上是一个现实中的有趣少女。她虽然有一个镇长父亲，但是和父亲关系很差，作为神社的下一代巫女，她虽然比普通少女多了一个身份，但是依旧向往大城市的喧嚣。当然，我们一开始的最大的印象就是，这位少女头发扎的很好。新海诚在描写上确实是一个非常出色，作为一个优秀导演，新海诚的刻画人物能力非常强大，没有任何介绍，也没有任何的刻意指明。不到30分钟，我们就能看到三叶究竟是什么样的一个女孩了。在朋友面前不拘小节，坦率，性格开朗又有自己的原则，但却有点小固执，没有什么优点，也没有什么缺点，作为一个“正常”的妹子，如果不是姣好的面容和相当丰满的“欧派”，属于扔在大街上甚至不会招来多少目光的人。而三叶的小固执和对大城市的向往，也构成了这部《你的名字。》的基础。我们谈到一个人物的时候，必须要把人物放在其设定背景里来谈谈，宫水三叶同样如此，虽然在我们眼中，向往大城市并不奇怪。但作为三叶的朋友却基本上都不是这么想的。事实上在本部电影里，除了三叶以外，几乎没有任何一个人“向往大城市”的生活。在这种情况下，三叶这种性格就显得有些特殊了，而因为这种特殊的性格和特殊的身份，神社巫女+镇长女儿，让三叶在镇子里是一个很“显眼”的人。甚至算得上特异了。你想喝JK巫女的口嚼酒吗？前半段剧情结束以后，三叶和泷交换身体，三叶成功来到大城市，最终故事才正式开始了，而随着剧情进展，三叶的“少女心”逐渐萌发，而对自己心爱人的向往和在整个迪亚马特彗星冲击阶段的行动。而三叶的特性也完全展开了。对爱情的向往，患得患失的“吃醋”，以及为了心爱之人直接花费3万日元只为见一面的决绝，都让人非常怜惜了。东京到飞弹（系守原型）新干线单程就需要14790日元（大约900人民币），来回更是需要近3万日元了，这是三叶十五个月的零花钱了，如果她每个月只有2000日元零花钱的话，这3万日元甚至算得上是她一个高中女生所有的积蓄了。与之后泷为了三叶疯狂冲向系守相比。三叶为泷的奉献也一点都不少。这两个人才是真正的“为爱不顾一切”，也只有“为爱不顾一切”，最终才能得到一个好结果。之后的彗星冲击剧情是三叶真正开始展现魅力的一段。虽然帮助心爱男孩追到了妹子，但是自己却终于发现了心里的感情，在去东京以后发现男孩子根本不认识自己，于是三叶剪掉了头发，最终死在了迪亚马特彗星的直接冲击之下。如果是之前的新海诚，或许第一部分就在这里结束了。之后立花泷发现系守被毁以后痛苦的度过一生，而三叶也就和明里一样真正意义上的成为了一个“悲情”角色了，最终结果并不会好到哪里去。但新海诚这次还是给了一个重新挽救的机会。泷和三叶的拯救以及自救开始。虽然在整部《你的名字。》里，立花泷的行动力和执行力才是最重要的，但三叶的力量在后半段一样不可小视。冲向系守拯救村民，说服父亲，以及帮助整个村子人来到城市这些很重要，但却并不是我们所关注的重点。在“身体”边的小女儿态才是最让人关注的。而这也是三叶真正的魅力所在——在心爱人面前，她是真的可以“为爱不顾一切”的。而我们大多数人所追求的爱情，或许也正是这样的。这不是几次的问题啊，笨蛋（只要是你摸几次都没关系的！）真的能做到“为爱不顾一切”的，最终结果都不会太差。在泷和三叶拯救系守五年后（对于三叶来说是八年），两个人终于相遇了，后面的结果我们自然已经不用多说了。新海诚：恭喜两人喜结良缘，好了，可以把我脖子上的刀拿下去了么？三叶是一个“现实”的少女。毋须质疑，即使有那么多的特异功能和身份，但抛掉外壳，她还是一个“现实”的少女。而最终结局之所以能完全不同，自然是因为“为爱不顾一切”。相对于泷的行动。有各种束缚的三叶能如此，或许才是最勇敢的吧。新海诚终于讲了一个好故事。也设定出了一位真正的少女。不仅仅是二次元，现实也一样是如此，很多事情或许并没有那么复杂，只是生活无法让我们走到那一步。三叶是勇敢的，但如果三叶遇到贵树这样的男孩子，或许最终结果也并不会多好，也只有另一个“为爱不顾一切”的泷，才能和三叶冲破时间和空间的阻隔走到一起。在你需要JK少女的口嚼酒之前。你真的能够“为爱不顾一切”吗？这，才是宫水三叶真正的魅力。', '', '4097', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10664', '0', '结绳编发步骤图解 你的名字宫水三叶好萌', '要说起这个动漫，还要看日本的，真的是太好看的，最近的电影动漫你的名字也是成功的吸引了人的眼球，来看看你的名字宫水三叶结绳编发步骤图解是怎样的：新海诚的最新作品《你的名字。》在日本成为第一部票房破百亿的非吉卜力的动画电影，而且国内也已经确认引进。来看看日本妹子是怎样扎发的。头绳，也就是組紐，象征着联系、羁绊(即姥姥口中的“結び”)，交出組紐自然象征着产生羁绊。把头发放下来，从上到下都梳开，方便编发头发对分成两部分，在耳后稍高一点的位置编一条麻花辫。另一边也编起来，发尾不要编到底，可以留两寸以上的长度。然后把两条辫子交叉向上盘起来，并用发夹牢牢固定住。绑上红色结绳，打一个蝴蝶结。盘发的过程中，发尾可以露出来，形成一个小小的马尾辫，结绳可以用红色丝巾代替。你的名字宫水三叶结绳编发步骤就介绍到这里了，可以和闺蜜一起去看这个电影动漫，回忆一下高中时候的味道，你暗恋过的帅哥在哪里呢？', '', '4097', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10665', '0', '立花泷最终等到了他的宫水三叶 属于你的那个人现又在世界的何方', '今天刚看完这部号称日本2016年票房最高的《你的名字》,说实话这些年我也算得上是观影无数了，自己早对各种电影套路不再感冒，但是这部电影还是感动了我，不愧是豆瓣评分高达8.5分的神作（这个分数在挑剔的豆瓣影评中算是很高分了），爱情是人类永远的主题，我们内心都渴望纯洁而又美丽的爱情，看电影让我们经历了主角们的人生，和他们一起感受生活的喜怒哀乐，有时候仿佛我们就成了他们自己。故事的一开始，宫水三叶在糸守町过着单调乏味的生活，内心极度渴望大都市的繁华，有一天她突然和在东京的立花泷交换了身体，变成了一个在东京生活的帅哥，而立花泷也变成了在乡村上学的巫女宫水三叶，交换身体的他们两个发生了许多令人啼笑皆非的故事，但是有一天，宫水三叶在交换身体帮助立花泷和奥寺美纪成功约会后，自己却突然伤感得流起眼泪来，或许三叶的潜意识里其实是知道自己爱着立花泷的，把自己爱的人介绍给别人约会，内心是舍不得的，三叶给立花泷打电话一直打不通，立花泷给给三叶打电话也没有打通，原来他们的相遇是一场时空的错乱交合，立花泷活在2016年，而三叶在2013年已经因为彗星撞击地球遇难而死了。立花泷根据交换身体时见到的那些画面找到了三叶待那个乡镇，眼前却只有一片废墟，两个已经相爱的人被时空隔绝，一切终究不过是命运开得一场玩笑。故事发展到现在，我内心也跟着主人公们心情一样变得有些沉重，内心里很纠结，觉得如果立花泷和三叶没有在一起或许是一个令人叹惋的凄美结局，得不到的爱情总是美好的，让无数观众哀叹的结局或许更能给我们留下永恒的印象，但是心里又有另一个想法在斗争，还是盼望着他们能在一起吧，这两个高中纯真年代的美丽爱情，真心不想让他们陷入破碎，自己仿佛变成了三叶和立花泷，经历了这么多的困苦和磨难，真正的爱情就应该有一个美好的结局，好在接下来的剧情中，三叶和泷终于又是再次交换身体，回到了过去，改变了历史，导演很善良，不忍心让我们看到令人伤痛的结局，三叶活了下来，只是她和泷在现实中没有再见面了。转眼又到了2021年，泷大学毕业后参加了工作，自己曾经喜欢过的奥寺美纪已经和别人订婚（貌似是和藤井司订婚了，也就是泷的高中死党），敕使河原和青梅竹马的名取早耶香在一起了，可是泷还是一个人孤零零的走在街头，在一个街角，泷和三叶再次相遇，这次他们再也没有错过彼此，故事的结局是一个美丽幸福的结局，你等的那个人，最终和你相遇，这一切仿佛都是上天的刻意安排，你们的相逢是这么的恰如其时。泷等到了他的三叶，但现实中的你，属于你的那个人又在哪里呢，这个月的七夕谁又将陪你度过？ 你的宫水三叶现在又在这个世界的哪个角落，他或者她现在又在做什么呢？', '', '4097', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10666', '0', '辅导班用《君名》宫水三叶做代言？这巧合太过神似', '新海诚导演的动画电影《你的名字。》一直到现在都有着极高的人气，只要在宣传中有和《你的名字。》相关的元素，往往就能收到各种关注。近日有网友发现日本一家名为早稻田学习研究会的辅导班，在面向高中生的春季招生班的广告中，使用的一个动漫角色与动画电影《你的名字。》中的女主角宫水三叶十分相似。有不少网友到认为这是抄袭，还将两个角色进行了对比。不过这个补习班在接受采访时表示，他们的这个角色在《你的名字。》上映前就已经存在，只是在设计上雷同而已。有网友称补习班宣传单印着的少女酷似《你的名字。》宫水三叶，并称补习班少女除了表情和部分细节以外，大腿、长筒袜、书包的位置、发型发装、胸前蝴蝶结以及整体构图都与宫水三叶一样，还专门发出了“验证图”。通过个图片，我们确实能够看到在构图上，俩个角色有着惊人的相似度。早稻田学习研究会在接受采访时否定抄袭说法，称这个角色“小望”是公司的设计师设计的，只是雷同，并没有使用《你的名字。》角色，而且早在《你的名字。》上映前就有使用了。不过对于这种说法，大部分网友表示不能接受，毕竟从构图上来说真的是太相似了。果然是人红是非多，看来之后得有好一阵子能看到这样的“巧合”了。', '', '4097', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10667', '0', '宫水三叶 制服来袭', '宫水三叶cn@幺鸡w❀一周年❀『你的名字』· 前前前世摄影后期:@LBen_不打光会死星人 B站:L【幺鸡】♥一周年♥『你的名字』', '', '4097', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10668', '0', 'Cosplay《你的名字》宫水三叶第二波 动作表情高度还原', '最近呢，网友再次发了一组高还原的三叶。之前有发过第一波，好多评论说“怎么没有摸胸的！”哈哈，那么现在福利到了，来看看当男女互换后，三叶起床时的“床照”吧~先来看看动画里立花泷穿越到三叶身体里的时候吧~再送上一波神还原的三叶。哦不，应该是泷⁄(⁄ ⁄•⁄ω⁄•⁄ ⁄)⁄。请点击此处输入图片描', '', '4097', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10669', '0', '御坂美琴被黑得最惨的一次，日本有的作者是真该打', '御坂美琴，我炮姐。作为动漫界赫赫有名的萌王，她指间跃动的电光，是多少人此生不变的信仰。也是因为有了她的存在，《某科学的超电磁炮》作为衍生作品却力压正传《魔法禁书目录》。这样的一个角色，Tfboy的《信仰之名》光是胆敢山寨她的op，就能引起阵阵口诛笔伐。但是小编偶然发现的一部《某科学的超电磁炮》的同人作品，却是把我吓了一大跳。这部漫画对我炮姐的恶搞，简直到了“岂有此理”的地步。也就是这部《某拳法家的刚掌波》大概光看封面大伙就能从中感受到浓浓的恶意，炮姐被画成了一个满脸横肉的男人。这个封面图我并没有截全，全图是四个主角都全的，但是为了不一下子就给大家太多冲击，我就只截取了上半部分。这部漫画基本把《超炮》黑了个通透，连当妈也受到波及。（放张黑子回回血）这部漫画的恶意下，首当其冲的是小编超级喜欢的御坂妹妹，这个作者不仅恶搞人物，当然也恶搞剧情，炮姐，算了，这个恶搞炮姐的什么鬼主角，见到被克隆的御坂妹妹，说了句“居然敢搞本大爷的复制品”就上去把复制品们打了一顿，复制品们被打的全身是血。这个时候恶搞黑子的什么鬼出来了，用着黑子的语调和姿势，却是一个丑绝人寰、满是腿毛的家伙。要制裁那个恶搞炮姐的什么鬼。这个时候出来一个头戴花环的光头白胡子老头，和一个头上有花，不知道带着什么鬼面具的角色。看样子就是小初春和没有能力但是被奉为“掀裙祖师”，虽然常常掀裙但是都是自己看，从来没让观众看过的泪子了。简直了炮姐的成名绝技和称号“超电磁炮”被恶搞成“刚波拳”和“雷王拳”，这部漫画还煞有其事的描写了这什么鬼和风纪委员的类似格斗番的决斗，如何打败了对方的空间移动能力，然后对方被打的吐血。连呱太都逃不出毒手，呱太变成了呱太本体加上一匹马头的什么鬼，恶搞版的那什么鬼居然也是受不了这个呱太的诱惑，表示要“退下”，这时上条当妈出场，自然是免不了一番魔改，不过看了四个女主，小编已经麻木了。这本漫画在只是在那个什么鬼头上加了呆毛的“最终之作”出场后，页数以经到了最后，总算是完了，小编的眼睛火辣辣的像是用眼睛吃了一顿重庆火锅一样，心中有说不出的对这个世界的生无可恋。最令我难以释怀的是这书封底还有下集封面和一些其他动漫的恶搞漫画，书的作者和封底的文字都是日文，说明这是日本的漫画，而且这样的漫画能出这么多显然是有销量的。简直不敢想象画出这漫画的人和买这漫画看的人到底都是些什么鬼，这作者要是被我遇见我简直想揍扁他的狗头，不说了让我静静。', '', '1842', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10670', '0', '维密天使艾尔莎·霍斯卡现身纽约东村，红绿撞色搭配大胆', '当地实际6月5日，艾尔莎·霍斯卡现身纽约东村（East Village）。（图片来自东方IC）艾尔莎·霍斯卡出生于瑞典斯德哥尔摩，曾是篮球运动员。（图片来自东方IC）后来毅然转职成模特，并于2015年成为维密天使。（图片来自东方IC）如今艾尔莎·霍斯卡仍然是世界顶级模特，并未涉足影视业。（图片来自东方IC）绿色上衣与红鞋子遥相呼应，撞色搭配十分大胆。（图片来自东方IC）路人小哥强势抢镜。（图片来自东方IC）艾尔莎·霍斯卡现身纽约东村。（图片来自东方IC）艾尔莎·霍斯卡现身纽约东村。（图片来自东方IC）艾尔莎·霍斯卡现身纽约东村。（图片来自东方IC）', '', '4705', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10671', '0', '维多利亚的秘密艾尔莎·霍斯卡亮相的新造型', '艾尔莎·霍斯卡的新金发头发的新潮，肩扫LOB的发型。这位28岁的瑞典模特在8月10日的370万个追随者中窥见了她最新的形象。放送诱人深V也没用！坎达尔穿黄色纸袋裤被嫌弃她提出的情趣内衣和牛仔套裤为即将到来的节日收集维多利亚的秘密。Hosk，谁赢得了她的翅膀，2014个品牌的安琪儿，被称为总是事情升温在社交媒体上与她挑逗风格。“你穿着泳衣几乎常年为维多利亚的秘密工作，”她解释道。“有这么多的芽，我们总是在内衣，所以你总是有准备和准备。”这意味着，究竟是什么呢？这位前职业篮球运动员，这意味着很多很多的锻炼。“我的工作很多，”她坚持。“我做很多拳击，我做巴雷的课……我觉得我锻炼了，所以我想吃什么就吃什么。”Hosk并不是唯一一个搅拌标题为她传切。同Bella Hadid和Rosie Huntington Whiteley也被称为摇摆风格相同。超模嫩妹Lottie Moss礼服太火辣像「被狗啃过」？惊艳的超模礼服，没有用半片布料的衣服！十亿人都在期待的维多利亚的秘密，一起来欣赏世界名模的风采！', '', '4705', null, null, null, null, '0', '0', '0', '0', '0', '1529207743', '0', null), ('10798', '0', '在网上交友时，要注意这4点！', '现在是互联网时代，很多人交友都选择了网上，不仅仅是因为快节奏的生活模式和工作压力大，没有时间在现实生活中寻找优质单身男，还因为互联网时代把人与人之间的距离拉近了，成为了人们生活中不可分割的工具。\n而且，根据目前的情况来看，很多年轻人网恋的概率特别高，容易深陷其中。不过，就像很多人担心的一样，网恋也有它不靠谱的地方，比如某些人被骗了。\n那今天，我们就来聊聊选择网络脱单时，我们要注意的一些事情。\n\n一、用平常心对待\n之所以把这一点放在前面来讲，是因为有不少人过度痴迷网络。\n虽然网友是一种新型交友的方式，但把所有的情感都寄托在网络，以期排解自己内心里的寂寞和无聊，实在是不明智的行为。\n因此，希望大家用平常心对待网络，不要太过痴迷。\n二、感情不要太过投入\n天南地北的两个人，因为网络而相识，然后相谈盛欢，开始相恋。\n虽然网恋我并不反对，但在这里，我还是想要告诉大家，不要投入太多情感，除非你们发展到线下，了解了对方的实际情况，然后再投入也不迟，毕竟多个心眼总没错。\n\n三、不要轻易透露个人信息\n相信不少人都看过一些网络诈骗案例吧？如果你在网上轻易透露自己的信息，特别是你身家不错，还向他人泄露了自己的个人财产，那被骗的几率就会增大。\n所以，在网上不要轻易透露自己的个人信息，一旦出现以各种理由向你借钱的人，就要及时处理。\n四、时刻保持清醒\n所谓恋爱，说到底还是要走进生活的。相较于传统方式的交友，网恋，只是改变你们认识的方式，并没有改变恋爱的本质。它是一种渠道，甚至于是一个媒介，让你们认识，并有缘相恋。\n因此，不管你们在网上谈得有多么轰轰烈烈，不可自拔，都要时刻保持清醒，不要因为对方的几句甜言蜜语就对他死心塌地，毕竟这也是对自己的一种保护。\n\n所以，当你们选择网络交友时，大家要注意以上几点哦~', '8231', '8261', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10799', '0', '6大交友注意事项：', '一，对陌生人时刻保持警惕不要轻信。\n　　有时候直觉会欺骗一个人，尽量多沟通多交流，对对方作出充分的了解。\n\n　　二，选择合适的约会时间和约会地点是对自己最好的保护。\n\n　　选择合适的约会时间。尽量不要选择晚上，周末的白天时间是很好的选择。实在不行选择了晚上约会，一定要控制时间，不要拖得太晚。如果真的合适，来日方长，不要把自己耗得太晚。任何时候，不要强迫自己去做使自己感到不安的事情。选择合适的约会地点。请在你们约会前确定一个首要原则：选择公共场所约会，并告知自己的朋友或家人。您一定多次听到过这样的劝告：单独去一个陌生、偏僻的场所和陌生人约会是多么危险。特别对女生来说，选择自己熟悉的公众场所，比如学校附近，是最好的选择。在没充分了解前，一定不要到其处所，或任何可能危险的地方。任何时候，不要强迫自己去做使自己感到不安的事情。\n\n　　三，约会时要察言观色\n　　您不可能通过网络了解一个人的真实背景或真正性格，所以约会时察言观色是加深对对方感性认知的好时机。随时观察对方的任何特征：吹牛、叹气、过激举动、眼神、表情等等，建立正确客观的第一印象对你们今后的关系发展大有裨益。\n\n　　四，保持平常心\n　　缘分属于你的一定就是你的，一定不要过早过快地投入自己的感情和人身。不属于你的即使你付出所有，也什么都得不到，除了情感和身心的伤害。\n\n　　五，请勿轻易委身于人\n　　不管是男性还是女性，在恋爱交往的过程中往往难以控制住自己的热情，但是，太多的恋爱悲剧告诉我们：只有当您深深了解并且信任一个人的时候，您才能付出自己的全部。很多人都难以承受恋爱中情感和身心受伤带来的痛苦！所以，我们奉劝您珍惜自己的情感，克制自己，理性交往，只有这样，才更有可能收获幸福的爱情。\n\n　　六、请勿和网友发生借贷关系\n　　新闻中常常看到被网友骗取财物的事件，我们提醒大学生征婚网的用户切勿和网友发生钱财或者物品的借贷关系。?', '8231', '8261', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10800', '0', '绘画定义', '绘画定义 :\n　　1.作画。用笔等工具，墨、颜料等材料，在纸、纺织物、墙壁等表面上画图或作其他可视的形象。\n　　2.绘出的图或画。\n　　绘画是一种在二维的平面上以手工方式临摹自然的艺术，在中世纪的欧洲，常把绘画称作“猴子的艺术”，因为如同猴子喜欢模仿人类活动一样，绘画也是模仿场景。 在20世纪以前，绘画模仿得越真实，技术越高超，但进入20世纪，随着摄影技术的出现和发展，绘画开始转向表现画家主观自我的方向，看到一幅画鉴赏家有时已经说不清画的是什么，但一定能说出是谁画的。每个画家开始发展自己独特的风格。不同的画家风格不同，也有不同的价值。20世纪初，有些画家的风格因评画者不能接受而被冷落甚至贬低，这就是为什么有些画家画的很好而并不出名或甚至被冻死、饿死的缘故。', '8236', '8266', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10801', '0', '爱一个人，爱到八分最相宜', '太爱一个人，会无原则地忍受他/她，慢慢地他/她习惯于这种纵容，无视你为他/她的付出，甚至会觉得你很烦，太没个性，甚至开始轻视，怠慢，不尊重你……\n\n　　太爱一个人，你无异于一支蜡烛，奋不顾身地燃烧，只为求得一时的光与热。待蜡烛燃尽，你什么都没有了。而对方只是一个手电筒，他/她可以不断放入新电池，永远保持活力。\n\n　　太爱一个人，他/她会习惯你对他/她的好，而忘了自己也应该付出，忘了你也一样需要得到同等的回报――他/她完全被你宠坏了。不要以为你爱对方十分他/她也会爱你十分，爱是不讲道理的，所以很多时候，爱也是不平等的。\n\n　　不要爱一个人爱的浑然忘却自我。那样全身心的爱只应出现在小说里，这个社会越来越不欢迎不顾一切的爱。给他/她呼吸的空间，也给自己留个余地――飞蛾扑火的爱情，正在进行时固然让人觉得壮美，但若他/她成为过去式时，你如何收拾拿一地的狼籍？投入那么多，你能否面对那惨重的损失？\n\n　　所以，爱一个人不要爱到十分，八分已经足够了。剩下的两分，用来爱自己。\n\n　　爱是毒药，说，容易，做到，很难。', '8232', '8262', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10802', '0', '如何快速和十二星座交朋友', '朋友是指人际关系已经发展到没有血缘关系,但又十分友好的人,之所以成为朋友,不管性格是相同,还是互补,三观相同,有共同话题是非常重要的因素,拥有一个真正的朋友,在他面前,你可以安心地做一个真正的自己,随心所欲,而他能够微笑的倾听,不仅是听你外在的话语,也能了解内在的心情。那么,该如何和12星座快速的成为好朋友呢?今天,就请和美国神婆星座网的南烛一起来探讨一下吧。\n\n白羊座\n白羊座是热情阳光,积极向上的人,他们喜欢和朋友一起疯,一起闹,因此想要和白羊快速成为朋友,一得举止大方,坦诚,没有心机,开的起玩笑,玩得起游戏,还知道怎么捉弄人。白羊座爱面子,属于吃软不吃硬的人,因此作为朋友,可不能比他们还倔哦,懂得变通,示弱,关系才会一直友好和谐。\n金牛座\n对于朋友金牛座其实并不觉得特别的重要,在他们的心里和陌生人成为朋友,最好是可以等价交换的,比如和这个人做朋友,我可以在他的身上学到一些做人的道理,和另外一个人成为朋友则可以吃到美味的食物等等,他们不喜欢亏欠,在对方给予自己的同时,金牛座也会为对方办力所能及的事。\n双子座\n双子座交朋友尤其注重关系上的和谐,一致性,人一定要有趣,大方,不拘小节,像是那些一点小事都要叽歪半天,追究个不停的人双子座绝对立马和他说再见。双子座喜欢可以和自己一起玩的朋友,性格外向还是内向都无所谓,重点只要不是管东管西的老古板就可以!\n\n巨蟹座\n巨蟹座虽然看着对谁都热情有礼,但其实内心十分缺乏安全感,若有若无的保持着一定的疏离感,因此拥有强大气场,性格霸道强势的人并不能很快与之成为朋友,只有性格温暖阳光的人才会迅速的走进巨蟹座保守的内心。\n狮子座\n热情爽朗的狮子座同样也会喜欢和自己兴趣相近的人,一样的大大咧咧,一样的疯疯癫癫,一样的乐天无忧,和这个人在一起,狮子座每天都觉得很开心。狮子座对待朋友很坦诚,义气,因此他们希望对方也可以同样的有话直说,而不是什么都藏在心里,或者在背后搞一些见不得人的事情。\n处女座\n性格被动慢热的处女座在交朋友方面还是很谨慎的,怎样才能成为他们的朋友最重要的一点就是真诚,要从对方的眼睛里,行动上看出他是真心诚意的喜欢自己,有共同的兴趣爱好,在一起总是说不完的话,唠不完的嗑。\n\n天秤座\n性格温和淡然的天秤座喜欢与自己性格相反的人交朋友,比如双子座,射手座这类既阳光又有趣的人,他们很快就会被其吸引,当自己犹豫不决,拿不定主意的时候,朋友可以迅速的帮忙,当天秤座陷入困境的时候,通常都会默默地为难,默默的想办法,此时如果朋友可以及时出现帮他们一把,相信天秤座虽然嘴上不说,心里肯定会感动到不行。\n天蝎座\n外表冷酷,内心火热的霸道总裁天蝎座,最喜欢的就是傻白甜配置的朋友了,如果一个人性格与自己类似,一样的阴沉,寡言,疑神疑鬼,相信一辈子都不可能有过多交集的,相反,当一个乐观开朗,善良真诚的人出现,天蝎座的目光就会不自觉的被其吸引,不自觉的想要靠近,因为在这个人面前,他们会放下所有的防备,内心格外的满足与充实。\n射手座\n对于朋友遍天下的射手座来说,要想成为他们的朋友,其实很简单,主动出击就行了,一句话,我可以成为你的朋友么?射手座肯定高兴的不得了,能够结识不同性格,不同背景下的人,一只是射手座最希望做到的。\n\n摩羯座\n摩羯座的人安静内向,总是孤独的一个人待在角落,对于太过吵闹的世界他们下意识的排斥,因此在交朋友方面,如果那个人是个话唠,说话突突突突跟个机关桥似的,那么摩羯座一定会觉得这个人好烦,然后立马远离。要想快速的和摩羯座交朋友,最主要的还是三观相同,人要成熟,说出的话要有内涵,知道该做什么,不该做什么。\n水瓶座\n要想成为水瓶座的朋友,你得和他们一样没有节操,一样的脱线,一样的热爱自由,水瓶座是很重视精神交流的人,如果三观不同,那根本没有认识的必要。相反,假如两人惺惺相惜,水瓶座就会将对方视为知己,好好珍惜。\n双鱼座\n外表温和,内心敏感的双鱼座在交朋友时,最看重的就是这个人善不善良,有没有同情心,这样的人会令双鱼座抛下全身的防备,可以充分信赖对方,毫无顾忌与之成为朋友。', '8233', '8263', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10803', '0', '什么叫学习', '学习是透过外界教授或从自身经验提高能力的过程。\n\n行为主义心理学的观点\n\n学习是外在环境刺激引起的行为的变化，要有可观察、可测量的外在变化。古典制约和操作制约的研究。并且以个体获得经验为结果。\n\n认知心理学的观点\n\n学习是内在倾向或能力的变化，内在变化与外显行为并不完全一致。（当然内在的变化还必须由外在的变化来推测），这种变化应是后天经验习得的，不是先天的反应或者生理成熟的作用。这种变化要能保持一段时期。\n\n综合两大学派可以这样来定义学习：学习是因经验而引起的行为或内在倾向与能力较为持久的良性变化。', '8234', '8264', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10804', '0', '单机游戏的分类', 'RPG= Role-playing Game=角色扮演游戏\n　　由玩家控制单个或多个角色(比如FF和SM),,该类型主要要强调玩家个人角色的成长及故事(可以理解成小说中的主角)\n　　代表作品：最终幻想、勇者斗恶龙等\n　　-------------------------分支\n　　S.RPG：战略角色扮演游戏.这种游戏类型类似于下棋,所以也可以说是战棋类游戏.角色都在一个有固定范围的棋盘状地图上进行战斗,你如果选中其中1个角色,会出现\"移动\",\"攻击\",\"特技\"等选择,这就是最明显的标志.(三国志和其他同系列的游戏有些不同，,在下边会为大家解释)\n　　代表作品:很有名气的<机器人大战>,中国故事改编的<封神榜.爱藏版>等', '8235', '8265', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10805', '0', '吃货的定义是什么？', '吃货，指爱吃的人。多指喜欢吃各类美食的人，并对美食有一种独特的向往、追求，有品位的美食爱好者、美食客、美食家。其中“吃”字的“口”字偏旁由“碗”的形状构成；“吃”字的“撇”由“勺”的形状构成；“横”以及“横折弯钩”中的“横”，由“筷子”的形状构成；“横折弯钩”中的“折弯钩”，由“嘴”的形状构成。这个由“碗、勺、筷子、嘴”形状构成的“吃”字，会在瞬间给人留下深刻的印象，激发人们的“食欲”，形象地感受到中国的吃货文化。', '8237', '8267', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10806', '0', '文学作品与影视作品的区别', '影视剧本和文学原著在表现方式、剧情乃至结尾的不同之处，总是会给人以不同的感受。\n好的影视作品是一个众人共同参与的大组合，它不仅需要剧本展现的精彩扣人心弦的故事，也需要演技到位、符合剧中人物个性特点的适当的演员，还需要导演、编剧、制片人这些创作领导班子的全权详细的策划，灯光、摄像、化妆、布景师、场计等各个方面参与者的默契配合，形成一个有机的整体。而文学作品大都数是作者个人的事情，作者只需要把自己想要描写的故事，想要表达的观点，用自己的表达方式，用自己特有的文字话语、文学写作风格以书面的文学作品的形式呈现出来。这种写作是纯粹的个人行为，没有过多外界的参与、渗透与介入，整个创作过程不需要别人的指导、干涉，是由作者独立个体在自己的空间中独自创作完成。\n但是，一个好的文学作品并不意味着一定是好的影视作品，同样，一部好的影视作品也不意味着它的文学性就一定很强。这是由他们各自的特点决定的。\n影视作品主要面向广大观众，它的受众群体广泛，都是普通的老百姓。老百姓希望从繁忙的工作、琐碎的家庭生活中得到解脱，在影视作品中放松自己，以旁观者的角度观看别人的生活，欣赏别人的世界。他们接触但不进入的观看模式，可以使自己暂时沉浸在剧情中，忘掉现实生活的存在，使自己的心灵得到短时间的转化，片刻的休息。仿佛走进另一个世界，即使这个世界也存在竞争、死亡、事业、压力、不公平，但是因为那是别人的事情，距离观众很遥远，因而，就很容易作为观众而不会对其感受那么强烈。正是因为普通的百姓是受众群体，所以影视作品才要拍得好看，拍得刺激，拍得真切，才能引起观众的兴趣，迎合观众的品味，产生共鸣，最终保证收视率。文学作品改变成影视作品后，比较大的变动就是通俗意味增加，文学意味减少。那么，怎样使通俗意味增加呢，比较常见的就是增加作品的情节，使得平缓的叙述变得复杂，叙述的线索更加繁琐，矛盾也由单条走向多条。以激烈尖锐的情节，推动故事的发展，利用观众的好奇心，使观众一集一集看下去，直到结束。', '8238', '8268', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10807', '0', '十二星座交友注意事项', '白羊座：白羊座其实很钟意自己能有上流社会的样子，所以一般交友时候都爱探听你的身家，如果你是高帅富，他一定会百般跟你交好。\n　　金牛座：金牛座是闷不吭声赚钱的那种人，对于无聊的交际并没太大的热爱，除非，你是那个可以给他提供各种理财、赚钱资讯的人。\n　　双子座：双子座的交友看似五湖四海不挑人，但是其实一旦他锁定所谓的“精英”人群，他就更会舌灿莲花，各种幽默风趣让对方喜欢上他。\n　　巨蟹座：这个看似温柔的好好先生，喜欢出入舒适的场所，所以他交往的人，虽说不一定要权贵，但是至少也要是中产。\n　　狮子座：狮子座喜欢俊男美女是出了名的，最好还要有个什么头衔，这样，大家会觉得人以群分的狮子座肯定也很牛气。\n　　处女座：处女座最大的本事就是挑选。挑人一定挑对自己最有帮助的。如果只是酒肉朋友，对他的人生没有助益，他是不会费时间跟你交往。\n　　天秤座：天秤座的人要愉快是物质精神缺一不可的。物质上能对他有帮助、精神上能与他交流，只有这样的人才是他认定的“好朋友”。\n　　天蝎座：天蝎座除了自己几乎不会相信任何人。朋友对他来说各个领域都要有一些，有人脉好办事，但是交心，他觉得是很蠢的事。\n　　射手座：射手座交友三教九流几乎不挑。反正人人爱他的阳光和灿烂，他也乐得，每当有事，就电话朋友出来帮忙\n　　摩羯座：摩羯座喜欢结交领导、主管、上级。想掌权的心实在太强烈了，无论走什么样的途径，只要能让他成功就可以。\n　　水瓶座：水瓶喜欢结交比自己年长的人。年长的人无论是经验还是人脉，都能直接帮到他。所以你可以看到他三五不时就找“老人家”串门。\n　　双鱼座：双鱼座的交友几乎是储备庞大的的“备胎团”，暧昧无所不在，各种备胎可以解决他的各种难事，谁还能比他更功利？', '8242', '8263', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10808', '0', '宅物是什么意思', '从字面上不难理解，它就是私物，放在家里的东西。\n这样的词往往会用在二、三次元的地方。他们会把有关动漫、游戏类的模型叫作宅物。不同层次的人或许有不同的理解含义。欢迎大家补充\n', '8243', '8273', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10809', '0', '什么是动漫资讯', '动漫”是动画和漫画的合称与缩写。在其他语言相当少用。随着现代传媒技术的发展，动画（animation或anime）和漫画（comics，manga；特别是故事性漫画）之间联系日趋紧密，两者常被合而为“动漫”。\n动漫资讯依托于动漫资讯网站，是以报道综合最新动漫资讯，动漫周边和日本御宅族文化动态为主。有时也会报道世界其他地方的类似动漫新闻动态。它提供新闻阅读与评论，使动漫爱好者之间能够互动交流。', '8244', '8274', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10810', '0', '绘画会给人们带来的好处', '1。它让你学会处理一个人的生活。有了绘画，你不再孤单。没事的时候，拿起笔，集中精神，一画就是一两个小时。不仅仅能度过没事的时间，还能够从中得到异样的满足。\n2。变得自信。一个会绘画的人，就仿佛有一技之长。不论是在学校，是在培训班，在集体的环境里，只要有绘画的机会，你会发现你有自己的优势。所以你会更自信。因为这是一种技术。而不是泡沫。\n3。对事物的理解更深入。当你开始画画的时候，无法避免的是你要了解一种人物的内心感受，一种环境的效果和意境。以及一些效果的搭配。从中你会更理解人们的思维，更理解一些美术效果的原由。而这些原由可以让你更好地感受生活。\n4。视觉上的欣赏。学会绘画的人，他们已经把生活拆成了自己假想的片段。对某一种形状，某一种颜色，往往有超出现实和本质的理解。所以当你看到繁华的都市，看到璀璨的夜景，看到美丽的图片，你会产生一种从心底散发出来的感慨。你会对这些美好的东西感觉到一种别人不能理解的境界。因此你的生活将被一种艺术包围，而这种艺术让你的心灵保持宁静和丰富。', '8236', '8266', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10811', '0', '真正的交友之道', '第一、以德交友，患难与共：要如何交朋友呢？首先须问自己想交什么样的朋友。如果希望交到真心的朋友，就要拿出自己的真心，以道德、以义气、以慈悲来交往；如此得来的朋友，在最紧要关头时，大都能同甘共苦。所谓「患难见真情」，在最困顿的时候，还能不变初衷的支助扶持，才是真正的朋友。\n第二、以诚交友，肝胆相照：和朋友相处，彼此要讲究知心，讲究坦诚，讲究肝胆相照。双方以真实的言语、真实的感情交往，摈除利害关系，拥有手足般的义气情谊，能相知相惜，相互关爱，彼此扶助，就是真正的肝胆相照了。如战国时的隐士田光，为助燕太子丹刺秦王，举荐荆轲，更为守秘而刎颈，田光之忠诚，可谓肝胆相照。\n第三、以知交友，见多识广：见识广博或具有专业知识的人，会受到朋友的尊重与信赖。同样的，要结交有内涵的朋友，也要先充实自己的内涵；懂得随时吸取新知的人，智能容易开启，也会吸引许多见多识广的人到身边来。刘禹锡之交往即是「谈笑有鸿儒，往来无白丁，可以调素琴，阅金经。」\n第四、以道交友，法乐融融：「道风德香传千里」，有道德的人、有修养的人，无人不欣喜，不论远近，大家都会争相来亲近。和他交往的人，也都会依他自勉，而得到提升，此即所谓「与善友交，入芝兰之室，久而不闻其香，即与之化矣」。', '8231', '8261', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10812', '0', '天使的爱情', '天使的爱情\n\n天使尚且如此，人间该如何相爱呢？相信这个短小的故事，足以回答这个复杂的问题。\n\n从前，一位天使路过山涧的时候，遇到一位女孩。他们相爱了，就在山上建造了爱的小屋。\n\n天使每天都要飞来飞去，但他真的很爱这位女孩，得空的时候都来陪伴她。\n\n一天，天使带着心爱的女孩，在山间散步。忽然，他说：\"如果有一天，你不再爱我了，我会离开你。因为没有爱的日子，我活不下去。那时候，我就会飞到另一个女孩的身边。\"\n\n女孩看了天使一会儿，坚定地说：\"我永远爱你！\"\n\n他们的日子过得挺幸福。但是，每当女孩想起天使的那句话，就开始烦躁不安了。她总觉得天使说不定哪一天就会离开她，飞到另一个女孩的身边了。于是，一天晚上，女孩趁着天使熟睡的时候，把天使的翅膀藏了起来。\n\n天亮以后，天使生气地说：\"把我的翅膀还给我！为什么要这样？你不爱我了，你不爱我了......\"\n\n\"我没有，我还是爱你的！我没有藏你的翅膀，真的，相信我好吗？\"\n\n\"你骗人，你说谎，我不相信你了，我感觉你不爱我了！\"\n\n当他从柜子里找出翅膀后，就头也不回地飞走了。\n\n女孩很难过，也很怀念那段美好的生活。她后悔了，就独自坐到山头的风口上，默默地忏悔：\"纵然我爱你爱得发狂，也不能剥夺你自由飞翔的权利，是吗？我应该给你足够的自由，让彼此有喘息的空间。我现在真的懂了，你还能回来吗？......\"\n\n忽然间，天使出现了。他温柔地说：\"我回来了，亲爱的！\"\n\n\"你真的不走了，真的还爱着我？\"\n\n天使微笑着说：\"我感觉到，你还是爱我的，对吗？只要你还爱着我，我就一直爱着你，直到你又不再爱我的时候。\"', '8251', '8281', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10813', '0', '动漫和漫画的区别', '动漫是动画和漫画的合称，漫画是用简单而夸张的手法来描绘生活或时事的图画。\n\n【拓展资料】\n\n动漫：\n动漫，即动画、漫画的合称，指动画与漫画的集合，取这两个词的第一个字合二为一称之为“动漫”，与游戏无关，并非专业术语。  其中，日本动漫是动漫领域中的典型代表，是动漫领域的领军人。\n\n漫画：\n漫画，是一种艺术形式，是用简单而夸张的手法来描绘生活或时事的图画。一般运用变形、比喻、象征、暗示、影射的方法。构成幽默诙谐的画面或画面组，以取得讽刺或歌颂的效果。\n\n漫画作为绘画作品经历了一个发展过程，从最初作为少数人的兴趣爱好，已成为人们的普遍读物，更是学生的最爱，并成为了漫画控。近年的作品主导一般为日本漫画和美国漫画。', '8252', '8282', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10814', '0', '小说的分类？', '按篇幅及其容量分，可分为长篇小说、中篇小说、短篇小说和微型小说。\n按内容分可分为历史小说、现代小说、科幻小说、公案小说、武侠小说、言情小说、传奇小说等。\n按写作体例分，可分为章回体小说、日记体小说、书信体小说、自传体小说等。\n按语言形式分，可分为文言小说、白话小说等。', '8253', '8283', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10815', '0', '影视动画专业一般要学什么课程？', '　影视动画是很有前景的产业，涉及行业非常广泛，如：电影特效，漫画创作，平面印刷品的设计，三维动画，虚拟仿真，多媒体技术，网页制作等。\n　　本专业培养掌握影视动画创作的基本理论和基本知识、影视动画设计及制作的技能；具有影视动画片的编导能力和初步的创作能力，并具有较高审美能力和艺术修养的应用型高级技术人才。在动画制作公司，影视公司，网络游戏公司，电视台，大型企业、事业和部队等单位，从事影视动画的设计与制作的相关工作。\n　　主要专业课程：传统动画原理、角色设计、场景设计、广告设计、视听语言、表演、脚本创作、影视场景建模、三维角色动画、影视材质特效等。', '8238', '8268', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10816', '0', 'ACG是什么意思？', 'A=Animation 动画 C=Comic 漫画\nG=Game 游戏\n因为这三者一直以来都是密不可分的 所以平时都是放在一起使用', '8255', '8285', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10817', '0', '推荐一下好看的美食番', '日式面包王，美食的俘虏，食戟之灵', '8258', '8288', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10818', '0', '什么是泡面番', '泡面番，就是一集时间很短的动画，通常在三分钟到六分钟左右不等，相当于泡一杯方便面的时间，等你把泡面泡好了， 动画也完了，因而被许多动漫爱好者如此戏称。\n部分日本动画制作公司以几集短篇连载动画作为Lite版，以测试市场反应，为正式版推出做准备，也会被称为泡面番。', '8259', '8289', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10819', '0', '日本动漫定义', '日本动漫，是日本动画（Animation）和日本漫画（Manga）的合称，日本动漫凭着剧情的创新和趣味性在全球占有着重要地位，是世界动漫文化的领军人。\n日本是典型的动漫强国，其动漫发展的模式具有鲜明的民族特色而不失创新和吸引力。动漫是日本的重要产业链，在全球鲜有对手。日本动漫的发展过程大致可分为4个阶段：萌芽期、探索期、成熟期、细化期。', '8260', '8290', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10820', '0', '', '随着路飞15亿的悬赏金额爆出来，路飞在所有已知的悬赏令金额中是最高的，路飞成了蛋糕岛篇最大的赢家，人称“路亿十五”。目前已知的10大最高悬赏令，路飞排名第一。\n\nNO.1：路飞\n在蛋糕岛篇中，路飞的一次营救山治行动，被摩根斯吹捧为捣毁四皇行动，并挖出了草帽大船团的秘密以及其他附属势力，加上路飞先后击败了8亿多的饼干果实克力架和10亿多的糯糯果实卡塔库栗，最终路飞被悬赏出了15亿的高额悬赏，荣登十亿俱乐部，成为目前已知的悬赏最高的海贼。\n\nNO.2和NO.3：卡塔库栗和杰克\n同样身为十亿俱乐部的还有卡塔库栗和杰克，四皇大妈海贼团三将星之首卡塔库栗悬赏10.57亿贝利，目前在悬赏榜上排第二，四皇百兽凯多麾下三灾之一旱灾杰克悬赏10亿贝利，排第三。据小道消息戏称，杰克之所以悬赏10亿，是因为其象牙价值9亿。\n\nNO.4和NO.5：斯慕吉和克力架\n悬赏金额紧追其后的自然是同为三将星的斯慕吉和饼干果实克力架。斯慕吉被海米们戏称为划水果实，整个蛋糕岛篇章中，斯慕吉作为三将星是出力最少的一个，通篇在划水，而克力架是路飞打倒的第一个将星。\n\nNO.6和NO.7：佩罗斯佩罗和萨博\n佩罗斯佩罗是三将星之下最高悬赏金额，高达7亿贝利，他是糖果果实能力者。再接着就是革命军二把手萨博的悬赏令了，高达6.02亿贝利。萨博的悬赏令并没有在正片中出现过，它是《海贼王》20周年杂志先行情报中透露出来的。\n\nNO.8、NO.9和NO.10：斯纳克、艾斯和青椒\n悬赏榜排名第8、9、10的分别是四皇大妈海贼团的原四将星斯纳克、原四皇白胡子海贼团的二番队队长艾斯和传说中的大海贼青椒，青椒巅峰时期的战力是超过5亿贝利的。', '', '4018', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10821', '0', '盘点六大“火影最强双人组”，鸣人佐助只排第二，第一当之无愧！', '这里给大家带来“火影忍者最强双人组排行榜”系列。\n6、卡卡西和凯\n\n和所有人都五五开的卡卡西，还有体术最强的八门凯，鉴于卡卡西忍界大战的能力大部分还是来源于带土的馈赠，凯皇则是需要牺牲生命换取的力量，所以这对组合相对排得后一些....\n5、AB组合\n\n四代雷影在雷遁护体下拥有顶尖的速度和防御，搭配上完美八尾人柱力奇拉比击败过无数强敌！\n4、长门小南\n\n佩恩六道的秘密没有公开前可谓是无敌的存在，找不到真身你也奈何不了他，佩恩杀掉自来也，单挑木叶全村硬扛八尾鸣人，轮回眼毕竟是神领域的瞳术，可惜最后长门被口遁说服去领了便当，至于小南…，六千亿张爆炸符就问你怕不怕。\n3、宇智波斑和千手柱间\n\n上古时代的两个BUG人物，两个人合作已经可以在当时的忍界为所欲为，打尾兽也跟玩似的....\n2、旋涡鸣人和宇智波佐助\n\n主角光环二人组，两种力量的传承，合力打赢了最终BOSS大筒木辉夜，成功弑神终结忍界大战。\n1、大筒木羽衣和大筒木羽村\n\n辉夜的两个儿子，都是忍者之神的存在，分别掌管着阴阳的力量，分别继承了辉夜的轮回眼和白眼，六道仙人选择了在人间传承忍术，而羽村选择了移居月球陪伴母亲。', '1', '4019', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10822', '0', '最近很火一部动漫 一拳超人好看在哪', '第一点，这部动漫的战斗场景设计非常新颖，以往的英雄题材很多都是艰难的突破一个个难题，在某次战役或者闯关中领悟了某种道理和技能，在一大堆独白之后迅速解决战斗，可能是这种场景运用次数太多了，观众失去了新鲜感从而造成心理上迫切期待新的战斗场景。\n第二点，人物设定独特，很多动漫迷在小的时候或多或少都有中二期，心中有着自己期待的英雄，而且都是希望他无可替代，非常强大。琦玉老师设定就很好满足了这些人的要求，毫无原因的强，并且的强大到没有任何办法颠覆。\n第三点，画面精良，尤其是打斗场景不光好而且多。就和喜欢漫威电影的（除了死忠粉以外）有多少是冲着剧情去的，不都是想去看看帅气的人物设定和宏大的场景特效，一拳超人也不例外，首先不可否定的便是这部动漫的打斗高潮的部分非常密集，基本上做到了集集都有，这都很好地吊起了观众的胃口，尤其是热血向的动漫迷。', '2', '7072', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10823', '0', '银魂最燃的几集', '红樱篇58～61，超经典，真选组动乱篇101～105，吉原炎上篇139～146蜘蛛篇177～181也都很棒，还有星海坊主篇40～43，芙蓉篇69～71，白血球王篇167～170，四天王篇210～214都很不错~', '3', '4020', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10824', '0', '大家觉得刀剑神域好看吗？', '刀剑神域既是一部感情戏，又是一部打斗戏，个人觉得前面非常精彩，但到了后面就有点变态了，(因为男主角：桐人和女主角：亚丝娜睡在一起，不知道有没有那个)总之这部戏真的还可以，值得大家一看！！！可能还合你们的口味喔！！！', '4', '0', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10825', '0', '柯南剧场版都有哪些？', '剧场版有：\n\n1、引爆摩天楼    2、第14个目标3、世纪末的魔术师4、瞳孔中的暗杀者5、通往天国的倒计时6、贝克街的亡灵7、迷宫的十字路 8、银翼的魔术师9、水平线上的阴谋10、侦探们的镇魂歌11、绀碧之棺12、战栗的乐谱13、漆黑的追踪者14、天空的遇难船15、沉默的15分钟16、第11个前锋17、绝海的侦探18、异次元的狙击手19、业火的向日葵20、纯黑的噩梦', '5', '7029', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10826', '0', '集齐七龙珠后怎么办？', '集齐七龙珠后，可以召唤神龙，实现愿望。\n召唤神龙的咒语：\n地球神龙：出来吧，神龙。\n那美克星神龙：\"出来吧，神龙。\"的那美克星语。发音是Takkaraputo,popporunga,pupiritto,paro。\n神龙：\n相传，世界上散落着7颗龙珠，各有一至七颗不等的五角星标记，集齐7颗龙珠念出咒语，就能呼唤出神龙，并能向其许一个愿望。（除了超出神龙能力范围之外愿望，如《复活的融合！悟空与贝吉塔》中，愿望并未达成。）神龙实现愿望后，7颗龙珠便会自动飞散，变成石头，一年后才可再次使用。', '6', '8043', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10827', '0', '死神动画 几集 主线 ', '死神代理篇（001－020，漫画剧情篇）\n尸魂界潜入篇（021－041，漫画剧情篇）\n尸魂界激斗篇（042－063，漫画剧情篇）\n破面·初现篇（110－127，漫画剧情篇)\n虚圈潜入篇（138－146，漫画剧情篇）\n破面·激斗篇（152－167，漫画剧情篇）\n虚圈十刃激斗篇（190－203，漫画剧情篇）\n过去篇（206—212，漫画剧情篇）\n空座町守护篇（215—226，漫画剧情篇）\n十刃决战篇（266—283，漫画剧情篇）\n空座町乱战篇（288—，漫画剧情篇）', '7', '7001', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10828', '0', '黑执事经典语录', '夏尔：\n一旦失去的东西，就再也拿不回来了。\n我不喜欢游戏半途而废。\n我不打没有把握的球。\n即使报了仇，死去的人也不会复生，更不会高兴。\n我在这里，因为这是我的愿望，是我的选择。我没有后悔，所以我不能撒娇，不论是对谁。\n我一个人站得住。我只是……觉得……有点疲惫。\n瞬间的犹豫可是会丧命的，所以我决不会犹豫。\n我不会停滞不前，踏出的每一步我都不会后悔。', '8', '8042', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10829', '0', '进击的巨人第三季播出时间', '电视动画《进击的巨人》改编自谏山创原作的同名漫画，由WIT STUDIO负责制作。\n\n动画第一期于2013年4月7日起在每日放送首播，全25话；第二期于2017年4月1日起每周六21：00在TOKYO MX首播 [1] ，全12话；第三期于2018年7月22日起每周日在NHK综合频道首播，全24话。 [2]  三期动画累计61话。', '9', '7239', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10830', '0', '为什么overlord第三季这么快出', '2015年出的第一季动画，当时出动画的目的是推广小说，结果大获成功，该作品人气爆增。中间两年多的时间该作品人气一直居高不下，所以就有了一月番的第二季，但本季度疯房子三开，投资大头明显在另两部作品那里，overload第二季的制作颇有点边角料凑数的粗糙感，但即使这样大部分观众依然买帐，并且人气再一次爆棚，迎来新一波高峰。如果说第一季是给小说打的广告，第二季就是时隔两年后对市场的试探，结果不用多说，b站6000万的播放量在那儿摆着。现在这个ip人气已达到出继续动画稳赚不赔的程度了，近几年来少有轻改作品能做到这个地步。观众想看，公司有钱赚，为什么不继续出呢？', '10', '1005', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10831', '0', '工作细胞讲了什么', '在一个人的身体中，约有60万亿个细胞，每种细胞都有各自的任务……冒失又慌张新人红血球小姐，在她差点被肺炎链球菌袭击时遇到了冷酷的白血球先生。白血球不仅在她差点被肺炎链球菌袭击时，救了她一命，还顺道帮她带路到肺部……当遇到擦伤时，就需要可爱的血小板妹妹帮忙让伤口愈合……本书将细胞及病菌拟人化，以深入浅出的方式介绍人体细胞的功能！！', '11', '7212', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10832', '0', '杀戮天使 zack为什么跟着ray跑', '之前是因为Zack看到ray“充满生机”所以想杀她，追着她跑。\n之后就是ray知道爸爸妈妈死后自己也想死，但是自杀是上不了天堂的，Zack又正好想回到地上，所以她和Zack达成约定ray帮Zack出去Zack就杀了ray。然后就一直跟在ray后面辣。', '12', '7208', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10833', '0', '战栗杀机讲了什么', '故事的主角是亚修·林克斯、奥村英二故事是以美国纽约为舞台。但故事的背景中涉及到越战。越战期间，美国士兵葛利夫·卡林斯突然以机关枪扫射同队士兵造成死伤，在一旁的马克斯·罗伯只好开枪射击他的脚来阻止其行动。而葛利夫突然说出了 BANANA FISH 这句不明不白的话。葛利夫?卡林斯就是亚修的哥哥，亚修是纽约贫民窟街头儿童集团的老大，日本人奥村英二在找街头儿童取材之际认识了亚修，而亚修一直追踪哥哥嘴里说的 BANANA FISH 到底是什么意思', '13', '7225', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10834', '0', '动画轻羽飞扬讲了什么', '少女们飞奔、跳跃、疾跑——\n作品描绘了以国际高中比赛为目标的县立北小町高中羽毛球部的轨迹。\n运动神经超群，但却回避着羽毛球的一年级生羽咲绫乃。\n以日本第一为目标，日夜练习的三年级生荒垣渚。\n支撑着羽毛球部的伙伴和教练，\n还有个性洋溢的对手们。', '14', '7227', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10835', '0', '天狼讲了什么', '昭和初期，帝都·东京府。怀抱乐器盒的奇妙团体，降临在东京车站。他们，是为了狩猎吸血鬼们而来的“猎人”——。\n其中有一位格外沉静，散发出非同寻常气氛的青年。身为狼人和人类的混血儿，被吸血鬼摧毁了故乡的那个男人，名为尤里。\n围绕着名为“天狼之匣”的圣柜，与吸血鬼展开死战的尤里等人。他们在战斗的最后得知的真相会是……？悠远的恩怨和策谋的螺旋相交错。至极的悬疑动作剧，于此开幕！', '15', '7209', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10836', '0', '为什么这么多人喜欢《魔道祖师》', '因为魔道祖师跟许多的重生文走向不一样，而且情节丰富\n里面的配角也很出彩，人物性格都很鲜明', '16', '8035', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10837', '0', '《全职高手》主要讲的是什么？', '《全职高手》讲述了网游顶尖高手叶修，遭到俱乐部的驱逐，而后在荣耀新开的第十区重新投入了游戏，召集一群志同道合的伙伴，重返巅峰之路的故事。', '17', '8032', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10838', '0', '斗罗大陆绝世唐门讲了什么', '这里没有魔法，没有斗气，没有武术，却有武魂。唐门创立万年之后的斗罗大陆上，唐门式微。一代天骄横空出世，新一代史莱克七怪能否重振唐门，谱写一曲绝世唐门之歌？\n百万年魂兽，手握日月摘星辰的死灵圣法神，导致唐门衰落的全新魂导器体系。一切的神奇都将一一展现。\n唐门暗器能否重振雄风，唐门能否重现辉煌，一切尽在绝世唐门！', '18', '7243', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10839', '0', '狐妖小红娘的剧情简介', '《狐妖小红娘》的故事围绕人与妖之间的爱情展开。根据古典小说记载，世上有人有妖，妖会与人相恋，妖寿命千万年，人的寿命有限，人死了，妖活着。人会投胎转世，但投胎以后，不记得上辈子的爱。妖如果痴情的话，就去找狐妖“购买”一项服务，让投胎转世的人，回忆起前世的爱。 狐妖红娘这一个角色就这样诞生，作品主要讲述了以红娘为职业的狐妖在为前世恋人牵红线过程当中发生的一系列有趣、神秘的故事。', '19', '7277', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10840', '0', '一人之下是中国拍的?', '一人之下》是2015年2月26日开始在网络平台腾讯动漫上连载的一部网络漫画作品，作者是米二。 2016年8月开始由浙江人民美术出版社发售漫画单行本。 作品亦改编为同名电视动漫。\n\n中日合作改编的电视动画：\n\n电视动画《一人之下》改编自米二创作的同名网络漫画，动画由日本动画公司Pandanium负责制作。\n\n中国网络版于2016年7月8日起每周五在版权网站更新国语版；7月15日起每周五更新日语版。日本电视版于2016年7月9日起每周六20：00在TOKYO MX首播。\n\n第2期《一人之下2》于2017年10月27日起在网络更新，动画制作更换为上海绘界文化传播有限公司。', '20', '7361', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10841', '0', '尸兄故事讲了什么', '两个盗墓贼挖出了个棺木，想要卖给老外结果在途中棺木从车厢里掉出来，进了水库，由此污染了整个中心城区的水厂，凡是喝了水的人都变成了丧尸，并且可以吞噬别人，达到进化。\n白小飞本是H市一个普通的屌丝，因没喝自来水而逃过一劫，为了找到打电话与其分手的女友小薇，白小飞踏上了与全城僵尸对抗的冒险征程.', '21', '8033', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10842', '0', '《秦时明月之天行九歌》主要讲的什么？', '络3D动画《天行九歌》，是由电视/网络3D动画《秦时明月》系列原制作团队杭州玄机科技信息技术有限公司打造的姐妹篇，两作在剧情上互通，但在品牌上各自独立。作为秦朝题材3A级武侠3D动画大作《秦时明月》的双子星品牌，《天行九歌》定位为战国题材3A级玄幻3D动画大作，故事以韩国公子韩非建立的“流沙”组织为主视角，展开六国对抗秦国的众生百态，引领观众进入战国末年风起云涌、群雄并起、百家争鸣的大时代中。', '22', '7321', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10843', '0', '动漫武庚纪是根据小说改编的吗', '动漫武庚纪不是根据小说改编的，是根据武庚纪的漫话《封神记》改编的。\n\n商朝末年，纣王断然拒绝玄差送来的\"天\"之神谕，将之投入火炬里烧毁，公然发布反抗神族之宣言。神族之主--\"天\"为此勃然大怒，带领神族的天间六部与周国军队来讨伐商国。\n\n骁勇善战的纣王被天界六部的斗部天魁一拳打入城墙之内，人类绝对承受不住如此重击!天界各部大神纷纷露面，嘲笑人类是\"可笑的猴子\"，对商兵展开了大屠杀。没想到这时纣王突然\"复活\"了，他的身上似乎是传说中的不死鸟盔甲!\n\n网络动画《武庚纪》是由沈乐平执导，杭州玄机科技信息技术有限公司根据香港漫画家郑健和的经典封神题材漫画(大陆简体字版更名为\"武庚纪\"连载于掌阅、腾讯动漫)改编创作的3D玄幻动画。该动画以\"超强脑洞封神演义\"为定位，力图为观众们演绎一出封神题材3D魔幻史诗巨制。动画在漫画原著基础上，沿袭中国古代神话背景，以纣王之子武庚不屈于神的奴役，追求独立自由生活而不断战斗的热血传奇为起点，穿插了人、神、冥三大种族为了争夺生存、自由和荣耀而不断战斗的千年传说。', '23', '8031', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10844', '0', '秦时明月介绍', '　《秦时明月》的故事背景取自秦始皇帝灭六国到西楚霸王项羽攻陷咸阳这段英雄辈出的历史时期，是一部以武侠为主题的全民型动漫，伴随着古灵精怪却又年少轻狂的主角荆天明去冒险，观众会切身体会到其成长的心灵历程；此外，豪侠剑客如盖聂、高渐离；神秘隐士如墨家、阴阳家、及各门各派的众多高手；纵横捭阖的秦始皇、少年项羽（即项少羽），这些众所周知的历史人物在出场时有了更具时代感的表现。丰富的人性化场景能够吸引观众自然而然融入其中，不同年龄层的观众都可以在剧中找到萌点。值得一提的是，整部动漫以一种别样的方式诠释了诸子百家文化思想的精髓，对现代中国产生深远影响的墨家、儒家、道家、法家、兵家、纵横家、阴阳家、农家、医家、名家等全都纷纷登场。在那段伟大辉煌的时代里，建长城、度量衡、征匈奴，哭孟姜等著名的历史事件和民间传说也将穿插其中。主人公同名士侠隐仗剑游走江湖，于时代变革的乱世之中经历了亲情、友情、爱情，正义的沧桑变幻、悲喜轮回，最终成为了终结这个时代、开创新纪元的决定性力量，也堪称是一段浪漫辉煌的中国式武侠历史传奇。\n　　故事背景', '24', '4017', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10845', '0', '小马宝莉里面的六个不同颜色的小马分别叫什么\n', '苹嘉、紫悦、珍奇、云宝、碧琪、柔柔。', '25', '8040', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10846', '0', '海绵宝宝介绍', '海绵宝宝是方块形的黄色海绵，住在比基尼海滩深海的一个菠萝(凤梨)里，他的宠物是一只会喵喵叫的小蜗，海绵宝宝喜欢捕捉水母，职业是蟹堡王里的头号厨师。派大星，章鱼哥和姗迪都是他的朋友。海绵宝宝总是能给平静的比基尼海滩制造麻烦，虽然闹出一些笑话，不过他总能摆脱困境，然后又制造新的麻烦......', '26', '8036', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10847', '0', '瑞克与莫蒂介绍', 'Rick和Morty是由 Justin Roiland和 Dan Harmon为卡通网络深夜节目成人游泳创作的美国成人动画科幻情景喜剧。该系列追随着疯狂科学家瑞克·桑切斯（ Rick Sanchez）以及他容易受影响的孙子莫蒂·史密斯（ Morty Smith）的脚步，展现了将他们的国内家庭生活和跨维度冒险的奇幻经历。Roiland为以上二人配音， 而Chris Parnell， Spencer Grammer和 Sarah Chalke为家庭的其他人物配音。\n这个系列源于“ 回到未来 ” 的动画短片，模仿电影 “ Doc and Mharti的真正动画冒险”，由Roiland创作的短片电影节101频道，恰好由Harmon共同创立。当成人游泳接近Harmon电视节目的想法时，他和Roiland决定根据短片开发一个节目，同时进行一些改动。\n\n2014年1月，系列重新播出了第二季，于2015年7月26日首播。2015年8月，“成人游泳”续延了第三季，由10集组成。它于2017年4月1日首播，预计将于2017年7月30日开始播出。该节目的创意和幽默都获得了广泛的赞誉。', '27', '8038', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10848', '0', 'RWBY红白黑黄里的角色有哪些', '主要的角色：\nRuby Rose（露比·罗丝/鲁比·洛斯） 这是队长\nWeiss Schnee（魏丝·雪倪）\nBlake Belladonna（布蕾克·贝拉多娜）\nYang Xiao Long（阳小龙）', '28', '1229', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10849', '0', '辛普森一家讲了什么', '辛普森是美国最普通的一个五口之家：爸爸荷马·辛普森是一个简单到不能再简单的男人，在斯普林菲尔德的核能工厂勉强算是有一份工作，只是在同一个职位上被解雇和再次聘用了无数次……荷马在酒馆中花掉的时间远比在家里多得多，因为只有在那里，他才能受到别人的欢迎--前提是他消费了。酒馆是一个完全能够让荷马放松的地方，他可以用自己钥匙发出难听的声音，可以把鼻涕擦在衬衫上，可以一杯接一杯地品尝散发着诱人香味的啤酒。荷马将那里当成一个只要喝掉几杯啤酒、吃掉一两个卤蛋就能让一切看起来都变成可能的地方：毕竟生命太短暂了，不可能慢慢攒钱，所以他的愿望就是：天上掉馅饼，一夜暴富。\n　　妈妈马芝·辛普森属于那种成功男人背后的完美女人，是辛普森家成员之间最可爱的“黏着剂”，用一种不可思议的乐观态度支持着每一位家人。她拥有着一种非凡的家务技能，总可以创造奇迹，能够将鸡骨变成时髦的项链、将剩菜剩饭变成人间美味，就连火蚁的入侵，也被变成了一场教育意义和娱乐性并存的昆虫马戏。尽管荷马自私自利，经常忘了家人的生日、纪念日甚至是假日；尽管他喜欢张着嘴嚼东西、嗜赌，还经常和一群与他一样处在底层社会的人跑到酒馆喝酒……但马芝从没有想过有一天离开荷马，她相信这一定是爱情的力量。面对荷马制造的无数次混乱，马芝从没有放弃过希望。\n　　大儿子巴特·辛普森是个喜欢恶作剧的小鬼头，对于别人说他过于依赖电视的指责充耳不闻，他有许多“爱好”，例如在减肥中心吃冰淇淋、升级了吐痰的“艺术”、站在房顶手拿棒球棍保卫自己的家不受UFO的袭击……作为一名执着不悔的捣蛋鬼，巴特受到了很多人的排斥，但是他不在乎，他只在乎，并恐惧着--有一天会良心发现，变成好人。\n　　大女儿丽莎·辛普森是智慧与理性的典范，可是她却是这个家里最容易被忽视的人--除非荷马或巴特为了实现自己那荒唐的计划需要借助她的头脑。丽莎只好将希望转移到了学校，企盼自己独特的能力可以在这里受到重视。情况表面上看确实如此，因为她的老师总是用最好的成绩“A”来奖励她的机灵与天分，可是在态度上却仍然是全权的冷淡。至于她的同学，却对她过分“注意”了，总是给她起各式各样的外号。丽莎只好将悲伤和失望全部化为力量，她喜欢吹萨克斯来疏导自己的情绪，还是曲棍球队的明星守门员。虽然模范学生丽莎远没有淘气的巴特受到的关注多，但两人却是最坚实的同盟，因为在巴特一连串恶魔似的计划中，丽莎是他永远的帮手。\n　　刚刚出生没多久的小女儿麦琪·辛普森总是用平静来抵抗家里的所有混乱，但一切都在她学会说话后有了改变。当荷马照顾麦琪时，她就可以自由自在地做任何她想做的事了，包括从狗盆里喝水，完成她的“探险”任务……麦琪只和最理解她的人做朋友，所以她的朋友就只有一只被称做“圣诞老人的小助手”的狗和一只叫“雪球二世”的猫。\n　　一切故事还要从荷马以及他新养的宠物猪说起，这一次，他的愚蠢行为引发了一场环境危机，不仅仅让家人陷入了危险之中，还差点把他的家乡斯普林菲尔德从地图上永久地抹去……', '29', '8039', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10850', '0', 'we bare bears讲了什么', 'We Bare Bears》（咱们裸熊）是Cartoon Network于2015年7月推出的电视卡通，改编自Daniel Chong创作的网络漫画《The Three Bare Bears》。\n卡通讲述一只叫Grizzly的热血棕熊、一只叫Panda的死宅熊猫、一只叫Ice Bear的高冷北极熊，这三只熊的日常生活故事', '30', '8037', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10851', '0', '利威尔战斗集数', '动画\n04 解散式之夜\n09 左臂的下落\n13 原初的欲求\n13.5(总结篇) 从那一天开始\n14 依旧无法直视对方\n15 特别作战班\n16 现在究竟该做什么\n17 女型巨人\n18 巨树之森\n19 紧咬不放\n20 埃尔文·史密斯\n21 铁槌\n22 失败者们', '31', '7239', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10852', '0', '骨傲天，老骨是什么？', '《Overlord》是日本轻小说家丸山くがね所著的奇幻冒险小说，原为网络连载，后改编成文库轻小说，于2012年7月30日出版该作品\n骨傲天就是这部动漫《overlord(又名不死者之王》主角', '32', '1005', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10853', '0', '血小板是哪个动漫角色', '血小板是由清水茜所创作漫画《工作细胞》及其衍生作品的登场角色。', '33', '7212', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10854', '0', '魏无羡是谁', '魏无羡是原创耽美小说《魔道祖师》中的男主角，丰神俊朗，潇洒不羁，世家公子排行第四。天资聪颖，不到20岁就自创鬼道，开宗立派，让没有金丹的人亦能修道，成为让人妒恨又羡慕的夷陵老祖。\n不夜天大开杀戒之后被仙门百家围剿，因恶鬼反噬死于乱葬岗。\n十三年后，莫玄羽为报复一直欺压他的姨母一家，使用禁术献舍，魏无羡得以重生', '34', '8035', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10855', '0', '我的英雄学院轰焦冻哪一集与绿谷成为朋友', '大概就是在第二季第十集的时候 绿谷为了让轰认清自己 不要迷茫 把火焰能力也用上 基本上是废了两个胳膊一条腿和轰对决 从这以后他俩就成为了朋友了', '35', '1138', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10856', '0', '杀戮天使女主经历', '《杀戮的天使》漫画和游戏的主人公。\n当瑞依醒过来时，身处在被水泥墙包围的房间里，并且失去了记忆。\n　　离开房间后在大楼内彷徨探索时，与携带者巨大镰刀的男人?札克相遇了。\n　　察觉自己有生命危险的瑞依甩开了札克，搭上电梯……。', '36', '7208', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10857', '0', '天狼中尤里介绍', '17岁。人狼和人类的混血儿。因此，与一般人相比有着非凡的身体能力。由于被吸血鬼毁灭了故乡而发誓要复仇，并作为“猎人”的一员投入了战斗中。他的夙愿究竟能否实现——\n过着孤独的生活，散发着一种让人无法靠近的气息。但是，偶尔也会展露出平静的微笑。\n武器是机关三节棍。通过各种方式使其变形，同时自己也使用与之相应的格斗术来战斗。', '37', '7209', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10858', '0', '2018年7月新番最值得看的作品推荐', '2018年7月新番最值得看的作品推荐\n七月番已经陆陆续续与我们大家见面了，你追了哪一部番？你这是小编一片诚意满满的推荐文，为你推荐最值得看的七月番\n\n\n经典续作开播\n《Over lord》第三季\n\n一个玩家在游戏停服时没有退出游戏，结果在等待系统强制注销时和自己的游戏公会一起穿越到了异世界，而且自己的麾下的NPC也都有了自己的意识。\n《free！》第三季\n\n曾在13年轰动一时的游泳运动动漫，当无数京黑都在指责京都只会做废萌带坏业界的时候，立马用free打脸，我们不仅有废萌，还有基萌哟~世上最强女性杀必死动画，证明了动漫中的男人也可以肆无忌惮的卖肉哟，性感的身材，帅气的动作，让广大妹纸怦然心动！\n《刃牙 最凶死囚篇》\n\n《刃牙》是日本漫画家板垣惠介所作的长寿格斗漫画作品，已经出了四部漫画的超强格斗漫改。1991年起连载至今，此前把手伸向众多老作品的Netflix也宣布了将把这部作品再度动画化。\n此次动画化，是第二部的的一个篇章，畅爽的肌肉碰撞，并衍生出认怂学、纯度诸如此类的梗。\n\n《进击的巨人》第三季\n\n这一季有24集！24集！24集！重要的事说三遍！不用再苦苦等待四年，真是令人振奋消息！\n第三季的内容将会进一步的偏向悬疑和解密，从与巨人的热血咆哮逐渐变成与人斗的精彩剧情，PV的每一帧都透露着富有，完美呈现出巨人这么多年来给人的固有印象。\n《登山少女》第三季\n\n日本动漫《登山少女》就是讲述一个对登山有恐惧心的妹子在伙伴们的鼓励下重新登顶的故事，少女轻百合的日常类动画总会治愈观众的心灵。\n《one room》第二季\n\n「One Room」是以第一人称为视角讲述你和三位美少女同居的短篇动画，让大家体会与三名不同设定的妹子的甜蜜同居生活。\n《偶像大师灰姑娘女孩剧场》第三季\n\n《偶像大师 灰姑娘女孩剧场》第3季视觉图也充满了夏日风情，图中的偶像们还是可爱的Q版形象，并且她们都穿上了泳装，背景则是沙滩大海，这些可爱的偶像们将在夏季带来怎样的有趣的故事非常令人期待。\n（网上盛传的《魔禁3》和《一拳超人2》没有登入暑期档，《刀剑神域》第三季也落在10月份）\n新作动画名单\n《后街女孩》\n\n讲述三名黑道小弟做错事被组织送到泰国做了变性手术，却一炮成名成为当红歌手！刺不刺激！\n\n真实演绎了什么叫堂堂娘子汉，目前漫画是有一百多话，内容是足够改编。绝对让你笑得眼睛都飙出来，搞笑程度为年度霸权级别。\n《千绪的上学路》\n\n讲述女子高中生千绪在上学路上遇到的种种障碍，中间会出现刺客信条、无主之地、csgo等许多游戏的梗，看到好有代入感。\n\n《碧蓝之海》\n\n千万不要被动画文艺小清新的标题给欺骗！\n因为这并不是一个冷门社团的动画，而是一个集恋爱、颜艺、神经病于一体的非常逗比大学校园日常。而且动画版导演是操刀银魂男高等许多爆笑大作的高松信司，期不期待！\n《高分少女》\n\n作品是以街机的游戏厅为舞台，讲述了热爱街机的电玩少年与同样爱街机的大小姐，因街霸相遇后的各种故事，看的时候可以让你常常回忆起，童年街机玩游戏的往事。\n《工作细胞》\n\n是一个通过角色们的互动，来讲述生物知识的作品。不要以为是幼儿的科普动画，其实原作是一部画风彪悍血腥、主角中二热血的“格斗漫”。有兴趣的小伙伴可以看看哦！\n《付丧神出租中》\n\n付丧神是日本妖怪的传说的概念，指器物放置不理100年，吸天地之灵气取日月之精华形成的灵魂而化成的妖怪。应该是一个讲妖怪趣事的温馨单元剧。\n《轻羽飞扬》\n\n一部羽毛球的运动漫，不要以为是日常的卖萌加点运动，打起球的画风无比生猛感觉变身成大魔王一样，让人深刻的感受到羽毛球是如何把萌妹子逼疯的！PV的音效震撼无比，瞬间就让人燃起来！\n《天狼》\n\n本季度王牌原创动画，是由日本顶级的武术指导安藤真裕监督。\n以猎杀吸血鬼为主题的酷炫作品，PV的BGM超级带感着实惊艳到了。', '', '8291', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('10859', '0', '萌股首次测试勇者调查', '喷萌股，得萌币（不\r\n你是一个普通人。有一天因为某个神秘的力量被召唤到了这个叫做“萌股”的异世界，成为了⋯⋯勇者！！\r\n我们邀请您，勇者大人，来回答几个问题。为了表示我们的感谢，我们会向您送上属于我们这个世界的货币：萌币2000枚，作为您在这个世界的生活资金\r\n请将您的手指按在下面的神秘字符之上（没，不用刺破手指，求你了别），我们会引导您去勇者大人专用的异次元房间来回答几个关于这个世界的问题\r\n（如果打不开的话，请长按复制链接然后使用别的浏览器魔法打开，咳）\r\n\r\nhttps://wj.qq.com/s/2292705/9d79\r\n\r\n重要提醒：咳咳，*答卷的时候，需要填写您的萌股ID，用来发放奖品,（这一点得感谢体贴的技术大大）点击上面的链接会自动添加到您的剪贴板里，答卷时复制即可~~\r\n\r\n\r\n（也可以查询萌股ID：我的——点头像——写在昵称下面）。\r\n\r\n\r\n\r\n**勇者大人如果多次答卷也只能获得一次的奖励喔，如果您喜欢萌股的话，请把这里推荐给你的小伙伴们吧！\r\n\r\n禁止发水贴哦，一经发现，会被严肃处理哒。', '', '8292', '', '', '', null, null, '0', null, '0', null, null, null, '0'), ('11160', '0', '2018年7月新番最值得看的作品推荐', '七月番已经陆陆续续与我们大家见面了，你追了哪一部番？你这是小编一片诚意满满的推荐文，为你推荐最值得看的七