insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '看着好尬，动漫毕竟和漫画是不一样的，这不愧是动态漫画。', '', '8034', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '经费不足的产物，和看漫画不同的就是稍微多了一点点的光影特效，还有配音。说到这配音，配的和电视上播的那些低龄向国产动漫一个样，和剧情一点都不搭，看得尴尬无比，还不如去看漫画呢。', '', '8034', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
