insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '反正吧个人挺喜欢的，我可能是因为太闲了，喜欢霍雨浩和呢个叫啥的女主。', '', '8034', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
