insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '画风老是让我想到草帽军团~特别格雷 怎么看怎么像艾斯啊~艾露莎让我御姐不行~格雷和纳兹让我腐女沸腾~还有吉拉尔的腹黑真是万岁~给我轰轰烈烈的战斗吧~！~据说是年番 那就有的等咯~', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
