insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '热血，从小追到大，经典之作', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '如何看待《妖精的尾巴》的结局？', '总的来说，《妖精的尾巴》大结局确实很仓促，而且强行喂狗粮。\n\n如何看待《妖精的尾巴》的结局？\n\n第一，很仓促这个问题，这个问题其实很早就已经开始了，前期的节奏还算是比较平稳，三大黑暗公会的差距总体看来也不是特别大，并且主角一行人在作战时虽然有些莫名其妙，但也算是比较精彩。但是到了后期，战斗力开始崩坏，而且完全没有说明主角的成长，护圣十二盾战斗力完全爆炸，因此给人感觉十分的突兀，还有结局一拳秒杀黑龙，艾琳和杰尔夫的自杀，给人莫名其妙的感觉。\n\n如何看待《妖精的尾巴》的结局？\n\n第二，强行完美结局，主角队无论如何都不会死，这一点是不是已经深入人心了？没错，马卡洛夫用了必死的妖精的法律，铁龙和死神同归于尽？不存在的，无论如何都会强行完美的。虽然这样感觉会很幸福，但是确实是有些突兀的。', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '不知道还能不能见到新一变大和小兰在一起的时候~', '', '7029', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
