insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '挺有逻辑性的，有几次只是看了前半段没看结尾，挺吸引人的，不过好多人说这么看好恐怖，我并没有这么觉得，这种可以让自己先推理下的感觉挺不错的，有时间我会把结尾补回来的。还是不错的动漫啦！', '', '7029', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
