insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '居然还有人说天魁打得过大王的，服了。', '别扯什么后面谁怎么说，后面怎么样，整体来看什么的，天不出手，六部都会被大王干掉，别说一个天魁，天魁伤得了天？打天魁天会这么费劲？跟天打的话，六部那些家伙肯定直接被天秒掉。', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '两个版本的投票结果会不会影响十刑接下来的人物性格？', '感觉第一个版本的红色火焰十刑貌似是愤怒+仇恨的性格第二个黑色的貌似是疯狂+暴躁+玩命的性格？这个投票建议我选哪一个啊？选了以后会不会影响接下来十刑的人物性格啊？来大佬帮我出出主意', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
