insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '求问白龙什么时候才能登场啊?', '求问大佬们，现在动画的进度到原著哪里了，大约多久白龙才能登场啊?\n好想看她，白龙预订了我的女神位置只看到她的图片和一些科普就喜欢上了她感觉自己也是很拼23333\n还有黑龙和黄龙什么情况啊_(:з」∠)_看原著大佬调侃有点慌，如果真有箭头我就抱住白龙站我白了【雾', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '大家微信都投第二个', '微信投票肯定是第二个啊，那才有复仇的感觉！第一个明明就是太极合了不死鸟', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '【阿追】我在晨曦中守护你', '遇见阿追是我觉得最幸运的事情！\n什么时候开始的呢？\n我深深喜欢的阿追，\n一个眼神，一个动作，一声言语，对我来说都很惊艳很温暖！\n我搜集关于阿追的信息，了解阿追的过去，\n我很心疼阿追，想要永远守护着阿追！', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '十刑不死鸟第二个形态是什么意思？原著有第二形态吗？', '虽然没看过原著但是也从各个渠道知道个大概！原著应该没这个第二形态吧？ 十刑不死鸟第二形态是不是也属于原创形态？还有一个问题十刑不死鸟第二形态是不是以原创剧情的方式登场？ 这个十刑不死鸟第二形态给我弄蒙圈子了！', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '还是那句话，给男三弄个白发会死？会死是不是？', '还是那句话，给男三弄个白发会死？会死是不是？后面一堆角色可以满足玄机的基佬紫癖好，干嘛非要把一头白发的天的儿子改成现在这种不知道是灰还是紫的发色？', '', '8031', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '十大事实告诉你 派大星其实并不蠢', '派大星在很多粉丝面前摆出那种二货作风，很多时候做那些让人不符合头绪使人推理到“脑子有问题”的因素。至于他所做的那些2b事情，但总是可以在紧密关头作出例子般的帮助，但有些只是越帮越坏使人发愤叫人无语。\n\n游戏中往往会给观众留下彩蛋 至于视频 也不例外 那么角色设定并没有官方确切鲜血点名 也就是说派大星是傻还是装笨我们不能立刻判定 因为官方说什么设定 就肯定是那个设定 就好比：一个人有精神问题而慢慢精神异常 他本人则不会感觉出来 但是 他身边的人觉得他就是个神经病 ， 那么他本人也不可能不通过医学手段就立刻听取别人的认同将自己认定为神经病 吧？！\n\n在人物判定中我很快就可以知道制作商想通过视频中动漫角色告诉我们 每个人的 性格 以及 行为\n\n比如海绵宝宝： 很明显就是普通人\n\n你可能会问为什么 因为有时候海绵宝宝很单纯 但是 有时候思维很丰富 过于奇怪\n\n如在某集的《秘密盒子》中，海绵宝宝很快就想知道派大星手里所谓的神秘盒子里面到底存放的什么奇怪的物品 同时甚至认为好朋友派大星里面是他杀人灭口藏置尸体的证据。 能如此想到自己朋友做如此恐怖的事情代表海绵宝宝可能从早就已经觉得派大星有些异常 ！！！！\n\n甚至不可相信的则是海绵宝宝明知道章鱼哥并不喜欢自己 但还是频繁骚扰报复章鱼哥.\n（你真的以为海绵宝宝不晓得章鱼哥所作所为？他可是个可以作出顶级美食鸣响临街的厨师）\n\nTop 10。神秘的盒子\n其实派大星藏匿手段极强 甚至让海绵宝宝都没有想到 在《神秘盒子》一集 派大星拿着一盒子给海绵宝宝看 而那盒子里面藏着的物品并不给海绵宝宝察觉观看到 \n而当海绵宝宝哭求能够让派大星打开盒子让海绵宝宝看看盒子里到底有什么的时候 派大星终于满足了他 只是盒子里面的东西让海绵宝宝目瞪口呆 ：里面的东西就是一根绳子\n\n当然海绵宝宝就认为他高估了派大星 其实派大星就是这么蠢转身回家睡觉的时候\n\n派大星竟然拉开绳子 盒子底下瞬间打开 里面正是 海绵宝宝在 《圣诞节》 和群鱼唱完圣诞老人来这里歌曲后 圣诞老人并没有来 被章鱼哥拍到的出丑照\n\nTop9。 驾照考试\n\n海绵宝宝一项与驾照毕业考证无缘 难免每次与驾照擦肩而过\n\n而就在 某集中 海绵宝宝表示自己考了第数百次 还没有通过驾照考试的时候 派大星则来到海绵宝宝身边问他为什么哭诉 当得知海绵宝宝告诉他考驾照三番五次失败后 派大星则为了证明驾照并不难考所以动身去试一翻后 结果一次就通 ！！\n\n甚至让海绵宝宝都惊吓住了\n\n\n¥注意 在某集中派大星甚至用对讲机帮助海绵宝宝差点通过考试 \n\ntop8-想象力\n\n首先我们要想到最要让章鱼哥发狂的那一集（ps：好像每一集章鱼哥都很发狂）\n\n在《秘密盒子》那集中 海绵宝宝连同派大星在盒子里面发出惊人响声 导致章鱼哥难以置信 章鱼哥连续认为是海绵宝宝和派大星使用录音机在盒子里面发出声音恶搞，只是章鱼哥每次想要得到真相都被他眼前所看到的所迷茫：盒子里面什么都没有 只有海绵宝宝和派大星在盒子里面呆呆坐着 。就当章鱼哥实在忍不住了 想要得到答案的同时问了问海绵宝宝 盒子是如何发出响声的 海绵宝宝给出的答案是：“只需要想象力”\n而章鱼哥表示打死也不会相信想象力会让箱子发出声音，章鱼哥也为此模仿示范在家里坐在箱子里 靠着想象力去让箱子发出声音 但三试二试都失败了……如果说章鱼哥是个艺术家 那么连章鱼哥都无法瞬间学会的东西 为什么派大星这个笨蛋也会“想象力”并在盒子发出噪音\n\nTop7。智力碾压\n\n在派大星父母要来见派大星的时候 派大星为了证明自己不会像当年小时候那样蠢而哭求能够让海绵宝宝帮助他，海绵宝宝为了帮助派大星而装傻并教给派大星智慧\n\n派大星父母回来了 可是派大星在他父母面前却是证明了他自己不是傻子 ，但是派大星演过了导致派大星父母认为海绵宝宝是个傻子。\n\n就在海绵宝宝不断用理论证明自己不是傻子的同时，派大星火上浇油让海绵宝宝慢慢精神错乱 最后冲回到家 。\n可是就在节目最后 派大星既然认错了自己的父母而刚刚陪他的并不是他的父母 而那两个海星表示自己并没有孩子\n\n那么问题来了 海绵宝宝与派大星从小相处 为什么海绵宝宝还不认识派大星的父母\n\n派大星肯定会认识自己的父母 可是为什么还要认陌生对象为父母呢\n\n这种情况表明 那两个陌生父母是派大星专门请来的\n\nTop6-伪装\n\n可以鲜明看出派大星伪装要比海绵宝宝更加隐蔽\n\n另一种说法就是海绵宝宝故意如此报漏伪装故意惹章鱼哥生气\n\nTop-5 哭泣\n\n海绵宝宝每次哭的时候 只有派大星会去哄他 但是派大星几乎没有在特殊情况下哭 即使哭的时候海绵宝宝也没有去哄\n\n按照我们民族的习俗 哭是给家长看的 既然如此 那么是否代表海绵宝宝还是需要在朋友的支持下成长前进呢？\n\nTop-4 饭店招牌\n\n在一集海之霸称霸记中 痞老板同样多次败给蟹老板 最终在他老婆凯伦的指导下 听取建议更改店外招牌\n\n当然招牌倒是改好了 可是出乎意外的是连痞老板都能想到的结果再次发生：连半只烤糊的鱼都没有经过\n\n然而连半只烤糊的鱼都没有经过的同时 只是有一只粉色海星缺意外站在他的饭店外参看招牌\n\n他就是派大星 派大星只是用现存的字母组成几个可笑的单词 海之霸好玩\n\n他成功帮助痞老板引进客人\n\n甚至几乎可以打败一直努力为蟹老板餐厅工作的海绵宝宝\n\n你以为就这么完了吗 还没有 派大星所做的真的惊动了蟹黄包的蟹老板 蟹老板与海绵宝宝商量许久之际 打算秘密探讨痞老板的秘密配方 \n\n万物都有欣赏度 果然顾客慢慢不再喜欢痞老板的海之霸 \n\n然而过了只有三天派大星再次掀起风云再起 成功打造新的招牌 好玩海之霸\n\n顾客在此爆棚\n\n只是最后派大星自己要选择离职 原因有千条 我觉得派大星离职是因为他不想工作\n\n最终蟹老连同海绵宝宝成功收买派大星 让派大星担任蟹黄包的招牌设计师\n\n派大星成功用几句单词 击败比基尼海滩最强的蟹黄包餐厅\n\n剩下的3条石锤回帖人多了再发-v-', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '同一集为什么有两个版本的配音呢？', '是同一集，有两种版本的内地口音。\n比如 口气 这一集，派大星给海绵宝宝讲故事安慰他的时候 一个版本用“企鹅” 另一个版本用“小贝贝”。而且整集的台词都有不同。\n到底有几个版本？', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '海绵宝宝今年都32岁啦......', '还记得那个驾照吗？\n1986年7月14日出生.....\n你们中的某些人比海绵宝宝老...', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '海绵宝宝真是神作啊，配音也很好', 'http://m.weibo.cn/1722706263/3761188951139943', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '盘点海绵宝宝的冷知识', '1.海绵宝宝是个艺术天才，无论是音乐方面还是美术方面。\n\n2..小蜗是个哲学家，他智商非常高。\n\n3..派大星牙齿也出现过多种形态\n\n4.章鱼哥的窗子的玻璃是否存在是一个未解之谜\n\n6..蟹黄堡最贵三层蟹黄堡套餐的只要4美元，蟹老板有一集赚了100w美元，至少要卖25w个蟹黄堡，但是比奇堡只有538个人，平均每人吃464个蟹黄堡，这真是个不可思议的数字。（注意我计算的基础是蟹黄堡百分百都是利润，以百分之五十的成本来算，这个数字又要翻一倍\n\n7.海底生物们都需要专门饮用特殊处理过的水而能不能直接饮用海水，否则会渴死。\n\n8.在海绵宝宝的海底世界里有火是非常正常的事情，大部分吃的东西都需要火烤之后才能食用，并且火很容易熄灭。\n\n9.海绵宝宝撕破了裤子那一集中的插曲Ripped Pants是大名鼎鼎的green day乐队演唱的，怪才乐队那一集的插曲sweet victory是David Glen Eisley演唱的。\n\n10.珍珍是蟹老板的养女，是在她在婴儿的时候被蟹老板捡到了的。\n\n11.珊迪是被派去海底研究的，几个猴子给她赞助。\n\n12.其实是理论上是“蟹皇堡”而不是“蟹黄堡”，Krabby patty是螃蟹馅饼的意思。被称为蟹黄堡只是更有意思的称法，其实汉堡原料中根本不含有蟹黄。\n\n13.海绵宝宝酷爱冷笑话\n\n14.蟹老板和痞老板小时候是好朋友 后来以为理想不同变成死对头\n\n15.海绵宝宝不存在绝对邪恶的反派 每个坏人都性格丰富 十分幽默\n\n16.你一定没发现没有第五条OvO\n', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
