insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '蟹老板和痞老板是那集和好的?', '蟹老板和痞老板是那集和好的?好想看!', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '你们喜欢哪对CP？', 'LZ只知道7种主角的官配，排列顺序以出现集数来排，CP在官配之后，如果没有你喜欢的，请自行补充～\n1.海珊【海绵宝宝x珊迪】早在第1集出现了他们俩的互动，而且在123-124集他们俩演过一次“假结婚”。\n\n2.凯痞【凯伦x痞老板】这个是非常的常见，因此可以看出他们是官配。\n\n3.蟹芙【蟹老板x泡芙老师（取这样子的名字好听算了）】这个虽然是官配，但感觉冷清了点了呢。我表示只看见他们互动过两集。\n\n4.派明【派大星x明迪】嗯…没有见过吧里谁萌过的……在电影1里面出现过的，但并没有互动过很多。\n\n5.蜗莉【小蜗x多（米）莉】两个蜗牛的相似度95%！一个是55集章鱼哥的宠物，一个是137集的谁家的宠物“Snellie”，当然最后也没有在一起，那只选择了高富帅！。“Snellie”这英文名有点像Mily？\n\n6.海珊【凑数】\n\n7.章章【章鱼哥x章鱼妹】恶魔妈妈买卖面膜，在章鱼村那集就互不相干的，然后又怎么一见钟情了…←_←这两人犹如一个模子刻出来的，不只这两人呢，而且整个章鱼村好像都是满满的章鱼哥。LZ喜欢这对。\n\n8.海章【海绵宝宝x 章鱼哥】这应该是人数最多的，而且互动多了去。手机党有福利啦\n\n9.海派【海绵宝宝x派大星】好基友，不解释。\n\n剩下的就不一一举例了\n10.蟹痞\n11.章珊\n12.章派\n13.章里【章鱼哥x拉里】新剧集\n14.蟹里【蟹老板x拉里】新剧集\n15.海痞\n16.海堡【海绵宝宝x蟹黄堡，海绵宝宝87集爱上蟹黄堡那集】', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '请问，这一集除了珊迪所有人都被绿光照过变成原型是哪一集', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '有时候看海绵宝宝真的会被笑死哈哈哈哈哈哈哈', '海绵宝宝：哈哈哈终于只剩我们俩了\n复仇者：啊真的只剩我们俩了？你需要的东西都准备齐了，今天不会再有派对了，只剩我们俩了吗？啊？\n海绵宝宝：只有你跟我站在地板上\n复仇者：如果这样的话，，，哈哈哈哈哈哈哈\n（突然灯被打开，派大星出现）\n派大星：派对真过瘾艾。。\n复仇者：啊对不起水桶，你该走了。\n海绵宝宝：我们应该相信派大星，他是我最好的朋友。\n复仇者：我们不能冒任何风险，因为我们知道他可能会是复仇者。\n派大星：啊？我是复仇者？我应该知道的，我现在要去自首了！！！\n（撞墙而出）\n海绵宝宝：原来派大星是复仇者，我还以为……\n（复仇者抓狂）\n复仇者：他不是复仇者！！！！！！（撕掉胡子）我才是！！！！！\n海绵宝宝：哈？不抹剃须膏就把胡子剃了。\n复仇者：这是假的白，吃，这是我在派对商店买的！！！！\n突然一大群人进来，\n章鱼哥：有人在说派对吗？（拿着气球）\n复仇者：啊啊啊啊啊啊啊。。。。（撞墙而出）我受不了了！！！！！\n复仇者都会被海绵宝宝逼疯，可想而知章鱼哥的抗压能力得有多强啊', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '【原创】黑化的心灵（珊迪X海绵宝宝）', '序---------置顶的留言\n•\n风，寒冷的吹着；雨，灰蒙蒙的下着………………这一切的一切，好像都在…………为这个可怜的一踏糊涂的少女珊迪陪葬…………\n•\n她的心 ，如同被一把利剑，狠狠的厮杀着，她恨那个叫丽丽依的女孩，她夺走了属于她的一切…………明明，明明就这么几天，海绵宝宝就要珊迪永远的在一起了呀！可偏偏天宫不做美，这个名叫丽丽依的女孩，用恶心的手段把海绵宝宝抢走了…………\n•\n珊迪在解释………………海绵宝宝不听。“海绵宝宝，你听我说。” “我不听！我不听！我不听！珊迪，你怎么是这样子的人！以后都不要来找我了！我要和丽丽依在一起！”\n•\n珊迪想要自我了结，派大星和章鱼哥拼命的阻止。“珊迪，你可不能想不开！” “珊迪，你是个好女孩，不要为了这种事情去轻视了自己的生命！”\n•\n珊迪心如火焚，好姐妹珍珍不停的安慰。“珊珊，你是不能这样子的！你要活下来给那个人渣丽丽依看！你要把海绵宝宝抢回来！”\n•\n•\n可是，这都没有用的…………最终，这个少女还是了结了自己的生命，躺在床上，拿着刀，往自己的动脉上狠狠划去，鲜嫩的血液很快就流遍了她的全身，死前，还不忘了对自己心心念念的海绵宝宝说句“我多么爱你呀！可是，但终究是一场，画不完的圆…………永别了，我的海绵，即便我们天各一方，但是珊迪依然是…………纵有桃花十里，取一朵放在心上，足矣…………………………”她死了，这个美丽的少女面带微笑着…………死去了…………寂静的月光画过她的脸颊那张曾经笑逐颜开的天仙脸…………如今却已僵硬………………\n•\n•\n•\n•\n•\n当海绵宝宝知道时，表面上没事，其实心里已经快要爆炸了，还被派大星当场打了一巴掌，可海绵宝宝并不知道，究竟。。。发生了什么呀？\n•\n•\n•\n•\n•\n死后的珊迪，打算解脱了这痛苦，一个人去快乐的生活，但是有个恶魔决定帮助她，祝她浴火重生，去向那个丽丽依的女孩讨伐，她答应了…………她重新来到了世上，来到了海底，来到了她们曾经充满欢乐的地方，她回想着这里的一点一滴，她已经不需要头盔了，因为此生的她虽然还是一只松鼠，但却早已不用了呼吸了………………\n•\n•\n•\n•\n•\n“嘻嘻嘻嘻嘻，丽丽依我来了，你，绝对不会认得出我的，哈哈哈哈哈哈哈哈！”', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '就是想问问有大学生还看海绵宝宝的吗？', '都说我幼稚，但真的很爱看！\n我还爱看熊出没和喜羊羊，这真的好吗？', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '追海绵宝宝的，你们多大了，表示都15岁了还在追', '', '8036', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '秦时明月什么时候出个干货版啊？', '先不说剧情一部放完也没多少前进了，每集前面五分钟几乎都是前情提要，遭不住啊，第四部后期拖戏太严重了，连我也看不下去了', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '秦时明月有个MV叫月中天很好听啊', '歌手怎么作品很少', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '画江湖越做越好，秦时明月则正好相反', '第二部灵主素质之高令人惊讶，无论画面节奏还是故事都有很大的进步，ed也很好听。\n秦时明月自从开启周更后，时间缩短不说，画质也越来越差，感觉都是即时演算的。\n打起来乱七八糟，画面节奏感很差。相比之下第四部几场打斗要精彩很多，哪怕是空山鸟语也比现在好很多。\n再加上故事线超慢且零碎，画面老是切来切去，真的连我都受不了了……感觉心思全放在手游上了，那种静下心来雕琢一件艺术品的心境已经荡然无存。\n真的是老秦迷了，从第一部一路走下来，感觉还是第三部最好，融入感情能打动人心。现在看着这些熟悉的角色，却无比陌生。', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '动画没手游赚钱啊', '手游才是重点动画只是附赠 还有动画出已经算好了 指不定哪天就说动画不做了后续发展请看游戏_(:з」∠)_\n从第三还是第四部开始就一直是拖拖拖了 反正就是随便看看，反正剧情没什么进展', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '先说明啊，咱不是黑。看秦时明月没感觉 ', '一直在追剧。但是也发现了一个问题。剧组越来越故意的迎合观众的想法和口味。同时新人物在不断的登场，旧人物没死几个。看导演要怎么编下去。正剧还在追但是番外篇和电影好差', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '秦时明月原声大碟OST2下载（诸子百家+万里长城）', '飞雪玉花、易水两岸、谁主沉浮……\n好听爆了，内牛满面\n\n来源：http://tieba.baidu.com/p/2683795 ... 6&cid=0#43217299666\n云盘：\nOST2 CD1：http://pan.baidu.com/share/link?shareid=2928640007&uk=3224884233\nOST2 CD2：http://pan.baidu.com/share/link?shareid=2923437234&uk=3224884233\n正版OST2 CD3是特典，欢迎支持国产~\n\n顺手补个OST1：http://pan.baidu.com/share/link?shareid=418443&uk=321440038', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '刚刚补完秦时明月', '来发几句牢骚\n故事整体性还比较强\n但是，叙事节奏太不对味了\n而且，在人气勉强及格的情况下\n就开始用OP回顾拖戏法\n每集开头都能跳掉6 7MIN\n更可怕的是\nSTAFF是FF7AC青么，借鉴 了无数动作片段，还有诸如贝恩对少爷的腰膝撞，居然能过审……\n还有，那多的不可思议ED，预算都烧到这了么', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '秦时明月优点一堆，缺点也特明显', '秦时明月优点一堆，缺点也特明显，但是一部动画最关键的是剧情，秦时明月这点做的太搓。\n\n单论节奏，真慢，10分钟能讲完的内容拖5集，反倒是一些日常剧情做的蛮不错。  \n\n\n还有一点就是太装逼，又装逼又拖时间，我觉得装逼其实就是为了拖时间吧。\n\n\n打斗做的还凑合，3DTV动画要求不能太高，但是总得来个正儿八经的分镜吧，就不说对话的时候那些拖时间又装逼的分镜多恶心了， 打斗的时候好歹把分镜做的让人看明白那些人在干嘛吧， 就会乱晃分镜，打斗的时候人没怎么动，镜头晃的超恶心，而且角度还总是不对，分镜是外行做的吧。  唯一好点的分镜还是抄袭最终幻想的。', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '一个困扰蛮久的问题', '在讨论设定时是选择依照剧情内时间发展顺序还是公司制作顺序？或者说二者如何平衡？玄机喜欢倒着讲故事，后来出的剧情时间线确是在之前剧情之前，设定要如何平衡？例如吧里传了好几年的紫女是月神的话题，论剧情时间线，紫女肯定在月神之前，但论制作时间，月神07年就出场了，紫女12年才在第四部回忆中出场，很多人喜欢把后来的人物套在之前的人物上，按照时间线顺序，但以玄机制作时间来讲，玄机难道在07年之前就计划好了12年的人物设定？还是说新粉和老粉先入为主不同？前段时间在优酷看第二部，弹幕里全在说墨玉麒麟是墨鸦、是弄玉，好像名字还蛮合适的，先看秦时第二部的观众不会觉得有什么，墨玉麒麟就是一个看不见脸的刺客，也不会去多想到底是谁，但是先看空山九歌的观众自然就会觉得和墨鸦弄玉有关系，或者说和空山九歌剧情里面出现的某某某有关系，因为空山九歌时间线在秦时之前，可即使墨玉麒麟也是08年出场的老人，在已经有墨玉麒麟这个人物的情况下，如果在目前九歌剧情中有和墨玉麒麟相似或者沾边的设定，玄机的制作方向肯定有向墨玉麒麟靠拢的意向，这又如何说？制造悬念，制造话题？再比如火魅术这个问题，火魅术第一次出现在剧情中无疑是第一部赤练发动的，第五集还是第六集来着忘了，最近在隔壁天行九歌中也出现了火魅术的字眼，只看天九的观众粉丝不会觉得有什么，但是看过秦时的观众都知道这是赤练的技能，于是就出现了“焰灵姬是红莲师父”这种话题，引发赤练粉和焰灵姬粉之间的**，其实从剧情时间线上来看师父这么讲真有可能，毕竟此时红莲还是个单纯的小公主，但是从制作时间上讲，这三个字无疑有向秦时赤练靠拢的意向，毕竟赤练07年就出场了，很多人提起火魅术第一反应就是赤练，起什么名字不好偏偏起火魅术这个名字，两种说法好像都没毛病？？？还有韩非是东皇太一这个话题，和上面的紫女是月神、弄玉墨鸦是墨玉麒麟一个性质，这一类话题，每一个神秘的角色都要和之前的人物沾点关系？因为这些人物时间线在秦时之前，总想让过去的人存在于秦时的世界？', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '很多人说罗网杀手不敢杀秦始皇。', '一个原因居然是怕死。', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '我儿也看秦时', '我儿四岁半了，这两天，我外侄子下载了龙腾万里来看！昨晚，我儿竟然不看他的夺宝熊兵了。他看龙腾万里，今晚又看！要知道，我儿很挑剔的，除了夺宝熊兵这个电影外，其他的一律不看的！能让他看上一眼的电视算是不错的了！所以，……', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '当你的大本命和二本命吵架，三本命劝架，四本命添油加醋', '卫庄和韩非吵架，盖聂去劝架，张良添油加醋煽风点火，白凤默默围观，最后实在受不了了，说:“你一直都这么任性!”\n其实其实除了前三位其他的人物喜爱程度一样', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '看了钟离昧小哥哥摘了帽子后……', '看了钟离昧小哥哥摘了帽子后突然很好奇蒙恬将军摘下帽子后的发型', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '按照惯例，沧海横流第一集要么讲其他故事，要么回忆，不会直接讲大泽山', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '论小六在中秋有爆料的可能性', '沈导说今年小六会播，秦迷又叫月饼，所以我想在中秋会不会有爆料呢？，比如开播什么的。。。。', '', '4017', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '吸血鬼和狼人是宿敌的设定，最早出自哪？', '不太清楚，不记得万物起源WOD里面有没有这个设定了', '', '7209', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '咋没见有人吹天狼呢？不应该呀！', '我们安藤哥哥也是江湖上响当当的一条好汉了', '', '7209', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '发个天狼的官方介绍，日语的', '昭和初期、帝都 東京府。楽器ケースを抱えた奇妙な一団が、東京駅に降り立った。\n彼らは、吸血鬼たちを狩るためにやってきた「狩人」――。そのなかに一際静かでただならぬ雰囲気を漂わせる青年がいた。人狼と人間との混血で、吸血鬼に故郷を滅ぼされたその男、名をユーリィという。\n「天狼の匣」と呼ばれる聖櫃を巡り、吸血鬼と死闘を繰り広げるユーリィたち。彼らが戦いの果てに知り得た真実とは……？\n悠遠たる恩讐と策謀の螺旋が交錯する、\n至極のサスペンス・アクション、ここに開幕！', '', '7209', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '杀天被禁的可能性大吗？ ', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '【真香】对杀戮天使态度的转变', '开始:哈？恐怖游戏，不玩不玩。我就是饿死，死外面，从这里跳下去，也不会玩一次恐怖游戏(被吓出阴影)\n\n游戏时;诶，这个xxx立绘好看，这个xxx我在官网见过你()，这个xxx又神助攻了，woc又是一口狗粮\n\n结束;谁和我说这是恐怖游戏我锤爆他的狗头，这明明是恋爱游戏结局也是高糖啊，楼里每个人都是天使啊啊啊！\n\n\n(看完ng再玩游戏，你会发现这其实是搞笑游戏)', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '孔Z己', 'Zack一到店，所有喝酒的层主便都看着他笑，有的叫道，“Zack，你又没杀死ray！”他不回答，对柜里说，“一袋薯片，要一瓶饮料。”便排出九文大钱。他们又故意的高声嚷道，“你一定又没把人家杀死！”Zack举起镰刀说，“你怎么这样凭空污人清白……”“什么清白？我前天亲眼见ray请求让你杀死她，你却没动手。”Zack便涨红了脸，绑带下的青筋条条绽出，争辩道，“杀ray不能现在杀……那是誓言！……现在杀ray的话，能算杀么？”接连便是难懂的话，什么“对神发誓”，什么“杀死你的神”之类，引得众层主都哄笑起来：店内外充满了快活的空气。', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '疯狂玩梗', '神父:老子楼呢？\n丹尼:老子眼球呢？\nc姐:老子钱呢？\n艾迪:老子棺材呢？\nzack:老子零食呢？\nray:老子高光呢？', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '【ZR】《背德的》(短篇/已完結)', '※大寫的OOC存在\n※自爽腦洞\n※這篇我寫得非常快樂，也祝大家食用愉快。\n\n\n\n-\n\n\n\n　　Zack與Ray是在同一張床上睡覺的。\n\n　　倒也不是一開始就是如此。去年冬天特別冷，不像早已習慣在野外流浪的Zack，Ray總是被凍得受不了，即使靠著火堆取暖、喝了熱湯、裹上再多的衣服，瘦小的Ray還是常常冷得直發抖，看不下去的Zack只好要Ray靠自己近一點，連睡覺的時候也讓Ray窩在自己旁邊。\n\n　　起初Zack還有點的不自在，雖說是自己提議的，但是兩個人睡在一起什麼的，還是會有一些的不習慣，每天早上睜開眼睛，發現Ray像隻金色小貓似的蜷縮在自己身旁總是會被她給嚇一跳。而Ray本人倒是不怎麼在意，除了偶爾會被睡相差的Zack大手一揮給打醒，其他並沒有什麼令她感到不妥的地方。\n\n　　兩人漸漸地習慣睡在一起的這件事，即使冬天早就結束，其中一方還是會主動的窩到另一方的身旁，但是有一天，Zack卻驚覺，不能再這樣下去了。\n\n　　原因是有天他因為手背上傳來的觸感而醒來，手臂被什麼給牢牢箍著，而手指關節彷彿也陷進了什麼，那種感覺異常的柔軟，但那種觸感既不是被褥也不像枕頭，睜開眼睛卻發現睡迷糊的Ray將自己的手緊緊抱在胸前，看見這一幕的Zack整個人都被嚇醒了，差一點就叫出聲來。\n\n　　Ray應該不是故意的，這小鬼只是把自己的手當成了娃娃之類的抱在懷裡。Zack如此安慰自己，像在唸咒語似的悄聲碎念，小心翼翼的將自己的手臂抽離Ray的，接著火速的衝出房外好讓自己冷靜冷靜。\n\n　　一直以來Zack都把Ray當作小鬼看，可是認真想起來，她可是一個正值青春期的少女。雖然不是那麼明顯，但她胸前那些微的隆起還是提醒了Zack，Ray是個女孩子的這件事，一想到這裡Zack便感到耳根子一陣莫名的發燙。\n\n　　Zack蹲在門邊懊惱的抓了抓自己的腦袋，不解自己怎麼就這麼在意Ray是異性這件事。從小到大Zack遇過的異性有很多，但是Zack不曾像現在這樣，為她們感到赧然失色。他從來沒有過這樣子的感覺，也沒有人告訴過他這種感覺是怎麼回事，Zack只是直覺上認為他們不能再繼續這樣下去，那樣子太危險了。\n\n\n\n　　當天晚上睡覺前Zack便告訴Ray，我覺得你該自己睡了。\n\n　　Ray問Zack為什麼，他說：「你好歹也是個女孩子，而我是成年男性，兩個人睡在一起總有點那什麼的。」\n\n　　Zack沒有講明白「那什麼的」究竟是什麼，不是因為他找不到適合的詞彙填上它，而是因為一旦說出來會惹得誰都羞澀。\n\n　　Ray像是個被父母要求獨立而露出難過表情的小孩，Zack差點就要因為Ray難得有些稚氣的表情而心軟，他甩甩頭用力將所有不捨拋於腦後，鐵下心將Ray趕到床上去，而自己抱著條小毯子躺到一旁的沙發上。\n\n　　兩個人似乎都不習慣不能靠著對方入睡的夜晚，彼此都在床上及沙發上輾轉數回，Zack才聽到床上傳來Ray勻稱且規律的呼吸聲。\n\n　　小鬼終究是小鬼，傷心個沒幾下便輕易的睡著了，倒是Zack怎麼樣也難以入眠。好不容易靠數著Ray的呼吸培養睡意，閉上眼睛，一隻腳已經邁進了夢鄉，卻又被不知道什麼時候醒來的Ray給扯了回來。\n\n　　「Zack、Zack。」Ray搖了搖自己的肩膀，將他給喚醒，「我還是想跟你睡。」\n\n　　清晨的四五點，天空還沒全亮，根本沒怎麼睡到的Zack被Ray叫起來他很想發脾氣，可是一看見Ray那不知為了什麼而焦急的模樣，他忽然又狠不下心，只好問她：「做夢了？」\n\n　　Ray既不講話也不點頭搖頭做個回應，Zack嘆了口氣，她總是在奇怪的地方上逞強。心裡頭還在掙扎著自己不該那麼快就對Ray心軟，身體卻先自作主張的，把沙發騰出了一個空間給Ray。\n\n　　沙發上不比床鋪寬敞，他們蓋著同一條毯子，兩個人幾乎是貼在一起睡的。\n\n\n\n　　而意識是非常恐怖的。\n\n\n\n　　Zack告訴自己，這不過就是做惡夢的小鬼在跟大人討安慰而已，用不著如此在意，可是Zack還是忍不住繃緊自己每條的感覺神經。\n\n　　怕摔下沙發的Ray緊緊地抓著自己的衣服，額頭輕抵胸口，隨著Ray的呼吸起伏，繃帶下的胸膛傳來一陣陣濕熱，弄得Zack有些發癢，不只是身體上，連心裡都覺得有些難耐。他感覺Ray全身的柔軟都與自己黏在了一塊，胸口也好，肚子也好，因為擁擠而交疊在一起的小腿也好，心裡頭有什麼正因此而躁動不安。\n\n　　原本的瞌睡蟲全被趕跑了，他低下頭，懷裡的Ray睡得很熟，彷彿剛才根本就沒做過惡夢這件事。\n\n　　──果真是個小鬼呀。要是不發出這種感嘆，Zack便覺得心裡一股罪惡感。\n\n　　她的眼睫毛似乎因為又夢見什麼而打顫，她毫無血色的臉白的可怕，但是仔細一看兩頰上還是有一抹淡淡的紅暈，她櫻桃色的嘴唇有種奇妙的吸引力，讓Zack想起前幾天在大街上看到的擁吻的情侶。\n\n　　接吻到底是什麼樣的感覺？Zack不清楚，從來對男女之事沒有任何興趣的Zack頭一次好奇起了親吻會是怎麼樣的感覺。他翻過身，雙手撐在Ray的身體兩側，讓Ray躺在身下。\n\n\n\n　　Zack有個糟糕的想法，他想要偷襲Ray。\n\n\n\n　　可是似乎是自己翻身的動作太大了，Ray一下子就醒了過來，眨巴著眼睛看著不知何時跑到了自己上面的Zack。錯愕的Zack進也不是，退也不是，他不想就此打退堂鼓而從Ray的身體上翻下來，接著被Ray追問自己在做什麼的時候支支吾吾回答不出來，他也不可能說謊，即使這是一件多麼令人羞恥的事也一樣。\n\n　　Zack只好硬著頭皮上了，緩緩俯下身讓鼻尖靠近Ray的，他們只差一毫米的距離就可以觸碰到對方的唇瓣，Ray就算年紀還小，但是Zack想做什麼心裡還是有幾分明白，儘管有點害怕，卻還是閉上眼睛等待Zack吻上自己的那一刻。\n\n　　可是Zack一看見她閉上眼睛他便膽怯了，她的不安如此明顯自己怎麼可能還下得了手，身子忽然一軟就倒在了Ray身上，臉埋在Ray的金色髮絲之間久久抬不起頭。\n\n　　「Zack？」Ray用她纖細的小手溫柔的摸了摸Zack的後腦勺，而頸間傳來對方因說話而吐出的溫熱。\n\n　　「即使是個連續殺人犯，心裡多少還是有些道德感的，Ray。」他說，「下次就算做惡夢也絕對不要再睡在一起。」\n\n　　「──太危險了。」\n\n　　Zack像是用盡一切的力量道出最後一句話，要不是耳邊還傳來Zack的呼吸聲，Ray還以為Zack已經死了。\n\n　　Ray有話想跟Zack說，可是她希望能看著Zack的眼睛說。她推了推Zack壓住自己的身體，又惡作劇的戳了戳他的腰，以為他會癢的受不了而從自己身上爬起來，可是無論Ray對Zack什麼他都一點反應也沒有。\n\n　　這原本該等到Ray把話說完才會對Zack做的，不過誰叫Zack不理她，她只好調換一下事情的先後順序，使勁拽起Zack的頭，捧著他的雙頰吻他。\n\n　　人生中第一次接吻竟然是強吻別人這是Ray始料未及的，忽然之間就被吻上的Zack不肯置信的瞪著眼前的Ray，Ray明顯看到了Zack繃帶之下的臉龐泛起了一大片的紅霞。\n\n　　不要以為與Zack相比Ray就會顯得冷靜多了，嘴唇相碰的那一刻Ray那機靈的小腦袋頓時停機了好幾秒，要不是Zack掙扎著她差點就忘記要放開彼此，她覺得兩頰一陣發燙，這種熱度順著耳根延伸到脖子，接著蔓延至全身。\n\n　　「如果Zack因為襲擊我而讓你有背德感的話，那就讓我來襲擊你就好了。」\n\n　　Ray的腦袋昏沉沉的一片，聲音還有些顫抖，Zack從沒看過Ray的臉這麼紅過，他這才發覺身下的這傢伙比被強吻的自己還要更難為情。\n\n　　他想跟她說並不是攻守互換背德感也會因此被抵銷，可是他懶得反駁了，發現對方比自己還要羞澀讓Zack有股佔了上風的感覺。\n\n\n\n　　道德感什麼的他不想管了，他此刻只想吻吻她，光明正大的。\n\n\n\n\nFin.\n\n\n\n\n－\n\n\n希望別沉了這貼', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '这里建了一个杀戮天使的语c群有没有人来玩呀', '心态崩了，本来想直接截图放qungui的但是次次吞\n无审，可重皮毕竟原作人物少嘛，都是原创的话人也不多还会脱离原作，。\n当然还是可原创的，原创需微审人设，就是看你有没有脱离现实玛丽苏啥的，外貌的话不想写文字你会画画的话也可以画出来（这么随意？？）。\n名片格式进群公告上会有的（其实是自己还没想好……）\n开时期（不要问我已经死掉了的人哪里来的时期，不接受的话可以不披，也许有人需要呢）\n可以骰，涉三适量，不禁表情包和语音，禁黄豆颜表（当然///这个样子的是勉强行的）。\n最好是上皮说话，要是你们玩梗闲聊的时候也可以用皮的语气（有歧义bu）来皮的话也是ok的\n对群规有任何补充或建议请指出，我可能会改。。。。。吧。。。。。', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '伸手党 求音乐', '看了tv版之后入坑游戏，发现大部分游戏bgm都有大佬提供资源，可是。\n为什么找不到b2时的火刑曲呢。。。\n如果有的话，请大佬指点。', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '【游戏w】用你的qq号前三位组成一句话', '第一位：1 扎克 2 蕾切尔 3 达尼 4 艾迪 5 凯西 6 神父 7 蕾切尔爸爸 8 蕾切尔妈妈 9 达尼母亲 0扎克孤儿院院长\n第二位 1 在月球表面 2 在床上 3 在男厕 4 在下水道 5 在餐厅 6 在楼顶 7 在地下室 8 在医院 9 在火星 0 在商场\n第三位 1 吃饭 2陪扎克 3玩手机 4和蕾切尔玩 5 砍人 6 上厕所 7吃东西 8 挖坟 9玩萌股 0 抖s\n\n蕾切尔在地下室砍人了解一下（', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '那个，弱弱问一句，同人是什么意思', '这个游戏不是原创的吗（网上查了查 没看到是啥的同人）', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '这是著名游戏？今天看了第一话', '本来还以为是什么很大的游戏，结果查了一下，发现是以前见过但是因为只有日语就没玩的小rpg（当时app store没找到英文版）\n这都能动画化的话，我是不是可以期待一波LiEat系列的啊（', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '【ZR】伤', '果然，还是很痛。\nzack看着缠着薄薄一圈绷带的腹部，虽然托那家伙的福，血已经止住了，可那份疼痛仍然在腹部徘徊。也不能做什么剧烈的活动，不然恐怕又要裂开。\n说到底，究竟是为什么自己非得这么憋屈不可啊?还得让那个小鬼帮自己找药，幸亏她没在那里死掉……\n正当这么想着的时候，zack抬起头缓缓打量半蹲在地上收拾东西的少女，视野里赫然出现一片鲜红-------“喂，ray!”\n“嗯?”ray应答着，手里的动作加快了几分，或许是牵扯到了伤口，眉头微微皱起。“没关系哦，马上就可以上去了。”仍然是波澜不惊的语调，似乎还带着哄小孩子的意味。\n“我不是说这个!你手臂和腿受了伤对吧？那就快包扎一下啊？不然万一跑起来可不方便。”\n“……嗯。”湛蓝色的眸子少有地出现意外，zack居然能注意到她的伤口，也算是……进步了一点吧。\n手指在针线包里翻找，似乎还剩下一点绷带，正准备用惯用手缠在小腿上时，却被手上传来的疼痛刺激得一怔-------差点忘了，这只手也受伤了呢。\n想再次缠上，手已经没什么力气了。更何况，她也不想把情况弄得更糟。\n自己真是没用啊，不过是受了一点小伤，就连给自己包扎伤口这点小事都做不到。说到受伤，zack好像一直在受伤呢，就连这次，zack也还是在负伤保护着自己……\n那种伤口，就是是“怪物”，也会觉得痛苦吧？\n看见旁边的少女停止了动作，甚至有些发愣，zack顿时提高了声调:“喂!”\n看着ray回过神来甚至有些措手不及的神情，zack更加不满了:“我说你啊，能不能不要突然就像没上发条的人偶一样一动不动啊？搞得我以为你死了一样!”\n“我没死 。”这次倒是很快地回答了。\nzack看着仍然缓缓向外渗着血液的伤口，内心忽然烦躁起来，语气也带着不易察觉的急躁:“那你就快点把你的伤口处理好啊？我想快点出发!”\nray不知不觉抓紧了自己上衣的下摆，下摆被她揉出一些褶皱。她犹豫着，过了好一阵才开口:“我……我的手没力气了。”也不知道他会是什么反应。\n“啊啊，那可真是麻烦。”zack好像很困扰似的揉了揉深褐色的头发，眼神却飘向另一边，思考着什么。\n……意料之中。\n算了，反正腿上的伤比较轻，真的跑起来应该不会有什么问题。现在最重要的，应该是赶紧去b1才对。\nray咬着唇，尽力使自己忽略时强时弱的痛觉 。\n“喂，过来!”耳边响起熟悉的声音，却好像又有什么不同。\nray疑惑地转过头，看见zack仍然坐在地上，没有要起来的意思。尽管不知道什么原因，ray还是缓缓移动身体，坐在了zack的对面。\nzack向她伸出缠满绷带的手，不容置疑地说:“把药和绷带给我!”\n“？”眼睛睁大，露出了少有的错愕表情。\n“所以说，反正你自己又做不到，还是让我来更方便一些吧？”\n“这点小伤，没关系的。”虽然是这么说, 但ray 还是得到了一点安慰。\n手里的药被直接抢去，动作快得简直不像一个受伤的人。\nray还想说什么，被zack不耐烦的话语阻止:“别乱动！我可不敢保证这不会痛。”\nray无奈的闭嘴不言，目光被zack的动作吸引。这种一贯主张暴力的人，做这种事居然意外的小心翼翼。用干净的棉棒沾上药液，轻柔地在伤口处擦拭，再用绷带缠上-------大概是做惯了吧，毕竟他浑身上下都缠着这种东西。\n“手给我。”ray回过神，慌忙把手臂递给他。\n手臂的伤口大概要深一些，红色的血肉微微翻出，与白皙不似真人的肤色形成了强烈对比。\n虽然伤口不大，可用棉棒沾药液这种方法有些杯水车薪了。\n没办法，zack一把抓过身边装着药液的瓶子，缓缓地倒上去。\nray没有说话，可瞬间紧绷的手臂还是暴露了她的疼痛。\n“……我都说了不能保证不会痛。”zack小声嘀咕道，手下动作又轻柔了几分。\n快速重复缠绷带的动作，zack如释重负地站起身，低头催促盯着自己伤口的ray:“快点走啦！这下没问题了吧！”\n“嗯。”ray也起身跟上zack的脚步。\n\n\n\n\n（时间轴:游戏第三章结尾ray帮zack找药）\n（bb:把ray的伤口写严重了一点，希望大家看得开心！）', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '超喜欢神父的 以及为什么医生血那么厚', '真希望杀天动画能用些游戏里的bgm，尤其是结局，要是用游戏的bgm更感动，如果用的是ed……那突然感觉大打折扣……', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '命最硬的应该还是Zack吧', '医生一开始在B5被Zack砍倒时有加装一些钢板防御而没受致命伤(虽然估计也是疼得不行)，后续两次远程武器+Zack补的两脚估计也是非要害部位，死因高机率是被自己启动自爆装置导致的火灾活活烧死。严格来说Zack的命更硬，B3各种装置都拿来怼Zack还不死也是堪称奇迹', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '是不是大家都是岚少那里入坑的', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '札克多少岁？', '只是说疑似20，真正的年龄没人知道。', '', '7208', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '有类似overlord的小说吗？', '在追新番overlord第二季，很带感，不知道有没有类似的小说？请大家推荐一下。', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', 'OVERLORD 如果安兹真的玩脱，守护者会是什么反应？', '小说补到12卷，看到这样的评论\n\n    实际上我觉得安兹本身的行动也是在小迪的预测中，只是不点破而是随着安兹行动而行动顺便诱导一下他以免偏离计划，都是在培训安兹啊\n\n\n我脑补了一下，感觉大坟墓的NPC全都是忠诚度超过一切，脑补能力满分\n不管什么情况都能脑补成安兹伟大\n所以如果安兹真的失败了，大概就是这样：\n——小迪，如你所见计划泡汤了。正如我常说的，我也会犯错，也有不及你们的地方。这件事，只能拜托你善后了。\n——安兹大人……难道说！？原来如此，我终于理解了大人的心意，安兹大人对我实在是太仁慈了。\n——嗯……嗯？你已经看穿我的心意了吗？\n——属下实在不敢说能够看穿大人的心思，但是大人给予我的考验，属下一定不会让大人失望！\n………………\n——小迪，安兹大人真的失败了吗？\n——阿尔贝德，我实在是太惭愧了！这是安兹大人对我的提醒和考验啊。\n——这是什么意思？\n——安兹大人恐怕早就全盘掌握了我的计划，并且一早就看出了其中的疏漏。但是为了不束缚我的行动，才故意没说明确指出，大人是希望我能够自行发现吧。\n      这种疏漏虽然一般不会出问题，但是安兹大人常提醒我们要警惕有大人等级的强者，而这种疏漏一旦被那样的强者发现，就会酿成失败。\n      大人宁可自己的贤名受损，亲自演出这场失败，都是为了教导我啊。\n——也就是说，所谓的善后……\n——是的，大人已经替我指出了问题，那么解决问题就是给我的考验。\n——恐怕大人是想要为我们创造成长的机会\n——没错，能够侍奉这样的安兹大人，实在是无上的光荣！', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', 'overlord不就是日本版临高启明吗？', '现实世界的宅，穿越之后靠生产力代差碾压土著。\n\n主要内容就是在穿越地发展自身势力、培养伪军、闲着没事虐杀土著爽一爽。\n\n两本书经常用的从土著角度描写穿越者的写法也很相似。', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', 'overlord这片的看点到底在哪？', '萌股上投票结果不错，卖4K多还有不少人替不值\n\n补了两集后，这啥玩意儿啊，龙傲天玩游戏跟一堆NPC互动？要不要这么空虚无聊啊\n也不知道是我老了还是怎么着，实在没法明白现在中学生的审美', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '网游类的动画，明确表现出氪金要素的除了overlord，别的', '网游不花钱，公司挣不到钱，玩家也玩的不爽\n各种网游类的轻小说都动画化了不少，表现出明显氪金要素的只有overlord\n男主的装备，公会的据点和npc的等级，小说和动画都表现出了充足的氪金要素\n其余网游类的动画\n《sao》：最早的sao别说氪金了，不赔偿到死就不错了，第二个精灵的那个，两个公会联合去打世界树副本，还要桐人掏腰包买装备。运营该死！这么个让玩家花钱的地都做不好！那个打枪的有真实货币系统，但是有人考证，那游戏其实人数不多，比《传奇》《魔兽》差远了，那个被诗乃干掉的重机枪手的装备，据说才1000rmb左右？根本不贵\n《.hack》《记录的地平线》我没看过，求看过的说一下，这两个游戏都太危险了，怎么上的市！', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '求推荐类似Overlord的以反英雄为主角的小说', '起点或者日轻都可以，就不发小说区了。\n\n总之就是希望能找到一些主角龙傲天但处于传统上的反方或者最终BOSS位置的小说。\n\n如果像Overlord一样侧重弱小角色方的描写那就更好了。\n\n求推', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '我看OVERLORD最令人高兴的一点居然是...', '终于不是女角一大堆都是卖肉装扮了！该包严实的就严严实实！居然会有正儿八经穿全身皮甲的盗贼和弓手啊！人设简直专业得让人感动啊！\n\n好吧...总是打成LOAD..........', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '犹豫了一波，还是发帖，骨傲天人气怎么回事？', '我算老几？算了…\n\n补充自己不喜欢看的原因\n\n我可能记忆出错，当时应该不止看了一集，可能看了多集，但是印象感觉一直都是在黑夜的场景里，或者城堡？宫殿里…\n\n所以看得有些压抑，另外就是主角是骷髅形象，个人一时难代入那形象，就算是兽人也许我也更好接受一些（这些只是个人原因）总之就是我看起来有些压抑感…\n\n主角们给我的反派感过重，所以一直没代入看着看着就弃了\n\n\n\n虽然发这帖也许会被喷，也可能被骂钓鱼\n\n但还是很费解骨头的人气怎么来的？\n\n记得第一季开播时，略微看过第一集（看没看完第一集或者多看几集已经忘了）已弃…\n\n所以长期不关注骨傲天的动态，结果刚才进了bilibili番剧导航的视频，介绍7月版权番，结果视频从头到尾都在期待骨傲天，评论也大多数在提这个\n\n我自己不是世界，也许我感觉乏味，人家就感觉有趣\n\n但是这种情况…很少自己完全提不起兴趣，结果b站人气那么高的\n\n之前坂本我居然还勉强看完了…', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '有没有人能进来详细的给我讲下骨傲天反人类的内容', '之前还以为自己补到最新卷结果只是在个野鸡网上看到的网友翻译版，没更到最新卷，而且文库版web版混着看得我都混乱了，具体进度大概是10卷看完了11卷刚开始，结果看到网上说反人类，求了解释但也只有里面角色的死法的例子，有没有人能给我具体的说一下反人类的 who when what啊，谁在第几卷里面做了什么，然后骨头对其的态度，我想看看到底骨头有多坏。\n也看到有人说如果看到第10卷还没觉得有问题的话那后面也不会觉得有啥问题了，真是这样的吗', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '骨傲天是一部人文主义作品', '拿蜥蜴人篇来说，一些人可能以大墓地对蜥蜴人的屠戮为由指责骨傲天这部作品“三观不正”，但这种指责只能说是基于一种对人文主义极为肤浅的庸俗理解。在这种理解中，人文主义被当成了一种思想正确、一套道德标准，人被摆放在不可侵犯的神圣位置上，所以骨傲天中不以为耻地屠戮人类和其他智慧生物就被视为“反人类”了。\n\n但是，人文主义在更深的层次上，关注的是人性，是人的本质。在蜥蜴人篇中，蜥蜴人虽然遭到大墓地的屠戮，但是蜥蜴人在扎里尤斯·沙沙的领导下，在与大墓地的战争中展现出了非凡的智慧和勇气，捍卫了战士的尊严，甚至令科塞特斯为之折服。蜥蜴人对抗大墓地，是至弱对抗至强，在至强面前，至弱的蜥蜴人用自己的智慧与勇气证明了自身作为理性动物的存在价值，得到了至强的承认，从而避免了亡族之祸。从这种意义上讲，骨傲天不但不“反人类”，反而是从人文主义出发，讴歌理性动物的能动本质。\n\n在这个吃鸡要看绿色血液的时代，骨傲天这样的作品对一些人来说可能都太过深奥了。', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '', '假如骨傲天穿越到中古战锤能否为所欲为？', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '这骨傲天，原著是群像剧？', '讲完冰蜥蜴，然后来个塞巴斯，后面不会都是他吧，是的话就不看了', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '回想WOW，会觉得骨傲天很帅也很正常吧', '玩过WOW的都知道，曾经骷髅药风靡一时，很多人都觉得变成骷髅很帅，可以说每个人都有一颗骷髅兵的心。 骨傲天也算是满足了这种审美吧？', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '骨傲天这轻小说的背景设定里未来是几乎无解了吧', 'ZT\n\n    http://tieba.baidu.com/p/4208582937?fr=ala0&pstaala=1\n\n\n只要看过《overlord不死者之王》原著的人，都会发现一个有点奇怪的地方。那就是身为主角的巫妖王安兹，或者说2138年的日本上班族铃木悟，在抵达异世界之后，虽然很努力地收集信息，想要弄明白是否有同伴来到这个世界，但却自始至终没怎么想过要如何回到原来的世界。\n\n因为，在他那个2138年的未来世界，普通人的生存环境实在是太可悲了。\n\n——在那个世界，当历史的车轮滚动到2138年的时候，全世界的地表环境已经被彻底污染，天空总是被黑色的雾霾笼罩，能见到太阳的时间非常少。因为雾中含有剧毒物质，普通人类外出散个步都需要防毒面罩。原本应该起到净化空气作用的植物，也因为日照不足而大量枯萎，因此进一步加深了空气污染的速度。以植物和果实为主食的鸟类和虫类的数量也锐减，整个生态系统堪称是土崩瓦解。\n\n然后，除了空气污染之外，水污染同样也相当严重，日本全部的河流都已变得污浊，纯净的饮用水成了价格高昂的生活必需品。一切依靠阳光和雨水的传统农耕方法也无以为继，可使用的农作物必须在室内无土培植，价格变得极为高昂，新鲜蔬果、肉类和水产品都成了富豪的专利。普通老百姓只能吃着难以下咽的合成食品来勉强维生，或者更加悲惨地去吃野外那些被污染的农作物，然后染上各种怪病死去。\n\n在这样的世界里，想要看一眼美丽的自然景色，呼吸一口纯净的新鲜空气，对于穷人而言都是奢望。也难怪主角在看见异世界的纯净星空，感受着草原上的晚风之时，会那么的激动万分了。\n\n但是，跟自然环境的污染破坏相比，人类社会内部的黑暗和颓丧，则更加令人心如死灰。\n\n——马克思在世的时候，曾经预言过要打破国家、民族、人种、宗教和语言的隔阂，让“全世界无产者联合起来”，而在之后一个世纪的世界革命之中，这一口号也被反复提起。但在巫妖王安兹的那个未来世界里，国家、民族、人种、宗教和语言的隔阂确实是被打破了，结果却并非“全世界无产者联合起来”，消灭剥削发展生产力。而是“全世界资产阶级联合起来”，紧密团结合作，联手压榨全世界的无产阶级。\n\n在那个世界里，大财团以一种匪夷所思的方式挣脱了政府的束缚，就是通过国际合作的方式，联手消灭全世界的所有政府和爱国者，最终结果是国家正规军被废黜了，财税部门也彻底完了蛋，各国政府理论上依然存在，但却无兵无财，事实上成为了全球财阀集团豢养的一群看门狗，甚至是装饰品。跨国财团联盟成功掌握了所有国家的司法、立法、行政三大权柄，成了整个世界的实质支配者。\n\n而原本由现代国家制度所保障的各项福利制度，也随之土崩瓦解，退休金、医疗保险、劳动保险、义务教育和最低工资都没有了，劳苦大众的生活水平直接掉到了工业革命初期的噩梦年代。\n\n当然，哪里有压迫，哪里就有反抗，这个定律在未来世界也是成立的。但面对底层百姓可能的反抗，统治世界的财阀集团也有一招釜底抽薪的绝户计，那就是严厉限制教育，让穷人处于愚昧状态，只能浑浑噩噩地生存，不能思考如何反抗，更想不出反抗的途径：在十九世纪，一群文盲工人或者抗税农夫的暴动，还能造成很大的损失。但在那个战争机器无人化的时代，不受任何法律和道德观念束缚的资本家，可以轻而易举地用武装无人机把罢工者和抗议者屠杀殆尽，然后再从贫民窟里招一批新的廉价劳动力进来。\n\n事实上，由于自动化生产和人工智能的普及，那个时代的资本家根本就不需要太多的劳动力，巴不得穷人死得多一些才好。反正机器人可以代替他们的岗位，而且任劳任怨，绝不会提条件和造反。\n\n即使是在二十世纪，全球亚非拉广大殖民地的人民，也是因为欧洲人在两次世界大战之中自相残杀，内耗过度，才赢得了独立和自由的机会。如果他们面对的是一个完全统一的欧美大西洋联合帝国，没有分裂内耗，没有世界大战导致的此消彼长，那么各个其他民族的独立自由运动，将会是多么的前景无望啊！\n\n然后，为了不让穷人掌握知识，无书可读，学校自然是越少越好，不仅义务教育被废止，连初等教育的学费都被规定得极为高昂。至于各种私塾和培训班，则是被严厉打击取缔的对象，私下里偷学知识是要进监狱的重罪，奖学金和助学贷款更是天方夜谭的东西。由于一切都只看金钱与门第，考试成绩变得毫无意义，当大学生接受高等教育，已经变成了一种类似贵族特权的玩意儿。日本的教育普及水平已经倒退回了江户时代，并且还在进一步向平安时代退化。平民能读上小学就已经不易，更多的人干脆都不识字了。\n\n只有小学生学历的巫妖王安兹，或者说上班族铃木悟，之所以看上去并不愚蠢，知识面还不算差，是因为他在社会上历练了那么多年，游戏公会里又有一个要好的玩家是大学教授，才多少学到了一些东西。\n\n总之，《overlord不死者之王》之中描述的那个2138年的未来地球，是一个自然环境严重破坏、社会阶层彻底固化的悲哀时代。富豪权贵醉生梦死，底层平民没有任何上升渠道，甚至难以得到知识。\n\n由于知识被垄断在极少数人的手中，科技的进一步研发和创新也是无从谈起，导致科学技术的发展基本停滞，甚至不进反退，整个人类文明都显得暮气深沉，处于一片走向衰亡的黄昏之中', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '骨傲天和巫妖王比如何', '骨傲天扔到wow世界里，他的大坟墓能做什么难度副本。扔到剧情里和巫妖王比哪个战斗力更高', '', '1005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '《龙珠：超》龟仙人被投诉动作轻浮 不宜让儿童观看', '说到动漫的中好色的角色，很多动漫迷都会想到《龙珠》中的龟仙人，这个老爷子从一出场就是各种没下线，但是虽然大家都认为龟仙人是个好色的老头，不过人家貌似也没做什么太过分的事情，所以大家也都是觉得龟仙人的好色更多是搞笑的成分。不过现在的一些日本家长可就不这样认为了，在动画《龙珠：超》中，龟仙人的一些好色举动近日就被观众向BPO机构（类似于日本的民间广电组织）投诉，也是引起了网友热议。\n\nBPO官方网站发表了播出的电视节目的观众意见，在“关于青少年的意见”中一条意见中写道：“在动画节目中，播出了无法违抗的年长男性触摸年轻女孩的身体等不好情节。这些跟故事情节毫无关系。我家孩子非常喜欢这部动画，每周都一集不落地看，这是不适宜在儿童向动画中播出的内容。”明眼的日本网友很快就发现这条观众意见指的是《龙珠：超》的第89集《谜之美女出现!天津流道场之怪》中龟仙人和女弟子由琳的相关情节。\n\nBPO全称为日本放送伦理·番组向上机构，是由日本民间放送联盟(民放连)以及日本放送协会(NHK)设置，是基于放送事业的公共性和社会影响的重大性，为了确保节目放送的正当性以及提高放送伦理道德为目的而成立的非盈利非政府团体。很多的动画内容都被投诉过。\n这条观众意见也引起了网友的热议：“时间和剧情都跟《龙珠超》的第89集对得上，看来龟仙人以后有麻烦。”“这个时代已经不允许龟仙人这样的变态老头存在了吗？这样的话《龙珠超》的TV动画会越来越无聊，连唯一的乐趣都没有了啊。”“我就想问这种程度的描写不是一直都有吗？现在比这样尺度大的多的有的是啊，为什么要投诉这个？”。其实和很多动画相比，龟仙人这个确实尺度很小，会被投诉估计是因为是在少儿档播出，怕带坏小孩子。不过《银魂》之前也曾吐槽过BPO，可见动漫迷对他们还是很不感冒的。', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '龙珠Legends没人玩么？演出炸裂啊', 'https://dble.bn-ent.net/jp\n先丢几个wiki\nhttps://appmedia.jp/dble\nhttps://gamy.jp/dbz-legends\n\nios貌似6s以下不能玩\n安卓貌似7.0以下版本不能玩\n\n\n玩了一会儿，演出太炸裂，SR排除率是5%，目前角色很少\n\n先玩几天看看', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '看了将近30小时新龙珠，还是得说好看', '甭管逻辑崩不崩，开始不觉得，看着看着就有当年在网吧追龙珠Z的时候的感觉，就是好看，对后续故事很期待，停不下来，哈哈，比现在的番剧好看多了，而且适合全年龄，适合给孩子看，做的真不错', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '话说除了龙珠外，还有没有主角打不过就合体的? ', '我记得除了数码暴龙和某合体变身少女外就没有了？', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '第一次看龙珠人造人篇，还以为17号是女性', '有人跟我一样感觉吗？感觉如果就看脸的话17号也很美女', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '国配《七龙珠》里天津饭的声线为什么前后有巨大差别？ ', '我记得以前看国配版本的《七龙珠》，天津饭和饺子两人第一次出现应该是原创的情节“猪鹿蝶”一集吧？然后我记得那一集登场的天津饭说话阴阳怪气的，像个反派人物。但是在后面再次登场时说话就变得沉稳很多了。这个配音的变化是怎么回事？', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '还有龙珠这种大量换队友的战斗漫吗？', '龙珠也算是战斗漫的奇葩，极少数按批换队友的漫，类似的我只想到圣石小子勉强算。那些死掉前队友才换的不算（比如剑风，杀戮都市等等都不算），因为队友实力不济被换掉的战斗漫还有哪些（PS：这个被换掉队友必须是戏份多，不是那种酱油队友）？', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '等下，龙珠超的凯尔根本就不是传说超级赛亚人啊', '各种信息不是很明白了吗，贝吉塔说这是赛亚人本来的样子，并没有说是传说赛亚人活着超赛本来的样子\n\n卡游里开尔的名字并没有超赛或者传超，能力里也没有，只是特别注明了暴走\n\n之前设定图有瘦身版开尔，有眼珠\n\n所以这里只是暴走形态，而不是传说超赛形态啊，至于模仿布罗利是服务粉丝，很快就会控制力量变成正常超赛形态', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '你们都在讨论堪巴是谁！', '你们那么快就忘记当初超级赛亚人之神的设定是什么？？ 超红是因为什么而诞生的？然后最后获胜的是谁？\n然后你再想一下堪巴的称号！你们还觉得这个人物是乱造的？？实力是乱排的？？\n堪巴有可能在那一次与超红对决之后把古代正义的赛亚人团灭了。就被弗拐走了！ （你要知道弗本来就能穿越时空。这种事对于弗来说轻而易举！）', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '堪巴不是传超凭什么比布罗利强那么多', '常态堪巴就能和超蓝贝吉特打，布罗利常态打不过超蓝悟空，开传超不知道能不能过常态堪巴', '', '8043', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
