insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '百年任务新连载热情和鼠绘都不打算汉化吗?', '其实从目前来看剧情还不错, 神龙都有了神还会远吗?说不定后面安库瑟拉姆都会出来, 后面的剧情还是很值得期待的', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '妖精的尾巴完全看不下去了', 'http://manhua.178.com/yaojingdeweiba/20338.shtml\n\n没有半点铺垫\n没有半点说明\n之前再怎么被人打得满地找牙\n突然大吼一声就把对手打趴了\n\n看一话花的时间比之前完现术战士还少\n时髦神至少分镜真的很时髦', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '妖精的尾巴欠缺了什么', '妖尾漫画有没有可能进化成比肩三大漫的存在  到底欠缺了什么？\n\n\n不要吐槽不在JUMP啦  正所谓英雄莫问出处    吐槽秒杀什么的   死神不也是莫名其妙的秒杀 鸣人不也只会开九尾挂\n\n\n妖尾正能量+MAX  情节太过单纯？  作者才能的问题？      对于公会的诠释超温暖人心啊   信念的力量 没有什么好怀疑的吧\n\n\n不发便当是硬伤？\n\n\n顺便说两句动画  应该是目前长篇动画里水准保持最稳定的了吧 CAST瞎眼   OP ED 换的勤  闪耀无比的BGM  虽然已经停了', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '妖精的尾巴主旋律用的什么乐器？', '最近才看妖尾动画，以前看过漫画，所以动画就没看，最近无聊实在没什么看得从头看一下发现音乐真不错。\n\n风笛的声音真好听。想求更多类似的调调。\n\n顺便那个真的是风笛么，就主题比较常出的那个，听起来像是弦乐拉奏类的，非常顺耳。\n\n下的OST1，这两首里面有这个乐器，都是开头出的。不确定是不是。\n\nFAIRY TAIL main theme\nInvoke magic', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '《妖精的尾巴》这种直接翻译的名字不够好吧', '《fairy tail》这漫画，我们似乎都习惯直接叫妖精的尾巴了，但我不能认同这种翻译，因为这个和主题偏离太大，尤其是“的”这个字更加是大错，在中文里加了“的”的话就是强调和突出，然而整个故事其实和“妖精”的“尾巴”没有任何关系，“妖精尾巴”是主角们的公会\n\n\n也有叫少年魔导师的，这个靠谱点，但也还是差点，因为毕竟里面也有大叔大婶老头啊\n\n\n如果翻译叫《魔道士公会》大家觉得怎么样？，挺贴题的，就是没啥深度就是了\n\n\n顺带一提，台版叫《魔导少年》', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
