insert into `bbs_post` ( `uid`, `title`, `content`, `label`, `edition_id`, `lat`, `lng`, `location`, `examin`, `operating_time`, `is_del`, `is_top`, `topped_at`, `is_essence`, `created_at`, `updated_at`, `deleted_at`) values ( '0', '漫画看完来吐槽', '无脑HE\n怎么说呢，没想到浓眉大眼的BOSS是假弟控，真萝莉控\n\n也不知怎么就追了那么久，虽然剧情一塌糊涂但好歹是个无脑**，连老头子都救回来了，基本上没有什么不完美的，能吐便当的全吐了\n\n感觉有点语无伦次，算了，最后，为真岛浩的福利和奶子干杯吧！', '', '7005', '', '', '', '0', '0', '0', '0', '0', '0', '0', '0', '0');
